INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2, N'الْحَمْدُ لِلَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3, N'الرَّحْمنِ الرَّحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4, N'مالِكِ يَوْمِ الدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5, N'إِيَّاكَ نَعْبُدُ وَ إِيَّاكَ نَسْتَعِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6, N'اهْدِنَا الصِّراطَ الْمُسْتَقِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (7, N'صِراطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَ لاَ الضَّالِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (8, N'الم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (9, N'ذلِكَ الْكِتابُ لا رَيْبَ فِيهِ هُدىً لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (10, N'الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَ يُقِيمُونَ الصَّلاةَ وَ مِمَّا رَزَقْناهُمْ يُنْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (11, N'وَ الَّذِينَ يُؤْمِنُونَ بِما أُنْزِلَ إِلَيْكَ وَ ما أُنْزِلَ مِنْ قَبْلِكَ وَ بِالْآخِرَةِ هُمْ يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (12, N'أُولئِكَ عَلى هُدىً مِنْ رَبِّهِمْ وَ أُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (13, N'إِنَّ الَّذِينَ كَفَرُوا سَواءٌ عَلَيْهِمْ أَ أَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (14, N'خَتَمَ اللَّهُ عَلى قُلُوبِهِمْ وَ عَلى سَمْعِهِمْ وَ عَلى أَبْصارِهِمْ غِشاوَةٌ وَ لَهُمْ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (15, N'وَ مِنَ النَّاسِ مَنْ يَقُولُ آمَنَّا بِاللَّهِ وَ بِالْيَوْمِ الْآخِرِ وَ ما هُمْ بِمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (16, N'يُخادِعُونَ اللَّهَ وَ الَّذِينَ آمَنُوا وَ ما يَخْدَعُونَ إِلاَّ أَنْفُسَهُمْ وَ ما يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (17, N'فِي قُلُوبِهِمْ مَرَضٌ فَزادَهُمُ اللَّهُ مَرَضاً وَ لَهُمْ عَذابٌ أَلِيمٌ بِما كانُوا يَكْذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (18, N'وَ إِذا قِيلَ لَهُمْ لا تُفْسِدُوا فِي الْأَرْضِ قالُوا إِنَّما نَحْنُ مُصْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (19, N'أَلا إِنَّهُمْ هُمُ الْمُفْسِدُونَ وَ لكِنْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (20, N'وَ إِذا قِيلَ لَهُمْ آمِنُوا كَما آمَنَ النَّاسُ قالُوا أَ نُؤْمِنُ كَما آمَنَ السُّفَهاءُ أَلا إِنَّهُمْ هُمُ السُّفَهاءُ وَ لكِنْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (21, N'وَ إِذا لَقُوا الَّذِينَ آمَنُوا قالُوا آمَنَّا وَ إِذا خَلَوْا إِلى شَياطِينِهِمْ قالُوا إِنَّا مَعَكُمْ إِنَّما نَحْنُ مُسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (22, N'اللَّهُ يَسْتَهْزِئُ بِهِمْ وَ يَمُدُّهُمْ فِي طُغْيانِهِمْ يَعْمَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (23, N'أُولئِكَ الَّذِينَ اشْتَرَوُا الضَّلالَةَ بِالْهُدى فَما رَبِحَتْ تِجارَتُهُمْ وَ ما كانُوا مُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (24, N'مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ ناراً فَلَمَّا أَضاءَتْ ما حَوْلَهُ ذَهَبَ اللَّهُ بِنُورِهِمْ وَ تَرَكَهُمْ فِي ظُلُماتٍ لا يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (25, N'صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لا يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (26, N'أَوْ كَصَيِّبٍ مِنَ السَّماءِ فِيهِ ظُلُماتٌ وَ رَعْدٌ وَ بَرْقٌ يَجْعَلُونَ أَصابِعَهُمْ فِي آذانِهِمْ مِنَ الصَّواعِقِ حَذَرَ الْمَوْتِ وَ اللَّهُ مُحِيطٌ بِالْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (27, N'يَكادُ الْبَرْقُ يَخْطَفُ أَبْصارَهُمْ كُلَّما أَضاءَ لَهُمْ مَشَوْا فِيهِ وَ إِذا أَظْلَمَ عَلَيْهِمْ قامُوا وَ لَوْ شاءَ اللَّهُ لَذَهَبَ بِسَمْعِهِمْ وَ أَبْصارِهِمْ إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (28, N'يا أَيُّهَا النَّاسُ اعْبُدُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ وَ الَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (29, N'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ فِراشاً وَ السَّماءَ بِناءً وَ أَنْزَلَ مِنَ السَّماءِ ماءً فَأَخْرَجَ بِهِ مِنَ الثَّمَراتِ رِزْقاً لَكُمْ فَلا تَجْعَلُوا لِلَّهِ أَنْداداً وَ أَنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (30, N'وَ إِنْ كُنْتُمْ فِي رَيْبٍ مِمَّا نَزَّلْنا عَلى عَبْدِنا فَأْتُوا بِسُورَةٍ مِنْ مِثْلِهِ وَ ادْعُوا شُهَداءَكُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (31, N'فَإِنْ لَمْ تَفْعَلُوا وَ لَنْ تَفْعَلُوا فَاتَّقُوا النَّارَ الَّتِي وَقُودُهَا النَّاسُ وَ الْحِجارَةُ أُعِدَّتْ لِلْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (32, N'وَ بَشِّرِ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ أَنَّ لَهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ كُلَّما رُزِقُوا مِنْها مِنْ ثَمَرَةٍ رِزْقاً قالُوا هذَا الَّذِي رُزِقْنا مِنْ قَبْلُ وَ أُتُوا بِهِ مُتَشابِهاً وَ لَهُمْ فِيها أَزْواجٌ مُطَهَّرَةٌ وَ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (33, N'إِنَّ اللَّهَ لا يَسْتَحْيِي أَنْ يَضْرِبَ مَثَلاً ما بَعُوضَةً فَما فَوْقَها فَأَمَّا الَّذِينَ آمَنُوا فَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِنْ رَبِّهِمْ وَ أَمَّا الَّذِينَ كَفَرُوا فَيَقُولُونَ ما ذا أَرادَ اللَّهُ بِهذا مَثَلاً يُضِلُّ بِهِ كَثِيراً وَ يَهْدِي بِهِ كَثِيراً وَ ما يُضِلُّ بِهِ إِلاَّ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (34, N'الَّذِينَ يَنْقُضُونَ عَهْدَ اللَّهِ مِنْ بَعْدِ مِيثاقِهِ وَ يَقْطَعُونَ ما أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ وَ يُفْسِدُونَ فِي الْأَرْضِ أُولئِكَ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (35, N'كَيْفَ تَكْفُرُونَ بِاللَّهِ وَ كُنْتُمْ أَمْواتاً فَأَحْياكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ثُمَّ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (36, N'هُوَ الَّذِي خَلَقَ لَكُمْ ما فِي الْأَرْضِ جَمِيعاً ثُمَّ اسْتَوى إِلَى السَّماءِ فَسَوَّاهُنَّ سَبْعَ سَماواتٍ وَ هُوَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (37, N'وَ إِذْ قالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي جاعِلٌ فِي الْأَرْضِ خَلِيفَةً قالُوا أَ تَجْعَلُ فِيها مَنْ يُفْسِدُ فِيها وَ يَسْفِكُ الدِّماءَ وَ نَحْنُ نُسَبِّحُ بِحَمْدِكَ وَ نُقَدِّسُ لَكَ قالَ إِنِّي أَعْلَمُ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (38, N'وَ عَلَّمَ آدَمَ الْأَسْماءَ كُلَّها ثُمَّ عَرَضَهُمْ عَلَى الْمَلائِكَةِ فَقالَ أَنْبِئُونِي بِأَسْماءِ هؤُلاءِ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (39, N'قالُوا سُبْحانَكَ لا عِلْمَ لَنا إِلاَّ ما عَلَّمْتَنا إِنَّكَ أَنْتَ الْعَلِيمُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (40, N'قالَ يا آدَمُ أَنْبِئْهُمْ بِأَسْمائِهِمْ فَلَمَّا أَنْبَأَهُمْ بِأَسْمائِهِمْ قالَ أَ لَمْ أَقُلْ لَكُمْ إِنِّي أَعْلَمُ غَيْبَ السَّماواتِ وَ الْأَرْضِ وَ أَعْلَمُ ما تُبْدُونَ وَ ما كُنْتُمْ تَكْتُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (41, N'وَ إِذْ قُلْنا لِلْمَلائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاَّ إِبْلِيسَ أَبى وَ اسْتَكْبَرَ وَ كانَ مِنَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (42, N'وَ قُلْنا يا آدَمُ اسْكُنْ أَنْتَ وَ زَوْجُكَ الْجَنَّةَ وَ كُلا مِنْها رَغَداً حَيْثُ شِئْتُما وَ لا تَقْرَبا هذِهِ الشَّجَرَةَ فَتَكُونا مِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (43, N'فَأَزَلَّهُمَا الشَّيْطانُ عَنْها فَأَخْرَجَهُما مِمَّا كانا فِيهِ وَ قُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ وَ لَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَ مَتاعٌ إِلى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (44, N'فَتَلَقَّى آدَمُ مِنْ رَبِّهِ كَلِماتٍ فَتابَ عَلَيْهِ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (45, N'قُلْنَا اهْبِطُوا مِنْها جَمِيعاً فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدىً فَمَنْ تَبِعَ هُدايَ فَلا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (46, N'وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآياتِنا أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (47, N'يا بَنِي إِسْرائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَ أَوْفُوا بِعَهْدِي أُوفِ بِعَهْدِكُمْ وَ إِيَّايَ فَارْهَبُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (48, N'وَ آمِنُوا بِما أَنْزَلْتُ مُصَدِّقاً لِما مَعَكُمْ وَ لا تَكُونُوا أَوَّلَ كافِرٍ بِهِ وَ لا تَشْتَرُوا بِآياتِي ثَمَناً قَلِيلاً وَ إِيَّايَ فَاتَّقُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (49, N'وَ لا تَلْبِسُوا الْحَقَّ بِالْباطِلِ وَ تَكْتُمُوا الْحَقَّ وَ أَنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (50, N'وَ أَقِيمُوا الصَّلاةَ وَ آتُوا الزَّكاةَ وَ ارْكَعُوا مَعَ الرَّاكِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (51, N'أَ تَأْمُرُونَ النَّاسَ بِالْبِرِّ وَ تَنْسَوْنَ أَنْفُسَكُمْ وَ أَنْتُمْ تَتْلُونَ الْكِتابَ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (52, N'وَ اسْتَعِينُوا بِالصَّبْرِ وَ الصَّلاةِ وَ إِنَّها لَكَبِيرَةٌ إِلاَّ عَلَى الْخاشِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (53, N'الَّذِينَ يَظُنُّونَ أَنَّهُمْ مُلاقُوا رَبِّهِمْ وَ أَنَّهُمْ إِلَيْهِ راجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (54, N'يا بَنِي إِسْرائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَ أَنِّي فَضَّلْتُكُمْ عَلَى الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (55, N'وَ اتَّقُوا يَوْماً لا تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئاً وَ لا يُقْبَلُ مِنْها شَفاعَةٌ وَ لا يُؤْخَذُ مِنْها عَدْلٌ وَ لا هُمْ يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (56, N'وَ إِذْ نَجَّيْناكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذابِ يُذَبِّحُونَ أَبْناءَكُمْ وَ يَسْتَحْيُونَ نِساءَكُمْ وَ فِي ذلِكُمْ بَلاءٌ مِنْ رَبِّكُمْ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (57, N'وَ إِذْ فَرَقْنا بِكُمُ الْبَحْرَ فَأَنْجَيْناكُمْ وَ أَغْرَقْنا آلَ فِرْعَوْنَ وَ أَنْتُمْ تَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (58, N'وَ إِذْ واعَدْنا مُوسى أَرْبَعِينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِهِ وَ أَنْتُمْ ظالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (59, N'ثُمَّ عَفَوْنا عَنْكُمْ مِنْ بَعْدِ ذلِكَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (60, N'وَ إِذْ آتَيْنا مُوسَى الْكِتابَ وَ الْفُرْقانَ لَعَلَّكُمْ تَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (61, N'وَ إِذْ قالَ مُوسى لِقَوْمِهِ يا قَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنْفُسَكُمْ بِاتِّخاذِكُمُ الْعِجْلَ فَتُوبُوا إِلى بارِئِكُمْ فَاقْتُلُوا أَنْفُسَكُمْ ذلِكُمْ خَيْرٌ لَكُمْ عِنْدَ بارِئِكُمْ فَتابَ عَلَيْكُمْ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (62, N'وَ إِذْ قُلْتُمْ يا مُوسى لَنْ نُؤْمِنَ لَكَ حَتَّى نَرَى اللَّهَ جَهْرَةً فَأَخَذَتْكُمُ الصَّاعِقَةُ وَ أَنْتُمْ تَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (63, N'ثُمَّ بَعَثْناكُمْ مِنْ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (64, N'وَ ظَلَّلْنا عَلَيْكُمُ الْغَمامَ وَ أَنْزَلْنا عَلَيْكُمُ الْمَنَّ وَ السَّلْوى كُلُوا مِنْ طَيِّباتِ ما رَزَقْناكُمْ وَ ما ظَلَمُونا وَ لكِنْ كانُوا أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (65, N'وَ إِذْ قُلْنَا ادْخُلُوا هذِهِ الْقَرْيَةَ فَكُلُوا مِنْها حَيْثُ شِئْتُمْ رَغَداً وَ ادْخُلُوا الْبابَ سُجَّداً وَ قُولُوا حِطَّةٌ نَغْفِرْ لَكُمْ خَطاياكُمْ وَ سَنَزِيدُ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (66, N'فَبَدَّلَ الَّذِينَ ظَلَمُوا قَوْلاً غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَنْزَلْنا عَلَى الَّذِينَ ظَلَمُوا رِجْزاً مِنَ السَّماءِ بِما كانُوا يَفْسُقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (67, N'وَ إِذِ اسْتَسْقى مُوسى لِقَوْمِهِ فَقُلْنَا اضْرِبْ بِعَصاكَ الْحَجَرَ فَانْفَجَرَتْ مِنْهُ اثْنَتا عَشْرَةَ عَيْناً قَدْ عَلِمَ كُلُّ أُناسٍ مَشْرَبَهُمْ كُلُوا وَ اشْرَبُوا مِنْ رِزْقِ اللَّهِ وَ لا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (68, N'وَ إِذْ قُلْتُمْ يا مُوسى لَنْ نَصْبِرَ عَلى طَعامٍ واحِدٍ فَادْعُ لَنا رَبَّكَ يُخْرِجْ لَنا مِمَّا تُنْبِتُ الْأَرْضُ مِنْ بَقْلِها وَ قِثَّائِها وَ فُومِها وَ عَدَسِها وَ بَصَلِها قالَ أَ تَسْتَبْدِلُونَ الَّذِي هُوَ أَدْنى بِالَّذِي هُوَ خَيْرٌ اهْبِطُوا مِصْراً فَإِنَّ لَكُمْ ما سَأَلْتُمْ وَ ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَ الْمَسْكَنَةُ وَ باؤُ بِغَضَبٍ مِنَ اللَّهِ ذلِكَ بِأَنَّهُمْ كانُوا يَكْفُرُونَ بِآياتِ اللَّهِ وَ يَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ الْحَقِّ ذلِكَ بِما عَصَوْا وَ كانُوا يَعْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (69, N'إِنَّ الَّذِينَ آمَنُوا وَ الَّذِينَ هادُوا وَ النَّصارى وَ الصَّابِئِينَ مَنْ آمَنَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ عَمِلَ صالِحاً فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَ لا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (70, N'وَ إِذْ أَخَذْنا مِيثاقَكُمْ وَ رَفَعْنا فَوْقَكُمُ الطُّورَ خُذُوا ما آتَيْناكُمْ بِقُوَّةٍ وَ اذْكُرُوا ما فِيهِ لَعَلَّكُمْ تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (71, N'ثُمَّ تَوَلَّيْتُمْ مِنْ بَعْدِ ذلِكَ فَلَوْ لا فَضْلُ اللَّهِ عَلَيْكُمْ وَ رَحْمَتُهُ لَكُنْتُمْ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (72, N'وَ لَقَدْ عَلِمْتُمُ الَّذِينَ اعْتَدَوْا مِنْكُمْ فِي السَّبْتِ فَقُلْنا لَهُمْ كُونُوا قِرَدَةً خاسِئِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (73, N'فَجَعَلْناها نَكالاً لِما بَيْنَ يَدَيْها وَ ما خَلْفَها وَ مَوْعِظَةً لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (74, N'وَ إِذْ قالَ مُوسى لِقَوْمِهِ إِنَّ اللَّهَ يَأْمُرُكُمْ أَنْ تَذْبَحُوا بَقَرَةً قالُوا أَ تَتَّخِذُنا هُزُواً قالَ أَعُوذُ بِاللَّهِ أَنْ أَكُونَ مِنَ الْجاهِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (75, N'قالُوا ادْعُ لَنا رَبَّكَ يُبَيِّنْ لَنا ما هِيَ قالَ إِنَّهُ يَقُولُ إِنَّها بَقَرَةٌ لا فارِضٌ وَ لا بِكْرٌ عَوانٌ بَيْنَ ذلِكَ فَافْعَلُوا ما تُؤْمَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (76, N'قالُوا ادْعُ لَنا رَبَّكَ يُبَيِّنْ لَنا ما لَوْنُها قالَ إِنَّهُ يَقُولُ إِنَّها بَقَرَةٌ صَفْراءُ فاقِعٌ لَوْنُها تَسُرُّ النَّاظِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (77, N'قالُوا ادْعُ لَنا رَبَّكَ يُبَيِّنْ لَنا ما هِيَ إِنَّ الْبَقَرَ تَشابَهَ عَلَيْنا وَ إِنَّا إِنْ شاءَ اللَّهُ لَمُهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (78, N'قالَ إِنَّهُ يَقُولُ إِنَّها بَقَرَةٌ لا ذَلُولٌ تُثِيرُ الْأَرْضَ وَ لا تَسْقِي الْحَرْثَ مُسَلَّمَةٌ لا شِيَةَ فِيها قالُوا الْآنَ جِئْتَ بِالْحَقِّ فَذَبَحُوها وَ ما كادُوا يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (79, N'وَ إِذْ قَتَلْتُمْ نَفْساً فَادَّارَأْتُمْ فِيها وَ اللَّهُ مُخْرِجٌ ما كُنْتُمْ تَكْتُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (80, N'فَقُلْنا اضْرِبُوهُ بِبَعْضِها كَذلِكَ يُحْيِ اللَّهُ الْمَوْتى وَ يُرِيكُمْ آياتِهِ لَعَلَّكُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (81, N'ثُمَّ قَسَتْ قُلُوبُكُمْ مِنْ بَعْدِ ذلِكَ فَهِيَ كَالْحِجارَةِ أَوْ أَشَدُّ قَسْوَةً وَ إِنَّ مِنَ الْحِجارَةِ لَما يَتَفَجَّرُ مِنْهُ الْأَنْهارُ وَ إِنَّ مِنْها لَما يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْماءُ وَ إِنَّ مِنْها لَما يَهْبِطُ مِنْ خَشْيَةِ اللَّهِ وَ مَا اللَّهُ بِغافِلٍ عَمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (82, N'أَ فَتَطْمَعُونَ أَنْ يُؤْمِنُوا لَكُمْ وَ قَدْ كانَ فَرِيقٌ مِنْهُمْ يَسْمَعُونَ كَلامَ اللَّهِ ثُمَّ يُحَرِّفُونَهُ مِنْ بَعْدِ ما عَقَلُوهُ وَ هُمْ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (83, N'وَ إِذا لَقُوا الَّذِينَ آمَنُوا قالُوا آمَنَّا وَ إِذا خَلا بَعْضُهُمْ إِلى بَعْضٍ قالُوا أَ تُحَدِّثُونَهُمْ بِما فَتَحَ اللَّهُ عَلَيْكُمْ لِيُحَاجُّوكُمْ بِهِ عِنْدَ رَبِّكُمْ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (84, N'أَ وَ لا يَعْلَمُونَ أَنَّ اللَّهَ يَعْلَمُ ما يُسِرُّونَ وَ ما يُعْلِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (85, N'وَ مِنْهُمْ أُمِّيُّونَ لا يَعْلَمُونَ الْكِتابَ إِلاَّ أَمانِيَّ وَ إِنْ هُمْ إِلاَّ يَظُنُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (86, N'فَوَيْلٌ لِلَّذِينَ يَكْتُبُونَ الْكِتابَ بِأَيْدِيهِمْ ثُمَّ يَقُولُونَ هذا مِنْ عِنْدِ اللَّهِ لِيَشْتَرُوا بِهِ ثَمَناً قَلِيلاً فَوَيْلٌ لَهُمْ مِمَّا كَتَبَتْ أَيْدِيهِمْ وَ وَيْلٌ لَهُمْ مِمَّا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (87, N'وَ قالُوا لَنْ تَمَسَّنَا النَّارُ إِلاَّ أَيَّاماً مَعْدُودَةً قُلْ أَتَّخَذْتُمْ عِنْدَ اللَّهِ عَهْداً فَلَنْ يُخْلِفَ اللَّهُ عَهْدَهُ أَمْ تَقُولُونَ عَلَى اللَّهِ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (88, N'بَلى مَنْ كَسَبَ سَيِّئَةً وَ أَحاطَتْ بِهِ خَطِيئَتُهُ فَأُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (89, N'وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ أُولئِكَ أَصْحابُ الْجَنَّةِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (90, N'وَ إِذْ أَخَذْنا مِيثاقَ بَنِي إِسْرائِيلَ لا تَعْبُدُونَ إِلاَّ اللَّهَ وَ بِالْوالِدَيْنِ إِحْساناً وَ ذِي الْقُرْبى وَ الْيَتامى وَ الْمَساكِينِ وَ قُولُوا لِلنَّاسِ حُسْناً وَ أَقِيمُوا الصَّلاةَ وَ آتُوا الزَّكاةَ ثُمَّ تَوَلَّيْتُمْ إِلاَّ قَلِيلاً مِنْكُمْ وَ أَنْتُمْ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (91, N'وَ إِذْ أَخَذْنا مِيثاقَكُمْ لا تَسْفِكُونَ دِماءَكُمْ وَ لا تُخْرِجُونَ أَنْفُسَكُمْ مِنْ دِيارِكُمْ ثُمَّ أَقْرَرْتُمْ وَ أَنْتُمْ تَشْهَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (92, N'ثُمَّ أَنْتُمْ هؤُلاءِ تَقْتُلُونَ أَنْفُسَكُمْ وَ تُخْرِجُونَ فَرِيقاً مِنْكُمْ مِنْ دِيارِهِمْ تَظاهَرُونَ عَلَيْهِمْ بِالْإِثْمِ وَ الْعُدْوانِ وَ إِنْ يَأْتُوكُمْ أُسارى تُفادُوهُمْ وَ هُوَ مُحَرَّمٌ عَلَيْكُمْ إِخْراجُهُمْ أَ فَتُؤْمِنُونَ بِبَعْضِ الْكِتابِ وَ تَكْفُرُونَ بِبَعْضٍ فَما جَزاءُ مَنْ يَفْعَلُ ذلِكَ مِنْكُمْ إِلاَّ خِزْيٌ فِي الْحَياةِ الدُّنْيا وَ يَوْمَ الْقِيامَةِ يُرَدُّونَ إِلى أَشَدِّ الْعَذابِ وَ مَا اللَّهُ بِغافِلٍ عَمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (93, N'أُولئِكَ الَّذِينَ اشْتَرَوُا الْحَياةَ الدُّنْيا بِالْآخِرَةِ فَلا يُخَفَّفُ عَنْهُمُ الْعَذابُ وَ لا هُمْ يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (94, N'وَ لَقَدْ آتَيْنا مُوسَى الْكِتابَ وَ قَفَّيْنا مِنْ بَعْدِهِ بِالرُّسُلِ وَ آتَيْنا عِيسَى ابْنَ مَرْيَمَ الْبَيِّناتِ وَ أَيَّدْناهُ بِرُوحِ الْقُدُسِ أَ فَكُلَّما جاءَكُمْ رَسُولٌ بِما لا تَهْوى أَنْفُسُكُمُ اسْتَكْبَرْتُمْ فَفَرِيقاً كَذَّبْتُمْ وَ فَرِيقاً تَقْتُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (95, N'وَ قالُوا قُلُوبُنا غُلْفٌ بَلْ لَعَنَهُمُ اللَّهُ بِكُفْرِهِمْ فَقَلِيلاً ما يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (96, N'وَ لَمَّا جاءَهُمْ كِتابٌ مِنْ عِنْدِ اللَّهِ مُصَدِّقٌ لِما مَعَهُمْ وَ كانُوا مِنْ قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذِينَ كَفَرُوا فَلَمَّا جاءَهُمْ ما عَرَفُوا كَفَرُوا بِهِ فَلَعْنَةُ اللَّهِ عَلَى الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (97, N'بِئْسَمَا اشْتَرَوْا بِهِ أَنْفُسَهُمْ أَنْ يَكْفُرُوا بِما أَنْزَلَ اللَّهُ بَغْياً أَنْ يُنَزِّلَ اللَّهُ مِنْ فَضْلِهِ عَلى مَنْ يَشاءُ مِنْ عِبادِهِ فَباؤُ بِغَضَبٍ عَلى غَضَبٍ وَ لِلْكافِرِينَ عَذابٌ مُهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (98, N'وَ إِذا قِيلَ لَهُمْ آمِنُوا بِما أَنْزَلَ اللَّهُ قالُوا نُؤْمِنُ بِما أُنْزِلَ عَلَيْنا وَ يَكْفُرُونَ بِما وَراءَهُ وَ هُوَ الْحَقُّ مُصَدِّقاً لِما مَعَهُمْ قُلْ فَلِمَ تَقْتُلُونَ أَنْبِياءَ اللَّهِ مِنْ قَبْلُ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (99, N'وَ لَقَدْ جاءَكُمْ مُوسى بِالْبَيِّناتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِهِ وَ أَنْتُمْ ظالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (100, N'وَ إِذْ أَخَذْنا مِيثاقَكُمْ وَ رَفَعْنا فَوْقَكُمُ الطُّورَ خُذُوا ما آتَيْناكُمْ بِقُوَّةٍ وَ اسْمَعُوا قالُوا سَمِعْنا وَ عَصَيْنا وَ أُشْرِبُوا فِي قُلُوبِهِمُ الْعِجْلَ بِكُفْرِهِمْ قُلْ بِئْسَما يَأْمُرُكُمْ بِهِ إِيمانُكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (101, N'قُلْ إِنْ كانَتْ لَكُمُ الدَّارُ الْآخِرَةُ عِنْدَ اللَّهِ خالِصَةً مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صادِقِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (102, N'وَ لَنْ يَتَمَنَّوْهُ أَبَداً بِما قَدَّمَتْ أَيْدِيهِمْ وَ اللَّهُ عَلِيمٌ بِالظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (103, N'وَ لَتَجِدَنَّهُمْ أَحْرَصَ النَّاسِ عَلى حَياةٍ وَ مِنَ الَّذِينَ أَشْرَكُوا يَوَدُّ أَحَدُهُمْ لَوْ يُعَمَّرُ أَلْفَ سَنَةٍ وَ ما هُوَ بِمُزَحْزِحِهِ مِنَ الْعَذابِ أَنْ يُعَمَّرَ وَ اللَّهُ بَصِيرٌ بِما يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (104, N'قُلْ مَنْ كانَ عَدُوًّا لِجِبْرِيلَ فَإِنَّهُ نَزَّلَهُ عَلى قَلْبِكَ بِإِذْنِ اللَّهِ مُصَدِّقاً لِما بَيْنَ يَدَيْهِ وَ هُدىً وَ بُشْرى لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (105, N'مَنْ كانَ عَدُوًّا لِلَّهِ وَ مَلائِكَتِهِ وَ رُسُلِهِ وَ جِبْرِيلَ وَ مِيكالَ فَإِنَّ اللَّهَ عَدُوٌّ لِلْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (106, N'وَ لَقَدْ أَنْزَلْنا إِلَيْكَ آياتٍ بَيِّناتٍ وَ ما يَكْفُرُ بِها إِلاَّ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (107, N'أَ وَ كُلَّما عاهَدُوا عَهْداً نَبَذَهُ فَرِيقٌ مِنْهُمْ بَلْ أَكْثَرُهُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (108, N'وَ لَمَّا جاءَهُمْ رَسُولٌ مِنْ عِنْدِ اللَّهِ مُصَدِّقٌ لِما مَعَهُمْ نَبَذَ فَرِيقٌ مِنَ الَّذِينَ أُوتُوا الْكِتابَ كِتابَ اللَّهِ وَراءَ ظُهُورِهِمْ كَأَنَّهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (109, N'وَ اتَّبَعُوا ما تَتْلُوا الشَّياطِينُ عَلى مُلْكِ سُلَيْمانَ وَ ما كَفَرَ سُلَيْمانُ وَ لكِنَّ الشَّياطِينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَ وَ ما أُنْزِلَ عَلَى الْمَلَكَيْنِ بِبابِلَ هارُوتَ وَ مارُوتَ وَ ما يُعَلِّمانِ مِنْ أَحَدٍ حَتَّى يَقُولا إِنَّما نَحْنُ فِتْنَةٌ فَلا تَكْفُرْ فَيَتَعَلَّمُونَ مِنْهُما ما يُفَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَ زَوْجِهِ وَ ما هُمْ بِضارِّينَ بِهِ مِنْ أَحَدٍ إِلاَّ بِإِذْنِ اللَّهِ وَ يَتَعَلَّمُونَ ما يَضُرُّهُمْ وَ لا يَنْفَعُهُمْ وَ لَقَدْ عَلِمُوا لَمَنِ اشْتَراهُ ما لَهُ فِي الْآخِرَةِ مِنْ خَلاقٍ وَ لَبِئْسَ ما شَرَوْا بِهِ أَنْفُسَهُمْ لَوْ كانُوا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (110, N'وَ لَوْ أَنَّهُمْ آمَنُوا وَ اتَّقَوْا لَمَثُوبَةٌ مِنْ عِنْدِ اللَّهِ خَيْرٌ لَوْ كانُوا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (111, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَقُولُوا راعِنا وَ قُولُوا انْظُرْنا وَ اسْمَعُوا وَ لِلْكافِرِينَ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (112, N'ما يَوَدُّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتابِ وَ لاَ الْمُشْرِكِينَ أَنْ يُنَزَّلَ عَلَيْكُمْ مِنْ خَيْرٍ مِنْ رَبِّكُمْ وَ اللَّهُ يَخْتَصُّ بِرَحْمَتِهِ مَنْ يَشاءُ وَ اللَّهُ ذُو الْفَضْلِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (113, N'ما نَنْسَخْ مِنْ آيَةٍ أَوْ نُنْسِها نَأْتِ بِخَيْرٍ مِنْها أَوْ مِثْلِها أَ لَمْ تَعْلَمْ أَنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (114, N'أَ لَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ ما لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَ لا نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (115, N'أَمْ تُرِيدُونَ أَنْ تَسْئَلُوا رَسُولَكُمْ كَما سُئِلَ مُوسى مِنْ قَبْلُ وَ مَنْ يَتَبَدَّلِ الْكُفْرَ بِالْإِيمانِ فَقَدْ ضَلَّ سَواءَ السَّبِيلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (116, N'وَدَّ كَثِيرٌ مِنْ أَهْلِ الْكِتابِ لَوْ يَرُدُّونَكُمْ مِنْ بَعْدِ إِيمانِكُمْ كُفَّاراً حَسَداً مِنْ عِنْدِ أَنْفُسِهِمْ مِنْ بَعْدِ ما تَبَيَّنَ لَهُمُ الْحَقُّ فَاعْفُوا وَ اصْفَحُوا حَتَّى يَأْتِيَ اللَّهُ بِأَمْرِهِ إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (117, N'وَ أَقِيمُوا الصَّلاةَ وَ آتُوا الزَّكاةَ وَ ما تُقَدِّمُوا لِأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللَّهِ إِنَّ اللَّهَ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (118, N'وَ قالُوا لَنْ يَدْخُلَ الْجَنَّةَ إِلاَّ مَنْ كانَ هُوداً أَوْ نَصارى تِلْكَ أَمانِيُّهُمْ قُلْ هاتُوا بُرْهانَكُمْ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (119, N'بَلى مَنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَ هُوَ مُحْسِنٌ فَلَهُ أَجْرُهُ عِنْدَ رَبِّهِ وَ لا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (120, N'وَ قالَتِ الْيَهُودُ لَيْسَتِ النَّصارى عَلى شَيْ‏ءٍ وَ قالَتِ النَّصارى لَيْسَتِ الْيَهُودُ عَلى شَيْ‏ءٍ وَ هُمْ يَتْلُونَ الْكِتابَ كَذلِكَ قالَ الَّذِينَ لا يَعْلَمُونَ مِثْلَ قَوْلِهِمْ فَاللَّهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيامَةِ فِيما كانُوا فِيهِ يَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (121, N'وَ مَنْ أَظْلَمُ مِمَّنْ مَنَعَ مَساجِدَ اللَّهِ أَنْ يُذْكَرَ فِيهَا اسْمُهُ وَ سَعى فِي خَرابِها أُولئِكَ ما كانَ لَهُمْ أَنْ يَدْخُلُوها إِلاَّ خائِفِينَ لَهُمْ فِي الدُّنْيا خِزْيٌ وَ لَهُمْ فِي الْآخِرَةِ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (122, N'وَ لِلَّهِ الْمَشْرِقُ وَ الْمَغْرِبُ فَأَيْنَما تُوَلُّوا فَثَمَّ وَجْهُ اللَّهِ إِنَّ اللَّهَ واسِعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (123, N'وَ قالُوا اتَّخَذَ اللَّهُ وَلَداً سُبْحانَهُ بَلْ لَهُ ما فِي السَّماواتِ وَ الْأَرْضِ كُلٌّ لَهُ قانِتُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (124, N'بَدِيعُ السَّماواتِ وَ الْأَرْضِ وَ إِذا قَضى أَمْراً فَإِنَّما يَقُولُ لَهُ كُنْ فَيَكُونُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (125, N'وَ قالَ الَّذِينَ لا يَعْلَمُونَ لَوْ لا يُكَلِّمُنَا اللَّهُ أَوْ تَأْتِينا آيَةٌ كَذلِكَ قالَ الَّذِينَ مِنْ قَبْلِهِمْ مِثْلَ قَوْلِهِمْ تَشابَهَتْ قُلُوبُهُمْ قَدْ بَيَّنَّا الْآياتِ لِقَوْمٍ يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (126, N'إِنَّا أَرْسَلْناكَ بِالْحَقِّ بَشِيراً وَ نَذِيراً وَ لا تُسْئَلُ عَنْ أَصْحابِ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (127, N'وَ لَنْ تَرْضى عَنْكَ الْيَهُودُ وَ لاَ النَّصارى حَتَّى تَتَّبِعَ مِلَّتَهُمْ قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدى وَ لَئِنِ اتَّبَعْتَ أَهْواءَهُمْ بَعْدَ الَّذِي جاءَكَ مِنَ الْعِلْمِ ما لَكَ مِنَ اللَّهِ مِنْ وَلِيٍّ وَ لا نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (128, N'الَّذِينَ آتَيْناهُمُ الْكِتابَ يَتْلُونَهُ حَقَّ تِلاوَتِهِ أُولئِكَ يُؤْمِنُونَ بِهِ وَ مَنْ يَكْفُرْ بِهِ فَأُولئِكَ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (129, N'يا بَنِي إِسْرائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَ أَنِّي فَضَّلْتُكُمْ عَلَى الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (130, N'وَ اتَّقُوا يَوْماً لا تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئاً وَ لا يُقْبَلُ مِنْها عَدْلٌ وَ لا تَنْفَعُها شَفاعَةٌ وَ لا هُمْ يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (131, N'وَ إِذِ ابْتَلى إِبْراهِيمَ رَبُّهُ بِكَلِماتٍ فَأَتَمَّهُنَّ قالَ إِنِّي جاعِلُكَ لِلنَّاسِ إِماماً قالَ وَ مِنْ ذُرِّيَّتِي قالَ لا يَنالُ عَهْدِي الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (132, N'وَ إِذْ جَعَلْنَا الْبَيْتَ مَثابَةً لِلنَّاسِ وَ أَمْناً وَ اتَّخِذُوا مِنْ مَقامِ إِبْراهِيمَ مُصَلًّى وَ عَهِدْنا إِلى إِبْراهِيمَ وَ إِسْماعِيلَ أَنْ طَهِّرا بَيْتِيَ لِلطَّائِفِينَ وَ الْعاكِفِينَ وَ الرُّكَّعِ السُّجُودِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (133, N'وَ إِذْ قالَ إِبْراهِيمُ رَبِّ اجْعَلْ هذا بَلَداً آمِناً وَ ارْزُقْ أَهْلَهُ مِنَ الثَّمَراتِ مَنْ آمَنَ مِنْهُمْ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ قالَ وَ مَنْ كَفَرَ فَأُمَتِّعُهُ قَلِيلاً ثُمَّ أَضْطَرُّهُ إِلى عَذابِ النَّارِ وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (134, N'وَ إِذْ يَرْفَعُ إِبْراهِيمُ الْقَواعِدَ مِنَ الْبَيْتِ وَ إِسْماعِيلُ رَبَّنا تَقَبَّلْ مِنَّا إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (135, N'رَبَّنا وَ اجْعَلْنا مُسْلِمَيْنِ لَكَ وَ مِنْ ذُرِّيَّتِنا أُمَّةً مُسْلِمَةً لَكَ وَ أَرِنا مَناسِكَنا وَ تُبْ عَلَيْنا إِنَّكَ أَنْتَ التَّوَّابُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (136, N'رَبَّنا وَ ابْعَثْ فِيهِمْ رَسُولاً مِنْهُمْ يَتْلُوا عَلَيْهِمْ آياتِكَ وَ يُعَلِّمُهُمُ الْكِتابَ وَ الْحِكْمَةَ وَ يُزَكِّيهِمْ إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (137, N'وَ مَنْ يَرْغَبُ عَنْ مِلَّةِ إِبْراهِيمَ إِلاَّ مَنْ سَفِهَ نَفْسَهُ وَ لَقَدِ اصْطَفَيْناهُ فِي الدُّنْيا وَ إِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (138, N'إِذْ قالَ لَهُ رَبُّهُ أَسْلِمْ قالَ أَسْلَمْتُ لِرَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (139, N'وَ وَصَّى بِها إِبْراهِيمُ بَنِيهِ وَ يَعْقُوبُ يا بَنِيَّ إِنَّ اللَّهَ اصْطَفى لَكُمُ الدِّينَ فَلا تَمُوتُنَّ إِلاَّ وَ أَنْتُمْ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (140, N'أَمْ كُنْتُمْ شُهَداءَ إِذْ حَضَرَ يَعْقُوبَ الْمَوْتُ إِذْ قالَ لِبَنِيهِ ما تَعْبُدُونَ مِنْ بَعْدِي قالُوا نَعْبُدُ إِلهَكَ وَ إِلهَ آبائِكَ إِبْراهِيمَ وَ إِسْماعِيلَ وَ إِسْحاقَ إِلهاً واحِداً وَ نَحْنُ لَهُ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (141, N'تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَها ما كَسَبَتْ وَ لَكُمْ ما كَسَبْتُمْ وَ لا تُسْئَلُونَ عَمَّا كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (142, N'وَ قالُوا كُونُوا هُوداً أَوْ نَصارى تَهْتَدُوا قُلْ بَلْ مِلَّةَ إِبْراهِيمَ حَنِيفاً وَ ما كانَ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (143, N'قُولُوا آمَنَّا بِاللَّهِ وَ ما أُنْزِلَ إِلَيْنا وَ ما أُنْزِلَ إِلى إِبْراهِيمَ وَ إِسْماعِيلَ وَ إِسْحاقَ وَ يَعْقُوبَ وَ الْأَسْباطِ وَ ما أُوتِيَ مُوسى وَ عِيسى وَ ما أُوتِيَ النَّبِيُّونَ مِنْ رَبِّهِمْ لا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْهُمْ وَ نَحْنُ لَهُ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (144, N'فَإِنْ آمَنُوا بِمِثْلِ ما آمَنْتُمْ بِهِ فَقَدِ اهْتَدَوْا وَ إِنْ تَوَلَّوْا فَإِنَّما هُمْ فِي شِقاقٍ فَسَيَكْفِيكَهُمُ اللَّهُ وَ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (145, N'صِبْغَةَ اللَّهِ وَ مَنْ أَحْسَنُ مِنَ اللَّهِ صِبْغَةً وَ نَحْنُ لَهُ عابِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (146, N'قُلْ أَ تُحَاجُّونَنا فِي اللَّهِ وَ هُوَ رَبُّنا وَ رَبُّكُمْ وَ لَنا أَعْمالُنا وَ لَكُمْ أَعْمالُكُمْ وَ نَحْنُ لَهُ مُخْلِصُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (147, N'أَمْ تَقُولُونَ إِنَّ إِبْراهِيمَ وَ إِسْماعِيلَ وَ إِسْحاقَ وَ يَعْقُوبَ وَ الْأَسْباطَ كانُوا هُوداً أَوْ نَصارى قُلْ أَ أَنْتُمْ أَعْلَمُ أَمِ اللَّهُ وَ مَنْ أَظْلَمُ مِمَّنْ كَتَمَ شَهادَةً عِنْدَهُ مِنَ اللَّهِ وَ مَا اللَّهُ بِغافِلٍ عَمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (148, N'تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَها ما كَسَبَتْ وَ لَكُمْ ما كَسَبْتُمْ وَ لا تُسْئَلُونَ عَمَّا كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (149, N'سَيَقُولُ السُّفَهاءُ مِنَ النَّاسِ ما وَلاَّهُمْ عَنْ قِبْلَتِهِمُ الَّتِي كانُوا عَلَيْها قُلْ لِلَّهِ الْمَشْرِقُ وَ الْمَغْرِبُ يَهْدِي مَنْ يَشاءُ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (150, N'وَ كَذلِكَ جَعَلْناكُمْ أُمَّةً وَسَطاً لِتَكُونُوا شُهَداءَ عَلَى النَّاسِ وَ يَكُونَ الرَّسُولُ عَلَيْكُمْ شَهِيداً وَ ما جَعَلْنَا الْقِبْلَةَ الَّتِي كُنْتَ عَلَيْها إِلاَّ لِنَعْلَمَ مَنْ يَتَّبِعُ الرَّسُولَ مِمَّنْ يَنْقَلِبُ عَلى عَقِبَيْهِ وَ إِنْ كانَتْ لَكَبِيرَةً إِلاَّ عَلَى الَّذِينَ هَدَى اللَّهُ وَ ما كانَ اللَّهُ لِيُضِيعَ إِيمانَكُمْ إِنَّ اللَّهَ بِالنَّاسِ لَرَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (151, N'قَدْ نَرى تَقَلُّبَ وَجْهِكَ فِي السَّماءِ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضاها فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرامِ وَ حَيْثُ ما كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ وَ إِنَّ الَّذِينَ أُوتُوا الْكِتابَ لَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِنْ رَبِّهِمْ وَ مَا اللَّهُ بِغافِلٍ عَمَّا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (152, N'وَ لَئِنْ أَتَيْتَ الَّذِينَ أُوتُوا الْكِتابَ بِكُلِّ آيَةٍ ما تَبِعُوا قِبْلَتَكَ وَ ما أَنْتَ بِتابِعٍ قِبْلَتَهُمْ وَ ما بَعْضُهُمْ بِتابِعٍ قِبْلَةَ بَعْضٍ وَ لَئِنِ اتَّبَعْتَ أَهْواءَهُمْ مِنْ بَعْدِ ما جاءَكَ مِنَ الْعِلْمِ إِنَّكَ إِذاً لَمِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (153, N'الَّذِينَ آتَيْناهُمُ الْكِتابَ يَعْرِفُونَهُ كَما يَعْرِفُونَ أَبْناءَهُمْ وَ إِنَّ فَرِيقاً مِنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَ هُمْ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (154, N'الْحَقُّ مِنْ رَبِّكَ فَلا تَكُونَنَّ مِنَ الْمُمْتَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (155, N'وَ لِكُلٍّ وِجْهَةٌ هُوَ مُوَلِّيها فَاسْتَبِقُوا الْخَيْراتِ أَيْنَ ما تَكُونُوا يَأْتِ بِكُمُ اللَّهُ جَمِيعاً إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (156, N'وَ مِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرامِ وَ إِنَّهُ لَلْحَقُّ مِنْ رَبِّكَ وَ مَا اللَّهُ بِغافِلٍ عَمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (157, N'وَ مِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرامِ وَ حَيْثُ ما كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ لِئَلاَّ يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ إِلاَّ الَّذِينَ ظَلَمُوا مِنْهُمْ فَلا تَخْشَوْهُمْ وَ اخْشَوْنِي وَ لِأُتِمَّ نِعْمَتِي عَلَيْكُمْ وَ لَعَلَّكُمْ تَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (158, N'كَما أَرْسَلْنا فِيكُمْ رَسُولاً مِنْكُمْ يَتْلُوا عَلَيْكُمْ آياتِنا وَ يُزَكِّيكُمْ وَ يُعَلِّمُكُمُ الْكِتابَ وَ الْحِكْمَةَ وَ يُعَلِّمُكُمْ ما لَمْ تَكُونُوا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (159, N'فَاذْكُرُونِي أَذْكُرْكُمْ وَ اشْكُرُوا لِي وَ لا تَكْفُرُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (160, N'يا أَيُّهَا الَّذِينَ آمَنُوا اسْتَعِينُوا بِالصَّبْرِ وَ الصَّلاةِ إِنَّ اللَّهَ مَعَ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (161, N'وَ لا تَقُولُوا لِمَنْ يُقْتَلُ فِي سَبِيلِ اللَّهِ أَمْواتٌ بَلْ أَحْياءٌ وَ لكِنْ لا تَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (162, N'وَ لَنَبْلُوَنَّكُمْ بِشَيْ‏ءٍ مِنَ الْخَوْفِ وَ الْجُوعِ وَ نَقْصٍ مِنَ الْأَمْوالِ وَ الْأَنْفُسِ وَ الثَّمَراتِ وَ بَشِّرِ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (163, N'الَّذِينَ إِذا أَصابَتْهُمْ مُصِيبَةٌ قالُوا إِنَّا لِلَّهِ وَ إِنَّا إِلَيْهِ راجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (164, N'أُولئِكَ عَلَيْهِمْ صَلَواتٌ مِنْ رَبِّهِمْ وَ رَحْمَةٌ وَ أُولئِكَ هُمُ الْمُهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (165, N'إِنَّ الصَّفا وَ الْمَرْوَةَ مِنْ شَعائِرِ اللَّهِ فَمَنْ حَجَّ الْبَيْتَ أَوِ اعْتَمَرَ فَلا جُناحَ عَلَيْهِ أَنْ يَطَّوَّفَ بِهِما وَ مَنْ تَطَوَّعَ خَيْراً فَإِنَّ اللَّهَ شاكِرٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (166, N'إِنَّ الَّذِينَ يَكْتُمُونَ ما أَنْزَلْنا مِنَ الْبَيِّناتِ وَ الْهُدى مِنْ بَعْدِ ما بَيَّنَّاهُ لِلنَّاسِ فِي الْكِتابِ أُولئِكَ يَلْعَنُهُمُ اللَّهُ وَ يَلْعَنُهُمُ اللاَّعِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (167, N'إِلاَّ الَّذِينَ تابُوا وَ أَصْلَحُوا وَ بَيَّنُوا فَأُولئِكَ أَتُوبُ عَلَيْهِمْ وَ أَنَا التَّوَّابُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (168, N'إِنَّ الَّذِينَ كَفَرُوا وَ ماتُوا وَ هُمْ كُفَّارٌ أُولئِكَ عَلَيْهِمْ لَعْنَةُ اللَّهِ وَ الْمَلائِكَةِ وَ النَّاسِ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (169, N'خالِدِينَ فِيها لا يُخَفَّفُ عَنْهُمُ الْعَذابُ وَ لا هُمْ يُنْظَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (170, N'وَ إِلهُكُمْ إِلهٌ واحِدٌ لا إِلهَ إِلاَّ هُوَ الرَّحْمنُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (171, N'إِنَّ فِي خَلْقِ السَّماواتِ وَ الْأَرْضِ وَ اخْتِلافِ اللَّيْلِ وَ النَّهارِ وَ الْفُلْكِ الَّتِي تَجْرِي فِي الْبَحْرِ بِما يَنْفَعُ النَّاسَ وَ ما أَنْزَلَ اللَّهُ مِنَ السَّماءِ مِنْ ماءٍ فَأَحْيا بِهِ الْأَرْضَ بَعْدَ مَوْتِها وَ بَثَّ فِيها مِنْ كُلِّ دَابَّةٍ وَ تَصْرِيفِ الرِّياحِ وَ السَّحابِ الْمُسَخَّرِ بَيْنَ السَّماءِ وَ الْأَرْضِ لَآياتٍ لِقَوْمٍ يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (172, N'وَ مِنَ النَّاسِ مَنْ يَتَّخِذُ مِنْ دُونِ اللَّهِ أَنْداداً يُحِبُّونَهُمْ كَحُبِّ اللَّهِ وَ الَّذِينَ آمَنُوا أَشَدُّ حُبًّا لِلَّهِ وَ لَوْ يَرَى الَّذِينَ ظَلَمُوا إِذْ يَرَوْنَ الْعَذابَ أَنَّ الْقُوَّةَ لِلَّهِ جَمِيعاً وَ أَنَّ اللَّهَ شَدِيدُ الْعَذابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (173, N'إِذْ تَبَرَّأَ الَّذِينَ اتُّبِعُوا مِنَ الَّذِينَ اتَّبَعُوا وَ رَأَوُا الْعَذابَ وَ تَقَطَّعَتْ بِهِمُ الْأَسْبابُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (174, N'وَ قالَ الَّذِينَ اتَّبَعُوا لَوْ أَنَّ لَنا كَرَّةً فَنَتَبَرَّأَ مِنْهُمْ كَما تَبَرَّؤُا مِنَّا كَذلِكَ يُرِيهِمُ اللَّهُ أَعْمالَهُمْ حَسَراتٍ عَلَيْهِمْ وَ ما هُمْ بِخارِجِينَ مِنَ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (175, N'يا أَيُّهَا النَّاسُ كُلُوا مِمَّا فِي الْأَرْضِ حَلالاً طَيِّباً وَ لا تَتَّبِعُوا خُطُواتِ الشَّيْطانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (176, N'إِنَّما يَأْمُرُكُمْ بِالسُّوءِ وَ الْفَحْشاءِ وَ أَنْ تَقُولُوا عَلَى اللَّهِ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (177, N'وَ إِذا قِيلَ لَهُمُ اتَّبِعُوا ما أَنْزَلَ اللَّهُ قالُوا بَلْ نَتَّبِعُ ما أَلْفَيْنا عَلَيْهِ آباءَنا أَ وَ لَوْ كانَ آباؤُهُمْ لا يَعْقِلُونَ شَيْئاً وَ لا يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (178, N'وَ مَثَلُ الَّذِينَ كَفَرُوا كَمَثَلِ الَّذِي يَنْعِقُ بِما لا يَسْمَعُ إِلاَّ دُعاءً وَ نِداءً صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (179, N'يا أَيُّهَا الَّذِينَ آمَنُوا كُلُوا مِنْ طَيِّباتِ ما رَزَقْناكُمْ وَ اشْكُرُوا لِلَّهِ إِنْ كُنْتُمْ إِيَّاهُ تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (180, N'إِنَّما حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَ الدَّمَ وَ لَحْمَ الْخِنْزِيرِ وَ ما أُهِلَّ بِهِ لِغَيْرِ اللَّهِ فَمَنِ اضْطُرَّ غَيْرَ باغٍ وَ لا عادٍ فَلا إِثْمَ عَلَيْهِ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (181, N'إِنَّ الَّذِينَ يَكْتُمُونَ ما أَنْزَلَ اللَّهُ مِنَ الْكِتابِ وَ يَشْتَرُونَ بِهِ ثَمَناً قَلِيلاً أُولئِكَ ما يَأْكُلُونَ فِي بُطُونِهِمْ إِلاَّ النَّارَ وَ لا يُكَلِّمُهُمُ اللَّهُ يَوْمَ الْقِيامَةِ وَ لا يُزَكِّيهِمْ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (182, N'أُولئِكَ الَّذِينَ اشْتَرَوُا الضَّلالَةَ بِالْهُدى وَ الْعَذابَ بِالْمَغْفِرَةِ فَما أَصْبَرَهُمْ عَلَى النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (183, N'ذلِكَ بِأَنَّ اللَّهَ نَزَّلَ الْكِتابَ بِالْحَقِّ وَ إِنَّ الَّذِينَ اخْتَلَفُوا فِي الْكِتابِ لَفِي شِقاقٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (184, N'لَيْسَ الْبِرَّ أَنْ تُوَلُّوا وُجُوهَكُمْ قِبَلَ الْمَشْرِقِ وَ الْمَغْرِبِ وَ لكِنَّ الْبِرَّ مَنْ آمَنَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ الْمَلائِكَةِ وَ الْكِتابِ وَ النَّبِيِّينَ وَ آتَى الْمالَ عَلى حُبِّهِ ذَوِي الْقُرْبى وَ الْيَتامى وَ الْمَساكِينَ وَ ابْنَ السَّبِيلِ وَ السَّائِلِينَ وَ فِي الرِّقابِ وَ أَقامَ الصَّلاةَ وَ آتَى الزَّكاةَ وَ الْمُوفُونَ بِعَهْدِهِمْ إِذا عاهَدُوا وَ الصَّابِرِينَ فِي الْبَأْساءِ وَ الضَّرَّاءِ وَ حِينَ الْبَأْسِ أُولئِكَ الَّذِينَ صَدَقُوا وَ أُولئِكَ هُمُ الْمُتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (185, N'يا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الْقِصاصُ فِي الْقَتْلى الْحُرُّ بِالْحُرِّ وَ الْعَبْدُ بِالْعَبْدِ وَ الْأُنْثى بِالْأُنْثى فَمَنْ عُفِيَ لَهُ مِنْ أَخِيهِ شَيْ‏ءٌ فَاتِّباعٌ بِالْمَعْرُوفِ وَ أَداءٌ إِلَيْهِ بِإِحْسانٍ ذلِكَ تَخْفِيفٌ مِنْ رَبِّكُمْ وَ رَحْمَةٌ فَمَنِ اعْتَدى بَعْدَ ذلِكَ فَلَهُ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (186, N'وَ لَكُمْ فِي الْقِصاصِ حَياةٌ يا أُولِي الْأَلْبابِ لَعَلَّكُمْ تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (187, N'كُتِبَ عَلَيْكُمْ إِذا حَضَرَ أَحَدَكُمُ الْمَوْتُ إِنْ تَرَكَ خَيْراً الْوَصِيَّةُ لِلْوالِدَيْنِ وَ الْأَقْرَبِينَ بِالْمَعْرُوفِ حَقًّا عَلَى الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (188, N'فَمَنْ بَدَّلَهُ بَعْدَ ما سَمِعَهُ فَإِنَّما إِثْمُهُ عَلَى الَّذِينَ يُبَدِّلُونَهُ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (189, N'فَمَنْ خافَ مِنْ مُوصٍ جَنَفاً أَوْ إِثْماً فَأَصْلَحَ بَيْنَهُمْ فَلا إِثْمَ عَلَيْهِ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (190, N'يا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الصِّيامُ كَما كُتِبَ عَلَى الَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (191, N'أَيَّاماً مَعْدُوداتٍ فَمَنْ كانَ مِنْكُمْ مَرِيضاً أَوْ عَلى سَفَرٍ فَعِدَّةٌ مِنْ أَيَّامٍ أُخَرَ وَ عَلَى الَّذِينَ يُطِيقُونَهُ فِدْيَةٌ طَعامُ مِسْكِينٍ فَمَنْ تَطَوَّعَ خَيْراً فَهُوَ خَيْرٌ لَهُ وَ أَنْ تَصُومُوا خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (192, N'شَهْرُ رَمَضانَ الَّذِي أُنْزِلَ فِيهِ الْقُرْآنُ هُدىً لِلنَّاسِ وَ بَيِّناتٍ مِنَ الْهُدى وَ الْفُرْقانِ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ وَ مَنْ كانَ مَرِيضاً أَوْ عَلى سَفَرٍ فَعِدَّةٌ مِنْ أَيَّامٍ أُخَرَ يُرِيدُ اللَّهُ بِكُمُ الْيُسْرَ وَ لا يُرِيدُ بِكُمُ الْعُسْرَ وَ لِتُكْمِلُوا الْعِدَّةَ وَ لِتُكَبِّرُوا اللَّهَ عَلى ما هَداكُمْ وَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (193, N'وَ إِذا سَأَلَكَ عِبادِي عَنِّي فَإِنِّي قَرِيبٌ أُجِيبُ دَعْوَةَ الدَّاعِ إِذا دَعانِ فَلْيَسْتَجِيبُوا لِي وَ لْيُؤْمِنُوا بِي لَعَلَّهُمْ يَرْشُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (194, N'أُحِلَّ لَكُمْ لَيْلَةَ الصِّيامِ الرَّفَثُ إِلى نِسائِكُمْ هُنَّ لِباسٌ لَكُمْ وَ أَنْتُمْ لِباسٌ لَهُنَّ عَلِمَ اللَّهُ أَنَّكُمْ كُنْتُمْ تَخْتانُونَ أَنْفُسَكُمْ فَتابَ عَلَيْكُمْ وَ عَفا عَنْكُمْ فَالْآنَ بَاشِرُوهُنَّ وَ ابْتَغُوا ما كَتَبَ اللَّهُ لَكُمْ وَ كُلُوا وَ اشْرَبُوا حَتَّى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْأَبْيَضُ مِنَ الْخَيْطِ الْأَسْوَدِ مِنَ الْفَجْرِ ثُمَّ أَتِمُّوا الصِّيامَ إِلَى اللَّيْلِ وَ لا تُبَاشِرُوهُنَّ وَ أَنْتُمْ عاكِفُونَ فِي الْمَساجِدِ تِلْكَ حُدُودُ اللَّهِ فَلا تَقْرَبُوها كَذلِكَ يُبَيِّنُ اللَّهُ آياتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (195, N'وَ لا تَأْكُلُوا أَمْوالَكُمْ بَيْنَكُمْ بِالْباطِلِ وَ تُدْلُوا بِها إِلَى الْحُكَّامِ لِتَأْكُلُوا فَرِيقاً مِنْ أَمْوالِ النَّاسِ بِالْإِثْمِ وَ أَنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (196, N'يَسْئَلُونَكَ عَنِ الْأَهِلَّةِ قُلْ هِيَ مَواقِيتُ لِلنَّاسِ وَ الْحَجِّ وَ لَيْسَ الْبِرُّ بِأَنْ تَأْتُوا الْبُيُوتَ مِنْ ظُهُورِها وَ لكِنَّ الْبِرَّ مَنِ اتَّقى وَ أْتُوا الْبُيُوتَ مِنْ أَبْوابِها وَ اتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (197, N'وَ قاتِلُوا فِي سَبِيلِ اللَّهِ الَّذِينَ يُقاتِلُونَكُمْ وَ لا تَعْتَدُوا إِنَّ اللَّهَ لا يُحِبُّ الْمُعْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (198, N'وَ اقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَ أَخْرِجُوهُمْ مِنْ حَيْثُ أَخْرَجُوكُمْ وَ الْفِتْنَةُ أَشَدُّ مِنَ الْقَتْلِ وَ لا تُقاتِلُوهُمْ عِنْدَ الْمَسْجِدِ الْحَرامِ حَتَّى يُقاتِلُوكُمْ فِيهِ فَإِنْ قاتَلُوكُمْ فَاقْتُلُوهُمْ كَذلِكَ جَزاءُ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (199, N'فَإِنِ انْتَهَوْا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (200, N'وَ قاتِلُوهُمْ حَتَّى لا تَكُونَ فِتْنَةٌ وَ يَكُونَ الدِّينُ لِلَّهِ فَإِنِ انْتَهَوْا فَلا عُدْوانَ إِلاَّ عَلَى الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (201, N'الشَّهْرُ الْحَرامُ بِالشَّهْرِ الْحَرامِ وَ الْحُرُماتُ قِصاصٌ فَمَنِ اعْتَدى عَلَيْكُمْ فَاعْتَدُوا عَلَيْهِ بِمِثْلِ مَا اعْتَدى عَلَيْكُمْ وَ اتَّقُوا اللَّهَ وَ اعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (202, N'وَ أَنْفِقُوا فِي سَبِيلِ اللَّهِ وَ لا تُلْقُوا بِأَيْدِيكُمْ إِلَى التَّهْلُكَةِ وَ أَحْسِنُوا إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (203, N'وَ أَتِمُّوا الْحَجَّ وَ الْعُمْرَةَ لِلَّهِ فَإِنْ أُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ وَ لا تَحْلِقُوا رُؤُسَكُمْ حَتَّى يَبْلُغَ الْهَدْيُ مَحِلَّهُ فَمَنْ كانَ مِنْكُمْ مَرِيضاً أَوْ بِهِ أَذىً مِنْ رَأْسِهِ فَفِدْيَةٌ مِنْ صِيامٍ أَوْ صَدَقَةٍ أَوْ نُسُكٍ فَإِذا أَمِنْتُمْ فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ إِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ فَمَنْ لَمْ يَجِدْ فَصِيامُ ثَلاثَةِ أَيَّامٍ فِي الْحَجِّ وَ سَبْعَةٍ إِذا رَجَعْتُمْ تِلْكَ عَشَرَةٌ كامِلَةٌ ذلِكَ لِمَنْ لَمْ يَكُنْ أَهْلُهُ حاضِرِي الْمَسْجِدِ الْحَرامِ وَ اتَّقُوا اللَّهَ وَ اعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (204, N'الْحَجُّ أَشْهُرٌ مَعْلُوماتٌ فَمَنْ فَرَضَ فِيهِنَّ الْحَجَّ فَلا رَفَثَ وَ لا فُسُوقَ وَ لا جِدالَ فِي الْحَجِّ وَ ما تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللَّهُ وَ تَزَوَّدُوا فَإِنَّ خَيْرَ الزَّادِ التَّقْوى وَ اتَّقُونِ يا أُولِي الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (205, N'لَيْسَ عَلَيْكُمْ جُناحٌ أَنْ تَبْتَغُوا فَضْلاً مِنْ رَبِّكُمْ فَإِذا أَفَضْتُمْ مِنْ عَرَفاتٍ فَاذْكُرُوا اللَّهَ عِنْدَ الْمَشْعَرِ الْحَرامِ وَ اذْكُرُوهُ كَما هَداكُمْ وَ إِنْ كُنْتُمْ مِنْ قَبْلِهِ لَمِنَ الضَّالِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (206, N'ثُمَّ أَفِيضُوا مِنْ حَيْثُ أَفاضَ النَّاسُ وَ اسْتَغْفِرُوا اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (207, N'فَإِذا قَضَيْتُمْ مَناسِكَكُمْ فَاذْكُرُوا اللَّهَ كَذِكْرِكُمْ آباءَكُمْ أَوْ أَشَدَّ ذِكْراً فَمِنَ النَّاسِ مَنْ يَقُولُ رَبَّنا آتِنا فِي الدُّنْيا وَ ما لَهُ فِي الْآخِرَةِ مِنْ خَلاقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (208, N'وَ مِنْهُمْ مَنْ يَقُولُ رَبَّنا آتِنا فِي الدُّنْيا حَسَنَةً وَ فِي الْآخِرَةِ حَسَنَةً وَ قِنا عَذابَ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (209, N'أُولئِكَ لَهُمْ نَصِيبٌ مِمَّا كَسَبُوا وَ اللَّهُ سَرِيعُ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (210, N'وَ اذْكُرُوا اللَّهَ فِي أَيَّامٍ مَعْدُوداتٍ فَمَنْ تَعَجَّلَ فِي يَوْمَيْنِ فَلا إِثْمَ عَلَيْهِ وَ مَنْ تَأَخَّرَ فَلا إِثْمَ عَلَيْهِ لِمَنِ اتَّقى وَ اتَّقُوا اللَّهَ وَ اعْلَمُوا أَنَّكُمْ إِلَيْهِ تُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (211, N'وَ مِنَ النَّاسِ مَنْ يُعْجِبُكَ قَوْلُهُ فِي الْحَياةِ الدُّنْيا وَ يُشْهِدُ اللَّهَ عَلى ما فِي قَلْبِهِ وَ هُوَ أَلَدُّ الْخِصامِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (212, N'وَ إِذا تَوَلَّى سَعى فِي الْأَرْضِ لِيُفْسِدَ فِيها وَ يُهْلِكَ الْحَرْثَ وَ النَّسْلَ وَ اللَّهُ لا يُحِبُّ الْفَسادَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (213, N'وَ إِذا قِيلَ لَهُ اتَّقِ اللَّهَ أَخَذَتْهُ الْعِزَّةُ بِالْإِثْمِ فَحَسْبُهُ جَهَنَّمُ وَ لَبِئْسَ الْمِهادُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (214, N'وَ مِنَ النَّاسِ مَنْ يَشْرِي نَفْسَهُ ابْتِغاءَ مَرْضاتِ اللَّهِ وَ اللَّهُ رَؤُفٌ بِالْعِبادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (215, N'يا أَيُّهَا الَّذِينَ آمَنُوا ادْخُلُوا فِي السِّلْمِ كَافَّةً وَ لا تَتَّبِعُوا خُطُواتِ الشَّيْطانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (216, N'فَإِنْ زَلَلْتُمْ مِنْ بَعْدِ ما جاءَتْكُمُ الْبَيِّناتُ فَاعْلَمُوا أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (217, N'هَلْ يَنْظُرُونَ إِلاَّ أَنْ يَأْتِيَهُمُ اللَّهُ فِي ظُلَلٍ مِنَ الْغَمامِ وَ الْمَلائِكَةُ وَ قُضِيَ الْأَمْرُ وَ إِلَى اللَّهِ تُرْجَعُ الْأُمُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (218, N'سَلْ بَنِي إِسْرائِيلَ كَمْ آتَيْناهُمْ مِنْ آيَةٍ بَيِّنَةٍ وَ مَنْ يُبَدِّلْ نِعْمَةَ اللَّهِ مِنْ بَعْدِ ما جاءَتْهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (219, N'زُيِّنَ لِلَّذِينَ كَفَرُوا الْحَياةُ الدُّنْيا وَ يَسْخَرُونَ مِنَ الَّذِينَ آمَنُوا وَ الَّذِينَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيامَةِ وَ اللَّهُ يَرْزُقُ مَنْ يَشاءُ بِغَيْرِ حِسابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (220, N'كانَ النَّاسُ أُمَّةً واحِدَةً فَبَعَثَ اللَّهُ النَّبِيِّينَ مُبَشِّرِينَ وَ مُنْذِرِينَ وَ أَنْزَلَ مَعَهُمُ الْكِتابَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيمَا اخْتَلَفُوا فِيهِ وَ مَا اخْتَلَفَ فِيهِ إِلاَّ الَّذِينَ أُوتُوهُ مِنْ بَعْدِ ما جاءَتْهُمُ الْبَيِّناتُ بَغْياً بَيْنَهُمْ فَهَدَى اللَّهُ الَّذِينَ آمَنُوا لِمَا اخْتَلَفُوا فِيهِ مِنَ الْحَقِّ بِإِذْنِهِ وَ اللَّهُ يَهْدِي مَنْ يَشاءُ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (221, N'أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَ لَمَّا يَأْتِكُمْ مَثَلُ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ مَسَّتْهُمُ الْبَأْساءُ وَ الضَّرَّاءُ وَ زُلْزِلُوا حَتَّى يَقُولَ الرَّسُولُ وَ الَّذِينَ آمَنُوا مَعَهُ مَتى نَصْرُ اللَّهِ أَلا إِنَّ نَصْرَ اللَّهِ قَرِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (222, N'يَسْئَلُونَكَ ما ذا يُنْفِقُونَ قُلْ ما أَنْفَقْتُمْ مِنْ خَيْرٍ فَلِلْوالِدَيْنِ وَ الْأَقْرَبِينَ وَ الْيَتامى وَ الْمَساكِينِ وَ ابْنِ السَّبِيلِ وَ ما تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (223, N'كُتِبَ عَلَيْكُمُ الْقِتالُ وَ هُوَ كُرْهٌ لَكُمْ وَ عَسى أَنْ تَكْرَهُوا شَيْئاً وَ هُوَ خَيْرٌ لَكُمْ وَ عَسى أَنْ تُحِبُّوا شَيْئاً وَ هُوَ شَرٌّ لَكُمْ وَ اللَّهُ يَعْلَمُ وَ أَنْتُمْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (224, N'يَسْئَلُونَكَ عَنِ الشَّهْرِ الْحَرامِ قِتالٍ فِيهِ قُلْ قِتالٌ فِيهِ كَبِيرٌ وَ صَدٌّ عَنْ سَبِيلِ اللَّهِ وَ كُفْرٌ بِهِ وَ الْمَسْجِدِ الْحَرامِ وَ إِخْراجُ أَهْلِهِ مِنْهُ أَكْبَرُ عِنْدَ اللَّهِ وَ الْفِتْنَةُ أَكْبَرُ مِنَ الْقَتْلِ وَ لا يَزالُونَ يُقاتِلُونَكُمْ حَتَّى يَرُدُّوكُمْ عَنْ دِينِكُمْ إِنِ اسْتَطاعُوا وَ مَنْ يَرْتَدِدْ مِنْكُمْ عَنْ دِينِهِ فَيَمُتْ وَ هُوَ كافِرٌ فَأُولئِكَ حَبِطَتْ أَعْمالُهُمْ فِي الدُّنْيا وَ الْآخِرَةِ وَ أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (225, N'إِنَّ الَّذِينَ آمَنُوا وَ الَّذِينَ هاجَرُوا وَ جاهَدُوا فِي سَبِيلِ اللَّهِ أُولئِكَ يَرْجُونَ رَحْمَتَ اللَّهِ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (226, N'يَسْئَلُونَكَ عَنِ الْخَمْرِ وَ الْمَيْسِرِ قُلْ فِيهِما إِثْمٌ كَبِيرٌ وَ مَنافِعُ لِلنَّاسِ وَ إِثْمُهُما أَكْبَرُ مِنْ نَفْعِهِما وَ يَسْئَلُونَكَ ما ذا يُنْفِقُونَ قُلِ الْعَفْوَ كَذلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآياتِ لَعَلَّكُمْ تَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (227, N'فِي الدُّنْيا وَ الْآخِرَةِ وَ يَسْئَلُونَكَ عَنِ الْيَتامى قُلْ إِصْلاحٌ لَهُمْ خَيْرٌ وَ إِنْ تُخالِطُوهُمْ فَإِخْوانُكُمْ وَ اللَّهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ وَ لَوْ شاءَ اللَّهُ لَأَعْنَتَكُمْ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (228, N'وَ لا تَنْكِحُوا الْمُشْرِكاتِ حَتَّى يُؤْمِنَّ وَ لَأَمَةٌ مُؤْمِنَةٌ خَيْرٌ مِنْ مُشْرِكَةٍ وَ لَوْ أَعْجَبَتْكُمْ وَ لا تُنْكِحُوا الْمُشْرِكِينَ حَتَّى يُؤْمِنُوا وَ لَعَبْدٌ مُؤْمِنٌ خَيْرٌ مِنْ مُشْرِكٍ وَ لَوْ أَعْجَبَكُمْ أُولئِكَ يَدْعُونَ إِلَى النَّارِ وَ اللَّهُ يَدْعُوا إِلَى الْجَنَّةِ وَ الْمَغْفِرَةِ بِإِذْنِهِ وَ يُبَيِّنُ آياتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (229, N'وَ يَسْئَلُونَكَ عَنِ الْمَحِيضِ قُلْ هُوَ أَذىً فَاعْتَزِلُوا النِّساءَ فِي الْمَحِيضِ وَ لا تَقْرَبُوهُنَّ حَتَّى يَطْهُرْنَ فَإِذا تَطَهَّرْنَ فَأْتُوهُنَّ مِنْ حَيْثُ أَمَرَكُمُ اللَّهُ إِنَّ اللَّهَ يُحِبُّ التَّوَّابِينَ وَ يُحِبُّ الْمُتَطَهِّرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (230, N'نِساؤُكُمْ حَرْثٌ لَكُمْ فَأْتُوا حَرْثَكُمْ أَنَّى شِئْتُمْ وَ قَدِّمُوا لِأَنْفُسِكُمْ وَ اتَّقُوا اللَّهَ وَ اعْلَمُوا أَنَّكُمْ مُلاقُوهُ وَ بَشِّرِ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (231, N'وَ لا تَجْعَلُوا اللَّهَ عُرْضَةً لِأَيْمانِكُمْ أَنْ تَبَرُّوا وَ تَتَّقُوا وَ تُصْلِحُوا بَيْنَ النَّاسِ وَ اللَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (232, N'لا يُؤاخِذُكُمُ اللَّهُ بِاللَّغْوِ فِي أَيْمانِكُمْ وَ لكِنْ يُؤاخِذُكُمْ بِما كَسَبَتْ قُلُوبُكُمْ وَ اللَّهُ غَفُورٌ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (233, N'لِلَّذِينَ يُؤْلُونَ مِنْ نِسائِهِمْ تَرَبُّصُ أَرْبَعَةِ أَشْهُرٍ فَإِنْ فاؤُ فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (234, N'وَ إِنْ عَزَمُوا الطَّلاقَ فَإِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (235, N'وَ الْمُطَلَّقاتُ يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ ثَلاثَةَ قُرُوءٍ وَ لا يَحِلُّ لَهُنَّ أَنْ يَكْتُمْنَ ما خَلَقَ اللَّهُ فِي أَرْحامِهِنَّ إِنْ كُنَّ يُؤْمِنَّ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ بُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذلِكَ إِنْ أَرادُوا إِصْلاحاً وَ لَهُنَّ مِثْلُ الَّذِي عَلَيْهِنَّ بِالْمَعْرُوفِ وَ لِلرِّجالِ عَلَيْهِنَّ دَرَجَةٌ وَ اللَّهُ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (236, N'الطَّلاقُ مَرَّتانِ فَإِمْساكٌ بِمَعْرُوفٍ أَوْ تَسْرِيحٌ بِإِحْسانٍ وَ لا يَحِلُّ لَكُمْ أَنْ تَأْخُذُوا مِمَّا آتَيْتُمُوهُنَّ شَيْئاً إِلاَّ أَنْ يَخافا أَلاَّ يُقِيما حُدُودَ اللَّهِ فَإِنْ خِفْتُمْ أَلاَّ يُقِيما حُدُودَ اللَّهِ فَلا جُناحَ عَلَيْهِما فِيمَا افْتَدَتْ بِهِ تِلْكَ حُدُودُ اللَّهِ فَلا تَعْتَدُوها وَ مَنْ يَتَعَدَّ حُدُودَ اللَّهِ فَأُولئِكَ هُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (237, N'فَإِنْ طَلَّقَها فَلا تَحِلُّ لَهُ مِنْ بَعْدُ حَتَّى تَنْكِحَ زَوْجاً غَيْرَهُ فَإِنْ طَلَّقَها فَلا جُناحَ عَلَيْهِما أَنْ يَتَراجَعا إِنْ ظَنَّا أَنْ يُقِيما حُدُودَ اللَّهِ وَ تِلْكَ حُدُودُ اللَّهِ يُبَيِّنُها لِقَوْمٍ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (238, N'وَ إِذا طَلَّقْتُمُ النِّساءَ فَبَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ سَرِّحُوهُنَّ بِمَعْرُوفٍ وَ لا تُمْسِكُوهُنَّ ضِراراً لِتَعْتَدُوا وَ مَنْ يَفْعَلْ ذلِكَ فَقَدْ ظَلَمَ نَفْسَهُ وَ لا تَتَّخِذُوا آياتِ اللَّهِ هُزُواً وَ اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ وَ ما أَنْزَلَ عَلَيْكُمْ مِنَ الْكِتابِ وَ الْحِكْمَةِ يَعِظُكُمْ بِهِ وَ اتَّقُوا اللَّهَ وَ اعْلَمُوا أَنَّ اللَّهَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (239, N'وَ إِذا طَلَّقْتُمُ النِّساءَ فَبَلَغْنَ أَجَلَهُنَّ فَلا تَعْضُلُوهُنَّ أَنْ يَنْكِحْنَ أَزْواجَهُنَّ إِذا تَراضَوْا بَيْنَهُمْ بِالْمَعْرُوفِ ذلِكَ يُوعَظُ بِهِ مَنْ كانَ مِنْكُمْ يُؤْمِنُ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ ذلِكُمْ أَزْكى لَكُمْ وَ أَطْهَرُ وَ اللَّهُ يَعْلَمُ وَ أَنْتُمْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (240, N'وَ الْوالِداتُ يُرْضِعْنَ أَوْلادَهُنَّ حَوْلَيْنِ كامِلَيْنِ لِمَنْ أَرادَ أَنْ يُتِمَّ الرَّضاعَةَ وَ عَلَى الْمَوْلُودِ لَهُ رِزْقُهُنَّ وَ كِسْوَتُهُنَّ بِالْمَعْرُوفِ لا تُكَلَّفُ نَفْسٌ إِلاَّ وُسْعَها لا تُضَارَّ والِدَةٌ بِوَلَدِها وَ لا مَوْلُودٌ لَهُ بِوَلَدِهِ وَ عَلَى الْوارِثِ مِثْلُ ذلِكَ فَإِنْ أَرادا فِصالاً عَنْ تَراضٍ مِنْهُما وَ تَشاوُرٍ فَلا جُناحَ عَلَيْهِما وَ إِنْ أَرَدْتُمْ أَنْ تَسْتَرْضِعُوا أَوْلادَكُمْ فَلا جُناحَ عَلَيْكُمْ إِذا سَلَّمْتُمْ ما آتَيْتُمْ بِالْمَعْرُوفِ وَ اتَّقُوا اللَّهَ وَ اعْلَمُوا أَنَّ اللَّهَ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (241, N'وَ الَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَ يَذَرُونَ أَزْواجاً يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ أَرْبَعَةَ أَشْهُرٍ وَ عَشْراً فَإِذا بَلَغْنَ أَجَلَهُنَّ فَلا جُناحَ عَلَيْكُمْ فِيما فَعَلْنَ فِي أَنْفُسِهِنَّ بِالْمَعْرُوفِ وَ اللَّهُ بِما تَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (242, N'وَ لا جُناحَ عَلَيْكُمْ فِيما عَرَّضْتُمْ بِهِ مِنْ خِطْبَةِ النِّساءِ أَوْ أَكْنَنْتُمْ فِي أَنْفُسِكُمْ عَلِمَ اللَّهُ أَنَّكُمْ سَتَذْكُرُونَهُنَّ وَ لكِنْ لا تُواعِدُوهُنَّ سِرًّا إِلاَّ أَنْ تَقُولُوا قَوْلاً مَعْرُوفاً وَ لا تَعْزِمُوا عُقْدَةَ النِّكاحِ حَتَّى يَبْلُغَ الْكِتابُ أَجَلَهُ وَ اعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ ما فِي أَنْفُسِكُمْ فَاحْذَرُوهُ وَ اعْلَمُوا أَنَّ اللَّهَ غَفُورٌ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (243, N'لا جُناحَ عَلَيْكُمْ إِنْ طَلَّقْتُمُ النِّساءَ ما لَمْ تَمَسُّوهُنَّ أَوْ تَفْرِضُوا لَهُنَّ فَرِيضَةً وَ مَتِّعُوهُنَّ عَلَى الْمُوسِعِ قَدَرُهُ وَ عَلَى الْمُقْتِرِ قَدَرُهُ مَتاعاً بِالْمَعْرُوفِ حَقًّا عَلَى الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (244, N'وَ إِنْ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ أَنْ تَمَسُّوهُنَّ وَ قَدْ فَرَضْتُمْ لَهُنَّ فَرِيضَةً فَنِصْفُ ما فَرَضْتُمْ إِلاَّ أَنْ يَعْفُونَ أَوْ يَعْفُوَا الَّذِي بِيَدِهِ عُقْدَةُ النِّكاحِ وَ أَنْ تَعْفُوا أَقْرَبُ لِلتَّقْوى وَ لا تَنْسَوُا الْفَضْلَ بَيْنَكُمْ إِنَّ اللَّهَ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (245, N'حافِظُوا عَلَى الصَّلَواتِ وَ الصَّلاةِ الْوُسْطى وَ قُومُوا لِلَّهِ قانِتِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (246, N'فَإِنْ خِفْتُمْ فَرِجالاً أَوْ رُكْباناً فَإِذا أَمِنْتُمْ فَاذْكُرُوا اللَّهَ كَما عَلَّمَكُمْ ما لَمْ تَكُونُوا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (247, N'وَ الَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَ يَذَرُونَ أَزْواجاً وَصِيَّةً لِأَزْواجِهِمْ مَتاعاً إِلَى الْحَوْلِ غَيْرَ إِخْراجٍ فَإِنْ خَرَجْنَ فَلا جُناحَ عَلَيْكُمْ فِي ما فَعَلْنَ فِي أَنْفُسِهِنَّ مِنْ مَعْرُوفٍ وَ اللَّهُ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (248, N'وَ لِلْمُطَلَّقاتِ مَتاعٌ بِالْمَعْرُوفِ حَقًّا عَلَى الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (249, N'كَذلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آياتِهِ لَعَلَّكُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (250, N'أَ لَمْ تَرَ إِلَى الَّذِينَ خَرَجُوا مِنْ دِيارِهِمْ وَ هُمْ أُلُوفٌ حَذَرَ الْمَوْتِ فَقالَ لَهُمُ اللَّهُ مُوتُوا ثُمَّ أَحْياهُمْ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (251, N'وَ قاتِلُوا فِي سَبِيلِ اللَّهِ وَ اعْلَمُوا أَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (252, N'مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضاً حَسَناً فَيُضاعِفَهُ لَهُ أَضْعافاً كَثِيرَةً وَ اللَّهُ يَقْبِضُ وَ يَبْصُطُ وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (253, N'أَ لَمْ تَرَ إِلَى الْمَلَإِ مِنْ بَنِي إِسْرائِيلَ مِنْ بَعْدِ مُوسى إِذْ قالُوا لِنَبِيٍّ لَهُمُ ابْعَثْ لَنا مَلِكاً نُقاتِلْ فِي سَبِيلِ اللَّهِ قالَ هَلْ عَسَيْتُمْ إِنْ كُتِبَ عَلَيْكُمُ الْقِتالُ أَلاَّ تُقاتِلُوا قالُوا وَ ما لَنا أَلاَّ نُقاتِلَ فِي سَبِيلِ اللَّهِ وَ قَدْ أُخْرِجْنا مِنْ دِيارِنا وَ أَبْنائِنا فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتالُ تَوَلَّوْا إِلاَّ قَلِيلاً مِنْهُمْ وَ اللَّهُ عَلِيمٌ بِالظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (254, N'وَ قالَ لَهُمْ نَبِيُّهُمْ إِنَّ اللَّهَ قَدْ بَعَثَ لَكُمْ طالُوتَ مَلِكاً قالُوا أَنَّى يَكُونُ لَهُ الْمُلْكُ عَلَيْنا وَ نَحْنُ أَحَقُّ بِالْمُلْكِ مِنْهُ وَ لَمْ يُؤْتَ سَعَةً مِنَ الْمالِ قالَ إِنَّ اللَّهَ اصْطَفاهُ عَلَيْكُمْ وَ زادَهُ بَسْطَةً فِي الْعِلْمِ وَ الْجِسْمِ وَ اللَّهُ يُؤْتِي مُلْكَهُ مَنْ يَشاءُ وَ اللَّهُ واسِعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (255, N'وَ قالَ لَهُمْ نَبِيُّهُمْ إِنَّ آيَةَ مُلْكِهِ أَنْ يَأْتِيَكُمُ التَّابُوتُ فِيهِ سَكِينَةٌ مِنْ رَبِّكُمْ وَ بَقِيَّةٌ مِمَّا تَرَكَ آلُ مُوسى وَ آلُ هارُونَ تَحْمِلُهُ الْمَلائِكَةُ إِنَّ فِي ذلِكَ لَآيَةً لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (256, N'فَلَمَّا فَصَلَ طالُوتُ بِالْجُنُودِ قالَ إِنَّ اللَّهَ مُبْتَلِيكُمْ بِنَهَرٍ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنِّي وَ مَنْ لَمْ يَطْعَمْهُ فَإِنَّهُ مِنِّي إِلاَّ مَنِ اغْتَرَفَ غُرْفَةً بِيَدِهِ فَشَرِبُوا مِنْهُ إِلاَّ قَلِيلاً مِنْهُمْ فَلَمَّا جاوَزَهُ هُوَ وَ الَّذِينَ آمَنُوا مَعَهُ قالُوا لا طاقَةَ لَنَا الْيَوْمَ بِجالُوتَ وَ جُنُودِهِ قالَ الَّذِينَ يَظُنُّونَ أَنَّهُمْ مُلاقُوا اللَّهِ كَمْ مِنْ فِئَةٍ قَلِيلَةٍ غَلَبَتْ فِئَةً كَثِيرَةً بِإِذْنِ اللَّهِ وَ اللَّهُ مَعَ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (257, N'وَ لَمَّا بَرَزُوا لِجالُوتَ وَ جُنُودِهِ قالُوا رَبَّنا أَفْرِغْ عَلَيْنا صَبْراً وَ ثَبِّتْ أَقْدامَنا وَ انْصُرْنا عَلَى الْقَوْمِ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (258, N'فَهَزَمُوهُمْ بِإِذْنِ اللَّهِ وَ قَتَلَ داوُدُ جالُوتَ وَ آتاهُ اللَّهُ الْمُلْكَ وَ الْحِكْمَةَ وَ عَلَّمَهُ مِمَّا يَشاءُ وَ لَوْ لا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَفَسَدَتِ الْأَرْضُ وَ لكِنَّ اللَّهَ ذُو فَضْلٍ عَلَى الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (259, N'تِلْكَ آياتُ اللَّهِ نَتْلُوها عَلَيْكَ بِالْحَقِّ وَ إِنَّكَ لَمِنَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (260, N'تِلْكَ الرُّسُلُ فَضَّلْنا بَعْضَهُمْ عَلى بَعْضٍ مِنْهُمْ مَنْ كَلَّمَ اللَّهُ وَ رَفَعَ بَعْضَهُمْ دَرَجاتٍ وَ آتَيْنا عِيسَى ابْنَ مَرْيَمَ الْبَيِّناتِ وَ أَيَّدْناهُ بِرُوحِ الْقُدُسِ وَ لَوْ شاءَ اللَّهُ مَا اقْتَتَلَ الَّذِينَ مِنْ بَعْدِهِمْ مِنْ بَعْدِ ما جاءَتْهُمُ الْبَيِّناتُ وَ لكِنِ اخْتَلَفُوا فَمِنْهُمْ مَنْ آمَنَ وَ مِنْهُمْ مَنْ كَفَرَ وَ لَوْ شاءَ اللَّهُ مَا اقْتَتَلُوا وَ لكِنَّ اللَّهَ يَفْعَلُ ما يُرِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (261, N'يا أَيُّهَا الَّذِينَ آمَنُوا أَنْفِقُوا مِمَّا رَزَقْناكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا بَيْعٌ فِيهِ وَ لا خُلَّةٌ وَ لا شَفاعَةٌ وَ الْكافِرُونَ هُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (262, N'اللَّهُ لا إِلهَ إِلاَّ هُوَ الْحَيُّ الْقَيُّومُ لا تَأْخُذُهُ سِنَةٌ وَ لا نَوْمٌ لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ مَنْ ذَا الَّذِي يَشْفَعُ عِنْدَهُ إِلاَّ بِإِذْنِهِ يَعْلَمُ ما بَيْنَ أَيْدِيهِمْ وَ ما خَلْفَهُمْ وَ لا يُحِيطُونَ بِشَيْ‏ءٍ مِنْ عِلْمِهِ إِلاَّ بِما شاءَ وَسِعَ كُرْسِيُّهُ السَّماواتِ وَ الْأَرْضَ وَ لا يَؤُدُهُ حِفْظُهُما وَ هُوَ الْعَلِيُّ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (263, N'لا إِكْراهَ فِي الدِّينِ قَدْ تَبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ فَمَنْ يَكْفُرْ بِالطَّاغُوتِ وَ يُؤْمِنْ بِاللَّهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقى لاَ انْفِصامَ لَها وَ اللَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (264, N'اللَّهُ وَلِيُّ الَّذِينَ آمَنُوا يُخْرِجُهُمْ مِنَ الظُّلُماتِ إِلَى النُّورِ وَ الَّذِينَ كَفَرُوا أَوْلِياؤُهُمُ الطَّاغُوتُ يُخْرِجُونَهُمْ مِنَ النُّورِ إِلَى الظُّلُماتِ أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (265, N'أَ لَمْ تَرَ إِلَى الَّذِي حَاجَّ إِبْراهِيمَ فِي رَبِّهِ أَنْ آتاهُ اللَّهُ الْمُلْكَ إِذْ قالَ إِبْراهِيمُ رَبِّيَ الَّذِي يُحْيِي وَ يُمِيتُ قالَ أَنَا أُحْيِي وَ أُمِيتُ قالَ إِبْراهِيمُ فَإِنَّ اللَّهَ يَأْتِي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِها مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِي كَفَرَ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (266, N'أَوْ كَالَّذِي مَرَّ عَلى قَرْيَةٍ وَ هِيَ خاوِيَةٌ عَلى عُرُوشِها قالَ أَنَّى يُحْيِي هذِهِ اللَّهُ بَعْدَ مَوْتِها فَأَماتَهُ اللَّهُ مِائَةَ عامٍ ثُمَّ بَعَثَهُ قالَ كَمْ لَبِثْتَ قالَ لَبِثْتُ يَوْماً أَوْ بَعْضَ يَوْمٍ قالَ بَلْ لَبِثْتَ مِائَةَ عامٍ فَانْظُرْ إِلى طَعامِكَ وَ شَرابِكَ لَمْ يَتَسَنَّهْ وَ انْظُرْ إِلى حِمارِكَ وَ لِنَجْعَلَكَ آيَةً لِلنَّاسِ وَ انْظُرْ إِلَى الْعِظامِ كَيْفَ نُنْشِزُها ثُمَّ نَكْسُوها لَحْماً فَلَمَّا تَبَيَّنَ لَهُ قالَ أَعْلَمُ أَنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (267, N'وَ إِذْ قالَ إِبْراهِيمُ رَبِّ أَرِنِي كَيْفَ تُحْيِ الْمَوْتى قالَ أَ وَ لَمْ تُؤْمِنْ قالَ بَلى وَ لكِنْ لِيَطْمَئِنَّ قَلْبِي قالَ فَخُذْ أَرْبَعَةً مِنَ الطَّيْرِ فَصُرْهُنَّ إِلَيْكَ ثُمَّ اجْعَلْ عَلى كُلِّ جَبَلٍ مِنْهُنَّ جُزْءاً ثُمَّ ادْعُهُنَّ يَأْتِينَكَ سَعْياً وَ اعْلَمْ أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (268, N'مَثَلُ الَّذِينَ يُنْفِقُونَ أَمْوالَهُمْ فِي سَبِيلِ اللَّهِ كَمَثَلِ حَبَّةٍ أَنْبَتَتْ سَبْعَ سَنابِلَ فِي كُلِّ سُنْبُلَةٍ مِائَةُ حَبَّةٍ وَ اللَّهُ يُضاعِفُ لِمَنْ يَشاءُ وَ اللَّهُ واسِعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (269, N'الَّذِينَ يُنْفِقُونَ أَمْوالَهُمْ فِي سَبِيلِ اللَّهِ ثُمَّ لا يُتْبِعُونَ ما أَنْفَقُوا مَنًّا وَ لا أَذىً لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَ لا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (270, N'قَوْلٌ مَعْرُوفٌ وَ مَغْفِرَةٌ خَيْرٌ مِنْ صَدَقَةٍ يَتْبَعُها أَذىً وَ اللَّهُ غَنِيٌّ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (271, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تُبْطِلُوا صَدَقاتِكُمْ بِالْمَنِّ وَ الْأَذى كَالَّذِي يُنْفِقُ مالَهُ رِئاءَ النَّاسِ وَ لا يُؤْمِنُ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ فَمَثَلُهُ كَمَثَلِ صَفْوانٍ عَلَيْهِ تُرابٌ فَأَصابَهُ وابِلٌ فَتَرَكَهُ صَلْداً لا يَقْدِرُونَ عَلى شَيْ‏ءٍ مِمَّا كَسَبُوا وَ اللَّهُ لا يَهْدِي الْقَوْمَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (272, N'وَ مَثَلُ الَّذِينَ يُنْفِقُونَ أَمْوالَهُمُ ابْتِغاءَ مَرْضاتِ اللَّهِ وَ تَثْبِيتاً مِنْ أَنْفُسِهِمْ كَمَثَلِ جَنَّةٍ بِرَبْوَةٍ أَصابَها وابِلٌ فَآتَتْ أُكُلَها ضِعْفَيْنِ فَإِنْ لَمْ يُصِبْها وابِلٌ فَطَلٌّ وَ اللَّهُ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (273, N'أَ يَوَدُّ أَحَدُكُمْ أَنْ تَكُونَ لَهُ جَنَّةٌ مِنْ نَخِيلٍ وَ أَعْنابٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ لَهُ فِيها مِنْ كُلِّ الثَّمَراتِ وَ أَصابَهُ الْكِبَرُ وَ لَهُ ذُرِّيَّةٌ ضُعَفاءُ فَأَصابَها إِعْصارٌ فِيهِ نارٌ فَاحْتَرَقَتْ كَذلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآياتِ لَعَلَّكُمْ تَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (274, N'يا أَيُّهَا الَّذِينَ آمَنُوا أَنْفِقُوا مِنْ طَيِّباتِ ما كَسَبْتُمْ وَ مِمَّا أَخْرَجْنا لَكُمْ مِنَ الْأَرْضِ وَ لا تَيَمَّمُوا الْخَبِيثَ مِنْهُ تُنْفِقُونَ وَ لَسْتُمْ بِآخِذِيهِ إِلاَّ أَنْ تُغْمِضُوا فِيهِ وَ اعْلَمُوا أَنَّ اللَّهَ غَنِيٌّ حَمِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (275, N'الشَّيْطانُ يَعِدُكُمُ الْفَقْرَ وَ يَأْمُرُكُمْ بِالْفَحْشاءِ وَ اللَّهُ يَعِدُكُمْ مَغْفِرَةً مِنْهُ وَ فَضْلاً وَ اللَّهُ واسِعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (276, N'يُؤْتِي الْحِكْمَةَ مَنْ يَشاءُ وَ مَنْ يُؤْتَ الْحِكْمَةَ فَقَدْ أُوتِيَ خَيْراً كَثِيراً وَ ما يَذَّكَّرُ إِلاَّ أُولُوا الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (277, N'وَ ما أَنْفَقْتُمْ مِنْ نَفَقَةٍ أَوْ نَذَرْتُمْ مِنْ نَذْرٍ فَإِنَّ اللَّهَ يَعْلَمُهُ وَ ما لِلظَّالِمِينَ مِنْ أَنْصارٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (278, N'إِنْ تُبْدُوا الصَّدَقاتِ فَنِعِمَّا هِيَ وَ إِنْ تُخْفُوها وَ تُؤْتُوهَا الْفُقَراءَ فَهُوَ خَيْرٌ لَكُمْ وَ يُكَفِّرُ عَنْكُمْ مِنْ سَيِّئاتِكُمْ وَ اللَّهُ بِما تَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (279, N'لَيْسَ عَلَيْكَ هُداهُمْ وَ لكِنَّ اللَّهَ يَهْدِي مَنْ يَشاءُ وَ ما تُنْفِقُوا مِنْ خَيْرٍ فَلِأَنْفُسِكُمْ وَ ما تُنْفِقُونَ إِلاَّ ابْتِغاءَ وَجْهِ اللَّهِ وَ ما تُنْفِقُوا مِنْ خَيْرٍ يُوَفَّ إِلَيْكُمْ وَ أَنْتُمْ لا تُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (280, N'لِلْفُقَراءِ الَّذِينَ أُحْصِرُوا فِي سَبِيلِ اللَّهِ لا يَسْتَطِيعُونَ ضَرْباً فِي الْأَرْضِ يَحْسَبُهُمُ الْجاهِلُ أَغْنِياءَ مِنَ التَّعَفُّفِ تَعْرِفُهُمْ بِسِيماهُمْ لا يَسْئَلُونَ النَّاسَ إِلْحافاً وَ ما تُنْفِقُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (281, N'الَّذِينَ يُنْفِقُونَ أَمْوالَهُمْ بِاللَّيْلِ وَ النَّهارِ سِرًّا وَ عَلانِيَةً فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَ لا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (282, N'الَّذِينَ يَأْكُلُونَ الرِّبا لا يَقُومُونَ إِلاَّ كَما يَقُومُ الَّذِي يَتَخَبَّطُهُ الشَّيْطانُ مِنَ الْمَسِّ ذلِكَ بِأَنَّهُمْ قالُوا إِنَّمَا الْبَيْعُ مِثْلُ الرِّبا وَ أَحَلَّ اللَّهُ الْبَيْعَ وَ حَرَّمَ الرِّبا فَمَنْ جاءَهُ مَوْعِظَةٌ مِنْ رَبِّهِ فَانْتَهى فَلَهُ ما سَلَفَ وَ أَمْرُهُ إِلَى اللَّهِ وَ مَنْ عادَ فَأُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (283, N'يَمْحَقُ اللَّهُ الرِّبا وَ يُرْبِي الصَّدَقاتِ وَ اللَّهُ لا يُحِبُّ كُلَّ كَفَّارٍ أَثِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (284, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ وَ أَقامُوا الصَّلاةَ وَ آتَوُا الزَّكاةَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَ لا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (285, N'يا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَ ذَرُوا ما بَقِيَ مِنَ الرِّبا إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (286, N'فَإِنْ لَمْ تَفْعَلُوا فَأْذَنُوا بِحَرْبٍ مِنَ اللَّهِ وَ رَسُولِهِ وَ إِنْ تُبْتُمْ فَلَكُمْ رُؤُسُ أَمْوالِكُمْ لا تَظْلِمُونَ وَ لا تُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (287, N'وَ إِنْ كانَ ذُو عُسْرَةٍ فَنَظِرَةٌ إِلى مَيْسَرَةٍ وَ أَنْ تَصَدَّقُوا خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (288, N'وَ اتَّقُوا يَوْماً تُرْجَعُونَ فِيهِ إِلَى اللَّهِ ثُمَّ تُوَفَّى كُلُّ نَفْسٍ ما كَسَبَتْ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (289, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا تَدايَنْتُمْ بِدَيْنٍ إِلى أَجَلٍ مُسَمًّى فَاكْتُبُوهُ وَ لْيَكْتُبْ بَيْنَكُمْ كاتِبٌ بِالْعَدْلِ وَ لا يَأْبَ كاتِبٌ أَنْ يَكْتُبَ كَما عَلَّمَهُ اللَّهُ فَلْيَكْتُبْ وَ لْيُمْلِلِ الَّذِي عَلَيْهِ الْحَقُّ وَ لْيَتَّقِ اللَّهَ رَبَّهُ وَ لا يَبْخَسْ مِنْهُ شَيْئاً فَإِنْ كانَ الَّذِي عَلَيْهِ الْحَقُّ سَفِيهاً أَوْ ضَعِيفاً أَوْ لا يَسْتَطِيعُ أَنْ يُمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُ بِالْعَدْلِ وَ اسْتَشْهِدُوا شَهِيدَيْنِ مِنْ رِجالِكُمْ فَإِنْ لَمْ يَكُونا رَجُلَيْنِ فَرَجُلٌ وَ امْرَأَتانِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَداءِ أَنْ تَضِلَّ إِحْداهُما فَتُذَكِّرَ إِحْداهُمَا الْأُخْرى وَ لا يَأْبَ الشُّهَداءُ إِذا ما دُعُوا وَ لا تَسْئَمُوا أَنْ تَكْتُبُوهُ صَغِيراً أَوْ كَبِيراً إِلى أَجَلِهِ ذلِكُمْ أَقْسَطُ عِنْدَ اللَّهِ وَ أَقْوَمُ لِلشَّهادَةِ وَ أَدْنى أَلاَّ تَرْتابُوا إِلاَّ أَنْ تَكُونَ تِجارَةً حاضِرَةً تُدِيرُونَها بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُناحٌ أَلاَّ تَكْتُبُوها وَ أَشْهِدُوا إِذا تَبايَعْتُمْ وَ لا يُضَارَّ كاتِبٌ وَ لا شَهِيدٌ وَ إِنْ تَفْعَلُوا فَإِنَّهُ فُسُوقٌ بِكُمْ وَ اتَّقُوا اللَّهَ وَ يُعَلِّمُكُمُ اللَّهُ وَ اللَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (290, N'وَ إِنْ كُنْتُمْ عَلى سَفَرٍ وَ لَمْ تَجِدُوا كاتِباً فَرِهانٌ مَقْبُوضَةٌ فَإِنْ أَمِنَ بَعْضُكُمْ بَعْضاً فَلْيُؤَدِّ الَّذِي اؤْتُمِنَ أَمانَتَهُ وَ لْيَتَّقِ اللَّهَ رَبَّهُ وَ لا تَكْتُمُوا الشَّهادَةَ وَ مَنْ يَكْتُمْها فَإِنَّهُ آثِمٌ قَلْبُهُ وَ اللَّهُ بِما تَعْمَلُونَ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (291, N'لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ إِنْ تُبْدُوا ما فِي أَنْفُسِكُمْ أَوْ تُخْفُوهُ يُحاسِبْكُمْ بِهِ اللَّهُ فَيَغْفِرُ لِمَنْ يَشاءُ وَ يُعَذِّبُ مَنْ يَشاءُ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (292, N'آمَنَ الرَّسُولُ بِما أُنْزِلَ إِلَيْهِ مِنْ رَبِّهِ وَ الْمُؤْمِنُونَ كُلٌّ آمَنَ بِاللَّهِ وَ مَلائِكَتِهِ وَ كُتُبِهِ وَ رُسُلِهِ لا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْ رُسُلِهِ وَ قالُوا سَمِعْنا وَ أَطَعْنا غُفْرانَكَ رَبَّنا وَ إِلَيْكَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (293, N'لا يُكَلِّفُ اللَّهُ نَفْساً إِلاَّ وُسْعَها لَها ما كَسَبَتْ وَ عَلَيْها مَا اكْتَسَبَتْ رَبَّنا لا تُؤاخِذْنا إِنْ نَسِينا أَوْ أَخْطَأْنا رَبَّنا وَ لا تَحْمِلْ عَلَيْنا إِصْراً كَما حَمَلْتَهُ عَلَى الَّذِينَ مِنْ قَبْلِنا رَبَّنا وَ لا تُحَمِّلْنا ما لا طاقَةَ لَنا بِهِ وَ اعْفُ عَنَّا وَ اغْفِرْ لَنا وَ ارْحَمْنا أَنْتَ مَوْلانا فَانْصُرْنا عَلَى الْقَوْمِ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (294, N'الم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (295, N'اللَّهُ لا إِلهَ إِلاَّ هُوَ الْحَيُّ الْقَيُّومُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (296, N'نَزَّلَ عَلَيْكَ الْكِتابَ بِالْحَقِّ مُصَدِّقاً لِما بَيْنَ يَدَيْهِ وَ أَنْزَلَ التَّوْراةَ وَ الْإِنْجِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (297, N'مِنْ قَبْلُ هُدىً لِلنَّاسِ وَ أَنْزَلَ الْفُرْقانَ إِنَّ الَّذِينَ كَفَرُوا بِآياتِ اللَّهِ لَهُمْ عَذابٌ شَدِيدٌ وَ اللَّهُ عَزِيزٌ ذُو انْتِقامٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (298, N'إِنَّ اللَّهَ لا يَخْفى عَلَيْهِ شَيْ‏ءٌ فِي الْأَرْضِ وَ لا فِي السَّماءِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (299, N'هُوَ الَّذِي يُصَوِّرُكُمْ فِي الْأَرْحامِ كَيْفَ يَشاءُ لا إِلهَ إِلاَّ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (300, N'هُوَ الَّذِي أَنْزَلَ عَلَيْكَ الْكِتابَ مِنْهُ آياتٌ مُحْكَماتٌ هُنَّ أُمُّ الْكِتابِ وَ أُخَرُ مُتَشابِهاتٌ فَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ ما تَشابَهَ مِنْهُ ابْتِغاءَ الْفِتْنَةِ وَ ابْتِغاءَ تَأْوِيلِهِ وَ ما يَعْلَمُ تَأْوِيلَهُ إِلاَّ اللَّهُ وَ الرَّاسِخُونَ فِي الْعِلْمِ يَقُولُونَ آمَنَّا بِهِ كُلٌّ مِنْ عِنْدِ رَبِّنا وَ ما يَذَّكَّرُ إِلاَّ أُولُوا الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (301, N'رَبَّنا لا تُزِغْ قُلُوبَنا بَعْدَ إِذْ هَدَيْتَنا وَ هَبْ لَنا مِنْ لَدُنْكَ رَحْمَةً إِنَّكَ أَنْتَ الْوَهَّابُ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (302, N'رَبَّنا إِنَّكَ جامِعُ النَّاسِ لِيَوْمٍ لا رَيْبَ فِيهِ إِنَّ اللَّهَ لا يُخْلِفُ الْمِيعادَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (303, N'إِنَّ الَّذِينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ أَمْوالُهُمْ وَ لا أَوْلادُهُمْ مِنَ اللَّهِ شَيْئاً وَ أُولئِكَ هُمْ وَقُودُ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (304, N'كَدَأْبِ آلِ فِرْعَوْنَ وَ الَّذِينَ مِنْ قَبْلِهِمْ كَذَّبُوا بِآياتِنا فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ وَ اللَّهُ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (305, N'قُلْ لِلَّذِينَ كَفَرُوا سَتُغْلَبُونَ وَ تُحْشَرُونَ إِلى جَهَنَّمَ وَ بِئْسَ الْمِهادُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (306, N'قَدْ كانَ لَكُمْ آيَةٌ فِي فِئَتَيْنِ الْتَقَتا فِئَةٌ تُقاتِلُ فِي سَبِيلِ اللَّهِ وَ أُخْرى كافِرَةٌ يَرَوْنَهُمْ مِثْلَيْهِمْ رَأْيَ الْعَيْنِ وَ اللَّهُ يُؤَيِّدُ بِنَصْرِهِ مَنْ يَشاءُ إِنَّ فِي ذلِكَ لَعِبْرَةً لِأُولِي الْأَبْصارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (307, N'زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَواتِ مِنَ النِّساءِ وَ الْبَنِينَ وَ الْقَناطِيرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَ الْفِضَّةِ وَ الْخَيْلِ الْمُسَوَّمَةِ وَ الْأَنْعامِ وَ الْحَرْثِ ذلِكَ مَتاعُ الْحَياةِ الدُّنْيا وَ اللَّهُ عِنْدَهُ حُسْنُ الْمَآبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (308, N'قُلْ أَ أُنَبِّئُكُمْ بِخَيْرٍ مِنْ ذلِكُمْ لِلَّذِينَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها وَ أَزْواجٌ مُطَهَّرَةٌ وَ رِضْوانٌ مِنَ اللَّهِ وَ اللَّهُ بَصِيرٌ بِالْعِبادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (309, N'الَّذِينَ يَقُولُونَ رَبَّنا إِنَّنا آمَنَّا فَاغْفِرْ لَنا ذُنُوبَنا وَ قِنا عَذابَ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (310, N'الصَّابِرِينَ وَ الصَّادِقِينَ وَ الْقانِتِينَ وَ الْمُنْفِقِينَ وَ الْمُسْتَغْفِرِينَ بِالْأَسْحارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (311, N'شَهِدَ اللَّهُ أَنَّهُ لا إِلهَ إِلاَّ هُوَ وَ الْمَلائِكَةُ وَ أُولُوا الْعِلْمِ قائِماً بِالْقِسْطِ لا إِلهَ إِلاَّ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (312, N'إِنَّ الدِّينَ عِنْدَ اللَّهِ الْإِسْلامُ وَ مَا اخْتَلَفَ الَّذِينَ أُوتُوا الْكِتابَ إِلاَّ مِنْ بَعْدِ ما جاءَهُمُ الْعِلْمُ بَغْياً بَيْنَهُمْ وَ مَنْ يَكْفُرْ بِآياتِ اللَّهِ فَإِنَّ اللَّهَ سَرِيعُ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (313, N'فَإِنْ حَاجُّوكَ فَقُلْ أَسْلَمْتُ وَجْهِيَ لِلَّهِ وَ مَنِ اتَّبَعَنِ وَ قُلْ لِلَّذِينَ أُوتُوا الْكِتابَ وَ الْأُمِّيِّينَ أَ أَسْلَمْتُمْ فَإِنْ أَسْلَمُوا فَقَدِ اهْتَدَوْا وَ إِنْ تَوَلَّوْا فَإِنَّما عَلَيْكَ الْبَلاغُ وَ اللَّهُ بَصِيرٌ بِالْعِبادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (314, N'إِنَّ الَّذِينَ يَكْفُرُونَ بِآياتِ اللَّهِ وَ يَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ حَقٍّ وَ يَقْتُلُونَ الَّذِينَ يَأْمُرُونَ بِالْقِسْطِ مِنَ النَّاسِ فَبَشِّرْهُمْ بِعَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (315, N'أُولئِكَ الَّذِينَ حَبِطَتْ أَعْمالُهُمْ فِي الدُّنْيا وَ الْآخِرَةِ وَ ما لَهُمْ مِنْ ناصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (316, N'أَ لَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيباً مِنَ الْكِتابِ يُدْعَوْنَ إِلى كِتابِ اللَّهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلَّى فَرِيقٌ مِنْهُمْ وَ هُمْ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (317, N'ذلِكَ بِأَنَّهُمْ قالُوا لَنْ تَمَسَّنَا النَّارُ إِلاَّ أَيَّاماً مَعْدُوداتٍ وَ غَرَّهُمْ فِي دِينِهِمْ ما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (318, N'فَكَيْفَ إِذا جَمَعْناهُمْ لِيَوْمٍ لا رَيْبَ فِيهِ وَ وُفِّيَتْ كُلُّ نَفْسٍ ما كَسَبَتْ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (319, N'قُلِ اللَّهُمَّ مالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَنْ تَشاءُ وَ تَنْزِعُ الْمُلْكَ مِمَّنْ تَشاءُ وَ تُعِزُّ مَنْ تَشاءُ وَ تُذِلُّ مَنْ تَشاءُ بِيَدِكَ الْخَيْرُ إِنَّكَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (320, N'تُولِجُ اللَّيْلَ فِي النَّهارِ وَ تُولِجُ النَّهارَ فِي اللَّيْلِ وَ تُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَ تُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَ تَرْزُقُ مَنْ تَشاءُ بِغَيْرِ حِسابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (321, N'لا يَتَّخِذِ الْمُؤْمِنُونَ الْكافِرِينَ أَوْلِياءَ مِنْ دُونِ الْمُؤْمِنِينَ وَ مَنْ يَفْعَلْ ذلِكَ فَلَيْسَ مِنَ اللَّهِ فِي شَيْ‏ءٍ إِلاَّ أَنْ تَتَّقُوا مِنْهُمْ تُقاةً وَ يُحَذِّرُكُمُ اللَّهُ نَفْسَهُ وَ إِلَى اللَّهِ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (322, N'قُلْ إِنْ تُخْفُوا ما فِي صُدُورِكُمْ أَوْ تُبْدُوهُ يَعْلَمْهُ اللَّهُ وَ يَعْلَمُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (323, N'يَوْمَ تَجِدُ كُلُّ نَفْسٍ ما عَمِلَتْ مِنْ خَيْرٍ مُحْضَراً وَ ما عَمِلَتْ مِنْ سُوءٍ تَوَدُّ لَوْ أَنَّ بَيْنَها وَ بَيْنَهُ أَمَداً بَعِيداً وَ يُحَذِّرُكُمُ اللَّهُ نَفْسَهُ وَ اللَّهُ رَؤُفٌ بِالْعِبادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (324, N'قُلْ إِنْ كُنْتُمْ تُحِبُّونَ اللَّهَ فَاتَّبِعُونِي يُحْبِبْكُمُ اللَّهُ وَ يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (325, N'قُلْ أَطِيعُوا اللَّهَ وَ الرَّسُولَ فَإِنْ تَوَلَّوْا فَإِنَّ اللَّهَ لا يُحِبُّ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (326, N'إِنَّ اللَّهَ اصْطَفى آدَمَ وَ نُوحاً وَ آلَ إِبْراهِيمَ وَ آلَ عِمْرانَ عَلَى الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (327, N'ذُرِّيَّةً بَعْضُها مِنْ بَعْضٍ وَ اللَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (328, N'إِذْ قالَتِ امْرَأَتُ عِمْرانَ رَبِّ إِنِّي نَذَرْتُ لَكَ ما فِي بَطْنِي مُحَرَّراً فَتَقَبَّلْ مِنِّي إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (329, N'فَلَمَّا وَضَعَتْها قالَتْ رَبِّ إِنِّي وَضَعْتُها أُنْثى وَ اللَّهُ أَعْلَمُ بِما وَضَعَتْ وَ لَيْسَ الذَّكَرُ كَالْأُنْثى وَ إِنِّي سَمَّيْتُها مَرْيَمَ وَ إِنِّي أُعِيذُها بِكَ وَ ذُرِّيَّتَها مِنَ الشَّيْطانِ الرَّجِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (330, N'فَتَقَبَّلَها رَبُّها بِقَبُولٍ حَسَنٍ وَ أَنْبَتَها نَباتاً حَسَناً وَ كَفَّلَها زَكَرِيَّا كُلَّما دَخَلَ عَلَيْها زَكَرِيَّا الْمِحْرابَ وَجَدَ عِنْدَها رِزْقاً قالَ يا مَرْيَمُ أَنَّى لَكِ هذا قالَتْ هُوَ مِنْ عِنْدِ اللَّهِ إِنَّ اللَّهَ يَرْزُقُ مَنْ يَشاءُ بِغَيْرِ حِسابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (331, N'هُنالِكَ دَعا زَكَرِيَّا رَبَّهُ قالَ رَبِّ هَبْ لِي مِنْ لَدُنْكَ ذُرِّيَّةً طَيِّبَةً إِنَّكَ سَمِيعُ الدُّعاءِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (332, N'فَنادَتْهُ الْمَلائِكَةُ وَ هُوَ قائِمٌ يُصَلِّي فِي الْمِحْرابِ أَنَّ اللَّهَ يُبَشِّرُكَ بِيَحْيى مُصَدِّقاً بِكَلِمَةٍ مِنَ اللَّهِ وَ سَيِّداً وَ حَصُوراً وَ نَبِيًّا مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (333, N'قالَ رَبِّ أَنَّى يَكُونُ لِي غُلامٌ وَ قَدْ بَلَغَنِيَ الْكِبَرُ وَ امْرَأَتِي عاقِرٌ قالَ كَذلِكَ اللَّهُ يَفْعَلُ ما يَشاءُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (334, N'قالَ رَبِّ اجْعَلْ لِي آيَةً قالَ آيَتُكَ أَلاَّ تُكَلِّمَ النَّاسَ ثَلاثَةَ أَيَّامٍ إِلاَّ رَمْزاً وَ اذْكُرْ رَبَّكَ كَثِيراً وَ سَبِّحْ بِالْعَشِيِّ وَ الْإِبْكارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (335, N'وَ إِذْ قالَتِ الْمَلائِكَةُ يا مَرْيَمُ إِنَّ اللَّهَ اصْطَفاكِ وَ طَهَّرَكِ وَ اصْطَفاكِ عَلى نِساءِ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (336, N'يا مَرْيَمُ اقْنُتِي لِرَبِّكِ وَ اسْجُدِي وَ ارْكَعِي مَعَ الرَّاكِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (337, N'ذلِكَ مِنْ أَنْباءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ وَ ما كُنْتَ لَدَيْهِمْ إِذْ يُلْقُونَ أَقْلامَهُمْ أَيُّهُمْ يَكْفُلُ مَرْيَمَ وَ ما كُنْتَ لَدَيْهِمْ إِذْ يَخْتَصِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (338, N'إِذْ قالَتِ الْمَلائِكَةُ يا مَرْيَمُ إِنَّ اللَّهَ يُبَشِّرُكِ بِكَلِمَةٍ مِنْهُ اسْمُهُ الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ وَجِيهاً فِي الدُّنْيا وَ الْآخِرَةِ وَ مِنَ الْمُقَرَّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (339, N'وَ يُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَ كَهْلاً وَ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (340, N'قالَتْ رَبِّ أَنَّى يَكُونُ لِي وَلَدٌ وَ لَمْ يَمْسَسْنِي بَشَرٌ قالَ كَذلِكِ اللَّهُ يَخْلُقُ ما يَشاءُ إِذا قَضى أَمْراً فَإِنَّما يَقُولُ لَهُ كُنْ فَيَكُونُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (341, N'وَ يُعَلِّمُهُ الْكِتابَ وَ الْحِكْمَةَ وَ التَّوْراةَ وَ الْإِنْجِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (342, N'وَ رَسُولاً إِلى بَنِي إِسْرائِيلَ أَنِّي قَدْ جِئْتُكُمْ بِآيَةٍ مِنْ رَبِّكُمْ أَنِّي أَخْلُقُ لَكُمْ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ فَأَنْفُخُ فِيهِ فَيَكُونُ طَيْراً بِإِذْنِ اللَّهِ وَ أُبْرِئُ الْأَكْمَهَ وَ الْأَبْرَصَ وَ أُحْيِ الْمَوْتى بِإِذْنِ اللَّهِ وَ أُنَبِّئُكُمْ بِما تَأْكُلُونَ وَ ما تَدَّخِرُونَ فِي بُيُوتِكُمْ إِنَّ فِي ذلِكَ لَآيَةً لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (343, N'وَ مُصَدِّقاً لِما بَيْنَ يَدَيَّ مِنَ التَّوْراةِ وَ لِأُحِلَّ لَكُمْ بَعْضَ الَّذِي حُرِّمَ عَلَيْكُمْ وَ جِئْتُكُمْ بِآيَةٍ مِنْ رَبِّكُمْ فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (344, N'إِنَّ اللَّهَ رَبِّي وَ رَبُّكُمْ فَاعْبُدُوهُ هذا صِراطٌ مُسْتَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (345, N'فَلَمَّا أَحَسَّ عِيسى مِنْهُمُ الْكُفْرَ قالَ مَنْ أَنْصارِي إِلَى اللَّهِ قالَ الْحَوارِيُّونَ نَحْنُ أَنْصارُ اللَّهِ آمَنَّا بِاللَّهِ وَ اشْهَدْ بِأَنَّا مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (346, N'رَبَّنا آمَنَّا بِما أَنْزَلْتَ وَ اتَّبَعْنَا الرَّسُولَ فَاكْتُبْنا مَعَ الشَّاهِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (347, N'وَ مَكَرُوا وَ مَكَرَ اللَّهُ وَ اللَّهُ خَيْرُ الْماكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (348, N'إِذْ قالَ اللَّهُ يا عِيسى إِنِّي مُتَوَفِّيكَ وَ رافِعُكَ إِلَيَّ وَ مُطَهِّرُكَ مِنَ الَّذِينَ كَفَرُوا وَ جاعِلُ الَّذِينَ اتَّبَعُوكَ فَوْقَ الَّذِينَ كَفَرُوا إِلى يَوْمِ الْقِيامَةِ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأَحْكُمُ بَيْنَكُمْ فِيما كُنْتُمْ فِيهِ تَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (349, N'فَأَمَّا الَّذِينَ كَفَرُوا فَأُعَذِّبُهُمْ عَذاباً شَدِيداً فِي الدُّنْيا وَ الْآخِرَةِ وَ ما لَهُمْ مِنْ ناصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (350, N'وَ أَمَّا الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَ اللَّهُ لا يُحِبُّ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (351, N'ذلِكَ نَتْلُوهُ عَلَيْكَ مِنَ الْآياتِ وَ الذِّكْرِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (352, N'إِنَّ مَثَلَ عِيسى عِنْدَ اللَّهِ كَمَثَلِ آدَمَ خَلَقَهُ مِنْ تُرابٍ ثُمَّ قالَ لَهُ كُنْ فَيَكُونُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (353, N'الْحَقُّ مِنْ رَبِّكَ فَلا تَكُنْ مِنَ الْمُمْتَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (354, N'فَمَنْ حَاجَّكَ فِيهِ مِنْ بَعْدِ ما جاءَكَ مِنَ الْعِلْمِ فَقُلْ تَعالَوْا نَدْعُ أَبْناءَنا وَ أَبْناءَكُمْ وَ نِساءَنا وَ نِساءَكُمْ وَ أَنْفُسَنا وَ أَنْفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَعْنَتَ اللَّهِ عَلَى الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (355, N'إِنَّ هذا لَهُوَ الْقَصَصُ الْحَقُّ وَ ما مِنْ إِلهٍ إِلاَّ اللَّهُ وَ إِنَّ اللَّهَ لَهُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (356, N'فَإِنْ تَوَلَّوْا فَإِنَّ اللَّهَ عَلِيمٌ بِالْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (357, N'قُلْ يا أَهْلَ الْكِتابِ تَعالَوْا إِلى كَلِمَةٍ سَواءٍ بَيْنَنا وَ بَيْنَكُمْ أَلاَّ نَعْبُدَ إِلاَّ اللَّهَ وَ لا نُشْرِكَ بِهِ شَيْئاً وَ لا يَتَّخِذَ بَعْضُنا بَعْضاً أَرْباباً مِنْ دُونِ اللَّهِ فَإِنْ تَوَلَّوْا فَقُولُوا اشْهَدُوا بِأَنَّا مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (358, N'يا أَهْلَ الْكِتابِ لِمَ تُحَاجُّونَ فِي إِبْراهِيمَ وَ ما أُنْزِلَتِ التَّوْراةُ وَ الْإِنْجِيلُ إِلاَّ مِنْ بَعْدِهِ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (359, N'ها أَنْتُمْ هؤُلاءِ حاجَجْتُمْ فِيما لَكُمْ بِهِ عِلْمٌ فَلِمَ تُحَاجُّونَ فِيما لَيْسَ لَكُمْ بِهِ عِلْمٌ وَ اللَّهُ يَعْلَمُ وَ أَنْتُمْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (360, N'ما كانَ إِبْراهِيمُ يَهُودِيًّا وَ لا نَصْرانِيًّا وَ لكِنْ كانَ حَنِيفاً مُسْلِماً وَ ما كانَ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (361, N'إِنَّ أَوْلَى النَّاسِ بِإِبْراهِيمَ لَلَّذِينَ اتَّبَعُوهُ وَ هذَا النَّبِيُّ وَ الَّذِينَ آمَنُوا وَ اللَّهُ وَلِيُّ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (362, N'وَدَّتْ طائِفَةٌ مِنْ أَهْلِ الْكِتابِ لَوْ يُضِلُّونَكُمْ وَ ما يُضِلُّونَ إِلاَّ أَنْفُسَهُمْ وَ ما يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (363, N'يا أَهْلَ الْكِتابِ لِمَ تَكْفُرُونَ بِآياتِ اللَّهِ وَ أَنْتُمْ تَشْهَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (364, N'يا أَهْلَ الْكِتابِ لِمَ تَلْبِسُونَ الْحَقَّ بِالْباطِلِ وَ تَكْتُمُونَ الْحَقَّ وَ أَنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (365, N'وَ قالَتْ طائِفَةٌ مِنْ أَهْلِ الْكِتابِ آمِنُوا بِالَّذِي أُنْزِلَ عَلَى الَّذِينَ آمَنُوا وَجْهَ النَّهارِ وَ اكْفُرُوا آخِرَهُ لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (366, N'وَ لا تُؤْمِنُوا إِلاَّ لِمَنْ تَبِعَ دِينَكُمْ قُلْ إِنَّ الْهُدى هُدَى اللَّهِ أَنْ يُؤْتى أَحَدٌ مِثْلَ ما أُوتِيتُمْ أَوْ يُحاجُّوكُمْ عِنْدَ رَبِّكُمْ قُلْ إِنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشاءُ وَ اللَّهُ واسِعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (367, N'يَخْتَصُّ بِرَحْمَتِهِ مَنْ يَشاءُ وَ اللَّهُ ذُو الْفَضْلِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (368, N'وَ مِنْ أَهْلِ الْكِتابِ مَنْ إِنْ تَأْمَنْهُ بِقِنْطارٍ يُؤَدِّهِ إِلَيْكَ وَ مِنْهُمْ مَنْ إِنْ تَأْمَنْهُ بِدِينارٍ لا يُؤَدِّهِ إِلَيْكَ إِلاَّ ما دُمْتَ عَلَيْهِ قائِماً ذلِكَ بِأَنَّهُمْ قالُوا لَيْسَ عَلَيْنا فِي الْأُمِّيِّينَ سَبِيلٌ وَ يَقُولُونَ عَلَى اللَّهِ الْكَذِبَ وَ هُمْ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (369, N'بَلى مَنْ أَوْفى بِعَهْدِهِ وَ اتَّقى فَإِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (370, N'إِنَّ الَّذِينَ يَشْتَرُونَ بِعَهْدِ اللَّهِ وَ أَيْمانِهِمْ ثَمَناً قَلِيلاً أُولئِكَ لا خَلاقَ لَهُمْ فِي الْآخِرَةِ وَ لا يُكَلِّمُهُمُ اللَّهُ وَ لا يَنْظُرُ إِلَيْهِمْ يَوْمَ الْقِيامَةِ وَ لا يُزَكِّيهِمْ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (371, N'وَ إِنَّ مِنْهُمْ لَفَرِيقاً يَلْوُونَ أَلْسِنَتَهُمْ بِالْكِتابِ لِتَحْسَبُوهُ مِنَ الْكِتابِ وَ ما هُوَ مِنَ الْكِتابِ وَ يَقُولُونَ هُوَ مِنْ عِنْدِ اللَّهِ وَ ما هُوَ مِنْ عِنْدِ اللَّهِ وَ يَقُولُونَ عَلَى اللَّهِ الْكَذِبَ وَ هُمْ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (372, N'ما كانَ لِبَشَرٍ أَنْ يُؤْتِيَهُ اللَّهُ الْكِتابَ وَ الْحُكْمَ وَ النُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُوا عِباداً لِي مِنْ دُونِ اللَّهِ وَ لكِنْ كُونُوا رَبَّانِيِّينَ بِما كُنْتُمْ تُعَلِّمُونَ الْكِتابَ وَ بِما كُنْتُمْ تَدْرُسُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (373, N'وَ لا يَأْمُرَكُمْ أَنْ تَتَّخِذُوا الْمَلائِكَةَ وَ النَّبِيِّينَ أَرْباباً أَ يَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ إِذْ أَنْتُمْ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (374, N'وَ إِذْ أَخَذَ اللَّهُ مِيثاقَ النَّبِيِّينَ لَما آتَيْتُكُمْ مِنْ كِتابٍ وَ حِكْمَةٍ ثُمَّ جاءَكُمْ رَسُولٌ مُصَدِّقٌ لِما مَعَكُمْ لَتُؤْمِنُنَّ بِهِ وَ لَتَنْصُرُنَّهُ قالَ أَ أَقْرَرْتُمْ وَ أَخَذْتُمْ عَلى ذلِكُمْ إِصْرِي قالُوا أَقْرَرْنا قالَ فَاشْهَدُوا وَ أَنَا مَعَكُمْ مِنَ الشَّاهِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (375, N'فَمَنْ تَوَلَّى بَعْدَ ذلِكَ فَأُولئِكَ هُمُ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (376, N'أَ فَغَيْرَ دِينِ اللَّهِ يَبْغُونَ وَ لَهُ أَسْلَمَ مَنْ فِي السَّماواتِ وَ الْأَرْضِ طَوْعاً وَ كَرْهاً وَ إِلَيْهِ يُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (377, N'قُلْ آمَنَّا بِاللَّهِ وَ ما أُنْزِلَ عَلَيْنا وَ ما أُنْزِلَ عَلى إِبْراهِيمَ وَ إِسْماعِيلَ وَ إِسْحاقَ وَ يَعْقُوبَ وَ الْأَسْباطِ وَ ما أُوتِيَ مُوسى وَ عِيسى وَ النَّبِيُّونَ مِنْ رَبِّهِمْ لا نُفَرِّقُ بَيْنَ أَحَدٍ مِنْهُمْ وَ نَحْنُ لَهُ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (378, N'وَ مَنْ يَبْتَغِ غَيْرَ الْإِسْلامِ دِيناً فَلَنْ يُقْبَلَ مِنْهُ وَ هُوَ فِي الْآخِرَةِ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (379, N'كَيْفَ يَهْدِي اللَّهُ قَوْماً كَفَرُوا بَعْدَ إِيمانِهِمْ وَ شَهِدُوا أَنَّ الرَّسُولَ حَقٌّ وَ جاءَهُمُ الْبَيِّناتُ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (380, N'أُولئِكَ جَزاؤُهُمْ أَنَّ عَلَيْهِمْ لَعْنَةَ اللَّهِ وَ الْمَلائِكَةِ وَ النَّاسِ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (381, N'خالِدِينَ فِيها لا يُخَفَّفُ عَنْهُمُ الْعَذابُ وَ لا هُمْ يُنْظَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (382, N'إِلاَّ الَّذِينَ تابُوا مِنْ بَعْدِ ذلِكَ وَ أَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (383, N'إِنَّ الَّذِينَ كَفَرُوا بَعْدَ إِيمانِهِمْ ثُمَّ ازْدادُوا كُفْراً لَنْ تُقْبَلَ تَوْبَتُهُمْ وَ أُولئِكَ هُمُ الضَّالُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (384, N'إِنَّ الَّذِينَ كَفَرُوا وَ ماتُوا وَ هُمْ كُفَّارٌ فَلَنْ يُقْبَلَ مِنْ أَحَدِهِمْ مِلْ‏ءُ الْأَرْضِ ذَهَباً وَ لَوِ افْتَدى بِهِ أُولئِكَ لَهُمْ عَذابٌ أَلِيمٌ وَ ما لَهُمْ مِنْ ناصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (385, N'لَنْ تَنالُوا الْبِرَّ حَتَّى تُنْفِقُوا مِمَّا تُحِبُّونَ وَ ما تُنْفِقُوا مِنْ شَيْ‏ءٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (386, N'كُلُّ الطَّعامِ كانَ حِلاًّ لِبَنِي إِسْرائِيلَ إِلاَّ ما حَرَّمَ إِسْرائِيلُ عَلى نَفْسِهِ مِنْ قَبْلِ أَنْ تُنَزَّلَ التَّوْراةُ قُلْ فَأْتُوا بِالتَّوْراةِ فَاتْلُوها إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (387, N'فَمَنِ افْتَرى عَلَى اللَّهِ الْكَذِبَ مِنْ بَعْدِ ذلِكَ فَأُولئِكَ هُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (388, N'قُلْ صَدَقَ اللَّهُ فَاتَّبِعُوا مِلَّةَ إِبْراهِيمَ حَنِيفاً وَ ما كانَ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (389, N'إِنَّ أَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبارَكاً وَ هُدىً لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (390, N'فِيهِ آياتٌ بَيِّناتٌ مَقامُ إِبْراهِيمَ وَ مَنْ دَخَلَهُ كانَ آمِناً وَ لِلَّهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطاعَ إِلَيْهِ سَبِيلاً وَ مَنْ كَفَرَ فَإِنَّ اللَّهَ غَنِيٌّ عَنِ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (391, N'قُلْ يا أَهْلَ الْكِتابِ لِمَ تَكْفُرُونَ بِآياتِ اللَّهِ وَ اللَّهُ شَهِيدٌ عَلى ما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (392, N'قُلْ يا أَهْلَ الْكِتابِ لِمَ تَصُدُّونَ عَنْ سَبِيلِ اللَّهِ مَنْ آمَنَ تَبْغُونَها عِوَجاً وَ أَنْتُمْ شُهَداءُ وَ مَا اللَّهُ بِغافِلٍ عَمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (393, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تُطِيعُوا فَرِيقاً مِنَ الَّذِينَ أُوتُوا الْكِتابَ يَرُدُّوكُمْ بَعْدَ إِيمانِكُمْ كافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (394, N'وَ كَيْفَ تَكْفُرُونَ وَ أَنْتُمْ تُتْلى عَلَيْكُمْ آياتُ اللَّهِ وَ فِيكُمْ رَسُولُهُ وَ مَنْ يَعْتَصِمْ بِاللَّهِ فَقَدْ هُدِيَ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (395, N'يا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ حَقَّ تُقاتِهِ وَ لا تَمُوتُنَّ إِلاَّ وَ أَنْتُمْ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (396, N'وَ اعْتَصِمُوا بِحَبْلِ اللَّهِ جَمِيعاً وَ لا تَفَرَّقُوا وَ اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ كُنْتُمْ أَعْداءً فَأَلَّفَ بَيْنَ قُلُوبِكُمْ فَأَصْبَحْتُمْ بِنِعْمَتِهِ إِخْواناً وَ كُنْتُمْ عَلى شَفا حُفْرَةٍ مِنَ النَّارِ فَأَنْقَذَكُمْ مِنْها كَذلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آياتِهِ لَعَلَّكُمْ تَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (397, N'وَ لْتَكُنْ مِنْكُمْ أُمَّةٌ يَدْعُونَ إِلَى الْخَيْرِ وَ يَأْمُرُونَ بِالْمَعْرُوفِ وَ يَنْهَوْنَ عَنِ الْمُنْكَرِ وَ أُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (398, N'وَ لا تَكُونُوا كَالَّذِينَ تَفَرَّقُوا وَ اخْتَلَفُوا مِنْ بَعْدِ ما جاءَهُمُ الْبَيِّناتُ وَ أُولئِكَ لَهُمْ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (399, N'يَوْمَ تَبْيَضُّ وُجُوهٌ وَ تَسْوَدُّ وُجُوهٌ فَأَمَّا الَّذِينَ اسْوَدَّتْ وُجُوهُهُمْ أَ كَفَرْتُمْ بَعْدَ إِيمانِكُمْ فَذُوقُوا الْعَذابَ بِما كُنْتُمْ تَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (400, N'وَ أَمَّا الَّذِينَ ابْيَضَّتْ وُجُوهُهُمْ فَفِي رَحْمَتِ اللَّهِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (401, N'تِلْكَ آياتُ اللَّهِ نَتْلُوها عَلَيْكَ بِالْحَقِّ وَ مَا اللَّهُ يُرِيدُ ظُلْماً لِلْعالَمِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (402, N'وَ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ إِلَى اللَّهِ تُرْجَعُ الْأُمُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (403, N'كُنْتُمْ خَيْرَ أُمَّةٍ أُخْرِجَتْ لِلنَّاسِ تَأْمُرُونَ بِالْمَعْرُوفِ وَ تَنْهَوْنَ عَنِ الْمُنْكَرِ وَ تُؤْمِنُونَ بِاللَّهِ وَ لَوْ آمَنَ أَهْلُ الْكِتابِ لَكانَ خَيْراً لَهُمْ مِنْهُمُ الْمُؤْمِنُونَ وَ أَكْثَرُهُمُ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (404, N'لَنْ يَضُرُّوكُمْ إِلاَّ أَذىً وَ إِنْ يُقاتِلُوكُمْ يُوَلُّوكُمُ الْأَدْبارَ ثُمَّ لا يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (405, N'ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ أَيْنَ ما ثُقِفُوا إِلاَّ بِحَبْلٍ مِنَ اللَّهِ وَ حَبْلٍ مِنَ النَّاسِ وَ باؤُ بِغَضَبٍ مِنَ اللَّهِ وَ ضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ذلِكَ بِأَنَّهُمْ كانُوا يَكْفُرُونَ بِآياتِ اللَّهِ وَ يَقْتُلُونَ الْأَنْبِياءَ بِغَيْرِ حَقٍّ ذلِكَ بِما عَصَوْا وَ كانُوا يَعْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (406, N'لَيْسُوا سَواءً مِنْ أَهْلِ الْكِتابِ أُمَّةٌ قائِمَةٌ يَتْلُونَ آياتِ اللَّهِ آناءَ اللَّيْلِ وَ هُمْ يَسْجُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (407, N'يُؤْمِنُونَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ يَأْمُرُونَ بِالْمَعْرُوفِ وَ يَنْهَوْنَ عَنِ الْمُنْكَرِ وَ يُسارِعُونَ فِي الْخَيْراتِ وَ أُولئِكَ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (408, N'وَ ما يَفْعَلُوا مِنْ خَيْرٍ فَلَنْ يُكْفَرُوهُ وَ اللَّهُ عَلِيمٌ بِالْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (409, N'إِنَّ الَّذِينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ أَمْوالُهُمْ وَ لا أَوْلادُهُمْ مِنَ اللَّهِ شَيْئاً وَ أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (410, N'مَثَلُ ما يُنْفِقُونَ فِي هذِهِ الْحَياةِ الدُّنْيا كَمَثَلِ رِيحٍ فِيها صِرٌّ أَصابَتْ حَرْثَ قَوْمٍ ظَلَمُوا أَنْفُسَهُمْ فَأَهْلَكَتْهُ وَ ما ظَلَمَهُمُ اللَّهُ وَ لكِنْ أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (411, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّخِذُوا بِطانَةً مِنْ دُونِكُمْ لا يَأْلُونَكُمْ خَبالاً وَدُّوا ما عَنِتُّمْ قَدْ بَدَتِ الْبَغْضاءُ مِنْ أَفْواهِهِمْ وَ ما تُخْفِي صُدُورُهُمْ أَكْبَرُ قَدْ بَيَّنَّا لَكُمُ الْآياتِ إِنْ كُنْتُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (412, N'ها أَنْتُمْ أُولاءِ تُحِبُّونَهُمْ وَ لا يُحِبُّونَكُمْ وَ تُؤْمِنُونَ بِالْكِتابِ كُلِّهِ وَ إِذا لَقُوكُمْ قالُوا آمَنَّا وَ إِذا خَلَوْا عَضُّوا عَلَيْكُمُ الْأَنامِلَ مِنَ الْغَيْظِ قُلْ مُوتُوا بِغَيْظِكُمْ إِنَّ اللَّهَ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (413, N'إِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْ وَ إِنْ تُصِبْكُمْ سَيِّئَةٌ يَفْرَحُوا بِها وَ إِنْ تَصْبِرُوا وَ تَتَّقُوا لا يَضُرُّكُمْ كَيْدُهُمْ شَيْئاً إِنَّ اللَّهَ بِما يَعْمَلُونَ مُحِيطٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (414, N'وَ إِذْ غَدَوْتَ مِنْ أَهْلِكَ تُبَوِّئُ الْمُؤْمِنِينَ مَقاعِدَ لِلْقِتالِ وَ اللَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (415, N'إِذْ هَمَّتْ طائِفَتانِ مِنْكُمْ أَنْ تَفْشَلا وَ اللَّهُ وَلِيُّهُما وَ عَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (416, N'وَ لَقَدْ نَصَرَكُمُ اللَّهُ بِبَدْرٍ وَ أَنْتُمْ أَذِلَّةٌ فَاتَّقُوا اللَّهَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (417, N'إِذْ تَقُولُ لِلْمُؤْمِنِينَ أَ لَنْ يَكْفِيَكُمْ أَنْ يُمِدَّكُمْ رَبُّكُمْ بِثَلاثَةِ آلافٍ مِنَ الْمَلائِكَةِ مُنْزَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (418, N'بَلى إِنْ تَصْبِرُوا وَ تَتَّقُوا وَ يَأْتُوكُمْ مِنْ فَوْرِهِمْ هذا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ آلافٍ مِنَ الْمَلائِكَةِ مُسَوِّمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (419, N'وَ ما جَعَلَهُ اللَّهُ إِلاَّ بُشْرى لَكُمْ وَ لِتَطْمَئِنَّ قُلُوبُكُمْ بِهِ وَ مَا النَّصْرُ إِلاَّ مِنْ عِنْدِ اللَّهِ الْعَزِيزِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (420, N'لِيَقْطَعَ طَرَفاً مِنَ الَّذِينَ كَفَرُوا أَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوا خائِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (421, N'لَيْسَ لَكَ مِنَ الْأَمْرِ شَيْ‏ءٌ أَوْ يَتُوبَ عَلَيْهِمْ أَوْ يُعَذِّبَهُمْ فَإِنَّهُمْ ظالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (422, N'وَ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ يَغْفِرُ لِمَنْ يَشاءُ وَ يُعَذِّبُ مَنْ يَشاءُ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (423, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَأْكُلُوا الرِّبَوا أَضْعافاً مُضاعَفَةً وَ اتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (424, N'وَ اتَّقُوا النَّارَ الَّتِي أُعِدَّتْ لِلْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (425, N'وَ أَطِيعُوا اللَّهَ وَ الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (426, N'وَ سارِعُوا إِلى مَغْفِرَةٍ مِنْ رَبِّكُمْ وَ جَنَّةٍ عَرْضُهَا السَّماواتُ وَ الْأَرْضُ أُعِدَّتْ لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (427, N'الَّذِينَ يُنْفِقُونَ فِي السَّرَّاءِ وَ الضَّرَّاءِ وَ الْكاظِمِينَ الْغَيْظَ وَ الْعافِينَ عَنِ النَّاسِ وَ اللَّهُ يُحِبُّ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (428, N'وَ الَّذِينَ إِذا فَعَلُوا فاحِشَةً أَوْ ظَلَمُوا أَنْفُسَهُمْ ذَكَرُوا اللَّهَ فَاسْتَغْفَرُوا لِذُنُوبِهِمْ وَ مَنْ يَغْفِرُ الذُّنُوبَ إِلاَّ اللَّهُ وَ لَمْ يُصِرُّوا عَلى ما فَعَلُوا وَ هُمْ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (429, N'أُولئِكَ جَزاؤُهُمْ مَغْفِرَةٌ مِنْ رَبِّهِمْ وَ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها وَ نِعْمَ أَجْرُ الْعامِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (430, N'قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌ فَسِيرُوا فِي الْأَرْضِ فَانْظُروا كَيْفَ كانَ عاقِبَةُ الْمُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (431, N'هذا بَيانٌ لِلنَّاسِ وَ هُدىً وَ مَوْعِظَةٌ لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (432, N'وَ لا تَهِنُوا وَ لا تَحْزَنُوا وَ أَنْتُمُ الْأَعْلَوْنَ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (433, N'إِنْ يَمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِثْلُهُ وَ تِلْكَ الْأَيَّامُ نُداوِلُها بَيْنَ النَّاسِ وَ لِيَعْلَمَ اللَّهُ الَّذِينَ آمَنُوا وَ يَتَّخِذَ مِنْكُمْ شُهَداءَ وَ اللَّهُ لا يُحِبُّ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (434, N'وَ لِيُمَحِّصَ اللَّهُ الَّذِينَ آمَنُوا وَ يَمْحَقَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (435, N'أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَ لَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جاهَدُوا مِنْكُمْ وَ يَعْلَمَ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (436, N'وَ لَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ أَنْ تَلْقَوْهُ فَقَدْ رَأَيْتُمُوهُ وَ أَنْتُمْ تَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (437, N'وَ ما مُحَمَّدٌ إِلاَّ رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ أَ فَإِنْ ماتَ أَوْ قُتِلَ انْقَلَبْتُمْ عَلى أَعْقابِكُمْ وَ مَنْ يَنْقَلِبْ عَلى عَقِبَيْهِ فَلَنْ يَضُرَّ اللَّهَ شَيْئاً وَ سَيَجْزِي اللَّهُ الشَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (438, N'وَ ما كانَ لِنَفْسٍ أَنْ تَمُوتَ إِلاَّ بِإِذْنِ اللَّهِ كِتاباً مُؤَجَّلاً وَ مَنْ يُرِدْ ثَوابَ الدُّنْيا نُؤْتِهِ مِنْها وَ مَنْ يُرِدْ ثَوابَ الْآخِرَةِ نُؤْتِهِ مِنْها وَ سَنَجْزِي الشَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (439, N'وَ كَأَيِّنْ مِنْ نَبِيٍّ قاتَلَ مَعَهُ رِبِّيُّونَ كَثِيرٌ فَما وَهَنُوا لِما أَصابَهُمْ فِي سَبِيلِ اللَّهِ وَ ما ضَعُفُوا وَ مَا اسْتَكانُوا وَ اللَّهُ يُحِبُّ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (440, N'وَ ما كانَ قَوْلَهُمْ إِلاَّ أَنْ قالُوا رَبَّنَا اغْفِرْ لَنا ذُنُوبَنا وَ إِسْرافَنا فِي أَمْرِنا وَ ثَبِّتْ أَقْدامَنا وَ انْصُرْنا عَلَى الْقَوْمِ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (441, N'فَآتاهُمُ اللَّهُ ثَوابَ الدُّنْيا وَ حُسْنَ ثَوابِ الْآخِرَةِ وَ اللَّهُ يُحِبُّ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (442, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تُطِيعُوا الَّذِينَ كَفَرُوا يَرُدُّوكُمْ عَلى أَعْقابِكُمْ فَتَنْقَلِبُوا خاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (443, N'بَلِ اللَّهُ مَوْلاكُمْ وَ هُوَ خَيْرُ النَّاصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (444, N'سَنُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ بِما أَشْرَكُوا بِاللَّهِ ما لَمْ يُنَزِّلْ بِهِ سُلْطاناً وَ مَأْواهُمُ النَّارُ وَ بِئْسَ مَثْوَى الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (445, N'وَ لَقَدْ صَدَقَكُمُ اللَّهُ وَعْدَهُ إِذْ تَحُسُّونَهُمْ بِإِذْنِهِ حَتَّى إِذا فَشِلْتُمْ وَ تَنازَعْتُمْ فِي الْأَمْرِ وَ عَصَيْتُمْ مِنْ بَعْدِ ما أَراكُمْ ما تُحِبُّونَ مِنْكُمْ مَنْ يُرِيدُ الدُّنْيا وَ مِنْكُمْ مَنْ يُرِيدُ الْآخِرَةَ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ وَ لَقَدْ عَفا عَنْكُمْ وَ اللَّهُ ذُو فَضْلٍ عَلَى الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (446, N'إِذْ تُصْعِدُونَ وَ لا تَلْوُونَ عَلى أَحَدٍ وَ الرَّسُولُ يَدْعُوكُمْ فِي أُخْراكُمْ فَأَثابَكُمْ غَمًّا بِغَمٍّ لِكَيْلا تَحْزَنُوا عَلى ما فاتَكُمْ وَ لا ما أَصابَكُمْ وَ اللَّهُ خَبِيرٌ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (447, N'ثُمَّ أَنْزَلَ عَلَيْكُمْ مِنْ بَعْدِ الْغَمِّ أَمَنَةً نُعاساً يَغْشى طائِفَةً مِنْكُمْ وَ طائِفَةٌ قَدْ أَهَمَّتْهُمْ أَنْفُسُهُمْ يَظُنُّونَ بِاللَّهِ غَيْرَ الْحَقِّ ظَنَّ الْجاهِلِيَّةِ يَقُولُونَ هَلْ لَنا مِنَ الْأَمْرِ مِنْ شَيْ‏ءٍ قُلْ إِنَّ الْأَمْرَ كُلَّهُ لِلَّهِ يُخْفُونَ فِي أَنْفُسِهِمْ ما لا يُبْدُونَ لَكَ يَقُولُونَ لَوْ كانَ لَنا مِنَ الْأَمْرِ شَيْ‏ءٌ ما قُتِلْنا هاهُنا قُلْ لَوْ كُنْتُمْ فِي بُيُوتِكُمْ لَبَرَزَ الَّذِينَ كُتِبَ عَلَيْهِمُ الْقَتْلُ إِلى مَضاجِعِهِمْ وَ لِيَبْتَلِيَ اللَّهُ ما فِي صُدُورِكُمْ وَ لِيُمَحِّصَ ما فِي قُلُوبِكُمْ وَ اللَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (448, N'إِنَّ الَّذِينَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعانِ إِنَّمَا اسْتَزَلَّهُمُ الشَّيْطانُ بِبَعْضِ ما كَسَبُوا وَ لَقَدْ عَفَا اللَّهُ عَنْهُمْ إِنَّ اللَّهَ غَفُورٌ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (449, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَكُونُوا كَالَّذِينَ كَفَرُوا وَ قالُوا لِإِخْوانِهِمْ إِذا ضَرَبُوا فِي الْأَرْضِ أَوْ كانُوا غُزًّى لَوْ كانُوا عِنْدَنا ما ماتُوا وَ ما قُتِلُوا لِيَجْعَلَ اللَّهُ ذلِكَ حَسْرَةً فِي قُلُوبِهِمْ وَ اللَّهُ يُحْيِي وَ يُمِيتُ وَ اللَّهُ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (450, N'وَ لَئِنْ قُتِلْتُمْ فِي سَبِيلِ اللَّهِ أَوْ مُتُّمْ لَمَغْفِرَةٌ مِنَ اللَّهِ وَ رَحْمَةٌ خَيْرٌ مِمَّا يَجْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (451, N'وَ لَئِنْ مُتُّمْ أَوْ قُتِلْتُمْ لَإِلَى اللَّهِ تُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (452, N'فَبِما رَحْمَةٍ مِنَ اللَّهِ لِنْتَ لَهُمْ وَ لَوْ كُنْتَ فَظًّا غَلِيظَ الْقَلْبِ لاَنْفَضُّوا مِنْ حَوْلِكَ فَاعْفُ عَنْهُمْ وَ اسْتَغْفِرْ لَهُمْ وَ شاوِرْهُمْ فِي الْأَمْرِ فَإِذا عَزَمْتَ فَتَوَكَّلْ عَلَى اللَّهِ إِنَّ اللَّهَ يُحِبُّ الْمُتَوَكِّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (453, N'إِنْ يَنْصُرْكُمُ اللَّهُ فَلا غالِبَ لَكُمْ وَ إِنْ يَخْذُلْكُمْ فَمَنْ ذَا الَّذِي يَنْصُرُكُمْ مِنْ بَعْدِهِ وَ عَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (454, N'وَ ما كانَ لِنَبِيٍّ أَنْ يَغُلَّ وَ مَنْ يَغْلُلْ يَأْتِ بِما غَلَّ يَوْمَ الْقِيامَةِ ثُمَّ تُوَفَّى كُلُّ نَفْسٍ ما كَسَبَتْ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (455, N'أَ فَمَنِ اتَّبَعَ رِضْوانَ اللَّهِ كَمَنْ باءَ بِسَخَطٍ مِنَ اللَّهِ وَ مَأْواهُ جَهَنَّمُ وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (456, N'هُمْ دَرَجاتٌ عِنْدَ اللَّهِ وَ اللَّهُ بَصِيرٌ بِما يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (457, N'لَقَدْ مَنَّ اللَّهُ عَلَى الْمُؤْمِنِينَ إِذْ بَعَثَ فِيهِمْ رَسُولاً مِنْ أَنْفُسِهِمْ يَتْلُوا عَلَيْهِمْ آياتِهِ وَ يُزَكِّيهِمْ وَ يُعَلِّمُهُمُ الْكِتابَ وَ الْحِكْمَةَ وَ إِنْ كانُوا مِنْ قَبْلُ لَفِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (458, N'أَ وَ لَمَّا أَصابَتْكُمْ مُصِيبَةٌ قَدْ أَصَبْتُمْ مِثْلَيْها قُلْتُمْ أَنَّى هذا قُلْ هُوَ مِنْ عِنْدِ أَنْفُسِكُمْ إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (459, N'وَ ما أَصابَكُمْ يَوْمَ الْتَقَى الْجَمْعانِ فَبِإِذْنِ اللَّهِ وَ لِيَعْلَمَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (460, N'وَ لِيَعْلَمَ الَّذِينَ نافَقُوا وَ قِيلَ لَهُمْ تَعالَوْا قاتِلُوا فِي سَبِيلِ اللَّهِ أَوِ ادْفَعُوا قالُوا لَوْ نَعْلَمُ قِتالاً لاَتَّبَعْناكُمْ هُمْ لِلْكُفْرِ يَوْمَئِذٍ أَقْرَبُ مِنْهُمْ لِلْإِيمانِ يَقُولُونَ بِأَفْواهِهِمْ ما لَيْسَ فِي قُلُوبِهِمْ وَ اللَّهُ أَعْلَمُ بِما يَكْتُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (461, N'الَّذِينَ قالُوا لِإِخْوانِهِمْ وَ قَعَدُوا لَوْ أَطاعُونا ما قُتِلُوا قُلْ فَادْرَؤُا عَنْ أَنْفُسِكُمُ الْمَوْتَ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (462, N'وَ لا تَحْسَبَنَّ الَّذِينَ قُتِلُوا فِي سَبِيلِ اللَّهِ أَمْواتاً بَلْ أَحْياءٌ عِنْدَ رَبِّهِمْ يُرْزَقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (463, N'فَرِحِينَ بِما آتاهُمُ اللَّهُ مِنْ فَضْلِهِ وَ يَسْتَبْشِرُونَ بِالَّذِينَ لَمْ يَلْحَقُوا بِهِمْ مِنْ خَلْفِهِمْ أَلاَّ خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (464, N'يَسْتَبْشِرُونَ بِنِعْمَةٍ مِنَ اللَّهِ وَ فَضْلٍ وَ أَنَّ اللَّهَ لا يُضِيعُ أَجْرَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (465, N'الَّذِينَ اسْتَجابُوا لِلَّهِ وَ الرَّسُولِ مِنْ بَعْدِ ما أَصابَهُمُ الْقَرْحُ لِلَّذِينَ أَحْسَنُوا مِنْهُمْ وَ اتَّقَوْا أَجْرٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (466, N'الَّذِينَ قالَ لَهُمُ النَّاسُ إِنَّ النَّاسَ قَدْ جَمَعُوا لَكُمْ فَاخْشَوْهُمْ فَزادَهُمْ إِيماناً وَ قالُوا حَسْبُنَا اللَّهُ وَ نِعْمَ الْوَكِيلُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (467, N'فَانْقَلَبُوا بِنِعْمَةٍ مِنَ اللَّهِ وَ فَضْلٍ لَمْ يَمْسَسْهُمْ سُوءٌ وَ اتَّبَعُوا رِضْوانَ اللَّهِ وَ اللَّهُ ذُو فَضْلٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (468, N'إِنَّما ذلِكُمُ الشَّيْطانُ يُخَوِّفُ أَوْلِياءَهُ فَلا تَخافُوهُمْ وَ خافُونِ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (469, N'وَ لا يَحْزُنْكَ الَّذِينَ يُسارِعُونَ فِي الْكُفْرِ إِنَّهُمْ لَنْ يَضُرُّوا اللَّهَ شَيْئاً يُرِيدُ اللَّهُ أَلاَّ يَجْعَلَ لَهُمْ حَظًّا فِي الْآخِرَةِ وَ لَهُمْ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (470, N'إِنَّ الَّذِينَ اشْتَرَوُا الْكُفْرَ بِالْإِيْمانِ لَنْ يَضُرُّوا اللَّهَ شَيْئاً وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (471, N'وَ لا يَحْسَبَنَّ الَّذِينَ كَفَرُوا أَنَّما نُمْلِي لَهُمْ خَيْرٌ لِأَنْفُسِهِمْ إِنَّما نُمْلِي لَهُمْ لِيَزْدادُوا إِثْماً وَ لَهُمْ عَذابٌ مُهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (472, N'ما كانَ اللَّهُ لِيَذَرَ الْمُؤْمِنِينَ عَلى ما أَنْتُمْ عَلَيْهِ حَتَّى يَمِيزَ الْخَبِيثَ مِنَ الطَّيِّبِ وَ ما كانَ اللَّهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَ لكِنَّ اللَّهَ يَجْتَبِي مِنْ رُسُلِهِ مَنْ يَشاءُ فَآمِنُوا بِاللَّهِ وَ رُسُلِهِ وَ إِنْ تُؤْمِنُوا وَ تَتَّقُوا فَلَكُمْ أَجْرٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (473, N'وَ لا يَحْسَبَنَّ الَّذِينَ يَبْخَلُونَ بِما آتاهُمُ اللَّهُ مِنْ فَضْلِهِ هُوَ خَيْراً لَهُمْ بَلْ هُوَ شَرٌّ لَهُمْ سَيُطَوَّقُونَ ما بَخِلُوا بِهِ يَوْمَ الْقِيامَةِ وَ لِلَّهِ مِيراثُ السَّماواتِ وَ الْأَرْضِ وَ اللَّهُ بِما تَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (474, N'لَقَدْ سَمِعَ اللَّهُ قَوْلَ الَّذِينَ قالُوا إِنَّ اللَّهَ فَقِيرٌ وَ نَحْنُ أَغْنِياءُ سَنَكْتُبُ ما قالُوا وَ قَتْلَهُمُ الْأَنْبِياءَ بِغَيْرِ حَقٍّ وَ نَقُولُ ذُوقُوا عَذابَ الْحَرِيقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (475, N'ذلِكَ بِما قَدَّمَتْ أَيْدِيكُمْ وَ أَنَّ اللَّهَ لَيْسَ بِظَلاَّمٍ لِلْعَبِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (476, N'الَّذِينَ قالُوا إِنَّ اللَّهَ عَهِدَ إِلَيْنا أَلاَّ نُؤْمِنَ لِرَسُولٍ حَتَّى يَأْتِيَنا بِقُرْبانٍ تَأْكُلُهُ النَّارُ قُلْ قَدْ جاءَكُمْ رُسُلٌ مِنْ قَبْلِي بِالْبَيِّناتِ وَ بِالَّذِي قُلْتُمْ فَلِمَ قَتَلْتُمُوهُمْ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (477, N'فَإِنْ كَذَّبُوكَ فَقَدْ كُذِّبَ رُسُلٌ مِنْ قَبْلِكَ جاؤُ بِالْبَيِّناتِ وَ الزُّبُرِ وَ الْكِتابِ الْمُنِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (478, N'كُلُّ نَفْسٍ ذائِقَةُ الْمَوْتِ وَ إِنَّما تُوَفَّوْنَ أُجُورَكُمْ يَوْمَ الْقِيامَةِ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَ أُدْخِلَ الْجَنَّةَ فَقَدْ فازَ وَ مَا الْحَياةُ الدُّنْيا إِلاَّ مَتاعُ الْغُرُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (479, N'لَتُبْلَوُنَّ فِي أَمْوالِكُمْ وَ أَنْفُسِكُمْ وَ لَتَسْمَعُنَّ مِنَ الَّذِينَ أُوتُوا الْكِتابَ مِنْ قَبْلِكُمْ وَ مِنَ الَّذِينَ أَشْرَكُوا أَذىً كَثِيراً وَ إِنْ تَصْبِرُوا وَ تَتَّقُوا فَإِنَّ ذلِكَ مِنْ عَزْمِ الْأُمُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (480, N'وَ إِذْ أَخَذَ اللَّهُ مِيثاقَ الَّذِينَ أُوتُوا الْكِتابَ لَتُبَيِّنُنَّهُ لِلنَّاسِ وَ لا تَكْتُمُونَهُ فَنَبَذُوهُ وَراءَ ظُهُورِهِمْ وَ اشْتَرَوْا بِهِ ثَمَناً قَلِيلاً فَبِئْسَ ما يَشْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (481, N'لا تَحْسَبَنَّ الَّذِينَ يَفْرَحُونَ بِما أَتَوْا وَ يُحِبُّونَ أَنْ يُحْمَدُوا بِما لَمْ يَفْعَلُوا فَلا تَحْسَبَنَّهُمْ بِمَفازَةٍ مِنَ الْعَذابِ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (482, N'وَ لِلَّهِ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (483, N'إِنَّ فِي خَلْقِ السَّماواتِ وَ الْأَرْضِ وَ اخْتِلافِ اللَّيْلِ وَ النَّهارِ لَآياتٍ لِأُولِي الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (484, N'الَّذِينَ يَذْكُرُونَ اللَّهَ قِياماً وَ قُعُوداً وَ عَلى جُنُوبِهِمْ وَ يَتَفَكَّرُونَ فِي خَلْقِ السَّماواتِ وَ الْأَرْضِ رَبَّنا ما خَلَقْتَ هذا باطِلاً سُبْحانَكَ فَقِنا عَذابَ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (485, N'رَبَّنا إِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ أَخْزَيْتَهُ وَ ما لِلظَّالِمِينَ مِنْ أَنْصارٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (486, N'رَبَّنا إِنَّنا سَمِعْنا مُنادِياً يُنادِي لِلْإِيمانِ أَنْ آمِنُوا بِرَبِّكُمْ فَآمَنَّا رَبَّنا فَاغْفِرْ لَنا ذُنُوبَنا وَ كَفِّرْ عَنَّا سَيِّئاتِنا وَ تَوَفَّنا مَعَ الْأَبْرارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (487, N'رَبَّنا وَ آتِنا ما وَعَدْتَنا عَلى رُسُلِكَ وَ لا تُخْزِنا يَوْمَ الْقِيامَةِ إِنَّكَ لا تُخْلِفُ الْمِيعادَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (488, N'فَاسْتَجابَ لَهُمْ رَبُّهُمْ أَنِّي لا أُضِيعُ عَمَلَ عامِلٍ مِنْكُمْ مِنْ ذَكَرٍ أَوْ أُنْثى بَعْضُكُمْ مِنْ بَعْضٍ فَالَّذِينَ هاجَرُوا وَ أُخْرِجُوا مِنْ دِيارِهِمْ وَ أُوذُوا فِي سَبِيلِي وَ قاتَلُوا وَ قُتِلُوا لَأُكَفِّرَنَّ عَنْهُمْ سَيِّئاتِهِمْ وَ لَأُدْخِلَنَّهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ ثَواباً مِنْ عِنْدِ اللَّهِ وَ اللَّهُ عِنْدَهُ حُسْنُ الثَّوابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (489, N'لا يَغُرَّنَّكَ تَقَلُّبُ الَّذِينَ كَفَرُوا فِي الْبِلادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (490, N'مَتاعٌ قَلِيلٌ ثُمَّ مَأْواهُمْ جَهَنَّمُ وَ بِئْسَ الْمِهادُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (491, N'لكِنِ الَّذِينَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها نُزُلاً مِنْ عِنْدِ اللَّهِ وَ ما عِنْدَ اللَّهِ خَيْرٌ لِلْأَبْرارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (492, N'وَ إِنَّ مِنْ أَهْلِ الْكِتابِ لَمَنْ يُؤْمِنُ بِاللَّهِ وَ ما أُنْزِلَ إِلَيْكُمْ وَ ما أُنْزِلَ إِلَيْهِمْ خاشِعِينَ لِلَّهِ لا يَشْتَرُونَ بِآياتِ اللَّهِ ثَمَناً قَلِيلاً أُولئِكَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ إِنَّ اللَّهَ سَرِيعُ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (493, N'يا أَيُّهَا الَّذِينَ آمَنُوا اصْبِرُوا وَ صابِرُوا وَ رابِطُوا وَ اتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (494, N'يا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ مِنْ نَفْسٍ واحِدَةٍ وَ خَلَقَ مِنْها زَوْجَها وَ بَثَّ مِنْهُما رِجالاً كَثِيراً وَ نِساءً وَ اتَّقُوا اللَّهَ الَّذِي تَسائَلُونَ بِهِ وَ الْأَرْحامَ إِنَّ اللَّهَ كانَ عَلَيْكُمْ رَقِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (495, N'وَ آتُوا الْيَتامى أَمْوالَهُمْ وَ لا تَتَبَدَّلُوا الْخَبِيثَ بِالطَّيِّبِ وَ لا تَأْكُلُوا أَمْوالَهُمْ إِلى أَمْوالِكُمْ إِنَّهُ كانَ حُوباً كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (496, N'وَ إِنْ خِفْتُمْ أَلاَّ تُقْسِطُوا فِي الْيَتامى فَانْكِحُوا ما طابَ لَكُمْ مِنَ النِّساءِ مَثْنى وَ ثُلاثَ وَ رُباعَ فَإِنْ خِفْتُمْ أَلاَّ تَعْدِلُوا فَواحِدَةً أَوْ ما مَلَكَتْ أَيْمانُكُمْ ذلِكَ أَدْنى أَلاَّ تَعُولُوا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (497, N'وَ آتُوا النِّساءَ صَدُقاتِهِنَّ نِحْلَةً فَإِنْ طِبْنَ لَكُمْ عَنْ شَيْ‏ءٍ مِنْهُ نَفْساً فَكُلُوهُ هَنِيئاً مَرِيئاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (498, N'وَ لا تُؤْتُوا السُّفَهاءَ أَمْوالَكُمُ الَّتِي جَعَلَ اللَّهُ لَكُمْ قِياماً وَ ارْزُقُوهُمْ فِيها وَ اكْسُوهُمْ وَ قُولُوا لَهُمْ قَوْلاً مَعْرُوفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (499, N'وَ ابْتَلُوا الْيَتامى حَتَّى إِذا بَلَغُوا النِّكاحَ فَإِنْ آنَسْتُمْ مِنْهُمْ رُشْداً فَادْفَعُوا إِلَيْهِمْ أَمْوالَهُمْ وَ لا تَأْكُلُوها إِسْرافاً وَ بِداراً أَنْ يَكْبَرُوا وَ مَنْ كانَ غَنِيًّا فَلْيَسْتَعْفِفْ وَ مَنْ كانَ فَقِيراً فَلْيَأْكُلْ بِالْمَعْرُوفِ فَإِذا دَفَعْتُمْ إِلَيْهِمْ أَمْوالَهُمْ فَأَشْهِدُوا عَلَيْهِمْ وَ كَفى بِاللَّهِ حَسِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (500, N'لِلرِّجالِ نَصِيبٌ مِمَّا تَرَكَ الْوالِدانِ وَ الْأَقْرَبُونَ وَ لِلنِّساءِ نَصِيبٌ مِمَّا تَرَكَ الْوالِدانِ وَ الْأَقْرَبُونَ مِمَّا قَلَّ مِنْهُ أَوْ كَثُرَ نَصِيباً مَفْرُوضاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (501, N'وَ إِذا حَضَرَ الْقِسْمَةَ أُولُوا الْقُرْبى وَ الْيَتامى وَ الْمَساكِينُ فَارْزُقُوهُمْ مِنْهُ وَ قُولُوا لَهُمْ قَوْلاً مَعْرُوفاً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (502, N'وَ لْيَخْشَ الَّذِينَ لَوْ تَرَكُوا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعافاً خافُوا عَلَيْهِمْ فَلْيَتَّقُوا اللَّهَ وَ لْيَقُولُوا قَوْلاً سَدِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (503, N'إِنَّ الَّذِينَ يَأْكُلُونَ أَمْوالَ الْيَتامى ظُلْماً إِنَّما يَأْكُلُونَ فِي بُطُونِهِمْ ناراً وَ سَيَصْلَوْنَ سَعِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (504, N'يُوصِيكُمُ اللَّهُ فِي أَوْلادِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الْأُنْثَيَيْنِ فَإِنْ كُنَّ نِساءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثا ما تَرَكَ وَ إِنْ كانَتْ واحِدَةً فَلَهَا النِّصْفُ وَ لِأَبَوَيْهِ لِكُلِّ واحِدٍ مِنْهُمَا السُّدُسُ مِمَّا تَرَكَ إِنْ كانَ لَهُ وَلَدٌ فَإِنْ لَمْ يَكُنْ لَهُ وَلَدٌ وَ وَرِثَهُ أَبَواهُ فَلِأُمِّهِ الثُّلُثُ فَإِنْ كانَ لَهُ إِخْوَةٌ فَلِأُمِّهِ السُّدُسُ مِنْ بَعْدِ وَصِيَّةٍ يُوصِي بِها أَوْ دَيْنٍ آباؤُكُمْ وَ أَبْناؤُكُمْ لا تَدْرُونَ أَيُّهُمْ أَقْرَبُ لَكُمْ نَفْعاً فَرِيضَةً مِنَ اللَّهِ إِنَّ اللَّهَ كانَ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (505, N'وَ لَكُمْ نِصْفُ ما تَرَكَ أَزْواجُكُمْ إِنْ لَمْ يَكُنْ لَهُنَّ وَلَدٌ فَإِنْ كانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ مِنْ بَعْدِ وَصِيَّةٍ يُوصِينَ بِها أَوْ دَيْنٍ وَ لَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ إِنْ لَمْ يَكُنْ لَكُمْ وَلَدٌ فَإِنْ كانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُمْ مِنْ بَعْدِ وَصِيَّةٍ تُوصُونَ بِها أَوْ دَيْنٍ وَ إِنْ كانَ رَجُلٌ يُورَثُ كَلالَةً أَوِ امْرَأَةٌ وَ لَهُ أَخٌ أَوْ أُخْتٌ فَلِكُلِّ واحِدٍ مِنْهُمَا السُّدُسُ فَإِنْ كانُوا أَكْثَرَ مِنْ ذلِكَ فَهُمْ شُرَكاءُ فِي الثُّلُثِ مِنْ بَعْدِ وَصِيَّةٍ يُوصى بِها أَوْ دَيْنٍ غَيْرَ مُضَارٍّ وَصِيَّةً مِنَ اللَّهِ وَ اللَّهُ عَلِيمٌ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (506, N'تِلْكَ حُدُودُ اللَّهِ وَ مَنْ يُطِعِ اللَّهَ وَ رَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها وَ ذلِكَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (507, N'وَ مَنْ يَعْصِ اللَّهَ وَ رَسُولَهُ وَ يَتَعَدَّ حُدُودَهُ يُدْخِلْهُ ناراً خالِداً فِيها وَ لَهُ عَذابٌ مُهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (508, N'وَ اللاَّتِي يَأْتِينَ الْفاحِشَةَ مِنْ نِسائِكُمْ فَاسْتَشْهِدُوا عَلَيْهِنَّ أَرْبَعَةً مِنْكُمْ فَإِنْ شَهِدُوا فَأَمْسِكُوهُنَّ فِي الْبُيُوتِ حَتَّى يَتَوَفَّاهُنَّ الْمَوْتُ أَوْ يَجْعَلَ اللَّهُ لَهُنَّ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (509, N'وَ الَّذانِ يَأْتِيانِها مِنْكُمْ فَآذُوهُما فَإِنْ تابا وَ أَصْلَحا فَأَعْرِضُوا عَنْهُما إِنَّ اللَّهَ كانَ تَوَّاباً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (510, N'إِنَّمَا التَّوْبَةُ عَلَى اللَّهِ لِلَّذِينَ يَعْمَلُونَ السُّوءَ بِجَهالَةٍ ثُمَّ يَتُوبُونَ مِنْ قَرِيبٍ فَأُولئِكَ يَتُوبُ اللَّهُ عَلَيْهِمْ وَ كانَ اللَّهُ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (511, N'وَ لَيْسَتِ التَّوْبَةُ لِلَّذِينَ يَعْمَلُونَ السَّيِّئاتِ حَتَّى إِذا حَضَرَ أَحَدَهُمُ الْمَوْتُ قالَ إِنِّي تُبْتُ الْآنَ وَ لاَ الَّذِينَ يَمُوتُونَ وَ هُمْ كُفَّارٌ أُولئِكَ أَعْتَدْنا لَهُمْ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (512, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا يَحِلُّ لَكُمْ أَنْ تَرِثُوا النِّساءَ كَرْهاً وَ لا تَعْضُلُوهُنَّ لِتَذْهَبُوا بِبَعْضِ ما آتَيْتُمُوهُنَّ إِلاَّ أَنْ يَأْتِينَ بِفاحِشَةٍ مُبَيِّنَةٍ وَ عاشِرُوهُنَّ بِالْمَعْرُوفِ فَإِنْ كَرِهْتُمُوهُنَّ فَعَسى أَنْ تَكْرَهُوا شَيْئاً وَ يَجْعَلَ اللَّهُ فِيهِ خَيْراً كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (513, N'وَ إِنْ أَرَدْتُمُ اسْتِبْدالَ زَوْجٍ مَكانَ زَوْجٍ وَ آتَيْتُمْ إِحْداهُنَّ قِنْطاراً فَلا تَأْخُذُوا مِنْهُ شَيْئاً أَ تَأْخُذُونَهُ بُهْتاناً وَ إِثْماً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (514, N'وَ كَيْفَ تَأْخُذُونَهُ وَ قَدْ أَفْضى بَعْضُكُمْ إِلى بَعْضٍ وَ أَخَذْنَ مِنْكُمْ مِيثاقاً غَلِيظاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (515, N'وَ لا تَنْكِحُوا ما نَكَحَ آباؤُكُمْ مِنَ النِّساءِ إِلاَّ ما قَدْ سَلَفَ إِنَّهُ كانَ فاحِشَةً وَ مَقْتاً وَ ساءَ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (516, N'حُرِّمَتْ عَلَيْكُمْ أُمَّهاتُكُمْ وَ بَناتُكُمْ وَ أَخَواتُكُمْ وَ عَمَّاتُكُمْ وَ خالاتُكُمْ وَ بَناتُ الْأَخِ وَ بَناتُ الْأُخْتِ وَ أُمَّهاتُكُمُ اللاَّتِي أَرْضَعْنَكُمْ وَ أَخَواتُكُمْ مِنَ الرَّضاعَةِ وَ أُمَّهاتُ نِسائِكُمْ وَ رَبائِبُكُمُ اللاَّتِي فِي حُجُورِكُمْ مِنْ نِسائِكُمُ اللاَّتِي دَخَلْتُمْ بِهِنَّ فَإِنْ لَمْ تَكُونُوا دَخَلْتُمْ بِهِنَّ فَلا جُناحَ عَلَيْكُمْ وَ حَلائِلُ أَبْنائِكُمُ الَّذِينَ مِنْ أَصْلابِكُمْ وَ أَنْ تَجْمَعُوا بَيْنَ الْأُخْتَيْنِ إِلاَّ ما قَدْ سَلَفَ إِنَّ اللَّهَ كانَ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (517, N'وَ الْمُحْصَناتُ مِنَ النِّساءِ إِلاَّ ما مَلَكَتْ أَيْمانُكُمْ كِتابَ اللَّهِ عَلَيْكُمْ وَ أُحِلَّ لَكُمْ ما وَراءَ ذلِكُمْ أَنْ تَبْتَغُوا بِأَمْوالِكُمْ مُحْصِنِينَ غَيْرَ مُسافِحِينَ فَمَا اسْتَمْتَعْتُمْ بِهِ مِنْهُنَّ فَآتُوهُنَّ أُجُورَهُنَّ فَرِيضَةً وَ لا جُناحَ عَلَيْكُمْ فِيما تَراضَيْتُمْ بِهِ مِنْ بَعْدِ الْفَرِيضَةِ إِنَّ اللَّهَ كانَ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (518, N'وَ مَنْ لَمْ يَسْتَطِعْ مِنْكُمْ طَوْلاً أَنْ يَنْكِحَ الْمُحْصَناتِ الْمُؤْمِناتِ فَمِنْ ما مَلَكَتْ أَيْمانُكُمْ مِنْ فَتَياتِكُمُ الْمُؤْمِناتِ وَ اللَّهُ أَعْلَمُ بِإِيمانِكُمْ بَعْضُكُمْ مِنْ بَعْضٍ فَانْكِحُوهُنَّ بِإِذْنِ أَهْلِهِنَّ وَ آتُوهُنَّ أُجُورَهُنَّ بِالْمَعْرُوفِ مُحْصَناتٍ غَيْرَ مُسافِحاتٍ وَ لا مُتَّخِذاتِ أَخْدانٍ فَإِذا أُحْصِنَّ فَإِنْ أَتَيْنَ بِفاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ ما عَلَى الْمُحْصَناتِ مِنَ الْعَذابِ ذلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْ وَ أَنْ تَصْبِرُوا خَيْرٌ لَكُمْ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (519, N'يُرِيدُ اللَّهُ لِيُبَيِّنَ لَكُمْ وَ يَهْدِيَكُمْ سُنَنَ الَّذِينَ مِنْ قَبْلِكُمْ وَ يَتُوبَ عَلَيْكُمْ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (520, N'وَ اللَّهُ يُرِيدُ أَنْ يَتُوبَ عَلَيْكُمْ وَ يُرِيدُ الَّذِينَ يَتَّبِعُونَ الشَّهَواتِ أَنْ تَمِيلُوا مَيْلاً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (521, N'يُرِيدُ اللَّهُ أَنْ يُخَفِّفَ عَنْكُمْ وَ خُلِقَ الْإِنْسانُ ضَعِيفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (522, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَأْكُلُوا أَمْوالَكُمْ بَيْنَكُمْ بِالْباطِلِ إِلاَّ أَنْ تَكُونَ تِجارَةً عَنْ تَراضٍ مِنْكُمْ وَ لا تَقْتُلُوا أَنْفُسَكُمْ إِنَّ اللَّهَ كانَ بِكُمْ رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (523, N'وَ مَنْ يَفْعَلْ ذلِكَ عُدْواناً وَ ظُلْماً فَسَوْفَ نُصْلِيهِ ناراً وَ كانَ ذلِكَ عَلَى اللَّهِ يَسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (524, N'إِنْ تَجْتَنِبُوا كَبائِرَ ما تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّئاتِكُمْ وَ نُدْخِلْكُمْ مُدْخَلاً كَرِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (525, N'وَ لا تَتَمَنَّوْا ما فَضَّلَ اللَّهُ بِهِ بَعْضَكُمْ عَلى بَعْضٍ لِلرِّجالِ نَصِيبٌ مِمَّا اكْتَسَبُوا وَ لِلنِّساءِ نَصِيبٌ مِمَّا اكْتَسَبْنَ وَ سْئَلُوا اللَّهَ مِنْ فَضْلِهِ إِنَّ اللَّهَ كانَ بِكُلِّ شَيْ‏ءٍ عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (526, N'وَ لِكُلٍّ جَعَلْنا مَوالِيَ مِمَّا تَرَكَ الْوالِدانِ وَ الْأَقْرَبُونَ وَ الَّذِينَ عَقَدَتْ أَيْمانُكُمْ فَآتُوهُمْ نَصِيبَهُمْ إِنَّ اللَّهَ كانَ عَلى كُلِّ شَيْ‏ءٍ شَهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (527, N'الرِّجالُ قَوَّامُونَ عَلَى النِّساءِ بِما فَضَّلَ اللَّهُ بَعْضَهُمْ عَلى بَعْضٍ وَ بِما أَنْفَقُوا مِنْ أَمْوالِهِمْ فَالصَّالِحاتُ قانِتاتٌ حافِظاتٌ لِلْغَيْبِ بِما حَفِظَ اللَّهُ وَ اللاَّتِي تَخافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَ اهْجُرُوهُنَّ فِي الْمَضاجِعِ وَ اضْرِبُوهُنَّ فَإِنْ أَطَعْنَكُمْ فَلا تَبْغُوا عَلَيْهِنَّ سَبِيلاً إِنَّ اللَّهَ كانَ عَلِيًّا كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (528, N'وَ إِنْ خِفْتُمْ شِقاقَ بَيْنِهِما فَابْعَثُوا حَكَماً مِنْ أَهْلِهِ وَ حَكَماً مِنْ أَهْلِها إِنْ يُرِيدا إِصْلاحاً يُوَفِّقِ اللَّهُ بَيْنَهُما إِنَّ اللَّهَ كانَ عَلِيماً خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (529, N'وَ اعْبُدُوا اللَّهَ وَ لا تُشْرِكُوا بِهِ شَيْئاً وَ بِالْوالِدَيْنِ إِحْساناً وَ بِذِي الْقُرْبى وَ الْيَتامى وَ الْمَساكِينِ وَ الْجارِ ذِي الْقُرْبى وَ الْجارِ الْجُنُبِ وَ الصَّاحِبِ بِالْجَنْبِ وَ ابْنِ السَّبِيلِ وَ ما مَلَكَتْ أَيْمانُكُمْ إِنَّ اللَّهَ لا يُحِبُّ مَنْ كانَ مُخْتالاً فَخُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (530, N'الَّذِينَ يَبْخَلُونَ وَ يَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَ يَكْتُمُونَ ما آتاهُمُ اللَّهُ مِنْ فَضْلِهِ وَ أَعْتَدْنا لِلْكافِرِينَ عَذاباً مُهِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (531, N'وَ الَّذِينَ يُنْفِقُونَ أَمْوالَهُمْ رِئاءَ النَّاسِ وَ لا يُؤْمِنُونَ بِاللَّهِ وَ لا بِالْيَوْمِ الْآخِرِ وَ مَنْ يَكُنِ الشَّيْطانُ لَهُ قَرِيناً فَساءَ قَرِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (532, N'وَ ما ذا عَلَيْهِمْ لَوْ آمَنُوا بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ أَنْفَقُوا مِمَّا رَزَقَهُمُ اللَّهُ وَ كانَ اللَّهُ بِهِمْ عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (533, N'إِنَّ اللَّهَ لا يَظْلِمُ مِثْقالَ ذَرَّةٍ وَ إِنْ تَكُ حَسَنَةً يُضاعِفْها وَ يُؤْتِ مِنْ لَدُنْهُ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (534, N'فَكَيْفَ إِذا جِئْنا مِنْ كُلِّ أُمَّةٍ بِشَهِيدٍ وَ جِئْنا بِكَ عَلى هؤُلاءِ شَهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (535, N'يَوْمَئِذٍ يَوَدُّ الَّذِينَ كَفَرُوا وَ عَصَوُا الرَّسُولَ لَوْ تُسَوَّى بِهِمُ الْأَرْضُ وَ لا يَكْتُمُونَ اللَّهَ حَدِيثاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (536, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَقْرَبُوا الصَّلاةَ وَ أَنْتُمْ سُكارى حَتَّى تَعْلَمُوا ما تَقُولُونَ وَ لا جُنُباً إِلاَّ عابِرِي سَبِيلٍ حَتَّى تَغْتَسِلُوا وَ إِنْ كُنْتُمْ مَرْضى أَوْ عَلى سَفَرٍ أَوْ جاءَ أَحَدٌ مِنْكُمْ مِنَ الْغائِطِ أَوْ لامَسْتُمُ النِّساءَ فَلَمْ تَجِدُوا ماءً فَتَيَمَّمُوا صَعِيداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَ أَيْدِيكُمْ إِنَّ اللَّهَ كانَ عَفُوًّا غَفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (537, N'أَ لَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيباً مِنَ الْكِتابِ يَشْتَرُونَ الضَّلالَةَ وَ يُرِيدُونَ أَنْ تَضِلُّوا السَّبِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (538, N'وَ اللَّهُ أَعْلَمُ بِأَعْدائِكُمْ وَ كَفى بِاللَّهِ وَلِيًّا وَ كَفى بِاللَّهِ نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (539, N'مِنَ الَّذِينَ هادُوا يُحَرِّفُونَ الْكَلِمَ عَنْ مَواضِعِهِ وَ يَقُولُونَ سَمِعْنا وَ عَصَيْنا وَ اسْمَعْ غَيْرَ مُسْمَعٍ وَ راعِنا لَيًّا بِأَلْسِنَتِهِمْ وَ طَعْناً فِي الدِّينِ وَ لَوْ أَنَّهُمْ قالُوا سَمِعْنا وَ أَطَعْنا وَ اسْمَعْ وَ انْظُرْنا لَكانَ خَيْراً لَهُمْ وَ أَقْوَمَ وَ لكِنْ لَعَنَهُمُ اللَّهُ بِكُفْرِهِمْ فَلا يُؤْمِنُونَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (540, N'يا أَيُّهَا الَّذِينَ أُوتُوا الْكِتابَ آمِنُوا بِما نَزَّلْنا مُصَدِّقاً لِما مَعَكُمْ مِنْ قَبْلِ أَنْ نَطْمِسَ وُجُوهاً فَنَرُدَّها عَلى أَدْبارِها أَوْ نَلْعَنَهُمْ كَما لَعَنَّا أَصْحابَ السَّبْتِ وَ كانَ أَمْرُ اللَّهِ مَفْعُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (541, N'إِنَّ اللَّهَ لا يَغْفِرُ أَنْ يُشْرَكَ بِهِ وَ يَغْفِرُ ما دُونَ ذلِكَ لِمَنْ يَشاءُ وَ مَنْ يُشْرِكْ بِاللَّهِ فَقَدِ افْتَرى إِثْماً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (542, N'أَ لَمْ تَرَ إِلَى الَّذِينَ يُزَكُّونَ أَنْفُسَهُمْ بَلِ اللَّهُ يُزَكِّي مَنْ يَشاءُ وَ لا يُظْلَمُونَ فَتِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (543, N'انْظُرْ كَيْفَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ وَ كَفى بِهِ إِثْماً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (544, N'أَ لَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيباً مِنَ الْكِتابِ يُؤْمِنُونَ بِالْجِبْتِ وَ الطَّاغُوتِ وَ يَقُولُونَ لِلَّذِينَ كَفَرُوا هؤُلاءِ أَهْدى مِنَ الَّذِينَ آمَنُوا سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (545, N'أُولئِكَ الَّذِينَ لَعَنَهُمُ اللَّهُ وَ مَنْ يَلْعَنِ اللَّهُ فَلَنْ تَجِدَ لَهُ نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (546, N'أَمْ لَهُمْ نَصِيبٌ مِنَ الْمُلْكِ فَإِذاً لا يُؤْتُونَ النَّاسَ نَقِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (547, N'أَمْ يَحْسُدُونَ النَّاسَ عَلى ما آتاهُمُ اللَّهُ مِنْ فَضْلِهِ فَقَدْ آتَيْنا آلَ إِبْراهِيمَ الْكِتابَ وَ الْحِكْمَةَ وَ آتَيْناهُمْ مُلْكاً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (548, N'فَمِنْهُمْ مَنْ آمَنَ بِهِ وَ مِنْهُمْ مَنْ صَدَّ عَنْهُ وَ كَفى بِجَهَنَّمَ سَعِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (549, N'إِنَّ الَّذِينَ كَفَرُوا بِآياتِنا سَوْفَ نُصْلِيهِمْ ناراً كُلَّما نَضِجَتْ جُلُودُهُمْ بَدَّلْناهُمْ جُلُوداً غَيْرَها لِيَذُوقُوا الْعَذابَ إِنَّ اللَّهَ كانَ عَزِيزاً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (550, N'وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها أَبَداً لَهُمْ فِيها أَزْواجٌ مُطَهَّرَةٌ وَ نُدْخِلُهُمْ ظِلاًّ ظَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (551, N'إِنَّ اللَّهَ يَأْمُرُكُمْ أَنْ تُؤَدُّوا الْأَماناتِ إِلى أَهْلِها وَ إِذا حَكَمْتُمْ بَيْنَ النَّاسِ أَنْ تَحْكُمُوا بِالْعَدْلِ إِنَّ اللَّهَ نِعِمَّا يَعِظُكُمْ بِهِ إِنَّ اللَّهَ كانَ سَمِيعاً بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (552, N'يا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَ أَطِيعُوا الرَّسُولَ وَ أُولِي الْأَمْرِ مِنْكُمْ فَإِنْ تَنازَعْتُمْ فِي شَيْ‏ءٍ فَرُدُّوهُ إِلَى اللَّهِ وَ الرَّسُولِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ ذلِكَ خَيْرٌ وَ أَحْسَنُ تَأْوِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (553, N'أَ لَمْ تَرَ إِلَى الَّذِينَ يَزْعُمُونَ أَنَّهُمْ آمَنُوا بِما أُنْزِلَ إِلَيْكَ وَ ما أُنْزِلَ مِنْ قَبْلِكَ يُرِيدُونَ أَنْ يَتَحاكَمُوا إِلَى الطَّاغُوتِ وَ قَدْ أُمِرُوا أَنْ يَكْفُرُوا بِهِ وَ يُرِيدُ الشَّيْطانُ أَنْ يُضِلَّهُمْ ضَلالاً بَعِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (554, N'وَ إِذا قِيلَ لَهُمْ تَعالَوْا إِلى ما أَنْزَلَ اللَّهُ وَ إِلَى الرَّسُولِ رَأَيْتَ الْمُنافِقِينَ يَصُدُّونَ عَنْكَ صُدُوداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (555, N'فَكَيْفَ إِذا أَصابَتْهُمْ مُصِيبَةٌ بِما قَدَّمَتْ أَيْدِيهِمْ ثُمَّ جاؤُكَ يَحْلِفُونَ بِاللَّهِ إِنْ أَرَدْنا إِلاَّ إِحْساناً وَ تَوْفِيقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (556, N'أُولئِكَ الَّذِينَ يَعْلَمُ اللَّهُ ما فِي قُلُوبِهِمْ فَأَعْرِضْ عَنْهُمْ وَ عِظْهُمْ وَ قُلْ لَهُمْ فِي أَنْفُسِهِمْ قَوْلاً بَلِيغاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (557, N'وَ ما أَرْسَلْنا مِنْ رَسُولٍ إِلاَّ لِيُطاعَ بِإِذْنِ اللَّهِ وَ لَوْ أَنَّهُمْ إِذْ ظَلَمُوا أَنْفُسَهُمْ جاؤُكَ فَاسْتَغْفَرُوا اللَّهَ وَ اسْتَغْفَرَ لَهُمُ الرَّسُولُ لَوَجَدُوا اللَّهَ تَوَّاباً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (558, N'فَلا وَ رَبِّكَ لا يُؤْمِنُونَ حَتَّى يُحَكِّمُوكَ فِيما شَجَرَ بَيْنَهُمْ ثُمَّ لا يَجِدُوا فِي أَنْفُسِهِمْ حَرَجاً مِمَّا قَضَيْتَ وَ يُسَلِّمُوا تَسْلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (559, N'وَ لَوْ أَنَّا كَتَبْنا عَلَيْهِمْ أَنِ اقْتُلُوا أَنْفُسَكُمْ أَوِ اخْرُجُوا مِنْ دِيارِكُمْ ما فَعَلُوهُ إِلاَّ قَلِيلٌ مِنْهُمْ وَ لَوْ أَنَّهُمْ فَعَلُوا ما يُوعَظُونَ بِهِ لَكانَ خَيْراً لَهُمْ وَ أَشَدَّ تَثْبِيتاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (560, N'وَ إِذاً لَآتَيْناهُمْ مِنْ لَدُنَّا أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (561, N'وَ لَهَدَيْناهُمْ صِراطاً مُسْتَقِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (562, N'وَ مَنْ يُطِعِ اللَّهَ وَ الرَّسُولَ فَأُولئِكَ مَعَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِمْ مِنَ النَّبِيِّينَ وَ الصِّدِّيقِينَ وَ الشُّهَداءِ وَ الصَّالِحِينَ وَ حَسُنَ أُولئِكَ رَفِيقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (563, N'ذلِكَ الْفَضْلُ مِنَ اللَّهِ وَ كَفى بِاللَّهِ عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (564, N'يا أَيُّهَا الَّذِينَ آمَنُوا خُذُوا حِذْرَكُمْ فَانْفِرُوا ثُباتٍ أَوِ انْفِرُوا جَمِيعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (565, N'وَ إِنَّ مِنْكُمْ لَمَنْ لَيُبَطِّئَنَّ فَإِنْ أَصابَتْكُمْ مُصِيبَةٌ قالَ قَدْ أَنْعَمَ اللَّهُ عَلَيَّ إِذْ لَمْ أَكُنْ مَعَهُمْ شَهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (566, N'وَ لَئِنْ أَصابَكُمْ فَضْلٌ مِنَ اللَّهِ لَيَقُولَنَّ كَأَنْ لَمْ تَكُنْ بَيْنَكُمْ وَ بَيْنَهُ مَوَدَّةٌ يا لَيْتَنِي كُنْتُ مَعَهُمْ فَأَفُوزَ فَوْزاً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (567, N'فَلْيُقاتِلْ فِي سَبِيلِ اللَّهِ الَّذِينَ يَشْرُونَ الْحَياةَ الدُّنْيا بِالْآخِرَةِ وَ مَنْ يُقاتِلْ فِي سَبِيلِ اللَّهِ فَيُقْتَلْ أَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيهِ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (568, N'وَ ما لَكُمْ لا تُقاتِلُونَ فِي سَبِيلِ اللَّهِ وَ الْمُسْتَضْعَفِينَ مِنَ الرِّجالِ وَ النِّساءِ وَ الْوِلْدانِ الَّذِينَ يَقُولُونَ رَبَّنا أَخْرِجْنا مِنْ هذِهِ الْقَرْيَةِ الظَّالِمِ أَهْلُها وَ اجْعَلْ لَنا مِنْ لَدُنْكَ وَلِيًّا وَ اجْعَلْ لَنا مِنْ لَدُنْكَ نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (569, N'الَّذِينَ آمَنُوا يُقاتِلُونَ فِي سَبِيلِ اللَّهِ وَ الَّذِينَ كَفَرُوا يُقاتِلُونَ فِي سَبِيلِ الطَّاغُوتِ فَقاتِلُوا أَوْلِياءَ الشَّيْطانِ إِنَّ كَيْدَ الشَّيْطانِ كانَ ضَعِيفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (570, N'أَ لَمْ تَرَ إِلَى الَّذِينَ قِيلَ لَهُمْ كُفُّوا أَيْدِيَكُمْ وَ أَقِيمُوا الصَّلاةَ وَ آتُوا الزَّكاةَ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتالُ إِذا فَرِيقٌ مِنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللَّهِ أَوْ أَشَدَّ خَشْيَةً وَ قالُوا رَبَّنا لِمَ كَتَبْتَ عَلَيْنَا الْقِتالَ لَوْ لا أَخَّرْتَنا إِلى أَجَلٍ قَرِيبٍ قُلْ مَتاعُ الدُّنْيا قَلِيلٌ وَ الْآخِرَةُ خَيْرٌ لِمَنِ اتَّقى وَ لا تُظْلَمُونَ فَتِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (571, N'أَيْنَما تَكُونُوا يُدْرِكْكُمُ الْمَوْتُ وَ لَوْ كُنْتُمْ فِي بُرُوجٍ مُشَيَّدَةٍ وَ إِنْ تُصِبْهُمْ حَسَنَةٌ يَقُولُوا هذِهِ مِنْ عِنْدِ اللَّهِ وَ إِنْ تُصِبْهُمْ سَيِّئَةٌ يَقُولُوا هذِهِ مِنْ عِنْدِكَ قُلْ كُلٌّ مِنْ عِنْدِ اللَّهِ فَما لِهؤُلاءِ الْقَوْمِ لا يَكادُونَ يَفْقَهُونَ حَدِيثاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (572, N'ما أَصابَكَ مِنْ حَسَنَةٍ فَمِنَ اللَّهِ وَ ما أَصابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَفْسِكَ وَ أَرْسَلْناكَ لِلنَّاسِ رَسُولاً وَ كَفى بِاللَّهِ شَهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (573, N'مَنْ يُطِعِ الرَّسُولَ فَقَدْ أَطاعَ اللَّهَ وَ مَنْ تَوَلَّى فَما أَرْسَلْناكَ عَلَيْهِمْ حَفِيظاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (574, N'وَ يَقُولُونَ طاعَةٌ فَإِذا بَرَزُوا مِنْ عِنْدِكَ بَيَّتَ طائِفَةٌ مِنْهُمْ غَيْرَ الَّذِي تَقُولُ وَ اللَّهُ يَكْتُبُ ما يُبَيِّتُونَ فَأَعْرِضْ عَنْهُمْ وَ تَوَكَّلْ عَلَى اللَّهِ وَ كَفى بِاللَّهِ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (575, N'أَ فَلا يَتَدَبَّرُونَ الْقُرْآنَ وَ لَوْ كانَ مِنْ عِنْدِ غَيْرِ اللَّهِ لَوَجَدُوا فِيهِ اخْتِلافاً كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (576, N'وَ إِذا جاءَهُمْ أَمْرٌ مِنَ الْأَمْنِ أَوِ الْخَوْفِ أَذاعُوا بِهِ وَ لَوْ رَدُّوهُ إِلَى الرَّسُولِ وَ إِلى أُولِي الْأَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذِينَ يَسْتَنْبِطُونَهُ مِنْهُمْ وَ لَوْ لا فَضْلُ اللَّهِ عَلَيْكُمْ وَ رَحْمَتُهُ لاَتَّبَعْتُمُ الشَّيْطانَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (577, N'فَقاتِلْ فِي سَبِيلِ اللَّهِ لا تُكَلَّفُ إِلاَّ نَفْسَكَ وَ حَرِّضِ الْمُؤْمِنِينَ عَسَى اللَّهُ أَنْ يَكُفَّ بَأْسَ الَّذِينَ كَفَرُوا وَ اللَّهُ أَشَدُّ بَأْساً وَ أَشَدُّ تَنْكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (578, N'مَنْ يَشْفَعْ شَفاعَةً حَسَنَةً يَكُنْ لَهُ نَصِيبٌ مِنْها وَ مَنْ يَشْفَعْ شَفاعَةً سَيِّئَةً يَكُنْ لَهُ كِفْلٌ مِنْها وَ كانَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ مُقِيتاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (579, N'وَ إِذا حُيِّيتُمْ بِتَحِيَّةٍ فَحَيُّوا بِأَحْسَنَ مِنْها أَوْ رُدُّوها إِنَّ اللَّهَ كانَ عَلى كُلِّ شَيْ‏ءٍ حَسِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (580, N'اللَّهُ لا إِلهَ إِلاَّ هُوَ لَيَجْمَعَنَّكُمْ إِلى يَوْمِ الْقِيامَةِ لا رَيْبَ فِيهِ وَ مَنْ أَصْدَقُ مِنَ اللَّهِ حَدِيثاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (581, N'فَما لَكُمْ فِي الْمُنافِقِينَ فِئَتَيْنِ وَ اللَّهُ أَرْكَسَهُمْ بِما كَسَبُوا أَ تُرِيدُونَ أَنْ تَهْدُوا مَنْ أَضَلَّ اللَّهُ وَ مَنْ يُضْلِلِ اللَّهُ فَلَنْ تَجِدَ لَهُ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (582, N'وَدُّوا لَوْ تَكْفُرُونَ كَما كَفَرُوا فَتَكُونُونَ سَواءً فَلا تَتَّخِذُوا مِنْهُمْ أَوْلِياءَ حَتَّى يُهاجِرُوا فِي سَبِيلِ اللَّهِ فَإِنْ تَوَلَّوْا فَخُذُوهُمْ وَ اقْتُلُوهُمْ حَيْثُ وَجَدْتُمُوهُمْ وَ لا تَتَّخِذُوا مِنْهُمْ وَلِيًّا وَ لا نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (583, N'إِلاَّ الَّذِينَ يَصِلُونَ إِلى قَوْمٍ بَيْنَكُمْ وَ بَيْنَهُمْ مِيثاقٌ أَوْ جاؤُكُمْ حَصِرَتْ صُدُورُهُمْ أَنْ يُقاتِلُوكُمْ أَوْ يُقاتِلُوا قَوْمَهُمْ وَ لَوْ شاءَ اللَّهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقاتَلُوكُمْ فَإِنِ اعْتَزَلُوكُمْ فَلَمْ يُقاتِلُوكُمْ وَ أَلْقَوْا إِلَيْكُمُ السَّلَمَ فَما جَعَلَ اللَّهُ لَكُمْ عَلَيْهِمْ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (584, N'سَتَجِدُونَ آخَرِينَ يُرِيدُونَ أَنْ يَأْمَنُوكُمْ وَ يَأْمَنُوا قَوْمَهُمْ كُلَّما رُدُّوا إِلَى الْفِتْنَةِ أُرْكِسُوا فِيها فَإِنْ لَمْ يَعْتَزِلُوكُمْ وَ يُلْقُوا إِلَيْكُمُ السَّلَمَ وَ يَكُفُّوا أَيْدِيَهُمْ فَخُذُوهُمْ وَ اقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَ أُولئِكُمْ جَعَلْنا لَكُمْ عَلَيْهِمْ سُلْطاناً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (585, N'وَ ما كانَ لِمُؤْمِنٍ أَنْ يَقْتُلَ مُؤْمِناً إِلاَّ خَطَأً وَ مَنْ قَتَلَ مُؤْمِناً خَطَأً فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَ دِيَةٌ مُسَلَّمَةٌ إِلى أَهْلِهِ إِلاَّ أَنْ يَصَّدَّقُوا فَإِنْ كانَ مِنْ قَوْمٍ عَدُوٍّ لَكُمْ وَ هُوَ مُؤْمِنٌ فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَ إِنْ كانَ مِنْ قَوْمٍ بَيْنَكُمْ وَ بَيْنَهُمْ مِيثاقٌ فَدِيَةٌ مُسَلَّمَةٌ إِلى أَهْلِهِ وَ تَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ فَمَنْ لَمْ يَجِدْ فَصِيامُ شَهْرَيْنِ مُتَتابِعَيْنِ تَوْبَةً مِنَ اللَّهِ وَ كانَ اللَّهُ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (586, N'وَ مَنْ يَقْتُلْ مُؤْمِناً مُتَعَمِّداً فَجَزاؤُهُ جَهَنَّمُ خالِداً فِيها وَ غَضِبَ اللَّهُ عَلَيْهِ وَ لَعَنَهُ وَ أَعَدَّ لَهُ عَذاباً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (587, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا ضَرَبْتُمْ فِي سَبِيلِ اللَّهِ فَتَبَيَّنُوا وَ لا تَقُولُوا لِمَنْ أَلْقى إِلَيْكُمُ السَّلامَ لَسْتَ مُؤْمِناً تَبْتَغُونَ عَرَضَ الْحَياةِ الدُّنْيا فَعِنْدَ اللَّهِ مَغانِمُ كَثِيرَةٌ كَذلِكَ كُنْتُمْ مِنْ قَبْلُ فَمَنَّ اللَّهُ عَلَيْكُمْ فَتَبَيَّنُوا إِنَّ اللَّهَ كانَ بِما تَعْمَلُونَ خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (588, N'لا يَسْتَوِي الْقاعِدُونَ مِنَ الْمُؤْمِنِينَ غَيْرُ أُولِي الضَّرَرِ وَ الْمُجاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوالِهِمْ وَ أَنْفُسِهِمْ فَضَّلَ اللَّهُ الْمُجاهِدِينَ بِأَمْوالِهِمْ وَ أَنْفُسِهِمْ عَلَى الْقاعِدِينَ دَرَجَةً وَ كُلاًّ وَعَدَ اللَّهُ الْحُسْنى وَ فَضَّلَ اللَّهُ الْمُجاهِدِينَ عَلَى الْقاعِدِينَ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (589, N'دَرَجاتٍ مِنْهُ وَ مَغْفِرَةً وَ رَحْمَةً وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (590, N'إِنَّ الَّذِينَ تَوَفَّاهُمُ الْمَلائِكَةُ ظالِمِي أَنْفُسِهِمْ قالُوا فِيمَ كُنْتُمْ قالُوا كُنَّا مُسْتَضْعَفِينَ فِي الْأَرْضِ قالُوا أَ لَمْ تَكُنْ أَرْضُ اللَّهِ واسِعَةً فَتُهاجِرُوا فِيها فَأُولئِكَ مَأْواهُمْ جَهَنَّمُ وَ ساءَتْ مَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (591, N'إِلاَّ الْمُسْتَضْعَفِينَ مِنَ الرِّجالِ وَ النِّساءِ وَ الْوِلْدانِ لا يَسْتَطِيعُونَ حِيلَةً وَ لا يَهْتَدُونَ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (592, N'فَأُولئِكَ عَسَى اللَّهُ أَنْ يَعْفُوَ عَنْهُمْ وَ كانَ اللَّهُ عَفُوًّا غَفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (593, N'وَ مَنْ يُهاجِرْ فِي سَبِيلِ اللَّهِ يَجِدْ فِي الْأَرْضِ مُراغَماً كَثِيراً وَ سَعَةً وَ مَنْ يَخْرُجْ مِنْ بَيْتِهِ مُهاجِراً إِلَى اللَّهِ وَ رَسُولِهِ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ أَجْرُهُ عَلَى اللَّهِ وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (594, N'وَ إِذا ضَرَبْتُمْ فِي الْأَرْضِ فَلَيْسَ عَلَيْكُمْ جُناحٌ أَنْ تَقْصُرُوا مِنَ الصَّلاةِ إِنْ خِفْتُمْ أَنْ يَفْتِنَكُمُ الَّذِينَ كَفَرُوا إِنَّ الْكافِرِينَ كانُوا لَكُمْ عَدُوًّا مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (595, N'وَ إِذا كُنْتَ فِيهِمْ فَأَقَمْتَ لَهُمُ الصَّلاةَ فَلْتَقُمْ طائِفَةٌ مِنْهُمْ مَعَكَ وَ لْيَأْخُذُوا أَسْلِحَتَهُمْ فَإِذا سَجَدُوا فَلْيَكُونُوا مِنْ وَرائِكُمْ وَ لْتَأْتِ طائِفَةٌ أُخْرى لَمْ يُصَلُّوا فَلْيُصَلُّوا مَعَكَ وَ لْيَأْخُذُوا حِذْرَهُمْ وَ أَسْلِحَتَهُمْ وَدَّ الَّذِينَ كَفَرُوا لَوْ تَغْفُلُونَ عَنْ أَسْلِحَتِكُمْ وَ أَمْتِعَتِكُمْ فَيَمِيلُونَ عَلَيْكُمْ مَيْلَةً واحِدَةً وَ لا جُناحَ عَلَيْكُمْ إِنْ كانَ بِكُمْ أَذىً مِنْ مَطَرٍ أَوْ كُنْتُمْ مَرْضى أَنْ تَضَعُوا أَسْلِحَتَكُمْ وَ خُذُوا حِذْرَكُمْ إِنَّ اللَّهَ أَعَدَّ لِلْكافِرِينَ عَذاباً مُهِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (596, N'فَإِذا قَضَيْتُمُ الصَّلاةَ فَاذْكُرُوا اللَّهَ قِياماً وَ قُعُوداً وَ عَلى جُنُوبِكُمْ فَإِذَا اطْمَأْنَنْتُمْ فَأَقِيمُوا الصَّلاةَ إِنَّ الصَّلاةَ كانَتْ عَلَى الْمُؤْمِنِينَ كِتاباً مَوْقُوتاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (597, N'وَ لا تَهِنُوا فِي ابْتِغاءِ الْقَوْمِ إِنْ تَكُونُوا تَأْلَمُونَ فَإِنَّهُمْ يَأْلَمُونَ كَما تَأْلَمُونَ وَ تَرْجُونَ مِنَ اللَّهِ ما لا يَرْجُونَ وَ كانَ اللَّهُ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (598, N'إِنَّا أَنْزَلْنا إِلَيْكَ الْكِتابَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِما أَراكَ اللَّهُ وَ لا تَكُنْ لِلْخائِنِينَ خَصِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (599, N'وَ اسْتَغْفِرِ اللَّهَ إِنَّ اللَّهَ كانَ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (600, N'وَ لا تُجادِلْ عَنِ الَّذِينَ يَخْتانُونَ أَنْفُسَهُمْ إِنَّ اللَّهَ لا يُحِبُّ مَنْ كانَ خَوَّاناً أَثِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (601, N'يَسْتَخْفُونَ مِنَ النَّاسِ وَ لا يَسْتَخْفُونَ مِنَ اللَّهِ وَ هُوَ مَعَهُمْ إِذْ يُبَيِّتُونَ ما لا يَرْضى مِنَ الْقَوْلِ وَ كانَ اللَّهُ بِما يَعْمَلُونَ مُحِيطاً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (602, N'ها أَنْتُمْ هؤُلاءِ جادَلْتُمْ عَنْهُمْ فِي الْحَياةِ الدُّنْيا فَمَنْ يُجادِلُ اللَّهَ عَنْهُمْ يَوْمَ الْقِيامَةِ أَمْ مَنْ يَكُونُ عَلَيْهِمْ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (603, N'وَ مَنْ يَعْمَلْ سُوءاً أَوْ يَظْلِمْ نَفْسَهُ ثُمَّ يَسْتَغْفِرِ اللَّهَ يَجِدِ اللَّهَ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (604, N'وَ مَنْ يَكْسِبْ إِثْماً فَإِنَّما يَكْسِبُهُ عَلى نَفْسِهِ وَ كانَ اللَّهُ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (605, N'وَ مَنْ يَكْسِبْ خَطِيئَةً أَوْ إِثْماً ثُمَّ يَرْمِ بِهِ بَرِيئاً فَقَدِ احْتَمَلَ بُهْتاناً وَ إِثْماً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (606, N'وَ لَوْ لا فَضْلُ اللَّهِ عَلَيْكَ وَ رَحْمَتُهُ لَهَمَّتْ طائِفَةٌ مِنْهُمْ أَنْ يُضِلُّوكَ وَ ما يُضِلُّونَ إِلاَّ أَنْفُسَهُمْ وَ ما يَضُرُّونَكَ مِنْ شَيْ‏ءٍ وَ أَنْزَلَ اللَّهُ عَلَيْكَ الْكِتابَ وَ الْحِكْمَةَ وَ عَلَّمَكَ ما لَمْ تَكُنْ تَعْلَمُ وَ كانَ فَضْلُ اللَّهِ عَلَيْكَ عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (607, N'لا خَيْرَ فِي كَثِيرٍ مِنْ نَجْواهُمْ إِلاَّ مَنْ أَمَرَ بِصَدَقَةٍ أَوْ مَعْرُوفٍ أَوْ إِصْلاحٍ بَيْنَ النَّاسِ وَ مَنْ يَفْعَلْ ذلِكَ ابْتِغاءَ مَرْضاتِ اللَّهِ فَسَوْفَ نُؤْتِيهِ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (608, N'وَ مَنْ يُشاقِقِ الرَّسُولَ مِنْ بَعْدِ ما تَبَيَّنَ لَهُ الْهُدى وَ يَتَّبِعْ غَيْرَ سَبِيلِ الْمُؤْمِنِينَ نُوَلِّهِ ما تَوَلَّى وَ نُصْلِهِ جَهَنَّمَ وَ ساءَتْ مَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (609, N'إِنَّ اللَّهَ لا يَغْفِرُ أَنْ يُشْرَكَ بِهِ وَ يَغْفِرُ ما دُونَ ذلِكَ لِمَنْ يَشاءُ وَ مَنْ يُشْرِكْ بِاللَّهِ فَقَدْ ضَلَّ ضَلالاً بَعِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (610, N'إِنْ يَدْعُونَ مِنْ دُونِهِ إِلاَّ إِناثاً وَ إِنْ يَدْعُونَ إِلاَّ شَيْطاناً مَرِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (611, N'لَعَنَهُ اللَّهُ وَ قالَ لَأَتَّخِذَنَّ مِنْ عِبادِكَ نَصِيباً مَفْرُوضاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (612, N'وَ لَأُضِلَّنَّهُمْ وَ لَأُمَنِّيَنَّهُمْ وَ لَآمُرَنَّهُمْ فَلَيُبَتِّكُنَّ آذانَ الْأَنْعامِ وَ لَآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللَّهِ وَ مَنْ يَتَّخِذِ الشَّيْطانَ وَلِيًّا مِنْ دُونِ اللَّهِ فَقَدْ خَسِرَ خُسْراناً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (613, N'يَعِدُهُمْ وَ يُمَنِّيهِمْ وَ ما يَعِدُهُمُ الشَّيْطانُ إِلاَّ غُرُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (614, N'أُولئِكَ مَأْواهُمْ جَهَنَّمُ وَ لا يَجِدُونَ عَنْها مَحِيصاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (615, N'وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها أَبَداً وَعْدَ اللَّهِ حَقًّا وَ مَنْ أَصْدَقُ مِنَ اللَّهِ قِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (616, N'لَيْسَ بِأَمانِيِّكُمْ وَ لا أَمانِيِّ أَهْلِ الْكِتابِ مَنْ يَعْمَلْ سُوءاً يُجْزَ بِهِ وَ لا يَجِدْ لَهُ مِنْ دُونِ اللَّهِ وَلِيًّا وَ لا نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (617, N'وَ مَنْ يَعْمَلْ مِنَ الصَّالِحاتِ مِنْ ذَكَرٍ أَوْ أُنْثى وَ هُوَ مُؤْمِنٌ فَأُولئِكَ يَدْخُلُونَ الْجَنَّةَ وَ لا يُظْلَمُونَ نَقِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (618, N'وَ مَنْ أَحْسَنُ دِيناً مِمَّنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَ هُوَ مُحْسِنٌ وَ اتَّبَعَ مِلَّةَ إِبْراهِيمَ حَنِيفاً وَ اتَّخَذَ اللَّهُ إِبْراهِيمَ خَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (619, N'وَ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ كانَ اللَّهُ بِكُلِّ شَيْ‏ءٍ مُحِيطاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (620, N'وَ يَسْتَفْتُونَكَ فِي النِّساءِ قُلِ اللَّهُ يُفْتِيكُمْ فِيهِنَّ وَ ما يُتْلى عَلَيْكُمْ فِي الْكِتابِ فِي يَتامَى النِّساءِ اللاَّتِي لا تُؤْتُونَهُنَّ ما كُتِبَ لَهُنَّ وَ تَرْغَبُونَ أَنْ تَنْكِحُوهُنَّ وَ الْمُسْتَضْعَفِينَ مِنَ الْوِلْدانِ وَ أَنْ تَقُومُوا لِلْيَتامى بِالْقِسْطِ وَ ما تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ كانَ بِهِ عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (621, N'وَ إِنِ امْرَأَةٌ خافَتْ مِنْ بَعْلِها نُشُوزاً أَوْ إِعْراضاً فَلا جُناحَ عَلَيْهِما أَنْ يُصْلِحا بَيْنَهُما صُلْحاً وَ الصُّلْحُ خَيْرٌ وَ أُحْضِرَتِ الْأَنْفُسُ الشُّحَّ وَ إِنْ تُحْسِنُوا وَ تَتَّقُوا فَإِنَّ اللَّهَ كانَ بِما تَعْمَلُونَ خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (622, N'وَ لَنْ تَسْتَطِيعُوا أَنْ تَعْدِلُوا بَيْنَ النِّساءِ وَ لَوْ حَرَصْتُمْ فَلا تَمِيلُوا كُلَّ الْمَيْلِ فَتَذَرُوها كَالْمُعَلَّقَةِ وَ إِنْ تُصْلِحُوا وَ تَتَّقُوا فَإِنَّ اللَّهَ كانَ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (623, N'وَ إِنْ يَتَفَرَّقا يُغْنِ اللَّهُ كُلاًّ مِنْ سَعَتِهِ وَ كانَ اللَّهُ واسِعاً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (624, N'وَ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ لَقَدْ وَصَّيْنَا الَّذِينَ أُوتُوا الْكِتابَ مِنْ قَبْلِكُمْ وَ إِيَّاكُمْ أَنِ اتَّقُوا اللَّهَ وَ إِنْ تَكْفُرُوا فَإِنَّ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ كانَ اللَّهُ غَنِيًّا حَمِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (625, N'وَ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ كَفى بِاللَّهِ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (626, N'إِنْ يَشَأْ يُذْهِبْكُمْ أَيُّهَا النَّاسُ وَ يَأْتِ بِآخَرِينَ وَ كانَ اللَّهُ عَلى ذلِكَ قَدِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (627, N'مَنْ كانَ يُرِيدُ ثَوابَ الدُّنْيا فَعِنْدَ اللَّهِ ثَوابُ الدُّنْيا وَ الْآخِرَةِ وَ كانَ اللَّهُ سَمِيعاً بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (628, N'يا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوَّامِينَ بِالْقِسْطِ شُهَداءَ لِلَّهِ وَ لَوْ عَلى أَنْفُسِكُمْ أَوِ الْوالِدَيْنِ وَ الْأَقْرَبِينَ إِنْ يَكُنْ غَنِيًّا أَوْ فَقِيراً فَاللَّهُ أَوْلى بِهِما فَلا تَتَّبِعُوا الْهَوى أَنْ تَعْدِلُوا وَ إِنْ تَلْوُوا أَوْ تُعْرِضُوا فَإِنَّ اللَّهَ كانَ بِما تَعْمَلُونَ خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (629, N'يا أَيُّهَا الَّذِينَ آمَنُوا آمِنُوا بِاللَّهِ وَ رَسُولِهِ وَ الْكِتابِ الَّذِي نَزَّلَ عَلى رَسُولِهِ وَ الْكِتابِ الَّذِي أَنْزَلَ مِنْ قَبْلُ وَ مَنْ يَكْفُرْ بِاللَّهِ وَ مَلائِكَتِهِ وَ كُتُبِهِ وَ رُسُلِهِ وَ الْيَوْمِ الْآخِرِ فَقَدْ ضَلَّ ضَلالاً بَعِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (630, N'إِنَّ الَّذِينَ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ ازْدادُوا كُفْراً لَمْ يَكُنِ اللَّهُ لِيَغْفِرَ لَهُمْ وَ لا لِيَهْدِيَهُمْ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (631, N'بَشِّرِ الْمُنافِقِينَ بِأَنَّ لَهُمْ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (632, N'الَّذِينَ يَتَّخِذُونَ الْكافِرِينَ أَوْلِياءَ مِنْ دُونِ الْمُؤْمِنِينَ أَ يَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ فَإِنَّ الْعِزَّةَ لِلَّهِ جَمِيعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (633, N'وَ قَدْ نَزَّلَ عَلَيْكُمْ فِي الْكِتابِ أَنْ إِذا سَمِعْتُمْ آياتِ اللَّهِ يُكْفَرُ بِها وَ يُسْتَهْزَأُ بِها فَلا تَقْعُدُوا مَعَهُمْ حَتَّى يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ إِنَّكُمْ إِذاً مِثْلُهُمْ إِنَّ اللَّهَ جامِعُ الْمُنافِقِينَ وَ الْكافِرِينَ فِي جَهَنَّمَ جَمِيعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (634, N'الَّذِينَ يَتَرَبَّصُونَ بِكُمْ فَإِنْ كانَ لَكُمْ فَتْحٌ مِنَ اللَّهِ قالُوا أَ لَمْ نَكُنْ مَعَكُمْ وَ إِنْ كانَ لِلْكافِرِينَ نَصِيبٌ قالُوا أَ لَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَ نَمْنَعْكُمْ مِنَ الْمُؤْمِنِينَ فَاللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيامَةِ وَ لَنْ يَجْعَلَ اللَّهُ لِلْكافِرِينَ عَلَى الْمُؤْمِنِينَ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (635, N'إِنَّ الْمُنافِقِينَ يُخادِعُونَ اللَّهَ وَ هُوَ خادِعُهُمْ وَ إِذا قامُوا إِلَى الصَّلاةِ قامُوا كُسالى يُراؤُنَ النَّاسَ وَ لا يَذْكُرُونَ اللَّهَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (636, N'مُذَبْذَبِينَ بَيْنَ ذلِكَ لا إِلى هؤُلاءِ وَ لا إِلى هؤُلاءِ وَ مَنْ يُضْلِلِ اللَّهُ فَلَنْ تَجِدَ لَهُ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (637, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّخِذُوا الْكافِرِينَ أَوْلِياءَ مِنْ دُونِ الْمُؤْمِنِينَ أَ تُرِيدُونَ أَنْ تَجْعَلُوا لِلَّهِ عَلَيْكُمْ سُلْطاناً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (638, N'إِنَّ الْمُنافِقِينَ فِي الدَّرْكِ الْأَسْفَلِ مِنَ النَّارِ وَ لَنْ تَجِدَ لَهُمْ نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (639, N'إِلاَّ الَّذِينَ تابُوا وَ أَصْلَحُوا وَ اعْتَصَمُوا بِاللَّهِ وَ أَخْلَصُوا دِينَهُمْ لِلَّهِ فَأُولئِكَ مَعَ الْمُؤْمِنِينَ وَ سَوْفَ يُؤْتِ اللَّهُ الْمُؤْمِنِينَ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (640, N'ما يَفْعَلُ اللَّهُ بِعَذابِكُمْ إِنْ شَكَرْتُمْ وَ آمَنْتُمْ وَ كانَ اللَّهُ شاكِراً عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (641, N'لا يُحِبُّ اللَّهُ الْجَهْرَ بِالسُّوءِ مِنَ الْقَوْلِ إِلاَّ مَنْ ظُلِمَ وَ كانَ اللَّهُ سَمِيعاً عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (642, N'إِنْ تُبْدُوا خَيْراً أَوْ تُخْفُوهُ أَوْ تَعْفُوا عَنْ سُوءٍ فَإِنَّ اللَّهَ كانَ عَفُوًّا قَدِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (643, N'إِنَّ الَّذِينَ يَكْفُرُونَ بِاللَّهِ وَ رُسُلِهِ وَ يُرِيدُونَ أَنْ يُفَرِّقُوا بَيْنَ اللَّهِ وَ رُسُلِهِ وَ يَقُولُونَ نُؤْمِنُ بِبَعْضٍ وَ نَكْفُرُ بِبَعْضٍ وَ يُرِيدُونَ أَنْ يَتَّخِذُوا بَيْنَ ذلِكَ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (644, N'أُولئِكَ هُمُ الْكافِرُونَ حَقًّا وَ أَعْتَدْنا لِلْكافِرِينَ عَذاباً مُهِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (645, N'وَ الَّذِينَ آمَنُوا بِاللَّهِ وَ رُسُلِهِ وَ لَمْ يُفَرِّقُوا بَيْنَ أَحَدٍ مِنْهُمْ أُولئِكَ سَوْفَ يُؤْتِيهِمْ أُجُورَهُمْ وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (646, N'يَسْئَلُكَ أَهْلُ الْكِتابِ أَنْ تُنَزِّلَ عَلَيْهِمْ كِتاباً مِنَ السَّماءِ فَقَدْ سَأَلُوا مُوسى أَكْبَرَ مِنْ ذلِكَ فَقالُوا أَرِنَا اللَّهَ جَهْرَةً فَأَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْ ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْ بَعْدِ ما جاءَتْهُمُ الْبَيِّناتُ فَعَفَوْنا عَنْ ذلِكَ وَ آتَيْنا مُوسى سُلْطاناً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (647, N'وَ رَفَعْنا فَوْقَهُمُ الطُّورَ بِمِيثاقِهِمْ وَ قُلْنا لَهُمُ ادْخُلُوا الْبابَ سُجَّداً وَ قُلْنا لَهُمْ لا تَعْدُوا فِي السَّبْتِ وَ أَخَذْنا مِنْهُمْ مِيثاقاً غَلِيظاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (648, N'فَبِما نَقْضِهِمْ مِيثاقَهُمْ وَ كُفْرِهِمْ بِآياتِ اللَّهِ وَ قَتْلِهِمُ الْأَنْبِياءَ بِغَيْرِ حَقٍّ وَ قَوْلِهِمْ قُلُوبُنا غُلْفٌ بَلْ طَبَعَ اللَّهُ عَلَيْها بِكُفْرِهِمْ فَلا يُؤْمِنُونَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (649, N'وَ بِكُفْرِهِمْ وَ قَوْلِهِمْ عَلى مَرْيَمَ بُهْتاناً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (650, N'وَ قَوْلِهِمْ إِنَّا قَتَلْنَا الْمَسِيحَ عِيسَى ابْنَ مَرْيَمَ رَسُولَ اللَّهِ وَ ما قَتَلُوهُ وَ ما صَلَبُوهُ وَ لكِنْ شُبِّهَ لَهُمْ وَ إِنَّ الَّذِينَ اخْتَلَفُوا فِيهِ لَفِي شَكٍّ مِنْهُ ما لَهُمْ بِهِ مِنْ عِلْمٍ إِلاَّ اتِّباعَ الظَّنِّ وَ ما قَتَلُوهُ يَقِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (651, N'بَلْ رَفَعَهُ اللَّهُ إِلَيْهِ وَ كانَ اللَّهُ عَزِيزاً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (652, N'وَ إِنْ مِنْ أَهْلِ الْكِتابِ إِلاَّ لَيُؤْمِنَنَّ بِهِ قَبْلَ مَوْتِهِ وَ يَوْمَ الْقِيامَةِ يَكُونُ عَلَيْهِمْ شَهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (653, N'فَبِظُلْمٍ مِنَ الَّذِينَ هادُوا حَرَّمْنا عَلَيْهِمْ طَيِّباتٍ أُحِلَّتْ لَهُمْ وَ بِصَدِّهِمْ عَنْ سَبِيلِ اللَّهِ كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (654, N'وَ أَخْذِهِمُ الرِّبَوا وَ قَدْ نُهُوا عَنْهُ وَ أَكْلِهِمْ أَمْوالَ النَّاسِ بِالْباطِلِ وَ أَعْتَدْنا لِلْكافِرِينَ مِنْهُمْ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (655, N'لكِنِ الرَّاسِخُونَ فِي الْعِلْمِ مِنْهُمْ وَ الْمُؤْمِنُونَ يُؤْمِنُونَ بِما أُنْزِلَ إِلَيْكَ وَ ما أُنْزِلَ مِنْ قَبْلِكَ وَ الْمُقِيمِينَ الصَّلاةَ وَ الْمُؤْتُونَ الزَّكاةَ وَ الْمُؤْمِنُونَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ أُولئِكَ سَنُؤْتِيهِمْ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (656, N'إِنَّا أَوْحَيْنا إِلَيْكَ كَما أَوْحَيْنا إِلى نُوحٍ وَ النَّبِيِّينَ مِنْ بَعْدِهِ وَ أَوْحَيْنا إِلى إِبْراهِيمَ وَ إِسْماعِيلَ وَ إِسْحاقَ وَ يَعْقُوبَ وَ الْأَسْباطِ وَ عِيسى وَ أَيُّوبَ وَ يُونُسَ وَ هارُونَ وَ سُلَيْمانَ وَ آتَيْنا داوُدَ زَبُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (657, N'وَ رُسُلاً قَدْ قَصَصْناهُمْ عَلَيْكَ مِنْ قَبْلُ وَ رُسُلاً لَمْ نَقْصُصْهُمْ عَلَيْكَ وَ كَلَّمَ اللَّهُ مُوسى تَكْلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (658, N'رُسُلاً مُبَشِّرِينَ وَ مُنْذِرِينَ لِئَلاَّ يَكُونَ لِلنَّاسِ عَلَى اللَّهِ حُجَّةٌ بَعْدَ الرُّسُلِ وَ كانَ اللَّهُ عَزِيزاً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (659, N'لكِنِ اللَّهُ يَشْهَدُ بِما أَنْزَلَ إِلَيْكَ أَنْزَلَهُ بِعِلْمِهِ وَ الْمَلائِكَةُ يَشْهَدُونَ وَ كَفى بِاللَّهِ شَهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (660, N'إِنَّ الَّذِينَ كَفَرُوا وَ صَدُّوا عَنْ سَبِيلِ اللَّهِ قَدْ ضَلُّوا ضَلالاً بَعِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (661, N'إِنَّ الَّذِينَ كَفَرُوا وَ ظَلَمُوا لَمْ يَكُنِ اللَّهُ لِيَغْفِرَ لَهُمْ وَ لا لِيَهْدِيَهُمْ طَرِيقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (662, N'إِلاَّ طَرِيقَ جَهَنَّمَ خالِدِينَ فِيها أَبَداً وَ كانَ ذلِكَ عَلَى اللَّهِ يَسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (663, N'يا أَيُّهَا النَّاسُ قَدْ جاءَكُمُ الرَّسُولُ بِالْحَقِّ مِنْ رَبِّكُمْ فَآمِنُوا خَيْراً لَكُمْ وَ إِنْ تَكْفُرُوا فَإِنَّ لِلَّهِ ما فِي السَّماواتِ وَ الْأَرْضِ وَ كانَ اللَّهُ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (664, N'يا أَهْلَ الْكِتابِ لا تَغْلُوا فِي دِينِكُمْ وَ لا تَقُولُوا عَلَى اللَّهِ إِلاَّ الْحَقَّ إِنَّمَا الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ رَسُولُ اللَّهِ وَ كَلِمَتُهُ أَلْقاها إِلى مَرْيَمَ وَ رُوحٌ مِنْهُ فَآمِنُوا بِاللَّهِ وَ رُسُلِهِ وَ لا تَقُولُوا ثَلاثَةٌ انْتَهُوا خَيْراً لَكُمْ إِنَّمَا اللَّهُ إِلهٌ واحِدٌ سُبْحانَهُ أَنْ يَكُونَ لَهُ وَلَدٌ لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ كَفى بِاللَّهِ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (665, N'لَنْ يَسْتَنْكِفَ الْمَسِيحُ أَنْ يَكُونَ عَبْداً لِلَّهِ وَ لاَ الْمَلائِكَةُ الْمُقَرَّبُونَ وَ مَنْ يَسْتَنْكِفْ عَنْ عِبادَتِهِ وَ يَسْتَكْبِرْ فَسَيَحْشُرُهُمْ إِلَيْهِ جَمِيعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (666, N'فَأَمَّا الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَ يَزِيدُهُمْ مِنْ فَضْلِهِ وَ أَمَّا الَّذِينَ اسْتَنْكَفُوا وَ اسْتَكْبَرُوا فَيُعَذِّبُهُمْ عَذاباً أَلِيماً وَ لا يَجِدُونَ لَهُمْ مِنْ دُونِ اللَّهِ وَلِيًّا وَ لا نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (667, N'يا أَيُّهَا النَّاسُ قَدْ جاءَكُمْ بُرْهانٌ مِنْ رَبِّكُمْ وَ أَنْزَلْنا إِلَيْكُمْ نُوراً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (668, N'فَأَمَّا الَّذِينَ آمَنُوا بِاللَّهِ وَ اعْتَصَمُوا بِهِ فَسَيُدْخِلُهُمْ فِي رَحْمَةٍ مِنْهُ وَ فَضْلٍ وَ يَهْدِيهِمْ إِلَيْهِ صِراطاً مُسْتَقِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (669, N'يَسْتَفْتُونَكَ قُلِ اللَّهُ يُفْتِيكُمْ فِي الْكَلالَةِ إِنِ امْرُؤٌ هَلَكَ لَيْسَ لَهُ وَلَدٌ وَ لَهُ أُخْتٌ فَلَها نِصْفُ ما تَرَكَ وَ هُوَ يَرِثُها إِنْ لَمْ يَكُنْ لَها وَلَدٌ فَإِنْ كانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثانِ مِمَّا تَرَكَ وَ إِنْ كانُوا إِخْوَةً رِجالاً وَ نِساءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْأُنْثَيَيْنِ يُبَيِّنُ اللَّهُ لَكُمْ أَنْ تَضِلُّوا وَ اللَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (670, N'يا أَيُّهَا الَّذِينَ آمَنُوا أَوْفُوا بِالْعُقُودِ أُحِلَّتْ لَكُمْ بَهِيمَةُ الْأَنْعامِ إِلاَّ ما يُتْلى عَلَيْكُمْ غَيْرَ مُحِلِّي الصَّيْدِ وَ أَنْتُمْ حُرُمٌ إِنَّ اللَّهَ يَحْكُمُ ما يُرِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (671, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تُحِلُّوا شَعائِرَ اللَّهِ وَ لاَ الشَّهْرَ الْحَرامَ وَ لاَ الْهَدْيَ وَ لاَ الْقَلائِدَ وَ لاَ آمِّينَ الْبَيْتَ الْحَرامَ يَبْتَغُونَ فَضْلاً مِنْ رَبِّهِمْ وَ رِضْواناً وَ إِذا حَلَلْتُمْ فَاصْطادُوا وَ لا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ أَنْ صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرامِ أَنْ تَعْتَدُوا وَ تَعاوَنُوا عَلَى الْبِرِّ وَ التَّقْوى وَ لا تَعاوَنُوا عَلَى الْإِثْمِ وَ الْعُدْوانِ وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (672, N'حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَ الدَّمُ وَ لَحْمُ الْخِنْزِيرِ وَ ما أُهِلَّ لِغَيْرِ اللَّهِ بِهِ وَ الْمُنْخَنِقَةُ وَ الْمَوْقُوذَةُ وَ الْمُتَرَدِّيَةُ وَ النَّطِيحَةُ وَ ما أَكَلَ السَّبُعُ إِلاَّ ما ذَكَّيْتُمْ وَ ما ذُبِحَ عَلَى النُّصُبِ وَ أَنْ تَسْتَقْسِمُوا بِالْأَزْلامِ ذلِكُمْ فِسْقٌ الْيَوْمَ يَئِسَ الَّذِينَ كَفَرُوا مِنْ دِينِكُمْ فَلا تَخْشَوْهُمْ وَ اخْشَوْنِ الْيَوْمَ أَكْمَلْتُ لَكُمْ دِينَكُمْ وَ أَتْمَمْتُ عَلَيْكُمْ نِعْمَتِي وَ رَضِيتُ لَكُمُ الْإِسْلامَ دِيناً فَمَنِ اضْطُرَّ فِي مَخْمَصَةٍ غَيْرَ مُتَجانِفٍ لِإِثْمٍ فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (673, N'يَسْئَلُونَكَ ما ذا أُحِلَّ لَهُمْ قُلْ أُحِلَّ لَكُمُ الطَّيِّباتُ وَ ما عَلَّمْتُمْ مِنَ الْجَوارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ اللَّهُ فَكُلُوا مِمَّا أَمْسَكْنَ عَلَيْكُمْ وَ اذْكُرُوا اسْمَ اللَّهِ عَلَيْهِ وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ سَرِيعُ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (674, N'الْيَوْمَ أُحِلَّ لَكُمُ الطَّيِّباتُ وَ طَعامُ الَّذِينَ أُوتُوا الْكِتابَ حِلٌّ لَكُمْ وَ طَعامُكُمْ حِلٌّ لَهُمْ وَ الْمُحْصَناتُ مِنَ الْمُؤْمِناتِ وَ الْمُحْصَناتُ مِنَ الَّذِينَ أُوتُوا الْكِتابَ مِنْ قَبْلِكُمْ إِذا آتَيْتُمُوهُنَّ أُجُورَهُنَّ مُحْصِنِينَ غَيْرَ مُسافِحِينَ وَ لا مُتَّخِذِي أَخْدانٍ وَ مَنْ يَكْفُرْ بِالْإِيمانِ فَقَدْ حَبِطَ عَمَلُهُ وَ هُوَ فِي الْآخِرَةِ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (675, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا قُمْتُمْ إِلَى الصَّلاةِ فَاغْسِلُوا وُجُوهَكُمْ وَ أَيْدِيَكُمْ إِلَى الْمَرافِقِ وَ امْسَحُوا بِرُؤُسِكُمْ وَ أَرْجُلَكُمْ إِلَى الْكَعْبَيْنِ وَ إِنْ كُنْتُمْ جُنُباً فَاطَّهَّرُوا وَ إِنْ كُنْتُمْ مَرْضى أَوْ عَلى سَفَرٍ أَوْ جاءَ أَحَدٌ مِنْكُمْ مِنَ الْغائِطِ أَوْ لامَسْتُمُ النِّساءَ فَلَمْ تَجِدُوا ماءً فَتَيَمَّمُوا صَعِيداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَ أَيْدِيكُمْ مِنْهُ ما يُرِيدُ اللَّهُ لِيَجْعَلَ عَلَيْكُمْ مِنْ حَرَجٍ وَ لكِنْ يُرِيدُ لِيُطَهِّرَكُمْ وَ لِيُتِمَّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (676, N'وَ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ وَ مِيثاقَهُ الَّذِي واثَقَكُمْ بِهِ إِذْ قُلْتُمْ سَمِعْنا وَ أَطَعْنا وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (677, N'يا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوَّامِينَ لِلَّهِ شُهَداءَ بِالْقِسْطِ وَ لا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ عَلى أَلاَّ تَعْدِلُوا اعْدِلُوا هُوَ أَقْرَبُ لِلتَّقْوى وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ خَبِيرٌ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (678, N'وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَهُمْ مَغْفِرَةٌ وَ أَجْرٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (679, N'وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآياتِنا أُولئِكَ أَصْحابُ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (680, N'يا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ هَمَّ قَوْمٌ أَنْ يَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ فَكَفَّ أَيْدِيَهُمْ عَنْكُمْ وَ اتَّقُوا اللَّهَ وَ عَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (681, N'وَ لَقَدْ أَخَذَ اللَّهُ مِيثاقَ بَنِي إِسْرائِيلَ وَ بَعَثْنا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيباً وَ قالَ اللَّهُ إِنِّي مَعَكُمْ لَئِنْ أَقَمْتُمُ الصَّلاةَ وَ آتَيْتُمُ الزَّكاةَ وَ آمَنْتُمْ بِرُسُلِي وَ عَزَّرْتُمُوهُمْ وَ أَقْرَضْتُمُ اللَّهَ قَرْضاً حَسَناً لَأُكَفِّرَنَّ عَنْكُمْ سَيِّئاتِكُمْ وَ لَأُدْخِلَنَّكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ فَمَنْ كَفَرَ بَعْدَ ذلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَواءَ السَّبِيلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (682, N'فَبِما نَقْضِهِمْ مِيثاقَهُمْ لَعَنَّاهُمْ وَ جَعَلْنا قُلُوبَهُمْ قاسِيَةً يُحَرِّفُونَ الْكَلِمَ عَنْ مَواضِعِهِ وَ نَسُوا حَظًّا مِمَّا ذُكِّرُوا بِهِ وَ لا تَزالُ تَطَّلِعُ عَلى خائِنَةٍ مِنْهُمْ إِلاَّ قَلِيلاً مِنْهُمْ فَاعْفُ عَنْهُمْ وَ اصْفَحْ إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (683, N'وَ مِنَ الَّذِينَ قالُوا إِنَّا نَصارى أَخَذْنا مِيثاقَهُمْ فَنَسُوا حَظًّا مِمَّا ذُكِّرُوا بِهِ فَأَغْرَيْنا بَيْنَهُمُ الْعَداوَةَ وَ الْبَغْضاءَ إِلى يَوْمِ الْقِيامَةِ وَ سَوْفَ يُنَبِّئُهُمُ اللَّهُ بِما كانُوا يَصْنَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (684, N'يا أَهْلَ الْكِتابِ قَدْ جاءَكُمْ رَسُولُنا يُبَيِّنُ لَكُمْ كَثِيراً مِمَّا كُنْتُمْ تُخْفُونَ مِنَ الْكِتابِ وَ يَعْفُوا عَنْ كَثِيرٍ قَدْ جاءَكُمْ مِنَ اللَّهِ نُورٌ وَ كِتابٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (685, N'يَهْدِي بِهِ اللَّهُ مَنِ اتَّبَعَ رِضْوانَهُ سُبُلَ السَّلامِ وَ يُخْرِجُهُمْ مِنَ الظُّلُماتِ إِلَى النُّورِ بِإِذْنِهِ وَ يَهْدِيهِمْ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (686, N'لَقَدْ كَفَرَ الَّذِينَ قالُوا إِنَّ اللَّهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ قُلْ فَمَنْ يَمْلِكُ مِنَ اللَّهِ شَيْئاً إِنْ أَرادَ أَنْ يُهْلِكَ الْمَسِيحَ ابْنَ مَرْيَمَ وَ أُمَّهُ وَ مَنْ فِي الْأَرْضِ جَمِيعاً وَ لِلَّهِ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُما يَخْلُقُ ما يَشاءُ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (687, N'وَ قالَتِ الْيَهُودُ وَ النَّصارى نَحْنُ أَبْناءُ اللَّهِ وَ أَحِبَّاؤُهُ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوبِكُمْ بَلْ أَنْتُمْ بَشَرٌ مِمَّنْ خَلَقَ يَغْفِرُ لِمَنْ يَشاءُ وَ يُعَذِّبُ مَنْ يَشاءُ وَ لِلَّهِ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُما وَ إِلَيْهِ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (688, N'يا أَهْلَ الْكِتابِ قَدْ جاءَكُمْ رَسُولُنا يُبَيِّنُ لَكُمْ عَلى فَتْرَةٍ مِنَ الرُّسُلِ أَنْ تَقُولُوا ما جاءَنا مِنْ بَشِيرٍ وَ لا نَذِيرٍ فَقَدْ جاءَكُمْ بَشِيرٌ وَ نَذِيرٌ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (689, N'وَ إِذْ قالَ مُوسى لِقَوْمِهِ يا قَوْمِ اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ جَعَلَ فِيكُمْ أَنْبِياءَ وَ جَعَلَكُمْ مُلُوكاً وَ آتاكُمْ ما لَمْ يُؤْتِ أَحَداً مِنَ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (690, N'يا قَوْمِ ادْخُلُوا الْأَرْضَ الْمُقَدَّسَةَ الَّتِي كَتَبَ اللَّهُ لَكُمْ وَ لا تَرْتَدُّوا عَلى أَدْبارِكُمْ فَتَنْقَلِبُوا خاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (691, N'قالُوا يا مُوسى إِنَّ فِيها قَوْماً جَبَّارِينَ وَ إِنَّا لَنْ نَدْخُلَها حَتَّى يَخْرُجُوا مِنْها فَإِنْ يَخْرُجُوا مِنْها فَإِنَّا داخِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (692, N'قالَ رَجُلانِ مِنَ الَّذِينَ يَخافُونَ أَنْعَمَ اللَّهُ عَلَيْهِمَا ادْخُلُوا عَلَيْهِمُ الْبابَ فَإِذا دَخَلْتُمُوهُ فَإِنَّكُمْ غالِبُونَ وَ عَلَى اللَّهِ فَتَوَكَّلُوا إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (693, N'قالُوا يا مُوسى إِنَّا لَنْ نَدْخُلَها أَبَداً ما دامُوا فِيها فَاذْهَبْ أَنْتَ وَ رَبُّكَ فَقاتِلا إِنَّا هاهُنا قاعِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (694, N'قالَ رَبِّ إِنِّي لا أَمْلِكُ إِلاَّ نَفْسِي وَ أَخِي فَافْرُقْ بَيْنَنا وَ بَيْنَ الْقَوْمِ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (695, N'قالَ فَإِنَّها مُحَرَّمَةٌ عَلَيْهِمْ أَرْبَعِينَ سَنَةً يَتِيهُونَ فِي الْأَرْضِ فَلا تَأْسَ عَلَى الْقَوْمِ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (696, N'وَ اتْلُ عَلَيْهِمْ نَبَأَ ابْنَيْ آدَمَ بِالْحَقِّ إِذْ قَرَّبا قُرْباناً فَتُقُبِّلَ مِنْ أَحَدِهِما وَ لَمْ يُتَقَبَّلْ مِنَ الْآخَرِ قالَ لَأَقْتُلَنَّكَ قالَ إِنَّما يَتَقَبَّلُ اللَّهُ مِنَ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (697, N'لَئِنْ بَسَطْتَ إِلَيَّ يَدَكَ لِتَقْتُلَنِي ما أَنَا بِباسِطٍ يَدِيَ إِلَيْكَ لِأَقْتُلَكَ إِنِّي أَخافُ اللَّهَ رَبَّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (698, N'إِنِّي أُرِيدُ أَنْ تَبُوءَ بِإِثْمِي وَ إِثْمِكَ فَتَكُونَ مِنْ أَصْحابِ النَّارِ وَ ذلِكَ جَزاءُ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (699, N'فَطَوَّعَتْ لَهُ نَفْسُهُ قَتْلَ أَخِيهِ فَقَتَلَهُ فَأَصْبَحَ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (700, N'فَبَعَثَ اللَّهُ غُراباً يَبْحَثُ فِي الْأَرْضِ لِيُرِيَهُ كَيْفَ يُوارِي سَوْأَةَ أَخِيهِ قالَ يا وَيْلَتى أَ عَجَزْتُ أَنْ أَكُونَ مِثْلَ هذَا الْغُرابِ فَأُوارِيَ سَوْأَةَ أَخِي فَأَصْبَحَ مِنَ النَّادِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (701, N'مِنْ أَجْلِ ذلِكَ كَتَبْنا عَلى بَنِي إِسْرائِيلَ أَنَّهُ مَنْ قَتَلَ نَفْساً بِغَيْرِ نَفْسٍ أَوْ فَسادٍ فِي الْأَرْضِ فَكَأَنَّما قَتَلَ النَّاسَ جَمِيعاً وَ مَنْ أَحْياها فَكَأَنَّما أَحْيَا النَّاسَ جَمِيعاً وَ لَقَدْ جاءَتْهُمْ رُسُلُنا بِالْبَيِّناتِ ثُمَّ إِنَّ كَثِيراً مِنْهُمْ بَعْدَ ذلِكَ فِي الْأَرْضِ لَمُسْرِفُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (702, N'إِنَّما جَزاءُ الَّذِينَ يُحارِبُونَ اللَّهَ وَ رَسُولَهُ وَ يَسْعَوْنَ فِي الْأَرْضِ فَساداً أَنْ يُقَتَّلُوا أَوْ يُصَلَّبُوا أَوْ تُقَطَّعَ أَيْدِيهِمْ وَ أَرْجُلُهُمْ مِنْ خِلافٍ أَوْ يُنْفَوْا مِنَ الْأَرْضِ ذلِكَ لَهُمْ خِزْيٌ فِي الدُّنْيا وَ لَهُمْ فِي الْآخِرَةِ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (703, N'إِلاَّ الَّذِينَ تابُوا مِنْ قَبْلِ أَنْ تَقْدِرُوا عَلَيْهِمْ فَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (704, N'يا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَ ابْتَغُوا إِلَيْهِ الْوَسِيلَةَ وَ جاهِدُوا فِي سَبِيلِهِ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (705, N'إِنَّ الَّذِينَ كَفَرُوا لَوْ أَنَّ لَهُمْ ما فِي الْأَرْضِ جَمِيعاً وَ مِثْلَهُ مَعَهُ لِيَفْتَدُوا بِهِ مِنْ عَذابِ يَوْمِ الْقِيامَةِ ما تُقُبِّلَ مِنْهُمْ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (706, N'يُرِيدُونَ أَنْ يَخْرُجُوا مِنَ النَّارِ وَ ما هُمْ بِخارِجِينَ مِنْها وَ لَهُمْ عَذابٌ مُقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (707, N'وَ السَّارِقُ وَ السَّارِقَةُ فَاقْطَعُوا أَيْدِيَهُما جَزاءً بِما كَسَبا نَكالاً مِنَ اللَّهِ وَ اللَّهُ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (708, N'فَمَنْ تابَ مِنْ بَعْدِ ظُلْمِهِ وَ أَصْلَحَ فَإِنَّ اللَّهَ يَتُوبُ عَلَيْهِ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (709, N'أَ لَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ يُعَذِّبُ مَنْ يَشاءُ وَ يَغْفِرُ لِمَنْ يَشاءُ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (710, N'يا أَيُّهَا الرَّسُولُ لا يَحْزُنْكَ الَّذِينَ يُسارِعُونَ فِي الْكُفْرِ مِنَ الَّذِينَ قالُوا آمَنَّا بِأَفْواهِهِمْ وَ لَمْ تُؤْمِنْ قُلُوبُهُمْ وَ مِنَ الَّذِينَ هادُوا سَمَّاعُونَ لِلْكَذِبِ سَمَّاعُونَ لِقَوْمٍ آخَرِينَ لَمْ يَأْتُوكَ يُحَرِّفُونَ الْكَلِمَ مِنْ بَعْدِ مَواضِعِهِ يَقُولُونَ إِنْ أُوتِيتُمْ هذا فَخُذُوهُ وَ إِنْ لَمْ تُؤْتَوْهُ فَاحْذَرُوا وَ مَنْ يُرِدِ اللَّهُ فِتْنَتَهُ فَلَنْ تَمْلِكَ لَهُ مِنَ اللَّهِ شَيْئاً أُولئِكَ الَّذِينَ لَمْ يُرِدِ اللَّهُ أَنْ يُطَهِّرَ قُلُوبَهُمْ لَهُمْ فِي الدُّنْيا خِزْيٌ وَ لَهُمْ فِي الْآخِرَةِ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (711, N'سَمَّاعُونَ لِلْكَذِبِ أَكَّالُونَ لِلسُّحْتِ فَإِنْ جاؤُكَ فَاحْكُمْ بَيْنَهُمْ أَوْ أَعْرِضْ عَنْهُمْ وَ إِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَضُرُّوكَ شَيْئاً وَ إِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (712, N'وَ كَيْفَ يُحَكِّمُونَكَ وَ عِنْدَهُمُ التَّوْراةُ فِيها حُكْمُ اللَّهِ ثُمَّ يَتَوَلَّوْنَ مِنْ بَعْدِ ذلِكَ وَ ما أُولئِكَ بِالْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (713, N'إِنَّا أَنْزَلْنَا التَّوْراةَ فِيها هُدىً وَ نُورٌ يَحْكُمُ بِهَا النَّبِيُّونَ الَّذِينَ أَسْلَمُوا لِلَّذِينَ هادُوا وَ الرَّبَّانِيُّونَ وَ الْأَحْبارُ بِمَا اسْتُحْفِظُوا مِنْ كِتابِ اللَّهِ وَ كانُوا عَلَيْهِ شُهَداءَ فَلا تَخْشَوُا النَّاسَ وَ اخْشَوْنِ وَ لا تَشْتَرُوا بِآياتِي ثَمَناً قَلِيلاً وَ مَنْ لَمْ يَحْكُمْ بِما أَنْزَلَ اللَّهُ فَأُولئِكَ هُمُ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (714, N'وَ كَتَبْنا عَلَيْهِمْ فِيها أَنَّ النَّفْسَ بِالنَّفْسِ وَ الْعَيْنَ بِالْعَيْنِ وَ الْأَنْفَ بِالْأَنْفِ وَ الْأُذُنَ بِالْأُذُنِ وَ السِّنَّ بِالسِّنِّ وَ الْجُرُوحَ قِصاصٌ فَمَنْ تَصَدَّقَ بِهِ فَهُوَ كَفَّارَةٌ لَهُ وَ مَنْ لَمْ يَحْكُمْ بِما أَنْزَلَ اللَّهُ فَأُولئِكَ هُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (715, N'وَ قَفَّيْنا عَلى آثارِهِمْ بِعِيسَى ابْنِ مَرْيَمَ مُصَدِّقاً لِما بَيْنَ يَدَيْهِ مِنَ التَّوْراةِ وَ آتَيْناهُ الْإِنْجِيلَ فِيهِ هُدىً وَ نُورٌ وَ مُصَدِّقاً لِما بَيْنَ يَدَيْهِ مِنَ التَّوْراةِ وَ هُدىً وَ مَوْعِظَةً لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (716, N'وَ لْيَحْكُمْ أَهْلُ الْإِنْجِيلِ بِما أَنْزَلَ اللَّهُ فِيهِ وَ مَنْ لَمْ يَحْكُمْ بِما أَنْزَلَ اللَّهُ فَأُولئِكَ هُمُ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (717, N'وَ أَنْزَلْنا إِلَيْكَ الْكِتابَ بِالْحَقِّ مُصَدِّقاً لِما بَيْنَ يَدَيْهِ مِنَ الْكِتابِ وَ مُهَيْمِناً عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِما أَنْزَلَ اللَّهُ وَ لا تَتَّبِعْ أَهْواءَهُمْ عَمَّا جاءَكَ مِنَ الْحَقِّ لِكُلٍّ جَعَلْنا مِنْكُمْ شِرْعَةً وَ مِنْهاجاً وَ لَوْ شاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً واحِدَةً وَ لكِنْ لِيَبْلُوَكُمْ فِي ما آتاكُمْ فَاسْتَبِقُوا الْخَيْراتِ إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعاً فَيُنَبِّئُكُمْ بِما كُنْتُمْ فِيهِ تَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (718, N'وَ أَنِ احْكُمْ بَيْنَهُمْ بِما أَنْزَلَ اللَّهُ وَ لا تَتَّبِعْ أَهْواءَهُمْ وَ احْذَرْهُمْ أَنْ يَفْتِنُوكَ عَنْ بَعْضِ ما أَنْزَلَ اللَّهُ إِلَيْكَ فَإِنْ تَوَلَّوْا فَاعْلَمْ أَنَّما يُرِيدُ اللَّهُ أَنْ يُصِيبَهُمْ بِبَعْضِ ذُنُوبِهِمْ وَ إِنَّ كَثِيراً مِنَ النَّاسِ لَفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (719, N'أَ فَحُكْمَ الْجاهِلِيَّةِ يَبْغُونَ وَ مَنْ أَحْسَنُ مِنَ اللَّهِ حُكْماً لِقَوْمٍ يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (720, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّخِذُوا الْيَهُودَ وَ النَّصارى أَوْلِياءَ بَعْضُهُمْ أَوْلِياءُ بَعْضٍ وَ مَنْ يَتَوَلَّهُمْ مِنْكُمْ فَإِنَّهُ مِنْهُمْ إِنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (721, N'فَتَرَى الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ يُسارِعُونَ فِيهِمْ يَقُولُونَ نَخْشى أَنْ تُصِيبَنا دائِرَةٌ فَعَسَى اللَّهُ أَنْ يَأْتِيَ بِالْفَتْحِ أَوْ أَمْرٍ مِنْ عِنْدِهِ فَيُصْبِحُوا عَلى ما أَسَرُّوا فِي أَنْفُسِهِمْ نادِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (722, N'وَ يَقُولُ الَّذِينَ آمَنُوا أَ هؤُلاءِ الَّذِينَ أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمانِهِمْ إِنَّهُمْ لَمَعَكُمْ حَبِطَتْ أَعْمالُهُمْ فَأَصْبَحُوا خاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (723, N'يا أَيُّهَا الَّذِينَ آمَنُوا مَنْ يَرْتَدَّ مِنْكُمْ عَنْ دِينِهِ فَسَوْفَ يَأْتِي اللَّهُ بِقَوْمٍ يُحِبُّهُمْ وَ يُحِبُّونَهُ أَذِلَّةٍ عَلَى الْمُؤْمِنِينَ أَعِزَّةٍ عَلَى الْكافِرِينَ يُجاهِدُونَ فِي سَبِيلِ اللَّهِ وَ لا يَخافُونَ لَوْمَةَ لائِمٍ ذلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشاءُ وَ اللَّهُ واسِعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (724, N'إِنَّما وَلِيُّكُمُ اللَّهُ وَ رَسُولُهُ وَ الَّذِينَ آمَنُوا الَّذِينَ يُقِيمُونَ الصَّلاةَ وَ يُؤْتُونَ الزَّكاةَ وَ هُمْ راكِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (725, N'وَ مَنْ يَتَوَلَّ اللَّهَ وَ رَسُولَهُ وَ الَّذِينَ آمَنُوا فَإِنَّ حِزْبَ اللَّهِ هُمُ الْغالِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (726, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّخِذُوا الَّذِينَ اتَّخَذُوا دِينَكُمْ هُزُواً وَ لَعِباً مِنَ الَّذِينَ أُوتُوا الْكِتابَ مِنْ قَبْلِكُمْ وَ الْكُفَّارَ أَوْلِياءَ وَ اتَّقُوا اللَّهَ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (727, N'وَ إِذا نادَيْتُمْ إِلَى الصَّلاةِ اتَّخَذُوها هُزُواً وَ لَعِباً ذلِكَ بِأَنَّهُمْ قَوْمٌ لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (728, N'قُلْ يا أَهْلَ الْكِتابِ هَلْ تَنْقِمُونَ مِنَّا إِلاَّ أَنْ آمَنَّا بِاللَّهِ وَ ما أُنْزِلَ إِلَيْنا وَ ما أُنْزِلَ مِنْ قَبْلُ وَ أَنَّ أَكْثَرَكُمْ فاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (729, N'قُلْ هَلْ أُنَبِّئُكُمْ بِشَرٍّ مِنْ ذلِكَ مَثُوبَةً عِنْدَ اللَّهِ مَنْ لَعَنَهُ اللَّهُ وَ غَضِبَ عَلَيْهِ وَ جَعَلَ مِنْهُمُ الْقِرَدَةَ وَ الْخَنازِيرَ وَ عَبَدَ الطَّاغُوتَ أُولئِكَ شَرٌّ مَكاناً وَ أَضَلُّ عَنْ سَواءِ السَّبِيلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (730, N'وَ إِذا جاؤُكُمْ قالُوا آمَنَّا وَ قَدْ دَخَلُوا بِالْكُفْرِ وَ هُمْ قَدْ خَرَجُوا بِهِ وَ اللَّهُ أَعْلَمُ بِما كانُوا يَكْتُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (731, N'وَ تَرى كَثِيراً مِنْهُمْ يُسارِعُونَ فِي الْإِثْمِ وَ الْعُدْوانِ وَ أَكْلِهِمُ السُّحْتَ لَبِئْسَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (732, N'لَوْ لا يَنْهاهُمُ الرَّبَّانِيُّونَ وَ الْأَحْبارُ عَنْ قَوْلِهِمُ الْإِثْمَ وَ أَكْلِهِمُ السُّحْتَ لَبِئْسَ ما كانُوا يَصْنَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (733, N'وَ قالَتِ الْيَهُودُ يَدُ اللَّهِ مَغْلُولَةٌ غُلَّتْ أَيْدِيهِمْ وَ لُعِنُوا بِما قالُوا بَلْ يَداهُ مَبْسُوطَتانِ يُنْفِقُ كَيْفَ يَشاءُ وَ لَيَزِيدَنَّ كَثِيراً مِنْهُمْ ما أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْياناً وَ كُفْراً وَ أَلْقَيْنا بَيْنَهُمُ الْعَداوَةَ وَ الْبَغْضاءَ إِلى يَوْمِ الْقِيامَةِ كُلَّما أَوْقَدُوا ناراً لِلْحَرْبِ أَطْفَأَهَا اللَّهُ وَ يَسْعَوْنَ فِي الْأَرْضِ فَساداً وَ اللَّهُ لا يُحِبُّ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (734, N'وَ لَوْ أَنَّ أَهْلَ الْكِتابِ آمَنُوا وَ اتَّقَوْا لَكَفَّرْنا عَنْهُمْ سَيِّئاتِهِمْ وَ لَأَدْخَلْناهُمْ جَنَّاتِ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (735, N'وَ لَوْ أَنَّهُمْ أَقامُوا التَّوْراةَ وَ الْإِنْجِيلَ وَ ما أُنْزِلَ إِلَيْهِمْ مِنْ رَبِّهِمْ لَأَكَلُوا مِنْ فَوْقِهِمْ وَ مِنْ تَحْتِ أَرْجُلِهِمْ مِنْهُمْ أُمَّةٌ مُقْتَصِدَةٌ وَ كَثِيرٌ مِنْهُمْ ساءَ ما يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (736, N'يا أَيُّهَا الرَّسُولُ بَلِّغْ ما أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ وَ إِنْ لَمْ تَفْعَلْ فَما بَلَّغْتَ رِسالَتَهُ وَ اللَّهُ يَعْصِمُكَ مِنَ النَّاسِ إِنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (737, N'قُلْ يا أَهْلَ الْكِتابِ لَسْتُمْ عَلى شَيْ‏ءٍ حَتَّى تُقِيمُوا التَّوْراةَ وَ الْإِنْجِيلَ وَ ما أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْ وَ لَيَزِيدَنَّ كَثِيراً مِنْهُمْ ما أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْياناً وَ كُفْراً فَلا تَأْسَ عَلَى الْقَوْمِ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (738, N'إِنَّ الَّذِينَ آمَنُوا وَ الَّذِينَ هادُوا وَ الصَّابِئُونَ وَ النَّصارى مَنْ آمَنَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ عَمِلَ صالِحاً فَلا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (739, N'لَقَدْ أَخَذْنا مِيثاقَ بَنِي إِسْرائِيلَ وَ أَرْسَلْنا إِلَيْهِمْ رُسُلاً كُلَّما جاءَهُمْ رَسُولٌ بِما لا تَهْوى أَنْفُسُهُمْ فَرِيقاً كَذَّبُوا وَ فَرِيقاً يَقْتُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (740, N'وَ حَسِبُوا أَلاَّ تَكُونَ فِتْنَةٌ فَعَمُوا وَ صَمُّوا ثُمَّ تابَ اللَّهُ عَلَيْهِمْ ثُمَّ عَمُوا وَ صَمُّوا كَثِيرٌ مِنْهُمْ وَ اللَّهُ بَصِيرٌ بِما يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (741, N'لَقَدْ كَفَرَ الَّذِينَ قالُوا إِنَّ اللَّهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ وَ قالَ الْمَسِيحُ يا بَنِي إِسْرائِيلَ اعْبُدُوا اللَّهَ رَبِّي وَ رَبَّكُمْ إِنَّهُ مَنْ يُشْرِكْ بِاللَّهِ فَقَدْ حَرَّمَ اللَّهُ عَلَيْهِ الْجَنَّةَ وَ مَأْواهُ النَّارُ وَ ما لِلظَّالِمِينَ مِنْ أَنْصارٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (742, N'لَقَدْ كَفَرَ الَّذِينَ قالُوا إِنَّ اللَّهَ ثالِثُ ثَلاثَةٍ وَ ما مِنْ إِلهٍ إِلاَّ إِلهٌ واحِدٌ وَ إِنْ لَمْ يَنْتَهُوا عَمَّا يَقُولُونَ لَيَمَسَّنَّ الَّذِينَ كَفَرُوا مِنْهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (743, N'أَ فَلا يَتُوبُونَ إِلَى اللَّهِ وَ يَسْتَغْفِرُونَهُ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (744, N'مَا الْمَسِيحُ ابْنُ مَرْيَمَ إِلاَّ رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ وَ أُمُّهُ صِدِّيقَةٌ كانا يَأْكُلانِ الطَّعامَ انْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْآياتِ ثُمَّ انْظُرْ أَنَّى يُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (745, N'قُلْ أَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ ما لا يَمْلِكُ لَكُمْ ضَرًّا وَ لا نَفْعاً وَ اللَّهُ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (746, N'قُلْ يا أَهْلَ الْكِتابِ لا تَغْلُوا فِي دِينِكُمْ غَيْرَ الْحَقِّ وَ لا تَتَّبِعُوا أَهْواءَ قَوْمٍ قَدْ ضَلُّوا مِنْ قَبْلُ وَ أَضَلُّوا كَثِيراً وَ ضَلُّوا عَنْ سَواءِ السَّبِيلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (747, N'لُعِنَ الَّذِينَ كَفَرُوا مِنْ بَنِي إِسْرائِيلَ عَلى لِسانِ داوُدَ وَ عِيسَى ابْنِ مَرْيَمَ ذلِكَ بِما عَصَوْا وَ كانُوا يَعْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (748, N'كانُوا لا يَتَناهَوْنَ عَنْ مُنكَرٍ فَعَلُوهُ لَبِئْسَ ما كانُوا يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (749, N'تَرى كَثِيراً مِنْهُمْ يَتَوَلَّوْنَ الَّذِينَ كَفَرُوا لَبِئْسَ ما قَدَّمَتْ لَهُمْ أَنْفُسُهُمْ أَنْ سَخِطَ اللَّهُ عَلَيْهِمْ وَ فِي الْعَذابِ هُمْ خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (750, N'وَ لَوْ كانُوا يُؤْمِنُونَ بِاللَّهِ وَ النَّبِيِّ وَ ما أُنْزِلَ إِلَيْهِ مَا اتَّخَذُوهُمْ أَوْلِياءَ وَ لكِنَّ كَثِيراً مِنْهُمْ فاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (751, N'لَتَجِدَنَّ أَشَدَّ النَّاسِ عَداوَةً لِلَّذِينَ آمَنُوا الْيَهُودَ وَ الَّذِينَ أَشْرَكُوا وَ لَتَجِدَنَّ أَقْرَبَهُمْ مَوَدَّةً لِلَّذِينَ آمَنُوا الَّذِينَ قالُوا إِنَّا نَصارى ذلِكَ بِأَنَّ مِنْهُمْ قِسِّيسِينَ وَ رُهْباناً وَ أَنَّهُمْ لا يَسْتَكْبِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (752, N'وَ إِذا سَمِعُوا ما أُنْزِلَ إِلَى الرَّسُولِ تَرى أَعْيُنَهُمْ تَفِيضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّ يَقُولُونَ رَبَّنا آمَنَّا فَاكْتُبْنا مَعَ الشَّاهِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (753, N'وَ ما لَنا لا نُؤْمِنُ بِاللَّهِ وَ ما جاءَنا مِنَ الْحَقِّ وَ نَطْمَعُ أَنْ يُدْخِلَنا رَبُّنا مَعَ الْقَوْمِ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (754, N'فَأَثابَهُمُ اللَّهُ بِما قالُوا جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها وَ ذلِكَ جَزاءُ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (755, N'وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآياتِنا أُولئِكَ أَصْحابُ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (756, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تُحَرِّمُوا طَيِّباتِ ما أَحَلَّ اللَّهُ لَكُمْ وَ لا تَعْتَدُوا إِنَّ اللَّهَ لا يُحِبُّ الْمُعْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (757, N'وَ كُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلالاً طَيِّباً وَ اتَّقُوا اللَّهَ الَّذِي أَنْتُمْ بِهِ مُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (758, N'لا يُؤاخِذُكُمُ اللَّهُ بِاللَّغْوِ فِي أَيْمانِكُمْ وَ لكِنْ يُؤاخِذُكُمْ بِما عَقَّدْتُمُ الْأَيْمانَ فَكَفَّارَتُهُ إِطْعامُ عَشَرَةِ مَساكِينَ مِنْ أَوْسَطِ ما تُطْعِمُونَ أَهْلِيكُمْ أَوْ كِسْوَتُهُمْ أَوْ تَحْرِيرُ رَقَبَةٍ فَمَنْ لَمْ يَجِدْ فَصِيامُ ثَلاثَةِ أَيَّامٍ ذلِكَ كَفَّارَةُ أَيْمانِكُمْ إِذا حَلَفْتُمْ وَ احْفَظُوا أَيْمانَكُمْ كَذلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آياتِهِ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (759, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْخَمْرُ وَ الْمَيْسِرُ وَ الْأَنْصابُ وَ الْأَزْلامُ رِجْسٌ مِنْ عَمَلِ الشَّيْطانِ فَاجْتَنِبُوهُ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (760, N'إِنَّما يُرِيدُ الشَّيْطانُ أَنْ يُوقِعَ بَيْنَكُمُ الْعَداوَةَ وَ الْبَغْضاءَ فِي الْخَمْرِ وَ الْمَيْسِرِ وَ يَصُدَّكُمْ عَنْ ذِكْرِ اللَّهِ وَ عَنِ الصَّلاةِ فَهَلْ أَنْتُمْ مُنْتَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (761, N'وَ أَطِيعُوا اللَّهَ وَ أَطِيعُوا الرَّسُولَ وَ احْذَرُوا فَإِنْ تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّما عَلى رَسُولِنَا الْبَلاغُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (762, N'لَيْسَ عَلَى الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ جُناحٌ فِيما طَعِمُوا إِذا مَا اتَّقَوْا وَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ ثُمَّ اتَّقَوْا وَ آمَنُوا ثُمَّ اتَّقَوْا وَ أَحْسَنُوا وَ اللَّهُ يُحِبُّ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (763, N'يا أَيُّهَا الَّذِينَ آمَنُوا لَيَبْلُوَنَّكُمُ اللَّهُ بِشَيْ‏ءٍ مِنَ الصَّيْدِ تَنالُهُ أَيْدِيكُمْ وَ رِماحُكُمْ لِيَعْلَمَ اللَّهُ مَنْ يَخافُهُ بِالْغَيْبِ فَمَنِ اعْتَدى بَعْدَ ذلِكَ فَلَهُ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (764, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَقْتُلُوا الصَّيْدَ وَ أَنْتُمْ حُرُمٌ وَ مَنْ قَتَلَهُ مِنْكُمْ مُتَعَمِّداً فَجَزاءٌ مِثْلُ ما قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهِ ذَوا عَدْلٍ مِنْكُمْ هَدْياً بالِغَ الْكَعْبَةِ أَوْ كَفَّارَةٌ طَعامُ مَساكِينَ أَوْ عَدْلُ ذلِكَ صِياماً لِيَذُوقَ وَبالَ أَمْرِهِ عَفَا اللَّهُ عَمَّا سَلَفَ وَ مَنْ عادَ فَيَنْتَقِمُ اللَّهُ مِنْهُ وَ اللَّهُ عَزِيزٌ ذُو انْتِقامٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (765, N'أُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَ طَعامُهُ مَتاعاً لَكُمْ وَ لِلسَّيَّارَةِ وَ حُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ ما دُمْتُمْ حُرُماً وَ اتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (766, N'جَعَلَ اللَّهُ الْكَعْبَةَ الْبَيْتَ الْحَرامَ قِياماً لِلنَّاسِ وَ الشَّهْرَ الْحَرامَ وَ الْهَدْيَ وَ الْقَلائِدَ ذلِكَ لِتَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ أَنَّ اللَّهَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (767, N'اعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقابِ وَ أَنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (768, N'ما عَلَى الرَّسُولِ إِلاَّ الْبَلاغُ وَ اللَّهُ يَعْلَمُ ما تُبْدُونَ وَ ما تَكْتُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (769, N'قُلْ لا يَسْتَوِي الْخَبِيثُ وَ الطَّيِّبُ وَ لَوْ أَعْجَبَكَ كَثْرَةُ الْخَبِيثِ فَاتَّقُوا اللَّهَ يا أُولِي الْأَلْبابِ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (770, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَسْئَلُوا عَنْ أَشْياءَ إِنْ تُبْدَ لَكُمْ تَسُؤْكُمْ وَ إِنْ تَسْئَلُوا عَنْها حِينَ يُنَزَّلُ الْقُرْآنُ تُبْدَ لَكُمْ عَفَا اللَّهُ عَنْها وَ اللَّهُ غَفُورٌ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (771, N'قَدْ سَأَلَها قَوْمٌ مِنْ قَبْلِكُمْ ثُمَّ أَصْبَحُوا بِها كافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (772, N'ما جَعَلَ اللَّهُ مِنْ بَحِيرَةٍ وَ لا سائِبَةٍ وَ لا وَصِيلَةٍ وَ لا حامٍ وَ لكِنَّ الَّذِينَ كَفَرُوا يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ وَ أَكْثَرُهُمْ لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (773, N'وَ إِذا قِيلَ لَهُمْ تَعالَوْا إِلى ما أَنْزَلَ اللَّهُ وَ إِلَى الرَّسُولِ قالُوا حَسْبُنا ما وَجَدْنا عَلَيْهِ آباءَنا أَ وَ لَوْ كانَ آباؤُهُمْ لا يَعْلَمُونَ شَيْئاً وَ لا يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (774, N'يا أَيُّهَا الَّذِينَ آمَنُوا عَلَيْكُمْ أَنْفُسَكُمْ لا يَضُرُّكُمْ مَنْ ضَلَّ إِذَا اهْتَدَيْتُمْ إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعاً فَيُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (775, N'يا أَيُّهَا الَّذِينَ آمَنُوا شَهادَةُ بَيْنِكُمْ إِذا حَضَرَ أَحَدَكُمُ الْمَوْتُ حِينَ الْوَصِيَّةِ اثْنانِ ذَوا عَدْلٍ مِنْكُمْ أَوْ آخَرانِ مِنْ غَيْرِكُمْ إِنْ أَنْتُمْ ضَرَبْتُمْ فِي الْأَرْضِ فَأَصابَتْكُمْ مُصِيبَةُ الْمَوْتِ تَحْبِسُونَهُما مِنْ بَعْدِ الصَّلاةِ فَيُقْسِمانِ بِاللَّهِ إِنِ ارْتَبْتُمْ لا نَشْتَرِي بِهِ ثَمَناً وَ لَوْ كانَ ذا قُرْبى وَ لا نَكْتُمُ شَهادَةَ اللَّهِ إِنَّا إِذاً لَمِنَ الْآثِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (776, N'فَإِنْ عُثِرَ عَلى أَنَّهُمَا اسْتَحَقَّا إِثْماً فَآخَرانِ يَقُومانِ مَقامَهُما مِنَ الَّذِينَ اسْتَحَقَّ عَلَيْهِمُ الْأَوْلَيانِ فَيُقْسِمانِ بِاللَّهِ لَشَهادَتُنا أَحَقُّ مِنْ شَهادَتِهِما وَ مَا اعْتَدَيْنا إِنَّا إِذاً لَمِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (777, N'ذلِكَ أَدْنى أَنْ يَأْتُوا بِالشَّهادَةِ عَلى وَجْهِها أَوْ يَخافُوا أَنْ تُرَدَّ أَيْمانٌ بَعْدَ أَيْمانِهِمْ وَ اتَّقُوا اللَّهَ وَ اسْمَعُوا وَ اللَّهُ لا يَهْدِي الْقَوْمَ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (778, N'يَوْمَ يَجْمَعُ اللَّهُ الرُّسُلَ فَيَقُولُ ما ذا أُجِبْتُمْ قالُوا لا عِلْمَ لَنا إِنَّكَ أَنْتَ عَلاَّمُ الْغُيُوبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (779, N'إِذْ قالَ اللَّهُ يا عِيسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِي عَلَيْكَ وَ عَلى والِدَتِكَ إِذْ أَيَّدْتُكَ بِرُوحِ الْقُدُسِ تُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَ كَهْلاً وَ إِذْ عَلَّمْتُكَ الْكِتابَ وَ الْحِكْمَةَ وَ التَّوْراةَ وَ الْإِنْجِيلَ وَ إِذْ تَخْلُقُ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ بِإِذْنِي فَتَنْفُخُ فِيها فَتَكُونُ طَيْراً بِإِذْنِي وَ تُبْرِئُ الْأَكْمَهَ وَ الْأَبْرَصَ بِإِذْنِي وَ إِذْ تُخْرِجُ الْمَوْتى بِإِذْنِي وَ إِذْ كَفَفْتُ بَنِي إِسْرائِيلَ عَنْكَ إِذْ جِئْتَهُمْ بِالْبَيِّناتِ فَقالَ الَّذِينَ كَفَرُوا مِنْهُمْ إِنْ هذا إِلاَّ سِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (780, N'وَ إِذْ أَوْحَيْتُ إِلَى الْحَوارِيِّينَ أَنْ آمِنُوا بِي وَ بِرَسُولِي قالُوا آمَنَّا وَ اشْهَدْ بِأَنَّنا مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (781, N'إِذْ قالَ الْحَوارِيُّونَ يا عِيسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيعُ رَبُّكَ أَنْ يُنَزِّلَ عَلَيْنا مائِدَةً مِنَ السَّماءِ قالَ اتَّقُوا اللَّهَ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (782, N'قالُوا نُرِيدُ أَنْ نَأْكُلَ مِنْها وَ تَطْمَئِنَّ قُلُوبُنا وَ نَعْلَمَ أَنْ قَدْ صَدَقْتَنا وَ نَكُونَ عَلَيْها مِنَ الشَّاهِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (783, N'قالَ عِيسَى ابْنُ مَرْيَمَ اللَّهُمَّ رَبَّنا أَنْزِلْ عَلَيْنا مائِدَةً مِنَ السَّماءِ تَكُونُ لَنا عِيداً لِأَوَّلِنا وَ آخِرِنا وَ آيَةً مِنْكَ وَ ارْزُقْنا وَ أَنْتَ خَيْرُ الرَّازِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (784, N'قالَ اللَّهُ إِنِّي مُنَزِّلُها عَلَيْكُمْ فَمَنْ يَكْفُرْ بَعْدُ مِنْكُمْ فَإِنِّي أُعَذِّبُهُ عَذاباً لا أُعَذِّبُهُ أَحَداً مِنَ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (785, N'وَ إِذْ قالَ اللَّهُ يا عِيسَى ابْنَ مَرْيَمَ أَ أَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُونِي وَ أُمِّي إِلهَيْنِ مِنْ دُونِ اللَّهِ قالَ سُبْحانَكَ ما يَكُونُ لِي أَنْ أَقُولَ ما لَيْسَ لِي بِحَقٍّ إِنْ كُنْتُ قُلْتُهُ فَقَدْ عَلِمْتَهُ تَعْلَمُ ما فِي نَفْسِي وَ لا أَعْلَمُ ما فِي نَفْسِكَ إِنَّكَ أَنْتَ عَلاَّمُ الْغُيُوبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (786, N'ما قُلْتُ لَهُمْ إِلاَّ ما أَمَرْتَنِي بِهِ أَنِ اعْبُدُوا اللَّهَ رَبِّي وَ رَبَّكُمْ وَ كُنْتُ عَلَيْهِمْ شَهِيداً ما دُمْتُ فِيهِمْ فَلَمَّا تَوَفَّيْتَنِي كُنْتَ أَنْتَ الرَّقِيبَ عَلَيْهِمْ وَ أَنْتَ عَلى كُلِّ شَيْ‏ءٍ شَهِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (787, N'إِنْ تُعَذِّبْهُمْ فَإِنَّهُمْ عِبادُكَ وَ إِنْ تَغْفِرْ لَهُمْ فَإِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (788, N'قالَ اللَّهُ هذا يَوْمُ يَنْفَعُ الصَّادِقِينَ صِدْقُهُمْ لَهُمْ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها أَبَداً رَضِيَ اللَّهُ عَنْهُمْ وَ رَضُوا عَنْهُ ذلِكَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (789, N'لِلَّهِ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ ما فِيهِنَّ وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (790, N'الْحَمْدُ لِلَّهِ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ وَ جَعَلَ الظُّلُماتِ وَ النُّورَ ثُمَّ الَّذِينَ كَفَرُوا بِرَبِّهِمْ يَعْدِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (791, N'هُوَ الَّذِي خَلَقَكُمْ مِنْ طِينٍ ثُمَّ قَضى أَجَلاً وَ أَجَلٌ مُسَمًّى عِنْدَهُ ثُمَّ أَنْتُمْ تَمْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (792, N'وَ هُوَ اللَّهُ فِي السَّماواتِ وَ فِي الْأَرْضِ يَعْلَمُ سِرَّكُمْ وَ جَهْرَكُمْ وَ يَعْلَمُ ما تَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (793, N'وَ ما تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آياتِ رَبِّهِمْ إِلاَّ كانُوا عَنْها مُعْرِضِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (794, N'فَقَدْ كَذَّبُوا بِالْحَقِّ لَمَّا جاءَهُمْ فَسَوْفَ يَأْتِيهِمْ أَنْباءُ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (795, N'أَ لَمْ يَرَوْا كَمْ أَهْلَكْنا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ مَكَّنَّاهُمْ فِي الْأَرْضِ ما لَمْ نُمَكِّنْ لَكُمْ وَ أَرْسَلْنَا السَّماءَ عَلَيْهِمْ مِدْراراً وَ جَعَلْنَا الْأَنْهارَ تَجْرِي مِنْ تَحْتِهِمْ فَأَهْلَكْناهُمْ بِذُنُوبِهِمْ وَ أَنْشَأْنا مِنْ بَعْدِهِمْ قَرْناً آخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (796, N'وَ لَوْ نَزَّلْنا عَلَيْكَ كِتاباً فِي قِرْطاسٍ فَلَمَسُوهُ بِأَيْدِيهِمْ لَقالَ الَّذِينَ كَفَرُوا إِنْ هذا إِلاَّ سِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (797, N'وَ قالُوا لَوْ لا أُنْزِلَ عَلَيْهِ مَلَكٌ وَ لَوْ أَنْزَلْنا مَلَكاً لَقُضِيَ الْأَمْرُ ثُمَّ لا يُنْظَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (798, N'وَ لَوْ جَعَلْناهُ مَلَكاً لَجَعَلْناهُ رَجُلاً وَ لَلَبَسْنا عَلَيْهِمْ ما يَلْبِسُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (799, N'وَ لَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحاقَ بِالَّذِينَ سَخِرُوا مِنْهُمْ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (800, N'قُلْ سِيرُوا فِي الْأَرْضِ ثُمَّ انْظُرُوا كَيْفَ كانَ عاقِبَةُ الْمُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (801, N'قُلْ لِمَنْ ما فِي السَّماواتِ وَ الْأَرْضِ قُلْ لِلَّهِ كَتَبَ عَلى نَفْسِهِ الرَّحْمَةَ لَيَجْمَعَنَّكُمْ إِلى يَوْمِ الْقِيامَةِ لا رَيْبَ فِيهِ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فَهُمْ لا يُؤْمِنُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (802, N'وَ لَهُ ما سَكَنَ فِي اللَّيْلِ وَ النَّهارِ وَ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (803, N'قُلْ أَ غَيْرَ اللَّهِ أَتَّخِذُ وَلِيًّا فاطِرِ السَّماواتِ وَ الْأَرْضِ وَ هُوَ يُطْعِمُ وَ لا يُطْعَمُ قُلْ إِنِّي أُمِرْتُ أَنْ أَكُونَ أَوَّلَ مَنْ أَسْلَمَ وَ لا تَكُونَنَّ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (804, N'قُلْ إِنِّي أَخافُ إِنْ عَصَيْتُ رَبِّي عَذابَ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (805, N'مَنْ يُصْرَفْ عَنْهُ يَوْمَئِذٍ فَقَدْ رَحِمَهُ وَ ذلِكَ الْفَوْزُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (806, N'وَ إِنْ يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلا كاشِفَ لَهُ إِلاَّ هُوَ وَ إِنْ يَمْسَسْكَ بِخَيْرٍ فَهُوَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (807, N'وَ هُوَ الْقاهِرُ فَوْقَ عِبادِهِ وَ هُوَ الْحَكِيمُ الْخَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (808, N'قُلْ أَيُّ شَيْ‏ءٍ أَكْبَرُ شَهادَةً قُلِ اللَّهُ شَهِيدٌ بَيْنِي وَ بَيْنَكُمْ وَ أُوحِيَ إِلَيَّ هذَا الْقُرْآنُ لِأُنْذِرَكُمْ بِهِ وَ مَنْ بَلَغَ أَ إِنَّكُمْ لَتَشْهَدُونَ أَنَّ مَعَ اللَّهِ آلِهَةً أُخْرى قُلْ لا أَشْهَدُ قُلْ إِنَّما هُوَ إِلهٌ واحِدٌ وَ إِنَّنِي بَرِي‏ءٌ مِمَّا تُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (809, N'الَّذِينَ آتَيْناهُمُ الْكِتابَ يَعْرِفُونَهُ كَما يَعْرِفُونَ أَبْناءَهُمُ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فَهُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (810, N'وَ مَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ كَذِباً أَوْ كَذَّبَ بِآياتِهِ إِنَّهُ لا يُفْلِحُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (811, N'وَ يَوْمَ نَحْشُرُهُمْ جَمِيعاً ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا أَيْنَ شُرَكاؤُكُمُ الَّذِينَ كُنْتُمْ تَزْعُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (812, N'ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ إِلاَّ أَنْ قالُوا وَ اللَّهِ رَبِّنا ما كُنَّا مُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (813, N'انْظُرْ كَيْفَ كَذَبُوا عَلى أَنْفُسِهِمْ وَ ضَلَّ عَنْهُمْ ما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (814, N'وَ مِنْهُمْ مَنْ يَسْتَمِعُ إِلَيْكَ وَ جَعَلْنا عَلى قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَ فِي آذانِهِمْ وَقْراً وَ إِنْ يَرَوْا كُلَّ آيَةٍ لا يُؤْمِنُوا بِها حَتَّى إِذا جاؤُكَ يُجادِلُونَكَ يَقُولُ الَّذِينَ كَفَرُوا إِنْ هذا إِلاَّ أَساطِيرُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (815, N'وَ هُمْ يَنْهَوْنَ عَنْهُ وَ يَنْأَوْنَ عَنْهُ وَ إِنْ يُهْلِكُونَ إِلاَّ أَنْفُسَهُمْ وَ ما يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (816, N'وَ لَوْ تَرى إِذْ وُقِفُوا عَلَى النَّارِ فَقالُوا يا لَيْتَنا نُرَدُّ وَ لا نُكَذِّبَ بِآياتِ رَبِّنا وَ نَكُونَ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (817, N'بَلْ بَدا لَهُمْ ما كانُوا يُخْفُونَ مِنْ قَبْلُ وَ لَوْ رُدُّوا لَعادُوا لِما نُهُوا عَنْهُ وَ إِنَّهُمْ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (818, N'وَ قالُوا إِنْ هِيَ إِلاَّ حَياتُنَا الدُّنْيا وَ ما نَحْنُ بِمَبْعُوثِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (819, N'وَ لَوْ تَرى إِذْ وُقِفُوا عَلى رَبِّهِمْ قالَ أَ لَيْسَ هذا بِالْحَقِّ قالُوا بَلى وَ رَبِّنا قالَ فَذُوقُوا الْعَذابَ بِما كُنْتُمْ تَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (820, N'قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقاءِ اللَّهِ حَتَّى إِذا جاءَتْهُمُ السَّاعَةُ بَغْتَةً قالُوا يا حَسْرَتَنا عَلى ما فَرَّطْنا فِيها وَ هُمْ يَحْمِلُونَ أَوْزارَهُمْ عَلى ظُهُورِهِمْ أَلا ساءَ ما يَزِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (821, N'وَ مَا الْحَياةُ الدُّنْيا إِلاَّ لَعِبٌ وَ لَهْوٌ وَ لَلدَّارُ الْآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (822, N'قَدْ نَعْلَمُ إِنَّهُ لَيَحْزُنُكَ الَّذِي يَقُولُونَ فَإِنَّهُمْ لا يُكَذِّبُونَكَ وَ لكِنَّ الظَّالِمِينَ بِآياتِ اللَّهِ يَجْحَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (823, N'وَ لَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ فَصَبَرُوا عَلى ما كُذِّبُوا وَ أُوذُوا حَتَّى أَتاهُمْ نَصْرُنا وَ لا مُبَدِّلَ لِكَلِماتِ اللَّهِ وَ لَقَدْ جاءَكَ مِنْ نَبَإِ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (824, N'وَ إِنْ كانَ كَبُرَ عَلَيْكَ إِعْراضُهُمْ فَإِنِ اسْتَطَعْتَ أَنْ تَبْتَغِيَ نَفَقاً فِي الْأَرْضِ أَوْ سُلَّماً فِي السَّماءِ فَتَأْتِيَهُمْ بِآيَةٍ وَ لَوْ شاءَ اللَّهُ لَجَمَعَهُمْ عَلَى الْهُدى فَلا تَكُونَنَّ مِنَ الْجاهِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (825, N'إِنَّما يَسْتَجِيبُ الَّذِينَ يَسْمَعُونَ وَ الْمَوْتى يَبْعَثُهُمُ اللَّهُ ثُمَّ إِلَيْهِ يُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (826, N'وَ قالُوا لَوْ لا نُزِّلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ قُلْ إِنَّ اللَّهَ قادِرٌ عَلى أَنْ يُنَزِّلَ آيَةً وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (827, N'وَ ما مِنْ دَابَّةٍ فِي الْأَرْضِ وَ لا طائِرٍ يَطِيرُ بِجَناحَيْهِ إِلاَّ أُمَمٌ أَمْثالُكُمْ ما فَرَّطْنا فِي الْكِتابِ مِنْ شَيْ‏ءٍ ثُمَّ إِلى رَبِّهِمْ يُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (828, N'وَ الَّذِينَ كَذَّبُوا بِآياتِنا صُمٌّ وَ بُكْمٌ فِي الظُّلُماتِ مَنْ يَشَأِ اللَّهُ يُضْلِلْهُ وَ مَنْ يَشَأْ يَجْعَلْهُ عَلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (829, N'قُلْ أَ رَأَيْتَكُمْ إِنْ أَتاكُمْ عَذابُ اللَّهِ أَوْ أَتَتْكُمُ السَّاعَةُ أَ غَيْرَ اللَّهِ تَدْعُونَ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (830, N'بَلْ إِيَّاهُ تَدْعُونَ فَيَكْشِفُ ما تَدْعُونَ إِلَيْهِ إِنْ شاءَ وَ تَنْسَوْنَ ما تُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (831, N'وَ لَقَدْ أَرْسَلْنا إِلى أُمَمٍ مِنْ قَبْلِكَ فَأَخَذْناهُمْ بِالْبَأْساءِ وَ الضَّرَّاءِ لَعَلَّهُمْ يَتَضَرَّعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (832, N'فَلَوْ لا إِذْ جاءَهُمْ بَأْسُنا تَضَرَّعُوا وَ لكِنْ قَسَتْ قُلُوبُهُمْ وَ زَيَّنَ لَهُمُ الشَّيْطانُ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (833, N'فَلَمَّا نَسُوا ما ذُكِّرُوا بِهِ فَتَحْنا عَلَيْهِمْ أَبْوابَ كُلِّ شَيْ‏ءٍ حَتَّى إِذا فَرِحُوا بِما أُوتُوا أَخَذْناهُمْ بَغْتَةً فَإِذا هُمْ مُبْلِسُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (834, N'فَقُطِعَ دابِرُ الْقَوْمِ الَّذِينَ ظَلَمُوا وَ الْحَمْدُ لِلَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (835, N'قُلْ أَ رَأَيْتُمْ إِنْ أَخَذَ اللَّهُ سَمْعَكُمْ وَ أَبْصارَكُمْ وَ خَتَمَ عَلى قُلُوبِكُمْ مَنْ إِلهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِهِ انْظُرْ كَيْفَ نُصَرِّفُ الْآياتِ ثُمَّ هُمْ يَصْدِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (836, N'قُلْ أَ رَأَيْتَكُمْ إِنْ أَتاكُمْ عَذابُ اللَّهِ بَغْتَةً أَوْ جَهْرَةً هَلْ يُهْلَكُ إِلاَّ الْقَوْمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (837, N'وَ ما نُرْسِلُ الْمُرْسَلِينَ إِلاَّ مُبَشِّرِينَ وَ مُنْذِرِينَ فَمَنْ آمَنَ وَ أَصْلَحَ فَلا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (838, N'وَ الَّذِينَ كَذَّبُوا بِآياتِنا يَمَسُّهُمُ الْعَذابُ بِما كانُوا يَفْسُقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (839, N'قُلْ لا أَقُولُ لَكُمْ عِنْدِي خَزائِنُ اللَّهِ وَ لا أَعْلَمُ الْغَيْبَ وَ لا أَقُولُ لَكُمْ إِنِّي مَلَكٌ إِنْ أَتَّبِعُ إِلاَّ ما يُوحى إِلَيَّ قُلْ هَلْ يَسْتَوِي الْأَعْمى وَ الْبَصِيرُ أَ فَلا تَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (840, N'وَ أَنْذِرْ بِهِ الَّذِينَ يَخافُونَ أَنْ يُحْشَرُوا إِلى رَبِّهِمْ لَيْسَ لَهُمْ مِنْ دُونِهِ وَلِيٌّ وَ لا شَفِيعٌ لَعَلَّهُمْ يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (841, N'وَ لا تَطْرُدِ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَداةِ وَ الْعَشِيِّ يُرِيدُونَ وَجْهَهُ ما عَلَيْكَ مِنْ حِسابِهِمْ مِنْ شَيْ‏ءٍ وَ ما مِنْ حِسابِكَ عَلَيْهِمْ مِنْ شَيْ‏ءٍ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (842, N'وَ كَذلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُوا أَ هؤُلاءِ مَنَّ اللَّهُ عَلَيْهِمْ مِنْ بَيْنِنا أَ لَيْسَ اللَّهُ بِأَعْلَمَ بِالشَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (843, N'وَ إِذا جاءَكَ الَّذِينَ يُؤْمِنُونَ بِآياتِنا فَقُلْ سَلامٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلى نَفْسِهِ الرَّحْمَةَ أَنَّهُ مَنْ عَمِلَ مِنْكُمْ سُوءاً بِجَهالَةٍ ثُمَّ تابَ مِنْ بَعْدِهِ وَ أَصْلَحَ فَأَنَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (844, N'وَ كَذلِكَ نُفَصِّلُ الْآياتِ وَ لِتَسْتَبِينَ سَبِيلُ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (845, N'قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ قُلْ لا أَتَّبِعُ أَهْواءَكُمْ قَدْ ضَلَلْتُ إِذاً وَ ما أَنَا مِنَ الْمُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (846, N'قُلْ إِنِّي عَلى بَيِّنَةٍ مِنْ رَبِّي وَ كَذَّبْتُمْ بِهِ ما عِنْدِي ما تَسْتَعْجِلُونَ بِهِ إِنِ الْحُكْمُ إِلاَّ لِلَّهِ يَقُصُّ الْحَقَّ وَ هُوَ خَيْرُ الْفاصِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (847, N'قُلْ لَوْ أَنَّ عِنْدِي ما تَسْتَعْجِلُونَ بِهِ لَقُضِيَ الْأَمْرُ بَيْنِي وَ بَيْنَكُمْ وَ اللَّهُ أَعْلَمُ بِالظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (848, N'وَ عِنْدَهُ مَفاتِحُ الْغَيْبِ لا يَعْلَمُها إِلاَّ هُوَ وَ يَعْلَمُ ما فِي الْبَرِّ وَ الْبَحْرِ وَ ما تَسْقُطُ مِنْ وَرَقَةٍ إِلاَّ يَعْلَمُها وَ لا حَبَّةٍ فِي ظُلُماتِ الْأَرْضِ وَ لا رَطْبٍ وَ لا يابِسٍ إِلاَّ فِي كِتابٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (849, N'وَ هُوَ الَّذِي يَتَوَفَّاكُمْ بِاللَّيْلِ وَ يَعْلَمُ ما جَرَحْتُمْ بِالنَّهارِ ثُمَّ يَبْعَثُكُمْ فِيهِ لِيُقْضى أَجَلٌ مُسَمًّى ثُمَّ إِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (850, N'وَ هُوَ الْقاهِرُ فَوْقَ عِبادِهِ وَ يُرْسِلُ عَلَيْكُمْ حَفَظَةً حَتَّى إِذا جاءَ أَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنا وَ هُمْ لا يُفَرِّطُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (851, N'ثُمَّ رُدُّوا إِلَى اللَّهِ مَوْلاهُمُ الْحَقِّ أَلا لَهُ الْحُكْمُ وَ هُوَ أَسْرَعُ الْحاسِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (852, N'قُلْ مَنْ يُنَجِّيكُمْ مِنْ ظُلُماتِ الْبَرِّ وَ الْبَحْرِ تَدْعُونَهُ تَضَرُّعاً وَ خُفْيَةً لَئِنْ أَنْجانا مِنْ هذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (853, N'قُلِ اللَّهُ يُنَجِّيكُمْ مِنْها وَ مِنْ كُلِّ كَرْبٍ ثُمَّ أَنْتُمْ تُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (854, N'قُلْ هُوَ الْقادِرُ عَلى أَنْ يَبْعَثَ عَلَيْكُمْ عَذاباً مِنْ فَوْقِكُمْ أَوْ مِنْ تَحْتِ أَرْجُلِكُمْ أَوْ يَلْبِسَكُمْ شِيَعاً وَ يُذِيقَ بَعْضَكُمْ بَأْسَ بَعْضٍ انْظُرْ كَيْفَ نُصَرِّفُ الْآياتِ لَعَلَّهُمْ يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (855, N'وَ كَذَّبَ بِهِ قَوْمُكَ وَ هُوَ الْحَقُّ قُلْ لَسْتُ عَلَيْكُمْ بِوَكِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (856, N'لِكُلِّ نَبَإٍ مُسْتَقَرٌّ وَ سَوْفَ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (857, N'وَ إِذا رَأَيْتَ الَّذِينَ يَخُوضُونَ فِي آياتِنا فَأَعْرِضْ عَنْهُمْ حَتَّى يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ وَ إِمَّا يُنْسِيَنَّكَ الشَّيْطانُ فَلا تَقْعُدْ بَعْدَ الذِّكْرى مَعَ الْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (858, N'وَ ما عَلَى الَّذِينَ يَتَّقُونَ مِنْ حِسابِهِمْ مِنْ شَيْ‏ءٍ وَ لكِنْ ذِكْرى لَعَلَّهُمْ يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (859, N'وَ ذَرِ الَّذِينَ اتَّخَذُوا دِينَهُمْ لَعِباً وَ لَهْواً وَ غَرَّتْهُمُ الْحَياةُ الدُّنْيا وَ ذَكِّرْ بِهِ أَنْ تُبْسَلَ نَفْسٌ بِما كَسَبَتْ لَيْسَ لَها مِنْ دُونِ اللَّهِ وَلِيٌّ وَ لا شَفِيعٌ وَ إِنْ تَعْدِلْ كُلَّ عَدْلٍ لا يُؤْخَذْ مِنْها أُولئِكَ الَّذِينَ أُبْسِلُوا بِما كَسَبُوا لَهُمْ شَرابٌ مِنْ حَمِيمٍ وَ عَذابٌ أَلِيمٌ بِما كانُوا يَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (860, N'قُلْ أَ نَدْعُوا مِنْ دُونِ اللَّهِ ما لا يَنْفَعُنا وَ لا يَضُرُّنا وَ نُرَدُّ عَلى أَعْقابِنا بَعْدَ إِذْ هَدانَا اللَّهُ كَالَّذِي اسْتَهْوَتْهُ الشَّياطِينُ فِي الْأَرْضِ حَيْرانَ لَهُ أَصْحابٌ يَدْعُونَهُ إِلَى الْهُدَى ائْتِنا قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدى وَ أُمِرْنا لِنُسْلِمَ لِرَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (861, N'وَ أَنْ أَقِيمُوا الصَّلاةَ وَ اتَّقُوهُ وَ هُوَ الَّذِي إِلَيْهِ تُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (862, N'وَ هُوَ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ بِالْحَقِّ وَ يَوْمَ يَقُولُ كُنْ فَيَكُونُ قَوْلُهُ الْحَقُّ وَ لَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِي الصُّورِ عالِمُ الْغَيْبِ وَ الشَّهادَةِ وَ هُوَ الْحَكِيمُ الْخَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (863, N'وَ إِذْ قالَ إِبْراهِيمُ لِأَبِيهِ آزَرَ أَ تَتَّخِذُ أَصْناماً آلِهَةً إِنِّي أَراكَ وَ قَوْمَكَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (864, N'وَ كَذلِكَ نُرِي إِبْراهِيمَ مَلَكُوتَ السَّماواتِ وَ الْأَرْضِ وَ لِيَكُونَ مِنَ الْمُوقِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (865, N'فَلَمَّا جَنَّ عَلَيْهِ اللَّيْلُ رَأى كَوْكَباً قالَ هذا رَبِّي فَلَمَّا أَفَلَ قالَ لا أُحِبُّ الْآفِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (866, N'فَلَمَّا رَأَى الْقَمَرَ بازِغاً قالَ هذا رَبِّي فَلَمَّا أَفَلَ قالَ لَئِنْ لَمْ يَهْدِنِي رَبِّي لَأَكُونَنَّ مِنَ الْقَوْمِ الضَّالِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (867, N'فَلَمَّا رَأَى الشَّمْسَ بازِغَةً قالَ هذا رَبِّي هذا أَكْبَرُ فَلَمَّا أَفَلَتْ قالَ يا قَوْمِ إِنِّي بَرِي‏ءٌ مِمَّا تُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (868, N'إِنِّي وَجَّهْتُ وَجْهِيَ لِلَّذِي فَطَرَ السَّماواتِ وَ الْأَرْضَ حَنِيفاً وَ ما أَنَا مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (869, N'وَ حاجَّهُ قَوْمُهُ قالَ أَ تُحاجُّونِّي فِي اللَّهِ وَ قَدْ هَدانِ وَ لا أَخافُ ما تُشْرِكُونَ بِهِ إِلاَّ أَنْ يَشاءَ رَبِّي شَيْئاً وَسِعَ رَبِّي كُلَّ شَيْ‏ءٍ عِلْماً أَ فَلا تَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (870, N'وَ كَيْفَ أَخافُ ما أَشْرَكْتُمْ وَ لا تَخافُونَ أَنَّكُمْ أَشْرَكْتُمْ بِاللَّهِ ما لَمْ يُنَزِّلْ بِهِ عَلَيْكُمْ سُلْطاناً فَأَيُّ الْفَرِيقَيْنِ أَحَقُّ بِالْأَمْنِ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (871, N'الَّذِينَ آمَنُوا وَ لَمْ يَلْبِسُوا إِيمانَهُمْ بِظُلْمٍ أُولئِكَ لَهُمُ الْأَمْنُ وَ هُمْ مُهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (872, N'وَ تِلْكَ حُجَّتُنا آتَيْناها إِبْراهِيمَ عَلى قَوْمِهِ نَرْفَعُ دَرَجاتٍ مَنْ نَشاءُ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (873, N'وَ وَهَبْنا لَهُ إِسْحاقَ وَ يَعْقُوبَ كُلاًّ هَدَيْنا وَ نُوحاً هَدَيْنا مِنْ قَبْلُ وَ مِنْ ذُرِّيَّتِهِ داوُدَ وَ سُلَيْمانَ وَ أَيُّوبَ وَ يُوسُفَ وَ مُوسى وَ هارُونَ وَ كَذلِكَ نَجْزِي الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (874, N'وَ زَكَرِيَّا وَ يَحْيى وَ عِيسى وَ إِلْياسَ كُلٌّ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (875, N'وَ إِسْماعِيلَ وَ الْيَسَعَ وَ يُونُسَ وَ لُوطاً وَ كلاًّ فَضَّلْنا عَلَى الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (876, N'وَ مِنْ آبائِهِمْ وَ ذُرِّيَّاتِهِمْ وَ إِخْوانِهِمْ وَ اجْتَبَيْناهُمْ وَ هَدَيْناهُمْ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (877, N'ذلِكَ هُدَى اللَّهِ يَهْدِي بِهِ مَنْ يَشاءُ مِنْ عِبادِهِ وَ لَوْ أَشْرَكُوا لَحَبِطَ عَنْهُمْ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (878, N'أُولئِكَ الَّذِينَ آتَيْناهُمُ الْكِتابَ وَ الْحُكْمَ وَ النُّبُوَّةَ فَإِنْ يَكْفُرْ بِها هؤُلاءِ فَقَدْ وَكَّلْنا بِها قَوْماً لَيْسُوا بِها بِكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (879, N'أُولئِكَ الَّذِينَ هَدَى اللَّهُ فَبِهُداهُمُ اقْتَدِهْ قُلْ لا أَسْئَلُكُمْ عَلَيْهِ أَجْراً إِنْ هُوَ إِلاَّ ذِكْرى لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (880, N'وَ ما قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ إِذْ قالُوا ما أَنْزَلَ اللَّهُ عَلى بَشَرٍ مِنْ شَيْ‏ءٍ قُلْ مَنْ أَنْزَلَ الْكِتابَ الَّذِي جاءَ بِهِ مُوسى نُوراً وَ هُدىً لِلنَّاسِ تَجْعَلُونَهُ قَراطِيسَ تُبْدُونَها وَ تُخْفُونَ كَثِيراً وَ عُلِّمْتُمْ ما لَمْ تَعْلَمُوا أَنْتُمْ وَ لا آباؤُكُمْ قُلِ اللَّهُ ثُمَّ ذَرْهُمْ فِي خَوْضِهِمْ يَلْعَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (881, N'وَ هذا كِتابٌ أَنْزَلْناهُ مُبارَكٌ مُصَدِّقُ الَّذِي بَيْنَ يَدَيْهِ وَ لِتُنْذِرَ أُمَّ الْقُرى وَ مَنْ حَوْلَها وَ الَّذِينَ يُؤْمِنُونَ بِالْآخِرَةِ يُؤْمِنُونَ بِهِ وَ هُمْ عَلى صَلاتِهِمْ يُحافِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (882, N'وَ مَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ كَذِباً أَوْ قالَ أُوحِيَ إِلَيَّ وَ لَمْ يُوحَ إِلَيْهِ شَيْ‏ءٌ وَ مَنْ قالَ سَأُنْزِلُ مِثْلَ ما أَنْزَلَ اللَّهُ وَ لَوْ تَرى إِذِ الظَّالِمُونَ فِي غَمَراتِ الْمَوْتِ وَ الْمَلائِكَةُ باسِطُوا أَيْدِيهِمْ أَخْرِجُوا أَنْفُسَكُمُ الْيَوْمَ تُجْزَوْنَ عَذابَ الْهُونِ بِما كُنْتُمْ تَقُولُونَ عَلَى اللَّهِ غَيْرَ الْحَقِّ وَ كُنْتُمْ عَنْ آياتِهِ تَسْتَكْبِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (883, N'وَ لَقَدْ جِئْتُمُونا فُرادى كَما خَلَقْناكُمْ أَوَّلَ مَرَّةٍ وَ تَرَكْتُمْ ما خَوَّلْناكُمْ وَراءَ ظُهُورِكُمْ وَ ما نَرى مَعَكُمْ شُفَعاءَكُمُ الَّذِينَ زَعَمْتُمْ أَنَّهُمْ فِيكُمْ شُرَكاءُ لَقَدْ تَقَطَّعَ بَيْنَكُمْ وَ ضَلَّ عَنْكُمْ ما كُنْتُمْ تَزْعُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (884, N'إِنَّ اللَّهَ فالِقُ الْحَبِّ وَ النَّوى يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَ مُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ذلِكُمُ اللَّهُ فَأَنَّى تُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (885, N'فالِقُ الْإِصْباحِ وَ جَعَلَ اللَّيْلَ سَكَناً وَ الشَّمْسَ وَ الْقَمَرَ حُسْباناً ذلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (886, N'وَ هُوَ الَّذِي جَعَلَ لَكُمُ النُّجُومَ لِتَهْتَدُوا بِها فِي ظُلُماتِ الْبَرِّ وَ الْبَحْرِ قَدْ فَصَّلْنَا الْآياتِ لِقَوْمٍ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (887, N'وَ هُوَ الَّذِي أَنْشَأَكُمْ مِنْ نَفْسٍ واحِدَةٍ فَمُسْتَقَرٌّ وَ مُسْتَوْدَعٌ قَدْ فَصَّلْنَا الْآياتِ لِقَوْمٍ يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (888, N'وَ هُوَ الَّذِي أَنْزَلَ مِنَ السَّماءِ ماءً فَأَخْرَجْنا بِهِ نَباتَ كُلِّ شَيْ‏ءٍ فَأَخْرَجْنا مِنْهُ خَضِراً نُخْرِجُ مِنْهُ حَبًّا مُتَراكِباً وَ مِنَ النَّخْلِ مِنْ طَلْعِها قِنْوانٌ دانِيَةٌ وَ جَنَّاتٍ مِنْ أَعْنابٍ وَ الزَّيْتُونَ وَ الرُّمَّانَ مُشْتَبِهاً وَ غَيْرَ مُتَشابِهٍ انْظُرُوا إِلى ثَمَرِهِ إِذا أَثْمَرَ وَ يَنْعِهِ إِنَّ فِي ذلِكُمْ لَآياتٍ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (889, N'وَ جَعَلُوا لِلَّهِ شُرَكاءَ الْجِنَّ وَ خَلَقَهُمْ وَ خَرَقُوا لَهُ بَنِينَ وَ بَناتٍ بِغَيْرِ عِلْمٍ سُبْحانَهُ وَ تَعالى عَمَّا يَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (890, N'بَدِيعُ السَّماواتِ وَ الْأَرْضِ أَنَّى يَكُونُ لَهُ وَلَدٌ وَ لَمْ تَكُنْ لَهُ صاحِبَةٌ وَ خَلَقَ كُلَّ شَيْ‏ءٍ وَ هُوَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (891, N'ذلِكُمُ اللَّهُ رَبُّكُمْ لا إِلهَ إِلاَّ هُوَ خالِقُ كُلِّ شَيْ‏ءٍ فَاعْبُدُوهُ وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ وَكِيلٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (892, N'لا تُدْرِكُهُ الْأَبْصارُ وَ هُوَ يُدْرِكُ الْأَبْصارَ وَ هُوَ اللَّطِيفُ الْخَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (893, N'قَدْ جاءَكُمْ بَصائِرُ مِنْ رَبِّكُمْ فَمَنْ أَبْصَرَ فَلِنَفْسِهِ وَ مَنْ عَمِيَ فَعَلَيْها وَ ما أَنَا عَلَيْكُمْ بِحَفِيظٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (894, N'وَ كَذلِكَ نُصَرِّفُ الْآياتِ وَ لِيَقُولُوا دَرَسْتَ وَ لِنُبَيِّنَهُ لِقَوْمٍ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (895, N'اتَّبِعْ ما أُوحِيَ إِلَيْكَ مِنْ رَبِّكَ لا إِلهَ إِلاَّ هُوَ وَ أَعْرِضْ عَنِ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (896, N'وَ لَوْ شاءَ اللَّهُ ما أَشْرَكُوا وَ ما جَعَلْناكَ عَلَيْهِمْ حَفِيظاً وَ ما أَنْتَ عَلَيْهِمْ بِوَكِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (897, N'وَ لا تَسُبُّوا الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ فَيَسُبُّوا اللَّهَ عَدْواً بِغَيْرِ عِلْمٍ كَذلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمْ ثُمَّ إِلى رَبِّهِمْ مَرْجِعُهُمْ فَيُنَبِّئُهُمْ بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (898, N'وَ أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمانِهِمْ لَئِنْ جاءَتْهُمْ آيَةٌ لَيُؤْمِنُنَّ بِها قُلْ إِنَّمَا الْآياتُ عِنْدَ اللَّهِ وَ ما يُشْعِرُكُمْ أَنَّها إِذا جاءَتْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (899, N'وَ نُقَلِّبُ أَفْئِدَتَهُمْ وَ أَبْصارَهُمْ كَما لَمْ يُؤْمِنُوا بِهِ أَوَّلَ مَرَّةٍ وَ نَذَرُهُمْ فِي طُغْيانِهِمْ يَعْمَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (900, N'وَ لَوْ أَنَّنا نَزَّلْنا إِلَيْهِمُ الْمَلائِكَةَ وَ كَلَّمَهُمُ الْمَوْتى وَ حَشَرْنا عَلَيْهِمْ كُلَّ شَيْ‏ءٍ قُبُلاً ما كانُوا لِيُؤْمِنُوا إِلاَّ أَنْ يَشاءَ اللَّهُ وَ لكِنَّ أَكْثَرَهُمْ يَجْهَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (901, N'وَ كَذلِكَ جَعَلْنا لِكُلِّ نَبِيٍّ عَدُوًّا شَياطِينَ الْإِنْسِ وَ الْجِنِّ يُوحِي بَعْضُهُمْ إِلى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوراً وَ لَوْ شاءَ رَبُّكَ ما فَعَلُوهُ فَذَرْهُمْ وَ ما يَفْتَرُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (902, N'وَ لِتَصْغى إِلَيْهِ أَفْئِدَةُ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ وَ لِيَرْضَوْهُ وَ لِيَقْتَرِفُوا ما هُمْ مُقْتَرِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (903, N'أَ فَغَيْرَ اللَّهِ أَبْتَغِي حَكَماً وَ هُوَ الَّذِي أَنْزَلَ إِلَيْكُمُ الْكِتابَ مُفَصَّلاً وَ الَّذِينَ آتَيْناهُمُ الْكِتابَ يَعْلَمُونَ أَنَّهُ مُنَزَّلٌ مِنْ رَبِّكَ بِالْحَقِّ فَلا تَكُونَنَّ مِنَ الْمُمْتَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (904, N'وَ تَمَّتْ كَلِمَةُ رَبِّكَ صِدْقاً وَ عَدْلاً لا مُبَدِّلَ لِكَلِماتِهِ وَ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (905, N'وَ إِنْ تُطِعْ أَكْثَرَ مَنْ فِي الْأَرْضِ يُضِلُّوكَ عَنْ سَبِيلِ اللَّهِ إِنْ يَتَّبِعُونَ إِلاَّ الظَّنَّ وَ إِنْ هُمْ إِلاَّ يَخْرُصُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (906, N'إِنَّ رَبَّكَ هُوَ أَعْلَمُ مَنْ يَضِلُّ عَنْ سَبِيلِهِ وَ هُوَ أَعْلَمُ بِالْمُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (907, N'فَكُلُوا مِمَّا ذُكِرَ اسْمُ اللَّهِ عَلَيْهِ إِنْ كُنْتُمْ بِآياتِهِ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (908, N'وَ ما لَكُمْ أَلاَّ تَأْكُلُوا مِمَّا ذُكِرَ اسْمُ اللَّهِ عَلَيْهِ وَ قَدْ فَصَّلَ لَكُمْ ما حَرَّمَ عَلَيْكُمْ إِلاَّ مَا اضْطُرِرْتُمْ إِلَيْهِ وَ إِنَّ كَثِيراً لَيُضِلُّونَ بِأَهْوائِهِمْ بِغَيْرِ عِلْمٍ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِالْمُعْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (909, N'وَ ذَرُوا ظاهِرَ الْإِثْمِ وَ باطِنَهُ إِنَّ الَّذِينَ يَكْسِبُونَ الْإِثْمَ سَيُجْزَوْنَ بِما كانُوا يَقْتَرِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (910, N'وَ لا تَأْكُلُوا مِمَّا لَمْ يُذْكَرِ اسْمُ اللَّهِ عَلَيْهِ وَ إِنَّهُ لَفِسْقٌ وَ إِنَّ الشَّياطِينَ لَيُوحُونَ إِلى أَوْلِيائِهِمْ لِيُجادِلُوكُمْ وَ إِنْ أَطَعْتُمُوهُمْ إِنَّكُمْ لَمُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (911, N'أَ وَ مَنْ كانَ مَيْتاً فَأَحْيَيْناهُ وَ جَعَلْنا لَهُ نُوراً يَمْشِي بِهِ فِي النَّاسِ كَمَنْ مَثَلُهُ فِي الظُّلُماتِ لَيْسَ بِخارِجٍ مِنْها كَذلِكَ زُيِّنَ لِلْكافِرِينَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (912, N'وَ كَذلِكَ جَعَلْنا فِي كُلِّ قَرْيَةٍ أَكابِرَ مُجْرِمِيها لِيَمْكُرُوا فِيها وَ ما يَمْكُرُونَ إِلاَّ بِأَنْفُسِهِمْ وَ ما يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (913, N'وَ إِذا جاءَتْهُمْ آيَةٌ قالُوا لَنْ نُؤْمِنَ حَتَّى نُؤْتى مِثْلَ ما أُوتِيَ رُسُلُ اللَّهِ اللَّهُ أَعْلَمُ حَيْثُ يَجْعَلُ رِسالَتَهُ سَيُصِيبُ الَّذِينَ أَجْرَمُوا صَغارٌ عِنْدَ اللَّهِ وَ عَذابٌ شَدِيدٌ بِما كانُوا يَمْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (914, N'فَمَنْ يُرِدِ اللَّهُ أَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْإِسْلامِ وَ مَنْ يُرِدْ أَنْ يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقاً حَرَجاً كَأَنَّما يَصَّعَّدُ فِي السَّماءِ كَذلِكَ يَجْعَلُ اللَّهُ الرِّجْسَ عَلَى الَّذِينَ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (915, N'وَ هذا صِراطُ رَبِّكَ مُسْتَقِيماً قَدْ فَصَّلْنَا الْآياتِ لِقَوْمٍ يَذَّكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (916, N'لَهُمْ دارُ السَّلامِ عِنْدَ رَبِّهِمْ وَ هُوَ وَلِيُّهُمْ بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (917, N'وَ يَوْمَ يَحْشُرُهُمْ جَمِيعاً يا مَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِنَ الْإِنْسِ وَ قالَ أَوْلِياؤُهُمْ مِنَ الْإِنْسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنا بِبَعْضٍ وَ بَلَغْنا أَجَلَنَا الَّذِي أَجَّلْتَ لَنا قالَ النَّارُ مَثْواكُمْ خالِدِينَ فِيها إِلاَّ ما شاءَ اللَّهُ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (918, N'وَ كَذلِكَ نُوَلِّي بَعْضَ الظَّالِمِينَ بَعْضاً بِما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (919, N'يا مَعْشَرَ الْجِنِّ وَ الْإِنْسِ أَ لَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ آياتِي وَ يُنْذِرُونَكُمْ لِقاءَ يَوْمِكُمْ هذا قالُوا شَهِدْنا عَلى أَنْفُسِنا وَ غَرَّتْهُمُ الْحَياةُ الدُّنْيا وَ شَهِدُوا عَلى أَنْفُسِهِمْ أَنَّهُمْ كانُوا كافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (920, N'ذلِكَ أَنْ لَمْ يَكُنْ رَبُّكَ مُهْلِكَ الْقُرى بِظُلْمٍ وَ أَهْلُها غافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (921, N'وَ لِكُلٍّ دَرَجاتٌ مِمَّا عَمِلُوا وَ ما رَبُّكَ بِغافِلٍ عَمَّا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (922, N'وَ رَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ إِنْ يَشَأْ يُذْهِبْكُمْ وَ يَسْتَخْلِفْ مِنْ بَعْدِكُمْ ما يَشاءُ كَما أَنْشَأَكُمْ مِنْ ذُرِّيَّةِ قَوْمٍ آخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (923, N'إِنَّ ما تُوعَدُونَ لَآتٍ وَ ما أَنْتُمْ بِمُعْجِزِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (924, N'قُلْ يا قَوْمِ اعْمَلُوا عَلى مَكانَتِكُمْ إِنِّي عامِلٌ فَسَوْفَ تَعْلَمُونَ مَنْ تَكُونُ لَهُ عاقِبَةُ الدَّارِ إِنَّهُ لا يُفْلِحُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (925, N'وَ جَعَلُوا لِلَّهِ مِمَّا ذَرَأَ مِنَ الْحَرْثِ وَ الْأَنْعامِ نَصِيباً فَقالُوا هذا لِلَّهِ بِزَعْمِهِمْ وَ هذا لِشُرَكائِنا فَما كانَ لِشُرَكائِهِمْ فَلا يَصِلُ إِلَى اللَّهِ وَ ما كانَ لِلَّهِ فَهُوَ يَصِلُ إِلى شُرَكائِهِمْ ساءَ ما يَحْكُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (926, N'وَ كَذلِكَ زَيَّنَ لِكَثِيرٍ مِنَ الْمُشْرِكِينَ قَتْلَ أَوْلادِهِمْ شُرَكاؤُهُمْ لِيُرْدُوهُمْ وَ لِيَلْبِسُوا عَلَيْهِمْ دِينَهُمْ وَ لَوْ شاءَ اللَّهُ ما فَعَلُوهُ فَذَرْهُمْ وَ ما يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (927, N'وَ قالُوا هذِهِ أَنْعامٌ وَ حَرْثٌ حِجْرٌ لا يَطْعَمُها إِلاَّ مَنْ نَشاءُ بِزَعْمِهِمْ وَ أَنْعامٌ حُرِّمَتْ ظُهُورُها وَ أَنْعامٌ لا يَذْكُرُونَ اسْمَ اللَّهِ عَلَيْهَا افْتِراءً عَلَيْهِ سَيَجْزِيهِمْ بِما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (928, N'وَ قالُوا ما فِي بُطُونِ هذِهِ الْأَنْعامِ خالِصَةٌ لِذُكُورِنا وَ مُحَرَّمٌ عَلى أَزْواجِنا وَ إِنْ يَكُنْ مَيْتَةً فَهُمْ فِيهِ شُرَكاءُ سَيَجْزِيهِمْ وَصْفَهُمْ إِنَّهُ حَكِيمٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (929, N'قَدْ خَسِرَ الَّذِينَ قَتَلُوا أَوْلادَهُمْ سَفَهاً بِغَيْرِ عِلْمٍ وَ حَرَّمُوا ما رَزَقَهُمُ اللَّهُ افْتِراءً عَلَى اللَّهِ قَدْ ضَلُّوا وَ ما كانُوا مُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (930, N'وَ هُوَ الَّذِي أَنْشَأَ جَنَّاتٍ مَعْرُوشاتٍ وَ غَيْرَ مَعْرُوشاتٍ وَ النَّخْلَ وَ الزَّرْعَ مُخْتَلِفاً أُكُلُهُ وَ الزَّيْتُونَ وَ الرُّمَّانَ مُتَشابِهاً وَ غَيْرَ مُتَشابِهٍ كُلُوا مِنْ ثَمَرِهِ إِذا أَثْمَرَ وَ آتُوا حَقَّهُ يَوْمَ حَصادِهِ وَ لا تُسْرِفُوا إِنَّهُ لا يُحِبُّ الْمُسْرِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (931, N'وَ مِنَ الْأَنْعامِ حَمُولَةً وَ فَرْشاً كُلُوا مِمَّا رَزَقَكُمُ اللَّهُ وَ لا تَتَّبِعُوا خُطُواتِ الشَّيْطانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (932, N'ثَمانِيَةَ أَزْواجٍ مِنَ الضَّأْنِ اثْنَيْنِ وَ مِنَ الْمَعْزِ اثْنَيْنِ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الْأُنْثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحامُ الْأُنْثَيَيْنِ نَبِّئُونِي بِعِلْمٍ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (933, N'وَ مِنَ الْإِبِلِ اثْنَيْنِ وَ مِنَ الْبَقَرِ اثْنَيْنِ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الْأُنْثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحامُ الْأُنْثَيَيْنِ أَمْ كُنْتُمْ شُهَداءَ إِذْ وَصَّاكُمُ اللَّهُ بِهذا فَمَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ كَذِباً لِيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍ إِنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (934, N'قُلْ لا أَجِدُ فِي ما أُوحِيَ إِلَيَّ مُحَرَّماً عَلى طاعِمٍ يَطْعَمُهُ إِلاَّ أَنْ يَكُونَ مَيْتَةً أَوْ دَماً مَسْفُوحاً أَوْ لَحْمَ خِنزِيرٍ فَإِنَّهُ رِجْسٌ أَوْ فِسْقاً أُهِلَّ لِغَيْرِ اللَّهِ بِهِ فَمَنِ اضْطُرَّ غَيْرَ باغٍ وَ لا عادٍ فَإِنَّ رَبَّكَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (935, N'وَ عَلَى الَّذِينَ هادُوا حَرَّمْنا كُلَّ ذِي ظُفُرٍ وَ مِنَ الْبَقَرِ وَ الْغَنَمِ حَرَّمْنا عَلَيْهِمْ شُحُومَهُما إِلاَّ ما حَمَلَتْ ظُهُورُهُما أَوِ الْحَوايا أَوْ مَا اخْتَلَطَ بِعَظْمٍ ذلِكَ جَزَيْناهُمْ بِبَغْيِهِمْ وَ إِنَّا لَصادِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (936, N'فَإِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُو رَحْمَةٍ واسِعَةٍ وَ لا يُرَدُّ بَأْسُهُ عَنِ الْقَوْمِ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (937, N'سَيَقُولُ الَّذِينَ أَشْرَكُوا لَوْ شاءَ اللَّهُ ما أَشْرَكْنا وَ لا آباؤُنا وَ لا حَرَّمْنا مِنْ شَيْ‏ءٍ كَذلِكَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ حَتَّى ذاقُوا بَأْسَنا قُلْ هَلْ عِنْدَكُمْ مِنْ عِلْمٍ فَتُخْرِجُوهُ لَنا إِنْ تَتَّبِعُونَ إِلاَّ الظَّنَّ وَ إِنْ أَنْتُمْ إِلاَّ تَخْرُصُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (938, N'قُلْ فَلِلَّهِ الْحُجَّةُ الْبالِغَةُ فَلَوْ شاءَ لَهَداكُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (939, N'قُلْ هَلُمَّ شُهَداءَكُمُ الَّذِينَ يَشْهَدُونَ أَنَّ اللَّهَ حَرَّمَ هذا فَإِنْ شَهِدُوا فَلا تَشْهَدْ مَعَهُمْ وَ لا تَتَّبِعْ أَهْواءَ الَّذِينَ كَذَّبُوا بِآياتِنا وَ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ وَ هُمْ بِرَبِّهِمْ يَعْدِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (940, N'قُلْ تَعالَوْا أَتْلُ ما حَرَّمَ رَبُّكُمْ عَلَيْكُمْ أَلاَّ تُشْرِكُوا بِهِ شَيْئاً وَ بِالْوالِدَيْنِ إِحْساناً وَ لا تَقْتُلُوا أَوْلادَكُمْ مِنْ إِمْلاقٍ نَحْنُ نَرْزُقُكُمْ وَ إِيَّاهُمْ وَ لا تَقْرَبُوا الْفَواحِشَ ما ظَهَرَ مِنْها وَ ما بَطَنَ وَ لا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلاَّ بِالْحَقِّ ذلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (941, N'وَ لا تَقْرَبُوا مالَ الْيَتِيمِ إِلاَّ بِالَّتِي هِيَ أَحْسَنُ حَتَّى يَبْلُغَ أَشُدَّهُ وَ أَوْفُوا الْكَيْلَ وَ الْمِيزانَ بِالْقِسْطِ لا نُكَلِّفُ نَفْساً إِلاَّ وُسْعَها وَ إِذا قُلْتُمْ فَاعْدِلُوا وَ لَوْ كانَ ذا قُرْبى وَ بِعَهْدِ اللَّهِ أَوْفُوا ذلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (942, N'وَ أَنَّ هذا صِراطِي مُسْتَقِيماً فَاتَّبِعُوهُ وَ لا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيلِهِ ذلِكُمْ وَصَّاكُمْ بِهِ لَعَلَّكُمْ تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (943, N'ثُمَّ آتَيْنا مُوسَى الْكِتابَ تَماماً عَلَى الَّذِي أَحْسَنَ وَ تَفْصِيلاً لِكُلِّ شَيْ‏ءٍ وَ هُدىً وَ رَحْمَةً لَعَلَّهُمْ بِلِقاءِ رَبِّهِمْ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (944, N'وَ هذا كِتابٌ أَنْزَلْناهُ مُبارَكٌ فَاتَّبِعُوهُ وَ اتَّقُوا لَعَلَّكُمْ تُرْحَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (945, N'أَنْ تَقُولُوا إِنَّما أُنْزِلَ الْكِتابُ عَلى طائِفَتَيْنِ مِنْ قَبْلِنا وَ إِنْ كُنَّا عَنْ دِراسَتِهِمْ لَغافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (946, N'أَوْ تَقُولُوا لَوْ أَنَّا أُنْزِلَ عَلَيْنَا الْكِتابُ لَكُنَّا أَهْدى مِنْهُمْ فَقَدْ جاءَكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ وَ هُدىً وَ رَحْمَةٌ فَمَنْ أَظْلَمُ مِمَّنْ كَذَّبَ بِآياتِ اللَّهِ وَ صَدَفَ عَنْها سَنَجْزِي الَّذِينَ يَصْدِفُونَ عَنْ آياتِنا سُوءَ الْعَذابِ بِما كانُوا يَصْدِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (947, N'هَلْ يَنْظُرُونَ إِلاَّ أَنْ تَأْتِيَهُمُ الْمَلائِكَةُ أَوْ يَأْتِيَ رَبُّكَ أَوْ يَأْتِيَ بَعْضُ آياتِ رَبِّكَ يَوْمَ يَأْتِي بَعْضُ آياتِ رَبِّكَ لا يَنْفَعُ نَفْساً إِيمانُها لَمْ تَكُنْ آمَنَتْ مِنْ قَبْلُ أَوْ كَسَبَتْ فِي إِيمانِها خَيْراً قُلِ انْتَظِرُوا إِنَّا مُنْتَظِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (948, N'إِنَّ الَّذِينَ فَرَّقُوا دِينَهُمْ وَ كانُوا شِيَعاً لَسْتَ مِنْهُمْ فِي شَيْ‏ءٍ إِنَّما أَمْرُهُمْ إِلَى اللَّهِ ثُمَّ يُنَبِّئُهُمْ بِما كانُوا يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (949, N'مَنْ جاءَ بِالْحَسَنَةِ فَلَهُ عَشْرُ أَمْثالِها وَ مَنْ جاءَ بِالسَّيِّئَةِ فَلا يُجْزى إِلاَّ مِثْلَها وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (950, N'قُلْ إِنَّنِي هَدانِي رَبِّي إِلى صِراطٍ مُسْتَقِيمٍ دِيناً قِيَماً مِلَّةَ إِبْراهِيمَ حَنِيفاً وَ ما كانَ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (951, N'قُلْ إِنَّ صَلاتِي وَ نُسُكِي وَ مَحْيايَ وَ مَماتِي لِلَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (952, N'لا شَرِيكَ لَهُ وَ بِذلِكَ أُمِرْتُ وَ أَنَا أَوَّلُ الْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (953, N'قُلْ أَ غَيْرَ اللَّهِ أَبْغِي رَبًّا وَ هُوَ رَبُّ كُلِّ شَيْ‏ءٍ وَ لا تَكْسِبُ كُلُّ نَفْسٍ إِلاَّ عَلَيْها وَ لا تَزِرُ وازِرَةٌ وِزْرَ أُخْرى ثُمَّ إِلى رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِما كُنْتُمْ فِيهِ تَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (954, N'وَ هُوَ الَّذِي جَعَلَكُمْ خَلائِفَ الْأَرْضِ وَ رَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجاتٍ لِيَبْلُوَكُمْ فِي ما آتاكُمْ إِنَّ رَبَّكَ سَرِيعُ الْعِقابِ وَ إِنَّهُ لَغَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (955, N'المص ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (956, N'كِتابٌ أُنْزِلَ إِلَيْكَ فَلا يَكُنْ فِي صَدْرِكَ حَرَجٌ مِنْهُ لِتُنْذِرَ بِهِ وَ ذِكْرى لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (957, N'اتَّبِعُوا ما أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْ وَ لا تَتَّبِعُوا مِنْ دُونِهِ أَوْلِياءَ قَلِيلاً ما تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (958, N'وَ كَمْ مِنْ قَرْيَةٍ أَهْلَكْناها فَجاءَها بَأْسُنا بَياتاً أَوْ هُمْ قائِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (959, N'فَما كانَ دَعْواهُمْ إِذْ جاءَهُمْ بَأْسُنا إِلاَّ أَنْ قالُوا إِنَّا كُنَّا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (960, N'فَلَنَسْئَلَنَّ الَّذِينَ أُرْسِلَ إِلَيْهِمْ وَ لَنَسْئَلَنَّ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (961, N'فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَ ما كُنَّا غائِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (962, N'وَ الْوَزْنُ يَوْمَئِذٍ الْحَقُّ فَمَنْ ثَقُلَتْ مَوازِينُهُ فَأُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (963, N'وَ مَنْ خَفَّتْ مَوازِينُهُ فَأُولئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ بِما كانُوا بِآياتِنا يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (964, N'وَ لَقَدْ مَكَّنَّاكُمْ فِي الْأَرْضِ وَ جَعَلْنا لَكُمْ فِيها مَعايِشَ قَلِيلاً ما تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (965, N'وَ لَقَدْ خَلَقْناكُمْ ثُمَّ صَوَّرْناكُمْ ثُمَّ قُلْنا لِلْمَلائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاَّ إِبْلِيسَ لَمْ يَكُنْ مِنَ السَّاجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (966, N'قالَ ما مَنَعَكَ أَلاَّ تَسْجُدَ إِذْ أَمَرْتُكَ قالَ أَنَا خَيْرٌ مِنْهُ خَلَقْتَنِي مِنْ نارٍ وَ خَلَقْتَهُ مِنْ طِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (967, N'قالَ فَاهْبِطْ مِنْها فَما يَكُونُ لَكَ أَنْ تَتَكَبَّرَ فِيها فَاخْرُجْ إِنَّكَ مِنَ الصَّاغِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (968, N'قالَ أَنْظِرْنِي إِلى يَوْمِ يُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (969, N'قالَ إِنَّكَ مِنَ الْمُنْظَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (970, N'قالَ فَبِما أَغْوَيْتَنِي لَأَقْعُدَنَّ لَهُمْ صِراطَكَ الْمُسْتَقِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (971, N'ثُمَّ لَآتِيَنَّهُمْ مِنْ بَيْنِ أَيْدِيهِمْ وَ مِنْ خَلْفِهِمْ وَ عَنْ أَيْمانِهِمْ وَ عَنْ شَمائِلِهِمْ وَ لا تَجِدُ أَكْثَرَهُمْ شاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (972, N'قالَ اخْرُجْ مِنْها مَذْؤُماً مَدْحُوراً لَمَنْ تَبِعَكَ مِنْهُمْ لَأَمْلَأَنَّ جَهَنَّمَ مِنْكُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (973, N'وَ يا آدَمُ اسْكُنْ أَنْتَ وَ زَوْجُكَ الْجَنَّةَ فَكُلا مِنْ حَيْثُ شِئْتُما وَ لا تَقْرَبا هذِهِ الشَّجَرَةَ فَتَكُونا مِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (974, N'فَوَسْوَسَ لَهُمَا الشَّيْطانُ لِيُبْدِيَ لَهُما ما وُورِيَ عَنْهُما مِنْ سَوْآتِهِما وَ قالَ ما نَهاكُما رَبُّكُما عَنْ هذِهِ الشَّجَرَةِ إِلاَّ أَنْ تَكُونا مَلَكَيْنِ أَوْ تَكُونا مِنَ الْخالِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (975, N'وَ قاسَمَهُما إِنِّي لَكُما لَمِنَ النَّاصِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (976, N'فَدَلاَّهُما بِغُرُورٍ فَلَمَّا ذاقَا الشَّجَرَةَ بَدَتْ لَهُما سَوْآتُهُما وَ طَفِقا يَخْصِفانِ عَلَيْهِما مِنْ وَرَقِ الْجَنَّةِ وَ ناداهُما رَبُّهُما أَ لَمْ أَنْهَكُما عَنْ تِلْكُمَا الشَّجَرَةِ وَ أَقُلْ لَكُما إِنَّ الشَّيْطانَ لَكُما عَدُوٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (977, N'قالا رَبَّنا ظَلَمْنا أَنْفُسَنا وَ إِنْ لَمْ تَغْفِرْ لَنا وَ تَرْحَمْنا لَنَكُونَنَّ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (978, N'قالَ اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ وَ لَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَ مَتاعٌ إِلى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (979, N'قالَ فِيها تَحْيَوْنَ وَ فِيها تَمُوتُونَ وَ مِنْها تُخْرَجُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (980, N'يا بَنِي آدَمَ قَدْ أَنْزَلْنا عَلَيْكُمْ لِباساً يُوارِي سَوْآتِكُمْ وَ رِيشاً وَ لِباسُ التَّقْوى ذلِكَ خَيْرٌ ذلِكَ مِنْ آياتِ اللَّهِ لَعَلَّهُمْ يَذَّكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (981, N'يا بَنِي آدَمَ لا يَفْتِنَنَّكُمُ الشَّيْطانُ كَما أَخْرَجَ أَبَوَيْكُمْ مِنَ الْجَنَّةِ يَنْزِعُ عَنْهُما لِباسَهُما لِيُرِيَهُما سَوْآتِهِما إِنَّهُ يَراكُمْ هُوَ وَ قَبِيلُهُ مِنْ حَيْثُ لا تَرَوْنَهُمْ إِنَّا جَعَلْنَا الشَّياطِينَ أَوْلِياءَ لِلَّذِينَ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (982, N'وَ إِذا فَعَلُوا فاحِشَةً قالُوا وَجَدْنا عَلَيْها آباءَنا وَ اللَّهُ أَمَرَنا بِها قُلْ إِنَّ اللَّهَ لا يَأْمُرُ بِالْفَحْشاءِ أَ تَقُولُونَ عَلَى اللَّهِ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (983, N'قُلْ أَمَرَ رَبِّي بِالْقِسْطِ وَ أَقِيمُوا وُجُوهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَ ادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ كَما بَدَأَكُمْ تَعُودُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (984, N'فَرِيقاً هَدى وَ فَرِيقاً حَقَّ عَلَيْهِمُ الضَّلالَةُ إِنَّهُمُ اتَّخَذُوا الشَّياطِينَ أَوْلِياءَ مِنْ دُونِ اللَّهِ وَ يَحْسَبُونَ أَنَّهُمْ مُهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (985, N'يا بَنِي آدَمَ خُذُوا زِينَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَ كُلُوا وَ اشْرَبُوا وَ لا تُسْرِفُوا إِنَّهُ لا يُحِبُّ الْمُسْرِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (986, N'قُلْ مَنْ حَرَّمَ زِينَةَ اللَّهِ الَّتِي أَخْرَجَ لِعِبادِهِ وَ الطَّيِّباتِ مِنَ الرِّزْقِ قُلْ هِيَ لِلَّذِينَ آمَنُوا فِي الْحَياةِ الدُّنْيا خالِصَةً يَوْمَ الْقِيامَةِ كَذلِكَ نُفَصِّلُ الْآياتِ لِقَوْمٍ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (987, N'قُلْ إِنَّما حَرَّمَ رَبِّيَ الْفَواحِشَ ما ظَهَرَ مِنْها وَ ما بَطَنَ وَ الْإِثْمَ وَ الْبَغْيَ بِغَيْرِ الْحَقِّ وَ أَنْ تُشْرِكُوا بِاللَّهِ ما لَمْ يُنَزِّلْ بِهِ سُلْطاناً وَ أَنْ تَقُولُوا عَلَى اللَّهِ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (988, N'وَ لِكُلِّ أُمَّةٍ أَجَلٌ فَإِذا جاءَ أَجَلُهُمْ لا يَسْتَأْخِرُونَ ساعَةً وَ لا يَسْتَقْدِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (989, N'يا بَنِي آدَمَ إِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ آياتِي فَمَنِ اتَّقى وَ أَصْلَحَ فَلا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (990, N'وَ الَّذِينَ كَذَّبُوا بِآياتِنا وَ اسْتَكْبَرُوا عَنْها أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (991, N'فَمَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ كَذِباً أَوْ كَذَّبَ بِآياتِهِ أُولئِكَ يَنالُهُمْ نَصِيبُهُمْ مِنَ الْكِتابِ حَتَّى إِذا جاءَتْهُمْ رُسُلُنا يَتَوَفَّوْنَهُمْ قالُوا أَيْنَ ما كُنْتُمْ تَدْعُونَ مِنْ دُونِ اللَّهِ قالُوا ضَلُّوا عَنَّا وَ شَهِدُوا عَلى أَنْفُسِهِمْ أَنَّهُمْ كانُوا كافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (992, N'قالَ ادْخُلُوا فِي أُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِنَ الْجِنِّ وَ الْإِنْسِ فِي النَّارِ كُلَّما دَخَلَتْ أُمَّةٌ لَعَنَتْ أُخْتَها حَتَّى إِذَا ادَّارَكُوا فِيها جَمِيعاً قالَتْ أُخْراهُمْ لِأُولاهُمْ رَبَّنا هؤُلاءِ أَضَلُّونا فَآتِهِمْ عَذاباً ضِعْفاً مِنَ النَّارِ قالَ لِكُلٍّ ضِعْفٌ وَ لكِنْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (993, N'وَ قالَتْ أُولاهُمْ لِأُخْراهُمْ فَما كانَ لَكُمْ عَلَيْنا مِنْ فَضْلٍ فَذُوقُوا الْعَذابَ بِما كُنْتُمْ تَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (994, N'إِنَّ الَّذِينَ كَذَّبُوا بِآياتِنا وَ اسْتَكْبَرُوا عَنْها لا تُفَتَّحُ لَهُمْ أَبْوابُ السَّماءِ وَ لا يَدْخُلُونَ الْجَنَّةَ حَتَّى يَلِجَ الْجَمَلُ فِي سَمِّ الْخِياطِ وَ كَذلِكَ نَجْزِي الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (995, N'لَهُمْ مِنْ جَهَنَّمَ مِهادٌ وَ مِنْ فَوْقِهِمْ غَواشٍ وَ كَذلِكَ نَجْزِي الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (996, N'وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لا نُكَلِّفُ نَفْساً إِلاَّ وُسْعَها أُولئِكَ أَصْحابُ الْجَنَّةِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (997, N'وَ نَزَعْنا ما فِي صُدُورِهِمْ مِنْ غِلٍّ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهارُ وَ قالُوا الْحَمْدُ لِلَّهِ الَّذِي هَدانا لِهذا وَ ما كُنَّا لِنَهْتَدِيَ لَوْ لا أَنْ هَدانَا اللَّهُ لَقَدْ جاءَتْ رُسُلُ رَبِّنا بِالْحَقِّ وَ نُودُوا أَنْ تِلْكُمُ الْجَنَّةُ أُورِثْتُمُوها بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (998, N'وَ نادى أَصْحابُ الْجَنَّةِ أَصْحابَ النَّارِ أَنْ قَدْ وَجَدْنا ما وَعَدَنا رَبُّنا حَقًّا فَهَلْ وَجَدْتُمْ ما وَعَدَ رَبُّكُمْ حَقًّا قالُوا نَعَمْ فَأَذَّنَ مُؤَذِّنٌ بَيْنَهُمْ أَنْ لَعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (999, N'الَّذِينَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَ يَبْغُونَها عِوَجاً وَ هُمْ بِالْآخِرَةِ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1000, N'وَ بَيْنَهُما حِجابٌ وَ عَلَى الْأَعْرافِ رِجالٌ يَعْرِفُونَ كُلاًّ بِسِيماهُمْ وَ نادَوْا أَصْحابَ الْجَنَّةِ أَنْ سَلامٌ عَلَيْكُمْ لَمْ يَدْخُلُوها وَ هُمْ يَطْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1001, N'وَ إِذا صُرِفَتْ أَبْصارُهُمْ تِلْقاءَ أَصْحابِ النَّارِ قالُوا رَبَّنا لا تَجْعَلْنا مَعَ الْقَوْمِ الظَّالِمِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1002, N'وَ نادى أَصْحابُ الْأَعْرافِ رِجالاً يَعْرِفُونَهُمْ بِسِيماهُمْ قالُوا ما أَغْنى عَنْكُمْ جَمْعُكُمْ وَ ما كُنْتُمْ تَسْتَكْبِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1003, N'أَ هؤُلاءِ الَّذِينَ أَقْسَمْتُمْ لا يَنالُهُمُ اللَّهُ بِرَحْمَةٍ ادْخُلُوا الْجَنَّةَ لا خَوْفٌ عَلَيْكُمْ وَ لا أَنْتُمْ تَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1004, N'وَ نادى أَصْحابُ النَّارِ أَصْحابَ الْجَنَّةِ أَنْ أَفِيضُوا عَلَيْنا مِنَ الْماءِ أَوْ مِمَّا رَزَقَكُمُ اللَّهُ قالُوا إِنَّ اللَّهَ حَرَّمَهُما عَلَى الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1005, N'الَّذِينَ اتَّخَذُوا دِينَهُمْ لَهْواً وَ لَعِباً وَ غَرَّتْهُمُ الْحَياةُ الدُّنْيا فَالْيَوْمَ نَنْساهُمْ كَما نَسُوا لِقاءَ يَوْمِهِمْ هذا وَ ما كانُوا بِآياتِنا يَجْحَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1006, N'وَ لَقَدْ جِئْناهُمْ بِكِتابٍ فَصَّلْناهُ عَلى عِلْمٍ هُدىً وَ رَحْمَةً لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1007, N'هَلْ يَنْظُرُونَ إِلاَّ تَأْوِيلَهُ يَوْمَ يَأْتِي تَأْوِيلُهُ يَقُولُ الَّذِينَ نَسُوهُ مِنْ قَبْلُ قَدْ جاءَتْ رُسُلُ رَبِّنا بِالْحَقِّ فَهَلْ لَنا مِنْ شُفَعاءَ فَيَشْفَعُوا لَنا أَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِي كُنَّا نَعْمَلُ قَدْ خَسِرُوا أَنْفُسَهُمْ وَ ضَلَّ عَنْهُمْ ما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1008, N'إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوى عَلَى الْعَرْشِ يُغْشِي اللَّيْلَ النَّهارَ يَطْلُبُهُ حَثِيثاً وَ الشَّمْسَ وَ الْقَمَرَ وَ النُّجُومَ مُسَخَّراتٍ بِأَمْرِهِ أَلا لَهُ الْخَلْقُ وَ الْأَمْرُ تَبارَكَ اللَّهُ رَبُّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1009, N'ادْعُوا رَبَّكُمْ تَضَرُّعاً وَ خُفْيَةً إِنَّهُ لا يُحِبُّ الْمُعْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1010, N'وَ لا تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلاحِها وَ ادْعُوهُ خَوْفاً وَ طَمَعاً إِنَّ رَحْمَتَ اللَّهِ قَرِيبٌ مِنَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1011, N'وَ هُوَ الَّذِي يُرْسِلُ الرِّياحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِهِ حَتَّى إِذا أَقَلَّتْ سَحاباً ثِقالاً سُقْناهُ لِبَلَدٍ مَيِّتٍ فَأَنْزَلْنا بِهِ الْماءَ فَأَخْرَجْنا بِهِ مِنْ كُلِّ الثَّمَراتِ كَذلِكَ نُخْرِجُ الْمَوْتى لَعَلَّكُمْ تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1012, N'وَ الْبَلَدُ الطَّيِّبُ يَخْرُجُ نَباتُهُ بِإِذْنِ رَبِّهِ وَ الَّذِي خَبُثَ لا يَخْرُجُ إِلاَّ نَكِداً كَذلِكَ نُصَرِّفُ الْآياتِ لِقَوْمٍ يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1013, N'لَقَدْ أَرْسَلْنا نُوحاً إِلى قَوْمِهِ فَقالَ يا قَوْمِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ إِنِّي أَخافُ عَلَيْكُمْ عَذابَ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1014, N'قالَ الْمَلَأُ مِنْ قَوْمِهِ إِنَّا لَنَراكَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1015, N'قالَ يا قَوْمِ لَيْسَ بِي ضَلالَةٌ وَ لكِنِّي رَسُولٌ مِنْ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1016, N'أُبَلِّغُكُمْ رِسالاتِ رَبِّي وَ أَنْصَحُ لَكُمْ وَ أَعْلَمُ مِنَ اللَّهِ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1017, N'أَ وَ عَجِبْتُمْ أَنْ جاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَ لِتَتَّقُوا وَ لَعَلَّكُمْ تُرْحَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1018, N'فَكَذَّبُوهُ فَأَنْجَيْناهُ وَ الَّذِينَ مَعَهُ فِي الْفُلْكِ وَ أَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآياتِنا إِنَّهُمْ كانُوا قَوْماً عَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1019, N'وَ إِلى عادٍ أَخاهُمْ هُوداً قالَ يا قَوْمِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ أَ فَلا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1020, N'قالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ إِنَّا لَنَراكَ فِي سَفاهَةٍ وَ إِنَّا لَنَظُنُّكَ مِنَ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1021, N'قالَ يا قَوْمِ لَيْسَ بِي سَفاهَةٌ وَ لكِنِّي رَسُولٌ مِنْ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1022, N'أُبَلِّغُكُمْ رِسالاتِ رَبِّي وَ أَنَا لَكُمْ ناصِحٌ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1023, N'أَ وَ عَجِبْتُمْ أَنْ جاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلى رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَ اذْكُرُوا إِذْ جَعَلَكُمْ خُلَفاءَ مِنْ بَعْدِ قَوْمِ نُوحٍ وَ زادَكُمْ فِي الْخَلْقِ بَصْطَةً فَاذْكُرُوا آلاءَ اللَّهِ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1024, N'قالُوا أَ جِئْتَنا لِنَعْبُدَ اللَّهَ وَحْدَهُ وَ نَذَرَ ما كانَ يَعْبُدُ آباؤُنا فَأْتِنا بِما تَعِدُنا إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1025, N'قالَ قَدْ وَقَعَ عَلَيْكُمْ مِنْ رَبِّكُمْ رِجْسٌ وَ غَضَبٌ أَ تُجادِلُونَنِي فِي أَسْماءٍ سَمَّيْتُمُوها أَنْتُمْ وَ آباؤُكُمْ ما نَزَّلَ اللَّهُ بِها مِنْ سُلْطانٍ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1026, N'فَأَنْجَيْناهُ وَ الَّذِينَ مَعَهُ بِرَحْمَةٍ مِنَّا وَ قَطَعْنا دابِرَ الَّذِينَ كَذَّبُوا بِآياتِنا وَ ما كانُوا مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1027, N'وَ إِلى ثَمُودَ أَخاهُمْ صالِحاً قالَ يا قَوْمِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ قَدْ جاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ هذِهِ ناقَةُ اللَّهِ لَكُمْ آيَةً فَذَرُوها تَأْكُلْ فِي أَرْضِ اللَّهِ وَ لا تَمَسُّوها بِسُوءٍ فَيَأْخُذَكُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1028, N'وَ اذْكُرُوا إِذْ جَعَلَكُمْ خُلَفاءَ مِنْ بَعْدِ عادٍ وَ بَوَّأَكُمْ فِي الْأَرْضِ تَتَّخِذُونَ مِنْ سُهُولِها قُصُوراً وَ تَنْحِتُونَ الْجِبالَ بُيُوتاً فَاذْكُرُوا آلاءَ اللَّهِ وَ لا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1029, N'قالَ الْمَلَأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لِلَّذِينَ اسْتُضْعِفُوا لِمَنْ آمَنَ مِنْهُمْ أَ تَعْلَمُونَ أَنَّ صالِحاً مُرْسَلٌ مِنْ رَبِّهِ قالُوا إِنَّا بِما أُرْسِلَ بِهِ مُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1030, N'قالَ الَّذِينَ اسْتَكْبَرُوا إِنَّا بِالَّذِي آمَنْتُمْ بِهِ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1031, N'فَعَقَرُوا النَّاقَةَ وَ عَتَوْا عَنْ أَمْرِ رَبِّهِمْ وَ قالُوا يا صالِحُ ائْتِنا بِما تَعِدُنا إِنْ كُنْتَ مِنَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1032, N'فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دارِهِمْ جاثِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1033, N'فَتَوَلَّى عَنْهُمْ وَ قالَ يا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسالَةَ رَبِّي وَ نَصَحْتُ لَكُمْ وَ لكِنْ لا تُحِبُّونَ النَّاصِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1034, N'وَ لُوطاً إِذْ قالَ لِقَوْمِهِ أَ تَأْتُونَ الْفاحِشَةَ ما سَبَقَكُمْ بِها مِنْ أَحَدٍ مِنَ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1035, N'إِنَّكُمْ لَتَأْتُونَ الرِّجالَ شَهْوَةً مِنْ دُونِ النِّساءِ بَلْ أَنْتُمْ قَوْمٌ مُسْرِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1036, N'وَ ما كانَ جَوابَ قَوْمِهِ إِلاَّ أَنْ قالُوا أَخْرِجُوهُمْ مِنْ قَرْيَتِكُمْ إِنَّهُمْ أُناسٌ يَتَطَهَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1037, N'فَأَنْجَيْناهُ وَ أَهْلَهُ إِلاَّ امْرَأَتَهُ كانَتْ مِنَ الْغابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1038, N'وَ أَمْطَرْنا عَلَيْهِمْ مَطَراً فَانْظُرْ كَيْفَ كانَ عاقِبَةُ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1039, N'وَ إِلى مَدْيَنَ أَخاهُمْ شُعَيْباً قالَ يا قَوْمِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ قَدْ جاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ فَأَوْفُوا الْكَيْلَ وَ الْمِيزانَ وَ لا تَبْخَسُوا النَّاسَ أَشْياءَهُمْ وَ لا تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلاحِها ذلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1040, N'وَ لا تَقْعُدُوا بِكُلِّ صِراطٍ تُوعِدُونَ وَ تَصُدُّونَ عَنْ سَبِيلِ اللَّهِ مَنْ آمَنَ بِهِ وَ تَبْغُونَها عِوَجاً وَ اذْكُرُوا إِذْ كُنْتُمْ قَلِيلاً فَكَثَّرَكُمْ وَ انْظُرُوا كَيْفَ كانَ عاقِبَةُ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1041, N'وَ إِنْ كانَ طائِفَةٌ مِنْكُمْ آمَنُوا بِالَّذِي أُرْسِلْتُ بِهِ وَ طائِفَةٌ لَمْ يُؤْمِنُوا فَاصْبِرُوا حَتَّى يَحْكُمَ اللَّهُ بَيْنَنا وَ هُوَ خَيْرُ الْحاكِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1042, N'قالَ الْمَلَأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لَنُخْرِجَنَّكَ يا شُعَيْبُ وَ الَّذِينَ آمَنُوا مَعَكَ مِنْ قَرْيَتِنا أَوْ لَتَعُودُنَّ فِي مِلَّتِنا قالَ أَ وَ لَوْ كُنَّا كارِهِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1043, N'قَدِ افْتَرَيْنا عَلَى اللَّهِ كَذِباً إِنْ عُدْنا فِي مِلَّتِكُمْ بَعْدَ إِذْ نَجَّانَا اللَّهُ مِنْها وَ ما يَكُونُ لَنا أَنْ نَعُودَ فِيها إِلاَّ أَنْ يَشاءَ اللَّهُ رَبُّنا وَسِعَ رَبُّنا كُلَّ شَيْ‏ءٍ عِلْماً عَلَى اللَّهِ تَوَكَّلْنا رَبَّنَا افْتَحْ بَيْنَنا وَ بَيْنَ قَوْمِنا بِالْحَقِّ وَ أَنْتَ خَيْرُ الْفاتِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1044, N'وَ قالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ لَئِنِ اتَّبَعْتُمْ شُعَيْباً إِنَّكُمْ إِذاً لَخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1045, N'فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دارِهِمْ جاثِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1046, N'الَّذِينَ كَذَّبُوا شُعَيْباً كَأَنْ لَمْ يَغْنَوْا فِيهَا الَّذِينَ كَذَّبُوا شُعَيْباً كانُوا هُمُ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1047, N'فَتَوَلَّى عَنْهُمْ وَ قالَ يا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسالاتِ رَبِّي وَ نَصَحْتُ لَكُمْ فَكَيْفَ آسى عَلى قَوْمٍ كافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1048, N'وَ ما أَرْسَلْنا فِي قَرْيَةٍ مِنْ نَبِيٍّ إِلاَّ أَخَذْنا أَهْلَها بِالْبَأْساءِ وَ الضَّرَّاءِ لَعَلَّهُمْ يَضَّرَّعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1049, N'ثُمَّ بَدَّلْنا مَكانَ السَّيِّئَةِ الْحَسَنَةَ حَتَّى عَفَوْا وَ قالُوا قَدْ مَسَّ آباءَنَا الضَّرَّاءُ وَ السَّرَّاءُ فَأَخَذْناهُمْ بَغْتَةً وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1050, N'وَ لَوْ أَنَّ أَهْلَ الْقُرى آمَنُوا وَ اتَّقَوْا لَفَتَحْنا عَلَيْهِمْ بَرَكاتٍ مِنَ السَّماءِ وَ الْأَرْضِ وَ لكِنْ كَذَّبُوا فَأَخَذْناهُمْ بِما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1051, N'أَ فَأَمِنَ أَهْلُ الْقُرى أَنْ يَأْتِيَهُمْ بَأْسُنا بَياتاً وَ هُمْ نائِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1052, N'أَ وَ أَمِنَ أَهْلُ الْقُرى أَنْ يَأْتِيَهُمْ بَأْسُنا ضُحًى وَ هُمْ يَلْعَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1053, N'أَ فَأَمِنُوا مَكْرَ اللَّهِ فَلا يَأْمَنُ مَكْرَ اللَّهِ إِلاَّ الْقَوْمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1054, N'أَ وَ لَمْ يَهْدِ لِلَّذِينَ يَرِثُونَ الْأَرْضَ مِنْ بَعْدِ أَهْلِها أَنْ لَوْ نَشاءُ أَصَبْناهُمْ بِذُنُوبِهِمْ وَ نَطْبَعُ عَلى قُلُوبِهِمْ فَهُمْ لا يَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1055, N'تِلْكَ الْقُرى نَقُصُّ عَلَيْكَ مِنْ أَنْبائِها وَ لَقَدْ جاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّناتِ فَما كانُوا لِيُؤْمِنُوا بِما كَذَّبُوا مِنْ قَبْلُ كَذلِكَ يَطْبَعُ اللَّهُ عَلى قُلُوبِ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1056, N'وَ ما وَجَدْنا لِأَكْثَرِهِمْ مِنْ عَهْدٍ وَ إِنْ وَجَدْنا أَكْثَرَهُمْ لَفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1057, N'ثُمَّ بَعَثْنا مِنْ بَعْدِهِمْ مُوسى بِآياتِنا إِلى فِرْعَوْنَ وَ مَلاَئِهِ فَظَلَمُوا بِها فَانْظُرْ كَيْفَ كانَ عاقِبَةُ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1058, N'وَ قالَ مُوسى يا فِرْعَوْنُ إِنِّي رَسُولٌ مِنْ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1059, N'حَقِيقٌ عَلى أَنْ لا أَقُولَ عَلَى اللَّهِ إِلاَّ الْحَقَّ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِنْ رَبِّكُمْ فَأَرْسِلْ مَعِيَ بَنِي إِسْرائِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1060, N'قالَ إِنْ كُنْتَ جِئْتَ بِآيَةٍ فَأْتِ بِها إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1061, N'فَأَلْقى عَصاهُ فَإِذا هِيَ ثُعْبانٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1062, N'وَ نَزَعَ يَدَهُ فَإِذا هِيَ بَيْضاءُ لِلنَّاظِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1063, N'قالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ إِنَّ هذا لَساحِرٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1064, N'يُرِيدُ أَنْ يُخْرِجَكُمْ مِنْ أَرْضِكُمْ فَما ذا تَأْمُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1065, N'قالُوا أَرْجِهْ وَ أَخاهُ وَ أَرْسِلْ فِي الْمَدائِنِ حاشِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1066, N'يَأْتُوكَ بِكُلِّ ساحِرٍ عَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1067, N'وَ جاءَ السَّحَرَةُ فِرْعَوْنَ قالُوا إِنَّ لَنا لَأَجْراً إِنْ كُنَّا نَحْنُ الْغالِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1068, N'قالَ نَعَمْ وَ إِنَّكُمْ لَمِنَ الْمُقَرَّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1069, N'قالُوا يا مُوسى إِمَّا أَنْ تُلْقِيَ وَ إِمَّا أَنْ نَكُونَ نَحْنُ الْمُلْقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1070, N'قالَ أَلْقُوا فَلَمَّا أَلْقَوْا سَحَرُوا أَعْيُنَ النَّاسِ وَ اسْتَرْهَبُوهُمْ وَ جاؤُ بِسِحْرٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1071, N'وَ أَوْحَيْنا إِلى مُوسى أَنْ أَلْقِ عَصاكَ فَإِذا هِيَ تَلْقَفُ ما يَأْفِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1072, N'فَوَقَعَ الْحَقُّ وَ بَطَلَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1073, N'فَغُلِبُوا هُنالِكَ وَ انْقَلَبُوا صاغِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1074, N'وَ أُلْقِيَ السَّحَرَةُ ساجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1075, N'قالُوا آمَنَّا بِرَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1076, N'رَبِّ مُوسى وَ هارُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1077, N'قالَ فِرْعَوْنُ آمَنْتُمْ بِهِ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّ هذا لَمَكْرٌ مَكَرْتُمُوهُ فِي الْمَدِينَةِ لِتُخْرِجُوا مِنْها أَهْلَها فَسَوْفَ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1078, N'لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَ أَرْجُلَكُمْ مِنْ خِلافٍ ثُمَّ لَأُصَلِّبَنَّكُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1079, N'قالُوا إِنَّا إِلى رَبِّنا مُنْقَلِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1080, N'وَ ما تَنْقِمُ مِنَّا إِلاَّ أَنْ آمَنَّا بِآياتِ رَبِّنا لَمَّا جاءَتْنا رَبَّنا أَفْرِغْ عَلَيْنا صَبْراً وَ تَوَفَّنا مُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1081, N'وَ قالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ أَ تَذَرُ مُوسى وَ قَوْمَهُ لِيُفْسِدُوا فِي الْأَرْضِ وَ يَذَرَكَ وَ آلِهَتَكَ قالَ سَنُقَتِّلُ أَبْناءَهُمْ وَ نَسْتَحْيِي نِساءَهُمْ وَ إِنَّا فَوْقَهُمْ قاهِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1082, N'قالَ مُوسى لِقَوْمِهِ اسْتَعِينُوا بِاللَّهِ وَ اصْبِرُوا إِنَّ الْأَرْضَ لِلَّهِ يُورِثُها مَنْ يَشاءُ مِنْ عِبادِهِ وَ الْعاقِبَةُ لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1083, N'قالُوا أُوذِينا مِنْ قَبْلِ أَنْ تَأْتِيَنا وَ مِنْ بَعْدِ ما جِئْتَنا قالَ عَسى رَبُّكُمْ أَنْ يُهْلِكَ عَدُوَّكُمْ وَ يَسْتَخْلِفَكُمْ فِي الْأَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1084, N'وَ لَقَدْ أَخَذْنا آلَ فِرْعَوْنَ بِالسِّنِينَ وَ نَقْصٍ مِنَ الثَّمَراتِ لَعَلَّهُمْ يَذَّكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1085, N'فَإِذا جاءَتْهُمُ الْحَسَنَةُ قالُوا لَنا هذِهِ وَ إِنْ تُصِبْهُمْ سَيِّئَةٌ يَطَّيَّرُوا بِمُوسى وَ مَنْ مَعَهُ أَلا إِنَّما طائِرُهُمْ عِنْدَ اللَّهِ وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1086, N'وَ قالُوا مَهْما تَأْتِنا بِهِ مِنْ آيَةٍ لِتَسْحَرَنا بِها فَما نَحْنُ لَكَ بِمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1087, N'فَأَرْسَلْنا عَلَيْهِمُ الطُّوفانَ وَ الْجَرادَ وَ الْقُمَّلَ وَ الضَّفادِعَ وَ الدَّمَ آياتٍ مُفَصَّلاتٍ فَاسْتَكْبَرُوا وَ كانُوا قَوْماً مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1088, N'وَ لَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قالُوا يا مُوسَى ادْعُ لَنا رَبَّكَ بِما عَهِدَ عِنْدَكَ لَئِنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَ لَنُرْسِلَنَّ مَعَكَ بَنِي إِسْرائِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1089, N'فَلَمَّا كَشَفْنا عَنْهُمُ الرِّجْزَ إِلى أَجَلٍ هُمْ بالِغُوهُ إِذا هُمْ يَنْكُثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1090, N'فَانْتَقَمْنا مِنْهُمْ فَأَغْرَقْناهُمْ فِي الْيَمِّ بِأَنَّهُمْ كَذَّبُوا بِآياتِنا وَ كانُوا عَنْها غافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1091, N'وَ أَوْرَثْنَا الْقَوْمَ الَّذِينَ كانُوا يُسْتَضْعَفُونَ مَشارِقَ الْأَرْضِ وَ مَغارِبَهَا الَّتِي بارَكْنا فِيها وَ تَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنى عَلى بَنِي إِسْرائِيلَ بِما صَبَرُوا وَ دَمَّرْنا ما كانَ يَصْنَعُ فِرْعَوْنُ وَ قَوْمُهُ وَ ما كانُوا يَعْرِشُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1092, N'وَ جاوَزْنا بِبَنِي إِسْرائِيلَ الْبَحْرَ فَأَتَوْا عَلى قَوْمٍ يَعْكُفُونَ عَلى أَصْنامٍ لَهُمْ قالُوا يا مُوسَى اجْعَلْ لَنا إِلهاً كَما لَهُمْ آلِهَةٌ قالَ إِنَّكُمْ قَوْمٌ تَجْهَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1093, N'إِنَّ هؤُلاءِ مُتَبَّرٌ ما هُمْ فِيهِ وَ باطِلٌ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1094, N'قالَ أَ غَيْرَ اللَّهِ أَبْغِيكُمْ إِلهاً وَ هُوَ فَضَّلَكُمْ عَلَى الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1095, N'وَ إِذْ أَنْجَيْناكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذابِ يُقَتِّلُونَ أَبْناءَكُمْ وَ يَسْتَحْيُونَ نِساءَكُمْ وَ فِي ذلِكُمْ بَلاءٌ مِنْ رَبِّكُمْ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1096, N'وَ واعَدْنا مُوسى ثَلاثِينَ لَيْلَةً وَ أَتْمَمْناها بِعَشْرٍ فَتَمَّ مِيقاتُ رَبِّهِ أَرْبَعِينَ لَيْلَةً وَ قالَ مُوسى لِأَخِيهِ هارُونَ اخْلُفْنِي فِي قَوْمِي وَ أَصْلِحْ وَ لا تَتَّبِعْ سَبِيلَ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1097, N'وَ لَمَّا جاءَ مُوسى لِمِيقاتِنا وَ كَلَّمَهُ رَبُّهُ قالَ رَبِّ أَرِنِي أَنْظُرْ إِلَيْكَ قالَ لَنْ تَرانِي وَ لكِنِ انْظُرْ إِلَى الْجَبَلِ فَإِنِ اسْتَقَرَّ مَكانَهُ فَسَوْفَ تَرانِي فَلَمَّا تَجَلَّى رَبُّهُ لِلْجَبَلِ جَعَلَهُ دَكًّا وَ خَرَّ مُوسى صَعِقاً فَلَمَّا أَفاقَ قالَ سُبْحانَكَ تُبْتُ إِلَيْكَ وَ أَنَا أَوَّلُ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1098, N'قالَ يا مُوسى إِنِّي اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسالاتِي وَ بِكَلامِي فَخُذْ ما آتَيْتُكَ وَ كُنْ مِنَ الشَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1099, N'وَ كَتَبْنا لَهُ فِي الْأَلْواحِ مِنْ كُلِّ شَيْ‏ءٍ مَوْعِظَةً وَ تَفْصِيلاً لِكُلِّ شَيْ‏ءٍ فَخُذْها بِقُوَّةٍ وَ أْمُرْ قَوْمَكَ يَأْخُذُوا بِأَحْسَنِها سَأُرِيكُمْ دارَ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1100, N'سَأَصْرِفُ عَنْ آياتِيَ الَّذِينَ يَتَكَبَّرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَ إِنْ يَرَوْا كُلَّ آيَةٍ لا يُؤْمِنُوا بِها وَ إِنْ يَرَوْا سَبِيلَ الرُّشْدِ لا يَتَّخِذُوهُ سَبِيلاً وَ إِنْ يَرَوْا سَبِيلَ الغَيِّ يَتَّخِذُوهُ سَبِيلاً ذلِكَ بِأَنَّهُمْ كَذَّبُوا بِآياتِنا وَ كانُوا عَنْها غافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1101, N'وَ الَّذِينَ كَذَّبُوا بِآياتِنا وَ لِقاءِ الْآخِرَةِ حَبِطَتْ أَعْمالُهُمْ هَلْ يُجْزَوْنَ إِلاَّ ما كانُوا يَعْمَلُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1102, N'وَ اتَّخَذَ قَوْمُ مُوسى مِنْ بَعْدِهِ مِنْ حُلِيِّهِمْ عِجْلاً جَسَداً لَهُ خُوارٌ أَ لَمْ يَرَوْا أَنَّهُ لا يُكَلِّمُهُمْ وَ لا يَهْدِيهِمْ سَبِيلاً اتَّخَذُوهُ وَ كانُوا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1103, N'وَ لَمَّا سُقِطَ فِي أَيْدِيهِمْ وَ رَأَوْا أَنَّهُمْ قَدْ ضَلُّوا قالُوا لَئِنْ لَمْ يَرْحَمْنا رَبُّنا وَ يَغْفِرْ لَنا لَنَكُونَنَّ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1104, N'وَ لَمَّا رَجَعَ مُوسى إِلى قَوْمِهِ غَضْبانَ أَسِفاً قالَ بِئْسَما خَلَفْتُمُونِي مِنْ بَعْدِي أَ عَجِلْتُمْ أَمْرَ رَبِّكُمْ وَ أَلْقَى الْأَلْواحَ وَ أَخَذَ بِرَأْسِ أَخِيهِ يَجُرُّهُ إِلَيْهِ قالَ ابْنَ أُمَّ إِنَّ الْقَوْمَ اسْتَضْعَفُونِي وَ كادُوا يَقْتُلُونَنِي فَلا تُشْمِتْ بِيَ الْأَعْداءَ وَ لا تَجْعَلْنِي مَعَ الْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1105, N'قالَ رَبِّ اغْفِرْ لِي وَ لِأَخِي وَ أَدْخِلْنا فِي رَحْمَتِكَ وَ أَنْتَ أَرْحَمُ الرَّاحِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1106, N'إِنَّ الَّذِينَ اتَّخَذُوا الْعِجْلَ سَيَنالُهُمْ غَضَبٌ مِنْ رَبِّهِمْ وَ ذِلَّةٌ فِي الْحَياةِ الدُّنْيا وَ كَذلِكَ نَجْزِي الْمُفْتَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1107, N'وَ الَّذِينَ عَمِلُوا السَّيِّئاتِ ثُمَّ تابُوا مِنْ بَعْدِها وَ آمَنُوا إِنَّ رَبَّكَ مِنْ بَعْدِها لَغَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1108, N'وَ لَمَّا سَكَتَ عَنْ مُوسَى الْغَضَبُ أَخَذَ الْأَلْواحَ وَ فِي نُسْخَتِها هُدىً وَ رَحْمَةٌ لِلَّذِينَ هُمْ لِرَبِّهِمْ يَرْهَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1109, N'وَ اخْتارَ مُوسى قَوْمَهُ سَبْعِينَ رَجُلاً لِمِيقاتِنا فَلَمَّا أَخَذَتْهُمُ الرَّجْفَةُ قالَ رَبِّ لَوْ شِئْتَ أَهْلَكْتَهُمْ مِنْ قَبْلُ وَ إِيَّايَ أَ تُهْلِكُنا بِما فَعَلَ السُّفَهاءُ مِنَّا إِنْ هِيَ إِلاَّ فِتْنَتُكَ تُضِلُّ بِها مَنْ تَشاءُ وَ تَهْدِي مَنْ تَشاءُ أَنْتَ وَلِيُّنا فَاغْفِرْ لَنا وَ ارْحَمْنا وَ أَنْتَ خَيْرُ الْغافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1110, N'وَ اكْتُبْ لَنا فِي هذِهِ الدُّنْيا حَسَنَةً وَ فِي الْآخِرَةِ إِنَّا هُدْنا إِلَيْكَ قالَ عَذابِي أُصِيبُ بِهِ مَنْ أَشاءُ وَ رَحْمَتِي وَسِعَتْ كُلَّ شَيْ‏ءٍ فَسَأَكْتُبُها لِلَّذِينَ يَتَّقُونَ وَ يُؤْتُونَ الزَّكاةَ وَ الَّذِينَ هُمْ بِآياتِنا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1111, N'الَّذِينَ يَتَّبِعُونَ الرَّسُولَ النَّبِيَّ الْأُمِّيَّ الَّذِي يَجِدُونَهُ مَكْتُوباً عِنْدَهُمْ فِي التَّوْراةِ وَ الْإِنْجِيلِ يَأْمُرُهُمْ بِالْمَعْرُوفِ وَ يَنْهاهُمْ عَنِ الْمُنْكَرِ وَ يُحِلُّ لَهُمُ الطَّيِّباتِ وَ يُحَرِّمُ عَلَيْهِمُ الْخَبائِثَ وَ يَضَعُ عَنْهُمْ إِصْرَهُمْ وَ الْأَغْلالَ الَّتِي كانَتْ عَلَيْهِمْ فَالَّذِينَ آمَنُوا بِهِ وَ عَزَّرُوهُ وَ نَصَرُوهُ وَ اتَّبَعُوا النُّورَ الَّذِي أُنْزِلَ مَعَهُ أُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1112, N'قُلْ يا أَيُّهَا النَّاسُ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ جَمِيعاً الَّذِي لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ لا إِلهَ إِلاَّ هُوَ يُحيِي وَ يُمِيتُ فَآمِنُوا بِاللَّهِ وَ رَسُولِهِ النَّبِيِّ الْأُمِّيِّ الَّذِي يُؤْمِنُ بِاللَّهِ وَ كَلِماتِهِ وَ اتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1113, N'وَ مِنْ قَوْمِ مُوسى أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَ بِهِ يَعْدِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1114, N'وَ قَطَّعْناهُمُ اثْنَتَيْ عَشْرَةَ أَسْباطاً أُمَماً وَ أَوْحَيْنا إِلى مُوسى إِذِ اسْتَسْقاهُ قَوْمُهُ أَنِ اضْرِبْ بِعَصاكَ الْحَجَرَ فَانْبَجَسَتْ مِنْهُ اثْنَتا عَشْرَةَ عَيْناً قَدْ عَلِمَ كُلُّ أُناسٍ مَشْرَبَهُمْ وَ ظَلَّلْنا عَلَيْهِمُ الْغَمامَ وَ أَنْزَلْنا عَلَيْهِمُ الْمَنَّ وَ السَّلْوى كُلُوا مِنْ طَيِّباتِ ما رَزَقْناكُمْ وَ ما ظَلَمُونا وَ لكِنْ كانُوا أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1115, N'وَ إِذْ قِيلَ لَهُمُ اسْكُنُوا هذِهِ الْقَرْيَةَ وَ كُلُوا مِنْها حَيْثُ شِئْتُمْ وَ قُولُوا حِطَّةٌ وَ ادْخُلُوا الْبابَ سُجَّداً نَغْفِرْ لَكُمْ خَطِيئاتِكُمْ سَنَزِيدُ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1116, N'فَبَدَّلَ الَّذِينَ ظَلَمُوا مِنْهُمْ قَوْلاً غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَرْسَلْنا عَلَيْهِمْ رِجْزاً مِنَ السَّماءِ بِما كانُوا يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1117, N'وَ سْئَلْهُمْ عَنِ الْقَرْيَةِ الَّتِي كانَتْ حاضِرَةَ الْبَحْرِ إِذْ يَعْدُونَ فِي السَّبْتِ إِذْ تَأْتِيهِمْ حِيتانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعاً وَ يَوْمَ لا يَسْبِتُونَ لا تَأْتِيهِمْ كَذلِكَ نَبْلُوهُمْ بِما كانُوا يَفْسُقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1118, N'وَ إِذْ قالَتْ أُمَّةٌ مِنْهُمْ لِمَ تَعِظُونَ قَوْماً اللَّهُ مُهْلِكُهُمْ أَوْ مُعَذِّبُهُمْ عَذاباً شَدِيداً قالُوا مَعْذِرَةً إِلى رَبِّكُمْ وَ لَعَلَّهُمْ يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1119, N'فَلَمَّا نَسُوا ما ذُكِّرُوا بِهِ أَنْجَيْنَا الَّذِينَ يَنْهَوْنَ عَنِ السُّوءِ وَ أَخَذْنَا الَّذِينَ ظَلَمُوا بِعَذابٍ بَئِيسٍ بِما كانُوا يَفْسُقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1120, N'فَلَمَّا عَتَوْا عَنْ ما نُهُوا عَنْهُ قُلْنا لَهُمْ كُونُوا قِرَدَةً خاسِئِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1121, N'وَ إِذْ تَأَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ إِلى يَوْمِ الْقِيامَةِ مَنْ يَسُومُهُمْ سُوءَ الْعَذابِ إِنَّ رَبَّكَ لَسَرِيعُ الْعِقابِ وَ إِنَّهُ لَغَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1122, N'وَ قَطَّعْناهُمْ فِي الْأَرْضِ أُمَماً مِنْهُمُ الصَّالِحُونَ وَ مِنْهُمْ دُونَ ذلِكَ وَ بَلَوْناهُمْ بِالْحَسَناتِ وَ السَّيِّئاتِ لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1123, N'فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ وَرِثُوا الْكِتابَ يَأْخُذُونَ عَرَضَ هذَا الْأَدْنى وَ يَقُولُونَ سَيُغْفَرُ لَنا وَ إِنْ يَأْتِهِمْ عَرَضٌ مِثْلُهُ يَأْخُذُوهُ أَ لَمْ يُؤْخَذْ عَلَيْهِمْ مِيثاقُ الْكِتابِ أَنْ لا يَقُولُوا عَلَى اللَّهِ إِلاَّ الْحَقَّ وَ دَرَسُوا ما فِيهِ وَ الدَّارُ الْآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1124, N'وَ الَّذِينَ يُمَسِّكُونَ بِالْكِتابِ وَ أَقامُوا الصَّلاةَ إِنَّا لا نُضِيعُ أَجْرَ الْمُصْلِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1125, N'وَ إِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَأَنَّهُ ظُلَّةٌ وَ ظَنُّوا أَنَّهُ واقِعٌ بِهِمْ خُذُوا ما آتَيْناكُمْ بِقُوَّةٍ وَ اذْكُرُوا ما فِيهِ لَعَلَّكُمْ تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1126, N'وَ إِذْ أَخَذَ رَبُّكَ مِنْ بَنِي آدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَ أَشْهَدَهُمْ عَلى أَنْفُسِهِمْ أَ لَسْتُ بِرَبِّكُمْ قالُوا بَلى شَهِدْنا أَنْ تَقُولُوا يَوْمَ الْقِيامَةِ إِنَّا كُنَّا عَنْ هذا غافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1127, N'أَوْ تَقُولُوا إِنَّما أَشْرَكَ آباؤُنا مِنْ قَبْلُ وَ كُنَّا ذُرِّيَّةً مِنْ بَعْدِهِمْ أَ فَتُهْلِكُنا بِما فَعَلَ الْمُبْطِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1128, N'وَ كَذلِكَ نُفَصِّلُ الْآياتِ وَ لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1129, N'وَ اتْلُ عَلَيْهِمْ نَبَأَ الَّذِي آتَيْناهُ آياتِنا فَانْسَلَخَ مِنْها فَأَتْبَعَهُ الشَّيْطانُ فَكانَ مِنَ الْغاوِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1130, N'وَ لَوْ شِئْنا لَرَفَعْناهُ بِها وَ لكِنَّهُ أَخْلَدَ إِلَى الْأَرْضِ وَ اتَّبَعَ هَواهُ فَمَثَلُهُ كَمَثَلِ الْكَلْبِ إِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ أَوْ تَتْرُكْهُ يَلْهَثْ ذلِكَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآياتِنا فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1131, N'ساءَ مَثَلاً الْقَوْمُ الَّذِينَ كَذَّبُوا بِآياتِنا وَ أَنْفُسَهُمْ كانُوا يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1132, N'مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِي وَ مَنْ يُضْلِلْ فَأُولئِكَ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1133, N'وَ لَقَدْ ذَرَأْنا لِجَهَنَّمَ كَثِيراً مِنَ الْجِنِّ وَ الْإِنْسِ لَهُمْ قُلُوبٌ لا يَفْقَهُونَ بِها وَ لَهُمْ أَعْيُنٌ لا يُبْصِرُونَ بِها وَ لَهُمْ آذانٌ لا يَسْمَعُونَ بِها أُولئِكَ كَالْأَنْعامِ بَلْ هُمْ أَضَلُّ أُولئِكَ هُمُ الْغافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1134, N'وَ لِلَّهِ الْأَسْماءُ الْحُسْنى فَادْعُوهُ بِها وَ ذَرُوا الَّذِينَ يُلْحِدُونَ فِي أَسْمائِهِ سَيُجْزَوْنَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1135, N'وَ مِمَّنْ خَلَقْنا أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَ بِهِ يَعْدِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1136, N'وَ الَّذِينَ كَذَّبُوا بِآياتِنا سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1137, N'وَ أُمْلِي لَهُمْ إِنَّ كَيْدِي مَتِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1138, N'أَ وَ لَمْ يَتَفَكَّرُوا ما بِصاحِبِهِمْ مِنْ جِنَّةٍ إِنْ هُوَ إِلاَّ نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1139, N'أَ وَ لَمْ يَنْظُرُوا فِي مَلَكُوتِ السَّماواتِ وَ الْأَرْضِ وَ ما خَلَقَ اللَّهُ مِنْ شَيْ‏ءٍ وَ أَنْ عَسى أَنْ يَكُونَ قَدِ اقْتَرَبَ أَجَلُهُمْ فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1140, N'مَنْ يُضْلِلِ اللَّهُ فَلا هادِيَ لَهُ وَ يَذَرُهُمْ فِي طُغْيانِهِمْ يَعْمَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1141, N'يَسْئَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْساها قُلْ إِنَّما عِلْمُها عِنْدَ رَبِّي لا يُجَلِّيها لِوَقْتِها إِلاَّ هُوَ ثَقُلَتْ فِي السَّماواتِ وَ الْأَرْضِ لا تَأْتِيكُمْ إِلاَّ بَغْتَةً يَسْئَلُونَكَ كَأَنَّكَ حَفِيٌّ عَنْها قُلْ إِنَّما عِلْمُها عِنْدَ اللَّهِ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1142, N'قُلْ لا أَمْلِكُ لِنَفْسِي نَفْعاً وَ لا ضَرًّا إِلاَّ ما شاءَ اللَّهُ وَ لَوْ كُنْتُ أَعْلَمُ الْغَيْبَ لاَسْتَكْثَرْتُ مِنَ الْخَيْرِ وَ ما مَسَّنِيَ السُّوءُ إِنْ أَنَا إِلاَّ نَذِيرٌ وَ بَشِيرٌ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1143, N'هُوَ الَّذِي خَلَقَكُمْ مِنْ نَفْسٍ واحِدَةٍ وَ جَعَلَ مِنْها زَوْجَها لِيَسْكُنَ إِلَيْها فَلَمَّا تَغَشَّاها حَمَلَتْ حَمْلاً خَفِيفاً فَمَرَّتْ بِهِ فَلَمَّا أَثْقَلَتْ دَعَوَا اللَّهَ رَبَّهُما لَئِنْ آتَيْتَنا صالِحاً لَنَكُونَنَّ مِنَ الشَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1144, N'فَلَمَّا آتاهُما صالِحاً جَعَلا لَهُ شُرَكاءَ فِيما آتاهُما فَتَعالَى اللَّهُ عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1145, N'أَ يُشْرِكُونَ ما لا يَخْلُقُ شَيْئاً وَ هُمْ يُخْلَقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1146, N'وَ لا يَسْتَطِيعُونَ لَهُمْ نَصْراً وَ لا أَنْفُسَهُمْ يَنْصُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1147, N'وَ إِنْ تَدْعُوهُمْ إِلَى الْهُدى لا يَتَّبِعُوكُمْ سَواءٌ عَلَيْكُمْ أَ دَعَوْتُمُوهُمْ أَمْ أَنْتُمْ صامِتُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1148, N'إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ عِبادٌ أَمْثالُكُمْ فَادْعُوهُمْ فَلْيَسْتَجِيبُوا لَكُمْ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1149, N'أَ لَهُمْ أَرْجُلٌ يَمْشُونَ بِها أَمْ لَهُمْ أَيْدٍ يَبْطِشُونَ بِها أَمْ لَهُمْ أَعْيُنٌ يُبْصِرُونَ بِها أَمْ لَهُمْ آذانٌ يَسْمَعُونَ بِها قُلِ ادْعُوا شُرَكاءَكُمْ ثُمَّ كِيدُونِ فَلا تُنْظِرُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1150, N'إِنَّ وَلِيِّيَ اللَّهُ الَّذِي نَزَّلَ الْكِتابَ وَ هُوَ يَتَوَلَّى الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1151, N'وَ الَّذِينَ تَدْعُونَ مِنْ دُونِهِ لا يَسْتَطِيعُونَ نَصْرَكُمْ وَ لا أَنْفُسَهُمْ يَنْصُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1152, N'وَ إِنْ تَدْعُوهُمْ إِلَى الْهُدى لا يَسْمَعُوا وَ تَراهُمْ يَنْظُرُونَ إِلَيْكَ وَ هُمْ لا يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1153, N'خُذِ الْعَفْوَ وَ أْمُرْ بِالْعُرْفِ وَ أَعْرِضْ عَنِ الْجاهِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1154, N'وَ إِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطانِ نَزْغٌ فَاسْتَعِذْ بِاللَّهِ إِنَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1155, N'إِنَّ الَّذِينَ اتَّقَوْا إِذا مَسَّهُمْ طائِفٌ مِنَ الشَّيْطانِ تَذَكَّرُوا فَإِذا هُمْ مُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1156, N'وَ إِخْوانُهُمْ يَمُدُّونَهُمْ فِي الغَيِّ ثُمَّ لا يُقْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1157, N'وَ إِذا لَمْ تَأْتِهِمْ بِآيَةٍ قالُوا لَوْ لا اجْتَبَيْتَها قُلْ إِنَّما أَتَّبِعُ ما يُوحى إِلَيَّ مِنْ رَبِّي هذا بَصائِرُ مِنْ رَبِّكُمْ وَ هُدىً وَ رَحْمَةٌ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1158, N'وَ إِذا قُرِئَ الْقُرْآنُ فَاسْتَمِعُوا لَهُ وَ أَنْصِتُوا لَعَلَّكُمْ تُرْحَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1159, N'وَ اذْكُرْ رَبَّكَ فِي نَفْسِكَ تَضَرُّعاً وَ خِيفَةً وَ دُونَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَ الْآصالِ وَ لا تَكُنْ مِنَ الْغافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1160, N'إِنَّ الَّذِينَ عِنْدَ رَبِّكَ لا يَسْتَكْبِرُونَ عَنْ عِبادَتِهِ وَ يُسَبِّحُونَهُ وَ لَهُ يَسْجُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1161, N'يَسْئَلُونَكَ عَنِ الْأَنْفالِ قُلِ الْأَنْفالُ لِلَّهِ وَ الرَّسُولِ فَاتَّقُوا اللَّهَ وَ أَصْلِحُوا ذاتَ بَيْنِكُمْ وَ أَطِيعُوا اللَّهَ وَ رَسُولَهُ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1162, N'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ إِذا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَ إِذا تُلِيَتْ عَلَيْهِمْ آياتُهُ زادَتْهُمْ إِيماناً وَ عَلى رَبِّهِمْ يَتَوَكَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1163, N'الَّذِينَ يُقِيمُونَ الصَّلاةَ وَ مِمَّا رَزَقْناهُمْ يُنْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1164, N'أُولئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا لَهُمْ دَرَجاتٌ عِنْدَ رَبِّهِمْ وَ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1165, N'كَما أَخْرَجَكَ رَبُّكَ مِنْ بَيْتِكَ بِالْحَقِّ وَ إِنَّ فَرِيقاً مِنَ الْمُؤْمِنِينَ لَكارِهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1166, N'يُجادِلُونَكَ فِي الْحَقِّ بَعْدَ ما تَبَيَّنَ كَأَنَّما يُساقُونَ إِلَى الْمَوْتِ وَ هُمْ يَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1167, N'وَ إِذْ يَعِدُكُمُ اللَّهُ إِحْدَى الطَّائِفَتَيْنِ أَنَّها لَكُمْ وَ تَوَدُّونَ أَنَّ غَيْرَ ذاتِ الشَّوْكَةِ تَكُونُ لَكُمْ وَ يُرِيدُ اللَّهُ أَنْ يُحِقَّ الْحَقَّ بِكَلِماتِهِ وَ يَقْطَعَ دابِرَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1168, N'لِيُحِقَّ الْحَقَّ وَ يُبْطِلَ الْباطِلَ وَ لَوْ كَرِهَ الْمُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1169, N'إِذْ تَسْتَغِيثُونَ رَبَّكُمْ فَاسْتَجابَ لَكُمْ أَنِّي مُمِدُّكُمْ بِأَلْفٍ مِنَ الْمَلائِكَةِ مُرْدِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1170, N'وَ ما جَعَلَهُ اللَّهُ إِلاَّ بُشْرى وَ لِتَطْمَئِنَّ بِهِ قُلُوبُكُمْ وَ مَا النَّصْرُ إِلاَّ مِنْ عِنْدِ اللَّهِ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1171, N'إِذْ يُغَشِّيكُمُ النُّعاسَ أَمَنَةً مِنْهُ وَ يُنَزِّلُ عَلَيْكُمْ مِنَ السَّماءِ ماءً لِيُطَهِّرَكُمْ بِهِ وَ يُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطانِ وَ لِيَرْبِطَ عَلى قُلُوبِكُمْ وَ يُثَبِّتَ بِهِ الْأَقْدامَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1172, N'إِذْ يُوحِي رَبُّكَ إِلَى الْمَلائِكَةِ أَنِّي مَعَكُمْ فَثَبِّتُوا الَّذِينَ آمَنُوا سَأُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ فَاضْرِبُوا فَوْقَ الْأَعْناقِ وَ اضْرِبُوا مِنْهُمْ كُلَّ بَنانٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1173, N'ذلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَ رَسُولَهُ وَ مَنْ يُشاقِقِ اللَّهَ وَ رَسُولَهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1174, N'ذلِكُمْ فَذُوقُوهُ وَ أَنَّ لِلْكافِرِينَ عَذابَ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1175, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا لَقِيتُمُ الَّذِينَ كَفَرُوا زَحْفاً فَلا تُوَلُّوهُمُ الْأَدْبارَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1176, N'وَ مَنْ يُوَلِّهِمْ يَوْمَئِذٍ دُبُرَهُ إِلاَّ مُتَحَرِّفاً لِقِتالٍ أَوْ مُتَحَيِّزاً إِلى فِئَةٍ فَقَدْ باءَ بِغَضَبٍ مِنَ اللَّهِ وَ مَأْواهُ جَهَنَّمُ وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1177, N'فَلَمْ تَقْتُلُوهُمْ وَ لكِنَّ اللَّهَ قَتَلَهُمْ وَ ما رَمَيْتَ إِذْ رَمَيْتَ وَ لكِنَّ اللَّهَ رَمى وَ لِيُبْلِيَ الْمُؤْمِنِينَ مِنْهُ بَلاءً حَسَناً إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1178, N'ذلِكُمْ وَ أَنَّ اللَّهَ مُوهِنُ كَيْدِ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1179, N'إِنْ تَسْتَفْتِحُوا فَقَدْ جاءَكُمُ الْفَتْحُ وَ إِنْ تَنْتَهُوا فَهُوَ خَيْرٌ لَكُمْ وَ إِنْ تَعُودُوا نَعُدْ وَ لَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْئاً وَ لَوْ كَثُرَتْ وَ أَنَّ اللَّهَ مَعَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1180, N'يا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَ رَسُولَهُ وَ لا تَوَلَّوْا عَنْهُ وَ أَنْتُمْ تَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1181, N'وَ لا تَكُونُوا كَالَّذِينَ قالُوا سَمِعْنا وَ هُمْ لا يَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1182, N'إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الصُّمُّ الْبُكْمُ الَّذِينَ لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1183, N'وَ لَوْ عَلِمَ اللَّهُ فِيهِمْ خَيْراً لَأَسْمَعَهُمْ وَ لَوْ أَسْمَعَهُمْ لَتَوَلَّوْا وَ هُمْ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1184, N'يا أَيُّهَا الَّذِينَ آمَنُوا اسْتَجِيبُوا لِلَّهِ وَ لِلرَّسُولِ إِذا دَعاكُمْ لِما يُحْيِيكُمْ وَ اعْلَمُوا أَنَّ اللَّهَ يَحُولُ بَيْنَ الْمَرْءِ وَ قَلْبِهِ وَ أَنَّهُ إِلَيْهِ تُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1185, N'وَ اتَّقُوا فِتْنَةً لا تُصِيبَنَّ الَّذِينَ ظَلَمُوا مِنْكُمْ خَاصَّةً وَ اعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1186, N'وَ اذْكُرُوا إِذْ أَنْتُمْ قَلِيلٌ مُسْتَضْعَفُونَ فِي الْأَرْضِ تَخافُونَ أَنْ يَتَخَطَّفَكُمُ النَّاسُ فَآواكُمْ وَ أَيَّدَكُمْ بِنَصْرِهِ وَ رَزَقَكُمْ مِنَ الطَّيِّباتِ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1187, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَخُونُوا اللَّهَ وَ الرَّسُولَ وَ تَخُونُوا أَماناتِكُمْ وَ أَنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1188, N'وَ اعْلَمُوا أَنَّما أَمْوالُكُمْ وَ أَوْلادُكُمْ فِتْنَةٌ وَ أَنَّ اللَّهَ عِنْدَهُ أَجْرٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1189, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تَتَّقُوا اللَّهَ يَجْعَلْ لَكُمْ فُرْقاناً وَ يُكَفِّرْ عَنْكُمْ سَيِّئاتِكُمْ وَ يَغْفِرْ لَكُمْ وَ اللَّهُ ذُو الْفَضْلِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1190, N'وَ إِذْ يَمْكُرُ بِكَ الَّذِينَ كَفَرُوا لِيُثْبِتُوكَ أَوْ يَقْتُلُوكَ أَوْ يُخْرِجُوكَ وَ يَمْكُرُونَ وَ يَمْكُرُ اللَّهُ وَ اللَّهُ خَيْرُ الْماكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1191, N'وَ إِذا تُتْلى عَلَيْهِمْ آياتُنا قالُوا قَدْ سَمِعْنا لَوْ نَشاءُ لَقُلْنا مِثْلَ هذا إِنْ هذا إِلاَّ أَساطِيرُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1192, N'وَ إِذْ قالُوا اللَّهُمَّ إِنْ كانَ هذا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَأَمْطِرْ عَلَيْنا حِجارَةً مِنَ السَّماءِ أَوِ ائْتِنا بِعَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1193, N'وَ ما كانَ اللَّهُ لِيُعَذِّبَهُمْ وَ أَنْتَ فِيهِمْ وَ ما كانَ اللَّهُ مُعَذِّبَهُمْ وَ هُمْ يَسْتَغْفِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1194, N'وَ ما لَهُمْ أَلاَّ يُعَذِّبَهُمُ اللَّهُ وَ هُمْ يَصُدُّونَ عَنِ الْمَسْجِدِ الْحَرامِ وَ ما كانُوا أَوْلِياءَهُ إِنْ أَوْلِياؤُهُ إِلاَّ الْمُتَّقُونَ وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1195, N'وَ ما كانَ صَلاتُهُمْ عِنْدَ الْبَيْتِ إِلاَّ مُكاءً وَ تَصْدِيَةً فَذُوقُوا الْعَذابَ بِما كُنْتُمْ تَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1196, N'إِنَّ الَّذِينَ كَفَرُوا يُنْفِقُونَ أَمْوالَهُمْ لِيَصُدُّوا عَنْ سَبِيلِ اللَّهِ فَسَيُنْفِقُونَها ثُمَّ تَكُونُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُونَ وَ الَّذِينَ كَفَرُوا إِلى جَهَنَّمَ يُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1197, N'لِيَمِيزَ اللَّهُ الْخَبِيثَ مِنَ الطَّيِّبِ وَ يَجْعَلَ الْخَبِيثَ بَعْضَهُ عَلى بَعْضٍ فَيَرْكُمَهُ جَمِيعاً فَيَجْعَلَهُ فِي جَهَنَّمَ أُولئِكَ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1198, N'قُلْ لِلَّذِينَ كَفَرُوا إِنْ يَنْتَهُوا يُغْفَرْ لَهُمْ ما قَدْ سَلَفَ وَ إِنْ يَعُودُوا فَقَدْ مَضَتْ سُنَّتُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1199, N'وَ قاتِلُوهُمْ حَتَّى لا تَكُونَ فِتْنَةٌ وَ يَكُونَ الدِّينُ كُلُّهُ لِلَّهِ فَإِنِ انْتَهَوْا فَإِنَّ اللَّهَ بِما يَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1200, N'وَ إِنْ تَوَلَّوْا فَاعْلَمُوا أَنَّ اللَّهَ مَوْلاكُمْ نِعْمَ الْمَوْلى وَ نِعْمَ النَّصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1201, N'وَ اعْلَمُوا أَنَّما غَنِمْتُمْ مِنْ شَيْ‏ءٍ فَأَنَّ لِلَّهِ خُمُسَهُ وَ لِلرَّسُولِ وَ لِذِي الْقُرْبى وَ الْيَتامى وَ الْمَساكِينِ وَ ابْنِ السَّبِيلِ إِنْ كُنْتُمْ آمَنْتُمْ بِاللَّهِ وَ ما أَنْزَلْنا عَلى عَبْدِنا يَوْمَ الْفُرْقانِ يَوْمَ الْتَقَى الْجَمْعانِ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1202, N'إِذْ أَنْتُمْ بِالْعُدْوَةِ الدُّنْيا وَ هُمْ بِالْعُدْوَةِ الْقُصْوى وَ الرَّكْبُ أَسْفَلَ مِنْكُمْ وَ لَوْ تَواعَدْتُمْ لاَخْتَلَفْتُمْ فِي الْمِيعادِ وَ لكِنْ لِيَقْضِيَ اللَّهُ أَمْراً كانَ مَفْعُولاً لِيَهْلِكَ مَنْ هَلَكَ عَنْ بَيِّنَةٍ وَ يَحْيى مَنْ حَيَّ عَنْ بَيِّنَةٍ وَ إِنَّ اللَّهَ لَسَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1203, N'إِذْ يُرِيكَهُمُ اللَّهُ فِي مَنامِكَ قَلِيلاً وَ لَوْ أَراكَهُمْ كَثِيراً لَفَشِلْتُمْ وَ لَتَنازَعْتُمْ فِي الْأَمْرِ وَ لكِنَّ اللَّهَ سَلَّمَ إِنَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1204, N'وَ إِذْ يُرِيكُمُوهُمْ إِذِ الْتَقَيْتُمْ فِي أَعْيُنِكُمْ قَلِيلاً وَ يُقَلِّلُكُمْ فِي أَعْيُنِهِمْ لِيَقْضِيَ اللَّهُ أَمْراً كانَ مَفْعُولاً وَ إِلَى اللَّهِ تُرْجَعُ الْأُمُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1205, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا لَقِيتُمْ فِئَةً فَاثْبُتُوا وَ اذْكُرُوا اللَّهَ كَثِيراً لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1206, N'وَ أَطِيعُوا اللَّهَ وَ رَسُولَهُ وَ لا تَنازَعُوا فَتَفْشَلُوا وَ تَذْهَبَ رِيحُكُمْ وَ اصْبِرُوا إِنَّ اللَّهَ مَعَ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1207, N'وَ لا تَكُونُوا كَالَّذِينَ خَرَجُوا مِنْ دِيارِهِمْ بَطَراً وَ رِئاءَ النَّاسِ وَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَ اللَّهُ بِما يَعْمَلُونَ مُحِيطٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1208, N'وَ إِذْ زَيَّنَ لَهُمُ الشَّيْطانُ أَعْمالَهُمْ وَ قالَ لا غالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَ إِنِّي جارٌ لَكُمْ فَلَمَّا تَراءَتِ الْفِئَتانِ نَكَصَ عَلى عَقِبَيْهِ وَ قالَ إِنِّي بَرِي‏ءٌ مِنْكُمْ إِنِّي أَرى ما لا تَرَوْنَ إِنِّي أَخافُ اللَّهَ وَ اللَّهُ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1209, N'إِذْ يَقُولُ الْمُنافِقُونَ وَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ غَرَّ هؤُلاءِ دِينُهُمْ وَ مَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَإِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1210, N'وَ لَوْ تَرى إِذْ يَتَوَفَّى الَّذِينَ كَفَرُوا الْمَلائِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَ أَدْبارَهُمْ وَ ذُوقُوا عَذابَ الْحَرِيقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1211, N'ذلِكَ بِما قَدَّمَتْ أَيْدِيكُمْ وَ أَنَّ اللَّهَ لَيْسَ بِظَلاَّمٍ لِلْعَبِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1212, N'كَدَأْبِ آلِ فِرْعَوْنَ وَ الَّذِينَ مِنْ قَبْلِهِمْ كَفَرُوا بِآياتِ اللَّهِ فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ إِنَّ اللَّهَ قَوِيٌّ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1213, N'ذلِكَ بِأَنَّ اللَّهَ لَمْ يَكُ مُغَيِّراً نِعْمَةً أَنْعَمَها عَلى قَوْمٍ حَتَّى يُغَيِّرُوا ما بِأَنْفُسِهِمْ وَ أَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1214, N'كَدَأْبِ آلِ فِرْعَوْنَ وَ الَّذِينَ مِنْ قَبْلِهِمْ كَذَّبُوا بِآياتِ رَبِّهِمْ فَأَهْلَكْناهُمْ بِذُنُوبِهِمْ وَ أَغْرَقْنا آلَ فِرْعَوْنَ وَ كُلٌّ كانُوا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1215, N'إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الَّذِينَ كَفَرُوا فَهُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1216, N'الَّذِينَ عاهَدْتَ مِنْهُمْ ثُمَّ يَنْقُضُونَ عَهْدَهُمْ فِي كُلِّ مَرَّةٍ وَ هُمْ لا يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1217, N'فَإِمَّا تَثْقَفَنَّهُمْ فِي الْحَرْبِ فَشَرِّدْ بِهِمْ مَنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1218, N'وَ إِمَّا تَخافَنَّ مِنْ قَوْمٍ خِيانَةً فَانْبِذْ إِلَيْهِمْ عَلى سَواءٍ إِنَّ اللَّهَ لا يُحِبُّ الْخائِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1219, N'وَ لا يَحْسَبَنَّ الَّذِينَ كَفَرُوا سَبَقُوا إِنَّهُمْ لا يُعْجِزُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1220, N'وَ أَعِدُّوا لَهُمْ مَا اسْتَطَعْتُمْ مِنْ قُوَّةٍ وَ مِنْ رِباطِ الْخَيْلِ تُرْهِبُونَ بِهِ عَدُوَّ اللَّهِ وَ عَدُوَّكُمْ وَ آخَرِينَ مِنْ دُونِهِمْ لا تَعْلَمُونَهُمُ اللَّهُ يَعْلَمُهُمْ وَ ما تُنْفِقُوا مِنْ شَيْ‏ءٍ فِي سَبِيلِ اللَّهِ يُوَفَّ إِلَيْكُمْ وَ أَنْتُمْ لا تُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1221, N'وَ إِنْ جَنَحُوا لِلسَّلْمِ فَاجْنَحْ لَها وَ تَوَكَّلْ عَلَى اللَّهِ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1222, N'وَ إِنْ يُرِيدُوا أَنْ يَخْدَعُوكَ فَإِنَّ حَسْبَكَ اللَّهُ هُوَ الَّذِي أَيَّدَكَ بِنَصْرِهِ وَ بِالْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1223, N'وَ أَلَّفَ بَيْنَ قُلُوبِهِمْ لَوْ أَنْفَقْتَ ما فِي الْأَرْضِ جَمِيعاً ما أَلَّفْتَ بَيْنَ قُلُوبِهِمْ وَ لكِنَّ اللَّهَ أَلَّفَ بَيْنَهُمْ إِنَّهُ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1224, N'يا أَيُّهَا النَّبِيُّ حَسْبُكَ اللَّهُ وَ مَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1225, N'يا أَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِينَ عَلَى الْقِتالِ إِنْ يَكُنْ مِنْكُمْ عِشْرُونَ صابِرُونَ يَغْلِبُوا مِائَتَيْنِ وَ إِنْ يَكُنْ مِنْكُمْ مِائَةٌ يَغْلِبُوا أَلْفاً مِنَ الَّذِينَ كَفَرُوا بِأَنَّهُمْ قَوْمٌ لا يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1226, N'الْآنَ خَفَّفَ اللَّهُ عَنْكُمْ وَ عَلِمَ أَنَّ فِيكُمْ ضَعْفاً فَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ صابِرَةٌ يَغْلِبُوا مِائَتَيْنِ وَ إِنْ يَكُنْ مِنْكُمْ أَلْفٌ يَغْلِبُوا أَلْفَيْنِ بِإِذْنِ اللَّهِ وَ اللَّهُ مَعَ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1227, N'ما كانَ لِنَبِيٍّ أَنْ يَكُونَ لَهُ أَسْرى حَتَّى يُثْخِنَ فِي الْأَرْضِ تُرِيدُونَ عَرَضَ الدُّنْيا وَ اللَّهُ يُرِيدُ الْآخِرَةَ وَ اللَّهُ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1228, N'لَوْ لا كِتابٌ مِنَ اللَّهِ سَبَقَ لَمَسَّكُمْ فِيما أَخَذْتُمْ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1229, N'فَكُلُوا مِمَّا غَنِمْتُمْ حَلالاً طَيِّباً وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1230, N'يا أَيُّهَا النَّبِيُّ قُلْ لِمَنْ فِي أَيْدِيكُمْ مِنَ الْأَسْرى إِنْ يَعْلَمِ اللَّهُ فِي قُلُوبِكُمْ خَيْراً يُؤْتِكُمْ خَيْراً مِمَّا أُخِذَ مِنْكُمْ وَ يَغْفِرْ لَكُمْ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1231, N'وَ إِنْ يُرِيدُوا خِيانَتَكَ فَقَدْ خانُوا اللَّهَ مِنْ قَبْلُ فَأَمْكَنَ مِنْهُمْ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1232, N'إِنَّ الَّذِينَ آمَنُوا وَ هاجَرُوا وَ جاهَدُوا بِأَمْوالِهِمْ وَ أَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ وَ الَّذِينَ آوَوْا وَ نَصَرُوا أُولئِكَ بَعْضُهُمْ أَوْلِياءُ بَعْضٍ وَ الَّذِينَ آمَنُوا وَ لَمْ يُهاجِرُوا ما لَكُمْ مِنْ وَلايَتِهِمْ مِنْ شَيْ‏ءٍ حَتَّى يُهاجِرُوا وَ إِنِ اسْتَنْصَرُوكُمْ فِي الدِّينِ فَعَلَيْكُمُ النَّصْرُ إِلاَّ عَلى قَوْمٍ بَيْنَكُمْ وَ بَيْنَهُمْ مِيثاقٌ وَ اللَّهُ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1233, N'وَ الَّذِينَ كَفَرُوا بَعْضُهُمْ أَوْلِياءُ بَعْضٍ إِلاَّ تَفْعَلُوهُ تَكُنْ فِتْنَةٌ فِي الْأَرْضِ وَ فَسادٌ كَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1234, N'وَ الَّذِينَ آمَنُوا وَ هاجَرُوا وَ جاهَدُوا فِي سَبِيلِ اللَّهِ وَ الَّذِينَ آوَوْا وَ نَصَرُوا أُولئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا لَهُمْ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1235, N'وَ الَّذِينَ آمَنُوا مِنْ بَعْدُ وَ هاجَرُوا وَ جاهَدُوا مَعَكُمْ فَأُولئِكَ مِنْكُمْ وَ أُولُوا الْأَرْحامِ بَعْضُهُمْ أَوْلى بِبَعْضٍ فِي كِتابِ اللَّهِ إِنَّ اللَّهَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1236, N'بَراءَةٌ مِنَ اللَّهِ وَ رَسُولِهِ إِلَى الَّذِينَ عاهَدْتُمْ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1237, N'فَسِيحُوا فِي الْأَرْضِ أَرْبَعَةَ أَشْهُرٍ وَ اعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ وَ أَنَّ اللَّهَ مُخْزِي الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1238, N'وَ أَذانٌ مِنَ اللَّهِ وَ رَسُولِهِ إِلَى النَّاسِ يَوْمَ الْحَجِّ الْأَكْبَرِ أَنَّ اللَّهَ بَرِي‏ءٌ مِنَ الْمُشْرِكِينَ وَ رَسُولُهُ فَإِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَكُمْ وَ إِنْ تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ وَ بَشِّرِ الَّذِينَ كَفَرُوا بِعَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1239, N'إِلاَّ الَّذِينَ عاهَدْتُمْ مِنَ الْمُشْرِكِينَ ثُمَّ لَمْ يَنْقُصُوكُمْ شَيْئاً وَ لَمْ يُظاهِرُوا عَلَيْكُمْ أَحَداً فَأَتِمُّوا إِلَيْهِمْ عَهْدَهُمْ إِلى مُدَّتِهِمْ إِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1240, N'فَإِذَا انْسَلَخَ الْأَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِينَ حَيْثُ وَجَدْتُمُوهُمْ وَ خُذُوهُمْ وَ احْصُرُوهُمْ وَ اقْعُدُوا لَهُمْ كُلَّ مَرْصَدٍ فَإِنْ تابُوا وَ أَقامُوا الصَّلاةَ وَ آتَوُا الزَّكاةَ فَخَلُّوا سَبِيلَهُمْ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1241, N'وَ إِنْ أَحَدٌ مِنَ الْمُشْرِكِينَ اسْتَجارَكَ فَأَجِرْهُ حَتَّى يَسْمَعَ كَلامَ اللَّهِ ثُمَّ أَبْلِغْهُ مَأْمَنَهُ ذلِكَ بِأَنَّهُمْ قَوْمٌ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1242, N'كَيْفَ يَكُونُ لِلْمُشْرِكِينَ عَهْدٌ عِنْدَ اللَّهِ وَ عِنْدَ رَسُولِهِ إِلاَّ الَّذِينَ عاهَدْتُمْ عِنْدَ الْمَسْجِدِ الْحَرامِ فَمَا اسْتَقامُوا لَكُمْ فَاسْتَقِيمُوا لَهُمْ إِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1243, N'كَيْفَ وَ إِنْ يَظْهَرُوا عَلَيْكُمْ لا يَرْقُبُوا فِيكُمْ إِلاًّ وَ لا ذِمَّةً يُرْضُونَكُمْ بِأَفْواهِهِمْ وَ تَأْبى قُلُوبُهُمْ وَ أَكْثَرُهُمْ فاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1244, N'اشْتَرَوْا بِآياتِ اللَّهِ ثَمَناً قَلِيلاً فَصَدُّوا عَنْ سَبِيلِهِ إِنَّهُمْ ساءَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1245, N'لا يَرْقُبُونَ فِي مُؤْمِنٍ إِلاًّ وَ لا ذِمَّةً وَ أُولئِكَ هُمُ الْمُعْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1246, N'فَإِنْ تابُوا وَ أَقامُوا الصَّلاةَ وَ آتَوُا الزَّكاةَ فَإِخْوانُكُمْ فِي الدِّينِ وَ نُفَصِّلُ الْآياتِ لِقَوْمٍ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1247, N'وَ إِنْ نَكَثُوا أَيْمانَهُمْ مِنْ بَعْدِ عَهْدِهِمْ وَ طَعَنُوا فِي دِينِكُمْ فَقاتِلُوا أَئِمَّةَ الْكُفْرِ إِنَّهُمْ لا أَيْمانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1248, N'أَ لا تُقاتِلُونَ قَوْماً نَكَثُوا أَيْمانَهُمْ وَ هَمُّوا بِإِخْراجِ الرَّسُولِ وَ هُمْ بَدَؤُكُمْ أَوَّلَ مَرَّةٍ أَ تَخْشَوْنَهُمْ فَاللَّهُ أَحَقُّ أَنْ تَخْشَوْهُ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1249, N'قاتِلُوهُمْ يُعَذِّبْهُمُ اللَّهُ بِأَيْدِيكُمْ وَ يُخْزِهِمْ وَ يَنْصُرْكُمْ عَلَيْهِمْ وَ يَشْفِ صُدُورَ قَوْمٍ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1250, N'وَ يُذْهِبْ غَيْظَ قُلُوبِهِمْ وَ يَتُوبُ اللَّهُ عَلى مَنْ يَشاءُ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1251, N'أَمْ حَسِبْتُمْ أَنْ تُتْرَكُوا وَ لَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جاهَدُوا مِنْكُمْ وَ لَمْ يَتَّخِذُوا مِنْ دُونِ اللَّهِ وَ لا رَسُولِهِ وَ لاَ الْمُؤْمِنِينَ وَلِيجَةً وَ اللَّهُ خَبِيرٌ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1252, N'ما كانَ لِلْمُشْرِكِينَ أَنْ يَعْمُرُوا مَساجِدَ اللَّهِ شاهِدِينَ عَلى أَنْفُسِهِمْ بِالْكُفْرِ أُولئِكَ حَبِطَتْ أَعْمالُهُمْ وَ فِي النَّارِ هُمْ خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1253, N'إِنَّما يَعْمُرُ مَساجِدَ اللَّهِ مَنْ آمَنَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ أَقامَ الصَّلاةَ وَ آتَى الزَّكاةَ وَ لَمْ يَخْشَ إِلاَّ اللَّهَ فَعَسى أُولئِكَ أَنْ يَكُونُوا مِنَ الْمُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1254, N'أَ جَعَلْتُمْ سِقايَةَ الْحاجِّ وَ عِمارَةَ الْمَسْجِدِ الْحَرامِ كَمَنْ آمَنَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ جاهَدَ فِي سَبِيلِ اللَّهِ لا يَسْتَوُونَ عِنْدَ اللَّهِ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1255, N'الَّذِينَ آمَنُوا وَ هاجَرُوا وَ جاهَدُوا فِي سَبِيلِ اللَّهِ بِأَمْوالِهِمْ وَ أَنْفُسِهِمْ أَعْظَمُ دَرَجَةً عِنْدَ اللَّهِ وَ أُولئِكَ هُمُ الْفائِزُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1256, N'يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِنْهُ وَ رِضْوانٍ وَ جَنَّاتٍ لَهُمْ فِيها نَعِيمٌ مُقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1257, N'خالِدِينَ فِيها أَبَداً إِنَّ اللَّهَ عِنْدَهُ أَجْرٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1258, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّخِذُوا آباءَكُمْ وَ إِخْوانَكُمْ أَوْلِياءَ إِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْإِيمانِ وَ مَنْ يَتَوَلَّهُمْ مِنْكُمْ فَأُولئِكَ هُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1259, N'قُلْ إِنْ كانَ آباؤُكُمْ وَ أَبْناؤُكُمْ وَ إِخْوانُكُمْ وَ أَزْواجُكُمْ وَ عَشِيرَتُكُمْ وَ أَمْوالٌ اقْتَرَفْتُمُوها وَ تِجارَةٌ تَخْشَوْنَ كَسادَها وَ مَساكِنُ تَرْضَوْنَها أَحَبَّ إِلَيْكُمْ مِنَ اللَّهِ وَ رَسُولِهِ وَ جِهادٍ فِي سَبِيلِهِ فَتَرَبَّصُوا حَتَّى يَأْتِيَ اللَّهُ بِأَمْرِهِ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1260, N'لَقَدْ نَصَرَكُمُ اللَّهُ فِي مَواطِنَ كَثِيرَةٍ وَ يَوْمَ حُنَيْنٍ إِذْ أَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْئاً وَ ضاقَتْ عَلَيْكُمُ الْأَرْضُ بِما رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُدْبِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1261, N'ثُمَّ أَنْزَلَ اللَّهُ سَكِينَتَهُ عَلى رَسُولِهِ وَ عَلَى الْمُؤْمِنِينَ وَ أَنْزَلَ جُنُوداً لَمْ تَرَوْها وَ عَذَّبَ الَّذِينَ كَفَرُوا وَ ذلِكَ جَزاءُ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1262, N'ثُمَّ يَتُوبُ اللَّهُ مِنْ بَعْدِ ذلِكَ عَلى مَنْ يَشاءُ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1263, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْمُشْرِكُونَ نَجَسٌ فَلا يَقْرَبُوا الْمَسْجِدَ الْحَرامَ بَعْدَ عامِهِمْ هذا وَ إِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيكُمُ اللَّهُ مِنْ فَضْلِهِ إِنْ شاءَ إِنَّ اللَّهَ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1264, N'قاتِلُوا الَّذِينَ لا يُؤْمِنُونَ بِاللَّهِ وَ لا بِالْيَوْمِ الْآخِرِ وَ لا يُحَرِّمُونَ ما حَرَّمَ اللَّهُ وَ رَسُولُهُ وَ لا يَدِينُونَ دِينَ الْحَقِّ مِنَ الَّذِينَ أُوتُوا الْكِتابَ حَتَّى يُعْطُوا الْجِزْيَةَ عَنْ يَدٍ وَ هُمْ صاغِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1265, N'وَ قالَتِ الْيَهُودُ عُزَيْرٌ ابْنُ اللَّهِ وَ قالَتِ النَّصارى الْمَسِيحُ ابْنُ اللَّهِ ذلِكَ قَوْلُهُمْ بِأَفْواهِهِمْ يُضاهِؤُنَ قَوْلَ الَّذِينَ كَفَرُوا مِنْ قَبْلُ قاتَلَهُمُ اللَّهُ أَنَّى يُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1266, N'اتَّخَذُوا أَحْبارَهُمْ وَ رُهْبانَهُمْ أَرْباباً مِنْ دُونِ اللَّهِ وَ الْمَسِيحَ ابْنَ مَرْيَمَ وَ ما أُمِرُوا إِلاَّ لِيَعْبُدُوا إِلهاً واحِداً لا إِلهَ إِلاَّ هُوَ سُبْحانَهُ عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1267, N'يُرِيدُونَ أَنْ يُطْفِؤُا نُورَ اللَّهِ بِأَفْواهِهِمْ وَ يَأْبَى اللَّهُ إِلاَّ أَنْ يُتِمَّ نُورَهُ وَ لَوْ كَرِهَ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1268, N'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدى وَ دِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَ لَوْ كَرِهَ الْمُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1269, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ كَثِيراً مِنَ الْأَحْبارِ وَ الرُّهْبانِ لَيَأْكُلُونَ أَمْوالَ النَّاسِ بِالْباطِلِ وَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَ الَّذِينَ يَكْنِزُونَ الذَّهَبَ وَ الْفِضَّةَ وَ لا يُنْفِقُونَها فِي سَبِيلِ اللَّهِ فَبَشِّرْهُمْ بِعَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1270, N'يَوْمَ يُحْمى عَلَيْها فِي نارِ جَهَنَّمَ فَتُكْوى بِها جِباهُهُمْ وَ جُنُوبُهُمْ وَ ظُهُورُهُمْ هذا ما كَنَزْتُمْ لِأَنْفُسِكُمْ فَذُوقُوا ما كُنْتُمْ تَكْنِزُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1271, N'إِنَّ عِدَّةَ الشُّهُورِ عِنْدَ اللَّهِ اثْنا عَشَرَ شَهْراً فِي كِتابِ اللَّهِ يَوْمَ خَلَقَ السَّماواتِ وَ الْأَرْضَ مِنْها أَرْبَعَةٌ حُرُمٌ ذلِكَ الدِّينُ الْقَيِّمُ فَلا تَظْلِمُوا فِيهِنَّ أَنْفُسَكُمْ وَ قاتِلُوا الْمُشْرِكِينَ كَافَّةً كَما يُقاتِلُونَكُمْ كَافَّةً وَ اعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1272, N'إِنَّمَا النَّسِي‏ءُ زِيادَةٌ فِي الْكُفْرِ يُضَلُّ بِهِ الَّذِينَ كَفَرُوا يُحِلُّونَهُ عاماً وَ يُحَرِّمُونَهُ عاماً لِيُواطِؤُا عِدَّةَ ما حَرَّمَ اللَّهُ فَيُحِلُّوا ما حَرَّمَ اللَّهُ زُيِّنَ لَهُمْ سُوءُ أَعْمالِهِمْ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1273, N'يا أَيُّهَا الَّذِينَ آمَنُوا ما لَكُمْ إِذا قِيلَ لَكُمُ انْفِرُوا فِي سَبِيلِ اللَّهِ اثَّاقَلْتُمْ إِلَى الْأَرْضِ أَ رَضِيتُمْ بِالْحَياةِ الدُّنْيا مِنَ الْآخِرَةِ فَما مَتاعُ الْحَياةِ الدُّنْيا فِي الْآخِرَةِ إِلاَّ قَلِيلٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1274, N'إِلاَّ تَنْفِرُوا يُعَذِّبْكُمْ عَذاباً أَلِيماً وَ يَسْتَبْدِلْ قَوْماً غَيْرَكُمْ وَ لا تَضُرُّوهُ شَيْئاً وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1275, N'إِلاَّ تَنْصُرُوهُ فَقَدْ نَصَرَهُ اللَّهُ إِذْ أَخْرَجَهُ الَّذِينَ كَفَرُوا ثانِيَ اثْنَيْنِ إِذْ هُما فِي الْغارِ إِذْ يَقُولُ لِصاحِبِهِ لا تَحْزَنْ إِنَّ اللَّهَ مَعَنا فَأَنْزَلَ اللَّهُ سَكِينَتَهُ عَلَيْهِ وَ أَيَّدَهُ بِجُنُودٍ لَمْ تَرَوْها وَ جَعَلَ كَلِمَةَ الَّذِينَ كَفَرُوا السُّفْلى وَ كَلِمَةُ اللَّهِ هِيَ الْعُلْيا وَ اللَّهُ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1276, N'انْفِرُوا خِفافاً وَ ثِقالاً وَ جاهِدُوا بِأَمْوالِكُمْ وَ أَنْفُسِكُمْ فِي سَبِيلِ اللَّهِ ذلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1277, N'لَوْ كانَ عَرَضاً قَرِيباً وَ سَفَراً قاصِداً لاَتَّبَعُوكَ وَ لكِنْ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُ وَ سَيَحْلِفُونَ بِاللَّهِ لَوِ اسْتَطَعْنا لَخَرَجْنا مَعَكُمْ يُهْلِكُونَ أَنْفُسَهُمْ وَ اللَّهُ يَعْلَمُ إِنَّهُمْ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1278, N'عَفَا اللَّهُ عَنْكَ لِمَ أَذِنْتَ لَهُمْ حَتَّى يَتَبَيَّنَ لَكَ الَّذِينَ صَدَقُوا وَ تَعْلَمَ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1279, N'لا يَسْتَأْذِنُكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ أَنْ يُجاهِدُوا بِأَمْوالِهِمْ وَ أَنْفُسِهِمْ وَ اللَّهُ عَلِيمٌ بِالْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1280, N'إِنَّما يَسْتَأْذِنُكَ الَّذِينَ لا يُؤْمِنُونَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ ارْتابَتْ قُلُوبُهُمْ فَهُمْ فِي رَيْبِهِمْ يَتَرَدَّدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1281, N'وَ لَوْ أَرادُوا الْخُرُوجَ لَأَعَدُّوا لَهُ عُدَّةً وَ لكِنْ كَرِهَ اللَّهُ انْبِعاثَهُمْ فَثَبَّطَهُمْ وَ قِيلَ اقْعُدُوا مَعَ الْقاعِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1282, N'لَوْ خَرَجُوا فِيكُمْ ما زادُوكُمْ إِلاَّ خَبالاً وَ لَأَوْضَعُوا خِلالَكُمْ يَبْغُونَكُمُ الْفِتْنَةَ وَ فِيكُمْ سَمَّاعُونَ لَهُمْ وَ اللَّهُ عَلِيمٌ بِالظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1283, N'لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَ قَلَّبُوا لَكَ الْأُمُورَ حَتَّى جاءَ الْحَقُّ وَ ظَهَرَ أَمْرُ اللَّهِ وَ هُمْ كارِهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1284, N'وَ مِنْهُمْ مَنْ يَقُولُ ائْذَنْ لِي وَ لا تَفْتِنِّي أَلا فِي الْفِتْنَةِ سَقَطُوا وَ إِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1285, N'إِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْ وَ إِنْ تُصِبْكَ مُصِيبَةٌ يَقُولُوا قَدْ أَخَذْنا أَمْرَنا مِنْ قَبْلُ وَ يَتَوَلَّوْا وَ هُمْ فَرِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1286, N'قُلْ لَنْ يُصِيبَنا إِلاَّ ما كَتَبَ اللَّهُ لَنا هُوَ مَوْلانا وَ عَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1287, N'قُلْ هَلْ تَرَبَّصُونَ بِنا إِلاَّ إِحْدَى الْحُسْنَيَيْنِ وَ نَحْنُ نَتَرَبَّصُ بِكُمْ أَنْ يُصِيبَكُمُ اللَّهُ بِعَذابٍ مِنْ عِنْدِهِ أَوْ بِأَيْدِينا فَتَرَبَّصُوا إِنَّا مَعَكُمْ مُتَرَبِّصُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1288, N'قُلْ أَنْفِقُوا طَوْعاً أَوْ كَرْهاً لَنْ يُتَقَبَّلَ مِنْكُمْ إِنَّكُمْ كُنْتُمْ قَوْماً فاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1289, N'وَ ما مَنَعَهُمْ أَنْ تُقْبَلَ مِنْهُمْ نَفَقاتُهُمْ إِلاَّ أَنَّهُمْ كَفَرُوا بِاللَّهِ وَ بِرَسُولِهِ وَ لا يَأْتُونَ الصَّلاةَ إِلاَّ وَ هُمْ كُسالى وَ لا يُنْفِقُونَ إِلاَّ وَ هُمْ كارِهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1290, N'فَلا تُعْجِبْكَ أَمْوالُهُمْ وَ لا أَوْلادُهُمْ إِنَّما يُرِيدُ اللَّهُ لِيُعَذِّبَهُمْ بِها فِي الْحَياةِ الدُّنْيا وَ تَزْهَقَ أَنْفُسُهُمْ وَ هُمْ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1291, N'وَ يَحْلِفُونَ بِاللَّهِ إِنَّهُمْ لَمِنْكُمْ وَ ما هُمْ مِنْكُمْ وَ لكِنَّهُمْ قَوْمٌ يَفْرَقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1292, N'لَوْ يَجِدُونَ مَلْجَأً أَوْ مَغاراتٍ أَوْ مُدَّخَلاً لَوَلَّوْا إِلَيْهِ وَ هُمْ يَجْمَحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1293, N'وَ مِنْهُمْ مَنْ يَلْمِزُكَ فِي الصَّدَقاتِ فَإِنْ أُعْطُوا مِنْها رَضُوا وَ إِنْ لَمْ يُعْطَوْا مِنْها إِذا هُمْ يَسْخَطُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1294, N'وَ لَوْ أَنَّهُمْ رَضُوا ما آتاهُمُ اللَّهُ وَ رَسُولُهُ وَ قالُوا حَسْبُنَا اللَّهُ سَيُؤْتِينَا اللَّهُ مِنْ فَضْلِهِ وَ رَسُولُهُ إِنَّا إِلَى اللَّهِ راغِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1295, N'إِنَّمَا الصَّدَقاتُ لِلْفُقَراءِ وَ الْمَساكِينِ وَ الْعامِلِينَ عَلَيْها وَ الْمُؤَلَّفَةِ قُلُوبُهُمْ وَ فِي الرِّقابِ وَ الْغارِمِينَ وَ فِي سَبِيلِ اللَّهِ وَ ابْنِ السَّبِيلِ فَرِيضَةً مِنَ اللَّهِ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1296, N'وَ مِنْهُمُ الَّذِينَ يُؤْذُونَ النَّبِيَّ وَ يَقُولُونَ هُوَ أُذُنٌ قُلْ أُذُنُ خَيْرٍ لَكُمْ يُؤْمِنُ بِاللَّهِ وَ يُؤْمِنُ لِلْمُؤْمِنِينَ وَ رَحْمَةٌ لِلَّذِينَ آمَنُوا مِنْكُمْ وَ الَّذِينَ يُؤْذُونَ رَسُولَ اللَّهِ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1297, N'يَحْلِفُونَ بِاللَّهِ لَكُمْ لِيُرْضُوكُمْ وَ اللَّهُ وَ رَسُولُهُ أَحَقُّ أَنْ يُرْضُوهُ إِنْ كانُوا مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1298, N'أَ لَمْ يَعْلَمُوا أَنَّهُ مَنْ يُحادِدِ اللَّهَ وَ رَسُولَهُ فَأَنَّ لَهُ نارَ جَهَنَّمَ خالِداً فِيها ذلِكَ الْخِزْيُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1299, N'يَحْذَرُ الْمُنافِقُونَ أَنْ تُنَزَّلَ عَلَيْهِمْ سُورَةٌ تُنَبِّئُهُمْ بِما فِي قُلُوبِهِمْ قُلِ اسْتَهْزِؤُا إِنَّ اللَّهَ مُخْرِجٌ ما تَحْذَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1300, N'وَ لَئِنْ سَأَلْتَهُمْ لَيَقُولُنَّ إِنَّما كُنَّا نَخُوضُ وَ نَلْعَبُ قُلْ أَ بِاللَّهِ وَ آياتِهِ وَ رَسُولِهِ كُنْتُمْ تَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1301, N'لا تَعْتَذِرُوا قَدْ كَفَرْتُمْ بَعْدَ إِيمانِكُمْ إِنْ نَعْفُ عَنْ طائِفَةٍ مِنْكُمْ نُعَذِّبْ طائِفَةً بِأَنَّهُمْ كانُوا مُجْرِمِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1302, N'الْمُنافِقُونَ وَ الْمُنافِقاتُ بَعْضُهُمْ مِنْ بَعْضٍ يَأْمُرُونَ بِالْمُنْكَرِ وَ يَنْهَوْنَ عَنِ الْمَعْرُوفِ وَ يَقْبِضُونَ أَيْدِيَهُمْ نَسُوا اللَّهَ فَنَسِيَهُمْ إِنَّ الْمُنافِقِينَ هُمُ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1303, N'وَعَدَ اللَّهُ الْمُنافِقِينَ وَ الْمُنافِقاتِ وَ الْكُفَّارَ نارَ جَهَنَّمَ خالِدِينَ فِيها هِيَ حَسْبُهُمْ وَ لَعَنَهُمُ اللَّهُ وَ لَهُمْ عَذابٌ مُقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1304, N'كَالَّذِينَ مِنْ قَبْلِكُمْ كانُوا أَشَدَّ مِنْكُمْ قُوَّةً وَ أَكْثَرَ أَمْوالاً وَ أَوْلاداً فَاسْتَمْتَعُوا بِخَلاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلاقِكُمْ كَمَا اسْتَمْتَعَ الَّذِينَ مِنْ قَبْلِكُمْ بِخَلاقِهِمْ وَ خُضْتُمْ كَالَّذِي خاضُوا أُولئِكَ حَبِطَتْ أَعْمالُهُمْ فِي الدُّنْيا وَ الْآخِرَةِ وَ أُولئِكَ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1305, N'أَ لَمْ يَأْتِهِمْ نَبَأُ الَّذِينَ مِنْ قَبْلِهِمْ قَوْمِ نُوحٍ وَ عادٍ وَ ثَمُودَ وَ قَوْمِ إِبْراهِيمَ وَ أَصْحابِ مَدْيَنَ وَ الْمُؤْتَفِكاتِ أَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّناتِ فَما كانَ اللَّهُ لِيَظْلِمَهُمْ وَ لكِنْ كانُوا أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1306, N'وَ الْمُؤْمِنُونَ وَ الْمُؤْمِناتُ بَعْضُهُمْ أَوْلِياءُ بَعْضٍ يَأْمُرُونَ بِالْمَعْرُوفِ وَ يَنْهَوْنَ عَنِ الْمُنْكَرِ وَ يُقِيمُونَ الصَّلاةَ وَ يُؤْتُونَ الزَّكاةَ وَ يُطِيعُونَ اللَّهَ وَ رَسُولَهُ أُولئِكَ سَيَرْحَمُهُمُ اللَّهُ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1307, N'وَعَدَ اللَّهُ الْمُؤْمِنِينَ وَ الْمُؤْمِناتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها وَ مَساكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ وَ رِضْوانٌ مِنَ اللَّهِ أَكْبَرُ ذلِكَ هُوَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1308, N'يا أَيُّهَا النَّبِيُّ جاهِدِ الْكُفَّارَ وَ الْمُنافِقِينَ وَ اغْلُظْ عَلَيْهِمْ وَ مَأْواهُمْ جَهَنَّمُ وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1309, N'يَحْلِفُونَ بِاللَّهِ ما قالُوا وَ لَقَدْ قالُوا كَلِمَةَ الْكُفْرِ وَ كَفَرُوا بَعْدَ إِسْلامِهِمْ وَ هَمُّوا بِما لَمْ يَنالُوا وَ ما نَقَمُوا إِلاَّ أَنْ أَغْناهُمُ اللَّهُ وَ رَسُولُهُ مِنْ فَضْلِهِ فَإِنْ يَتُوبُوا يَكُ خَيْراً لَهُمْ وَ إِنْ يَتَوَلَّوْا يُعَذِّبْهُمُ اللَّهُ عَذاباً أَلِيماً فِي الدُّنْيا وَ الْآخِرَةِ وَ ما لَهُمْ فِي الْأَرْضِ مِنْ وَلِيٍّ وَ لا نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1310, N'وَ مِنْهُمْ مَنْ عاهَدَ اللَّهَ لَئِنْ آتانا مِنْ فَضْلِهِ لَنَصَّدَّقَنَّ وَ لَنَكُونَنَّ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1311, N'فَلَمَّا آتاهُمْ مِنْ فَضْلِهِ بَخِلُوا بِهِ وَ تَوَلَّوْا وَ هُمْ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1312, N'فَأَعْقَبَهُمْ نِفاقاً فِي قُلُوبِهِمْ إِلى يَوْمِ يَلْقَوْنَهُ بِما أَخْلَفُوا اللَّهَ ما وَعَدُوهُ وَ بِما كانُوا يَكْذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1313, N'أَ لَمْ يَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ سِرَّهُمْ وَ نَجْواهُمْ وَ أَنَّ اللَّهَ عَلاَّمُ الْغُيُوبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1314, N'الَّذِينَ يَلْمِزُونَ الْمُطَّوِّعِينَ مِنَ الْمُؤْمِنِينَ فِي الصَّدَقاتِ وَ الَّذِينَ لا يَجِدُونَ إِلاَّ جُهْدَهُمْ فَيَسْخَرُونَ مِنْهُمْ سَخِرَ اللَّهُ مِنْهُمْ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1315, N'اسْتَغْفِرْ لَهُمْ أَوْ لا تَسْتَغْفِرْ لَهُمْ إِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِينَ مَرَّةً فَلَنْ يَغْفِرَ اللَّهُ لَهُمْ ذلِكَ بِأَنَّهُمْ كَفَرُوا بِاللَّهِ وَ رَسُولِهِ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1316, N'فَرِحَ الْمُخَلَّفُونَ بِمَقْعَدِهِمْ خِلافَ رَسُولِ اللَّهِ وَ كَرِهُوا أَنْ يُجاهِدُوا بِأَمْوالِهِمْ وَ أَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ وَ قالُوا لا تَنْفِرُوا فِي الْحَرِّ قُلْ نارُ جَهَنَّمَ أَشَدُّ حَرًّا لَوْ كانُوا يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1317, N'فَلْيَضْحَكُوا قَلِيلاً وَ لْيَبْكُوا كَثِيراً جَزاءً بِما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1318, N'فَإِنْ رَجَعَكَ اللَّهُ إِلى طائِفَةٍ مِنْهُمْ فَاسْتَأْذَنُوكَ لِلْخُرُوجِ فَقُلْ لَنْ تَخْرُجُوا مَعِيَ أَبَداً وَ لَنْ تُقاتِلُوا مَعِيَ عَدُوًّا إِنَّكُمْ رَضِيتُمْ بِالْقُعُودِ أَوَّلَ مَرَّةٍ فَاقْعُدُوا مَعَ الْخالِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1319, N'وَ لا تُصَلِّ عَلى أَحَدٍ مِنْهُمْ ماتَ أَبَداً وَ لا تَقُمْ عَلى قَبْرِهِ إِنَّهُمْ كَفَرُوا بِاللَّهِ وَ رَسُولِهِ وَ ماتُوا وَ هُمْ فاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1320, N'وَ لا تُعْجِبْكَ أَمْوالُهُمْ وَ أَوْلادُهُمْ إِنَّما يُرِيدُ اللَّهُ أَنْ يُعَذِّبَهُمْ بِها فِي الدُّنْيا وَ تَزْهَقَ أَنْفُسُهُمْ وَ هُمْ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1321, N'وَ إِذا أُنْزِلَتْ سُورَةٌ أَنْ آمِنُوا بِاللَّهِ وَ جاهِدُوا مَعَ رَسُولِهِ اسْتَأْذَنَكَ أُولُوا الطَّوْلِ مِنْهُمْ وَ قالُوا ذَرْنا نَكُنْ مَعَ الْقاعِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1322, N'رَضُوا بِأَنْ يَكُونُوا مَعَ الْخَوالِفِ وَ طُبِعَ عَلى قُلُوبِهِمْ فَهُمْ لا يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1323, N'لكِنِ الرَّسُولُ وَ الَّذِينَ آمَنُوا مَعَهُ جاهَدُوا بِأَمْوالِهِمْ وَ أَنْفُسِهِمْ وَ أُولئِكَ لَهُمُ الْخَيْراتُ وَ أُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1324, N'أَعَدَّ اللَّهُ لَهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها ذلِكَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1325, N'وَ جاءَ الْمُعَذِّرُونَ مِنَ الْأَعْرابِ لِيُؤْذَنَ لَهُمْ وَ قَعَدَ الَّذِينَ كَذَبُوا اللَّهَ وَ رَسُولَهُ سَيُصِيبُ الَّذِينَ كَفَرُوا مِنْهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1326, N'لَيْسَ عَلَى الضُّعَفاءِ وَ لا عَلَى الْمَرْضى وَ لا عَلَى الَّذِينَ لا يَجِدُونَ ما يُنْفِقُونَ حَرَجٌ إِذا نَصَحُوا لِلَّهِ وَ رَسُولِهِ ما عَلَى الْمُحْسِنِينَ مِنْ سَبِيلٍ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1327, N'وَ لا عَلَى الَّذِينَ إِذا ما أَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لا أَجِدُ ما أَحْمِلُكُمْ عَلَيْهِ تَوَلَّوْا وَ أَعْيُنُهُمْ تَفِيضُ مِنَ الدَّمْعِ حَزَناً أَلاَّ يَجِدُوا ما يُنْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1328, N'إِنَّمَا السَّبِيلُ عَلَى الَّذِينَ يَسْتَأْذِنُونَكَ وَ هُمْ أَغْنِياءُ رَضُوا بِأَنْ يَكُونُوا مَعَ الْخَوالِفِ وَ طَبَعَ اللَّهُ عَلى قُلُوبِهِمْ فَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1329, N'يَعْتَذِرُونَ إِلَيْكُمْ إِذا رَجَعْتُمْ إِلَيْهِمْ قُلْ لا تَعْتَذِرُوا لَنْ نُؤْمِنَ لَكُمْ قَدْ نَبَّأَنَا اللَّهُ مِنْ أَخْبارِكُمْ وَ سَيَرَى اللَّهُ عَمَلَكُمْ وَ رَسُولُهُ ثُمَّ تُرَدُّونَ إِلى عالِمِ الْغَيْبِ وَ الشَّهادَةِ فَيُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1330, N'سَيَحْلِفُونَ بِاللَّهِ لَكُمْ إِذَا انْقَلَبْتُمْ إِلَيْهِمْ لِتُعْرِضُوا عَنْهُمْ فَأَعْرِضُوا عَنْهُمْ إِنَّهُمْ رِجْسٌ وَ مَأْواهُمْ جَهَنَّمُ جَزاءً بِما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1331, N'يَحْلِفُونَ لَكُمْ لِتَرْضَوْا عَنْهُمْ فَإِنْ تَرْضَوْا عَنْهُمْ فَإِنَّ اللَّهَ لا يَرْضى عَنِ الْقَوْمِ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1332, N'الْأَعْرابُ أَشَدُّ كُفْراً وَ نِفاقاً وَ أَجْدَرُ أَلاَّ يَعْلَمُوا حُدُودَ ما أَنْزَلَ اللَّهُ عَلى رَسُولِهِ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1333, N'وَ مِنَ الْأَعْرابِ مَنْ يَتَّخِذُ ما يُنْفِقُ مَغْرَماً وَ يَتَرَبَّصُ بِكُمُ الدَّوائِرَ عَلَيْهِمْ دائِرَةُ السَّوْءِ وَ اللَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1334, N'وَ مِنَ الْأَعْرابِ مَنْ يُؤْمِنُ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ يَتَّخِذُ ما يُنْفِقُ قُرُباتٍ عِنْدَ اللَّهِ وَ صَلَواتِ الرَّسُولِ أَلا إِنَّها قُرْبَةٌ لَهُمْ سَيُدْخِلُهُمُ اللَّهُ فِي رَحْمَتِهِ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1335, N'وَ السَّابِقُونَ الْأَوَّلُونَ مِنَ الْمُهاجِرِينَ وَ الْأَنْصارِ وَ الَّذِينَ اتَّبَعُوهُمْ بِإِحْسانٍ رَضِيَ اللَّهُ عَنْهُمْ وَ رَضُوا عَنْهُ وَ أَعَدَّ لَهُمْ جَنَّاتٍ تَجْرِي تَحْتَهَا الْأَنْهارُ خالِدِينَ فِيها أَبَداً ذلِكَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1336, N'وَ مِمَّنْ حَوْلَكُمْ مِنَ الْأَعْرابِ مُنافِقُونَ وَ مِنْ أَهْلِ الْمَدِينَةِ مَرَدُوا عَلَى النِّفاقِ لا تَعْلَمُهُمْ نَحْنُ نَعْلَمُهُمْ سَنُعَذِّبُهُمْ مَرَّتَيْنِ ثُمَّ يُرَدُّونَ إِلى عَذابٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1337, N'وَ آخَرُونَ اعْتَرَفُوا بِذُنُوبِهِمْ خَلَطُوا عَمَلاً صالِحاً وَ آخَرَ سَيِّئاً عَسَى اللَّهُ أَنْ يَتُوبَ عَلَيْهِمْ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1338, N'خُذْ مِنْ أَمْوالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَ تُزَكِّيهِمْ بِها وَ صَلِّ عَلَيْهِمْ إِنَّ صَلاتَكَ سَكَنٌ لَهُمْ وَ اللَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1339, N'أَ لَمْ يَعْلَمُوا أَنَّ اللَّهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبادِهِ وَ يَأْخُذُ الصَّدَقاتِ وَ أَنَّ اللَّهَ هُوَ التَّوَّابُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1340, N'وَ قُلِ اعْمَلُوا فَسَيَرَى اللَّهُ عَمَلَكُمْ وَ رَسُولُهُ وَ الْمُؤْمِنُونَ وَ سَتُرَدُّونَ إِلى عالِمِ الْغَيْبِ وَ الشَّهادَةِ فَيُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1341, N'وَ آخَرُونَ مُرْجَوْنَ لِأَمْرِ اللَّهِ إِمَّا يُعَذِّبُهُمْ وَ إِمَّا يَتُوبُ عَلَيْهِمْ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1342, N'وَ الَّذِينَ اتَّخَذُوا مَسْجِداً ضِراراً وَ كُفْراً وَ تَفْرِيقاً بَيْنَ الْمُؤْمِنِينَ وَ إِرْصاداً لِمَنْ حارَبَ اللَّهَ وَ رَسُولَهُ مِنْ قَبْلُ وَ لَيَحْلِفُنَّ إِنْ أَرَدْنا إِلاَّ الْحُسْنى وَ اللَّهُ يَشْهَدُ إِنَّهُمْ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1343, N'لا تَقُمْ فِيهِ أَبَداً لَمَسْجِدٌ أُسِّسَ عَلَى التَّقْوى مِنْ أَوَّلِ يَوْمٍ أَحَقُّ أَنْ تَقُومَ فِيهِ فِيهِ رِجالٌ يُحِبُّونَ أَنْ يَتَطَهَّرُوا وَ اللَّهُ يُحِبُّ الْمُطَّهِّرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1344, N'أَ فَمَنْ أَسَّسَ بُنْيانَهُ عَلى تَقْوى مِنَ اللَّهِ وَ رِضْوانٍ خَيْرٌ أَمْ مَنْ أَسَّسَ بُنْيانَهُ عَلى شَفا جُرُفٍ هارٍ فَانْهارَ بِهِ فِي نارِ جَهَنَّمَ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1345, N'لا يَزالُ بُنْيانُهُمُ الَّذِي بَنَوْا رِيبَةً فِي قُلُوبِهِمْ إِلاَّ أَنْ تَقَطَّعَ قُلُوبُهُمْ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1346, N'إِنَّ اللَّهَ اشْتَرى مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَ أَمْوالَهُمْ بِأَنَّ لَهُمُ الْجَنَّةَ يُقاتِلُونَ فِي سَبِيلِ اللَّهِ فَيَقْتُلُونَ وَ يُقْتَلُونَ وَعْداً عَلَيْهِ حَقًّا فِي التَّوْراةِ وَ الْإِنْجِيلِ وَ الْقُرْآنِ وَ مَنْ أَوْفى بِعَهْدِهِ مِنَ اللَّهِ فَاسْتَبْشِرُوا بِبَيْعِكُمُ الَّذِي بايَعْتُمْ بِهِ وَ ذلِكَ هُوَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1347, N'التَّائِبُونَ الْعابِدُونَ الْحامِدُونَ السَّائِحُونَ الرَّاكِعُونَ السَّاجِدُونَ الْآمِرُونَ بِالْمَعْرُوفِ وَ النَّاهُونَ عَنِ الْمُنْكَرِ وَ الْحافِظُونَ لِحُدُودِ اللَّهِ وَ بَشِّرِ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1348, N'ما كانَ لِلنَّبِيِّ وَ الَّذِينَ آمَنُوا أَنْ يَسْتَغْفِرُوا لِلْمُشْرِكِينَ وَ لَوْ كانُوا أُولِي قُرْبى مِنْ بَعْدِ ما تَبَيَّنَ لَهُمْ أَنَّهُمْ أَصْحابُ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1349, N'وَ ما كانَ اسْتِغْفارُ إِبْراهِيمَ لِأَبِيهِ إِلاَّ عَنْ مَوْعِدَةٍ وَعَدَها إِيَّاهُ فَلَمَّا تَبَيَّنَ لَهُ أَنَّهُ عَدُوٌّ لِلَّهِ تَبَرَّأَ مِنْهُ إِنَّ إِبْراهِيمَ لَأَوَّاهٌ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1350, N'وَ ما كانَ اللَّهُ لِيُضِلَّ قَوْماً بَعْدَ إِذْ هَداهُمْ حَتَّى يُبَيِّنَ لَهُمْ ما يَتَّقُونَ إِنَّ اللَّهَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1351, N'إِنَّ اللَّهَ لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ يُحْيِي وَ يُمِيتُ وَ ما لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَ لا نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1352, N'لَقَدْ تابَ اللَّهُ عَلَى النَّبِيِّ وَ الْمُهاجِرِينَ وَ الْأَنْصارِ الَّذِينَ اتَّبَعُوهُ فِي ساعَةِ الْعُسْرَةِ مِنْ بَعْدِ ما كادَ يَزِيغُ قُلُوبُ فَرِيقٍ مِنْهُمْ ثُمَّ تابَ عَلَيْهِمْ إِنَّهُ بِهِمْ رَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1353, N'وَ عَلَى الثَّلاثَةِ الَّذِينَ خُلِّفُوا حَتَّى إِذا ضاقَتْ عَلَيْهِمُ الْأَرْضُ بِما رَحُبَتْ وَ ضاقَتْ عَلَيْهِمْ أَنْفُسُهُمْ وَ ظَنُّوا أَنْ لا مَلْجَأَ مِنَ اللَّهِ إِلاَّ إِلَيْهِ ثُمَّ تابَ عَلَيْهِمْ لِيَتُوبُوا إِنَّ اللَّهَ هُوَ التَّوَّابُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1354, N'يا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَ كُونُوا مَعَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1355, N'ما كانَ لِأَهْلِ الْمَدِينَةِ وَ مَنْ حَوْلَهُمْ مِنَ الْأَعْرابِ أَنْ يَتَخَلَّفُوا عَنْ رَسُولِ اللَّهِ وَ لا يَرْغَبُوا بِأَنْفُسِهِمْ عَنْ نَفْسِهِ ذلِكَ بِأَنَّهُمْ لا يُصِيبُهُمْ ظَمَأٌ وَ لا نَصَبٌ وَ لا مَخْمَصَةٌ فِي سَبِيلِ اللَّهِ وَ لا يَطَؤُنَ مَوْطِئاً يَغِيظُ الْكُفَّارَ وَ لا يَنالُونَ مِنْ عَدُوٍّ نَيْلاً إِلاَّ كُتِبَ لَهُمْ بِهِ عَمَلٌ صالِحٌ إِنَّ اللَّهَ لا يُضِيعُ أَجْرَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1356, N'وَ لا يُنْفِقُونَ نَفَقَةً صَغِيرَةً وَ لا كَبِيرَةً وَ لا يَقْطَعُونَ وادِياً إِلاَّ كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1357, N'وَ ما كانَ الْمُؤْمِنُونَ لِيَنْفِرُوا كَافَّةً فَلَوْ لا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طائِفَةٌ لِيَتَفَقَّهُوا فِي الدِّينِ وَ لِيُنْذِرُوا قَوْمَهُمْ إِذا رَجَعُوا إِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1358, N'يا أَيُّهَا الَّذِينَ آمَنُوا قاتِلُوا الَّذِينَ يَلُونَكُمْ مِنَ الْكُفَّارِ وَ لْيَجِدُوا فِيكُمْ غِلْظَةً وَ اعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1359, N'وَ إِذا ما أُنْزِلَتْ سُورَةٌ فَمِنْهُمْ مَنْ يَقُولُ أَيُّكُمْ زادَتْهُ هذِهِ إِيماناً فَأَمَّا الَّذِينَ آمَنُوا فَزادَتْهُمْ إِيماناً وَ هُمْ يَسْتَبْشِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1360, N'وَ أَمَّا الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ فَزادَتْهُمْ رِجْساً إِلَى رِجْسِهِمْ وَ ماتُوا وَ هُمْ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1361, N'أَ وَ لا يَرَوْنَ أَنَّهُمْ يُفْتَنُونَ فِي كُلِّ عامٍ مَرَّةً أَوْ مَرَّتَيْنِ ثُمَّ لا يَتُوبُونَ وَ لا هُمْ يَذَّكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1362, N'وَ إِذا ما أُنْزِلَتْ سُورَةٌ نَظَرَ بَعْضُهُمْ إِلى بَعْضٍ هَلْ يَراكُمْ مِنْ أَحَدٍ ثُمَّ انْصَرَفُوا صَرَفَ اللَّهُ قُلُوبَهُمْ بِأَنَّهُمْ قَوْمٌ لا يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1363, N'لَقَدْ جاءَكُمْ رَسُولٌ مِنْ أَنْفُسِكُمْ عَزِيزٌ عَلَيْهِ ما عَنِتُّمْ حَرِيصٌ عَلَيْكُمْ بِالْمُؤْمِنِينَ رَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1364, N'فَإِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللَّهُ لا إِلهَ إِلاَّ هُوَ عَلَيْهِ تَوَكَّلْتُ وَ هُوَ رَبُّ الْعَرْشِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1365, N'الر تِلْكَ آياتُ الْكِتابِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1366, N'أَ كانَ لِلنَّاسِ عَجَباً أَنْ أَوْحَيْنا إِلى رَجُلٍ مِنْهُمْ أَنْ أَنْذِرِ النَّاسَ وَ بَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ قالَ الْكافِرُونَ إِنَّ هذا لَساحِرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1367, N'إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوى عَلَى الْعَرْشِ يُدَبِّرُ الْأَمْرَ ما مِنْ شَفِيعٍ إِلاَّ مِنْ بَعْدِ إِذْنِهِ ذلِكُمُ اللَّهُ رَبُّكُمْ فَاعْبُدُوهُ أَ فَلا تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1368, N'إِلَيْهِ مَرْجِعُكُمْ جَمِيعاً وَعْدَ اللَّهِ حَقًّا إِنَّهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ لِيَجْزِيَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ بِالْقِسْطِ وَ الَّذِينَ كَفَرُوا لَهُمْ شَرابٌ مِنْ حَمِيمٍ وَ عَذابٌ أَلِيمٌ بِما كانُوا يَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1369, N'هُوَ الَّذِي جَعَلَ الشَّمْسَ ضِياءً وَ الْقَمَرَ نُوراً وَ قَدَّرَهُ مَنازِلَ لِتَعْلَمُوا عَدَدَ السِّنِينَ وَ الْحِسابَ ما خَلَقَ اللَّهُ ذلِكَ إِلاَّ بِالْحَقِّ يُفَصِّلُ الْآياتِ لِقَوْمٍ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1370, N'إِنَّ فِي اخْتِلافِ اللَّيْلِ وَ النَّهارِ وَ ما خَلَقَ اللَّهُ فِي السَّماواتِ وَ الْأَرْضِ لَآياتٍ لِقَوْمٍ يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1371, N'إِنَّ الَّذِينَ لا يَرْجُونَ لِقاءَنا وَ رَضُوا بِالْحَياةِ الدُّنْيا وَ اطْمَأَنُّوا بِها وَ الَّذِينَ هُمْ عَنْ آياتِنا غافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1372, N'أُولئِكَ مَأْواهُمُ النَّارُ بِما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1373, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ يَهْدِيهِمْ رَبُّهُمْ بِإِيمانِهِمْ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهارُ فِي جَنَّاتِ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1374, N'دَعْواهُمْ فِيها سُبْحانَكَ اللَّهُمَّ وَ تَحِيَّتُهُمْ فِيها سَلامٌ وَ آخِرُ دَعْواهُمْ أَنِ الْحَمْدُ لِلَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1375, N'وَ لَوْ يُعَجِّلُ اللَّهُ لِلنَّاسِ الشَّرَّ اسْتِعْجالَهُمْ بِالْخَيْرِ لَقُضِيَ إِلَيْهِمْ أَجَلُهُمْ فَنَذَرُ الَّذِينَ لا يَرْجُونَ لِقاءَنا فِي طُغْيانِهِمْ يَعْمَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1376, N'وَ إِذا مَسَّ الْإِنْسانَ الضُّرُّ دَعانا لِجَنْبِهِ أَوْ قاعِداً أَوْ قائِماً فَلَمَّا كَشَفْنا عَنْهُ ضُرَّهُ مَرَّ كَأَنْ لَمْ يَدْعُنا إِلى ضُرٍّ مَسَّهُ كَذلِكَ زُيِّنَ لِلْمُسْرِفِينَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1377, N'وَ لَقَدْ أَهْلَكْنَا الْقُرُونَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُوا وَ جاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّناتِ وَ ما كانُوا لِيُؤْمِنُوا كَذلِكَ نَجْزِي الْقَوْمَ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1378, N'ثُمَّ جَعَلْناكُمْ خَلائِفَ فِي الْأَرْضِ مِنْ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1379, N'وَ إِذا تُتْلى عَلَيْهِمْ آياتُنا بَيِّناتٍ قالَ الَّذِينَ لا يَرْجُونَ لِقاءَنَا ائْتِ بِقُرْآنٍ غَيْرِ هذا أَوْ بَدِّلْهُ قُلْ ما يَكُونُ لِي أَنْ أُبَدِّلَهُ مِنْ تِلْقاءِ نَفْسِي إِنْ أَتَّبِعُ إِلاَّ ما يُوحى إِلَيَّ إِنِّي أَخافُ إِنْ عَصَيْتُ رَبِّي عَذابَ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1380, N'قُلْ لَوْ شاءَ اللَّهُ ما تَلَوْتُهُ عَلَيْكُمْ وَ لا أَدْراكُمْ بِهِ فَقَدْ لَبِثْتُ فِيكُمْ عُمُراً مِنْ قَبْلِهِ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1381, N'فَمَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ كَذِباً أَوْ كَذَّبَ بِآياتِهِ إِنَّهُ لا يُفْلِحُ الْمُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1382, N'وَ يَعْبُدُونَ مِنْ دُونِ اللَّهِ ما لا يَضُرُّهُمْ وَ لا يَنْفَعُهُمْ وَ يَقُولُونَ هؤُلاءِ شُفَعاؤُنا عِنْدَ اللَّهِ قُلْ أَ تُنَبِّئُونَ اللَّهَ بِما لا يَعْلَمُ فِي السَّماواتِ وَ لا فِي الْأَرْضِ سُبْحانَهُ وَ تَعالى عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1383, N'وَ ما كانَ النَّاسُ إِلاَّ أُمَّةً واحِدَةً فَاخْتَلَفُوا وَ لَوْ لا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ فِيما فِيهِ يَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1384, N'وَ يَقُولُونَ لَوْ لا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ فَقُلْ إِنَّمَا الْغَيْبُ لِلَّهِ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1385, N'وَ إِذا أَذَقْنَا النَّاسَ رَحْمَةً مِنْ بَعْدِ ضَرَّاءَ مَسَّتْهُمْ إِذا لَهُمْ مَكْرٌ فِي آياتِنا قُلِ اللَّهُ أَسْرَعُ مَكْراً إِنَّ رُسُلَنا يَكْتُبُونَ ما تَمْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1386, N'هُوَ الَّذِي يُسَيِّرُكُمْ فِي الْبَرِّ وَ الْبَحْرِ حَتَّى إِذا كُنْتُمْ فِي الْفُلْكِ وَ جَرَيْنَ بِهِمْ بِرِيحٍ طَيِّبَةٍ وَ فَرِحُوا بِها جاءَتْها رِيحٌ عاصِفٌ وَ جاءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكانٍ وَ ظَنُّوا أَنَّهُمْ أُحِيطَ بِهِمْ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ لَئِنْ أَنْجَيْتَنا مِنْ هذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1387, N'فَلَمَّا أَنْجاهُمْ إِذا هُمْ يَبْغُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ يا أَيُّهَا النَّاسُ إِنَّما بَغْيُكُمْ عَلى أَنْفُسِكُمْ مَتاعَ الْحَياةِ الدُّنْيا ثُمَّ إِلَيْنا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1388, N'إِنَّما مَثَلُ الْحَياةِ الدُّنْيا كَماءٍ أَنْزَلْناهُ مِنَ السَّماءِ فَاخْتَلَطَ بِهِ نَباتُ الْأَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَ الْأَنْعامُ حَتَّى إِذا أَخَذَتِ الْأَرْضُ زُخْرُفَها وَ ازَّيَّنَتْ وَ ظَنَّ أَهْلُها أَنَّهُمْ قادِرُونَ عَلَيْها أَتاها أَمْرُنا لَيْلاً أَوْ نَهاراً فَجَعَلْناها حَصِيداً كَأَنْ لَمْ تَغْنَ بِالْأَمْسِ كَذلِكَ نُفَصِّلُ الْآياتِ لِقَوْمٍ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1389, N'وَ اللَّهُ يَدْعُوا إِلى دارِ السَّلامِ وَ يَهْدِي مَنْ يَشاءُ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1390, N'لِلَّذِينَ أَحْسَنُوا الْحُسْنى وَ زِيادَةٌ وَ لا يَرْهَقُ وُجُوهَهُمْ قَتَرٌ وَ لا ذِلَّةٌ أُولئِكَ أَصْحابُ الْجَنَّةِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1391, N'وَ الَّذِينَ كَسَبُوا السَّيِّئاتِ جَزاءُ سَيِّئَةٍ بِمِثْلِها وَ تَرْهَقُهُمْ ذِلَّةٌ ما لَهُمْ مِنَ اللَّهِ مِنْ عاصِمٍ كَأَنَّما أُغْشِيَتْ وُجُوهُهُمْ قِطَعاً مِنَ اللَّيْلِ مُظْلِماً أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1392, N'وَ يَوْمَ نَحْشُرُهُمْ جَمِيعاً ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا مَكانَكُمْ أَنْتُمْ وَ شُرَكاؤُكُمْ فَزَيَّلْنا بَيْنَهُمْ وَ قالَ شُرَكاؤُهُمْ ما كُنْتُمْ إِيَّانا تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1393, N'فَكَفى بِاللَّهِ شَهِيداً بَيْنَنا وَ بَيْنَكُمْ إِنْ كُنَّا عَنْ عِبادَتِكُمْ لَغافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1394, N'هُنالِكَ تَبْلُوا كُلُّ نَفْسٍ ما أَسْلَفَتْ وَ رُدُّوا إِلَى اللَّهِ مَوْلاهُمُ الْحَقِّ وَ ضَلَّ عَنْهُمْ ما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1395, N'قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّماءِ وَ الْأَرْضِ أَمَّنْ يَمْلِكُ السَّمْعَ وَ الْأَبْصارَ وَ مَنْ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَ يُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَ مَنْ يُدَبِّرُ الْأَمْرَ فَسَيَقُولُونَ اللَّهُ فَقُلْ أَ فَلا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1396, N'فَذلِكُمُ اللَّهُ رَبُّكُمُ الْحَقُّ فَما ذا بَعْدَ الْحَقِّ إِلاَّ الضَّلالُ فَأَنَّى تُصْرَفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1397, N'كَذلِكَ حَقَّتْ كَلِمَةُ رَبِّكَ عَلَى الَّذِينَ فَسَقُوا أَنَّهُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1398, N'قُلْ هَلْ مِنْ شُرَكائِكُمْ مَنْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ قُلِ اللَّهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ فَأَنَّى تُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1399, N'قُلْ هَلْ مِنْ شُرَكائِكُمْ مَنْ يَهْدِي إِلَى الْحَقِّ قُلِ اللَّهُ يَهْدِي لِلْحَقِّ أَ فَمَنْ يَهْدِي إِلَى الْحَقِّ أَحَقُّ أَنْ يُتَّبَعَ أَمَّنْ لا يَهِدِّي إِلاَّ أَنْ يُهْدى فَما لَكُمْ كَيْفَ تَحْكُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1400, N'وَ ما يَتَّبِعُ أَكْثَرُهُمْ إِلاَّ ظَنًّا إِنَّ الظَّنَّ لا يُغْنِي مِنَ الْحَقِّ شَيْئاً إِنَّ اللَّهَ عَلِيمٌ بِما يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1401, N'وَ ما كانَ هذَا الْقُرْآنُ أَنْ يُفْتَرى مِنْ دُونِ اللَّهِ وَ لكِنْ تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَ تَفْصِيلَ الْكِتابِ لا رَيْبَ فِيهِ مِنْ رَبِّ الْعالَمِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1402, N'أَمْ يَقُولُونَ افْتَراهُ قُلْ فَأْتُوا بِسُورَةٍ مِثْلِهِ وَ ادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1403, N'بَلْ كَذَّبُوا بِما لَمْ يُحِيطُوا بِعِلْمِهِ وَ لَمَّا يَأْتِهِمْ تَأْوِيلُهُ كَذلِكَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كانَ عاقِبَةُ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1404, N'وَ مِنْهُمْ مَنْ يُؤْمِنُ بِهِ وَ مِنْهُمْ مَنْ لا يُؤْمِنُ بِهِ وَ رَبُّكَ أَعْلَمُ بِالْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1405, N'وَ إِنْ كَذَّبُوكَ فَقُلْ لِي عَمَلِي وَ لَكُمْ عَمَلُكُمْ أَنْتُمْ بَرِيئُونَ مِمَّا أَعْمَلُ وَ أَنَا بَرِي‏ءٌ مِمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1406, N'وَ مِنْهُمْ مَنْ يَسْتَمِعُونَ إِلَيْكَ أَ فَأَنْتَ تُسْمِعُ الصُّمَّ وَ لَوْ كانُوا لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1407, N'وَ مِنْهُمْ مَنْ يَنْظُرُ إِلَيْكَ أَ فَأَنْتَ تَهْدِي الْعُمْيَ وَ لَوْ كانُوا لا يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1408, N'إِنَّ اللَّهَ لا يَظْلِمُ النَّاسَ شَيْئاً وَ لكِنَّ النَّاسَ أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1409, N'وَ يَوْمَ يَحْشُرُهُمْ كَأَنْ لَمْ يَلْبَثُوا إِلاَّ ساعَةً مِنَ النَّهارِ يَتَعارَفُونَ بَيْنَهُمْ قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقاءِ اللَّهِ وَ ما كانُوا مُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1410, N'وَ إِمَّا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنا مَرْجِعُهُمْ ثُمَّ اللَّهُ شَهِيدٌ عَلى ما يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1411, N'وَ لِكُلِّ أُمَّةٍ رَسُولٌ فَإِذا جاءَ رَسُولُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1412, N'وَ يَقُولُونَ مَتى هذَا الْوَعْدُ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1413, N'قُلْ لا أَمْلِكُ لِنَفْسِي ضَرًّا وَ لا نَفْعاً إِلاَّ ما شاءَ اللَّهُ لِكُلِّ أُمَّةٍ أَجَلٌ إِذا جاءَ أَجَلُهُمْ فَلا يَسْتَأْخِرُونَ ساعَةً وَ لا يَسْتَقْدِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1414, N'قُلْ أَ رَأَيْتُمْ إِنْ أَتاكُمْ عَذابُهُ بَياتاً أَوْ نَهاراً ما ذا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1415, N'أَ ثُمَّ إِذا ما وَقَعَ آمَنْتُمْ بِهِ آلْآنَ وَ قَدْ كُنْتُمْ بِهِ تَسْتَعْجِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1416, N'ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذابَ الْخُلْدِ هَلْ تُجْزَوْنَ إِلاَّ بِما كُنْتُمْ تَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1417, N'وَ يَسْتَنْبِئُونَكَ أَ حَقٌّ هُوَ قُلْ إِي وَ رَبِّي إِنَّهُ لَحَقٌّ وَ ما أَنْتُمْ بِمُعْجِزِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1418, N'وَ لَوْ أَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ ما فِي الْأَرْضِ لاَفْتَدَتْ بِهِ وَ أَسَرُّوا النَّدامَةَ لَمَّا رَأَوُا الْعَذابَ وَ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1419, N'أَلا إِنَّ لِلَّهِ ما فِي السَّماواتِ وَ الْأَرْضِ أَلا إِنَّ وَعْدَ اللَّهِ حَقٌّ وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1420, N'هُوَ يُحيِي وَ يُمِيتُ وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1421, N'يا أَيُّهَا النَّاسُ قَدْ جاءَتْكُمْ مَوْعِظَةٌ مِنْ رَبِّكُمْ وَ شِفاءٌ لِما فِي الصُّدُورِ وَ هُدىً وَ رَحْمَةٌ لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1422, N'قُلْ بِفَضْلِ اللَّهِ وَ بِرَحْمَتِهِ فَبِذلِكَ فَلْيَفْرَحُوا هُوَ خَيْرٌ مِمَّا يَجْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1423, N'قُلْ أَ رَأَيْتُمْ ما أَنْزَلَ اللَّهُ لَكُمْ مِنْ رِزْقٍ فَجَعَلْتُمْ مِنْهُ حَراماً وَ حَلالاً قُلْ آللَّهُ أَذِنَ لَكُمْ أَمْ عَلَى اللَّهِ تَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1424, N'وَ ما ظَنُّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ يَوْمَ الْقِيامَةِ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَ لكِنَّ أَكْثَرَهُمْ لا يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1425, N'وَ ما تَكُونُ فِي شَأْنٍ وَ ما تَتْلُوا مِنْهُ مِنْ قُرْآنٍ وَ لا تَعْمَلُونَ مِنْ عَمَلٍ إِلاَّ كُنَّا عَلَيْكُمْ شُهُوداً إِذْ تُفِيضُونَ فِيهِ وَ ما يَعْزُبُ عَنْ رَبِّكَ مِنْ مِثْقالِ ذَرَّةٍ فِي الْأَرْضِ وَ لا فِي السَّماءِ وَ لا أَصْغَرَ مِنْ ذلِكَ وَ لا أَكْبَرَ إِلاَّ فِي كِتابٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1426, N'أَلا إِنَّ أَوْلِياءَ اللَّهِ لا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1427, N'الَّذِينَ آمَنُوا وَ كانُوا يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1428, N'لَهُمُ الْبُشْرى فِي الْحَياةِ الدُّنْيا وَ فِي الْآخِرَةِ لا تَبْدِيلَ لِكَلِماتِ اللَّهِ ذلِكَ هُوَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1429, N'وَ لا يَحْزُنْكَ قَوْلُهُمْ إِنَّ الْعِزَّةَ لِلَّهِ جَمِيعاً هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1430, N'أَلا إِنَّ لِلَّهِ مَنْ فِي السَّماواتِ وَ مَنْ فِي الْأَرْضِ وَ ما يَتَّبِعُ الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ شُرَكاءَ إِنْ يَتَّبِعُونَ إِلاَّ الظَّنَّ وَ إِنْ هُمْ إِلاَّ يَخْرُصُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1431, N'هُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَ النَّهارَ مُبْصِراً إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1432, N'قالُوا اتَّخَذَ اللَّهُ وَلَداً سُبْحانَهُ هُوَ الْغَنِيُّ لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ إِنْ عِنْدَكُمْ مِنْ سُلْطانٍ بِهذا أَ تَقُولُونَ عَلَى اللَّهِ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1433, N'قُلْ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لا يُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1434, N'مَتاعٌ فِي الدُّنْيا ثُمَّ إِلَيْنا مَرْجِعُهُمْ ثُمَّ نُذِيقُهُمُ الْعَذابَ الشَّدِيدَ بِما كانُوا يَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1435, N'وَ اتْلُ عَلَيْهِمْ نَبَأَ نُوحٍ إِذْ قالَ لِقَوْمِهِ يا قَوْمِ إِنْ كانَ كَبُرَ عَلَيْكُمْ مَقامِي وَ تَذْكِيرِي بِآياتِ اللَّهِ فَعَلَى اللَّهِ تَوَكَّلْتُ فَأَجْمِعُوا أَمْرَكُمْ وَ شُرَكاءَكُمْ ثُمَّ لا يَكُنْ أَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُوا إِلَيَّ وَ لا تُنْظِرُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1436, N'فَإِنْ تَوَلَّيْتُمْ فَما سَأَلْتُكُمْ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلاَّ عَلَى اللَّهِ وَ أُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1437, N'فَكَذَّبُوهُ فَنَجَّيْناهُ وَ مَنْ مَعَهُ فِي الْفُلْكِ وَ جَعَلْناهُمْ خَلائِفَ وَ أَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآياتِنا فَانْظُرْ كَيْفَ كانَ عاقِبَةُ الْمُنْذَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1438, N'ثُمَّ بَعَثْنا مِنْ بَعْدِهِ رُسُلاً إِلى قَوْمِهِمْ فَجاؤُهُمْ بِالْبَيِّناتِ فَما كانُوا لِيُؤْمِنُوا بِما كَذَّبُوا بِهِ مِنْ قَبْلُ كَذلِكَ نَطْبَعُ عَلى قُلُوبِ الْمُعْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1439, N'ثُمَّ بَعَثْنا مِنْ بَعْدِهِمْ مُوسى وَ هارُونَ إِلى فِرْعَوْنَ وَ مَلاَئِهِ بِآياتِنا فَاسْتَكْبَرُوا وَ كانُوا قَوْماً مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1440, N'فَلَمَّا جاءَهُمُ الْحَقُّ مِنْ عِنْدِنا قالُوا إِنَّ هذا لَسِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1441, N'قالَ مُوسى أَ تَقُولُونَ لِلْحَقِّ لَمَّا جاءَكُمْ أَ سِحْرٌ هذا وَ لا يُفْلِحُ السَّاحِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1442, N'قالُوا أَ جِئْتَنا لِتَلْفِتَنا عَمَّا وَجَدْنا عَلَيْهِ آباءَنا وَ تَكُونَ لَكُمَا الْكِبْرِياءُ فِي الْأَرْضِ وَ ما نَحْنُ لَكُما بِمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1443, N'وَ قالَ فِرْعَوْنُ ائْتُونِي بِكُلِّ ساحِرٍ عَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1444, N'فَلَمَّا جاءَ السَّحَرَةُ قالَ لَهُمْ مُوسى أَلْقُوا ما أَنْتُمْ مُلْقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1445, N'فَلَمَّا أَلْقَوْا قالَ مُوسى ما جِئْتُمْ بِهِ السِّحْرُ إِنَّ اللَّهَ سَيُبْطِلُهُ إِنَّ اللَّهَ لا يُصْلِحُ عَمَلَ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1446, N'وَ يُحِقُّ اللَّهُ الْحَقَّ بِكَلِماتِهِ وَ لَوْ كَرِهَ الْمُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1447, N'فَما آمَنَ لِمُوسى إِلاَّ ذُرِّيَّةٌ مِنْ قَوْمِهِ عَلى خَوْفٍ مِنْ فِرْعَوْنَ وَ مَلاَئِهِمْ أَنْ يَفْتِنَهُمْ وَ إِنَّ فِرْعَوْنَ لَعالٍ فِي الْأَرْضِ وَ إِنَّهُ لَمِنَ الْمُسْرِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1448, N'وَ قالَ مُوسى يا قَوْمِ إِنْ كُنْتُمْ آمَنْتُمْ بِاللَّهِ فَعَلَيْهِ تَوَكَّلُوا إِنْ كُنْتُمْ مُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1449, N'فَقالُوا عَلَى اللَّهِ تَوَكَّلْنا رَبَّنا لا تَجْعَلْنا فِتْنَةً لِلْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1450, N'وَ نَجِّنا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1451, N'وَ أَوْحَيْنا إِلى مُوسى وَ أَخِيهِ أَنْ تَبَوَّءا لِقَوْمِكُما بِمِصْرَ بُيُوتاً وَ اجْعَلُوا بُيُوتَكُمْ قِبْلَةً وَ أَقِيمُوا الصَّلاةَ وَ بَشِّرِ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1452, N'وَ قالَ مُوسى رَبَّنا إِنَّكَ آتَيْتَ فِرْعَوْنَ وَ مَلَأَهُ زِينَةً وَ أَمْوالاً فِي الْحَياةِ الدُّنْيا رَبَّنا لِيُضِلُّوا عَنْ سَبِيلِكَ رَبَّنَا اطْمِسْ عَلى أَمْوالِهِمْ وَ اشْدُدْ عَلى قُلُوبِهِمْ فَلا يُؤْمِنُوا حَتَّى يَرَوُا الْعَذابَ الْأَلِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1453, N'قالَ قَدْ أُجِيبَتْ دَعْوَتُكُما فَاسْتَقِيما وَ لا تَتَّبِعانِّ سَبِيلَ الَّذِينَ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1454, N'وَ جاوَزْنا بِبَنِي إِسْرائِيلَ الْبَحْرَ فَأَتْبَعَهُمْ فِرْعَوْنُ وَ جُنُودُهُ بَغْياً وَ عَدْواً حَتَّى إِذا أَدْرَكَهُ الْغَرَقُ قالَ آمَنْتُ أَنَّهُ لا إِلهَ إِلاَّ الَّذِي آمَنَتْ بِهِ بَنُوا إِسْرائِيلَ وَ أَنَا مِنَ الْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1455, N'آلْآنَ وَ قَدْ عَصَيْتَ قَبْلُ وَ كُنْتَ مِنَ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1456, N'فَالْيَوْمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنْ خَلْفَكَ آيَةً وَ إِنَّ كَثِيراً مِنَ النَّاسِ عَنْ آياتِنا لَغافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1457, N'وَ لَقَدْ بَوَّأْنا بَنِي إِسْرائِيلَ مُبَوَّأَ صِدْقٍ وَ رَزَقْناهُمْ مِنَ الطَّيِّباتِ فَمَا اخْتَلَفُوا حَتَّى جاءَهُمُ الْعِلْمُ إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ يَوْمَ الْقِيامَةِ فِيما كانُوا فِيهِ يَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1458, N'فَإِنْ كُنْتَ فِي شَكٍّ مِمَّا أَنْزَلْنا إِلَيْكَ فَسْئَلِ الَّذِينَ يَقْرَؤُنَ الْكِتابَ مِنْ قَبْلِكَ لَقَدْ جاءَكَ الْحَقُّ مِنْ رَبِّكَ فَلا تَكُونَنَّ مِنَ المُمْتَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1459, N'وَ لا تَكُونَنَّ مِنَ الَّذِينَ كَذَّبُوا بِآياتِ اللَّهِ فَتَكُونَ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1460, N'إِنَّ الَّذِينَ حَقَّتْ عَلَيْهِمْ كَلِمَتُ رَبِّكَ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1461, N'وَ لَوْ جاءَتْهُمْ كُلُّ آيَةٍ حَتَّى يَرَوُا الْعَذابَ الْأَلِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1462, N'فَلَوْ لا كانَتْ قَرْيَةٌ آمَنَتْ فَنَفَعَها إِيمانُها إِلاَّ قَوْمَ يُونُسَ لَمَّا آمَنُوا كَشَفْنا عَنْهُمْ عَذابَ الْخِزْيِ فِي الْحَياةِ الدُّنْيا وَ مَتَّعْناهُمْ إِلى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1463, N'وَ لَوْ شاءَ رَبُّكَ لَآمَنَ مَنْ فِي الْأَرْضِ كُلُّهُمْ جَمِيعاً أَ فَأَنْتَ تُكْرِهُ النَّاسَ حَتَّى يَكُونُوا مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1464, N'وَ ما كانَ لِنَفْسٍ أَنْ تُؤْمِنَ إِلاَّ بِإِذْنِ اللَّهِ وَ يَجْعَلُ الرِّجْسَ عَلَى الَّذِينَ لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1465, N'قُلِ انْظُرُوا ما ذا فِي السَّماواتِ وَ الْأَرْضِ وَ ما تُغْنِي الْآياتُ وَ النُّذُرُ عَنْ قَوْمٍ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1466, N'فَهَلْ يَنْتَظِرُونَ إِلاَّ مِثْلَ أَيَّامِ الَّذِينَ خَلَوْا مِنْ قَبْلِهِمْ قُلْ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1467, N'ثُمَّ نُنَجِّي رُسُلَنا وَ الَّذِينَ آمَنُوا كَذلِكَ حَقًّا عَلَيْنا نُنْجِ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1468, N'قُلْ يا أَيُّهَا النَّاسُ إِنْ كُنْتُمْ فِي شَكٍّ مِنْ دِينِي فَلا أَعْبُدُ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ وَ لكِنْ أَعْبُدُ اللَّهَ الَّذِي يَتَوَفَّاكُمْ وَ أُمِرْتُ أَنْ أَكُونَ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1469, N'وَ أَنْ أَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفاً وَ لا تَكُونَنَّ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1470, N'وَ لا تَدْعُ مِنْ دُونِ اللَّهِ ما لا يَنْفَعُكَ وَ لا يَضُرُّكَ فَإِنْ فَعَلْتَ فَإِنَّكَ إِذاً مِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1471, N'وَ إِنْ يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلا كاشِفَ لَهُ إِلاَّ هُوَ وَ إِنْ يُرِدْكَ بِخَيْرٍ فَلا رَادَّ لِفَضْلِهِ يُصِيبُ بِهِ مَنْ يَشاءُ مِنْ عِبادِهِ وَ هُوَ الْغَفُورُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1472, N'قُلْ يا أَيُّهَا النَّاسُ قَدْ جاءَكُمُ الْحَقُّ مِنْ رَبِّكُمْ فَمَنِ اهْتَدى فَإِنَّما يَهْتَدِي لِنَفْسِهِ وَ مَنْ ضَلَّ فَإِنَّما يَضِلُّ عَلَيْها وَ ما أَنَا عَلَيْكُمْ بِوَكِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1473, N'وَ اتَّبِعْ ما يُوحى إِلَيْكَ وَ اصْبِرْ حَتَّى يَحْكُمَ اللَّهُ وَ هُوَ خَيْرُ الْحاكِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1474, N'الر كِتابٌ أُحْكِمَتْ آياتُهُ ثُمَّ فُصِّلَتْ مِنْ لَدُنْ حَكِيمٍ خَبِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1475, N'أَلاَّ تَعْبُدُوا إِلاَّ اللَّهَ إِنَّنِي لَكُمْ مِنْهُ نَذِيرٌ وَ بَشِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1476, N'وَ أَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُمَتِّعْكُمْ مَتاعاً حَسَناً إِلى أَجَلٍ مُسَمًّى وَ يُؤْتِ كُلَّ ذِي فَضْلٍ فَضْلَهُ وَ إِنْ تَوَلَّوْا فَإِنِّي أَخافُ عَلَيْكُمْ عَذابَ يَوْمٍ كَبِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1477, N'إِلَى اللَّهِ مَرْجِعُكُمْ وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1478, N'أَلا إِنَّهُمْ يَثْنُونَ صُدُورَهُمْ لِيَسْتَخْفُوا مِنْهُ أَلا حِينَ يَسْتَغْشُونَ ثِيابَهُمْ يَعْلَمُ ما يُسِرُّونَ وَ ما يُعْلِنُونَ إِنَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1479, N'وَ ما مِنْ دَابَّةٍ فِي الْأَرْضِ إِلاَّ عَلَى اللَّهِ رِزْقُها وَ يَعْلَمُ مُسْتَقَرَّها وَ مُسْتَوْدَعَها كُلٌّ فِي كِتابٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1480, N'وَ هُوَ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ فِي سِتَّةِ أَيَّامٍ وَ كانَ عَرْشُهُ عَلَى الْماءِ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلاً وَ لَئِنْ قُلْتَ إِنَّكُمْ مَبْعُوثُونَ مِنْ بَعْدِ الْمَوْتِ لَيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ هذا إِلاَّ سِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1481, N'وَ لَئِنْ أَخَّرْنا عَنْهُمُ الْعَذابَ إِلى أُمَّةٍ مَعْدُودَةٍ لَيَقُولُنَّ ما يَحْبِسُهُ أَلا يَوْمَ يَأْتِيهِمْ لَيْسَ مَصْرُوفاً عَنْهُمْ وَ حاقَ بِهِمْ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1482, N'وَ لَئِنْ أَذَقْنَا الْإِنْسانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْناها مِنْهُ إِنَّهُ لَيَؤُسٌ كَفُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1483, N'وَ لَئِنْ أَذَقْناهُ نَعْماءَ بَعْدَ ضَرَّاءَ مَسَّتْهُ لَيَقُولَنَّ ذَهَبَ السَّيِّئاتُ عَنِّي إِنَّهُ لَفَرِحٌ فَخُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1484, N'إِلاَّ الَّذِينَ صَبَرُوا وَ عَمِلُوا الصَّالِحاتِ أُولئِكَ لَهُمْ مَغْفِرَةٌ وَ أَجْرٌ كَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1485, N'فَلَعَلَّكَ تارِكٌ بَعْضَ ما يُوحى إِلَيْكَ وَ ضائِقٌ بِهِ صَدْرُكَ أَنْ يَقُولُوا لَوْ لا أُنْزِلَ عَلَيْهِ كَنْزٌ أَوْ جاءَ مَعَهُ مَلَكٌ إِنَّما أَنْتَ نَذِيرٌ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ وَكِيلٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1486, N'أَمْ يَقُولُونَ افْتَراهُ قُلْ فَأْتُوا بِعَشْرِ سُوَرٍ مِثْلِهِ مُفْتَرَياتٍ وَ ادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللَّهِ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1487, N'فَإِلَّمْ يَسْتَجِيبُوا لَكُمْ فَاعْلَمُوا أَنَّما أُنْزِلَ بِعِلْمِ اللَّهِ وَ أَنْ لا إِلهَ إِلاَّ هُوَ فَهَلْ أَنْتُمْ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1488, N'مَنْ كانَ يُرِيدُ الْحَياةَ الدُّنْيا وَ زِينَتَها نُوَفِّ إِلَيْهِمْ أَعْمالَهُمْ فِيها وَ هُمْ فِيها لا يُبْخَسُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1489, N'أُولئِكَ الَّذِينَ لَيْسَ لَهُمْ فِي الْآخِرَةِ إِلاَّ النَّارُ وَ حَبِطَ ما صَنَعُوا فِيها وَ باطِلٌ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1490, N'أَ فَمَنْ كانَ عَلى بَيِّنَةٍ مِنْ رَبِّهِ وَ يَتْلُوهُ شاهِدٌ مِنْهُ وَ مِنْ قَبْلِهِ كِتابُ مُوسى إِماماً وَ رَحْمَةً أُولئِكَ يُؤْمِنُونَ بِهِ وَ مَنْ يَكْفُرْ بِهِ مِنَ الْأَحْزابِ فَالنَّارُ مَوْعِدُهُ فَلا تَكُ فِي مِرْيَةٍ مِنْهُ إِنَّهُ الْحَقُّ مِنْ رَبِّكَ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1491, N'وَ مَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ كَذِباً أُولئِكَ يُعْرَضُونَ عَلى رَبِّهِمْ وَ يَقُولُ الْأَشْهادُ هؤُلاءِ الَّذِينَ كَذَبُوا عَلى رَبِّهِمْ أَلا لَعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1492, N'الَّذِينَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَ يَبْغُونَها عِوَجاً وَ هُمْ بِالْآخِرَةِ هُمْ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1493, N'أُولئِكَ لَمْ يَكُونُوا مُعْجِزِينَ فِي الْأَرْضِ وَ ما كانَ لَهُمْ مِنْ دُونِ اللَّهِ مِنْ أَوْلِياءَ يُضاعَفُ لَهُمُ الْعَذابُ ما كانُوا يَسْتَطِيعُونَ السَّمْعَ وَ ما كانُوا يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1494, N'أُولئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ وَ ضَلَّ عَنْهُمْ ما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1495, N'لا جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْأَخْسَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1496, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ وَ أَخْبَتُوا إِلى رَبِّهِمْ أُولئِكَ أَصْحابُ الْجَنَّةِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1497, N'مَثَلُ الْفَرِيقَيْنِ كَالْأَعْمى وَ الْأَصَمِّ وَ الْبَصِيرِ وَ السَّمِيعِ هَلْ يَسْتَوِيانِ مَثَلاً أَ فَلا تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1498, N'وَ لَقَدْ أَرْسَلْنا نُوحاً إِلى قَوْمِهِ إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1499, N'أَنْ لا تَعْبُدُوا إِلاَّ اللَّهَ إِنِّي أَخافُ عَلَيْكُمْ عَذابَ يَوْمٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1500, N'فَقالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ ما نَراكَ إِلاَّ بَشَراً مِثْلَنا وَ ما نَراكَ اتَّبَعَكَ إِلاَّ الَّذِينَ هُمْ أَراذِلُنا بادِيَ الرَّأْيِ وَ ما نَرى لَكُمْ عَلَيْنا مِنْ فَضْلٍ بَلْ نَظُنُّكُمْ كاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1501, N'قالَ يا قَوْمِ أَ رَأَيْتُمْ إِنْ كُنْتُ عَلى بَيِّنَةٍ مِنْ رَبِّي وَ آتانِي رَحْمَةً مِنْ عِنْدِهِ فَعُمِّيَتْ عَلَيْكُمْ أَ نُلْزِمُكُمُوها وَ أَنْتُمْ لَها كارِهُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1502, N'وَ يا قَوْمِ لا أَسْئَلُكُمْ عَلَيْهِ مالاً إِنْ أَجرِيَ إِلاَّ عَلَى اللَّهِ وَ ما أَنَا بِطارِدِ الَّذِينَ آمَنُوا إِنَّهُمْ مُلاقُوا رَبِّهِمْ وَ لكِنِّي أَراكُمْ قَوْماً تَجْهَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1503, N'وَ يا قَوْمِ مَنْ يَنْصُرُنِي مِنَ اللَّهِ إِنْ طَرَدْتُهُمْ أَ فَلا تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1504, N'وَ لا أَقُولُ لَكُمْ عِنْدِي خَزائِنُ اللَّهِ وَ لا أَعْلَمُ الْغَيْبَ وَ لا أَقُولُ إِنِّي مَلَكٌ وَ لا أَقُولُ لِلَّذِينَ تَزْدَرِي أَعْيُنُكُمْ لَنْ يُؤْتِيَهُمُ اللَّهُ خَيْراً اللَّهُ أَعْلَمُ بِما فِي أَنْفُسِهِمْ إِنِّي إِذاً لَمِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1505, N'قالُوا يا نُوحُ قَدْ جادَلْتَنا فَأَكْثَرْتَ جِدالَنا فَأْتِنا بِما تَعِدُنا إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1506, N'قالَ إِنَّما يَأْتِيكُمْ بِهِ اللَّهُ إِنْ شاءَ وَ ما أَنْتُمْ بِمُعْجِزِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1507, N'وَ لا يَنْفَعُكُمْ نُصْحِي إِنْ أَرَدْتُ أَنْ أَنْصَحَ لَكُمْ إِنْ كانَ اللَّهُ يُرِيدُ أَنْ يُغْوِيَكُمْ هُوَ رَبُّكُمْ وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1508, N'أَمْ يَقُولُونَ افْتَراهُ قُلْ إِنِ افْتَرَيْتُهُ فَعَلَيَّ إِجْرامِي وَ أَنَا بَرِي‏ءٌ مِمَّا تُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1509, N'وَ أُوحِيَ إِلى نُوحٍ أَنَّهُ لَنْ يُؤْمِنَ مِنْ قَوْمِكَ إِلاَّ مَنْ قَدْ آمَنَ فَلا تَبْتَئِسْ بِما كانُوا يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1510, N'وَ اصْنَعِ الْفُلْكَ بِأَعْيُنِنا وَ وَحْيِنا وَ لا تُخاطِبْنِي فِي الَّذِينَ ظَلَمُوا إِنَّهُمْ مُغْرَقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1511, N'وَ يَصْنَعُ الْفُلْكَ وَ كُلَّما مَرَّ عَلَيْهِ مَلَأٌ مِنْ قَوْمِهِ سَخِرُوا مِنْهُ قالَ إِنْ تَسْخَرُوا مِنَّا فَإِنَّا نَسْخَرُ مِنْكُمْ كَما تَسْخَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1512, N'فَسَوْفَ تَعْلَمُونَ مَنْ يَأْتِيهِ عَذابٌ يُخْزِيهِ وَ يَحِلُّ عَلَيْهِ عَذابٌ مُقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1513, N'حَتَّى إِذا جاءَ أَمْرُنا وَ فارَ التَّنُّورُ قُلْنَا احْمِلْ فِيها مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَ أَهْلَكَ إِلاَّ مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَ مَنْ آمَنَ وَ ما آمَنَ مَعَهُ إِلاَّ قَلِيلٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1514, N'وَ قالَ ارْكَبُوا فِيها بِسْمِ اللَّهِ مَجْراها وَ مُرْساها إِنَّ رَبِّي لَغَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1515, N'وَ هِيَ تَجْرِي بِهِمْ فِي مَوْجٍ كَالْجِبالِ وَ نادى نُوحٌ ابْنَهُ وَ كانَ فِي مَعْزِلٍ يا بُنَيَّ ارْكَبْ مَعَنا وَ لا تَكُنْ مَعَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1516, N'قالَ سَآوِي إِلى جَبَلٍ يَعْصِمُنِي مِنَ الْماءِ قالَ لا عاصِمَ الْيَوْمَ مِنْ أَمْرِ اللَّهِ إِلاَّ مَنْ رَحِمَ وَ حالَ بَيْنَهُمَا الْمَوْجُ فَكانَ مِنَ الْمُغْرَقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1517, N'وَ قِيلَ يا أَرْضُ ابْلَعِي ماءَكِ وَ يا سَماءُ أَقْلِعِي وَ غِيضَ الْماءُ وَ قُضِيَ الْأَمْرُ وَ اسْتَوَتْ عَلَى الْجُودِيِّ وَ قِيلَ بُعْداً لِلْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1518, N'وَ نادى نُوحٌ رَبَّهُ فَقالَ رَبِّ إِنَّ ابْنِي مِنْ أَهْلِي وَ إِنَّ وَعْدَكَ الْحَقُّ وَ أَنْتَ أَحْكَمُ الْحاكِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1519, N'قالَ يا نُوحُ إِنَّهُ لَيْسَ مِنْ أَهْلِكَ إِنَّهُ عَمَلٌ غَيْرُ صالِحٍ فَلا تَسْئَلْنِ ما لَيْسَ لَكَ بِهِ عِلْمٌ إِنِّي أَعِظُكَ أَنْ تَكُونَ مِنَ الْجاهِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1520, N'قالَ رَبِّ إِنِّي أَعُوذُ بِكَ أَنْ أَسْئَلَكَ ما لَيْسَ لِي بِهِ عِلْمٌ وَ إِلاَّ تَغْفِرْ لِي وَ تَرْحَمْنِي أَكُنْ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1521, N'قِيلَ يا نُوحُ اهْبِطْ بِسَلامٍ مِنَّا وَ بَرَكاتٍ عَلَيْكَ وَ عَلى أُمَمٍ مِمَّنْ مَعَكَ وَ أُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِنَّا عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1522, N'تِلْكَ مِنْ أَنْباءِ الْغَيْبِ نُوحِيها إِلَيْكَ ما كُنْتَ تَعْلَمُها أَنْتَ وَ لا قَوْمُكَ مِنْ قَبْلِ هذا فَاصْبِرْ إِنَّ الْعاقِبَةَ لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1523, N'وَ إِلى عادٍ أَخاهُمْ هُوداً قالَ يا قَوْمِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ إِنْ أَنْتُمْ إِلاَّ مُفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1524, N'يا قَوْمِ لا أَسْئَلُكُمْ عَلَيْهِ أَجْراً إِنْ أَجْرِيَ إِلاَّ عَلَى الَّذِي فَطَرَنِي أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1525, N'وَ يا قَوْمِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُرْسِلِ السَّماءَ عَلَيْكُمْ مِدْراراً وَ يَزِدْكُمْ قُوَّةً إِلى قُوَّتِكُمْ وَ لا تَتَوَلَّوْا مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1526, N'قالُوا يا هُودُ ما جِئْتَنا بِبَيِّنَةٍ وَ ما نَحْنُ بِتارِكِي آلِهَتِنا عَنْ قَوْلِكَ وَ ما نَحْنُ لَكَ بِمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1527, N'إِنْ نَقُولُ إِلاَّ اعْتَراكَ بَعْضُ آلِهَتِنا بِسُوءٍ قالَ إِنِّي أُشْهِدُ اللَّهَ وَ اشْهَدُوا أَنِّي بَرِي‏ءٌ مِمَّا تُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1528, N'مِنْ دُونِهِ فَكِيدُونِي جَمِيعاً ثُمَّ لا تُنْظِرُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1529, N'إِنِّي تَوَكَّلْتُ عَلَى اللَّهِ رَبِّي وَ رَبِّكُمْ ما مِنْ دَابَّةٍ إِلاَّ هُوَ آخِذٌ بِناصِيَتِها إِنَّ رَبِّي عَلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1530, N'فَإِنْ تَوَلَّوْا فَقَدْ أَبْلَغْتُكُمْ ما أُرْسِلْتُ بِهِ إِلَيْكُمْ وَ يَسْتَخْلِفُ رَبِّي قَوْماً غَيْرَكُمْ وَ لا تَضُرُّونَهُ شَيْئاً إِنَّ رَبِّي عَلى كُلِّ شَيْ‏ءٍ حَفِيظٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1531, N'وَ لَمَّا جاءَ أَمْرُنا نَجَّيْنا هُوداً وَ الَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَ نَجَّيْناهُمْ مِنْ عَذابٍ غَلِيظٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1532, N'وَ تِلْكَ عادٌ جَحَدُوا بِآياتِ رَبِّهِمْ وَ عَصَوْا رُسُلَهُ وَ اتَّبَعُوا أَمْرَ كُلِّ جَبَّارٍ عَنِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1533, N'وَ أُتْبِعُوا فِي هذِهِ الدُّنْيا لَعْنَةً وَ يَوْمَ الْقِيامَةِ أَلا إِنَّ عاداً كَفَرُوا رَبَّهُمْ أَلا بُعْداً لِعادٍ قَوْمِ هُودٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1534, N'وَ إِلى ثَمُودَ أَخاهُمْ صالِحاً قالَ يا قَوْمِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ هُوَ أَنْشَأَكُمْ مِنَ الْأَرْضِ وَ اسْتَعْمَرَكُمْ فِيها فَاسْتَغْفِرُوهُ ثُمَّ تُوبُوا إِلَيْهِ إِنَّ رَبِّي قَرِيبٌ مُجِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1535, N'قالُوا يا صالِحُ قَدْ كُنْتَ فِينا مَرْجُوًّا قَبْلَ هذا أَ تَنْهانا أَنْ نَعْبُدَ ما يَعْبُدُ آباؤُنا وَ إِنَّنا لَفِي شَكٍّ مِمَّا تَدْعُونا إِلَيْهِ مُرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1536, N'قالَ يا قَوْمِ أَ رَأَيْتُمْ إِنْ كُنْتُ عَلى بَيِّنَةٍ مِنْ رَبِّي وَ آتانِي مِنْهُ رَحْمَةً فَمَنْ يَنْصُرُنِي مِنَ اللَّهِ إِنْ عَصَيْتُهُ فَما تَزِيدُونَنِي غَيْرَ تَخْسِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1537, N'وَ يا قَوْمِ هذِهِ ناقَةُ اللَّهِ لَكُمْ آيَةً فَذَرُوها تَأْكُلْ فِي أَرْضِ اللَّهِ وَ لا تَمَسُّوها بِسُوءٍ فَيَأْخُذَكُمْ عَذابٌ قَرِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1538, N'فَعَقَرُوها فَقالَ تَمَتَّعُوا فِي دارِكُمْ ثَلاثَةَ أَيَّامٍ ذلِكَ وَعْدٌ غَيْرُ مَكْذُوبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1539, N'فَلَمَّا جاءَ أَمْرُنا نَجَّيْنا صالِحاً وَ الَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَ مِنْ خِزْيِ يَوْمِئِذٍ إِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيزُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1540, N'وَ أَخَذَ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيارِهِمْ جاثِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1541, N'كَأَنْ لَمْ يَغْنَوْا فِيها أَلا إِنَّ ثَمُودَ كَفَرُوا رَبَّهُمْ أَلا بُعْداً لِثَمُودَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1542, N'وَ لَقَدْ جاءَتْ رُسُلُنا إِبْراهِيمَ بِالْبُشْرى قالُوا سَلاماً قالَ سَلامٌ فَما لَبِثَ أَنْ جاءَ بِعِجْلٍ حَنِيذٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1543, N'فَلَمَّا رَأى أَيْدِيَهُمْ لا تَصِلُ إِلَيْهِ نَكِرَهُمْ وَ أَوْجَسَ مِنْهُمْ خِيفَةً قالُوا لا تَخَفْ إِنَّا أُرْسِلْنا إِلى قَوْمِ لُوطٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1544, N'وَ امْرَأَتُهُ قائِمَةٌ فَضَحِكَتْ فَبَشَّرْناها بِإِسْحاقَ وَ مِنْ وَراءِ إِسْحاقَ يَعْقُوبَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1545, N'قالَتْ يا وَيْلَتى أَ أَلِدُ وَ أَنَا عَجُوزٌ وَ هذا بَعْلِي شَيْخاً إِنَّ هذا لَشَيْ‏ءٌ عَجِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1546, N'قالُوا أَ تَعْجَبِينَ مِنْ أَمْرِ اللَّهِ رَحْمَتُ اللَّهِ وَ بَرَكاتُهُ عَلَيْكُمْ أَهْلَ الْبَيْتِ إِنَّهُ حَمِيدٌ مَجِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1547, N'فَلَمَّا ذَهَبَ عَنْ إِبْراهِيمَ الرَّوْعُ وَ جاءَتْهُ الْبُشْرى يُجادِلُنا فِي قَوْمِ لُوطٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1548, N'إِنَّ إِبْراهِيمَ لَحَلِيمٌ أَوَّاهٌ مُنِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1549, N'يا إِبْراهِيمُ أَعْرِضْ عَنْ هذا إِنَّهُ قَدْ جاءَ أَمْرُ رَبِّكَ وَ إِنَّهُمْ آتِيهِمْ عَذابٌ غَيْرُ مَرْدُودٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1550, N'وَ لَمَّا جاءَتْ رُسُلُنا لُوطاً سِي‏ءَ بِهِمْ وَ ضاقَ بِهِمْ ذَرْعاً وَ قالَ هذا يَوْمٌ عَصِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1551, N'وَ جاءَهُ قَوْمُهُ يُهْرَعُونَ إِلَيْهِ وَ مِنْ قَبْلُ كانُوا يَعْمَلُونَ السَّيِّئاتِ قالَ يا قَوْمِ هؤُلاءِ بَناتِي هُنَّ أَطْهَرُ لَكُمْ فَاتَّقُوا اللَّهَ وَ لا تُخْزُونِ فِي ضَيْفِي أَ لَيْسَ مِنْكُمْ رَجُلٌ رَشِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1552, N'قالُوا لَقَدْ عَلِمْتَ ما لَنا فِي بَناتِكَ مِنْ حَقٍّ وَ إِنَّكَ لَتَعْلَمُ ما نُرِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1553, N'قالَ لَوْ أَنَّ لِي بِكُمْ قُوَّةً أَوْ آوِي إِلى رُكْنٍ شَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1554, N'قالُوا يا لُوطُ إِنَّا رُسُلُ رَبِّكَ لَنْ يَصِلُوا إِلَيْكَ فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَ لا يَلْتَفِتْ مِنْكُمْ أَحَدٌ إِلاَّ امْرَأَتَكَ إِنَّهُ مُصِيبُها ما أَصابَهُمْ إِنَّ مَوْعِدَهُمُ الصُّبْحُ أَ لَيْسَ الصُّبْحُ بِقَرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1555, N'فَلَمَّا جاءَ أَمْرُنا جَعَلْنا عالِيَها سافِلَها وَ أَمْطَرْنا عَلَيْها حِجارَةً مِنْ سِجِّيلٍ مَنْضُودٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1556, N'مُسَوَّمَةً عِنْدَ رَبِّكَ وَ ما هِيَ مِنَ الظَّالِمِينَ بِبَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1557, N'وَ إِلى مَدْيَنَ أَخاهُمْ شُعَيْباً قالَ يا قَوْمِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ وَ لا تَنْقُصُوا الْمِكْيالَ وَ الْمِيزانَ إِنِّي أَراكُمْ بِخَيْرٍ وَ إِنِّي أَخافُ عَلَيْكُمْ عَذابَ يَوْمٍ مُحِيطٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1558, N'وَ يا قَوْمِ أَوْفُوا الْمِكْيالَ وَ الْمِيزانَ بِالْقِسْطِ وَ لا تَبْخَسُوا النَّاسَ أَشْياءَهُمْ وَ لا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1559, N'بَقِيَّتُ اللَّهِ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ وَ ما أَنَا عَلَيْكُمْ بِحَفِيظٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1560, N'قالُوا يا شُعَيْبُ أَ صَلاتُكَ تَأْمُرُكَ أَنْ نَتْرُكَ ما يَعْبُدُ آباؤُنا أَوْ أَنْ نَفْعَلَ فِي أَمْوالِنا ما نَشؤُا إِنَّكَ لَأَنْتَ الْحَلِيمُ الرَّشِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1561, N'قالَ يا قَوْمِ أَ رَأَيْتُمْ إِنْ كُنْتُ عَلى بَيِّنَةٍ مِنْ رَبِّي وَ رَزَقَنِي مِنْهُ رِزْقاً حَسَناً وَ ما أُرِيدُ أَنْ أُخالِفَكُمْ إِلى ما أَنْهاكُمْ عَنْهُ إِنْ أُرِيدُ إِلاَّ الْإِصْلاحَ مَا اسْتَطَعْتُ وَ ما تَوْفِيقِي إِلاَّ بِاللَّهِ عَلَيْهِ تَوَكَّلْتُ وَ إِلَيْهِ أُنِيبُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1562, N'وَ يا قَوْمِ لا يَجْرِمَنَّكُمْ شِقاقِي أَنْ يُصِيبَكُمْ مِثْلُ ما أَصابَ قَوْمَ نُوحٍ أَوْ قَوْمَ هُودٍ أَوْ قَوْمَ صالِحٍ وَ ما قَوْمُ لُوطٍ مِنْكُمْ بِبَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1563, N'وَ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ إِنَّ رَبِّي رَحِيمٌ وَدُودٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1564, N'قالُوا يا شُعَيْبُ ما نَفْقَهُ كَثِيراً مِمَّا تَقُولُ وَ إِنَّا لَنَراكَ فِينا ضَعِيفاً وَ لَوْ لا رَهْطُكَ لَرَجَمْناكَ وَ ما أَنْتَ عَلَيْنا بِعَزِيزٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1565, N'قالَ يا قَوْمِ أَ رَهْطِي أَعَزُّ عَلَيْكُمْ مِنَ اللَّهِ وَ اتَّخَذْتُمُوهُ وَراءَكُمْ ظِهْرِيًّا إِنَّ رَبِّي بِما تَعْمَلُونَ مُحِيطٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1566, N'وَ يا قَوْمِ اعْمَلُوا عَلى مَكانَتِكُمْ إِنِّي عامِلٌ سَوْفَ تَعْلَمُونَ مَنْ يَأْتِيهِ عَذابٌ يُخْزِيهِ وَ مَنْ هُوَ كاذِبٌ وَ ارْتَقِبُوا إِنِّي مَعَكُمْ رَقِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1567, N'وَ لَمَّا جاءَ أَمْرُنا نَجَّيْنا شُعَيْباً وَ الَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنَّا وَ أَخَذَتِ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيارِهِمْ جاثِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1568, N'كَأَنْ لَمْ يَغْنَوْا فِيها أَلا بُعْداً لِمَدْيَنَ كَما بَعِدَتْ ثَمُودُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1569, N'وَ لَقَدْ أَرْسَلْنا مُوسى بِآياتِنا وَ سُلْطانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1570, N'إِلى فِرْعَوْنَ وَ مَلاَئِهِ فَاتَّبَعُوا أَمْرَ فِرْعَوْنَ وَ ما أَمْرُ فِرْعَوْنَ بِرَشِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1571, N'يَقْدُمُ قَوْمَهُ يَوْمَ الْقِيامَةِ فَأَوْرَدَهُمُ النَّارَ وَ بِئْسَ الْوِرْدُ الْمَوْرُودُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1572, N'وَ أُتْبِعُوا فِي هذِهِ لَعْنَةً وَ يَوْمَ الْقِيامَةِ بِئْسَ الرِّفْدُ الْمَرْفُودُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1573, N'ذلِكَ مِنْ أَنْباءِ الْقُرى نَقُصُّهُ عَلَيْكَ مِنْها قائِمٌ وَ حَصِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1574, N'وَ ما ظَلَمْناهُمْ وَ لكِنْ ظَلَمُوا أَنْفُسَهُمْ فَما أَغْنَتْ عَنْهُمْ آلِهَتُهُمُ الَّتِي يَدْعُونَ مِنْ دُونِ اللَّهِ مِنْ شَيْ‏ءٍ لَمَّا جاءَ أَمْرُ رَبِّكَ وَ ما زادُوهُمْ غَيْرَ تَتْبِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1575, N'وَ كَذلِكَ أَخْذُ رَبِّكَ إِذا أَخَذَ الْقُرى وَ هِيَ ظالِمَةٌ إِنَّ أَخْذَهُ أَلِيمٌ شَدِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1576, N'إِنَّ فِي ذلِكَ لَآيَةً لِمَنْ خافَ عَذابَ الْآخِرَةِ ذلِكَ يَوْمٌ مَجْمُوعٌ لَهُ النَّاسُ وَ ذلِكَ يَوْمٌ مَشْهُودٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1577, N'وَ ما نُؤَخِّرُهُ إِلاَّ لِأَجَلٍ مَعْدُودٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1578, N'يَوْمَ يَأْتِ لا تَكَلَّمُ نَفْسٌ إِلاَّ بِإِذْنِهِ فَمِنْهُمْ شَقِيٌّ وَ سَعِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1579, N'فَأَمَّا الَّذِينَ شَقُوا فَفِي النَّارِ لَهُمْ فِيها زَفِيرٌ وَ شَهِيقٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1580, N'خالِدِينَ فِيها ما دامَتِ السَّماواتُ وَ الْأَرْضُ إِلاَّ ما شاءَ رَبُّكَ إِنَّ رَبَّكَ فَعَّالٌ لِما يُرِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1581, N'وَ أَمَّا الَّذِينَ سُعِدُوا فَفِي الْجَنَّةِ خالِدِينَ فِيها ما دامَتِ السَّماواتُ وَ الْأَرْضُ إِلاَّ ما شاءَ رَبُّكَ عَطاءً غَيْرَ مَجْذُوذٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1582, N'فَلا تَكُ فِي مِرْيَةٍ مِمَّا يَعْبُدُ هؤُلاءِ ما يَعْبُدُونَ إِلاَّ كَما يَعْبُدُ آباؤُهُمْ مِنْ قَبْلُ وَ إِنَّا لَمُوَفُّوهُمْ نَصِيبَهُمْ غَيْرَ مَنْقُوصٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1583, N'وَ لَقَدْ آتَيْنا مُوسَى الْكِتابَ فَاخْتُلِفَ فِيهِ وَ لَوْ لا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ وَ إِنَّهُمْ لَفِي شَكٍّ مِنْهُ مُرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1584, N'وَ إِنَّ كُلاًّ لَمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ أَعْمالَهُمْ إِنَّهُ بِما يَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1585, N'فَاسْتَقِمْ كَما أُمِرْتَ وَ مَنْ تابَ مَعَكَ وَ لا تَطْغَوْا إِنَّهُ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1586, N'وَ لا تَرْكَنُوا إِلَى الَّذِينَ ظَلَمُوا فَتَمَسَّكُمُ النَّارُ وَ ما لَكُمْ مِنْ دُونِ اللَّهِ مِنْ أَوْلِياءَ ثُمَّ لا تُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1587, N'وَ أَقِمِ الصَّلاةَ طَرَفَيِ النَّهارِ وَ زُلَفاً مِنَ اللَّيْلِ إِنَّ الْحَسَناتِ يُذْهِبْنَ السَّيِّئاتِ ذلِكَ ذِكْرى لِلذَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1588, N'وَ اصْبِرْ فَإِنَّ اللَّهَ لا يُضِيعُ أَجْرَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1589, N'فَلَوْ لا كانَ مِنَ الْقُرُونِ مِنْ قَبْلِكُمْ أُولُوا بَقِيَّةٍ يَنْهَوْنَ عَنِ الْفَسادِ فِي الْأَرْضِ إِلاَّ قَلِيلاً مِمَّنْ أَنْجَيْنا مِنْهُمْ وَ اتَّبَعَ الَّذِينَ ظَلَمُوا ما أُتْرِفُوا فِيهِ وَ كانُوا مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1590, N'وَ ما كانَ رَبُّكَ لِيُهْلِكَ الْقُرى بِظُلْمٍ وَ أَهْلُها مُصْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1591, N'وَ لَوْ شاءَ رَبُّكَ لَجَعَلَ النَّاسَ أُمَّةً واحِدَةً وَ لا يَزالُونَ مُخْتَلِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1592, N'إِلاَّ مَنْ رَحِمَ رَبُّكَ وَ لِذلِكَ خَلَقَهُمْ وَ تَمَّتْ كَلِمَةُ رَبِّكَ لَأَمْلَأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَ النَّاسِ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1593, N'وَ كُلاًّ نَقُصُّ عَلَيْكَ مِنْ أَنْباءِ الرُّسُلِ ما نُثَبِّتُ بِهِ فُؤادَكَ وَ جاءَكَ فِي هذِهِ الْحَقُّ وَ مَوْعِظَةٌ وَ ذِكْرى لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1594, N'وَ قُلْ لِلَّذِينَ لا يُؤْمِنُونَ اعْمَلُوا عَلى مَكانَتِكُمْ إِنَّا عامِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1595, N'وَ انْتَظِرُوا إِنَّا مُنْتَظِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1596, N'وَ لِلَّهِ غَيْبُ السَّماواتِ وَ الْأَرْضِ وَ إِلَيْهِ يُرْجَعُ الْأَمْرُ كُلُّهُ فَاعْبُدْهُ وَ تَوَكَّلْ عَلَيْهِ وَ ما رَبُّكَ بِغافِلٍ عَمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1597, N'الر تِلْكَ آياتُ الْكِتابِ الْمُبِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1598, N'إِنَّا أَنْزَلْناهُ قُرْآناً عَرَبِيًّا لَعَلَّكُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1599, N'نَحْنُ نَقُصُّ عَلَيْكَ أَحْسَنَ الْقَصَصِ بِما أَوْحَيْنا إِلَيْكَ هذَا الْقُرْآنَ وَ إِنْ كُنْتَ مِنْ قَبْلِهِ لَمِنَ الْغافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1600, N'إِذْ قالَ يُوسُفُ لِأَبِيهِ يا أَبَتِ إِنِّي رَأَيْتُ أَحَدَ عَشَرَ كَوْكَباً وَ الشَّمْسَ وَ الْقَمَرَ رَأَيْتُهُمْ لِي ساجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1601, N'قالَ يا بُنَيَّ لا تَقْصُصْ رُؤْياكَ عَلى إِخْوَتِكَ فَيَكِيدُوا لَكَ كَيْداً إِنَّ الشَّيْطانَ لِلْإِنْسانِ عَدُوٌّ مُبِينٌ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1602, N'وَ كَذلِكَ يَجْتَبِيكَ رَبُّكَ وَ يُعَلِّمُكَ مِنْ تَأْوِيلِ الْأَحادِيثِ وَ يُتِمُّ نِعْمَتَهُ عَلَيْكَ وَ عَلى آلِ يَعْقُوبَ كَما أَتَمَّها عَلى أَبَوَيْكَ مِنْ قَبْلُ إِبْراهِيمَ وَ إِسْحاقَ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1603, N'لَقَدْ كانَ فِي يُوسُفَ وَ إِخْوَتِهِ آياتٌ لِلسَّائِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1604, N'إِذْ قالُوا لَيُوسُفُ وَ أَخُوهُ أَحَبُّ إِلى أَبِينا مِنَّا وَ نَحْنُ عُصْبَةٌ إِنَّ أَبانا لَفِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1605, N'اقْتُلُوا يُوسُفَ أَوِ اطْرَحُوهُ أَرْضاً يَخْلُ لَكُمْ وَجْهُ أَبِيكُمْ وَ تَكُونُوا مِنْ بَعْدِهِ قَوْماً صالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1606, N'قالَ قائِلٌ مِنْهُمْ لا تَقْتُلُوا يُوسُفَ وَ أَلْقُوهُ فِي غَيابَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ إِنْ كُنْتُمْ فاعِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1607, N'قالُوا يا أَبانا ما لَكَ لا تَأْمَنَّا عَلى يُوسُفَ وَ إِنَّا لَهُ لَناصِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1608, N'أَرْسِلْهُ مَعَنا غَداً يَرْتَعْ وَ يَلْعَبْ وَ إِنَّا لَهُ لَحافِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1609, N'قالَ إِنِّي لَيَحْزُنُنِي أَنْ تَذْهَبُوا بِهِ وَ أَخافُ أَنْ يَأْكُلَهُ الذِّئْبُ وَ أَنْتُمْ عَنْهُ غافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1610, N'قالُوا لَئِنْ أَكَلَهُ الذِّئْبُ وَ نَحْنُ عُصْبَةٌ إِنَّا إِذاً لَخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1611, N'فَلَمَّا ذَهَبُوا بِهِ وَ أَجْمَعُوا أَنْ يَجْعَلُوهُ فِي غَيابَتِ الْجُبِّ وَ أَوْحَيْنا إِلَيْهِ لَتُنَبِّئَنَّهُمْ بِأَمْرِهِمْ هذا وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1612, N'وَ جاؤُ أَباهُمْ عِشاءً يَبْكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1613, N'قالُوا يا أَبانا إِنَّا ذَهَبْنا نَسْتَبِقُ وَ تَرَكْنا يُوسُفَ عِنْدَ مَتاعِنا فَأَكَلَهُ الذِّئْبُ وَ ما أَنْتَ بِمُؤْمِنٍ لَنا وَ لَوْ كُنَّا صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1614, N'وَ جاؤُ عَلى قَمِيصِهِ بِدَمٍ كَذِبٍ قالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْراً فَصَبْرٌ جَمِيلٌ وَ اللَّهُ الْمُسْتَعانُ عَلى ما تَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1615, N'وَ جاءَتْ سَيَّارَةٌ فَأَرْسَلُوا وارِدَهُمْ فَأَدْلى دَلْوَهُ قالَ يا بُشْرى هذا غُلامٌ وَ أَسَرُّوهُ بِضاعَةً وَ اللَّهُ عَلِيمٌ بِما يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1616, N'وَ شَرَوْهُ بِثَمَنٍ بَخْسٍ دَراهِمَ مَعْدُودَةٍ وَ كانُوا فِيهِ مِنَ الزَّاهِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1617, N'وَ قالَ الَّذِي اشْتَراهُ مِنْ مِصْرَ لاِمْرَأَتِهِ أَكْرِمِي مَثْواهُ عَسى أَنْ يَنْفَعَنا أَوْ نَتَّخِذَهُ وَلَداً وَ كَذلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ وَ لِنُعَلِّمَهُ مِنْ تَأْوِيلِ الْأَحادِيثِ وَ اللَّهُ غالِبٌ عَلى أَمْرِهِ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1618, N'وَ لَمَّا بَلَغَ أَشُدَّهُ آتَيْناهُ حُكْماً وَ عِلْماً وَ كَذلِكَ نَجْزِي الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1619, N'وَ راوَدَتْهُ الَّتِي هُوَ فِي بَيْتِها عَنْ نَفْسِهِ وَ غَلَّقَتِ الْأَبْوابَ وَ قالَتْ هَيْتَ لَكَ قالَ مَعاذَ اللَّهِ إِنَّهُ رَبِّي أَحْسَنَ مَثْوايَ إِنَّهُ لا يُفْلِحُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1620, N'وَ لَقَدْ هَمَّتْ بِهِ وَ هَمَّ بِها لَوْ لا أَنْ رَأى بُرْهانَ رَبِّهِ كَذلِكَ لِنَصْرِفَ عَنْهُ السُّوءَ وَ الْفَحْشاءَ إِنَّهُ مِنْ عِبادِنَا الْمُخْلَصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1621, N'وَ اسْتَبَقَا الْبابَ وَ قَدَّتْ قَمِيصَهُ مِنْ دُبُرٍ وَ أَلْفَيا سَيِّدَها لَدَى الْبابِ قالَتْ ما جَزاءُ مَنْ أَرادَ بِأَهْلِكَ سُوءاً إِلاَّ أَنْ يُسْجَنَ أَوْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1622, N'قالَ هِيَ راوَدَتْنِي عَنْ نَفْسِي وَ شَهِدَ شاهِدٌ مِنْ أَهْلِها إِنْ كانَ قَمِيصُهُ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَ هُوَ مِنَ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1623, N'وَ إِنْ كانَ قَمِيصُهُ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَ هُوَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1624, N'فَلَمَّا رَأى قَمِيصَهُ قُدَّ مِنْ دُبُرٍ قالَ إِنَّهُ مِنْ كَيْدِكُنَّ إِنَّ كَيْدَكُنَّ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1625, N'يُوسُفُ أَعْرِضْ عَنْ هذا وَ اسْتَغْفِرِي لِذَنْبِكِ إِنَّكِ كُنْتِ مِنَ الْخاطِئِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1626, N'وَ قالَ نِسْوَةٌ فِي الْمَدِينَةِ امْرَأَتُ الْعَزِيزِ تُراوِدُ فَتاها عَنْ نَفْسِهِ قَدْ شَغَفَها حُبًّا إِنَّا لَنَراها فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1627, N'فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ أَرْسَلَتْ إِلَيْهِنَّ وَ أَعْتَدَتْ لَهُنَّ مُتَّكَأً وَ آتَتْ كُلَّ واحِدَةٍ مِنْهُنَّ سِكِّيناً وَ قالَتِ اخْرُجْ عَلَيْهِنَّ فَلَمَّا رَأَيْنَهُ أَكْبَرْنَهُ وَ قَطَّعْنَ أَيْدِيَهُنَّ وَ قُلْنَ حاشَ لِلَّهِ ما هذا بَشَراً إِنْ هذا إِلاَّ مَلَكٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1628, N'قالَتْ فَذلِكُنَّ الَّذِي لُمْتُنَّنِي فِيهِ وَ لَقَدْ راوَدْتُهُ عَنْ نَفْسِهِ فَاسْتَعْصَمَ وَ لَئِنْ لَمْ يَفْعَلْ ما آمُرُهُ لَيُسْجَنَنَّ وَ لَيَكُوناً مِنَ الصَّاغِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1629, N'قالَ رَبِّ السِّجْنُ أَحَبُّ إِلَيَّ مِمَّا يَدْعُونَنِي إِلَيْهِ وَ إِلاَّ تَصْرِفْ عَنِّي كَيْدَهُنَّ أَصْبُ إِلَيْهِنَّ وَ أَكُنْ مِنَ الْجاهِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1630, N'فَاسْتَجابَ لَهُ رَبُّهُ فَصَرَفَ عَنْهُ كَيْدَهُنَّ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1631, N'ثُمَّ بَدا لَهُمْ مِنْ بَعْدِ ما رَأَوُا الْآياتِ لَيَسْجُنُنَّهُ حَتَّى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1632, N'وَ دَخَلَ مَعَهُ السِّجْنَ فَتَيانِ قالَ أَحَدُهُما إِنِّي أَرانِي أَعْصِرُ خَمْراً وَ قالَ الْآخَرُ إِنِّي أَرانِي أَحْمِلُ فَوْقَ رَأْسِي خُبْزاً تَأْكُلُ الطَّيْرُ مِنْهُ نَبِّئْنا بِتَأْوِيلِهِ إِنَّا نَراكَ مِنَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1633, N'قالَ لا يَأْتِيكُما طَعامٌ تُرْزَقانِهِ إِلاَّ نَبَّأْتُكُما بِتَأْوِيلِهِ قَبْلَ أَنْ يَأْتِيَكُما ذلِكُما مِمَّا عَلَّمَنِي رَبِّي إِنِّي تَرَكْتُ مِلَّةَ قَوْمٍ لا يُؤْمِنُونَ بِاللَّهِ وَ هُمْ بِالْآخِرَةِ هُمْ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1634, N'وَ اتَّبَعْتُ مِلَّةَ آبائِي إِبْراهِيمَ وَ إِسْحاقَ وَ يَعْقُوبَ ما كانَ لَنا أَنْ نُشْرِكَ بِاللَّهِ مِنْ شَيْ‏ءٍ ذلِكَ مِنْ فَضْلِ اللَّهِ عَلَيْنا وَ عَلَى النَّاسِ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1635, N'يا صاحِبَيِ السِّجْنِ أَ أَرْبابٌ مُتَفَرِّقُونَ خَيْرٌ أَمِ اللَّهُ الْواحِدُ الْقَهَّارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1636, N'ما تَعْبُدُونَ مِنْ دُونِهِ إِلاَّ أَسْماءً سَمَّيْتُمُوها أَنْتُمْ وَ آباؤُكُمْ ما أَنْزَلَ اللَّهُ بِها مِنْ سُلْطانٍ إِنِ الْحُكْمُ إِلاَّ لِلَّهِ أَمَرَ أَلاَّ تَعْبُدُوا إِلاَّ إِيَّاهُ ذلِكَ الدِّينُ الْقَيِّمُ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1637, N'يا صاحِبَيِ السِّجْنِ أَمَّا أَحَدُكُما فَيَسْقِي رَبَّهُ خَمْراً وَ أَمَّا الْآخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَأْسِهِ قُضِيَ الْأَمْرُ الَّذِي فِيهِ تَسْتَفْتِيانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1638, N'وَ قالَ لِلَّذِي ظَنَّ أَنَّهُ ناجٍ مِنْهُمَا اذْكُرْنِي عِنْدَ رَبِّكَ فَأَنْساهُ الشَّيْطانُ ذِكْرَ رَبِّهِ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1639, N'وَ قالَ الْمَلِكُ إِنِّي أَرى سَبْعَ بَقَراتٍ سِمانٍ يَأْكُلُهُنَّ سَبْعٌ عِجافٌ وَ سَبْعَ سُنْبُلاتٍ خُضْرٍ وَ أُخَرَ يابِساتٍ يا أَيُّهَا الْمَلَأُ أَفْتُونِي فِي رُءْيايَ إِنْ كُنْتُمْ لِلرُّءْيا تَعْبُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1640, N'قالُوا أَضْغاثُ أَحْلامٍ وَ ما نَحْنُ بِتَأْوِيلِ الْأَحْلامِ بِعالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1641, N'وَ قالَ الَّذِي نَجا مِنْهُما وَ ادَّكَرَ بَعْدَ أُمَّةٍ أَنَا أُنَبِّئُكُمْ بِتَأْوِيلِهِ فَأَرْسِلُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1642, N'يُوسُفُ أَيُّهَا الصِّدِّيقُ أَفْتِنا فِي سَبْعِ بَقَراتٍ سِمانٍ يَأْكُلُهُنَّ سَبْعٌ عِجافٌ وَ سَبْعِ سُنْبُلاتٍ خُضْرٍ وَ أُخَرَ يابِساتٍ لَعَلِّي أَرْجِعُ إِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1643, N'قالَ تَزْرَعُونَ سَبْعَ سِنِينَ دَأَباً فَما حَصَدْتُمْ فَذَرُوهُ فِي سُنْبُلِهِ إِلاَّ قَلِيلاً مِمَّا تَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1644, N'ثُمَّ يَأْتِي مِنْ بَعْدِ ذلِكَ سَبْعٌ شِدادٌ يَأْكُلْنَ ما قَدَّمْتُمْ لَهُنَّ إِلاَّ قَلِيلاً مِمَّا تُحْصِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1645, N'ثُمَّ يَأْتِي مِنْ بَعْدِ ذلِكَ عامٌ فِيهِ يُغاثُ النَّاسُ وَ فِيهِ يَعْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1646, N'وَ قالَ الْمَلِكُ ائْتُونِي بِهِ فَلَمَّا جاءَهُ الرَّسُولُ قالَ ارْجِعْ إِلى رَبِّكَ فَسْئَلْهُ ما بالُ النِّسْوَةِ اللاَّتِي قَطَّعْنَ أَيْدِيَهُنَّ إِنَّ رَبِّي بِكَيْدِهِنَّ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1647, N'قالَ ما خَطْبُكُنَّ إِذْ راوَدْتُنَّ يُوسُفَ عَنْ نَفْسِهِ قُلْنَ حاشَ لِلَّهِ ما عَلِمْنا عَلَيْهِ مِنْ سُوءٍ قالَتِ امْرَأَةُ الْعَزِيزِ الْآنَ حَصْحَصَ الْحَقُّ أَنَا راوَدْتُهُ عَنْ نَفْسِهِ وَ إِنَّهُ لَمِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1648, N'ذلِكَ لِيَعْلَمَ أَنِّي لَمْ أَخُنْهُ بِالْغَيْبِ وَ أَنَّ اللَّهَ لا يَهْدِي كَيْدَ الْخائِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1649, N'وَ ما أُبَرِّئُ نَفْسِي إِنَّ النَّفْسَ لَأَمَّارَةٌ بِالسُّوءِ إِلاَّ ما رَحِمَ رَبِّي إِنَّ رَبِّي غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1650, N'وَ قالَ الْمَلِكُ ائْتُونِي بِهِ أَسْتَخْلِصْهُ لِنَفْسِي فَلَمَّا كَلَّمَهُ قالَ إِنَّكَ الْيَوْمَ لَدَيْنا مَكِينٌ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1651, N'قالَ اجْعَلْنِي عَلى خَزائِنِ الْأَرْضِ إِنِّي حَفِيظٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1652, N'وَ كَذلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ يَتَبَوَّأُ مِنْها حَيْثُ يَشاءُ نُصِيبُ بِرَحْمَتِنا مَنْ نَشاءُ وَ لا نُضِيعُ أَجْرَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1653, N'وَ لَأَجْرُ الْآخِرَةِ خَيْرٌ لِلَّذِينَ آمَنُوا وَ كانُوا يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1654, N'وَ جاءَ إِخْوَةُ يُوسُفَ فَدَخَلُوا عَلَيْهِ فَعَرَفَهُمْ وَ هُمْ لَهُ مُنْكِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1655, N'وَ لَمَّا جَهَّزَهُمْ بِجَهازِهِمْ قالَ ائْتُونِي بِأَخٍ لَكُمْ مِنْ أَبِيكُمْ أَ لا تَرَوْنَ أَنِّي أُوفِي الْكَيْلَ وَ أَنَا خَيْرُ الْمُنْزِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1656, N'فَإِنْ لَمْ تَأْتُونِي بِهِ فَلا كَيْلَ لَكُمْ عِنْدِي وَ لا تَقْرَبُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1657, N'قالُوا سَنُراوِدُ عَنْهُ أَباهُ وَ إِنَّا لَفاعِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1658, N'وَ قالَ لِفِتْيانِهِ اجْعَلُوا بِضاعَتَهُمْ فِي رِحالِهِمْ لَعَلَّهُمْ يَعْرِفُونَها إِذَا انْقَلَبُوا إِلى أَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1659, N'فَلَمَّا رَجَعُوا إِلى أَبِيهِمْ قالُوا يا أَبانا مُنِعَ مِنَّا الْكَيْلُ فَأَرْسِلْ مَعَنا أَخانا نَكْتَلْ وَ إِنَّا لَهُ لَحافِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1660, N'قالَ هَلْ آمَنُكُمْ عَلَيْهِ إِلاَّ كَما أَمِنْتُكُمْ عَلى أَخِيهِ مِنْ قَبْلُ فَاللَّهُ خَيْرٌ حافِظاً وَ هُوَ أَرْحَمُ الرَّاحِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1661, N'وَ لَمَّا فَتَحُوا مَتاعَهُمْ وَجَدُوا بِضاعَتَهُمْ رُدَّتْ إِلَيْهِمْ قالُوا يا أَبانا ما نَبْغِي هذِهِ بِضاعَتُنا رُدَّتْ إِلَيْنا وَ نَمِيرُ أَهْلَنا وَ نَحْفَظُ أَخانا وَ نَزْدادُ كَيْلَ بَعِيرٍ ذلِكَ كَيْلٌ يَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1662, N'قالَ لَنْ أُرْسِلَهُ مَعَكُمْ حَتَّى تُؤْتُونِ مَوْثِقاً مِنَ اللَّهِ لَتَأْتُنَّنِي بِهِ إِلاَّ أَنْ يُحاطَ بِكُمْ فَلَمَّا آتَوْهُ مَوْثِقَهُمْ قالَ اللَّهُ عَلى ما نَقُولُ وَكِيلٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1663, N'وَ قالَ يا بَنِيَّ لا تَدْخُلُوا مِنْ بابٍ واحِدٍ وَ ادْخُلُوا مِنْ أَبْوابٍ مُتَفَرِّقَةٍ وَ ما أُغْنِي عَنْكُمْ مِنَ اللَّهِ مِنْ شَيْ‏ءٍ إِنِ الْحُكْمُ إِلاَّ لِلَّهِ عَلَيْهِ تَوَكَّلْتُ وَ عَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1664, N'وَ لَمَّا دَخَلُوا مِنْ حَيْثُ أَمَرَهُمْ أَبُوهُمْ ما كانَ يُغْنِي عَنْهُمْ مِنَ اللَّهِ مِنْ شَيْ‏ءٍ إِلاَّ حاجَةً فِي نَفْسِ يَعْقُوبَ قَضاها وَ إِنَّهُ لَذُو عِلْمٍ لِما عَلَّمْناهُ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1665, N'وَ لَمَّا دَخَلُوا عَلى يُوسُفَ آوى إِلَيْهِ أَخاهُ قالَ إِنِّي أَنَا أَخُوكَ فَلا تَبْتَئِسْ بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1666, N'فَلَمَّا جَهَّزَهُمْ بِجَهازِهِمْ جَعَلَ السِّقايَةَ فِي رَحْلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا الْعِيرُ إِنَّكُمْ لَسارِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1667, N'قالُوا وَ أَقْبَلُوا عَلَيْهِمْ ما ذا تَفْقِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1668, N'قالُوا نَفْقِدُ صُواعَ الْمَلِكِ وَ لِمَنْ جاءَ بِهِ حِمْلُ بَعِيرٍ وَ أَنَا بِهِ زَعِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1669, N'قالُوا تَاللَّهِ لَقَدْ عَلِمْتُمْ ما جِئْنا لِنُفْسِدَ فِي الْأَرْضِ وَ ما كُنَّا سارِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1670, N'قالُوا فَما جَزاؤُهُ إِنْ كُنْتُمْ كاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1671, N'قالُوا جَزاؤُهُ مَنْ وُجِدَ فِي رَحْلِهِ فَهُوَ جَزاؤُهُ كَذلِكَ نَجْزِي الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1672, N'فَبَدَأَ بِأَوْعِيَتِهِمْ قَبْلَ وِعاءِ أَخِيهِ ثُمَّ اسْتَخْرَجَها مِنْ وِعاءِ أَخِيهِ كَذلِكَ كِدْنا لِيُوسُفَ ما كانَ لِيَأْخُذَ أَخاهُ فِي دِينِ الْمَلِكِ إِلاَّ أَنْ يَشاءَ اللَّهُ نَرْفَعُ دَرَجاتٍ مَنْ نَشاءُ وَ فَوْقَ كُلِّ ذِي عِلْمٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1673, N'قالُوا إِنْ يَسْرِقْ فَقَدْ سَرَقَ أَخٌ لَهُ مِنْ قَبْلُ فَأَسَرَّها يُوسُفُ فِي نَفْسِهِ وَ لَمْ يُبْدِها لَهُمْ قالَ أَنْتُمْ شَرٌّ مَكاناً وَ اللَّهُ أَعْلَمُ بِما تَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1674, N'قالُوا يا أَيُّهَا الْعَزِيزُ إِنَّ لَهُ أَباً شَيْخاً كَبِيراً فَخُذْ أَحَدَنا مَكانَهُ إِنَّا نَراكَ مِنَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1675, N'قالَ مَعاذَ اللَّهِ أَنْ نَأْخُذَ إِلاَّ مَنْ وَجَدْنا مَتاعَنا عِنْدَهُ إِنَّا إِذاً لَظالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1676, N'فَلَمَّا اسْتَيْأَسُوا مِنْهُ خَلَصُوا نَجِيًّا قالَ كَبِيرُهُمْ أَ لَمْ تَعْلَمُوا أَنَّ أَباكُمْ قَدْ أَخَذَ عَلَيْكُمْ مَوْثِقاً مِنَ اللَّهِ وَ مِنْ قَبْلُ ما فَرَّطْتُمْ فِي يُوسُفَ فَلَنْ أَبْرَحَ الْأَرْضَ حَتَّى يَأْذَنَ لِي أَبِي أَوْ يَحْكُمَ اللَّهُ لِي وَ هُوَ خَيْرُ الْحاكِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1677, N'ارْجِعُوا إِلى أَبِيكُمْ فَقُولُوا يا أَبانا إِنَّ ابْنَكَ سَرَقَ وَ ما شَهِدْنا إِلاَّ بِما عَلِمْنا وَ ما كُنَّا لِلْغَيْبِ حافِظِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1678, N'وَ سْئَلِ الْقَرْيَةَ الَّتِي كُنَّا فِيها وَ الْعِيرَ الَّتِي أَقْبَلْنا فِيها وَ إِنَّا لَصادِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1679, N'قالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْراً فَصَبْرٌ جَمِيلٌ عَسَى اللَّهُ أَنْ يَأْتِيَنِي بِهِمْ جَمِيعاً إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1680, N'وَ تَوَلَّى عَنْهُمْ وَ قالَ يا أَسَفى عَلى يُوسُفَ وَ ابْيَضَّتْ عَيْناهُ مِنَ الْحُزْنِ فَهُوَ كَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1681, N'قالُوا تَاللَّهِ تَفْتَؤُا تَذْكُرُ يُوسُفَ حَتَّى تَكُونَ حَرَضاً أَوْ تَكُونَ مِنَ الْهالِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1682, N'قالَ إِنَّما أَشْكُوا بَثِّي وَ حُزْنِي إِلَى اللَّهِ وَ أَعْلَمُ مِنَ اللَّهِ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1683, N'يا بَنِيَّ اذْهَبُوا فَتَحَسَّسُوا مِنْ يُوسُفَ وَ أَخِيهِ وَ لا تَيْأَسُوا مِنْ رَوْحِ اللَّهِ إِنَّهُ لا يَيْأَسُ مِنْ رَوْحِ اللَّهِ إِلاَّ الْقَوْمُ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1684, N'فَلَمَّا دَخَلُوا عَلَيْهِ قالُوا يا أَيُّهَا الْعَزِيزُ مَسَّنا وَ أَهْلَنَا الضُّرُّ وَ جِئْنا بِبِضاعَةٍ مُزْجاةٍ فَأَوْفِ لَنَا الْكَيْلَ وَ تَصَدَّقْ عَلَيْنا إِنَّ اللَّهَ يَجْزِي الْمُتَصَدِّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1685, N'قالَ هَلْ عَلِمْتُمْ ما فَعَلْتُمْ بِيُوسُفَ وَ أَخِيهِ إِذْ أَنْتُمْ جاهِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1686, N'قالُوا أَ إِنَّكَ لَأَنْتَ يُوسُفُ قالَ أَنَا يُوسُفُ وَ هذا أَخِي قَدْ مَنَّ اللَّهُ عَلَيْنا إِنَّهُ مَنْ يَتَّقِ وَ يَصْبِرْ فَإِنَّ اللَّهَ لا يُضِيعُ أَجْرَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1687, N'قالُوا تَاللَّهِ لَقَدْ آثَرَكَ اللَّهُ عَلَيْنا وَ إِنْ كُنَّا لَخاطِئِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1688, N'قالَ لا تَثْرِيبَ عَلَيْكُمُ الْيَوْمَ يَغْفِرُ اللَّهُ لَكُمْ وَ هُوَ أَرْحَمُ الرَّاحِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1689, N'اذْهَبُوا بِقَمِيصِي هذا فَأَلْقُوهُ عَلى وَجْهِ أَبِي يَأْتِ بَصِيراً وَ أْتُونِي بِأَهْلِكُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1690, N'وَ لَمَّا فَصَلَتِ الْعِيرُ قالَ أَبُوهُمْ إِنِّي لَأَجِدُ رِيحَ يُوسُفَ لَوْ لا أَنْ تُفَنِّدُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1691, N'قالُوا تَاللَّهِ إِنَّكَ لَفِي ضَلالِكَ الْقَدِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1692, N'فَلَمَّا أَنْ جاءَ الْبَشِيرُ أَلْقاهُ عَلى وَجْهِهِ فَارْتَدَّ بَصِيراً قالَ أَ لَمْ أَقُلْ لَكُمْ إِنِّي أَعْلَمُ مِنَ اللَّهِ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1693, N'قالُوا يا أَبانَا اسْتَغْفِرْ لَنا ذُنُوبَنا إِنَّا كُنَّا خاطِئِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1694, N'قالَ سَوْفَ أَسْتَغْفِرُ لَكُمْ رَبِّي إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1695, N'فَلَمَّا دَخَلُوا عَلى يُوسُفَ آوى إِلَيْهِ أَبَوَيْهِ وَ قالَ ادْخُلُوا مِصْرَ إِنْ شاءَ اللَّهُ آمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1696, N'وَ رَفَعَ أَبَوَيْهِ عَلَى الْعَرْشِ وَ خَرُّوا لَهُ سُجَّداً وَ قالَ يا أَبَتِ هذا تَأْوِيلُ رُءْيايَ مِنْ قَبْلُ قَدْ جَعَلَها رَبِّي حَقًّا وَ قَدْ أَحْسَنَ بِي إِذْ أَخْرَجَنِي مِنَ السِّجْنِ وَ جاءَ بِكُمْ مِنَ الْبَدْوِ مِنْ بَعْدِ أَنْ نَزَغَ الشَّيْطانُ بَيْنِي وَ بَيْنَ إِخْوَتِي إِنَّ رَبِّي لَطِيفٌ لِما يَشاءُ إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1697, N'رَبِّ قَدْ آتَيْتَنِي مِنَ الْمُلْكِ وَ عَلَّمْتَنِي مِنْ تَأْوِيلِ الْأَحادِيثِ فاطِرَ السَّماواتِ وَ الْأَرْضِ أَنْتَ وَلِيِّي فِي الدُّنْيا وَ الْآخِرَةِ تَوَفَّنِي مُسْلِماً وَ أَلْحِقْنِي بِالصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1698, N'ذلِكَ مِنْ أَنْباءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ وَ ما كُنْتَ لَدَيْهِمْ إِذْ أَجْمَعُوا أَمْرَهُمْ وَ هُمْ يَمْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1699, N'وَ ما أَكْثَرُ النَّاسِ وَ لَوْ حَرَصْتَ بِمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1700, N'وَ ما تَسْئَلُهُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ هُوَ إِلاَّ ذِكْرٌ لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1701, N'وَ كَأَيِّنْ مِنْ آيَةٍ فِي السَّماواتِ وَ الْأَرْضِ يَمُرُّونَ عَلَيْها وَ هُمْ عَنْها مُعْرِضُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1702, N'وَ ما يُؤْمِنُ أَكْثَرُهُمْ بِاللَّهِ إِلاَّ وَ هُمْ مُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1703, N'أَ فَأَمِنُوا أَنْ تَأْتِيَهُمْ غاشِيَةٌ مِنْ عَذابِ اللَّهِ أَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1704, N'قُلْ هذِهِ سَبِيلِي أَدْعُوا إِلَى اللَّهِ عَلى بَصِيرَةٍ أَنَا وَ مَنِ اتَّبَعَنِي وَ سُبْحانَ اللَّهِ وَ ما أَنَا مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1705, N'وَ ما أَرْسَلْنا مِنْ قَبْلِكَ إِلاَّ رِجالاً نُوحِي إِلَيْهِمْ مِنْ أَهْلِ الْقُرى أَ فَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كانَ عاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ وَ لَدارُ الْآخِرَةِ خَيْرٌ لِلَّذِينَ اتَّقَوْا أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1706, N'حَتَّى إِذَا اسْتَيْأَسَ الرُّسُلُ وَ ظَنُّوا أَنَّهُمْ قَدْ كُذِبُوا جاءَهُمْ نَصْرُنا فَنُجِّيَ مَنْ نَشاءُ وَ لا يُرَدُّ بَأْسُنا عَنِ الْقَوْمِ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1707, N'لَقَدْ كانَ فِي قَصَصِهِمْ عِبْرَةٌ لِأُولِي الْأَلْبابِ ما كانَ حَدِيثاً يُفْتَرى وَ لكِنْ تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَ تَفْصِيلَ كُلِّ شَيْ‏ءٍ وَ هُدىً وَ رَحْمَةً لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1708, N'المر تِلْكَ آياتُ الْكِتابِ وَ الَّذِي أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الْحَقُّ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1709, N'اللَّهُ الَّذِي رَفَعَ السَّماواتِ بِغَيْرِ عَمَدٍ تَرَوْنَها ثُمَّ اسْتَوى عَلَى الْعَرْشِ وَ سَخَّرَ الشَّمْسَ وَ الْقَمَرَ كُلٌّ يَجْرِي لِأَجَلٍ مُسَمًّى يُدَبِّرُ الْأَمْرَ يُفَصِّلُ الْآياتِ لَعَلَّكُمْ بِلِقاءِ رَبِّكُمْ تُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1710, N'وَ هُوَ الَّذِي مَدَّ الْأَرْضَ وَ جَعَلَ فِيها رَواسِيَ وَ أَنْهاراً وَ مِنْ كُلِّ الثَّمَراتِ جَعَلَ فِيها زَوْجَيْنِ اثْنَيْنِ يُغْشِي اللَّيْلَ النَّهارَ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1711, N'وَ فِي الْأَرْضِ قِطَعٌ مُتَجاوِراتٌ وَ جَنَّاتٌ مِنْ أَعْنابٍ وَ زَرْعٌ وَ نَخِيلٌ صِنْوانٌ وَ غَيْرُ صِنْوانٍ يُسْقى بِماءٍ واحِدٍ وَ نُفَضِّلُ بَعْضَها عَلى بَعْضٍ فِي الْأُكُلِ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1712, N'وَ إِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ أَ إِذا كُنَّا تُراباً أَ إِنَّا لَفِي خَلْقٍ جَدِيدٍ أُولئِكَ الَّذِينَ كَفَرُوا بِرَبِّهِمْ وَ أُولئِكَ الْأَغْلالُ فِي أَعْناقِهِمْ وَ أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1713, N'وَ يَسْتَعْجِلُونَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَ قَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلاتُ وَ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ لِلنَّاسِ عَلى ظُلْمِهِمْ وَ إِنَّ رَبَّكَ لَشَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1714, N'وَ يَقُولُ الَّذِينَ كَفَرُوا لَوْ لا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ إِنَّما أَنْتَ مُنْذِرٌ وَ لِكُلِّ قَوْمٍ هادٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1715, N'اللَّهُ يَعْلَمُ ما تَحْمِلُ كُلُّ أُنْثى وَ ما تَغِيضُ الْأَرْحامُ وَ ما تَزْدادُ وَ كُلُّ شَيْ‏ءٍ عِنْدَهُ بِمِقْدارٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1716, N'عالِمُ الْغَيْبِ وَ الشَّهادَةِ الْكَبِيرُ الْمُتَعالِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1717, N'سَواءٌ مِنْكُمْ مَنْ أَسَرَّ الْقَوْلَ وَ مَنْ جَهَرَ بِهِ وَ مَنْ هُوَ مُسْتَخْفٍ بِاللَّيْلِ وَ سارِبٌ بِالنَّهارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1718, N'لَهُ مُعَقِّباتٌ مِنْ بَيْنِ يَدَيْهِ وَ مِنْ خَلْفِهِ يَحْفَظُونَهُ مِنْ أَمْرِ اللَّهِ إِنَّ اللَّهَ لا يُغَيِّرُ ما بِقَوْمٍ حَتَّى يُغَيِّرُوا ما بِأَنْفُسِهِمْ وَ إِذا أَرادَ اللَّهُ بِقَوْمٍ سُوْءاً فَلا مَرَدَّ لَهُ وَ ما لَهُمْ مِنْ دُونِهِ مِنْ والٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1719, N'هُوَ الَّذِي يُرِيكُمُ الْبَرْقَ خَوْفاً وَ طَمَعاً وَ يُنْشِئُ السَّحابَ الثِّقالَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1720, N'وَ يُسَبِّحُ الرَّعْدُ بِحَمْدِهِ وَ الْمَلائِكَةُ مِنْ خِيفَتِهِ وَ يُرْسِلُ الصَّواعِقَ فَيُصِيبُ بِها مَنْ يَشاءُ وَ هُمْ يُجادِلُونَ فِي اللَّهِ وَ هُوَ شَدِيدُ الْمِحالِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1721, N'لَهُ دَعْوَةُ الْحَقِّ وَ الَّذِينَ يَدْعُونَ مِنْ دُونِهِ لا يَسْتَجِيبُونَ لَهُمْ بِشَيْ‏ءٍ إِلاَّ كَباسِطِ كَفَّيْهِ إِلَى الْماءِ لِيَبْلُغَ فاهُ وَ ما هُوَ بِبالِغِهِ وَ ما دُعاءُ الْكافِرِينَ إِلاَّ فِي ضَلالٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1722, N'وَ لِلَّهِ يَسْجُدُ مَنْ فِي السَّماواتِ وَ الْأَرْضِ طَوْعاً وَ كَرْهاً وَ ظِلالُهُمْ بِالْغُدُوِّ وَ الْآصالِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1723, N'قُلْ مَنْ رَبُّ السَّماواتِ وَ الْأَرْضِ قُلِ اللَّهُ قُلْ أَ فَاتَّخَذْتُمْ مِنْ دُونِهِ أَوْلِياءَ لا يَمْلِكُونَ لِأَنْفُسِهِمْ نَفْعاً وَ لا ضَرًّا قُلْ هَلْ يَسْتَوِي الْأَعْمى وَ الْبَصِيرُ أَمْ هَلْ تَسْتَوِي الظُّلُماتُ وَ النُّورُ أَمْ جَعَلُوا لِلَّهِ شُرَكاءَ خَلَقُوا كَخَلْقِهِ فَتَشابَهَ الْخَلْقُ عَلَيْهِمْ قُلِ اللَّهُ خالِقُ كُلِّ شَيْ‏ءٍ وَ هُوَ الْواحِدُ الْقَهَّارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1724, N'أَنْزَلَ مِنَ السَّماءِ ماءً فَسالَتْ أَوْدِيَةٌ بِقَدَرِها فَاحْتَمَلَ السَّيْلُ زَبَداً رابِياً وَ مِمَّا يُوقِدُونَ عَلَيْهِ فِي النَّارِ ابْتِغاءَ حِلْيَةٍ أَوْ مَتاعٍ زَبَدٌ مِثْلُهُ كَذلِكَ يَضْرِبُ اللَّهُ الْحَقَّ وَ الْباطِلَ فَأَمَّا الزَّبَدُ فَيَذْهَبُ جُفاءً وَ أَمَّا ما يَنْفَعُ النَّاسَ فَيَمْكُثُ فِي الْأَرْضِ كَذلِكَ يَضْرِبُ اللَّهُ الْأَمْثالَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1725, N'لِلَّذِينَ اسْتَجابُوا لِرَبِّهِمُ الْحُسْنى وَ الَّذِينَ لَمْ يَسْتَجِيبُوا لَهُ لَوْ أَنَّ لَهُمْ ما فِي الْأَرْضِ جَمِيعاً وَ مِثْلَهُ مَعَهُ لاَفْتَدَوْا بِهِ أُولئِكَ لَهُمْ سُوءُ الْحِسابِ وَ مَأْواهُمْ جَهَنَّمُ وَ بِئْسَ الْمِهادُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1726, N'أَ فَمَنْ يَعْلَمُ أَنَّما أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الْحَقُّ كَمَنْ هُوَ أَعْمى إِنَّما يَتَذَكَّرُ أُولُوا الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1727, N'الَّذِينَ يُوفُونَ بِعَهْدِ اللَّهِ وَ لا يَنْقُضُونَ الْمِيثاقَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1728, N'وَ الَّذِينَ يَصِلُونَ ما أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ وَ يَخْشَوْنَ رَبَّهُمْ وَ يَخافُونَ سُوءَ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1729, N'وَ الَّذِينَ صَبَرُوا ابْتِغاءَ وَجْهِ رَبِّهِمْ وَ أَقامُوا الصَّلاةَ وَ أَنْفَقُوا مِمَّا رَزَقْناهُمْ سِرًّا وَ عَلانِيَةً وَ يَدْرَؤُنَ بِالْحَسَنَةِ السَّيِّئَةَ أُولئِكَ لَهُمْ عُقْبَى الدَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1730, N'جَنَّاتُ عَدْنٍ يَدْخُلُونَها وَ مَنْ صَلَحَ مِنْ آبائِهِمْ وَ أَزْواجِهِمْ وَ ذُرِّيَّاتِهِمْ وَ الْمَلائِكَةُ يَدْخُلُونَ عَلَيْهِمْ مِنْ كُلِّ بابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1731, N'سَلامٌ عَلَيْكُمْ بِما صَبَرْتُمْ فَنِعْمَ عُقْبَى الدَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1732, N'وَ الَّذِينَ يَنْقُضُونَ عَهْدَ اللَّهِ مِنْ بَعْدِ مِيثاقِهِ وَ يَقْطَعُونَ ما أَمَرَ اللَّهُ بِهِ أَنْ يُوصَلَ وَ يُفْسِدُونَ فِي الْأَرْضِ أُولئِكَ لَهُمُ اللَّعْنَةُ وَ لَهُمْ سُوءُ الدَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1733, N'اللَّهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ وَ يَقْدِرُ وَ فَرِحُوا بِالْحَياةِ الدُّنْيا وَ مَا الْحَياةُ الدُّنْيا فِي الْآخِرَةِ إِلاَّ مَتاعٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1734, N'وَ يَقُولُ الَّذِينَ كَفَرُوا لَوْ لا أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ قُلْ إِنَّ اللَّهَ يُضِلُّ مَنْ يَشاءُ وَ يَهْدِي إِلَيْهِ مَنْ أَنابَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1735, N'الَّذِينَ آمَنُوا وَ تَطْمَئِنُّ قُلُوبُهُمْ بِذِكْرِ اللَّهِ أَلا بِذِكْرِ اللَّهِ تَطْمَئِنُّ الْقُلُوبُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1736, N'الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ طُوبى لَهُمْ وَ حُسْنُ مَآبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1737, N'كَذلِكَ أَرْسَلْناكَ فِي أُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِها أُمَمٌ لِتَتْلُوَا عَلَيْهِمُ الَّذِي أَوْحَيْنا إِلَيْكَ وَ هُمْ يَكْفُرُونَ بِالرَّحْمنِ قُلْ هُوَ رَبِّي لا إِلهَ إِلاَّ هُوَ عَلَيْهِ تَوَكَّلْتُ وَ إِلَيْهِ مَتابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1738, N'وَ لَوْ أَنَّ قُرْآناً سُيِّرَتْ بِهِ الْجِبالُ أَوْ قُطِّعَتْ بِهِ الْأَرْضُ أَوْ كُلِّمَ بِهِ الْمَوْتى بَلْ لِلَّهِ الْأَمْرُ جَمِيعاً أَ فَلَمْ يَيْأَسِ الَّذِينَ آمَنُوا أَنْ لَوْ يَشاءُ اللَّهُ لَهَدَى النَّاسَ جَمِيعاً وَ لا يَزالُ الَّذِينَ كَفَرُوا تُصِيبُهُمْ بِما صَنَعُوا قارِعَةٌ أَوْ تَحُلُّ قَرِيباً مِنْ دارِهِمْ حَتَّى يَأْتِيَ وَعْدُ اللَّهِ إِنَّ اللَّهَ لا يُخْلِفُ الْمِيعادَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1739, N'وَ لَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَأَمْلَيْتُ لِلَّذِينَ كَفَرُوا ثُمَّ أَخَذْتُهُمْ فَكَيْفَ كانَ عِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1740, N'أَ فَمَنْ هُوَ قائِمٌ عَلى كُلِّ نَفْسٍ بِما كَسَبَتْ وَ جَعَلُوا لِلَّهِ شُرَكاءَ قُلْ سَمُّوهُمْ أَمْ تُنَبِّئُونَهُ بِما لا يَعْلَمُ فِي الْأَرْضِ أَمْ بِظاهِرٍ مِنَ الْقَوْلِ بَلْ زُيِّنَ لِلَّذِينَ كَفَرُوا مَكْرُهُمْ وَ صُدُّوا عَنِ السَّبِيلِ وَ مَنْ يُضْلِلِ اللَّهُ فَما لَهُ مِنْ هادٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1741, N'لَهُمْ عَذابٌ فِي الْحَياةِ الدُّنْيا وَ لَعَذابُ الْآخِرَةِ أَشَقُّ وَ ما لَهُمْ مِنَ اللَّهِ مِنْ واقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1742, N'مَثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ أُكُلُها دائِمٌ وَ ظِلُّها تِلْكَ عُقْبَى الَّذِينَ اتَّقَوْا وَ عُقْبَى الْكافِرِينَ النَّارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1743, N'وَ الَّذِينَ آتَيْناهُمُ الْكِتابَ يَفْرَحُونَ بِما أُنْزِلَ إِلَيْكَ وَ مِنَ الْأَحْزابِ مَنْ يُنْكِرُ بَعْضَهُ قُلْ إِنَّما أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ وَ لا أُشْرِكَ بِهِ إِلَيْهِ أَدْعُوا وَ إِلَيْهِ مَآبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1744, N'وَ كَذلِكَ أَنْزَلْناهُ حُكْماً عَرَبِيًّا وَ لَئِنِ اتَّبَعْتَ أَهْواءَهُمْ بَعْدَ ما جاءَكَ مِنَ الْعِلْمِ ما لَكَ مِنَ اللَّهِ مِنْ وَلِيٍّ وَ لا واقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1745, N'وَ لَقَدْ أَرْسَلْنا رُسُلاً مِنْ قَبْلِكَ وَ جَعَلْنا لَهُمْ أَزْواجاً وَ ذُرِّيَّةً وَ ما كانَ لِرَسُولٍ أَنْ يَأْتِيَ بِآيَةٍ إِلاَّ بِإِذْنِ اللَّهِ لِكُلِّ أَجَلٍ كِتابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1746, N'يَمْحُوا اللَّهُ ما يَشاءُ وَ يُثْبِتُ وَ عِنْدَهُ أُمُّ الْكِتابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1747, N'وَ إِنْ ما نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِنَّما عَلَيْكَ الْبَلاغُ وَ عَلَيْنَا الْحِسابُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1748, N'أَ وَ لَمْ يَرَوْا أَنَّا نَأْتِي الْأَرْضَ نَنْقُصُها مِنْ أَطْرافِها وَ اللَّهُ يَحْكُمُ لا مُعَقِّبَ لِحُكْمِهِ وَ هُوَ سَرِيعُ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1749, N'وَ قَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ فَلِلَّهِ الْمَكْرُ جَمِيعاً يَعْلَمُ ما تَكْسِبُ كُلُّ نَفْسٍ وَ سَيَعْلَمُ الْكُفَّارُ لِمَنْ عُقْبَى الدَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1750, N'وَ يَقُولُ الَّذِينَ كَفَرُوا لَسْتَ مُرْسَلاً قُلْ كَفى بِاللَّهِ شَهِيداً بَيْنِي وَ بَيْنَكُمْ وَ مَنْ عِنْدَهُ عِلْمُ الْكِتابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1751, N'الر كِتابٌ أَنْزَلْناهُ إِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُماتِ إِلَى النُّورِ بِإِذْنِ رَبِّهِمْ إِلى صِراطِ الْعَزِيزِ الْحَمِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1752, N'اللَّهِ الَّذِي لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ وَيْلٌ لِلْكافِرِينَ مِنْ عَذابٍ شَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1753, N'الَّذِينَ يَسْتَحِبُّونَ الْحَياةَ الدُّنْيا عَلَى الْآخِرَةِ وَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَ يَبْغُونَها عِوَجاً أُولئِكَ فِي ضَلالٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1754, N'وَ ما أَرْسَلْنا مِنْ رَسُولٍ إِلاَّ بِلِسانِ قَوْمِهِ لِيُبَيِّنَ لَهُمْ فَيُضِلُّ اللَّهُ مَنْ يَشاءُ وَ يَهْدِي مَنْ يَشاءُ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1755, N'وَ لَقَدْ أَرْسَلْنا مُوسى بِآياتِنا أَنْ أَخْرِجْ قَوْمَكَ مِنَ الظُّلُماتِ إِلَى النُّورِ وَ ذَكِّرْهُمْ بِأَيَّامِ اللَّهِ إِنَّ فِي ذلِكَ لَآياتٍ لِكُلِّ صَبَّارٍ شَكُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1756, N'وَ إِذْ قالَ مُوسى لِقَوْمِهِ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ أَنْجاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذابِ وَ يُذَبِّحُونَ أَبْناءَكُمْ وَ يَسْتَحْيُونَ نِساءَكُمْ وَ فِي ذلِكُمْ بَلاءٌ مِنْ رَبِّكُمْ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1757, N'وَ إِذْ تَأَذَّنَ رَبُّكُمْ لَئِنْ شَكَرْتُمْ لَأَزِيدَنَّكُمْ وَ لَئِنْ كَفَرْتُمْ إِنَّ عَذابِي لَشَدِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1758, N'وَ قالَ مُوسى إِنْ تَكْفُرُوا أَنْتُمْ وَ مَنْ فِي الْأَرْضِ جَمِيعاً فَإِنَّ اللَّهَ لَغَنِيٌّ حَمِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1759, N'أَ لَمْ يَأْتِكُمْ نَبَؤُا الَّذِينَ مِنْ قَبْلِكُمْ قَوْمِ نُوحٍ وَ عادٍ وَ ثَمُودَ وَ الَّذِينَ مِنْ بَعْدِهِمْ لا يَعْلَمُهُمْ إِلاَّ اللَّهُ جاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّناتِ فَرَدُّوا أَيْدِيَهُمْ فِي أَفْواهِهِمْ وَ قالُوا إِنَّا كَفَرْنا بِما أُرْسِلْتُمْ بِهِ وَ إِنَّا لَفِي شَكٍّ مِمَّا تَدْعُونَنا إِلَيْهِ مُرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1760, N'قالَتْ رُسُلُهُمْ أَ فِي اللَّهِ شَكٌّ فاطِرِ السَّماواتِ وَ الْأَرْضِ يَدْعُوكُمْ لِيَغْفِرَ لَكُمْ مِنْ ذُنُوبِكُمْ وَ يُؤَخِّرَكُمْ إِلى أَجَلٍ مُسَمًّى قالُوا إِنْ أَنْتُمْ إِلاَّ بَشَرٌ مِثْلُنا تُرِيدُونَ أَنْ تَصُدُّونا عَمَّا كانَ يَعْبُدُ آباؤُنا فَأْتُونا بِسُلْطانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1761, N'قالَتْ لَهُمْ رُسُلُهُمْ إِنْ نَحْنُ إِلاَّ بَشَرٌ مِثْلُكُمْ وَ لكِنَّ اللَّهَ يَمُنُّ عَلى مَنْ يَشاءُ مِنْ عِبادِهِ وَ ما كانَ لَنا أَنْ نَأْتِيَكُمْ بِسُلْطانٍ إِلاَّ بِإِذْنِ اللَّهِ وَ عَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1762, N'وَ ما لَنا أَلاَّ نَتَوَكَّلَ عَلَى اللَّهِ وَ قَدْ هَدانا سُبُلَنا وَ لَنَصْبِرَنَّ عَلى ما آذَيْتُمُونا وَ عَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1763, N'وَ قالَ الَّذِينَ كَفَرُوا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِنْ أَرْضِنا أَوْ لَتَعُودُنَّ فِي مِلَّتِنا فَأَوْحى إِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1764, N'وَ لَنُسْكِنَنَّكُمُ الْأَرْضَ مِنْ بَعْدِهِمْ ذلِكَ لِمَنْ خافَ مَقامِي وَ خافَ وَعِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1765, N'وَ اسْتَفْتَحُوا وَ خابَ كُلُّ جَبَّارٍ عَنِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1766, N'مِنْ وَرائِهِ جَهَنَّمُ وَ يُسْقى مِنْ ماءٍ صَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1767, N'يَتَجَرَّعُهُ وَ لا يَكادُ يُسِيغُهُ وَ يَأْتِيهِ الْمَوْتُ مِنْ كُلِّ مَكانٍ وَ ما هُوَ بِمَيِّتٍ وَ مِنْ وَرائِهِ عَذابٌ غَلِيظٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1768, N'مَثَلُ الَّذِينَ كَفَرُوا بِرَبِّهِمْ أَعْمالُهُمْ كَرَمادٍ اشْتَدَّتْ بِهِ الرِّيحُ فِي يَوْمٍ عاصِفٍ لا يَقْدِرُونَ مِمَّا كَسَبُوا عَلى شَيْ‏ءٍ ذلِكَ هُوَ الضَّلالُ الْبَعِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1769, N'أَ لَمْ تَرَ أَنَّ اللَّهَ خَلَقَ السَّماواتِ وَ الْأَرْضَ بِالْحَقِّ إِنْ يَشَأْ يُذْهِبْكُمْ وَ يَأْتِ بِخَلْقٍ جَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1770, N'وَ ما ذلِكَ عَلَى اللَّهِ بِعَزِيزٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1771, N'وَ بَرَزُوا لِلَّهِ جَمِيعاً فَقالَ الضُّعَفاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنَّا كُنَّا لَكُمْ تَبَعاً فَهَلْ أَنْتُمْ مُغْنُونَ عَنَّا مِنْ عَذابِ اللَّهِ مِنْ شَيْ‏ءٍ قالُوا لَوْ هَدانَا اللَّهُ لَهَدَيْناكُمْ سَواءٌ عَلَيْنا أَ جَزِعْنا أَمْ صَبَرْنا ما لَنا مِنْ مَحِيصٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1772, N'وَ قالَ الشَّيْطانُ لَمَّا قُضِيَ الْأَمْرُ إِنَّ اللَّهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَ وَعَدْتُكُمْ فَأَخْلَفْتُكُمْ وَ ما كانَ لِي عَلَيْكُمْ مِنْ سُلْطانٍ إِلاَّ أَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِي فَلا تَلُومُونِي وَ لُومُوا أَنْفُسَكُمْ ما أَنَا بِمُصْرِخِكُمْ وَ ما أَنْتُمْ بِمُصْرِخِيَّ إِنِّي كَفَرْتُ بِما أَشْرَكْتُمُونِ مِنْ قَبْلُ إِنَّ الظَّالِمِينَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1773, N'وَ أُدْخِلَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها بِإِذْنِ رَبِّهِمْ تَحِيَّتُهُمْ فِيها سَلامٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1774, N'أَ لَمْ تَرَ كَيْفَ ضَرَبَ اللَّهُ مَثَلاً كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ أَصْلُها ثابِتٌ وَ فَرْعُها فِي السَّماءِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1775, N'تُؤْتِي أُكُلَها كُلَّ حِينٍ بِإِذْنِ رَبِّها وَ يَضْرِبُ اللَّهُ الْأَمْثالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1776, N'وَ مَثَلُ كَلِمَةٍ خَبِيثَةٍ كَشَجَرَةٍ خَبِيثَةٍ اجْتُثَّتْ مِنْ فَوْقِ الْأَرْضِ ما لَها مِنْ قَرارٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1777, N'يُثَبِّتُ اللَّهُ الَّذِينَ آمَنُوا بِالْقَوْلِ الثَّابِتِ فِي الْحَياةِ الدُّنْيا وَ فِي الْآخِرَةِ وَ يُضِلُّ اللَّهُ الظَّالِمِينَ وَ يَفْعَلُ اللَّهُ ما يَشاءُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1778, N'أَ لَمْ تَرَ إِلَى الَّذِينَ بَدَّلُوا نِعْمَتَ اللَّهِ كُفْراً وَ أَحَلُّوا قَوْمَهُمْ دارَ الْبَوارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1779, N'جَهَنَّمَ يَصْلَوْنَها وَ بِئْسَ الْقَرارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1780, N'وَ جَعَلُوا لِلَّهِ أَنْداداً لِيُضِلُّوا عَنْ سَبِيلِهِ قُلْ تَمَتَّعُوا فَإِنَّ مَصِيرَكُمْ إِلَى النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1781, N'قُلْ لِعِبادِيَ الَّذِينَ آمَنُوا يُقِيمُوا الصَّلاةَ وَ يُنْفِقُوا مِمَّا رَزَقْناهُمْ سِرًّا وَ عَلانِيَةً مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا بَيْعٌ فِيهِ وَ لا خِلالٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1782, N'اللَّهُ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ وَ أَنْزَلَ مِنَ السَّماءِ ماءً فَأَخْرَجَ بِهِ مِنَ الثَّمَراتِ رِزْقاً لَكُمْ وَ سَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِي الْبَحْرِ بِأَمْرِهِ وَ سَخَّرَ لَكُمُ الْأَنْهارَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1783, N'وَ سَخَّرَ لَكُمُ الشَّمْسَ وَ الْقَمَرَ دائِبَيْنِ وَ سَخَّرَ لَكُمُ اللَّيْلَ وَ النَّهارَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1784, N'وَ آتاكُمْ مِنْ كُلِّ ما سَأَلْتُمُوهُ وَ إِنْ تَعُدُّوا نِعْمَتَ اللَّهِ لا تُحْصُوها إِنَّ الْإِنْسانَ لَظَلُومٌ كَفَّارٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1785, N'وَ إِذْ قالَ إِبْراهِيمُ رَبِّ اجْعَلْ هَذَا الْبَلَدَ آمِناً وَ اجْنُبْنِي وَ بَنِيَّ أَنْ نَعْبُدَ الْأَصْنامَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1786, N'رَبِّ إِنَّهُنَّ أَضْلَلْنَ كَثِيراً مِنَ النَّاسِ فَمَنْ تَبِعَنِي فَإِنَّهُ مِنِّي وَ مَنْ عَصانِي فَإِنَّكَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1787, N'رَبَّنا إِنِّي أَسْكَنْتُ مِنْ ذُرِّيَّتِي بِوادٍ غَيْرِ ذِي زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِ رَبَّنا لِيُقِيمُوا الصَّلاةَ فَاجْعَلْ أَفْئِدَةً مِنَ النَّاسِ تَهْوِي إِلَيْهِمْ وَ ارْزُقْهُمْ مِنَ الثَّمَراتِ لَعَلَّهُمْ يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1788, N'رَبَّنا إِنَّكَ تَعْلَمُ ما نُخْفِي وَ ما نُعْلِنُ وَ ما يَخْفى عَلَى اللَّهِ مِنْ شَيْ‏ءٍ فِي الْأَرْضِ وَ لا فِي السَّماءِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1789, N'الْحَمْدُ لِلَّهِ الَّذِي وَهَبَ لِي عَلَى الْكِبَرِ إِسْماعِيلَ وَ إِسْحاقَ إِنَّ رَبِّي لَسَمِيعُ الدُّعاءِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1790, N'رَبِّ اجْعَلْنِي مُقِيمَ الصَّلاةِ وَ مِنْ ذُرِّيَّتِي رَبَّنا وَ تَقَبَّلْ دُعاءِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1791, N'رَبَّنَا اغْفِرْ لِي وَ لِوالِدَيَّ وَ لِلْمُؤْمِنِينَ يَوْمَ يَقُومُ الْحِسابُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1792, N'وَ لا تَحْسَبَنَّ اللَّهَ غافِلاً عَمَّا يَعْمَلُ الظَّالِمُونَ إِنَّما يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيهِ الْأَبْصارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1793, N'مُهْطِعِينَ مُقْنِعِي رُؤُسِهِمْ لا يَرْتَدُّ إِلَيْهِمْ طَرْفُهُمْ وَ أَفْئِدَتُهُمْ هَواءٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1794, N'وَ أَنْذِرِ النَّاسَ يَوْمَ يَأْتِيهِمُ الْعَذابُ فَيَقُولُ الَّذِينَ ظَلَمُوا رَبَّنا أَخِّرْنا إِلى أَجَلٍ قَرِيبٍ نُجِبْ دَعْوَتَكَ وَ نَتَّبِعِ الرُّسُلَ أَ وَ لَمْ تَكُونُوا أَقْسَمْتُمْ مِنْ قَبْلُ ما لَكُمْ مِنْ زَوالٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1795, N'وَ سَكَنْتُمْ فِي مَساكِنِ الَّذِينَ ظَلَمُوا أَنْفُسَهُمْ وَ تَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنا بِهِمْ وَ ضَرَبْنا لَكُمُ الْأَمْثالَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1796, N'وَ قَدْ مَكَرُوا مَكْرَهُمْ وَ عِنْدَ اللَّهِ مَكْرُهُمْ وَ إِنْ كانَ مَكْرُهُمْ لِتَزُولَ مِنْهُ الْجِبالُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1797, N'فَلا تَحْسَبَنَّ اللَّهَ مُخْلِفَ وَعْدِهِ رُسُلَهُ إِنَّ اللَّهَ عَزِيزٌ ذُو انتِقامٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1798, N'يَوْمَ تُبَدَّلُ الْأَرْضُ غَيْرَ الْأَرْضِ وَ السَّماواتُ وَ بَرَزُوا لِلَّهِ الْواحِدِ الْقَهَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1799, N'وَ تَرَى الْمُجْرِمِينَ يَوْمَئِذٍ مُقَرَّنِينَ فِي الْأَصْفادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1800, N'سَرابِيلُهُمْ مِنْ قَطِرانٍ وَ تَغْشى وُجُوهَهُمُ النَّارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1801, N'لِيَجْزِيَ اللَّهُ كُلَّ نَفْسٍ ما كَسَبَتْ إِنَّ اللَّهَ سَرِيعُ الْحِسابِ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1802, N'هذا بَلاغٌ لِلنَّاسِ وَ لِيُنْذَرُوا بِهِ وَ لِيَعْلَمُوا أَنَّما هُوَ إِلهٌ واحِدٌ وَ لِيَذَّكَّرَ أُولُوا الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1803, N'الر تِلْكَ آياتُ الْكِتابِ وَ قُرْآنٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1804, N'رُبَما يَوَدُّ الَّذِينَ كَفَرُوا لَوْ كانُوا مُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1805, N'ذَرْهُمْ يَأْكُلُوا وَ يَتَمَتَّعُوا وَ يُلْهِهِمُ الْأَمَلُ فَسَوْفَ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1806, N'وَ ما أَهْلَكْنا مِنْ قَرْيَةٍ إِلاَّ وَ لَها كِتابٌ مَعْلُومٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1807, N'ما تَسْبِقُ مِنْ أُمَّةٍ أَجَلَها وَ ما يَسْتَأْخِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1808, N'وَ قالُوا يا أَيُّهَا الَّذِي نُزِّلَ عَلَيْهِ الذِّكْرُ إِنَّكَ لَمَجْنُونٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1809, N'لَوْ ما تَأْتِينا بِالْمَلائِكَةِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1810, N'ما نُنَزِّلُ الْمَلائِكَةَ إِلاَّ بِالْحَقِّ وَ ما كانُوا إِذاً مُنْظَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1811, N'إِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَ إِنَّا لَهُ لَحافِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1812, N'وَ لَقَدْ أَرْسَلْنا مِنْ قَبْلِكَ فِي شِيَعِ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1813, N'وَ ما يَأْتِيهِمْ مِنْ رَسُولٍ إِلاَّ كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1814, N'كَذلِكَ نَسْلُكُهُ فِي قُلُوبِ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1815, N'لا يُؤْمِنُونَ بِهِ وَ قَدْ خَلَتْ سُنَّةُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1816, N'وَ لَوْ فَتَحْنا عَلَيْهِمْ باباً مِنَ السَّماءِ فَظَلُّوا فِيهِ يَعْرُجُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1817, N'لَقالُوا إِنَّما سُكِّرَتْ أَبْصارُنا بَلْ نَحْنُ قَوْمٌ مَسْحُورُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1818, N'وَ لَقَدْ جَعَلْنا فِي السَّماءِ بُرُوجاً وَ زَيَّنَّاها لِلنَّاظِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1819, N'وَ حَفِظْناها مِنْ كُلِّ شَيْطانٍ رَجِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1820, N'إِلاَّ مَنِ اسْتَرَقَ السَّمْعَ فَأَتْبَعَهُ شِهابٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1821, N'وَ الْأَرْضَ مَدَدْناها وَ أَلْقَيْنا فِيها رَواسِيَ وَ أَنْبَتْنا فِيها مِنْ كُلِّ شَيْ‏ءٍ مَوْزُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1822, N'وَ جَعَلْنا لَكُمْ فِيها مَعايِشَ وَ مَنْ لَسْتُمْ لَهُ بِرازِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1823, N'وَ إِنْ مِنْ شَيْ‏ءٍ إِلاَّ عِنْدَنا خَزائِنُهُ وَ ما نُنَزِّلُهُ إِلاَّ بِقَدَرٍ مَعْلُومٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1824, N'وَ أَرْسَلْنَا الرِّياحَ لَواقِحَ فَأَنْزَلْنا مِنَ السَّماءِ ماءً فَأَسْقَيْناكُمُوهُ وَ ما أَنْتُمْ لَهُ بِخازِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1825, N'وَ إِنَّا لَنَحْنُ نُحْيِي وَ نُمِيتُ وَ نَحْنُ الْوارِثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1826, N'وَ لَقَدْ عَلِمْنَا الْمُسْتَقْدِمِينَ مِنْكُمْ وَ لَقَدْ عَلِمْنَا الْمُسْتَأْخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1827, N'وَ إِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْ إِنَّهُ حَكِيمٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1828, N'وَ لَقَدْ خَلَقْنَا الْإِنْسانَ مِنْ صَلْصالٍ مِنْ حَمَإٍ مَسْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1829, N'وَ الْجَانَّ خَلَقْناهُ مِنْ قَبْلُ مِنْ نارِ السَّمُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1830, N'وَ إِذْ قالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي خالِقٌ بَشَراً مِنْ صَلْصالٍ مِنْ حَمَإٍ مَسْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1831, N'فَإِذا سَوَّيْتُهُ وَ نَفَخْتُ فِيهِ مِنْ رُوحِي فَقَعُوا لَهُ ساجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1832, N'فَسَجَدَ الْمَلائِكَةُ كُلُّهُمْ أَجْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1833, N'إِلاَّ إِبْلِيسَ أَبى أَنْ يَكُونَ مَعَ السَّاجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1834, N'قالَ يا إِبْلِيسُ ما لَكَ أَلاَّ تَكُونَ مَعَ السَّاجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1835, N'قالَ لَمْ أَكُنْ لِأَسْجُدَ لِبَشَرٍ خَلَقْتَهُ مِنْ صَلْصالٍ مِنْ حَمَإٍ مَسْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1836, N'قالَ فَاخْرُجْ مِنْها فَإِنَّكَ رَجِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1837, N'وَ إِنَّ عَلَيْكَ اللَّعْنَةَ إِلى يَوْمِ الدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1838, N'قالَ رَبِّ فَأَنْظِرْنِي إِلى يَوْمِ يُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1839, N'قالَ فَإِنَّكَ مِنَ الْمُنْظَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1840, N'إِلى يَوْمِ الْوَقْتِ الْمَعْلُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1841, N'قالَ رَبِّ بِما أَغْوَيْتَنِي لَأُزَيِّنَنَّ لَهُمْ فِي الْأَرْضِ وَ لَأُغْوِيَنَّهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1842, N'إِلاَّ عِبادَكَ مِنْهُمُ الْمُخْلَصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1843, N'قالَ هذا صِراطٌ عَلَيَّ مُسْتَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1844, N'إِنَّ عِبادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطانٌ إِلاَّ مَنِ اتَّبَعَكَ مِنَ الْغاوِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1845, N'وَ إِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1846, N'لَها سَبْعَةُ أَبْوابٍ لِكُلِّ بابٍ مِنْهُمْ جُزْءٌ مَقْسُومٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1847, N'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَ عُيُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1848, N'ادْخُلُوها بِسَلامٍ آمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1849, N'وَ نَزَعْنا ما فِي صُدُورِهِمْ مِنْ غِلٍّ إِخْواناً عَلى سُرُرٍ مُتَقابِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1850, N'لا يَمَسُّهُمْ فِيها نَصَبٌ وَ ما هُمْ مِنْها بِمُخْرَجِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1851, N'نَبِّئْ عِبادِي أَنِّي أَنَا الْغَفُورُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1852, N'وَ أَنَّ عَذابِي هُوَ الْعَذابُ الْأَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1853, N'وَ نَبِّئْهُمْ عَنْ ضَيْفِ إِبْراهِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1854, N'إِذْ دَخَلُوا عَلَيْهِ فَقالُوا سَلاماً قالَ إِنَّا مِنْكُمْ وَجِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1855, N'قالُوا لا تَوْجَلْ إِنَّا نُبَشِّرُكَ بِغُلامٍ عَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1856, N'قالَ أَ بَشَّرْتُمُونِي عَلى أَنْ مَسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1857, N'قالُوا بَشَّرْناكَ بِالْحَقِّ فَلا تَكُنْ مِنَ الْقانِطِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1858, N'قالَ وَ مَنْ يَقْنَطُ مِنْ رَحْمَةِ رَبِّهِ إِلاَّ الضَّالُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1859, N'قالَ فَما خَطْبُكُمْ أَيُّهَا الْمُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1860, N'قالُوا إِنَّا أُرْسِلْنا إِلى قَوْمٍ مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1861, N'إِلاَّ آلَ لُوطٍ إِنَّا لَمُنَجُّوهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1862, N'إِلاَّ امْرَأَتَهُ قَدَّرْنا إِنَّها لَمِنَ الْغابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1863, N'فَلَمَّا جاءَ آلَ لُوطٍ الْمُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1864, N'قالَ إِنَّكُمْ قَوْمٌ مُنْكَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1865, N'قالُوا بَلْ جِئْناكَ بِما كانُوا فِيهِ يَمْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1866, N'وَ أَتَيْناكَ بِالْحَقِّ وَ إِنَّا لَصادِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1867, N'فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَ اتَّبِعْ أَدْبارَهُمْ وَ لا يَلْتَفِتْ مِنْكُمْ أَحَدٌ وَ امْضُوا حَيْثُ تُؤْمَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1868, N'وَ قَضَيْنا إِلَيْهِ ذلِكَ الْأَمْرَ أَنَّ دابِرَ هؤُلاءِ مَقْطُوعٌ مُصْبِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1869, N'وَ جاءَ أَهْلُ الْمَدِينَةِ يَسْتَبْشِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1870, N'قالَ إِنَّ هؤُلاءِ ضَيْفِي فَلا تَفْضَحُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1871, N'وَ اتَّقُوا اللَّهَ وَ لا تُخْزُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1872, N'قالُوا أَ وَ لَمْ نَنْهَكَ عَنِ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1873, N'قالَ هؤُلاءِ بَناتِي إِنْ كُنْتُمْ فاعِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1874, N'لَعَمْرُكَ إِنَّهُمْ لَفِي سَكْرَتِهِمْ يَعْمَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1875, N'فَأَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1876, N'فَجَعَلْنا عالِيَها سافِلَها وَ أَمْطَرْنا عَلَيْهِمْ حِجارَةً مِنْ سِجِّيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1877, N'إِنَّ فِي ذلِكَ لَآياتٍ لِلْمُتَوَسِّمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1878, N'وَ إِنَّها لَبِسَبِيلٍ مُقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1879, N'إِنَّ فِي ذلِكَ لَآيَةً لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1880, N'وَ إِنْ كانَ أَصْحابُ الْأَيْكَةِ لَظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1881, N'فَانْتَقَمْنا مِنْهُمْ وَ إِنَّهُما لَبِإِمامٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1882, N'وَ لَقَدْ كَذَّبَ أَصْحابُ الْحِجْرِ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1883, N'وَ آتَيْناهُمْ آياتِنا فَكانُوا عَنْها مُعْرِضِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1884, N'وَ كانُوا يَنْحِتُونَ مِنَ الْجِبالِ بُيُوتاً آمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1885, N'فَأَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1886, N'فَما أَغْنى عَنْهُمْ ما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1887, N'وَ ما خَلَقْنَا السَّماواتِ وَ الْأَرْضَ وَ ما بَيْنَهُما إِلاَّ بِالْحَقِّ وَ إِنَّ السَّاعَةَ لَآتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَمِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1888, N'إِنَّ رَبَّكَ هُوَ الْخَلاَّقُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1889, N'وَ لَقَدْ آتَيْناكَ سَبْعاً مِنَ الْمَثانِي وَ الْقُرْآنَ الْعَظِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1890, N'لا تَمُدَّنَّ عَيْنَيْكَ إِلى ما مَتَّعْنا بِهِ أَزْواجاً مِنْهُمْ وَ لا تَحْزَنْ عَلَيْهِمْ وَ اخْفِضْ جَناحَكَ لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1891, N'وَ قُلْ إِنِّي أَنَا النَّذِيرُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1892, N'كَما أَنْزَلْنا عَلَى الْمُقْتَسِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1893, N'الَّذِينَ جَعَلُوا الْقُرْآنَ عِضِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1894, N'فَوَ رَبِّكَ لَنَسْئَلَنَّهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1895, N'عَمَّا كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1896, N'فَاصْدَعْ بِما تُؤْمَرُ وَ أَعْرِضْ عَنِ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1897, N'إِنَّا كَفَيْناكَ الْمُسْتَهْزِئِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1898, N'الَّذِينَ يَجْعَلُونَ مَعَ اللَّهِ إِلهاً آخَرَ فَسَوْفَ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1899, N'وَ لَقَدْ نَعْلَمُ أَنَّكَ يَضِيقُ صَدْرُكَ بِما يَقُولُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1900, N'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَ كُنْ مِنَ السَّاجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1901, N'وَ اعْبُدْ رَبَّكَ حَتَّى يَأْتِيَكَ الْيَقِينُ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1902, N'أَتى أَمْرُ اللَّهِ فَلا تَسْتَعْجِلُوهُ سُبْحانَهُ وَ تَعالى عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1903, N'يُنَزِّلُ الْمَلائِكَةَ بِالرُّوحِ مِنْ أَمْرِهِ عَلى مَنْ يَشاءُ مِنْ عِبادِهِ أَنْ أَنْذِرُوا أَنَّهُ لا إِلهَ إِلاَّ أَنَا فَاتَّقُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1904, N'خَلَقَ السَّماواتِ وَ الْأَرْضَ بِالْحَقِّ تَعالى عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1905, N'خَلَقَ الْإِنْسانَ مِنْ نُطْفَةٍ فَإِذا هُوَ خَصِيمٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1906, N'وَ الْأَنْعامَ خَلَقَها لَكُمْ فِيها دِفْ‏ءٌ وَ مَنافِعُ وَ مِنْها تَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1907, N'وَ لَكُمْ فِيها جَمالٌ حِينَ تُرِيحُونَ وَ حِينَ تَسْرَحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1908, N'وَ تَحْمِلُ أَثْقالَكُمْ إِلى بَلَدٍ لَمْ تَكُونُوا بالِغِيهِ إِلاَّ بِشِقِّ الْأَنْفُسِ إِنَّ رَبَّكُمْ لَرَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1909, N'وَ الْخَيْلَ وَ الْبِغالَ وَ الْحَمِيرَ لِتَرْكَبُوها وَ زِينَةً وَ يَخْلُقُ ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1910, N'وَ عَلَى اللَّهِ قَصْدُ السَّبِيلِ وَ مِنْها جائِرٌ وَ لَوْ شاءَ لَهَداكُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1911, N'هُوَ الَّذِي أَنْزَلَ مِنَ السَّماءِ ماءً لَكُمْ مِنْهُ شَرابٌ وَ مِنْهُ شَجَرٌ فِيهِ تُسِيمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1912, N'يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَ الزَّيْتُونَ وَ النَّخِيلَ وَ الْأَعْنابَ وَ مِنْ كُلِّ الثَّمَراتِ إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1913, N'وَ سَخَّرَ لَكُمُ اللَّيْلَ وَ النَّهارَ وَ الشَّمْسَ وَ الْقَمَرَ وَ النُّجُومُ مُسَخَّراتٌ بِأَمْرِهِ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1914, N'وَ ما ذَرَأَ لَكُمْ فِي الْأَرْضِ مُخْتَلِفاً أَلْوانُهُ إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَذَّكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1915, N'وَ هُوَ الَّذِي سَخَّرَ الْبَحْرَ لِتَأْكُلُوا مِنْهُ لَحْماً طَرِيًّا وَ تَسْتَخْرِجُوا مِنْهُ حِلْيَةً تَلْبَسُونَها وَ تَرَى الْفُلْكَ مَواخِرَ فِيهِ وَ لِتَبْتَغُوا مِنْ فَضْلِهِ وَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1916, N'وَ أَلْقى فِي الْأَرْضِ رَواسِيَ أَنْ تَمِيدَ بِكُمْ وَ أَنْهاراً وَ سُبُلاً لَعَلَّكُمْ تَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1917, N'وَ عَلاماتٍ وَ بِالنَّجْمِ هُمْ يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1918, N'أَ فَمَنْ يَخْلُقُ كَمَنْ لا يَخْلُقُ أَ فَلا تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1919, N'وَ إِنْ تَعُدُّوا نِعْمَةَ اللَّهِ لا تُحْصُوها إِنَّ اللَّهَ لَغَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1920, N'وَ اللَّهُ يَعْلَمُ ما تُسِرُّونَ وَ ما تُعْلِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1921, N'وَ الَّذِينَ يَدْعُونَ مِنْ دُونِ اللَّهِ لا يَخْلُقُونَ شَيْئاً وَ هُمْ يُخْلَقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1922, N'أَمْواتٌ غَيْرُ أَحْياءٍ وَ ما يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1923, N'إِلهُكُمْ إِلهٌ واحِدٌ فَالَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ قُلُوبُهُمْ مُنْكِرَةٌ وَ هُمْ مُسْتَكْبِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1924, N'لا جَرَمَ أَنَّ اللَّهَ يَعْلَمُ ما يُسِرُّونَ وَ ما يُعْلِنُونَ إِنَّهُ لا يُحِبُّ الْمُسْتَكْبِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1925, N'وَ إِذا قِيلَ لَهُمْ ما ذا أَنْزَلَ رَبُّكُمْ قالُوا أَساطِيرُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1926, N'لِيَحْمِلُوا أَوْزارَهُمْ كامِلَةً يَوْمَ الْقِيامَةِ وَ مِنْ أَوْزارِ الَّذِينَ يُضِلُّونَهُمْ بِغَيْرِ عِلْمٍ أَلا ساءَ ما يَزِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1927, N'قَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ فَأَتَى اللَّهُ بُنْيانَهُمْ مِنَ الْقَواعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَ أَتاهُمُ الْعَذابُ مِنْ حَيْثُ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1928, N'ثُمَّ يَوْمَ الْقِيامَةِ يُخْزِيهِمْ وَ يَقُولُ أَيْنَ شُرَكائِيَ الَّذِينَ كُنْتُمْ تُشَاقُّونَ فِيهِمْ قالَ الَّذِينَ أُوتُوا الْعِلْمَ إِنَّ الْخِزْيَ الْيَوْمَ وَ السُّوءَ عَلَى الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1929, N'الَّذِينَ تَتَوَفَّاهُمُ الْمَلائِكَةُ ظالِمِي أَنْفُسِهِمْ فَأَلْقَوُا السَّلَمَ ما كُنَّا نَعْمَلُ مِنْ سُوءٍ بَلى إِنَّ اللَّهَ عَلِيمٌ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1930, N'فَادْخُلُوا أَبْوابَ جَهَنَّمَ خالِدِينَ فِيها فَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1931, N'وَ قِيلَ لِلَّذِينَ اتَّقَوْا ما ذا أَنْزَلَ رَبُّكُمْ قالُوا خَيْراً لِلَّذِينَ أَحْسَنُوا فِي هذِهِ الدُّنْيا حَسَنَةٌ وَ لَدارُ الْآخِرَةِ خَيْرٌ وَ لَنِعْمَ دارُ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1932, N'جَنَّاتُ عَدْنٍ يَدْخُلُونَها تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ لَهُمْ فِيها ما يَشاؤُنَ كَذلِكَ يَجْزِي اللَّهُ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1933, N'الَّذِينَ تَتَوَفَّاهُمُ الْمَلائِكَةُ طَيِّبِينَ يَقُولُونَ سَلامٌ عَلَيْكُمْ ادْخُلُوا الْجَنَّةَ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1934, N'هَلْ يَنْظُرُونَ إِلاَّ أَنْ تَأْتِيَهُمُ الْمَلائِكَةُ أَوْ يَأْتِيَ أَمْرُ رَبِّكَ كَذلِكَ فَعَلَ الَّذِينَ مِنْ قَبْلِهِمْ وَ ما ظَلَمَهُمُ اللَّهُ وَ لكِنْ كانُوا أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1935, N'فَأَصابَهُمْ سَيِّئاتُ ما عَمِلُوا وَ حاقَ بِهِمْ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1936, N'وَ قالَ الَّذِينَ أَشْرَكُوا لَوْ شاءَ اللَّهُ ما عَبَدْنا مِنْ دُونِهِ مِنْ شَيْ‏ءٍ نَحْنُ وَ لا آباؤُنا وَ لا حَرَّمْنا مِنْ دُونِهِ مِنْ شَيْ‏ءٍ كَذلِكَ فَعَلَ الَّذِينَ مِنْ قَبْلِهِمْ فَهَلْ عَلَى الرُّسُلِ إِلاَّ الْبَلاغُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1937, N'وَ لَقَدْ بَعَثْنا فِي كُلِّ أُمَّةٍ رَسُولاً أَنِ اعْبُدُوا اللَّهَ وَ اجْتَنِبُوا الطَّاغُوتَ فَمِنْهُمْ مَنْ هَدَى اللَّهُ وَ مِنْهُمْ مَنْ حَقَّتْ عَلَيْهِ الضَّلالَةُ فَسِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كانَ عاقِبَةُ الْمُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1938, N'إِنْ تَحْرِصْ عَلى هُداهُمْ فَإِنَّ اللَّهَ لا يَهْدِي مَنْ يُضِلُّ وَ ما لَهُمْ مِنْ ناصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1939, N'وَ أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمانِهِمْ لا يَبْعَثُ اللَّهُ مَنْ يَمُوتُ بَلى وَعْداً عَلَيْهِ حَقًّا وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1940, N'لِيُبَيِّنَ لَهُمُ الَّذِي يَخْتَلِفُونَ فِيهِ وَ لِيَعْلَمَ الَّذِينَ كَفَرُوا أَنَّهُمْ كانُوا كاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1941, N'إِنَّما قَوْلُنا لِشَيْ‏ءٍ إِذا أَرَدْناهُ أَنْ نَقُولَ لَهُ كُنْ فَيَكُونُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1942, N'وَ الَّذِينَ هاجَرُوا فِي اللَّهِ مِنْ بَعْدِ ما ظُلِمُوا لَنُبَوِّئَنَّهُمْ فِي الدُّنْيا حَسَنَةً وَ لَأَجْرُ الْآخِرَةِ أَكْبَرُ لَوْ كانُوا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1943, N'الَّذِينَ صَبَرُوا وَ عَلى رَبِّهِمْ يَتَوَكَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1944, N'وَ ما أَرْسَلْنا مِنْ قَبْلِكَ إِلاَّ رِجالاً نُوحِي إِلَيْهِمْ فَسْئَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1945, N'بِالْبَيِّناتِ وَ الزُّبُرِ وَ أَنْزَلْنا إِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ ما نُزِّلَ إِلَيْهِمْ وَ لَعَلَّهُمْ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1946, N'أَ فَأَمِنَ الَّذِينَ مَكَرُوا السَّيِّئاتِ أَنْ يَخْسِفَ اللَّهُ بِهِمُ الْأَرْضَ أَوْ يَأْتِيَهُمُ الْعَذابُ مِنْ حَيْثُ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1947, N'أَوْ يَأْخُذَهُمْ فِي تَقَلُّبِهِمْ فَما هُمْ بِمُعْجِزِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1948, N'أَوْ يَأْخُذَهُمْ عَلى تَخَوُّفٍ فَإِنَّ رَبَّكُمْ لَرَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1949, N'أَ وَ لَمْ يَرَوْا إِلى ما خَلَقَ اللَّهُ مِنْ شَيْ‏ءٍ يَتَفَيَّؤُا ظِلالُهُ عَنِ الْيَمِينِ وَ الشَّمائِلِ سُجَّداً لِلَّهِ وَ هُمْ داخِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1950, N'وَ لِلَّهِ يَسْجُدُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ مِنْ دابَّةٍ وَ الْمَلائِكَةُ وَ هُمْ لا يَسْتَكْبِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1951, N'يَخافُونَ رَبَّهُمْ مِنْ فَوْقِهِمْ وَ يَفْعَلُونَ ما يُؤْمَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1952, N'وَ قالَ اللَّهُ لا تَتَّخِذُوا إِلهَيْنِ اثْنَيْنِ إِنَّما هُوَ إِلهٌ واحِدٌ فَإِيَّايَ فَارْهَبُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1953, N'وَ لَهُ ما فِي السَّماواتِ وَ الْأَرْضِ وَ لَهُ الدِّينُ واصِباً أَ فَغَيْرَ اللَّهِ تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1954, N'وَ ما بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللَّهِ ثُمَّ إِذا مَسَّكُمُ الضُّرُّ فَإِلَيْهِ تَجْئَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1955, N'ثُمَّ إِذا كَشَفَ الضُّرَّ عَنْكُمْ إِذا فَرِيقٌ مِنْكُمْ بِرَبِّهِمْ يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1956, N'لِيَكْفُرُوا بِما آتَيْناهُمْ فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1957, N'وَ يَجْعَلُونَ لِما لا يَعْلَمُونَ نَصِيباً مِمَّا رَزَقْناهُمْ تَاللَّهِ لَتُسْئَلُنَّ عَمَّا كُنْتُمْ تَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1958, N'وَ يَجْعَلُونَ لِلَّهِ الْبَناتِ سُبْحانَهُ وَ لَهُمْ ما يَشْتَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1959, N'وَ إِذا بُشِّرَ أَحَدُهُمْ بِالْأُنْثى ظَلَّ وَجْهُهُ مُسْوَدًّا وَ هُوَ كَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1960, N'يَتَوارى مِنَ الْقَوْمِ مِنْ سُوءِ ما بُشِّرَ بِهِ أَ يُمْسِكُهُ عَلى هُونٍ أَمْ يَدُسُّهُ فِي التُّرابِ أَلا ساءَ ما يَحْكُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1961, N'لِلَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ مَثَلُ السَّوْءِ وَ لِلَّهِ الْمَثَلُ الْأَعْلى وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1962, N'وَ لَوْ يُؤاخِذُ اللَّهُ النَّاسَ بِظُلْمِهِمْ ما تَرَكَ عَلَيْها مِنْ دَابَّةٍ وَ لكِنْ يُؤَخِّرُهُمْ إِلى أَجَلٍ مُسَمًّى فَإِذا جاءَ أَجَلُهُمْ لا يَسْتَأْخِرُونَ ساعَةً وَ لا يَسْتَقْدِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1963, N'وَ يَجْعَلُونَ لِلَّهِ ما يَكْرَهُونَ وَ تَصِفُ أَلْسِنَتُهُمُ الْكَذِبَ أَنَّ لَهُمُ الْحُسْنى لا جَرَمَ أَنَّ لَهُمُ النَّارَ وَ أَنَّهُمْ مُفْرَطُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1964, N'تَاللَّهِ لَقَدْ أَرْسَلْنا إِلى أُمَمٍ مِنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطانُ أَعْمالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1965, N'وَ ما أَنْزَلْنا عَلَيْكَ الْكِتابَ إِلاَّ لِتُبَيِّنَ لَهُمُ الَّذِي اخْتَلَفُوا فِيهِ وَ هُدىً وَ رَحْمَةً لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1966, N'وَ اللَّهُ أَنْزَلَ مِنَ السَّماءِ ماءً فَأَحْيا بِهِ الْأَرْضَ بَعْدَ مَوْتِها إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1967, N'وَ إِنَّ لَكُمْ فِي الْأَنْعامِ لَعِبْرَةً نُسْقِيكُمْ مِمَّا فِي بُطُونِهِ مِنْ بَيْنِ فَرْثٍ وَ دَمٍ لَبَناً خالِصاً سائِغاً لِلشَّارِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1968, N'وَ مِنْ ثَمَراتِ النَّخِيلِ وَ الْأَعْنابِ تَتَّخِذُونَ مِنْهُ سَكَراً وَ رِزْقاً حَسَناً إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1969, N'وَ أَوْحى رَبُّكَ إِلَى النَّحْلِ أَنِ اتَّخِذِي مِنَ الْجِبالِ بُيُوتاً وَ مِنَ الشَّجَرِ وَ مِمَّا يَعْرِشُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1970, N'ثُمَّ كُلِي مِنْ كُلِّ الثَّمَراتِ فَاسْلُكِي سُبُلَ رَبِّكِ ذُلُلاً يَخْرُجُ مِنْ بُطُونِها شَرابٌ مُخْتَلِفٌ أَلْوانُهُ فِيهِ شِفاءٌ لِلنَّاسِ إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1971, N'وَ اللَّهُ خَلَقَكُمْ ثُمَّ يَتَوَفَّاكُمْ وَ مِنْكُمْ مَنْ يُرَدُّ إِلى أَرْذَلِ الْعُمُرِ لِكَيْ لا يَعْلَمَ بَعْدَ عِلْمٍ شَيْئاً إِنَّ اللَّهَ عَلِيمٌ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1972, N'وَ اللَّهُ فَضَّلَ بَعْضَكُمْ عَلى بَعْضٍ فِي الرِّزْقِ فَمَا الَّذِينَ فُضِّلُوا بِرَادِّي رِزْقِهِمْ عَلى ما مَلَكَتْ أَيْمانُهُمْ فَهُمْ فِيهِ سَواءٌ أَ فَبِنِعْمَةِ اللَّهِ يَجْحَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1973, N'وَ اللَّهُ جَعَلَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْواجاً وَ جَعَلَ لَكُمْ مِنْ أَزْواجِكُمْ بَنِينَ وَ حَفَدَةً وَ رَزَقَكُمْ مِنَ الطَّيِّباتِ أَ فَبِالْباطِلِ يُؤْمِنُونَ وَ بِنِعْمَتِ اللَّهِ هُمْ يَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1974, N'وَ يَعْبُدُونَ مِنْ دُونِ اللَّهِ ما لا يَمْلِكُ لَهُمْ رِزْقاً مِنَ السَّماواتِ وَ الْأَرْضِ شَيْئاً وَ لا يَسْتَطِيعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1975, N'فَلا تَضْرِبُوا لِلَّهِ الْأَمْثالَ إِنَّ اللَّهَ يَعْلَمُ وَ أَنْتُمْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1976, N'ضَرَبَ اللَّهُ مَثَلاً عَبْداً مَمْلُوكاً لا يَقْدِرُ عَلى شَيْ‏ءٍ وَ مَنْ رَزَقْناهُ مِنَّا رِزْقاً حَسَناً فَهُوَ يُنْفِقُ مِنْهُ سِرًّا وَ جَهْراً هَلْ يَسْتَوُونَ الْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1977, N'وَ ضَرَبَ اللَّهُ مَثَلاً رَجُلَيْنِ أَحَدُهُما أَبْكَمُ لا يَقْدِرُ عَلى شَيْ‏ءٍ وَ هُوَ كَلٌّ عَلى مَوْلاهُ أَيْنَما يُوَجِّهْهُ لا يَأْتِ بِخَيْرٍ هَلْ يَسْتَوِي هُوَ وَ مَنْ يَأْمُرُ بِالْعَدْلِ وَ هُوَ عَلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1978, N'وَ لِلَّهِ غَيْبُ السَّماواتِ وَ الْأَرْضِ وَ ما أَمْرُ السَّاعَةِ إِلاَّ كَلَمْحِ الْبَصَرِ أَوْ هُوَ أَقْرَبُ إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1979, N'وَ اللَّهُ أَخْرَجَكُمْ مِنْ بُطُونِ أُمَّهاتِكُمْ لا تَعْلَمُونَ شَيْئاً وَ جَعَلَ لَكُمُ السَّمْعَ وَ الْأَبْصارَ وَ الْأَفْئِدَةَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1980, N'أَ لَمْ يَرَوْا إِلَى الطَّيْرِ مُسَخَّراتٍ فِي جَوِّ السَّماءِ ما يُمْسِكُهُنَّ إِلاَّ اللَّهُ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1981, N'وَ اللَّهُ جَعَلَ لَكُمْ مِنْ بُيُوتِكُمْ سَكَناً وَ جَعَلَ لَكُمْ مِنْ جُلُودِ الْأَنْعامِ بُيُوتاً تَسْتَخِفُّونَها يَوْمَ ظَعْنِكُمْ وَ يَوْمَ إِقامَتِكُمْ وَ مِنْ أَصْوافِها وَ أَوْبارِها وَ أَشْعارِها أَثاثاً وَ مَتاعاً إِلى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1982, N'وَ اللَّهُ جَعَلَ لَكُمْ مِمَّا خَلَقَ ظِلالاً وَ جَعَلَ لَكُمْ مِنَ الْجِبالِ أَكْناناً وَ جَعَلَ لَكُمْ سَرابِيلَ تَقِيكُمُ الْحَرَّ وَ سَرابِيلَ تَقِيكُمْ بَأْسَكُمْ كَذلِكَ يُتِمُّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1983, N'فَإِنْ تَوَلَّوْا فَإِنَّما عَلَيْكَ الْبَلاغُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1984, N'يَعْرِفُونَ نِعْمَتَ اللَّهِ ثُمَّ يُنْكِرُونَها وَ أَكْثَرُهُمُ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1985, N'وَ يَوْمَ نَبْعَثُ مِنْ كُلِّ أُمَّةٍ شَهِيداً ثُمَّ لا يُؤْذَنُ لِلَّذِينَ كَفَرُوا وَ لا هُمْ يُسْتَعْتَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1986, N'وَ إِذا رَأَى الَّذِينَ ظَلَمُوا الْعَذابَ فَلا يُخَفَّفُ عَنْهُمْ وَ لا هُمْ يُنْظَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1987, N'وَ إِذا رَأَى الَّذِينَ أَشْرَكُوا شُرَكاءَهُمْ قالُوا رَبَّنا هؤُلاءِ شُرَكاؤُنَا الَّذِينَ كُنَّا نَدْعُوا مِنْ دُونِكَ فَأَلْقَوْا إِلَيْهِمُ الْقَوْلَ إِنَّكُمْ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1988, N'وَ أَلْقَوْا إِلَى اللَّهِ يَوْمَئِذٍ السَّلَمَ وَ ضَلَّ عَنْهُمْ ما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1989, N'الَّذِينَ كَفَرُوا وَ صَدُّوا عَنْ سَبِيلِ اللَّهِ زِدْناهُمْ عَذاباً فَوْقَ الْعَذابِ بِما كانُوا يُفْسِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1990, N'وَ يَوْمَ نَبْعَثُ فِي كُلِّ أُمَّةٍ شَهِيداً عَلَيْهِمْ مِنْ أَنْفُسِهِمْ وَ جِئْنا بِكَ شَهِيداً عَلى هؤُلاءِ وَ نَزَّلْنا عَلَيْكَ الْكِتابَ تِبْياناً لِكُلِّ شَيْ‏ءٍ وَ هُدىً وَ رَحْمَةً وَ بُشْرى لِلْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1991, N'إِنَّ اللَّهَ يَأْمُرُ بِالْعَدْلِ وَ الْإِحْسانِ وَ إِيتاءِ ذِي الْقُرْبى وَ يَنْهى عَنِ الْفَحْشاءِ وَ الْمُنْكَرِ وَ الْبَغْيِ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1992, N'وَ أَوْفُوا بِعَهْدِ اللَّهِ إِذا عاهَدْتُمْ وَ لا تَنْقُضُوا الْأَيْمانَ بَعْدَ تَوْكِيدِها وَ قَدْ جَعَلْتُمُ اللَّهَ عَلَيْكُمْ كَفِيلاً إِنَّ اللَّهَ يَعْلَمُ ما تَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1993, N'وَ لا تَكُونُوا كَالَّتِي نَقَضَتْ غَزْلَها مِنْ بَعْدِ قُوَّةٍ أَنْكاثاً تَتَّخِذُونَ أَيْمانَكُمْ دَخَلاً بَيْنَكُمْ أَنْ تَكُونَ أُمَّةٌ هِيَ أَرْبى مِنْ أُمَّةٍ إِنَّما يَبْلُوكُمُ اللَّهُ بِهِ وَ لَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيامَةِ ما كُنْتُمْ فِيهِ تَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1994, N'وَ لَوْ شاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً واحِدَةً وَ لكِنْ يُضِلُّ مَنْ يَشاءُ وَ يَهْدِي مَنْ يَشاءُ وَ لَتُسْئَلُنَّ عَمَّا كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1995, N'وَ لا تَتَّخِذُوا أَيْمانَكُمْ دَخَلاً بَيْنَكُمْ فَتَزِلَّ قَدَمٌ بَعْدَ ثُبُوتِها وَ تَذُوقُوا السُّوءَ بِما صَدَدْتُمْ عَنْ سَبِيلِ اللَّهِ وَ لَكُمْ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1996, N'وَ لا تَشْتَرُوا بِعَهْدِ اللَّهِ ثَمَناً قَلِيلاً إِنَّما عِنْدَ اللَّهِ هُوَ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1997, N'ما عِنْدَكُمْ يَنْفَدُ وَ ما عِنْدَ اللَّهِ باقٍ وَ لَنَجْزِيَنَّ الَّذِينَ صَبَرُوا أَجْرَهُمْ بِأَحْسَنِ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1998, N'مَنْ عَمِلَ صالِحاً مِنْ ذَكَرٍ أَوْ أُنْثى وَ هُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَياةً طَيِّبَةً وَ لَنَجْزِيَنَّهُمْ أَجْرَهُمْ بِأَحْسَنِ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (1999, N'فَإِذا قَرَأْتَ الْقُرْآنَ فَاسْتَعِذْ بِاللَّهِ مِنَ الشَّيْطانِ الرَّجِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2000, N'إِنَّهُ لَيْسَ لَهُ سُلْطانٌ عَلَى الَّذِينَ آمَنُوا وَ عَلى رَبِّهِمْ يَتَوَكَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2001, N'إِنَّما سُلْطانُهُ عَلَى الَّذِينَ يَتَوَلَّوْنَهُ وَ الَّذِينَ هُمْ بِهِ مُشْرِكُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2002, N'وَ إِذا بَدَّلْنا آيَةً مَكانَ آيَةٍ وَ اللَّهُ أَعْلَمُ بِما يُنَزِّلُ قالُوا إِنَّما أَنْتَ مُفْتَرٍ بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2003, N'قُلْ نَزَّلَهُ رُوحُ الْقُدُسِ مِنْ رَبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِينَ آمَنُوا وَ هُدىً وَ بُشْرى لِلْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2004, N'وَ لَقَدْ نَعْلَمُ أَنَّهُمْ يَقُولُونَ إِنَّما يُعَلِّمُهُ بَشَرٌ لِسانُ الَّذِي يُلْحِدُونَ إِلَيْهِ أَعْجَمِيٌّ وَ هذا لِسانٌ عَرَبِيٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2005, N'إِنَّ الَّذِينَ لا يُؤْمِنُونَ بِآياتِ اللَّهِ لا يَهْدِيهِمُ اللَّهُ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2006, N'إِنَّما يَفْتَرِي الْكَذِبَ الَّذِينَ لا يُؤْمِنُونَ بِآياتِ اللَّهِ وَ أُولئِكَ هُمُ الْكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2007, N'مَنْ كَفَرَ بِاللَّهِ مِنْ بَعْدِ إِيمانِهِ إِلاَّ مَنْ أُكْرِهَ وَ قَلْبُهُ مُطْمَئِنٌّ بِالْإِيمانِ وَ لكِنْ مَنْ شَرَحَ بِالْكُفْرِ صَدْراً فَعَلَيْهِمْ غَضَبٌ مِنَ اللَّهِ وَ لَهُمْ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2008, N'ذلِكَ بِأَنَّهُمُ اسْتَحَبُّوا الْحَياةَ الدُّنْيا عَلَى الْآخِرَةِ وَ أَنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2009, N'أُولئِكَ الَّذِينَ طَبَعَ اللَّهُ عَلى قُلُوبِهِمْ وَ سَمْعِهِمْ وَ أَبْصارِهِمْ وَ أُولئِكَ هُمُ الْغافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2010, N'لا جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2011, N'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هاجَرُوا مِنْ بَعْدِ ما فُتِنُوا ثُمَّ جاهَدُوا وَ صَبَرُوا إِنَّ رَبَّكَ مِنْ بَعْدِها لَغَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2012, N'يَوْمَ تَأْتِي كُلُّ نَفْسٍ تُجادِلُ عَنْ نَفْسِها وَ تُوَفَّى كُلُّ نَفْسٍ ما عَمِلَتْ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2013, N'وَ ضَرَبَ اللَّهُ مَثَلاً قَرْيَةً كانَتْ آمِنَةً مُطْمَئِنَّةً يَأْتِيها رِزْقُها رَغَداً مِنْ كُلِّ مَكانٍ فَكَفَرَتْ بِأَنْعُمِ اللَّهِ فَأَذاقَهَا اللَّهُ لِباسَ الْجُوعِ وَ الْخَوْفِ بِما كانُوا يَصْنَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2014, N'وَ لَقَدْ جاءَهُمْ رَسُولٌ مِنْهُمْ فَكَذَّبُوهُ فَأَخَذَهُمُ الْعَذابُ وَ هُمْ ظالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2015, N'فَكُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلالاً طَيِّباً وَ اشْكُرُوا نِعْمَتَ اللَّهِ إِنْ كُنْتُمْ إِيَّاهُ تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2016, N'إِنَّما حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَ الدَّمَ وَ لَحْمَ الْخِنْزِيرِ وَ ما أُهِلَّ لِغَيْرِ اللَّهِ بِهِ فَمَنِ اضْطُرَّ غَيْرَ باغٍ وَ لا عادٍ فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2017, N'وَ لا تَقُولُوا لِما تَصِفُ أَلْسِنَتُكُمُ الْكَذِبَ هذا حَلالٌ وَ هذا حَرامٌ لِتَفْتَرُوا عَلَى اللَّهِ الْكَذِبَ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لا يُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2018, N'مَتاعٌ قَلِيلٌ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2019, N'وَ عَلَى الَّذِينَ هادُوا حَرَّمْنا ما قَصَصْنا عَلَيْكَ مِنْ قَبْلُ وَ ما ظَلَمْناهُمْ وَ لكِنْ كانُوا أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2020, N'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُوا السُّوءَ بِجَهالَةٍ ثُمَّ تابُوا مِنْ بَعْدِ ذلِكَ وَ أَصْلَحُوا إِنَّ رَبَّكَ مِنْ بَعْدِها لَغَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2021, N'إِنَّ إِبْراهِيمَ كانَ أُمَّةً قانِتاً لِلَّهِ حَنِيفاً وَ لَمْ يَكُ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2022, N'شاكِراً لِأَنْعُمِهِ اجْتَباهُ وَ هَداهُ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2023, N'وَ آتَيْناهُ فِي الدُّنْيا حَسَنَةً وَ إِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2024, N'ثُمَّ أَوْحَيْنا إِلَيْكَ أَنِ اتَّبِعْ مِلَّةَ إِبْراهِيمَ حَنِيفاً وَ ما كانَ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2025, N'إِنَّما جُعِلَ السَّبْتُ عَلَى الَّذِينَ اخْتَلَفُوا فِيهِ وَ إِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيامَةِ فِيما كانُوا فِيهِ يَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2026, N'ادْعُ إِلى سَبِيلِ رَبِّكَ بِالْحِكْمَةِ وَ الْمَوْعِظَةِ الْحَسَنَةِ وَ جادِلْهُمْ بِالَّتِي هِيَ أَحْسَنُ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَ هُوَ أَعْلَمُ بِالْمُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2027, N'وَ إِنْ عاقَبْتُمْ فَعاقِبُوا بِمِثْلِ ما عُوقِبْتُمْ بِهِ وَ لَئِنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِلصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2028, N'وَ اصْبِرْ وَ ما صَبْرُكَ إِلاَّ بِاللَّهِ وَ لا تَحْزَنْ عَلَيْهِمْ وَ لا تَكُ فِي ضَيْقٍ مِمَّا يَمْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2029, N'إِنَّ اللَّهَ مَعَ الَّذِينَ اتَّقَوْا وَ الَّذِينَ هُمْ مُحْسِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2030, N'سُبْحانَ الَّذِي أَسْرى بِعَبْدِهِ لَيْلاً مِنَ الْمَسْجِدِ الْحَرامِ إِلَى الْمَسْجِدِ الْأَقْصَى الَّذِي بارَكْنا حَوْلَهُ لِنُرِيَهُ مِنْ آياتِنا إِنَّهُ هُوَ السَّمِيعُ الْبَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2031, N'وَ آتَيْنا مُوسَى الْكِتابَ وَ جَعَلْناهُ هُدىً لِبَنِي إِسْرائِيلَ أَلاَّ تَتَّخِذُوا مِنْ دُونِي وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2032, N'ذُرِّيَّةَ مَنْ حَمَلْنا مَعَ نُوحٍ إِنَّهُ كانَ عَبْداً شَكُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2033, N'وَ قَضَيْنا إِلى بَنِي إِسْرائِيلَ فِي الْكِتابِ لَتُفْسِدُنَّ فِي الْأَرْضِ مَرَّتَيْنِ وَ لَتَعْلُنَّ عُلُوًّا كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2034, N'فَإِذا جاءَ وَعْدُ أُولاهُما بَعَثْنا عَلَيْكُمْ عِباداً لَنا أُولِي بَأْسٍ شَدِيدٍ فَجاسُوا خِلالَ الدِّيارِ وَ كانَ وَعْداً مَفْعُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2035, N'ثُمَّ رَدَدْنا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَ أَمْدَدْناكُمْ بِأَمْوالٍ وَ بَنِينَ وَ جَعَلْناكُمْ أَكْثَرَ نَفِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2036, N'إِنْ أَحْسَنْتُمْ أَحْسَنْتُمْ لِأَنْفُسِكُمْ وَ إِنْ أَسَأْتُمْ فَلَها فَإِذا جاءَ وَعْدُ الْآخِرَةِ لِيَسُوؤُا وُجُوهَكُمْ وَ لِيَدْخُلُوا الْمَسْجِدَ كَما دَخَلُوهُ أَوَّلَ مَرَّةٍ وَ لِيُتَبِّرُوا ما عَلَوْا تَتْبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2037, N'عَسى رَبُّكُمْ أَنْ يَرْحَمَكُمْ وَ إِنْ عُدْتُمْ عُدْنا وَ جَعَلْنا جَهَنَّمَ لِلْكافِرِينَ حَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2038, N'إِنَّ هذَا الْقُرْآنَ يَهْدِي لِلَّتِي هِيَ أَقْوَمُ وَ يُبَشِّرُ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصَّالِحاتِ أَنَّ لَهُمْ أَجْراً كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2039, N'وَ أَنَّ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ أَعْتَدْنا لَهُمْ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2040, N'وَ يَدْعُ الْإِنْسانُ بِالشَّرِّ دُعاءَهُ بِالْخَيْرِ وَ كانَ الْإِنْسانُ عَجُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2041, N'وَ جَعَلْنَا اللَّيْلَ وَ النَّهارَ آيَتَيْنِ فَمَحَوْنا آيَةَ اللَّيْلِ وَ جَعَلْنا آيَةَ النَّهارِ مُبْصِرَةً لِتَبْتَغُوا فَضْلاً مِنْ رَبِّكُمْ وَ لِتَعْلَمُوا عَدَدَ السِّنِينَ وَ الْحِسابَ وَ كُلَّ شَيْ‏ءٍ فَصَّلْناهُ تَفْصِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2042, N'وَ كُلَّ إِنسانٍ أَلْزَمْناهُ طائِرَهُ فِي عُنُقِهِ وَ نُخْرِجُ لَهُ يَوْمَ الْقِيامَةِ كِتاباً يَلْقاهُ مَنْشُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2043, N'اقْرَأْ كِتابَكَ كَفى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2044, N'مَنِ اهْتَدى فَإِنَّما يَهْتَدِي لِنَفْسِهِ وَ مَنْ ضَلَّ فَإِنَّما يَضِلُّ عَلَيْها وَ لا تَزِرُ وازِرَةٌ وِزْرَ أُخْرى وَ ما كُنَّا مُعَذِّبِينَ حَتَّى نَبْعَثَ رَسُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2045, N'وَ إِذا أَرَدْنا أَنْ نُهْلِكَ قَرْيَةً أَمَرْنا مُتْرَفِيها فَفَسَقُوا فِيها فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْناها تَدْمِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2046, N'وَ كَمْ أَهْلَكْنا مِنَ الْقُرُونِ مِنْ بَعْدِ نُوحٍ وَ كَفى بِرَبِّكَ بِذُنُوبِ عِبادِهِ خَبِيراً بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2047, N'مَنْ كانَ يُرِيدُ الْعاجِلَةَ عَجَّلْنا لَهُ فِيها ما نَشاءُ لِمَنْ نُرِيدُ ثُمَّ جَعَلْنا لَهُ جَهَنَّمَ يَصْلاها مَذْمُوماً مَدْحُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2048, N'وَ مَنْ أَرادَ الْآخِرَةَ وَ سَعى لَها سَعْيَها وَ هُوَ مُؤْمِنٌ فَأُولئِكَ كانَ سَعْيُهُمْ مَشْكُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2049, N'كُلاًّ نُمِدُّ هؤُلاءِ وَ هَؤُلاءِ مِنْ عَطاءِ رَبِّكَ وَ ما كانَ عَطاءُ رَبِّكَ مَحْظُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2050, N'انْظُرْ كَيْفَ فَضَّلْنا بَعْضَهُمْ عَلى بَعْضٍ وَ لَلْآخِرَةُ أَكْبَرُ دَرَجاتٍ وَ أَكْبَرُ تَفْضِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2051, N'لا تَجْعَلْ مَعَ اللَّهِ إِلهاً آخَرَ فَتَقْعُدَ مَذْمُوماً مَخْذُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2052, N'وَ قَضى رَبُّكَ أَلاَّ تَعْبُدُوا إِلاَّ إِيَّاهُ وَ بِالْوالِدَيْنِ إِحْساناً إِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ أَحَدُهُما أَوْ كِلاهُما فَلا تَقُلْ لَهُما أُفٍّ وَ لا تَنْهَرْهُما وَ قُلْ لَهُما قَوْلاً كَرِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2053, N'وَ اخْفِضْ لَهُما جَناحَ الذُّلِّ مِنَ الرَّحْمَةِ وَ قُلْ رَبِّ ارْحَمْهُما كَما رَبَّيانِي صَغِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2054, N'رَبُّكُمْ أَعْلَمُ بِما فِي نُفُوسِكُمْ إِنْ تَكُونُوا صالِحِينَ فَإِنَّهُ كانَ لِلْأَوَّابِينَ غَفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2055, N'وَ آتِ ذَا الْقُرْبى حَقَّهُ وَ الْمِسْكِينَ وَ ابْنَ السَّبِيلِ وَ لا تُبَذِّرْ تَبْذِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2056, N'إِنَّ الْمُبَذِّرِينَ كانُوا إِخْوانَ الشَّياطِينِ وَ كانَ الشَّيْطانُ لِرَبِّهِ كَفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2057, N'وَ إِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغاءَ رَحْمَةٍ مِنْ رَبِّكَ تَرْجُوها فَقُلْ لَهُمْ قَوْلاً مَيْسُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2058, N'وَ لا تَجْعَلْ يَدَكَ مَغْلُولَةً إِلى عُنُقِكَ وَ لا تَبْسُطْها كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُوماً مَحْسُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2059, N'إِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ وَ يَقْدِرُ إِنَّهُ كانَ بِعِبادِهِ خَبِيراً بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2060, N'وَ لا تَقْتُلُوا أَوْلادَكُمْ خَشْيَةَ إِمْلاقٍ نَحْنُ نَرْزُقُهُمْ وَ إِيَّاكُمْ إِنَّ قَتْلَهُمْ كانَ خِطْأً كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2061, N'وَ لا تَقْرَبُوا الزِّنى إِنَّهُ كانَ فاحِشَةً وَ ساءَ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2062, N'وَ لا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلاَّ بِالْحَقِّ وَ مَنْ قُتِلَ مَظْلُوماً فَقَدْ جَعَلْنا لِوَلِيِّهِ سُلْطاناً فَلا يُسْرِفْ فِي الْقَتْلِ إِنَّهُ كانَ مَنْصُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2063, N'وَ لا تَقْرَبُوا مالَ الْيَتِيمِ إِلاَّ بِالَّتِي هِيَ أَحْسَنُ حَتَّى يَبْلُغَ أَشُدَّهُ وَ أَوْفُوا بِالْعَهْدِ إِنَّ الْعَهْدَ كانَ مَسْؤُلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2064, N'وَ أَوْفُوا الْكَيْلَ إِذا كِلْتُمْ وَ زِنُوا بِالْقِسْطاسِ الْمُسْتَقِيمِ ذلِكَ خَيْرٌ وَ أَحْسَنُ تَأْوِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2065, N'وَ لا تَقْفُ ما لَيْسَ لَكَ بِهِ عِلْمٌ إِنَّ السَّمْعَ وَ الْبَصَرَ وَ الْفُؤادَ كُلُّ أُولئِكَ كانَ عَنْهُ مَسْؤُلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2066, N'وَ لا تَمْشِ فِي الْأَرْضِ مَرَحاً إِنَّكَ لَنْ تَخْرِقَ الْأَرْضَ وَ لَنْ تَبْلُغَ الْجِبالَ طُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2067, N'كُلُّ ذلِكَ كانَ سَيِّئُهُ عِنْدَ رَبِّكَ مَكْرُوهاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2068, N'ذلِكَ مِمَّا أَوْحى إِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِ وَ لا تَجْعَلْ مَعَ اللَّهِ إِلهاً آخَرَ فَتُلْقى فِي جَهَنَّمَ مَلُوماً مَدْحُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2069, N'أَ فَأَصْفاكُمْ رَبُّكُمْ بِالْبَنِينَ وَ اتَّخَذَ مِنَ الْمَلائِكَةِ إِناثاً إِنَّكُمْ لَتَقُولُونَ قَوْلاً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2070, N'وَ لَقَدْ صَرَّفْنا فِي هذَا الْقُرْآنِ لِيَذَّكَّرُوا وَ ما يَزِيدُهُمْ إِلاَّ نُفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2071, N'قُلْ لَوْ كانَ مَعَهُ آلِهَةٌ كَما يَقُولُونَ إِذاً لاَبْتَغَوْا إِلى ذِي الْعَرْشِ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2072, N'سُبْحانَهُ وَ تَعالى عَمَّا يَقُولُونَ عُلُوًّا كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2073, N'تُسَبِّحُ لَهُ السَّماواتُ السَّبْعُ وَ الْأَرْضُ وَ مَنْ فِيهِنَّ وَ إِنْ مِنْ شَيْ‏ءٍ إِلاَّ يُسَبِّحُ بِحَمْدِهِ وَ لكِنْ لا تَفْقَهُونَ تَسْبِيحَهُمْ إِنَّهُ كانَ حَلِيماً غَفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2074, N'وَ إِذا قَرَأْتَ الْقُرْآنَ جَعَلْنا بَيْنَكَ وَ بَيْنَ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ حِجاباً مَسْتُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2075, N'وَ جَعَلْنا عَلى قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَ فِي آذانِهِمْ وَقْراً وَ إِذا ذَكَرْتَ رَبَّكَ فِي الْقُرْآنِ وَحْدَهُ وَلَّوْا عَلى أَدْبارِهِمْ نُفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2076, N'نَحْنُ أَعْلَمُ بِما يَسْتَمِعُونَ بِهِ إِذْ يَسْتَمِعُونَ إِلَيْكَ وَ إِذْ هُمْ نَجْوى إِذْ يَقُولُ الظَّالِمُونَ إِنْ تَتَّبِعُونَ إِلاَّ رَجُلاً مَسْحُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2077, N'انْظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثالَ فَضَلُّوا فَلا يَسْتَطِيعُونَ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2078, N'وَ قالُوا أَ إِذا كُنَّا عِظاماً وَ رُفاتاً أَ إِنَّا لَمَبْعُوثُونَ خَلْقاً جَدِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2079, N'قُلْ كُونُوا حِجارَةً أَوْ حَدِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2080, N'أَوْ خَلْقاً مِمَّا يَكْبُرُ فِي صُدُورِكُمْ فَسَيَقُولُونَ مَنْ يُعِيدُنا قُلِ الَّذِي فَطَرَكُمْ أَوَّلَ مَرَّةٍ فَسَيُنْغِضُونَ إِلَيْكَ رُؤُسَهُمْ وَ يَقُولُونَ مَتى هُوَ قُلْ عَسى أَنْ يَكُونَ قَرِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2081, N'يَوْمَ يَدْعُوكُمْ فَتَسْتَجِيبُونَ بِحَمْدِهِ وَ تَظُنُّونَ إِنْ لَبِثْتُمْ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2082, N'وَ قُلْ لِعِبادِي يَقُولُوا الَّتِي هِيَ أَحْسَنُ إِنَّ الشَّيْطانَ يَنْزَغُ بَيْنَهُمْ إِنَّ الشَّيْطانَ كانَ لِلْإِنْسانِ عَدُوًّا مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2083, N'رَبُّكُمْ أَعْلَمُ بِكُمْ إِنْ يَشَأْ يَرْحَمْكُمْ أَوْ إِنْ يَشَأْ يُعَذِّبْكُمْ وَ ما أَرْسَلْناكَ عَلَيْهِمْ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2084, N'وَ رَبُّكَ أَعْلَمُ بِمَنْ فِي السَّماواتِ وَ الْأَرْضِ وَ لَقَدْ فَضَّلْنا بَعْضَ النَّبِيِّينَ عَلى بَعْضٍ وَ آتَيْنا داوُدَ زَبُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2085, N'قُلِ ادْعُوا الَّذِينَ زَعَمْتُمْ مِنْ دُونِهِ فَلا يَمْلِكُونَ كَشْفَ الضُّرِّ عَنْكُمْ وَ لا تَحْوِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2086, N'أُولئِكَ الَّذِينَ يَدْعُونَ يَبْتَغُونَ إِلى رَبِّهِمُ الْوَسِيلَةَ أَيُّهُمْ أَقْرَبُ وَ يَرْجُونَ رَحْمَتَهُ وَ يَخافُونَ عَذابَهُ إِنَّ عَذابَ رَبِّكَ كانَ مَحْذُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2087, N'وَ إِنْ مِنْ قَرْيَةٍ إِلاَّ نَحْنُ مُهْلِكُوها قَبْلَ يَوْمِ الْقِيامَةِ أَوْ مُعَذِّبُوها عَذاباً شَدِيداً كانَ ذلِكَ فِي الْكِتابِ مَسْطُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2088, N'وَ ما مَنَعَنا أَنْ نُرْسِلَ بِالْآياتِ إِلاَّ أَنْ كَذَّبَ بِهَا الْأَوَّلُونَ وَ آتَيْنا ثَمُودَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوا بِها وَ ما نُرْسِلُ بِالْآياتِ إِلاَّ تَخْوِيفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2089, N'وَ إِذْ قُلْنا لَكَ إِنَّ رَبَّكَ أَحاطَ بِالنَّاسِ وَ ما جَعَلْنَا الرُّؤْيَا الَّتِي أَرَيْناكَ إِلاَّ فِتْنَةً لِلنَّاسِ وَ الشَّجَرَةَ الْمَلْعُونَةَ فِي الْقُرْآنِ وَ نُخَوِّفُهُمْ فَما يَزِيدُهُمْ إِلاَّ طُغْياناً كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2090, N'وَ إِذْ قُلْنا لِلْمَلائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاَّ إِبْلِيسَ قالَ أَ أَسْجُدُ لِمَنْ خَلَقْتَ طِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2091, N'قالَ أَ رَأَيْتَكَ هذَا الَّذِي كَرَّمْتَ عَلَيَّ لَئِنْ أَخَّرْتَنِ إِلى يَوْمِ الْقِيامَةِ لَأَحْتَنِكَنَّ ذُرِّيَّتَهُ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2092, N'قالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَإِنَّ جَهَنَّمَ جَزاؤُكُمْ جَزاءً مَوْفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2093, N'وَ اسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَ أَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَ رَجِلِكَ وَ شارِكْهُمْ فِي الْأَمْوالِ وَ الْأَوْلادِ وَ عِدْهُمْ وَ ما يَعِدُهُمُ الشَّيْطانُ إِلاَّ غُرُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2094, N'إِنَّ عِبادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطانٌ وَ كَفى بِرَبِّكَ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2095, N'رَبُّكُمُ الَّذِي يُزْجِي لَكُمُ الْفُلْكَ فِي الْبَحْرِ لِتَبْتَغُوا مِنْ فَضْلِهِ إِنَّهُ كانَ بِكُمْ رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2096, N'وَ إِذا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَنْ تَدْعُونَ إِلاَّ إِيَّاهُ فَلَمَّا نَجَّاكُمْ إِلَى الْبَرِّ أَعْرَضْتُمْ وَ كانَ الْإِنْسانُ كَفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2097, N'أَ فَأَمِنْتُمْ أَنْ يَخْسِفَ بِكُمْ جانِبَ الْبَرِّ أَوْ يُرْسِلَ عَلَيْكُمْ حاصِباً ثُمَّ لا تَجِدُوا لَكُمْ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2098, N'أَمْ أَمِنْتُمْ أَنْ يُعِيدَكُمْ فِيهِ تارَةً أُخْرى فَيُرْسِلَ عَلَيْكُمْ قاصِفاً مِنَ الرِّيحِ فَيُغْرِقَكُمْ بِما كَفَرْتُمْ ثُمَّ لا تَجِدُوا لَكُمْ عَلَيْنا بِهِ تَبِيعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2099, N'وَ لَقَدْ كَرَّمْنا بَنِي آدَمَ وَ حَمَلْناهُمْ فِي الْبَرِّ وَ الْبَحْرِ وَ رَزَقْناهُمْ مِنَ الطَّيِّباتِ وَ فَضَّلْناهُمْ عَلى كَثِيرٍ مِمَّنْ خَلَقْنا تَفْضِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2100, N'يَوْمَ نَدْعُوا كُلَّ أُناسٍ بِإِمامِهِمْ فَمَنْ أُوتِيَ كِتابَهُ بِيَمِينِهِ فَأُولئِكَ يَقْرَؤُنَ كِتابَهُمْ وَ لا يُظْلَمُونَ فَتِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2101, N'وَ مَنْ كانَ فِي هذِهِ أَعْمى فَهُوَ فِي الْآخِرَةِ أَعْمى وَ أَضَلُّ سَبِيلاً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2102, N'وَ إِنْ كادُوا لَيَفْتِنُونَكَ عَنِ الَّذِي أَوْحَيْنا إِلَيْكَ لِتَفْتَرِيَ عَلَيْنا غَيْرَهُ وَ إِذاً لاَتَّخَذُوكَ خَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2103, N'وَ لَوْ لا أَنْ ثَبَّتْناكَ لَقَدْ كِدْتَ تَرْكَنُ إِلَيْهِمْ شَيْئاً قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2104, N'إِذاً لَأَذَقْناكَ ضِعْفَ الْحَياةِ وَ ضِعْفَ الْمَماتِ ثُمَّ لا تَجِدُ لَكَ عَلَيْنا نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2105, N'وَ إِنْ كادُوا لَيَسْتَفِزُّونَكَ مِنَ الْأَرْضِ لِيُخْرِجُوكَ مِنْها وَ إِذاً لا يَلْبَثُونَ خِلافَكَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2106, N'سُنَّةَ مَنْ قَدْ أَرْسَلْنا قَبْلَكَ مِنْ رُسُلِنا وَ لا تَجِدُ لِسُنَّتِنا تَحْوِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2107, N'أَقِمِ الصَّلاةَ لِدُلُوكِ الشَّمْسِ إِلى غَسَقِ اللَّيْلِ وَ قُرْآنَ الْفَجْرِ إِنَّ قُرْآنَ الْفَجْرِ كانَ مَشْهُوداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2108, N'وَ مِنَ اللَّيْلِ فَتَهَجَّدْ بِهِ نافِلَةً لَكَ عَسى أَنْ يَبْعَثَكَ رَبُّكَ مَقاماً مَحْمُوداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2109, N'وَ قُلْ رَبِّ أَدْخِلْنِي مُدْخَلَ صِدْقٍ وَ أَخْرِجْنِي مُخْرَجَ صِدْقٍ وَ اجْعَلْ لِي مِنْ لَدُنْكَ سُلْطاناً نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2110, N'وَ قُلْ جاءَ الْحَقُّ وَ زَهَقَ الْباطِلُ إِنَّ الْباطِلَ كانَ زَهُوقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2111, N'وَ نُنَزِّلُ مِنَ الْقُرْآنِ ما هُوَ شِفاءٌ وَ رَحْمَةٌ لِلْمُؤْمِنِينَ وَ لا يَزِيدُ الظَّالِمِينَ إِلاَّ خَساراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2112, N'وَ إِذا أَنْعَمْنا عَلَى الْإِنْسانِ أَعْرَضَ وَ نَأى بِجانِبِهِ وَ إِذا مَسَّهُ الشَّرُّ كانَ يَؤُساً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2113, N'قُلْ كُلٌّ يَعْمَلُ عَلى شاكِلَتِهِ فَرَبُّكُمْ أَعْلَمُ بِمَنْ هُوَ أَهْدى سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2114, N'وَ يَسْئَلُونَكَ عَنِ الرُّوحِ قُلِ الرُّوحُ مِنْ أَمْرِ رَبِّي وَ ما أُوتِيتُمْ مِنَ الْعِلْمِ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2115, N'وَ لَئِنْ شِئْنا لَنَذْهَبَنَّ بِالَّذِي أَوْحَيْنا إِلَيْكَ ثُمَّ لا تَجِدُ لَكَ بِهِ عَلَيْنا وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2116, N'إِلاَّ رَحْمَةً مِنْ رَبِّكَ إِنَّ فَضْلَهُ كانَ عَلَيْكَ كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2117, N'قُلْ لَئِنِ اجْتَمَعَتِ الْإِنْسُ وَ الْجِنُّ عَلى أَنْ يَأْتُوا بِمِثْلِ هذَا الْقُرْآنِ لا يَأْتُونَ بِمِثْلِهِ وَ لَوْ كانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2118, N'وَ لَقَدْ صَرَّفْنا لِلنَّاسِ فِي هذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ فَأَبى أَكْثَرُ النَّاسِ إِلاَّ كُفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2119, N'وَ قالُوا لَنْ نُؤْمِنَ لَكَ حَتَّى تَفْجُرَ لَنا مِنَ الْأَرْضِ يَنْبُوعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2120, N'أَوْ تَكُونَ لَكَ جَنَّةٌ مِنْ نَخِيلٍ وَ عِنَبٍ فَتُفَجِّرَ الْأَنْهارَ خِلالَها تَفْجِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2121, N'أَوْ تُسْقِطَ السَّماءَ كَما زَعَمْتَ عَلَيْنا كِسَفاً أَوْ تَأْتِيَ بِاللَّهِ وَ الْمَلائِكَةِ قَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2122, N'أَوْ يَكُونَ لَكَ بَيْتٌ مِنْ زُخْرُفٍ أَوْ تَرْقى فِي السَّماءِ وَ لَنْ نُؤْمِنَ لِرُقِيِّكَ حَتَّى تُنَزِّلَ عَلَيْنا كِتاباً نَقْرَؤُهُ قُلْ سُبْحانَ رَبِّي هَلْ كُنْتُ إِلاَّ بَشَراً رَسُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2123, N'وَ ما مَنَعَ النَّاسَ أَنْ يُؤْمِنُوا إِذْ جاءَهُمُ الْهُدى إِلاَّ أَنْ قالُوا أَ بَعَثَ اللَّهُ بَشَراً رَسُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2124, N'قُلْ لَوْ كانَ فِي الْأَرْضِ مَلائِكَةٌ يَمْشُونَ مُطْمَئِنِّينَ لَنَزَّلْنا عَلَيْهِمْ مِنَ السَّماءِ مَلَكاً رَسُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2125, N'قُلْ كَفى بِاللَّهِ شَهِيداً بَيْنِي وَ بَيْنَكُمْ إِنَّهُ كانَ بِعِبادِهِ خَبِيراً بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2126, N'وَ مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ وَ مَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُمْ أَوْلِياءَ مِنْ دُونِهِ وَ نَحْشُرُهُمْ يَوْمَ الْقِيامَةِ عَلى وُجُوهِهِمْ عُمْياً وَ بُكْماً وَ صُمًّا مَأْواهُمْ جَهَنَّمُ كُلَّما خَبَتْ زِدْناهُمْ سَعِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2127, N'ذلِكَ جَزاؤُهُمْ بِأَنَّهُمْ كَفَرُوا بِآياتِنا وَ قالُوا أَ إِذا كُنَّا عِظاماً وَ رُفاتاً أَ إِنَّا لَمَبْعُوثُونَ خَلْقاً جَدِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2128, N'أَ وَ لَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ قادِرٌ عَلى أَنْ يَخْلُقَ مِثْلَهُمْ وَ جَعَلَ لَهُمْ أَجَلاً لا رَيْبَ فِيهِ فَأَبَى الظَّالِمُونَ إِلاَّ كُفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2129, N'قُلْ لَوْ أَنْتُمْ تَمْلِكُونَ خَزائِنَ رَحْمَةِ رَبِّي إِذاً لَأَمْسَكْتُمْ خَشْيَةَ الْإِنْفاقِ وَ كانَ الْإِنْسانُ قَتُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2130, N'وَ لَقَدْ آتَيْنا مُوسى تِسْعَ آياتٍ بَيِّناتٍ فَسْئَلْ بَنِي إِسْرائِيلَ إِذْ جاءَهُمْ فَقالَ لَهُ فِرْعَوْنُ إِنِّي لَأَظُنُّكَ يا مُوسى مَسْحُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2131, N'قالَ لَقَدْ عَلِمْتَ ما أَنْزَلَ هؤُلاءِ إِلاَّ رَبُّ السَّماواتِ وَ الْأَرْضِ بَصائِرَ وَ إِنِّي لَأَظُنُّكَ يا فِرْعَوْنُ مَثْبُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2132, N'فَأَرادَ أَنْ يَسْتَفِزَّهُمْ مِنَ الْأَرْضِ فَأَغْرَقْناهُ وَ مَنْ مَعَهُ جَمِيعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2133, N'وَ قُلْنا مِنْ بَعْدِهِ لِبَنِي إِسْرائِيلَ اسْكُنُوا الْأَرْضَ فَإِذا جاءَ وَعْدُ الْآخِرَةِ جِئْنا بِكُمْ لَفِيفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2134, N'وَ بِالْحَقِّ أَنْزَلْناهُ وَ بِالْحَقِّ نَزَلَ وَ ما أَرْسَلْناكَ إِلاَّ مُبَشِّراً وَ نَذِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2135, N'وَ قُرْآناً فَرَقْناهُ لِتَقْرَأَهُ عَلَى النَّاسِ عَلى مُكْثٍ وَ نَزَّلْناهُ تَنْزِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2136, N'قُلْ آمِنُوا بِهِ أَوْ لا تُؤْمِنُوا إِنَّ الَّذِينَ أُوتُوا الْعِلْمَ مِنْ قَبْلِهِ إِذا يُتْلى عَلَيْهِمْ يَخِرُّونَ لِلْأَذْقانِ سُجَّداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2137, N'وَ يَقُولُونَ سُبْحانَ رَبِّنا إِنْ كانَ وَعْدُ رَبِّنا لَمَفْعُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2138, N'وَ يَخِرُّونَ لِلْأَذْقانِ يَبْكُونَ وَ يَزِيدُهُمْ خُشُوعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2139, N'قُلِ ادْعُوا اللَّهَ أَوِ ادْعُوا الرَّحْمنَ أَيًّا ما تَدْعُوا فَلَهُ الْأَسْماءُ الْحُسْنى وَ لا تَجْهَرْ بِصَلاتِكَ وَ لا تُخافِتْ بِها وَ ابْتَغِ بَيْنَ ذلِكَ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2140, N'وَ قُلِ الْحَمْدُ لِلَّهِ الَّذِي لَمْ يَتَّخِذْ وَلَداً وَ لَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ وَ لَمْ يَكُنْ لَهُ وَلِيٌّ مِنَ الذُّلِّ وَ كَبِّرْهُ تَكْبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2141, N'الْحَمْدُ لِلَّهِ الَّذِي أَنْزَلَ عَلى عَبْدِهِ الْكِتابَ وَ لَمْ يَجْعَلْ لَهُ عِوَجاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2142, N'قَيِّماً لِيُنْذِرَ بَأْساً شَدِيداً مِنْ لَدُنْهُ وَ يُبَشِّرَ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصَّالِحاتِ أَنَّ لَهُمْ أَجْراً حَسَناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2143, N'ماكِثِينَ فِيهِ أَبَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2144, N'وَ يُنْذِرَ الَّذِينَ قالُوا اتَّخَذَ اللَّهُ وَلَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2145, N'ما لَهُمْ بِهِ مِنْ عِلْمٍ وَ لا لِآبائِهِمْ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ أَفْواهِهِمْ إِنْ يَقُولُونَ إِلاَّ كَذِباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2146, N'فَلَعَلَّكَ باخِعٌ نَفْسَكَ عَلى آثارِهِمْ إِنْ لَمْ يُؤْمِنُوا بِهذَا الْحَدِيثِ أَسَفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2147, N'إِنَّا جَعَلْنا ما عَلَى الْأَرْضِ زِينَةً لَها لِنَبْلُوَهُمْ أَيُّهُمْ أَحْسَنُ عَمَلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2148, N'وَ إِنَّا لَجاعِلُونَ ما عَلَيْها صَعِيداً جُرُزاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2149, N'أَمْ حَسِبْتَ أَنَّ أَصْحابَ الْكَهْفِ وَ الرَّقِيمِ كانُوا مِنْ آياتِنا عَجَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2150, N'إِذْ أَوَى الْفِتْيَةُ إِلَى الْكَهْفِ فَقالُوا رَبَّنا آتِنا مِنْ لَدُنْكَ رَحْمَةً وَ هَيِّئْ لَنا مِنْ أَمْرِنا رَشَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2151, N'فَضَرَبْنا عَلَى آذانِهِمْ فِي الْكَهْفِ سِنِينَ عَدَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2152, N'ثُمَّ بَعَثْناهُمْ لِنَعْلَمَ أَيُّ الْحِزْبَيْنِ أَحْصى لِما لَبِثُوا أَمَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2153, N'نَحْنُ نَقُصُّ عَلَيْكَ نَبَأَهُمْ بِالْحَقِّ إِنَّهُمْ فِتْيَةٌ آمَنُوا بِرَبِّهِمْ وَ زِدْناهُمْ هُدىً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2154, N'وَ رَبَطْنا عَلى قُلُوبِهِمْ إِذْ قامُوا فَقالُوا رَبُّنا رَبُّ السَّماواتِ وَ الْأَرْضِ لَنْ نَدْعُوَا مِنْ دُونِهِ إِلهاً لَقَدْ قُلْنا إِذاً شَطَطاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2155, N'هؤُلاءِ قَوْمُنَا اتَّخَذُوا مِنْ دُونِهِ آلِهَةً لَوْ لا يَأْتُونَ عَلَيْهِمْ بِسُلْطانٍ بَيِّنٍ فَمَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ كَذِباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2156, N'وَ إِذِ اعْتَزَلْتُمُوهُمْ وَ ما يَعْبُدُونَ إِلاَّ اللَّهَ فَأْوُوا إِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِنْ رَحْمَتِهِ وَ يُهَيِّئْ لَكُمْ مِنْ أَمْرِكُمْ مِرفَقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2157, N'وَ تَرَى الشَّمْسَ إِذا طَلَعَتْ تَتَزاوَرُ عَنْ كَهْفِهِمْ ذاتَ الْيَمِينِ وَ إِذا غَرَبَتْ تَقْرِضُهُمْ ذاتَ الشِّمالِ وَ هُمْ فِي فَجْوَةٍ مِنْهُ ذلِكَ مِنْ آياتِ اللَّهِ مَنْ يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ وَ مَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُ وَلِيًّا مُرْشِداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2158, N'وَ تَحْسَبُهُمْ أَيْقاظاً وَ هُمْ رُقُودٌ وَ نُقَلِّبُهُمْ ذاتَ الْيَمِينِ وَ ذاتَ الشِّمالِ وَ كَلْبُهُمْ باسِطٌ ذِراعَيْهِ بِالْوَصِيدِ لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِراراً وَ لَمُلِئْتَ مِنْهُمْ رُعْباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2159, N'وَ كَذلِكَ بَعَثْناهُمْ لِيَتَسائَلُوا بَيْنَهُمْ قالَ قائِلٌ مِنْهُمْ كَمْ لَبِثْتُمْ قالُوا لَبِثْنا يَوْماً أَوْ بَعْضَ يَوْمٍ قالُوا رَبُّكُمْ أَعْلَمُ بِما لَبِثْتُمْ فَابْعَثُوا أَحَدَكُمْ بِوَرِقِكُمْ هذِهِ إِلَى الْمَدِينَةِ فَلْيَنْظُرْ أَيُّها أَزْكى طَعاماً فَلْيَأْتِكُمْ بِرِزْقٍ مِنْهُ وَ لْيَتَلَطَّفْ وَ لا يُشْعِرَنَّ بِكُمْ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2160, N'إِنَّهُمْ إِنْ يَظْهَرُوا عَلَيْكُمْ يَرْجُمُوكُمْ أَوْ يُعِيدُوكُمْ فِي مِلَّتِهِمْ وَ لَنْ تُفْلِحُوا إِذاً أَبَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2161, N'وَ كَذلِكَ أَعْثَرْنا عَلَيْهِمْ لِيَعْلَمُوا أَنَّ وَعْدَ اللَّهِ حَقٌّ وَ أَنَّ السَّاعَةَ لا رَيْبَ فِيها إِذْ يَتَنازَعُونَ بَيْنَهُمْ أَمْرَهُمْ فَقالُوا ابْنُوا عَلَيْهِمْ بُنْياناً رَبُّهُمْ أَعْلَمُ بِهِمْ قالَ الَّذِينَ غَلَبُوا عَلى أَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَسْجِداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2162, N'سَيَقُولُونَ ثَلاثَةٌ رابِعُهُمْ كَلْبُهُمْ وَ يَقُولُونَ خَمْسَةٌ سادِسُهُمْ كَلْبُهُمْ رَجْماً بِالْغَيْبِ وَ يَقُولُونَ سَبْعَةٌ وَ ثامِنُهُمْ كَلْبُهُمْ قُلْ رَبِّي أَعْلَمُ بِعِدَّتِهِمْ ما يَعْلَمُهُمْ إِلاَّ قَلِيلٌ فَلا تُمارِ فِيهِمْ إِلاَّ مِراءً ظاهِراً وَ لا تَسْتَفْتِ فِيهِمْ مِنْهُمْ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2163, N'وَ لا تَقُولَنَّ لِشَيْ‏ءٍ إِنِّي فاعِلٌ ذلِكَ غَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2164, N'إِلاَّ أَنْ يَشاءَ اللَّهُ وَ اذْكُرْ رَبَّكَ إِذا نَسِيتَ وَ قُلْ عَسى أَنْ يَهْدِيَنِ رَبِّي لِأَقْرَبَ مِنْ هذا رَشَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2165, N'وَ لَبِثُوا فِي كَهْفِهِمْ ثَلاثَ مِائَةٍ سِنِينَ وَ ازْدَادُوا تِسْعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2166, N'قُلِ اللَّهُ أَعْلَمُ بِما لَبِثُوا لَهُ غَيْبُ السَّماواتِ وَ الْأَرْضِ أَبْصِرْ بِهِ وَ أَسْمِعْ ما لَهُمْ مِنْ دُونِهِ مِنْ وَلِيٍّ وَ لا يُشْرِكُ فِي حُكْمِهِ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2167, N'وَ اتْلُ ما أُوحِيَ إِلَيْكَ مِنْ كِتابِ رَبِّكَ لا مُبَدِّلَ لِكَلِماتِهِ وَ لَنْ تَجِدَ مِنْ دُونِهِ مُلْتَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2168, N'وَ اصْبِرْ نَفْسَكَ مَعَ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَداةِ وَ الْعَشِيِّ يُرِيدُونَ وَجْهَهُ وَ لا تَعْدُ عَيْناكَ عَنْهُمْ تُرِيدُ زِينَةَ الْحَياةِ الدُّنْيا وَ لا تُطِعْ مَنْ أَغْفَلْنا قَلْبَهُ عَنْ ذِكْرِنا وَ اتَّبَعَ هَواهُ وَ كانَ أَمْرُهُ فُرُطاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2169, N'وَ قُلِ الْحَقُّ مِنْ رَبِّكُمْ فَمَنْ شاءَ فَلْيُؤْمِنْ وَ مَنْ شاءَ فَلْيَكْفُرْ إِنَّا أَعْتَدْنا لِلظَّالِمِينَ ناراً أَحاطَ بِهِمْ سُرادِقُها وَ إِنْ يَسْتَغِيثُوا يُغاثُوا بِماءٍ كَالْمُهْلِ يَشْوِي الْوُجُوهَ بِئْسَ الشَّرابُ وَ ساءَتْ مُرْتَفَقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2170, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ إِنَّا لا نُضِيعُ أَجْرَ مَنْ أَحْسَنَ عَمَلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2171, N'أُولئِكَ لَهُمْ جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهارُ يُحَلَّوْنَ فِيها مِنْ أَساوِرَ مِنْ ذَهَبٍ وَ يَلْبَسُونَ ثِياباً خُضْراً مِنْ سُنْدُسٍ وَ إِسْتَبْرَقٍ مُتَّكِئِينَ فِيها عَلَى الْأَرائِكِ نِعْمَ الثَّوابُ وَ حَسُنَتْ مُرْتَفَقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2172, N'وَ اضْرِبْ لَهُمْ مَثَلاً رَجُلَيْنِ جَعَلْنا لِأَحَدِهِما جَنَّتَيْنِ مِنْ أَعْنابٍ وَ حَفَفْناهُما بِنَخْلٍ وَ جَعَلْنا بَيْنَهُما زَرْعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2173, N'كِلْتَا الْجَنَّتَيْنِ آتَتْ أُكُلَها وَ لَمْ تَظْلِمْ مِنْهُ شَيْئاً وَ فَجَّرْنا خِلالَهُما نَهَراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2174, N'وَ كانَ لَهُ ثَمَرٌ فَقالَ لِصاحِبِهِ وَ هُوَ يُحاوِرُهُ أَنَا أَكْثَرُ مِنْكَ مالاً وَ أَعَزُّ نَفَراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2175, N'وَ دَخَلَ جَنَّتَهُ وَ هُوَ ظالِمٌ لِنَفْسِهِ قالَ ما أَظُنُّ أَنْ تَبِيدَ هذِهِ أَبَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2176, N'وَ ما أَظُنُّ السَّاعَةَ قائِمَةً وَ لَئِنْ رُدِدْتُ إِلى رَبِّي لَأَجِدَنَّ خَيْراً مِنْها مُنْقَلَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2177, N'قالَ لَهُ صاحِبُهُ وَ هُوَ يُحاوِرُهُ أَ كَفَرْتَ بِالَّذِي خَلَقَكَ مِنْ تُرابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ سَوَّاكَ رَجُلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2178, N'لكِنَّا هُوَ اللَّهُ رَبِّي وَ لا أُشْرِكُ بِرَبِّي أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2179, N'وَ لَوْ لا إِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ ما شاءَ اللَّهُ لا قُوَّةَ إِلاَّ بِاللَّهِ إِنْ تَرَنِ أَنَا أَقَلَّ مِنْكَ مالاً وَ وَلَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2180, N'فَعَسى رَبِّي أَنْ يُؤْتِيَنِ خَيْراً مِنْ جَنَّتِكَ وَ يُرْسِلَ عَلَيْها حُسْباناً مِنَ السَّماءِ فَتُصْبِحَ صَعِيداً زَلَقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2181, N'أَوْ يُصْبِحَ ماؤُها غَوْراً فَلَنْ تَسْتَطِيعَ لَهُ طَلَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2182, N'وَ أُحِيطَ بِثَمَرِهِ فَأَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلى ما أَنْفَقَ فِيها وَ هِيَ خاوِيَةٌ عَلى عُرُوشِها وَ يَقُولُ يا لَيْتَنِي لَمْ أُشْرِكْ بِرَبِّي أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2183, N'وَ لَمْ تَكُنْ لَهُ فِئَةٌ يَنْصُرُونَهُ مِنْ دُونِ اللَّهِ وَ ما كانَ مُنْتَصِراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2184, N'هُنالِكَ الْوَلايَةُ لِلَّهِ الْحَقِّ هُوَ خَيْرٌ ثَواباً وَ خَيْرٌ عُقْباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2185, N'وَ اضْرِبْ لَهُمْ مَثَلَ الْحَياةِ الدُّنْيا كَماءٍ أَنْزَلْناهُ مِنَ السَّماءِ فَاخْتَلَطَ بِهِ نَباتُ الْأَرْضِ فَأَصْبَحَ هَشِيماً تَذْرُوهُ الرِّياحُ وَ كانَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ مُقْتَدِراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2186, N'الْمالُ وَ الْبَنُونَ زِينَةُ الْحَياةِ الدُّنْيا وَ الْباقِياتُ الصَّالِحاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَواباً وَ خَيْرٌ أَمَلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2187, N'وَ يَوْمَ نُسَيِّرُ الْجِبالَ وَ تَرَى الْأَرْضَ بارِزَةً وَ حَشَرْناهُمْ فَلَمْ نُغادِرْ مِنْهُمْ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2188, N'وَ عُرِضُوا عَلى رَبِّكَ صَفًّا لَقَدْ جِئْتُمُونا كَما خَلَقْناكُمْ أَوَّلَ مَرَّةٍ بَلْ زَعَمْتُمْ أَلَّنْ نَجْعَلَ لَكُمْ مَوْعِداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2189, N'وَ وُضِعَ الْكِتابُ فَتَرَى الْمُجْرِمِينَ مُشْفِقِينَ مِمَّا فِيهِ وَ يَقُولُونَ يا وَيْلَتَنا ما لِهذَا الْكِتابِ لا يُغادِرُ صَغِيرَةً وَ لا كَبِيرَةً إِلاَّ أَحْصاها وَ وَجَدُوا ما عَمِلُوا حاضِراً وَ لا يَظْلِمُ رَبُّكَ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2190, N'وَ إِذْ قُلْنا لِلْمَلائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاَّ إِبْلِيسَ كانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ أَمْرِ رَبِّهِ أَ فَتَتَّخِذُونَهُ وَ ذُرِّيَّتَهُ أَوْلِياءَ مِنْ دُونِي وَ هُمْ لَكُمْ عَدُوٌّ بِئْسَ لِلظَّالِمِينَ بَدَلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2191, N'ما أَشْهَدْتُهُمْ خَلْقَ السَّماواتِ وَ الْأَرْضِ وَ لا خَلْقَ أَنْفُسِهِمْ وَ ما كُنْتُ مُتَّخِذَ الْمُضِلِّينَ عَضُداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2192, N'وَ يَوْمَ يَقُولُ نادُوا شُرَكائِيَ الَّذِينَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَ جَعَلْنا بَيْنَهُمْ مَوْبِقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2193, N'وَ رَأَى الْمُجْرِمُونَ النَّارَ فَظَنُّوا أَنَّهُمْ مُواقِعُوها وَ لَمْ يَجِدُوا عَنْها مَصْرِفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2194, N'وَ لَقَدْ صَرَّفْنا فِي هذَا الْقُرْآنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍ وَ كانَ الْإِنْسانُ أَكْثَرَ شَيْ‏ءٍ جَدَلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2195, N'وَ ما مَنَعَ النَّاسَ أَنْ يُؤْمِنُوا إِذْ جاءَهُمُ الْهُدى وَ يَسْتَغْفِرُوا رَبَّهُمْ إِلاَّ أَنْ تَأْتِيَهُمْ سُنَّةُ الْأَوَّلِينَ أَوْ يَأْتِيَهُمُ الْعَذابُ قُبُلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2196, N'وَ ما نُرْسِلُ الْمُرْسَلِينَ إِلاَّ مُبَشِّرِينَ وَ مُنْذِرِينَ وَ يُجادِلُ الَّذِينَ كَفَرُوا بِالْباطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ وَ اتَّخَذُوا آياتِي وَ ما أُنْذِرُوا هُزُواً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2197, N'وَ مَنْ أَظْلَمُ مِمَّنْ ذُكِّرَ بِآياتِ رَبِّهِ فَأَعْرَضَ عَنْها وَ نَسِيَ ما قَدَّمَتْ يَداهُ إِنَّا جَعَلْنا عَلى قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَ فِي آذانِهِمْ وَقْراً وَ إِنْ تَدْعُهُمْ إِلَى الْهُدى فَلَنْ يَهْتَدُوا إِذاً أَبَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2198, N'وَ رَبُّكَ الْغَفُورُ ذُو الرَّحْمَةِ لَوْ يُؤاخِذُهُمْ بِما كَسَبُوا لَعَجَّلَ لَهُمُ الْعَذابَ بَلْ لَهُمْ مَوْعِدٌ لَنْ يَجِدُوا مِنْ دُونِهِ مَوْئِلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2199, N'وَ تِلْكَ الْقُرى أَهْلَكْناهُمْ لَمَّا ظَلَمُوا وَ جَعَلْنا لِمَهْلِكِهِمْ مَوْعِداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2200, N'وَ إِذْ قالَ مُوسى لِفَتاهُ لا أَبْرَحُ حَتَّى أَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ أَوْ أَمْضِيَ حُقُباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2201, N'فَلَمَّا بَلَغا مَجْمَعَ بَيْنِهِما نَسِيا حُوتَهُما فَاتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ سَرَباً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2202, N'فَلَمَّا جاوَزا قالَ لِفَتاهُ آتِنا غَداءَنا لَقَدْ لَقِينا مِنْ سَفَرِنا هذا نَصَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2203, N'قالَ أَ رَأَيْتَ إِذْ أَوَيْنا إِلَى الصَّخْرَةِ فَإِنِّي نَسِيتُ الْحُوتَ وَ ما أَنْسانِيهُ إِلاَّ الشَّيْطانُ أَنْ أَذْكُرَهُ وَ اتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ عَجَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2204, N'قالَ ذلِكَ ما كُنَّا نَبْغِ فَارْتَدَّا عَلى آثارِهِما قَصَصاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2205, N'فَوَجَدا عَبْداً مِنْ عِبادِنا آتَيْناهُ رَحْمَةً مِنْ عِنْدِنا وَ عَلَّمْناهُ مِنْ لَدُنَّا عِلْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2206, N'قالَ لَهُ مُوسى هَلْ أَتَّبِعُكَ عَلى أَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2207, N'قالَ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2208, N'وَ كَيْفَ تَصْبِرُ عَلى ما لَمْ تُحِطْ بِهِ خُبْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2209, N'قالَ سَتَجِدُنِي إِنْ شاءَ اللَّهُ صابِراً وَ لا أَعْصِي لَكَ أَمْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2210, N'قالَ فَإِنِ اتَّبَعْتَنِي فَلا تَسْئَلْنِي عَنْ شَيْ‏ءٍ حَتَّى أُحْدِثَ لَكَ مِنْهُ ذِكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2211, N'فَانْطَلَقا حَتَّى إِذا رَكِبا فِي السَّفِينَةِ خَرَقَها قالَ أَ خَرَقْتَها لِتُغْرِقَ أَهْلَها لَقَدْ جِئْتَ شَيْئاً إِمْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2212, N'قالَ أَ لَمْ أَقُلْ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2213, N'قالَ لا تُؤاخِذْنِي بِما نَسِيتُ وَ لا تُرْهِقْنِي مِنْ أَمْرِي عُسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2214, N'فَانْطَلَقا حَتَّى إِذا لَقِيا غُلاماً فَقَتَلَهُ قالَ أَ قَتَلْتَ نَفْساً زَكِيَّةً بِغَيْرِ نَفْسٍ لَقَدْ جِئْتَ شَيْئاً نُكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2215, N'قالَ أَ لَمْ أَقُلْ لَكَ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2216, N'قالَ إِنْ سَأَلْتُكَ عَنْ شَيْ‏ءٍ بَعْدَها فَلا تُصاحِبْنِي قَدْ بَلَغْتَ مِنْ لَدُنِّي عُذْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2217, N'فَانْطَلَقا حَتَّى إِذا أَتَيا أَهْلَ قَرْيَةٍ اسْتَطْعَما أَهْلَها فَأَبَوْا أَنْ يُضَيِّفُوهُما فَوَجَدا فِيها جِداراً يُرِيدُ أَنْ يَنْقَضَّ فَأَقامَهُ قالَ لَوْ شِئْتَ لاَتَّخَذْتَ عَلَيْهِ أَجْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2218, N'قالَ هذا فِراقُ بَيْنِي وَ بَيْنِكَ سَأُنَبِّئُكَ بِتَأْوِيلِ ما لَمْ تَسْتَطِعْ عَلَيْهِ صَبْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2219, N'أَمَّا السَّفِينَةُ فَكانَتْ لِمَساكِينَ يَعْمَلُونَ فِي الْبَحْرِ فَأَرَدْتُ أَنْ أَعِيبَها وَ كانَ وَراءَهُمْ مَلِكٌ يَأْخُذُ كُلَّ سَفِينَةٍ غَصْباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2220, N'وَ أَمَّا الْغُلامُ فَكانَ أَبَواهُ مُؤْمِنَيْنِ فَخَشِينا أَنْ يُرْهِقَهُما طُغْياناً وَ كُفْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2221, N'فَأَرَدْنا أَنْ يُبْدِلَهُما رَبُّهُما خَيْراً مِنْهُ زَكاةً وَ أَقْرَبَ رُحْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2222, N'وَ أَمَّا الْجِدارُ فَكانَ لِغُلامَيْنِ يَتِيمَيْنِ فِي الْمَدِينَةِ وَ كانَ تَحْتَهُ كَنْزٌ لَهُما وَ كانَ أَبُوهُما صالِحاً فَأَرادَ رَبُّكَ أَنْ يَبْلُغا أَشُدَّهُما وَ يَسْتَخْرِجا كَنزَهُما رَحْمَةً مِنْ رَبِّكَ وَ ما فَعَلْتُهُ عَنْ أَمْرِي ذلِكَ تَأْوِيلُ ما لَمْ تَسْطِعْ عَلَيْهِ صَبْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2223, N'وَ يَسْئَلُونَكَ عَنْ ذِي الْقَرْنَيْنِ قُلْ سَأَتْلُوا عَلَيْكُمْ مِنْهُ ذِكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2224, N'إِنَّا مَكَّنَّا لَهُ فِي الْأَرْضِ وَ آتَيْناهُ مِنْ كُلِّ شَيْ‏ءٍ سَبَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2225, N'فَأَتْبَعَ سَبَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2226, N'حَتَّى إِذا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَها تَغْرُبُ فِي عَيْنٍ حَمِئَةٍ وَ وَجَدَ عِنْدَها قَوْماً قُلْنا يا ذَا الْقَرْنَيْنِ إِمَّا أَنْ تُعَذِّبَ وَ إِمَّا أَنْ تَتَّخِذَ فِيهِمْ حُسْناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2227, N'قالَ أَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهُ ثُمَّ يُرَدُّ إِلى رَبِّهِ فَيُعَذِّبُهُ عَذاباً نُكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2228, N'وَ أَمَّا مَنْ آمَنَ وَ عَمِلَ صالِحاً فَلَهُ جَزاءً الْحُسْنى وَ سَنَقُولُ لَهُ مِنْ أَمْرِنا يُسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2229, N'ثُمَّ أَتْبَعَ سَبَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2230, N'حَتَّى إِذا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَها تَطْلُعُ عَلى قَوْمٍ لَمْ نَجْعَلْ لَهُمْ مِنْ دُونِها سِتْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2231, N'كَذلِكَ وَ قَدْ أَحَطْنا بِما لَدَيْهِ خُبْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2232, N'ثُمَّ أَتْبَعَ سَبَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2233, N'حَتَّى إِذا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُونِهِما قَوْماً لا يَكادُونَ يَفْقَهُونَ قَوْلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2234, N'قالُوا يا ذَا الْقَرْنَيْنِ إِنَّ يَأْجُوجَ وَ مَأْجُوجَ مُفْسِدُونَ فِي الْأَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجاً عَلى أَنْ تَجْعَلَ بَيْنَنا وَ بَيْنَهُمْ سَدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2235, N'قالَ ما مَكَّنِّي فِيهِ رَبِّي خَيْرٌ فَأَعِينُونِي بِقُوَّةٍ أَجْعَلْ بَيْنَكُمْ وَ بَيْنَهُمْ رَدْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2236, N'آتُونِي زُبَرَ الْحَدِيدِ حَتَّى إِذا ساوى بَيْنَ الصَّدَفَيْنِ قالَ انْفُخُوا حَتَّى إِذا جَعَلَهُ ناراً قالَ آتُونِي أُفْرِغْ عَلَيْهِ قِطْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2237, N'فَمَا اسْطاعُوا أَنْ يَظْهَرُوهُ وَ مَا اسْتَطاعُوا لَهُ نَقْباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2238, N'قالَ هذا رَحْمَةٌ مِنْ رَبِّي فَإِذا جاءَ وَعْدُ رَبِّي جَعَلَهُ دَكَّاءَ وَ كانَ وَعْدُ رَبِّي حَقًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2239, N'وَ تَرَكْنا بَعْضَهُمْ يَوْمَئِذٍ يَمُوجُ فِي بَعْضٍ وَ نُفِخَ فِي الصُّورِ فَجَمَعْناهُمْ جَمْعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2240, N'وَ عَرَضْنا جَهَنَّمَ يَوْمَئِذٍ لِلْكافِرِينَ عَرْضاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2241, N'الَّذِينَ كانَتْ أَعْيُنُهُمْ فِي غِطاءٍ عَنْ ذِكْرِي وَ كانُوا لا يَسْتَطِيعُونَ سَمْعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2242, N'أَ فَحَسِبَ الَّذِينَ كَفَرُوا أَنْ يَتَّخِذُوا عِبادِي مِنْ دُونِي أَوْلِياءَ إِنَّا أَعْتَدْنا جَهَنَّمَ لِلْكافِرِينَ نُزُلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2243, N'قُلْ هَلْ نُنَبِّئُكُمْ بِالْأَخْسَرِينَ أَعْمالاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2244, N'الَّذِينَ ضَلَّ سَعْيُهُمْ فِي الْحَياةِ الدُّنْيا وَ هُمْ يَحْسَبُونَ أَنَّهُمْ يُحْسِنُونَ صُنْعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2245, N'أُولئِكَ الَّذِينَ كَفَرُوا بِآياتِ رَبِّهِمْ وَ لِقائِهِ فَحَبِطَتْ أَعْمالُهُمْ فَلا نُقِيمُ لَهُمْ يَوْمَ الْقِيامَةِ وَزْناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2246, N'ذلِكَ جَزاؤُهُمْ جَهَنَّمُ بِما كَفَرُوا وَ اتَّخَذُوا آياتِي وَ رُسُلِي هُزُواً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2247, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ كانَتْ لَهُمْ جَنَّاتُ الْفِرْدَوْسِ نُزُلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2248, N'خالِدِينَ فِيها لا يَبْغُونَ عَنْها حِوَلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2249, N'قُلْ لَوْ كانَ الْبَحْرُ مِداداً لِكَلِماتِ رَبِّي لَنَفِدَ الْبَحْرُ قَبْلَ أَنْ تَنْفَدَ كَلِماتُ رَبِّي وَ لَوْ جِئْنا بِمِثْلِهِ مَدَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2250, N'قُلْ إِنَّما أَنَا بَشَرٌ مِثْلُكُمْ يُوحى إِلَيَّ أَنَّما إِلهُكُمْ إِلهٌ واحِدٌ فَمَنْ كانَ يَرْجُوا لِقاءَ رَبِّهِ فَلْيَعْمَلْ عَمَلاً صالِحاً وَ لا يُشْرِكْ بِعِبادَةِ رَبِّهِ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2251, N'كهيعص ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2252, N'ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهُ زَكَرِيَّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2253, N'إِذْ نادى رَبَّهُ نِداءً خَفِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2254, N'قالَ رَبِّ إِنِّي وَهَنَ الْعَظْمُ مِنِّي وَ اشْتَعَلَ الرَّأْسُ شَيْباً وَ لَمْ أَكُنْ بِدُعائِكَ رَبِّ شَقِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2255, N'وَ إِنِّي خِفْتُ الْمَوالِيَ مِنْ وَرائِي وَ كانَتِ امْرَأَتِي عاقِراً فَهَبْ لِي مِنْ لَدُنْكَ وَلِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2256, N'يَرِثُنِي وَ يَرِثُ مِنْ آلِ يَعْقُوبَ وَ اجْعَلْهُ رَبِّ رَضِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2257, N'يا زَكَرِيَّا إِنَّا نُبَشِّرُكَ بِغُلامٍ اسْمُهُ يَحْيى لَمْ نَجْعَلْ لَهُ مِنْ قَبْلُ سَمِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2258, N'قالَ رَبِّ أَنَّى يَكُونُ لِي غُلامٌ وَ كانَتِ امْرَأَتِي عاقِراً وَ قَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2259, N'قالَ كَذلِكَ قالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَ قَدْ خَلَقْتُكَ مِنْ قَبْلُ وَ لَمْ تَكُ شَيْئاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2260, N'قالَ رَبِّ اجْعَلْ لِي آيَةً قالَ آيَتُكَ أَلاَّ تُكَلِّمَ النَّاسَ ثَلاثَ لَيالٍ سَوِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2261, N'فَخَرَجَ عَلى قَوْمِهِ مِنَ الْمِحْرابِ فَأَوْحى إِلَيْهِمْ أَنْ سَبِّحُوا بُكْرَةً وَ عَشِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2262, N'يا يَحْيى خُذِ الْكِتابَ بِقُوَّةٍ وَ آتَيْناهُ الْحُكْمَ صَبِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2263, N'وَ حَناناً مِنْ لَدُنَّا وَ زَكاةً وَ كانَ تَقِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2264, N'وَ بَرًّا بِوالِدَيْهِ وَ لَمْ يَكُنْ جَبَّاراً عَصِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2265, N'وَ سَلامٌ عَلَيْهِ يَوْمَ وُلِدَ وَ يَوْمَ يَمُوتُ وَ يَوْمَ يُبْعَثُ حَيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2266, N'وَ اذْكُرْ فِي الْكِتابِ مَرْيَمَ إِذِ انْتَبَذَتْ مِنْ أَهْلِها مَكاناً شَرْقِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2267, N'فَاتَّخَذَتْ مِنْ دُونِهِمْ حِجاباً فَأَرْسَلْنا إِلَيْها رُوحَنا فَتَمَثَّلَ لَها بَشَراً سَوِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2268, N'قالَتْ إِنِّي أَعُوذُ بِالرَّحْمنِ مِنْكَ إِنْ كُنْتَ تَقِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2269, N'قالَ إِنَّما أَنَا رَسُولُ رَبِّكِ لِأَهَبَ لَكِ غُلاماً زَكِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2270, N'قالَتْ أَنَّى يَكُونُ لِي غُلامٌ وَ لَمْ يَمْسَسْنِي بَشَرٌ وَ لَمْ أَكُ بَغِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2271, N'قالَ كَذلِكِ قالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌ وَ لِنَجْعَلَهُ آيَةً لِلنَّاسِ وَ رَحْمَةً مِنَّا وَ كانَ أَمْراً مَقْضِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2272, N'فَحَمَلَتْهُ فَانْتَبَذَتْ بِهِ مَكاناً قَصِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2273, N'فَأَجاءَهَا الْمَخاضُ إِلى جِذْعِ النَّخْلَةِ قالَتْ يا لَيْتَنِي مِتُّ قَبْلَ هذا وَ كُنْتُ نَسْياً مَنْسِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2274, N'فَناداها مِنْ تَحْتِها أَلاَّ تَحْزَنِي قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2275, N'وَ هُزِّي إِلَيْكِ بِجِذْعِ النَّخْلَةِ تُساقِطْ عَلَيْكِ رُطَباً جَنِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2276, N'فَكُلِي وَ اشْرَبِي وَ قَرِّي عَيْناً فَإِمَّا تَرَيِنَّ مِنَ الْبَشَرِ أَحَداً فَقُولِي إِنِّي نَذَرْتُ لِلرَّحْمنِ صَوْماً فَلَنْ أُكَلِّمَ الْيَوْمَ إِنْسِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2277, N'فَأَتَتْ بِهِ قَوْمَها تَحْمِلُهُ قالُوا يا مَرْيَمُ لَقَدْ جِئْتِ شَيْئاً فَرِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2278, N'يا أُخْتَ هارُونَ ما كانَ أَبُوكِ امْرَأَ سَوْءٍ وَ ما كانَتْ أُمُّكِ بَغِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2279, N'فَأَشارَتْ إِلَيْهِ قالُوا كَيْفَ نُكَلِّمُ مَنْ كانَ فِي الْمَهْدِ صَبِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2280, N'قالَ إِنِّي عَبْدُ اللَّهِ آتانِيَ الْكِتابَ وَ جَعَلَنِي نَبِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2281, N'وَ جَعَلَنِي مُبارَكاً أَيْنَ ما كُنْتُ وَ أَوْصانِي بِالصَّلاةِ وَ الزَّكاةِ ما دُمْتُ حَيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2282, N'وَ بَرًّا بِوالِدَتِي وَ لَمْ يَجْعَلْنِي جَبَّاراً شَقِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2283, N'وَ السَّلامُ عَلَيَّ يَوْمَ وُلِدْتُ وَ يَوْمَ أَمُوتُ وَ يَوْمَ أُبْعَثُ حَيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2284, N'ذلِكَ عِيسَى ابْنُ مَرْيَمَ قَوْلَ الْحَقِّ الَّذِي فِيهِ يَمْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2285, N'ما كانَ لِلَّهِ أَنْ يَتَّخِذَ مِنْ وَلَدٍ سُبْحانَهُ إِذا قَضى أَمْراً فَإِنَّما يَقُولُ لَهُ كُنْ فَيَكُونُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2286, N'وَ إِنَّ اللَّهَ رَبِّي وَ رَبُّكُمْ فَاعْبُدُوهُ هذا صِراطٌ مُسْتَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2287, N'فَاخْتَلَفَ الْأَحْزابُ مِنْ بَيْنِهِمْ فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنْ مَشْهَدِ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2288, N'أَسْمِعْ بِهِمْ وَ أَبْصِرْ يَوْمَ يَأْتُونَنا لكِنِ الظَّالِمُونَ الْيَوْمَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2289, N'وَ أَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ إِذْ قُضِيَ الْأَمْرُ وَ هُمْ فِي غَفْلَةٍ وَ هُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2290, N'إِنَّا نَحْنُ نَرِثُ الْأَرْضَ وَ مَنْ عَلَيْها وَ إِلَيْنا يُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2291, N'وَ اذْكُرْ فِي الْكِتابِ إِبْراهِيمَ إِنَّهُ كانَ صِدِّيقاً نَبِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2292, N'إِذْ قالَ لِأَبِيهِ يا أَبَتِ لِمَ تَعْبُدُ ما لا يَسْمَعُ وَ لا يُبْصِرُ وَ لا يُغْنِي عَنْكَ شَيْئاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2293, N'يا أَبَتِ إِنِّي قَدْ جاءَنِي مِنَ الْعِلْمِ ما لَمْ يَأْتِكَ فَاتَّبِعْنِي أَهْدِكَ صِراطاً سَوِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2294, N'يا أَبَتِ لا تَعْبُدِ الشَّيْطانَ إِنَّ الشَّيْطانَ كانَ لِلرَّحْمنِ عَصِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2295, N'يا أَبَتِ إِنِّي أَخافُ أَنْ يَمَسَّكَ عَذابٌ مِنَ الرَّحْمنِ فَتَكُونَ لِلشَّيْطانِ وَلِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2296, N'قالَ أَ راغِبٌ أَنْتَ عَنْ آلِهَتِي يا إِبْراهِيمُ لَئِنْ لَمْ تَنْتَهِ لَأَرْجُمَنَّكَ وَ اهْجُرْنِي مَلِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2297, N'قالَ سَلامٌ عَلَيْكَ سَأَسْتَغْفِرُ لَكَ رَبِّي إِنَّهُ كانَ بِي حَفِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2298, N'وَ أَعْتَزِلُكُمْ وَ ما تَدْعُونَ مِنْ دُونِ اللَّهِ وَ أَدْعُوا رَبِّي عَسى أَلاَّ أَكُونَ بِدُعاءِ رَبِّي شَقِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2299, N'فَلَمَّا اعْتَزَلَهُمْ وَ ما يَعْبُدُونَ مِنْ دُونِ اللَّهِ وَهَبْنا لَهُ إِسْحاقَ وَ يَعْقُوبَ وَ كُلاًّ جَعَلْنا نَبِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2300, N'وَ وَهَبْنا لَهُمْ مِنْ رَحْمَتِنا وَ جَعَلْنا لَهُمْ لِسانَ صِدْقٍ عَلِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2301, N'وَ اذْكُرْ فِي الْكِتابِ مُوسى إِنَّهُ كانَ مُخْلَصاً وَ كانَ رَسُولاً نَبِيًّا ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2302, N'وَ نادَيْناهُ مِنْ جانِبِ الطُّورِ الْأَيْمَنِ وَ قَرَّبْناهُ نَجِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2303, N'وَ وَهَبْنا لَهُ مِنْ رَحْمَتِنا أَخاهُ هارُونَ نَبِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2304, N'وَ اذْكُرْ فِي الْكِتابِ إِسْماعِيلَ إِنَّهُ كانَ صادِقَ الْوَعْدِ وَ كانَ رَسُولاً نَبِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2305, N'وَ كانَ يَأْمُرُ أَهْلَهُ بِالصَّلاةِ وَ الزَّكاةِ وَ كانَ عِنْدَ رَبِّهِ مَرْضِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2306, N'وَ اذْكُرْ فِي الْكِتابِ إِدْرِيسَ إِنَّهُ كانَ صِدِّيقاً نَبِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2307, N'وَ رَفَعْناهُ مَكاناً عَلِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2308, N'أُولئِكَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِمْ مِنَ النَّبِيِّينَ مِنْ ذُرِّيَّةِ آدَمَ وَ مِمَّنْ حَمَلْنا مَعَ نُوحٍ وَ مِنْ ذُرِّيَّةِ إِبْراهِيمَ وَ إِسْرائِيلَ وَ مِمَّنْ هَدَيْنا وَ اجْتَبَيْنا إِذا تُتْلى عَلَيْهِمْ آياتُ الرَّحْمنِ خَرُّوا سُجَّداً وَ بُكِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2309, N'فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ أَضاعُوا الصَّلاةَ وَ اتَّبَعُوا الشَّهَواتِ فَسَوْفَ يَلْقَوْنَ غَيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2310, N'إِلاَّ مَنْ تابَ وَ آمَنَ وَ عَمِلَ صالِحاً فَأُولئِكَ يَدْخُلُونَ الْجَنَّةَ وَ لا يُظْلَمُونَ شَيْئاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2311, N'جَنَّاتِ عَدْنٍ الَّتِي وَعَدَ الرَّحْمنُ عِبادَهُ بِالْغَيْبِ إِنَّهُ كانَ وَعْدُهُ مَأْتِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2312, N'لا يَسْمَعُونَ فِيها لَغْواً إِلاَّ سَلاماً وَ لَهُمْ رِزْقُهُمْ فِيها بُكْرَةً وَ عَشِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2313, N'تِلْكَ الْجَنَّةُ الَّتِي نُورِثُ مِنْ عِبادِنا مَنْ كانَ تَقِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2314, N'وَ ما نَتَنَزَّلُ إِلاَّ بِأَمْرِ رَبِّكَ لَهُ ما بَيْنَ أَيْدِينا وَ ما خَلْفَنا وَ ما بَيْنَ ذلِكَ وَ ما كانَ رَبُّكَ نَسِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2315, N'رَبُّ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُما فَاعْبُدْهُ وَ اصْطَبِرْ لِعِبادَتِهِ هَلْ تَعْلَمُ لَهُ سَمِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2316, N'وَ يَقُولُ الْإِنْسانُ أَ إِذا ما مِتُّ لَسَوْفَ أُخْرَجُ حَيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2317, N'أَ وَ لا يَذْكُرُ الْإِنْسانُ أَنَّا خَلَقْناهُ مِنْ قَبْلُ وَ لَمْ يَكُ شَيْئاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2318, N'فَوَ رَبِّكَ لَنَحْشُرَنَّهُمْ وَ الشَّياطِينَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2319, N'ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ شِيعَةٍ أَيُّهُمْ أَشَدُّ عَلَى الرَّحْمنِ عِتِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2320, N'ثُمَّ لَنَحْنُ أَعْلَمُ بِالَّذِينَ هُمْ أَوْلى بِها صِلِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2321, N'وَ إِنْ مِنْكُمْ إِلاَّ وارِدُها كانَ عَلى رَبِّكَ حَتْماً مَقْضِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2322, N'ثُمَّ نُنَجِّي الَّذِينَ اتَّقَوْا وَ نَذَرُ الظَّالِمِينَ فِيها جِثِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2323, N'وَ إِذا تُتْلى عَلَيْهِمْ آياتُنا بَيِّناتٍ قالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَيُّ الْفَرِيقَيْنِ خَيْرٌ مَقاماً وَ أَحْسَنُ نَدِيًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2324, N'وَ كَمْ أَهْلَكْنا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ أَحْسَنُ أَثاثاً وَ رِءْياً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2325, N'قُلْ مَنْ كانَ فِي الضَّلالَةِ فَلْيَمْدُدْ لَهُ الرَّحْمنُ مَدًّا حَتَّى إِذا رَأَوْا ما يُوعَدُونَ إِمَّا الْعَذابَ وَ إِمَّا السَّاعَةَ فَسَيَعْلَمُونَ مَنْ هُوَ شَرٌّ مَكاناً وَ أَضْعَفُ جُنْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2326, N'وَ يَزِيدُ اللَّهُ الَّذِينَ اهْتَدَوْا هُدىً وَ الْباقِياتُ الصَّالِحاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَواباً وَ خَيْرٌ مَرَدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2327, N'أَ فَرَأَيْتَ الَّذِي كَفَرَ بِآياتِنا وَ قالَ لَأُوتَيَنَّ مالاً وَ وَلَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2328, N'أَطَّلَعَ الْغَيْبَ أَمِ اتَّخَذَ عِنْدَ الرَّحْمنِ عَهْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2329, N'كَلاَّ سَنَكْتُبُ ما يَقُولُ وَ نَمُدُّ لَهُ مِنَ الْعَذابِ مَدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2330, N'وَ نَرِثُهُ ما يَقُولُ وَ يَأْتِينا فَرْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2331, N'وَ اتَّخَذُوا مِنْ دُونِ اللَّهِ آلِهَةً لِيَكُونُوا لَهُمْ عِزًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2332, N'كَلاَّ سَيَكْفُرُونَ بِعِبادَتِهِمْ وَ يَكُونُونَ عَلَيْهِمْ ضِدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2333, N'أَ لَمْ تَرَ أَنَّا أَرْسَلْنَا الشَّياطِينَ عَلَى الْكافِرِينَ تَؤُزُّهُمْ أَزًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2334, N'فَلا تَعْجَلْ عَلَيْهِمْ إِنَّما نَعُدُّ لَهُمْ عَدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2335, N'يَوْمَ نَحْشُرُ الْمُتَّقِينَ إِلَى الرَّحْمنِ وَفْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2336, N'وَ نَسُوقُ الْمُجْرِمِينَ إِلى جَهَنَّمَ وِرْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2337, N'لا يَمْلِكُونَ الشَّفاعَةَ إِلاَّ مَنِ اتَّخَذَ عِنْدَ الرَّحْمنِ عَهْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2338, N'وَ قالُوا اتَّخَذَ الرَّحْمنُ وَلَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2339, N'لَقَدْ جِئْتُمْ شَيْئاً إِدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2340, N'تَكادُ السَّماواتُ يَتَفَطَّرْنَ مِنْهُ وَ تَنْشَقُّ الْأَرْضُ وَ تَخِرُّ الْجِبالُ هَدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2341, N'أَنْ دَعَوْا لِلرَّحْمنِ وَلَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2342, N'وَ ما يَنْبَغِي لِلرَّحْمنِ أَنْ يَتَّخِذَ وَلَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2343, N'إِنْ كُلُّ مَنْ فِي السَّماواتِ وَ الْأَرْضِ إِلاَّ آتِي الرَّحْمنِ عَبْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2344, N'لَقَدْ أَحْصاهُمْ وَ عَدَّهُمْ عَدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2345, N'وَ كُلُّهُمْ آتِيهِ يَوْمَ الْقِيامَةِ فَرْداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2346, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ سَيَجْعَلُ لَهُمُ الرَّحْمنُ وُدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2347, N'فَإِنَّما يَسَّرْناهُ بِلِسانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِينَ وَ تُنْذِرَ بِهِ قَوْماً لُدًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2348, N'وَ كَمْ أَهْلَكْنا قَبْلَهُمْ مِنْ قَرْنٍ هَلْ تُحِسُّ مِنْهُمْ مِنْ أَحَدٍ أَوْ تَسْمَعُ لَهُمْ رِكْزاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2349, N'طه ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2350, N'ما أَنْزَلْنا عَلَيْكَ الْقُرْآنَ لِتَشْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2351, N'إِلاَّ تَذْكِرَةً لِمَنْ يَخْشى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2352, N'تَنْزِيلاً مِمَّنْ خَلَقَ الْأَرْضَ وَ السَّماواتِ الْعُلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2353, N'الرَّحْمنُ عَلَى الْعَرْشِ اسْتَوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2354, N'لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ ما بَيْنَهُما وَ ما تَحْتَ الثَّرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2355, N'وَ إِنْ تَجْهَرْ بِالْقَوْلِ فَإِنَّهُ يَعْلَمُ السِّرَّ وَ أَخْفى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2356, N'اللَّهُ لا إِلهَ إِلاَّ هُوَ لَهُ الْأَسْماءُ الْحُسْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2357, N'وَ هَلْ أَتاكَ حَدِيثُ مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2358, N'إِذْ رَأى ناراً فَقالَ لِأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ ناراً لَعَلِّي آتِيكُمْ مِنْها بِقَبَسٍ أَوْ أَجِدُ عَلَى النَّارِ هُدىً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2359, N'فَلَمَّا أَتاها نُودِيَ يا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2360, N'إِنِّي أَنَا رَبُّكَ فَاخْلَعْ نَعْلَيْكَ إِنَّكَ بِالْوادِ الْمُقَدَّسِ طُوىً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2361, N'وَ أَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِما يُوحى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2362, N'إِنَّنِي أَنَا اللَّهُ لا إِلهَ إِلاَّ أَنَا فَاعْبُدْنِي وَ أَقِمِ الصَّلاةَ لِذِكْرِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2363, N'إِنَّ السَّاعَةَ آتِيَةٌ أَكادُ أُخْفِيها لِتُجْزى كُلُّ نَفْسٍ بِما تَسْعى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2364, N'فَلا يَصُدَّنَّكَ عَنْها مَنْ لا يُؤْمِنُ بِها وَ اتَّبَعَ هَواهُ فَتَرْدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2365, N'وَ ما تِلْكَ بِيَمِينِكَ يا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2366, N'قالَ هِيَ عَصايَ أَتَوَكَّؤُا عَلَيْها وَ أَهُشُّ بِها عَلى غَنَمِي وَ لِيَ فِيها مَآرِبُ أُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2367, N'قالَ أَلْقِها يا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2368, N'فَأَلْقاها فَإِذا هِيَ حَيَّةٌ تَسْعى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2369, N'قالَ خُذْها وَ لا تَخَفْ سَنُعِيدُها سِيرَتَهَا الْأُولى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2370, N'وَ اضْمُمْ يَدَكَ إِلى جَناحِكَ تَخْرُجْ بَيْضاءَ مِنْ غَيْرِ سُوءٍ آيَةً أُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2371, N'لِنُرِيَكَ مِنْ آياتِنَا الْكُبْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2372, N'اذْهَبْ إِلى فِرْعَوْنَ إِنَّهُ طَغى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2373, N'قالَ رَبِّ اشْرَحْ لِي صَدْرِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2374, N'وَ يَسِّرْ لِي أَمْرِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2375, N'وَ احْلُلْ عُقْدَةً مِنْ لِسانِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2376, N'يَفْقَهُوا قَوْلِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2377, N'وَ اجْعَلْ لِي وَزِيراً مِنْ أَهْلِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2378, N'هارُونَ أَخِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2379, N'اشْدُدْ بِهِ أَزْرِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2380, N'وَ أَشْرِكْهُ فِي أَمْرِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2381, N'كَيْ نُسَبِّحَكَ كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2382, N'وَ نَذْكُرَكَ كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2383, N'إِنَّكَ كُنْتَ بِنا بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2384, N'قالَ قَدْ أُوتِيتَ سُؤْلَكَ يا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2385, N'وَ لَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً أُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2386, N'إِذْ أَوْحَيْنا إِلى أُمِّكَ ما يُوحى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2387, N'أَنِ اقْذِفِيهِ فِي التَّابُوتِ فَاقْذِفِيهِ فِي الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِي وَ عَدُوٌّ لَهُ وَ أَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنِّي وَ لِتُصْنَعَ عَلى عَيْنِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2388, N'إِذْ تَمْشِي أُخْتُكَ فَتَقُولُ هَلْ أَدُلُّكُمْ عَلى مَنْ يَكْفُلُهُ فَرَجَعْناكَ إِلى أُمِّكَ كَيْ تَقَرَّ عَيْنُها وَ لا تَحْزَنَ وَ قَتَلْتَ نَفْساً فَنَجَّيْناكَ مِنَ الْغَمِّ وَ فَتَنَّاكَ فُتُوناً فَلَبِثْتَ سِنِينَ فِي أَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلى قَدَرٍ يا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2389, N'وَ اصْطَنَعْتُكَ لِنَفْسِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2390, N'اذْهَبْ أَنْتَ وَ أَخُوكَ بِآياتِي وَ لا تَنِيا فِي ذِكْرِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2391, N'اذْهَبا إِلى فِرْعَوْنَ إِنَّهُ طَغى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2392, N'فَقُولا لَهُ قَوْلاً لَيِّناً لَعَلَّهُ يَتَذَكَّرُ أَوْ يَخْشى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2393, N'قالا رَبَّنا إِنَّنا نَخافُ أَنْ يَفْرُطَ عَلَيْنا أَوْ أَنْ يَطْغى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2394, N'قالَ لا تَخافا إِنَّنِي مَعَكُما أَسْمَعُ وَ أَرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2395, N'فَأْتِياهُ فَقُولا إِنَّا رَسُولا رَبِّكَ فَأَرْسِلْ مَعَنا بَنِي إِسْرائِيلَ وَ لا تُعَذِّبْهُمْ قَدْ جِئْناكَ بِآيَةٍ مِنْ رَبِّكَ وَ السَّلامُ عَلى مَنِ اتَّبَعَ الْهُدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2396, N'إِنَّا قَدْ أُوحِيَ إِلَيْنا أَنَّ الْعَذابَ عَلى مَنْ كَذَّبَ وَ تَوَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2397, N'قالَ فَمَنْ رَبُّكُما يا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2398, N'قالَ رَبُّنَا الَّذِي أَعْطى كُلَّ شَيْ‏ءٍ خَلْقَهُ ثُمَّ هَدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2399, N'قالَ فَما بالُ الْقُرُونِ الْأُولى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2400, N'قالَ عِلْمُها عِنْدَ رَبِّي فِي كِتابٍ لا يَضِلُّ رَبِّي وَ لا يَنْسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2401, N'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ مَهْداً وَ سَلَكَ لَكُمْ فِيها سُبُلاً وَ أَنْزَلَ مِنَ السَّماءِ ماءً فَأَخْرَجْنا بِهِ أَزْواجاً مِنْ نَباتٍ شَتَّى ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2402, N'كُلُوا وَ ارْعَوْا أَنْعامَكُمْ إِنَّ فِي ذلِكَ لَآياتٍ لِأُولِي النُّهى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2403, N'مِنْها خَلَقْناكُمْ وَ فِيها نُعِيدُكُمْ وَ مِنْها نُخْرِجُكُمْ تارَةً أُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2404, N'وَ لَقَدْ أَرَيْناهُ آياتِنا كُلَّها فَكَذَّبَ وَ أَبى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2405, N'قالَ أَ جِئْتَنا لِتُخْرِجَنا مِنْ أَرْضِنا بِسِحْرِكَ يا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2406, N'فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِثْلِهِ فَاجْعَلْ بَيْنَنا وَ بَيْنَكَ مَوْعِداً لا نُخْلِفُهُ نَحْنُ وَ لا أَنْتَ مَكاناً سُوىً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2407, N'قالَ مَوْعِدُكُمْ يَوْمُ الزِّينَةِ وَ أَنْ يُحْشَرَ النَّاسُ ضُحًى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2408, N'فَتَوَلَّى فِرْعَوْنُ فَجَمَعَ كَيْدَهُ ثُمَّ أَتى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2409, N'قالَ لَهُمْ مُوسى وَيْلَكُمْ لا تَفْتَرُوا عَلَى اللَّهِ كَذِباً فَيُسْحِتَكُمْ بِعَذابٍ وَ قَدْ خابَ مَنِ افْتَرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2410, N'فَتَنازَعُوا أَمْرَهُمْ بَيْنَهُمْ وَ أَسَرُّوا النَّجْوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2411, N'قالُوا إِنْ هذانِ لَساحِرانِ يُرِيدانِ أَنْ يُخْرِجاكُمْ مِنْ أَرْضِكُمْ بِسِحْرِهِما وَ يَذْهَبا بِطَرِيقَتِكُمُ الْمُثْلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2412, N'فَأَجْمِعُوا كَيْدَكُمْ ثُمَّ ائْتُوا صَفًّا وَ قَدْ أَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2413, N'قالُوا يا مُوسى إِمَّا أَنْ تُلْقِيَ وَ إِمَّا أَنْ نَكُونَ أَوَّلَ مَنْ أَلْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2414, N'قالَ بَلْ أَلْقُوا فَإِذا حِبالُهُمْ وَ عِصِيُّهُمْ يُخَيَّلُ إِلَيْهِ مِنْ سِحْرِهِمْ أَنَّها تَسْعى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2415, N'فَأَوْجَسَ فِي نَفْسِهِ خِيفَةً مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2416, N'قُلْنا لا تَخَفْ إِنَّكَ أَنْتَ الْأَعْلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2417, N'وَ أَلْقِ ما فِي يَمِينِكَ تَلْقَفْ ما صَنَعُوا إِنَّما صَنَعُوا كَيْدُ ساحِرٍ وَ لا يُفْلِحُ السَّاحِرُ حَيْثُ أَتى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2418, N'فَأُلْقِيَ السَّحَرَةُ سُجَّداً قالُوا آمَنَّا بِرَبِّ هارُونَ وَ مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2419, N'قالَ آمَنْتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ فَلَأُقَطِّعَنَّ أَيْدِيَكُمْ وَ أَرْجُلَكُمْ مِنْ خِلافٍ وَ لَأُصَلِّبَنَّكُمْ فِي جُذُوعِ النَّخْلِ وَ لَتَعْلَمُنَّ أَيُّنا أَشَدُّ عَذاباً وَ أَبْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2420, N'قالُوا لَنْ نُؤْثِرَكَ عَلى ما جاءَنا مِنَ الْبَيِّناتِ وَ الَّذِي فَطَرَنا فَاقْضِ ما أَنْتَ قاضٍ إِنَّما تَقْضِي هذِهِ الْحَياةَ الدُّنْيا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2421, N'إِنَّا آمَنَّا بِرَبِّنا لِيَغْفِرَ لَنا خَطايانا وَ ما أَكْرَهْتَنا عَلَيْهِ مِنَ السِّحْرِ وَ اللَّهُ خَيْرٌ وَ أَبْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2422, N'إِنَّهُ مَنْ يَأْتِ رَبَّهُ مُجْرِماً فَإِنَّ لَهُ جَهَنَّمَ لا يَمُوتُ فِيها وَ لا يَحْيى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2423, N'وَ مَنْ يَأْتِهِ مُؤْمِناً قَدْ عَمِلَ الصَّالِحاتِ فَأُولئِكَ لَهُمُ الدَّرَجاتُ الْعُلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2424, N'جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها وَ ذلِكَ جَزاءُ مَنْ تَزَكَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2425, N'وَ لَقَدْ أَوْحَيْنا إِلى مُوسى أَنْ أَسْرِ بِعِبادِي فَاضْرِبْ لَهُمْ طَرِيقاً فِي الْبَحْرِ يَبَساً لا تَخافُ دَرَكاً وَ لا تَخْشى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2426, N'فَأَتْبَعَهُمْ فِرْعَوْنُ بِجُنُودِهِ فَغَشِيَهُمْ مِنَ الْيَمِّ ما غَشِيَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2427, N'وَ أَضَلَّ فِرْعَوْنُ قَوْمَهُ وَ ما هَدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2428, N'يا بَنِي إِسْرائِيلَ قَدْ أَنْجَيْناكُمْ مِنْ عَدُوِّكُمْ وَ واعَدْناكُمْ جانِبَ الطُّورِ الْأَيْمَنَ وَ نَزَّلْنا عَلَيْكُمُ الْمَنَّ وَ السَّلْوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2429, N'كُلُوا مِنْ طَيِّباتِ ما رَزَقْناكُمْ وَ لا تَطْغَوْا فِيهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِي وَ مَنْ يَحْلِلْ عَلَيْهِ غَضَبِي فَقَدْ هَوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2430, N'وَ إِنِّي لَغَفَّارٌ لِمَنْ تابَ وَ آمَنَ وَ عَمِلَ صالِحاً ثُمَّ اهْتَدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2431, N'وَ ما أَعْجَلَكَ عَنْ قَوْمِكَ يا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2432, N'قالَ هُمْ أُولاءِ عَلى أَثَرِي وَ عَجِلْتُ إِلَيْكَ رَبِّ لِتَرْضى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2433, N'قالَ فَإِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْ بَعْدِكَ وَ أَضَلَّهُمُ السَّامِرِيُّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2434, N'فَرَجَعَ مُوسى إِلى قَوْمِهِ غَضْبانَ أَسِفاً قالَ يا قَوْمِ أَ لَمْ يَعِدْكُمْ رَبُّكُمْ وَعْداً حَسَناً أَ فَطالَ عَلَيْكُمُ الْعَهْدُ أَمْ أَرَدْتُمْ أَنْ يَحِلَّ عَلَيْكُمْ غَضَبٌ مِنْ رَبِّكُمْ فَأَخْلَفْتُمْ مَوْعِدِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2435, N'قالُوا ما أَخْلَفْنا مَوْعِدَكَ بِمَلْكِنا وَ لكِنَّا حُمِّلْنا أَوْزاراً مِنْ زِينَةِ الْقَوْمِ فَقَذَفْناها فَكَذلِكَ أَلْقَى السَّامِرِيُّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2436, N'فَأَخْرَجَ لَهُمْ عِجْلاً جَسَداً لَهُ خُوارٌ فَقالُوا هذا إِلهُكُمْ وَ إِلهُ مُوسى فَنَسِيَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2437, N'أَ فَلا يَرَوْنَ أَلاَّ يَرْجِعُ إِلَيْهِمْ قَوْلاً وَ لا يَمْلِكُ لَهُمْ ضَرًّا وَ لا نَفْعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2438, N'وَ لَقَدْ قالَ لَهُمْ هارُونُ مِنْ قَبْلُ يا قَوْمِ إِنَّما فُتِنْتُمْ بِهِ وَ إِنَّ رَبَّكُمُ الرَّحْمنُ فَاتَّبِعُونِي وَ أَطِيعُوا أَمْرِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2439, N'قالُوا لَنْ نَبْرَحَ عَلَيْهِ عاكِفِينَ حَتَّى يَرْجِعَ إِلَيْنا مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2440, N'قالَ يا هارُونُ ما مَنَعَكَ إِذْ رَأَيْتَهُمْ ضَلُّوا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2441, N'أَلاَّ تَتَّبِعَنِ أَ فَعَصَيْتَ أَمْرِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2442, N'قالَ يَا بْنَ أُمَّ لا تَأْخُذْ بِلِحْيَتِي وَ لا بِرَأْسِي إِنِّي خَشِيتُ أَنْ تَقُولَ فَرَّقْتَ بَيْنَ بَنِي إِسْرائِيلَ وَ لَمْ تَرْقُبْ قَوْلِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2443, N'قالَ فَما خَطْبُكَ يا سامِرِيُّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2444, N'قالَ بَصُرْتُ بِما لَمْ يَبْصُرُوا بِهِ فَقَبَضْتُ قَبْضَةً مِنْ أَثَرِ الرَّسُولِ فَنَبَذْتُها وَ كَذلِكَ سَوَّلَتْ لِي نَفْسِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2445, N'قالَ فَاذْهَبْ فَإِنَّ لَكَ فِي الْحَياةِ أَنْ تَقُولَ لا مِساسَ وَ إِنَّ لَكَ مَوْعِداً لَنْ تُخْلَفَهُ وَ انْظُرْ إِلى إِلهِكَ الَّذِي ظَلْتَ عَلَيْهِ عاكِفاً لَنُحَرِّقَنَّهُ ثُمَّ لَنَنْسِفَنَّهُ فِي الْيَمِّ نَسْفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2446, N'إِنَّما إِلهُكُمُ اللَّهُ الَّذِي لا إِلهَ إِلاَّ هُوَ وَسِعَ كُلَّ شَيْ‏ءٍ عِلْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2447, N'كَذلِكَ نَقُصُّ عَلَيْكَ مِنْ أَنْباءِ ما قَدْ سَبَقَ وَ قَدْ آتَيْناكَ مِنْ لَدُنَّا ذِكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2448, N'مَنْ أَعْرَضَ عَنْهُ فَإِنَّهُ يَحْمِلُ يَوْمَ الْقِيامَةِ وِزْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2449, N'خالِدِينَ فِيهِ وَ ساءَ لَهُمْ يَوْمَ الْقِيامَةِ حِمْلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2450, N'يَوْمَ يُنْفَخُ فِي الصُّورِ وَ نَحْشُرُ الْمُجْرِمِينَ يَوْمَئِذٍ زُرْقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2451, N'يَتَخافَتُونَ بَيْنَهُمْ إِنْ لَبِثْتُمْ إِلاَّ عَشْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2452, N'نَحْنُ أَعْلَمُ بِما يَقُولُونَ إِذْ يَقُولُ أَمْثَلُهُمْ طَرِيقَةً إِنْ لَبِثْتُمْ إِلاَّ يَوْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2453, N'وَ يَسْئَلُونَكَ عَنِ الْجِبالِ فَقُلْ يَنْسِفُها رَبِّي نَسْفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2454, N'فَيَذَرُها قاعاً صَفْصَفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2455, N'لا تَرى فِيها عِوَجاً وَ لا أَمْتاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2456, N'يَوْمَئِذٍ يَتَّبِعُونَ الدَّاعِيَ لا عِوَجَ لَهُ وَ خَشَعَتِ الْأَصْواتُ لِلرَّحْمنِ فَلا تَسْمَعُ إِلاَّ هَمْساً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2457, N'يَوْمَئِذٍ لا تَنْفَعُ الشَّفاعَةُ إِلاَّ مَنْ أَذِنَ لَهُ الرَّحْمنُ وَ رَضِيَ لَهُ قَوْلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2458, N'يَعْلَمُ ما بَيْنَ أَيْدِيهِمْ وَ ما خَلْفَهُمْ وَ لا يُحِيطُونَ بِهِ عِلْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2459, N'وَ عَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِ وَ قَدْ خابَ مَنْ حَمَلَ ظُلْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2460, N'وَ مَنْ يَعْمَلْ مِنَ الصَّالِحاتِ وَ هُوَ مُؤْمِنٌ فَلا يَخافُ ظُلْماً وَ لا هَضْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2461, N'وَ كَذلِكَ أَنْزَلْناهُ قُرْآناً عَرَبِيًّا وَ صَرَّفْنا فِيهِ مِنَ الْوَعِيدِ لَعَلَّهُمْ يَتَّقُونَ أَوْ يُحْدِثُ لَهُمْ ذِكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2462, N'فَتَعالَى اللَّهُ الْمَلِكُ الْحَقُّ وَ لا تَعْجَلْ بِالْقُرْآنِ مِنْ قَبْلِ أَنْ يُقْضى إِلَيْكَ وَحْيُهُ وَ قُلْ رَبِّ زِدْنِي عِلْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2463, N'وَ لَقَدْ عَهِدْنا إِلى آدَمَ مِنْ قَبْلُ فَنَسِيَ وَ لَمْ نَجِدْ لَهُ عَزْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2464, N'وَ إِذْ قُلْنا لِلْمَلائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاَّ إِبْلِيسَ أَبى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2465, N'فَقُلْنا يا آدَمُ إِنَّ هذا عَدُوٌّ لَكَ وَ لِزَوْجِكَ فَلا يُخْرِجَنَّكُما مِنَ الْجَنَّةِ فَتَشْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2466, N'إِنَّ لَكَ أَلاَّ تَجُوعَ فِيها وَ لا تَعْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2467, N'وَ أَنَّكَ لا تَظْمَؤُا فِيها وَ لا تَضْحى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2468, N'فَوَسْوَسَ إِلَيْهِ الشَّيْطانُ قالَ يا آدَمُ هَلْ أَدُلُّكَ عَلى شَجَرَةِ الْخُلْدِ وَ مُلْكٍ لا يَبْلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2469, N'فَأَكَلا مِنْها فَبَدَتْ لَهُما سَوْآتُهُما وَ طَفِقا يَخْصِفانِ عَلَيْهِما مِنْ وَرَقِ الْجَنَّةِ وَ عَصى آدَمُ رَبَّهُ فَغَوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2470, N'ثُمَّ اجْتَباهُ رَبُّهُ فَتابَ عَلَيْهِ وَ هَدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2471, N'قالَ اهْبِطا مِنْها جَمِيعاً بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ فَإِمَّا يَأْتِيَنَّكُمْ مِنِّي هُدىً فَمَنِ اتَّبَعَ هُدايَ فَلا يَضِلُّ وَ لا يَشْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2472, N'وَ مَنْ أَعْرَضَ عَنْ ذِكْرِي فَإِنَّ لَهُ مَعِيشَةً ضَنْكاً وَ نَحْشُرُهُ يَوْمَ الْقِيامَةِ أَعْمى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2473, N'قالَ رَبِّ لِمَ حَشَرْتَنِي أَعْمى وَ قَدْ كُنْتُ بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2474, N'قالَ كَذلِكَ أَتَتْكَ آياتُنا فَنَسِيتَها وَ كَذلِكَ الْيَوْمَ تُنْسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2475, N'وَ كَذلِكَ نَجْزِي مَنْ أَسْرَفَ وَ لَمْ يُؤْمِنْ بِآياتِ رَبِّهِ وَ لَعَذابُ الْآخِرَةِ أَشَدُّ وَ أَبْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2476, N'أَ فَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنا قَبْلَهُمْ مِنَ الْقُرُونِ يَمْشُونَ فِي مَساكِنِهِمْ إِنَّ فِي ذلِكَ لَآياتٍ لِأُولِي النُّهى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2477, N'وَ لَوْ لا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَكانَ لِزاماً وَ أَجَلٌ مُسَمًّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2478, N'فَاصْبِرْ عَلى ما يَقُولُونَ وَ سَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَ قَبْلَ غُرُوبِها وَ مِنْ آناءِ اللَّيْلِ فَسَبِّحْ وَ أَطْرافَ النَّهارِ لَعَلَّكَ تَرْضى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2479, N'وَ لا تَمُدَّنَّ عَيْنَيْكَ إِلى ما مَتَّعْنا بِهِ أَزْواجاً مِنْهُمْ زَهْرَةَ الْحَياةِ الدُّنْيا لِنَفْتِنَهُمْ فِيهِ وَ رِزْقُ رَبِّكَ خَيْرٌ وَ أَبْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2480, N'وَ أْمُرْ أَهْلَكَ بِالصَّلاةِ وَ اصْطَبِرْ عَلَيْها لا نَسْئَلُكَ رِزْقاً نَحْنُ نَرْزُقُكَ وَ الْعاقِبَةُ لِلتَّقْوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2481, N'وَ قالُوا لَوْ لا يَأْتِينا بِآيَةٍ مِنْ رَبِّهِ أَ وَ لَمْ تَأْتِهِمْ بَيِّنَةُ ما فِي الصُّحُفِ الْأُولى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2482, N'وَ لَوْ أَنَّا أَهْلَكْناهُمْ بِعَذابٍ مِنْ قَبْلِهِ لَقالُوا رَبَّنا لَوْ لا أَرْسَلْتَ إِلَيْنا رَسُولاً فَنَتَّبِعَ آياتِكَ مِنْ قَبْلِ أَنْ نَذِلَّ وَ نَخْزى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2483, N'قُلْ كُلٌّ مُتَرَبِّصٌ فَتَرَبَّصُوا فَسَتَعْلَمُونَ مَنْ أَصْحابُ الصِّراطِ السَّوِيِّ وَ مَنِ اهْتَدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2484, N'اقْتَرَبَ لِلنَّاسِ حِسابُهُمْ وَ هُمْ فِي غَفْلَةٍ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2485, N'ما يَأْتِيهِمْ مِنْ ذِكْرٍ مِنْ رَبِّهِمْ مُحْدَثٍ إِلاَّ اسْتَمَعُوهُ وَ هُمْ يَلْعَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2486, N'لاهِيَةً قُلُوبُهُمْ وَ أَسَرُّوا النَّجْوَى الَّذِينَ ظَلَمُوا هَلْ هذا إِلاَّ بَشَرٌ مِثْلُكُمْ أَ فَتَأْتُونَ السِّحْرَ وَ أَنْتُمْ تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2487, N'قالَ رَبِّي يَعْلَمُ الْقَوْلَ فِي السَّماءِ وَ الْأَرْضِ وَ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2488, N'بَلْ قالُوا أَضْغاثُ أَحْلامٍ بَلِ افْتَراهُ بَلْ هُوَ شاعِرٌ فَلْيَأْتِنا بِآيَةٍ كَما أُرْسِلَ الْأَوَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2489, N'ما آمَنَتْ قَبْلَهُمْ مِنْ قَرْيَةٍ أَهْلَكْناها أَ فَهُمْ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2490, N'وَ ما أَرْسَلْنا قَبْلَكَ إِلاَّ رِجالاً نُوحِي إِلَيْهِمْ فَسْئَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2491, N'وَ ما جَعَلْناهُمْ جَسَداً لا يَأْكُلُونَ الطَّعامَ وَ ما كانُوا خالِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2492, N'ثُمَّ صَدَقْناهُمُ الْوَعْدَ فَأَنْجَيْناهُمْ وَ مَنْ نَشاءُ وَ أَهْلَكْنَا الْمُسْرِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2493, N'لَقَدْ أَنْزَلْنا إِلَيْكُمْ كِتاباً فِيهِ ذِكْرُكُمْ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2494, N'وَ كَمْ قَصَمْنا مِنْ قَرْيَةٍ كانَتْ ظالِمَةً وَ أَنْشَأْنا بَعْدَها قَوْماً آخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2495, N'فَلَمَّا أَحَسُّوا بَأْسَنا إِذا هُمْ مِنْها يَرْكُضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2496, N'لا تَرْكُضُوا وَ ارْجِعُوا إِلى ما أُتْرِفْتُمْ فِيهِ وَ مَساكِنِكُمْ لَعَلَّكُمْ تُسْئَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2497, N'قالُوا يا وَيْلَنا إِنَّا كُنَّا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2498, N'فَما زالَتْ تِلْكَ دَعْواهُمْ حَتَّى جَعَلْناهُمْ حَصِيداً خامِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2499, N'وَ ما خَلَقْنَا السَّماءَ وَ الْأَرْضَ وَ ما بَيْنَهُما لاعِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2500, N'لَوْ أَرَدْنا أَنْ نَتَّخِذَ لَهْواً لاَتَّخَذْناهُ مِنْ لَدُنَّا إِنْ كُنَّا فاعِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2501, N'بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْباطِلِ فَيَدْمَغُهُ فَإِذا هُوَ زاهِقٌ وَ لَكُمُ الْوَيْلُ مِمَّا تَصِفُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2502, N'وَ لَهُ مَنْ فِي السَّماواتِ وَ الْأَرْضِ وَ مَنْ عِنْدَهُ لا يَسْتَكْبِرُونَ عَنْ عِبادَتِهِ وَ لا يَسْتَحْسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2503, N'يُسَبِّحُونَ اللَّيْلَ وَ النَّهارَ لا يَفْتُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2504, N'أَمِ اتَّخَذُوا آلِهَةً مِنَ الْأَرْضِ هُمْ يُنْشِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2505, N'لَوْ كانَ فِيهِما آلِهَةٌ إِلاَّ اللَّهُ لَفَسَدَتا فَسُبْحانَ اللَّهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2506, N'لا يُسْئَلُ عَمَّا يَفْعَلُ وَ هُمْ يُسْئَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2507, N'أَمِ اتَّخَذُوا مِنْ دُونِهِ آلِهَةً قُلْ هاتُوا بُرْهانَكُمْ هذا ذِكْرُ مَنْ مَعِيَ وَ ذِكْرُ مَنْ قَبْلِي بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ الْحَقَّ فَهُمْ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2508, N'وَ ما أَرْسَلْنا مِنْ قَبْلِكَ مِنْ رَسُولٍ إِلاَّ نُوحِي إِلَيْهِ أَنَّهُ لا إِلهَ إِلاَّ أَنَا فَاعْبُدُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2509, N'وَ قالُوا اتَّخَذَ الرَّحْمنُ وَلَداً سُبْحانَهُ بَلْ عِبادٌ مُكْرَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2510, N'لا يَسْبِقُونَهُ بِالْقَوْلِ وَ هُمْ بِأَمْرِهِ يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2511, N'يَعْلَمُ ما بَيْنَ أَيْدِيهِمْ وَ ما خَلْفَهُمْ وَ لا يَشْفَعُونَ إِلاَّ لِمَنِ ارْتَضى وَ هُمْ مِنْ خَشْيَتِهِ مُشْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2512, N'وَ مَنْ يَقُلْ مِنْهُمْ إِنِّي إِلهٌ مِنْ دُونِهِ فَذلِكَ نَجْزِيهِ جَهَنَّمَ كَذلِكَ نَجْزِي الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2513, N'أَ وَ لَمْ يَرَ الَّذِينَ كَفَرُوا أَنَّ السَّماواتِ وَ الْأَرْضَ كانَتا رَتْقاً فَفَتَقْناهُما وَ جَعَلْنا مِنَ الْماءِ كُلَّ شَيْ‏ءٍ حَيٍّ أَ فَلا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2514, N'وَ جَعَلْنا فِي الْأَرْضِ رَواسِيَ أَنْ تَمِيدَ بِهِمْ وَ جَعَلْنا فِيها فِجاجاً سُبُلاً لَعَلَّهُمْ يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2515, N'وَ جَعَلْنَا السَّماءَ سَقْفاً مَحْفُوظاً وَ هُمْ عَنْ آياتِها مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2516, N'وَ هُوَ الَّذِي خَلَقَ اللَّيْلَ وَ النَّهارَ وَ الشَّمْسَ وَ الْقَمَرَ كُلٌّ فِي فَلَكٍ يَسْبَحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2517, N'وَ ما جَعَلْنا لِبَشَرٍ مِنْ قَبْلِكَ الْخُلْدَ أَ فَإِنْ مِتَّ فَهُمُ الْخالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2518, N'كُلُّ نَفْسٍ ذائِقَةُ الْمَوْتِ وَ نَبْلُوكُمْ بِالشَّرِّ وَ الْخَيْرِ فِتْنَةً وَ إِلَيْنا تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2519, N'وَ إِذا رَآكَ الَّذِينَ كَفَرُوا إِنْ يَتَّخِذُونَكَ إِلاَّ هُزُواً أَ هذَا الَّذِي يَذْكُرُ آلِهَتَكُمْ وَ هُمْ بِذِكْرِ الرَّحْمنِ هُمْ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2520, N'خُلِقَ الْإِنْسانُ مِنْ عَجَلٍ سَأُرِيكُمْ آياتِي فَلا تَسْتَعْجِلُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2521, N'وَ يَقُولُونَ مَتى هذَا الْوَعْدُ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2522, N'لَوْ يَعْلَمُ الَّذِينَ كَفَرُوا حِينَ لا يَكُفُّونَ عَنْ وُجُوهِهِمُ النَّارَ وَ لا عَنْ ظُهُورِهِمْ وَ لا هُمْ يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2523, N'بَلْ تَأْتِيهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلا يَسْتَطِيعُونَ رَدَّها وَ لا هُمْ يُنْظَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2524, N'وَ لَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحاقَ بِالَّذِينَ سَخِرُوا مِنْهُمْ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2525, N'قُلْ مَنْ يَكْلَؤُكُمْ بِاللَّيْلِ وَ النَّهارِ مِنَ الرَّحْمنِ بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2526, N'أَمْ لَهُمْ آلِهَةٌ تَمْنَعُهُمْ مِنْ دُونِنا لا يَسْتَطِيعُونَ نَصْرَ أَنْفُسِهِمْ وَ لا هُمْ مِنَّا يُصْحَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2527, N'بَلْ مَتَّعْنا هؤُلاءِ وَ آباءَهُمْ حَتَّى طالَ عَلَيْهِمُ الْعُمُرُ أَ فَلا يَرَوْنَ أَنَّا نَأْتِي الْأَرْضَ نَنْقُصُها مِنْ أَطْرافِها أَ فَهُمُ الْغالِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2528, N'قُلْ إِنَّما أُنْذِرُكُمْ بِالْوَحْيِ وَ لا يَسْمَعُ الصُّمُّ الدُّعاءَ إِذا ما يُنْذَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2529, N'وَ لَئِنْ مَسَّتْهُمْ نَفْحَةٌ مِنْ عَذابِ رَبِّكَ لَيَقُولُنَّ يا وَيْلَنا إِنَّا كُنَّا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2530, N'وَ نَضَعُ الْمَوازِينَ الْقِسْطَ لِيَوْمِ الْقِيامَةِ فَلا تُظْلَمُ نَفْسٌ شَيْئاً وَ إِنْ كانَ مِثْقالَ حَبَّةٍ مِنْ خَرْدَلٍ أَتَيْنا بِها وَ كَفى بِنا حاسِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2531, N'وَ لَقَدْ آتَيْنا مُوسى وَ هارُونَ الْفُرْقانَ وَ ضِياءً وَ ذِكْراً لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2532, N'الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَ هُمْ مِنَ السَّاعَةِ مُشْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2533, N'وَ هذا ذِكْرٌ مُبارَكٌ أَنْزَلْناهُ أَ فَأَنْتُمْ لَهُ مُنْكِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2534, N'وَ لَقَدْ آتَيْنا إِبْراهِيمَ رُشْدَهُ مِنْ قَبْلُ وَ كُنَّا بِهِ عالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2535, N'إِذْ قالَ لِأَبِيهِ وَ قَوْمِهِ ما هذِهِ التَّماثِيلُ الَّتِي أَنْتُمْ لَها عاكِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2536, N'قالُوا وَجَدْنا آباءَنا لَها عابِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2537, N'قالَ لَقَدْ كُنْتُمْ أَنْتُمْ وَ آباؤُكُمْ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2538, N'قالُوا أَ جِئْتَنا بِالْحَقِّ أَمْ أَنْتَ مِنَ اللاَّعِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2539, N'قالَ بَلْ رَبُّكُمْ رَبُّ السَّماواتِ وَ الْأَرْضِ الَّذِي فَطَرَهُنَّ وَ أَنَا عَلى ذلِكُمْ مِنَ الشَّاهِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2540, N'وَ تَاللَّهِ لَأَكِيدَنَّ أَصْنامَكُمْ بَعْدَ أَنْ تُوَلُّوا مُدْبِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2541, N'فَجَعَلَهُمْ جُذاذاً إِلاَّ كَبِيراً لَهُمْ لَعَلَّهُمْ إِلَيْهِ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2542, N'قالُوا مَنْ فَعَلَ هذا بِآلِهَتِنا إِنَّهُ لَمِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2543, N'قالُوا سَمِعْنا فَتًى يَذْكُرُهُمْ يُقالُ لَهُ إِبْراهِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2544, N'قالُوا فَأْتُوا بِهِ عَلى أَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2545, N'قالُوا أَ أَنْتَ فَعَلْتَ هذا بِآلِهَتِنا يا إِبْراهِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2546, N'قالَ بَلْ فَعَلَهُ كَبِيرُهُمْ هذا فَسْئَلُوهُمْ إِنْ كانُوا يَنْطِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2547, N'فَرَجَعُوا إِلى أَنْفُسِهِمْ فَقالُوا إِنَّكُمْ أَنْتُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2548, N'ثُمَّ نُكِسُوا عَلى رُؤُسِهِمْ لَقَدْ عَلِمْتَ ما هؤُلاءِ يَنْطِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2549, N'قالَ أَ فَتَعْبُدُونَ مِنْ دُونِ اللَّهِ ما لا يَنْفَعُكُمْ شَيْئاً وَ لا يَضُرُّكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2550, N'أُفٍّ لَكُمْ وَ لِما تَعْبُدُونَ مِنْ دُونِ اللَّهِ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2551, N'قالُوا حَرِّقُوهُ وَ انْصُرُوا آلِهَتَكُمْ إِنْ كُنْتُمْ فاعِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2552, N'قُلْنا يا نارُ كُونِي بَرْداً وَ سَلاماً عَلى إِبْراهِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2553, N'وَ أَرادُوا بِهِ كَيْداً فَجَعَلْناهُمُ الْأَخْسَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2554, N'وَ نَجَّيْناهُ وَ لُوطاً إِلَى الْأَرْضِ الَّتِي بارَكْنا فِيها لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2555, N'وَ وَهَبْنا لَهُ إِسْحاقَ وَ يَعْقُوبَ نافِلَةً وَ كُلاًّ جَعَلْنا صالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2556, N'وَ جَعَلْناهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنا وَ أَوْحَيْنا إِلَيْهِمْ فِعْلَ الْخَيْراتِ وَ إِقامَ الصَّلاةِ وَ إِيتاءَ الزَّكاةِ وَ كانُوا لَنا عابِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2557, N'وَ لُوطاً آتَيْناهُ حُكْماً وَ عِلْماً وَ نَجَّيْناهُ مِنَ الْقَرْيَةِ الَّتِي كانَتْ تَعْمَلُ الْخَبائِثَ إِنَّهُمْ كانُوا قَوْمَ سَوْءٍ فاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2558, N'وَ أَدْخَلْناهُ فِي رَحْمَتِنا إِنَّهُ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2559, N'وَ نُوحاً إِذْ نادى مِنْ قَبْلُ فَاسْتَجَبْنا لَهُ فَنَجَّيْناهُ وَ أَهْلَهُ مِنَ الْكَرْبِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2560, N'وَ نَصَرْناهُ مِنَ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآياتِنا إِنَّهُمْ كانُوا قَوْمَ سَوْءٍ فَأَغْرَقْناهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2561, N'وَ داوُدَ وَ سُلَيْمانَ إِذْ يَحْكُمانِ فِي الْحَرْثِ إِذْ نَفَشَتْ فِيهِ غَنَمُ الْقَوْمِ وَ كُنَّا لِحُكْمِهِمْ شاهِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2562, N'فَفَهَّمْناها سُلَيْمانَ وَ كُلاًّ آتَيْنا حُكْماً وَ عِلْماً وَ سَخَّرْنا مَعَ داوُدَ الْجِبالَ يُسَبِّحْنَ وَ الطَّيْرَ وَ كُنَّا فاعِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2563, N'وَ عَلَّمْناهُ صَنْعَةَ لَبُوسٍ لَكُمْ لِتُحْصِنَكُمْ مِنْ بَأْسِكُمْ فَهَلْ أَنْتُمْ شاكِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2564, N'وَ لِسُلَيْمانَ الرِّيحَ عاصِفَةً تَجْرِي بِأَمْرِهِ إِلى الْأَرْضِ الَّتِي بارَكْنا فِيها وَ كُنَّا بِكُلِّ شَيْ‏ءٍ عالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2565, N'وَ مِنَ الشَّياطِينِ مَنْ يَغُوصُونَ لَهُ وَ يَعْمَلُونَ عَمَلاً دُونَ ذلِكَ وَ كُنَّا لَهُمْ حافِظِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2566, N'وَ أَيُّوبَ إِذْ نادى رَبَّهُ أَنِّي مَسَّنِيَ الضُّرُّ وَ أَنْتَ أَرْحَمُ الرَّاحِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2567, N'فَاسْتَجَبْنا لَهُ فَكَشَفْنا ما بِهِ مِنْ ضُرٍّ وَ آتَيْناهُ أَهْلَهُ وَ مِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنْ عِنْدِنا وَ ذِكْرى لِلْعابِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2568, N'وَ إِسْماعِيلَ وَ إِدْرِيسَ وَ ذَا الْكِفْلِ كُلٌّ مِنَ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2569, N'وَ أَدْخَلْناهُمْ فِي رَحْمَتِنا إِنَّهُمْ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2570, N'وَ ذَا النُّونِ إِذْ ذَهَبَ مُغاضِباً فَظَنَّ أَنْ لَنْ نَقْدِرَ عَلَيْهِ فَنادى فِي الظُّلُماتِ أَنْ لا إِلهَ إِلاَّ أَنْتَ سُبْحانَكَ إِنِّي كُنْتُ مِنَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2571, N'فَاسْتَجَبْنا لَهُ وَ نَجَّيْناهُ مِنَ الْغَمِّ وَ كَذلِكَ نُنْجِي الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2572, N'وَ زَكَرِيَّا إِذْ نادى رَبَّهُ رَبِّ لا تَذَرْنِي فَرْداً وَ أَنْتَ خَيْرُ الْوارِثِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2573, N'فَاسْتَجَبْنا لَهُ وَ وَهَبْنا لَهُ يَحْيى وَ أَصْلَحْنا لَهُ زَوْجَهُ إِنَّهُمْ كانُوا يُسارِعُونَ فِي الْخَيْراتِ وَ يَدْعُونَنا رَغَباً وَ رَهَباً وَ كانُوا لَنا خاشِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2574, N'وَ الَّتِي أَحْصَنَتْ فَرْجَها فَنَفَخْنا فِيها مِنْ رُوحِنا وَ جَعَلْناها وَ ابْنَها آيَةً لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2575, N'إِنَّ هذِهِ أُمَّتُكُمْ أُمَّةً واحِدَةً وَ أَنَا رَبُّكُمْ فَاعْبُدُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2576, N'وَ تَقَطَّعُوا أَمْرَهُمْ بَيْنَهُمْ كُلٌّ إِلَيْنا راجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2577, N'فَمَنْ يَعْمَلْ مِنَ الصَّالِحاتِ وَ هُوَ مُؤْمِنٌ فَلا كُفْرانَ لِسَعْيِهِ وَ إِنَّا لَهُ كاتِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2578, N'وَ حَرامٌ عَلى قَرْيَةٍ أَهْلَكْناها أَنَّهُمْ لا يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2579, N'حَتَّى إِذا فُتِحَتْ يَأْجُوجُ وَ مَأْجُوجُ وَ هُمْ مِنْ كُلِّ حَدَبٍ يَنْسِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2580, N'وَ اقْتَرَبَ الْوَعْدُ الْحَقُّ فَإِذا هِيَ شاخِصَةٌ أَبْصارُ الَّذِينَ كَفَرُوا يا وَيْلَنا قَدْ كُنَّا فِي غَفْلَةٍ مِنْ هذا بَلْ كُنَّا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2581, N'إِنَّكُمْ وَ ما تَعْبُدُونَ مِنْ دُونِ اللَّهِ حَصَبُ جَهَنَّمَ أَنْتُمْ لَها وارِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2582, N'لَوْ كانَ هؤُلاءِ آلِهَةً ما وَرَدُوها وَ كُلٌّ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2583, N'لَهُمْ فِيها زَفِيرٌ وَ هُمْ فِيها لا يَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2584, N'إِنَّ الَّذِينَ سَبَقَتْ لَهُمْ مِنَّا الْحُسْنى أُولئِكَ عَنْها مُبْعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2585, N'لا يَسْمَعُونَ حَسِيسَها وَ هُمْ فِي مَا اشْتَهَتْ أَنْفُسُهُمْ خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2586, N'لا يَحْزُنُهُمُ الْفَزَعُ الْأَكْبَرُ وَ تَتَلَقَّاهُمُ الْمَلائِكَةُ هذا يَوْمُكُمُ الَّذِي كُنْتُمْ تُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2587, N'يَوْمَ نَطْوِي السَّماءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِ كَما بَدَأْنا أَوَّلَ خَلْقٍ نُعِيدُهُ وَعْداً عَلَيْنا إِنَّا كُنَّا فاعِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2588, N'وَ لَقَدْ كَتَبْنا فِي الزَّبُورِ مِنْ بَعْدِ الذِّكْرِ أَنَّ الْأَرْضَ يَرِثُها عِبادِيَ الصَّالِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2589, N'إِنَّ فِي هذا لَبَلاغاً لِقَوْمٍ عابِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2590, N'وَ ما أَرْسَلْناكَ إِلاَّ رَحْمَةً لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2591, N'قُلْ إِنَّما يُوحى إِلَيَّ أَنَّما إِلهُكُمْ إِلهٌ واحِدٌ فَهَلْ أَنْتُمْ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2592, N'فَإِنْ تَوَلَّوْا فَقُلْ آذَنْتُكُمْ عَلى سَواءٍ وَ إِنْ أَدْرِي أَ قَرِيبٌ أَمْ بَعِيدٌ ما تُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2593, N'إِنَّهُ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَ يَعْلَمُ ما تَكْتُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2594, N'وَ إِنْ أَدْرِي لَعَلَّهُ فِتْنَةٌ لَكُمْ وَ مَتاعٌ إِلى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2595, N'قالَ رَبِّ احْكُمْ بِالْحَقِّ وَ رَبُّنَا الرَّحْمنُ الْمُسْتَعانُ عَلى ما تَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2596, N'يا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ إِنَّ زَلْزَلَةَ السَّاعَةِ شَيْ‏ءٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2597, N'يَوْمَ تَرَوْنَها تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّا أَرْضَعَتْ وَ تَضَعُ كُلُّ ذاتِ حَمْلٍ حَمْلَها وَ تَرَى النَّاسَ سُكارى وَ ما هُمْ بِسُكارى وَ لكِنَّ عَذابَ اللَّهِ شَدِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2598, N'وَ مِنَ النَّاسِ مَنْ يُجادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَ يَتَّبِعُ كُلَّ شَيْطانٍ مَرِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2599, N'كُتِبَ عَلَيْهِ أَنَّهُ مَنْ تَوَلاَّهُ فَأَنَّهُ يُضِلُّهُ وَ يَهْدِيهِ إِلى عَذابِ السَّعِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2600, N'يا أَيُّهَا النَّاسُ إِنْ كُنْتُمْ فِي رَيْبٍ مِنَ الْبَعْثِ فَإِنَّا خَلَقْناكُمْ مِنْ تُرابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُضْغَةٍ مُخَلَّقَةٍ وَ غَيْرِ مُخَلَّقَةٍ لِنُبَيِّنَ لَكُمْ وَ نُقِرُّ فِي الْأَرْحامِ ما نَشاءُ إِلى أَجَلٍ مُسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلاً ثُمَّ لِتَبْلُغُوا أَشُدَّكُمْ وَ مِنْكُمْ مَنْ يُتَوَفَّى وَ مِنْكُمْ مَنْ يُرَدُّ إِلى أَرْذَلِ الْعُمُرِ لِكَيْلا يَعْلَمَ مِنْ بَعْدِ عِلْمٍ شَيْئاً وَ تَرَى الْأَرْضَ هامِدَةً فَإِذا أَنْزَلْنا عَلَيْهَا الْماءَ اهْتَزَّتْ وَ رَبَتْ وَ أَنْبَتَتْ مِنْ كُلِّ زَوْجٍ بَهِيجٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2601, N'ذلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَ أَنَّهُ يُحْيِ الْمَوْتى وَ أَنَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2602, N'وَ أَنَّ السَّاعَةَ آتِيَةٌ لا رَيْبَ فِيها وَ أَنَّ اللَّهَ يَبْعَثُ مَنْ فِي الْقُبُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2603, N'وَ مِنَ النَّاسِ مَنْ يُجادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَ لا هُدىً وَ لا كِتابٍ مُنِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2604, N'ثانِيَ عِطْفِهِ لِيُضِلَّ عَنْ سَبِيلِ اللَّهِ لَهُ فِي الدُّنْيا خِزْيٌ وَ نُذِيقُهُ يَوْمَ الْقِيامَةِ عَذابَ الْحَرِيقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2605, N'ذلِكَ بِما قَدَّمَتْ يَداكَ وَ أَنَّ اللَّهَ لَيْسَ بِظَلاَّمٍ لِلْعَبِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2606, N'وَ مِنَ النَّاسِ مَنْ يَعْبُدُ اللَّهَ عَلى حَرْفٍ فَإِنْ أَصابَهُ خَيْرٌ اطْمَأَنَّ بِهِ وَ إِنْ أَصابَتْهُ فِتْنَةٌ انْقَلَبَ عَلى وَجْهِهِ خَسِرَ الدُّنْيا وَ الْآخِرَةَ ذلِكَ هُوَ الْخُسْرانُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2607, N'يَدْعُوا مِنْ دُونِ اللَّهِ ما لا يَضُرُّهُ وَ ما لا يَنْفَعُهُ ذلِكَ هُوَ الضَّلالُ الْبَعِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2608, N'يَدْعُوا لَمَنْ ضَرُّهُ أَقْرَبُ مِنْ نَفْعِهِ لَبِئْسَ الْمَوْلى وَ لَبِئْسَ الْعَشِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2609, N'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ إِنَّ اللَّهَ يَفْعَلُ ما يُرِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2610, N'مَنْ كانَ يَظُنُّ أَنْ لَنْ يَنْصُرَهُ اللَّهُ فِي الدُّنْيا وَ الْآخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ إِلَى السَّماءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهُ ما يَغِيظُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2611, N'وَ كَذلِكَ أَنْزَلْناهُ آياتٍ بَيِّناتٍ وَ أَنَّ اللَّهَ يَهْدِي مَنْ يُرِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2612, N'إِنَّ الَّذِينَ آمَنُوا وَ الَّذِينَ هادُوا وَ الصَّابِئِينَ وَ النَّصارى وَ الْمَجُوسَ وَ الَّذِينَ أَشْرَكُوا إِنَّ اللَّهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيامَةِ إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ شَهِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2613, N'أَ لَمْ تَرَ أَنَّ اللَّهَ يَسْجُدُ لَهُ مَنْ فِي السَّماواتِ وَ مَنْ فِي الْأَرْضِ وَ الشَّمْسُ وَ الْقَمَرُ وَ النُّجُومُ وَ الْجِبالُ وَ الشَّجَرُ وَ الدَّوَابُّ وَ كَثِيرٌ مِنَ النَّاسِ وَ كَثِيرٌ حَقَّ عَلَيْهِ الْعَذابُ وَ مَنْ يُهِنِ اللَّهُ فَما لَهُ مِنْ مُكْرِمٍ إِنَّ اللَّهَ يَفْعَلُ ما يَشاءُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2614, N'هذانِ خَصْمانِ اخْتَصَمُوا فِي رَبِّهِمْ فَالَّذِينَ كَفَرُوا قُطِّعَتْ لَهُمْ ثِيابٌ مِنْ نارٍ يُصَبُّ مِنْ فَوْقِ رُؤُسِهِمُ الْحَمِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2615, N'يُصْهَرُ بِهِ ما فِي بُطُونِهِمْ وَ الْجُلُودُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2616, N'وَ لَهُمْ مَقامِعُ مِنْ حَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2617, N'كُلَّما أَرادُوا أَنْ يَخْرُجُوا مِنْها مِنْ غَمٍّ أُعِيدُوا فِيها وَ ذُوقُوا عَذابَ الْحَرِيقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2618, N'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ يُحَلَّوْنَ فِيها مِنْ أَساوِرَ مِنْ ذَهَبٍ وَ لُؤْلُؤاً وَ لِباسُهُمْ فِيها حَرِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2619, N'وَ هُدُوا إِلَى الطَّيِّبِ مِنَ الْقَوْلِ وَ هُدُوا إِلى صِراطِ الْحَمِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2620, N'إِنَّ الَّذِينَ كَفَرُوا وَ يَصُدُّونَ عَنْ سَبِيلِ اللَّهِ وَ الْمَسْجِدِ الْحَرامِ الَّذِي جَعَلْناهُ لِلنَّاسِ سَواءً الْعاكِفُ فِيهِ وَ الْبادِ وَ مَنْ يُرِدْ فِيهِ بِإِلْحادٍ بِظُلْمٍ نُذِقْهُ مِنْ عَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2621, N'وَ إِذْ بَوَّأْنا لِإِبْراهِيمَ مَكانَ الْبَيْتِ أَنْ لا تُشْرِكْ بِي شَيْئاً وَ طَهِّرْ بَيْتِيَ لِلطَّائِفِينَ وَ الْقائِمِينَ وَ الرُّكَّعِ السُّجُودِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2622, N'وَ أَذِّنْ فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجالاً وَ عَلى كُلِّ ضامِرٍ يَأْتِينَ مِنْ كُلِّ فَجٍّ عَمِيقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2623, N'لِيَشْهَدُوا مَنافِعَ لَهُمْ وَ يَذْكُرُوا اسْمَ اللَّهِ فِي أَيَّامٍ مَعْلُوماتٍ عَلى ما رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعامِ فَكُلُوا مِنْها وَ أَطْعِمُوا الْبائِسَ الْفَقِيرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2624, N'ثُمَّ لْيَقْضُوا تَفَثَهُمْ وَ لْيُوفُوا نُذُورَهُمْ وَ لْيَطَّوَّفُوا بِالْبَيْتِ الْعَتِيقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2625, N'ذلِكَ وَ مَنْ يُعَظِّمْ حُرُماتِ اللَّهِ فَهُوَ خَيْرٌ لَهُ عِنْدَ رَبِّهِ وَ أُحِلَّتْ لَكُمُ الْأَنْعامُ إِلاَّ ما يُتْلى عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْأَوْثانِ وَ اجْتَنِبُوا قَوْلَ الزُّورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2626, N'حُنَفاءَ لِلَّهِ غَيْرَ مُشْرِكِينَ بِهِ وَ مَنْ يُشْرِكْ بِاللَّهِ فَكَأَنَّما خَرَّ مِنَ السَّماءِ فَتَخْطَفُهُ الطَّيْرُ أَوْ تَهْوِي بِهِ الرِّيحُ فِي مَكانٍ سَحِيقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2627, N'ذلِكَ وَ مَنْ يُعَظِّمْ شَعائِرَ اللَّهِ فَإِنَّها مِنْ تَقْوَى الْقُلُوبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2628, N'لَكُمْ فِيها مَنافِعُ إِلى أَجَلٍ مُسَمًّى ثُمَّ مَحِلُّها إِلَى الْبَيْتِ الْعَتِيقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2629, N'وَ لِكُلِّ أُمَّةٍ جَعَلْنا مَنْسَكاً لِيَذْكُرُوا اسْمَ اللَّهِ عَلى ما رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعامِ فَإِلهُكُمْ إِلهٌ واحِدٌ فَلَهُ أَسْلِمُوا وَ بَشِّرِ الْمُخْبِتِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2630, N'الَّذِينَ إِذا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَ الصَّابِرِينَ عَلى ما أَصابَهُمْ وَ الْمُقِيمِي الصَّلاةِ وَ مِمَّا رَزَقْناهُمْ يُنْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2631, N'وَ الْبُدْنَ جَعَلْناها لَكُمْ مِنْ شَعائِرِ اللَّهِ لَكُمْ فِيها خَيْرٌ فَاذْكُرُوا اسْمَ اللَّهِ عَلَيْها صَوافَّ فَإِذا وَجَبَتْ جُنُوبُها فَكُلُوا مِنْها وَ أَطْعِمُوا الْقانِعَ وَ الْمُعْتَرَّ كَذلِكَ سَخَّرْناها لَكُمْ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2632, N'لَنْ يَنالَ اللَّهَ لُحُومُها وَ لا دِماؤُها وَ لكِنْ يَنالُهُ التَّقْوى مِنْكُمْ كَذلِكَ سَخَّرَها لَكُمْ لِتُكَبِّرُوا اللَّهَ عَلى ما هَداكُمْ وَ بَشِّرِ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2633, N'إِنَّ اللَّهَ يُدافِعُ عَنِ الَّذِينَ آمَنُوا إِنَّ اللَّهَ لا يُحِبُّ كُلَّ خَوَّانٍ كَفُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2634, N'أُذِنَ لِلَّذِينَ يُقاتَلُونَ بِأَنَّهُمْ ظُلِمُوا وَ إِنَّ اللَّهَ عَلى نَصْرِهِمْ لَقَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2635, N'الَّذِينَ أُخْرِجُوا مِنْ دِيارِهِمْ بِغَيْرِ حَقٍّ إِلاَّ أَنْ يَقُولُوا رَبُّنَا اللَّهُ وَ لَوْ لا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَهُدِّمَتْ صَوامِعُ وَ بِيَعٌ وَ صَلَواتٌ وَ مَساجِدُ يُذْكَرُ فِيهَا اسْمُ اللَّهِ كَثِيراً وَ لَيَنْصُرَنَّ اللَّهُ مَنْ يَنْصُرُهُ إِنَّ اللَّهَ لَقَوِيٌّ عَزِيزٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2636, N'الَّذِينَ إِنْ مَكَّنَّاهُمْ فِي الْأَرْضِ أَقامُوا الصَّلاةَ وَ آتَوُا الزَّكاةَ وَ أَمَرُوا بِالْمَعْرُوفِ وَ نَهَوْا عَنِ الْمُنْكَرِ وَ لِلَّهِ عاقِبَةُ الْأُمُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2637, N'وَ إِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَ عادٌ وَ ثَمُودُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2638, N'وَ قَوْمُ إِبْراهِيمَ وَ قَوْمُ لُوطٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2639, N'وَ أَصْحابُ مَدْيَنَ وَ كُذِّبَ مُوسى فَأَمْلَيْتُ لِلْكافِرِينَ ثُمَّ أَخَذْتُهُمْ فَكَيْفَ كانَ نَكِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2640, N'فَكَأَيِّنْ مِنْ قَرْيَةٍ أَهْلَكْناها وَ هِيَ ظالِمَةٌ فَهِيَ خاوِيَةٌ عَلى عُرُوشِها وَ بِئْرٍ مُعَطَّلَةٍ وَ قَصْرٍ مَشِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2641, N'أَ فَلَمْ يَسِيرُوا فِي الْأَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِها أَوْ آذانٌ يَسْمَعُونَ بِها فَإِنَّها لا تَعْمَى الْأَبْصارُ وَ لكِنْ تَعْمَى الْقُلُوبُ الَّتِي فِي الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2642, N'وَ يَسْتَعْجِلُونَكَ بِالْعَذابِ وَ لَنْ يُخْلِفَ اللَّهُ وَعْدَهُ وَ إِنَّ يَوْماً عِنْدَ رَبِّكَ كَأَلْفِ سَنَةٍ مِمَّا تَعُدُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2643, N'وَ كَأَيِّنْ مِنْ قَرْيَةٍ أَمْلَيْتُ لَها وَ هِيَ ظالِمَةٌ ثُمَّ أَخَذْتُها وَ إِلَيَّ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2644, N'قُلْ يا أَيُّهَا النَّاسُ إِنَّما أَنَا لَكُمْ نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2645, N'فَالَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَهُمْ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2646, N'وَ الَّذِينَ سَعَوْا فِي آياتِنا مُعاجِزِينَ أُولئِكَ أَصْحابُ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2647, N'وَ ما أَرْسَلْنا مِنْ قَبْلِكَ مِنْ رَسُولٍ وَ لا نَبِيٍّ إِلاَّ إِذا تَمَنَّى أَلْقَى الشَّيْطانُ فِي أُمْنِيَّتِهِ فَيَنْسَخُ اللَّهُ ما يُلْقِي الشَّيْطانُ ثُمَّ يُحْكِمُ اللَّهُ آياتِهِ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2648, N'لِيَجْعَلَ ما يُلْقِي الشَّيْطانُ فِتْنَةً لِلَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَ الْقاسِيَةِ قُلُوبُهُمْ وَ إِنَّ الظَّالِمِينَ لَفِي شِقاقٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2649, N'وَ لِيَعْلَمَ الَّذِينَ أُوتُوا الْعِلْمَ أَنَّهُ الْحَقُّ مِنْ رَبِّكَ فَيُؤْمِنُوا بِهِ فَتُخْبِتَ لَهُ قُلُوبُهُمْ وَ إِنَّ اللَّهَ لَهادِ الَّذِينَ آمَنُوا إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2650, N'وَ لا يَزالُ الَّذِينَ كَفَرُوا فِي مِرْيَةٍ مِنْهُ حَتَّى تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً أَوْ يَأْتِيَهُمْ عَذابُ يَوْمٍ عَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2651, N'الْمُلْكُ يَوْمَئِذٍ لِلَّهِ يَحْكُمُ بَيْنَهُمْ فَالَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ فِي جَنَّاتِ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2652, N'وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآياتِنا فَأُولئِكَ لَهُمْ عَذابٌ مُهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2653, N'وَ الَّذِينَ هاجَرُوا فِي سَبِيلِ اللَّهِ ثُمَّ قُتِلُوا أَوْ ماتُوا لَيَرْزُقَنَّهُمُ اللَّهُ رِزْقاً حَسَناً وَ إِنَّ اللَّهَ لَهُوَ خَيْرُ الرَّازِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2654, N'لَيُدْخِلَنَّهُمْ مُدْخَلاً يَرْضَوْنَهُ وَ إِنَّ اللَّهَ لَعَلِيمٌ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2655, N'ذلِكَ وَ مَنْ عاقَبَ بِمِثْلِ ما عُوقِبَ بِهِ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللَّهُ إِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2656, N'ذلِكَ بِأَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهارِ وَ يُولِجُ النَّهارَ فِي اللَّيْلِ وَ أَنَّ اللَّهَ سَمِيعٌ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2657, N'ذلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَ أَنَّ ما يَدْعُونَ مِنْ دُونِهِ هُوَ الْباطِلُ وَ أَنَّ اللَّهَ هُوَ الْعَلِيُّ الْكَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2658, N'أَ لَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّماءِ ماءً فَتُصْبِحُ الْأَرْضُ مُخْضَرَّةً إِنَّ اللَّهَ لَطِيفٌ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2659, N'لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ إِنَّ اللَّهَ لَهُوَ الْغَنِيُّ الْحَمِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2660, N'أَ لَمْ تَرَ أَنَّ اللَّهَ سَخَّرَ لَكُمْ ما فِي الْأَرْضِ وَ الْفُلْكَ تَجْرِي فِي الْبَحْرِ بِأَمْرِهِ وَ يُمْسِكُ السَّماءَ أَنْ تَقَعَ عَلَى الْأَرْضِ إِلاَّ بِإِذْنِهِ إِنَّ اللَّهَ بِالنَّاسِ لَرَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2661, N'وَ هُوَ الَّذِي أَحْياكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ إِنَّ الْإِنْسانَ لَكَفُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2662, N'لِكُلِّ أُمَّةٍ جَعَلْنا مَنْسَكاً هُمْ ناسِكُوهُ فَلا يُنازِعُنَّكَ فِي الْأَمْرِ وَ ادْعُ إِلى رَبِّكَ إِنَّكَ لَعَلى هُدىً مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2663, N'وَ إِنْ جادَلُوكَ فَقُلِ اللَّهُ أَعْلَمُ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2664, N'اللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيامَةِ فِيما كُنْتُمْ فِيهِ تَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2665, N'أَ لَمْ تَعْلَمْ أَنَّ اللَّهَ يَعْلَمُ ما فِي السَّماءِ وَ الْأَرْضِ إِنَّ ذلِكَ فِي كِتابٍ إِنَّ ذلِكَ عَلَى اللَّهِ يَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2666, N'وَ يَعْبُدُونَ مِنْ دُونِ اللَّهِ ما لَمْ يُنَزِّلْ بِهِ سُلْطاناً وَ ما لَيْسَ لَهُمْ بِهِ عِلْمٌ وَ ما لِلظَّالِمِينَ مِنْ نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2667, N'وَ إِذا تُتْلى عَلَيْهِمْ آياتُنا بَيِّناتٍ تَعْرِفُ فِي وُجُوهِ الَّذِينَ كَفَرُوا الْمُنْكَرَ يَكادُونَ يَسْطُونَ بِالَّذِينَ يَتْلُونَ عَلَيْهِمْ آياتِنا قُلْ أَ فَأُنَبِّئُكُمْ بِشَرٍّ مِنْ ذلِكُمُ النَّارُ وَعَدَهَا اللَّهُ الَّذِينَ كَفَرُوا وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2668, N'يا أَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوا لَهُ إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ لَنْ يَخْلُقُوا ذُباباً وَ لَوِ اجْتَمَعُوا لَهُ وَ إِنْ يَسْلُبْهُمُ الذُّبابُ شَيْئاً لا يَسْتَنْقِذُوهُ مِنْهُ ضَعُفَ الطَّالِبُ وَ الْمَطْلُوبُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2669, N'ما قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ إِنَّ اللَّهَ لَقَوِيٌّ عَزِيزٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2670, N'اللَّهُ يَصْطَفِي مِنَ الْمَلائِكَةِ رُسُلاً وَ مِنَ النَّاسِ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2671, N'يَعْلَمُ ما بَيْنَ أَيْدِيهِمْ وَ ما خَلْفَهُمْ وَ إِلَى اللَّهِ تُرْجَعُ الْأُمُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2672, N'يا أَيُّهَا الَّذِينَ آمَنُوا ارْكَعُوا وَ اسْجُدُوا وَ اعْبُدُوا رَبَّكُمْ وَ افْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2673, N'وَ جاهِدُوا فِي اللَّهِ حَقَّ جِهادِهِ هُوَ اجْتَباكُمْ وَ ما جَعَلَ عَلَيْكُمْ فِي الدِّينِ مِنْ حَرَجٍ مِلَّةَ أَبِيكُمْ إِبْراهِيمَ هُوَ سَمَّاكُمُ الْمُسْلِمِينَ مِنْ قَبْلُ وَ فِي هذا لِيَكُونَ الرَّسُولُ شَهِيداً عَلَيْكُمْ وَ تَكُونُوا شُهَداءَ عَلَى النَّاسِ فَأَقِيمُوا الصَّلاةَ وَ آتُوا الزَّكاةَ وَ اعْتَصِمُوا بِاللَّهِ هُوَ مَوْلاكُمْ فَنِعْمَ الْمَوْلى وَ نِعْمَ النَّصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2674, N'قَدْ أَفْلَحَ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2675, N'الَّذِينَ هُمْ فِي صَلاتِهِمْ خاشِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2676, N'وَ الَّذِينَ هُمْ عَنِ اللَّغْوِ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2677, N'وَ الَّذِينَ هُمْ لِلزَّكاةِ فاعِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2678, N'وَ الَّذِينَ هُمْ لِفُرُوجِهِمْ حافِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2679, N'إِلاَّ عَلى أَزْواجِهِمْ أَوْ ما مَلَكَتْ أَيْمانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2680, N'فَمَنِ ابْتَغى وَراءَ ذلِكَ فَأُولئِكَ هُمُ العادُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2681, N'وَ الَّذِينَ هُمْ لِأَماناتِهِمْ وَ عَهْدِهِمْ راعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2682, N'وَ الَّذِينَ هُمْ عَلى صَلَواتِهِمْ يُحافِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2683, N'أُولئِكَ هُمُ الْوارِثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2684, N'الَّذِينَ يَرِثُونَ الْفِرْدَوْسَ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2685, N'وَ لَقَدْ خَلَقْنَا الْإِنْسانَ مِنْ سُلالَةٍ مِنْ طِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2686, N'ثُمَّ جَعَلْناهُ نُطْفَةً فِي قَرارٍ مَكِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2687, N'ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظاماً فَكَسَوْنَا الْعِظامَ لَحْماً ثُمَّ أَنْشَأْناهُ خَلْقاً آخَرَ فَتَبارَكَ اللَّهُ أَحْسَنُ الْخالِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2688, N'ثُمَّ إِنَّكُمْ بَعْدَ ذلِكَ لَمَيِّتُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2689, N'ثُمَّ إِنَّكُمْ يَوْمَ الْقِيامَةِ تُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2690, N'وَ لَقَدْ خَلَقْنا فَوْقَكُمْ سَبْعَ طَرائِقَ وَ ما كُنَّا عَنِ الْخَلْقِ غافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2691, N'وَ أَنْزَلْنا مِنَ السَّماءِ ماءً بِقَدَرٍ فَأَسْكَنَّاهُ فِي الْأَرْضِ وَ إِنَّا عَلى ذَهابٍ بِهِ لَقادِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2692, N'فَأَنْشَأْنا لَكُمْ بِهِ جَنَّاتٍ مِنْ نَخِيلٍ وَ أَعْنابٍ لَكُمْ فِيها فَواكِهُ كَثِيرَةٌ وَ مِنْها تَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2693, N'وَ شَجَرَةً تَخْرُجُ مِنْ طُورِ سَيْناءَ تَنْبُتُ بِالدُّهْنِ وَ صِبْغٍ لِلْآكِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2694, N'وَ إِنَّ لَكُمْ فِي الْأَنْعامِ لَعِبْرَةً نُسْقِيكُمْ مِمَّا فِي بُطُونِها وَ لَكُمْ فِيها مَنافِعُ كَثِيرَةٌ وَ مِنْها تَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2695, N'وَ عَلَيْها وَ عَلَى الْفُلْكِ تُحْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2696, N'وَ لَقَدْ أَرْسَلْنا نُوحاً إِلى قَوْمِهِ فَقالَ يا قَوْمِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ أَ فَلا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2697, N'فَقالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ ما هذا إِلاَّ بَشَرٌ مِثْلُكُمْ يُرِيدُ أَنْ يَتَفَضَّلَ عَلَيْكُمْ وَ لَوْ شاءَ اللَّهُ لَأَنْزَلَ مَلائِكَةً ما سَمِعْنا بِهذا فِي آبائِنَا الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2698, N'إِنْ هُوَ إِلاَّ رَجُلٌ بِهِ جِنَّةٌ فَتَرَبَّصُوا بِهِ حَتَّى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2699, N'قالَ رَبِّ انْصُرْنِي بِما كَذَّبُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2700, N'فَأَوْحَيْنا إِلَيْهِ أَنِ اصْنَعِ الْفُلْكَ بِأَعْيُنِنا وَ وَحْيِنا فَإِذا جاءَ أَمْرُنا وَ فارَ التَّنُّورُ فَاسْلُكْ فِيها مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَ أَهْلَكَ إِلاَّ مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْ وَ لا تُخاطِبْنِي فِي الَّذِينَ ظَلَمُوا إِنَّهُمْ مُغْرَقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2701, N'فَإِذَا اسْتَوَيْتَ أَنْتَ وَ مَنْ مَعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلَّهِ الَّذِي نَجَّانا مِنَ الْقَوْمِ الظَّالِمِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2702, N'وَ قُلْ رَبِّ أَنْزِلْنِي مُنْزَلاً مُبارَكاً وَ أَنْتَ خَيْرُ الْمُنْزِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2703, N'إِنَّ فِي ذلِكَ لَآياتٍ وَ إِنْ كُنَّا لَمُبْتَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2704, N'ثُمَّ أَنْشَأْنا مِنْ بَعْدِهِمْ قَرْناً آخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2705, N'فَأَرْسَلْنا فِيهِمْ رَسُولاً مِنْهُمْ أَنِ اعْبُدُوا اللَّهَ ما لَكُمْ مِنْ إِلهٍ غَيْرُهُ أَ فَلا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2706, N'وَ قالَ الْمَلَأُ مِنْ قَوْمِهِ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِلِقاءِ الْآخِرَةِ وَ أَتْرَفْناهُمْ فِي الْحَياةِ الدُّنْيا ما هذا إِلاَّ بَشَرٌ مِثْلُكُمْ يَأْكُلُ مِمَّا تَأْكُلُونَ مِنْهُ وَ يَشْرَبُ مِمَّا تَشْرَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2707, N'وَ لَئِنْ أَطَعْتُمْ بَشَراً مِثْلَكُمْ إِنَّكُمْ إِذاً لَخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2708, N'أَ يَعِدُكُمْ أَنَّكُمْ إِذا مِتُّمْ وَ كُنْتُمْ تُراباً وَ عِظاماً أَنَّكُمْ مُخْرَجُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2709, N'هَيْهاتَ هَيْهاتَ لِما تُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2710, N'إِنْ هِيَ إِلاَّ حَياتُنَا الدُّنْيا نَمُوتُ وَ نَحْيا وَ ما نَحْنُ بِمَبْعُوثِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2711, N'إِنْ هُوَ إِلاَّ رَجُلٌ افْتَرى عَلَى اللَّهِ كَذِباً وَ ما نَحْنُ لَهُ بِمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2712, N'قالَ رَبِّ انْصُرْنِي بِما كَذَّبُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2713, N'قالَ عَمَّا قَلِيلٍ لَيُصْبِحُنَّ نادِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2714, N'فَأَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْناهُمْ غُثاءً فَبُعْداً لِلْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2715, N'ثُمَّ أَنْشَأْنا مِنْ بَعْدِهِمْ قُرُوناً آخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2716, N'ما تَسْبِقُ مِنْ أُمَّةٍ أَجَلَها وَ ما يَسْتَأْخِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2717, N'ثُمَّ أَرْسَلْنا رُسُلَنا تَتْرا كُلَّ ما جاءَ أُمَّةً رَسُولُها كَذَّبُوهُ فَأَتْبَعْنا بَعْضَهُمْ بَعْضاً وَ جَعَلْناهُمْ أَحادِيثَ فَبُعْداً لِقَوْمٍ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2718, N'ثُمَّ أَرْسَلْنا مُوسى وَ أَخاهُ هارُونَ بِآياتِنا وَ سُلْطانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2719, N'إِلى فِرْعَوْنَ وَ مَلاَئِهِ فَاسْتَكْبَرُوا وَ كانُوا قَوْماً عالِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2720, N'فَقالُوا أَ نُؤْمِنُ لِبَشَرَيْنِ مِثْلِنا وَ قَوْمُهُما لَنا عابِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2721, N'فَكَذَّبُوهُما فَكانُوا مِنَ الْمُهْلَكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2722, N'وَ لَقَدْ آتَيْنا مُوسَى الْكِتابَ لَعَلَّهُمْ يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2723, N'وَ جَعَلْنَا ابْنَ مَرْيَمَ وَ أُمَّهُ آيَةً وَ آوَيْناهُما إِلى رَبْوَةٍ ذاتِ قَرارٍ وَ مَعِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2724, N'يا أَيُّهَا الرُّسُلُ كُلُوا مِنَ الطَّيِّباتِ وَ اعْمَلُوا صالِحاً إِنِّي بِما تَعْمَلُونَ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2725, N'وَ إِنَّ هذِهِ أُمَّتُكُمْ أُمَّةً واحِدَةً وَ أَنَا رَبُّكُمْ فَاتَّقُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2726, N'فَتَقَطَّعُوا أَمْرَهُمْ بَيْنَهُمْ زُبُراً كُلُّ حِزْبٍ بِما لَدَيْهِمْ فَرِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2727, N'فَذَرْهُمْ فِي غَمْرَتِهِمْ حَتَّى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2728, N'أَ يَحْسَبُونَ أَنَّما نُمِدُّهُمْ بِهِ مِنْ مالٍ وَ بَنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2729, N'نُسارِعُ لَهُمْ فِي الْخَيْراتِ بَلْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2730, N'إِنَّ الَّذِينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2731, N'وَ الَّذِينَ هُمْ بِآياتِ رَبِّهِمْ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2732, N'وَ الَّذِينَ هُمْ بِرَبِّهِمْ لا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2733, N'وَ الَّذِينَ يُؤْتُونَ ما آتَوْا وَ قُلُوبُهُمْ وَجِلَةٌ أَنَّهُمْ إِلى رَبِّهِمْ راجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2734, N'أُولئِكَ يُسارِعُونَ فِي الْخَيْراتِ وَ هُمْ لَها سابِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2735, N'وَ لا نُكَلِّفُ نَفْساً إِلاَّ وُسْعَها وَ لَدَيْنا كِتابٌ يَنْطِقُ بِالْحَقِّ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2736, N'بَلْ قُلُوبُهُمْ فِي غَمْرَةٍ مِنْ هذا وَ لَهُمْ أَعْمالٌ مِنْ دُونِ ذلِكَ هُمْ لَها عامِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2737, N'حَتَّى إِذا أَخَذْنا مُتْرَفِيهِمْ بِالْعَذابِ إِذا هُمْ يَجْأَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2738, N'لا تَجْأَرُوا الْيَوْمَ إِنَّكُمْ مِنَّا لا تُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2739, N'قَدْ كانَتْ آياتِي تُتْلى عَلَيْكُمْ فَكُنْتُمْ عَلى أَعْقابِكُمْ تَنْكِصُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2740, N'مُسْتَكْبِرِينَ بِهِ سامِراً تَهْجُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2741, N'أَ فَلَمْ يَدَّبَّرُوا الْقَوْلَ أَمْ جاءَهُمْ ما لَمْ يَأْتِ آباءَهُمُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2742, N'أَمْ لَمْ يَعْرِفُوا رَسُولَهُمْ فَهُمْ لَهُ مُنْكِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2743, N'أَمْ يَقُولُونَ بِهِ جِنَّةٌ بَلْ جاءَهُمْ بِالْحَقِّ وَ أَكْثَرُهُمْ لِلْحَقِّ كارِهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2744, N'وَ لَوِ اتَّبَعَ الْحَقُّ أَهْواءَهُمْ لَفَسَدَتِ السَّماواتُ وَ الْأَرْضُ وَ مَنْ فِيهِنَّ بَلْ أَتَيْناهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2745, N'أَمْ تَسْأَلُهُمْ خَرْجاً فَخَراجُ رَبِّكَ خَيْرٌ وَ هُوَ خَيْرُ الرَّازِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2746, N'وَ إِنَّكَ لَتَدْعُوهُمْ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2747, N'وَ إِنَّ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ عَنِ الصِّراطِ لَناكِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2748, N'وَ لَوْ رَحِمْناهُمْ وَ كَشَفْنا ما بِهِمْ مِنْ ضُرٍّ لَلَجُّوا فِي طُغْيانِهِمْ يَعْمَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2749, N'وَ لَقَدْ أَخَذْناهُمْ بِالْعَذابِ فَمَا اسْتَكانُوا لِرَبِّهِمْ وَ ما يَتَضَرَّعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2750, N'حَتَّى إِذا فَتَحْنا عَلَيْهِمْ باباً ذا عَذابٍ شَدِيدٍ إِذا هُمْ فِيهِ مُبْلِسُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2751, N'وَ هُوَ الَّذِي أَنْشَأَ لَكُمُ السَّمْعَ وَ الْأَبْصارَ وَ الْأَفْئِدَةَ قَلِيلاً ما تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2752, N'وَ هُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَ إِلَيْهِ تُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2753, N'وَ هُوَ الَّذِي يُحْيِي وَ يُمِيتُ وَ لَهُ اخْتِلافُ اللَّيْلِ وَ النَّهارِ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2754, N'بَلْ قالُوا مِثْلَ ما قالَ الْأَوَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2755, N'قالُوا أَ إِذا مِتْنا وَ كُنَّا تُراباً وَ عِظاماً أَ إِنَّا لَمَبْعُوثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2756, N'لَقَدْ وُعِدْنا نَحْنُ وَ آباؤُنا هذا مِنْ قَبْلُ إِنْ هذا إِلاَّ أَساطِيرُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2757, N'قُلْ لِمَنِ الْأَرْضُ وَ مَنْ فِيها إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2758, N'سَيَقُولُونَ لِلَّهِ قُلْ أَ فَلا تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2759, N'قُلْ مَنْ رَبُّ السَّماواتِ السَّبْعِ وَ رَبُّ الْعَرْشِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2760, N'سَيَقُولُونَ لِلَّهِ قُلْ أَ فَلا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2761, N'قُلْ مَنْ بِيَدِهِ مَلَكُوتُ كُلِّ شَيْ‏ءٍ وَ هُوَ يُجِيرُ وَ لا يُجارُ عَلَيْهِ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2762, N'سَيَقُولُونَ لِلَّهِ قُلْ فَأَنَّى تُسْحَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2763, N'بَلْ أَتَيْناهُمْ بِالْحَقِّ وَ إِنَّهُمْ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2764, N'مَا اتَّخَذَ اللَّهُ مِنْ وَلَدٍ وَ ما كانَ مَعَهُ مِنْ إِلهٍ إِذاً لَذَهَبَ كُلُّ إِلهٍ بِما خَلَقَ وَ لَعَلا بَعْضُهُمْ عَلى بَعْضٍ سُبْحانَ اللَّهِ عَمَّا يَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2765, N'عالِمِ الْغَيْبِ وَ الشَّهادَةِ فَتَعالى عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2766, N'قُلْ رَبِّ إِمَّا تُرِيَنِّي ما يُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2767, N'رَبِّ فَلا تَجْعَلْنِي فِي الْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2768, N'وَ إِنَّا عَلى أَنْ نُرِيَكَ ما نَعِدُهُمْ لَقادِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2769, N'ادْفَعْ بِالَّتِي هِيَ أَحْسَنُ السَّيِّئَةَ نَحْنُ أَعْلَمُ بِما يَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2770, N'وَ قُلْ رَبِّ أَعُوذُ بِكَ مِنْ هَمَزاتِ الشَّياطِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2771, N'وَ أَعُوذُ بِكَ رَبِّ أَنْ يَحْضُرُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2772, N'حَتَّى إِذا جاءَ أَحَدَهُمُ الْمَوْتُ قالَ رَبِّ ارْجِعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2773, N'لَعَلِّي أَعْمَلُ صالِحاً فِيما تَرَكْتُ كَلاَّ إِنَّها كَلِمَةٌ هُوَ قائِلُها وَ مِنْ وَرائِهِمْ بَرْزَخٌ إِلى يَوْمِ يُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2774, N'فَإِذا نُفِخَ فِي الصُّورِ فَلا أَنْسابَ بَيْنَهُمْ يَوْمَئِذٍ وَ لا يَتَساءَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2775, N'فَمَنْ ثَقُلَتْ مَوازِينُهُ فَأُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2776, N'وَ مَنْ خَفَّتْ مَوازِينُهُ فَأُولئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فِي جَهَنَّمَ خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2777, N'تَلْفَحُ وُجُوهَهُمُ النَّارُ وَ هُمْ فِيها كالِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2778, N'أَ لَمْ تَكُنْ آياتِي تُتْلى عَلَيْكُمْ فَكُنْتُمْ بِها تُكَذِّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2779, N'قالُوا رَبَّنا غَلَبَتْ عَلَيْنا شِقْوَتُنا وَ كُنَّا قَوْماً ضالِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2780, N'رَبَّنا أَخْرِجْنا مِنْها فَإِنْ عُدْنا فَإِنَّا ظالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2781, N'قالَ اخْسَؤُا فِيها وَ لا تُكَلِّمُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2782, N'إِنَّهُ كانَ فَرِيقٌ مِنْ عِبادِي يَقُولُونَ رَبَّنا آمَنَّا فَاغْفِرْ لَنا وَ ارْحَمْنا وَ أَنْتَ خَيْرُ الرَّاحِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2783, N'فَاتَّخَذْتُمُوهُمْ سِخْرِيًّا حَتَّى أَنْسَوْكُمْ ذِكْرِي وَ كُنْتُمْ مِنْهُمْ تَضْحَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2784, N'إِنِّي جَزَيْتُهُمُ الْيَوْمَ بِما صَبَرُوا أَنَّهُمْ هُمُ الْفائِزُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2785, N'قالَ كَمْ لَبِثْتُمْ فِي الْأَرْضِ عَدَدَ سِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2786, N'قالُوا لَبِثْنا يَوْماً أَوْ بَعْضَ يَوْمٍ فَسْئَلِ الْعادِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2787, N'قالَ إِنْ لَبِثْتُمْ إِلاَّ قَلِيلاً لَوْ أَنَّكُمْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2788, N'أَ فَحَسِبْتُمْ أَنَّما خَلَقْناكُمْ عَبَثاً وَ أَنَّكُمْ إِلَيْنا لا تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2789, N'فَتَعالَى اللَّهُ الْمَلِكُ الْحَقُّ لا إِلهَ إِلاَّ هُوَ رَبُّ الْعَرْشِ الْكَرِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2790, N'وَ مَنْ يَدْعُ مَعَ اللَّهِ إِلهاً آخَرَ لا بُرْهانَ لَهُ بِهِ فَإِنَّما حِسابُهُ عِنْدَ رَبِّهِ إِنَّهُ لا يُفْلِحُ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2791, N'وَ قُلْ رَبِّ اغْفِرْ وَ ارْحَمْ وَ أَنْتَ خَيْرُ الرَّاحِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2792, N'سُورَةٌ أَنْزَلْناها وَ فَرَضْناها وَ أَنْزَلْنا فِيها آياتٍ بَيِّناتٍ لَعَلَّكُمْ تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2793, N'الزَّانِيَةُ وَ الزَّانِي فَاجْلِدُوا كُلَّ واحِدٍ مِنْهُما مِائَةَ جَلْدَةٍ وَ لا تَأْخُذْكُمْ بِهِما رَأْفَةٌ فِي دِينِ اللَّهِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ لْيَشْهَدْ عَذابَهُما طائِفَةٌ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2794, N'الزَّانِي لا يَنْكِحُ إِلاَّ زانِيَةً أَوْ مُشْرِكَةً وَ الزَّانِيَةُ لا يَنْكِحُها إِلاَّ زانٍ أَوْ مُشْرِكٌ وَ حُرِّمَ ذلِكَ عَلَى الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2795, N'وَ الَّذِينَ يَرْمُونَ الْمُحْصَناتِ ثُمَّ لَمْ يَأْتُوا بِأَرْبَعَةِ شُهَداءَ فَاجْلِدُوهُمْ ثَمانِينَ جَلْدَةً وَ لا تَقْبَلُوا لَهُمْ شَهادَةً أَبَداً وَ أُولئِكَ هُمُ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2796, N'إِلاَّ الَّذِينَ تابُوا مِنْ بَعْدِ ذلِكَ وَ أَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2797, N'وَ الَّذِينَ يَرْمُونَ أَزْواجَهُمْ وَ لَمْ يَكُنْ لَهُمْ شُهَداءُ إِلاَّ أَنْفُسُهُمْ فَشَهادَةُ أَحَدِهِمْ أَرْبَعُ شَهاداتٍ بِاللَّهِ إِنَّهُ لَمِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2798, N'وَ الْخامِسَةُ أَنَّ لَعْنَتَ اللَّهِ عَلَيْهِ إِنْ كانَ مِنَ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2799, N'وَ يَدْرَؤُا عَنْهَا الْعَذابَ أَنْ تَشْهَدَ أَرْبَعَ شَهاداتٍ بِاللَّهِ إِنَّهُ لَمِنَ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2800, N'وَ الْخامِسَةَ أَنَّ غَضَبَ اللَّهِ عَلَيْها إِنْ كانَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2801, N'وَ لَوْ لا فَضْلُ اللَّهِ عَلَيْكُمْ وَ رَحْمَتُهُ وَ أَنَّ اللَّهَ تَوَّابٌ حَكِيمٌ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2802, N'إِنَّ الَّذِينَ جاؤُ بِالْإِفْكِ عُصْبَةٌ مِنْكُمْ لا تَحْسَبُوهُ شَرًّا لَكُمْ بَلْ هُوَ خَيْرٌ لَكُمْ لِكُلِّ امْرِئٍ مِنْهُمْ مَا اكْتَسَبَ مِنَ الْإِثْمِ وَ الَّذِي تَوَلَّى كِبْرَهُ مِنْهُمْ لَهُ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2803, N'لَوْ لا إِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَ الْمُؤْمِناتُ بِأَنْفُسِهِمْ خَيْراً وَ قالُوا هذا إِفْكٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2804, N'لَوْ لا جاؤُ عَلَيْهِ بِأَرْبَعَةِ شُهَداءَ فَإِذْ لَمْ يَأْتُوا بِالشُّهَداءِ فَأُولئِكَ عِنْدَ اللَّهِ هُمُ الْكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2805, N'وَ لَوْ لا فَضْلُ اللَّهِ عَلَيْكُمْ وَ رَحْمَتُهُ فِي الدُّنْيا وَ الْآخِرَةِ لَمَسَّكُمْ فِيما أَفَضْتُمْ فِيهِ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2806, N'إِذْ تَلَقَّوْنَهُ بِأَلْسِنَتِكُمْ وَ تَقُولُونَ بِأَفْواهِكُمْ ما لَيْسَ لَكُمْ بِهِ عِلْمٌ وَ تَحْسَبُونَهُ هَيِّناً وَ هُوَ عِنْدَ اللَّهِ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2807, N'وَ لَوْ لا إِذْ سَمِعْتُمُوهُ قُلْتُمْ ما يَكُونُ لَنا أَنْ نَتَكَلَّمَ بِهذا سُبْحانَكَ هذا بُهْتانٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2808, N'يَعِظُكُمُ اللَّهُ أَنْ تَعُودُوا لِمِثْلِهِ أَبَداً إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2809, N'وَ يُبَيِّنُ اللَّهُ لَكُمُ الْآياتِ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2810, N'إِنَّ الَّذِينَ يُحِبُّونَ أَنْ تَشِيعَ الْفاحِشَةُ فِي الَّذِينَ آمَنُوا لَهُمْ عَذابٌ أَلِيمٌ فِي الدُّنْيا وَ الْآخِرَةِ وَ اللَّهُ يَعْلَمُ وَ أَنْتُمْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2811, N'وَ لَوْ لا فَضْلُ اللَّهِ عَلَيْكُمْ وَ رَحْمَتُهُ وَ أَنَّ اللَّهَ رَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2812, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّبِعُوا خُطُواتِ الشَّيْطانِ وَ مَنْ يَتَّبِعْ خُطُواتِ الشَّيْطانِ فَإِنَّهُ يَأْمُرُ بِالْفَحْشاءِ وَ الْمُنْكَرِ وَ لَوْ لا فَضْلُ اللَّهِ عَلَيْكُمْ وَ رَحْمَتُهُ ما زَكى مِنْكُمْ مِنْ أَحَدٍ أَبَداً وَ لكِنَّ اللَّهَ يُزَكِّي مَنْ يَشاءُ وَ اللَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2813, N'وَ لا يَأْتَلِ أُولُوا الْفَضْلِ مِنْكُمْ وَ السَّعَةِ أَنْ يُؤْتُوا أُولِي الْقُرْبى وَ الْمَساكِينَ وَ الْمُهاجِرِينَ فِي سَبِيلِ اللَّهِ وَ لْيَعْفُوا وَ لْيَصْفَحُوا أَ لا تُحِبُّونَ أَنْ يَغْفِرَ اللَّهُ لَكُمْ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2814, N'إِنَّ الَّذِينَ يَرْمُونَ الْمُحْصَناتِ الْغافِلاتِ الْمُؤْمِناتِ لُعِنُوا فِي الدُّنْيا وَ الْآخِرَةِ وَ لَهُمْ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2815, N'يَوْمَ تَشْهَدُ عَلَيْهِمْ أَلْسِنَتُهُمْ وَ أَيْدِيهِمْ وَ أَرْجُلُهُمْ بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2816, N'يَوْمَئِذٍ يُوَفِّيهِمُ اللَّهُ دِينَهُمُ الْحَقَّ وَ يَعْلَمُونَ أَنَّ اللَّهَ هُوَ الْحَقُّ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2817, N'الْخَبِيثاتُ لِلْخَبِيثِينَ وَ الْخَبِيثُونَ لِلْخَبِيثاتِ وَ الطَّيِّباتُ لِلطَّيِّبِينَ وَ الطَّيِّبُونَ لِلطَّيِّباتِ أُولئِكَ مُبَرَّؤُنَ مِمَّا يَقُولُونَ لَهُمْ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2818, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَدْخُلُوا بُيُوتاً غَيْرَ بُيُوتِكُمْ حَتَّى تَسْتَأْنِسُوا وَ تُسَلِّمُوا عَلى أَهْلِها ذلِكُمْ خَيْرٌ لَكُمْ لَعَلَّكُمْ تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2819, N'فَإِنْ لَمْ تَجِدُوا فِيها أَحَداً فَلا تَدْخُلُوها حَتَّى يُؤْذَنَ لَكُمْ وَ إِنْ قِيلَ لَكُمُ ارْجِعُوا فَارْجِعُوا هُوَ أَزْكى لَكُمْ وَ اللَّهُ بِما تَعْمَلُونَ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2820, N'لَيْسَ عَلَيْكُمْ جُناحٌ أَنْ تَدْخُلُوا بُيُوتاً غَيْرَ مَسْكُونَةٍ فِيها مَتاعٌ لَكُمْ وَ اللَّهُ يَعْلَمُ ما تُبْدُونَ وَ ما تَكْتُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2821, N'قُلْ لِلْمُؤْمِنِينَ يَغُضُّوا مِنْ أَبْصارِهِمْ وَ يَحْفَظُوا فُرُوجَهُمْ ذلِكَ أَزْكى لَهُمْ إِنَّ اللَّهَ خَبِيرٌ بِما يَصْنَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2822, N'وَ قُلْ لِلْمُؤْمِناتِ يَغْضُضْنَ مِنْ أَبْصارِهِنَّ وَ يَحْفَظْنَ فُرُوجَهُنَّ وَ لا يُبْدِينَ زِينَتَهُنَّ إِلاَّ ما ظَهَرَ مِنْها وَ لْيَضْرِبْنَ بِخُمُرِهِنَّ عَلى جُيُوبِهِنَّ وَ لا يُبْدِينَ زِينَتَهُنَّ إِلاَّ لِبُعُولَتِهِنَّ أَوْ آبائِهِنَّ أَوْ آباءِ بُعُولَتِهِنَّ أَوْ أَبْنائِهِنَّ أَوْ أَبْناءِ بُعُولَتِهِنَّ أَوْ إِخْوانِهِنَّ أَوْ بَنِي إِخْوانِهِنَّ أَوْ بَنِي أَخَواتِهِنَّ أَوْ نِسائِهِنَّ أَوْ ما مَلَكَتْ أَيْمانُهُنَّ أَوِ التَّابِعِينَ غَيْرِ أُولِي الْإِرْبَةِ مِنَ الرِّجالِ أَوِ الطِّفْلِ الَّذِينَ لَمْ يَظْهَرُوا عَلى عَوْراتِ النِّساءِ وَ لا يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ ما يُخْفِينَ مِنْ زِينَتِهِنَّ وَ تُوبُوا إِلَى اللَّهِ جَمِيعاً أَيُّهَا الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2823, N'وَ أَنْكِحُوا الْأَيامى مِنْكُمْ وَ الصَّالِحِينَ مِنْ عِبادِكُمْ وَ إِمائِكُمْ إِنْ يَكُونُوا فُقَراءَ يُغْنِهِمُ اللَّهُ مِنْ فَضْلِهِ وَ اللَّهُ واسِعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2824, N'وَ لْيَسْتَعْفِفِ الَّذِينَ لا يَجِدُونَ نِكاحاً حَتَّى يُغْنِيَهُمُ اللَّهُ مِنْ فَضْلِهِ وَ الَّذِينَ يَبْتَغُونَ الْكِتابَ مِمَّا مَلَكَتْ أَيْمانُكُمْ فَكاتِبُوهُمْ إِنْ عَلِمْتُمْ فِيهِمْ خَيْراً وَ آتُوهُمْ مِنْ مالِ اللَّهِ الَّذِي آتاكُمْ وَ لا تُكْرِهُوا فَتَياتِكُمْ عَلَى الْبِغاءِ إِنْ أَرَدْنَ تَحَصُّناً لِتَبْتَغُوا عَرَضَ الْحَياةِ الدُّنْيا وَ مَنْ يُكْرِهْهُنَّ فَإِنَّ اللَّهَ مِنْ بَعْدِ إِكْراهِهِنَّ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2825, N'وَ لَقَدْ أَنْزَلْنا إِلَيْكُمْ آياتٍ مُبَيِّناتٍ وَ مَثَلاً مِنَ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ وَ مَوْعِظَةً لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2826, N'اللَّهُ نُورُ السَّماواتِ وَ الْأَرْضِ مَثَلُ نُورِهِ كَمِشْكاةٍ فِيها مِصْباحٌ الْمِصْباحُ فِي زُجاجَةٍ الزُّجاجَةُ كَأَنَّها كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِنْ شَجَرَةٍ مُبارَكَةٍ زَيْتُونَةٍ لا شَرْقِيَّةٍ وَ لا غَرْبِيَّةٍ يَكادُ زَيْتُها يُضِي‏ءُ وَ لَوْ لَمْ تَمْسَسْهُ نارٌ نُورٌ عَلى نُورٍ يَهْدِي اللَّهُ لِنُورِهِ مَنْ يَشاءُ وَ يَضْرِبُ اللَّهُ الْأَمْثالَ لِلنَّاسِ وَ اللَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2827, N'فِي بُيُوتٍ أَذِنَ اللَّهُ أَنْ تُرْفَعَ وَ يُذْكَرَ فِيهَا اسْمُهُ يُسَبِّحُ لَهُ فِيها بِالْغُدُوِّ وَ الْآصالِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2828, N'رِجالٌ لا تُلْهِيهِمْ تِجارَةٌ وَ لا بَيْعٌ عَنْ ذِكْرِ اللَّهِ وَ إِقامِ الصَّلاةِ وَ إِيتاءِ الزَّكاةِ يَخافُونَ يَوْماً تَتَقَلَّبُ فِيهِ الْقُلُوبُ وَ الْأَبْصارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2829, N'لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ ما عَمِلُوا وَ يَزِيدَهُمْ مِنْ فَضْلِهِ وَ اللَّهُ يَرْزُقُ مَنْ يَشاءُ بِغَيْرِ حِسابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2830, N'وَ الَّذِينَ كَفَرُوا أَعْمالُهُمْ كَسَرابٍ بِقِيعَةٍ يَحْسَبُهُ الظَّمْآنُ ماءً حَتَّى إِذا جاءَهُ لَمْ يَجِدْهُ شَيْئاً وَ وَجَدَ اللَّهَ عِنْدَهُ فَوَفَّاهُ حِسابَهُ وَ اللَّهُ سَرِيعُ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2831, N'أَوْ كَظُلُماتٍ فِي بَحْرٍ لُجِّيٍّ يَغْشاهُ مَوْجٌ مِنْ فَوْقِهِ مَوْجٌ مِنْ فَوْقِهِ سَحابٌ ظُلُماتٌ بَعْضُها فَوْقَ بَعْضٍ إِذا أَخْرَجَ يَدَهُ لَمْ يَكَدْ يَراها وَ مَنْ لَمْ يَجْعَلِ اللَّهُ لَهُ نُوراً فَما لَهُ مِنْ نُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2832, N'أَ لَمْ تَرَ أَنَّ اللَّهَ يُسَبِّحُ لَهُ مَنْ فِي السَّماواتِ وَ الْأَرْضِ وَ الطَّيْرُ صَافَّاتٍ كُلٌّ قَدْ عَلِمَ صَلاتَهُ وَ تَسْبِيحَهُ وَ اللَّهُ عَلِيمٌ بِما يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2833, N'وَ لِلَّهِ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ إِلَى اللَّهِ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2834, N'أَ لَمْ تَرَ أَنَّ اللَّهَ يُزْجِي سَحاباً ثُمَّ يُؤَلِّفُ بَيْنَهُ ثُمَّ يَجْعَلُهُ رُكاماً فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلالِهِ وَ يُنَزِّلُ مِنَ السَّماءِ مِنْ جِبالٍ فِيها مِنْ بَرَدٍ فَيُصِيبُ بِهِ مَنْ يَشاءُ وَ يَصْرِفُهُ عَنْ مَنْ يَشاءُ يَكادُ سَنا بَرْقِهِ يَذْهَبُ بِالْأَبْصارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2835, N'يُقَلِّبُ اللَّهُ اللَّيْلَ وَ النَّهارَ إِنَّ فِي ذلِكَ لَعِبْرَةً لِأُولِي الْأَبْصارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2836, N'وَ اللَّهُ خَلَقَ كُلَّ دَابَّةٍ مِنْ ماءٍ فَمِنْهُمْ مَنْ يَمْشِي عَلى بَطْنِهِ وَ مِنْهُمْ مَنْ يَمْشِي عَلى رِجْلَيْنِ وَ مِنْهُمْ مَنْ يَمْشِي عَلى أَرْبَعٍ يَخْلُقُ اللَّهُ ما يَشاءُ إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2837, N'لَقَدْ أَنْزَلْنا آياتٍ مُبَيِّناتٍ وَ اللَّهُ يَهْدِي مَنْ يَشاءُ إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2838, N'وَ يَقُولُونَ آمَنَّا بِاللَّهِ وَ بِالرَّسُولِ وَ أَطَعْنا ثُمَّ يَتَوَلَّى فَرِيقٌ مِنْهُمْ مِنْ بَعْدِ ذلِكَ وَ ما أُولئِكَ بِالْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2839, N'وَ إِذا دُعُوا إِلَى اللَّهِ وَ رَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ إِذا فَرِيقٌ مِنْهُمْ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2840, N'وَ إِنْ يَكُنْ لَهُمُ الْحَقُّ يَأْتُوا إِلَيْهِ مُذْعِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2841, N'أَ فِي قُلُوبِهِمْ مَرَضٌ أَمِ ارْتابُوا أَمْ يَخافُونَ أَنْ يَحِيفَ اللَّهُ عَلَيْهِمْ وَ رَسُولُهُ بَلْ أُولئِكَ هُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2842, N'إِنَّما كانَ قَوْلَ الْمُؤْمِنِينَ إِذا دُعُوا إِلَى اللَّهِ وَ رَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ أَنْ يَقُولُوا سَمِعْنا وَ أَطَعْنا وَ أُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2843, N'وَ مَنْ يُطِعِ اللَّهَ وَ رَسُولَهُ وَ يَخْشَ اللَّهَ وَ يَتَّقْهِ فَأُولئِكَ هُمُ الْفائِزُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2844, N'وَ أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمانِهِمْ لَئِنْ أَمَرْتَهُمْ لَيَخْرُجُنَّ قُلْ لا تُقْسِمُوا طاعَةٌ مَعْرُوفَةٌ إِنَّ اللَّهَ خَبِيرٌ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2845, N'قُلْ أَطِيعُوا اللَّهَ وَ أَطِيعُوا الرَّسُولَ فَإِنْ تَوَلَّوْا فَإِنَّما عَلَيْهِ ما حُمِّلَ وَ عَلَيْكُمْ ما حُمِّلْتُمْ وَ إِنْ تُطِيعُوهُ تَهْتَدُوا وَ ما عَلَى الرَّسُولِ إِلاَّ الْبَلاغُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2846, N'وَعَدَ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَ عَمِلُوا الصَّالِحاتِ لَيَسْتَخْلِفَنَّهُمْ فِي الْأَرْضِ كَمَا اسْتَخْلَفَ الَّذِينَ مِنْ قَبْلِهِمْ وَ لَيُمَكِّنَنَّ لَهُمْ دِينَهُمُ الَّذِي ارْتَضى لَهُمْ وَ لَيُبَدِّلَنَّهُمْ مِنْ بَعْدِ خَوْفِهِمْ أَمْناً يَعْبُدُونَنِي لا يُشْرِكُونَ بِي شَيْئاً وَ مَنْ كَفَرَ بَعْدَ ذلِكَ فَأُولئِكَ هُمُ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2847, N'وَ أَقِيمُوا الصَّلاةَ وَ آتُوا الزَّكاةَ وَ أَطِيعُوا الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2848, N'لا تَحْسَبَنَّ الَّذِينَ كَفَرُوا مُعْجِزِينَ فِي الْأَرْضِ وَ مَأْواهُمُ النَّارُ وَ لَبِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2849, N'يا أَيُّهَا الَّذِينَ آمَنُوا لِيَسْتَأْذِنْكُمُ الَّذِينَ مَلَكَتْ أَيْمانُكُمْ وَ الَّذِينَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلاثَ مَرَّاتٍ مِنْ قَبْلِ صَلاةِ الْفَجْرِ وَ حِينَ تَضَعُونَ ثِيابَكُمْ مِنَ الظَّهِيرَةِ وَ مِنْ بَعْدِ صَلاةِ الْعِشاءِ ثَلاثُ عَوْراتٍ لَكُمْ لَيْسَ عَلَيْكُمْ وَ لا عَلَيْهِمْ جُناحٌ بَعْدَهُنَّ طَوَّافُونَ عَلَيْكُمْ بَعْضُكُمْ عَلى بَعْضٍ كَذلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآياتِ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2850, N'وَ إِذا بَلَغَ الْأَطْفالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوا كَمَا اسْتَأْذَنَ الَّذِينَ مِنْ قَبْلِهِمْ كَذلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آياتِهِ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2851, N'وَ الْقَواعِدُ مِنَ النِّساءِ اللاَّتِي لا يَرْجُونَ نِكاحاً فَلَيْسَ عَلَيْهِنَّ جُناحٌ أَنْ يَضَعْنَ ثِيابَهُنَّ غَيْرَ مُتَبَرِّجاتٍ بِزِينَةٍ وَ أَنْ يَسْتَعْفِفْنَ خَيْرٌ لَهُنَّ وَ اللَّهُ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2852, N'لَيْسَ عَلَى الْأَعْمى حَرَجٌ وَ لا عَلَى الْأَعْرَجِ حَرَجٌ وَ لا عَلَى الْمَرِيضِ حَرَجٌ وَ لا عَلى أَنْفُسِكُمْ أَنْ تَأْكُلُوا مِنْ بُيُوتِكُمْ أَوْ بُيُوتِ آبائِكُمْ أَوْ بُيُوتِ أُمَّهاتِكُمْ أَوْ بُيُوتِ إِخْوانِكُمْ أَوْ بُيُوتِ أَخَواتِكُمْ أَوْ بُيُوتِ أَعْمامِكُمْ أَوْ بُيُوتِ عَمَّاتِكُمْ أَوْ بُيُوتِ أَخْوالِكُمْ أَوْ بُيُوتِ خالاتِكُمْ أَوْ ما مَلَكْتُمْ مَفاتِحَهُ أَوْ صَدِيقِكُمْ لَيْسَ عَلَيْكُمْ جُناحٌ أَنْ تَأْكُلُوا جَمِيعاً أَوْ أَشْتاتاً فَإِذا دَخَلْتُمْ بُيُوتاً فَسَلِّمُوا عَلى أَنْفُسِكُمْ تَحِيَّةً مِنْ عِنْدِ اللَّهِ مُبارَكَةً طَيِّبَةً كَذلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآياتِ لَعَلَّكُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2853, N'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَ رَسُولِهِ وَ إِذا كانُوا مَعَهُ عَلى أَمْرٍ جامِعٍ لَمْ يَذْهَبُوا حَتَّى يَسْتَأْذِنُوهُ إِنَّ الَّذِينَ يَسْتَأْذِنُونَكَ أُولئِكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَ رَسُولِهِ فَإِذَا اسْتَأْذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِمَنْ شِئْتَ مِنْهُمْ وَ اسْتَغْفِرْ لَهُمُ اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2854, N'لا تَجْعَلُوا دُعاءَ الرَّسُولِ بَيْنَكُمْ كَدُعاءِ بَعْضِكُمْ بَعْضاً قَدْ يَعْلَمُ اللَّهُ الَّذِينَ يَتَسَلَّلُونَ مِنْكُمْ لِواذاً فَلْيَحْذَرِ الَّذِينَ يُخالِفُونَ عَنْ أَمْرِهِ أَنْ تُصِيبَهُمْ فِتْنَةٌ أَوْ يُصِيبَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2855, N'أَلا إِنَّ لِلَّهِ ما فِي السَّماواتِ وَ الْأَرْضِ قَدْ يَعْلَمُ ما أَنْتُمْ عَلَيْهِ وَ يَوْمَ يُرْجَعُونَ إِلَيْهِ فَيُنَبِّئُهُمْ بِما عَمِلُوا وَ اللَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2856, N'تَبارَكَ الَّذِي نَزَّلَ الْفُرْقانَ عَلى عَبْدِهِ لِيَكُونَ لِلْعالَمِينَ نَذِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2857, N'الَّذِي لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ لَمْ يَتَّخِذْ وَلَداً وَ لَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ وَ خَلَقَ كُلَّ شَيْ‏ءٍ فَقَدَّرَهُ تَقْدِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2858, N'وَ اتَّخَذُوا مِنْ دُونِهِ آلِهَةً لا يَخْلُقُونَ شَيْئاً وَ هُمْ يُخْلَقُونَ وَ لا يَمْلِكُونَ لِأَنْفُسِهِمْ ضَرًّا وَ لا نَفْعاً وَ لا يَمْلِكُونَ مَوْتاً وَ لا حَياةً وَ لا نُشُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2859, N'وَ قالَ الَّذِينَ كَفَرُوا إِنْ هَذا إِلاَّ إِفْكٌ افْتَراهُ وَ أَعانَهُ عَلَيْهِ قَوْمٌ آخَرُونَ فَقَدْ جاؤُ ظُلْماً وَ زُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2860, N'وَ قالُوا أَساطِيرُ الْأَوَّلِينَ اكْتَتَبَها فَهِيَ تُمْلى عَلَيْهِ بُكْرَةً وَ أَصِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2861, N'قُلْ أَنْزَلَهُ الَّذِي يَعْلَمُ السِّرَّ فِي السَّماواتِ وَ الْأَرْضِ إِنَّهُ كانَ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2862, N'وَ قالُوا ما لِهذَا الرَّسُولِ يَأْكُلُ الطَّعامَ وَ يَمْشِي فِي الْأَسْواقِ لَوْ لا أُنْزِلَ إِلَيْهِ مَلَكٌ فَيَكُونَ مَعَهُ نَذِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2863, N'أَوْ يُلْقى إِلَيْهِ كَنْزٌ أَوْ تَكُونُ لَهُ جَنَّةٌ يَأْكُلُ مِنْها وَ قالَ الظَّالِمُونَ إِنْ تَتَّبِعُونَ إِلاَّ رَجُلاً مَسْحُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2864, N'انْظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثالَ فَضَلُّوا فَلا يَسْتَطِيعُونَ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2865, N'تَبارَكَ الَّذِي إِنْ شاءَ جَعَلَ لَكَ خَيْراً مِنْ ذلِكَ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ وَ يَجْعَلْ لَكَ قُصُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2866, N'بَلْ كَذَّبُوا بِالسَّاعَةِ وَ أَعْتَدْنا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَعِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2867, N'إِذا رَأَتْهُمْ مِنْ مَكانٍ بَعِيدٍ سَمِعُوا لَها تَغَيُّظاً وَ زَفِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2868, N'وَ إِذا أُلْقُوا مِنْها مَكاناً ضَيِّقاً مُقَرَّنِينَ دَعَوْا هُنالِكَ ثُبُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2869, N'لا تَدْعُوا الْيَوْمَ ثُبُوراً واحِداً وَ ادْعُوا ثُبُوراً كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2870, N'قُلْ أَ ذلِكَ خَيْرٌ أَمْ جَنَّةُ الْخُلْدِ الَّتِي وُعِدَ الْمُتَّقُونَ كانَتْ لَهُمْ جَزاءً وَ مَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2871, N'لَهُمْ فِيها ما يَشاؤُنَ خالِدِينَ كانَ عَلى رَبِّكَ وَعْداً مَسْؤُلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2872, N'وَ يَوْمَ يَحْشُرُهُمْ وَ ما يَعْبُدُونَ مِنْ دُونِ اللَّهِ فَيَقُولُ أَ أَنْتُمْ أَضْلَلْتُمْ عِبادِي هؤُلاءِ أَمْ هُمْ ضَلُّوا السَّبِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2873, N'قالُوا سُبْحانَكَ ما كانَ يَنْبَغِي لَنا أَنْ نَتَّخِذَ مِنْ دُونِكَ مِنْ أَوْلِياءَ وَ لكِنْ مَتَّعْتَهُمْ وَ آباءَهُمْ حَتَّى نَسُوا الذِّكْرَ وَ كانُوا قَوْماً بُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2874, N'فَقَدْ كَذَّبُوكُمْ بِما تَقُولُونَ فَما تَسْتَطِيعُونَ صَرْفاً وَ لا نَصْراً وَ مَنْ يَظْلِمْ مِنْكُمْ نُذِقْهُ عَذاباً كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2875, N'وَ ما أَرْسَلْنا قَبْلَكَ مِنَ الْمُرْسَلِينَ إِلاَّ إِنَّهُمْ لَيَأْكُلُونَ الطَّعامَ وَ يَمْشُونَ فِي الْأَسْواقِ وَ جَعَلْنا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً أَ تَصْبِرُونَ وَ كانَ رَبُّكَ بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2876, N'وَ قالَ الَّذِينَ لا يَرْجُونَ لِقاءَنا لَوْ لا أُنْزِلَ عَلَيْنَا الْمَلائِكَةُ أَوْ نَرى رَبَّنا لَقَدِ اسْتَكْبَرُوا فِي أَنْفُسِهِمْ وَ عَتَوْا عُتُوًّا كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2877, N'يَوْمَ يَرَوْنَ الْمَلائِكَةَ لا بُشْرى يَوْمَئِذٍ لِلْمُجْرِمِينَ وَ يَقُولُونَ حِجْراً مَحْجُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2878, N'وَ قَدِمْنا إِلى ما عَمِلُوا مِنْ عَمَلٍ فَجَعَلْناهُ هَباءً مَنْثُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2879, N'أَصْحابُ الْجَنَّةِ يَوْمَئِذٍ خَيْرٌ مُسْتَقَرًّا وَ أَحْسَنُ مَقِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2880, N'وَ يَوْمَ تَشَقَّقُ السَّماءُ بِالْغَمامِ وَ نُزِّلَ الْمَلائِكَةُ تَنْزِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2881, N'الْمُلْكُ يَوْمَئِذٍ الْحَقُّ لِلرَّحْمنِ وَ كانَ يَوْماً عَلَى الْكافِرِينَ عَسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2882, N'وَ يَوْمَ يَعَضُّ الظَّالِمُ عَلى يَدَيْهِ يَقُولُ يا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2883, N'يا وَيْلَتى لَيْتَنِي لَمْ أَتَّخِذْ فُلاناً خَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2884, N'لَقَدْ أَضَلَّنِي عَنِ الذِّكْرِ بَعْدَ إِذْ جاءَنِي وَ كانَ الشَّيْطانُ لِلْإِنْسانِ خَذُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2885, N'وَ قالَ الرَّسُولُ يا رَبِّ إِنَّ قَوْمِي اتَّخَذُوا هذَا الْقُرْآنَ مَهْجُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2886, N'وَ كَذلِكَ جَعَلْنا لِكُلِّ نَبِيٍّ عَدُوًّا مِنَ الْمُجْرِمِينَ وَ كَفى بِرَبِّكَ هادِياً وَ نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2887, N'وَ قالَ الَّذِينَ كَفَرُوا لَوْ لا نُزِّلَ عَلَيْهِ الْقُرْآنُ جُمْلَةً واحِدَةً كَذلِكَ لِنُثَبِّتَ بِهِ فُؤادَكَ وَ رَتَّلْناهُ تَرْتِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2888, N'وَ لا يَأْتُونَكَ بِمَثَلٍ إِلاَّ جِئْناكَ بِالْحَقِّ وَ أَحْسَنَ تَفْسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2889, N'الَّذِينَ يُحْشَرُونَ عَلى وُجُوهِهِمْ إِلى جَهَنَّمَ أُوْلئِكَ شَرٌّ مَكاناً وَ أَضَلُّ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2890, N'وَ لَقَدْ آتَيْنا مُوسَى الْكِتابَ وَ جَعَلْنا مَعَهُ أَخاهُ هارُونَ وَزِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2891, N'فَقُلْنَا اذْهَبا إِلَى الْقَوْمِ الَّذِينَ كَذَّبُوا بِآياتِنا فَدَمَّرْناهُمْ تَدْمِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2892, N'وَ قَوْمَ نُوحٍ لَمَّا كَذَّبُوا الرُّسُلَ أَغْرَقْناهُمْ وَ جَعَلْناهُمْ لِلنَّاسِ آيَةً وَ أَعْتَدْنا لِلظَّالِمِينَ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2893, N'وَ عاداً وَ ثَمُودَ وَ أَصْحابَ الرَّسِّ وَ قُرُوناً بَيْنَ ذلِكَ كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2894, N'وَ كُلاًّ ضَرَبْنا لَهُ الْأَمْثالَ وَ كُلاًّ تَبَّرْنا تَتْبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2895, N'وَ لَقَدْ أَتَوْا عَلَى الْقَرْيَةِ الَّتِي أُمْطِرَتْ مَطَرَ السَّوْءِ أَ فَلَمْ يَكُونُوا يَرَوْنَها بَلْ كانُوا لا يَرْجُونَ نُشُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2896, N'وَ إِذا رَأَوْكَ إِنْ يَتَّخِذُونَكَ إِلاَّ هُزُواً أَ هذَا الَّذِي بَعَثَ اللَّهُ رَسُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2897, N'إِنْ كادَ لَيُضِلُّنا عَنْ آلِهَتِنا لَوْ لا أَنْ صَبَرْنا عَلَيْها وَ سَوْفَ يَعْلَمُونَ حِينَ يَرَوْنَ الْعَذابَ مَنْ أَضَلُّ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2898, N'أَ رَأَيْتَ مَنِ اتَّخَذَ إِلهَهُ هَواهُ أَ فَأَنْتَ تَكُونُ عَلَيْهِ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2899, N'أَمْ تَحْسَبُ أَنَّ أَكْثَرَهُمْ يَسْمَعُونَ أَوْ يَعْقِلُونَ إِنْ هُمْ إِلاَّ كَالْأَنْعامِ بَلْ هُمْ أَضَلُّ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2900, N'أَ لَمْ تَرَ إِلى رَبِّكَ كَيْفَ مَدَّ الظِّلَّ وَ لَوْ شاءَ لَجَعَلَهُ ساكِناً ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2901, N'ثُمَّ قَبَضْناهُ إِلَيْنا قَبْضاً يَسِيراً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2902, N'وَ هُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِباساً وَ النَّوْمَ سُباتاً وَ جَعَلَ النَّهارَ نُشُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2903, N'وَ هُوَ الَّذِي أَرْسَلَ الرِّياحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِهِ وَ أَنْزَلْنا مِنَ السَّماءِ ماءً طَهُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2904, N'لِنُحْيِيَ بِهِ بَلْدَةً مَيْتاً وَ نُسْقِيَهُ مِمَّا خَلَقْنا أَنْعاماً وَ أَناسِيَّ كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2905, N'وَ لَقَدْ صَرَّفْناهُ بَيْنَهُمْ لِيَذَّكَّرُوا فَأَبى أَكْثَرُ النَّاسِ إِلاَّ كُفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2906, N'وَ لَوْ شِئْنا لَبَعَثْنا فِي كُلِّ قَرْيَةٍ نَذِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2907, N'فَلا تُطِعِ الْكافِرِينَ وَ جاهِدْهُمْ بِهِ جِهاداً كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2908, N'وَ هُوَ الَّذِي مَرَجَ الْبَحْرَيْنِ هذا عَذْبٌ فُراتٌ وَ هذا مِلْحٌ أُجاجٌ وَ جَعَلَ بَيْنَهُما بَرْزَخاً وَ حِجْراً مَحْجُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2909, N'وَ هُوَ الَّذِي خَلَقَ مِنَ الْماءِ بَشَراً فَجَعَلَهُ نَسَباً وَ صِهْراً وَ كانَ رَبُّكَ قَدِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2910, N'وَ يَعْبُدُونَ مِنْ دُونِ اللَّهِ ما لا يَنْفَعُهُمْ وَ لا يَضُرُّهُمْ وَ كانَ الْكافِرُ عَلى رَبِّهِ ظَهِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2911, N'وَ ما أَرْسَلْناكَ إِلاَّ مُبَشِّراً وَ نَذِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2912, N'قُلْ ما أَسْئَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِلاَّ مَنْ شاءَ أَنْ يَتَّخِذَ إِلى رَبِّهِ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2913, N'وَ تَوَكَّلْ عَلَى الْحَيِّ الَّذِي لا يَمُوتُ وَ سَبِّحْ بِحَمْدِهِ وَ كَفى بِهِ بِذُنُوبِ عِبادِهِ خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2914, N'الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ وَ ما بَيْنَهُما فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوى عَلَى الْعَرْشِ الرَّحْمنُ فَسْئَلْ بِهِ خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2915, N'وَ إِذا قِيلَ لَهُمُ اسْجُدُوا لِلرَّحْمنِ قالُوا وَ مَا الرَّحْمنُ أَ نَسْجُدُ لِما تَأْمُرُنا وَ زادَهُمْ نُفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2916, N'تَبارَكَ الَّذِي جَعَلَ فِي السَّماءِ بُرُوجاً وَ جَعَلَ فِيها سِراجاً وَ قَمَراً مُنِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2917, N'وَ هُوَ الَّذِي جَعَلَ اللَّيْلَ وَ النَّهارَ خِلْفَةً لِمَنْ أَرادَ أَنْ يَذَّكَّرَ أَوْ أَرادَ شُكُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2918, N'وَ عِبادُ الرَّحْمنِ الَّذِينَ يَمْشُونَ عَلَى الْأَرْضِ هَوْناً وَ إِذا خاطَبَهُمُ الْجاهِلُونَ قالُوا سَلاماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2919, N'وَ الَّذِينَ يَبِيتُونَ لِرَبِّهِمْ سُجَّداً وَ قِياماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2920, N'وَ الَّذِينَ يَقُولُونَ رَبَّنَا اصْرِفْ عَنَّا عَذابَ جَهَنَّمَ إِنَّ عَذابَها كانَ غَراماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2921, N'إِنَّها ساءَتْ مُسْتَقَرًّا وَ مُقاماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2922, N'وَ الَّذِينَ إِذا أَنْفَقُوا لَمْ يُسْرِفُوا وَ لَمْ يَقْتُرُوا وَ كانَ بَيْنَ ذلِكَ قَواماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2923, N'وَ الَّذِينَ لا يَدْعُونَ مَعَ اللَّهِ إِلهاً آخَرَ وَ لا يَقْتُلُونَ النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلاَّ بِالْحَقِّ وَ لا يَزْنُونَ وَ مَنْ يَفْعَلْ ذلِكَ يَلْقَ أَثاماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2924, N'يُضاعَفْ لَهُ الْعَذابُ يَوْمَ الْقِيامَةِ وَ يَخْلُدْ فِيهِ مُهاناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2925, N'إِلاَّ مَنْ تابَ وَ آمَنَ وَ عَمِلَ عَمَلاً صالِحاً فَأُوْلئِكَ يُبَدِّلُ اللَّهُ سَيِّئاتِهِمْ حَسَناتٍ وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2926, N'وَ مَنْ تابَ وَ عَمِلَ صالِحاً فَإِنَّهُ يَتُوبُ إِلَى اللَّهِ مَتاباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2927, N'وَ الَّذِينَ لا يَشْهَدُونَ الزُّورَ وَ إِذا مَرُّوا بِاللَّغْوِ مَرُّوا كِراماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2928, N'وَ الَّذِينَ إِذا ذُكِّرُوا بِآياتِ رَبِّهِمْ لَمْ يَخِرُّوا عَلَيْها صُمًّا وَ عُمْياناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2929, N'وَ الَّذِينَ يَقُولُونَ رَبَّنا هَبْ لَنا مِنْ أَزْواجِنا وَ ذُرِّيَّاتِنا قُرَّةَ أَعْيُنٍ وَ اجْعَلْنا لِلْمُتَّقِينَ إِماماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2930, N'أُوْلئِكَ يُجْزَوْنَ الْغُرْفَةَ بِما صَبَرُوا وَ يُلَقَّوْنَ فِيها تَحِيَّةً وَ سَلاماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2931, N'خالِدِينَ فِيها حَسُنَتْ مُسْتَقَرًّا وَ مُقاماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2932, N'قُلْ ما يَعْبَؤُا بِكُمْ رَبِّي لَوْ لا دُعاؤُكُمْ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُونُ لِزاماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2933, N'طسم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2934, N'تِلْكَ آياتُ الْكِتابِ الْمُبِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2935, N'لَعَلَّكَ باخِعٌ نَفْسَكَ أَلاَّ يَكُونُوا مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2936, N'إِنْ نَشَأْ نُنَزِّلْ عَلَيْهِمْ مِنَ السَّماءِ آيَةً فَظَلَّتْ أَعْناقُهُمْ لَها خاضِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2937, N'وَ ما يَأْتِيهِمْ مِنْ ذِكْرٍ مِنَ الرَّحْمنِ مُحْدَثٍ إِلاَّ كانُوا عَنْهُ مُعْرِضِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2938, N'فَقَدْ كَذَّبُوا فَسَيَأْتِيهِمْ أَنْبؤُا ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2939, N'أَ وَ لَمْ يَرَوْا إِلَى الْأَرْضِ كَمْ أَنْبَتْنا فِيها مِنْ كُلِّ زَوْجٍ كَرِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2940, N'إِنَّ فِي ذلِكَ لَآيَةً وَ ما كانَ أَكْثَرُهُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2941, N'وَ إِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2942, N'وَ إِذْ نادى رَبُّكَ مُوسى أَنِ ائْتِ الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2943, N'قَوْمَ فِرْعَوْنَ أَ لا يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2944, N'قالَ رَبِّ إِنِّي أَخافُ أَنْ يُكَذِّبُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2945, N'وَ يَضِيقُ صَدْرِي وَ لا يَنْطَلِقُ لِسانِي فَأَرْسِلْ إِلى هارُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2946, N'وَ لَهُمْ عَلَيَّ ذَنْبٌ فَأَخافُ أَنْ يَقْتُلُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2947, N'قالَ كَلاَّ فَاذْهَبا بِآياتِنا إِنَّا مَعَكُمْ مُسْتَمِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2948, N'فَأْتِيا فِرْعَوْنَ فَقُولا إِنَّا رَسُولُ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2949, N'أَنْ أَرْسِلْ مَعَنا بَنِي إِسْرائِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2950, N'قالَ أَ لَمْ نُرَبِّكَ فِينا وَلِيداً وَ لَبِثْتَ فِينا مِنْ عُمُرِكَ سِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2951, N'وَ فَعَلْتَ فَعْلَتَكَ الَّتِي فَعَلْتَ وَ أَنْتَ مِنَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2952, N'قالَ فَعَلْتُها إِذاً وَ أَنَا مِنَ الضَّالِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2953, N'فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِي رَبِّي حُكْماً وَ جَعَلَنِي مِنَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2954, N'وَ تِلْكَ نِعْمَةٌ تَمُنُّها عَلَيَّ أَنْ عَبَّدْتَ بَنِي إِسْرائِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2955, N'قالَ فِرْعَوْنُ وَ ما رَبُّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2956, N'قالَ رَبُّ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُمَا إِنْ كُنْتُمْ مُوقِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2957, N'قالَ لِمَنْ حَوْلَهُ أَ لا تَسْتَمِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2958, N'قالَ رَبُّكُمْ وَ رَبُّ آبائِكُمُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2959, N'قالَ إِنَّ رَسُولَكُمُ الَّذِي أُرْسِلَ إِلَيْكُمْ لَمَجْنُونٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2960, N'قالَ رَبُّ الْمَشْرِقِ وَ الْمَغْرِبِ وَ ما بَيْنَهُما إِنْ كُنْتُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2961, N'قالَ لَئِنِ اتَّخَذْتَ إِلهَاً غَيْرِي لَأَجْعَلَنَّكَ مِنَ الْمَسْجُونِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2962, N'قالَ أَ وَ لَوْ جِئْتُكَ بِشَيْ‏ءٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2963, N'قالَ فَأْتِ بِهِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2964, N'فَأَلْقى عَصاهُ فَإِذا هِيَ ثُعْبانٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2965, N'وَ نَزَعَ يَدَهُ فَإِذا هِيَ بَيْضاءُ لِلنَّاظِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2966, N'قالَ لِلْمَلَإِ حَوْلَهُ إِنَّ هذا لَساحِرٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2967, N'يُرِيدُ أَنْ يُخْرِجَكُمْ مِنْ أَرْضِكُمْ بِسِحْرِهِ فَما ذا تَأْمُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2968, N'قالُوا أَرْجِهْ وَ أَخاهُ وَ ابْعَثْ فِي الْمَدائِنِ حاشِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2969, N'يَأْتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2970, N'فَجُمِعَ السَّحَرَةُ لِمِيقاتِ يَوْمٍ مَعْلُومٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2971, N'وَ قِيلَ لِلنَّاسِ هَلْ أَنْتُمْ مُجْتَمِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2972, N'لَعَلَّنا نَتَّبِعُ السَّحَرَةَ إِنْ كانُوا هُمُ الْغالِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2973, N'فَلَمَّا جاءَ السَّحَرَةُ قالُوا لِفِرْعَوْنَ أَ إِنَّ لَنا لَأَجْراً إِنْ كُنَّا نَحْنُ الْغالِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2974, N'قالَ نَعَمْ وَ إِنَّكُمْ إِذاً لَمِنَ الْمُقَرَّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2975, N'قالَ لَهُمْ مُوسى أَلْقُوا ما أَنْتُمْ مُلْقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2976, N'فَأَلْقَوْا حِبالَهُمْ وَ عِصِيَّهُمْ وَ قالُوا بِعِزَّةِ فِرْعَوْنَ إِنَّا لَنَحْنُ الْغالِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2977, N'فَأَلْقى مُوسى عَصاهُ فَإِذا هِيَ تَلْقَفُ ما يَأْفِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2978, N'فَأُلْقِيَ السَّحَرَةُ ساجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2979, N'قالُوا آمَنَّا بِرَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2980, N'رَبِّ مُوسى وَ هارُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2981, N'قالَ آمَنْتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ فَلَسَوْفَ تَعْلَمُونَ لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَ أَرْجُلَكُمْ مِنْ خِلافٍ وَ لَأُصَلِّبَنَّكُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2982, N'قالُوا لا ضَيْرَ إِنَّا إِلى رَبِّنا مُنْقَلِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2983, N'إِنَّا نَطْمَعُ أَنْ يَغْفِرَ لَنا رَبُّنا خَطايانا أَنْ كُنَّا أَوَّلَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2984, N'وَ أَوْحَيْنا إِلى مُوسى أَنْ أَسْرِ بِعِبادِي إِنَّكُمْ مُتَّبَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2985, N'فَأَرْسَلَ فِرْعَوْنُ فِي الْمَدائِنِ حاشِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2986, N'إِنَّ هؤُلاءِ لَشِرْذِمَةٌ قَلِيلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2987, N'وَ إِنَّهُمْ لَنا لَغائِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2988, N'وَ إِنَّا لَجَمِيعٌ حاذِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2989, N'فَأَخْرَجْناهُمْ مِنْ جَنَّاتٍ وَ عُيُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2990, N'وَ كُنُوزٍ وَ مَقامٍ كَرِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2991, N'كَذلِكَ وَ أَوْرَثْناها بَنِي إِسْرائِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2992, N'فَأَتْبَعُوهُمْ مُشْرِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2993, N'فَلَمَّا تَراءَا الْجَمْعانِ قالَ أَصْحابُ مُوسى إِنَّا لَمُدْرَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2994, N'قالَ كَلاَّ إِنَّ مَعِي رَبِّي سَيَهْدِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2995, N'فَأَوْحَيْنا إِلى مُوسى أَنِ اضْرِبْ بِعَصاكَ الْبَحْرَ فَانْفَلَقَ فَكانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2996, N'وَ أَزْلَفْنا ثَمَّ الْآخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2997, N'وَ أَنْجَيْنا مُوسى وَ مَنْ مَعَهُ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2998, N'ثُمَّ أَغْرَقْنَا الْآخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (2999, N'إِنَّ فِي ذلِكَ لَآيَةً وَ ما كانَ أَكْثَرُهُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3000, N'وَ إِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3001, N'وَ اتْلُ عَلَيْهِمْ نَبَأَ إِبْراهِيمَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3002, N'إِذْ قالَ لِأَبِيهِ وَ قَوْمِهِ ما تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3003, N'قالُوا نَعْبُدُ أَصْناماً فَنَظَلُّ لَها عاكِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3004, N'قالَ هَلْ يَسْمَعُونَكُمْ إِذْ تَدْعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3005, N'أَوْ يَنْفَعُونَكُمْ أَوْ يَضُرُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3006, N'قالُوا بَلْ وَجَدْنا آباءَنا كَذلِكَ يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3007, N'قالَ أَ فَرَأَيْتُمْ ما كُنْتُمْ تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3008, N'أَنْتُمْ وَ آباؤُكُمُ الْأَقْدَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3009, N'فَإِنَّهُمْ عَدُوٌّ لِي إِلاَّ رَبَّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3010, N'الَّذِي خَلَقَنِي فَهُوَ يَهْدِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3011, N'وَ الَّذِي هُوَ يُطْعِمُنِي وَ يَسْقِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3012, N'وَ إِذا مَرِضْتُ فَهُوَ يَشْفِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3013, N'وَ الَّذِي يُمِيتُنِي ثُمَّ يُحْيِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3014, N'وَ الَّذِي أَطْمَعُ أَنْ يَغْفِرَ لِي خَطِيئَتِي يَوْمَ الدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3015, N'رَبِّ هَبْ لِي حُكْماً وَ أَلْحِقْنِي بِالصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3016, N'وَ اجْعَلْ لِي لِسانَ صِدْقٍ فِي الْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3017, N'وَ اجْعَلْنِي مِنْ وَرَثَةِ جَنَّةِ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3018, N'وَ اغْفِرْ لِأَبِي إِنَّهُ كانَ مِنَ الضَّالِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3019, N'وَ لا تُخْزِنِي يَوْمَ يُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3020, N'يَوْمَ لا يَنْفَعُ مالٌ وَ لا بَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3021, N'إِلاَّ مَنْ أَتَى اللَّهَ بِقَلْبٍ سَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3022, N'وَ أُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3023, N'وَ بُرِّزَتِ الْجَحِيمُ لِلْغاوِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3024, N'وَ قِيلَ لَهُمْ أَيْنَ ما كُنْتُمْ تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3025, N'مِنْ دُونِ اللَّهِ هَلْ يَنْصُرُونَكُمْ أَوْ يَنْتَصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3026, N'فَكُبْكِبُوا فِيها هُمْ وَ الْغاوُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3027, N'وَ جُنُودُ إِبْلِيسَ أَجْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3028, N'قالُوا وَ هُمْ فِيها يَخْتَصِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3029, N'تَاللَّهِ إِنْ كُنَّا لَفِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3030, N'إِذْ نُسَوِّيكُمْ بِرَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3031, N'وَ ما أَضَلَّنا إِلاَّ الْمُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3032, N'فَما لَنا مِنْ شافِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3033, N'وَ لا صَدِيقٍ حَمِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3034, N'فَلَوْ أَنَّ لَنا كَرَّةً فَنَكُونَ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3035, N'إِنَّ فِي ذلِكَ لَآيَةً وَ ما كانَ أَكْثَرُهُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3036, N'وَ إِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3037, N'كَذَّبَتْ قَوْمُ نُوحٍ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3038, N'إِذْ قالَ لَهُمْ أَخُوهُمْ نُوحٌ أَ لا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3039, N'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3040, N'فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3041, N'وَ ما أَسْئَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلاَّ عَلى رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3042, N'فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3043, N'قالُوا أَ نُؤْمِنُ لَكَ وَ اتَّبَعَكَ الْأَرْذَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3044, N'قالَ وَ ما عِلْمِي بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3045, N'إِنْ حِسابُهُمْ إِلاَّ عَلى رَبِّي لَوْ تَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3046, N'وَ ما أَنَا بِطارِدِ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3047, N'إِنْ أَنَا إِلاَّ نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3048, N'قالُوا لَئِنْ لَمْ تَنْتَهِ يا نُوحُ لَتَكُونَنَّ مِنَ الْمَرْجُومِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3049, N'قالَ رَبِّ إِنَّ قَوْمِي كَذَّبُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3050, N'فَافْتَحْ بَيْنِي وَ بَيْنَهُمْ فَتْحاً وَ نَجِّنِي وَ مَنْ مَعِيَ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3051, N'فَأَنْجَيْناهُ وَ مَنْ مَعَهُ فِي الْفُلْكِ الْمَشْحُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3052, N'ثُمَّ أَغْرَقْنا بَعْدُ الْباقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3053, N'إِنَّ فِي ذلِكَ لَآيَةً وَ ما كانَ أَكْثَرُهُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3054, N'وَ إِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3055, N'كَذَّبَتْ عادٌ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3056, N'إِذْ قالَ لَهُمْ أَخُوهُمْ هُودٌ أَ لا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3057, N'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3058, N'فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3059, N'وَ ما أَسْئَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلاَّ عَلى رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3060, N'أَ تَبْنُونَ بِكُلِّ رِيعٍ آيَةً تَعْبَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3061, N'وَ تَتَّخِذُونَ مَصانِعَ لَعَلَّكُمْ تَخْلُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3062, N'وَ إِذا بَطَشْتُمْ بَطَشْتُمْ جَبَّارِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3063, N'فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3064, N'وَ اتَّقُوا الَّذِي أَمَدَّكُمْ بِما تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3065, N'أَمَدَّكُمْ بِأَنْعامٍ وَ بَنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3066, N'وَ جَنَّاتٍ وَ عُيُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3067, N'إِنِّي أَخافُ عَلَيْكُمْ عَذابَ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3068, N'قالُوا سَواءٌ عَلَيْنا أَ وَعَظْتَ أَمْ لَمْ تَكُنْ مِنَ الْواعِظِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3069, N'إِنْ هذا إِلاَّ خُلُقُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3070, N'وَ ما نَحْنُ بِمُعَذَّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3071, N'فَكَذَّبُوهُ فَأَهْلَكْناهُمْ إِنَّ فِي ذلِكَ لَآيَةً وَ ما كانَ أَكْثَرُهُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3072, N'وَ إِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3073, N'كَذَّبَتْ ثَمُودُ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3074, N'إِذْ قالَ لَهُمْ أَخُوهُمْ صالِحٌ أَ لا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3075, N'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3076, N'فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3077, N'وَ ما أَسْئَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلاَّ عَلى رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3078, N'أَ تُتْرَكُونَ فِي ما هاهُنا آمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3079, N'فِي جَنَّاتٍ وَ عُيُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3080, N'وَ زُرُوعٍ وَ نَخْلٍ طَلْعُها هَضِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3081, N'وَ تَنْحِتُونَ مِنَ الْجِبالِ بُيُوتاً فارِهِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3082, N'فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3083, N'وَ لا تُطِيعُوا أَمْرَ الْمُسْرِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3084, N'الَّذِينَ يُفْسِدُونَ فِي الْأَرْضِ وَ لا يُصْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3085, N'قالُوا إِنَّما أَنْتَ مِنَ الْمُسَحَّرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3086, N'ما أَنْتَ إِلاَّ بَشَرٌ مِثْلُنا فَأْتِ بِآيَةٍ إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3087, N'قالَ هذِهِ ناقَةٌ لَها شِرْبٌ وَ لَكُمْ شِرْبُ يَوْمٍ مَعْلُومٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3088, N'وَ لا تَمَسُّوها بِسُوءٍ فَيَأْخُذَكُمْ عَذابُ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3089, N'فَعَقَرُوها فَأَصْبَحُوا نادِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3090, N'فَأَخَذَهُمُ الْعَذابُ إِنَّ فِي ذلِكَ لَآيَةً وَ ما كانَ أَكْثَرُهُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3091, N'وَ إِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3092, N'كَذَّبَتْ قَوْمُ لُوطٍ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3093, N'إِذْ قالَ لَهُمْ أَخُوهُمْ لُوطٌ أَ لا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3094, N'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3095, N'فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3096, N'وَ ما أَسْئَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلاَّ عَلى رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3097, N'أَ تَأْتُونَ الذُّكْرانَ مِنَ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3098, N'وَ تَذَرُونَ ما خَلَقَ لَكُمْ رَبُّكُمْ مِنْ أَزْواجِكُمْ بَلْ أَنْتُمْ قَوْمٌ عادُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3099, N'قالُوا لَئِنْ لَمْ تَنْتَهِ يا لُوطُ لَتَكُونَنَّ مِنَ الْمُخْرَجِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3100, N'قالَ إِنِّي لِعَمَلِكُمْ مِنَ الْقالِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3101, N'رَبِّ نَجِّنِي وَ أَهْلِي مِمَّا يَعْمَلُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3102, N'فَنَجَّيْناهُ وَ أَهْلَهُ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3103, N'إِلاَّ عَجُوزاً فِي الْغابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3104, N'ثُمَّ دَمَّرْنَا الْآخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3105, N'وَ أَمْطَرْنا عَلَيْهِمْ مَطَراً فَساءَ مَطَرُ الْمُنْذَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3106, N'إِنَّ فِي ذلِكَ لَآيَةً وَ ما كانَ أَكْثَرُهُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3107, N'وَ إِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3108, N'كَذَّبَ أَصْحابُ الْأَيْكَةِ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3109, N'إِذْ قالَ لَهُمْ شُعَيْبٌ أَ لا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3110, N'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3111, N'فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3112, N'وَ ما أَسْئَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلاَّ عَلى رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3113, N'أَوْفُوا الْكَيْلَ وَ لا تَكُونُوا مِنَ الْمُخْسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3114, N'وَ زِنُوا بِالْقِسْطاسِ الْمُسْتَقِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3115, N'وَ لا تَبْخَسُوا النَّاسَ أَشْياءَهُمْ وَ لا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3116, N'وَ اتَّقُوا الَّذِي خَلَقَكُمْ وَ الْجِبِلَّةَ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3117, N'قالُوا إِنَّما أَنْتَ مِنَ الْمُسَحَّرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3118, N'وَ ما أَنْتَ إِلاَّ بَشَرٌ مِثْلُنا وَ إِنْ نَظُنُّكَ لَمِنَ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3119, N'فَأَسْقِطْ عَلَيْنا كِسَفاً مِنَ السَّماءِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3120, N'قالَ رَبِّي أَعْلَمُ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3121, N'فَكَذَّبُوهُ فَأَخَذَهُمْ عَذابُ يَوْمِ الظُّلَّةِ إِنَّهُ كانَ عَذابَ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3122, N'إِنَّ فِي ذلِكَ لَآيَةً وَ ما كانَ أَكْثَرُهُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3123, N'وَ إِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3124, N'وَ إِنَّهُ لَتَنْزِيلُ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3125, N'نَزَلَ بِهِ الرُّوحُ الْأَمِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3126, N'عَلى قَلْبِكَ لِتَكُونَ مِنَ الْمُنْذِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3127, N'بِلِسانٍ عَرَبِيٍّ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3128, N'وَ إِنَّهُ لَفِي زُبُرِ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3129, N'أَ وَ لَمْ يَكُنْ لَهُمْ آيَةً أَنْ يَعْلَمَهُ عُلَماءُ بَنِي إِسْرائِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3130, N'وَ لَوْ نَزَّلْناهُ عَلى بَعْضِ الْأَعْجَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3131, N'فَقَرَأَهُ عَلَيْهِمْ ما كانُوا بِهِ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3132, N'كَذلِكَ سَلَكْناهُ فِي قُلُوبِ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3133, N'لا يُؤْمِنُونَ بِهِ حَتَّى يَرَوُا الْعَذابَ الْأَلِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3134, N'فَيَأْتِيَهُمْ بَغْتَةً وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3135, N'فَيَقُولُوا هَلْ نَحْنُ مُنْظَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3136, N'أَ فَبِعَذابِنا يَسْتَعْجِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3137, N'أَ فَرَأَيْتَ إِنْ مَتَّعْناهُمْ سِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3138, N'ثُمَّ جاءَهُمْ ما كانُوا يُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3139, N'ما أَغْنى عَنْهُمْ ما كانُوا يُمَتَّعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3140, N'وَ ما أَهْلَكْنا مِنْ قَرْيَةٍ إِلاَّ لَها مُنْذِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3141, N'ذِكْرى وَ ما كُنَّا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3142, N'وَ ما تَنَزَّلَتْ بِهِ الشَّياطِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3143, N'وَ ما يَنْبَغِي لَهُمْ وَ ما يَسْتَطِيعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3144, N'إِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3145, N'فَلا تَدْعُ مَعَ اللَّهِ إِلهاً آخَرَ فَتَكُونَ مِنَ الْمُعَذَّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3146, N'وَ أَنْذِرْ عَشِيرَتَكَ الْأَقْرَبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3147, N'وَ اخْفِضْ جَناحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3148, N'فَإِنْ عَصَوْكَ فَقُلْ إِنِّي بَرِي‏ءٌ مِمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3149, N'وَ تَوَكَّلْ عَلَى الْعَزِيزِ الرَّحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3150, N'الَّذِي يَراكَ حِينَ تَقُومُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3151, N'وَ تَقَلُّبَكَ فِي السَّاجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3152, N'إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3153, N'هَلْ أُنَبِّئُكُمْ عَلى مَنْ تَنَزَّلُ الشَّياطِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3154, N'تَنَزَّلُ عَلى كُلِّ أَفَّاكٍ أَثِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3155, N'يُلْقُونَ السَّمْعَ وَ أَكْثَرُهُمْ كاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3156, N'وَ الشُّعَراءُ يَتَّبِعُهُمُ الْغاوُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3157, N'أَ لَمْ تَرَ أَنَّهُمْ فِي كُلِّ وادٍ يَهِيمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3158, N'وَ أَنَّهُمْ يَقُولُونَ ما لا يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3159, N'إِلاَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ وَ ذَكَرُوا اللَّهَ كَثِيراً وَ انْتَصَرُوا مِنْ بَعْدِ ما ظُلِمُوا وَ سَيَعْلَمُ الَّذِينَ ظَلَمُوا أَيَّ مُنْقَلَبٍ يَنْقَلِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3160, N'طس تِلْكَ آياتُ الْقُرْآنِ وَ كِتابٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3161, N'هُدىً وَ بُشْرى لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3162, N'الَّذِينَ يُقِيمُونَ الصَّلاةَ وَ يُؤْتُونَ الزَّكاةَ وَ هُمْ بِالْآخِرَةِ هُمْ يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3163, N'إِنَّ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ زَيَّنَّا لَهُمْ أَعْمالَهُمْ فَهُمْ يَعْمَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3164, N'أُوْلئِكَ الَّذِينَ لَهُمْ سُوءُ الْعَذابِ وَ هُمْ فِي الْآخِرَةِ هُمُ الْأَخْسَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3165, N'وَ إِنَّكَ لَتُلَقَّى الْقُرْآنَ مِنْ لَدُنْ حَكِيمٍ عَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3166, N'إِذْ قالَ مُوسى لِأَهْلِهِ إِنِّي آنَسْتُ ناراً سَآتِيكُمْ مِنْها بِخَبَرٍ أَوْ آتِيكُمْ بِشِهابٍ قَبَسٍ لَعَلَّكُمْ تَصْطَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3167, N'فَلَمَّا جاءَها نُودِيَ أَنْ بُورِكَ مَنْ فِي النَّارِ وَ مَنْ حَوْلَها وَ سُبْحانَ اللَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3168, N'يا مُوسى إِنَّهُ أَنَا اللَّهُ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3169, N'وَ أَلْقِ عَصاكَ فَلَمَّا رَآها تَهْتَزُّ كَأَنَّها جَانٌّ وَلَّى مُدْبِراً وَ لَمْ يُعَقِّبْ يا مُوسى لا تَخَفْ إِنِّي لا يَخافُ لَدَيَّ الْمُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3170, N'إِلاَّ مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْناً بَعْدَ سُوءٍ فَإِنِّي غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3171, N'وَ أَدْخِلْ يَدَكَ فِي جَيْبِكَ تَخْرُجْ بَيْضاءَ مِنْ غَيْرِ سُوءٍ فِي تِسْعِ آياتٍ إِلى فِرْعَوْنَ وَ قَوْمِهِ إِنَّهُمْ كانُوا قَوْماً فاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3172, N'فَلَمَّا جاءَتْهُمْ آياتُنا مُبْصِرَةً قالُوا هذا سِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3173, N'وَ جَحَدُوا بِها وَ اسْتَيْقَنَتْها أَنْفُسُهُمْ ظُلْماً وَ عُلُوًّا فَانْظُرْ كَيْفَ كانَ عاقِبَةُ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3174, N'وَ لَقَدْ آتَيْنا داوُدَ وَ سُلَيْمانَ عِلْماً وَ قالاَ الْحَمْدُ لِلَّهِ الَّذِي فَضَّلَنا عَلى كَثِيرٍ مِنْ عِبادِهِ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3175, N'وَ وَرِثَ سُلَيْمانُ داوُدَ وَ قالَ يا أَيُّهَا النَّاسُ عُلِّمْنا مَنْطِقَ الطَّيْرِ وَ أُوتِينا مِنْ كُلِّ شَيْ‏ءٍ إِنَّ هذا لَهُوَ الْفَضْلُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3176, N'وَ حُشِرَ لِسُلَيْمانَ جُنُودُهُ مِنَ الْجِنِّ وَ الْإِنْسِ وَ الطَّيْرِ فَهُمْ يُوزَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3177, N'حَتَّى إِذا أَتَوْا عَلى وادِ النَّمْلِ قالَتْ نَمْلَةٌ يا أَيُّهَا النَّمْلُ ادْخُلُوا مَساكِنَكُمْ لا يَحْطِمَنَّكُمْ سُلَيْمانُ وَ جُنُودُهُ وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3178, N'فَتَبَسَّمَ ضاحِكاً مِنْ قَوْلِها وَ قالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَ عَلى والِدَيَّ وَ أَنْ أَعْمَلَ صالِحاً تَرْضاهُ وَ أَدْخِلْنِي بِرَحْمَتِكَ فِي عِبادِكَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3179, N'وَ تَفَقَّدَ الطَّيْرَ فَقالَ ما لِيَ لا أَرَى الْهُدْهُدَ أَمْ كانَ مِنَ الْغائِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3180, N'لَأُعَذِّبَنَّهُ عَذاباً شَدِيداً أَوْ لَأَذْبَحَنَّهُ أَوْ لَيَأْتِيَنِّي بِسُلْطانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3181, N'فَمَكَثَ غَيْرَ بَعِيدٍ فَقالَ أَحَطْتُ بِما لَمْ تُحِطْ بِهِ وَ جِئْتُكَ مِنْ سَبَإٍ بِنَبَإٍ يَقِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3182, N'إِنِّي وَجَدْتُ امْرَأَةً تَمْلِكُهُمْ وَ أُوتِيَتْ مِنْ كُلِّ شَيْ‏ءٍ وَ لَها عَرْشٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3183, N'وَجَدْتُها وَ قَوْمَها يَسْجُدُونَ لِلشَّمْسِ مِنْ دُونِ اللَّهِ وَ زَيَّنَ لَهُمُ الشَّيْطانُ أَعْمالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ فَهُمْ لا يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3184, N'أَلاَّ يَسْجُدُوا لِلَّهِ الَّذِي يُخْرِجُ الْخَبْ‏ءَ فِي السَّماواتِ وَ الْأَرْضِ وَ يَعْلَمُ ما تُخْفُونَ وَ ما تُعْلِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3185, N'اللَّهُ لا إِلهَ إِلاَّ هُوَ رَبُّ الْعَرْشِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3186, N'قالَ سَنَنْظُرُ أَ صَدَقْتَ أَمْ كُنْتَ مِنَ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3187, N'اذْهَبْ بِكِتابِي هذا فَأَلْقِهْ إِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ ما ذا يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3188, N'قالَتْ يا أَيُّهَا الْمَلَأُ إِنِّي أُلْقِيَ إِلَيَّ كِتابٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3189, N'إِنَّهُ مِنْ سُلَيْمانَ وَ إِنَّهُ بِسْمِ اللَّهِ الرَّحْمنِ الرَّحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3190, N'أَلاَّ تَعْلُوا عَلَيَّ وَ أْتُونِي مُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3191, N'قالَتْ يا أَيُّهَا الْمَلَأُ أَفْتُونِي فِي أَمْرِي ما كُنْتُ قاطِعَةً أَمْراً حَتَّى تَشْهَدُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3192, N'قالُوا نَحْنُ أُولُوا قُوَّةٍ وَ أُولُوا بَأْسٍ شَدِيدٍ وَ الْأَمْرُ إِلَيْكِ فَانْظُرِي ما ذا تَأْمُرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3193, N'قالَتْ إِنَّ الْمُلُوكَ إِذا دَخَلُوا قَرْيَةً أَفْسَدُوها وَ جَعَلُوا أَعِزَّةَ أَهْلِها أَذِلَّةً وَ كَذلِكَ يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3194, N'وَ إِنِّي مُرْسِلَةٌ إِلَيْهِمْ بِهَدِيَّةٍ فَناظِرَةٌ بِمَ يَرْجِعُ الْمُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3195, N'فَلَمَّا جاءَ سُلَيْمانَ قالَ أَ تُمِدُّونَنِ بِمالٍ فَما آتانِيَ اللَّهُ خَيْرٌ مِمَّا آتاكُمْ بَلْ أَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3196, N'ارْجِعْ إِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُودٍ لا قِبَلَ لَهُمْ بِها وَ لَنُخْرِجَنَّهُمْ مِنْها أَذِلَّةً وَ هُمْ صاغِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3197, N'قالَ يا أَيُّهَا الْمَلَؤُا أَيُّكُمْ يَأْتِينِي بِعَرْشِها قَبْلَ أَنْ يَأْتُونِي مُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3198, N'قالَ عِفْرِيتٌ مِنَ الْجِنِّ أَنَا آتِيكَ بِهِ قَبْلَ أَنْ تَقُومَ مِنْ مَقامِكَ وَ إِنِّي عَلَيْهِ لَقَوِيٌّ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3199, N'قالَ الَّذِي عِنْدَهُ عِلْمٌ مِنَ الْكِتابِ أَنَا آتِيكَ بِهِ قَبْلَ أَنْ يَرْتَدَّ إِلَيْكَ طَرْفُكَ فَلَمَّا رَآهُ مُسْتَقِرًّا عِنْدَهُ قالَ هذا مِنْ فَضْلِ رَبِّي لِيَبْلُوَنِي أَ أَشْكُرُ أَمْ أَكْفُرُ وَ مَنْ شَكَرَ فَإِنَّما يَشْكُرُ لِنَفْسِهِ وَ مَنْ كَفَرَ فَإِنَّ رَبِّي غَنِيٌّ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3200, N'قالَ نَكِّرُوا لَها عَرْشَها نَنْظُرْ أَ تَهْتَدِي أَمْ تَكُونُ مِنَ الَّذِينَ لا يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3201, N'فَلَمَّا جاءَتْ قِيلَ أَ هكَذا عَرْشُكِ قالَتْ كَأَنَّهُ هُوَ وَ أُوتِينَا الْعِلْمَ مِنْ قَبْلِها وَ كُنَّا مُسْلِمِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3202, N'وَ صَدَّها ما كانَتْ تَعْبُدُ مِنْ دُونِ اللَّهِ إِنَّها كانَتْ مِنْ قَوْمٍ كافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3203, N'قِيلَ لَهَا ادْخُلِي الصَّرْحَ فَلَمَّا رَأَتْهُ حَسِبَتْهُ لُجَّةً وَ كَشَفَتْ عَنْ ساقَيْها قالَ إِنَّهُ صَرْحٌ مُمَرَّدٌ مِنْ قَوارِيرَ قالَتْ رَبِّ إِنِّي ظَلَمْتُ نَفْسِي وَ أَسْلَمْتُ مَعَ سُلَيْمانَ لِلَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3204, N'وَ لَقَدْ أَرْسَلْنا إِلى ثَمُودَ أَخاهُمْ صالِحاً أَنِ اعْبُدُوا اللَّهَ فَإِذا هُمْ فَرِيقانِ يَخْتَصِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3205, N'قالَ يا قَوْمِ لِمَ تَسْتَعْجِلُونَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ لَوْ لا تَسْتَغْفِرُونَ اللَّهَ لَعَلَّكُمْ تُرْحَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3206, N'قالُوا اطَّيَّرْنا بِكَ وَ بِمَنْ مَعَكَ قالَ طائِرُكُمْ عِنْدَ اللَّهِ بَلْ أَنْتُمْ قَوْمٌ تُفْتَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3207, N'وَ كانَ فِي الْمَدِينَةِ تِسْعَةُ رَهْطٍ يُفْسِدُونَ فِي الْأَرْضِ وَ لا يُصْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3208, N'قالُوا تَقاسَمُوا بِاللَّهِ لَنُبَيِّتَنَّهُ وَ أَهْلَهُ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِ ما شَهِدْنا مَهْلِكَ أَهْلِهِ وَ إِنَّا لَصادِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3209, N'وَ مَكَرُوا مَكْراً وَ مَكَرْنا مَكْراً وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3210, N'فَانْظُرْ كَيْفَ كانَ عاقِبَةُ مَكْرِهِمْ أَنَّا دَمَّرْناهُمْ وَ قَوْمَهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3211, N'فَتِلْكَ بُيُوتُهُمْ خاوِيَةً بِما ظَلَمُوا إِنَّ فِي ذلِكَ لَآيَةً لِقَوْمٍ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3212, N'وَ أَنْجَيْنَا الَّذِينَ آمَنُوا وَ كانُوا يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3213, N'وَ لُوطاً إِذْ قالَ لِقَوْمِهِ أَ تَأْتُونَ الْفاحِشَةَ وَ أَنْتُمْ تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3214, N'أَ إِنَّكُمْ لَتَأْتُونَ الرِّجالَ شَهْوَةً مِنْ دُونِ النِّساءِ بَلْ أَنْتُمْ قَوْمٌ تَجْهَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3215, N'فَما كانَ جَوابَ قَوْمِهِ إِلاَّ أَنْ قالُوا أَخْرِجُوا آلَ لُوطٍ مِنْ قَرْيَتِكُمْ إِنَّهُمْ أُناسٌ يَتَطَهَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3216, N'فَأَنْجَيْناهُ وَ أَهْلَهُ إِلاَّ امْرَأَتَهُ قَدَّرْناها مِنَ الْغابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3217, N'وَ أَمْطَرْنا عَلَيْهِمْ مَطَراً فَساءَ مَطَرُ الْمُنْذَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3218, N'قُلِ الْحَمْدُ لِلَّهِ وَ سَلامٌ عَلى عِبادِهِ الَّذِينَ اصْطَفى آللَّهُ خَيْرٌ أَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3219, N'أَمَّنْ خَلَقَ السَّماواتِ وَ الْأَرْضَ وَ أَنْزَلَ لَكُمْ مِنَ السَّماءِ ماءً فَأَنْبَتْنا بِهِ حَدائِقَ ذاتَ بَهْجَةٍ ما كانَ لَكُمْ أَنْ تُنْبِتُوا شَجَرَها أَ إِلهٌ مَعَ اللَّهِ بَلْ هُمْ قَوْمٌ يَعْدِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3220, N'أَمَّنْ جَعَلَ الْأَرْضَ قَراراً وَ جَعَلَ خِلالَها أَنْهاراً وَ جَعَلَ لَها رَواسِيَ وَ جَعَلَ بَيْنَ الْبَحْرَيْنِ حاجِزاً أَ إِلهٌ مَعَ اللَّهِ بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3221, N'أَمَّنْ يُجِيبُ الْمُضْطَرَّ إِذا دَعاهُ وَ يَكْشِفُ السُّوءَ وَ يَجْعَلُكُمْ خُلَفاءَ الْأَرْضِ أَ إِلهٌ مَعَ اللَّهِ قَلِيلاً ما تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3222, N'أَمَّنْ يَهْدِيكُمْ فِي ظُلُماتِ الْبَرِّ وَ الْبَحْرِ وَ مَنْ يُرْسِلُ الرِّياحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِهِ أَ إِلهٌ مَعَ اللَّهِ تَعالَى اللَّهُ عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3223, N'أَمَّنْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ وَ مَنْ يَرْزُقُكُمْ مِنَ السَّماءِ وَ الْأَرْضِ أَ إِلهٌ مَعَ اللَّهِ قُلْ هاتُوا بُرْهانَكُمْ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3224, N'قُلْ لا يَعْلَمُ مَنْ فِي السَّماواتِ وَ الْأَرْضِ الْغَيْبَ إِلاَّ اللَّهُ وَ ما يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3225, N'بَلِ ادَّارَكَ عِلْمُهُمْ فِي الْآخِرَةِ بَلْ هُمْ فِي شَكٍّ مِنْها بَلْ هُمْ مِنْها عَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3226, N'وَ قالَ الَّذِينَ كَفَرُوا أَ إِذا كُنَّا تُراباً وَ آباؤُنا أَ إِنَّا لَمُخْرَجُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3227, N'لَقَدْ وُعِدْنا هذا نَحْنُ وَ آباؤُنا مِنْ قَبْلُ إِنْ هذا إِلاَّ أَساطِيرُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3228, N'قُلْ سِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كانَ عاقِبَةُ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3229, N'وَ لا تَحْزَنْ عَلَيْهِمْ وَ لا تَكُنْ فِي ضَيْقٍ مِمَّا يَمْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3230, N'وَ يَقُولُونَ مَتى هذَا الْوَعْدُ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3231, N'قُلْ عَسى أَنْ يَكُونَ رَدِفَ لَكُمْ بَعْضُ الَّذِي تَسْتَعْجِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3232, N'وَ إِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ وَ لَكِنَّ أَكْثَرَهُمْ لا يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3233, N'وَ إِنَّ رَبَّكَ لَيَعْلَمُ ما تُكِنُّ صُدُورُهُمْ وَ ما يُعْلِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3234, N'وَ ما مِنْ غائِبَةٍ فِي السَّماءِ وَ الْأَرْضِ إِلاَّ فِي كِتابٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3235, N'إِنَّ هذَا الْقُرْآنَ يَقُصُّ عَلى بَنِي إِسْرائِيلَ أَكْثَرَ الَّذِي هُمْ فِيهِ يَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3236, N'وَ إِنَّهُ لَهُدىً وَ رَحْمَةٌ لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3237, N'إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ بِحُكْمِهِ وَ هُوَ الْعَزِيزُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3238, N'فَتَوَكَّلْ عَلَى اللَّهِ إِنَّكَ عَلَى الْحَقِّ الْمُبِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3239, N'إِنَّكَ لا تُسْمِعُ الْمَوْتى وَ لا تُسْمِعُ الصُّمَّ الدُّعاءَ إِذا وَلَّوْا مُدْبِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3240, N'وَ ما أَنْتَ بِهادِي الْعُمْيِ عَنْ ضَلالَتِهِمْ إِنْ تُسْمِعُ إِلاَّ مَنْ يُؤْمِنُ بِآياتِنا فَهُمْ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3241, N'وَ إِذا وَقَعَ الْقَوْلُ عَلَيْهِمْ أَخْرَجْنا لَهُمْ دَابَّةً مِنَ الْأَرْضِ تُكَلِّمُهُمْ أَنَّ النَّاسَ كانُوا بِآياتِنا لا يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3242, N'وَ يَوْمَ نَحْشُرُ مِنْ كُلِّ أُمَّةٍ فَوْجاً مِمَّنْ يُكَذِّبُ بِآياتِنا فَهُمْ يُوزَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3243, N'حَتَّى إِذا جاؤُ قالَ أَ كَذَّبْتُمْ بِآياتِي وَ لَمْ تُحِيطُوا بِها عِلْماً أَمَّا ذا كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3244, N'وَ وَقَعَ الْقَوْلُ عَلَيْهِمْ بِما ظَلَمُوا فَهُمْ لا يَنْطِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3245, N'أَ لَمْ يَرَوْا أَنَّا جَعَلْنَا اللَّيْلَ لِيَسْكُنُوا فِيهِ وَ النَّهارَ مُبْصِراً إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3246, N'وَ يَوْمَ يُنْفَخُ فِي الصُّورِ فَفَزِعَ مَنْ فِي السَّماواتِ وَ مَنْ فِي الْأَرْضِ إِلاَّ مَنْ شاءَ اللَّهُ وَ كُلٌّ أَتَوْهُ داخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3247, N'وَ تَرَى الْجِبالَ تَحْسَبُها جامِدَةً وَ هِيَ تَمُرُّ مَرَّ السَّحابِ صُنْعَ اللَّهِ الَّذِي أَتْقَنَ كُلَّ شَيْ‏ءٍ إِنَّهُ خَبِيرٌ بِما تَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3248, N'مَنْ جاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْها وَ هُمْ مِنْ فَزَعٍ يَوْمَئِذٍ آمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3249, N'وَ مَنْ جاءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوهُهُمْ فِي النَّارِ هَلْ تُجْزَوْنَ إِلاَّ ما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3250, N'إِنَّما أُمِرْتُ أَنْ أَعْبُدَ رَبَّ هذِهِ الْبَلْدَةِ الَّذِي حَرَّمَها وَ لَهُ كُلُّ شَيْ‏ءٍ وَ أُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3251, N'وَ أَنْ أَتْلُوَا الْقُرْآنَ فَمَنِ اهْتَدى فَإِنَّما يَهْتَدِي لِنَفْسِهِ وَ مَنْ ضَلَّ فَقُلْ إِنَّما أَنَا مِنَ الْمُنْذِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3252, N'وَ قُلِ الْحَمْدُ لِلَّهِ سَيُرِيكُمْ آياتِهِ فَتَعْرِفُونَها وَ ما رَبُّكَ بِغافِلٍ عَمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3253, N'طسم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3254, N'تِلْكَ آياتُ الْكِتابِ الْمُبِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3255, N'نَتْلُوا عَلَيْكَ مِنْ نَبَإِ مُوسى وَ فِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3256, N'إِنَّ فِرْعَوْنَ عَلا فِي الْأَرْضِ وَ جَعَلَ أَهْلَها شِيَعاً يَسْتَضْعِفُ طائِفَةً مِنْهُمْ يُذَبِّحُ أَبْناءَهُمْ وَ يَسْتَحْيِي نِساءَهُمْ إِنَّهُ كانَ مِنَ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3257, N'وَ نُرِيدُ أَنْ نَمُنَّ عَلَى الَّذِينَ اسْتُضْعِفُوا فِي الْأَرْضِ وَ نَجْعَلَهُمْ أَئِمَّةً وَ نَجْعَلَهُمُ الْوارِثِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3258, N'وَ نُمَكِّنَ لَهُمْ فِي الْأَرْضِ وَ نُرِيَ فِرْعَوْنَ وَ هامانَ وَ جُنُودَهُما مِنْهُمْ ما كانُوا يَحْذَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3259, N'وَ أَوْحَيْنا إِلى أُمِّ مُوسى أَنْ أَرْضِعِيهِ فَإِذا خِفْتِ عَلَيْهِ فَأَلْقِيهِ فِي الْيَمِّ وَ لا تَخافِي وَ لا تَحْزَنِي إِنَّا رَادُّوهُ إِلَيْكِ وَ جاعِلُوهُ مِنَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3260, N'فَالْتَقَطَهُ آلُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُوًّا وَ حَزَناً إِنَّ فِرْعَوْنَ وَ هامانَ وَ جُنُودَهُما كانُوا خاطِئِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3261, N'وَ قالَتِ امْرَأَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِي وَ لَكَ لا تَقْتُلُوهُ عَسى أَنْ يَنْفَعَنا أَوْ نَتَّخِذَهُ وَلَداً وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3262, N'وَ أَصْبَحَ فُؤادُ أُمِّ مُوسى فارِغاً إِنْ كادَتْ لَتُبْدِي بِهِ لَوْ لا أَنْ رَبَطْنا عَلى قَلْبِها لِتَكُونَ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3263, N'وَ قالَتْ لِأُخْتِهِ قُصِّيهِ فَبَصُرَتْ بِهِ عَنْ جُنُبٍ وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3264, N'وَ حَرَّمْنا عَلَيْهِ الْمَراضِعَ مِنْ قَبْلُ فَقالَتْ هَلْ أَدُلُّكُمْ عَلى أَهْلِ بَيْتٍ يَكْفُلُونَهُ لَكُمْ وَ هُمْ لَهُ ناصِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3265, N'فَرَدَدْناهُ إِلى أُمِّهِ كَيْ تَقَرَّ عَيْنُها وَ لا تَحْزَنَ وَ لِتَعْلَمَ أَنَّ وَعْدَ اللَّهِ حَقٌّ وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3266, N'وَ لَمَّا بَلَغَ أَشُدَّهُ وَ اسْتَوى آتَيْناهُ حُكْماً وَ عِلْماً وَ كَذلِكَ نَجْزِي الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3267, N'وَ دَخَلَ الْمَدِينَةَ عَلى حِينِ غَفْلَةٍ مِنْ أَهْلِها فَوَجَدَ فِيها رَجُلَيْنِ يَقْتَتِلانِ هذا مِنْ شِيعَتِهِ وَ هذا مِنْ عَدُوِّهِ فَاسْتَغاثَهُ الَّذِي مِنْ شِيعَتِهِ عَلَى الَّذِي مِنْ عَدُوِّهِ فَوَكَزَهُ مُوسى فَقَضى عَلَيْهِ قالَ هذا مِنْ عَمَلِ الشَّيْطانِ إِنَّهُ عَدُوٌّ مُضِلٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3268, N'قالَ رَبِّ إِنِّي ظَلَمْتُ نَفْسِي فَاغْفِرْ لِي فَغَفَرَ لَهُ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3269, N'قالَ رَبِّ بِما أَنْعَمْتَ عَلَيَّ فَلَنْ أَكُونَ ظَهِيراً لِلْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3270, N'فَأَصْبَحَ فِي الْمَدِينَةِ خائِفاً يَتَرَقَّبُ فَإِذَا الَّذِي اسْتَنْصَرَهُ بِالْأَمْسِ يَسْتَصْرِخُهُ قالَ لَهُ مُوسى إِنَّكَ لَغَوِيٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3271, N'فَلَمَّا أَنْ أَرادَ أَنْ يَبْطِشَ بِالَّذِي هُوَ عَدُوٌّ لَهُما قالَ يا مُوسى أَ تُرِيدُ أَنْ تَقْتُلَنِي كَما قَتَلْتَ نَفْساً بِالْأَمْسِ إِنْ تُرِيدُ إِلاَّ أَنْ تَكُونَ جَبَّاراً فِي الْأَرْضِ وَ ما تُرِيدُ أَنْ تَكُونَ مِنَ الْمُصْلِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3272, N'وَ جاءَ رَجُلٌ مِنْ أَقْصَى الْمَدِينَةِ يَسْعى قالَ يا مُوسى إِنَّ الْمَلَأَ يَأْتَمِرُونَ بِكَ لِيَقْتُلُوكَ فَاخْرُجْ إِنِّي لَكَ مِنَ النَّاصِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3273, N'فَخَرَجَ مِنْها خائِفاً يَتَرَقَّبُ قالَ رَبِّ نَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3274, N'وَ لَمَّا تَوَجَّهَ تِلْقاءَ مَدْيَنَ قالَ عَسى رَبِّي أَنْ يَهْدِيَنِي سَواءَ السَّبِيلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3275, N'وَ لَمَّا وَرَدَ ماءَ مَدْيَنَ وَجَدَ عَلَيْهِ أُمَّةً مِنَ النَّاسِ يَسْقُونَ وَ وَجَدَ مِنْ دُونِهِمُ امْرَأَتَيْنِ تَذُودانِ قالَ ما خَطْبُكُما قالَتا لا نَسْقِي حَتَّى يُصْدِرَ الرِّعاءُ وَ أَبُونا شَيْخٌ كَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3276, N'فَسَقى لَهُما ثُمَّ تَوَلَّى إِلَى الظِّلِّ فَقالَ رَبِّ إِنِّي لِما أَنْزَلْتَ إِلَيَّ مِنْ خَيْرٍ فَقِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3277, N'فَجاءَتْهُ إِحْداهُما تَمْشِي عَلَى اسْتِحْياءٍ قالَتْ إِنَّ أَبِي يَدْعُوكَ لِيَجْزِيَكَ أَجْرَ ما سَقَيْتَ لَنا فَلَمَّا جاءَهُ وَ قَصَّ عَلَيْهِ الْقَصَصَ قالَ لا تَخَفْ نَجَوْتَ مِنَ الْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3278, N'قالَتْ إِحْداهُما يا أَبَتِ اسْتَأْجِرْهُ إِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْأَمِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3279, N'قالَ إِنِّي أُرِيدُ أَنْ أُنْكِحَكَ إِحْدَى ابْنَتَيَّ هاتَيْنِ عَلى أَنْ تَأْجُرَنِي ثَمانِيَ حِجَجٍ فَإِنْ أَتْمَمْتَ عَشْراً فَمِنْ عِنْدِكَ وَ ما أُرِيدُ أَنْ أَشُقَّ عَلَيْكَ سَتَجِدُنِي إِنْ شاءَ اللَّهُ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3280, N'قالَ ذلِكَ بَيْنِي وَ بَيْنَكَ أَيَّمَا الْأَجَلَيْنِ قَضَيْتُ فَلا عُدْوانَ عَلَيَّ وَ اللَّهُ عَلى ما نَقُولُ وَكِيلٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3281, N'فَلَمَّا قَضى مُوسَى الْأَجَلَ وَ سارَ بِأَهْلِهِ آنَسَ مِنْ جانِبِ الطُّورِ ناراً قالَ لِأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ ناراً لَعَلِّي آتِيكُمْ مِنْها بِخَبَرٍ أَوْ جَذْوَةٍ مِنَ النَّارِ لَعَلَّكُمْ تَصْطَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3282, N'فَلَمَّا أَتاها نُودِيَ مِنْ شاطِئِ الْوادِ الْأَيْمَنِ فِي الْبُقْعَةِ الْمُبارَكَةِ مِنَ الشَّجَرَةِ أَنْ يا مُوسى إِنِّي أَنَا اللَّهُ رَبُّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3283, N'وَ أَنْ أَلْقِ عَصاكَ فَلَمَّا رَآها تَهْتَزُّ كَأَنَّها جَانٌّ وَلَّى مُدْبِراً وَ لَمْ يُعَقِّبْ يا مُوسى أَقْبِلْ وَ لا تَخَفْ إِنَّكَ مِنَ الْآمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3284, N'اسْلُكْ يَدَكَ فِي جَيْبِكَ تَخْرُجْ بَيْضاءَ مِنْ غَيْرِ سُوءٍ وَ اضْمُمْ إِلَيْكَ جَناحَكَ مِنَ الرَّهْبِ فَذانِكَ بُرْهانانِ مِنْ رَبِّكَ إِلى فِرْعَوْنَ وَ مَلاَئِهِ إِنَّهُمْ كانُوا قَوْماً فاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3285, N'قالَ رَبِّ إِنِّي قَتَلْتُ مِنْهُمْ نَفْساً فَأَخافُ أَنْ يَقْتُلُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3286, N'وَ أَخِي هارُونُ هُوَ أَفْصَحُ مِنِّي لِساناً فَأَرْسِلْهُ مَعِي رِدْءاً يُصَدِّقُنِي إِنِّي أَخافُ أَنْ يُكَذِّبُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3287, N'قالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَ نَجْعَلُ لَكُما سُلْطاناً فَلا يَصِلُونَ إِلَيْكُما بِآياتِنا أَنْتُما وَ مَنِ اتَّبَعَكُمَا الْغالِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3288, N'فَلَمَّا جاءَهُمْ مُوسى بِآياتِنا بَيِّناتٍ قالُوا ما هذا إِلاَّ سِحْرٌ مُفْتَرىً وَ ما سَمِعْنا بِهذا فِي آبائِنَا الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3289, N'وَ قالَ مُوسى رَبِّي أَعْلَمُ بِمَنْ جاءَ بِالْهُدى مِنْ عِنْدِهِ وَ مَنْ تَكُونُ لَهُ عاقِبَةُ الدَّارِ إِنَّهُ لا يُفْلِحُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3290, N'وَ قالَ فِرْعَوْنُ يا أَيُّهَا الْمَلَأُ ما عَلِمْتُ لَكُمْ مِنْ إِلهٍ غَيْرِي فَأَوْقِدْ لِي يا هامانُ عَلَى الطِّينِ فَاجْعَلْ لِي صَرْحاً لَعَلِّي أَطَّلِعُ إِلى إِلهِ مُوسى وَ إِنِّي لَأَظُنُّهُ مِنَ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3291, N'وَ اسْتَكْبَرَ هُوَ وَ جُنُودُهُ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَ ظَنُّوا أَنَّهُمْ إِلَيْنا لا يُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3292, N'فَأَخَذْناهُ وَ جُنُودَهُ فَنَبَذْناهُمْ فِي الْيَمِّ فَانْظُرْ كَيْفَ كانَ عاقِبَةُ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3293, N'وَ جَعَلْناهُمْ أَئِمَّةً يَدْعُونَ إِلَى النَّارِ وَ يَوْمَ الْقِيامَةِ لا يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3294, N'وَ أَتْبَعْناهُمْ فِي هذِهِ الدُّنْيا لَعْنَةً وَ يَوْمَ الْقِيامَةِ هُمْ مِنَ الْمَقْبُوحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3295, N'وَ لَقَدْ آتَيْنا مُوسَى الْكِتابَ مِنْ بَعْدِ ما أَهْلَكْنَا الْقُرُونَ الْأُولى بَصائِرَ لِلنَّاسِ وَ هُدىً وَ رَحْمَةً لَعَلَّهُمْ يَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3296, N'وَ ما كُنْتَ بِجانِبِ الْغَرْبِيِّ إِذْ قَضَيْنا إِلى مُوسَى الْأَمْرَ وَ ما كُنْتَ مِنَ الشَّاهِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3297, N'وَ لكِنَّا أَنْشَأْنا قُرُوناً فَتَطاوَلَ عَلَيْهِمُ الْعُمُرُ وَ ما كُنْتَ ثاوِياً فِي أَهْلِ مَدْيَنَ تَتْلُوا عَلَيْهِمْ آياتِنا وَ لكِنَّا كُنَّا مُرْسِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3298, N'وَ ما كُنْتَ بِجانِبِ الطُّورِ إِذْ نادَيْنا وَ لكِنْ رَحْمَةً مِنْ رَبِّكَ لِتُنْذِرَ قَوْماً ما أَتاهُمْ مِنْ نَذِيرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3299, N'وَ لَوْ لا أَنْ تُصِيبَهُمْ مُصِيبَةٌ بِما قَدَّمَتْ أَيْدِيهِمْ فَيَقُولُوا رَبَّنا لَوْ لا أَرْسَلْتَ إِلَيْنا رَسُولاً فَنَتَّبِعَ آياتِكَ وَ نَكُونَ مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3300, N'فَلَمَّا جاءَهُمُ الْحَقُّ مِنْ عِنْدِنا قالُوا لَوْ لا أُوتِيَ مِثْلَ ما أُوتِيَ مُوسى أَ وَ لَمْ يَكْفُرُوا بِما أُوتِيَ مُوسى مِنْ قَبْلُ قالُوا سِحْرانِ تَظاهَرا وَ قالُوا إِنَّا بِكُلٍّ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3301, N'قُلْ فَأْتُوا بِكِتابٍ مِنْ عِنْدِ اللَّهِ هُوَ أَهْدى مِنْهُما أَتَّبِعْهُ إِنْ كُنْتُمْ صادِقِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3302, N'فَإِنْ لَمْ يَسْتَجِيبُوا لَكَ فَاعْلَمْ أَنَّما يَتَّبِعُونَ أَهْواءَهُمْ وَ مَنْ أَضَلُّ مِمَّنِ اتَّبَعَ هَواهُ بِغَيْرِ هُدىً مِنَ اللَّهِ إِنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3303, N'وَ لَقَدْ وَصَّلْنا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3304, N'الَّذِينَ آتَيْناهُمُ الْكِتابَ مِنْ قَبْلِهِ هُمْ بِهِ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3305, N'وَ إِذا يُتْلى عَلَيْهِمْ قالُوا آمَنَّا بِهِ إِنَّهُ الْحَقُّ مِنْ رَبِّنا إِنَّا كُنَّا مِنْ قَبْلِهِ مُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3306, N'أُولئِكَ يُؤْتَوْنَ أَجْرَهُمْ مَرَّتَيْنِ بِما صَبَرُوا وَ يَدْرَؤُنَ بِالْحَسَنَةِ السَّيِّئَةَ وَ مِمَّا رَزَقْناهُمْ يُنْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3307, N'وَ إِذا سَمِعُوا اللَّغْوَ أَعْرَضُوا عَنْهُ وَ قالُوا لَنا أَعْمالُنا وَ لَكُمْ أَعْمالُكُمْ سَلامٌ عَلَيْكُمْ لا نَبْتَغِي الْجاهِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3308, N'إِنَّكَ لا تَهْدِي مَنْ أَحْبَبْتَ وَ لكِنَّ اللَّهَ يَهْدِي مَنْ يَشاءُ وَ هُوَ أَعْلَمُ بِالْمُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3309, N'وَ قالُوا إِنْ نَتَّبِعِ الْهُدى مَعَكَ نُتَخَطَّفْ مِنْ أَرْضِنا أَ وَ لَمْ نُمَكِّنْ لَهُمْ حَرَماً آمِناً يُجْبى إِلَيْهِ ثَمَراتُ كُلِّ شَيْ‏ءٍ رِزْقاً مِنْ لَدُنَّا وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3310, N'وَ كَمْ أَهْلَكْنا مِنْ قَرْيَةٍ بَطِرَتْ مَعِيشَتَها فَتِلْكَ مَساكِنُهُمْ لَمْ تُسْكَنْ مِنْ بَعْدِهِمْ إِلاَّ قَلِيلاً وَ كُنَّا نَحْنُ الْوارِثِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3311, N'وَ ما كانَ رَبُّكَ مُهْلِكَ الْقُرى حَتَّى يَبْعَثَ فِي أُمِّها رَسُولاً يَتْلُوا عَلَيْهِمْ آياتِنا وَ ما كُنَّا مُهْلِكِي الْقُرى إِلاَّ وَ أَهْلُها ظالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3312, N'وَ ما أُوتِيتُمْ مِنْ شَيْ‏ءٍ فَمَتاعُ الْحَياةِ الدُّنْيا وَ زِينَتُها وَ ما عِنْدَ اللَّهِ خَيْرٌ وَ أَبْقى أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3313, N'أَ فَمَنْ وَعَدْناهُ وَعْداً حَسَناً فَهُوَ لاقِيهِ كَمَنْ مَتَّعْناهُ مَتاعَ الْحَياةِ الدُّنْيا ثُمَّ هُوَ يَوْمَ الْقِيامَةِ مِنَ الْمُحْضَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3314, N'وَ يَوْمَ يُنادِيهِمْ فَيَقُولُ أَيْنَ شُرَكائِيَ الَّذِينَ كُنْتُمْ تَزْعُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3315, N'قالَ الَّذِينَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنا هؤُلاءِ الَّذِينَ أَغْوَيْنا أَغْوَيْناهُمْ كَما غَوَيْنا تَبَرَّأْنا إِلَيْكَ ما كانُوا إِيَّانا يَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3316, N'وَ قِيلَ ادْعُوا شُرَكاءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَ رَأَوُا الْعَذابَ لَوْ أَنَّهُمْ كانُوا يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3317, N'وَ يَوْمَ يُنادِيهِمْ فَيَقُولُ ما ذا أَجَبْتُمُ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3318, N'فَعَمِيَتْ عَلَيْهِمُ الْأَنْباءُ يَوْمَئِذٍ فَهُمْ لا يَتَساءَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3319, N'فَأَمَّا مَنْ تابَ وَ آمَنَ وَ عَمِلَ صالِحاً فَعَسى أَنْ يَكُونَ مِنَ الْمُفْلِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3320, N'وَ رَبُّكَ يَخْلُقُ ما يَشاءُ وَ يَخْتارُ ما كانَ لَهُمُ الْخِيَرَةُ سُبْحانَ اللَّهِ وَ تَعالى عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3321, N'وَ رَبُّكَ يَعْلَمُ ما تُكِنُّ صُدُورُهُمْ وَ ما يُعْلِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3322, N'وَ هُوَ اللَّهُ لا إِلهَ إِلاَّ هُوَ لَهُ الْحَمْدُ فِي الْأُولى وَ الْآخِرَةِ وَ لَهُ الْحُكْمُ وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3323, N'قُلْ أَ رَأَيْتُمْ إِنْ جَعَلَ اللَّهُ عَلَيْكُمُ اللَّيْلَ سَرْمَداً إِلى يَوْمِ الْقِيامَةِ مَنْ إِلهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِضِياءٍ أَ فَلا تَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3324, N'قُلْ أَ رَأَيْتُمْ إِنْ جَعَلَ اللَّهُ عَلَيْكُمُ النَّهارَ سَرْمَداً إِلى يَوْمِ الْقِيامَةِ مَنْ إِلهٌ غَيْرُ اللَّهِ يَأْتِيكُمْ بِلَيْلٍ تَسْكُنُونَ فِيهِ أَ فَلا تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3325, N'وَ مِنْ رَحْمَتِهِ جَعَلَ لَكُمُ اللَّيْلَ وَ النَّهارَ لِتَسْكُنُوا فِيهِ وَ لِتَبْتَغُوا مِنْ فَضْلِهِ وَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3326, N'وَ يَوْمَ يُنادِيهِمْ فَيَقُولُ أَيْنَ شُرَكائِيَ الَّذِينَ كُنْتُمْ تَزْعُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3327, N'وَ نَزَعْنا مِنْ كُلِّ أُمَّةٍ شَهِيداً فَقُلْنا هاتُوا بُرْهانَكُمْ فَعَلِمُوا أَنَّ الْحَقَّ لِلَّهِ وَ ضَلَّ عَنْهُمْ ما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3328, N'إِنَّ قارُونَ كانَ مِنْ قَوْمِ مُوسى فَبَغى عَلَيْهِمْ وَ آتَيْناهُ مِنَ الْكُنُوزِ ما إِنَّ مَفاتِحَهُ لَتَنُوأُ بِالْعُصْبَةِ أُولِي الْقُوَّةِ إِذْ قالَ لَهُ قَوْمُهُ لا تَفْرَحْ إِنَّ اللَّهَ لا يُحِبُّ الْفَرِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3329, N'وَ ابْتَغِ فِيما آتاكَ اللَّهُ الدَّارَ الْآخِرَةَ وَ لا تَنْسَ نَصِيبَكَ مِنَ الدُّنْيا وَ أَحْسِنْ كَما أَحْسَنَ اللَّهُ إِلَيْكَ وَ لا تَبْغِ الْفَسادَ فِي الْأَرْضِ إِنَّ اللَّهَ لا يُحِبُّ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3330, N'قالَ إِنَّما أُوتِيتُهُ عَلى عِلْمٍ عِنْدِي أَ وَ لَمْ يَعْلَمْ أَنَّ اللَّهَ قَدْ أَهْلَكَ مِنْ قَبْلِهِ مِنَ الْقُرُونِ مَنْ هُوَ أَشَدُّ مِنْهُ قُوَّةً وَ أَكْثَرُ جَمْعاً وَ لا يُسْئَلُ عَنْ ذُنُوبِهِمُ الْمُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3331, N'فَخَرَجَ عَلى قَوْمِهِ فِي زِينَتِهِ قالَ الَّذِينَ يُرِيدُونَ الْحَياةَ الدُّنْيا يا لَيْتَ لَنا مِثْلَ ما أُوتِيَ قارُونُ إِنَّهُ لَذُو حَظٍّ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3332, N'وَ قالَ الَّذِينَ أُوتُوا الْعِلْمَ وَيْلَكُمْ ثَوابُ اللَّهِ خَيْرٌ لِمَنْ آمَنَ وَ عَمِلَ صالِحاً وَ لا يُلَقَّاها إِلاَّ الصَّابِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3333, N'فَخَسَفْنا بِهِ وَ بِدارِهِ الْأَرْضَ فَما كانَ لَهُ مِنْ فِئَةٍ يَنْصُرُونَهُ مِنْ دُونِ اللَّهِ وَ ما كانَ مِنَ المُنْتَصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3334, N'وَ أَصْبَحَ الَّذِينَ تَمَنَّوْا مَكانَهُ بِالْأَمْسِ يَقُولُونَ وَيْكَأَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ مِنْ عِبادِهِ وَ يَقْدِرُ لَوْ لا أَنْ مَنَّ اللَّهُ عَلَيْنا لَخَسَفَ بِنا وَيْكَأَنَّهُ لا يُفْلِحُ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3335, N'تِلْكَ الدَّارُ الْآخِرَةُ نَجْعَلُها لِلَّذِينَ لا يُرِيدُونَ عُلُوًّا فِي الْأَرْضِ وَ لا فَساداً وَ الْعاقِبَةُ لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3336, N'مَنْ جاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِنْها وَ مَنْ جاءَ بِالسَّيِّئَةِ فَلا يُجْزَى الَّذِينَ عَمِلُوا السَّيِّئاتِ إِلاَّ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3337, N'إِنَّ الَّذِي فَرَضَ عَلَيْكَ الْقُرْآنَ لَرادُّكَ إِلى مَعادٍ قُلْ رَبِّي أَعْلَمُ مَنْ جاءَ بِالْهُدى وَ مَنْ هُوَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3338, N'وَ ما كُنْتَ تَرْجُوا أَنْ يُلْقى إِلَيْكَ الْكِتابُ إِلاَّ رَحْمَةً مِنْ رَبِّكَ فَلا تَكُونَنَّ ظَهِيراً لِلْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3339, N'وَ لا يَصُدُّنَّكَ عَنْ آياتِ اللَّهِ بَعْدَ إِذْ أُنْزِلَتْ إِلَيْكَ وَ ادْعُ إِلى رَبِّكَ وَ لا تَكُونَنَّ مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3340, N'وَ لا تَدْعُ مَعَ اللَّهِ إِلهاً آخَرَ لا إِلهَ إِلاَّ هُوَ كُلُّ شَيْ‏ءٍ هالِكٌ إِلاَّ وَجْهَهُ لَهُ الْحُكْمُ وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3341, N'الم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3342, N'أَ حَسِبَ النَّاسُ أَنْ يُتْرَكُوا أَنْ يَقُولُوا آمَنَّا وَ هُمْ لا يُفْتَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3343, N'وَ لَقَدْ فَتَنَّا الَّذِينَ مِنْ قَبْلِهِمْ فَلَيَعْلَمَنَّ اللَّهُ الَّذِينَ صَدَقُوا وَ لَيَعْلَمَنَّ الْكاذِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3344, N'أَمْ حَسِبَ الَّذِينَ يَعْمَلُونَ السَّيِّئاتِ أَنْ يَسْبِقُونا ساءَ ما يَحْكُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3345, N'مَنْ كانَ يَرْجُوا لِقاءَ اللَّهِ فَإِنَّ أَجَلَ اللَّهِ لَآتٍ وَ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3346, N'وَ مَنْ جاهَدَ فَإِنَّما يُجاهِدُ لِنَفْسِهِ إِنَّ اللَّهَ لَغَنِيٌّ عَنِ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3347, N'وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّئاتِهِمْ وَ لَنَجْزِيَنَّهُمْ أَحْسَنَ الَّذِي كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3348, N'وَ وَصَّيْنَا الْإِنْسانَ بِوالِدَيْهِ حُسْناً وَ إِنْ جاهَداكَ لِتُشْرِكَ بِي ما لَيْسَ لَكَ بِهِ عِلْمٌ فَلا تُطِعْهُما إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3349, N'وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَنُدْخِلَنَّهُمْ فِي الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3350, N'وَ مِنَ النَّاسِ مَنْ يَقُولُ آمَنَّا بِاللَّهِ فَإِذا أُوذِيَ فِي اللَّهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذابِ اللَّهِ وَ لَئِنْ جاءَ نَصْرٌ مِنْ رَبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمْ أَ وَ لَيْسَ اللَّهُ بِأَعْلَمَ بِما فِي صُدُورِ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3351, N'وَ لَيَعْلَمَنَّ اللَّهُ الَّذِينَ آمَنُوا وَ لَيَعْلَمَنَّ الْمُنافِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3352, N'وَ قالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا اتَّبِعُوا سَبِيلَنا وَ لْنَحْمِلْ خَطاياكُمْ وَ ما هُمْ بِحامِلِينَ مِنْ خَطاياهُمْ مِنْ شَيْ‏ءٍ إِنَّهُمْ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3353, N'وَ لَيَحْمِلُنَّ أَثْقالَهُمْ وَ أَثْقالاً مَعَ أَثْقالِهِمْ وَ لَيُسْئَلُنَّ يَوْمَ الْقِيامَةِ عَمَّا كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3354, N'وَ لَقَدْ أَرْسَلْنا نُوحاً إِلى قَوْمِهِ فَلَبِثَ فِيهِمْ أَلْفَ سَنَةٍ إِلاَّ خَمْسِينَ عاماً فَأَخَذَهُمُ الطُّوفانُ وَ هُمْ ظالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3355, N'فَأَنْجَيْناهُ وَ أَصْحابَ السَّفِينَةِ وَ جَعَلْناها آيَةً لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3356, N'وَ إِبْراهِيمَ إِذْ قالَ لِقَوْمِهِ اعْبُدُوا اللَّهَ وَ اتَّقُوهُ ذلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3357, N'إِنَّما تَعْبُدُونَ مِنْ دُونِ اللَّهِ أَوْثاناً وَ تَخْلُقُونَ إِفْكاً إِنَّ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ لا يَمْلِكُونَ لَكُمْ رِزْقاً فَابْتَغُوا عِنْدَ اللَّهِ الرِّزْقَ وَ اعْبُدُوهُ وَ اشْكُرُوا لَهُ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3358, N'وَ إِنْ تُكَذِّبُوا فَقَدْ كَذَّبَ أُمَمٌ مِنْ قَبْلِكُمْ وَ ما عَلَى الرَّسُولِ إِلاَّ الْبَلاغُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3359, N'أَ وَ لَمْ يَرَوْا كَيْفَ يُبْدِئُ اللَّهُ الْخَلْقَ ثُمَّ يُعِيدُهُ إِنَّ ذلِكَ عَلَى اللَّهِ يَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3360, N'قُلْ سِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ بَدَأَ الْخَلْقَ ثُمَّ اللَّهُ يُنْشِئُ النَّشْأَةَ الْآخِرَةَ إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3361, N'يُعَذِّبُ مَنْ يَشاءُ وَ يَرْحَمُ مَنْ يَشاءُ وَ إِلَيْهِ تُقْلَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3362, N'وَ ما أَنْتُمْ بِمُعْجِزِينَ فِي الْأَرْضِ وَ لا فِي السَّماءِ وَ ما لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَ لا نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3363, N'وَ الَّذِينَ كَفَرُوا بِآياتِ اللَّهِ وَ لِقائِهِ أُولئِكَ يَئِسُوا مِنْ رَحْمَتِي وَ أُولئِكَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3364, N'فَما كانَ جَوابَ قَوْمِهِ إِلاَّ أَنْ قالُوا اقْتُلُوهُ أَوْ حَرِّقُوهُ فَأَنْجاهُ اللَّهُ مِنَ النَّارِ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3365, N'وَ قالَ إِنَّمَا اتَّخَذْتُمْ مِنْ دُونِ اللَّهِ أَوْثاناً مَوَدَّةَ بَيْنِكُمْ فِي الْحَياةِ الدُّنْيا ثُمَّ يَوْمَ الْقِيامَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَ يَلْعَنُ بَعْضُكُمْ بَعْضاً وَ مَأْواكُمُ النَّارُ وَ ما لَكُمْ مِنْ ناصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3366, N'فَآمَنَ لَهُ لُوطٌ وَ قالَ إِنِّي مُهاجِرٌ إِلى رَبِّي إِنَّهُ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3367, N'وَ وَهَبْنا لَهُ إِسْحاقَ وَ يَعْقُوبَ وَ جَعَلْنا فِي ذُرِّيَّتِهِ النُّبُوَّةَ وَ الْكِتابَ وَ آتَيْناهُ أَجْرَهُ فِي الدُّنْيا وَ إِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3368, N'وَ لُوطاً إِذْ قالَ لِقَوْمِهِ إِنَّكُمْ لَتَأْتُونَ الْفاحِشَةَ ما سَبَقَكُمْ بِها مِنْ أَحَدٍ مِنَ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3369, N'أَ إِنَّكُمْ لَتَأْتُونَ الرِّجالَ وَ تَقْطَعُونَ السَّبِيلَ وَ تَأْتُونَ فِي نادِيكُمُ الْمُنْكَرَ فَما كانَ جَوابَ قَوْمِهِ إِلاَّ أَنْ قالُوا ائْتِنا بِعَذابِ اللَّهِ إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3370, N'قالَ رَبِّ انْصُرْنِي عَلَى الْقَوْمِ الْمُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3371, N'وَ لَمَّا جاءَتْ رُسُلُنا إِبْراهِيمَ بِالْبُشْرى قالُوا إِنَّا مُهْلِكُوا أَهْلِ هذِهِ الْقَرْيَةِ إِنَّ أَهْلَها كانُوا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3372, N'قالَ إِنَّ فِيها لُوطاً قالُوا نَحْنُ أَعْلَمُ بِمَنْ فِيها لَنُنَجِّيَنَّهُ وَ أَهْلَهُ إِلاَّ امْرَأَتَهُ كانَتْ مِنَ الْغابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3373, N'وَ لَمَّا أَنْ جاءَتْ رُسُلُنا لُوطاً سِي‏ءَ بِهِمْ وَ ضاقَ بِهِمْ ذَرْعاً وَ قالُوا لا تَخَفْ وَ لا تَحْزَنْ إِنَّا مُنَجُّوكَ وَ أَهْلَكَ إِلاَّ امْرَأَتَكَ كانَتْ مِنَ الْغابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3374, N'إِنَّا مُنْزِلُونَ عَلى أَهْلِ هذِهِ الْقَرْيَةِ رِجْزاً مِنَ السَّماءِ بِما كانُوا يَفْسُقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3375, N'وَ لَقَدْ تَرَكْنا مِنْها آيَةً بَيِّنَةً لِقَوْمٍ يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3376, N'وَ إِلى مَدْيَنَ أَخاهُمْ شُعَيْباً فَقالَ يا قَوْمِ اعْبُدُوا اللَّهَ وَ ارْجُوا الْيَوْمَ الْآخِرَ وَ لا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3377, N'فَكَذَّبُوهُ فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دارِهِمْ جاثِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3378, N'وَ عاداً وَ ثَمُودَ وَ قَدْ تَبَيَّنَ لَكُمْ مِنْ مَساكِنِهِمْ وَ زَيَّنَ لَهُمُ الشَّيْطانُ أَعْمالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ وَ كانُوا مُسْتَبْصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3379, N'وَ قارُونَ وَ فِرْعَوْنَ وَ هامانَ وَ لَقَدْ جاءَهُمْ مُوسى بِالْبَيِّناتِ فَاسْتَكْبَرُوا فِي الْأَرْضِ وَ ما كانُوا سابِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3380, N'فَكُلاًّ أَخَذْنا بِذَنْبِهِ فَمِنْهُمْ مَنْ أَرْسَلْنا عَلَيْهِ حاصِباً وَ مِنْهُمْ مَنْ أَخَذَتْهُ الصَّيْحَةُ وَ مِنْهُمْ مَنْ خَسَفْنا بِهِ الْأَرْضَ وَ مِنْهُمْ مَنْ أَغْرَقْنا وَ ما كانَ اللَّهُ لِيَظْلِمَهُمْ وَ لكِنْ كانُوا أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3381, N'مَثَلُ الَّذِينَ اتَّخَذُوا مِنْ دُونِ اللَّهِ أَوْلِياءَ كَمَثَلِ الْعَنْكَبُوتِ اتَّخَذَتْ بَيْتاً وَ إِنَّ أَوْهَنَ الْبُيُوتِ لَبَيْتُ الْعَنْكَبُوتِ لَوْ كانُوا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3382, N'إِنَّ اللَّهَ يَعْلَمُ ما يَدْعُونَ مِنْ دُونِهِ مِنْ شَيْ‏ءٍ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3383, N'وَ تِلْكَ الْأَمْثالُ نَضْرِبُها لِلنَّاسِ وَ ما يَعْقِلُها إِلاَّ الْعالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3384, N'خَلَقَ اللَّهُ السَّماواتِ وَ الْأَرْضَ بِالْحَقِّ إِنَّ فِي ذلِكَ لَآيَةً لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3385, N'اتْلُ ما أُوحِيَ إِلَيْكَ مِنَ الْكِتابِ وَ أَقِمِ الصَّلاةَ إِنَّ الصَّلاةَ تَنْهى عَنِ الْفَحْشاءِ وَ الْمُنْكَرِ وَ لَذِكْرُ اللَّهِ أَكْبَرُ وَ اللَّهُ يَعْلَمُ ما تَصْنَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3386, N'وَ لا تُجادِلُوا أَهْلَ الْكِتابِ إِلاَّ بِالَّتِي هِيَ أَحْسَنُ إِلاَّ الَّذِينَ ظَلَمُوا مِنْهُمْ وَ قُولُوا آمَنَّا بِالَّذِي أُنْزِلَ إِلَيْنا وَ أُنْزِلَ إِلَيْكُمْ وَ إِلهُنا وَ إِلهُكُمْ واحِدٌ وَ نَحْنُ لَهُ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3387, N'وَ كَذلِكَ أَنْزَلْنا إِلَيْكَ الْكِتابَ فَالَّذِينَ آتَيْناهُمُ الْكِتابَ يُؤْمِنُونَ بِهِ وَ مِنْ هؤُلاءِ مَنْ يُؤْمِنُ بِهِ وَ ما يَجْحَدُ بِآياتِنا إِلاَّ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3388, N'وَ ما كُنْتَ تَتْلُوا مِنْ قَبْلِهِ مِنْ كِتابٍ وَ لا تَخُطُّهُ بِيَمِينِكَ إِذاً لاَرْتابَ الْمُبْطِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3389, N'بَلْ هُوَ آياتٌ بَيِّناتٌ فِي صُدُورِ الَّذِينَ أُوتُوا الْعِلْمَ وَ ما يَجْحَدُ بِآياتِنا إِلاَّ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3390, N'وَ قالُوا لَوْ لا أُنْزِلَ عَلَيْهِ آياتٌ مِنْ رَبِّهِ قُلْ إِنَّمَا الْآياتُ عِنْدَ اللَّهِ وَ إِنَّما أَنَا نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3391, N'أَ وَ لَمْ يَكْفِهِمْ أَنَّا أَنْزَلْنا عَلَيْكَ الْكِتابَ يُتْلى عَلَيْهِمْ إِنَّ فِي ذلِكَ لَرَحْمَةً وَ ذِكْرى لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3392, N'قُلْ كَفى بِاللَّهِ بَيْنِي وَ بَيْنَكُمْ شَهِيداً يَعْلَمُ ما فِي السَّماواتِ وَ الْأَرْضِ وَ الَّذِينَ آمَنُوا بِالْباطِلِ وَ كَفَرُوا بِاللَّهِ أُولئِكَ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3393, N'وَ يَسْتَعْجِلُونَكَ بِالْعَذابِ وَ لَوْ لا أَجَلٌ مُسَمًّى لَجاءَهُمُ الْعَذابُ وَ لَيَأْتِيَنَّهُمْ بَغْتَةً وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3394, N'يَسْتَعْجِلُونَكَ بِالْعَذابِ وَ إِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3395, N'يَوْمَ يَغْشاهُمُ الْعَذابُ مِنْ فَوْقِهِمْ وَ مِنْ تَحْتِ أَرْجُلِهِمْ وَ يَقُولُ ذُوقُوا ما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3396, N'يا عِبادِيَ الَّذِينَ آمَنُوا إِنَّ أَرْضِي واسِعَةٌ فَإِيَّايَ فَاعْبُدُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3397, N'كُلُّ نَفْسٍ ذائِقَةُ الْمَوْتِ ثُمَّ إِلَيْنا تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3398, N'وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَنُبَوِّئَنَّهُمْ مِنَ الْجَنَّةِ غُرَفاً تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها نِعْمَ أَجْرُ الْعامِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3399, N'الَّذِينَ صَبَرُوا وَ عَلى رَبِّهِمْ يَتَوَكَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3400, N'وَ كَأَيِّنْ مِنْ دَابَّةٍ لا تَحْمِلُ رِزْقَهَا اللَّهُ يَرْزُقُها وَ إِيَّاكُمْ وَ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3401, N'وَ لَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّماواتِ وَ الْأَرْضَ وَ سَخَّرَ الشَّمْسَ وَ الْقَمَرَ لَيَقُولُنَّ اللَّهُ فَأَنَّى يُؤْفَكُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3402, N'اللَّهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ مِنْ عِبادِهِ وَ يَقْدِرُ لَهُ إِنَّ اللَّهَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3403, N'وَ لَئِنْ سَأَلْتَهُمْ مَنْ نَزَّلَ مِنَ السَّماءِ ماءً فَأَحْيا بِهِ الْأَرْضَ مِنْ بَعْدِ مَوْتِها لَيَقُولُنَّ اللَّهُ قُلِ الْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3404, N'وَ ما هذِهِ الْحَياةُ الدُّنْيا إِلاَّ لَهْوٌ وَ لَعِبٌ وَ إِنَّ الدَّارَ الْآخِرَةَ لَهِيَ الْحَيَوانُ لَوْ كانُوا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3405, N'فَإِذا رَكِبُوا فِي الْفُلْكِ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ إِذا هُمْ يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3406, N'لِيَكْفُرُوا بِما آتَيْناهُمْ وَ لِيَتَمَتَّعُوا فَسَوْفَ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3407, N'أَ وَ لَمْ يَرَوْا أَنَّا جَعَلْنا حَرَماً آمِناً وَ يُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْ أَ فَبِالْباطِلِ يُؤْمِنُونَ وَ بِنِعْمَةِ اللَّهِ يَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3408, N'وَ مَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ كَذِباً أَوْ كَذَّبَ بِالْحَقِّ لَمَّا جاءَهُ أَ لَيْسَ فِي جَهَنَّمَ مَثْوىً لِلْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3409, N'وَ الَّذِينَ جاهَدُوا فِينا لَنَهْدِيَنَّهُمْ سُبُلَنا وَ إِنَّ اللَّهَ لَمَعَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3410, N'الم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3411, N'غُلِبَتِ الرُّومُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3412, N'فِي أَدْنَى الْأَرْضِ وَ هُمْ مِنْ بَعْدِ غَلَبِهِمْ سَيَغْلِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3413, N'فِي بِضْعِ سِنِينَ لِلَّهِ الْأَمْرُ مِنْ قَبْلُ وَ مِنْ بَعْدُ وَ يَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3414, N'بِنَصْرِ اللَّهِ يَنْصُرُ مَنْ يَشاءُ وَ هُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3415, N'وَعْدَ اللَّهِ لا يُخْلِفُ اللَّهُ وَعْدَهُ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3416, N'يَعْلَمُونَ ظاهِراً مِنَ الْحَياةِ الدُّنْيا وَ هُمْ عَنِ الْآخِرَةِ هُمْ غافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3417, N'أَ وَ لَمْ يَتَفَكَّرُوا فِي أَنْفُسِهِمْ ما خَلَقَ اللَّهُ السَّماواتِ وَ الْأَرْضَ وَ ما بَيْنَهُما إِلاَّ بِالْحَقِّ وَ أَجَلٍ مُسَمًّى وَ إِنَّ كَثِيراً مِنَ النَّاسِ بِلِقاءِ رَبِّهِمْ لَكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3418, N'أَ وَ لَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كانَ عاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ كانُوا أَشَدَّ مِنْهُمْ قُوَّةً وَ أَثارُوا الْأَرْضَ وَ عَمَرُوها أَكْثَرَ مِمَّا عَمَرُوها وَ جاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّناتِ فَما كانَ اللَّهُ لِيَظْلِمَهُمْ وَ لكِنْ كانُوا أَنْفُسَهُمْ يَظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3419, N'ثُمَّ كانَ عاقِبَةَ الَّذِينَ أَساؤُا السُّواى أَنْ كَذَّبُوا بِآياتِ اللَّهِ وَ كانُوا بِها يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3420, N'اللَّهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ ثُمَّ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3421, N'وَ يَوْمَ تَقُومُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3422, N'وَ لَمْ يَكُنْ لَهُمْ مِنْ شُرَكائِهِمْ شُفَعاءُ وَ كانُوا بِشُرَكائِهِمْ كافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3423, N'وَ يَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَتَفَرَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3424, N'فَأَمَّا الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ فَهُمْ فِي رَوْضَةٍ يُحْبَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3425, N'وَ أَمَّا الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآياتِنا وَ لِقاءِ الْآخِرَةِ فَأُولئِكَ فِي الْعَذابِ مُحْضَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3426, N'فَسُبْحانَ اللَّهِ حِينَ تُمْسُونَ وَ حِينَ تُصْبِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3427, N'وَ لَهُ الْحَمْدُ فِي السَّماواتِ وَ الْأَرْضِ وَ عَشِيًّا وَ حِينَ تُظْهِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3428, N'يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَ يُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَ يُحْيِ الْأَرْضَ بَعْدَ مَوْتِها وَ كَذلِكَ تُخْرَجُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3429, N'وَ مِنْ آياتِهِ أَنْ خَلَقَكُمْ مِنْ تُرابٍ ثُمَّ إِذا أَنْتُمْ بَشَرٌ تَنْتَشِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3430, N'وَ مِنْ آياتِهِ أَنْ خَلَقَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْواجاً لِتَسْكُنُوا إِلَيْها وَ جَعَلَ بَيْنَكُمْ مَوَدَّةً وَ رَحْمَةً إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3431, N'وَ مِنْ آياتِهِ خَلْقُ السَّماواتِ وَ الْأَرْضِ وَ اخْتِلافُ أَلْسِنَتِكُمْ وَ أَلْوانِكُمْ إِنَّ فِي ذلِكَ لَآياتٍ لِلْعالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3432, N'وَ مِنْ آياتِهِ مَنامُكُمْ بِاللَّيْلِ وَ النَّهارِ وَ ابْتِغاؤُكُمْ مِنْ فَضْلِهِ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3433, N'وَ مِنْ آياتِهِ يُرِيكُمُ الْبَرْقَ خَوْفاً وَ طَمَعاً وَ يُنَزِّلُ مِنَ السَّماءِ ماءً فَيُحْيِي بِهِ الْأَرْضَ بَعْدَ مَوْتِها إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3434, N'وَ مِنْ آياتِهِ أَنْ تَقُومَ السَّماءُ وَ الْأَرْضُ بِأَمْرِهِ ثُمَّ إِذا دَعاكُمْ دَعْوَةً مِنَ الْأَرْضِ إِذا أَنْتُمْ تَخْرُجُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3435, N'وَ لَهُ مَنْ فِي السَّماواتِ وَ الْأَرْضِ كُلٌّ لَهُ قانِتُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3436, N'وَ هُوَ الَّذِي يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ وَ هُوَ أَهْوَنُ عَلَيْهِ وَ لَهُ الْمَثَلُ الْأَعْلى فِي السَّماواتِ وَ الْأَرْضِ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3437, N'ضَرَبَ لَكُمْ مَثَلاً مِنْ أَنْفُسِكُمْ هَلْ لَكُمْ مِنْ ما مَلَكَتْ أَيْمانُكُمْ مِنْ شُرَكاءَ فِي ما رَزَقْناكُمْ فَأَنْتُمْ فِيهِ سَواءٌ تَخافُونَهُمْ كَخِيفَتِكُمْ أَنْفُسَكُمْ كَذلِكَ نُفَصِّلُ الْآياتِ لِقَوْمٍ يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3438, N'بَلِ اتَّبَعَ الَّذِينَ ظَلَمُوا أَهْواءَهُمْ بِغَيْرِ عِلْمٍ فَمَنْ يَهْدِي مَنْ أَضَلَّ اللَّهُ وَ ما لَهُمْ مِنْ ناصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3439, N'فَأَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفاً فِطْرَتَ اللَّهِ الَّتِي فَطَرَ النَّاسَ عَلَيْها لا تَبْدِيلَ لِخَلْقِ اللَّهِ ذلِكَ الدِّينُ الْقَيِّمُ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3440, N'مُنِيبِينَ إِلَيْهِ وَ اتَّقُوهُ وَ أَقِيمُوا الصَّلاةَ وَ لا تَكُونُوا مِنَ الْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3441, N'مِنَ الَّذِينَ فَرَّقُوا دِينَهُمْ وَ كانُوا شِيَعاً كُلُّ حِزْبٍ بِما لَدَيْهِمْ فَرِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3442, N'وَ إِذا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُنِيبِينَ إِلَيْهِ ثُمَّ إِذا أَذاقَهُمْ مِنْهُ رَحْمَةً إِذا فَرِيقٌ مِنْهُمْ بِرَبِّهِمْ يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3443, N'لِيَكْفُرُوا بِما آتَيْناهُمْ فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3444, N'أَمْ أَنْزَلْنا عَلَيْهِمْ سُلْطاناً فَهُوَ يَتَكَلَّمُ بِما كانُوا بِهِ يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3445, N'وَ إِذا أَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوا بِها وَ إِنْ تُصِبْهُمْ سَيِّئَةٌ بِما قَدَّمَتْ أَيْدِيهِمْ إِذا هُمْ يَقْنَطُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3446, N'أَ وَ لَمْ يَرَوْا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ وَ يَقْدِرُ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3447, N'فَآتِ ذَا الْقُرْبى حَقَّهُ وَ الْمِسْكِينَ وَ ابْنَ السَّبِيلِ ذلِكَ خَيْرٌ لِلَّذِينَ يُرِيدُونَ وَجْهَ اللَّهِ وَ أُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3448, N'وَ ما آتَيْتُمْ مِنْ رِباً لِيَرْبُوَا فِي أَمْوالِ النَّاسِ فَلا يَرْبُوا عِنْدَ اللَّهِ وَ ما آتَيْتُمْ مِنْ زَكاةٍ تُرِيدُونَ وَجْهَ اللَّهِ فَأُولئِكَ هُمُ الْمُضْعِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3449, N'اللَّهُ الَّذِي خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ هَلْ مِنْ شُرَكائِكُمْ مَنْ يَفْعَلُ مِنْ ذلِكُمْ مِنْ شَيْ‏ءٍ سُبْحانَهُ وَ تَعالى عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3450, N'ظَهَرَ الْفَسادُ فِي الْبَرِّ وَ الْبَحْرِ بِما كَسَبَتْ أَيْدِي النَّاسِ لِيُذِيقَهُمْ بَعْضَ الَّذِي عَمِلُوا لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3451, N'قُلْ سِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كانَ عاقِبَةُ الَّذِينَ مِنْ قَبْلُ كانَ أَكْثَرُهُمْ مُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3452, N'فَأَقِمْ وَجْهَكَ لِلدِّينِ الْقَيِّمِ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا مَرَدَّ لَهُ مِنَ اللَّهِ يَوْمَئِذٍ يَصَّدَّعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3453, N'مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُ وَ مَنْ عَمِلَ صالِحاً فَلِأَنْفُسِهِمْ يَمْهَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3454, N'لِيَجْزِيَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ مِنْ فَضْلِهِ إِنَّهُ لا يُحِبُّ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3455, N'وَ مِنْ آياتِهِ أَنْ يُرْسِلَ الرِّياحَ مُبَشِّراتٍ وَ لِيُذِيقَكُمْ مِنْ رَحْمَتِهِ وَ لِتَجْرِيَ الْفُلْكُ بِأَمْرِهِ وَ لِتَبْتَغُوا مِنْ فَضْلِهِ وَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3456, N'وَ لَقَدْ أَرْسَلْنا مِنْ قَبْلِكَ رُسُلاً إِلى قَوْمِهِمْ فَجاؤُهُمْ بِالْبَيِّناتِ فَانْتَقَمْنا مِنَ الَّذِينَ أَجْرَمُوا وَ كانَ حَقًّا عَلَيْنا نَصْرُ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3457, N'اللَّهُ الَّذِي يُرْسِلُ الرِّياحَ فَتُثِيرُ سَحاباً فَيَبْسُطُهُ فِي السَّماءِ كَيْفَ يَشاءُ وَ يَجْعَلُهُ كِسَفاً فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلالِهِ فَإِذا أَصابَ بِهِ مَنْ يَشاءُ مِنْ عِبادِهِ إِذا هُمْ يَسْتَبْشِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3458, N'وَ إِنْ كانُوا مِنْ قَبْلِ أَنْ يُنَزَّلَ عَلَيْهِمْ مِنْ قَبْلِهِ لَمُبْلِسِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3459, N'فَانْظُرْ إِلى آثارِ رَحْمَتِ اللَّهِ كَيْفَ يُحْيِ الْأَرْضَ بَعْدَ مَوْتِها إِنَّ ذلِكَ لَمُحْيِ الْمَوْتى وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3460, N'وَ لَئِنْ أَرْسَلْنا رِيحاً فَرَأَوْهُ مُصْفَرًّا لَظَلُّوا مِنْ بَعْدِهِ يَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3461, N'فَإِنَّكَ لا تُسْمِعُ الْمَوْتى وَ لا تُسْمِعُ الصُّمَّ الدُّعاءَ إِذا وَلَّوْا مُدْبِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3462, N'وَ ما أَنْتَ بِهادِ الْعُمْيِ عَنْ ضَلالَتِهِمْ إِنْ تُسْمِعُ إِلاَّ مَنْ يُؤْمِنُ بِآياتِنا فَهُمْ مُسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3463, N'اللَّهُ الَّذِي خَلَقَكُمْ مِنْ ضَعْفٍ ثُمَّ جَعَلَ مِنْ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْ بَعْدِ قُوَّةٍ ضَعْفاً وَ شَيْبَةً يَخْلُقُ ما يَشاءُ وَ هُوَ الْعَلِيمُ الْقَدِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3464, N'وَ يَوْمَ تَقُومُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُونَ ما لَبِثُوا غَيْرَ ساعَةٍ كَذلِكَ كانُوا يُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3465, N'وَ قالَ الَّذِينَ أُوتُوا الْعِلْمَ وَ الْإِيمانَ لَقَدْ لَبِثْتُمْ فِي كِتابِ اللَّهِ إِلى يَوْمِ الْبَعْثِ فَهذا يَوْمُ الْبَعْثِ وَ لكِنَّكُمْ كُنْتُمْ لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3466, N'فَيَوْمَئِذٍ لا يَنْفَعُ الَّذِينَ ظَلَمُوا مَعْذِرَتُهُمْ وَ لا هُمْ يُسْتَعْتَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3467, N'وَ لَقَدْ ضَرَبْنا لِلنَّاسِ فِي هذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ وَ لَئِنْ جِئْتَهُمْ بِآيَةٍ لَيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ أَنْتُمْ إِلاَّ مُبْطِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3468, N'كَذلِكَ يَطْبَعُ اللَّهُ عَلى قُلُوبِ الَّذِينَ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3469, N'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ وَ لا يَسْتَخِفَّنَّكَ الَّذِينَ لا يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3470, N'الم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3471, N'تِلْكَ آياتُ الْكِتابِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3472, N'هُدىً وَ رَحْمَةً لِلْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3473, N'الَّذِينَ يُقِيمُونَ الصَّلاةَ وَ يُؤْتُونَ الزَّكاةَ وَ هُمْ بِالْآخِرَةِ هُمْ يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3474, N'أُولئِكَ عَلى هُدىً مِنْ رَبِّهِمْ وَ أُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3475, N'وَ مِنَ النَّاسِ مَنْ يَشْتَرِي لَهْوَ الْحَدِيثِ لِيُضِلَّ عَنْ سَبِيلِ اللَّهِ بِغَيْرِ عِلْمٍ وَ يَتَّخِذَها هُزُواً أُولئِكَ لَهُمْ عَذابٌ مُهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3476, N'وَ إِذا تُتْلى عَلَيْهِ آياتُنا وَلَّى مُسْتَكْبِراً كَأَنْ لَمْ يَسْمَعْها كَأَنَّ فِي أُذُنَيْهِ وَقْراً فَبَشِّرْهُ بِعَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3477, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَهُمْ جَنَّاتُ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3478, N'خالِدِينَ فِيها وَعْدَ اللَّهِ حَقًّا وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3479, N'خَلَقَ السَّماواتِ بِغَيْرِ عَمَدٍ تَرَوْنَها وَ أَلْقى فِي الْأَرْضِ رَواسِيَ أَنْ تَمِيدَ بِكُمْ وَ بَثَّ فِيها مِنْ كُلِّ دابَّةٍ وَ أَنْزَلْنا مِنَ السَّماءِ ماءً فَأَنْبَتْنا فِيها مِنْ كُلِّ زَوْجٍ كَرِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3480, N'هذا خَلْقُ اللَّهِ فَأَرُونِي ما ذا خَلَقَ الَّذِينَ مِنْ دُونِهِ بَلِ الظَّالِمُونَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3481, N'وَ لَقَدْ آتَيْنا لُقْمانَ الْحِكْمَةَ أَنِ اشْكُرْ لِلَّهِ وَ مَنْ يَشْكُرْ فَإِنَّما يَشْكُرُ لِنَفْسِهِ وَ مَنْ كَفَرَ فَإِنَّ اللَّهَ غَنِيٌّ حَمِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3482, N'وَ إِذْ قالَ لُقْمانُ لاِبْنِهِ وَ هُوَ يَعِظُهُ يا بُنَيَّ لا تُشْرِكْ بِاللَّهِ إِنَّ الشِّرْكَ لَظُلْمٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3483, N'وَ وَصَّيْنَا الْإِنْسانَ بِوالِدَيْهِ حَمَلَتْهُ أُمُّهُ وَهْناً عَلى وَهْنٍ وَ فِصالُهُ فِي عامَيْنِ أَنِ اشْكُرْ لِي وَ لِوالِدَيْكَ إِلَيَّ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3484, N'وَ إِنْ جاهَداكَ عَلى أَنْ تُشْرِكَ بِي ما لَيْسَ لَكَ بِهِ عِلْمٌ فَلا تُطِعْهُما وَ صاحِبْهُما فِي الدُّنْيا مَعْرُوفاً وَ اتَّبِعْ سَبِيلَ مَنْ أَنابَ إِلَيَّ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3485, N'يا بُنَيَّ إِنَّها إِنْ تَكُ مِثْقالَ حَبَّةٍ مِنْ خَرْدَلٍ فَتَكُنْ فِي صَخْرَةٍ أَوْ فِي السَّماواتِ أَوْ فِي الْأَرْضِ يَأْتِ بِهَا اللَّهُ إِنَّ اللَّهَ لَطِيفٌ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3486, N'يا بُنَيَّ أَقِمِ الصَّلاةَ وَ أْمُرْ بِالْمَعْرُوفِ وَ انْهَ عَنِ الْمُنْكَرِ وَ اصْبِرْ عَلى ما أَصابَكَ إِنَّ ذلِكَ مِنْ عَزْمِ الْأُمُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3487, N'وَ لا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَ لا تَمْشِ فِي الْأَرْضِ مَرَحاً إِنَّ اللَّهَ لا يُحِبُّ كُلَّ مُخْتالٍ فَخُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3488, N'وَ اقْصِدْ فِي مَشْيِكَ وَ اغْضُضْ مِنْ صَوْتِكَ إِنَّ أَنْكَرَ الْأَصْواتِ لَصَوْتُ الْحَمِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3489, N'أَ لَمْ تَرَوْا أَنَّ اللَّهَ سَخَّرَ لَكُمْ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ أَسْبَغَ عَلَيْكُمْ نِعَمَهُ ظاهِرَةً وَ باطِنَةً وَ مِنَ النَّاسِ مَنْ يُجادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَ لا هُدىً وَ لا كِتابٍ مُنِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3490, N'وَ إِذا قِيلَ لَهُمُ اتَّبِعُوا ما أَنْزَلَ اللَّهُ قالُوا بَلْ نَتَّبِعُ ما وَجَدْنا عَلَيْهِ آباءَنا أَ وَ لَوْ كانَ الشَّيْطانُ يَدْعُوهُمْ إِلى عَذابِ السَّعِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3491, N'وَ مَنْ يُسْلِمْ وَجْهَهُ إِلَى اللَّهِ وَ هُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقى وَ إِلَى اللَّهِ عاقِبَةُ الْأُمُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3492, N'وَ مَنْ كَفَرَ فَلا يَحْزُنْكَ كُفْرُهُ إِلَيْنا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِما عَمِلُوا إِنَّ اللَّهَ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3493, N'نُمَتِّعُهُمْ قَلِيلاً ثُمَّ نَضْطَرُّهُمْ إِلى عَذابٍ غَلِيظٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3494, N'وَ لَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّماواتِ وَ الْأَرْضَ لَيَقُولُنَّ اللَّهُ قُلِ الْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3495, N'لِلَّهِ ما فِي السَّماواتِ وَ الْأَرْضِ إِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3496, N'وَ لَوْ أَنَّ ما فِي الْأَرْضِ مِنْ شَجَرَةٍ أَقْلامٌ وَ الْبَحْرُ يَمُدُّهُ مِنْ بَعْدِهِ سَبْعَةُ أَبْحُرٍ ما نَفِدَتْ كَلِماتُ اللَّهِ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3497, N'ما خَلْقُكُمْ وَ لا بَعْثُكُمْ إِلاَّ كَنَفْسٍ واحِدَةٍ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3498, N'أَ لَمْ تَرَ أَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهارِ وَ يُولِجُ النَّهارَ فِي اللَّيْلِ وَ سَخَّرَ الشَّمْسَ وَ الْقَمَرَ كُلٌّ يَجْرِي إِلى أَجَلٍ مُسَمًّى وَ أَنَّ اللَّهَ بِما تَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3499, N'ذلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَ أَنَّ ما يَدْعُونَ مِنْ دُونِهِ الْباطِلُ وَ أَنَّ اللَّهَ هُوَ الْعَلِيُّ الْكَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3500, N'أَ لَمْ تَرَ أَنَّ الْفُلْكَ تَجْرِي فِي الْبَحْرِ بِنِعْمَتِ اللَّهِ لِيُرِيَكُمْ مِنْ آياتِهِ إِنَّ فِي ذلِكَ لَآياتٍ لِكُلِّ صَبَّارٍ شَكُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3501, N'وَ إِذا غَشِيَهُمْ مَوْجٌ كَالظُّلَلِ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ فَمِنْهُمْ مُقْتَصِدٌ وَ ما يَجْحَدُ بِآياتِنا إِلاَّ كُلُّ خَتَّارٍ كَفُورٍ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3502, N'يا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ وَ اخْشَوْا يَوْماً لا يَجْزِي والِدٌ عَنْ وَلَدِهِ وَ لا مَوْلُودٌ هُوَ جازٍ عَنْ والِدِهِ شَيْئاً إِنَّ وَعْدَ اللَّهِ حَقٌّ فَلا تَغُرَّنَّكُمُ الْحَياةُ الدُّنْيا وَ لا يَغُرَّنَّكُمْ بِاللَّهِ الْغَرُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3503, N'إِنَّ اللَّهَ عِنْدَهُ عِلْمُ السَّاعَةِ وَ يُنَزِّلُ الْغَيْثَ وَ يَعْلَمُ ما فِي الْأَرْحامِ وَ ما تَدْرِي نَفْسٌ ما ذا تَكْسِبُ غَداً وَ ما تَدْرِي نَفْسٌ بِأَيِّ أَرْضٍ تَمُوتُ إِنَّ اللَّهَ عَلِيمٌ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3504, N'الم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3505, N'تَنْزِيلُ الْكِتابِ لا رَيْبَ فِيهِ مِنْ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3506, N'أَمْ يَقُولُونَ افْتَراهُ بَلْ هُوَ الْحَقُّ مِنْ رَبِّكَ لِتُنْذِرَ قَوْماً ما أَتاهُمْ مِنْ نَذِيرٍ مِنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3507, N'اللَّهُ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ وَ ما بَيْنَهُما فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوى عَلَى الْعَرْشِ ما لَكُمْ مِنْ دُونِهِ مِنْ وَلِيٍّ وَ لا شَفِيعٍ أَ فَلا تَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3508, N'يُدَبِّرُ الْأَمْرَ مِنَ السَّماءِ إِلَى الْأَرْضِ ثُمَّ يَعْرُجُ إِلَيْهِ فِي يَوْمٍ كانَ مِقْدارُهُ أَلْفَ سَنَةٍ مِمَّا تَعُدُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3509, N'ذلِكَ عالِمُ الْغَيْبِ وَ الشَّهادَةِ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3510, N'الَّذِي أَحْسَنَ كُلَّ شَيْ‏ءٍ خَلَقَهُ وَ بَدَأَ خَلْقَ الْإِنْسانِ مِنْ طِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3511, N'ثُمَّ جَعَلَ نَسْلَهُ مِنْ سُلالَةٍ مِنْ ماءٍ مَهِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3512, N'ثُمَّ سَوَّاهُ وَ نَفَخَ فِيهِ مِنْ رُوحِهِ وَ جَعَلَ لَكُمُ السَّمْعَ وَ الْأَبْصارَ وَ الْأَفْئِدَةَ قَلِيلاً ما تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3513, N'وَ قالُوا أَ إِذا ضَلَلْنا فِي الْأَرْضِ أَ إِنَّا لَفِي خَلْقٍ جَدِيدٍ بَلْ هُمْ بِلِقاءِ رَبِّهِمْ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3514, N'قُلْ يَتَوَفَّاكُمْ مَلَكُ الْمَوْتِ الَّذِي وُكِّلَ بِكُمْ ثُمَّ إِلى رَبِّكُمْ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3515, N'وَ لَوْ تَرى إِذِ الْمُجْرِمُونَ ناكِسُوا رُؤُسِهِمْ عِنْدَ رَبِّهِمْ رَبَّنا أَبْصَرْنا وَ سَمِعْنا فَارْجِعْنا نَعْمَلْ صالِحاً إِنَّا مُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3516, N'وَ لَوْ شِئْنا لَآتَيْنا كُلَّ نَفْسٍ هُداها وَ لكِنْ حَقَّ الْقَوْلُ مِنِّي لَأَمْلَأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَ النَّاسِ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3517, N'فَذُوقُوا بِما نَسِيتُمْ لِقاءَ يَوْمِكُمْ هذا إِنَّا نَسِيناكُمْ وَ ذُوقُوا عَذابَ الْخُلْدِ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3518, N'إِنَّما يُؤْمِنُ بِآياتِنَا الَّذِينَ إِذا ذُكِّرُوا بِها خَرُّوا سُجَّداً وَ سَبَّحُوا بِحَمْدِ رَبِّهِمْ وَ هُمْ لا يَسْتَكْبِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3519, N'تَتَجافى جُنُوبُهُمْ عَنِ الْمَضاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفاً وَ طَمَعاً وَ مِمَّا رَزَقْناهُمْ يُنْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3520, N'فَلا تَعْلَمُ نَفْسٌ ما أُخْفِيَ لَهُمْ مِنْ قُرَّةِ أَعْيُنٍ جَزاءً بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3521, N'أَ فَمَنْ كانَ مُؤْمِناً كَمَنْ كانَ فاسِقاً لا يَسْتَوُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3522, N'أَمَّا الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ فَلَهُمْ جَنَّاتُ الْمَأْوى نُزُلاً بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3523, N'وَ أَمَّا الَّذِينَ فَسَقُوا فَمَأْواهُمُ النَّارُ كُلَّما أَرادُوا أَنْ يَخْرُجُوا مِنْها أُعِيدُوا فِيها وَ قِيلَ لَهُمْ ذُوقُوا عَذابَ النَّارِ الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3524, N'وَ لَنُذِيقَنَّهُمْ مِنَ الْعَذابِ الْأَدْنى دُونَ الْعَذابِ الْأَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3525, N'وَ مَنْ أَظْلَمُ مِمَّنْ ذُكِّرَ بِآياتِ رَبِّهِ ثُمَّ أَعْرَضَ عَنْها إِنَّا مِنَ الْمُجْرِمِينَ مُنْتَقِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3526, N'وَ لَقَدْ آتَيْنا مُوسَى الْكِتابَ فَلا تَكُنْ فِي مِرْيَةٍ مِنْ لِقائِهِ وَ جَعَلْناهُ هُدىً لِبَنِي إِسْرائِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3527, N'وَ جَعَلْنا مِنْهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنا لَمَّا صَبَرُوا وَ كانُوا بِآياتِنا يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3528, N'إِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيامَةِ فِيما كانُوا فِيهِ يَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3529, N'أَ وَ لَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنا مِنْ قَبْلِهِمْ مِنَ الْقُرُونِ يَمْشُونَ فِي مَساكِنِهِمْ إِنَّ فِي ذلِكَ لَآياتٍ أَ فَلا يَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3530, N'أَ وَ لَمْ يَرَوْا أَنَّا نَسُوقُ الْماءَ إِلَى الْأَرْضِ الْجُرُزِ فَنُخْرِجُ بِهِ زَرْعاً تَأْكُلُ مِنْهُ أَنْعامُهُمْ وَ أَنْفُسُهُمْ أَ فَلا يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3531, N'وَ يَقُولُونَ مَتى هذَا الْفَتْحُ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3532, N'قُلْ يَوْمَ الْفَتْحِ لا يَنْفَعُ الَّذِينَ كَفَرُوا إِيمانُهُمْ وَ لا هُمْ يُنْظَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3533, N'فَأَعْرِضْ عَنْهُمْ وَ انْتَظِرْ إِنَّهُمْ مُنْتَظِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3534, N'يا أَيُّهَا النَّبِيُّ اتَّقِ اللَّهَ وَ لا تُطِعِ الْكافِرِينَ وَ الْمُنافِقِينَ إِنَّ اللَّهَ كانَ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3535, N'وَ اتَّبِعْ ما يُوحى إِلَيْكَ مِنْ رَبِّكَ إِنَّ اللَّهَ كانَ بِما تَعْمَلُونَ خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3536, N'وَ تَوَكَّلْ عَلَى اللَّهِ وَ كَفى بِاللَّهِ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3537, N'ما جَعَلَ اللَّهُ لِرَجُلٍ مِنْ قَلْبَيْنِ فِي جَوْفِهِ وَ ما جَعَلَ أَزْواجَكُمُ اللاَّئِي تُظاهِرُونَ مِنْهُنَّ أُمَّهاتِكُمْ وَ ما جَعَلَ أَدْعِياءَكُمْ أَبْناءَكُمْ ذلِكُمْ قَوْلُكُمْ بِأَفْواهِكُمْ وَ اللَّهُ يَقُولُ الْحَقَّ وَ هُوَ يَهْدِي السَّبِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3538, N'ادْعُوهُمْ لِآبائِهِمْ هُوَ أَقْسَطُ عِنْدَ اللَّهِ فَإِنْ لَمْ تَعْلَمُوا آباءَهُمْ فَإِخْوانُكُمْ فِي الدِّينِ وَ مَوالِيكُمْ وَ لَيْسَ عَلَيْكُمْ جُناحٌ فِيما أَخْطَأْتُمْ بِهِ وَ لكِنْ ما تَعَمَّدَتْ قُلُوبُكُمْ وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3539, N'النَّبِيُّ أَوْلى بِالْمُؤْمِنِينَ مِنْ أَنْفُسِهِمْ وَ أَزْواجُهُ أُمَّهاتُهُمْ وَ أُولُوا الْأَرْحامِ بَعْضُهُمْ أَوْلى بِبَعْضٍ فِي كِتابِ اللَّهِ مِنَ الْمُؤْمِنِينَ وَ الْمُهاجِرِينَ إِلاَّ أَنْ تَفْعَلُوا إِلى أَوْلِيائِكُمْ مَعْرُوفاً كانَ ذلِكَ فِي الْكِتابِ مَسْطُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3540, N'وَ إِذْ أَخَذْنا مِنَ النَّبِيِّينَ مِيثاقَهُمْ وَ مِنْكَ وَ مِنْ نُوحٍ وَ إِبْراهِيمَ وَ مُوسى وَ عِيسَى ابْنِ مَرْيَمَ وَ أَخَذْنا مِنْهُمْ مِيثاقاً غَلِيظاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3541, N'لِيَسْئَلَ الصَّادِقِينَ عَنْ صِدْقِهِمْ وَ أَعَدَّ لِلْكافِرِينَ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3542, N'يا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ جاءَتْكُمْ جُنُودٌ فَأَرْسَلْنا عَلَيْهِمْ رِيحاً وَ جُنُوداً لَمْ تَرَوْها وَ كانَ اللَّهُ بِما تَعْمَلُونَ بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3543, N'إِذْ جاؤُكُمْ مِنْ فَوْقِكُمْ وَ مِنْ أَسْفَلَ مِنْكُمْ وَ إِذْ زاغَتِ الْأَبْصارُ وَ بَلَغَتِ الْقُلُوبُ الْحَناجِرَ وَ تَظُنُّونَ بِاللَّهِ الظُّنُونَا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3544, N'هُنالِكَ ابْتُلِيَ الْمُؤْمِنُونَ وَ زُلْزِلُوا زِلْزالاً شَدِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3545, N'وَ إِذْ يَقُولُ الْمُنافِقُونَ وَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ ما وَعَدَنَا اللَّهُ وَ رَسُولُهُ إِلاَّ غُرُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3546, N'وَ إِذْ قالَتْ طائِفَةٌ مِنْهُمْ يا أَهْلَ يَثْرِبَ لا مُقامَ لَكُمْ فَارْجِعُوا وَ يَسْتَأْذِنُ فَرِيقٌ مِنْهُمُ النَّبِيَّ يَقُولُونَ إِنَّ بُيُوتَنا عَوْرَةٌ وَ ما هِيَ بِعَوْرَةٍ إِنْ يُرِيدُونَ إِلاَّ فِراراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3547, N'وَ لَوْ دُخِلَتْ عَلَيْهِمْ مِنْ أَقْطارِها ثُمَّ سُئِلُوا الْفِتْنَةَ لَآتَوْها وَ ما تَلَبَّثُوا بِها إِلاَّ يَسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3548, N'وَ لَقَدْ كانُوا عاهَدُوا اللَّهَ مِنْ قَبْلُ لا يُوَلُّونَ الْأَدْبارَ وَ كانَ عَهْدُ اللَّهِ مَسْؤُلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3549, N'قُلْ لَنْ يَنْفَعَكُمُ الْفِرارُ إِنْ فَرَرْتُمْ مِنَ الْمَوْتِ أَوِ الْقَتْلِ وَ إِذاً لا تُمَتَّعُونَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3550, N'قُلْ مَنْ ذَا الَّذِي يَعْصِمُكُمْ مِنَ اللَّهِ إِنْ أَرادَ بِكُمْ سُوءاً أَوْ أَرادَ بِكُمْ رَحْمَةً وَ لا يَجِدُونَ لَهُمْ مِنْ دُونِ اللَّهِ وَلِيًّا وَ لا نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3551, N'قَدْ يَعْلَمُ اللَّهُ الْمُعَوِّقِينَ مِنْكُمْ وَ الْقائِلِينَ لِإِخْوانِهِمْ هَلُمَّ إِلَيْنا وَ لا يَأْتُونَ الْبَأْسَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3552, N'أَشِحَّةً عَلَيْكُمْ فَإِذا جاءَ الْخَوْفُ رَأَيْتَهُمْ يَنْظُرُونَ إِلَيْكَ تَدُورُ أَعْيُنُهُمْ كَالَّذِي يُغْشى عَلَيْهِ مِنَ الْمَوْتِ فَإِذا ذَهَبَ الْخَوْفُ سَلَقُوكُمْ بِأَلْسِنَةٍ حِدادٍ أَشِحَّةً عَلَى الْخَيْرِ أُولئِكَ لَمْ يُؤْمِنُوا فَأَحْبَطَ اللَّهُ أَعْمالَهُمْ وَ كانَ ذلِكَ عَلَى اللَّهِ يَسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3553, N'يَحْسَبُونَ الْأَحْزابَ لَمْ يَذْهَبُوا وَ إِنْ يَأْتِ الْأَحْزابُ يَوَدُّوا لَوْ أَنَّهُمْ بادُونَ فِي الْأَعْرابِ يَسْئَلُونَ عَنْ أَنْبائِكُمْ وَ لَوْ كانُوا فِيكُمْ ما قاتَلُوا إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3554, N'لَقَدْ كانَ لَكُمْ فِي رَسُولِ اللَّهِ أُسْوَةٌ حَسَنَةٌ لِمَنْ كانَ يَرْجُوا اللَّهَ وَ الْيَوْمَ الْآخِرَ وَ ذَكَرَ اللَّهَ كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3555, N'وَ لَمَّا رَأَ الْمُؤْمِنُونَ الْأَحْزابَ قالُوا هذا ما وَعَدَنَا اللَّهُ وَ رَسُولُهُ وَ صَدَقَ اللَّهُ وَ رَسُولُهُ وَ ما زادَهُمْ إِلاَّ إِيماناً وَ تَسْلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3556, N'مِنَ الْمُؤْمِنِينَ رِجالٌ صَدَقُوا ما عاهَدُوا اللَّهَ عَلَيْهِ فَمِنْهُمْ مَنْ قَضى نَحْبَهُ وَ مِنْهُمْ مَنْ يَنْتَظِرُ وَ ما بَدَّلُوا تَبْدِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3557, N'لِيَجْزِيَ اللَّهُ الصَّادِقِينَ بِصِدْقِهِمْ وَ يُعَذِّبَ الْمُنافِقِينَ إِنْ شاءَ أَوْ يَتُوبَ عَلَيْهِمْ إِنَّ اللَّهَ كانَ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3558, N'وَ رَدَّ اللَّهُ الَّذِينَ كَفَرُوا بِغَيْظِهِمْ لَمْ يَنالُوا خَيْراً وَ كَفَى اللَّهُ الْمُؤْمِنِينَ الْقِتالَ وَ كانَ اللَّهُ قَوِيًّا عَزِيزاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3559, N'وَ أَنْزَلَ الَّذِينَ ظاهَرُوهُمْ مِنْ أَهْلِ الْكِتابِ مِنْ صَياصِيهِمْ وَ قَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ فَرِيقاً تَقْتُلُونَ وَ تَأْسِرُونَ فَرِيقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3560, N'وَ أَوْرَثَكُمْ أَرْضَهُمْ وَ دِيارَهُمْ وَ أَمْوالَهُمْ وَ أَرْضاً لَمْ تَطَؤُها وَ كانَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3561, N'يا أَيُّهَا النَّبِيُّ قُلْ لِأَزْواجِكَ إِنْ كُنْتُنَّ تُرِدْنَ الْحَياةَ الدُّنْيا وَ زِينَتَها فَتَعالَيْنَ أُمَتِّعْكُنَّ وَ أُسَرِّحْكُنَّ سَراحاً جَمِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3562, N'وَ إِنْ كُنْتُنَّ تُرِدْنَ اللَّهَ وَ رَسُولَهُ وَ الدَّارَ الْآخِرَةَ فَإِنَّ اللَّهَ أَعَدَّ لِلْمُحْسِناتِ مِنْكُنَّ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3563, N'يا نِساءَ النَّبِيِّ مَنْ يَأْتِ مِنْكُنَّ بِفاحِشَةٍ مُبَيِّنَةٍ يُضاعَفْ لَهَا الْعَذابُ ضِعْفَيْنِ وَ كانَ ذلِكَ عَلَى اللَّهِ يَسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3564, N'وَ مَنْ يَقْنُتْ مِنْكُنَّ لِلَّهِ وَ رَسُولِهِ وَ تَعْمَلْ صالِحاً نُؤْتِها أَجْرَها مَرَّتَيْنِ وَ أَعْتَدْنا لَها رِزْقاً كَرِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3565, N'يا نِساءَ النَّبِيِّ لَسْتُنَّ كَأَحَدٍ مِنَ النِّساءِ إِنِ اتَّقَيْتُنَّ فَلا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِي فِي قَلْبِهِ مَرَضٌ وَ قُلْنَ قَوْلاً مَعْرُوفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3566, N'وَ قَرْنَ فِي بُيُوتِكُنَّ وَ لا تَبَرَّجْنَ تَبَرُّجَ الْجاهِلِيَّةِ الْأُولى وَ أَقِمْنَ الصَّلاةَ وَ آتِينَ الزَّكاةَ وَ أَطِعْنَ اللَّهَ وَ رَسُولَهُ إِنَّما يُرِيدُ اللَّهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ أَهْلَ الْبَيْتِ وَ يُطَهِّرَكُمْ تَطْهِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3567, N'وَ اذْكُرْنَ ما يُتْلى فِي بُيُوتِكُنَّ مِنْ آياتِ اللَّهِ وَ الْحِكْمَةِ إِنَّ اللَّهَ كانَ لَطِيفاً خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3568, N'إِنَّ الْمُسْلِمِينَ وَ الْمُسْلِماتِ وَ الْمُؤْمِنِينَ وَ الْمُؤْمِناتِ وَ الْقانِتِينَ وَ الْقانِتاتِ وَ الصَّادِقِينَ وَ الصَّادِقاتِ وَ الصَّابِرِينَ وَ الصَّابِراتِ وَ الْخاشِعِينَ وَ الْخاشِعاتِ وَ الْمُتَصَدِّقِينَ وَ الْمُتَصَدِّقاتِ وَ الصَّائِمِينَ وَ الصَّائِماتِ وَ الْحافِظِينَ فُرُوجَهُمْ وَ الْحافِظاتِ وَ الذَّاكِرِينَ اللَّهَ كَثِيراً وَ الذَّاكِراتِ أَعَدَّ اللَّهُ لَهُمْ مَغْفِرَةً وَ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3569, N'وَ ما كانَ لِمُؤْمِنٍ وَ لا مُؤْمِنَةٍ إِذا قَضَى اللَّهُ وَ رَسُولُهُ أَمْراً أَنْ يَكُونَ لَهُمُ الْخِيَرَةُ مِنْ أَمْرِهِمْ وَ مَنْ يَعْصِ اللَّهَ وَ رَسُولَهُ فَقَدْ ضَلَّ ضَلالاً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3570, N'وَ إِذْ تَقُولُ لِلَّذِي أَنْعَمَ اللَّهُ عَلَيْهِ وَ أَنْعَمْتَ عَلَيْهِ أَمْسِكْ عَلَيْكَ زَوْجَكَ وَ اتَّقِ اللَّهَ وَ تُخْفِي فِي نَفْسِكَ مَا اللَّهُ مُبْدِيهِ وَ تَخْشَى النَّاسَ وَ اللَّهُ أَحَقُّ أَنْ تَخْشاهُ فَلَمَّا قَضى زَيْدٌ مِنْها وَطَراً زَوَّجْناكَها لِكَيْ لا يَكُونَ عَلَى الْمُؤْمِنِينَ حَرَجٌ فِي أَزْواجِ أَدْعِيائِهِمْ إِذا قَضَوْا مِنْهُنَّ وَطَراً وَ كانَ أَمْرُ اللَّهِ مَفْعُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3571, N'ما كانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيما فَرَضَ اللَّهُ لَهُ سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ وَ كانَ أَمْرُ اللَّهِ قَدَراً مَقْدُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3572, N'الَّذِينَ يُبَلِّغُونَ رِسالاتِ اللَّهِ وَ يَخْشَوْنَهُ وَ لا يَخْشَوْنَ أَحَداً إِلاَّ اللَّهَ وَ كَفى بِاللَّهِ حَسِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3573, N'ما كانَ مُحَمَّدٌ أَبا أَحَدٍ مِنْ رِجالِكُمْ وَ لكِنْ رَسُولَ اللَّهِ وَ خاتَمَ النَّبِيِّينَ وَ كانَ اللَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3574, N'يا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا اللَّهَ ذِكْراً كَثِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3575, N'وَ سَبِّحُوهُ بُكْرَةً وَ أَصِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3576, N'هُوَ الَّذِي يُصَلِّي عَلَيْكُمْ وَ مَلائِكَتُهُ لِيُخْرِجَكُمْ مِنَ الظُّلُماتِ إِلَى النُّورِ وَ كانَ بِالْمُؤْمِنِينَ رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3577, N'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُ سَلامٌ وَ أَعَدَّ لَهُمْ أَجْراً كَرِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3578, N'يا أَيُّهَا النَّبِيُّ إِنَّا أَرْسَلْناكَ شاهِداً وَ مُبَشِّراً وَ نَذِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3579, N'وَ داعِياً إِلَى اللَّهِ بِإِذْنِهِ وَ سِراجاً مُنِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3580, N'وَ بَشِّرِ الْمُؤْمِنِينَ بِأَنَّ لَهُمْ مِنَ اللَّهِ فَضْلاً كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3581, N'وَ لا تُطِعِ الْكافِرِينَ وَ الْمُنافِقِينَ وَ دَعْ أَذاهُمْ وَ تَوَكَّلْ عَلَى اللَّهِ وَ كَفى بِاللَّهِ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3582, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا نَكَحْتُمُ الْمُؤْمِناتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ أَنْ تَمَسُّوهُنَّ فَما لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّونَها فَمَتِّعُوهُنَّ وَ سَرِّحُوهُنَّ سَراحاً جَمِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3583, N'يا أَيُّهَا النَّبِيُّ إِنَّا أَحْلَلْنا لَكَ أَزْواجَكَ اللاَّتِي آتَيْتَ أُجُورَهُنَّ وَ ما مَلَكَتْ يَمِينُكَ مِمَّا أَفاءَ اللَّهُ عَلَيْكَ وَ بَناتِ عَمِّكَ وَ بَناتِ عَمَّاتِكَ وَ بَناتِ خالِكَ وَ بَناتِ خالاتِكَ اللاَّتِي هاجَرْنَ مَعَكَ وَ امْرَأَةً مُؤْمِنَةً إِنْ وَهَبَتْ نَفْسَها لِلنَّبِيِّ إِنْ أَرادَ النَّبِيُّ أَنْ يَسْتَنْكِحَها خالِصَةً لَكَ مِنْ دُونِ الْمُؤْمِنِينَ قَدْ عَلِمْنا ما فَرَضْنا عَلَيْهِمْ فِي أَزْواجِهِمْ وَ ما مَلَكَتْ أَيْمانُهُمْ لِكَيْلا يَكُونَ عَلَيْكَ حَرَجٌ وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3584, N'تُرْجِي مَنْ تَشاءُ مِنْهُنَّ وَ تُؤْوِي إِلَيْكَ مَنْ تَشاءُ وَ مَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلا جُناحَ عَلَيْكَ ذلِكَ أَدْنى أَنْ تَقَرَّ أَعْيُنُهُنَّ وَ لا يَحْزَنَّ وَ يَرْضَيْنَ بِما آتَيْتَهُنَّ كُلُّهُنَّ وَ اللَّهُ يَعْلَمُ ما فِي قُلُوبِكُمْ وَ كانَ اللَّهُ عَلِيماً حَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3585, N'لا يَحِلُّ لَكَ النِّساءُ مِنْ بَعْدُ وَ لا أَنْ تَبَدَّلَ بِهِنَّ مِنْ أَزْواجٍ وَ لَوْ أَعْجَبَكَ حُسْنُهُنَّ إِلاَّ ما مَلَكَتْ يَمِينُكَ وَ كانَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ رَقِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3586, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَدْخُلُوا بُيُوتَ النَّبِيِّ إِلاَّ أَنْ يُؤْذَنَ لَكُمْ إِلى طَعامٍ غَيْرَ ناظِرِينَ إِناهُ وَ لكِنْ إِذا دُعِيتُمْ فَادْخُلُوا فَإِذا طَعِمْتُمْ فَانْتَشِرُوا وَ لا مُسْتَأْنِسِينَ لِحَدِيثٍ إِنَّ ذلِكُمْ كانَ يُؤْذِي النَّبِيَّ فَيَسْتَحْيِي مِنْكُمْ وَ اللَّهُ لا يَسْتَحْيِي مِنَ الْحَقِّ وَ إِذا سَأَلْتُمُوهُنَّ مَتاعاً فَسْئَلُوهُنَّ مِنْ وَراءِ حِجابٍ ذلِكُمْ أَطْهَرُ لِقُلُوبِكُمْ وَ قُلُوبِهِنَّ وَ ما كانَ لَكُمْ أَنْ تُؤْذُوا رَسُولَ اللَّهِ وَ لا أَنْ تَنْكِحُوا أَزْواجَهُ مِنْ بَعْدِهِ أَبَداً إِنَّ ذلِكُمْ كانَ عِنْدَ اللَّهِ عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3587, N'إِنْ تُبْدُوا شَيْئاً أَوْ تُخْفُوهُ فَإِنَّ اللَّهَ كانَ بِكُلِّ شَيْ‏ءٍ عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3588, N'لا جُناحَ عَلَيْهِنَّ فِي آبائِهِنَّ وَ لا أَبْنائِهِنَّ وَ لا إِخْوانِهِنَّ وَ لا أَبْناءِ إِخْوانِهِنَّ وَ لا أَبْناءِ أَخَواتِهِنَّ وَ لا نِسائِهِنَّ وَ لا ما مَلَكَتْ أَيْمانُهُنَّ وَ اتَّقِينَ اللَّهَ إِنَّ اللَّهَ كانَ عَلى كُلِّ شَيْ‏ءٍ شَهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3589, N'إِنَّ اللَّهَ وَ مَلائِكَتَهُ يُصَلُّونَ عَلَى النَّبِيِّ يا أَيُّهَا الَّذِينَ آمَنُوا صَلُّوا عَلَيْهِ وَ سَلِّمُوا تَسْلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3590, N'إِنَّ الَّذِينَ يُؤْذُونَ اللَّهَ وَ رَسُولَهُ لَعَنَهُمُ اللَّهُ فِي الدُّنْيا وَ الْآخِرَةِ وَ أَعَدَّ لَهُمْ عَذاباً مُهِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3591, N'وَ الَّذِينَ يُؤْذُونَ الْمُؤْمِنِينَ وَ الْمُؤْمِناتِ بِغَيْرِ مَا اكْتَسَبُوا فَقَدِ احْتَمَلُوا بُهْتاناً وَ إِثْماً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3592, N'يا أَيُّهَا النَّبِيُّ قُلْ لِأَزْواجِكَ وَ بَناتِكَ وَ نِساءِ الْمُؤْمِنِينَ يُدْنِينَ عَلَيْهِنَّ مِنْ جَلاَبِيبِهِنَّ ذلِكَ أَدْنى أَنْ يُعْرَفْنَ فَلا يُؤْذَيْنَ وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3593, N'لَئِنْ لَمْ يَنْتَهِ الْمُنافِقُونَ وَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَ الْمُرْجِفُونَ فِي الْمَدِينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لا يُجاوِرُونَكَ فِيها إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3594, N'مَلْعُونِينَ أَيْنَما ثُقِفُوا أُخِذُوا وَ قُتِّلُوا تَقْتِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3595, N'سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِنْ قَبْلُ وَ لَنْ تَجِدَ لِسُنَّةِ اللَّهِ تَبْدِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3596, N'يَسْئَلُكَ النَّاسُ عَنِ السَّاعَةِ قُلْ إِنَّما عِلْمُها عِنْدَ اللَّهِ وَ ما يُدْرِيكَ لَعَلَّ السَّاعَةَ تَكُونُ قَرِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3597, N'إِنَّ اللَّهَ لَعَنَ الْكافِرِينَ وَ أَعَدَّ لَهُمْ سَعِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3598, N'خالِدِينَ فِيها أَبَداً لا يَجِدُونَ وَلِيًّا وَ لا نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3599, N'يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِي النَّارِ يَقُولُونَ يا لَيْتَنا أَطَعْنَا اللَّهَ وَ أَطَعْنَا الرَّسُولاَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3600, N'وَ قالُوا رَبَّنا إِنَّا أَطَعْنا سادَتَنا وَ كُبَراءَنا فَأَضَلُّونَا السَّبِيلاَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3601, N'رَبَّنا آتِهِمْ ضِعْفَيْنِ مِنَ الْعَذابِ وَ الْعَنْهُمْ لَعْناً كَبِيراً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3602, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَكُونُوا كَالَّذِينَ آذَوْا مُوسى فَبَرَّأَهُ اللَّهُ مِمَّا قالُوا وَ كانَ عِنْدَ اللَّهِ وَجِيهاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3603, N'يا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَ قُولُوا قَوْلاً سَدِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3604, N'يُصْلِحْ لَكُمْ أَعْمالَكُمْ وَ يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَ مَنْ يُطِعِ اللَّهَ وَ رَسُولَهُ فَقَدْ فازَ فَوْزاً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3605, N'إِنَّا عَرَضْنَا الْأَمانَةَ عَلَى السَّماواتِ وَ الْأَرْضِ وَ الْجِبالِ فَأَبَيْنَ أَنْ يَحْمِلْنَها وَ أَشْفَقْنَ مِنْها وَ حَمَلَهَا الْإِنْسانُ إِنَّهُ كانَ ظَلُوماً جَهُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3606, N'لِيُعَذِّبَ اللَّهُ الْمُنافِقِينَ وَ الْمُنافِقاتِ وَ الْمُشْرِكِينَ وَ الْمُشْرِكاتِ وَ يَتُوبَ اللَّهُ عَلَى الْمُؤْمِنِينَ وَ الْمُؤْمِناتِ وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3607, N'الْحَمْدُ لِلَّهِ الَّذِي لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ لَهُ الْحَمْدُ فِي الْآخِرَةِ وَ هُوَ الْحَكِيمُ الْخَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3608, N'يَعْلَمُ ما يَلِجُ فِي الْأَرْضِ وَ ما يَخْرُجُ مِنْها وَ ما يَنْزِلُ مِنَ السَّماءِ وَ ما يَعْرُجُ فِيها وَ هُوَ الرَّحِيمُ الْغَفُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3609, N'وَ قالَ الَّذِينَ كَفَرُوا لا تَأْتِينَا السَّاعَةُ قُلْ بَلى وَ رَبِّي لَتَأْتِيَنَّكُمْ عالِمِ الْغَيْبِ لا يَعْزُبُ عَنْهُ مِثْقالُ ذَرَّةٍ فِي السَّماواتِ وَ لا فِي الْأَرْضِ وَ لا أَصْغَرُ مِنْ ذلِكَ وَ لا أَكْبَرُ إِلاَّ فِي كِتابٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3610, N'لِيَجْزِيَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ أُولئِكَ لَهُمْ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3611, N'وَ الَّذِينَ سَعَوْا فِي آياتِنا مُعاجِزِينَ أُولئِكَ لَهُمْ عَذابٌ مِنْ رِجْزٍ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3612, N'وَ يَرَى الَّذِينَ أُوتُوا الْعِلْمَ الَّذِي أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ هُوَ الْحَقَّ وَ يَهْدِي إِلى صِراطِ الْعَزِيزِ الْحَمِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3613, N'وَ قالَ الَّذِينَ كَفَرُوا هَلْ نَدُلُّكُمْ عَلى رَجُلٍ يُنَبِّئُكُمْ إِذا مُزِّقْتُمْ كُلَّ مُمَزَّقٍ إِنَّكُمْ لَفِي خَلْقٍ جَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3614, N'أَفْتَرى عَلَى اللَّهِ كَذِباً أَمْ بِهِ جِنَّةٌ بَلِ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ فِي الْعَذابِ وَ الضَّلالِ الْبَعِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3615, N'أَ فَلَمْ يَرَوْا إِلى ما بَيْنَ أَيْدِيهِمْ وَ ما خَلْفَهُمْ مِنَ السَّماءِ وَ الْأَرْضِ إِنْ نَشَأْ نَخْسِفْ بِهِمُ الْأَرْضَ أَوْ نُسْقِطْ عَلَيْهِمْ كِسَفاً مِنَ السَّماءِ إِنَّ فِي ذلِكَ لَآيَةً لِكُلِّ عَبْدٍ مُنِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3616, N'وَ لَقَدْ آتَيْنا داوُدَ مِنَّا فَضْلاً يا جِبالُ أَوِّبِي مَعَهُ وَ الطَّيْرَ وَ أَلَنَّا لَهُ الْحَدِيدَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3617, N'أَنِ اعْمَلْ سابِغاتٍ وَ قَدِّرْ فِي السَّرْدِ وَ اعْمَلُوا صالِحاً إِنِّي بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3618, N'وَ لِسُلَيْمانَ الرِّيحَ غُدُوُّها شَهْرٌ وَ رَواحُها شَهْرٌ وَ أَسَلْنا لَهُ عَيْنَ الْقِطْرِ وَ مِنَ الْجِنِّ مَنْ يَعْمَلُ بَيْنَ يَدَيْهِ بِإِذْنِ رَبِّهِ وَ مَنْ يَزِغْ مِنْهُمْ عَنْ أَمْرِنا نُذِقْهُ مِنْ عَذابِ السَّعِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3619, N'يَعْمَلُونَ لَهُ ما يَشاءُ مِنْ مَحارِيبَ وَ تَماثِيلَ وَ جِفانٍ كَالْجَوابِ وَ قُدُورٍ راسِياتٍ اعْمَلُوا آلَ داوُدَ شُكْراً وَ قَلِيلٌ مِنْ عِبادِيَ الشَّكُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3620, N'فَلَمَّا قَضَيْنا عَلَيْهِ الْمَوْتَ ما دَلَّهُمْ عَلى مَوْتِهِ إِلاَّ دَابَّةُ الْأَرْضِ تَأْكُلُ مِنْسَأَتَهُ فَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ أَنْ لَوْ كانُوا يَعْلَمُونَ الْغَيْبَ ما لَبِثُوا فِي الْعَذابِ الْمُهِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3621, N'لَقَدْ كانَ لِسَبَإٍ فِي مَسْكَنِهِمْ آيَةٌ جَنَّتانِ عَنْ يَمِينٍ وَ شِمالٍ كُلُوا مِنْ رِزْقِ رَبِّكُمْ وَ اشْكُرُوا لَهُ بَلْدَةٌ طَيِّبَةٌ وَ رَبٌّ غَفُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3622, N'فَأَعْرَضُوا فَأَرْسَلْنا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَ بَدَّلْناهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَواتَيْ أُكُلٍ خَمْطٍ وَ أَثْلٍ وَ شَيْ‏ءٍ مِنْ سِدْرٍ قَلِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3623, N'ذلِكَ جَزَيْناهُمْ بِما كَفَرُوا وَ هَلْ نُجازِي إِلاَّ الْكَفُورَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3624, N'وَ جَعَلْنا بَيْنَهُمْ وَ بَيْنَ الْقُرَى الَّتِي بارَكْنا فِيها قُرىً ظاهِرَةً وَ قَدَّرْنا فِيهَا السَّيْرَ سِيرُوا فِيها لَيالِيَ وَ أَيَّاماً آمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3625, N'فَقالُوا رَبَّنا باعِدْ بَيْنَ أَسْفارِنا وَ ظَلَمُوا أَنْفُسَهُمْ فَجَعَلْناهُمْ أَحادِيثَ وَ مَزَّقْناهُمْ كُلَّ مُمَزَّقٍ إِنَّ فِي ذلِكَ لَآياتٍ لِكُلِّ صَبَّارٍ شَكُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3626, N'وَ لَقَدْ صَدَّقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُ فَاتَّبَعُوهُ إِلاَّ فَرِيقاً مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3627, N'وَ ما كانَ لَهُ عَلَيْهِمْ مِنْ سُلْطانٍ إِلاَّ لِنَعْلَمَ مَنْ يُؤْمِنُ بِالْآخِرَةِ مِمَّنْ هُوَ مِنْها فِي شَكٍّ وَ رَبُّكَ عَلى كُلِّ شَيْ‏ءٍ حَفِيظٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3628, N'قُلِ ادْعُوا الَّذِينَ زَعَمْتُمْ مِنْ دُونِ اللَّهِ لا يَمْلِكُونَ مِثْقالَ ذَرَّةٍ فِي السَّماواتِ وَ لا فِي الْأَرْضِ وَ ما لَهُمْ فِيهِما مِنْ شِرْكٍ وَ ما لَهُ مِنْهُمْ مِنْ ظَهِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3629, N'وَ لا تَنْفَعُ الشَّفاعَةُ عِنْدَهُ إِلاَّ لِمَنْ أَذِنَ لَهُ حَتَّى إِذا فُزِّعَ عَنْ قُلُوبِهِمْ قالُوا ما ذا قالَ رَبُّكُمْ قالُوا الْحَقَّ وَ هُوَ الْعَلِيُّ الْكَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3630, N'قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّماواتِ وَ الْأَرْضِ قُلِ اللَّهُ وَ إِنَّا أَوْ إِيَّاكُمْ لَعَلى هُدىً أَوْ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3631, N'قُلْ لا تُسْئَلُونَ عَمَّا أَجْرَمْنا وَ لا نُسْئَلُ عَمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3632, N'قُلْ يَجْمَعُ بَيْنَنا رَبُّنا ثُمَّ يَفْتَحُ بَيْنَنا بِالْحَقِّ وَ هُوَ الْفَتَّاحُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3633, N'قُلْ أَرُونِيَ الَّذِينَ أَلْحَقْتُمْ بِهِ شُرَكاءَ كَلاَّ بَلْ هُوَ اللَّهُ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3634, N'وَ ما أَرْسَلْناكَ إِلاَّ كَافَّةً لِلنَّاسِ بَشِيراً وَ نَذِيراً وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3635, N'وَ يَقُولُونَ مَتى هذَا الْوَعْدُ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3636, N'قُلْ لَكُمْ مِيعادُ يَوْمٍ لا تَسْتَأْخِرُونَ عَنْهُ ساعَةً وَ لا تَسْتَقْدِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3637, N'وَ قالَ الَّذِينَ كَفَرُوا لَنْ نُؤْمِنَ بِهذَا الْقُرْآنِ وَ لا بِالَّذِي بَيْنَ يَدَيْهِ وَ لَوْ تَرى إِذِ الظَّالِمُونَ مَوْقُوفُونَ عِنْدَ رَبِّهِمْ يَرْجِعُ بَعْضُهُمْ إِلى بَعْضٍ الْقَوْلَ يَقُولُ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا لَوْ لا أَنْتُمْ لَكُنَّا مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3638, N'قالَ الَّذِينَ اسْتَكْبَرُوا لِلَّذِينَ اسْتُضْعِفُوا أَ نَحْنُ صَدَدْناكُمْ عَنِ الْهُدى بَعْدَ إِذْ جاءَكُمْ بَلْ كُنْتُمْ مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3639, N'وَ قالَ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا بَلْ مَكْرُ اللَّيْلِ وَ النَّهارِ إِذْ تَأْمُرُونَنا أَنْ نَكْفُرَ بِاللَّهِ وَ نَجْعَلَ لَهُ أَنْداداً وَ أَسَرُّوا النَّدامَةَ لَمَّا رَأَوُا الْعَذابَ وَ جَعَلْنَا الْأَغْلالَ فِي أَعْناقِ الَّذِينَ كَفَرُوا هَلْ يُجْزَوْنَ إِلاَّ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3640, N'وَ ما أَرْسَلْنا فِي قَرْيَةٍ مِنْ نَذِيرٍ إِلاَّ قالَ مُتْرَفُوها إِنَّا بِما أُرْسِلْتُمْ بِهِ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3641, N'وَ قالُوا نَحْنُ أَكْثَرُ أَمْوالاً وَ أَوْلاداً وَ ما نَحْنُ بِمُعَذَّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3642, N'قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ وَ يَقْدِرُ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3643, N'وَ ما أَمْوالُكُمْ وَ لا أَوْلادُكُمْ بِالَّتِي تُقَرِّبُكُمْ عِنْدَنا زُلْفى إِلاَّ مَنْ آمَنَ وَ عَمِلَ صالِحاً فَأُولئِكَ لَهُمْ جَزاءُ الضِّعْفِ بِما عَمِلُوا وَ هُمْ فِي الْغُرُفاتِ آمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3644, N'وَ الَّذِينَ يَسْعَوْنَ فِي آياتِنا مُعاجِزِينَ أُولئِكَ فِي الْعَذابِ مُحْضَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3645, N'قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ مِنْ عِبادِهِ وَ يَقْدِرُ لَهُ وَ ما أَنْفَقْتُمْ مِنْ شَيْ‏ءٍ فَهُوَ يُخْلِفُهُ وَ هُوَ خَيْرُ الرَّازِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3646, N'وَ يَوْمَ يَحْشُرُهُمْ جَمِيعاً ثُمَّ يَقُولُ لِلْمَلائِكَةِ أَ هؤُلاءِ إِيَّاكُمْ كانُوا يَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3647, N'قالُوا سُبْحانَكَ أَنْتَ وَلِيُّنا مِنْ دُونِهِمْ بَلْ كانُوا يَعْبُدُونَ الْجِنَّ أَكْثَرُهُمْ بِهِمْ مُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3648, N'فَالْيَوْمَ لا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَفْعاً وَ لا ضَرًّا وَ نَقُولُ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذابَ النَّارِ الَّتِي كُنْتُمْ بِها تُكَذِّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3649, N'وَ إِذا تُتْلى عَلَيْهِمْ آياتُنا بَيِّناتٍ قالُوا ما هذا إِلاَّ رَجُلٌ يُرِيدُ أَنْ يَصُدَّكُمْ عَمَّا كانَ يَعْبُدُ آباؤُكُمْ وَ قالُوا ما هذا إِلاَّ إِفْكٌ مُفْتَرىً وَ قالَ الَّذِينَ كَفَرُوا لِلْحَقِّ لَمَّا جاءَهُمْ إِنْ هذا إِلاَّ سِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3650, N'وَ ما آتَيْناهُمْ مِنْ كُتُبٍ يَدْرُسُونَها وَ ما أَرْسَلْنا إِلَيْهِمْ قَبْلَكَ مِنْ نَذِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3651, N'وَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ وَ ما بَلَغُوا مِعْشارَ ما آتَيْناهُمْ فَكَذَّبُوا رُسُلِي فَكَيْفَ كانَ نَكِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3652, N'قُلْ إِنَّما أَعِظُكُمْ بِواحِدَةٍ أَنْ تَقُومُوا لِلَّهِ مَثْنى وَ فُرادى ثُمَّ تَتَفَكَّرُوا ما بِصاحِبِكُمْ مِنْ جِنَّةٍ إِنْ هُوَ إِلاَّ نَذِيرٌ لَكُمْ بَيْنَ يَدَيْ عَذابٍ شَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3653, N'قُلْ ما سَأَلْتُكُمْ مِنْ أَجْرٍ فَهُوَ لَكُمْ إِنْ أَجْرِيَ إِلاَّ عَلَى اللَّهِ وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ شَهِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3654, N'قُلْ إِنَّ رَبِّي يَقْذِفُ بِالْحَقِّ عَلاَّمُ الْغُيُوبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3655, N'قُلْ جاءَ الْحَقُّ وَ ما يُبْدِئُ الْباطِلُ وَ ما يُعِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3656, N'قُلْ إِنْ ضَلَلْتُ فَإِنَّما أَضِلُّ عَلى نَفْسِي وَ إِنِ اهْتَدَيْتُ فَبِما يُوحِي إِلَيَّ رَبِّي إِنَّهُ سَمِيعٌ قَرِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3657, N'وَ لَوْ تَرى إِذْ فَزِعُوا فَلا فَوْتَ وَ أُخِذُوا مِنْ مَكانٍ قَرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3658, N'وَ قالُوا آمَنَّا بِهِ وَ أَنَّى لَهُمُ التَّناوُشُ مِنْ مَكانٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3659, N'وَ قَدْ كَفَرُوا بِهِ مِنْ قَبْلُ وَ يَقْذِفُونَ بِالْغَيْبِ مِنْ مَكانٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3660, N'وَ حِيلَ بَيْنَهُمْ وَ بَيْنَ ما يَشْتَهُونَ كَما فُعِلَ بِأَشْياعِهِمْ مِنْ قَبْلُ إِنَّهُمْ كانُوا فِي شَكٍّ مُرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3661, N'الْحَمْدُ لِلَّهِ فاطِرِ السَّماواتِ وَ الْأَرْضِ جاعِلِ الْمَلائِكَةِ رُسُلاً أُولِي أَجْنِحَةٍ مَثْنى وَ ثُلاثَ وَ رُباعَ يَزِيدُ فِي الْخَلْقِ ما يَشاءُ إِنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3662, N'ما يَفْتَحِ اللَّهُ لِلنَّاسِ مِنْ رَحْمَةٍ فَلا مُمْسِكَ لَها وَ ما يُمْسِكْ فَلا مُرْسِلَ لَهُ مِنْ بَعْدِهِ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3663, N'يا أَيُّهَا النَّاسُ اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ هَلْ مِنْ خالِقٍ غَيْرُ اللَّهِ يَرْزُقُكُمْ مِنَ السَّماءِ وَ الْأَرْضِ لا إِلهَ إِلاَّ هُوَ فَأَنَّى تُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3664, N'وَ إِنْ يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ وَ إِلَى اللَّهِ تُرْجَعُ الْأُمُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3665, N'يا أَيُّهَا النَّاسُ إِنَّ وَعْدَ اللَّهِ حَقٌّ فَلا تَغُرَّنَّكُمُ الْحَياةُ الدُّنْيا وَ لا يَغُرَّنَّكُمْ بِاللَّهِ الْغَرُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3666, N'إِنَّ الشَّيْطانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُوًّا إِنَّما يَدْعُوا حِزْبَهُ لِيَكُونُوا مِنْ أَصْحابِ السَّعِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3667, N'الَّذِينَ كَفَرُوا لَهُمْ عَذابٌ شَدِيدٌ وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَهُمْ مَغْفِرَةٌ وَ أَجْرٌ كَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3668, N'أَ فَمَنْ زُيِّنَ لَهُ سُوءُ عَمَلِهِ فَرَآهُ حَسَناً فَإِنَّ اللَّهَ يُضِلُّ مَنْ يَشاءُ وَ يَهْدِي مَنْ يَشاءُ فَلا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَراتٍ إِنَّ اللَّهَ عَلِيمٌ بِما يَصْنَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3669, N'وَ اللَّهُ الَّذِي أَرْسَلَ الرِّياحَ فَتُثِيرُ سَحاباً فَسُقْناهُ إِلى بَلَدٍ مَيِّتٍ فَأَحْيَيْنا بِهِ الْأَرْضَ بَعْدَ مَوْتِها كَذلِكَ النُّشُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3670, N'مَنْ كانَ يُرِيدُ الْعِزَّةَ فَلِلَّهِ الْعِزَّةُ جَمِيعاً إِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَ الْعَمَلُ الصَّالِحُ يَرْفَعُهُ وَ الَّذِينَ يَمْكُرُونَ السَّيِّئاتِ لَهُمْ عَذابٌ شَدِيدٌ وَ مَكْرُ أُولئِكَ هُوَ يَبُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3671, N'وَ اللَّهُ خَلَقَكُمْ مِنْ تُرابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ جَعَلَكُمْ أَزْواجاً وَ ما تَحْمِلُ مِنْ أُنْثى وَ لا تَضَعُ إِلاَّ بِعِلْمِهِ وَ ما يُعَمَّرُ مِنْ مُعَمَّرٍ وَ لا يُنْقَصُ مِنْ عُمُرِهِ إِلاَّ فِي كِتابٍ إِنَّ ذلِكَ عَلَى اللَّهِ يَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3672, N'وَ ما يَسْتَوِي الْبَحْرانِ هذا عَذْبٌ فُراتٌ سائِغٌ شَرابُهُ وَ هذا مِلْحٌ أُجاجٌ وَ مِنْ كُلٍّ تَأْكُلُونَ لَحْماً طَرِيًّا وَ تَسْتَخْرِجُونَ حِلْيَةً تَلْبَسُونَها وَ تَرَى الْفُلْكَ فِيهِ مَواخِرَ لِتَبْتَغُوا مِنْ فَضْلِهِ وَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3673, N'يُولِجُ اللَّيْلَ فِي النَّهارِ وَ يُولِجُ النَّهارَ فِي اللَّيْلِ وَ سَخَّرَ الشَّمْسَ وَ الْقَمَرَ كُلٌّ يَجْرِي لِأَجَلٍ مُسَمًّى ذلِكُمُ اللَّهُ رَبُّكُمْ لَهُ الْمُلْكُ وَ الَّذِينَ تَدْعُونَ مِنْ دُونِهِ ما يَمْلِكُونَ مِنْ قِطْمِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3674, N'إِنْ تَدْعُوهُمْ لا يَسْمَعُوا دُعاءَكُمْ وَ لَوْ سَمِعُوا مَا اسْتَجابُوا لَكُمْ وَ يَوْمَ الْقِيامَةِ يَكْفُرُونَ بِشِرْكِكُمْ وَ لا يُنَبِّئُكَ مِثْلُ خَبِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3675, N'يا أَيُّهَا النَّاسُ أَنْتُمُ الْفُقَراءُ إِلَى اللَّهِ وَ اللَّهُ هُوَ الْغَنِيُّ الْحَمِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3676, N'إِنْ يَشَأْ يُذْهِبْكُمْ وَ يَأْتِ بِخَلْقٍ جَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3677, N'وَ ما ذلِكَ عَلَى اللَّهِ بِعَزِيزٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3678, N'وَ لا تَزِرُ وازِرَةٌ وِزْرَ أُخْرى وَ إِنْ تَدْعُ مُثْقَلَةٌ إِلى حِمْلِها لا يُحْمَلْ مِنْهُ شَيْ‏ءٌ وَ لَوْ كانَ ذا قُرْبى إِنَّما تُنْذِرُ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَ أَقامُوا الصَّلاةَ وَ مَنْ تَزَكَّى فَإِنَّما يَتَزَكَّى لِنَفْسِهِ وَ إِلَى اللَّهِ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3679, N'وَ ما يَسْتَوِي الْأَعْمى وَ الْبَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3680, N'وَ لاَ الظُّلُماتُ وَ لاَ النُّورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3681, N'وَ لاَ الظِّلُّ وَ لاَ الْحَرُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3682, N'وَ ما يَسْتَوِي الْأَحْياءُ وَ لاَ الْأَمْواتُ إِنَّ اللَّهَ يُسْمِعُ مَنْ يَشاءُ وَ ما أَنْتَ بِمُسْمِعٍ مَنْ فِي الْقُبُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3683, N'إِنْ أَنْتَ إِلاَّ نَذِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3684, N'إِنَّا أَرْسَلْناكَ بِالْحَقِّ بَشِيراً وَ نَذِيراً وَ إِنْ مِنْ أُمَّةٍ إِلاَّ خَلا فِيها نَذِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3685, N'وَ إِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ جاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّناتِ وَ بِالزُّبُرِ وَ بِالْكِتابِ الْمُنِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3686, N'ثُمَّ أَخَذْتُ الَّذِينَ كَفَرُوا فَكَيْفَ كانَ نَكِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3687, N'أَ لَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّماءِ ماءً فَأَخْرَجْنا بِهِ ثَمَراتٍ مُخْتَلِفاً أَلْوانُها وَ مِنَ الْجِبالِ جُدَدٌ بِيضٌ وَ حُمْرٌ مُخْتَلِفٌ أَلْوانُها وَ غَرابِيبُ سُودٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3688, N'وَ مِنَ النَّاسِ وَ الدَّوَابِّ وَ الْأَنْعامِ مُخْتَلِفٌ أَلْوانُهُ كَذلِكَ إِنَّما يَخْشَى اللَّهَ مِنْ عِبادِهِ الْعُلَماءُ إِنَّ اللَّهَ عَزِيزٌ غَفُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3689, N'إِنَّ الَّذِينَ يَتْلُونَ كِتابَ اللَّهِ وَ أَقامُوا الصَّلاةَ وَ أَنْفَقُوا مِمَّا رَزَقْناهُمْ سِرًّا وَ عَلانِيَةً يَرْجُونَ تِجارَةً لَنْ تَبُورَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3690, N'لِيُوَفِّيَهُمْ أُجُورَهُمْ وَ يَزِيدَهُمْ مِنْ فَضْلِهِ إِنَّهُ غَفُورٌ شَكُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3691, N'وَ الَّذِي أَوْحَيْنا إِلَيْكَ مِنَ الْكِتابِ هُوَ الْحَقُّ مُصَدِّقاً لِما بَيْنَ يَدَيْهِ إِنَّ اللَّهَ بِعِبادِهِ لَخَبِيرٌ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3692, N'ثُمَّ أَوْرَثْنَا الْكِتابَ الَّذِينَ اصْطَفَيْنا مِنْ عِبادِنا فَمِنْهُمْ ظالِمٌ لِنَفْسِهِ وَ مِنْهُمْ مُقْتَصِدٌ وَ مِنْهُمْ سابِقٌ بِالْخَيْراتِ بِإِذْنِ اللَّهِ ذلِكَ هُوَ الْفَضْلُ الْكَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3693, N'جَنَّاتُ عَدْنٍ يَدْخُلُونَها يُحَلَّوْنَ فِيها مِنْ أَساوِرَ مِنْ ذَهَبٍ وَ لُؤْلُؤاً وَ لِباسُهُمْ فِيها حَرِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3694, N'وَ قالُوا الْحَمْدُ لِلَّهِ الَّذِي أَذْهَبَ عَنَّا الْحَزَنَ إِنَّ رَبَّنا لَغَفُورٌ شَكُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3695, N'الَّذِي أَحَلَّنا دارَ الْمُقامَةِ مِنْ فَضْلِهِ لا يَمَسُّنا فِيها نَصَبٌ وَ لا يَمَسُّنا فِيها لُغُوبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3696, N'وَ الَّذِينَ كَفَرُوا لَهُمْ نارُ جَهَنَّمَ لا يُقْضى عَلَيْهِمْ فَيَمُوتُوا وَ لا يُخَفَّفُ عَنْهُمْ مِنْ عَذابِها كَذلِكَ نَجْزِي كُلَّ كَفُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3697, N'وَ هُمْ يَصْطَرِخُونَ فِيها رَبَّنا أَخْرِجْنا نَعْمَلْ صالِحاً غَيْرَ الَّذِي كُنَّا نَعْمَلُ أَ وَ لَمْ نُعَمِّرْكُمْ ما يَتَذَكَّرُ فِيهِ مَنْ تَذَكَّرَ وَ جاءَكُمُ النَّذِيرُ فَذُوقُوا فَما لِلظَّالِمِينَ مِنْ نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3698, N'إِنَّ اللَّهَ عالِمُ غَيْبِ السَّماواتِ وَ الْأَرْضِ إِنَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3699, N'هُوَ الَّذِي جَعَلَكُمْ خَلائِفَ فِي الْأَرْضِ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهُ وَ لا يَزِيدُ الْكافِرِينَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ إِلاَّ مَقْتاً وَ لا يَزِيدُ الْكافِرِينَ كُفْرُهُمْ إِلاَّ خَساراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3700, N'قُلْ أَ رَأَيْتُمْ شُرَكاءَكُمُ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ أَرُونِي ما ذا خَلَقُوا مِنَ الْأَرْضِ أَمْ لَهُمْ شِرْكٌ فِي السَّماواتِ أَمْ آتَيْناهُمْ كِتاباً فَهُمْ عَلى بَيِّنَةٍ مِنْهُ بَلْ إِنْ يَعِدُ الظَّالِمُونَ بَعْضُهُمْ بَعْضاً إِلاَّ غُرُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3701, N'إِنَّ اللَّهَ يُمْسِكُ السَّماواتِ وَ الْأَرْضَ أَنْ تَزُولا وَ لَئِنْ زالَتا إِنْ أَمْسَكَهُما مِنْ أَحَدٍ مِنْ بَعْدِهِ إِنَّهُ كانَ حَلِيماً غَفُوراً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3702, N'وَ أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمانِهِمْ لَئِنْ جاءَهُمْ نَذِيرٌ لَيَكُونُنَّ أَهْدى مِنْ إِحْدَى الْأُمَمِ فَلَمَّا جاءَهُمْ نَذِيرٌ ما زادَهُمْ إِلاَّ نُفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3703, N'اسْتِكْباراً فِي الْأَرْضِ وَ مَكْرَ السَّيِّئِ وَ لا يَحِيقُ الْمَكْرُ السَّيِّئُ إِلاَّ بِأَهْلِهِ فَهَلْ يَنْظُرُونَ إِلاَّ سُنَّتَ الْأَوَّلِينَ فَلَنْ تَجِدَ لِسُنَّتِ اللَّهِ تَبْدِيلاً وَ لَنْ تَجِدَ لِسُنَّتِ اللَّهِ تَحْوِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3704, N'أَ وَ لَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كانَ عاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ وَ كانُوا أَشَدَّ مِنْهُمْ قُوَّةً وَ ما كانَ اللَّهُ لِيُعْجِزَهُ مِنْ شَيْ‏ءٍ فِي السَّماواتِ وَ لا فِي الْأَرْضِ إِنَّهُ كانَ عَلِيماً قَدِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3705, N'وَ لَوْ يُؤاخِذُ اللَّهُ النَّاسَ بِما كَسَبُوا ما تَرَكَ عَلى ظَهْرِها مِنْ دَابَّةٍ وَ لكِنْ يُؤَخِّرُهُمْ إِلى أَجَلٍ مُسَمًّى فَإِذا جاءَ أَجَلُهُمْ فَإِنَّ اللَّهَ كانَ بِعِبادِهِ بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3706, N'يس ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3707, N'وَ الْقُرْآنِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3708, N'إِنَّكَ لَمِنَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3709, N'عَلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3710, N'تَنْزِيلَ الْعَزِيزِ الرَّحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3711, N'لِتُنْذِرَ قَوْماً ما أُنْذِرَ آباؤُهُمْ فَهُمْ غافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3712, N'لَقَدْ حَقَّ الْقَوْلُ عَلى أَكْثَرِهِمْ فَهُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3713, N'إِنَّا جَعَلْنا فِي أَعْناقِهِمْ أَغْلالاً فَهِيَ إِلَى الْأَذْقانِ فَهُمْ مُقْمَحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3714, N'وَ جَعَلْنا مِنْ بَيْنِ أَيْدِيهِمْ سَدًّا وَ مِنْ خَلْفِهِمْ سَدًّا فَأَغْشَيْناهُمْ فَهُمْ لا يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3715, N'وَ سَواءٌ عَلَيْهِمْ أَ أَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3716, N'إِنَّما تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَ خَشِيَ الرَّحْمنَ بِالْغَيْبِ فَبَشِّرْهُ بِمَغْفِرَةٍ وَ أَجْرٍ كَرِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3717, N'إِنَّا نَحْنُ نُحْيِ الْمَوْتى وَ نَكْتُبُ ما قَدَّمُوا وَ آثارَهُمْ وَ كُلَّ شَيْ‏ءٍ أَحْصَيْناهُ فِي إِمامٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3718, N'وَ اضْرِبْ لَهُمْ مَثَلاً أَصْحابَ الْقَرْيَةِ إِذْ جاءَهَا الْمُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3719, N'إِذْ أَرْسَلْنا إِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوهُما فَعَزَّزْنا بِثالِثٍ فَقالُوا إِنَّا إِلَيْكُمْ مُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3720, N'قالُوا ما أَنْتُمْ إِلاَّ بَشَرٌ مِثْلُنا وَ ما أَنْزَلَ الرَّحْمنُ مِنْ شَيْ‏ءٍ إِنْ أَنْتُمْ إِلاَّ تَكْذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3721, N'قالُوا رَبُّنا يَعْلَمُ إِنَّا إِلَيْكُمْ لَمُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3722, N'وَ ما عَلَيْنا إِلاَّ الْبَلاغُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3723, N'قالُوا إِنَّا تَطَيَّرْنا بِكُمْ لَئِنْ لَمْ تَنْتَهُوا لَنَرْجُمَنَّكُمْ وَ لَيَمَسَّنَّكُمْ مِنَّا عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3724, N'قالُوا طائِرُكُمْ مَعَكُمْ أَ إِنْ ذُكِّرْتُمْ بَلْ أَنْتُمْ قَوْمٌ مُسْرِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3725, N'وَ جاءَ مِنْ أَقْصَا الْمَدِينَةِ رَجُلٌ يَسْعى قالَ يا قَوْمِ اتَّبِعُوا الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3726, N'اتَّبِعُوا مَنْ لا يَسْئَلُكُمْ أَجْراً وَ هُمْ مُهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3727, N'وَ ما لِيَ لا أَعْبُدُ الَّذِي فَطَرَنِي وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3728, N'أَ أَتَّخِذُ مِنْ دُونِهِ آلِهَةً إِنْ يُرِدْنِ الرَّحْمنُ بِضُرٍّ لا تُغْنِ عَنِّي شَفاعَتُهُمْ شَيْئاً وَ لا يُنْقِذُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3729, N'إِنِّي إِذاً لَفِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3730, N'إِنِّي آمَنْتُ بِرَبِّكُمْ فَاسْمَعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3731, N'قِيلَ ادْخُلِ الْجَنَّةَ قالَ يا لَيْتَ قَوْمِي يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3732, N'بِما غَفَرَ لِي رَبِّي وَ جَعَلَنِي مِنَ الْمُكْرَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3733, N'وَ ما أَنْزَلْنا عَلى قَوْمِهِ مِنْ بَعْدِهِ مِنْ جُنْدٍ مِنَ السَّماءِ وَ ما كُنَّا مُنْزِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3734, N'إِنْ كانَتْ إِلاَّ صَيْحَةً واحِدَةً فَإِذا هُمْ خامِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3735, N'يا حَسْرَةً عَلَى الْعِبادِ ما يَأْتِيهِمْ مِنْ رَسُولٍ إِلاَّ كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3736, N'أَ لَمْ يَرَوْا كَمْ أَهْلَكْنا قَبْلَهُمْ مِنَ الْقُرُونِ أَنَّهُمْ إِلَيْهِمْ لا يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3737, N'وَ إِنْ كُلٌّ لَمَّا جَمِيعٌ لَدَيْنا مُحْضَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3738, N'وَ آيَةٌ لَهُمُ الْأَرْضُ الْمَيْتَةُ أَحْيَيْناها وَ أَخْرَجْنا مِنْها حَبًّا فَمِنْهُ يَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3739, N'وَ جَعَلْنا فِيها جَنَّاتٍ مِنْ نَخِيلٍ وَ أَعْنابٍ وَ فَجَّرْنا فِيها مِنَ الْعُيُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3740, N'لِيَأْكُلُوا مِنْ ثَمَرِهِ وَ ما عَمِلَتْهُ أَيْدِيهِمْ أَ فَلا يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3741, N'سُبْحانَ الَّذِي خَلَقَ الْأَزْواجَ كُلَّها مِمَّا تُنْبِتُ الْأَرْضُ وَ مِنْ أَنْفُسِهِمْ وَ مِمَّا لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3742, N'وَ آيَةٌ لَهُمُ اللَّيْلُ نَسْلَخُ مِنْهُ النَّهارَ فَإِذا هُمْ مُظْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3743, N'وَ الشَّمْسُ تَجْرِي لِمُسْتَقَرٍّ لَها ذلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3744, N'وَ الْقَمَرَ قَدَّرْناهُ مَنازِلَ حَتَّى عادَ كَالْعُرْجُونِ الْقَدِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3745, N'لاَ الشَّمْسُ يَنْبَغِي لَها أَنْ تُدْرِكَ الْقَمَرَ وَ لاَ اللَّيْلُ سابِقُ النَّهارِ وَ كُلٌّ فِي فَلَكٍ يَسْبَحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3746, N'وَ آيَةٌ لَهُمْ أَنَّا حَمَلْنا ذُرِّيَّتَهُمْ فِي الْفُلْكِ الْمَشْحُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3747, N'وَ خَلَقْنا لَهُمْ مِنْ مِثْلِهِ ما يَرْكَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3748, N'وَ إِنْ نَشَأْ نُغْرِقْهُمْ فَلا صَرِيخَ لَهُمْ وَ لا هُمْ يُنْقَذُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3749, N'إِلاَّ رَحْمَةً مِنَّا وَ مَتاعاً إِلى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3750, N'وَ إِذا قِيلَ لَهُمُ اتَّقُوا ما بَيْنَ أَيْدِيكُمْ وَ ما خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3751, N'وَ ما تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آياتِ رَبِّهِمْ إِلاَّ كانُوا عَنْها مُعْرِضِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3752, N'وَ إِذا قِيلَ لَهُمْ أَنْفِقُوا مِمَّا رَزَقَكُمُ اللَّهُ قالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَ نُطْعِمُ مَنْ لَوْ يَشاءُ اللَّهُ أَطْعَمَهُ إِنْ أَنْتُمْ إِلاَّ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3753, N'وَ يَقُولُونَ مَتى هذَا الْوَعْدُ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3754, N'ما يَنْظُرُونَ إِلاَّ صَيْحَةً واحِدَةً تَأْخُذُهُمْ وَ هُمْ يَخِصِّمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3755, N'فَلا يَسْتَطِيعُونَ تَوْصِيَةً وَ لا إِلى أَهْلِهِمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3756, N'وَ نُفِخَ فِي الصُّورِ فَإِذا هُمْ مِنَ الْأَجْداثِ إِلى رَبِّهِمْ يَنْسِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3757, N'قالُوا يا وَيْلَنا مَنْ بَعَثَنا مِنْ مَرْقَدِنا هذا ما وَعَدَ الرَّحْمنُ وَ صَدَقَ الْمُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3758, N'إِنْ كانَتْ إِلاَّ صَيْحَةً واحِدَةً فَإِذا هُمْ جَمِيعٌ لَدَيْنا مُحْضَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3759, N'فَالْيَوْمَ لا تُظْلَمُ نَفْسٌ شَيْئاً وَ لا تُجْزَوْنَ إِلاَّ ما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3760, N'إِنَّ أَصْحابَ الْجَنَّةِ الْيَوْمَ فِي شُغُلٍ فاكِهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3761, N'هُمْ وَ أَزْواجُهُمْ فِي ظِلالٍ عَلَى الْأَرائِكِ مُتَّكِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3762, N'لَهُمْ فِيها فاكِهَةٌ وَ لَهُمْ ما يَدَّعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3763, N'سَلامٌ قَوْلاً مِنْ رَبٍّ رَحِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3764, N'وَ امْتازُوا الْيَوْمَ أَيُّهَا الْمُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3765, N'أَ لَمْ أَعْهَدْ إِلَيْكُمْ يا بَنِي آدَمَ أَنْ لا تَعْبُدُوا الشَّيْطانَ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3766, N'وَ أَنِ اعْبُدُونِي هذا صِراطٌ مُسْتَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3767, N'وَ لَقَدْ أَضَلَّ مِنْكُمْ جِبِلاًّ كَثِيراً أَ فَلَمْ تَكُونُوا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3768, N'هذِهِ جَهَنَّمُ الَّتِي كُنْتُمْ تُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3769, N'اصْلَوْهَا الْيَوْمَ بِما كُنْتُمْ تَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3770, N'الْيَوْمَ نَخْتِمُ عَلى أَفْواهِهِمْ وَ تُكَلِّمُنا أَيْدِيهِمْ وَ تَشْهَدُ أَرْجُلُهُمْ بِما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3771, N'وَ لَوْ نَشاءُ لَطَمَسْنا عَلى أَعْيُنِهِمْ فَاسْتَبَقُوا الصِّراطَ فَأَنَّى يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3772, N'وَ لَوْ نَشاءُ لَمَسَخْناهُمْ عَلى مَكانَتِهِمْ فَمَا اسْتَطاعُوا مُضِيًّا وَ لا يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3773, N'وَ مَنْ نُعَمِّرْهُ نُنَكِّسْهُ فِي الْخَلْقِ أَ فَلا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3774, N'وَ ما عَلَّمْناهُ الشِّعْرَ وَ ما يَنْبَغِي لَهُ إِنْ هُوَ إِلاَّ ذِكْرٌ وَ قُرْآنٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3775, N'لِيُنْذِرَ مَنْ كانَ حَيًّا وَ يَحِقَّ الْقَوْلُ عَلَى الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3776, N'أَ وَ لَمْ يَرَوْا أَنَّا خَلَقْنا لَهُمْ مِمَّا عَمِلَتْ أَيْدِينا أَنْعاماً فَهُمْ لَها مالِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3777, N'وَ ذَلَّلْناها لَهُمْ فَمِنْها رَكُوبُهُمْ وَ مِنْها يَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3778, N'وَ لَهُمْ فِيها مَنافِعُ وَ مَشارِبُ أَ فَلا يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3779, N'وَ اتَّخَذُوا مِنْ دُونِ اللَّهِ آلِهَةً لَعَلَّهُمْ يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3780, N'لا يَسْتَطِيعُونَ نَصْرَهُمْ وَ هُمْ لَهُمْ جُنْدٌ مُحْضَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3781, N'فَلا يَحْزُنْكَ قَوْلُهُمْ إِنَّا نَعْلَمُ ما يُسِرُّونَ وَ ما يُعْلِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3782, N'أَ وَ لَمْ يَرَ الْإِنْسانُ أَنَّا خَلَقْناهُ مِنْ نُطْفَةٍ فَإِذا هُوَ خَصِيمٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3783, N'وَ ضَرَبَ لَنا مَثَلاً وَ نَسِيَ خَلْقَهُ قالَ مَنْ يُحْيِ الْعِظامَ وَ هِيَ رَمِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3784, N'قُلْ يُحْيِيهَا الَّذِي أَنْشَأَها أَوَّلَ مَرَّةٍ وَ هُوَ بِكُلِّ خَلْقٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3785, N'الَّذِي جَعَلَ لَكُمْ مِنَ الشَّجَرِ الْأَخْضَرِ ناراً فَإِذا أَنْتُمْ مِنْهُ تُوقِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3786, N'أَ وَ لَيْسَ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ بِقادِرٍ عَلى أَنْ يَخْلُقَ مِثْلَهُمْ بَلى وَ هُوَ الْخَلاَّقُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3787, N'إِنَّما أَمْرُهُ إِذا أَرادَ شَيْئاً أَنْ يَقُولَ لَهُ كُنْ فَيَكُونُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3788, N'فَسُبْحانَ الَّذِي بِيَدِهِ مَلَكُوتُ كُلِّ شَيْ‏ءٍ وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3789, N'وَ الصَّافَّاتِ صَفًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3790, N'فَالزَّاجِراتِ زَجْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3791, N'فَالتَّالِياتِ ذِكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3792, N'إِنَّ إِلهَكُمْ لَواحِدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3793, N'رَبُّ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُما وَ رَبُّ الْمَشارِقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3794, N'إِنَّا زَيَّنَّا السَّماءَ الدُّنْيا بِزِينَةٍ الْكَواكِبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3795, N'وَ حِفْظاً مِنْ كُلِّ شَيْطانٍ مارِدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3796, N'لا يَسَّمَّعُونَ إِلَى الْمَلَإِ الْأَعْلى وَ يُقْذَفُونَ مِنْ كُلِّ جانِبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3797, N'دُحُوراً وَ لَهُمْ عَذابٌ واصِبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3798, N'إِلاَّ مَنْ خَطِفَ الْخَطْفَةَ فَأَتْبَعَهُ شِهابٌ ثاقِبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3799, N'فَاسْتَفْتِهِمْ أَ هُمْ أَشَدُّ خَلْقاً أَمْ مَنْ خَلَقْنا إِنَّا خَلَقْناهُمْ مِنْ طِينٍ لازِبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3800, N'بَلْ عَجِبْتَ وَ يَسْخَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3801, N'وَ إِذا ذُكِّرُوا لا يَذْكُرُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3802, N'وَ إِذا رَأَوْا آيَةً يَسْتَسْخِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3803, N'وَ قالُوا إِنْ هذا إِلاَّ سِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3804, N'أَ إِذا مِتْنا وَ كُنَّا تُراباً وَ عِظاماً أَ إِنَّا لَمَبْعُوثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3805, N'أَ وَ آباؤُنَا الْأَوَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3806, N'قُلْ نَعَمْ وَ أَنْتُمْ داخِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3807, N'فَإِنَّما هِيَ زَجْرَةٌ واحِدَةٌ فَإِذا هُمْ يَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3808, N'وَ قالُوا يا وَيْلَنا هذا يَوْمُ الدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3809, N'هذا يَوْمُ الْفَصْلِ الَّذِي كُنْتُمْ بِهِ تُكَذِّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3810, N'احْشُرُوا الَّذِينَ ظَلَمُوا وَ أَزْواجَهُمْ وَ ما كانُوا يَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3811, N'مِنْ دُونِ اللَّهِ فَاهْدُوهُمْ إِلى صِراطِ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3812, N'وَ قِفُوهُمْ إِنَّهُمْ مَسْؤُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3813, N'ما لَكُمْ لا تَناصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3814, N'بَلْ هُمُ الْيَوْمَ مُسْتَسْلِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3815, N'وَ أَقْبَلَ بَعْضُهُمْ عَلى بَعْضٍ يَتَساءَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3816, N'قالُوا إِنَّكُمْ كُنْتُمْ تَأْتُونَنا عَنِ الْيَمِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3817, N'قالُوا بَلْ لَمْ تَكُونُوا مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3818, N'وَ ما كانَ لَنا عَلَيْكُمْ مِنْ سُلْطانٍ بَلْ كُنْتُمْ قَوْماً طاغِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3819, N'فَحَقَّ عَلَيْنا قَوْلُ رَبِّنا إِنَّا لَذائِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3820, N'فَأَغْوَيْناكُمْ إِنَّا كُنَّا غاوِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3821, N'فَإِنَّهُمْ يَوْمَئِذٍ فِي الْعَذابِ مُشْتَرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3822, N'إِنَّا كَذلِكَ نَفْعَلُ بِالْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3823, N'إِنَّهُمْ كانُوا إِذا قِيلَ لَهُمْ لا إِلهَ إِلاَّ اللَّهُ يَسْتَكْبِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3824, N'وَ يَقُولُونَ أَ إِنَّا لَتارِكُوا آلِهَتِنا لِشاعِرٍ مَجْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3825, N'بَلْ جاءَ بِالْحَقِّ وَ صَدَّقَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3826, N'إِنَّكُمْ لَذائِقُوا الْعَذابِ الْأَلِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3827, N'وَ ما تُجْزَوْنَ إِلاَّ ما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3828, N'إِلاَّ عِبادَ اللَّهِ الْمُخْلَصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3829, N'أُولئِكَ لَهُمْ رِزْقٌ مَعْلُومٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3830, N'فَواكِهُ وَ هُمْ مُكْرَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3831, N'فِي جَنَّاتِ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3832, N'عَلى سُرُرٍ مُتَقابِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3833, N'يُطافُ عَلَيْهِمْ بِكَأْسٍ مِنْ مَعِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3834, N'بَيْضاءَ لَذَّةٍ لِلشَّارِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3835, N'لا فِيها غَوْلٌ وَ لا هُمْ عَنْها يُنْزَفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3836, N'وَ عِنْدَهُمْ قاصِراتُ الطَّرْفِ عِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3837, N'كَأَنَّهُنَّ بَيْضٌ مَكْنُونٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3838, N'فَأَقْبَلَ بَعْضُهُمْ عَلى بَعْضٍ يَتَساءَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3839, N'قالَ قائِلٌ مِنْهُمْ إِنِّي كانَ لِي قَرِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3840, N'يَقُولُ أَ إِنَّكَ لَمِنَ الْمُصَدِّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3841, N'أَ إِذا مِتْنا وَ كُنَّا تُراباً وَ عِظاماً أَ إِنَّا لَمَدِينُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3842, N'قالَ هَلْ أَنْتُمْ مُطَّلِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3843, N'فَاطَّلَعَ فَرَآهُ فِي سَواءِ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3844, N'قالَ تَاللَّهِ إِنْ كِدْتَ لَتُرْدِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3845, N'وَ لَوْ لا نِعْمَةُ رَبِّي لَكُنْتُ مِنَ الْمُحْضَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3846, N'أَ فَما نَحْنُ بِمَيِّتِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3847, N'إِلاَّ مَوْتَتَنَا الْأُولى وَ ما نَحْنُ بِمُعَذَّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3848, N'إِنَّ هذا لَهُوَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3849, N'لِمِثْلِ هذا فَلْيَعْمَلِ الْعامِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3850, N'أَ ذلِكَ خَيْرٌ نُزُلاً أَمْ شَجَرَةُ الزَّقُّومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3851, N'إِنَّا جَعَلْناها فِتْنَةً لِلظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3852, N'إِنَّها شَجَرَةٌ تَخْرُجُ فِي أَصْلِ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3853, N'طَلْعُها كَأَنَّهُ رُؤُسُ الشَّياطِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3854, N'فَإِنَّهُمْ لَآكِلُونَ مِنْها فَمالِؤُنَ مِنْهَا الْبُطُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3855, N'ثُمَّ إِنَّ لَهُمْ عَلَيْها لَشَوْباً مِنْ حَمِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3856, N'ثُمَّ إِنَّ مَرْجِعَهُمْ لَإِلَى الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3857, N'إِنَّهُمْ أَلْفَوْا آباءَهُمْ ضالِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3858, N'فَهُمْ عَلى آثارِهِمْ يُهْرَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3859, N'وَ لَقَدْ ضَلَّ قَبْلَهُمْ أَكْثَرُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3860, N'وَ لَقَدْ أَرْسَلْنا فِيهِمْ مُنْذِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3861, N'فَانْظُرْ كَيْفَ كانَ عاقِبَةُ الْمُنْذَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3862, N'إِلاَّ عِبادَ اللَّهِ الْمُخْلَصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3863, N'وَ لَقَدْ نادانا نُوحٌ فَلَنِعْمَ الْمُجِيبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3864, N'وَ نَجَّيْناهُ وَ أَهْلَهُ مِنَ الْكَرْبِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3865, N'وَ جَعَلْنا ذُرِّيَّتَهُ هُمُ الْباقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3866, N'وَ تَرَكْنا عَلَيْهِ فِي الْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3867, N'سَلامٌ عَلى نُوحٍ فِي الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3868, N'إِنَّا كَذلِكَ نَجْزِي الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3869, N'إِنَّهُ مِنْ عِبادِنَا الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3870, N'ثُمَّ أَغْرَقْنَا الْآخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3871, N'وَ إِنَّ مِنْ شِيعَتِهِ لَإِبْراهِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3872, N'إِذْ جاءَ رَبَّهُ بِقَلْبٍ سَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3873, N'إِذْ قالَ لِأَبِيهِ وَ قَوْمِهِ ما ذا تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3874, N'أَ إِفْكاً آلِهَةً دُونَ اللَّهِ تُرِيدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3875, N'فَما ظَنُّكُمْ بِرَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3876, N'فَنَظَرَ نَظْرَةً فِي النُّجُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3877, N'فَقالَ إِنِّي سَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3878, N'فَتَوَلَّوْا عَنْهُ مُدْبِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3879, N'فَراغَ إِلى آلِهَتِهِمْ فَقالَ أَ لا تَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3880, N'ما لَكُمْ لا تَنْطِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3881, N'فَراغَ عَلَيْهِمْ ضَرْباً بِالْيَمِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3882, N'فَأَقْبَلُوا إِلَيْهِ يَزِفُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3883, N'قالَ أَ تَعْبُدُونَ ما تَنْحِتُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3884, N'وَ اللَّهُ خَلَقَكُمْ وَ ما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3885, N'قالُوا ابْنُوا لَهُ بُنْياناً فَأَلْقُوهُ فِي الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3886, N'فَأَرادُوا بِهِ كَيْداً فَجَعَلْناهُمُ الْأَسْفَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3887, N'وَ قالَ إِنِّي ذاهِبٌ إِلى رَبِّي سَيَهْدِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3888, N'رَبِّ هَبْ لِي مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3889, N'فَبَشَّرْناهُ بِغُلامٍ حَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3890, N'فَلَمَّا بَلَغَ مَعَهُ السَّعْيَ قالَ يا بُنَيَّ إِنِّي أَرى فِي الْمَنامِ أَنِّي أَذْبَحُكَ فَانْظُرْ ما ذا تَرى قالَ يا أَبَتِ افْعَلْ ما تُؤْمَرُ سَتَجِدُنِي إِنْ شاءَ اللَّهُ مِنَ الصَّابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3891, N'فَلَمَّا أَسْلَما وَ تَلَّهُ لِلْجَبِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3892, N'وَ نادَيْناهُ أَنْ يا إِبْراهِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3893, N'قَدْ صَدَّقْتَ الرُّؤْيا إِنَّا كَذلِكَ نَجْزِي الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3894, N'إِنَّ هذا لَهُوَ الْبَلاءُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3895, N'وَ فَدَيْناهُ بِذِبْحٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3896, N'وَ تَرَكْنا عَلَيْهِ فِي الْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3897, N'سَلامٌ عَلى إِبْراهِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3898, N'كَذلِكَ نَجْزِي الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3899, N'إِنَّهُ مِنْ عِبادِنَا الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3900, N'وَ بَشَّرْناهُ بِإِسْحاقَ نَبِيًّا مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3901, N'وَ بارَكْنا عَلَيْهِ وَ عَلى إِسْحاقَ وَ مِنْ ذُرِّيَّتِهِما مُحْسِنٌ وَ ظالِمٌ لِنَفْسِهِ مُبِينٌ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3902, N'وَ لَقَدْ مَنَنَّا عَلى مُوسى وَ هارُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3903, N'وَ نَجَّيْناهُما وَ قَوْمَهُما مِنَ الْكَرْبِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3904, N'وَ نَصَرْناهُمْ فَكانُوا هُمُ الْغالِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3905, N'وَ آتَيْناهُمَا الْكِتابَ الْمُسْتَبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3906, N'وَ هَدَيْناهُمَا الصِّراطَ الْمُسْتَقِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3907, N'وَ تَرَكْنا عَلَيْهِما فِي الْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3908, N'سَلامٌ عَلى مُوسى وَ هارُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3909, N'إِنَّا كَذلِكَ نَجْزِي الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3910, N'إِنَّهُما مِنْ عِبادِنَا الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3911, N'وَ إِنَّ إِلْياسَ لَمِنَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3912, N'إِذْ قالَ لِقَوْمِهِ أَ لا تَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3913, N'أَ تَدْعُونَ بَعْلاً وَ تَذَرُونَ أَحْسَنَ الْخالِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3914, N'اللَّهَ رَبَّكُمْ وَ رَبَّ آبائِكُمُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3915, N'فَكَذَّبُوهُ فَإِنَّهُمْ لَمُحْضَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3916, N'إِلاَّ عِبادَ اللَّهِ الْمُخْلَصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3917, N'وَ تَرَكْنا عَلَيْهِ فِي الْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3918, N'سَلامٌ عَلى إِلْ‏ياسِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3919, N'إِنَّا كَذلِكَ نَجْزِي الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3920, N'إِنَّهُ مِنْ عِبادِنَا الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3921, N'وَ إِنَّ لُوطاً لَمِنَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3922, N'إِذْ نَجَّيْناهُ وَ أَهْلَهُ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3923, N'إِلاَّ عَجُوزاً فِي الْغابِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3924, N'ثُمَّ دَمَّرْنَا الْآخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3925, N'وَ إِنَّكُمْ لَتَمُرُّونَ عَلَيْهِمْ مُصْبِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3926, N'وَ بِاللَّيْلِ أَ فَلا تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3927, N'وَ إِنَّ يُونُسَ لَمِنَ الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3928, N'إِذْ أَبَقَ إِلَى الْفُلْكِ الْمَشْحُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3929, N'فَساهَمَ فَكانَ مِنَ الْمُدْحَضِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3930, N'فَالْتَقَمَهُ الْحُوتُ وَ هُوَ مُلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3931, N'فَلَوْ لا أَنَّهُ كانَ مِنَ الْمُسَبِّحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3932, N'لَلَبِثَ فِي بَطْنِهِ إِلى يَوْمِ يُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3933, N'فَنَبَذْناهُ بِالْعَراءِ وَ هُوَ سَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3934, N'وَ أَنْبَتْنا عَلَيْهِ شَجَرَةً مِنْ يَقْطِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3935, N'وَ أَرْسَلْناهُ إِلى مِائَةِ أَلْفٍ أَوْ يَزِيدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3936, N'فَآمَنُوا فَمَتَّعْناهُمْ إِلى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3937, N'فَاسْتَفْتِهِمْ أَ لِرَبِّكَ الْبَناتُ وَ لَهُمُ الْبَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3938, N'أَمْ خَلَقْنَا الْمَلائِكَةَ إِناثاً وَ هُمْ شاهِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3939, N'أَلا إِنَّهُمْ مِنْ إِفْكِهِمْ لَيَقُولُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3940, N'وَلَدَ اللَّهُ وَ إِنَّهُمْ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3941, N'أَصْطَفَى الْبَناتِ عَلَى الْبَنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3942, N'ما لَكُمْ كَيْفَ تَحْكُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3943, N'أَ فَلا تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3944, N'أَمْ لَكُمْ سُلْطانٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3945, N'فَأْتُوا بِكِتابِكُمْ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3946, N'وَ جَعَلُوا بَيْنَهُ وَ بَيْنَ الْجِنَّةِ نَسَباً وَ لَقَدْ عَلِمَتِ الْجِنَّةُ إِنَّهُمْ لَمُحْضَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3947, N'سُبْحانَ اللَّهِ عَمَّا يَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3948, N'إِلاَّ عِبادَ اللَّهِ الْمُخْلَصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3949, N'فَإِنَّكُمْ وَ ما تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3950, N'ما أَنْتُمْ عَلَيْهِ بِفاتِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3951, N'إِلاَّ مَنْ هُوَ صالِ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3952, N'وَ ما مِنَّا إِلاَّ لَهُ مَقامٌ مَعْلُومٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3953, N'وَ إِنَّا لَنَحْنُ الصَّافُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3954, N'وَ إِنَّا لَنَحْنُ الْمُسَبِّحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3955, N'وَ إِنْ كانُوا لَيَقُولُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3956, N'لَوْ أَنَّ عِنْدَنا ذِكْراً مِنَ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3957, N'لَكُنَّا عِبادَ اللَّهِ الْمُخْلَصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3958, N'فَكَفَرُوا بِهِ فَسَوْفَ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3959, N'وَ لَقَدْ سَبَقَتْ كَلِمَتُنا لِعِبادِنَا الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3960, N'إِنَّهُمْ لَهُمُ الْمَنْصُورُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3961, N'وَ إِنَّ جُنْدَنا لَهُمُ الْغالِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3962, N'فَتَوَلَّ عَنْهُمْ حَتَّى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3963, N'وَ أَبْصِرْهُمْ فَسَوْفَ يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3964, N'أَ فَبِعَذابِنا يَسْتَعْجِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3965, N'فَإِذا نَزَلَ بِساحَتِهِمْ فَساءَ صَباحُ الْمُنْذَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3966, N'وَ تَوَلَّ عَنْهُمْ حَتَّى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3967, N'وَ أَبْصِرْ فَسَوْفَ يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3968, N'سُبْحانَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3969, N'وَ سَلامٌ عَلَى الْمُرْسَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3970, N'وَ الْحَمْدُ لِلَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3971, N'ص وَ الْقُرْآنِ ذِي الذِّكْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3972, N'بَلِ الَّذِينَ كَفَرُوا فِي عِزَّةٍ وَ شِقاقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3973, N'كَمْ أَهْلَكْنا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ فَنادَوْا وَ لاتَ حِينَ مَناصٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3974, N'وَ عَجِبُوا أَنْ جاءَهُمْ مُنْذِرٌ مِنْهُمْ وَ قالَ الْكافِرُونَ هذا ساحِرٌ كَذَّابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3975, N'أَ جَعَلَ الْآلِهَةَ إِلهاً واحِداً إِنَّ هذا لَشَيْ‏ءٌ عُجابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3976, N'وَ انْطَلَقَ الْمَلَأُ مِنْهُمْ أَنِ امْشُوا وَ اصْبِرُوا عَلى آلِهَتِكُمْ إِنَّ هذا لَشَيْ‏ءٌ يُرادُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3977, N'ما سَمِعْنا بِهذا فِي الْمِلَّةِ الْآخِرَةِ إِنْ هذا إِلاَّ اخْتِلاقٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3978, N'أَ أُنْزِلَ عَلَيْهِ الذِّكْرُ مِنْ بَيْنِنا بَلْ هُمْ فِي شَكٍّ مِنْ ذِكْرِي بَلْ لَمَّا يَذُوقُوا عَذابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3979, N'أَمْ عِنْدَهُمْ خَزائِنُ رَحْمَةِ رَبِّكَ الْعَزِيزِ الْوَهَّابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3980, N'أَمْ لَهُمْ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُما فَلْيَرْتَقُوا فِي الْأَسْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3981, N'جُنْدٌ ما هُنالِكَ مَهْزُومٌ مِنَ الْأَحْزابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3982, N'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَ عادٌ وَ فِرْعَوْنُ ذُو الْأَوْتادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3983, N'وَ ثَمُودُ وَ قَوْمُ لُوطٍ وَ أَصْحابُ الْأَيْكَةِ أُولئِكَ الْأَحْزابُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3984, N'إِنْ كُلٌّ إِلاَّ كَذَّبَ الرُّسُلَ فَحَقَّ عِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3985, N'وَ ما يَنْظُرُ هؤُلاءِ إِلاَّ صَيْحَةً واحِدَةً ما لَها مِنْ فَواقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3986, N'وَ قالُوا رَبَّنا عَجِّلْ لَنا قِطَّنا قَبْلَ يَوْمِ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3987, N'اصْبِرْ عَلى ما يَقُولُونَ وَ اذْكُرْ عَبْدَنا داوُدَ ذَا الْأَيْدِ إِنَّهُ أَوَّابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3988, N'إِنَّا سَخَّرْنَا الْجِبالَ مَعَهُ يُسَبِّحْنَ بِالْعَشِيِّ وَ الْإِشْراقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3989, N'وَ الطَّيْرَ مَحْشُورَةً كُلٌّ لَهُ أَوَّابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3990, N'وَ شَدَدْنا مُلْكَهُ وَ آتَيْناهُ الْحِكْمَةَ وَ فَصْلَ الْخِطابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3991, N'وَ هَلْ أَتاكَ نَبَأُ الْخَصْمِ إِذْ تَسَوَّرُوا الْمِحْرابَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3992, N'إِذْ دَخَلُوا عَلى داوُدَ فَفَزِعَ مِنْهُمْ قالُوا لا تَخَفْ خَصْمانِ بَغى بَعْضُنا عَلى بَعْضٍ فَاحْكُمْ بَيْنَنا بِالْحَقِّ وَ لا تُشْطِطْ وَ اهْدِنا إِلى سَواءِ الصِّراطِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3993, N'إِنَّ هذا أَخِي لَهُ تِسْعٌ وَ تِسْعُونَ نَعْجَةً وَ لِيَ نَعْجَةٌ واحِدَةٌ فَقالَ أَكْفِلْنِيها وَ عَزَّنِي فِي الْخِطابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3994, N'قالَ لَقَدْ ظَلَمَكَ بِسُؤالِ نَعْجَتِكَ إِلى نِعاجِهِ وَ إِنَّ كَثِيراً مِنَ الْخُلَطاءِ لَيَبْغِي بَعْضُهُمْ عَلى بَعْضٍ إِلاَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ وَ قَلِيلٌ ما هُمْ وَ ظَنَّ داوُدُ أَنَّما فَتَنَّاهُ فَاسْتَغْفَرَ رَبَّهُ وَ خَرَّ راكِعاً وَ أَنابَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3995, N'فَغَفَرْنا لَهُ ذلِكَ وَ إِنَّ لَهُ عِنْدَنا لَزُلْفى وَ حُسْنَ مَآبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3996, N'يا داوُدُ إِنَّا جَعَلْناكَ خَلِيفَةً فِي الْأَرْضِ فَاحْكُمْ بَيْنَ النَّاسِ بِالْحَقِّ وَ لا تَتَّبِعِ الْهَوى فَيُضِلَّكَ عَنْ سَبِيلِ اللَّهِ إِنَّ الَّذِينَ يَضِلُّونَ عَنْ سَبِيلِ اللَّهِ لَهُمْ عَذابٌ شَدِيدٌ بِما نَسُوا يَوْمَ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3997, N'وَ ما خَلَقْنَا السَّماءَ وَ الْأَرْضَ وَ ما بَيْنَهُما باطِلاً ذلِكَ ظَنُّ الَّذِينَ كَفَرُوا فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنَ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3998, N'أَمْ نَجْعَلُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ كَالْمُفْسِدِينَ فِي الْأَرْضِ أَمْ نَجْعَلُ الْمُتَّقِينَ كَالْفُجَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (3999, N'كِتابٌ أَنْزَلْناهُ إِلَيْكَ مُبارَكٌ لِيَدَّبَّرُوا آياتِهِ وَ لِيَتَذَكَّرَ أُولُوا الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4000, N'وَ وَهَبْنا لِداوُدَ سُلَيْمانَ نِعْمَ الْعَبْدُ إِنَّهُ أَوَّابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4001, N'إِذْ عُرِضَ عَلَيْهِ بِالْعَشِيِّ الصَّافِناتُ الْجِيادُ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4002, N'فَقالَ إِنِّي أَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ رَبِّي حَتَّى تَوارَتْ بِالْحِجابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4003, N'رُدُّوها عَلَيَّ فَطَفِقَ مَسْحاً بِالسُّوقِ وَ الْأَعْناقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4004, N'وَ لَقَدْ فَتَنَّا سُلَيْمانَ وَ أَلْقَيْنا عَلى كُرْسِيِّهِ جَسَداً ثُمَّ أَنابَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4005, N'قالَ رَبِّ اغْفِرْ لِي وَ هَبْ لِي مُلْكاً لا يَنْبَغِي لِأَحَدٍ مِنْ بَعْدِي إِنَّكَ أَنْتَ الْوَهَّابُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4006, N'فَسَخَّرْنا لَهُ الرِّيحَ تَجْرِي بِأَمْرِهِ رُخاءً حَيْثُ أَصابَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4007, N'وَ الشَّياطِينَ كُلَّ بَنَّاءٍ وَ غَوَّاصٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4008, N'وَ آخَرِينَ مُقَرَّنِينَ فِي الْأَصْفادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4009, N'هذا عَطاؤُنا فَامْنُنْ أَوْ أَمْسِكْ بِغَيْرِ حِسابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4010, N'وَ إِنَّ لَهُ عِنْدَنا لَزُلْفى وَ حُسْنَ مَآبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4011, N'وَ اذْكُرْ عَبْدَنا أَيُّوبَ إِذْ نادى رَبَّهُ أَنِّي مَسَّنِيَ الشَّيْطانُ بِنُصْبٍ وَ عَذابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4012, N'ارْكُضْ بِرِجْلِكَ هذا مُغْتَسَلٌ بارِدٌ وَ شَرابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4013, N'وَ وَهَبْنا لَهُ أَهْلَهُ وَ مِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنَّا وَ ذِكْرى لِأُولِي الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4014, N'وَ خُذْ بِيَدِكَ ضِغْثاً فَاضْرِبْ بِهِ وَ لا تَحْنَثْ إِنَّا وَجَدْناهُ صابِراً نِعْمَ الْعَبْدُ إِنَّهُ أَوَّابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4015, N'وَ اذْكُرْ عِبادَنا إِبْراهِيمَ وَ إِسْحاقَ وَ يَعْقُوبَ أُولِي الْأَيْدِي وَ الْأَبْصارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4016, N'إِنَّا أَخْلَصْناهُمْ بِخالِصَةٍ ذِكْرَى الدَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4017, N'وَ إِنَّهُمْ عِنْدَنا لَمِنَ الْمُصْطَفَيْنَ الْأَخْيارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4018, N'وَ اذْكُرْ إِسْماعِيلَ وَ الْيَسَعَ وَ ذَا الْكِفْلِ وَ كُلٌّ مِنَ الْأَخْيارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4019, N'هذا ذِكْرٌ وَ إِنَّ لِلْمُتَّقِينَ لَحُسْنَ مَآبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4020, N'جَنَّاتِ عَدْنٍ مُفَتَّحَةً لَهُمُ الْأَبْوابُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4021, N'مُتَّكِئِينَ فِيها يَدْعُونَ فِيها بِفاكِهَةٍ كَثِيرَةٍ وَ شَرابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4022, N'وَ عِنْدَهُمْ قاصِراتُ الطَّرْفِ أَتْرابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4023, N'هذا ما تُوعَدُونَ لِيَوْمِ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4024, N'إِنَّ هذا لَرِزْقُنا ما لَهُ مِنْ نَفادٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4025, N'هذا وَ إِنَّ لِلطَّاغِينَ لَشَرَّ مَآبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4026, N'جَهَنَّمَ يَصْلَوْنَها فَبِئْسَ الْمِهادُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4027, N'هذا فَلْيَذُوقُوهُ حَمِيمٌ وَ غَسَّاقٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4028, N'وَ آخَرُ مِنْ شَكْلِهِ أَزْواجٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4029, N'هذا فَوْجٌ مُقْتَحِمٌ مَعَكُمْ لا مَرْحَباً بِهِمْ إِنَّهُمْ صالُوا النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4030, N'قالُوا بَلْ أَنْتُمْ لا مَرْحَباً بِكُمْ أَنْتُمْ قَدَّمْتُمُوهُ لَنا فَبِئْسَ الْقَرارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4031, N'قالُوا رَبَّنا مَنْ قَدَّمَ لَنا هذا فَزِدْهُ عَذاباً ضِعْفاً فِي النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4032, N'وَ قالُوا ما لَنا لا نَرى رِجالاً كُنَّا نَعُدُّهُمْ مِنَ الْأَشْرارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4033, N'أَتَّخَذْناهُمْ سِخْرِيًّا أَمْ زاغَتْ عَنْهُمُ الْأَبْصارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4034, N'إِنَّ ذلِكَ لَحَقٌّ تَخاصُمُ أَهْلِ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4035, N'قُلْ إِنَّما أَنَا مُنْذِرٌ وَ ما مِنْ إِلهٍ إِلاَّ اللَّهُ الْواحِدُ الْقَهَّارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4036, N'رَبُّ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُمَا الْعَزِيزُ الْغَفَّارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4037, N'قُلْ هُوَ نَبَأٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4038, N'أَنْتُمْ عَنْهُ مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4039, N'ما كانَ لِي مِنْ عِلْمٍ بِالْمَلَإِ الْأَعْلى إِذْ يَخْتَصِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4040, N'إِنْ يُوحى إِلَيَّ إِلاَّ أَنَّما أَنَا نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4041, N'إِذْ قالَ رَبُّكَ لِلْمَلائِكَةِ إِنِّي خالِقٌ بَشَراً مِنْ طِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4042, N'فَإِذا سَوَّيْتُهُ وَ نَفَخْتُ فِيهِ مِنْ رُوحِي فَقَعُوا لَهُ ساجِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4043, N'فَسَجَدَ الْمَلائِكَةُ كُلُّهُمْ أَجْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4044, N'إِلاَّ إِبْلِيسَ اسْتَكْبَرَ وَ كانَ مِنَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4045, N'قالَ يا إِبْلِيسُ ما مَنَعَكَ أَنْ تَسْجُدَ لِما خَلَقْتُ بِيَدَيَّ أَسْتَكْبَرْتَ أَمْ كُنْتَ مِنَ الْعالِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4046, N'قالَ أَنَا خَيْرٌ مِنْهُ خَلَقْتَنِي مِنْ نارٍ وَ خَلَقْتَهُ مِنْ طِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4047, N'قالَ فَاخْرُجْ مِنْها فَإِنَّكَ رَجِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4048, N'وَ إِنَّ عَلَيْكَ لَعْنَتِي إِلى يَوْمِ الدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4049, N'قالَ رَبِّ فَأَنْظِرْنِي إِلى يَوْمِ يُبْعَثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4050, N'قالَ فَإِنَّكَ مِنَ الْمُنْظَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4051, N'إِلى يَوْمِ الْوَقْتِ الْمَعْلُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4052, N'قالَ فَبِعِزَّتِكَ لَأُغْوِيَنَّهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4053, N'إِلاَّ عِبادَكَ مِنْهُمُ الْمُخْلَصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4054, N'قالَ فَالْحَقُّ وَ الْحَقَّ أَقُولُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4055, N'لَأَمْلَأَنَّ جَهَنَّمَ مِنْكَ وَ مِمَّنْ تَبِعَكَ مِنْهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4056, N'قُلْ ما أَسْئَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ وَ ما أَنَا مِنَ الْمُتَكَلِّفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4057, N'إِنْ هُوَ إِلاَّ ذِكْرٌ لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4058, N'وَ لَتَعْلَمُنَّ نَبَأَهُ بَعْدَ حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4059, N'تَنْزِيلُ الْكِتابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4060, N'إِنَّا أَنْزَلْنا إِلَيْكَ الْكِتابَ بِالْحَقِّ فَاعْبُدِ اللَّهَ مُخْلِصاً لَهُ الدِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4061, N'أَلا لِلَّهِ الدِّينُ الْخالِصُ وَ الَّذِينَ اتَّخَذُوا مِنْ دُونِهِ أَوْلِياءَ ما نَعْبُدُهُمْ إِلاَّ لِيُقَرِّبُونا إِلَى اللَّهِ زُلْفى إِنَّ اللَّهَ يَحْكُمُ بَيْنَهُمْ فِي ما هُمْ فِيهِ يَخْتَلِفُونَ إِنَّ اللَّهَ لا يَهْدِي مَنْ هُوَ كاذِبٌ كَفَّارٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4062, N'لَوْ أَرادَ اللَّهُ أَنْ يَتَّخِذَ وَلَداً لاَصْطَفى مِمَّا يَخْلُقُ ما يَشاءُ سُبْحانَهُ هُوَ اللَّهُ الْواحِدُ الْقَهَّارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4063, N'خَلَقَ السَّماواتِ وَ الْأَرْضَ بِالْحَقِّ يُكَوِّرُ اللَّيْلَ عَلَى النَّهارِ وَ يُكَوِّرُ النَّهارَ عَلَى اللَّيْلِ وَ سَخَّرَ الشَّمْسَ وَ الْقَمَرَ كُلٌّ يَجْرِي لِأَجَلٍ مُسَمًّى أَلا هُوَ الْعَزِيزُ الْغَفَّارُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4064, N'خَلَقَكُمْ مِنْ نَفْسٍ واحِدَةٍ ثُمَّ جَعَلَ مِنْها زَوْجَها وَ أَنْزَلَ لَكُمْ مِنَ الْأَنْعامِ ثَمانِيَةَ أَزْواجٍ يَخْلُقُكُمْ فِي بُطُونِ أُمَّهاتِكُمْ خَلْقاً مِنْ بَعْدِ خَلْقٍ فِي ظُلُماتٍ ثَلاثٍ ذلِكُمُ اللَّهُ رَبُّكُمْ لَهُ الْمُلْكُ لا إِلهَ إِلاَّ هُوَ فَأَنَّى تُصْرَفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4065, N'إِنْ تَكْفُرُوا فَإِنَّ اللَّهَ غَنِيٌّ عَنْكُمْ وَ لا يَرْضى لِعِبادِهِ الْكُفْرَ وَ إِنْ تَشْكُرُوا يَرْضَهُ لَكُمْ وَ لا تَزِرُ وازِرَةٌ وِزْرَ أُخْرى ثُمَّ إِلى رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ إِنَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4066, N'وَ إِذا مَسَّ الْإِنْسانَ ضُرٌّ دَعا رَبَّهُ مُنِيباً إِلَيْهِ ثُمَّ إِذا خَوَّلَهُ نِعْمَةً مِنْهُ نَسِيَ ما كانَ يَدْعُوا إِلَيْهِ مِنْ قَبْلُ وَ جَعَلَ لِلَّهِ أَنْداداً لِيُضِلَّ عَنْ سَبِيلِهِ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيلاً إِنَّكَ مِنْ أَصْحابِ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4067, N'أَمَّنْ هُوَ قانِتٌ آناءَ اللَّيْلِ ساجِداً وَ قائِماً يَحْذَرُ الْآخِرَةَ وَ يَرْجُوا رَحْمَةَ رَبِّهِ قُلْ هَلْ يَسْتَوِي الَّذِينَ يَعْلَمُونَ وَ الَّذِينَ لا يَعْلَمُونَ إِنَّما يَتَذَكَّرُ أُولُوا الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4068, N'قُلْ يا عِبادِ الَّذِينَ آمَنُوا اتَّقُوا رَبَّكُمْ لِلَّذِينَ أَحْسَنُوا فِي هذِهِ الدُّنْيا حَسَنَةٌ وَ أَرْضُ اللَّهِ واسِعَةٌ إِنَّما يُوَفَّى الصَّابِرُونَ أَجْرَهُمْ بِغَيْرِ حِسابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4069, N'قُلْ إِنِّي أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ مُخْلِصاً لَهُ الدِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4070, N'وَ أُمِرْتُ لِأَنْ أَكُونَ أَوَّلَ الْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4071, N'قُلْ إِنِّي أَخافُ إِنْ عَصَيْتُ رَبِّي عَذابَ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4072, N'قُلِ اللَّهَ أَعْبُدُ مُخْلِصاً لَهُ دِينِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4073, N'فَاعْبُدُوا ما شِئْتُمْ مِنْ دُونِهِ قُلْ إِنَّ الْخاسِرِينَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ وَ أَهْلِيهِمْ يَوْمَ الْقِيامَةِ أَلا ذلِكَ هُوَ الْخُسْرانُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4074, N'لَهُمْ مِنْ فَوْقِهِمْ ظُلَلٌ مِنَ النَّارِ وَ مِنْ تَحْتِهِمْ ظُلَلٌ ذلِكَ يُخَوِّفُ اللَّهُ بِهِ عِبادَهُ يا عِبادِ فَاتَّقُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4075, N'وَ الَّذِينَ اجْتَنَبُوا الطَّاغُوتَ أَنْ يَعْبُدُوها وَ أَنابُوا إِلَى اللَّهِ لَهُمُ الْبُشْرى فَبَشِّرْ عِبادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4076, N'الَّذِينَ يَسْتَمِعُونَ الْقَوْلَ فَيَتَّبِعُونَ أَحْسَنَهُ أُولئِكَ الَّذِينَ هَداهُمُ اللَّهُ وَ أُولئِكَ هُمْ أُولُوا الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4077, N'أَ فَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ الْعَذابِ أَ فَأَنْتَ تُنْقِذُ مَنْ فِي النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4078, N'لكِنِ الَّذِينَ اتَّقَوْا رَبَّهُمْ لَهُمْ غُرَفٌ مِنْ فَوْقِها غُرَفٌ مَبْنِيَّةٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ وَعْدَ اللَّهِ لا يُخْلِفُ اللَّهُ الْمِيعادَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4079, N'أَ لَمْ تَرَ أَنَّ اللَّهَ أَنْزَلَ مِنَ السَّماءِ ماءً فَسَلَكَهُ يَنابِيعَ فِي الْأَرْضِ ثُمَّ يُخْرِجُ بِهِ زَرْعاً مُخْتَلِفاً أَلْوانُهُ ثُمَّ يَهِيجُ فَتَراهُ مُصْفَرًّا ثُمَّ يَجْعَلُهُ حُطاماً إِنَّ فِي ذلِكَ لَذِكْرى لِأُولِي الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4080, N'أَ فَمَنْ شَرَحَ اللَّهُ صَدْرَهُ لِلْإِسْلامِ فَهُوَ عَلى نُورٍ مِنْ رَبِّهِ فَوَيْلٌ لِلْقاسِيَةِ قُلُوبُهُمْ مِنْ ذِكْرِ اللَّهِ أُولئِكَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4081, N'اللَّهُ نَزَّلَ أَحْسَنَ الْحَدِيثِ كِتاباً مُتَشابِهاً مَثانِيَ تَقْشَعِرُّ مِنْهُ جُلُودُ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ ثُمَّ تَلِينُ جُلُودُهُمْ وَ قُلُوبُهُمْ إِلى ذِكْرِ اللَّهِ ذلِكَ هُدَى اللَّهِ يَهْدِي بِهِ مَنْ يَشاءُ وَ مَنْ يُضْلِلِ اللَّهُ فَما لَهُ مِنْ هادٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4082, N'أَ فَمَنْ يَتَّقِي بِوَجْهِهِ سُوءَ الْعَذابِ يَوْمَ الْقِيامَةِ وَ قِيلَ لِلظَّالِمِينَ ذُوقُوا ما كُنْتُمْ تَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4083, N'كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَأَتاهُمُ الْعَذابُ مِنْ حَيْثُ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4084, N'فَأَذاقَهُمُ اللَّهُ الْخِزْيَ فِي الْحَياةِ الدُّنْيا وَ لَعَذابُ الْآخِرَةِ أَكْبَرُ لَوْ كانُوا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4085, N'وَ لَقَدْ ضَرَبْنا لِلنَّاسِ فِي هذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ لَعَلَّهُمْ يَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4086, N'قُرْآناً عَرَبِيًّا غَيْرَ ذِي عِوَجٍ لَعَلَّهُمْ يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4087, N'ضَرَبَ اللَّهُ مَثَلاً رَجُلاً فِيهِ شُرَكاءُ مُتَشاكِسُونَ وَ رَجُلاً سَلَماً لِرَجُلٍ هَلْ يَسْتَوِيانِ مَثَلاً الْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4088, N'إِنَّكَ مَيِّتٌ وَ إِنَّهُمْ مَيِّتُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4089, N'ثُمَّ إِنَّكُمْ يَوْمَ الْقِيامَةِ عِنْدَ رَبِّكُمْ تَخْتَصِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4090, N'فَمَنْ أَظْلَمُ مِمَّنْ كَذَبَ عَلَى اللَّهِ وَ كَذَّبَ بِالصِّدْقِ إِذْ جاءَهُ أَ لَيْسَ فِي جَهَنَّمَ مَثْوىً لِلْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4091, N'وَ الَّذِي جاءَ بِالصِّدْقِ وَ صَدَّقَ بِهِ أُولئِكَ هُمُ الْمُتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4092, N'لَهُمْ ما يَشاؤُنَ عِنْدَ رَبِّهِمْ ذلِكَ جَزاءُ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4093, N'لِيُكَفِّرَ اللَّهُ عَنْهُمْ أَسْوَأَ الَّذِي عَمِلُوا وَ يَجْزِيَهُمْ أَجْرَهُمْ بِأَحْسَنِ الَّذِي كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4094, N'أَ لَيْسَ اللَّهُ بِكافٍ عَبْدَهُ وَ يُخَوِّفُونَكَ بِالَّذِينَ مِنْ دُونِهِ وَ مَنْ يُضْلِلِ اللَّهُ فَما لَهُ مِنْ هادٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4095, N'وَ مَنْ يَهْدِ اللَّهُ فَما لَهُ مِنْ مُضِلٍّ أَ لَيْسَ اللَّهُ بِعَزِيزٍ ذِي انْتِقامٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4096, N'وَ لَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّماواتِ وَ الْأَرْضَ لَيَقُولُنَّ اللَّهُ قُلْ أَ فَرَأَيْتُمْ ما تَدْعُونَ مِنْ دُونِ اللَّهِ إِنْ أَرادَنِيَ اللَّهُ بِضُرٍّ هَلْ هُنَّ كاشِفاتُ ضُرِّهِ أَوْ أَرادَنِي بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكاتُ رَحْمَتِهِ قُلْ حَسْبِيَ اللَّهُ عَلَيْهِ يَتَوَكَّلُ الْمُتَوَكِّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4097, N'قُلْ يا قَوْمِ اعْمَلُوا عَلى مَكانَتِكُمْ إِنِّي عامِلٌ فَسَوْفَ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4098, N'مَنْ يَأْتِيهِ عَذابٌ يُخْزِيهِ وَ يَحِلُّ عَلَيْهِ عَذابٌ مُقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4099, N'إِنَّا أَنْزَلْنا عَلَيْكَ الْكِتابَ لِلنَّاسِ بِالْحَقِّ فَمَنِ اهْتَدى فَلِنَفْسِهِ وَ مَنْ ضَلَّ فَإِنَّما يَضِلُّ عَلَيْها وَ ما أَنْتَ عَلَيْهِمْ بِوَكِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4100, N'اللَّهُ يَتَوَفَّى الْأَنْفُسَ حِينَ مَوْتِها وَ الَّتِي لَمْ تَمُتْ فِي مَنامِها فَيُمْسِكُ الَّتِي قَضى عَلَيْهَا الْمَوْتَ وَ يُرْسِلُ الْأُخْرى إِلى أَجَلٍ مُسَمًّى إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4101, N'أَمِ اتَّخَذُوا مِنْ دُونِ اللَّهِ شُفَعاءَ قُلْ أَ وَ لَوْ كانُوا لا يَمْلِكُونَ شَيْئاً وَ لا يَعْقِلُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4102, N'قُلْ لِلَّهِ الشَّفاعَةُ جَمِيعاً لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ ثُمَّ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4103, N'وَ إِذا ذُكِرَ اللَّهُ وَحْدَهُ اشْمَأَزَّتْ قُلُوبُ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ وَ إِذا ذُكِرَ الَّذِينَ مِنْ دُونِهِ إِذا هُمْ يَسْتَبْشِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4104, N'قُلِ اللَّهُمَّ فاطِرَ السَّماواتِ وَ الْأَرْضِ عالِمَ الْغَيْبِ وَ الشَّهادَةِ أَنْتَ تَحْكُمُ بَيْنَ عِبادِكَ فِي ما كانُوا فِيهِ يَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4105, N'وَ لَوْ أَنَّ لِلَّذِينَ ظَلَمُوا ما فِي الْأَرْضِ جَمِيعاً وَ مِثْلَهُ مَعَهُ لاَفْتَدَوْا بِهِ مِنْ سُوءِ الْعَذابِ يَوْمَ الْقِيامَةِ وَ بَدا لَهُمْ مِنَ اللَّهِ ما لَمْ يَكُونُوا يَحْتَسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4106, N'وَ بَدا لَهُمْ سَيِّئاتُ ما كَسَبُوا وَ حاقَ بِهِمْ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4107, N'فَإِذا مَسَّ الْإِنْسانَ ضُرٌّ دَعانا ثُمَّ إِذا خَوَّلْناهُ نِعْمَةً مِنَّا قالَ إِنَّما أُوتِيتُهُ عَلى عِلْمٍ بَلْ هِيَ فِتْنَةٌ وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4108, N'قَدْ قالَهَا الَّذِينَ مِنْ قَبْلِهِمْ فَما أَغْنى عَنْهُمْ ما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4109, N'فَأَصابَهُمْ سَيِّئاتُ ما كَسَبُوا وَ الَّذِينَ ظَلَمُوا مِنْ هؤُلاءِ سَيُصِيبُهُمْ سَيِّئاتُ ما كَسَبُوا وَ ما هُمْ بِمُعْجِزِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4110, N'أَ وَ لَمْ يَعْلَمُوا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ وَ يَقْدِرُ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4111, N'قُلْ يا عِبادِيَ الَّذِينَ أَسْرَفُوا عَلى أَنْفُسِهِمْ لا تَقْنَطُوا مِنْ رَحْمَةِ اللَّهِ إِنَّ اللَّهَ يَغْفِرُ الذُّنُوبَ جَمِيعاً إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4112, N'وَ أَنِيبُوا إِلى رَبِّكُمْ وَ أَسْلِمُوا لَهُ مِنْ قَبْلِ أَنْ يَأْتِيَكُمُ الْعَذابُ ثُمَّ لا تُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4113, N'وَ اتَّبِعُوا أَحْسَنَ ما أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَكُمُ الْعَذابُ بَغْتَةً وَ أَنْتُمْ لا تَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4114, N'أَنْ تَقُولَ نَفْسٌ يا حَسْرَتى عَلى ما فَرَّطْتُ فِي جَنْبِ اللَّهِ وَ إِنْ كُنْتُ لَمِنَ السَّاخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4115, N'أَوْ تَقُولَ لَوْ أَنَّ اللَّهَ هَدانِي لَكُنْتُ مِنَ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4116, N'أَوْ تَقُولَ حِينَ تَرَى الْعَذابَ لَوْ أَنَّ لِي كَرَّةً فَأَكُونَ مِنَ الْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4117, N'بَلى قَدْ جاءَتْكَ آياتِي فَكَذَّبْتَ بِها وَ اسْتَكْبَرْتَ وَ كُنْتَ مِنَ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4118, N'وَ يَوْمَ الْقِيامَةِ تَرَى الَّذِينَ كَذَبُوا عَلَى اللَّهِ وُجُوهُهُمْ مُسْوَدَّةٌ أَ لَيْسَ فِي جَهَنَّمَ مَثْوىً لِلْمُتَكَبِّرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4119, N'وَ يُنَجِّي اللَّهُ الَّذِينَ اتَّقَوْا بِمَفازَتِهِمْ لا يَمَسُّهُمُ السُّوءُ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4120, N'اللَّهُ خالِقُ كُلِّ شَيْ‏ءٍ وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ وَكِيلٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4121, N'لَهُ مَقالِيدُ السَّماواتِ وَ الْأَرْضِ وَ الَّذِينَ كَفَرُوا بِآياتِ اللَّهِ أُولئِكَ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4122, N'قُلْ أَ فَغَيْرَ اللَّهِ تَأْمُرُونِّي أَعْبُدُ أَيُّهَا الْجاهِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4123, N'وَ لَقَدْ أُوحِيَ إِلَيْكَ وَ إِلَى الَّذِينَ مِنْ قَبْلِكَ لَئِنْ أَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَ لَتَكُونَنَّ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4124, N'بَلِ اللَّهَ فَاعْبُدْ وَ كُنْ مِنَ الشَّاكِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4125, N'وَ ما قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ وَ الْأَرْضُ جَمِيعاً قَبْضَتُهُ يَوْمَ الْقِيامَةِ وَ السَّماواتُ مَطْوِيَّاتٌ بِيَمِينِهِ سُبْحانَهُ وَ تَعالى عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4126, N'وَ نُفِخَ فِي الصُّورِ فَصَعِقَ مَنْ فِي السَّماواتِ وَ مَنْ فِي الْأَرْضِ إِلاَّ مَنْ شاءَ اللَّهُ ثُمَّ نُفِخَ فِيهِ أُخْرى فَإِذا هُمْ قِيامٌ يَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4127, N'وَ أَشْرَقَتِ الْأَرْضُ بِنُورِ رَبِّها وَ وُضِعَ الْكِتابُ وَ جِي‏ءَ بِالنَّبِيِّينَ وَ الشُّهَداءِ وَ قُضِيَ بَيْنَهُمْ بِالْحَقِّ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4128, N'وَ وُفِّيَتْ كُلُّ نَفْسٍ ما عَمِلَتْ وَ هُوَ أَعْلَمُ بِما يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4129, N'وَ سِيقَ الَّذِينَ كَفَرُوا إِلى جَهَنَّمَ زُمَراً حَتَّى إِذا جاؤُها فُتِحَتْ أَبْوابُها وَ قالَ لَهُمْ خَزَنَتُها أَ لَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَتْلُونَ عَلَيْكُمْ آياتِ رَبِّكُمْ وَ يُنْذِرُونَكُمْ لِقاءَ يَوْمِكُمْ هذا قالُوا بَلى وَ لكِنْ حَقَّتْ كَلِمَةُ الْعَذابِ عَلَى الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4130, N'قِيلَ ادْخُلُوا أَبْوابَ جَهَنَّمَ خالِدِينَ فِيها فَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4131, N'وَ سِيقَ الَّذِينَ اتَّقَوْا رَبَّهُمْ إِلَى الْجَنَّةِ زُمَراً حَتَّى إِذا جاؤُها وَ فُتِحَتْ أَبْوابُها وَ قالَ لَهُمْ خَزَنَتُها سَلامٌ عَلَيْكُمْ طِبْتُمْ فَادْخُلُوها خالِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4132, N'وَ قالُوا الْحَمْدُ لِلَّهِ الَّذِي صَدَقَنا وَعْدَهُ وَ أَوْرَثَنَا الْأَرْضَ نَتَبَوَّأُ مِنَ الْجَنَّةِ حَيْثُ نَشاءُ فَنِعْمَ أَجْرُ الْعامِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4133, N'وَ تَرَى الْمَلائِكَةَ حَافِّينَ مِنْ حَوْلِ الْعَرْشِ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَ قُضِيَ بَيْنَهُمْ بِالْحَقِّ وَ قِيلَ الْحَمْدُ لِلَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4134, N'حم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4135, N'تَنْزِيلُ الْكِتابِ مِنَ اللَّهِ الْعَزِيزِ الْعَلِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4136, N'غافِرِ الذَّنْبِ وَ قابِلِ التَّوْبِ شَدِيدِ الْعِقابِ ذِي الطَّوْلِ لا إِلهَ إِلاَّ هُوَ إِلَيْهِ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4137, N'ما يُجادِلُ فِي آياتِ اللَّهِ إِلاَّ الَّذِينَ كَفَرُوا فَلا يَغْرُرْكَ تَقَلُّبُهُمْ فِي الْبِلادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4138, N'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَ الْأَحْزابُ مِنْ بَعْدِهِمْ وَ هَمَّتْ كُلُّ أُمَّةٍ بِرَسُولِهِمْ لِيَأْخُذُوهُ وَ جادَلُوا بِالْباطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ فَأَخَذْتُهُمْ فَكَيْفَ كانَ عِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4139, N'وَ كَذلِكَ حَقَّتْ كَلِمَةُ رَبِّكَ عَلَى الَّذِينَ كَفَرُوا أَنَّهُمْ أَصْحابُ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4140, N'الَّذِينَ يَحْمِلُونَ الْعَرْشَ وَ مَنْ حَوْلَهُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَ يُؤْمِنُونَ بِهِ وَ يَسْتَغْفِرُونَ لِلَّذِينَ آمَنُوا رَبَّنا وَسِعْتَ كُلَّ شَيْ‏ءٍ رَحْمَةً وَ عِلْماً فَاغْفِرْ لِلَّذِينَ تابُوا وَ اتَّبَعُوا سَبِيلَكَ وَ قِهِمْ عَذابَ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4141, N'رَبَّنا وَ أَدْخِلْهُمْ جَنَّاتِ عَدْنٍ الَّتِي وَعَدْتَهُمْ وَ مَنْ صَلَحَ مِنْ آبائِهِمْ وَ أَزْواجِهِمْ وَ ذُرِّيَّاتِهِمْ إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4142, N'وَ قِهِمُ السَّيِّئاتِ وَ مَنْ تَقِ السَّيِّئاتِ يَوْمَئِذٍ فَقَدْ رَحِمْتَهُ وَ ذلِكَ هُوَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4143, N'إِنَّ الَّذِينَ كَفَرُوا يُنادَوْنَ لَمَقْتُ اللَّهِ أَكْبَرُ مِنْ مَقْتِكُمْ أَنْفُسَكُمْ إِذْ تُدْعَوْنَ إِلَى الْإِيمانِ فَتَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4144, N'قالُوا رَبَّنا أَمَتَّنَا اثْنَتَيْنِ وَ أَحْيَيْتَنَا اثْنَتَيْنِ فَاعْتَرَفْنا بِذُنُوبِنا فَهَلْ إِلى خُرُوجٍ مِنْ سَبِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4145, N'ذلِكُمْ بِأَنَّهُ إِذا دُعِيَ اللَّهُ وَحْدَهُ كَفَرْتُمْ وَ إِنْ يُشْرَكْ بِهِ تُؤْمِنُوا فَالْحُكْمُ لِلَّهِ الْعَلِيِّ الْكَبِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4146, N'هُوَ الَّذِي يُرِيكُمْ آياتِهِ وَ يُنَزِّلُ لَكُمْ مِنَ السَّماءِ رِزْقاً وَ ما يَتَذَكَّرُ إِلاَّ مَنْ يُنِيبُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4147, N'فَادْعُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ وَ لَوْ كَرِهَ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4148, N'رَفِيعُ الدَّرَجاتِ ذُو الْعَرْشِ يُلْقِي الرُّوحَ مِنْ أَمْرِهِ عَلى مَنْ يَشاءُ مِنْ عِبادِهِ لِيُنْذِرَ يَوْمَ التَّلاقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4149, N'يَوْمَ هُمْ بارِزُونَ لا يَخْفى عَلَى اللَّهِ مِنْهُمْ شَيْ‏ءٌ لِمَنِ الْمُلْكُ الْيَوْمَ لِلَّهِ الْواحِدِ الْقَهَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4150, N'الْيَوْمَ تُجْزى كُلُّ نَفْسٍ بِما كَسَبَتْ لا ظُلْمَ الْيَوْمَ إِنَّ اللَّهَ سَرِيعُ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4151, N'وَ أَنْذِرْهُمْ يَوْمَ الْآزِفَةِ إِذِ الْقُلُوبُ لَدَى الْحَناجِرِ كاظِمِينَ ما لِلظَّالِمِينَ مِنْ حَمِيمٍ وَ لا شَفِيعٍ يُطاعُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4152, N'يَعْلَمُ خائِنَةَ الْأَعْيُنِ وَ ما تُخْفِي الصُّدُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4153, N'وَ اللَّهُ يَقْضِي بِالْحَقِّ وَ الَّذِينَ يَدْعُونَ مِنْ دُونِهِ لا يَقْضُونَ بِشَيْ‏ءٍ إِنَّ اللَّهَ هُوَ السَّمِيعُ الْبَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4154, N'أَ وَ لَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كانَ عاقِبَةُ الَّذِينَ كانُوا مِنْ قَبْلِهِمْ كانُوا هُمْ أَشَدَّ مِنْهُمْ قُوَّةً وَ آثاراً فِي الْأَرْضِ فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ وَ ما كانَ لَهُمْ مِنَ اللَّهِ مِنْ واقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4155, N'ذلِكَ بِأَنَّهُمْ كانَتْ تَأْتِيهِمْ رُسُلُهُمْ بِالْبَيِّناتِ فَكَفَرُوا فَأَخَذَهُمُ اللَّهُ إِنَّهُ قَوِيٌّ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4156, N'وَ لَقَدْ أَرْسَلْنا مُوسى بِآياتِنا وَ سُلْطانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4157, N'إِلى فِرْعَوْنَ وَ هامانَ وَ قارُونَ فَقالُوا ساحِرٌ كَذَّابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4158, N'فَلَمَّا جاءَهُمْ بِالْحَقِّ مِنْ عِنْدِنا قالُوا اقْتُلُوا أَبْناءَ الَّذِينَ آمَنُوا مَعَهُ وَ اسْتَحْيُوا نِساءَهُمْ وَ ما كَيْدُ الْكافِرِينَ إِلاَّ فِي ضَلالٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4159, N'وَ قالَ فِرْعَوْنُ ذَرُونِي أَقْتُلْ مُوسى وَ لْيَدْعُ رَبَّهُ إِنِّي أَخافُ أَنْ يُبَدِّلَ دِينَكُمْ أَوْ أَنْ يُظْهِرَ فِي الْأَرْضِ الْفَسادَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4160, N'وَ قالَ مُوسى إِنِّي عُذْتُ بِرَبِّي وَ رَبِّكُمْ مِنْ كُلِّ مُتَكَبِّرٍ لا يُؤْمِنُ بِيَوْمِ الْحِسابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4161, N'وَ قالَ رَجُلٌ مُؤْمِنٌ مِنْ آلِ فِرْعَوْنَ يَكْتُمُ إِيمانَهُ أَ تَقْتُلُونَ رَجُلاً أَنْ يَقُولَ رَبِّيَ اللَّهُ وَ قَدْ جاءَكُمْ بِالْبَيِّناتِ مِنْ رَبِّكُمْ وَ إِنْ يَكُ كاذِباً فَعَلَيْهِ كَذِبُهُ وَ إِنْ يَكُ صادِقاً يُصِبْكُمْ بَعْضُ الَّذِي يَعِدُكُمْ إِنَّ اللَّهَ لا يَهْدِي مَنْ هُوَ مُسْرِفٌ كَذَّابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4162, N'يا قَوْمِ لَكُمُ الْمُلْكُ الْيَوْمَ ظاهِرِينَ فِي الْأَرْضِ فَمَنْ يَنْصُرُنا مِنْ بَأْسِ اللَّهِ إِنْ جاءَنا قالَ فِرْعَوْنُ ما أُرِيكُمْ إِلاَّ ما أَرى وَ ما أَهْدِيكُمْ إِلاَّ سَبِيلَ الرَّشادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4163, N'وَ قالَ الَّذِي آمَنَ يا قَوْمِ إِنِّي أَخافُ عَلَيْكُمْ مِثْلَ يَوْمِ الْأَحْزابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4164, N'مِثْلَ دَأْبِ قَوْمِ نُوحٍ وَ عادٍ وَ ثَمُودَ وَ الَّذِينَ مِنْ بَعْدِهِمْ وَ مَا اللَّهُ يُرِيدُ ظُلْماً لِلْعِبادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4165, N'وَ يا قَوْمِ إِنِّي أَخافُ عَلَيْكُمْ يَوْمَ التَّنادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4166, N'يَوْمَ تُوَلُّونَ مُدْبِرِينَ ما لَكُمْ مِنَ اللَّهِ مِنْ عاصِمٍ وَ مَنْ يُضْلِلِ اللَّهُ فَما لَهُ مِنْ هادٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4167, N'وَ لَقَدْ جاءَكُمْ يُوسُفُ مِنْ قَبْلُ بِالْبَيِّناتِ فَما زِلْتُمْ فِي شَكٍّ مِمَّا جاءَكُمْ بِهِ حَتَّى إِذا هَلَكَ قُلْتُمْ لَنْ يَبْعَثَ اللَّهُ مِنْ بَعْدِهِ رَسُولاً كَذلِكَ يُضِلُّ اللَّهُ مَنْ هُوَ مُسْرِفٌ مُرْتابٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4168, N'الَّذِينَ يُجادِلُونَ فِي آياتِ اللَّهِ بِغَيْرِ سُلْطانٍ أَتاهُمْ كَبُرَ مَقْتاً عِنْدَ اللَّهِ وَ عِنْدَ الَّذِينَ آمَنُوا كَذلِكَ يَطْبَعُ اللَّهُ عَلى كُلِّ قَلْبِ مُتَكَبِّرٍ جَبَّارٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4169, N'وَ قالَ فِرْعَوْنُ يا هامانُ ابْنِ لِي صَرْحاً لَعَلِّي أَبْلُغُ الْأَسْبابَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4170, N'أَسْبابَ السَّماواتِ فَأَطَّلِعَ إِلى إِلهِ مُوسى وَ إِنِّي لَأَظُنُّهُ كاذِباً وَ كَذلِكَ زُيِّنَ لِفِرْعَوْنَ سُوءُ عَمَلِهِ وَ صُدَّ عَنِ السَّبِيلِ وَ ما كَيْدُ فِرْعَوْنَ إِلاَّ فِي تَبابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4171, N'وَ قالَ الَّذِي آمَنَ يا قَوْمِ اتَّبِعُونِ أَهْدِكُمْ سَبِيلَ الرَّشادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4172, N'يا قَوْمِ إِنَّما هذِهِ الْحَياةُ الدُّنْيا مَتاعٌ وَ إِنَّ الْآخِرَةَ هِيَ دارُ الْقَرارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4173, N'مَنْ عَمِلَ سَيِّئَةً فَلا يُجْزى إِلاَّ مِثْلَها وَ مَنْ عَمِلَ صالِحاً مِنْ ذَكَرٍ أَوْ أُنْثى وَ هُوَ مُؤْمِنٌ فَأُولئِكَ يَدْخُلُونَ الْجَنَّةَ يُرْزَقُونَ فِيها بِغَيْرِ حِسابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4174, N'وَ يا قَوْمِ ما لِي أَدْعُوكُمْ إِلَى النَّجاةِ وَ تَدْعُونَنِي إِلَى النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4175, N'تَدْعُونَنِي لِأَكْفُرَ بِاللَّهِ وَ أُشْرِكَ بِهِ ما لَيْسَ لِي بِهِ عِلْمٌ وَ أَنَا أَدْعُوكُمْ إِلَى الْعَزِيزِ الْغَفَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4176, N'لا جَرَمَ أَنَّما تَدْعُونَنِي إِلَيْهِ لَيْسَ لَهُ دَعْوَةٌ فِي الدُّنْيا وَ لا فِي الْآخِرَةِ وَ أَنَّ مَرَدَّنا إِلَى اللَّهِ وَ أَنَّ الْمُسْرِفِينَ هُمْ أَصْحابُ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4177, N'فَسَتَذْكُرُونَ ما أَقُولُ لَكُمْ وَ أُفَوِّضُ أَمْرِي إِلَى اللَّهِ إِنَّ اللَّهَ بَصِيرٌ بِالْعِبادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4178, N'فَوَقاهُ اللَّهُ سَيِّئاتِ ما مَكَرُوا وَ حاقَ بِآلِ فِرْعَوْنَ سُوءُ الْعَذابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4179, N'النَّارُ يُعْرَضُونَ عَلَيْها غُدُوًّا وَ عَشِيًّا وَ يَوْمَ تَقُومُ السَّاعَةُ أَدْخِلُوا آلَ فِرْعَوْنَ أَشَدَّ الْعَذابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4180, N'وَ إِذْ يَتَحاجُّونَ فِي النَّارِ فَيَقُولُ الضُّعَفاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنَّا كُنَّا لَكُمْ تَبَعاً فَهَلْ أَنْتُمْ مُغْنُونَ عَنَّا نَصِيباً مِنَ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4181, N'قالَ الَّذِينَ اسْتَكْبَرُوا إِنَّا كُلٌّ فِيها إِنَّ اللَّهَ قَدْ حَكَمَ بَيْنَ الْعِبادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4182, N'وَ قالَ الَّذِينَ فِي النَّارِ لِخَزَنَةِ جَهَنَّمَ ادْعُوا رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْماً مِنَ الْعَذابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4183, N'قالُوا أَ وَ لَمْ تَكُ تَأْتِيكُمْ رُسُلُكُمْ بِالْبَيِّناتِ قالُوا بَلى قالُوا فَادْعُوا وَ ما دُعاءُ الْكافِرِينَ إِلاَّ فِي ضَلالٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4184, N'إِنَّا لَنَنْصُرُ رُسُلَنا وَ الَّذِينَ آمَنُوا فِي الْحَياةِ الدُّنْيا وَ يَوْمَ يَقُومُ الْأَشْهادُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4185, N'يَوْمَ لا يَنْفَعُ الظَّالِمِينَ مَعْذِرَتُهُمْ وَ لَهُمُ اللَّعْنَةُ وَ لَهُمْ سُوءُ الدَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4186, N'وَ لَقَدْ آتَيْنا مُوسَى الْهُدى وَ أَوْرَثْنا بَنِي إِسْرائِيلَ الْكِتابَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4187, N'هُدىً وَ ذِكْرى لِأُولِي الْأَلْبابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4188, N'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ وَ اسْتَغْفِرْ لِذَنْبِكَ وَ سَبِّحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَ الْإِبْكارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4189, N'إِنَّ الَّذِينَ يُجادِلُونَ فِي آياتِ اللَّهِ بِغَيْرِ سُلْطانٍ أَتاهُمْ إِنْ فِي صُدُورِهِمْ إِلاَّ كِبْرٌ ما هُمْ بِبالِغِيهِ فَاسْتَعِذْ بِاللَّهِ إِنَّهُ هُوَ السَّمِيعُ الْبَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4190, N'لَخَلْقُ السَّماواتِ وَ الْأَرْضِ أَكْبَرُ مِنْ خَلْقِ النَّاسِ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4191, N'وَ ما يَسْتَوِي الْأَعْمى وَ الْبَصِيرُ وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ وَ لاَ الْمُسِي‏ءُ قَلِيلاً ما تَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4192, N'إِنَّ السَّاعَةَ لَآتِيَةٌ لا رَيْبَ فِيها وَ لكِنَّ أَكْثَرَ النَّاسِ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4193, N'وَ قالَ رَبُّكُمُ ادْعُونِي أَسْتَجِبْ لَكُمْ إِنَّ الَّذِينَ يَسْتَكْبِرُونَ عَنْ عِبادَتِي سَيَدْخُلُونَ جَهَنَّمَ داخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4194, N'اللَّهُ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَ النَّهارَ مُبْصِراً إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4195, N'ذلِكُمُ اللَّهُ رَبُّكُمْ خالِقُ كُلِّ شَيْ‏ءٍ لا إِلهَ إِلاَّ هُوَ فَأَنَّى تُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4196, N'كَذلِكَ يُؤْفَكُ الَّذِينَ كانُوا بِآياتِ اللَّهِ يَجْحَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4197, N'اللَّهُ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ قَراراً وَ السَّماءَ بِناءً وَ صَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَ رَزَقَكُمْ مِنَ الطَّيِّباتِ ذلِكُمُ اللَّهُ رَبُّكُمْ فَتَبارَكَ اللَّهُ رَبُّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4198, N'هُوَ الْحَيُّ لا إِلهَ إِلاَّ هُوَ فَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ الْحَمْدُ لِلَّهِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4199, N'قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللَّهِ لَمَّا جاءَنِي الْبَيِّناتُ مِنْ رَبِّي وَ أُمِرْتُ أَنْ أُسْلِمَ لِرَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4200, N'هُوَ الَّذِي خَلَقَكُمْ مِنْ تُرابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ يُخْرِجُكُمْ طِفْلاً ثُمَّ لِتَبْلُغُوا أَشُدَّكُمْ ثُمَّ لِتَكُونُوا شُيُوخاً وَ مِنْكُمْ مَنْ يُتَوَفَّى مِنْ قَبْلُ وَ لِتَبْلُغُوا أَجَلاً مُسَمًّى وَ لَعَلَّكُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4201, N'هُوَ الَّذِي يُحْيِي وَ يُمِيتُ فَإِذا قَضى أَمْراً فَإِنَّما يَقُولُ لَهُ كُنْ فَيَكُونُ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4202, N'أَ لَمْ تَرَ إِلَى الَّذِينَ يُجادِلُونَ فِي آياتِ اللَّهِ أَنَّى يُصْرَفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4203, N'الَّذِينَ كَذَّبُوا بِالْكِتابِ وَ بِما أَرْسَلْنا بِهِ رُسُلَنا فَسَوْفَ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4204, N'إِذِ الْأَغْلالُ فِي أَعْناقِهِمْ وَ السَّلاسِلُ يُسْحَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4205, N'فِي الْحَمِيمِ ثُمَّ فِي النَّارِ يُسْجَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4206, N'ثُمَّ قِيلَ لَهُمْ أَيْنَ ما كُنْتُمْ تُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4207, N'مِنْ دُونِ اللَّهِ قالُوا ضَلُّوا عَنَّا بَلْ لَمْ نَكُنْ نَدْعُوا مِنْ قَبْلُ شَيْئاً كَذلِكَ يُضِلُّ اللَّهُ الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4208, N'ذلِكُمْ بِما كُنْتُمْ تَفْرَحُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَ بِما كُنْتُمْ تَمْرَحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4209, N'ادْخُلُوا أَبْوابَ جَهَنَّمَ خالِدِينَ فِيها فَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4210, N'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ فَإِمَّا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنا يُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4211, N'وَ لَقَدْ أَرْسَلْنا رُسُلاً مِنْ قَبْلِكَ مِنْهُمْ مَنْ قَصَصْنا عَلَيْكَ وَ مِنْهُمْ مَنْ لَمْ نَقْصُصْ عَلَيْكَ وَ ما كانَ لِرَسُولٍ أَنْ يَأْتِيَ بِآيَةٍ إِلاَّ بِإِذْنِ اللَّهِ فَإِذا جاءَ أَمْرُ اللَّهِ قُضِيَ بِالْحَقِّ وَ خَسِرَ هُنالِكَ الْمُبْطِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4212, N'اللَّهُ الَّذِي جَعَلَ لَكُمُ الْأَنْعامَ لِتَرْكَبُوا مِنْها وَ مِنْها تَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4213, N'وَ لَكُمْ فِيها مَنافِعُ وَ لِتَبْلُغُوا عَلَيْها حاجَةً فِي صُدُورِكُمْ وَ عَلَيْها وَ عَلَى الْفُلْكِ تُحْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4214, N'وَ يُرِيكُمْ آياتِهِ فَأَيَّ آياتِ اللَّهِ تُنْكِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4215, N'أَ فَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كانَ عاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ كانُوا أَكْثَرَ مِنْهُمْ وَ أَشَدَّ قُوَّةً وَ آثاراً فِي الْأَرْضِ فَما أَغْنى عَنْهُمْ ما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4216, N'فَلَمَّا جاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّناتِ فَرِحُوا بِما عِنْدَهُمْ مِنَ الْعِلْمِ وَ حاقَ بِهِمْ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4217, N'فَلَمَّا رَأَوْا بَأْسَنا قالُوا آمَنَّا بِاللَّهِ وَحْدَهُ وَ كَفَرْنا بِما كُنَّا بِهِ مُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4218, N'فَلَمْ يَكُ يَنْفَعُهُمْ إِيمانُهُمْ لَمَّا رَأَوْا بَأْسَنا سُنَّتَ اللَّهِ الَّتِي قَدْ خَلَتْ فِي عِبادِهِ وَ خَسِرَ هُنالِكَ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4219, N'حم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4220, N'تَنْزِيلٌ مِنَ الرَّحْمنِ الرَّحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4221, N'كِتابٌ فُصِّلَتْ آياتُهُ قُرْآناً عَرَبِيًّا لِقَوْمٍ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4222, N'بَشِيراً وَ نَذِيراً فَأَعْرَضَ أَكْثَرُهُمْ فَهُمْ لا يَسْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4223, N'وَ قالُوا قُلُوبُنا فِي أَكِنَّةٍ مِمَّا تَدْعُونا إِلَيْهِ وَ فِي آذانِنا وَقْرٌ وَ مِنْ بَيْنِنا وَ بَيْنِكَ حِجابٌ فَاعْمَلْ إِنَّنا عامِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4224, N'قُلْ إِنَّما أَنَا بَشَرٌ مِثْلُكُمْ يُوحى إِلَيَّ أَنَّما إِلهُكُمْ إِلهٌ واحِدٌ فَاسْتَقِيمُوا إِلَيْهِ وَ اسْتَغْفِرُوهُ وَ وَيْلٌ لِلْمُشْرِكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4225, N'الَّذِينَ لا يُؤْتُونَ الزَّكاةَ وَ هُمْ بِالْآخِرَةِ هُمْ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4226, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4227, N'قُلْ أَ إِنَّكُمْ لَتَكْفُرُونَ بِالَّذِي خَلَقَ الْأَرْضَ فِي يَوْمَيْنِ وَ تَجْعَلُونَ لَهُ أَنْداداً ذلِكَ رَبُّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4228, N'وَ جَعَلَ فِيها رَواسِيَ مِنْ فَوْقِها وَ بارَكَ فِيها وَ قَدَّرَ فِيها أَقْواتَها فِي أَرْبَعَةِ أَيَّامٍ سَواءً لِلسَّائِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4229, N'ثُمَّ اسْتَوى إِلَى السَّماءِ وَ هِيَ دُخانٌ فَقالَ لَها وَ لِلْأَرْضِ ائْتِيا طَوْعاً أَوْ كَرْهاً قالَتا أَتَيْنا طائِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4230, N'فَقَضاهُنَّ سَبْعَ سَماواتٍ فِي يَوْمَيْنِ وَ أَوْحى فِي كُلِّ سَماءٍ أَمْرَها وَ زَيَّنَّا السَّماءَ الدُّنْيا بِمَصابِيحَ وَ حِفْظاً ذلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4231, N'فَإِنْ أَعْرَضُوا فَقُلْ أَنْذَرْتُكُمْ صاعِقَةً مِثْلَ صاعِقَةِ عادٍ وَ ثَمُودَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4232, N'إِذْ جاءَتْهُمُ الرُّسُلُ مِنْ بَيْنِ أَيْدِيهِمْ وَ مِنْ خَلْفِهِمْ أَلاَّ تَعْبُدُوا إِلاَّ اللَّهَ قالُوا لَوْ شاءَ رَبُّنا لَأَنْزَلَ مَلائِكَةً فَإِنَّا بِما أُرْسِلْتُمْ بِهِ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4233, N'فَأَمَّا عادٌ فَاسْتَكْبَرُوا فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَ قالُوا مَنْ أَشَدُّ مِنَّا قُوَّةً أَ وَ لَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَهُمْ هُوَ أَشَدُّ مِنْهُمْ قُوَّةً وَ كانُوا بِآياتِنا يَجْحَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4234, N'فَأَرْسَلْنا عَلَيْهِمْ رِيحاً صَرْصَراً فِي أَيَّامٍ نَحِساتٍ لِنُذِيقَهُمْ عَذابَ الْخِزْيِ فِي الْحَياةِ الدُّنْيا وَ لَعَذابُ الْآخِرَةِ أَخْزى وَ هُمْ لا يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4235, N'وَ أَمَّا ثَمُودُ فَهَدَيْناهُمْ فَاسْتَحَبُّوا الْعَمى عَلَى الْهُدى فَأَخَذَتْهُمْ صاعِقَةُ الْعَذابِ الْهُونِ بِما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4236, N'وَ نَجَّيْنَا الَّذِينَ آمَنُوا وَ كانُوا يَتَّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4237, N'وَ يَوْمَ يُحْشَرُ أَعْداءُ اللَّهِ إِلَى النَّارِ فَهُمْ يُوزَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4238, N'حَتَّى إِذا ما جاؤُها شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَ أَبْصارُهُمْ وَ جُلُودُهُمْ بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4239, N'وَ قالُوا لِجُلُودِهِمْ لِمَ شَهِدْتُمْ عَلَيْنا قالُوا أَنْطَقَنَا اللَّهُ الَّذِي أَنْطَقَ كُلَّ شَيْ‏ءٍ وَ هُوَ خَلَقَكُمْ أَوَّلَ مَرَّةٍ وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4240, N'وَ ما كُنْتُمْ تَسْتَتِرُونَ أَنْ يَشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَ لا أَبْصارُكُمْ وَ لا جُلُودُكُمْ وَ لكِنْ ظَنَنْتُمْ أَنَّ اللَّهَ لا يَعْلَمُ كَثِيراً مِمَّا تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4241, N'وَ ذلِكُمْ ظَنُّكُمُ الَّذِي ظَنَنْتُمْ بِرَبِّكُمْ أَرْداكُمْ فَأَصْبَحْتُمْ مِنَ الْخاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4242, N'فَإِنْ يَصْبِرُوا فَالنَّارُ مَثْوىً لَهُمْ وَ إِنْ يَسْتَعْتِبُوا فَما هُمْ مِنَ الْمُعْتَبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4243, N'وَ قَيَّضْنا لَهُمْ قُرَناءَ فَزَيَّنُوا لَهُمْ ما بَيْنَ أَيْدِيهِمْ وَ ما خَلْفَهُمْ وَ حَقَّ عَلَيْهِمُ الْقَوْلُ فِي أُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِنَ الْجِنِّ وَ الْإِنْسِ إِنَّهُمْ كانُوا خاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4244, N'وَ قالَ الَّذِينَ كَفَرُوا لا تَسْمَعُوا لِهذَا الْقُرْآنِ وَ الْغَوْا فِيهِ لَعَلَّكُمْ تَغْلِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4245, N'فَلَنُذِيقَنَّ الَّذِينَ كَفَرُوا عَذاباً شَدِيداً وَ لَنَجْزِيَنَّهُمْ أَسْوَأَ الَّذِي كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4246, N'ذلِكَ جَزاءُ أَعْداءِ اللَّهِ النَّارُ لَهُمْ فِيها دارُ الْخُلْدِ جَزاءً بِما كانُوا بِآياتِنا يَجْحَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4247, N'وَ قالَ الَّذِينَ كَفَرُوا رَبَّنا أَرِنَا الَّذَيْنِ أَضَلاَّنا مِنَ الْجِنِّ وَ الْإِنْسِ نَجْعَلْهُما تَحْتَ أَقْدامِنا لِيَكُونا مِنَ الْأَسْفَلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4248, N'إِنَّ الَّذِينَ قالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقامُوا تَتَنَزَّلُ عَلَيْهِمُ الْمَلائِكَةُ أَلاَّ تَخافُوا وَ لا تَحْزَنُوا وَ أَبْشِرُوا بِالْجَنَّةِ الَّتِي كُنْتُمْ تُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4249, N'نَحْنُ أَوْلِياؤُكُمْ فِي الْحَياةِ الدُّنْيا وَ فِي الْآخِرَةِ وَ لَكُمْ فِيها ما تَشْتَهِي أَنْفُسُكُمْ وَ لَكُمْ فِيها ما تَدَّعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4250, N'نُزُلاً مِنْ غَفُورٍ رَحِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4251, N'وَ مَنْ أَحْسَنُ قَوْلاً مِمَّنْ دَعا إِلَى اللَّهِ وَ عَمِلَ صالِحاً وَ قالَ إِنَّنِي مِنَ الْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4252, N'وَ لا تَسْتَوِي الْحَسَنَةُ وَ لاَ السَّيِّئَةُ ادْفَعْ بِالَّتِي هِيَ أَحْسَنُ فَإِذَا الَّذِي بَيْنَكَ وَ بَيْنَهُ عَداوَةٌ كَأَنَّهُ وَلِيٌّ حَمِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4253, N'وَ ما يُلَقَّاها إِلاَّ الَّذِينَ صَبَرُوا وَ ما يُلَقَّاها إِلاَّ ذُو حَظٍّ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4254, N'وَ إِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطانِ نَزْغٌ فَاسْتَعِذْ بِاللَّهِ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4255, N'وَ مِنْ آياتِهِ اللَّيْلُ وَ النَّهارُ وَ الشَّمْسُ وَ الْقَمَرُ لا تَسْجُدُوا لِلشَّمْسِ وَ لا لِلْقَمَرِ وَ اسْجُدُوا لِلَّهِ الَّذِي خَلَقَهُنَّ إِنْ كُنْتُمْ إِيَّاهُ تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4256, N'فَإِنِ اسْتَكْبَرُوا فَالَّذِينَ عِنْدَ رَبِّكَ يُسَبِّحُونَ لَهُ بِاللَّيْلِ وَ النَّهارِ وَ هُمْ لا يَسْأَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4257, N'وَ مِنْ آياتِهِ أَنَّكَ تَرَى الْأَرْضَ خاشِعَةً فَإِذا أَنْزَلْنا عَلَيْهَا الْماءَ اهْتَزَّتْ وَ رَبَتْ إِنَّ الَّذِي أَحْياها لَمُحْيِ الْمَوْتى إِنَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4258, N'إِنَّ الَّذِينَ يُلْحِدُونَ فِي آياتِنا لا يَخْفَوْنَ عَلَيْنا أَ فَمَنْ يُلْقى فِي النَّارِ خَيْرٌ أَمْ مَنْ يَأْتِي آمِناً يَوْمَ الْقِيامَةِ اعْمَلُوا ما شِئْتُمْ إِنَّهُ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4259, N'إِنَّ الَّذِينَ كَفَرُوا بِالذِّكْرِ لَمَّا جاءَهُمْ وَ إِنَّهُ لَكِتابٌ عَزِيزٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4260, N'لا يَأْتِيهِ الْباطِلُ مِنْ بَيْنِ يَدَيْهِ وَ لا مِنْ خَلْفِهِ تَنْزِيلٌ مِنْ حَكِيمٍ حَمِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4261, N'ما يُقالُ لَكَ إِلاَّ ما قَدْ قِيلَ لِلرُّسُلِ مِنْ قَبْلِكَ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ وَ ذُو عِقابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4262, N'وَ لَوْ جَعَلْناهُ قُرْآناً أَعْجَمِيًّا لَقالُوا لَوْ لا فُصِّلَتْ آياتُهُ ءَ أَعْجَمِيٌّ وَ عَرَبِيٌّ قُلْ هُوَ لِلَّذِينَ آمَنُوا هُدىً وَ شِفاءٌ وَ الَّذِينَ لا يُؤْمِنُونَ فِي آذانِهِمْ وَقْرٌ وَ هُوَ عَلَيْهِمْ عَمًى أُولئِكَ يُنادَوْنَ مِنْ مَكانٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4263, N'وَ لَقَدْ آتَيْنا مُوسَى الْكِتابَ فَاخْتُلِفَ فِيهِ وَ لَوْ لا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ وَ إِنَّهُمْ لَفِي شَكٍّ مِنْهُ مُرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4264, N'مَنْ عَمِلَ صالِحاً فَلِنَفْسِهِ وَ مَنْ أَساءَ فَعَلَيْها وَ ما رَبُّكَ بِظَلاَّمٍ لِلْعَبِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4265, N'إِلَيْهِ يُرَدُّ عِلْمُ السَّاعَةِ وَ ما تَخْرُجُ مِنْ ثَمَراتٍ مِنْ أَكْمامِها وَ ما تَحْمِلُ مِنْ أُنْثى وَ لا تَضَعُ إِلاَّ بِعِلْمِهِ وَ يَوْمَ يُنادِيهِمْ أَيْنَ شُرَكائِي قالُوا آذَنَّاكَ ما مِنَّا مِنْ شَهِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4266, N'وَ ضَلَّ عَنْهُمْ ما كانُوا يَدْعُونَ مِنْ قَبْلُ وَ ظَنُّوا ما لَهُمْ مِنْ مَحِيصٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4267, N'لا يَسْأَمُ الْإِنْسانُ مِنْ دُعاءِ الْخَيْرِ وَ إِنْ مَسَّهُ الشَّرُّ فَيَؤُسٌ قَنُوطٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4268, N'وَ لَئِنْ أَذَقْناهُ رَحْمَةً مِنَّا مِنْ بَعْدِ ضَرَّاءَ مَسَّتْهُ لَيَقُولَنَّ هذا لِي وَ ما أَظُنُّ السَّاعَةَ قائِمَةً وَ لَئِنْ رُجِعْتُ إِلى رَبِّي إِنَّ لِي عِنْدَهُ لَلْحُسْنى فَلَنُنَبِّئَنَّ الَّذِينَ كَفَرُوا بِما عَمِلُوا وَ لَنُذِيقَنَّهُمْ مِنْ عَذابٍ غَلِيظٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4269, N'وَ إِذا أَنْعَمْنا عَلَى الْإِنْسانِ أَعْرَضَ وَ نَأى بِجانِبِهِ وَ إِذا مَسَّهُ الشَّرُّ فَذُو دُعاءٍ عَرِيضٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4270, N'قُلْ أَ رَأَيْتُمْ إِنْ كانَ مِنْ عِنْدِ اللَّهِ ثُمَّ كَفَرْتُمْ بِهِ مَنْ أَضَلُّ مِمَّنْ هُوَ فِي شِقاقٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4271, N'سَنُرِيهِمْ آياتِنا فِي الْآفاقِ وَ فِي أَنْفُسِهِمْ حَتَّى يَتَبَيَّنَ لَهُمْ أَنَّهُ الْحَقُّ أَ وَ لَمْ يَكْفِ بِرَبِّكَ أَنَّهُ عَلى كُلِّ شَيْ‏ءٍ شَهِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4272, N'أَلا إِنَّهُمْ فِي مِرْيَةٍ مِنْ لِقاءِ رَبِّهِمْ أَلا إِنَّهُ بِكُلِّ شَيْ‏ءٍ مُحِيطٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4273, N'حم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4274, N'عسق ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4275, N'كَذلِكَ يُوحِي إِلَيْكَ وَ إِلَى الَّذِينَ مِنْ قَبْلِكَ اللَّهُ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4276, N'لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ هُوَ الْعَلِيُّ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4277, N'تَكادُ السَّماواتُ يَتَفَطَّرْنَ مِنْ فَوْقِهِنَّ وَ الْمَلائِكَةُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَ يَسْتَغْفِرُونَ لِمَنْ فِي الْأَرْضِ أَلا إِنَّ اللَّهَ هُوَ الْغَفُورُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4278, N'وَ الَّذِينَ اتَّخَذُوا مِنْ دُونِهِ أَوْلِياءَ اللَّهُ حَفِيظٌ عَلَيْهِمْ وَ ما أَنْتَ عَلَيْهِمْ بِوَكِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4279, N'وَ كَذلِكَ أَوْحَيْنا إِلَيْكَ قُرْآناً عَرَبِيًّا لِتُنْذِرَ أُمَّ الْقُرى وَ مَنْ حَوْلَها وَ تُنْذِرَ يَوْمَ الْجَمْعِ لا رَيْبَ فِيهِ فَرِيقٌ فِي الْجَنَّةِ وَ فَرِيقٌ فِي السَّعِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4280, N'وَ لَوْ شاءَ اللَّهُ لَجَعَلَهُمْ أُمَّةً واحِدَةً وَ لكِنْ يُدْخِلُ مَنْ يَشاءُ فِي رَحْمَتِهِ وَ الظَّالِمُونَ ما لَهُمْ مِنْ وَلِيٍّ وَ لا نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4281, N'أَمِ اتَّخَذُوا مِنْ دُونِهِ أَوْلِياءَ فَاللَّهُ هُوَ الْوَلِيُّ وَ هُوَ يُحْيِ الْمَوْتى وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4282, N'وَ مَا اخْتَلَفْتُمْ فِيهِ مِنْ شَيْ‏ءٍ فَحُكْمُهُ إِلَى اللَّهِ ذلِكُمُ اللَّهُ رَبِّي عَلَيْهِ تَوَكَّلْتُ وَ إِلَيْهِ أُنِيبُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4283, N'فاطِرُ السَّماواتِ وَ الْأَرْضِ جَعَلَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْواجاً وَ مِنَ الْأَنْعامِ أَزْواجاً يَذْرَؤُكُمْ فِيهِ لَيْسَ كَمِثْلِهِ شَيْ‏ءٌ وَ هُوَ السَّمِيعُ الْبَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4284, N'لَهُ مَقالِيدُ السَّماواتِ وَ الْأَرْضِ يَبْسُطُ الرِّزْقَ لِمَنْ يَشاءُ وَ يَقْدِرُ إِنَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4285, N'شَرَعَ لَكُمْ مِنَ الدِّينِ ما وَصَّى بِهِ نُوحاً وَ الَّذِي أَوْحَيْنا إِلَيْكَ وَ ما وَصَّيْنا بِهِ إِبْراهِيمَ وَ مُوسى وَ عِيسى أَنْ أَقِيمُوا الدِّينَ وَ لا تَتَفَرَّقُوا فِيهِ كَبُرَ عَلَى الْمُشْرِكِينَ ما تَدْعُوهُمْ إِلَيْهِ اللَّهُ يَجْتَبِي إِلَيْهِ مَنْ يَشاءُ وَ يَهْدِي إِلَيْهِ مَنْ يُنِيبُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4286, N'وَ ما تَفَرَّقُوا إِلاَّ مِنْ بَعْدِ ما جاءَهُمُ الْعِلْمُ بَغْياً بَيْنَهُمْ وَ لَوْ لا كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ إِلى أَجَلٍ مُسَمًّى لَقُضِيَ بَيْنَهُمْ وَ إِنَّ الَّذِينَ أُورِثُوا الْكِتابَ مِنْ بَعْدِهِمْ لَفِي شَكٍّ مِنْهُ مُرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4287, N'فَلِذلِكَ فَادْعُ وَ اسْتَقِمْ كَما أُمِرْتَ وَ لا تَتَّبِعْ أَهْواءَهُمْ وَ قُلْ آمَنْتُ بِما أَنْزَلَ اللَّهُ مِنْ كِتابٍ وَ أُمِرْتُ لِأَعْدِلَ بَيْنَكُمُ اللَّهُ رَبُّنا وَ رَبُّكُمْ لَنا أَعْمالُنا وَ لَكُمْ أَعْمالُكُمْ لا حُجَّةَ بَيْنَنا وَ بَيْنَكُمُ اللَّهُ يَجْمَعُ بَيْنَنا وَ إِلَيْهِ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4288, N'وَ الَّذِينَ يُحَاجُّونَ فِي اللَّهِ مِنْ بَعْدِ ما اسْتُجِيبَ لَهُ حُجَّتُهُمْ داحِضَةٌ عِنْدَ رَبِّهِمْ وَ عَلَيْهِمْ غَضَبٌ وَ لَهُمْ عَذابٌ شَدِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4289, N'اللَّهُ الَّذِي أَنْزَلَ الْكِتابَ بِالْحَقِّ وَ الْمِيزانَ وَ ما يُدْرِيكَ لَعَلَّ السَّاعَةَ قَرِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4290, N'يَسْتَعْجِلُ بِهَا الَّذِينَ لا يُؤْمِنُونَ بِها وَ الَّذِينَ آمَنُوا مُشْفِقُونَ مِنْها وَ يَعْلَمُونَ أَنَّهَا الْحَقُّ أَلا إِنَّ الَّذِينَ يُمارُونَ فِي السَّاعَةِ لَفِي ضَلالٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4291, N'اللَّهُ لَطِيفٌ بِعِبادِهِ يَرْزُقُ مَنْ يَشاءُ وَ هُوَ الْقَوِيُّ الْعَزِيزُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4292, N'مَنْ كانَ يُرِيدُ حَرْثَ الْآخِرَةِ نَزِدْ لَهُ فِي حَرْثِهِ وَ مَنْ كانَ يُرِيدُ حَرْثَ الدُّنْيا نُؤْتِهِ مِنْها وَ ما لَهُ فِي الْآخِرَةِ مِنْ نَصِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4293, N'أَمْ لَهُمْ شُرَكاءُ شَرَعُوا لَهُمْ مِنَ الدِّينِ ما لَمْ يَأْذَنْ بِهِ اللَّهُ وَ لَوْ لا كَلِمَةُ الْفَصْلِ لَقُضِيَ بَيْنَهُمْ وَ إِنَّ الظَّالِمِينَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4294, N'تَرَى الظَّالِمِينَ مُشْفِقِينَ مِمَّا كَسَبُوا وَ هُوَ واقِعٌ بِهِمْ وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ فِي رَوْضاتِ الْجَنَّاتِ لَهُمْ ما يَشاؤُنَ عِنْدَ رَبِّهِمْ ذلِكَ هُوَ الْفَضْلُ الْكَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4295, N'ذلِكَ الَّذِي يُبَشِّرُ اللَّهُ عِبادَهُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ قُلْ لا أَسْئَلُكُمْ عَلَيْهِ أَجْراً إِلاَّ الْمَوَدَّةَ فِي الْقُرْبى وَ مَنْ يَقْتَرِفْ حَسَنَةً نَزِدْ لَهُ فِيها حُسْناً إِنَّ اللَّهَ غَفُورٌ شَكُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4296, N'أَمْ يَقُولُونَ افْتَرى عَلَى اللَّهِ كَذِباً فَإِنْ يَشَإِ اللَّهُ يَخْتِمْ عَلى قَلْبِكَ وَ يَمْحُ اللَّهُ الْباطِلَ وَ يُحِقُّ الْحَقَّ بِكَلِماتِهِ إِنَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4297, N'وَ هُوَ الَّذِي يَقْبَلُ التَّوْبَةَ عَنْ عِبادِهِ وَ يَعْفُوا عَنِ السَّيِّئاتِ وَ يَعْلَمُ ما تَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4298, N'وَ يَسْتَجِيبُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ وَ يَزِيدُهُمْ مِنْ فَضْلِهِ وَ الْكافِرُونَ لَهُمْ عَذابٌ شَدِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4299, N'وَ لَوْ بَسَطَ اللَّهُ الرِّزْقَ لِعِبادِهِ لَبَغَوْا فِي الْأَرْضِ وَ لكِنْ يُنَزِّلُ بِقَدَرٍ ما يَشاءُ إِنَّهُ بِعِبادِهِ خَبِيرٌ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4300, N'وَ هُوَ الَّذِي يُنَزِّلُ الْغَيْثَ مِنْ بَعْدِ ما قَنَطُوا وَ يَنْشُرُ رَحْمَتَهُ وَ هُوَ الْوَلِيُّ الْحَمِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4301, N'وَ مِنْ آياتِهِ خَلْقُ السَّماواتِ وَ الْأَرْضِ وَ ما بَثَّ فِيهِما مِنْ دابَّةٍ وَ هُوَ عَلى جَمْعِهِمْ إِذا يَشاءُ قَدِيرٌ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4302, N'وَ ما أَصابَكُمْ مِنْ مُصِيبَةٍ فَبِما كَسَبَتْ أَيْدِيكُمْ وَ يَعْفُوا عَنْ كَثِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4303, N'وَ ما أَنْتُمْ بِمُعْجِزِينَ فِي الْأَرْضِ وَ ما لَكُمْ مِنْ دُونِ اللَّهِ مِنْ وَلِيٍّ وَ لا نَصِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4304, N'وَ مِنْ آياتِهِ الْجَوارِ فِي الْبَحْرِ كَالْأَعْلامِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4305, N'إِنْ يَشَأْ يُسْكِنِ الرِّيحَ فَيَظْلَلْنَ رَواكِدَ عَلى ظَهْرِهِ إِنَّ فِي ذلِكَ لَآياتٍ لِكُلِّ صَبَّارٍ شَكُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4306, N'أَوْ يُوبِقْهُنَّ بِما كَسَبُوا وَ يَعْفُ عَنْ كَثِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4307, N'وَ يَعْلَمَ الَّذِينَ يُجادِلُونَ فِي آياتِنا ما لَهُمْ مِنْ مَحِيصٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4308, N'فَما أُوتِيتُمْ مِنْ شَيْ‏ءٍ فَمَتاعُ الْحَياةِ الدُّنْيا وَ ما عِنْدَ اللَّهِ خَيْرٌ وَ أَبْقى لِلَّذِينَ آمَنُوا وَ عَلى رَبِّهِمْ يَتَوَكَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4309, N'وَ الَّذِينَ يَجْتَنِبُونَ كَبائِرَ الْإِثْمِ وَ الْفَواحِشَ وَ إِذا ما غَضِبُوا هُمْ يَغْفِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4310, N'وَ الَّذِينَ اسْتَجابُوا لِرَبِّهِمْ وَ أَقامُوا الصَّلاةَ وَ أَمْرُهُمْ شُورى بَيْنَهُمْ وَ مِمَّا رَزَقْناهُمْ يُنْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4311, N'وَ الَّذِينَ إِذا أَصابَهُمُ الْبَغْيُ هُمْ يَنْتَصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4312, N'وَ جَزاءُ سَيِّئَةٍ سَيِّئَةٌ مِثْلُها فَمَنْ عَفا وَ أَصْلَحَ فَأَجْرُهُ عَلَى اللَّهِ إِنَّهُ لا يُحِبُّ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4313, N'وَ لَمَنِ انْتَصَرَ بَعْدَ ظُلْمِهِ فَأُولئِكَ ما عَلَيْهِمْ مِنْ سَبِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4314, N'إِنَّمَا السَّبِيلُ عَلَى الَّذِينَ يَظْلِمُونَ النَّاسَ وَ يَبْغُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ أُولئِكَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4315, N'وَ لَمَنْ صَبَرَ وَ غَفَرَ إِنَّ ذلِكَ لَمِنْ عَزْمِ الْأُمُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4316, N'وَ مَنْ يُضْلِلِ اللَّهُ فَما لَهُ مِنْ وَلِيٍّ مِنْ بَعْدِهِ وَ تَرَى الظَّالِمِينَ لَمَّا رَأَوُا الْعَذابَ يَقُولُونَ هَلْ إِلى مَرَدٍّ مِنْ سَبِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4317, N'وَ تَراهُمْ يُعْرَضُونَ عَلَيْها خاشِعِينَ مِنَ الذُّلِّ يَنْظُرُونَ مِنْ طَرْفٍ خَفِيٍّ وَ قالَ الَّذِينَ آمَنُوا إِنَّ الْخاسِرِينَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ وَ أَهْلِيهِمْ يَوْمَ الْقِيامَةِ أَلا إِنَّ الظَّالِمِينَ فِي عَذابٍ مُقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4318, N'وَ ما كانَ لَهُمْ مِنْ أَوْلِياءَ يَنْصُرُونَهُمْ مِنْ دُونِ اللَّهِ وَ مَنْ يُضْلِلِ اللَّهُ فَما لَهُ مِنْ سَبِيلٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4319, N'اسْتَجِيبُوا لِرَبِّكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لا مَرَدَّ لَهُ مِنَ اللَّهِ ما لَكُمْ مِنْ مَلْجَإٍ يَوْمَئِذٍ وَ ما لَكُمْ مِنْ نَكِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4320, N'فَإِنْ أَعْرَضُوا فَما أَرْسَلْناكَ عَلَيْهِمْ حَفِيظاً إِنْ عَلَيْكَ إِلاَّ الْبَلاغُ وَ إِنَّا إِذا أَذَقْنَا الْإِنْسانَ مِنَّا رَحْمَةً فَرِحَ بِها وَ إِنْ تُصِبْهُمْ سَيِّئَةٌ بِما قَدَّمَتْ أَيْدِيهِمْ فَإِنَّ الْإِنْسانَ كَفُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4321, N'لِلَّهِ مُلْكُ السَّماواتِ وَ الْأَرْضِ يَخْلُقُ ما يَشاءُ يَهَبُ لِمَنْ يَشاءُ إِناثاً وَ يَهَبُ لِمَنْ يَشاءُ الذُّكُورَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4322, N'أَوْ يُزَوِّجُهُمْ ذُكْراناً وَ إِناثاً وَ يَجْعَلُ مَنْ يَشاءُ عَقِيماً إِنَّهُ عَلِيمٌ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4323, N'وَ ما كانَ لِبَشَرٍ أَنْ يُكَلِّمَهُ اللَّهُ إِلاَّ وَحْياً أَوْ مِنْ وَراءِ حِجابٍ أَوْ يُرْسِلَ رَسُولاً فَيُوحِيَ بِإِذْنِهِ ما يَشاءُ إِنَّهُ عَلِيٌّ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4324, N'وَ كَذلِكَ أَوْحَيْنا إِلَيْكَ رُوحاً مِنْ أَمْرِنا ما كُنْتَ تَدْرِي مَا الْكِتابُ وَ لاَ الْإِيمانُ وَ لكِنْ جَعَلْناهُ نُوراً نَهْدِي بِهِ مَنْ نَشاءُ مِنْ عِبادِنا وَ إِنَّكَ لَتَهْدِي إِلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4325, N'صِراطِ اللَّهِ الَّذِي لَهُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ أَلا إِلَى اللَّهِ تَصِيرُ الْأُمُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4326, N'حم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4327, N'وَ الْكِتابِ الْمُبِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4328, N'إِنَّا جَعَلْناهُ قُرْآناً عَرَبِيًّا لَعَلَّكُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4329, N'وَ إِنَّهُ فِي أُمِّ الْكِتابِ لَدَيْنا لَعَلِيٌّ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4330, N'أَ فَنَضْرِبُ عَنْكُمُ الذِّكْرَ صَفْحاً أَنْ كُنْتُمْ قَوْماً مُسْرِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4331, N'وَ كَمْ أَرْسَلْنا مِنْ نَبِيٍّ فِي الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4332, N'وَ ما يَأْتِيهِمْ مِنْ نَبِيٍّ إِلاَّ كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4333, N'فَأَهْلَكْنا أَشَدَّ مِنْهُمْ بَطْشاً وَ مَضى مَثَلُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4334, N'وَ لَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَ السَّماواتِ وَ الْأَرْضَ لَيَقُولُنَّ خَلَقَهُنَّ الْعَزِيزُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4335, N'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ مَهْداً وَ جَعَلَ لَكُمْ فِيها سُبُلاً لَعَلَّكُمْ تَهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4336, N'وَ الَّذِي نَزَّلَ مِنَ السَّماءِ ماءً بِقَدَرٍ فَأَنْشَرْنا بِهِ بَلْدَةً مَيْتاً كَذلِكَ تُخْرَجُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4337, N'وَ الَّذِي خَلَقَ الْأَزْواجَ كُلَّها وَ جَعَلَ لَكُمْ مِنَ الْفُلْكِ وَ الْأَنْعامِ ما تَرْكَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4338, N'لِتَسْتَوُوا عَلى ظُهُورِهِ ثُمَّ تَذْكُرُوا نِعْمَةَ رَبِّكُمْ إِذَا اسْتَوَيْتُمْ عَلَيْهِ وَ تَقُولُوا سُبْحانَ الَّذِي سَخَّرَ لَنا هذا وَ ما كُنَّا لَهُ مُقْرِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4339, N'وَ إِنَّا إِلى رَبِّنا لَمُنْقَلِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4340, N'وَ جَعَلُوا لَهُ مِنْ عِبادِهِ جُزْءاً إِنَّ الْإِنْسانَ لَكَفُورٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4341, N'أَمِ اتَّخَذَ مِمَّا يَخْلُقُ بَناتٍ وَ أَصْفاكُمْ بِالْبَنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4342, N'وَ إِذا بُشِّرَ أَحَدُهُمْ بِما ضَرَبَ لِلرَّحْمنِ مَثَلاً ظَلَّ وَجْهُهُ مُسْوَدًّا وَ هُوَ كَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4343, N'أَ وَ مَنْ يُنَشَّؤُا فِي الْحِلْيَةِ وَ هُوَ فِي الْخِصامِ غَيْرُ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4344, N'وَ جَعَلُوا الْمَلائِكَةَ الَّذِينَ هُمْ عِبادُ الرَّحْمنِ إِناثاً أَ شَهِدُوا خَلْقَهُمْ سَتُكْتَبُ شَهادَتُهُمْ وَ يُسْئَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4345, N'وَ قالُوا لَوْ شاءَ الرَّحْمنُ ما عَبَدْناهُمْ ما لَهُمْ بِذلِكَ مِنْ عِلْمٍ إِنْ هُمْ إِلاَّ يَخْرُصُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4346, N'أَمْ آتَيْناهُمْ كِتاباً مِنْ قَبْلِهِ فَهُمْ بِهِ مُسْتَمْسِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4347, N'بَلْ قالُوا إِنَّا وَجَدْنا آباءَنا عَلى أُمَّةٍ وَ إِنَّا عَلى آثارِهِمْ مُهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4348, N'وَ كَذلِكَ ما أَرْسَلْنا مِنْ قَبْلِكَ فِي قَرْيَةٍ مِنْ نَذِيرٍ إِلاَّ قالَ مُتْرَفُوها إِنَّا وَجَدْنا آباءَنا عَلى أُمَّةٍ وَ إِنَّا عَلى آثارِهِمْ مُقْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4349, N'قالَ أَ وَ لَوْ جِئْتُكُمْ بِأَهْدى مِمَّا وَجَدْتُمْ عَلَيْهِ آباءَكُمْ قالُوا إِنَّا بِما أُرْسِلْتُمْ بِهِ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4350, N'فَانْتَقَمْنا مِنْهُمْ فَانْظُرْ كَيْفَ كانَ عاقِبَةُ الْمُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4351, N'وَ إِذْ قالَ إِبْراهِيمُ لِأَبِيهِ وَ قَوْمِهِ إِنَّنِي بَراءٌ مِمَّا تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4352, N'إِلاَّ الَّذِي فَطَرَنِي فَإِنَّهُ سَيَهْدِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4353, N'وَ جَعَلَها كَلِمَةً باقِيَةً فِي عَقِبِهِ لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4354, N'بَلْ مَتَّعْتُ هؤُلاءِ وَ آباءَهُمْ حَتَّى جاءَهُمُ الْحَقُّ وَ رَسُولٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4355, N'وَ لَمَّا جاءَهُمُ الْحَقُّ قالُوا هذا سِحْرٌ وَ إِنَّا بِهِ كافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4356, N'وَ قالُوا لَوْ لا نُزِّلَ هذَا الْقُرْآنُ عَلى رَجُلٍ مِنَ الْقَرْيَتَيْنِ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4357, N'أَ هُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَ نَحْنُ قَسَمْنا بَيْنَهُمْ مَعِيشَتَهُمْ فِي الْحَياةِ الدُّنْيا وَ رَفَعْنا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجاتٍ لِيَتَّخِذَ بَعْضُهُمْ بَعْضاً سُخْرِيًّا وَ رَحْمَتُ رَبِّكَ خَيْرٌ مِمَّا يَجْمَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4358, N'وَ لَوْ لا أَنْ يَكُونَ النَّاسُ أُمَّةً واحِدَةً لَجَعَلْنا لِمَنْ يَكْفُرُ بِالرَّحْمنِ لِبُيُوتِهِمْ سُقُفاً مِنْ فِضَّةٍ وَ مَعارِجَ عَلَيْها يَظْهَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4359, N'وَ لِبُيُوتِهِمْ أَبْواباً وَ سُرُراً عَلَيْها يَتَّكِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4360, N'وَ زُخْرُفاً وَ إِنْ كُلُّ ذلِكَ لَمَّا مَتاعُ الْحَياةِ الدُّنْيا وَ الْآخِرَةُ عِنْدَ رَبِّكَ لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4361, N'وَ مَنْ يَعْشُ عَنْ ذِكْرِ الرَّحْمنِ نُقَيِّضْ لَهُ شَيْطاناً فَهُوَ لَهُ قَرِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4362, N'وَ إِنَّهُمْ لَيَصُدُّونَهُمْ عَنِ السَّبِيلِ وَ يَحْسَبُونَ أَنَّهُمْ مُهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4363, N'حَتَّى إِذا جاءَنا قالَ يا لَيْتَ بَيْنِي وَ بَيْنَكَ بُعْدَ الْمَشْرِقَيْنِ فَبِئْسَ الْقَرِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4364, N'وَ لَنْ يَنْفَعَكُمُ الْيَوْمَ إِذْ ظَلَمْتُمْ أَنَّكُمْ فِي الْعَذابِ مُشْتَرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4365, N'أَ فَأَنْتَ تُسْمِعُ الصُّمَّ أَوْ تَهْدِي الْعُمْيَ وَ مَنْ كانَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4366, N'فَإِمَّا نَذْهَبَنَّ بِكَ فَإِنَّا مِنْهُمْ مُنْتَقِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4367, N'أَوْ نُرِيَنَّكَ الَّذِي وَعَدْناهُمْ فَإِنَّا عَلَيْهِمْ مُقْتَدِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4368, N'فَاسْتَمْسِكْ بِالَّذِي أُوحِيَ إِلَيْكَ إِنَّكَ عَلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4369, N'وَ إِنَّهُ لَذِكْرٌ لَكَ وَ لِقَوْمِكَ وَ سَوْفَ تُسْئَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4370, N'وَ سْئَلْ مَنْ أَرْسَلْنا مِنْ قَبْلِكَ مِنْ رُسُلِنا أَ جَعَلْنا مِنْ دُونِ الرَّحْمنِ آلِهَةً يُعْبَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4371, N'وَ لَقَدْ أَرْسَلْنا مُوسى بِآياتِنا إِلى فِرْعَوْنَ وَ مَلاَئِهِ فَقالَ إِنِّي رَسُولُ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4372, N'فَلَمَّا جاءَهُمْ بِآياتِنا إِذا هُمْ مِنْها يَضْحَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4373, N'وَ ما نُرِيهِمْ مِنْ آيَةٍ إِلاَّ هِيَ أَكْبَرُ مِنْ أُخْتِها وَ أَخَذْناهُمْ بِالْعَذابِ لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4374, N'وَ قالُوا يا أَيُّهَا السَّاحِرُ ادْعُ لَنا رَبَّكَ بِما عَهِدَ عِنْدَكَ إِنَّنا لَمُهْتَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4375, N'فَلَمَّا كَشَفْنا عَنْهُمُ الْعَذابَ إِذا هُمْ يَنْكُثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4376, N'وَ نادى فِرْعَوْنُ فِي قَوْمِهِ قالَ يا قَوْمِ أَ لَيْسَ لِي مُلْكُ مِصْرَ وَ هذِهِ الْأَنْهارُ تَجْرِي مِنْ تَحْتِي أَ فَلا تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4377, N'أَمْ أَنَا خَيْرٌ مِنْ هذَا الَّذِي هُوَ مَهِينٌ وَ لا يَكادُ يُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4378, N'فَلَوْ لا أُلْقِيَ عَلَيْهِ أَسْوِرَةٌ مِنْ ذَهَبٍ أَوْ جاءَ مَعَهُ الْمَلائِكَةُ مُقْتَرِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4379, N'فَاسْتَخَفَّ قَوْمَهُ فَأَطاعُوهُ إِنَّهُمْ كانُوا قَوْماً فاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4380, N'فَلَمَّا آسَفُونا انْتَقَمْنا مِنْهُمْ فَأَغْرَقْناهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4381, N'فَجَعَلْناهُمْ سَلَفاً وَ مَثَلاً لِلْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4382, N'وَ لَمَّا ضُرِبَ ابْنُ مَرْيَمَ مَثَلاً إِذا قَوْمُكَ مِنْهُ يَصِدُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4383, N'وَ قالُوا أَ آلِهَتُنا خَيْرٌ أَمْ هُوَ ما ضَرَبُوهُ لَكَ إِلاَّ جَدَلاً بَلْ هُمْ قَوْمٌ خَصِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4384, N'إِنْ هُوَ إِلاَّ عَبْدٌ أَنْعَمْنا عَلَيْهِ وَ جَعَلْناهُ مَثَلاً لِبَنِي إِسْرائِيلَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4385, N'وَ لَوْ نَشاءُ لَجَعَلْنا مِنْكُمْ مَلائِكَةً فِي الْأَرْضِ يَخْلُفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4386, N'وَ إِنَّهُ لَعِلْمٌ لِلسَّاعَةِ فَلا تَمْتَرُنَّ بِها وَ اتَّبِعُونِ هذا صِراطٌ مُسْتَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4387, N'وَ لا يَصُدَّنَّكُمُ الشَّيْطانُ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4388, N'وَ لَمَّا جاءَ عِيسى بِالْبَيِّناتِ قالَ قَدْ جِئْتُكُمْ بِالْحِكْمَةِ وَ لِأُبَيِّنَ لَكُمْ بَعْضَ الَّذِي تَخْتَلِفُونَ فِيهِ فَاتَّقُوا اللَّهَ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4389, N'إِنَّ اللَّهَ هُوَ رَبِّي وَ رَبُّكُمْ فَاعْبُدُوهُ هذا صِراطٌ مُسْتَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4390, N'فَاخْتَلَفَ الْأَحْزابُ مِنْ بَيْنِهِمْ فَوَيْلٌ لِلَّذِينَ ظَلَمُوا مِنْ عَذابِ يَوْمٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4391, N'هَلْ يَنْظُرُونَ إِلاَّ السَّاعَةَ أَنْ تَأْتِيَهُمْ بَغْتَةً وَ هُمْ لا يَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4392, N'الْأَخِلاَّءُ يَوْمَئِذٍ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ إِلاَّ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4393, N'يا عِبادِ لا خَوْفٌ عَلَيْكُمُ الْيَوْمَ وَ لا أَنْتُمْ تَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4394, N'الَّذِينَ آمَنُوا بِآياتِنا وَ كانُوا مُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4395, N'ادْخُلُوا الْجَنَّةَ أَنْتُمْ وَ أَزْواجُكُمْ تُحْبَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4396, N'يُطافُ عَلَيْهِمْ بِصِحافٍ مِنْ ذَهَبٍ وَ أَكْوابٍ وَ فِيها ما تَشْتَهِيهِ الْأَنْفُسُ وَ تَلَذُّ الْأَعْيُنُ وَ أَنْتُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4397, N'وَ تِلْكَ الْجَنَّةُ الَّتِي أُورِثْتُمُوها بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4398, N'لَكُمْ فِيها فاكِهَةٌ كَثِيرَةٌ مِنْها تَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4399, N'إِنَّ الْمُجْرِمِينَ فِي عَذابِ جَهَنَّمَ خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4400, N'لا يُفَتَّرُ عَنْهُمْ وَ هُمْ فِيهِ مُبْلِسُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4401, N'وَ ما ظَلَمْناهُمْ وَ لكِنْ كانُوا هُمُ الظَّالِمِينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4402, N'وَ نادَوْا يا مالِكُ لِيَقْضِ عَلَيْنا رَبُّكَ قالَ إِنَّكُمْ ماكِثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4403, N'لَقَدْ جِئْناكُمْ بِالْحَقِّ وَ لكِنَّ أَكْثَرَكُمْ لِلْحَقِّ كارِهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4404, N'أَمْ أَبْرَمُوا أَمْراً فَإِنَّا مُبْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4405, N'أَمْ يَحْسَبُونَ أَنَّا لا نَسْمَعُ سِرَّهُمْ وَ نَجْواهُمْ بَلى وَ رُسُلُنا لَدَيْهِمْ يَكْتُبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4406, N'قُلْ إِنْ كانَ لِلرَّحْمنِ وَلَدٌ فَأَنَا أَوَّلُ الْعابِدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4407, N'سُبْحانَ رَبِّ السَّماواتِ وَ الْأَرْضِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4408, N'فَذَرْهُمْ يَخُوضُوا وَ يَلْعَبُوا حَتَّى يُلاقُوا يَوْمَهُمُ الَّذِي يُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4409, N'وَ هُوَ الَّذِي فِي السَّماءِ إِلهٌ وَ فِي الْأَرْضِ إِلهٌ وَ هُوَ الْحَكِيمُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4410, N'وَ تَبارَكَ الَّذِي لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُما وَ عِنْدَهُ عِلْمُ السَّاعَةِ وَ إِلَيْهِ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4411, N'وَ لا يَمْلِكُ الَّذِينَ يَدْعُونَ مِنْ دُونِهِ الشَّفاعَةَ إِلاَّ مَنْ شَهِدَ بِالْحَقِّ وَ هُمْ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4412, N'وَ لَئِنْ سَأَلْتَهُمْ مَنْ خَلَقَهُمْ لَيَقُولُنَّ اللَّهُ فَأَنَّى يُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4413, N'وَ قِيلِهِ يا رَبِّ إِنَّ هؤُلاءِ قَوْمٌ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4414, N'فَاصْفَحْ عَنْهُمْ وَ قُلْ سَلامٌ فَسَوْفَ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4415, N'حم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4416, N'وَ الْكِتابِ الْمُبِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4417, N'إِنَّا أَنْزَلْناهُ فِي لَيْلَةٍ مُبارَكَةٍ إِنَّا كُنَّا مُنْذِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4418, N'فِيها يُفْرَقُ كُلُّ أَمْرٍ حَكِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4419, N'أَمْراً مِنْ عِنْدِنا إِنَّا كُنَّا مُرْسِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4420, N'رَحْمَةً مِنْ رَبِّكَ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4421, N'رَبِّ السَّماواتِ وَ الْأَرْضِ وَ ما بَيْنَهُما إِنْ كُنْتُمْ مُوقِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4422, N'لا إِلهَ إِلاَّ هُوَ يُحْيِي وَ يُمِيتُ رَبُّكُمْ وَ رَبُّ آبائِكُمُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4423, N'بَلْ هُمْ فِي شَكٍّ يَلْعَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4424, N'فَارْتَقِبْ يَوْمَ تَأْتِي السَّماءُ بِدُخانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4425, N'يَغْشَى النَّاسَ هذا عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4426, N'رَبَّنَا اكْشِفْ عَنَّا الْعَذابَ إِنَّا مُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4427, N'أَنَّى لَهُمُ الذِّكْرى وَ قَدْ جاءَهُمْ رَسُولٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4428, N'ثُمَّ تَوَلَّوْا عَنْهُ وَ قالُوا مُعَلَّمٌ مَجْنُونٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4429, N'إِنَّا كاشِفُوا الْعَذابِ قَلِيلاً إِنَّكُمْ عائِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4430, N'يَوْمَ نَبْطِشُ الْبَطْشَةَ الْكُبْرى إِنَّا مُنْتَقِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4431, N'وَ لَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَ جاءَهُمْ رَسُولٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4432, N'أَنْ أَدُّوا إِلَيَّ عِبادَ اللَّهِ إِنِّي لَكُمْ رَسُولٌ أَمِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4433, N'وَ أَنْ لا تَعْلُوا عَلَى اللَّهِ إِنِّي آتِيكُمْ بِسُلْطانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4434, N'وَ إِنِّي عُذْتُ بِرَبِّي وَ رَبِّكُمْ أَنْ تَرْجُمُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4435, N'وَ إِنْ لَمْ تُؤْمِنُوا لِي فَاعْتَزِلُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4436, N'فَدَعا رَبَّهُ أَنَّ هؤُلاءِ قَوْمٌ مُجْرِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4437, N'فَأَسْرِ بِعِبادِي لَيْلاً إِنَّكُمْ مُتَّبَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4438, N'وَ اتْرُكِ الْبَحْرَ رَهْواً إِنَّهُمْ جُنْدٌ مُغْرَقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4439, N'كَمْ تَرَكُوا مِنْ جَنَّاتٍ وَ عُيُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4440, N'وَ زُرُوعٍ وَ مَقامٍ كَرِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4441, N'وَ نَعْمَةٍ كانُوا فِيها فاكِهِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4442, N'كَذلِكَ وَ أَوْرَثْناها قَوْماً آخَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4443, N'فَما بَكَتْ عَلَيْهِمُ السَّماءُ وَ الْأَرْضُ وَ ما كانُوا مُنْظَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4444, N'وَ لَقَدْ نَجَّيْنا بَنِي إِسْرائِيلَ مِنَ الْعَذابِ الْمُهِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4445, N'مِنْ فِرْعَوْنَ إِنَّهُ كانَ عالِياً مِنَ الْمُسْرِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4446, N'وَ لَقَدِ اخْتَرْناهُمْ عَلى عِلْمٍ عَلَى الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4447, N'وَ آتَيْناهُمْ مِنَ الْآياتِ ما فِيهِ بَلؤُا مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4448, N'إِنَّ هؤُلاءِ لَيَقُولُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4449, N'إِنْ هِيَ إِلاَّ مَوْتَتُنَا الْأُولى وَ ما نَحْنُ بِمُنْشَرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4450, N'فَأْتُوا بِآبائِنا إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4451, N'أَ هُمْ خَيْرٌ أَمْ قَوْمُ تُبَّعٍ وَ الَّذِينَ مِنْ قَبْلِهِمْ أَهْلَكْناهُمْ إِنَّهُمْ كانُوا مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4452, N'وَ ما خَلَقْنَا السَّماواتِ وَ الْأَرْضَ وَ ما بَيْنَهُما لاعِبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4453, N'ما خَلَقْناهُما إِلاَّ بِالْحَقِّ وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4454, N'إِنَّ يَوْمَ الْفَصْلِ مِيقاتُهُمْ أَجْمَعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4455, N'يَوْمَ لا يُغْنِي مَوْلًى عَنْ مَوْلًى شَيْئاً وَ لا هُمْ يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4456, N'إِلاَّ مَنْ رَحِمَ اللَّهُ إِنَّهُ هُوَ الْعَزِيزُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4457, N'إِنَّ شَجَرَةَ الزَّقُّومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4458, N'طَعامُ الْأَثِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4459, N'كَالْمُهْلِ يَغْلِي فِي الْبُطُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4460, N'كَغَلْيِ الْحَمِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4461, N'خُذُوهُ فَاعْتِلُوهُ إِلى سَواءِ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4462, N'ثُمَّ صُبُّوا فَوْقَ رَأْسِهِ مِنْ عَذابِ الْحَمِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4463, N'ذُقْ إِنَّكَ أَنْتَ الْعَزِيزُ الْكَرِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4464, N'إِنَّ هذا ما كُنْتُمْ بِهِ تَمْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4465, N'إِنَّ الْمُتَّقِينَ فِي مَقامٍ أَمِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4466, N'فِي جَنَّاتٍ وَ عُيُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4467, N'يَلْبَسُونَ مِنْ سُندُسٍ وَ إِسْتَبْرَقٍ مُتَقابِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4468, N'كَذلِكَ وَ زَوَّجْناهُمْ بِحُورٍ عِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4469, N'يَدْعُونَ فِيها بِكُلِّ فاكِهَةٍ آمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4470, N'لا يَذُوقُونَ فِيهَا الْمَوْتَ إِلاَّ الْمَوْتَةَ الْأُولى وَ وَقاهُمْ عَذابَ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4471, N'فَضْلاً مِنْ رَبِّكَ ذلِكَ هُوَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4472, N'فَإِنَّما يَسَّرْناهُ بِلِسانِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4473, N'فَارْتَقِبْ إِنَّهُمْ مُرْتَقِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4474, N'حم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4475, N'تَنْزِيلُ الْكِتابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4476, N'إِنَّ فِي السَّماواتِ وَ الْأَرْضِ لَآياتٍ لِلْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4477, N'وَ فِي خَلْقِكُمْ وَ ما يَبُثُّ مِنْ دابَّةٍ آياتٌ لِقَوْمٍ يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4478, N'وَ اخْتِلافِ اللَّيْلِ وَ النَّهارِ وَ ما أَنْزَلَ اللَّهُ مِنَ السَّماءِ مِنْ رِزْقٍ فَأَحْيا بِهِ الْأَرْضَ بَعْدَ مَوْتِها وَ تَصْرِيفِ الرِّياحِ آياتٌ لِقَوْمٍ يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4479, N'تِلْكَ آياتُ اللَّهِ نَتْلُوها عَلَيْكَ بِالْحَقِّ فَبِأَيِّ حَدِيثٍ بَعْدَ اللَّهِ وَ آياتِهِ يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4480, N'وَيْلٌ لِكُلِّ أَفَّاكٍ أَثِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4481, N'يَسْمَعُ آياتِ اللَّهِ تُتْلى عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِراً كَأَنْ لَمْ يَسْمَعْها فَبَشِّرْهُ بِعَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4482, N'وَ إِذا عَلِمَ مِنْ آياتِنا شَيْئاً اتَّخَذَها هُزُواً أُولئِكَ لَهُمْ عَذابٌ مُهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4483, N'مِنْ وَرائِهِمْ جَهَنَّمُ وَ لا يُغْنِي عَنْهُمْ ما كَسَبُوا شَيْئاً وَ لا مَا اتَّخَذُوا مِنْ دُونِ اللَّهِ أَوْلِياءَ وَ لَهُمْ عَذابٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4484, N'هذا هُدىً وَ الَّذِينَ كَفَرُوا بِآياتِ رَبِّهِمْ لَهُمْ عَذابٌ مِنْ رِجْزٍ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4485, N'اللَّهُ الَّذِي سَخَّرَ لَكُمُ الْبَحْرَ لِتَجْرِيَ الْفُلْكُ فِيهِ بِأَمْرِهِ وَ لِتَبْتَغُوا مِنْ فَضْلِهِ وَ لَعَلَّكُمْ تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4486, N'وَ سَخَّرَ لَكُمْ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ جَمِيعاً مِنْهُ إِنَّ فِي ذلِكَ لَآياتٍ لِقَوْمٍ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4487, N'قُلْ لِلَّذِينَ آمَنُوا يَغْفِرُوا لِلَّذِينَ لا يَرْجُونَ أَيَّامَ اللَّهِ لِيَجْزِيَ قَوْماً بِما كانُوا يَكْسِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4488, N'مَنْ عَمِلَ صالِحاً فَلِنَفْسِهِ وَ مَنْ أَساءَ فَعَلَيْها ثُمَّ إِلى رَبِّكُمْ تُرْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4489, N'وَ لَقَدْ آتَيْنا بَنِي إِسْرائِيلَ الْكِتابَ وَ الْحُكْمَ وَ النُّبُوَّةَ وَ رَزَقْناهُمْ مِنَ الطَّيِّباتِ وَ فَضَّلْناهُمْ عَلَى الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4490, N'وَ آتَيْناهُمْ بَيِّناتٍ مِنَ الْأَمْرِ فَمَا اخْتَلَفُوا إِلاَّ مِنْ بَعْدِ ما جاءَهُمُ الْعِلْمُ بَغْياً بَيْنَهُمْ إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ يَوْمَ الْقِيامَةِ فِيما كانُوا فِيهِ يَخْتَلِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4491, N'ثُمَّ جَعَلْناكَ عَلى شَرِيعَةٍ مِنَ الْأَمْرِ فَاتَّبِعْها وَ لا تَتَّبِعْ أَهْواءَ الَّذِينَ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4492, N'إِنَّهُمْ لَنْ يُغْنُوا عَنْكَ مِنَ اللَّهِ شَيْئاً وَ إِنَّ الظَّالِمِينَ بَعْضُهُمْ أَوْلِياءُ بَعْضٍ وَ اللَّهُ وَلِيُّ الْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4493, N'هذا بَصائِرُ لِلنَّاسِ وَ هُدىً وَ رَحْمَةٌ لِقَوْمٍ يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4494, N'أَمْ حَسِبَ الَّذِينَ اجْتَرَحُوا السَّيِّئاتِ أَنْ نَجْعَلَهُمْ كَالَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ سَواءً مَحْياهُمْ وَ مَماتُهُمْ ساءَ ما يَحْكُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4495, N'وَ خَلَقَ اللَّهُ السَّماواتِ وَ الْأَرْضَ بِالْحَقِّ وَ لِتُجْزى كُلُّ نَفْسٍ بِما كَسَبَتْ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4496, N'أَ فَرَأَيْتَ مَنِ اتَّخَذَ إِلهَهُ هَواهُ وَ أَضَلَّهُ اللَّهُ عَلى عِلْمٍ وَ خَتَمَ عَلى سَمْعِهِ وَ قَلْبِهِ وَ جَعَلَ عَلى بَصَرِهِ غِشاوَةً فَمَنْ يَهْدِيهِ مِنْ بَعْدِ اللَّهِ أَ فَلا تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4497, N'وَ قالُوا ما هِيَ إِلاَّ حَياتُنَا الدُّنْيا نَمُوتُ وَ نَحْيا وَ ما يُهْلِكُنا إِلاَّ الدَّهْرُ وَ ما لَهُمْ بِذلِكَ مِنْ عِلْمٍ إِنْ هُمْ إِلاَّ يَظُنُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4498, N'وَ إِذا تُتْلى عَلَيْهِمْ آياتُنا بَيِّناتٍ ما كانَ حُجَّتَهُمْ إِلاَّ أَنْ قالُوا ائْتُوا بِآبائِنا إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4499, N'قُلِ اللَّهُ يُحْيِيكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يَجْمَعُكُمْ إِلى يَوْمِ الْقِيامَةِ لا رَيْبَ فِيهِ وَ لكِنَّ أَكْثَرَ النَّاسِ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4500, N'وَ لِلَّهِ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ يَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَخْسَرُ الْمُبْطِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4501, N'وَ تَرى كُلَّ أُمَّةٍ جاثِيَةً كُلُّ أُمَّةٍ تُدْعى إِلى كِتابِهَا الْيَوْمَ تُجْزَوْنَ ما كُنْتُمْ تَعْمَلُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4502, N'هذا كِتابُنا يَنْطِقُ عَلَيْكُمْ بِالْحَقِّ إِنَّا كُنَّا نَسْتَنْسِخُ ما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4503, N'فَأَمَّا الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ فَيُدْخِلُهُمْ رَبُّهُمْ فِي رَحْمَتِهِ ذلِكَ هُوَ الْفَوْزُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4504, N'وَ أَمَّا الَّذِينَ كَفَرُوا أَ فَلَمْ تَكُنْ آياتِي تُتْلى عَلَيْكُمْ فَاسْتَكْبَرْتُمْ وَ كُنْتُمْ قَوْماً مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4505, N'وَ إِذا قِيلَ إِنَّ وَعْدَ اللَّهِ حَقٌّ وَ السَّاعَةُ لا رَيْبَ فِيها قُلْتُمْ ما نَدْرِي مَا السَّاعَةُ إِنْ نَظُنُّ إِلاَّ ظَنًّا وَ ما نَحْنُ بِمُسْتَيْقِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4506, N'وَ بَدا لَهُمْ سَيِّئاتُ ما عَمِلُوا وَ حاقَ بِهِمْ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4507, N'وَ قِيلَ الْيَوْمَ نَنْساكُمْ كَما نَسِيتُمْ لِقاءَ يَوْمِكُمْ هذا وَ مَأْواكُمُ النَّارُ وَ ما لَكُمْ مِنْ ناصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4508, N'ذلِكُمْ بِأَنَّكُمُ اتَّخَذْتُمْ آياتِ اللَّهِ هُزُواً وَ غَرَّتْكُمُ الْحَياةُ الدُّنْيا فَالْيَوْمَ لا يُخْرَجُونَ مِنْها وَ لا هُمْ يُسْتَعْتَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4509, N'فَلِلَّهِ الْحَمْدُ رَبِّ السَّماواتِ وَ رَبِّ الْأَرْضِ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4510, N'وَ لَهُ الْكِبْرِياءُ فِي السَّماواتِ وَ الْأَرْضِ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4511, N'حم ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4512, N'تَنْزِيلُ الْكِتابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4513, N'ما خَلَقْنَا السَّماواتِ وَ الْأَرْضَ وَ ما بَيْنَهُما إِلاَّ بِالْحَقِّ وَ أَجَلٍ مُسَمًّى وَ الَّذِينَ كَفَرُوا عَمَّا أُنْذِرُوا مُعْرِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4514, N'قُلْ أَ رَأَيْتُمْ ما تَدْعُونَ مِنْ دُونِ اللَّهِ أَرُونِي ما ذا خَلَقُوا مِنَ الْأَرْضِ أَمْ لَهُمْ شِرْكٌ فِي السَّماواتِ ائْتُونِي بِكِتابٍ مِنْ قَبْلِ هذا أَوْ أَثارَةٍ مِنْ عِلْمٍ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4515, N'وَ مَنْ أَضَلُّ مِمَّنْ يَدْعُوا مِنْ دُونِ اللَّهِ مَنْ لا يَسْتَجِيبُ لَهُ إِلى يَوْمِ الْقِيامَةِ وَ هُمْ عَنْ دُعائِهِمْ غافِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4516, N'وَ إِذا حُشِرَ النَّاسُ كانُوا لَهُمْ أَعْداءً وَ كانُوا بِعِبادَتِهِمْ كافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4517, N'وَ إِذا تُتْلى عَلَيْهِمْ آياتُنا بَيِّناتٍ قالَ الَّذِينَ كَفَرُوا لِلْحَقِّ لَمَّا جاءَهُمْ هذا سِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4518, N'أَمْ يَقُولُونَ افْتَراهُ قُلْ إِنِ افْتَرَيْتُهُ فَلا تَمْلِكُونَ لِي مِنَ اللَّهِ شَيْئاً هُوَ أَعْلَمُ بِما تُفِيضُونَ فِيهِ كَفى بِهِ شَهِيداً بَيْنِي وَ بَيْنَكُمْ وَ هُوَ الْغَفُورُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4519, N'قُلْ ما كُنْتُ بِدْعاً مِنَ الرُّسُلِ وَ ما أَدْرِي ما يُفْعَلُ بِي وَ لا بِكُمْ إِنْ أَتَّبِعُ إِلاَّ ما يُوحى إِلَيَّ وَ ما أَنَا إِلاَّ نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4520, N'قُلْ أَ رَأَيْتُمْ إِنْ كانَ مِنْ عِنْدِ اللَّهِ وَ كَفَرْتُمْ بِهِ وَ شَهِدَ شاهِدٌ مِنْ بَنِي إِسْرائِيلَ عَلى مِثْلِهِ فَآمَنَ وَ اسْتَكْبَرْتُمْ إِنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4521, N'وَ قالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا لَوْ كانَ خَيْراً ما سَبَقُونا إِلَيْهِ وَ إِذْ لَمْ يَهْتَدُوا بِهِ فَسَيَقُولُونَ هذا إِفْكٌ قَدِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4522, N'وَ مِنْ قَبْلِهِ كِتابُ مُوسى إِماماً وَ رَحْمَةً وَ هذا كِتابٌ مُصَدِّقٌ لِساناً عَرَبِيًّا لِيُنْذِرَ الَّذِينَ ظَلَمُوا وَ بُشْرى لِلْمُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4523, N'إِنَّ الَّذِينَ قالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقامُوا فَلا خَوْفٌ عَلَيْهِمْ وَ لا هُمْ يَحْزَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4524, N'أُولئِكَ أَصْحابُ الْجَنَّةِ خالِدِينَ فِيها جَزاءً بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4525, N'وَ وَصَّيْنَا الْإِنْسانَ بِوالِدَيْهِ إِحْساناً حَمَلَتْهُ أُمُّهُ كُرْهاً وَ وَضَعَتْهُ كُرْهاً وَ حَمْلُهُ وَ فِصالُهُ ثَلاثُونَ شَهْراً حَتَّى إِذا بَلَغَ أَشُدَّهُ وَ بَلَغَ أَرْبَعِينَ سَنَةً قالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَ عَلى والِدَيَّ وَ أَنْ أَعْمَلَ صالِحاً تَرْضاهُ وَ أَصْلِحْ لِي فِي ذُرِّيَّتِي إِنِّي تُبْتُ إِلَيْكَ وَ إِنِّي مِنَ الْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4526, N'أُولئِكَ الَّذِينَ نَتَقَبَّلُ عَنْهُمْ أَحْسَنَ ما عَمِلُوا وَ نَتَجاوَزُ عَنْ سَيِّئاتِهِمْ فِي أَصْحابِ الْجَنَّةِ وَعْدَ الصِّدْقِ الَّذِي كانُوا يُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4527, N'وَ الَّذِي قالَ لِوالِدَيْهِ أُفٍّ لَكُما أَ تَعِدانِنِي أَنْ أُخْرَجَ وَ قَدْ خَلَتِ الْقُرُونُ مِنْ قَبْلِي وَ هُما يَسْتَغِيثانِ اللَّهَ وَيْلَكَ آمِنْ إِنَّ وَعْدَ اللَّهِ حَقٌّ فَيَقُولُ ما هذا إِلاَّ أَساطِيرُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4528, N'أُولئِكَ الَّذِينَ حَقَّ عَلَيْهِمُ الْقَوْلُ فِي أُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِنَ الْجِنِّ وَ الْإِنْسِ إِنَّهُمْ كانُوا خاسِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4529, N'وَ لِكُلٍّ دَرَجاتٌ مِمَّا عَمِلُوا وَ لِيُوَفِّيَهُمْ أَعْمالَهُمْ وَ هُمْ لا يُظْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4530, N'وَ يَوْمَ يُعْرَضُ الَّذِينَ كَفَرُوا عَلَى النَّارِ أَذْهَبْتُمْ طَيِّباتِكُمْ فِي حَياتِكُمُ الدُّنْيا وَ اسْتَمْتَعْتُمْ بِها فَالْيَوْمَ تُجْزَوْنَ عَذابَ الْهُونِ بِما كُنْتُمْ تَسْتَكْبِرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَ بِما كُنْتُمْ تَفْسُقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4531, N'وَ اذْكُرْ أَخا عادٍ إِذْ أَنْذَرَ قَوْمَهُ بِالْأَحْقافِ وَ قَدْ خَلَتِ النُّذُرُ مِنْ بَيْنِ يَدَيْهِ وَ مِنْ خَلْفِهِ أَلاَّ تَعْبُدُوا إِلاَّ اللَّهَ إِنِّي أَخافُ عَلَيْكُمْ عَذابَ يَوْمٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4532, N'قالُوا أَ جِئْتَنا لِتَأْفِكَنا عَنْ آلِهَتِنا فَأْتِنا بِما تَعِدُنا إِنْ كُنْتَ مِنَ الصَّادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4533, N'قالَ إِنَّمَا الْعِلْمُ عِنْدَ اللَّهِ وَ أُبَلِّغُكُمْ ما أُرْسِلْتُ بِهِ وَ لكِنِّي أَراكُمْ قَوْماً تَجْهَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4534, N'فَلَمَّا رَأَوْهُ عارِضاً مُسْتَقْبِلَ أَوْدِيَتِهِمْ قالُوا هذا عارِضٌ مُمْطِرُنا بَلْ هُوَ مَا اسْتَعْجَلْتُمْ بِهِ رِيحٌ فِيها عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4535, N'تُدَمِّرُ كُلَّ شَيْ‏ءٍ بِأَمْرِ رَبِّها فَأَصْبَحُوا لا يُرى إِلاَّ مَساكِنُهُمْ كَذلِكَ نَجْزِي الْقَوْمَ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4536, N'وَ لَقَدْ مَكَّنَّاهُمْ فِيما إِنْ مَكَّنَّاكُمْ فِيهِ وَ جَعَلْنا لَهُمْ سَمْعاً وَ أَبْصاراً وَ أَفْئِدَةً فَما أَغْنى عَنْهُمْ سَمْعُهُمْ وَ لا أَبْصارُهُمْ وَ لا أَفْئِدَتُهُمْ مِنْ شَيْ‏ءٍ إِذْ كانُوا يَجْحَدُونَ بِآياتِ اللَّهِ وَ حاقَ بِهِمْ ما كانُوا بِهِ يَسْتَهْزِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4537, N'وَ لَقَدْ أَهْلَكْنا ما حَوْلَكُمْ مِنَ الْقُرى وَ صَرَّفْنَا الْآياتِ لَعَلَّهُمْ يَرْجِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4538, N'فَلَوْ لا نَصَرَهُمُ الَّذِينَ اتَّخَذُوا مِنْ دُونِ اللَّهِ قُرْباناً آلِهَةً بَلْ ضَلُّوا عَنْهُمْ وَ ذلِكَ إِفْكُهُمْ وَ ما كانُوا يَفْتَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4539, N'وَ إِذْ صَرَفْنا إِلَيْكَ نَفَراً مِنَ الْجِنِّ يَسْتَمِعُونَ الْقُرْآنَ فَلَمَّا حَضَرُوهُ قالُوا أَنْصِتُوا فَلَمَّا قُضِيَ وَلَّوْا إِلى قَوْمِهِمْ مُنْذِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4540, N'قالُوا يا قَوْمَنا إِنَّا سَمِعْنا كِتاباً أُنْزِلَ مِنْ بَعْدِ مُوسى مُصَدِّقاً لِما بَيْنَ يَدَيْهِ يَهْدِي إِلَى الْحَقِّ وَ إِلى طَرِيقٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4541, N'يا قَوْمَنا أَجِيبُوا داعِيَ اللَّهِ وَ آمِنُوا بِهِ يَغْفِرْ لَكُمْ مِنْ ذُنُوبِكُمْ وَ يُجِرْكُمْ مِنْ عَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4542, N'وَ مَنْ لا يُجِبْ داعِيَ اللَّهِ فَلَيْسَ بِمُعْجِزٍ فِي الْأَرْضِ وَ لَيْسَ لَهُ مِنْ دُونِهِ أَوْلِياءُ أُولئِكَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4543, N'أَ وَ لَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ وَ لَمْ يَعْيَ بِخَلْقِهِنَّ بِقادِرٍ عَلى أَنْ يُحْيِيَ الْمَوْتى بَلى إِنَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4544, N'وَ يَوْمَ يُعْرَضُ الَّذِينَ كَفَرُوا عَلَى النَّارِ أَ لَيْسَ هذا بِالْحَقِّ قالُوا بَلى وَ رَبِّنا قالَ فَذُوقُوا الْعَذابَ بِما كُنْتُمْ تَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4545, N'فَاصْبِرْ كَما صَبَرَ أُولُوا الْعَزْمِ مِنَ الرُّسُلِ وَ لا تَسْتَعْجِلْ لَهُمْ كَأَنَّهُمْ يَوْمَ يَرَوْنَ ما يُوعَدُونَ لَمْ يَلْبَثُوا إِلاَّ ساعَةً مِنْ نَهارٍ بَلاغٌ فَهَلْ يُهْلَكُ إِلاَّ الْقَوْمُ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4546, N'الَّذِينَ كَفَرُوا وَ صَدُّوا عَنْ سَبِيلِ اللَّهِ أَضَلَّ أَعْمالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4547, N'وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ وَ آمَنُوا بِما نُزِّلَ عَلى مُحَمَّدٍ وَ هُوَ الْحَقُّ مِنْ رَبِّهِمْ كَفَّرَ عَنْهُمْ سَيِّئاتِهِمْ وَ أَصْلَحَ بالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4548, N'ذلِكَ بِأَنَّ الَّذِينَ كَفَرُوا اتَّبَعُوا الْباطِلَ وَ أَنَّ الَّذِينَ آمَنُوا اتَّبَعُوا الْحَقَّ مِنْ رَبِّهِمْ كَذلِكَ يَضْرِبُ اللَّهُ لِلنَّاسِ أَمْثالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4549, N'فَإِذا لَقِيتُمُ الَّذِينَ كَفَرُوا فَضَرْبَ الرِّقابِ حَتَّى إِذا أَثْخَنْتُمُوهُمْ فَشُدُّوا الْوَثاقَ فَإِمَّا مَنًّا بَعْدُ وَ إِمَّا فِداءً حَتَّى تَضَعَ الْحَرْبُ أَوْزارَها ذلِكَ وَ لَوْ يَشاءُ اللَّهُ لاَنْتَصَرَ مِنْهُمْ وَ لكِنْ لِيَبْلُوَا بَعْضَكُمْ بِبَعْضٍ وَ الَّذِينَ قُتِلُوا فِي سَبِيلِ اللَّهِ فَلَنْ يُضِلَّ أَعْمالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4550, N'سَيَهْدِيهِمْ وَ يُصْلِحُ بالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4551, N'وَ يُدْخِلُهُمُ الْجَنَّةَ عَرَّفَها لَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4552, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تَنْصُرُوا اللَّهَ يَنْصُرْكُمْ وَ يُثَبِّتْ أَقْدامَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4553, N'وَ الَّذِينَ كَفَرُوا فَتَعْساً لَهُمْ وَ أَضَلَّ أَعْمالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4554, N'ذلِكَ بِأَنَّهُمْ كَرِهُوا ما أَنْزَلَ اللَّهُ فَأَحْبَطَ أَعْمالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4555, N'أَ فَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كانَ عاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ دَمَّرَ اللَّهُ عَلَيْهِمْ وَ لِلْكافِرِينَ أَمْثالُها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4556, N'ذلِكَ بِأَنَّ اللَّهَ مَوْلَى الَّذِينَ آمَنُوا وَ أَنَّ الْكافِرِينَ لا مَوْلى لَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4557, N'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ وَ الَّذِينَ كَفَرُوا يَتَمَتَّعُونَ وَ يَأْكُلُونَ كَما تَأْكُلُ الْأَنْعامُ وَ النَّارُ مَثْوىً لَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4558, N'وَ كَأَيِّنْ مِنْ قَرْيَةٍ هِيَ أَشَدُّ قُوَّةً مِنْ قَرْيَتِكَ الَّتِي أَخْرَجَتْكَ أَهْلَكْناهُمْ فَلا ناصِرَ لَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4559, N'أَ فَمَنْ كانَ عَلى بَيِّنَةٍ مِنْ رَبِّهِ كَمَنْ زُيِّنَ لَهُ سُوءُ عَمَلِهِ وَ اتَّبَعُوا أَهْواءَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4560, N'مَثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ فِيها أَنْهارٌ مِنْ ماءٍ غَيْرِ آسِنٍ وَ أَنْهارٌ مِنْ لَبَنٍ لَمْ يَتَغَيَّرْ طَعْمُهُ وَ أَنْهارٌ مِنْ خَمْرٍ لَذَّةٍ لِلشَّارِبِينَ وَ أَنْهارٌ مِنْ عَسَلٍ مُصَفًّى وَ لَهُمْ فِيها مِنْ كُلِّ الثَّمَراتِ وَ مَغْفِرَةٌ مِنْ رَبِّهِمْ كَمَنْ هُوَ خالِدٌ فِي النَّارِ وَ سُقُوا ماءً حَمِيماً فَقَطَّعَ أَمْعاءَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4561, N'وَ مِنْهُمْ مَنْ يَسْتَمِعُ إِلَيْكَ حَتَّى إِذا خَرَجُوا مِنْ عِنْدِكَ قالُوا لِلَّذِينَ أُوتُوا الْعِلْمَ ما ذا قالَ آنِفاً أُولئِكَ الَّذِينَ طَبَعَ اللَّهُ عَلى قُلُوبِهِمْ وَ اتَّبَعُوا أَهْواءَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4562, N'وَ الَّذِينَ اهْتَدَوْا زادَهُمْ هُدىً وَ آتاهُمْ تَقْواهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4563, N'فَهَلْ يَنْظُرُونَ إِلاَّ السَّاعَةَ أَنْ تَأْتِيَهُمْ بَغْتَةً فَقَدْ جاءَ أَشْراطُها فَأَنَّى لَهُمْ إِذا جاءَتْهُمْ ذِكْراهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4564, N'فَاعْلَمْ أَنَّهُ لا إِلهَ إِلاَّ اللَّهُ وَ اسْتَغْفِرْ لِذَنْبِكَ وَ لِلْمُؤْمِنِينَ وَ الْمُؤْمِناتِ وَ اللَّهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَ مَثْواكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4565, N'وَ يَقُولُ الَّذِينَ آمَنُوا لَوْ لا نُزِّلَتْ سُورَةٌ فَإِذا أُنْزِلَتْ سُورَةٌ مُحْكَمَةٌ وَ ذُكِرَ فِيهَا الْقِتالُ رَأَيْتَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ يَنْظُرُونَ إِلَيْكَ نَظَرَ الْمَغْشِيِّ عَلَيْهِ مِنَ الْمَوْتِ فَأَوْلى لَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4566, N'طاعَةٌ وَ قَوْلٌ مَعْرُوفٌ فَإِذا عَزَمَ الْأَمْرُ فَلَوْ صَدَقُوا اللَّهَ لَكانَ خَيْراً لَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4567, N'فَهَلْ عَسَيْتُمْ إِنْ تَوَلَّيْتُمْ أَنْ تُفْسِدُوا فِي الْأَرْضِ وَ تُقَطِّعُوا أَرْحامَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4568, N'أُولئِكَ الَّذِينَ لَعَنَهُمُ اللَّهُ فَأَصَمَّهُمْ وَ أَعْمى أَبْصارَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4569, N'أَ فَلا يَتَدَبَّرُونَ الْقُرْآنَ أَمْ عَلى قُلُوبٍ أَقْفالُها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4570, N'إِنَّ الَّذِينَ ارْتَدُّوا عَلى أَدْبارِهِمْ مِنْ بَعْدِ ما تَبَيَّنَ لَهُمُ الْهُدَى الشَّيْطانُ سَوَّلَ لَهُمْ وَ أَمْلى لَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4571, N'ذلِكَ بِأَنَّهُمْ قالُوا لِلَّذِينَ كَرِهُوا ما نَزَّلَ اللَّهُ سَنُطِيعُكُمْ فِي بَعْضِ الْأَمْرِ وَ اللَّهُ يَعْلَمُ إِسْرارَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4572, N'فَكَيْفَ إِذا تَوَفَّتْهُمُ الْمَلائِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَ أَدْبارَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4573, N'ذلِكَ بِأَنَّهُمُ اتَّبَعُوا ما أَسْخَطَ اللَّهَ وَ كَرِهُوا رِضْوانَهُ فَأَحْبَطَ أَعْمالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4574, N'أَمْ حَسِبَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ أَنْ لَنْ يُخْرِجَ اللَّهُ أَضْغانَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4575, N'وَ لَوْ نَشاءُ لَأَرَيْناكَهُمْ فَلَعَرَفْتَهُمْ بِسِيماهُمْ وَ لَتَعْرِفَنَّهُمْ فِي لَحْنِ الْقَوْلِ وَ اللَّهُ يَعْلَمُ أَعْمالَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4576, N'وَ لَنَبْلُوَنَّكُمْ حَتَّى نَعْلَمَ الْمُجاهِدِينَ مِنْكُمْ وَ الصَّابِرِينَ وَ نَبْلُوَا أَخْبارَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4577, N'إِنَّ الَّذِينَ كَفَرُوا وَ صَدُّوا عَنْ سَبِيلِ اللَّهِ وَ شَاقُّوا الرَّسُولَ مِنْ بَعْدِ ما تَبَيَّنَ لَهُمُ الْهُدى لَنْ يَضُرُّوا اللَّهَ شَيْئاً وَ سَيُحْبِطُ أَعْمالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4578, N'يا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَ أَطِيعُوا الرَّسُولَ وَ لا تُبْطِلُوا أَعْمالَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4579, N'إِنَّ الَّذِينَ كَفَرُوا وَ صَدُّوا عَنْ سَبِيلِ اللَّهِ ثُمَّ ماتُوا وَ هُمْ كُفَّارٌ فَلَنْ يَغْفِرَ اللَّهُ لَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4580, N'فَلا تَهِنُوا وَ تَدْعُوا إِلَى السَّلْمِ وَ أَنْتُمُ الْأَعْلَوْنَ وَ اللَّهُ مَعَكُمْ وَ لَنْ يَتِرَكُمْ أَعْمالَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4581, N'إِنَّمَا الْحَياةُ الدُّنْيا لَعِبٌ وَ لَهْوٌ وَ إِنْ تُؤْمِنُوا وَ تَتَّقُوا يُؤْتِكُمْ أُجُورَكُمْ وَ لا يَسْئَلْكُمْ أَمْوالَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4582, N'إِنْ يَسْئَلْكُمُوها فَيُحْفِكُمْ تَبْخَلُوا وَ يُخْرِجْ أَضْغانَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4583, N'ها أَنْتُمْ هؤُلاءِ تُدْعَوْنَ لِتُنْفِقُوا فِي سَبِيلِ اللَّهِ فَمِنْكُمْ مَنْ يَبْخَلُ وَ مَنْ يَبْخَلْ فَإِنَّما يَبْخَلُ عَنْ نَفْسِهِ وَ اللَّهُ الْغَنِيُّ وَ أَنْتُمُ الْفُقَراءُ وَ إِنْ تَتَوَلَّوْا يَسْتَبْدِلْ قَوْماً غَيْرَكُمْ ثُمَّ لا يَكُونُوا أَمْثالَكُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4584, N'إِنَّا فَتَحْنا لَكَ فَتْحاً مُبِيناً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4585, N'لِيَغْفِرَ لَكَ اللَّهُ ما تَقَدَّمَ مِنْ ذَنْبِكَ وَ ما تَأَخَّرَ وَ يُتِمَّ نِعْمَتَهُ عَلَيْكَ وَ يَهْدِيَكَ صِراطاً مُسْتَقِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4586, N'وَ يَنْصُرَكَ اللَّهُ نَصْراً عَزِيزاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4587, N'هُوَ الَّذِي أَنْزَلَ السَّكِينَةَ فِي قُلُوبِ الْمُؤْمِنِينَ لِيَزْدادُوا إِيماناً مَعَ إِيمانِهِمْ وَ لِلَّهِ جُنُودُ السَّماواتِ وَ الْأَرْضِ وَ كانَ اللَّهُ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4588, N'لِيُدْخِلَ الْمُؤْمِنِينَ وَ الْمُؤْمِناتِ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها وَ يُكَفِّرَ عَنْهُمْ سَيِّئاتِهِمْ وَ كانَ ذلِكَ عِنْدَ اللَّهِ فَوْزاً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4589, N'وَ يُعَذِّبَ الْمُنافِقِينَ وَ الْمُنافِقاتِ وَ الْمُشْرِكِينَ وَ الْمُشْرِكاتِ الظَّانِّينَ بِاللَّهِ ظَنَّ السَّوْءِ عَلَيْهِمْ دائِرَةُ السَّوْءِ وَ غَضِبَ اللَّهُ عَلَيْهِمْ وَ لَعَنَهُمْ وَ أَعَدَّ لَهُمْ جَهَنَّمَ وَ ساءَتْ مَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4590, N'وَ لِلَّهِ جُنُودُ السَّماواتِ وَ الْأَرْضِ وَ كانَ اللَّهُ عَزِيزاً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4591, N'إِنَّا أَرْسَلْناكَ شاهِداً وَ مُبَشِّراً وَ نَذِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4592, N'لِتُؤْمِنُوا بِاللَّهِ وَ رَسُولِهِ وَ تُعَزِّرُوهُ وَ تُوَقِّرُوهُ وَ تُسَبِّحُوهُ بُكْرَةً وَ أَصِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4593, N'إِنَّ الَّذِينَ يُبايِعُونَكَ إِنَّما يُبايِعُونَ اللَّهَ يَدُ اللَّهِ فَوْقَ أَيْدِيهِمْ فَمَنْ نَكَثَ فَإِنَّما يَنْكُثُ عَلى نَفْسِهِ وَ مَنْ أَوْفى بِما عاهَدَ عَلَيْهُ اللَّهَ فَسَيُؤْتِيهِ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4594, N'سَيَقُولُ لَكَ الْمُخَلَّفُونَ مِنَ الْأَعْرابِ شَغَلَتْنا أَمْوالُنا وَ أَهْلُونا فَاسْتَغْفِرْ لَنا يَقُولُونَ بِأَلْسِنَتِهِمْ ما لَيْسَ فِي قُلُوبِهِمْ قُلْ فَمَنْ يَمْلِكُ لَكُمْ مِنَ اللَّهِ شَيْئاً إِنْ أَرادَ بِكُمْ ضَرًّا أَوْ أَرادَ بِكُمْ نَفْعاً بَلْ كانَ اللَّهُ بِما تَعْمَلُونَ خَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4595, N'بَلْ ظَنَنْتُمْ أَنْ لَنْ يَنْقَلِبَ الرَّسُولُ وَ الْمُؤْمِنُونَ إِلى أَهْلِيهِمْ أَبَداً وَ زُيِّنَ ذلِكَ فِي قُلُوبِكُمْ وَ ظَنَنْتُمْ ظَنَّ السَّوْءِ وَ كُنْتُمْ قَوْماً بُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4596, N'وَ مَنْ لَمْ يُؤْمِنْ بِاللَّهِ وَ رَسُولِهِ فَإِنَّا أَعْتَدْنا لِلْكافِرِينَ سَعِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4597, N'وَ لِلَّهِ مُلْكُ السَّماواتِ وَ الْأَرْضِ يَغْفِرُ لِمَنْ يَشاءُ وَ يُعَذِّبُ مَنْ يَشاءُ وَ كانَ اللَّهُ غَفُوراً رَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4598, N'سَيَقُولُ الْمُخَلَّفُونَ إِذَا انْطَلَقْتُمْ إِلى مَغانِمَ لِتَأْخُذُوها ذَرُونا نَتَّبِعْكُمْ يُرِيدُونَ أَنْ يُبَدِّلُوا كَلامَ اللَّهِ قُلْ لَنْ تَتَّبِعُونا كَذلِكُمْ قالَ اللَّهُ مِنْ قَبْلُ فَسَيَقُولُونَ بَلْ تَحْسُدُونَنا بَلْ كانُوا لا يَفْقَهُونَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4599, N'قُلْ لِلْمُخَلَّفِينَ مِنَ الْأَعْرابِ سَتُدْعَوْنَ إِلى قَوْمٍ أُولِي بَأْسٍ شَدِيدٍ تُقاتِلُونَهُمْ أَوْ يُسْلِمُونَ فَإِنْ تُطِيعُوا يُؤْتِكُمُ اللَّهُ أَجْراً حَسَناً وَ إِنْ تَتَوَلَّوْا كَما تَوَلَّيْتُمْ مِنْ قَبْلُ يُعَذِّبْكُمْ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4600, N'لَيْسَ عَلَى الْأَعْمى حَرَجٌ وَ لا عَلَى الْأَعْرَجِ حَرَجٌ وَ لا عَلَى الْمَرِيضِ حَرَجٌ وَ مَنْ يُطِعِ اللَّهَ وَ رَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ وَ مَنْ يَتَوَلَّ يُعَذِّبْهُ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4601, N'لَقَدْ رَضِيَ اللَّهُ عَنِ الْمُؤْمِنِينَ إِذْ يُبايِعُونَكَ تَحْتَ الشَّجَرَةِ فَعَلِمَ ما فِي قُلُوبِهِمْ فَأَنْزَلَ السَّكِينَةَ عَلَيْهِمْ وَ أَثابَهُمْ فَتْحاً قَرِيباً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4602, N'وَ مَغانِمَ كَثِيرَةً يَأْخُذُونَها وَ كانَ اللَّهُ عَزِيزاً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4603, N'وَعَدَكُمُ اللَّهُ مَغانِمَ كَثِيرَةً تَأْخُذُونَها فَعَجَّلَ لَكُمْ هذِهِ وَ كَفَّ أَيْدِيَ النَّاسِ عَنْكُمْ وَ لِتَكُونَ آيَةً لِلْمُؤْمِنِينَ وَ يَهْدِيَكُمْ صِراطاً مُسْتَقِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4604, N'وَ أُخْرى لَمْ تَقْدِرُوا عَلَيْها قَدْ أَحاطَ اللَّهُ بِها وَ كانَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4605, N'وَ لَوْ قاتَلَكُمُ الَّذِينَ كَفَرُوا لَوَلَّوُا الْأَدْبارَ ثُمَّ لا يَجِدُونَ وَلِيًّا وَ لا نَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4606, N'سُنَّةَ اللَّهِ الَّتِي قَدْ خَلَتْ مِنْ قَبْلُ وَ لَنْ تَجِدَ لِسُنَّةِ اللَّهِ تَبْدِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4607, N'وَ هُوَ الَّذِي كَفَّ أَيْدِيَهُمْ عَنْكُمْ وَ أَيْدِيَكُمْ عَنْهُمْ بِبَطْنِ مَكَّةَ مِنْ بَعْدِ أَنْ أَظْفَرَكُمْ عَلَيْهِمْ وَ كانَ اللَّهُ بِما تَعْمَلُونَ بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4608, N'هُمُ الَّذِينَ كَفَرُوا وَ صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرامِ وَ الْهَدْيَ مَعْكُوفاً أَنْ يَبْلُغَ مَحِلَّهُ وَ لَوْ لا رِجالٌ مُؤْمِنُونَ وَ نِساءٌ مُؤْمِناتٌ لَمْ تَعْلَمُوهُمْ أَنْ تَطَؤُهُمْ فَتُصِيبَكُمْ مِنْهُمْ مَعَرَّةٌ بِغَيْرِ عِلْمٍ لِيُدْخِلَ اللَّهُ فِي رَحْمَتِهِ مَنْ يَشاءُ لَوْ تَزَيَّلُوا لَعَذَّبْنَا الَّذِينَ كَفَرُوا مِنْهُمْ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4609, N'إِذْ جَعَلَ الَّذِينَ كَفَرُوا فِي قُلُوبِهِمُ الْحَمِيَّةَ حَمِيَّةَ الْجاهِلِيَّةِ فَأَنْزَلَ اللَّهُ سَكِينَتَهُ عَلى رَسُولِهِ وَ عَلَى الْمُؤْمِنِينَ وَ أَلْزَمَهُمْ كَلِمَةَ التَّقْوى وَ كانُوا أَحَقَّ بِها وَ أَهْلَها وَ كانَ اللَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4610, N'لَقَدْ صَدَقَ اللَّهُ رَسُولَهُ الرُّؤْيا بِالْحَقِّ لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرامَ إِنْ شاءَ اللَّهُ آمِنِينَ مُحَلِّقِينَ رُؤُسَكُمْ وَ مُقَصِّرِينَ لا تَخافُونَ فَعَلِمَ ما لَمْ تَعْلَمُوا فَجَعَلَ مِنْ دُونِ ذلِكَ فَتْحاً قَرِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4611, N'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدى وَ دِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَ كَفى بِاللَّهِ شَهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4612, N'مُحَمَّدٌ رَسُولُ اللَّهِ وَ الَّذِينَ مَعَهُ أَشِدَّاءُ عَلَى الْكُفَّارِ رُحَماءُ بَيْنَهُمْ تَراهُمْ رُكَّعاً سُجَّداً يَبْتَغُونَ فَضْلاً مِنَ اللَّهِ وَ رِضْواناً سِيماهُمْ فِي وُجُوهِهِمْ مِنْ أَثَرِ السُّجُودِ ذلِكَ مَثَلُهُمْ فِي التَّوْراةِ وَ مَثَلُهُمْ فِي الْإِنْجِيلِ كَزَرْعٍ أَخْرَجَ شَطْأَهُ فَآزَرَهُ فَاسْتَغْلَظَ فَاسْتَوى عَلى سُوقِهِ يُعْجِبُ الزُّرَّاعَ لِيَغِيظَ بِهِمُ الْكُفَّارَ وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ مِنْهُمْ مَغْفِرَةً وَ أَجْراً عَظِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4613, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تُقَدِّمُوا بَيْنَ يَدَيِ اللَّهِ وَ رَسُولِهِ وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4614, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَرْفَعُوا أَصْواتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ وَ لا تَجْهَرُوا لَهُ بِالْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ أَنْ تَحْبَطَ أَعْمالُكُمْ وَ أَنْتُمْ لا تَشْعُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4615, N'إِنَّ الَّذِينَ يَغُضُّونَ أَصْواتَهُمْ عِنْدَ رَسُولِ اللَّهِ أُولئِكَ الَّذِينَ امْتَحَنَ اللَّهُ قُلُوبَهُمْ لِلتَّقْوى لَهُمْ مَغْفِرَةٌ وَ أَجْرٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4616, N'إِنَّ الَّذِينَ يُنادُونَكَ مِنْ وَراءِ الْحُجُراتِ أَكْثَرُهُمْ لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4617, N'وَ لَوْ أَنَّهُمْ صَبَرُوا حَتَّى تَخْرُجَ إِلَيْهِمْ لَكانَ خَيْراً لَهُمْ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4618, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنْ جاءَكُمْ فاسِقٌ بِنَبَإٍ فَتَبَيَّنُوا أَنْ تُصِيبُوا قَوْماً بِجَهالَةٍ فَتُصْبِحُوا عَلى ما فَعَلْتُمْ نادِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4619, N'وَ اعْلَمُوا أَنَّ فِيكُمْ رَسُولَ اللَّهِ لَوْ يُطِيعُكُمْ فِي كَثِيرٍ مِنَ الْأَمْرِ لَعَنِتُّمْ وَ لكِنَّ اللَّهَ حَبَّبَ إِلَيْكُمُ الْإِيمانَ وَ زَيَّنَهُ فِي قُلُوبِكُمْ وَ كَرَّهَ إِلَيْكُمُ الْكُفْرَ وَ الْفُسُوقَ وَ الْعِصْيانَ أُولئِكَ هُمُ الرَّاشِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4620, N'فَضْلاً مِنَ اللَّهِ وَ نِعْمَةً وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4621, N'وَ إِنْ طائِفَتانِ مِنَ الْمُؤْمِنِينَ اقْتَتَلُوا فَأَصْلِحُوا بَيْنَهُما فَإِنْ بَغَتْ إِحْداهُما عَلَى الْأُخْرى فَقاتِلُوا الَّتِي تَبْغِي حَتَّى تَفِي‏ءَ إِلى أَمْرِ اللَّهِ فَإِنْ فاءَتْ فَأَصْلِحُوا بَيْنَهُما بِالْعَدْلِ وَ أَقْسِطُوا إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4622, N'إِنَّمَا الْمُؤْمِنُونَ إِخْوَةٌ فَأَصْلِحُوا بَيْنَ أَخَوَيْكُمْ وَ اتَّقُوا اللَّهَ لَعَلَّكُمْ تُرْحَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4623, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا يَسْخَرْ قَوْمٌ مِنْ قَوْمٍ عَسى أَنْ يَكُونُوا خَيْراً مِنْهُمْ وَ لا نِساءٌ مِنْ نِساءٍ عَسى أَنْ يَكُنَّ خَيْراً مِنْهُنَّ وَ لا تَلْمِزُوا أَنْفُسَكُمْ وَ لا تَنابَزُوا بِالْأَلْقابِ بِئْسَ الاِسْمُ الْفُسُوقُ بَعْدَ الْإِيمانِ وَ مَنْ لَمْ يَتُبْ فَأُولئِكَ هُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4624, N'يا أَيُّهَا الَّذِينَ آمَنُوا اجْتَنِبُوا كَثِيراً مِنَ الظَّنِّ إِنَّ بَعْضَ الظَّنِّ إِثْمٌ وَ لا تَجَسَّسُوا وَ لا يَغْتَبْ بَعْضُكُمْ بَعْضاً أَ يُحِبُّ أَحَدُكُمْ أَنْ يَأْكُلَ لَحْمَ أَخِيهِ مَيْتاً فَكَرِهْتُمُوهُ وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ تَوَّابٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4625, N'يا أَيُّهَا النَّاسُ إِنَّا خَلَقْناكُمْ مِنْ ذَكَرٍ وَ أُنْثى وَ جَعَلْناكُمْ شُعُوباً وَ قَبائِلَ لِتَعارَفُوا إِنَّ أَكْرَمَكُمْ عِنْدَ اللَّهِ أَتْقاكُمْ إِنَّ اللَّهَ عَلِيمٌ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4626, N'قالَتِ الْأَعْرابُ آمَنَّا قُلْ لَمْ تُؤْمِنُوا وَ لكِنْ قُولُوا أَسْلَمْنا وَ لَمَّا يَدْخُلِ الْإِيمانُ فِي قُلُوبِكُمْ وَ إِنْ تُطِيعُوا اللَّهَ وَ رَسُولَهُ لا يَلِتْكُمْ مِنْ أَعْمالِكُمْ شَيْئاً إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4627, N'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَ رَسُولِهِ ثُمَّ لَمْ يَرْتابُوا وَ جاهَدُوا بِأَمْوالِهِمْ وَ أَنْفُسِهِمْ فِي سَبِيلِ اللَّهِ أُولئِكَ هُمُ الصَّادِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4628, N'قُلْ أَ تُعَلِّمُونَ اللَّهَ بِدِينِكُمْ وَ اللَّهُ يَعْلَمُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ اللَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4629, N'يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا قُلْ لا تَمُنُّوا عَلَيَّ إِسْلامَكُمْ بَلِ اللَّهُ يَمُنُّ عَلَيْكُمْ أَنْ هَداكُمْ لِلْإِيمانِ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4630, N'إِنَّ اللَّهَ يَعْلَمُ غَيْبَ السَّماواتِ وَ الْأَرْضِ وَ اللَّهُ بَصِيرٌ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4631, N'ق وَ الْقُرْآنِ الْمَجِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4632, N'بَلْ عَجِبُوا أَنْ جاءَهُمْ مُنْذِرٌ مِنْهُمْ فَقالَ الْكافِرُونَ هذا شَيْ‏ءٌ عَجِيبٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4633, N'أَ إِذا مِتْنا وَ كُنَّا تُراباً ذلِكَ رَجْعٌ بَعِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4634, N'قَدْ عَلِمْنا ما تَنْقُصُ الْأَرْضُ مِنْهُمْ وَ عِنْدَنا كِتابٌ حَفِيظٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4635, N'بَلْ كَذَّبُوا بِالْحَقِّ لَمَّا جاءَهُمْ فَهُمْ فِي أَمْرٍ مَرِيجٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4636, N'أَ فَلَمْ يَنْظُرُوا إِلَى السَّماءِ فَوْقَهُمْ كَيْفَ بَنَيْناها وَ زَيَّنَّاها وَ ما لَها مِنْ فُرُوجٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4637, N'وَ الْأَرْضَ مَدَدْناها وَ أَلْقَيْنا فِيها رَواسِيَ وَ أَنْبَتْنا فِيها مِنْ كُلِّ زَوْجٍ بَهِيجٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4638, N'تَبْصِرَةً وَ ذِكْرى لِكُلِّ عَبْدٍ مُنِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4639, N'وَ نَزَّلْنا مِنَ السَّماءِ ماءً مُبارَكاً فَأَنْبَتْنا بِهِ جَنَّاتٍ وَ حَبَّ الْحَصِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4640, N'وَ النَّخْلَ باسِقاتٍ لَها طَلْعٌ نَضِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4641, N'رِزْقاً لِلْعِبادِ وَ أَحْيَيْنا بِهِ بَلْدَةً مَيْتاً كَذلِكَ الْخُرُوجُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4642, N'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَ أَصْحابُ الرَّسِّ وَ ثَمُودُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4643, N'وَ عادٌ وَ فِرْعَوْنُ وَ إِخْوانُ لُوطٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4644, N'وَ أَصْحابُ الْأَيْكَةِ وَ قَوْمُ تُبَّعٍ كُلٌّ كَذَّبَ الرُّسُلَ فَحَقَّ وَعِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4645, N'أَ فَعَيِينا بِالْخَلْقِ الْأَوَّلِ بَلْ هُمْ فِي لَبْسٍ مِنْ خَلْقٍ جَدِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4646, N'وَ لَقَدْ خَلَقْنَا الْإِنْسانَ وَ نَعْلَمُ ما تُوَسْوِسُ بِهِ نَفْسُهُ وَ نَحْنُ أَقْرَبُ إِلَيْهِ مِنْ حَبْلِ الْوَرِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4647, N'إِذْ يَتَلَقَّى الْمُتَلَقِّيانِ عَنِ الْيَمِينِ وَ عَنِ الشِّمالِ قَعِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4648, N'ما يَلْفِظُ مِنْ قَوْلٍ إِلاَّ لَدَيْهِ رَقِيبٌ عَتِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4649, N'وَ جاءَتْ سَكْرَةُ الْمَوْتِ بِالْحَقِّ ذلِكَ ما كُنْتَ مِنْهُ تَحِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4650, N'وَ نُفِخَ فِي الصُّورِ ذلِكَ يَوْمُ الْوَعِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4651, N'وَ جاءَتْ كُلُّ نَفْسٍ مَعَها سائِقٌ وَ شَهِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4652, N'لَقَدْ كُنْتَ فِي غَفْلَةٍ مِنْ هذا فَكَشَفْنا عَنْكَ غِطاءَكَ فَبَصَرُكَ الْيَوْمَ حَدِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4653, N'وَ قالَ قَرِينُهُ هذا ما لَدَيَّ عَتِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4654, N'أَلْقِيا فِي جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4655, N'مَنَّاعٍ لِلْخَيْرِ مُعْتَدٍ مُرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4656, N'الَّذِي جَعَلَ مَعَ اللَّهِ إِلهاً آخَرَ فَأَلْقِياهُ فِي الْعَذابِ الشَّدِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4657, N'قالَ قَرِينُهُ رَبَّنا ما أَطْغَيْتُهُ وَ لكِنْ كانَ فِي ضَلالٍ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4658, N'قالَ لا تَخْتَصِمُوا لَدَيَّ وَ قَدْ قَدَّمْتُ إِلَيْكُمْ بِالْوَعِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4659, N'ما يُبَدَّلُ الْقَوْلُ لَدَيَّ وَ ما أَنَا بِظَلاَّمٍ لِلْعَبِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4660, N'يَوْمَ نَقُولُ لِجَهَنَّمَ هَلِ امْتَلَأْتِ وَ تَقُولُ هَلْ مِنْ مَزِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4661, N'وَ أُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِينَ غَيْرَ بَعِيدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4662, N'هذا ما تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4663, N'مَنْ خَشِيَ الرَّحْمنَ بِالْغَيْبِ وَ جاءَ بِقَلْبٍ مُنِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4664, N'ادْخُلُوها بِسَلامٍ ذلِكَ يَوْمُ الْخُلُودِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4665, N'لَهُمْ ما يَشاؤُنَ فِيها وَ لَدَيْنا مَزِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4666, N'وَ كَمْ أَهْلَكْنا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ أَشَدُّ مِنْهُمْ بَطْشاً فَنَقَّبُوا فِي الْبِلادِ هَلْ مِنْ مَحِيصٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4667, N'إِنَّ فِي ذلِكَ لَذِكْرى لِمَنْ كانَ لَهُ قَلْبٌ أَوْ أَلْقَى السَّمْعَ وَ هُوَ شَهِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4668, N'وَ لَقَدْ خَلَقْنَا السَّماواتِ وَ الْأَرْضَ وَ ما بَيْنَهُما فِي سِتَّةِ أَيَّامٍ وَ ما مَسَّنا مِنْ لُغُوبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4669, N'فَاصْبِرْ عَلى ما يَقُولُونَ وَ سَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَ قَبْلَ الْغُرُوبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4670, N'وَ مِنَ اللَّيْلِ فَسَبِّحْهُ وَ أَدْبارَ السُّجُودِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4671, N'وَ اسْتَمِعْ يَوْمَ يُنادِ الْمُنادِ مِنْ مَكانٍ قَرِيبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4672, N'يَوْمَ يَسْمَعُونَ الصَّيْحَةَ بِالْحَقِّ ذلِكَ يَوْمُ الْخُرُوجِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4673, N'إِنَّا نَحْنُ نُحْيِي وَ نُمِيتُ وَ إِلَيْنَا الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4674, N'يَوْمَ تَشَقَّقُ الْأَرْضُ عَنْهُمْ سِراعاً ذلِكَ حَشْرٌ عَلَيْنا يَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4675, N'نَحْنُ أَعْلَمُ بِما يَقُولُونَ وَ ما أَنْتَ عَلَيْهِمْ بِجَبَّارٍ فَذَكِّرْ بِالْقُرْآنِ مَنْ يَخافُ وَعِيدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4676, N'وَ الذَّارِياتِ ذَرْواً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4677, N'فَالْحامِلاتِ وِقْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4678, N'فَالْجارِياتِ يُسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4679, N'فَالْمُقَسِّماتِ أَمْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4680, N'إِنَّما تُوعَدُونَ لَصادِقٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4681, N'وَ إِنَّ الدِّينَ لَواقِعٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4682, N'وَ السَّماءِ ذاتِ الْحُبُكِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4683, N'إِنَّكُمْ لَفِي قَوْلٍ مُخْتَلِفٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4684, N'يُؤْفَكُ عَنْهُ مَنْ أُفِكَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4685, N'قُتِلَ الْخَرَّاصُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4686, N'الَّذِينَ هُمْ فِي غَمْرَةٍ ساهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4687, N'يَسْئَلُونَ أَيَّانَ يَوْمُ الدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4688, N'يَوْمَ هُمْ عَلَى النَّارِ يُفْتَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4689, N'ذُوقُوا فِتْنَتَكُمْ هذَا الَّذِي كُنْتُمْ بِهِ تَسْتَعْجِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4690, N'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَ عُيُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4691, N'آخِذِينَ ما آتاهُمْ رَبُّهُمْ إِنَّهُمْ كانُوا قَبْلَ ذلِكَ مُحْسِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4692, N'كانُوا قَلِيلاً مِنَ اللَّيْلِ ما يَهْجَعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4693, N'وَ بِالْأَسْحارِ هُمْ يَسْتَغْفِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4694, N'وَ فِي أَمْوالِهِمْ حَقٌّ لِلسَّائِلِ وَ الْمَحْرُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4695, N'وَ فِي الْأَرْضِ آياتٌ لِلْمُوقِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4696, N'وَ فِي أَنْفُسِكُمْ أَ فَلا تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4697, N'وَ فِي السَّماءِ رِزْقُكُمْ وَ ما تُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4698, N'فَوَ رَبِّ السَّماءِ وَ الْأَرْضِ إِنَّهُ لَحَقٌّ مِثْلَ ما أَنَّكُمْ تَنْطِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4699, N'هَلْ أَتاكَ حَدِيثُ ضَيْفِ إِبْراهِيمَ الْمُكْرَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4700, N'إِذْ دَخَلُوا عَلَيْهِ فَقالُوا سَلاماً قالَ سَلامٌ قَوْمٌ مُنْكَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4701, N'فَراغَ إِلى أَهْلِهِ فَجاءَ بِعِجْلٍ سَمِينٍ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4702, N'فَقَرَّبَهُ إِلَيْهِمْ قالَ أَ لا تَأْكُلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4703, N'فَأَوْجَسَ مِنْهُمْ خِيفَةً قالُوا لا تَخَفْ وَ بَشَّرُوهُ بِغُلامٍ عَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4704, N'فَأَقْبَلَتِ امْرَأَتُهُ فِي صَرَّةٍ فَصَكَّتْ وَجْهَها وَ قالَتْ عَجُوزٌ عَقِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4705, N'قالُوا كَذلِكَ قالَ رَبُّكِ إِنَّهُ هُوَ الْحَكِيمُ الْعَلِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4706, N'قالَ فَما خَطْبُكُمْ أَيُّهَا الْمُرْسَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4707, N'قالُوا إِنَّا أُرْسِلْنا إِلى قَوْمٍ مُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4708, N'لِنُرْسِلَ عَلَيْهِمْ حِجارَةً مِنْ طِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4709, N'مُسَوَّمَةً عِنْدَ رَبِّكَ لِلْمُسْرِفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4710, N'فَأَخْرَجْنا مَنْ كانَ فِيها مِنَ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4711, N'فَما وَجَدْنا فِيها غَيْرَ بَيْتٍ مِنَ الْمُسْلِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4712, N'وَ تَرَكْنا فِيها آيَةً لِلَّذِينَ يَخافُونَ الْعَذابَ الْأَلِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4713, N'وَ فِي مُوسى إِذْ أَرْسَلْناهُ إِلى فِرْعَوْنَ بِسُلْطانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4714, N'فَتَوَلَّى بِرُكْنِهِ وَ قالَ ساحِرٌ أَوْ مَجْنُونٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4715, N'فَأَخَذْناهُ وَ جُنُودَهُ فَنَبَذْناهُمْ فِي الْيَمِّ وَ هُوَ مُلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4716, N'وَ فِي عادٍ إِذْ أَرْسَلْنا عَلَيْهِمُ الرِّيحَ الْعَقِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4717, N'ما تَذَرُ مِنْ شَيْ‏ءٍ أَتَتْ عَلَيْهِ إِلاَّ جَعَلَتْهُ كَالرَّمِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4718, N'وَ فِي ثَمُودَ إِذْ قِيلَ لَهُمْ تَمَتَّعُوا حَتَّى حِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4719, N'فَعَتَوْا عَنْ أَمْرِ رَبِّهِمْ فَأَخَذَتْهُمُ الصَّاعِقَةُ وَ هُمْ يَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4720, N'فَمَا اسْتَطاعُوا مِنْ قِيامٍ وَ ما كانُوا مُنْتَصِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4721, N'وَ قَوْمَ نُوحٍ مِنْ قَبْلُ إِنَّهُمْ كانُوا قَوْماً فاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4722, N'وَ السَّماءَ بَنَيْناها بِأَيْدٍ وَ إِنَّا لَمُوسِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4723, N'وَ الْأَرْضَ فَرَشْناها فَنِعْمَ الْماهِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4724, N'وَ مِنْ كُلِّ شَيْ‏ءٍ خَلَقْنا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4725, N'فَفِرُّوا إِلَى اللَّهِ إِنِّي لَكُمْ مِنْهُ نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4726, N'وَ لا تَجْعَلُوا مَعَ اللَّهِ إِلهاً آخَرَ إِنِّي لَكُمْ مِنْهُ نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4727, N'كَذلِكَ ما أَتَى الَّذِينَ مِنْ قَبْلِهِمْ مِنْ رَسُولٍ إِلاَّ قالُوا ساحِرٌ أَوْ مَجْنُونٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4728, N'أَ تَواصَوْا بِهِ بَلْ هُمْ قَوْمٌ طاغُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4729, N'فَتَوَلَّ عَنْهُمْ فَما أَنْتَ بِمَلُومٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4730, N'وَ ذَكِّرْ فَإِنَّ الذِّكْرى تَنْفَعُ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4731, N'وَ ما خَلَقْتُ الْجِنَّ وَ الْإِنْسَ إِلاَّ لِيَعْبُدُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4732, N'ما أُرِيدُ مِنْهُمْ مِنْ رِزْقٍ وَ ما أُرِيدُ أَنْ يُطْعِمُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4733, N'إِنَّ اللَّهَ هُوَ الرَّزَّاقُ ذُو الْقُوَّةِ الْمَتِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4734, N'فَإِنَّ لِلَّذِينَ ظَلَمُوا ذَنُوباً مِثْلَ ذَنُوبِ أَصْحابِهِمْ فَلا يَسْتَعْجِلُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4735, N'فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنْ يَوْمِهِمُ الَّذِي يُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4736, N'وَ الطُّورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4737, N'وَ كِتابٍ مَسْطُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4738, N'فِي رَقٍّ مَنْشُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4739, N'وَ الْبَيْتِ الْمَعْمُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4740, N'وَ السَّقْفِ الْمَرْفُوعِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4741, N'وَ الْبَحْرِ الْمَسْجُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4742, N'إِنَّ عَذابَ رَبِّكَ لَواقِعٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4743, N'ما لَهُ مِنْ دافِعٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4744, N'يَوْمَ تَمُورُ السَّماءُ مَوْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4745, N'وَ تَسِيرُ الْجِبالُ سَيْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4746, N'فَوَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4747, N'الَّذِينَ هُمْ فِي خَوْضٍ يَلْعَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4748, N'يَوْمَ يُدَعُّونَ إِلى نارِ جَهَنَّمَ دَعًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4749, N'هذِهِ النَّارُ الَّتِي كُنْتُمْ بِها تُكَذِّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4750, N'أَ فَسِحْرٌ هذا أَمْ أَنْتُمْ لا تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4751, N'اصْلَوْها فَاصْبِرُوا أَوْ لا تَصْبِرُوا سَواءٌ عَلَيْكُمْ إِنَّما تُجْزَوْنَ ما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4752, N'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَ نَعِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4753, N'فاكِهِينَ بِما آتاهُمْ رَبُّهُمْ وَ وَقاهُمْ رَبُّهُمْ عَذابَ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4754, N'كُلُوا وَ اشْرَبُوا هَنِيئاً بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4755, N'مُتَّكِئِينَ عَلى سُرُرٍ مَصْفُوفَةٍ وَ زَوَّجْناهُمْ بِحُورٍ عِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4756, N'وَ الَّذِينَ آمَنُوا وَ اتَّبَعَتْهُمْ ذُرِّيَّتُهُمْ بِإِيمانٍ أَلْحَقْنا بِهِمْ ذُرِّيَّتَهُمْ وَ ما أَلَتْناهُمْ مِنْ عَمَلِهِمْ مِنْ شَيْ‏ءٍ كُلُّ امْرِئٍ بِما كَسَبَ رَهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4757, N'وَ أَمْدَدْناهُمْ بِفاكِهَةٍ وَ لَحْمٍ مِمَّا يَشْتَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4758, N'يَتَنازَعُونَ فِيها كَأْساً لا لَغْوٌ فِيها وَ لا تَأْثِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4759, N'وَ يَطُوفُ عَلَيْهِمْ غِلْمانٌ لَهُمْ كَأَنَّهُمْ لُؤْلُؤٌ مَكْنُونٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4760, N'وَ أَقْبَلَ بَعْضُهُمْ عَلى بَعْضٍ يَتَساءَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4761, N'قالُوا إِنَّا كُنَّا قَبْلُ فِي أَهْلِنا مُشْفِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4762, N'فَمَنَّ اللَّهُ عَلَيْنا وَ وَقانا عَذابَ السَّمُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4763, N'إِنَّا كُنَّا مِنْ قَبْلُ نَدْعُوهُ إِنَّهُ هُوَ الْبَرُّ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4764, N'فَذَكِّرْ فَما أَنْتَ بِنِعْمَةِ رَبِّكَ بِكاهِنٍ وَ لا مَجْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4765, N'أَمْ يَقُولُونَ شاعِرٌ نَتَرَبَّصُ بِهِ رَيْبَ الْمَنُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4766, N'قُلْ تَرَبَّصُوا فَإِنِّي مَعَكُمْ مِنَ الْمُتَرَبِّصِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4767, N'أَمْ تَأْمُرُهُمْ أَحْلامُهُمْ بِهذا أَمْ هُمْ قَوْمٌ طاغُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4768, N'أَمْ يَقُولُونَ تَقَوَّلَهُ بَلْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4769, N'فَلْيَأْتُوا بِحَدِيثٍ مِثْلِهِ إِنْ كانُوا صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4770, N'أَمْ خُلِقُوا مِنْ غَيْرِ شَيْ‏ءٍ أَمْ هُمُ الْخالِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4771, N'أَمْ خَلَقُوا السَّماواتِ وَ الْأَرْضَ بَلْ لا يُوقِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4772, N'أَمْ عِنْدَهُمْ خَزائِنُ رَبِّكَ أَمْ هُمُ الْمُصَيْطِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4773, N'أَمْ لَهُمْ سُلَّمٌ يَسْتَمِعُونَ فِيهِ فَلْيَأْتِ مُسْتَمِعُهُمْ بِسُلْطانٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4774, N'أَمْ لَهُ الْبَناتُ وَ لَكُمُ الْبَنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4775, N'أَمْ تَسْئَلُهُمْ أَجْراً فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4776, N'أَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4777, N'أَمْ يُرِيدُونَ كَيْداً فَالَّذِينَ كَفَرُوا هُمُ الْمَكِيدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4778, N'أَمْ لَهُمْ إِلهٌ غَيْرُ اللَّهِ سُبْحانَ اللَّهِ عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4779, N'وَ إِنْ يَرَوْا كِسْفاً مِنَ السَّماءِ ساقِطاً يَقُولُوا سَحابٌ مَرْكُومٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4780, N'فَذَرْهُمْ حَتَّى يُلاقُوا يَوْمَهُمُ الَّذِي فِيهِ يُصْعَقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4781, N'يَوْمَ لا يُغْنِي عَنْهُمْ كَيْدُهُمْ شَيْئاً وَ لا هُمْ يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4782, N'وَ إِنَّ لِلَّذِينَ ظَلَمُوا عَذاباً دُونَ ذلِكَ وَ لكِنَّ أَكْثَرَهُمْ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4783, N'وَ اصْبِرْ لِحُكْمِ رَبِّكَ فَإِنَّكَ بِأَعْيُنِنا وَ سَبِّحْ بِحَمْدِ رَبِّكَ حِينَ تَقُومُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4784, N'وَ مِنَ اللَّيْلِ فَسَبِّحْهُ وَ إِدْبارَ النُّجُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4785, N'وَ النَّجْمِ إِذا هَوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4786, N'ما ضَلَّ صاحِبُكُمْ وَ ما غَوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4787, N'وَ ما يَنْطِقُ عَنِ الْهَوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4788, N'إِنْ هُوَ إِلاَّ وَحْيٌ يُوحى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4789, N'عَلَّمَهُ شَدِيدُ الْقُوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4790, N'ذُو مِرَّةٍ فَاسْتَوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4791, N'وَ هُوَ بِالْأُفُقِ الْأَعْلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4792, N'ثُمَّ دَنا فَتَدَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4793, N'فَكانَ قابَ قَوْسَيْنِ أَوْ أَدْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4794, N'فَأَوْحى إِلى عَبْدِهِ ما أَوْحى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4795, N'ما كَذَبَ الْفُؤادُ ما رَأى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4796, N'أَ فَتُمارُونَهُ عَلى ما يَرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4797, N'وَ لَقَدْ رَآهُ نَزْلَةً أُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4798, N'عِنْدَ سِدْرَةِ الْمُنْتَهى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4799, N'عِنْدَها جَنَّةُ الْمَأْوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4800, N'إِذْ يَغْشَى السِّدْرَةَ ما يَغْشى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4801, N'ما زاغَ الْبَصَرُ وَ ما طَغى ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4802, N'لَقَدْ رَأى مِنْ آياتِ رَبِّهِ الْكُبْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4803, N'أَ فَرَأَيْتُمُ اللاَّتَ وَ الْعُزَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4804, N'وَ مَناةَ الثَّالِثَةَ الْأُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4805, N'أَ لَكُمُ الذَّكَرُ وَ لَهُ الْأُنْثى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4806, N'تِلْكَ إِذاً قِسْمَةٌ ضِيزى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4807, N'إِنْ هِيَ إِلاَّ أَسْماءٌ سَمَّيْتُمُوها أَنْتُمْ وَ آباؤُكُمْ ما أَنْزَلَ اللَّهُ بِها مِنْ سُلْطانٍ إِنْ يَتَّبِعُونَ إِلاَّ الظَّنَّ وَ ما تَهْوَى الْأَنْفُسُ وَ لَقَدْ جاءَهُمْ مِنْ رَبِّهِمُ الْهُدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4808, N'أَمْ لِلْإِنْسانِ ما تَمَنَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4809, N'فَلِلَّهِ الْآخِرَةُ وَ الْأُولى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4810, N'وَ كَمْ مِنْ مَلَكٍ فِي السَّماواتِ لا تُغْنِي شَفاعَتُهُمْ شَيْئاً إِلاَّ مِنْ بَعْدِ أَنْ يَأْذَنَ اللَّهُ لِمَنْ يَشاءُ وَ يَرْضى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4811, N'إِنَّ الَّذِينَ لا يُؤْمِنُونَ بِالْآخِرَةِ لَيُسَمُّونَ الْمَلائِكَةَ تَسْمِيَةَ الْأُنْثى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4812, N'وَ ما لَهُمْ بِهِ مِنْ عِلْمٍ إِنْ يَتَّبِعُونَ إِلاَّ الظَّنَّ وَ إِنَّ الظَّنَّ لا يُغْنِي مِنَ الْحَقِّ شَيْئاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4813, N'فَأَعْرِضْ عَنْ مَنْ تَوَلَّى عَنْ ذِكْرِنا وَ لَمْ يُرِدْ إِلاَّ الْحَياةَ الدُّنْيا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4814, N'ذلِكَ مَبْلَغُهُمْ مِنَ الْعِلْمِ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَ هُوَ أَعْلَمُ بِمَنِ اهْتَدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4815, N'وَ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ لِيَجْزِيَ الَّذِينَ أَساؤُا بِما عَمِلُوا وَ يَجْزِيَ الَّذِينَ أَحْسَنُوا بِالْحُسْنَى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4816, N'الَّذِينَ يَجْتَنِبُونَ كَبائِرَ الْإِثْمِ وَ الْفَواحِشَ إِلاَّ اللَّمَمَ إِنَّ رَبَّكَ واسِعُ الْمَغْفِرَةِ هُوَ أَعْلَمُ بِكُمْ إِذْ أَنْشَأَكُمْ مِنَ الْأَرْضِ وَ إِذْ أَنْتُمْ أَجِنَّةٌ فِي بُطُونِ أُمَّهاتِكُمْ فَلا تُزَكُّوا أَنْفُسَكُمْ هُوَ أَعْلَمُ بِمَنِ اتَّقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4817, N'أَ فَرَأَيْتَ الَّذِي تَوَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4818, N'وَ أَعْطى قَلِيلاً وَ أَكْدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4819, N'أَ عِنْدَهُ عِلْمُ الْغَيْبِ فَهُوَ يَرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4820, N'أَمْ لَمْ يُنَبَّأْ بِما فِي صُحُفِ مُوسى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4821, N'وَ إِبْراهِيمَ الَّذِي وَفَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4822, N'أَلاَّ تَزِرُ وازِرَةٌ وِزْرَ أُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4823, N'وَ أَنْ لَيْسَ لِلْإِنْسانِ إِلاَّ ما سَعى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4824, N'وَ أَنَّ سَعْيَهُ سَوْفَ يُرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4825, N'ثُمَّ يُجْزاهُ الْجَزاءَ الْأَوْفى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4826, N'وَ أَنَّ إِلى رَبِّكَ الْمُنْتَهى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4827, N'وَ أَنَّهُ هُوَ أَضْحَكَ وَ أَبْكى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4828, N'وَ أَنَّهُ هُوَ أَماتَ وَ أَحْيا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4829, N'وَ أَنَّهُ خَلَقَ الزَّوْجَيْنِ الذَّكَرَ وَ الْأُنْثى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4830, N'مِنْ نُطْفَةٍ إِذا تُمْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4831, N'وَ أَنَّ عَلَيْهِ النَّشْأَةَ الْأُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4832, N'وَ أَنَّهُ هُوَ أَغْنى وَ أَقْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4833, N'وَ أَنَّهُ هُوَ رَبُّ الشِّعْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4834, N'وَ أَنَّهُ أَهْلَكَ عاداً الْأُولى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4835, N'وَ ثَمُودَ فَما أَبْقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4836, N'وَ قَوْمَ نُوحٍ مِنْ قَبْلُ إِنَّهُمْ كانُوا هُمْ أَظْلَمَ وَ أَطْغى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4837, N'وَ الْمُؤْتَفِكَةَ أَهْوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4838, N'فَغَشَّاها ما غَشَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4839, N'فَبِأَيِّ آلاءِ رَبِّكَ تَتَمارى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4840, N'هذا نَذِيرٌ مِنَ النُّذُرِ الْأُولى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4841, N'أَزِفَتِ الْآزِفَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4842, N'لَيْسَ لَها مِنْ دُونِ اللَّهِ كاشِفَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4843, N'أَ فَمِنْ هذَا الْحَدِيثِ تَعْجَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4844, N'وَ تَضْحَكُونَ وَ لا تَبْكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4845, N'وَ أَنْتُمْ سامِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4846, N'فَاسْجُدُوا لِلَّهِ وَ اعْبُدُوا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4847, N'اقْتَرَبَتِ السَّاعَةُ وَ انْشَقَّ الْقَمَرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4848, N'وَ إِنْ يَرَوْا آيَةً يُعْرِضُوا وَ يَقُولُوا سِحْرٌ مُسْتَمِرٌّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4849, N'وَ كَذَّبُوا وَ اتَّبَعُوا أَهْواءَهُمْ وَ كُلُّ أَمْرٍ مُسْتَقِرٌّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4850, N'وَ لَقَدْ جاءَهُمْ مِنَ الْأَنْباءِ ما فِيهِ مُزْدَجَرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4851, N'حِكْمَةٌ بالِغَةٌ فَما تُغْنِ النُّذُرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4852, N'فَتَوَلَّ عَنْهُمْ يَوْمَ يَدْعُ الدَّاعِ إِلى شَيْ‏ءٍ نُكُرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4853, N'خُشَّعاً أَبْصارُهُمْ يَخْرُجُونَ مِنَ الْأَجْداثِ كَأَنَّهُمْ جَرادٌ مُنْتَشِرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4854, N'مُهْطِعِينَ إِلَى الدَّاعِ يَقُولُ الْكافِرُونَ هذا يَوْمٌ عَسِرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4855, N'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ فَكَذَّبُوا عَبْدَنا وَ قالُوا مَجْنُونٌ وَ ازْدُجِرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4856, N'فَدَعا رَبَّهُ أَنِّي مَغْلُوبٌ فَانْتَصِرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4857, N'فَفَتَحْنا أَبْوابَ السَّماءِ بِماءٍ مُنْهَمِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4858, N'وَ فَجَّرْنَا الْأَرْضَ عُيُوناً فَالْتَقَى الْماءُ عَلى أَمْرٍ قَدْ قُدِرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4859, N'وَ حَمَلْناهُ عَلى ذاتِ أَلْواحٍ وَ دُسُرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4860, N'تَجْرِي بِأَعْيُنِنا جَزاءً لِمَنْ كانَ كُفِرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4861, N'وَ لَقَدْ تَرَكْناها آيَةً فَهَلْ مِنْ مُدَّكِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4862, N'فَكَيْفَ كانَ عَذابِي وَ نُذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4863, N'وَ لَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4864, N'كَذَّبَتْ عادٌ فَكَيْفَ كانَ عَذابِي وَ نُذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4865, N'إِنَّا أَرْسَلْنا عَلَيْهِمْ رِيحاً صَرْصَراً فِي يَوْمِ نَحْسٍ مُسْتَمِرٍّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4866, N'تَنْزِعُ النَّاسَ كَأَنَّهُمْ أَعْجازُ نَخْلٍ مُنْقَعِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4867, N'فَكَيْفَ كانَ عَذابِي وَ نُذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4868, N'وَ لَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4869, N'كَذَّبَتْ ثَمُودُ بِالنُّذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4870, N'فَقالُوا أَ بَشَراً مِنَّا واحِداً نَتَّبِعُهُ إِنَّا إِذاً لَفِي ضَلالٍ وَ سُعُرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4871, N'أَ أُلْقِيَ الذِّكْرُ عَلَيْهِ مِنْ بَيْنِنا بَلْ هُوَ كَذَّابٌ أَشِرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4872, N'سَيَعْلَمُونَ غَداً مَنِ الْكَذَّابُ الْأَشِرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4873, N'إِنَّا مُرْسِلُوا النَّاقَةِ فِتْنَةً لَهُمْ فَارْتَقِبْهُمْ وَ اصْطَبِرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4874, N'وَ نَبِّئْهُمْ أَنَّ الْماءَ قِسْمَةٌ بَيْنَهُمْ كُلُّ شِرْبٍ مُحْتَضَرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4875, N'فَنادَوْا صاحِبَهُمْ فَتَعاطى فَعَقَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4876, N'فَكَيْفَ كانَ عَذابِي وَ نُذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4877, N'إِنَّا أَرْسَلْنا عَلَيْهِمْ صَيْحَةً واحِدَةً فَكانُوا كَهَشِيمِ الْمُحْتَظِرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4878, N'وَ لَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4879, N'كَذَّبَتْ قَوْمُ لُوطٍ بِالنُّذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4880, N'إِنَّا أَرْسَلْنا عَلَيْهِمْ حاصِباً إِلاَّ آلَ لُوطٍ نَجَّيْناهُمْ بِسَحَرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4881, N'نِعْمَةً مِنْ عِنْدِنا كَذلِكَ نَجْزِي مَنْ شَكَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4882, N'وَ لَقَدْ أَنْذَرَهُمْ بَطْشَتَنا فَتَمارَوْا بِالنُّذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4883, N'وَ لَقَدْ راوَدُوهُ عَنْ ضَيْفِهِ فَطَمَسْنا أَعْيُنَهُمْ فَذُوقُوا عَذابِي وَ نُذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4884, N'وَ لَقَدْ صَبَّحَهُمْ بُكْرَةً عَذابٌ مُسْتَقِرٌّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4885, N'فَذُوقُوا عَذابِي وَ نُذُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4886, N'وَ لَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِنْ مُدَّكِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4887, N'وَ لَقَدْ جاءَ آلَ فِرْعَوْنَ النُّذُرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4888, N'كَذَّبُوا بِآياتِنا كُلِّها فَأَخَذْناهُمْ أَخْذَ عَزِيزٍ مُقْتَدِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4889, N'أَ كُفَّارُكُمْ خَيْرٌ مِنْ أُولئِكُمْ أَمْ لَكُمْ بَراءَةٌ فِي الزُّبُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4890, N'أَمْ يَقُولُونَ نَحْنُ جَمِيعٌ مُنْتَصِرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4891, N'سَيُهْزَمُ الْجَمْعُ وَ يُوَلُّونَ الدُّبُرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4892, N'بَلِ السَّاعَةُ مَوْعِدُهُمْ وَ السَّاعَةُ أَدْهى وَ أَمَرُّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4893, N'إِنَّ الْمُجْرِمِينَ فِي ضَلالٍ وَ سُعُرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4894, N'يَوْمَ يُسْحَبُونَ فِي النَّارِ عَلى وُجُوهِهِمْ ذُوقُوا مَسَّ سَقَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4895, N'إِنَّا كُلَّ شَيْ‏ءٍ خَلَقْناهُ بِقَدَرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4896, N'وَ ما أَمْرُنا إِلاَّ واحِدَةٌ كَلَمْحٍ بِالْبَصَرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4897, N'وَ لَقَدْ أَهْلَكْنا أَشْياعَكُمْ فَهَلْ مِنْ مُدَّكِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4898, N'وَ كُلُّ شَيْ‏ءٍ فَعَلُوهُ فِي الزُّبُرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4899, N'وَ كُلُّ صَغِيرٍ وَ كَبِيرٍ مُسْتَطَرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4900, N'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَ نَهَرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4901, N'فِي مَقْعَدِ صِدْقٍ عِنْدَ مَلِيكٍ مُقْتَدِرٍ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4902, N'الرَّحْمنُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4903, N'عَلَّمَ الْقُرْآنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4904, N'خَلَقَ الْإِنْسانَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4905, N'عَلَّمَهُ الْبَيانَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4906, N'الشَّمْسُ وَ الْقَمَرُ بِحُسْبانٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4907, N'وَ النَّجْمُ وَ الشَّجَرُ يَسْجُدانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4908, N'وَ السَّماءَ رَفَعَها وَ وَضَعَ الْمِيزانَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4909, N'أَلاَّ تَطْغَوْا فِي الْمِيزانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4910, N'وَ أَقِيمُوا الْوَزْنَ بِالْقِسْطِ وَ لا تُخْسِرُوا الْمِيزانَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4911, N'وَ الْأَرْضَ وَضَعَها لِلْأَنامِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4912, N'فِيها فاكِهَةٌ وَ النَّخْلُ ذاتُ الْأَكْمامِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4913, N'وَ الْحَبُّ ذُو الْعَصْفِ وَ الرَّيْحانُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4914, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4915, N'خَلَقَ الْإِنْسانَ مِنْ صَلْصالٍ كَالْفَخَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4916, N'وَ خَلَقَ الْجَانَّ مِنْ مارِجٍ مِنْ نارٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4917, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4918, N'رَبُّ الْمَشْرِقَيْنِ وَ رَبُّ الْمَغْرِبَيْنِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4919, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4920, N'مَرَجَ الْبَحْرَيْنِ يَلْتَقِيانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4921, N'بَيْنَهُما بَرْزَخٌ لا يَبْغِيانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4922, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4923, N'يَخْرُجُ مِنْهُمَا اللُّؤْلُؤُ وَ الْمَرْجانُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4924, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4925, N'وَ لَهُ الْجَوارِ الْمُنْشَآتُ فِي الْبَحْرِ كَالْأَعْلامِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4926, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4927, N'كُلُّ مَنْ عَلَيْها فانٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4928, N'وَ يَبْقى وَجْهُ رَبِّكَ ذُو الْجَلالِ وَ الْإِكْرامِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4929, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4930, N'يَسْئَلُهُ مَنْ فِي السَّماواتِ وَ الْأَرْضِ كُلَّ يَوْمٍ هُوَ فِي شَأْنٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4931, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4932, N'سَنَفْرُغُ لَكُمْ أَيُّهَ الثَّقَلانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4933, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4934, N'يا مَعْشَرَ الْجِنِّ وَ الْإِنْسِ إِنِ اسْتَطَعْتُمْ أَنْ تَنْفُذُوا مِنْ أَقْطارِ السَّماواتِ وَ الْأَرْضِ فَانْفُذُوا لا تَنْفُذُونَ إِلاَّ بِسُلْطانٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4935, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4936, N'يُرْسَلُ عَلَيْكُما شُواظٌ مِنْ نارٍ وَ نُحاسٌ فَلا تَنْتَصِرانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4937, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4938, N'فَإِذَا انْشَقَّتِ السَّماءُ فَكانَتْ وَرْدَةً كَالدِّهانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4939, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4940, N'فَيَوْمَئِذٍ لا يُسْئَلُ عَنْ ذَنْبِهِ إِنْسٌ وَ لا جَانٌّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4941, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
