INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4971, N'فِيهِنَّ خَيْراتٌ حِسانٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4972, N'فَبِأَيِّ آلاءِ رَبِّكُما تُكَذِّبانِ ')
