INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4982, N'خافِضَةٌ رافِعَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4983, N'إِذا رُجَّتِ الْأَرْضُ رَجًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4984, N'وَ بُسَّتِ الْجِبالُ بَسًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4985, N'فَكانَتْ هَباءً مُنْبَثًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4986, N'وَ كُنْتُمْ أَزْواجاً ثَلاثَةً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4987, N'فَأَصْحابُ الْمَيْمَنَةِ ما أَصْحابُ الْمَيْمَنَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4988, N'وَ أَصْحابُ الْمَشْئَمَةِ ما أَصْحابُ الْمَشْئَمَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4989, N'وَ السَّابِقُونَ السَّابِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4990, N'أُولئِكَ الْمُقَرَّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4991, N'فِي جَنَّاتِ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4992, N'ثُلَّةٌ مِنَ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4993, N'وَ قَلِيلٌ مِنَ الْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4994, N'عَلى سُرُرٍ مَوْضُونَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4995, N'مُتَّكِئِينَ عَلَيْها مُتَقابِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4996, N'يَطُوفُ عَلَيْهِمْ وِلْدانٌ مُخَلَّدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4997, N'بِأَكْوابٍ وَ أَبارِيقَ وَ كَأْسٍ مِنْ مَعِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4998, N'لا يُصَدَّعُونَ عَنْها وَ لا يُنْزِفُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (4999, N'وَ فاكِهَةٍ مِمَّا يَتَخَيَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5000, N'وَ لَحْمِ طَيْرٍ مِمَّا يَشْتَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5001, N'وَ حُورٌ عِينٌ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5002, N'كَأَمْثالِ اللُّؤْلُؤِ الْمَكْنُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5003, N'جَزاءً بِما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5004, N'لا يَسْمَعُونَ فِيها لَغْواً وَ لا تَأْثِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5005, N'إِلاَّ قِيلاً سَلاماً سَلاماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5006, N'وَ أَصْحابُ الْيَمِينِ ما أَصْحابُ الْيَمِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5007, N'فِي سِدْرٍ مَخْضُودٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5008, N'وَ طَلْحٍ مَنْضُودٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5009, N'وَ ظِلٍّ مَمْدُودٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5010, N'وَ ماءٍ مَسْكُوبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5011, N'وَ فاكِهَةٍ كَثِيرَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5012, N'لا مَقْطُوعَةٍ وَ لا مَمْنُوعَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5013, N'وَ فُرُشٍ مَرْفُوعَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5014, N'إِنَّا أَنْشَأْناهُنَّ إِنْشاءً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5015, N'فَجَعَلْناهُنَّ أَبْكاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5016, N'عُرُباً أَتْراباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5017, N'لِأَصْحابِ الْيَمِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5018, N'ثُلَّةٌ مِنَ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5019, N'وَ ثُلَّةٌ مِنَ الْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5020, N'وَ أَصْحابُ الشِّمالِ ما أَصْحابُ الشِّمالِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5021, N'فِي سَمُومٍ وَ حَمِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5022, N'وَ ظِلٍّ مِنْ يَحْمُومٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5023, N'لا بارِدٍ وَ لا كَرِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5024, N'إِنَّهُمْ كانُوا قَبْلَ ذلِكَ مُتْرَفِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5025, N'وَ كانُوا يُصِرُّونَ عَلَى الْحِنْثِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5026, N'وَ كانُوا يَقُولُونَ أَ إِذا مِتْنا وَ كُنَّا تُراباً وَ عِظاماً أَ إِنَّا لَمَبْعُوثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5027, N'أَ وَ آباؤُنَا الْأَوَّلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5028, N'قُلْ إِنَّ الْأَوَّلِينَ وَ الْآخِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5029, N'لَمَجْمُوعُونَ إِلى مِيقاتِ يَوْمٍ مَعْلُومٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5030, N'ثُمَّ إِنَّكُمْ أَيُّهَا الضَّالُّونَ الْمُكَذِّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5031, N'لَآكِلُونَ مِنْ شَجَرٍ مِنْ زَقُّومٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5032, N'فَمالِؤُنَ مِنْهَا الْبُطُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5033, N'فَشارِبُونَ عَلَيْهِ مِنَ الْحَمِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5034, N'فَشارِبُونَ شُرْبَ الْهِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5035, N'هذا نُزُلُهُمْ يَوْمَ الدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5036, N'نَحْنُ خَلَقْناكُمْ فَلَوْ لا تُصَدِّقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5037, N'أَ فَرَأَيْتُمْ ما تُمْنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5038, N'أَ أَنْتُمْ تَخْلُقُونَهُ أَمْ نَحْنُ الْخالِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5039, N'نَحْنُ قَدَّرْنا بَيْنَكُمُ الْمَوْتَ وَ ما نَحْنُ بِمَسْبُوقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5040, N'عَلى أَنْ نُبَدِّلَ أَمْثالَكُمْ وَ نُنْشِئَكُمْ فِي ما لا تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5041, N'وَ لَقَدْ عَلِمْتُمُ النَّشْأَةَ الْأُولى فَلَوْ لا تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5042, N'أَ فَرَأَيْتُمْ ما تَحْرُثُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5043, N'أَ أَنْتُمْ تَزْرَعُونَهُ أَمْ نَحْنُ الزَّارِعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5044, N'لَوْ نَشاءُ لَجَعَلْناهُ حُطاماً فَظَلْتُمْ تَفَكَّهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5045, N'إِنَّا لَمُغْرَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5046, N'بَلْ نَحْنُ مَحْرُومُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5047, N'أَ فَرَأَيْتُمُ الْماءَ الَّذِي تَشْرَبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5048, N'أَ أَنْتُمْ أَنْزَلْتُمُوهُ مِنَ الْمُزْنِ أَمْ نَحْنُ الْمُنْزِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5049, N'لَوْ نَشاءُ جَعَلْناهُ أُجاجاً فَلَوْ لا تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5050, N'أَ فَرَأَيْتُمُ النَّارَ الَّتِي تُورُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5051, N'أَ أَنْتُمْ أَنْشَأْتُمْ شَجَرَتَها أَمْ نَحْنُ الْمُنْشِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5052, N'نَحْنُ جَعَلْناها تَذْكِرَةً وَ مَتاعاً لِلْمُقْوِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5053, N'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5054, N'فَلا أُقْسِمُ بِمَواقِعِ النُّجُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5055, N'وَ إِنَّهُ لَقَسَمٌ لَوْ تَعْلَمُونَ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5056, N'إِنَّهُ لَقُرْآنٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5057, N'فِي كِتابٍ مَكْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5058, N'لا يَمَسُّهُ إِلاَّ الْمُطَهَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5059, N'تَنْزِيلٌ مِنْ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5060, N'أَ فَبِهذَا الْحَدِيثِ أَنْتُمْ مُدْهِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5061, N'وَ تَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5062, N'فَلَوْ لا إِذا بَلَغَتِ الْحُلْقُومَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5063, N'وَ أَنْتُمْ حِينَئِذٍ تَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5064, N'وَ نَحْنُ أَقْرَبُ إِلَيْهِ مِنْكُمْ وَ لكِنْ لا تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5065, N'فَلَوْ لا إِنْ كُنْتُمْ غَيْرَ مَدِينِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5066, N'تَرْجِعُونَها إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5067, N'فَأَمَّا إِنْ كانَ مِنَ الْمُقَرَّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5068, N'فَرَوْحٌ وَ رَيْحانٌ وَ جَنَّةُ نَعِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5069, N'وَ أَمَّا إِنْ كانَ مِنْ أَصْحابِ الْيَمِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5070, N'فَسَلامٌ لَكَ مِنْ أَصْحابِ الْيَمِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5071, N'وَ أَمَّا إِنْ كانَ مِنَ الْمُكَذِّبِينَ الضَّالِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5072, N'فَنُزُلٌ مِنْ حَمِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5073, N'وَ تَصْلِيَةُ جَحِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5074, N'إِنَّ هذا لَهُوَ حَقُّ الْيَقِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5075, N'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5076, N'سَبَّحَ لِلَّهِ ما فِي السَّماواتِ وَ الْأَرْضِ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5077, N'لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ يُحْيِي وَ يُمِيتُ وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5078, N'هُوَ الْأَوَّلُ وَ الْآخِرُ وَ الظَّاهِرُ وَ الْباطِنُ وَ هُوَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5079, N'هُوَ الَّذِي خَلَقَ السَّماواتِ وَ الْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوى عَلَى الْعَرْشِ يَعْلَمُ ما يَلِجُ فِي الْأَرْضِ وَ ما يَخْرُجُ مِنْها وَ ما يَنْزِلُ مِنَ السَّماءِ وَ ما يَعْرُجُ فِيها وَ هُوَ مَعَكُمْ أَيْنَ ما كُنْتُمْ وَ اللَّهُ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5080, N'لَهُ مُلْكُ السَّماواتِ وَ الْأَرْضِ وَ إِلَى اللَّهِ تُرْجَعُ الْأُمُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5081, N'يُولِجُ اللَّيْلَ فِي النَّهارِ وَ يُولِجُ النَّهارَ فِي اللَّيْلِ وَ هُوَ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5082, N'آمِنُوا بِاللَّهِ وَ رَسُولِهِ وَ أَنْفِقُوا مِمَّا جَعَلَكُمْ مُسْتَخْلَفِينَ فِيهِ فَالَّذِينَ آمَنُوا مِنْكُمْ وَ أَنْفَقُوا لَهُمْ أَجْرٌ كَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5083, N'وَ ما لَكُمْ لا تُؤْمِنُونَ بِاللَّهِ وَ الرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا بِرَبِّكُمْ وَ قَدْ أَخَذَ مِيثاقَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5084, N'هُوَ الَّذِي يُنَزِّلُ عَلى عَبْدِهِ آياتٍ بَيِّناتٍ لِيُخْرِجَكُمْ مِنَ الظُّلُماتِ إِلَى النُّورِ وَ إِنَّ اللَّهَ بِكُمْ لَرَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5085, N'وَ ما لَكُمْ أَلاَّ تُنْفِقُوا فِي سَبِيلِ اللَّهِ وَ لِلَّهِ مِيراثُ السَّماواتِ وَ الْأَرْضِ لا يَسْتَوِي مِنْكُمْ مَنْ أَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَ قاتَلَ أُولئِكَ أَعْظَمُ دَرَجَةً مِنَ الَّذِينَ أَنْفَقُوا مِنْ بَعْدُ وَ قاتَلُوا وَ كُلاًّ وَعَدَ اللَّهُ الْحُسْنى وَ اللَّهُ بِما تَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5086, N'مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضاً حَسَناً فَيُضاعِفَهُ لَهُ وَ لَهُ أَجْرٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5087, N'يَوْمَ تَرَى الْمُؤْمِنِينَ وَ الْمُؤْمِناتِ يَسْعى نُورُهُمْ بَيْنَ أَيْدِيهِمْ وَ بِأَيْمانِهِمْ بُشْراكُمُ الْيَوْمَ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها ذلِكَ هُوَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5088, N'يَوْمَ يَقُولُ الْمُنافِقُونَ وَ الْمُنافِقاتُ لِلَّذِينَ آمَنُوا انْظُرُونا نَقْتَبِسْ مِنْ نُورِكُمْ قِيلَ ارْجِعُوا وَراءَكُمْ فَالْتَمِسُوا نُوراً فَضُرِبَ بَيْنَهُمْ بِسُورٍ لَهُ بابٌ باطِنُهُ فِيهِ الرَّحْمَةُ وَ ظاهِرُهُ مِنْ قِبَلِهِ الْعَذابُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5089, N'يُنادُونَهُمْ أَ لَمْ نَكُنْ مَعَكُمْ قالُوا بَلى وَ لكِنَّكُمْ فَتَنْتُمْ أَنْفُسَكُمْ وَ تَرَبَّصْتُمْ وَ ارْتَبْتُمْ وَ غَرَّتْكُمُ الْأَمانِيُّ حَتَّى جاءَ أَمْرُ اللَّهِ وَ غَرَّكُمْ بِاللَّهِ الْغَرُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5090, N'فَالْيَوْمَ لا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَ لا مِنَ الَّذِينَ كَفَرُوا مَأْواكُمُ النَّارُ هِيَ مَوْلاكُمْ وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5091, N'أَ لَمْ يَأْنِ لِلَّذِينَ آمَنُوا أَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللَّهِ وَ ما نَزَلَ مِنَ الْحَقِّ وَ لا يَكُونُوا كَالَّذِينَ أُوتُوا الْكِتابَ مِنْ قَبْلُ فَطالَ عَلَيْهِمُ الْأَمَدُ فَقَسَتْ قُلُوبُهُمْ وَ كَثِيرٌ مِنْهُمْ فاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5092, N'اعْلَمُوا أَنَّ اللَّهَ يُحْيِ الْأَرْضَ بَعْدَ مَوْتِها قَدْ بَيَّنَّا لَكُمُ الْآياتِ لَعَلَّكُمْ تَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5093, N'إِنَّ الْمُصَّدِّقِينَ وَ الْمُصَّدِّقاتِ وَ أَقْرَضُوا اللَّهَ قَرْضاً حَسَناً يُضاعَفُ لَهُمْ وَ لَهُمْ أَجْرٌ كَرِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5094, N'وَ الَّذِينَ آمَنُوا بِاللَّهِ وَ رُسُلِهِ أُولئِكَ هُمُ الصِّدِّيقُونَ وَ الشُّهَداءُ عِنْدَ رَبِّهِمْ لَهُمْ أَجْرُهُمْ وَ نُورُهُمْ وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآياتِنا أُولئِكَ أَصْحابُ الْجَحِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5095, N'اعْلَمُوا أَنَّمَا الْحَياةُ الدُّنْيا لَعِبٌ وَ لَهْوٌ وَ زِينَةٌ وَ تَفاخُرٌ بَيْنَكُمْ وَ تَكاثُرٌ فِي الْأَمْوالِ وَ الْأَوْلادِ كَمَثَلِ غَيْثٍ أَعْجَبَ الْكُفَّارَ نَباتُهُ ثُمَّ يَهِيجُ فَتَراهُ مُصْفَرًّا ثُمَّ يَكُونُ حُطاماً وَ فِي الْآخِرَةِ عَذابٌ شَدِيدٌ وَ مَغْفِرَةٌ مِنَ اللَّهِ وَ رِضْوانٌ وَ مَا الْحَياةُ الدُّنْيا إِلاَّ مَتاعُ الْغُرُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5096, N'سابِقُوا إِلى مَغْفِرَةٍ مِنْ رَبِّكُمْ وَ جَنَّةٍ عَرْضُها كَعَرْضِ السَّماءِ وَ الْأَرْضِ أُعِدَّتْ لِلَّذِينَ آمَنُوا بِاللَّهِ وَ رُسُلِهِ ذلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشاءُ وَ اللَّهُ ذُو الْفَضْلِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5097, N'ما أَصابَ مِنْ مُصِيبَةٍ فِي الْأَرْضِ وَ لا فِي أَنْفُسِكُمْ إِلاَّ فِي كِتابٍ مِنْ قَبْلِ أَنْ نَبْرَأَها إِنَّ ذلِكَ عَلَى اللَّهِ يَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5098, N'لِكَيْلا تَأْسَوْا عَلى ما فاتَكُمْ وَ لا تَفْرَحُوا بِما آتاكُمْ وَ اللَّهُ لا يُحِبُّ كُلَّ مُخْتالٍ فَخُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5099, N'الَّذِينَ يَبْخَلُونَ وَ يَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَ مَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5100, N'لَقَدْ أَرْسَلْنا رُسُلَنا بِالْبَيِّناتِ وَ أَنْزَلْنا مَعَهُمُ الْكِتابَ وَ الْمِيزانَ لِيَقُومَ النَّاسُ بِالْقِسْطِ وَ أَنْزَلْنَا الْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَ مَنافِعُ لِلنَّاسِ وَ لِيَعْلَمَ اللَّهُ مَنْ يَنْصُرُهُ وَ رُسُلَهُ بِالْغَيْبِ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5101, N'وَ لَقَدْ أَرْسَلْنا نُوحاً وَ إِبْراهِيمَ وَ جَعَلْنا فِي ذُرِّيَّتِهِمَا النُّبُوَّةَ وَ الْكِتابَ فَمِنْهُمْ مُهْتَدٍ وَ كَثِيرٌ مِنْهُمْ فاسِقُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5102, N'ثُمَّ قَفَّيْنا عَلى آثارِهِمْ بِرُسُلِنا وَ قَفَّيْنا بِعِيسَى ابْنِ مَرْيَمَ وَ آتَيْناهُ الْإِنْجِيلَ وَ جَعَلْنا فِي قُلُوبِ الَّذِينَ اتَّبَعُوهُ رَأْفَةً وَ رَحْمَةً وَ رَهْبانِيَّةً ابْتَدَعُوها ما كَتَبْناها عَلَيْهِمْ إِلاَّ ابْتِغاءَ رِضْوانِ اللَّهِ فَما رَعَوْها حَقَّ رِعايَتِها فَآتَيْنَا الَّذِينَ آمَنُوا مِنْهُمْ أَجْرَهُمْ وَ كَثِيرٌ مِنْهُمْ فاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5103, N'يا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَ آمِنُوا بِرَسُولِهِ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَحْمَتِهِ وَ يَجْعَلْ لَكُمْ نُوراً تَمْشُونَ بِهِ وَ يَغْفِرْ لَكُمْ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5104, N'لِئَلاَّ يَعْلَمَ أَهْلُ الْكِتابِ أَلاَّ يَقْدِرُونَ عَلى شَيْ‏ءٍ مِنْ فَضْلِ اللَّهِ وَ أَنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشاءُ وَ اللَّهُ ذُو الْفَضْلِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5105, N'قَدْ سَمِعَ اللَّهُ قَوْلَ الَّتِي تُجادِلُكَ فِي زَوْجِها وَ تَشْتَكِي إِلَى اللَّهِ وَ اللَّهُ يَسْمَعُ تَحاوُرَكُما إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5106, N'الَّذِينَ يُظاهِرُونَ مِنْكُمْ مِنْ نِسائِهِمْ ما هُنَّ أُمَّهاتِهِمْ إِنْ أُمَّهاتُهُمْ إِلاَّ اللاَّئِي وَلَدْنَهُمْ وَ إِنَّهُمْ لَيَقُولُونَ مُنْكَراً مِنَ الْقَوْلِ وَ زُوراً وَ إِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5107, N'وَ الَّذِينَ يُظاهِرُونَ مِنْ نِسائِهِمْ ثُمَّ يَعُودُونَ لِما قالُوا فَتَحْرِيرُ رَقَبَةٍ مِنْ قَبْلِ أَنْ يَتَمَاسَّا ذلِكُمْ تُوعَظُونَ بِهِ وَ اللَّهُ بِما تَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5108, N'فَمَنْ لَمْ يَجِدْ فَصِيامُ شَهْرَيْنِ مُتَتابِعَيْنِ مِنْ قَبْلِ أَنْ يَتَمَاسَّا فَمَنْ لَمْ يَسْتَطِعْ فَإِطْعامُ سِتِّينَ مِسْكِيناً ذلِكَ لِتُؤْمِنُوا بِاللَّهِ وَ رَسُولِهِ وَ تِلْكَ حُدُودُ اللَّهِ وَ لِلْكافِرِينَ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5109, N'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَ رَسُولَهُ كُبِتُوا كَما كُبِتَ الَّذِينَ مِنْ قَبْلِهِمْ وَ قَدْ أَنْزَلْنا آياتٍ بَيِّناتٍ وَ لِلْكافِرِينَ عَذابٌ مُهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5110, N'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعاً فَيُنَبِّئُهُمْ بِما عَمِلُوا أَحْصاهُ اللَّهُ وَ نَسُوهُ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ شَهِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5111, N'أَ لَمْ تَرَ أَنَّ اللَّهَ يَعْلَمُ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ ما يَكُونُ مِنْ نَجْوى ثَلاثَةٍ إِلاَّ هُوَ رابِعُهُمْ وَ لا خَمْسَةٍ إِلاَّ هُوَ سادِسُهُمْ وَ لا أَدْنى مِنْ ذلِكَ وَ لا أَكْثَرَ إِلاَّ هُوَ مَعَهُمْ أَيْنَ ما كانُوا ثُمَّ يُنَبِّئُهُمْ بِما عَمِلُوا يَوْمَ الْقِيامَةِ إِنَّ اللَّهَ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5112, N'أَ لَمْ تَرَ إِلَى الَّذِينَ نُهُوا عَنِ النَّجْوى ثُمَّ يَعُودُونَ لِما نُهُوا عَنْهُ وَ يَتَناجَوْنَ بِالْإِثْمِ وَ الْعُدْوانِ وَ مَعْصِيَةِ الرَّسُولِ وَ إِذا جاؤُكَ حَيَّوْكَ بِما لَمْ يُحَيِّكَ بِهِ اللَّهُ وَ يَقُولُونَ فِي أَنْفُسِهِمْ لَوْ لا يُعَذِّبُنَا اللَّهُ بِما نَقُولُ حَسْبُهُمْ جَهَنَّمُ يَصْلَوْنَها فَبِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5113, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا تَناجَيْتُمْ فَلا تَتَناجَوْا بِالْإِثْمِ وَ الْعُدْوانِ وَ مَعْصِيَةِ الرَّسُولِ وَ تَناجَوْا بِالْبِرِّ وَ التَّقْوى وَ اتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5114, N'إِنَّمَا النَّجْوى مِنَ الشَّيْطانِ لِيَحْزُنَ الَّذِينَ آمَنُوا وَ لَيْسَ بِضارِّهِمْ شَيْئاً إِلاَّ بِإِذْنِ اللَّهِ وَ عَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5115, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا قِيلَ لَكُمْ تَفَسَّحُوا فِي الْمَجالِسِ فَافْسَحُوا يَفْسَحِ اللَّهُ لَكُمْ وَ إِذا قِيلَ انْشُزُوا فَانْشُزُوا يَرْفَعِ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَ الَّذِينَ أُوتُوا الْعِلْمَ دَرَجاتٍ وَ اللَّهُ بِما تَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5116, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا ناجَيْتُمُ الرَّسُولَ فَقَدِّمُوا بَيْنَ يَدَيْ نَجْواكُمْ صَدَقَةً ذلِكَ خَيْرٌ لَكُمْ وَ أَطْهَرُ فَإِنْ لَمْ تَجِدُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5117, N'أَ أَشْفَقْتُمْ أَنْ تُقَدِّمُوا بَيْنَ يَدَيْ نَجْواكُمْ صَدَقاتٍ فَإِذْ لَمْ تَفْعَلُوا وَ تابَ اللَّهُ عَلَيْكُمْ فَأَقِيمُوا الصَّلاةَ وَ آتُوا الزَّكاةَ وَ أَطِيعُوا اللَّهَ وَ رَسُولَهُ وَ اللَّهُ خَبِيرٌ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5118, N'أَ لَمْ تَرَ إِلَى الَّذِينَ تَوَلَّوْا قَوْماً غَضِبَ اللَّهُ عَلَيْهِمْ ما هُمْ مِنْكُمْ وَ لا مِنْهُمْ وَ يَحْلِفُونَ عَلَى الْكَذِبِ وَ هُمْ يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5119, N'أَعَدَّ اللَّهُ لَهُمْ عَذاباً شَدِيداً إِنَّهُمْ ساءَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5120, N'اتَّخَذُوا أَيْمانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ فَلَهُمْ عَذابٌ مُهِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5121, N'لَنْ تُغْنِيَ عَنْهُمْ أَمْوالُهُمْ وَ لا أَوْلادُهُمْ مِنَ اللَّهِ شَيْئاً أُولئِكَ أَصْحابُ النَّارِ هُمْ فِيها خالِدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5122, N'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعاً فَيَحْلِفُونَ لَهُ كَما يَحْلِفُونَ لَكُمْ وَ يَحْسَبُونَ أَنَّهُمْ عَلى شَيْ‏ءٍ أَلا إِنَّهُمْ هُمُ الْكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5123, N'اسْتَحْوَذَ عَلَيْهِمُ الشَّيْطانُ فَأَنْساهُمْ ذِكْرَ اللَّهِ أُولئِكَ حِزْبُ الشَّيْطانِ أَلا إِنَّ حِزْبَ الشَّيْطانِ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5124, N'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَ رَسُولَهُ أُولئِكَ فِي الْأَذَلِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5125, N'كَتَبَ اللَّهُ لَأَغْلِبَنَّ أَنَا وَ رُسُلِي إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5126, N'لا تَجِدُ قَوْماً يُؤْمِنُونَ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ يُوادُّونَ مَنْ حَادَّ اللَّهَ وَ رَسُولَهُ وَ لَوْ كانُوا آباءَهُمْ أَوْ أَبْناءَهُمْ أَوْ إِخْوانَهُمْ أَوْ عَشِيرَتَهُمْ أُولئِكَ كَتَبَ فِي قُلُوبِهِمُ الْإِيمانَ وَ أَيَّدَهُمْ بِرُوحٍ مِنْهُ وَ يُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها رَضِيَ اللَّهُ عَنْهُمْ وَ رَضُوا عَنْهُ أُولئِكَ حِزْبُ اللَّهِ أَلا إِنَّ حِزْبَ اللَّهِ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5127, N'سَبَّحَ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5128, N'هُوَ الَّذِي أَخْرَجَ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتابِ مِنْ دِيارِهِمْ لِأَوَّلِ الْحَشْرِ ما ظَنَنْتُمْ أَنْ يَخْرُجُوا وَ ظَنُّوا أَنَّهُمْ مانِعَتُهُمْ حُصُونُهُمْ مِنَ اللَّهِ فَأَتاهُمُ اللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا وَ قَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ يُخْرِبُونَ بُيُوتَهُمْ بِأَيْدِيهِمْ وَ أَيْدِي الْمُؤْمِنِينَ فَاعْتَبِرُوا يا أُولِي الْأَبْصارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5129, N'وَ لَوْ لا أَنْ كَتَبَ اللَّهُ عَلَيْهِمُ الْجَلاءَ لَعَذَّبَهُمْ فِي الدُّنْيا وَ لَهُمْ فِي الْآخِرَةِ عَذابُ النَّارِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5130, N'ذلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَ رَسُولَهُ وَ مَنْ يُشَاقِّ اللَّهَ فَإِنَّ اللَّهَ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5131, N'ما قَطَعْتُمْ مِنْ لِينَةٍ أَوْ تَرَكْتُمُوها قائِمَةً عَلى أُصُولِها فَبِإِذْنِ اللَّهِ وَ لِيُخْزِيَ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5132, N'وَ ما أَفاءَ اللَّهُ عَلى رَسُولِهِ مِنْهُمْ فَما أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَ لا رِكابٍ وَ لكِنَّ اللَّهَ يُسَلِّطُ رُسُلَهُ عَلى مَنْ يَشاءُ وَ اللَّهُ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5133, N'ما أَفاءَ اللَّهُ عَلى رَسُولِهِ مِنْ أَهْلِ الْقُرى فَلِلَّهِ وَ لِلرَّسُولِ وَ لِذِي الْقُرْبى وَ الْيَتامى وَ الْمَساكِينِ وَ ابْنِ السَّبِيلِ كَيْ لا يَكُونَ دُولَةً بَيْنَ الْأَغْنِياءِ مِنْكُمْ وَ ما آتاكُمُ الرَّسُولُ فَخُذُوهُ وَ ما نَهاكُمْ عَنْهُ فَانْتَهُوا وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ شَدِيدُ الْعِقابِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5134, N'لِلْفُقَراءِ الْمُهاجِرِينَ الَّذِينَ أُخْرِجُوا مِنْ دِيارِهِمْ وَ أَمْوالِهِمْ يَبْتَغُونَ فَضْلاً مِنَ اللَّهِ وَ رِضْواناً وَ يَنْصُرُونَ اللَّهَ وَ رَسُولَهُ أُولئِكَ هُمُ الصَّادِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5135, N'وَ الَّذِينَ تَبَوَّؤُا الدَّارَ وَ الْإِيمانَ مِنْ قَبْلِهِمْ يُحِبُّونَ مَنْ هاجَرَ إِلَيْهِمْ وَ لا يَجِدُونَ فِي صُدُورِهِمْ حاجَةً مِمَّا أُوتُوا وَ يُؤْثِرُونَ عَلى أَنْفُسِهِمْ وَ لَوْ كانَ بِهِمْ خَصاصَةٌ وَ مَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5136, N'وَ الَّذِينَ جاؤُ مِنْ بَعْدِهِمْ يَقُولُونَ رَبَّنَا اغْفِرْ لَنا وَ لِإِخْوانِنَا الَّذِينَ سَبَقُونا بِالْإِيمانِ وَ لا تَجْعَلْ فِي قُلُوبِنا غِلاًّ لِلَّذِينَ آمَنُوا رَبَّنا إِنَّكَ رَؤُفٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5137, N'أَ لَمْ تَرَ إِلَى الَّذِينَ نافَقُوا يَقُولُونَ لِإِخْوانِهِمُ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتابِ لَئِنْ أُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَ لا نُطِيعُ فِيكُمْ أَحَداً أَبَداً وَ إِنْ قُوتِلْتُمْ لَنَنْصُرَنَّكُمْ وَ اللَّهُ يَشْهَدُ إِنَّهُمْ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5138, N'لَئِنْ أُخْرِجُوا لا يَخْرُجُونَ مَعَهُمْ وَ لَئِنْ قُوتِلُوا لا يَنْصُرُونَهُمْ وَ لَئِنْ نَصَرُوهُمْ لَيُوَلُّنَّ الْأَدْبارَ ثُمَّ لا يُنْصَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5139, N'لَأَنْتُمْ أَشَدُّ رَهْبَةً فِي صُدُورِهِمْ مِنَ اللَّهِ ذلِكَ بِأَنَّهُمْ قَوْمٌ لا يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5140, N'لا يُقاتِلُونَكُمْ جَمِيعاً إِلاَّ فِي قُرىً مُحَصَّنَةٍ أَوْ مِنْ وَراءِ جُدُرٍ بَأْسُهُمْ بَيْنَهُمْ شَدِيدٌ تَحْسَبُهُمْ جَمِيعاً وَ قُلُوبُهُمْ شَتَّى ذلِكَ بِأَنَّهُمْ قَوْمٌ لا يَعْقِلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5141, N'كَمَثَلِ الَّذِينَ مِنْ قَبْلِهِمْ قَرِيباً ذاقُوا وَبالَ أَمْرِهِمْ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5142, N'كَمَثَلِ الشَّيْطانِ إِذْ قالَ لِلْإِنْسانِ اكْفُرْ فَلَمَّا كَفَرَ قالَ إِنِّي بَرِي‏ءٌ مِنْكَ إِنِّي أَخافُ اللَّهَ رَبَّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5143, N'فَكانَ عاقِبَتَهُما أَنَّهُما فِي النَّارِ خالِدَيْنِ فِيها وَ ذلِكَ جَزاءُ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5144, N'يا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَ لْتَنْظُرْ نَفْسٌ ما قَدَّمَتْ لِغَدٍ وَ اتَّقُوا اللَّهَ إِنَّ اللَّهَ خَبِيرٌ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5145, N'وَ لا تَكُونُوا كَالَّذِينَ نَسُوا اللَّهَ فَأَنْساهُمْ أَنْفُسَهُمْ أُولئِكَ هُمُ الْفاسِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5146, N'لا يَسْتَوِي أَصْحابُ النَّارِ وَ أَصْحابُ الْجَنَّةِ أَصْحابُ الْجَنَّةِ هُمُ الْفائِزُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5147, N'لَوْ أَنْزَلْنا هذَا الْقُرْآنَ عَلى جَبَلٍ لَرَأَيْتَهُ خاشِعاً مُتَصَدِّعاً مِنْ خَشْيَةِ اللَّهِ وَ تِلْكَ الْأَمْثالُ نَضْرِبُها لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5148, N'هُوَ اللَّهُ الَّذِي لا إِلهَ إِلاَّ هُوَ عالِمُ الْغَيْبِ وَ الشَّهادَةِ هُوَ الرَّحْمنُ الرَّحِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5149, N'هُوَ اللَّهُ الَّذِي لا إِلهَ إِلاَّ هُوَ الْمَلِكُ الْقُدُّوسُ السَّلامُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيزُ الْجَبَّارُ الْمُتَكَبِّرُ سُبْحانَ اللَّهِ عَمَّا يُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5150, N'هُوَ اللَّهُ الْخالِقُ الْبارِئُ الْمُصَوِّرُ لَهُ الْأَسْماءُ الْحُسْنى يُسَبِّحُ لَهُ ما فِي السَّماواتِ وَ الْأَرْضِ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5151, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَّخِذُوا عَدُوِّي وَ عَدُوَّكُمْ أَوْلِياءَ تُلْقُونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَ قَدْ كَفَرُوا بِما جاءَكُمْ مِنَ الْحَقِّ يُخْرِجُونَ الرَّسُولَ وَ إِيَّاكُمْ أَنْ تُؤْمِنُوا بِاللَّهِ رَبِّكُمْ إِنْ كُنْتُمْ خَرَجْتُمْ جِهاداً فِي سَبِيلِي وَ ابْتِغاءَ مَرْضاتِي تُسِرُّونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَ أَنَا أَعْلَمُ بِما أَخْفَيْتُمْ وَ ما أَعْلَنْتُمْ وَ مَنْ يَفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَواءَ السَّبِيلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5152, N'إِنْ يَثْقَفُوكُمْ يَكُونُوا لَكُمْ أَعْداءً وَ يَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ وَ أَلْسِنَتَهُمْ بِالسُّوءِ وَ وَدُّوا لَوْ تَكْفُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5153, N'لَنْ تَنْفَعَكُمْ أَرْحامُكُمْ وَ لا أَوْلادُكُمْ يَوْمَ الْقِيامَةِ يَفْصِلُ بَيْنَكُمْ وَ اللَّهُ بِما تَعْمَلُونَ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5154, N'قَدْ كانَتْ لَكُمْ أُسْوَةٌ حَسَنَةٌ فِي إِبْراهِيمَ وَ الَّذِينَ مَعَهُ إِذْ قالُوا لِقَوْمِهِمْ إِنَّا بُرَآؤُا مِنْكُمْ وَ مِمَّا تَعْبُدُونَ مِنْ دُونِ اللَّهِ كَفَرْنا بِكُمْ وَ بَدا بَيْنَنا وَ بَيْنَكُمُ الْعَداوَةُ وَ الْبَغْضاءُ أَبَداً حَتَّى تُؤْمِنُوا بِاللَّهِ وَحْدَهُ إِلاَّ قَوْلَ إِبْراهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَ ما أَمْلِكُ لَكَ مِنَ اللَّهِ مِنْ شَيْ‏ءٍ رَبَّنا عَلَيْكَ تَوَكَّلْنا وَ إِلَيْكَ أَنَبْنا وَ إِلَيْكَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5155, N'رَبَّنا لا تَجْعَلْنا فِتْنَةً لِلَّذِينَ كَفَرُوا وَ اغْفِرْ لَنا رَبَّنا إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5156, N'لَقَدْ كانَ لَكُمْ فِيهِمْ أُسْوَةٌ حَسَنَةٌ لِمَنْ كانَ يَرْجُوا اللَّهَ وَ الْيَوْمَ الْآخِرَ وَ مَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5157, N'عَسَى اللَّهُ أَنْ يَجْعَلَ بَيْنَكُمْ وَ بَيْنَ الَّذِينَ عادَيْتُمْ مِنْهُمْ مَوَدَّةً وَ اللَّهُ قَدِيرٌ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5158, N'لا يَنْهاكُمُ اللَّهُ عَنِ الَّذِينَ لَمْ يُقاتِلُوكُمْ فِي الدِّينِ وَ لَمْ يُخْرِجُوكُمْ مِنْ دِيارِكُمْ أَنْ تَبَرُّوهُمْ وَ تُقْسِطُوا إِلَيْهِمْ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5159, N'إِنَّما يَنْهاكُمُ اللَّهُ عَنِ الَّذِينَ قاتَلُوكُمْ فِي الدِّينِ وَ أَخْرَجُوكُمْ مِنْ دِيارِكُمْ وَ ظاهَرُوا عَلى إِخْراجِكُمْ أَنْ تَوَلَّوْهُمْ وَ مَنْ يَتَوَلَّهُمْ فَأُولئِكَ هُمُ الظَّالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5160, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا جاءَكُمُ الْمُؤْمِناتُ مُهاجِراتٍ فَامْتَحِنُوهُنَّ اللَّهُ أَعْلَمُ بِإِيمانِهِنَّ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِناتٍ فَلا تَرْجِعُوهُنَّ إِلَى الْكُفَّارِ لا هُنَّ حِلٌّ لَهُمْ وَ لا هُمْ يَحِلُّونَ لَهُنَّ وَ آتُوهُمْ ما أَنْفَقُوا وَ لا جُناحَ عَلَيْكُمْ أَنْ تَنْكِحُوهُنَّ إِذا آتَيْتُمُوهُنَّ أُجُورَهُنَّ وَ لا تُمْسِكُوا بِعِصَمِ الْكَوافِرِ وَ سْئَلُوا ما أَنْفَقْتُمْ وَ لْيَسْئَلُوا ما أَنْفَقُوا ذلِكُمْ حُكْمُ اللَّهِ يَحْكُمُ بَيْنَكُمْ وَ اللَّهُ عَلِيمٌ حَكِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5161, N'وَ إِنْ فاتَكُمْ شَيْ‏ءٌ مِنْ أَزْواجِكُمْ إِلَى الْكُفَّارِ فَعاقَبْتُمْ فَآتُوا الَّذِينَ ذَهَبَتْ أَزْواجُهُمْ مِثْلَ ما أَنْفَقُوا وَ اتَّقُوا اللَّهَ الَّذِي أَنْتُمْ بِهِ مُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5162, N'يا أَيُّهَا النَّبِيُّ إِذا جاءَكَ الْمُؤْمِناتُ يُبايِعْنَكَ عَلى أَنْ لا يُشْرِكْنَ بِاللَّهِ شَيْئاً وَ لا يَسْرِقْنَ وَ لا يَزْنِينَ وَ لا يَقْتُلْنَ أَوْلادَهُنَّ وَ لا يَأْتِينَ بِبُهْتانٍ يَفْتَرِينَهُ بَيْنَ أَيْدِيهِنَّ وَ أَرْجُلِهِنَّ وَ لا يَعْصِينَكَ فِي مَعْرُوفٍ فَبايِعْهُنَّ وَ اسْتَغْفِرْ لَهُنَّ اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5163, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تَتَوَلَّوْا قَوْماً غَضِبَ اللَّهُ عَلَيْهِمْ قَدْ يَئِسُوا مِنَ الْآخِرَةِ كَما يَئِسَ الْكُفَّارُ مِنْ أَصْحابِ الْقُبُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5164, N'سَبَّحَ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5165, N'يا أَيُّهَا الَّذِينَ آمَنُوا لِمَ تَقُولُونَ ما لا تَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5166, N'كَبُرَ مَقْتاً عِنْدَ اللَّهِ أَنْ تَقُولُوا ما لا تَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5167, N'إِنَّ اللَّهَ يُحِبُّ الَّذِينَ يُقاتِلُونَ فِي سَبِيلِهِ صَفًّا كَأَنَّهُمْ بُنْيانٌ مَرْصُوصٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5168, N'وَ إِذْ قالَ مُوسى لِقَوْمِهِ يا قَوْمِ لِمَ تُؤْذُونَنِي وَ قَدْ تَعْلَمُونَ أَنِّي رَسُولُ اللَّهِ إِلَيْكُمْ فَلَمَّا زاغُوا أَزاغَ اللَّهُ قُلُوبَهُمْ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5169, N'وَ إِذْ قالَ عِيسَى ابْنُ مَرْيَمَ يا بَنِي إِسْرائِيلَ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ مُصَدِّقاً لِما بَيْنَ يَدَيَّ مِنَ التَّوْراةِ وَ مُبَشِّراً بِرَسُولٍ يَأْتِي مِنْ بَعْدِي اسْمُهُ أَحْمَدُ فَلَمَّا جاءَهُمْ بِالْبَيِّناتِ قالُوا هذا سِحْرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5170, N'وَ مَنْ أَظْلَمُ مِمَّنِ افْتَرى عَلَى اللَّهِ الْكَذِبَ وَ هُوَ يُدْعى إِلَى الْإِسْلامِ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5171, N'يُرِيدُونَ لِيُطْفِؤُا نُورَ اللَّهِ بِأَفْواهِهِمْ وَ اللَّهُ مُتِمُّ نُورِهِ وَ لَوْ كَرِهَ الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5172, N'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدى وَ دِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَ لَوْ كَرِهَ الْمُشْرِكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5173, N'يا أَيُّهَا الَّذِينَ آمَنُوا هَلْ أَدُلُّكُمْ عَلى تِجارَةٍ تُنْجِيكُمْ مِنْ عَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5174, N'تُؤْمِنُونَ بِاللَّهِ وَ رَسُولِهِ وَ تُجاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوالِكُمْ وَ أَنْفُسِكُمْ ذلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5175, N'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَ يُدْخِلْكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ وَ مَساكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ ذلِكَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5176, N'وَ أُخْرى تُحِبُّونَها نَصْرٌ مِنَ اللَّهِ وَ فَتْحٌ قَرِيبٌ وَ بَشِّرِ الْمُؤْمِنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5177, N'يا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا أَنْصارَ اللَّهِ كَما قالَ عِيسَى ابْنُ مَرْيَمَ لِلْحَوارِيِّينَ مَنْ أَنْصارِي إِلَى اللَّهِ قالَ الْحَوارِيُّونَ نَحْنُ أَنْصارُ اللَّهِ فَآمَنَتْ طائِفَةٌ مِنْ بَنِي إِسْرائِيلَ وَ كَفَرَتْ طائِفَةٌ فَأَيَّدْنَا الَّذِينَ آمَنُوا عَلى عَدُوِّهِمْ فَأَصْبَحُوا ظاهِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5178, N'يُسَبِّحُ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ الْمَلِكِ الْقُدُّوسِ الْعَزِيزِ الْحَكِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5179, N'هُوَ الَّذِي بَعَثَ فِي الْأُمِّيِّينَ رَسُولاً مِنْهُمْ يَتْلُوا عَلَيْهِمْ آياتِهِ وَ يُزَكِّيهِمْ وَ يُعَلِّمُهُمُ الْكِتابَ وَ الْحِكْمَةَ وَ إِنْ كانُوا مِنْ قَبْلُ لَفِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5180, N'وَ آخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْ وَ هُوَ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5181, N'ذلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشاءُ وَ اللَّهُ ذُو الْفَضْلِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5182, N'مَثَلُ الَّذِينَ حُمِّلُوا التَّوْراةَ ثُمَّ لَمْ يَحْمِلُوها كَمَثَلِ الْحِمارِ يَحْمِلُ أَسْفاراً بِئْسَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآياتِ اللَّهِ وَ اللَّهُ لا يَهْدِي الْقَوْمَ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5183, N'قُلْ يا أَيُّهَا الَّذِينَ هادُوا إِنْ زَعَمْتُمْ أَنَّكُمْ أَوْلِياءُ لِلَّهِ مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5184, N'وَ لا يَتَمَنَّوْنَهُ أَبَداً بِما قَدَّمَتْ أَيْدِيهِمْ وَ اللَّهُ عَلِيمٌ بِالظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5185, N'قُلْ إِنَّ الْمَوْتَ الَّذِي تَفِرُّونَ مِنْهُ فَإِنَّهُ مُلاقِيكُمْ ثُمَّ تُرَدُّونَ إِلى عالِمِ الْغَيْبِ وَ الشَّهادَةِ فَيُنَبِّئُكُمْ بِما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5186, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِذا نُودِيَ لِلصَّلاةِ مِنْ يَوْمِ الْجُمُعَةِ فَاسْعَوْا إِلى ذِكْرِ اللَّهِ وَ ذَرُوا الْبَيْعَ ذلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5187, N'فَإِذا قُضِيَتِ الصَّلاةُ فَانْتَشِرُوا فِي الْأَرْضِ وَ ابْتَغُوا مِنْ فَضْلِ اللَّهِ وَ اذْكُرُوا اللَّهَ كَثِيراً لَعَلَّكُمْ تُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5188, N'وَ إِذا رَأَوْا تِجارَةً أَوْ لَهْواً انْفَضُّوا إِلَيْها وَ تَرَكُوكَ قائِماً قُلْ ما عِنْدَ اللَّهِ خَيْرٌ مِنَ اللَّهْوِ وَ مِنَ التِّجارَةِ وَ اللَّهُ خَيْرُ الرَّازِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5189, N'إِذا جاءَكَ الْمُنافِقُونَ قالُوا نَشْهَدُ إِنَّكَ لَرَسُولُ اللَّهِ وَ اللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُ وَ اللَّهُ يَشْهَدُ إِنَّ الْمُنافِقِينَ لَكاذِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5190, N'اتَّخَذُوا أَيْمانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ إِنَّهُمْ ساءَ ما كانُوا يَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5191, N'ذلِكَ بِأَنَّهُمْ آمَنُوا ثُمَّ كَفَرُوا فَطُبِعَ عَلى قُلُوبِهِمْ فَهُمْ لا يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5192, N'وَ إِذا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسامُهُمْ وَ إِنْ يَقُولُوا تَسْمَعْ لِقَوْلِهِمْ كَأَنَّهُمْ خُشُبٌ مُسَنَّدَةٌ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْ هُمُ الْعَدُوُّ فَاحْذَرْهُمْ قاتَلَهُمُ اللَّهُ أَنَّى يُؤْفَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5193, N'وَ إِذا قِيلَ لَهُمْ تَعالَوْا يَسْتَغْفِرْ لَكُمْ رَسُولُ اللَّهِ لَوَّوْا رُؤُسَهُمْ وَ رَأَيْتَهُمْ يَصُدُّونَ وَ هُمْ مُسْتَكْبِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5194, N'سَواءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْ لَنْ يَغْفِرَ اللَّهُ لَهُمْ إِنَّ اللَّهَ لا يَهْدِي الْقَوْمَ الْفاسِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5195, N'هُمُ الَّذِينَ يَقُولُونَ لا تُنْفِقُوا عَلى مَنْ عِنْدَ رَسُولِ اللَّهِ حَتَّى يَنْفَضُّوا وَ لِلَّهِ خَزائِنُ السَّماواتِ وَ الْأَرْضِ وَ لكِنَّ الْمُنافِقِينَ لا يَفْقَهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5196, N'يَقُولُونَ لَئِنْ رَجَعْنا إِلَى الْمَدِينَةِ لَيُخْرِجَنَّ الْأَعَزُّ مِنْهَا الْأَذَلَّ وَ لِلَّهِ الْعِزَّةُ وَ لِرَسُولِهِ وَ لِلْمُؤْمِنِينَ وَ لكِنَّ الْمُنافِقِينَ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5197, N'يا أَيُّهَا الَّذِينَ آمَنُوا لا تُلْهِكُمْ أَمْوالُكُمْ وَ لا أَوْلادُكُمْ عَنْ ذِكْرِ اللَّهِ وَ مَنْ يَفْعَلْ ذلِكَ فَأُولئِكَ هُمُ الْخاسِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5198, N'وَ أَنْفِقُوا مِنْ ما رَزَقْناكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ أَحَدَكُمُ الْمَوْتُ فَيَقُولَ رَبِّ لَوْ لا أَخَّرْتَنِي إِلى أَجَلٍ قَرِيبٍ فَأَصَّدَّقَ وَ أَكُنْ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5199, N'وَ لَنْ يُؤَخِّرَ اللَّهُ نَفْساً إِذا جاءَ أَجَلُها وَ اللَّهُ خَبِيرٌ بِما تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5200, N'يُسَبِّحُ لِلَّهِ ما فِي السَّماواتِ وَ ما فِي الْأَرْضِ لَهُ الْمُلْكُ وَ لَهُ الْحَمْدُ وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5201, N'هُوَ الَّذِي خَلَقَكُمْ فَمِنْكُمْ كافِرٌ وَ مِنْكُمْ مُؤْمِنٌ وَ اللَّهُ بِما تَعْمَلُونَ بَصِيرٌ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5202, N'خَلَقَ السَّماواتِ وَ الْأَرْضَ بِالْحَقِّ وَ صَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَ إِلَيْهِ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5203, N'يَعْلَمُ ما فِي السَّماواتِ وَ الْأَرْضِ وَ يَعْلَمُ ما تُسِرُّونَ وَ ما تُعْلِنُونَ وَ اللَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5204, N'أَ لَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ كَفَرُوا مِنْ قَبْلُ فَذاقُوا وَبالَ أَمْرِهِمْ وَ لَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5205, N'ذلِكَ بِأَنَّهُ كانَتْ تَأْتِيهِمْ رُسُلُهُمْ بِالْبَيِّناتِ فَقالُوا أَ بَشَرٌ يَهْدُونَنا فَكَفَرُوا وَ تَوَلَّوْا وَ اسْتَغْنَى اللَّهُ وَ اللَّهُ غَنِيٌّ حَمِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5206, N'زَعَمَ الَّذِينَ كَفَرُوا أَنْ لَنْ يُبْعَثُوا قُلْ بَلى وَ رَبِّي لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِما عَمِلْتُمْ وَ ذلِكَ عَلَى اللَّهِ يَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5207, N'فَآمِنُوا بِاللَّهِ وَ رَسُولِهِ وَ النُّورِ الَّذِي أَنْزَلْنا وَ اللَّهُ بِما تَعْمَلُونَ خَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5208, N'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ذلِكَ يَوْمُ التَّغابُنِ وَ مَنْ يُؤْمِنْ بِاللَّهِ وَ يَعْمَلْ صالِحاً يُكَفِّرْ عَنْهُ سَيِّئاتِهِ وَ يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها أَبَداً ذلِكَ الْفَوْزُ الْعَظِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5209, N'وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآياتِنا أُولئِكَ أَصْحابُ النَّارِ خالِدِينَ فِيها وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5210, N'ما أَصابَ مِنْ مُصِيبَةٍ إِلاَّ بِإِذْنِ اللَّهِ وَ مَنْ يُؤْمِنْ بِاللَّهِ يَهْدِ قَلْبَهُ وَ اللَّهُ بِكُلِّ شَيْ‏ءٍ عَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5211, N'وَ أَطِيعُوا اللَّهَ وَ أَطِيعُوا الرَّسُولَ فَإِنْ تَوَلَّيْتُمْ فَإِنَّما عَلى رَسُولِنَا الْبَلاغُ الْمُبِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5212, N'اللَّهُ لا إِلهَ إِلاَّ هُوَ وَ عَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5213, N'يا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ مِنْ أَزْواجِكُمْ وَ أَوْلادِكُمْ عَدُوًّا لَكُمْ فَاحْذَرُوهُمْ وَ إِنْ تَعْفُوا وَ تَصْفَحُوا وَ تَغْفِرُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5214, N'إِنَّما أَمْوالُكُمْ وَ أَوْلادُكُمْ فِتْنَةٌ وَ اللَّهُ عِنْدَهُ أَجْرٌ عَظِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5215, N'فَاتَّقُوا اللَّهَ مَا اسْتَطَعْتُمْ وَ اسْمَعُوا وَ أَطِيعُوا وَ أَنْفِقُوا خَيْراً لِأَنْفُسِكُمْ وَ مَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولئِكَ هُمُ الْمُفْلِحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5216, N'إِنْ تُقْرِضُوا اللَّهَ قَرْضاً حَسَناً يُضاعِفْهُ لَكُمْ وَ يَغْفِرْ لَكُمْ وَ اللَّهُ شَكُورٌ حَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5217, N'عالِمُ الْغَيْبِ وَ الشَّهادَةِ الْعَزِيزُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5218, N'يا أَيُّهَا النَّبِيُّ إِذا طَلَّقْتُمُ النِّساءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَ أَحْصُوا الْعِدَّةَ وَ اتَّقُوا اللَّهَ رَبَّكُمْ لا تُخْرِجُوهُنَّ مِنْ بُيُوتِهِنَّ وَ لا يَخْرُجْنَ إِلاَّ أَنْ يَأْتِينَ بِفاحِشَةٍ مُبَيِّنَةٍ وَ تِلْكَ حُدُودُ اللَّهِ وَ مَنْ يَتَعَدَّ حُدُودَ اللَّهِ فَقَدْ ظَلَمَ نَفْسَهُ لا تَدْرِي لَعَلَّ اللَّهَ يُحْدِثُ بَعْدَ ذلِكَ أَمْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5219, N'فَإِذا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فارِقُوهُنَّ بِمَعْرُوفٍ وَ أَشْهِدُوا ذَوَيْ عَدْلٍ مِنْكُمْ وَ أَقِيمُوا الشَّهادَةَ لِلَّهِ ذلِكُمْ يُوعَظُ بِهِ مَنْ كانَ يُؤْمِنُ بِاللَّهِ وَ الْيَوْمِ الْآخِرِ وَ مَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مَخْرَجاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5220, N'وَ يَرْزُقْهُ مِنْ حَيْثُ لا يَحْتَسِبُ وَ مَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ إِنَّ اللَّهَ بالِغُ أَمْرِهِ قَدْ جَعَلَ اللَّهُ لِكُلِّ شَيْ‏ءٍ قَدْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5221, N'وَ اللاَّئِي يَئِسْنَ مِنَ الْمَحِيضِ مِنْ نِسائِكُمْ إِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلاثَةُ أَشْهُرٍ وَ اللاَّئِي لَمْ يَحِضْنَ وَ أُولاتُ الْأَحْمالِ أَجَلُهُنَّ أَنْ يَضَعْنَ حَمْلَهُنَّ وَ مَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مِنْ أَمْرِهِ يُسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5222, N'ذلِكَ أَمْرُ اللَّهِ أَنْزَلَهُ إِلَيْكُمْ وَ مَنْ يَتَّقِ اللَّهَ يُكَفِّرْ عَنْهُ سَيِّئاتِهِ وَ يُعْظِمْ لَهُ أَجْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5223, N'أَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِنْ وُجْدِكُمْ وَ لا تُضآرُّوهُنَّ لِتُضَيِّقُوا عَلَيْهِنَّ وَ إِنْ كُنَّ أُولاتِ حَمْلٍ فَأَنْفِقُوا عَلَيْهِنَّ حَتَّى يَضَعْنَ حَمْلَهُنَّ فَإِنْ أَرْضَعْنَ لَكُمْ فَآتُوهُنَّ أُجُورَهُنَّ وَ أْتَمِرُوا بَيْنَكُمْ بِمَعْرُوفٍ وَ إِنْ تَعاسَرْتُمْ فَسَتُرْضِعُ لَهُ أُخْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5224, N'لِيُنْفِقْ ذُو سَعَةٍ مِنْ سَعَتِهِ وَ مَنْ قُدِرَ عَلَيْهِ رِزْقُهُ فَلْيُنْفِقْ مِمَّا آتاهُ اللَّهُ لا يُكَلِّفُ اللَّهُ نَفْساً إِلاَّ ما آتاها سَيَجْعَلُ اللَّهُ بَعْدَ عُسْرٍ يُسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5225, N'وَ كَأَيِّنْ مِنْ قَرْيَةٍ عَتَتْ عَنْ أَمْرِ رَبِّها وَ رُسُلِهِ فَحاسَبْناها حِساباً شَدِيداً وَ عَذَّبْناها عَذاباً نُكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5226, N'فَذاقَتْ وَبالَ أَمْرِها وَ كانَ عاقِبَةُ أَمْرِها خُسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5227, N'أَعَدَّ اللَّهُ لَهُمْ عَذاباً شَدِيداً فَاتَّقُوا اللَّهَ يا أُولِي الْأَلْبابِ الَّذِينَ آمَنُوا قَدْ أَنْزَلَ اللَّهُ إِلَيْكُمْ ذِكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5228, N'رَسُولاً يَتْلُوا عَلَيْكُمْ آياتِ اللَّهِ مُبَيِّناتٍ لِيُخْرِجَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ مِنَ الظُّلُماتِ إِلَى النُّورِ وَ مَنْ يُؤْمِنْ بِاللَّهِ وَ يَعْمَلْ صالِحاً يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها أَبَداً قَدْ أَحْسَنَ اللَّهُ لَهُ رِزْقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5229, N'اللَّهُ الَّذِي خَلَقَ سَبْعَ سَماواتٍ وَ مِنَ الْأَرْضِ مِثْلَهُنَّ يَتَنَزَّلُ الْأَمْرُ بَيْنَهُنَّ لِتَعْلَمُوا أَنَّ اللَّهَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ وَ أَنَّ اللَّهَ قَدْ أَحاطَ بِكُلِّ شَيْ‏ءٍ عِلْماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5230, N'يا أَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ ما أَحَلَّ اللَّهُ لَكَ تَبْتَغِي مَرْضاتَ أَزْواجِكَ وَ اللَّهُ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5231, N'قَدْ فَرَضَ اللَّهُ لَكُمْ تَحِلَّةَ أَيْمانِكُمْ وَ اللَّهُ مَوْلاكُمْ وَ هُوَ الْعَلِيمُ الْحَكِيمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5232, N'وَ إِذْ أَسَرَّ النَّبِيُّ إِلى بَعْضِ أَزْواجِهِ حَدِيثاً فَلَمَّا نَبَّأَتْ بِهِ وَ أَظْهَرَهُ اللَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُ وَ أَعْرَضَ عَنْ بَعْضٍ فَلَمَّا نَبَّأَها بِهِ قالَتْ مَنْ أَنْبَأَكَ هذا قالَ نَبَّأَنِيَ الْعَلِيمُ الْخَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5233, N'إِنْ تَتُوبا إِلَى اللَّهِ فَقَدْ صَغَتْ قُلُوبُكُما وَ إِنْ تَظاهَرا عَلَيْهِ فَإِنَّ اللَّهَ هُوَ مَوْلاهُ وَ جِبْرِيلُ وَ صالِحُ الْمُؤْمِنِينَ وَ الْمَلائِكَةُ بَعْدَ ذلِكَ ظَهِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5234, N'عَسى رَبُّهُ إِنْ طَلَّقَكُنَّ أَنْ يُبْدِلَهُ أَزْواجاً خَيْراً مِنْكُنَّ مُسْلِماتٍ مُؤْمِناتٍ قانِتاتٍ تائِباتٍ عابِداتٍ سائِحاتٍ ثَيِّباتٍ وَ أَبْكاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5235, N'يا أَيُّهَا الَّذِينَ آمَنُوا قُوا أَنْفُسَكُمْ وَ أَهْلِيكُمْ ناراً وَقُودُهَا النَّاسُ وَ الْحِجارَةُ عَلَيْها مَلائِكَةٌ غِلاظٌ شِدادٌ لا يَعْصُونَ اللَّهَ ما أَمَرَهُمْ وَ يَفْعَلُونَ ما يُؤْمَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5236, N'يا أَيُّهَا الَّذِينَ كَفَرُوا لا تَعْتَذِرُوا الْيَوْمَ إِنَّما تُجْزَوْنَ ما كُنْتُمْ تَعْمَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5237, N'يا أَيُّهَا الَّذِينَ آمَنُوا تُوبُوا إِلَى اللَّهِ تَوْبَةً نَصُوحاً عَسى رَبُّكُمْ أَنْ يُكَفِّرَ عَنْكُمْ سَيِّئاتِكُمْ وَ يُدْخِلَكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ يَوْمَ لا يُخْزِي اللَّهُ النَّبِيَّ وَ الَّذِينَ آمَنُوا مَعَهُ نُورُهُمْ يَسْعى بَيْنَ أَيْدِيهِمْ وَ بِأَيْمانِهِمْ يَقُولُونَ رَبَّنا أَتْمِمْ لَنا نُورَنا وَ اغْفِرْ لَنا إِنَّكَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5238, N'يا أَيُّهَا النَّبِيُّ جاهِدِ الْكُفَّارَ وَ الْمُنافِقِينَ وَ اغْلُظْ عَلَيْهِمْ وَ مَأْواهُمْ جَهَنَّمُ وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5239, N'ضَرَبَ اللَّهُ مَثَلاً لِلَّذِينَ كَفَرُوا امْرَأَتَ نُوحٍ وَ امْرَأَتَ لُوطٍ كانَتا تَحْتَ عَبْدَيْنِ مِنْ عِبادِنا صالِحَيْنِ فَخانَتاهُما فَلَمْ يُغْنِيا عَنْهُما مِنَ اللَّهِ شَيْئاً وَ قِيلَ ادْخُلاَ النَّارَ مَعَ الدَّاخِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5240, N'وَ ضَرَبَ اللَّهُ مَثَلاً لِلَّذِينَ آمَنُوا امْرَأَتَ فِرْعَوْنَ إِذْ قالَتْ رَبِّ ابْنِ لِي عِنْدَكَ بَيْتاً فِي الْجَنَّةِ وَ نَجِّنِي مِنْ فِرْعَوْنَ وَ عَمَلِهِ وَ نَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5241, N'وَ مَرْيَمَ ابْنَتَ عِمْرانَ الَّتِي أَحْصَنَتْ فَرْجَها فَنَفَخْنا فِيهِ مِنْ رُوحِنا وَ صَدَّقَتْ بِكَلِماتِ رَبِّها وَ كُتُبِهِ وَ كانَتْ مِنَ الْقانِتِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5242, N'تَبارَكَ الَّذِي بِيَدِهِ الْمُلْكُ وَ هُوَ عَلى كُلِّ شَيْ‏ءٍ قَدِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5243, N'الَّذِي خَلَقَ الْمَوْتَ وَ الْحَياةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلاً وَ هُوَ الْعَزِيزُ الْغَفُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5244, N'الَّذِي خَلَقَ سَبْعَ سَماواتٍ طِباقاً ما تَرى فِي خَلْقِ الرَّحْمنِ مِنْ تَفاوُتٍ فَارْجِعِ الْبَصَرَ هَلْ تَرى مِنْ فُطُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5245, N'ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ إِلَيْكَ الْبَصَرُ خاسِئاً وَ هُوَ حَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5246, N'وَ لَقَدْ زَيَّنَّا السَّماءَ الدُّنْيا بِمَصابِيحَ وَ جَعَلْناها رُجُوماً لِلشَّياطِينِ وَ أَعْتَدْنا لَهُمْ عَذابَ السَّعِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5247, N'وَ لِلَّذِينَ كَفَرُوا بِرَبِّهِمْ عَذابُ جَهَنَّمَ وَ بِئْسَ الْمَصِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5248, N'إِذا أُلْقُوا فِيها سَمِعُوا لَها شَهِيقاً وَ هِيَ تَفُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5249, N'تَكادُ تَمَيَّزُ مِنَ الْغَيْظِ كُلَّما أُلْقِيَ فِيها فَوْجٌ سَأَلَهُمْ خَزَنَتُها أَ لَمْ يَأْتِكُمْ نَذِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5250, N'قالُوا بَلى قَدْ جاءَنا نَذِيرٌ فَكَذَّبْنا وَ قُلْنا ما نَزَّلَ اللَّهُ مِنْ شَيْ‏ءٍ إِنْ أَنْتُمْ إِلاَّ فِي ضَلالٍ كَبِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5251, N'وَ قالُوا لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ ما كُنَّا فِي أَصْحابِ السَّعِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5252, N'فَاعْتَرَفُوا بِذَنْبِهِمْ فَسُحْقاً لِأَصْحابِ السَّعِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5253, N'إِنَّ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَغْفِرَةٌ وَ أَجْرٌ كَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5254, N'وَ أَسِرُّوا قَوْلَكُمْ أَوِ اجْهَرُوا بِهِ إِنَّهُ عَلِيمٌ بِذاتِ الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5255, N'أَ لا يَعْلَمُ مَنْ خَلَقَ وَ هُوَ اللَّطِيفُ الْخَبِيرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5256, N'هُوَ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ ذَلُولاً فَامْشُوا فِي مَناكِبِها وَ كُلُوا مِنْ رِزْقِهِ وَ إِلَيْهِ النُّشُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5257, N'أَ أَمِنْتُمْ مَنْ فِي السَّماءِ أَنْ يَخْسِفَ بِكُمُ الْأَرْضَ فَإِذا هِيَ تَمُورُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5258, N'أَمْ أَمِنْتُمْ مَنْ فِي السَّماءِ أَنْ يُرْسِلَ عَلَيْكُمْ حاصِباً فَسَتَعْلَمُونَ كَيْفَ نَذِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5259, N'وَ لَقَدْ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَكَيْفَ كانَ نَكِيرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5260, N'أَ وَ لَمْ يَرَوْا إِلَى الطَّيْرِ فَوْقَهُمْ صافَّاتٍ وَ يَقْبِضْنَ ما يُمْسِكُهُنَّ إِلاَّ الرَّحْمنُ إِنَّهُ بِكُلِّ شَيْ‏ءٍ بَصِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5261, N'أَمَّنْ هذَا الَّذِي هُوَ جُنْدٌ لَكُمْ يَنْصُرُكُمْ مِنْ دُونِ الرَّحْمنِ إِنِ الْكافِرُونَ إِلاَّ فِي غُرُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5262, N'أَمَّنْ هذَا الَّذِي يَرْزُقُكُمْ إِنْ أَمْسَكَ رِزْقَهُ بَلْ لَجُّوا فِي عُتُوٍّ وَ نُفُورٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5263, N'أَ فَمَنْ يَمْشِي مُكِبًّا عَلى وَجْهِهِ أَهْدى أَمَّنْ يَمْشِي سَوِيًّا عَلى صِراطٍ مُسْتَقِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5264, N'قُلْ هُوَ الَّذِي أَنْشَأَكُمْ وَ جَعَلَ لَكُمُ السَّمْعَ وَ الْأَبْصارَ وَ الْأَفْئِدَةَ قَلِيلاً ما تَشْكُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5265, N'قُلْ هُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَ إِلَيْهِ تُحْشَرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5266, N'وَ يَقُولُونَ مَتى هذَا الْوَعْدُ إِنْ كُنْتُمْ صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5267, N'قُلْ إِنَّمَا الْعِلْمُ عِنْدَ اللَّهِ وَ إِنَّما أَنَا نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5268, N'فَلَمَّا رَأَوْهُ زُلْفَةً سِيئَتْ وُجُوهُ الَّذِينَ كَفَرُوا وَ قِيلَ هذَا الَّذِي كُنْتُمْ بِهِ تَدَّعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5269, N'قُلْ أَ رَأَيْتُمْ إِنْ أَهْلَكَنِيَ اللَّهُ وَ مَنْ مَعِيَ أَوْ رَحِمَنا فَمَنْ يُجِيرُ الْكافِرِينَ مِنْ عَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5270, N'قُلْ هُوَ الرَّحْمنُ آمَنَّا بِهِ وَ عَلَيْهِ تَوَكَّلْنا فَسَتَعْلَمُونَ مَنْ هُوَ فِي ضَلالٍ مُبِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5271, N'قُلْ أَ رَأَيْتُمْ إِنْ أَصْبَحَ ماؤُكُمْ غَوْراً فَمَنْ يَأْتِيكُمْ بِماءٍ مَعِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5272, N'ن وَ الْقَلَمِ وَ ما يَسْطُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5273, N'ما أَنْتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5274, N'وَ إِنَّ لَكَ لَأَجْراً غَيْرَ مَمْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5275, N'وَ إِنَّكَ لَعَلى خُلُقٍ عَظِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5276, N'فَسَتُبْصِرُ وَ يُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5277, N'بِأَيِّكُمُ الْمَفْتُونُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5278, N'إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَ هُوَ أَعْلَمُ بِالْمُهْتَدِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5279, N'فَلا تُطِعِ الْمُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5280, N'وَدُّوا لَوْ تُدْهِنُ فَيُدْهِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5281, N'وَ لا تُطِعْ كُلَّ حَلاَّفٍ مَهِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5282, N'هَمَّازٍ مَشَّاءٍ بِنَمِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5283, N'مَنَّاعٍ لِلْخَيْرِ مُعْتَدٍ أَثِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5284, N'عُتُلٍّ بَعْدَ ذلِكَ زَنِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5285, N'أَنْ كانَ ذا مالٍ وَ بَنِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5286, N'إِذا تُتْلى عَلَيْهِ آياتُنا قالَ أَساطِيرُ الْأَوَّلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5287, N'سَنَسِمُهُ عَلَى الْخُرْطُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5288, N'إِنَّا بَلَوْناهُمْ كَما بَلَوْنا أَصْحابَ الْجَنَّةِ إِذْ أَقْسَمُوا لَيَصْرِمُنَّها مُصْبِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5289, N'وَ لا يَسْتَثْنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5290, N'فَطافَ عَلَيْها طائِفٌ مِنْ رَبِّكَ وَ هُمْ نائِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5291, N'فَأَصْبَحَتْ كَالصَّرِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5292, N'فَتَنادَوْا مُصْبِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5293, N'أَنِ اغْدُوا عَلى حَرْثِكُمْ إِنْ كُنْتُمْ صارِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5294, N'فَانْطَلَقُوا وَ هُمْ يَتَخافَتُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5295, N'أَنْ لا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُمْ مِسْكِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5296, N'وَ غَدَوْا عَلى حَرْدٍ قادِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5297, N'فَلَمَّا رَأَوْها قالُوا إِنَّا لَضَالُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5298, N'بَلْ نَحْنُ مَحْرُومُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5299, N'قالَ أَوْسَطُهُمْ أَ لَمْ أَقُلْ لَكُمْ لَوْ لا تُسَبِّحُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5300, N'قالُوا سُبْحانَ رَبِّنا إِنَّا كُنَّا ظالِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5301, N'فَأَقْبَلَ بَعْضُهُمْ عَلى بَعْضٍ يَتَلاوَمُونَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5302, N'قالُوا يا وَيْلَنا إِنَّا كُنَّا طاغِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5303, N'عَسى رَبُّنا أَنْ يُبْدِلَنا خَيْراً مِنْها إِنَّا إِلى رَبِّنا راغِبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5304, N'كَذلِكَ الْعَذابُ وَ لَعَذابُ الْآخِرَةِ أَكْبَرُ لَوْ كانُوا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5305, N'إِنَّ لِلْمُتَّقِينَ عِنْدَ رَبِّهِمْ جَنَّاتِ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5306, N'أَ فَنَجْعَلُ الْمُسْلِمِينَ كَالْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5307, N'ما لَكُمْ كَيْفَ تَحْكُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5308, N'أَمْ لَكُمْ كِتابٌ فِيهِ تَدْرُسُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5309, N'إِنَّ لَكُمْ فِيهِ لَما تَخَيَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5310, N'أَمْ لَكُمْ أَيْمانٌ عَلَيْنا بالِغَةٌ إِلى يَوْمِ الْقِيامَةِ إِنَّ لَكُمْ لَما تَحْكُمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5311, N'سَلْهُمْ أَيُّهُمْ بِذلِكَ زَعِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5312, N'أَمْ لَهُمْ شُرَكاءُ فَلْيَأْتُوا بِشُرَكائِهِمْ إِنْ كانُوا صادِقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5313, N'يَوْمَ يُكْشَفُ عَنْ ساقٍ وَ يُدْعَوْنَ إِلَى السُّجُودِ فَلا يَسْتَطِيعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5314, N'خاشِعَةً أَبْصارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ وَ قَدْ كانُوا يُدْعَوْنَ إِلَى السُّجُودِ وَ هُمْ سالِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5315, N'فَذَرْنِي وَ مَنْ يُكَذِّبُ بِهذَا الْحَدِيثِ سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5316, N'وَ أُمْلِي لَهُمْ إِنَّ كَيْدِي مَتِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5317, N'أَمْ تَسْئَلُهُمْ أَجْراً فَهُمْ مِنْ مَغْرَمٍ مُثْقَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5318, N'أَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5319, N'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَ لا تَكُنْ كَصاحِبِ الْحُوتِ إِذْ نادى وَ هُوَ مَكْظُومٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5320, N'لَوْ لا أَنْ تَدارَكَهُ نِعْمَةٌ مِنْ رَبِّهِ لَنُبِذَ بِالْعَراءِ وَ هُوَ مَذْمُومٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5321, N'فَاجْتَباهُ رَبُّهُ فَجَعَلَهُ مِنَ الصَّالِحِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5322, N'وَ إِنْ يَكادُ الَّذِينَ كَفَرُوا لَيُزْلِقُونَكَ بِأَبْصارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَ يَقُولُونَ إِنَّهُ لَمَجْنُونٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5323, N'وَ ما هُوَ إِلاَّ ذِكْرٌ لِلْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5324, N'الْحَاقَّةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5325, N'مَا الْحَاقَّةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5326, N'وَ ما أَدْراكَ مَا الْحَاقَّةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5327, N'كَذَّبَتْ ثَمُودُ وَ عادٌ بِالْقارِعَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5328, N'فَأَمَّا ثَمُودُ فَأُهْلِكُوا بِالطَّاغِيَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5329, N'وَ أَمَّا عادٌ فَأُهْلِكُوا بِرِيحٍ صَرْصَرٍ عاتِيَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5330, N'سَخَّرَها عَلَيْهِمْ سَبْعَ لَيالٍ وَ ثَمانِيَةَ أَيَّامٍ حُسُوماً فَتَرَى الْقَوْمَ فِيها صَرْعى كَأَنَّهُمْ أَعْجازُ نَخْلٍ خاوِيَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5331, N'فَهَلْ تَرى لَهُمْ مِنْ باقِيَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5332, N'وَ جاءَ فِرْعَوْنُ وَ مَنْ قَبْلَهُ وَ الْمُؤْتَفِكاتُ بِالْخاطِئَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5333, N'فَعَصَوْا رَسُولَ رَبِّهِمْ فَأَخَذَهُمْ أَخْذَةً رابِيَةً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5334, N'إِنَّا لَمَّا طَغَى الْماءُ حَمَلْناكُمْ فِي الْجارِيَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5335, N'لِنَجْعَلَها لَكُمْ تَذْكِرَةً وَ تَعِيَها أُذُنٌ واعِيَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5336, N'فَإِذا نُفِخَ فِي الصُّورِ نَفْخَةٌ واحِدَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5337, N'وَ حُمِلَتِ الْأَرْضُ وَ الْجِبالُ فَدُكَّتا دَكَّةً واحِدَةً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5338, N'فَيَوْمَئِذٍ وَقَعَتِ الْواقِعَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5339, N'وَ انْشَقَّتِ السَّماءُ فَهِيَ يَوْمَئِذٍ واهِيَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5340, N'وَ الْمَلَكُ عَلى أَرْجائِها وَ يَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمانِيَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5341, N'يَوْمَئِذٍ تُعْرَضُونَ لا تَخْفى مِنْكُمْ خافِيَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5342, N'فَأَمَّا مَنْ أُوتِيَ كِتابَهُ بِيَمِينِهِ فَيَقُولُ هاؤُمُ اقْرَؤُا كِتابِيَهْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5343, N'إِنِّي ظَنَنْتُ أَنِّي مُلاقٍ حِسابِيَهْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5344, N'فَهُوَ فِي عِيشَةٍ راضِيَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5345, N'فِي جَنَّةٍ عالِيَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5346, N'قُطُوفُها دانِيَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5347, N'كُلُوا وَ اشْرَبُوا هَنِيئاً بِما أَسْلَفْتُمْ فِي الْأَيَّامِ الْخالِيَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5348, N'وَ أَمَّا مَنْ أُوتِيَ كِتابَهُ بِشِمالِهِ فَيَقُولُ يا لَيْتَنِي لَمْ أُوتَ كِتابِيَهْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5349, N'وَ لَمْ أَدْرِ ما حِسابِيَهْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5350, N'يا لَيْتَها كانَتِ الْقاضِيَةَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5351, N'ما أَغْنى عَنِّي مالِيَهْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5352, N'هَلَكَ عَنِّي سُلْطانِيَهْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5353, N'خُذُوهُ فَغُلُّوهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5354, N'ثُمَّ الْجَحِيمَ صَلُّوهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5355, N'ثُمَّ فِي سِلْسِلَةٍ ذَرْعُها سَبْعُونَ ذِراعاً فَاسْلُكُوهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5356, N'إِنَّهُ كانَ لا يُؤْمِنُ بِاللَّهِ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5357, N'وَ لا يَحُضُّ عَلى طَعامِ الْمِسْكِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5358, N'فَلَيْسَ لَهُ الْيَوْمَ هاهُنا حَمِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5359, N'وَ لا طَعامٌ إِلاَّ مِنْ غِسْلِينٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5360, N'لا يَأْكُلُهُ إِلاَّ الْخاطِؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5361, N'فَلا أُقْسِمُ بِما تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5362, N'وَ ما لا تُبْصِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5363, N'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5364, N'وَ ما هُوَ بِقَوْلِ شاعِرٍ قَلِيلاً ما تُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5365, N'وَ لا بِقَوْلِ كاهِنٍ قَلِيلاً ما تَذَكَّرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5366, N'تَنْزِيلٌ مِنْ رَبِّ الْعالَمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5367, N'وَ لَوْ تَقَوَّلَ عَلَيْنا بَعْضَ الْأَقاوِيلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5368, N'لَأَخَذْنا مِنْهُ بِالْيَمِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5369, N'ثُمَّ لَقَطَعْنا مِنْهُ الْوَتِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5370, N'فَما مِنْكُمْ مِنْ أَحَدٍ عَنْهُ حاجِزِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5371, N'وَ إِنَّهُ لَتَذْكِرَةٌ لِلْمُتَّقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5372, N'وَ إِنَّا لَنَعْلَمُ أَنَّ مِنْكُمْ مُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5373, N'وَ إِنَّهُ لَحَسْرَةٌ عَلَى الْكافِرِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5374, N'وَ إِنَّهُ لَحَقُّ الْيَقِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5375, N'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5376, N'سَأَلَ سائِلٌ بِعَذابٍ واقِعٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5377, N'لِلْكافِرينَ لَيْسَ لَهُ دافِعٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5378, N'مِنَ اللَّهِ ذِي الْمَعارِجِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5379, N'تَعْرُجُ الْمَلائِكَةُ وَ الرُّوحُ إِلَيْهِ فِي يَوْمٍ كانَ مِقْدارُهُ خَمْسِينَ أَلْفَ سَنَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5380, N'فَاصْبِرْ صَبْراً جَمِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5381, N'إِنَّهُمْ يَرَوْنَهُ بَعِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5382, N'وَ نَراهُ قَرِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5383, N'يَوْمَ تَكُونُ السَّماءُ كَالْمُهْلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5384, N'وَ تَكُونُ الْجِبالُ كَالْعِهْنِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5385, N'وَ لا يَسْئَلُ حَمِيمٌ حَمِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5386, N'يُبَصَّرُونَهُمْ يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَدِي مِنْ عَذابِ يَوْمِئِذٍ بِبَنِيهِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5387, N'وَ صاحِبَتِهِ وَ أَخِيهِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5388, N'وَ فَصِيلَتِهِ الَّتِي تُؤْوِيهِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5389, N'وَ مَنْ فِي الْأَرْضِ جَمِيعاً ثُمَّ يُنْجِيهِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5390, N'كَلاَّ إِنَّها لَظى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5391, N'نَزَّاعَةً لِلشَّوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5392, N'تَدْعُوا مَنْ أَدْبَرَ وَ تَوَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5393, N'وَ جَمَعَ فَأَوْعى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5394, N'إِنَّ الْإِنْسانَ خُلِقَ هَلُوعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5395, N'إِذا مَسَّهُ الشَّرُّ جَزُوعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5396, N'وَ إِذا مَسَّهُ الْخَيْرُ مَنُوعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5397, N'إِلاَّ الْمُصَلِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5398, N'الَّذِينَ هُمْ عَلى صَلاتِهِمْ دائِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5399, N'وَ الَّذِينَ فِي أَمْوالِهِمْ حَقٌّ مَعْلُومٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5400, N'لِلسَّائِلِ وَ الْمَحْرُومِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5401, N'وَ الَّذِينَ يُصَدِّقُونَ بِيَوْمِ الدِّينِ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5402, N'وَ الَّذِينَ هُمْ مِنْ عَذابِ رَبِّهِمْ مُشْفِقُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5403, N'إِنَّ عَذابَ رَبِّهِمْ غَيْرُ مَأْمُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5404, N'وَ الَّذِينَ هُمْ لِفُرُوجِهِمْ حافِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5405, N'إِلاَّ عَلى أَزْواجِهِمْ أَوْ ما مَلَكَتْ أَيْمانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5406, N'فَمَنِ ابْتَغى وَراءَ ذلِكَ فَأُولئِكَ هُمُ العادُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5407, N'وَ الَّذِينَ هُمْ لِأَماناتِهِمْ وَ عَهْدِهِمْ راعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5408, N'وَ الَّذِينَ هُمْ بِشَهاداتِهِمْ قائِمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5409, N'وَ الَّذِينَ هُمْ عَلى صَلاتِهِمْ يُحافِظُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5410, N'أُولئِكَ فِي جَنَّاتٍ مُكْرَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5411, N'فَما لِ الَّذِينَ كَفَرُوا قِبَلَكَ مُهْطِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5412, N'عَنِ الْيَمِينِ وَ عَنِ الشِّمالِ عِزِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5413, N'أَ يَطْمَعُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُدْخَلَ جَنَّةَ نَعِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5414, N'كَلاَّ إِنَّا خَلَقْناهُمْ مِمَّا يَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5415, N'فَلا أُقْسِمُ بِرَبِّ الْمَشارِقِ وَ الْمَغارِبِ إِنَّا لَقادِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5416, N'عَلى أَنْ نُبَدِّلَ خَيْراً مِنْهُمْ وَ ما نَحْنُ بِمَسْبُوقِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5417, N'فَذَرْهُمْ يَخُوضُوا وَ يَلْعَبُوا حَتَّى يُلاقُوا يَوْمَهُمُ الَّذِي يُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5418, N'يَوْمَ يَخْرُجُونَ مِنَ الْأَجْداثِ سِراعاً كَأَنَّهُمْ إِلى نُصُبٍ يُوفِضُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5419, N'خاشِعَةً أَبْصارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ذلِكَ الْيَوْمُ الَّذِي كانُوا يُوعَدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5420, N'إِنَّا أَرْسَلْنا نُوحاً إِلى قَوْمِهِ أَنْ أَنْذِرْ قَوْمَكَ مِنْ قَبْلِ أَنْ يَأْتِيَهُمْ عَذابٌ أَلِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5421, N'قالَ يا قَوْمِ إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5422, N'أَنِ اعْبُدُوا اللَّهَ وَ اتَّقُوهُ وَ أَطِيعُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5423, N'يَغْفِرْ لَكُمْ مِنْ ذُنُوبِكُمْ وَ يُؤَخِّرْكُمْ إِلى أَجَلٍ مُسَمًّى إِنَّ أَجَلَ اللَّهِ إِذا جاءَ لا يُؤَخَّرُ لَوْ كُنْتُمْ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5424, N'قالَ رَبِّ إِنِّي دَعَوْتُ قَوْمِي لَيْلاً وَ نَهاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5425, N'فَلَمْ يَزِدْهُمْ دُعائِي إِلاَّ فِراراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5426, N'وَ إِنِّي كُلَّما دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوا أَصابِعَهُمْ فِي آذانِهِمْ وَ اسْتَغْشَوْا ثِيابَهُمْ وَ أَصَرُّوا وَ اسْتَكْبَرُوا اسْتِكْباراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5427, N'ثُمَّ إِنِّي دَعَوْتُهُمْ جِهاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5428, N'ثُمَّ إِنِّي أَعْلَنْتُ لَهُمْ وَ أَسْرَرْتُ لَهُمْ إِسْراراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5429, N'فَقُلْتُ اسْتَغْفِرُوا رَبَّكُمْ إِنَّهُ كانَ غَفَّاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5430, N'يُرْسِلِ السَّماءَ عَلَيْكُمْ مِدْراراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5431, N'وَ يُمْدِدْكُمْ بِأَمْوالٍ وَ بَنِينَ وَ يَجْعَلْ لَكُمْ جَنَّاتٍ وَ يَجْعَلْ لَكُمْ أَنْهاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5432, N'ما لَكُمْ لا تَرْجُونَ لِلَّهِ وَقاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5433, N'وَ قَدْ خَلَقَكُمْ أَطْواراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5434, N'أَ لَمْ تَرَوْا كَيْفَ خَلَقَ اللَّهُ سَبْعَ سَماواتٍ طِباقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5435, N'وَ جَعَلَ الْقَمَرَ فِيهِنَّ نُوراً وَ جَعَلَ الشَّمْسَ سِراجاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5436, N'وَ اللَّهُ أَنْبَتَكُمْ مِنَ الْأَرْضِ نَباتاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5437, N'ثُمَّ يُعِيدُكُمْ فِيها وَ يُخْرِجُكُمْ إِخْراجاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5438, N'وَ اللَّهُ جَعَلَ لَكُمُ الْأَرْضَ بِساطاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5439, N'لِتَسْلُكُوا مِنْها سُبُلاً فِجاجاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5440, N'قالَ نُوحٌ رَبِّ إِنَّهُمْ عَصَوْنِي وَ اتَّبَعُوا مَنْ لَمْ يَزِدْهُ مالُهُ وَ وَلَدُهُ إِلاَّ خَساراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5441, N'وَ مَكَرُوا مَكْراً كُبَّاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5442, N'وَ قالُوا لا تَذَرُنَّ آلِهَتَكُمْ وَ لا تَذَرُنَّ وَدًّا وَ لا سُواعاً وَ لا يَغُوثَ وَ يَعُوقَ وَ نَسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5443, N'وَ قَدْ أَضَلُّوا كَثِيراً وَ لا تَزِدِ الظَّالِمِينَ إِلاَّ ضَلالاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5444, N'مِمَّا خَطِيئاتِهِمْ أُغْرِقُوا فَأُدْخِلُوا ناراً فَلَمْ يَجِدُوا لَهُمْ مِنْ دُونِ اللَّهِ أَنْصاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5445, N'وَ قالَ نُوحٌ رَبِّ لا تَذَرْ عَلَى الْأَرْضِ مِنَ الْكافِرِينَ دَيَّاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5446, N'إِنَّكَ إِنْ تَذَرْهُمْ يُضِلُّوا عِبادَكَ وَ لا يَلِدُوا إِلاَّ فاجِراً كَفَّاراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5447, N'رَبِّ اغْفِرْ لِي وَ لِوالِدَيَّ وَ لِمَنْ دَخَلَ بَيْتِيَ مُؤْمِناً وَ لِلْمُؤْمِنِينَ وَ الْمُؤْمِناتِ وَ لا تَزِدِ الظَّالِمِينَ إِلاَّ تَباراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5448, N'قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِنَ الْجِنِّ فَقالُوا إِنَّا سَمِعْنا قُرْآناً عَجَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5449, N'يَهْدِي إِلَى الرُّشْدِ فَآمَنَّا بِهِ وَ لَنْ نُشْرِكَ بِرَبِّنا أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5450, N'وَ أَنَّهُ تَعالى جَدُّ رَبِّنا مَا اتَّخَذَ صاحِبَةً وَ لا وَلَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5451, N'وَ أَنَّهُ كانَ يَقُولُ سَفِيهُنا عَلَى اللَّهِ شَطَطاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5452, N'وَ أَنَّا ظَنَنَّا أَنْ لَنْ تَقُولَ الْإِنْسُ وَ الْجِنُّ عَلَى اللَّهِ كَذِباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5453, N'وَ أَنَّهُ كانَ رِجالٌ مِنَ الْإِنْسِ يَعُوذُونَ بِرِجالٍ مِنَ الْجِنِّ فَزادُوهُمْ رَهَقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5454, N'وَ أَنَّهُمْ ظَنُّوا كَما ظَنَنْتُمْ أَنْ لَنْ يَبْعَثَ اللَّهُ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5455, N'وَ أَنَّا لَمَسْنَا السَّماءَ فَوَجَدْناها مُلِئَتْ حَرَساً شَدِيداً وَ شُهُباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5456, N'وَ أَنَّا كُنَّا نَقْعُدُ مِنْها مَقاعِدَ لِلسَّمْعِ فَمَنْ يَسْتَمِعِ الْآنَ يَجِدْ لَهُ شِهاباً رَصَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5457, N'وَ أَنَّا لا نَدْرِي أَ شَرٌّ أُرِيدَ بِمَنْ فِي الْأَرْضِ أَمْ أَرادَ بِهِمْ رَبُّهُمْ رَشَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5458, N'وَ أَنَّا مِنَّا الصَّالِحُونَ وَ مِنَّا دُونَ ذلِكَ كُنَّا طَرائِقَ قِدَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5459, N'وَ أَنَّا ظَنَنَّا أَنْ لَنْ نُعْجِزَ اللَّهَ فِي الْأَرْضِ وَ لَنْ نُعْجِزَهُ هَرَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5460, N'وَ أَنَّا لَمَّا سَمِعْنَا الْهُدى آمَنَّا بِهِ فَمَنْ يُؤْمِنْ بِرَبِّهِ فَلا يَخافُ بَخْساً وَ لا رَهَقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5461, N'وَ أَنَّا مِنَّا الْمُسْلِمُونَ وَ مِنَّا الْقاسِطُونَ فَمَنْ أَسْلَمَ فَأُولئِكَ تَحَرَّوْا رَشَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5462, N'وَ أَمَّا الْقاسِطُونَ فَكانُوا لِجَهَنَّمَ حَطَباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5463, N'وَ أَنْ لَوِ اسْتَقامُوا عَلَى الطَّرِيقَةِ لَأَسْقَيْناهُمْ ماءً غَدَقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5464, N'لِنَفْتِنَهُمْ فِيهِ وَ مَنْ يُعْرِضْ عَنْ ذِكْرِ رَبِّهِ يَسْلُكْهُ عَذاباً صَعَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5465, N'وَ أَنَّ الْمَساجِدَ لِلَّهِ فَلا تَدْعُوا مَعَ اللَّهِ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5466, N'وَ أَنَّهُ لَمَّا قامَ عَبْدُ اللَّهِ يَدْعُوهُ كادُوا يَكُونُونَ عَلَيْهِ لِبَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5467, N'قُلْ إِنَّما أَدْعُوا رَبِّي وَ لا أُشْرِكُ بِهِ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5468, N'قُلْ إِنِّي لا أَمْلِكُ لَكُمْ ضَرًّا وَ لا رَشَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5469, N'قُلْ إِنِّي لَنْ يُجِيرَنِي مِنَ اللَّهِ أَحَدٌ وَ لَنْ أَجِدَ مِنْ دُونِهِ مُلْتَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5470, N'إِلاَّ بَلاغاً مِنَ اللَّهِ وَ رِسالاتِهِ وَ مَنْ يَعْصِ اللَّهَ وَ رَسُولَهُ فَإِنَّ لَهُ نارَ جَهَنَّمَ خالِدِينَ فِيها أَبَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5471, N'حَتَّى إِذا رَأَوْا ما يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ أَضْعَفُ ناصِراً وَ أَقَلُّ عَدَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5472, N'قُلْ إِنْ أَدْرِي أَ قَرِيبٌ ما تُوعَدُونَ أَمْ يَجْعَلُ لَهُ رَبِّي أَمَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5473, N'عالِمُ الْغَيْبِ فَلا يُظْهِرُ عَلى غَيْبِهِ أَحَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5474, N'إِلاَّ مَنِ ارْتَضى مِنْ رَسُولٍ فَإِنَّهُ يَسْلُكُ مِنْ بَيْنِ يَدَيْهِ وَ مِنْ خَلْفِهِ رَصَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5475, N'لِيَعْلَمَ أَنْ قَدْ أَبْلَغُوا رِسالاتِ رَبِّهِمْ وَ أَحاطَ بِما لَدَيْهِمْ وَ أَحْصى كُلَّ شَيْ‏ءٍ عَدَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5476, N'يا أَيُّهَا الْمُزَّمِّلُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5477, N'قُمِ اللَّيْلَ إِلاَّ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5478, N'نِصْفَهُ أَوِ انْقُصْ مِنْهُ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5479, N'أَوْ زِدْ عَلَيْهِ وَ رَتِّلِ الْقُرْآنَ تَرْتِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5480, N'إِنَّا سَنُلْقِي عَلَيْكَ قَوْلاً ثَقِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5481, N'إِنَّ ناشِئَةَ اللَّيْلِ هِيَ أَشَدُّ وَطْئاً وَ أَقْوَمُ قِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5482, N'إِنَّ لَكَ فِي النَّهارِ سَبْحاً طَوِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5483, N'وَ اذْكُرِ اسْمَ رَبِّكَ وَ تَبَتَّلْ إِلَيْهِ تَبْتِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5484, N'رَبُّ الْمَشْرِقِ وَ الْمَغْرِبِ لا إِلهَ إِلاَّ هُوَ فَاتَّخِذْهُ وَكِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5485, N'وَ اصْبِرْ عَلى ما يَقُولُونَ وَ اهْجُرْهُمْ هَجْراً جَمِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5486, N'وَ ذَرْنِي وَ الْمُكَذِّبِينَ أُولِي النَّعْمَةِ وَ مَهِّلْهُمْ قَلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5487, N'إِنَّ لَدَيْنا أَنْكالاً وَ جَحِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5488, N'وَ طَعاماً ذا غُصَّةٍ وَ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5489, N'يَوْمَ تَرْجُفُ الْأَرْضُ وَ الْجِبالُ وَ كانَتِ الْجِبالُ كَثِيباً مَهِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5490, N'إِنَّا أَرْسَلْنا إِلَيْكُمْ رَسُولاً شاهِداً عَلَيْكُمْ كَما أَرْسَلْنا إِلى فِرْعَوْنَ رَسُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5491, N'فَعَصى فِرْعَوْنُ الرَّسُولَ فَأَخَذْناهُ أَخْذاً وَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5492, N'فَكَيْفَ تَتَّقُونَ إِنْ كَفَرْتُمْ يَوْماً يَجْعَلُ الْوِلْدانَ شِيباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5493, N'السَّماءُ مُنْفَطِرٌ بِهِ كانَ وَعْدُهُ مَفْعُولاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5494, N'إِنَّ هذِهِ تَذْكِرَةٌ فَمَنْ شاءَ اتَّخَذَ إِلى رَبِّهِ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5495, N'إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنى مِنْ ثُلُثَيِ اللَّيْلِ وَ نِصْفَهُ وَ ثُلُثَهُ وَ طائِفَةٌ مِنَ الَّذِينَ مَعَكَ وَ اللَّهُ يُقَدِّرُ اللَّيْلَ وَ النَّهارَ عَلِمَ أَنْ لَنْ تُحْصُوهُ فَتابَ عَلَيْكُمْ فَاقْرَؤُا ما تَيَسَّرَ مِنَ الْقُرْآنِ عَلِمَ أَنْ سَيَكُونُ مِنْكُمْ مَرْضى وَ آخَرُونَ يَضْرِبُونَ فِي الْأَرْضِ يَبْتَغُونَ مِنْ فَضْلِ اللَّهِ وَ آخَرُونَ يُقاتِلُونَ فِي سَبِيلِ اللَّهِ فَاقْرَؤُا ما تَيَسَّرَ مِنْهُ وَ أَقِيمُوا الصَّلاةَ وَ آتُوا الزَّكاةَ وَ أَقْرِضُوا اللَّهَ قَرْضاً حَسَناً وَ ما تُقَدِّمُوا لِأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللَّهِ هُوَ خَيْراً وَ أَعْظَمَ أَجْراً وَ اسْتَغْفِرُوا اللَّهَ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5496, N'يا أَيُّهَا الْمُدَّثِّرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5497, N'قُمْ فَأَنْذِرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5498, N'وَ رَبَّكَ فَكَبِّرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5499, N'وَ ثِيابَكَ فَطَهِّرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5500, N'وَ الرُّجْزَ فَاهْجُرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5501, N'وَ لا تَمْنُنْ تَسْتَكْثِرُ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5502, N'وَ لِرَبِّكَ فَاصْبِرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5503, N'فَإِذا نُقِرَ فِي النَّاقُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5504, N'فَذلِكَ يَوْمَئِذٍ يَوْمٌ عَسِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5505, N'عَلَى الْكافِرِينَ غَيْرُ يَسِيرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5506, N'ذَرْنِي وَ مَنْ خَلَقْتُ وَحِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5507, N'وَ جَعَلْتُ لَهُ مالاً مَمْدُوداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5508, N'وَ بَنِينَ شُهُوداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5509, N'وَ مَهَّدْتُ لَهُ تَمْهِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5510, N'ثُمَّ يَطْمَعُ أَنْ أَزِيدَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5511, N'كَلاَّ إِنَّهُ كانَ لِآياتِنا عَنِيداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5512, N'سَأُرْهِقُهُ صَعُوداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5513, N'إِنَّهُ فَكَّرَ وَ قَدَّرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5514, N'فَقُتِلَ كَيْفَ قَدَّرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5515, N'ثُمَّ قُتِلَ كَيْفَ قَدَّرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5516, N'ثُمَّ نَظَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5517, N'ثُمَّ عَبَسَ وَ بَسَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5518, N'ثُمَّ أَدْبَرَ وَ اسْتَكْبَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5519, N'فَقالَ إِنْ هذا إِلاَّ سِحْرٌ يُؤْثَرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5520, N'إِنْ هذا إِلاَّ قَوْلُ الْبَشَرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5521, N'سَأُصْلِيهِ سَقَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5522, N'وَ ما أَدْراكَ ما سَقَرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5523, N'لا تُبْقِي وَ لا تَذَرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5524, N'لَوَّاحَةٌ لِلْبَشَرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5525, N'عَلَيْها تِسْعَةَ عَشَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5526, N'وَ ما جَعَلْنا أَصْحابَ النَّارِ إِلاَّ مَلائِكَةً وَ ما جَعَلْنا عِدَّتَهُمْ إِلاَّ فِتْنَةً لِلَّذِينَ كَفَرُوا لِيَسْتَيْقِنَ الَّذِينَ أُوتُوا الْكِتابَ وَ يَزْدادَ الَّذِينَ آمَنُوا إِيماناً وَ لا يَرْتابَ الَّذِينَ أُوتُوا الْكِتابَ وَ الْمُؤْمِنُونَ وَ لِيَقُولَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَ الْكافِرُونَ ما ذا أَرادَ اللَّهُ بِهذا مَثَلاً كَذلِكَ يُضِلُّ اللَّهُ مَنْ يَشاءُ وَ يَهْدِي مَنْ يَشاءُ وَ ما يَعْلَمُ جُنُودَ رَبِّكَ إِلاَّ هُوَ وَ ما هِيَ إِلاَّ ذِكْرى لِلْبَشَرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5527, N'كَلاَّ وَ الْقَمَرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5528, N'وَ اللَّيْلِ إِذْ أَدْبَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5529, N'وَ الصُّبْحِ إِذا أَسْفَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5530, N'إِنَّها لَإِحْدَى الْكُبَرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5531, N'نَذِيراً لِلْبَشَرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5532, N'لِمَنْ شاءَ مِنْكُمْ أَنْ يَتَقَدَّمَ أَوْ يَتَأَخَّرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5533, N'كُلُّ نَفْسٍ بِما كَسَبَتْ رَهِينَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5534, N'إِلاَّ أَصْحابَ الْيَمِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5535, N'فِي جَنَّاتٍ يَتَساءَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5536, N'عَنِ الْمُجْرِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5537, N'ما سَلَكَكُمْ فِي سَقَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5538, N'قالُوا لَمْ نَكُ مِنَ الْمُصَلِّينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5539, N'وَ لَمْ نَكُ نُطْعِمُ الْمِسْكِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5540, N'وَ كُنَّا نَخُوضُ مَعَ الْخائِضِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5541, N'وَ كُنَّا نُكَذِّبُ بِيَوْمِ الدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5542, N'حَتَّى أَتانَا الْيَقِينُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5543, N'فَما تَنْفَعُهُمْ شَفاعَةُ الشَّافِعِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5544, N'فَما لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِضِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5545, N'كَأَنَّهُمْ حُمُرٌ مُسْتَنْفِرَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5546, N'فَرَّتْ مِنْ قَسْوَرَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5547, N'بَلْ يُرِيدُ كُلُّ امْرِئٍ مِنْهُمْ أَنْ يُؤْتى صُحُفاً مُنَشَّرَةً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5548, N'كَلاَّ بَلْ لا يَخافُونَ الْآخِرَةَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5549, N'كَلاَّ إِنَّهُ تَذْكِرَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5550, N'فَمَنْ شاءَ ذَكَرَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5551, N'وَ ما يَذْكُرُونَ إِلاَّ أَنْ يَشاءَ اللَّهُ هُوَ أَهْلُ التَّقْوى وَ أَهْلُ الْمَغْفِرَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5552, N'لا أُقْسِمُ بِيَوْمِ الْقِيامَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5553, N'وَ لا أُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5554, N'أَ يَحْسَبُ الْإِنْسانُ أَلَّنْ نَجْمَعَ عِظامَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5555, N'بَلى قادِرِينَ عَلى أَنْ نُسَوِّيَ بَنانَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5556, N'بَلْ يُرِيدُ الْإِنْسانُ لِيَفْجُرَ أَمامَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5557, N'يَسْئَلُ أَيَّانَ يَوْمُ الْقِيامَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5558, N'فَإِذا بَرِقَ الْبَصَرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5559, N'وَ خَسَفَ الْقَمَرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5560, N'وَ جُمِعَ الشَّمْسُ وَ الْقَمَرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5561, N'يَقُولُ الْإِنْسانُ يَوْمَئِذٍ أَيْنَ الْمَفَرُّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5562, N'كَلاَّ لا وَزَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5563, N'إِلى رَبِّكَ يَوْمَئِذٍ الْمُسْتَقَرُّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5564, N'يُنَبَّؤُا الْإِنْسانُ يَوْمَئِذٍ بِما قَدَّمَ وَ أَخَّرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5565, N'بَلِ الْإِنْسانُ عَلى نَفْسِهِ بَصِيرَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5566, N'وَ لَوْ أَلْقى مَعاذِيرَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5567, N'لا تُحَرِّكْ بِهِ لِسانَكَ لِتَعْجَلَ بِهِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5568, N'إِنَّ عَلَيْنا جَمْعَهُ وَ قُرْآنَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5569, N'فَإِذا قَرَأْناهُ فَاتَّبِعْ قُرْآنَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5570, N'ثُمَّ إِنَّ عَلَيْنا بَيانَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5571, N'كَلاَّ بَلْ تُحِبُّونَ الْعاجِلَةَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5572, N'وَ تَذَرُونَ الْآخِرَةَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5573, N'وُجُوهٌ يَوْمَئِذٍ ناضِرَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5574, N'إِلى رَبِّها ناظِرَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5575, N'وَ وُجُوهٌ يَوْمَئِذٍ باسِرَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5576, N'تَظُنُّ أَنْ يُفْعَلَ بِها فاقِرَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5577, N'كَلاَّ إِذا بَلَغَتِ التَّراقِيَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5578, N'وَ قِيلَ مَنْ راقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5579, N'وَ ظَنَّ أَنَّهُ الْفِراقُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5580, N'وَ الْتَفَّتِ السَّاقُ بِالسَّاقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5581, N'إِلى رَبِّكَ يَوْمَئِذٍ الْمَساقُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5582, N'فَلا صَدَّقَ وَ لا صَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5583, N'وَ لكِنْ كَذَّبَ وَ تَوَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5584, N'ثُمَّ ذَهَبَ إِلى أَهْلِهِ يَتَمَطَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5585, N'أَوْلى لَكَ فَأَوْلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5586, N'ثُمَّ أَوْلى لَكَ فَأَوْلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5587, N'أَ يَحْسَبُ الْإِنْسانُ أَنْ يُتْرَكَ سُدىً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5588, N'أَ لَمْ يَكُ نُطْفَةً مِنْ مَنِيٍّ يُمْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5589, N'ثُمَّ كانَ عَلَقَةً فَخَلَقَ فَسَوَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5590, N'فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَ الْأُنْثى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5591, N'أَ لَيْسَ ذلِكَ بِقادِرٍ عَلى أَنْ يُحْيِيَ الْمَوْتى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5592, N'هَلْ أَتى عَلَى الْإِنْسانِ حِينٌ مِنَ الدَّهْرِ لَمْ يَكُنْ شَيْئاً مَذْكُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5593, N'إِنَّا خَلَقْنَا الْإِنْسانَ مِنْ نُطْفَةٍ أَمْشاجٍ نَبْتَلِيهِ فَجَعَلْناهُ سَمِيعاً بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5594, N'إِنَّا هَدَيْناهُ السَّبِيلَ إِمَّا شاكِراً وَ إِمَّا كَفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5595, N'إِنَّا أَعْتَدْنا لِلْكافِرِينَ سَلاسِلَ وَ أَغْلالاً وَ سَعِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5596, N'إِنَّ الْأَبْرارَ يَشْرَبُونَ مِنْ كَأْسٍ كانَ مِزاجُها كافُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5597, N'عَيْناً يَشْرَبُ بِها عِبادُ اللَّهِ يُفَجِّرُونَها تَفْجِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5598, N'يُوفُونَ بِالنَّذْرِ وَ يَخافُونَ يَوْماً كانَ شَرُّهُ مُسْتَطِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5599, N'وَ يُطْعِمُونَ الطَّعامَ عَلى حُبِّهِ مِسْكِيناً وَ يَتِيماً وَ أَسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5600, N'إِنَّما نُطْعِمُكُمْ لِوَجْهِ اللَّهِ لا نُرِيدُ مِنْكُمْ جَزاءً وَ لا شُكُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5601, N'إِنَّا نَخافُ مِنْ رَبِّنا يَوْماً عَبُوساً قَمْطَرِيراً ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5602, N'فَوَقاهُمُ اللَّهُ شَرَّ ذلِكَ الْيَوْمِ وَ لَقَّاهُمْ نَضْرَةً وَ سُرُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5603, N'وَ جَزاهُمْ بِما صَبَرُوا جَنَّةً وَ حَرِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5604, N'مُتَّكِئِينَ فِيها عَلَى الْأَرائِكِ لا يَرَوْنَ فِيها شَمْساً وَ لا زَمْهَرِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5605, N'وَ دانِيَةً عَلَيْهِمْ ظِلالُها وَ ذُلِّلَتْ قُطُوفُها تَذْلِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5606, N'وَ يُطافُ عَلَيْهِمْ بِآنِيَةٍ مِنْ فِضَّةٍ وَ أَكْوابٍ كانَتْ قَوارِيرَا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5607, N'قَوارِيرَا مِنْ فِضَّةٍ قَدَّرُوها تَقْدِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5608, N'وَ يُسْقَوْنَ فِيها كَأْساً كانَ مِزاجُها زَنْجَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5609, N'عَيْناً فِيها تُسَمَّى سَلْسَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5610, N'وَ يَطُوفُ عَلَيْهِمْ وِلْدانٌ مُخَلَّدُونَ إِذا رَأَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤاً مَنْثُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5611, N'وَ إِذا رَأَيْتَ ثَمَّ رَأَيْتَ نَعِيماً وَ مُلْكاً كَبِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5612, N'عالِيَهُمْ ثِيابُ سُندُسٍ خُضْرٌ وَ إِسْتَبْرَقٌ وَ حُلُّوا أَساوِرَ مِنْ فِضَّةٍ وَ سَقاهُمْ رَبُّهُمْ شَراباً طَهُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5613, N'إِنَّ هذا كانَ لَكُمْ جَزاءً وَ كانَ سَعْيُكُمْ مَشْكُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5614, N'إِنَّا نَحْنُ نَزَّلْنا عَلَيْكَ الْقُرْآنَ تَنْزِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5615, N'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَ لا تُطِعْ مِنْهُمْ آثِماً أَوْ كَفُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5616, N'وَ اذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَ أَصِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5617, N'وَ مِنَ اللَّيْلِ فَاسْجُدْ لَهُ وَ سَبِّحْهُ لَيْلاً طَوِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5618, N'إِنَّ هؤُلاءِ يُحِبُّونَ الْعاجِلَةَ وَ يَذَرُونَ وَراءَهُمْ يَوْماً ثَقِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5619, N'نَحْنُ خَلَقْناهُمْ وَ شَدَدْنا أَسْرَهُمْ وَ إِذا شِئْنا بَدَّلْنا أَمْثالَهُمْ تَبْدِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5620, N'إِنَّ هذِهِ تَذْكِرَةٌ فَمَنْ شاءَ اتَّخَذَ إِلى رَبِّهِ سَبِيلاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5621, N'وَ ما تَشاؤُنَ إِلاَّ أَنْ يَشاءَ اللَّهُ إِنَّ اللَّهَ كانَ عَلِيماً حَكِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5622, N'يُدْخِلُ مَنْ يَشاءُ فِي رَحْمَتِهِ وَ الظَّالِمِينَ أَعَدَّ لَهُمْ عَذاباً أَلِيماً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5623, N'وَ الْمُرْسَلاتِ عُرْفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5624, N'فَالْعاصِفاتِ عَصْفاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5625, N'وَ النَّاشِراتِ نَشْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5626, N'فَالْفارِقاتِ فَرْقاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5627, N'فَالْمُلْقِياتِ ذِكْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5628, N'عُذْراً أَوْ نُذْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5629, N'إِنَّما تُوعَدُونَ لَواقِعٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5630, N'فَإِذَا النُّجُومُ طُمِسَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5631, N'وَ إِذَا السَّماءُ فُرِجَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5632, N'وَ إِذَا الْجِبالُ نُسِفَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5633, N'وَ إِذَا الرُّسُلُ أُقِّتَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5634, N'لِأَيِّ يَوْمٍ أُجِّلَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5635, N'لِيَوْمِ الْفَصْلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5636, N'وَ ما أَدْراكَ ما يَوْمُ الْفَصْلِ ')
