INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5645, N'فَقَدَرْنا فَنِعْمَ الْقادِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5646, N'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5647, N'أَ لَمْ نَجْعَلِ الْأَرْضَ كِفاتاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5648, N'أَحْياءً وَ أَمْواتاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5649, N'وَ جَعَلْنا فِيها رَواسِيَ شامِخاتٍ وَ أَسْقَيْناكُمْ ماءً فُراتاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5650, N'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ ')
