INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5669, N'وَيْلٌ يَوْمَئِذٍ لِلْمُكَذِّبِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5670, N'وَ إِذا قِيلَ لَهُمُ ارْكَعُوا لا يَرْكَعُونَ ')
