INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5872, N'تَعْرِفُ فِي وُجُوهِهِمْ نَضْرَةَ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5873, N'يُسْقَوْنَ مِنْ رَحِيقٍ مَخْتُومٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5874, N'خِتامُهُ مِسْكٌ وَ فِي ذلِكَ فَلْيَتَنافَسِ الْمُتَنافِسُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5875, N'وَ مِزاجُهُ مِنْ تَسْنِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5876, N'عَيْناً يَشْرَبُ بِهَا الْمُقَرَّبُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5877, N'إِنَّ الَّذِينَ أَجْرَمُوا كانُوا مِنَ الَّذِينَ آمَنُوا يَضْحَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5878, N'وَ إِذا مَرُّوا بِهِمْ يَتَغامَزُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5879, N'وَ إِذَا انْقَلَبُوا إِلى أَهْلِهِمُ انْقَلَبُوا فَكِهِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5880, N'وَ إِذا رَأَوْهُمْ قالُوا إِنَّ هؤُلاءِ لَضالُّونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5881, N'وَ ما أُرْسِلُوا عَلَيْهِمْ حافِظِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5882, N'فَالْيَوْمَ الَّذِينَ آمَنُوا مِنَ الْكُفَّارِ يَضْحَكُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5883, N'عَلَى الْأَرائِكِ يَنْظُرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5884, N'هَلْ ثُوِّبَ الْكُفَّارُ ما كانُوا يَفْعَلُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5885, N'إِذَا السَّماءُ انْشَقَّتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5886, N'وَ أَذِنَتْ لِرَبِّها وَ حُقَّتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5887, N'وَ إِذَا الْأَرْضُ مُدَّتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5888, N'وَ أَلْقَتْ ما فِيها وَ تَخَلَّتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5889, N'وَ أَذِنَتْ لِرَبِّها وَ حُقَّتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5890, N'يا أَيُّهَا الْإِنْسانُ إِنَّكَ كادِحٌ إِلى رَبِّكَ كَدْحاً فَمُلاقِيهِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5891, N'فَأَمَّا مَنْ أُوتِيَ كِتابَهُ بِيَمِينِهِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5892, N'فَسَوْفَ يُحاسَبُ حِساباً يَسِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5893, N'وَ يَنْقَلِبُ إِلى أَهْلِهِ مَسْرُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5894, N'وَ أَمَّا مَنْ أُوتِيَ كِتابَهُ وَراءَ ظَهْرِهِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5895, N'فَسَوْفَ يَدْعُوا ثُبُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5896, N'وَ يَصْلى سَعِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5897, N'إِنَّهُ كانَ فِي أَهْلِهِ مَسْرُوراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5898, N'إِنَّهُ ظَنَّ أَنْ لَنْ يَحُورَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5899, N'بَلى إِنَّ رَبَّهُ كانَ بِهِ بَصِيراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5900, N'فَلا أُقْسِمُ بِالشَّفَقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5901, N'وَ اللَّيْلِ وَ ما وَسَقَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5902, N'وَ الْقَمَرِ إِذَا اتَّسَقَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5903, N'لَتَرْكَبُنَّ طَبَقاً عَنْ طَبَقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5904, N'فَما لَهُمْ لا يُؤْمِنُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5905, N'وَ إِذا قُرِئَ عَلَيْهِمُ الْقُرْآنُ لا يَسْجُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5906, N'بَلِ الَّذِينَ كَفَرُوا يُكَذِّبُونَ ')
