INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5908, N'فَبَشِّرْهُمْ بِعَذابٍ أَلِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5909, N'إِلاَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5910, N'وَ السَّماءِ ذاتِ الْبُرُوجِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5911, N'وَ الْيَوْمِ الْمَوْعُودِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5912, N'وَ شاهِدٍ وَ مَشْهُودٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5913, N'قُتِلَ أَصْحابُ الْأُخْدُودِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5914, N'النَّارِ ذاتِ الْوَقُودِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5915, N'إِذْ هُمْ عَلَيْها قُعُودٌ ')
