INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5924, N'ذُو الْعَرْشِ الْمَجِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5925, N'فَعَّالٌ لِما يُرِيدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5926, N'هَلْ أَتاكَ حَدِيثُ الْجُنُودِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5927, N'فِرْعَوْنَ وَ ثَمُودَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5928, N'بَلِ الَّذِينَ كَفَرُوا فِي تَكْذِيبٍ ')
