INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5932, N'وَ السَّماءِ وَ الطَّارِقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5933, N'وَ ما أَدْراكَ مَا الطَّارِقُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5934, N'النَّجْمُ الثَّاقِبُ ')
