INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5951, N'وَ الَّذِي قَدَّرَ فَهَدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5952, N'وَ الَّذِي أَخْرَجَ الْمَرْعى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5953, N'فَجَعَلَهُ غُثاءً أَحْوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5954, N'سَنُقْرِئُكَ فَلا تَنْسى ')
