INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5959, N'وَ يَتَجَنَّبُهَا الْأَشْقَى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5960, N'الَّذِي يَصْلَى النَّارَ الْكُبْرى ')
