INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5970, N'عامِلَةٌ ناصِبَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5971, N'تَصْلى ناراً حامِيَةً ')
