INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5977, N'فِي جَنَّةٍ عالِيَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5978, N'لا تَسْمَعُ فِيها لاغِيَةً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5979, N'فِيها عَيْنٌ جارِيَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5980, N'فِيها سُرُرٌ مَرْفُوعَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5981, N'وَ أَكْوابٌ مَوْضُوعَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5982, N'وَ نَمارِقُ مَصْفُوفَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5983, N'وَ زَرابِيُّ مَبْثُوثَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5984, N'أَ فَلا يَنْظُرُونَ إِلَى الْإِبِلِ كَيْفَ خُلِقَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5985, N'وَ إِلَى السَّماءِ كَيْفَ رُفِعَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5986, N'وَ إِلَى الْجِبالِ كَيْفَ نُصِبَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5987, N'وَ إِلَى الْأَرْضِ كَيْفَ سُطِحَتْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5988, N'فَذَكِّرْ إِنَّما أَنْتَ مُذَكِّرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5989, N'لَسْتَ عَلَيْهِمْ بِمُصَيْطِرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5990, N'إِلاَّ مَنْ تَوَلَّى وَ كَفَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5991, N'فَيُعَذِّبُهُ اللَّهُ الْعَذابَ الْأَكْبَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5992, N'إِنَّ إِلَيْنا إِيابَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5993, N'ثُمَّ إِنَّ عَلَيْنا حِسابَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5994, N'وَ الْفَجْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5995, N'وَ لَيالٍ عَشْرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5996, N'وَ الشَّفْعِ وَ الْوَتْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5997, N'وَ اللَّيْلِ إِذا يَسْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5998, N'هَلْ فِي ذلِكَ قَسَمٌ لِذِي حِجْرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (5999, N'أَ لَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعادٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6000, N'إِرَمَ ذاتِ الْعِمادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6001, N'الَّتِي لَمْ يُخْلَقْ مِثْلُها فِي الْبِلادِ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6002, N'وَ ثَمُودَ الَّذِينَ جابُوا الصَّخْرَ بِالْوادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6003, N'وَ فِرْعَوْنَ ذِي الْأَوْتادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6004, N'الَّذِينَ طَغَوْا فِي الْبِلادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6005, N'فَأَكْثَرُوا فِيهَا الْفَسادَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6006, N'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذابٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6007, N'إِنَّ رَبَّكَ لَبِالْمِرْصادِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6008, N'فَأَمَّا الْإِنْسانُ إِذا مَا ابْتَلاهُ رَبُّهُ فَأَكْرَمَهُ وَ نَعَّمَهُ فَيَقُولُ رَبِّي أَكْرَمَنِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6009, N'وَ أَمَّا إِذا مَا ابْتَلاهُ فَقَدَرَ عَلَيْهِ رِزْقَهُ فَيَقُولُ رَبِّي أَهانَنِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6010, N'كَلاَّ بَلْ لا تُكْرِمُونَ الْيَتِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6011, N'وَ لا تَحَاضُّونَ عَلى طَعامِ الْمِسْكِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6012, N'وَ تَأْكُلُونَ التُّراثَ أَكْلاً لَمًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6013, N'وَ تُحِبُّونَ الْمالَ حُبًّا جَمًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6014, N'كَلاَّ إِذا دُكَّتِ الْأَرْضُ دَكًّا دَكًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6015, N'وَ جاءَ رَبُّكَ وَ الْمَلَكُ صَفًّا صَفًّا ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6016, N'وَ جِي‏ءَ يَوْمَئِذٍ بِجَهَنَّمَ يَوْمَئِذٍ يَتَذَكَّرُ الْإِنْسانُ وَ أَنَّى لَهُ الذِّكْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6017, N'يَقُولُ يا لَيْتَنِي قَدَّمْتُ لِحَياتِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6018, N'فَيَوْمَئِذٍ لا يُعَذِّبُ عَذابَهُ أَحَدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6019, N'وَ لا يُوثِقُ وَثاقَهُ أَحَدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6020, N'يا أَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6021, N'ارْجِعِي إِلى رَبِّكِ راضِيَةً مَرْضِيَّةً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6022, N'فَادْخُلِي فِي عِبادِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6023, N'وَ ادْخُلِي جَنَّتِي ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6024, N'لا أُقْسِمُ بِهذَا الْبَلَدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6025, N'وَ أَنْتَ حِلٌّ بِهذَا الْبَلَدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6026, N'وَ والِدٍ وَ ما وَلَدَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6027, N'لَقَدْ خَلَقْنَا الْإِنْسانَ فِي كَبَدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6028, N'أَ يَحْسَبُ أَنْ لَنْ يَقْدِرَ عَلَيْهِ أَحَدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6029, N'يَقُولُ أَهْلَكْتُ مالاً لُبَداً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6030, N'أَ يَحْسَبُ أَنْ لَمْ يَرَهُ أَحَدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6031, N'أَ لَمْ نَجْعَلْ لَهُ عَيْنَيْنِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6032, N'وَ لِساناً وَ شَفَتَيْنِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6033, N'وَ هَدَيْناهُ النَّجْدَيْنِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6034, N'فَلاَ اقْتَحَمَ الْعَقَبَةَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6035, N'وَ ما أَدْراكَ مَا الْعَقَبَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6036, N'فَكُّ رَقَبَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6037, N'أَوْ إِطْعامٌ فِي يَوْمٍ ذِي مَسْغَبَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6038, N'يَتِيماً ذا مَقْرَبَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6039, N'أَوْ مِسْكِيناً ذا مَتْرَبَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6040, N'ثُمَّ كانَ مِنَ الَّذِينَ آمَنُوا وَ تَواصَوْا بِالصَّبْرِ وَ تَواصَوْا بِالْمَرْحَمَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6041, N'أُولئِكَ أَصْحابُ الْمَيْمَنَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6042, N'وَ الَّذِينَ كَفَرُوا بِآياتِنا هُمْ أَصْحابُ الْمَشْأَمَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6043, N'عَلَيْهِمْ نارٌ مُؤْصَدَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6044, N'وَ الشَّمْسِ وَ ضُحاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6045, N'وَ الْقَمَرِ إِذا تَلاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6046, N'وَ النَّهارِ إِذا جَلاَّها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6047, N'وَ اللَّيْلِ إِذا يَغْشاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6048, N'وَ السَّماءِ وَ ما بَناها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6049, N'وَ الْأَرْضِ وَ ما طَحاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6050, N'وَ نَفْسٍ وَ ما سَوَّاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6051, N'فَأَلْهَمَها فُجُورَها وَ تَقْواها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6052, N'قَدْ أَفْلَحَ مَنْ زَكَّاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6053, N'وَ قَدْ خابَ مَنْ دَسَّاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6054, N'كَذَّبَتْ ثَمُودُ بِطَغْواها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6055, N'إِذِ انْبَعَثَ أَشْقاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6056, N'فَقالَ لَهُمْ رَسُولُ اللَّهِ ناقَةَ اللَّهِ وَ سُقْياها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6057, N'فَكَذَّبُوهُ فَعَقَرُوها فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْبِهِمْ فَسَوَّاها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6058, N'وَ لا يَخافُ عُقْباها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6059, N'وَ اللَّيْلِ إِذا يَغْشى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6060, N'وَ النَّهارِ إِذا تَجَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6061, N'وَ ما خَلَقَ الذَّكَرَ وَ الْأُنْثى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6062, N'إِنَّ سَعْيَكُمْ لَشَتَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6063, N'فَأَمَّا مَنْ أَعْطى وَ اتَّقى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6064, N'وَ صَدَّقَ بِالْحُسْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6065, N'فَسَنُيَسِّرُهُ لِلْيُسْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6066, N'وَ أَمَّا مَنْ بَخِلَ وَ اسْتَغْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6067, N'وَ كَذَّبَ بِالْحُسْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6068, N'فَسَنُيَسِّرُهُ لِلْعُسْرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6069, N'وَ ما يُغْنِي عَنْهُ مالُهُ إِذا تَرَدَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6070, N'إِنَّ عَلَيْنا لَلْهُدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6071, N'وَ إِنَّ لَنا لَلْآخِرَةَ وَ الْأُولى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6072, N'فَأَنْذَرْتُكُمْ ناراً تَلَظَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6073, N'لا يَصْلاها إِلاَّ الْأَشْقَى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6074, N'الَّذِي كَذَّبَ وَ تَوَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6075, N'وَ سَيُجَنَّبُهَا الْأَتْقَى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6076, N'الَّذِي يُؤْتِي مالَهُ يَتَزَكَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6077, N'وَ ما لِأَحَدٍ عِنْدَهُ مِنْ نِعْمَةٍ تُجْزى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6078, N'إِلاَّ ابْتِغاءَ وَجْهِ رَبِّهِ الْأَعْلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6079, N'وَ لَسَوْفَ يَرْضى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6080, N'وَ الضُّحى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6081, N'وَ اللَّيْلِ إِذا سَجى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6082, N'ما وَدَّعَكَ رَبُّكَ وَ ما قَلى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6083, N'وَ لَلْآخِرَةُ خَيْرٌ لَكَ مِنَ الْأُولى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6084, N'وَ لَسَوْفَ يُعْطِيكَ رَبُّكَ فَتَرْضى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6085, N'أَ لَمْ يَجِدْكَ يَتِيماً فَآوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6086, N'وَ وَجَدَكَ ضَالاًّ فَهَدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6087, N'وَ وَجَدَكَ عائِلاً فَأَغْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6088, N'فَأَمَّا الْيَتِيمَ فَلا تَقْهَرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6089, N'وَ أَمَّا السَّائِلَ فَلا تَنْهَرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6090, N'وَ أَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6091, N'أَ لَمْ نَشْرَحْ لَكَ صَدْرَكَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6092, N'وَ وَضَعْنا عَنْكَ وِزْرَكَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6093, N'الَّذِي أَنْقَضَ ظَهْرَكَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6094, N'وَ رَفَعْنا لَكَ ذِكْرَكَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6095, N'فَإِنَّ مَعَ الْعُسْرِ يُسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6096, N'إِنَّ مَعَ الْعُسْرِ يُسْراً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6097, N'فَإِذا فَرَغْتَ فَانْصَبْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6098, N'وَ إِلى رَبِّكَ فَارْغَبْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6099, N'وَ التِّينِ وَ الزَّيْتُونِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6100, N'وَ طُورِ سِينِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6101, N'وَ هذَا الْبَلَدِ الْأَمِينِ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6102, N'لَقَدْ خَلَقْنَا الْإِنْسانَ فِي أَحْسَنِ تَقْوِيمٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6103, N'ثُمَّ رَدَدْناهُ أَسْفَلَ سافِلِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6104, N'إِلاَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6105, N'فَما يُكَذِّبُكَ بَعْدُ بِالدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6106, N'أَ لَيْسَ اللَّهُ بِأَحْكَمِ الْحاكِمِينَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6107, N'اقْرَأْ بِاسْمِ رَبِّكَ الَّذِي خَلَقَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6108, N'خَلَقَ الْإِنْسانَ مِنْ عَلَقٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6109, N'اقْرَأْ وَ رَبُّكَ الْأَكْرَمُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6110, N'الَّذِي عَلَّمَ بِالْقَلَمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6111, N'عَلَّمَ الْإِنْسانَ ما لَمْ يَعْلَمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6112, N'كَلاَّ إِنَّ الْإِنْسانَ لَيَطْغى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6113, N'أَنْ رَآهُ اسْتَغْنى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6114, N'إِنَّ إِلى رَبِّكَ الرُّجْعى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6115, N'أَ رَأَيْتَ الَّذِي يَنْهى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6116, N'عَبْداً إِذا صَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6117, N'أَ رَأَيْتَ إِنْ كانَ عَلَى الْهُدى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6118, N'أَوْ أَمَرَ بِالتَّقْوى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6119, N'أَ رَأَيْتَ إِنْ كَذَّبَ وَ تَوَلَّى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6120, N'أَ لَمْ يَعْلَمْ بِأَنَّ اللَّهَ يَرى ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6121, N'كَلاَّ لَئِنْ لَمْ يَنْتَهِ لَنَسْفَعاً بِالنَّاصِيَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6122, N'ناصِيَةٍ كاذِبَةٍ خاطِئَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6123, N'فَلْيَدْعُ نادِيَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6124, N'سَنَدْعُ الزَّبانِيَةَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6125, N'كَلاَّ لا تُطِعْهُ وَ اسْجُدْ وَ اقْتَرِبْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6126, N'إِنَّا أَنْزَلْناهُ فِي لَيْلَةِ الْقَدْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6127, N'وَ ما أَدْراكَ ما لَيْلَةُ الْقَدْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6128, N'لَيْلَةُ الْقَدْرِ خَيْرٌ مِنْ أَلْفِ شَهْرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6129, N'تَنَزَّلُ الْمَلائِكَةُ وَ الرُّوحُ فِيها بِإِذْنِ رَبِّهِمْ مِنْ كُلِّ أَمْرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6130, N'سَلامٌ هِيَ حَتَّى مَطْلَعِ الْفَجْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6131, N'لَمْ يَكُنِ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتابِ وَ الْمُشْرِكِينَ مُنْفَكِّينَ حَتَّى تَأْتِيَهُمُ الْبَيِّنَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6132, N'رَسُولٌ مِنَ اللَّهِ يَتْلُوا صُحُفاً مُطَهَّرَةً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6133, N'فِيها كُتُبٌ قَيِّمَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6134, N'وَ ما تَفَرَّقَ الَّذِينَ أُوتُوا الْكِتابَ إِلاَّ مِنْ بَعْدِ ما جاءَتْهُمُ الْبَيِّنَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6135, N'وَ ما أُمِرُوا إِلاَّ لِيَعْبُدُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ حُنَفاءَ وَ يُقِيمُوا الصَّلاةَ وَ يُؤْتُوا الزَّكاةَ وَ ذلِكَ دِينُ الْقَيِّمَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6136, N'إِنَّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتابِ وَ الْمُشْرِكِينَ فِي نارِ جَهَنَّمَ خالِدِينَ فِيها أُولئِكَ هُمْ شَرُّ الْبَرِيَّةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6137, N'إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ أُولئِكَ هُمْ خَيْرُ الْبَرِيَّةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6138, N'جَزاؤُهُمْ عِنْدَ رَبِّهِمْ جَنَّاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهارُ خالِدِينَ فِيها أَبَداً رَضِيَ اللَّهُ عَنْهُمْ وَ رَضُوا عَنْهُ ذلِكَ لِمَنْ خَشِيَ رَبَّهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6139, N'إِذا زُلْزِلَتِ الْأَرْضُ زِلْزالَها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6140, N'وَ أَخْرَجَتِ الْأَرْضُ أَثْقالَها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6141, N'وَ قالَ الْإِنْسانُ ما لَها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6142, N'يَوْمَئِذٍ تُحَدِّثُ أَخْبارَها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6143, N'بِأَنَّ رَبَّكَ أَوْحى لَها ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6144, N'يَوْمَئِذٍ يَصْدُرُ النَّاسُ أَشْتاتاً لِيُرَوْا أَعْمالَهُمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6145, N'فَمَنْ يَعْمَلْ مِثْقالَ ذَرَّةٍ خَيْراً يَرَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6146, N'وَ مَنْ يَعْمَلْ مِثْقالَ ذَرَّةٍ شَرًّا يَرَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6147, N'وَ الْعادِياتِ ضَبْحاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6148, N'فَالْمُورِياتِ قَدْحاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6149, N'فَالْمُغِيراتِ صُبْحاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6150, N'فَأَثَرْنَ بِهِ نَقْعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6151, N'فَوَسَطْنَ بِهِ جَمْعاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6152, N'إِنَّ الْإِنْسانَ لِرَبِّهِ لَكَنُودٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6153, N'وَ إِنَّهُ عَلى ذلِكَ لَشَهِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6154, N'وَ إِنَّهُ لِحُبِّ الْخَيْرِ لَشَدِيدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6155, N'أَ فَلا يَعْلَمُ إِذا بُعْثِرَ ما فِي الْقُبُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6156, N'وَ حُصِّلَ ما فِي الصُّدُورِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6157, N'إِنَّ رَبَّهُمْ بِهِمْ يَوْمَئِذٍ لَخَبِيرٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6158, N'الْقارِعَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6159, N'مَا الْقارِعَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6160, N'وَ ما أَدْراكَ مَا الْقارِعَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6161, N'يَوْمَ يَكُونُ النَّاسُ كَالْفَراشِ الْمَبْثُوثِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6162, N'وَ تَكُونُ الْجِبالُ كَالْعِهْنِ الْمَنْفُوشِ ')
