INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6164, N'فَهُوَ فِي عِيشَةٍ راضِيَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6165, N'وَ أَمَّا مَنْ خَفَّتْ مَوازِينُهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6166, N'فَأُمُّهُ هاوِيَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6167, N'وَ ما أَدْراكَ ما هِيَهْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6168, N'نارٌ حامِيَةٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6169, N'أَلْهاكُمُ التَّكاثُرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6170, N'حَتَّى زُرْتُمُ الْمَقابِرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6171, N'كَلاَّ سَوْفَ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6172, N'ثُمَّ كَلاَّ سَوْفَ تَعْلَمُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6173, N'كَلاَّ لَوْ تَعْلَمُونَ عِلْمَ الْيَقِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6174, N'لَتَرَوُنَّ الْجَحِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6175, N'ثُمَّ لَتَرَوُنَّها عَيْنَ الْيَقِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6176, N'ثُمَّ لَتُسْئَلُنَّ يَوْمَئِذٍ عَنِ النَّعِيمِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6177, N'وَ الْعَصْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6178, N'إِنَّ الْإِنْسانَ لَفِي خُسْرٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6179, N'إِلاَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصَّالِحاتِ وَ تَواصَوْا بِالْحَقِّ وَ تَواصَوْا بِالصَّبْرِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6180, N'وَيْلٌ لِكُلِّ هُمَزَةٍ لُمَزَةٍ ')
