INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6182, N'يَحْسَبُ أَنَّ مالَهُ أَخْلَدَهُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6183, N'كَلاَّ لَيُنْبَذَنَّ فِي الْحُطَمَةِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6184, N'وَ ما أَدْراكَ مَا الْحُطَمَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6185, N'نارُ اللَّهِ الْمُوقَدَةُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6186, N'الَّتِي تَطَّلِعُ عَلَى الْأَفْئِدَةِ ')
