INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6188, N'فِي عَمَدٍ مُمَدَّدَةٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6189, N'أَ لَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحابِ الْفِيلِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6190, N'أَ لَمْ يَجْعَلْ كَيْدَهُمْ فِي تَضْلِيلٍ ')
