INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6193, N'فَجَعَلَهُمْ كَعَصْفٍ مَأْكُولٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6194, N'لِإِيلافِ قُرَيْشٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6195, N'إِيلافِهِمْ رِحْلَةَ الشِّتاءِ وَ الصَّيْفِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6196, N'فَلْيَعْبُدُوا رَبَّ هذَا الْبَيْتِ ')
