INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6198, N'أَ رَأَيْتَ الَّذِي يُكَذِّبُ بِالدِّينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6199, N'فَذلِكَ الَّذِي يَدُعُّ الْيَتِيمَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6200, N'وَ لا يَحُضُّ عَلى طَعامِ الْمِسْكِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6201, N'فَوَيْلٌ لِلْمُصَلِّينَ ')
GO
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6202, N'الَّذِينَ هُمْ عَنْ صَلاتِهِمْ ساهُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6203, N'الَّذِينَ هُمْ يُراؤُنَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6204, N'وَ يَمْنَعُونَ الْماعُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6205, N'إِنَّا أَعْطَيْناكَ الْكَوْثَرَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6206, N'فَصَلِّ لِرَبِّكَ وَ انْحَرْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6207, N'إِنَّ شانِئَكَ هُوَ الْأَبْتَرُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6208, N'قُلْ يا أَيُّهَا الْكافِرُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6209, N'لا أَعْبُدُ ما تَعْبُدُونَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6210, N'وَ لا أَنْتُمْ عابِدُونَ ما أَعْبُدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6211, N'وَ لا أَنا عابِدٌ ما عَبَدْتُّمْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6212, N'وَ لا أَنْتُمْ عابِدُونَ ما أَعْبُدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6213, N'لَكُمْ دِينُكُمْ وَ لِيَ دِينِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6214, N'إِذا جاءَ نَصْرُ اللَّهِ وَ الْفَتْحُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6215, N'وَ رَأَيْتَ النَّاسَ يَدْخُلُونَ فِي دِينِ اللَّهِ أَفْواجاً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6216, N'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَ اسْتَغْفِرْهُ إِنَّهُ كانَ تَوَّاباً ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6217, N'تَبَّتْ يَدا أَبِي لَهَبٍ وَ تَبَّ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6218, N'ما أَغْنى عَنْهُ مالُهُ وَ ما كَسَبَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6219, N'سَيَصْلى ناراً ذاتَ لَهَبٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6220, N'وَ امْرَأَتُهُ حَمَّالَةَ الْحَطَبِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6221, N'فِي جِيدِها حَبْلٌ مِنْ مَسَدٍ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6222, N'قُلْ هُوَ اللَّهُ أَحَدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6223, N'اللَّهُ الصَّمَدُ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6224, N'لَمْ يَلِدْ وَ لَمْ يُولَدْ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6225, N'وَ لَمْ يَكُنْ لَهُ كُفُواً أَحَدٌ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6226, N'قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6227, N'مِنْ شَرِّ ما خَلَقَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6228, N'وَ مِنْ شَرِّ غاسِقٍ إِذا وَقَبَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6229, N'وَ مِنْ شَرِّ النَّفَّاثاتِ فِي الْعُقَدِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6230, N'وَ مِنْ شَرِّ حاسِدٍ إِذا حَسَدَ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6231, N'قُلْ أَعُوذُ بِرَبِّ النَّاسِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6232, N'مَلِكِ النَّاسِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6233, N'إِلهِ النَّاسِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6234, N'مِنْ شَرِّ الْوَسْواسِ الْخَنَّاسِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6235, N'الَّذِي يُوَسْوِسُ فِي صُدُورِ النَّاسِ ')
INSERT [dbo].[Quran_Ayeh_AR] ([ID], [ayeh_text]) VALUES (6236, N'مِنَ الْجِنَّةِ وَ النَّاسِ ')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2, N'ستايش مخصوص خداوندي است که پروردگار جهانيان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3, N'(خداوندي که) بخشنده و بخشايشگر است (و رحمت عام و خاصش همگان را فرا گرفته).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4, N'(خداوندي که) مالک روز جزاست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5, N'(پروردگارا!) تنها تو را مي‌پرستيم؛ و تنها از تو ياري مي‌جوييم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6, N'ما را به راه راست هدايت کن...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (7, N'راه کساني که آنان را مشمول نعمت خود ساختي؛ نه کساني که بر آنان غضب کرده‌اي؛ و نه گمراهان.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (8, N'الم (بزرگ است خداوندي که اين کتاب عظيم را، از حروف ساده الفبا به وجود آورده).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (9, N'آن کتاب با عظمتي است که شک در آن راه ندارد؛ و مايه هدايت پرهيزکاران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (10, N'(پرهيزکاران) کساني هستند که به غيب [=آنچه از حس پوشيده و پنهان است‌] ايمان مي‌آورند؛ و نماز را برپا مي‌دارند؛ و از تمام نعمتها و مواهبي که به آنان روزي داده‌ايم، انفاق مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (11, N'و آنان که به آنچه بر تو نازل شده، و آنچه پيش از تو (بر پيامبران پيشين) نازل گرديده، ايمان مي‌آورند؛ و به رستاخيز يقين دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (12, N'آنان بر طريق هدايت پروردگارشانند؛ و آنان رستگارانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (13, N'کساني که کافر شدند، براي آنان تفاوت نمي‌کند که آنان را (از عذاب الهي) بترساني يا نترساني؛ ايمان نخواهند آورد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (14, N'خدا بر دلها و گوشهاي آنان مهر نهاده؛ و بر چشمهايشان پرده‌اي افکنده شده؛ و عذاب بزرگي در انتظار آنهاست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (15, N'گروهي از مردم کساني هستند که مي‌گويند: «به خدا و روز رستاخيز ايمان آورده‌ايم.» در حالي که ايمان ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (16, N'مي‌خواهند خدا و مؤمنان را فريب دهند؛ در حالي که جز خودشان را فريب نمي‌دهند؛ (اما) نمي‌فهمند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (17, N'در دلهاي آنان يک نوع بيماري است؛ خداوند بر بيماري آنان افزوده؛ و به خاطر دروغهايي که ميگفتند، عذاب دردناکي در انتظار آنهاست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (18, N'و هنگامي که به آنان گفته شود: «در زمين فساد نکنيد» مي‌گويند: «ما فقط اصلاح‌کننده‌ايم»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (19, N'آگاه باشيد! اينها همان مفسدانند؛ ولي نمي‌فهمند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (20, N'و هنگامي که به آنان گفته شود: «همانند (ساير) مردم ايمان بياوريد!» مي‌گويند: «آيا همچون ابلهان ايمان بياوريم؟!» بدانيد اينها همان ابلهانند ولي نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (21, N'و هنگامي که افراد باايمان را ملاقات مي‌کنند، و مي‌گويند: «ما ايمان آورده‌ايم!» (ولي) هنگامي که با شيطانهاي خود خلوت مي‌کنند، مي‌گويند: «ما با شمائيم! ما فقط (آنها را) مسخره مي‌کنيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (22, N'خداوند آنان را استهزا مي‌کند؛ و آنها را در طغيانشان نگه مي‌دارد، تا سرگردان شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (23, N'آنان کساني هستند که «هدايت» را به «گمراهي» فروخته‌اند؛ و (اين) تجارت آنها سودي نداده؛ و هدايت نيافته‌اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (24, N'آنان [= منافقان‌] همانند کسي هستند که آتشي افروخته (تا در بيابان تاريک، راه خود را پيدا کند)، ولي هنگامي که آتش اطراف او را روشن ساخت، خداوند (طوفاني مي‌فرستد و) آن را خاموش مي‌کند؛ و در تاريکيهاي وحشتناکي که چشم کار نمي‌کند، آنها را رها مي‌سازد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (25, N'آنها کران، گنگها و کورانند؛ لذا (از راه خطا) بازنمي‌گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (26, N'يا همچون باراني از آسمان، که در شب تاريک همراه با رعد و برق و صاعقه (بر سر رهگذران) ببارد. آنها از ترس مرگ، انگشتانشان را در گوشهاي خود مي‌گذارند؛ تا صداي صاعقه را نشنوند. و خداوند به کافران احاطه دارد (و در قبضه قدرت او هستند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (27, N'(روشنائي خيره کننده) برق، نزديک است چشمانشان را بربايد. هر زمان که (برق جستن مي‌کند، و صفحه بيابان را) براي آنها روشن مي‌سازد، (چند گامي) در پرتو آن راه مي‌روند؛ و چون خاموش مي‌شود، توقف مي‌کنند. و اگر خدا بخواهد، گوش و چشم آنها را از بين مي‌برد؛ چرا که خداوند بر هر چيز تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (28, N'اي مردم! پروردگار خود را پرستش کنيد؛ آن کس که شما، و کساني را که پيش از شما بودند آفريد، تا پرهيزکار شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (29, N'آن کس که زمين را بستر شما، و آسمان [= جو زمين‌] را همچون سقفي بالاي سر شما قرار داد؛ و از آسمان آبي فرو فرستاد؛ و به وسيله آن، ميوه‌ها را پرورش داد؛ تا روزي شما باشد. بنابر اين، براي خدا همتاياني قرار ندهيد، در حالي که مي‌دانيد (هيچ يک از آنها، نه شما را آفريده‌اند، و نه شما را روزي مي‌دهند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (30, N'و اگر در باره آنچه بر بنده خود [= پيامبر] نازل کرده‌ايم شک و ترديد داريد ،( دست کم) يک سوره همانند آن بياوريد؛ و گواهان خود را - غير خدا - براي اين کار، فرا خوانيد اگر راست مي‌گوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (31, N'پس اگر چنين نکنيد - که هرگز نخواهيد کرد - از آتشي بترسيد که هيزم آن، بدنهاي مردم (گنهکار) و سنگها [= بتها] است، و براي کافران، آماده شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (32, N'به کساني که ايمان آورده، و کارهاي شايسته انجام داده‌اند، بشارت ده که باغهايي از بهشت براي آنهاست که نهرها از زير درختانش جاريست. هر زمان که ميوه‌اي از آن، به آنان داده شود، مي‌گويند: «اين همان است که قبلا به ما روزي داده شده بود.(ولي اينها چقدر از آنها بهتر و عاليتر است.)» و ميوه‌هايي که براي آنها آورده مي‌شود، همه (از نظر خوبي و زيبايي) يکسانند. و براي آنان همسراني پاک و پاکيزه است، و جاودانه در آن خواهند بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (33, N'خداوند از اين که (به موجودات ظاهرا کوچکي مانند) پشه، و حتي کمتر از آن، مثال بزند شرم نمي‌کند. (در اين ميان) آنان که ايمان آورده‌اند، مي‌دانند که آن، حقيقتي است از طرف پروردگارشان؛ و اما آنها که راه کفر را پيموده‌اند، (اين موضوع را بهانه کرده) مي‌گويند: «منظور خداوند از اين مثل چه بوده است؟!» (آري،) خدا جمع زيادي را با آن گمراه، و گروه بسياري را هدايت مي‌کند؛ ولي تنها فاسقان را با آن گمراه مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (34, N'فاسقان کساني هستند که پيمان خدا را، پس از محکم ساختن آن، ميشکنند؛ و پيوندهايي را که خدا دستور داده برقرار سازند، قطع نموده، و در روي زمين فساد ميکنند؛ اينها زيانکارانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (35, N'چگونه به خداوند کافر مي‌شويد؟! در حالي که شما مردگان (و اجسام بي‌روحي) بوديد ، و او شما را زنده کرد؛ سپس شما را مي‌ميراند؛ و بار ديگر شما را زنده مي‌کند؛ سپس به سوي او بازگردانده مي‌شويد.(بنابر اين، نه حيات و زندگي شما از شماست، و نه مرگتان؛ آنچه داريد از خداست).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (36, N'او خدايي است که همه آنچه را (از نعمتها) در زمين وجود دارد، براي شما آفريد؛ سپس به آسمان پرداخت؛ و آنها را به صورت هفت آسمان مرتب نمود؛ و او به هر چيز آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (37, N'(به خاطر بياور) هنگامي را که پروردگارت به فرشتگان گفت: «من در روي زمين، جانشيني [= نماينده‌اي‌] قرار خواهم داد.» فرشتگان گفتند: «پروردگارا! »آيا کسي را در آن قرار مي‌دهي که فساد و خونريزي کند؟! (زيرا موجودات زميني ديگر، که قبل از اين آدم وجود داشتند نيز، به فساد و خونريزي آلوده شدند. اگر هدف از آفرينش اين انسان، عبادت است،) ما تسبيح و حمد تو را بجا مي‌آوريم، و تو را تقديس مي‌کنيم.» پروردگار فرمود: «من حقايقي را مي‌دانم که شما نمي‌دانيد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (38, N'سپس علم اسماء [= علم اسرار آفرينش و نامگذاري موجودات‌] را همگي به آدم آموخت. بعد آنها را به فرشتگان عرضه داشت و فرمود: «اگر راست مي‌گوييد، اسامي اينها را به من خبر دهيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (39, N'فرشتگان عرض کردند: «منزهي تو! ما چيزي جز آنچه به ما تعليم داده‌اي، نمي‌دانيم؛ تو دانا و حکيمي.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (40, N'فرمود: «اي آدم! آنان را از اسامي (و اسرار) اين موجودات آگاه کن.» هنگامي که آنان را آگاه کرد، خداوند فرمود: «آيا به شما نگفتم که من، غيب آسمانها و زمين را ميدانم؟! و نيز ميدانم آنچه را شما آشکار ميکنيد، و آنچه را پنهان ميداشتيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (41, N'و (ياد کن) هنگامي را که به فرشتگان گفتيم: «براي آدم سجده و خضوع کنيد!» همگي سجده کردند؛ جز ابليس که سر باز زد، و تکبر ورزيد، (و به خاطر نافرماني و تکبرش) از کافران شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (42, N'و گفتيم: «اي آدم! تو با همسرت در بهشت سکونت کن؛ و از (نعمتهاي) آن، از هر جا مي‌خواهيد، گوارا بخوريد؛ (اما) نزديک اين درخت نشويد؛ که از ستمگران خواهيد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (43, N'پس شيطان موجب لغزش آنها از بهشت شد؛ و آنان را از آنچه در آن بودند، بيرون کرد. و (در اين هنگام) به آنها گفتيم: «همگي (به زمين) فرود آييد! در حالي که بعضي دشمن ديگري خواهيد بود. و براي شما در زمين، تا مدت معيني قرارگاه و وسيله بهره برداري خواهد بود.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (44, N'سپس آدم از پروردگارش کلماتي دريافت داشت؛ (و با آنها توبه کرد.) و خداوند توبه او را پذيرفت؛ چرا که خداوند توبه‌پذير و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (45, N'گفتيم: «همگي از آن، فرود آييد! هرگاه هدايتي از طرف من براي شما آمد، کساني که از آن پيروي کنند، نه ترسي بر آنهاست، و نه غمگين شوند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (46, N'و کساني که کافر شدند، و آيات ما را دروغ پنداشتند اهل دوزخند؛ و هميشه در آن خواهند بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (47, N'اي فرزندان اسرائيل! نعمتهايي را که به شما ارزاني داشتم به ياد آوريد! و به پيماني که با من بسته‌ايد وفا کنيد، تا من نيز به پيمان شما وفا کنم.( و در راه انجام وظيفه، و عمل به پيمانها) تنها از من بترسيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (48, N'و به آنچه نازل کرده‌ام [= قرآن‌] ايمان بياوريد! که نشانه‌هاي آن، با آنچه در کتابهاي شماست، مطابقت دارد؛ و نخستين کافر به آن نباشيد! و آيات مرا به بهاي ناچيزي نفروشيد! (و به خاطر درآمد مختصري، نشانه‌هاي قرآن و پيامبر اسلام را، که در کتب شما موجود است، پنهان نکنيد!) و تنها از من (و مخالفت دستورهايم) بترسيد (نه از مردم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (49, N'و حق را با باطل نياميزيد! و حقيقت را با اينکه مي‌دانيد کتمان نکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (50, N'و نماز را بپا داريد، و زکات را بپردازيد، و همراه رکوع کنندگان رکوع کنيد (و نماز را با جماعت بگزاريد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (51, N'آيا مردم را به نيکي (و ايمان به پيامبري که صفات او آشکارا در تورات آمده) دعوت مي‌کنيد، اما خودتان را فراموش مي‌نماييد؛ با اينکه شما کتاب (آسماني) را مي‌خوانيد! آيا نمي‌انديشيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (52, N'از صبر و نماز ياري جوئيد؛ (و با استقامت و مهار هوسهاي دروني و توجه به پروردگار ، نيرو بگيريد؛) و اين کار، جز براي خاشعان، گران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (53, N'آنها کساني هستند که مي‌دانند ديدارکننده پروردگار خويشند، و به سوي او بازمي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (54, N'اي بني اسرائيل! نعمتهايي را که به شما ارزاني داشتم به خاطر بياوريد؛ و (نيز به ياد آوريد که) من، شما را بر جهانيان، برتري بخشيدم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (55, N'و از آن روز بترسيد که کسي مجازات ديگري را نمي‌پذيرد و نه از او شفاعت پذيرفته مي‌شود؛ و نه غرامت از او قبول خواهد شد؛ و نه ياري مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (56, N'و (نيز به ياد آوريد) آن زمان که شما را از چنگال فرعونيان رهايي بخشيديم؛ که همواره شما را به بدترين صورت آزار مي‌دادند: پسران شما را سر مي‌بريدند؛ و زنان شما را (براي کنيزي) زنده نگه مي‌داشتند. و در اينها، آزمايش بزرگي از طرف پروردگارتان براي شما بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (57, N'و (به خاطر بياوريد) هنگامي را که دريا را براي شما شکافتيم؛ و شما را نجات داديم؛ و فرعونيان را غرق ساختيم؛ در حالي که شما تماشا مي‌کرديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (58, N'و (به ياد آوريد) هنگامي را که با موسي چهل شب وعده گذارديم؛ (و او، براي گرفتن فرمانهاي الهي، به ميعادگاه آمد؛) سپس شما گوساله را بعد از او (معبود خود) انتخاب نموديد؛ در حالي که ستمکار بوديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (59, N'سپس شما را بعد از آن بخشيديم؛ شايد شکر (اين نعمت را) بجا آوريد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (60, N'و (نيز به خاطر آوريد) هنگامي را که به موسي، کتاب و وسيله تشخيص (حق از باطل) را داديم؛ تا هدايت شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (61, N'و زماني را که موسي به قوم خود گفت: «اي قوم من! شما با انتخاب گوساله (براي پرستش) به خود ستم کرديد! پس توبه کنيد؛ و به سوي خالق خود باز گرديد! و خود را [=يکديگر را] به قتل برسانيد! اين کار، براي شما در پيشگاه پروردگارتان بهتر است.» سپس خداوند توبه شما را پذيرفت؛ زيرا که او توبه‌پذير و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (62, N'و (نيز به ياد آوريد) هنگامي را که گفتيد: «اي موسي! ما هرگز به تو ايمان نخواهيم آورد؛ مگر اينکه خدا را آشکارا (با چشم خود) ببينيم!» پس صاعقه شما را گرفت؛ در حالي که تماشا مي‌کرديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (63, N'سپس شما را پس از مرگتان، حيات بخشيديم؛ شايد شکر (نعمت او را) بجا آوريد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (64, N'و ابر را بر شما سايبان قرار داديم؛ و «من» [= شيره مخصوص و لذيذ درختان ] و «سلوي» [= مرغان مخصوص شبيه کبوتر] را بر شما فرستاديم؛ (و گفتيم:) «از نعمتهاي پاکيزه‌اي که به شما روزي داده‌ايم بخوريد!» (ولي شما کفران کرديد!) آنها به ما ستم نکردند؛ بلکه به خود ستم مي‌نمودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (65, N'و (به خاطر بياوريد) زماني را که گفتيم: «در اين شهر [= بيت المقدس‌] وارد شويد! و از نعمتهاي فراوان آن، هر چه مي‌خواهيد بخوريد! و از در (معبد بيت المقدس) با خضوع و خشوع وارد گرديد! و بگوييد: «خداوندا! گناهان ما را بريز!» تا خطاهاي شما را ببخشيم؛ و به نيکوکاران پاداش بيشتري خواهيم داد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (66, N'اما افراد ستمگر، اين سخن را که به آنها گفته شده بود، تغيير دادند؛ (و به جاي آن، جمله استهزاآميزي گفتند؛) لذا بر ستمگران، در برابر اين نافرماني ، عذابي از آسمان فرستاديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (67, N'و (به ياد آوريد) زماني را که موسي براي قوم خويش، آب طلبيد، به او دستور داديم: «عصاي خود را بر آن سنگ مخصوص بزن!» ناگاه دوازده چشمه آب از آن جوشيد؛ آن‌گونه که هر يک (از طوايف دوازده‌گانه بني اسرائيل)، چشمه مخصوص خود را مي‌شناختند! (و گفتيم:) «از روزيهاي الهي بخوريد و بياشاميد! و در زمين فساد نکنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (68, N'و (نيز به خاطر بياوريد) زماني را که گفتيد: «اي موسي! هرگز حاضر نيستيم به يک نوع غذا اکتفا کنيم! از خداي خود بخواه که از آنچه زمين مي‌روياند، از سبزيجات و خيار و سير و عدس و پيازش، براي ما فراهم سازد.» موسي گفت: «آيا غذاي پست‌تر را به جاي غذاي بهتر انتخاب مي‌کنيد؟! (اکنون که چنين است، بکوشيد از اين بيابان) در شهري فرود آئيد؛ زيرا هر چه خواستيد، در آنجا براي شما هست.» و (مهر) ذلت و نياز، بر پيشاني آنها زده شد؛ و باز گرفتار خشم خدائي شدند ؛ چرا که آنان نسبت به آيات الهي، کفر مي‌ورزيدند؛ و پيامبران را به ناحق مي‌کشتند. اينها به خاطر آن بود که گناهکار و متجاوز بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (69, N'کساني که (به پيامبر اسلام) ايمان آورده‌اند، و کساني که به آئين يهود گرويدند و نصاري و صابئان [= پيروان يحيي‌] هر گاه به خدا و روز رستاخيز ايمان آورند ، و عمل صالح انجام دهند، پاداششان نزد پروردگارشان مسلم است؛ و هيچ‌گونه ترس و اندوهي براي آنها نيست. (هر کدام از پيروان اديان الهي، که در عصر و زمان خود، بر طبق وظايف و فرمان دين عمل کرده‌اند، مأجور و رستگارند.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (70, N'و (به ياد آوريد) زماني را که از شما پيمان گرفتيم؛ و کوه طور را بالاي سر شما قرار داديم؛ (و به شما گفتيم:) «آنچه را (از آيات و دستورهاي خداوند) به شما داده‌ايم، با قدرت بگيريد؛ و آنچه را در آن است به ياد داشته باشيد (و به آن عمل کنيد)؛ شايد پرهيزکار شويد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (71, N'سپس شما پس از اين، روگردان شديد؛ و اگر فضل و رحمت خداوند بر شما نبود، از زيانکاران بوديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (72, N'به طور قطع از حال کساني از شما، که در روز شنبه نافرماني و گناه کردند، آگاه شده‌ايد! ما به آنها گفتيم: «به صورت بوزينه‌هايي طردشده درآييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (73, N'ما اين کيفر را درس عبرتي براي مردم آن زمان و نسلهاي بعد از آنان، و پند و اندرزي براي پرهيزکاران قرار داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (74, N'و (به ياد آوريد) هنگامي را که موسي به قوم خود گفت: «خداوند به شما دستور مي‌دهد ماده‌گاوي را ذبح کنيد (و قطعه‌اي از بدن آن را به مقتولي که قاتل او شناخته نشده بزنيد، تا زنده شود و قاتل خويش را معرفي کند؛ و غوغا خاموش گردد.)» گفتند: «آيا ما را مسخره مي‌کني؟» (موسي) گفت: «به خدا پناه مي‌برم از اينکه از جاهلان باشم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (75, N'گفتند: «(پس) از خداي خود بخواه که براي ما روشن کند اين ماده‌گاو چگونه ماده‌گاوي باشد؟» گفت: خداوند مي‌فرمايد: «ماده‌گاوي است که نه پير و از کار افتاده باشد، و نه بکر و جوان؛ بلکه ميان اين دو باشد. آنچه به شما دستور داده شده، (هر چه زودتر) انجام دهيد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (76, N'گفتند: «از پروردگار خود بخواه که براي ما روشن سازد رنگ آن چگونه باشد؟ « گفت: خداوند مي‌گويد: «گاوي باشد زرد يکدست، که رنگ آن، بينندگان را شاد و مسرور سازد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (77, N'گفتند: «از خدايت بخواه براي ما روشن کند که چگونه گاوي بايد باشد؟ زيرا اين گاو براي ما مبهم شده! و اگر خدا بخواهد ما هدايت خواهيم شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (78, N'گفت: خداوند مي‌فرمايد: «گاوي باشد که نه براي شخم زدن رام شده؛ و نه براي زراعت آبکشي کند؛ از هر عيبي برکنار باشد، و حتي هيچ‌گونه رنگ ديگري در آن نباشد.» گفتند: «الان حق مطلب را آوردي!» سپس (چنان گاوي را پيدا کردند و) آن را سر بريدند؛ ولي مايل نبودند اين کار را انجام دهند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (79, N'و (به ياد آوريد) هنگامي را که فردي را به قتل رسانديد؛ سپس درباره (قاتل) او به نزاع پرداختيد؛ و خداوند آنچه را مخفي مي‌داشتيد، آشکار مي‌سازد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (80, N'سپس گفتيم: «قسمتي از گاو را به مقتول بزنيد! (تا زنده شود، و قاتل را معرفي کند.) خداوند اين‌گونه مردگان را زنده مي‌کند؛ و آيات خود را به شما نشان مي‌دهد؛ شايد انديشه کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (81, N'سپس دلهاي شما بعد از اين واقعه سخت شد؛ همچون سنگ، يا سخت‌تر! چرا که پاره‌اي از سنگها مي‌شکافد، و از آن نهرها جاري مي‌شود؛ و پاره‌اي از آنها شکاف برمي‌دارد ، و آب از آن تراوش مي‌کند؛ و پاره‌اي از خوف خدا (از فراز کوه) به زير مي‌افتد؛ (اما دلهاي شما، نه از خوف خدا مي‌تپد، و نه سرچشمه علم و دانش و عواطف انساني است!) و خداوند از اعمال شما غافل نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (82, N'آيا انتظار داريد به (آئين) شما ايمان بياورند، با اينکه عده‌اي از آنان، سخنان خدا را مي‌شنيدند و پس از فهميدن، آن را تحريف مي‌کردند، در حالي که علم و اطلاع داشتند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (83, N'و هنگامي که مؤمنان را ملاقات کنند، مي‌گويند: «ايمان آورده‌ايم.» ولي هنگامي که با يکديگر خلوت مي‌کنند، (بعضي به بعضي ديگر اعتراض کرده،) مي‌گويند: «چرا مطالبي را که خداوند (در باره صفات پيامبر اسلام) براي شما بيان کرد، به مسلمانان بازگو مي‌کنيد تا (روز رستاخيز) در پيشگاه خدا، بر ضد شما به آن استدلال کنند؟! آيا نمي‌فهميد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (84, N'آيا اينها نمي‌دانند خداوند آنچه را پنهان مي‌دارند يا آشکار مي‌کنند مي‌داند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (85, N'و پاره‌اي از آنان عواماني هستند که کتاب خدا را جز يک مشت خيالات و آرزوها نمي‌دانند؛ و تنها به پندارهايشان دل بسته‌اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (86, N'پس واي بر آنها که نوشته‌اي با دست خود مي‌نويسند، سپس مي‌گويند: «اين، از طرف خداست.» تا آن را به بهاي کمي بفروشند. پس واي بر آنها از آنچه با دست خود نوشتند؛ و واي بر آنان از آنچه از اين راه به دست مي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (87, N'و گفتند: «هرگز آتش دوزخ، جز چند روزي، به ما نخواهد رسيد.» بگو: «آيا پيماني از خدا گرفته‌ايد؟! -و خداوند هرگز از پيمانش تخلف نمي‌ورزد- يا چيزي را که نمي‌دانيد به خدا نسبت مي‌دهيد»؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (88, N'آري، کساني که کسب گناه کنند، و آثار گناه، سراسر وجودشان را بپوشاند، آنها اهل آتشند؛ و جاودانه در آن خواهند بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (89, N'و آنها که ايمان آورده، و کارهاي شايسته انجام داده‌اند، آنان اهل بهشتند؛ و هميشه در آن خواهند ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (90, N'و (به ياد آوريد) زماني را که از بني اسرائيل پيمان گرفتيم که جز خداوند يگانه را پرستش نکنيد؛ و به پدر و مادر و نزديکان و يتيمان و بينوايان نيکي کنيد؛ و به مردم نيک بگوييد؛ نماز را برپا داريد؛ و زکات بدهيد. سپس (با اينکه پيمان بسته بوديد) همه شما -جز عده کمي- سرپيچي کرديد؛ و (از وفاي به پيمان خود) روي‌گردان شديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (91, N'و هنگامي را که از شما پيمان گرفتيم که خون هم را نريزيد؛ و يکديگر را از سرزمين خود، بيرون نکنيد. سپس شما اقرار کرديد؛ (و بر اين پيمان) گواه بوديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (92, N'اما اين شما هستيد که يکديگر را مي‌کشيد و جمعي از خودتان را از سرزمينشان بيرون مي‌کنيد؛ و در اين گناه و تجاوز، به يکديگر کمک مي‌نماييد؛ (و اينها همه نقض پيماني است که با خدا بسته‌ايد) در حالي که اگر بعضي از آنها به صورت اسيران نزد شما آيند، فديه مي‌دهيد و آنان را آزاد مي‌سازيد! با اينکه بيرون ساختن آنان بر شما حرام بود. آيا به بعضي از دستورات کتاب آسماني ايمان مي‌آوريد، و به بعضي کافر مي‌شويد؟! براي کسي از شما که اين عمل (تبعيض در ميان احکام و قوانين الهي) را انجام دهد، جز رسوايي در اين جهان، چيزي نخواهد بود، و روز رستاخيز به شديدترين عذابها گرفتار مي‌شوند. و خداوند از آنچه انجام مي‌دهيد غافل نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (93, N'اينها همان کسانند که آخرت را به زندگي دنيا فروخته‌اند؛ از اين رو عذاب آنها تخفيف داده نمي‌شود؛ و کسي آنها را ياري نخواهد کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (94, N'ما به موسي کتاب (تورات) داديم؛ و بعد از او، پيامبراني پشت سر هم فرستاديم؛ و به عيسي بن مريم دلايل روشن داديم؛ و او را به وسيله روح القدس تأييد کرديم. آيا چنين نيست که هر زمان، پيامبري چيزي بر خلاف هواي نفس شما آورد، در برابر او تکبر کرديد (و از ايمان آوردن به او خودداري نموديد)؛ پس عده‌اي را تکذيب کرده، و جمعي را به قتل رسانديد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (95, N'و (آنها از روي استهزا) گفتند: دلهاي ما در غلاف است! (و ما از گفته تو چيزي نمي‌فهميم. آري، همين طور است!) خداوند آنها را به خاطر کفرشان، از رحمت خود دور ساخته، (به همين دليل، چيزي درک نمي‌کنند؛) و کمتر ايمان مي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (96, N'و هنگامي که از طرف خداوند، کتابي براي آنها آمد که موافق نشانه‌هايي بود که با خود داشتند، و پيش از اين، به خود نويد پيروزي بر کافران مي‌دادند (که با کمک آن، بر دشمنان پيروز گردند.) با اين همه، هنگامي که اين کتاب، و پيامبري را که از قبل شناخته بودند نزد آنها آمد، به او کافر شدند؛ لعنت خدا بر کافران باد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (97, N'ولي آنها در مقابل بهاي بدي، خود را فروختند؛ که به ناروا، به آياتي که خدا فرستاده بود، کافر شدند. و معترض بودند، چرا خداوند به فضل خويش، بر هر کس از بندگانش بخواهد، آيات خود را نازل مي‌کند؟! از اين رو به خشمي بعد از خشمي (از سوي خدا) گرفتار شدند. و براي کافران مجازاتي خوارکننده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (98, N'و هنگامي که به آنها گفته شود: «به آنچه خداوند نازل فرموده، ايمان بياوريد!»مي‌گويند: «ما به چيزي ايمان مي‌آوريم که بر خود ما نازل شده است.» و به غير آن، کافر مي‌شوند؛ در حالي که حق است؛ و آياتي را که بر آنها نازل شده، تصديق مي‌کند. بگو: «اگر (راست مي‌گوييد، و به آياتي که بر خودتان نازل شده) ايمان داريد، پس چرا پيامبران خدا را پيش از اين، به قتل مي‌رسانديد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (99, N'و (نيز) موسي آن همه معجزات را براي شما آورد، و شما پس از (غيبت) او، گوساله را انتخاب کرديد؛ در حالي که ستمگر بوديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (100, N'و (به ياد آوريد) زماني را که از شما پيمان گرفتيم؛ و کوه طور را بالاي سر شما برافراشتيم؛ (و گفتيم:) «اين دستوراتي را که به شما داده‌ايم محکم بگيريد، و درست بشنويد!» آنها گفتند: «شنيديم؛ ولي مخالفت کرديم.» و دلهاي آنها، بر اثر کفرشان، با محبت گوساله آميخته شد. بگو: «ايمان شما، چه فرمان بدي به شما مي‌دهد، اگر ايمان داريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (101, N'بگو: «اگر آن (چنان که مدعي هستيد) سراي ديگر در نزد خدا، مخصوص شماست نه ساير مردم، پس آرزوي مرگ کنيد اگر راست مي‌گوييد!»')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (102, N'ولي آنها، به خاطر اعمال بدي که پيش از خود فرستاده‌اند، هرگز آرزوي مرگ نخواهند کرد؛ و خداوند از ستمگران آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (103, N'و آنها را حريص‌ترين مردم -حتي حريصتر از مشرکان- بر زندگي (اين دنيا، و اندوختن ثروت) خواهي يافت؛ (تا آنجا) که هر يک از آنها آرزو دارد هزار سال عمر به او داده شود! در حالي که اين عمر طولاني، او را از کيفر (الهي) باز نخواهد داشت. و خداوند به اعمال آنها بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (104, N'(آنها مي‌گويند: «چون فرشته‌اي که وحي را بر تو نازل مي‌کند، جبرئيل است، و ما با جبرئيل دشمن هستيم، به تو ايمان نمي‌آوريم!») بگو: «کسي که دشمن جبرئيل باشد (در حقيقت دشمن خداست) چرا که او به فرمان خدا، قرآن را بر قلب تو نازل کرده است؛ در حالي که کتب آسماني پيشين را تصديق مي‌کند؛ و هدايت و بشارت است براي مؤمنان.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (105, N'کسي که دشمن خدا و فرشتگان و رسولان او و جبرئيل و ميکائيل باشد (کافر است؛ و) خداوند دشمن کافران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (106, N'ما نشانه‌هاي روشني براي تو فرستاديم؛ و جز فاسقان کسي به آنها کفر نمي‌ورزد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (107, N'و آيا چنين نيست که هر بار آنها [=يهود] پيماني (با خدا و پيامبر)بستند، جمعي آن را دور افکندند (و مخالفت کردند.) آري، بيشتر آنان ايمان نمي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (108, N'و هنگامي که فرستاده‌اي از سوي خدا به سراغشان آمد، و با نشانه‌هايي که نزد آنها بود مطابقت داشت، جمعي از آنان که به آنها کتاب (آسماني) داده شده بود ، کتاب خدا را پشت سر افکندند؛ گويي هيچ از آن خبر ندارند!!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (109, N'و (يهود) از آنچه شياطين در عصر سليمان بر مردم مي‌خواندند پيروي کردند. سليمان هرگز (دست به سحر نيالود؛ و) کافر نشد؛ ولي شياطين کفر ورزيدند؛ و به مردم سحر آموختند. و (نيز يهود) از آنچه بر دو فرشته بابل «هاروت» و «ماروت»، نازل شد پيروي کردند. (آن دو، راه سحر کردن را، براي آشنايي با طرز ابطال آن، به مردم ياد مي‌دادند. و) به هيچ کس چيزي ياد نمي‌دادند، مگر اينکه از پيش به او مي‌گفتند: «ما وسيله آزمايشيم کافر نشو! (و از اين تعليمات، سوء استفاده نکن!)» ولي آنها از آن دو فرشته، مطالبي را مي‌آموختند که بتوانند به وسيله آن، ميان مرد و همسرش جدايي بيفکنند؛ ولي هيچ گاه نمي‌توانند بدون اجازه خداوند، به انساني زيان برسانند. آنها قسمتهايي را فرامي‌گرفتند که به آنان زيان مي‌رسانيد و نفعي نمي‌داد. و مسلما مي‌دانستند هر کسي خريدار اين گونه متاع باشد، در آخرت بهره‌اي نخواهد داشت. و چه زشت و ناپسند بود آنچه خود را به آن فروختند، اگر مي‌دانستند!!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (110, N'و اگر آنها ايمان مي‌آوردند و پرهيزکاري پيشه مي‌کردند، پاداشي که نزد خداست ، براي آنان بهتر بود، اگر آگاهي داشتند!!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (111, N'اي افراد باايمان! (هنگامي که از پيغمبر تقاضاي مهلت براي درک آيات قرآن مي‌کنيد) نگوييد: «راعنا»؛ بلکه بگوييد: «انظرنا». (زيرا کلمه اول، هم به معني «ما را مهلت بده!»، و هم به معني «ما را تحميق کن!» مي‌باشد؛ و دستاويزي براي دشمنان است.) و (آنچه به شما دستور داده مي‌شود) بشنويد! و براي کافران (و استهزاکنندگان) عذاب دردناکي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (112, N'کافران اهل کتاب، و (همچنين) مشرکان، دوست ندارند که از سوي خداوند، خير و برکتي بر شما نازل گردد؛ در حالي که خداوند، رحمت خود را به هر کس بخواهد ، اختصاص مي‌دهد؛ و خداوند، صاحب فضل بزرگ است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (113, N'هر حکمي را نسخ کنيم، و يا نسخ آن را به تأخير اندازيم، بهتر از آن، يا همانند آن را مي‌آوريم. آيا نمي‌دانستي که خداوند بر هر چيز توانا است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (114, N'آيا نمي‌دانستي که حکومت آسمانها و زمين، از آن خداست؟! (و حق دارد هر گونه تغيير و تبديلي در احکام خود طبق مصالح بدهد؟!) و جز خدا، ولي و ياوري براي شما نيست. (و اوست که مصلحت شما را مي‌داند و تعيين مي‌کند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (115, N'آيا مي‌خواهيد از پيامبر خود، همان تقاضاي (نامعقولي را) بکنيد که پيش از اين، از موسي کردند؟! (و با اين بهانه‌جويي‌ها، از ايمان آوردن سر باز زدند.) کسي که کفر را به جاي ايمان بپذيرد، از راه مستقيم (عقل و فطرت) گمراه شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (116, N'بسياري از اهل کتاب، از روي حسد -که در وجود آنها ريشه دوانده- آرزو مي‌کردند شما را بعد از اسلام و ايمان، به حال کفر باز گردانند؛ با اينکه حق براي آنها کاملا روشن شده است. شما آنها را عفو کنيد و گذشت نماييد؛ تا خداوند فرمان خودش (فرمان جهاد) را بفرستد؛ خداوند بر هر چيزي تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (117, N'و نماز را برپا داريد و زکات را ادا کنيد؛ و هر کار خيري را براي خود از پيش مي‌فرستيد، آن را نزد خدا (در سراي ديگر) خواهيد يافت؛ خداوند به اعمال شما بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (118, N'آنها گفتند: «هيچ کس، جز يهود يا نصاري، هرگز داخل بهشت نخواهد شد.» اين آرزوي آنهاست! بگو: «اگر راست مي‌گوييد، دليل خود را (بر اين موضوع) بياوريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (119, N'آري، کسي که روي خود را تسليم خدا کند و نيکوکار باشد، پاداش او نزد پروردگارش ثابت است؛ نه ترسي بر آنهاست و نه غمگين مي‌شوند. (بنابر اين، بهشت خدا در انحصار هيچ گروهي نيست.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (120, N'يهوديان گفتند: «مسيحيان هيچ موقعيتي (نزد خدا) ندارند»، و مسيحيان نيز گفتند: «يهوديان هيچ موقعيتي ندارند (و بر باطلند)»؛ در حالي که هر دو دسته، کتاب آسماني را مي‌خوانند (و بايد از اين گونه تعصبها برکنار باشند)افراد نادان (ديگر، همچون مشرکان) نيز، سخني همانند سخن آنها داشتند! خداوند، روز قيامت، در باره آنچه در آن اختلاف داشتند، داوري مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (121, N'کيست ستمکارتر از آن کس که از بردن نام خدا در مساجد او جلوگيري کرد و سعي در ويراني آنها نمود؟! شايسته نيست آنان، جز با ترس و وحشت، وارد اين (کانونهاي عبادت) شوند. بهره آنها در دنيا (فقط)رسوايي است و در سراي ديگر، عذاب عظيم (الهي)!!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (122, N'مشرق و مغرب، از آن خداست! و به هر سو رو کنيد، خدا آنجاست! خداوند بي‌نياز و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (123, N'و (يهود و نصاري و مشرکان) گفتند: «خداوند، فرزندي براي خود انتخاب کرده است»! -منزه است او- بلکه آنچه در آسمانها و زمين است، از آن اوست؛ و همه در برابر او خاضعند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (124, N'هستي بخش آسمانها و زمين اوست! و هنگامي که فرمان وجود چيزي را صادر کند، تنها مي‌گويد: «موجود باش!» و آن، فوري موجود مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (125, N'افراد ناآگاه گفتند: «چرا خدا با ما سخن نمي‌گويد؟! و يا چرا آيه و نشانه‌اي براي خود ما نمي‌آيد؟!» پيشينيان آنها نيز، همين گونه سخن مي‌گفتند؛ دلها و افکارشان مشابه يکديگر است؛ ولي ما (به اندازه کافي) آيات و نشانه‌ها را براي اهل يقين (و حقيقت‌جويان) روشن ساخته‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (126, N'ما تو را به حق، براي بشارت و بيم دادن (مردم جهان) فرستاديم؛ و تو مسئول (گمراهي) دوزخيان (پس از ابلاغ رسالت) نيستي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (127, N'هرگز يهود و نصاري از تو راضي نخواهند شد، (تا به طور کامل، تسليم خواسته‌هاي آنها شوي، و) از آيين (تحريف يافته) آنان، پيروي کني. بگو: «هدايت، تنها هدايت الهي است!» و اگر از هوي و هوسهاي آنان پيروي کني، بعد از آنکه آگاه شده‌اي، هيچ سرپرست و ياوري از سوي خدا براي تو نخواهد بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (128, N'کساني که کتاب آسماني به آنها داده‌ايم [= يهود و نصاري‌] آن را چنان که شايسته آن است مي‌خوانند؛ آنها به پيامبر اسلام ايمان مي‌آورند؛ و کساني که به او کافر شوند، زيانکارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (129, N'اي بني اسرائيل! نعمت مرا، که به شما ارزاني داشتم، به ياد آوريد! و(نيز به خاطر آوريد) که من شما را بر جهانيان برتري بخشيدم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (130, N'از روزي بترسيد که هيچ کس از ديگري دفاع نمي‌کند؛ و هيچ‌گونه عوضي از او قبول نمي‌شود؛ و شفاعت، او را سود نمي‌دهد؛ و (از هيچ سوئي) ياري نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (131, N'(به خاطر آوريد) هنگامي که خداوند، ابراهيم را با وسايل گوناگوني آزمود. و او به خوبي از عهده اين آزمايشها برآمد. خداوند به او فرمود: «من تو را امام و پيشواي مردم قرار دادم!» ابراهيم عرض کرد: «از دودمان من (نيز اماماني قرار بده!)» خداوند فرمود: «پيمان من، به ستمکاران نمي‌رسد! (و تنها آن دسته از فرزندان تو که پاک و معصوم باشند، شايسته اين مقامند)».')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (132, N'و (به خاطر بياوريد) هنگامي که خانه کعبه را محل بازگشت و مرکز امن و امان براي مردم قرار داديم! و (براي تجديد خاطره،) از مقام ابراهيم، عبادتگاهي براي خود انتخاب کنيد! و ما به ابراهيم و اسماعيل امر کرديم که: «خانه مرا براي طواف‌کنندگان و مجاوران و رکوع‌کنندگان و سجده‌کنندگان، پاک و پاکيزه کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (133, N'و (به ياد آوريد) هنگامي را که ابراهيم عرض کرد: «پروردگارا! اين سرزمين را شهر امني قرار ده! و اهل آن را -آنها که به خدا و روز بازپسين، ايمان آورده‌اند- از ثمرات (گوناگون)، روزي ده!» (گفت:) «ما دعاي تو را اجابت کرديم؛ و مؤمنان را از انواع برکات، بهره‌مند ساختيم؛ (اما به آنها که کافر شدند، بهره کمي خواهيم داد؛ سپس آنها را به عذاب آتش مي‌کشانيم؛ و چه بد سرانجامي دارند»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (134, N'و (نيز به ياد آوريد) هنگامي را که ابراهيم و اسماعيل، پايه‌هاي خانه (کعبه) را بالا مي‌بردند، (و مي‌گفتند:) «پروردگارا! از ما بپذير، که تو شنوا و دانايي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (135, N'پروردگارا! ما را تسليم فرمان خود قرار ده! و از دودمان ما، امتي که تسليم فرمانت باشند، به وجود آور! و طرز عبادتمان را به ما نشان ده و توبه ما را بپذير ، که تو توبه‌پذير و مهرباني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (136, N'پروردگارا! در ميان آنها پيامبري از خودشان برانگيز، تا آيات تو را بر آنان بخواند، و آنها را کتاب و حکمت بياموزد، و پاکيزه کند؛ زيرا تو توانا و حکيمي (و بر اين کار، قادري)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (137, N'چز افراد سفيه و نادان، چه کسي از آيين ابراهيم، (با آن پاکي و درخشندگي،) روي‌گردان خواهد شد؟! ما او را در اين جهان برگزيديم؛ و او در جهان ديگر، از صالحان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (138, N'در آن هنگام که پروردگارش به او گفت: اسلام بياور! (و در برابر حق، تسليم باش! او فرمان پروردگار را، از جان و دل پذيرفت؛ و) گفت: «در برابر پروردگار جهانيان، تسليم شدم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (139, N'و ابراهيم و يعقوب (در واپسين لحظات عمر،) فرزندان خود را به اين آيين، وصيت کردند؛ (و هر کدام به فرزندان خويش گفتند:) «فرزندان من! خداوند اين آيين پاک را براي شما برگزيده است؛ و شما، جز به آيين اسلام [= تسليم در برابر فرمان خدا] از دنيا نرويد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (140, N'آيا هنگامي که مرگ يعقوب فرا رسيد، شما حاضر بوديد؟! در آن هنگام که به فرزندان خود گفت: «پس از من، چه چيز را مي‌پرستيد؟» گفتند: «خداي تو، و خداي پدرانت، ابراهيم و اسماعيل و اسحاق، خداوند يکتا را، و ما در برابر او تسليم هستيم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (141, N'آنها امتي بودند که درگذشتند. اعمال آنان، مربوط به خودشان بود و اعمال شما نيز مربوط به خود شماست؛ و شما هيچ‌گاه مسئول اعمال آنها نخواهيد بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (142, N'(اهل کتاب) گفتند: «يهودي يا مسيحي شويد، تا هدايت يابيد!» بگو: «(اين آيينهاي تحريف شده، هرگز نمي‌تواند موجب هدايت گردد،) بلکه از آيين خالص ابراهيم پيروي کنيد! و او هرگز از مشرکان نبود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (143, N'بگوييد: «ما به خدا ايمان آورده‌ايم؛ و به آنچه بر ما نازل شده؛ و آنچه بر ابراهيم و اسماعيل و اسحاق و يعقوب و پيامبران از فرزندان او نازل گرديد ، و (همچنين) آنچه به موسي و عيسي و پيامبران (ديگر) از طرف پروردگار داده شده است، و در ميان هيچ يک از آنها جدايي قائل نمي‌شويم، و در برابر فرمان خدا تسليم هستيم؛ (و تعصبات نژادي و اغراض شخصي، سبب نمي‌شود که بعضي را بپذيريم و بعضي را رها کنيم.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (144, N'اگر آنها نيز به مانند آنچه شما ايمان آورده‌ايد ايمان بياورند، هدايت يافته‌اند؛ و اگر سرپيچي کنند، از حق جدا شده‌اند و خداوند، شر آنها را از تو دفع مي‌کند؛ و او شنونده و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (145, N'رنگ خدايي (بپذيريد! رنگ ايمان و توحيد و اسلام؛) و چه رنگي از رنگ خدايي بهتر است؟! و ما تنها او را عبادت مي‌کنيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (146, N'بگو: «آيا در باره خداوند با ما محاجه مي‌کنيد؟! در حالي که او، پروردگار ما و شماست؛ و اعمال ما از آن ما، و اعمال شما از آن شماست؛ و ما او را با اخلاص پرستش مي‌کنيم، (و موحد خالصيم).»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (147, N'يا مي‌گوييد: «ابراهيم و اسماعيل و اسحاق و يعقوب و اسباط، يهودي يا نصراني بودند»؟! بگو: «شما بهتر مي‌دانيد يا خدا؟! (و با اينکه مي‌دانيد آنها يهودي يا نصراني نبودند، چرا حقيقت را کتمان مي‌کنيد؟)» و چه کسي ستمکارتر است از آن کس که گواهي و شهادت الهي را که نزد اوست، کتمان مي‌کند؟! و خدا از اعمال شما غافل نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (148, N'(به هر حال) آنها امتي بودند که درگذشتند. آنچه کردند، براي خودشان است؛ و آنچه هم شما کرده‌ايد، براي خودتان است؛ و شما مسئول اعمال آنها نيستيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (149, N'به زودي سبک‌مغزان از مردم مي‌گويند: «چه چيز آنها [= مسلمانان‌] را، از قبله‌اي که بر آن بودند، بازگردانيد؟!» بگو: «مشرق و مغرب، از آن خداست؛ خدا هر کس را بخواهد، به راه راست هدايت مي‌کند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (150, N'همان‌گونه (که قبله شما، يک قبله ميانه است) شما را نيز، امت ميانه‌اي قرار داديم (در حد اعتدال، ميان افراط و تفريط؛) تا بر مردم گواه باشيد؛ و پيامبر هم بر شما گواه است. و ما، آن قبله‌اي را که قبلا بر آن بودي، تنها براي اين قرار داديم که افرادي که از پيامبر پيروي مي‌کنند، از آنها که به جاهليت بازمي‌گردند، مشخص شوند. و مسلما اين حکم، جز بر کساني که خداوند آنها را هدايت کرده، دشوار بود. (اين را نيز بدانيد که نمازهاي شما در برابر قبله سابق، صحيح بوده است؛) و خدا هرگز ايمان [= نماز] شما را ضايع نمي‌گرداند؛ زيرا خداوند، نسبت به مردم، رحيم و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (151, N'نگاه‌هاي انتظارآميز تو را به سوي آسمان (براي تعيين قبله نهايي) مي‌بينيم! اکنون تو را به سوي قبله‌اي که از آن خشنود باشي، باز مي‌گردانيم. پس روي خود را به سوي مسجد الحرام کن! و هر جا باشيد، روي خود را به سوي آن بگردانيد! و کساني که کتاب آسماني به آنها داده شده، بخوبي مي‌دانند اين فرمان حقي است که از ناحيه پروردگارشان صادر شده؛ (و در کتابهاي خود خوانده‌اند که پيغمبر اسلام، به سوي دو قبله، نماز مي‌خواند). و خداوند از اعمال آنها (در مخفي داشتن اين آيات) غافل نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (152, N'سوگند که اگر براي (اين گروه از) اهل کتاب، هرگونه آيه (و نشانه و دليلي) بياوري، از قبله تو پيروي نخواهند کرد؛ و تو نيز، هيچ‌گاه از قبله آنان، پيروي نخواهي نمود. (آنها نبايد تصور کنند که بار ديگر، تغيير قبله امکان‌پذير است!) و حتي هيچ‌يک از آنها، پيروي از قبله ديگري نخواهد کرد! و اگر تو، پس از اين آگاهي، متابعت هوسهاي آنها کني، مسلما از ستمگران خواهي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (153, N'کساني که کتاب آسماني به آنان داده‌ايم، او [= پيامبر] را همچون فرزندان خود مي‌شناسند؛ (ولي) جمعي از آنان، حق را آگاهانه کتمان مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (154, N'اين (فرمان تغيير قبله) حکم حقي از طرف پروردگار توست، بنابراين، هرگز از ترديدکنندگان در آن مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (155, N'هر طايفه‌اي قبله‌اي دارد که خداوند آن را تعيين کرده است؛ (بنابراين، زياد در باره قبله گفتگو نکنيد! و به جاي آن،) در نيکي‌ها و اعمال خير، بر يکديگر سبقت جوييد! هر جا باشيد، خداوند همه شما را (براي پاداش و کيفر در برابر اعمال نيک و بد، در روز رستاخيز،) حاضر مي‌کند؛ زيرا او، بر هر کاري تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (156, N'از هر جا (و از هر شهر و نقطه‌اي) خارج شدي، (به هنگام نماز،) روي خود را به جانب «مسجد الحرام» کن! اين دستور حقي از طرف پروردگار توست! و خداوند، از آنچه انجام مي‌دهيد، غافل نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (157, N'و از هر جا خارج شدي، روي خود را به جانب مسجد الحرام کن! و هر جا بوديد، روي خود را به سوي آن کنيد! تا مردم، جز ظالمان (که دست از لجاجت برنمي‌دارند،) دليلي بر ضد شما نداشته باشند؛ (زيرا از نشانه‌هاي پيامبر، که در کتب آسماني پيشين آمده، اين است که او، به سوي دو قبله، نماز مي‌خواند.) از آنها نترسيد! و (تنها) از من بترسيد! (اين تغيير قبله، به خاطر آن بود که) نعمت خود را بر شما تمام کنم، شايد هدايت شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (158, N'همان‌گونه (که با تغيير قبله، نعمت خود را بر شما کامل کرديم،) رسولي از خودتان در ميان شما فرستاديم؛ تا آيات ما را بر شما بخواند؛ و شما را پاک کند؛ و به شما، کتاب و حکمت بياموزد؛ و آنچه را نمي‌دانستيد، به شما ياد دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (159, N'پس به ياد من باشيد، تا به ياد شما باشم! و شکر مرا گوييد و (در برابر نعمتهايم) کفران نکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (160, N'اي افرادي که ايمان آورده‌ايد! از صبر (و استقامت) و نماز، کمک بگيريد! (زيرا) خداوند با صابران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (161, N'و به آنها که در راه خدا کشته مي‌شوند، مرده نگوييد! بلکه آنان زنده‌اند،ولي شما نمي‌فهميد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (162, N'قطعا همه شما را با چيزي از ترس، گرسنگي، و کاهش در مالها و جانها و ميوه‌ها، آزمايش مي‌کنيم؛ و بشارت ده به استقامت‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (163, N'آنها که هر گاه مصيبتي به ايشان مي‌رسد، مي‌گويند: «ما از آن خدائيم؛ و به سوي او بازمي‌گرديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (164, N'اينها، همانها هستند که الطاف و رحمت خدا شامل حالشان شده؛ و آنها هستند هدايت‌يافتگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (165, N'«صفا» و «مروه» از شعائر (و نشانه‌هاي) خداست! بنابراين، کساني که حج خانه خدا و يا عمره انجام مي‌دهند، مانعي نيست که بر آن دو طواف کنند؛ (و سعي صفا و مروه انجام دهند. و هرگز اعمال بي‌رويه مشرکان، که بتهايي بر اين دو کوه نصب کرده بودند، از موقعيت اين دو مکان مقدس نمي‌کاهد!) و کسي که فرمان خدا را در انجام کارهاي نيک اطاعت کند، خداوند (در برابر عمل او) شکرگزار، و (از افعال وي) آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (166, N'کساني که دلايل روشن، و وسيله هدايتي را که نازل کرده‌ايم، بعد از آنکه در کتاب براي مردم بيان نموديم، کتمان کنند، خدا آنها را لعنت مي‌کند؛ و همه لعن‌کنندگان نيز، آنها را لعن مي‌کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (167, N'مگر آنها که توبه و بازگشت کردند، و (اعمال بد خود را، با اعمال نيک،) اصلاح نمودند، (و آنچه را کتمان کرده بودند؛ آشکار ساختند؛) من توبه آنها را مي‌پذيرم؛ که من تواب و رحيمم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (168, N'کساني که کافر شدند، و در حال کفر از دنيا رفتند، لعنت خداوند و فرشتگان و همه مردم بر آنها خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (169, N'هميشه در آن (لعن و دوري از رحمت پروردگار) باقي مي‌مانند؛ نه در عذاب آنان تخفيف داده مي‌شود، و نه مهلتي خواهند داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (170, N'و خداي شما، خداوند يگانه‌اي است، که غير از او معبودي نيست! اوست بخشنده و مهربان (و داراي رحمت عام و خاص)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (171, N'در آفرينش آسمانها و زمين، و آمد و شد شب و روز، و کشتيهايي که در دريا به سود مردم در حرکتند، و آبي که خداوند از آسمان نازل کرده، و با آن، زمين را پس از مرگ، زنده نموده، و انواع جنبندگان را در آن گسترده، و (همچنين) در تغيير مسير بادها و ابرهايي که ميان زمين و آسمان مسخرند، نشانه‌هايي است (از ذات پاک خدا و يگانگي او) براي مردمي که عقل دارند و مي‌انديشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (172, N'بعضي از مردم، معبودهايي غير از خداوند براي خود انتخاب مي‌کنند؛ و آنها را همچون خدا دوست مي‌دارند. اما آنها که ايمان دارند، عشقشان به خدا، (از مشرکان نسبت به معبودهاشان،) شديدتر است. و آنها که ستم کردند، (و معبودي غير خدا برگزيدند،) هنگامي که عذاب (الهي) را مشاهده کنند، خواهند دانست که تمام قدرت ، از آن خداست؛ و خدا داراي مجازات شديد است؛ (نه معبودهاي خيالي که از آنها مي‌هراسند.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (173, N'در آن هنگام، رهبران (گمراه و گمراه‌کننده) از پيروان خود، بيزاري مي‌جويند؛ و کيفر خدا را مشاهده مي‌کنند؛ و دستشان از همه جا کوتاه مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (174, N'و (در اين هنگام) پيروان مي‌گويند: «کاش بار ديگر به دنيا برمي‌گشتيم، تا از آنها [= پيشوايان گمراه‌] بيزاري جوييم، آن چنان که آنان (امروز) از ما بيزاري جستند! (آري،) خداوند اين چنين اعمال آنها را به صورت حسرت‌زايي به آنان نشان مي‌دهد؛ و هرگز از آتش (دوزخ) خارج نخواهند شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (175, N'اي مردم! از آنچه در زمين است، حلال و پاکيزه بخوريد! و از گامهاي شيطان، پيروي نکنيد! چه اينکه او، دشمن آشکار شماست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (176, N'او شما را فقط به بديها و کار زشت فرمان مي‌دهد؛ (و نيز دستور مي‌دهد) آنچه را که نمي‌دانيد، به خدا نسبت دهيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (177, N'و هنگامي که به آنها گفته شود: «از آنچه خدا نازل کرده است، پيروي کنيد!» مي‌گويند: «نه، ما از آنچه پدران خود را بر آن يافتيم، پيروي مي‌نماييم.» آيا اگر پدران آنها، چيزي نمي‌فهميدند و هدايت نيافتند (باز از آنها پيروي خواهند کرد)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (178, N'مثل (تو در دعوت) کافران، بسان کسي است که (گوسفندان و حيوانات را براي نجات از چنگال خطر،) صدا مي‌زند؛ ولي آنها چيزي جز سر و صدا نمي‌شنوند؛ (و حقيقت و مفهوم گفتار او را درک نمي‌کنند. اين کافران، در واقع) کر و لال و نابينا هستند؛ از اين رو چيزي نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (179, N'اي کساني که ايمان آورده‌ايد! از نعمتهاي پاکيزه‌اي که به شما روزي داده‌ايم، بخوريد و شکر خدا را بجا آوريد؛ اگر او را پرستش مي‌کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (180, N'خداوند، تنها (گوشت) مردار، خون، گوشت خوک و آنچه را نام غير خدا به هنگام ذبح بر آن گفته شود، حرام کرده است. (ولي) آن کس که مجبور شود، در صورتي که ستمگر و متجاوز نباشد، گناهي بر او نيست؛ (و مي‌تواند براي حفظ جان خود، در موقع ضرورت، از آن بخورد؛) خداوند بخشنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (181, N'کساني که کتمان مي‌کنند آنچه را خدا از کتاب نازل کرده، و آن را به بهاي کمي مي‌فروشند، آنها جز آتش چيزي نمي‌خورند؛ (و هدايا و اموالي که از اين رهگذر به دست مي‌آورند، در حقيقت آتش سوزاني است.) و خداوند، روز قيامت، با آنها سخن نمي‌گويد؛ و آنان را پاکيزه نمي‌کند؛ و براي آنها عذاب دردناکي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (182, N'اينان، همانهايي هستند که گمراهي را با هدايت، و عذاب را با آمرزش، مبادله کرده‌اند؛ راستي چقدر در برابر عذاب خداوند، شکيبا هستند!!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (183, N'اينها، به خاطر آن است که خداوند، کتاب (آسماني) را به حق، (و توأم با نشانه‌ها و دلايل روشن،) نازل کرده؛ و آنها که در آن اختلاف مي‌کنند، (و با کتمان و تحريف، اختلاف به وجود مي‌آورند،) در شکاف و (پراکندگي) عميقي قرار دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (184, N'نيکي، (تنها) اين نيست که (به هنگام نماز،) روي خود را به سوي مشرق و (يا) مغرب کنيد؛ (و تمام گفتگوي شما، در باره قبله و تغيير آن باشد؛ و همه وقت خود را مصروف آن سازيد؛) بلکه نيکي (و نيکوکار) کسي است که به خدا، و روز رستاخيز ، و فرشتگان، و کتاب (آسماني)، و پيامبران، ايمان آورده؛ و مال (خود) را، با همه علاقه‌اي که به آن دارد، به خويشاوندان و يتيمان و مسکينان و واماندگان در راه و سائلان و بردگان، انفاق مي‌کند؛ نماز را برپا مي‌دارد و زکات را مي‌پردازد؛ و (همچنين) کساني که به عهد خود -به هنگامي که عهد بستند-وفا مي‌کنند؛ و در برابر محروميتها و بيماريها و در ميدان جنگ، استقامت به خرج مي‌دهند؛ اينها کساني هستند که راست مي‌گويند؛ و (گفتارشان با اعتقادشان هماهنگ است؛) و اينها هستند پرهيزکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (185, N'اي افرادي که ايمان آورده‌ايد! حکم قصاص در مورد کشتگان، بر شما نوشته شده است: آزاد در برابر آزاد، و برده در برابر برده، و زن در برابر زن، پس اگر کسي از سوي برادر (ديني) خود، چيزي به او بخشيده شود، (و حکم قصاص او، تبديل به خونبها گردد،) بايد از راه پسنديده پيروي کند. (و صاحب خون، حال پرداخت کننده ديه را در نظر بگيرد.) و او [= قاتل‌] نيز، به نيکي ديه را (به ولي مقتول) بپردازد؛ (و در آن، مسامحه نکند.) اين، تخفيف و رحمتي است از ناحيه پروردگار شما! و کسي که بعد از آن، تجاوز کند، عذاب دردناکي خواهد داشت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (186, N'و براي شما در قصاص، حيات و زندگي است، اي صاحبان خرد! شايد شما تقوا پيشه کنيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (187, N'بر شما نوشته شده: «هنگامي که يکي از شما را مرگ فرا رسد، اگر چيز خوبي [= مالي‌] از خود به جاي گذارده، براي پدر و مادر و نزديکان، بطور شايسته وصيت کند! اين حقي است بر پرهيزکاران!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (188, N'پس کساني که بعد از شنيدنش آن را تغيير دهند، گناه آن، تنها بر کساني است که آن (وصيت) را تغيير مي‌دهند؛ خداوند، شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (189, N'و کسي که از انحراف وصيت کننده (و تمايل يک‌جانبه او به بعض ورثه)، يا از گناه او (که مبادا وصيت به کار خلافي کند) بترسد، و ميان آنها را اصلاح دهد، گناهي بر او نيست؛ (و مشمول حکم تبديل وصيت نمي‌باشد.) خداوند، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (190, N'اي افرادي که ايمان آورده‌ايد! روزه بر شما نوشته شده، همان‌گونه که بر کساني که قبل از شما بودند نوشته شد؛ تا پرهيزکار شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (191, N'چند روز معدودي را (بايد روزه بداريد!) و هر کس از شما بيمار يا مسافر باشد تعدادي از روزهاي ديگر را (روزه بدارد) و بر کساني که روزه براي آنها طاقت‌فرساست؛ (همچون بيماران مزمن، و پيرمردان و پيرزنان،) لازم است کفاره بدهند: مسکيني را اطعام کنند؛ و کسي که کار خيري انجام دهد، براي او بهتر است؛ و روزه داشتن براي شما بهتر است اگر بدانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (192, N'(روزه، در چند روز معدود) ماه رمضان است؛ ماهي که قرآن، براي راهنمايي مردم ، و نشانه‌هاي هدايت، و فرق ميان حق و باطل، در آن نازل شده است. پس آن کس از شما که در ماه رمضان در حضر باشد، روزه بدارد! و آن کس که بيمار يا در سفر است، روزهاي ديگري را به جاي آن، روزه بگيرد! خداوند، راحتي شما را مي‌خواهد، نه زحمت شما را! هدف اين است که اين روزها را تکميل کنيد؛ و خدا را بر اينکه شما را هدايت کرده، بزرگ بشمريد؛ باشد که شکرگزاري کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (193, N'و هنگامي که بندگان من، از تو در باره من سؤال کنند، (بگو:) من نزديکم! دعاي دعا کننده را، به هنگامي که مرا مي‌خواند، پاسخ مي‌گويم! پس بايد دعوت مرا بپذيرند، و به من ايمان بياورند، تا راه يابند (و به مقصد برسند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (194, N'آميزش جنسي با همسرانتان، در شب روزهايي که روزه مي‌گيريد، حلال است. آنها لباس شما هستند؛ و شما لباس آنها (هر دو زينت هم و سبب حفظ يکديگريد). خداوند مي‌دانست که شما به خود خيانت مي‌کرديد؛ (و اين کار ممنوع را انجام مي‌داديد؛) پس توبه شما را پذيرفت و شما را بخشيد. اکنون با آنها آميزش کنيد، و آنچه را خدا براي شما مقرر داشته، طلب نماييد! و بخوريد و بياشاميد، تا رشته سپيد صبح، از رشته سياه (شب) براي شما آشکار گردد! سپس روزه را تا شب، تکميل کنيد! و در حالي که در مساجد به اعتکاف پرداخته‌ايد، با زنان آميزش نکنيد! اين، مرزهاي الهي است؛ پس به آن نزديک نشويد! خداوند، اين چنين آيات خود را براي مردم، روشن مي‌سازد، باشد که پرهيزکار گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (195, N'و اموال يکديگر را به باطل (و ناحق) در ميان خود نخوريد! و براي خوردن بخشي از اموال مردم به گناه، (قسمتي از) آن را (به عنوان رشوه) به قضات ندهيد، در حالي که مي‌دانيد (اين کار، گناه است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (196, N'در باره «هلالهاي ماه» از تو سؤال مي‌کنند؛ بگو: «آنها، بيان اوقات (و تقويم طبيعي) براي (نظام زندگي) مردم و (تعيين وقت) حج است». و (آن چنان که در جاهليت مرسوم بود که به هنگام حج، که جامه احرام مي‌پوشيدند، از در خانه وارد نمي‌شدند، و از نقب پشت خانه وارد مي‌شدند، نکنيد!) کار نيک، آن نيست که از پشت خانه‌ها وارد شويد؛ بلکه نيکي اين است که پرهيزگار باشيد! و از در خانه‌ها وارد شويد و تقوا پيشه کنيد، تا رستگار گرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (197, N'و در راه خدا، با کساني که با شما مي‌جنگند، نبرد کنيد! و از حد تجاوز نکنيد ، که خدا تعدي‌کنندگان را دوست نمي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (198, N'و آنها را [= بت پرستاني که از هيچ گونه جنايتي ابا ندارند] هر کجا يافتيد، به قتل برسانيد! و از آن جا که شما را بيرون ساختند [= مکه‌]، آنها را بيرون کنيد! و فتنه (و بت پرستي) از کشتار هم بدتر است! و با آنها، در نزد مسجد الحرام (در منطقه حرم)، جنگ نکنيد! مگر اينکه در آن جا با شما بجنگند. پس اگر (در آن جا) با شما پيکار کردند، آنها را به قتل برسانيد! چنين است جزاي کافران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (199, N'و اگر خودداري کردند، خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (200, N'و با آنها پيکار کنيد! تا فتنه (و بت پرستي، و سلب آزادي از مردم،) باقي نماند؛ و دين، مخصوص خدا گردد. پس اگر (از روش نادرست خود) دست برداشتند، (مزاحم آنها نشويد! زيرا) تعدي جز بر ستمکاران روا نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (201, N'ماه حرام، در برابر ماه حرام! (اگر دشمنان، احترام آن را شکستند، و در آن با شما جنگيدند، شما نيز حق داريد مقابله به مثل کنيد.) و تمام حرامها، (قابل) قصاص است. و (به طور کلي) هر کس به شما تجاوز کرد، همانند آن بر او تعدي کنيد! و از خدا بپرهيزيد (و زياده روي ننماييد)! و بدانيد خدا با پرهيزکاران است!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (202, N'و در راه خدا، انفاق کنيد! و (با ترک انفاق،) خود را به دست خود، به هلاکت نيفکنيد! و نيکي کنيد! که خداوند، نيکوکاران را دوست مي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (203, N'و حج و عمره را براي خدا به اتمام برسانيد! و اگر محصور شديد، (و مانعي مانند ترس از دشمن يا بيماري، اجازه نداد که پس از احرام‌بستن، وارد مکه شويد،) آنچه از قرباني فراهم شود (ذبح کنيد، و از احرام خارج شويد)! و سرهاي خود را نتراشيد، تا قرباني به محلش برسد (و در قربانگاه ذبح شود)! و اگر کسي از شما بيمار بود، و يا ناراحتي در سر داشت، (و ناچار بود سر خود را بتراشد،) بايد فديه و کفاره‌اي از قبيل روزه يا صدقه يا گوسفندي بدهد! و هنگامي که (از بيماري و دشمن) در امان بوديد، هر کس با ختم عمره، حج را آغاز کند، آنچه از قرباني براي او ميسر است (ذبح کند)! و هر که نيافت، سه روز در ايام حج، و هفت روز هنگامي که باز مي‌گرديد، روزه بدارد! اين، ده روز کامل است. (البته) اين براي کسي است که خانواده او، نزد مسجد الحرام نباشد [= اهل مکه و اطراف آن نباشد]. و از خدا بپرهيزيد! و بدانيد که او، سخت‌کيفر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (204, N'حج، در ماه‌هاي معيني است! و کساني که (با بستن احرام، و شروع به مناسک حج،) حج را بر خود فرض کرده‌اند، (بايد بدانند که) در حج، آميزش جنسي با زنان، و گناه و جدال نيست! و آنچه از کارهاي نيک انجام دهيد، خدا آن را مي‌داند. و زاد و توشه تهيه کنيد، که بهترين زاد و توشه، پرهيزکاري است! و از من بپرهيزيد اي خردمندان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (205, N'گناهي بر شما نيست که از فضل پروردگارتان (و از منافع اقتصادي در ايام حج) طلب کنيد (که يکي از منافع حج، پي ريزي يک اقتصاد صحيح است). و هنگامي که از «عرفات» کوچ کرديد، خدا را نزد «مشعر الحرام» ياد کنيد! او را ياد کنيد همان‌طور که شما را هدايت نمود و قطعا شما پيش از اين، از گمراهان بوديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (206, N'سپس از همان‌جا که مردم کوچ مي‌کنند، (به سوي سرزمين مني) کوچ کنيد! و از خداوند، آمرزش بطلبيد، که خدا آمرزنده مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (207, N'و هنگامي که مناسک (حج) خود را انجام داديد، خدا را ياد کنيد، همانند يادآوري از پدرانتان (آن‌گونه که رسم آن زمان بود) بلکه از آن هم بيشتر! (در اين مراسم ، مردم دو گروهند:) بعضي از مردم مي‌گويند: «خداوندا! به ما در دنيا، (*نيکي*) عطا کن!» ولي در آخرت، بهره‌اي ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (208, N'و بعضي مي‌گويند: «پروردگارا! به ما در دنيا (*نيکي*) عطا کن! و در آخرت نيز (*نيکي*) مرحمت فرما! و ما را از عذاب آتش نگاه دار!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (209, N'آنها از کار (و دعاي) خود، نصيب و بهره‌اي دارند؛ و خداوند، سريع الحساب است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (210, N'و خدا را در روزهاي معيني ياد کنيد! (روزهاي 11 و 12 و 13 ماه ذي حجه). و هر کس شتاب کند، (و ذکر خدا را) در دو روز انجام دهد، گناهي بر او نيست، و هر که تأخير کند، (و سه روز انجام دهد نيز) گناهي بر او نيست؛ براي کسي که تقوا پيشه کند. و از خدا بپرهيزيد! و بدانيد شما به سوي او محشور خواهيد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (211, N'و از مردم، کساني هستند که گفتار آنان، در زندگي دنيا مايه اعجاب تو مي‌شود؛ (در ظاهر، اظهار محبت شديد مي‌کنند) و خدا را بر آنچه در دل دارند گواه مي‌گيرند. (اين در حالي است که) آنان، سرسخت‌ترين دشمنانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (212, N'(نشانه آن، اين است که) هنگامي که روي برمي‌گردانند (و از نزد تو خارج مي‌شوند)، در راه فساد در زمين، کوشش مي‌کنند، و زراعتها و چهارپايان را نابود مي‌سازند؛ (با اينکه مي‌دانند) خدا فساد را دوست نمي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (213, N'و هنگامي که به آنها گفته شود: «از خدا بترسيد!» (لجاجت آنان بيشتر مي‌شود)، و لجاجت و تعصب، آنها را به گناه مي‌کشاند. آتش دوزخ براي آنان کافي است؛ و چه بد جايگاهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (214, N'بعضي از مردم (با ايمان و فداکار، همچون علي (ع) در «ليلة المبيت» به هنگام خفتن در جايگاه پيغمبر ص)، جان خود را به خاطر خشنودي خدا مي‌فروشند؛ و خداوند نسبت به بندگان مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (215, N'اي کساني که ايمان آورده‌ايد همگي در صلح و آشتي درآييد! و از گامهاي شيطان، پيروي نکنيد؛ که او دشمن آشکار شماست‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (216, N'و اگر بعد از اين همه نشانه‌هاي روشن، که براي شما آمده است، لغزش کرديد (و گمراه شديد)، بدانيد (از چنگال عدالت خدا، فرار نتوانيد کرد؛) که خداوند، توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (217, N'آيا (پيروان فرمان شيطان، پس از اين همه نشانه‌ها و برنامه‌هاي روشن) انتظار دارند که خداوند و فرشتگان، در سايه‌هائي از ابرها به سوي آنان بيايند (و دلايل تازه‌اي در اختيارشان بگذارند؟! با اينکه چنين چيزي محال است!) و همه چيز انجام شده، و همه کارها به سوي خدا بازمي‌گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (218, N'از بني اسرائيل بپرس: «چه اندازه نشانه‌هاي روشن به آنها داديم؟» (ولي آنان، نعمتها و امکانات مادي و معنوي را که خداوند در اختيارشان گذاشته بود، در راه غلط به کار گرفتند.) و کسي که نعمت خدا را، پس از آن که به سراغش آمد، تبديل کند (و در مسير خلاف به کار گيرد، گرفتار عذاب شديد الهي خواهد شد) که خداوند شديد العقاب است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (219, N'زندگي دنيا براي کافران زينت داده شده است، از اين‌رو افراد باايمان را (که گاهي دستشان تهي است)، مسخره مي‌کنند؛ در حالي که پرهيزگاران در قيامت، بالاتر از آنان هستند؛ (چراکه ارزشهاي حقيقي در آنجا آشکار مي‌گردد، و صورت عيني به خود مي‌گيرد؛) و خداوند، هر کس را بخواهد بدون حساب روزي مي‌دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (220, N'مردم (در آغاز) يک دسته بودند؛ (و تضادي در ميان آنها وجود نداشت. بتدريج جوامع و طبقات پديد آمد و اختلافات و تضادهايي در ميان آنها پيدا شد، در اين حال) خداوند، پيامبران را برانگيخت؛ تا مردم را بشارت و بيم دهند و کتاب آسماني، که به سوي حق دعوت مي‌کرد، با آنها نازل نمود؛ تا در ميان مردم، در آنچه اختلاف داشتند، داوري کند. (افراد باايمان، در آن اختلاف نکردند؛) تنها (گروهي از) کساني که کتاب را دريافت داشته بودند، و نشانه‌هاي روشن به آنها رسيده بود، به خاطر انحراف از حق و ستمگري، در آن اختلاف کردند. خداوند ، آنهايي را که ايمان آورده بودند، به حقيقت آنچه مورد اختلاف بود، به فرمان خودش، رهبري نمود. (اما افراد بي‌ايمان، همچنان در گمراهي و اختلاف، باقي ماندند.) و خدا، هر کس را بخواهد، به راه راست هدايت مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (221, N'آيا گمان کرديد داخل بهشت مي‌شويد، بي‌آنکه حوادثي همچون حوادث گذشتگان به شما برسد؟! همانان که گرفتاريها و ناراحتيها به آنها رسيد، و آن چنان ناراحت شدند که پيامبر و افرادي که ايمان آورده بودند گفتند: «پس ياري خدا کي خواهد آمد؟!» (در اين هنگام، تقاضاي ياري از او کردند، و به آنها گفته شد:) آگاه باشيد، ياري خدا نزديک است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (222, N'از تو سؤال مي‌کنند چه چيز انفاق کنند؟ بگو: «هر خير و نيکي (و سرمايه سودمند مادي و معنوي) که انفاق مي‌کنيد، بايد براي پدر و مادر و نزديکان و يتيمان و مستمندان و درماندگان در راه باشد.» و هر کار خيري که انجام دهيد، خداوند از آن آگاه است.( لازم نيست تظاهر کنيد، او مي‌داند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (223, N'جهاد در راه خدا، بر شما مقرر شد؛ در حالي که برايتان ناخوشايند است. چه بسا چيزي را خوش نداشته باشيد، حال آن که خير شما در آن است. و يا چيزي را دوست داشته باشيد، حال آنکه شر شما در آن است. و خدا مي‌داند، و شما نمي‌دانيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (224, N'از تو، در باره جنگ کردن در ماه حرام، سؤال مي‌کنند؛ بگو: «جنگ در آن، (گناهي) بزرگ است؛ ولي جلوگيري از راه خدا (و گرايش مردم به آيين حق) و کفر ورزيدن نسبت به او و هتک احترام مسجد الحرام، و اخراج ساکنان آن، نزد خداوند مهمتر از آن است؛ و ايجاد فتنه، (و محيط نامساعد، که مردم را به کفر، تشويق و از ايمان بازمي‌دارد) حتي از قتل بالاتر است. و مشرکان، پيوسته با شما مي‌جنگند ، تا اگر بتوانند شما را از آيينتان برگردانند؛ ولي کسي که از آيينش برگردد ، و در حال کفر بميرد، تمام اعمال نيک (گذشته) او، در دنيا و آخرت، برباد مي‌رود؛ و آنان اهل دوزخند؛ و هميشه در آن خواهند بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (225, N'کساني که ايمان آورده و کساني که هجرت کرده و در راه خدا جهاد نموده‌اند، آنها اميد به رحمت پروردگار دارند و خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (226, N'در باره شراب و قمار از تو سؤال مي‌کنند، بگو: «در آنها گناه و زيان بزرگي است؛ و منافعي (از نظر مادي) براي مردم در بردارد؛ (ولي) گناه آنها از نفعشان بيشتر است. و از تو مي‌پرسند چه چيز انفاق کنند؟ بگو: از مازاد نيازمندي خود.» اينچنين خداوند آيات را براي شما روشن مي‌سازد، شايد انديشه کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (227, N'(تا انديشه کنيد) درباره دنيا و آخرت! و از تو در باره يتيمان سؤال مي‌کنند، بگو: «اصلاح کار آنان بهتر است. و اگر زندگي خود را با زندگي آنان بياميزيد، (مانعي ندارد؛) آنها برادر (ديني) شما هستند.» (و همچون يک برادر با آنها رفتار کنيد!) خداوند، مفسدان را از مصلحان، بازمي‌شناسد. و اگر خدا بخواهد، شما را به زحمت مي‌اندازد؛ (و دستور مي‌دهد در عين سرپرستي يتيمان، زندگي و اموال آنها را بکلي از اموال خود، جدا سازيد؛ ولي خداوند چنين نمي‌کند؛) زيرا او توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (228, N'و با زنان مشرک و بت‌پرست، تا ايمان نياورده‌اند، ازدواج نکنيد! (اگر چه جز به ازدواج با کنيزان، دسترسي نداشته باشيد؛ زيرا) کنيز باايمان، از زن آزاد بت‌پرست، بهتر است؛ هر چند (زيبايي، يا ثروت، يا موقعيت او) شما را به شگفتي آورد. و زنان خود را به ازدواج مردان بت‌پرست، تا ايمان نياورده‌اند، در نياوريد! (اگر چه ناچار شويد آنها را به همسري غلامان باايمان درآوريد؛ زيرا) يک غلام باايمان، از يک مرد آزاد بت‌پرست، بهتر است؛ هر چند (مال و موقعيت و زيبايي او،) شما را به شگفتي آورد. آنها دعوت به سوي آتش مي‌کنند؛ و خدا دعوت به بهشت و آمرزش به فرمان خود مي‌نمايد، و آيات خويش را براي مردم روشن مي‌سازد؛ شايد متذکر شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (229, N'و از تو، در باره خون حيض سؤال مي‌کنند، بگو: «چيز زيانبار و آلوده‌اي است؛ از اين‌رو در حالت قاعدگي، از آنان کناره‌گيري کنيد! و با آنها نزديکي ننمايد، تا پاک شوند! و هنگامي که پاک شدند، از طريقي که خدا به شما فرمان داده، با آنها آميزش کنيد! خداوند، توبه‌کنندگان را دوست دارد، و پاکان را (نيز) دوست دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (230, N'زنان شما، محل بذرافشاني شما هستند؛ پس هر زمان که بخواهيد، مي‌توانيد با آنها آميزش کنيد. و (سعي نمائيد از اين فرصت بهره گرفته، با پرورش فرزندان صالح) اثر نيکي براي خود، از پيش بفرستيد! و از خدا بپرهيزيد و بدانيد او را ملاقات خواهيد کرد و به مؤمنان، بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (231, N'خدا را در معرض سوگندهاي خود قرار ندهيد! و براي اينکه نيکي کنيد، و تقوا پيشه سازيد، و در ميان مردم اصلاح کنيد (سوگند ياد ننماييد)! و خداوند شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (232, N'خداوند شما را به خاطر سوگندهايي که بدون توجه ياد مي‌کنيد، مؤاخذه نخواهد کرد، اما به آنچه دلهاي شما کسب کرده، (و سوگندهايي که از روي اراده و اختيار، ياد مي‌کنيد،) مؤاخذه مي‌کند. و خداوند، آمرزنده و بردبار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (233, N'کساني که زنان خود را «ايلاء» مي‌نمايند [= سوگند ياد مي‌کنند که با آنها ، آميزش‌جنسي ننمايند،] حق دارند چهار ماه انتظار بکشند. (و در ضمن اين چهار ماه، وضع خود را با همسر خويش، از نظر ادامه زندگي يا طلاق، روشن سازند.) اگر (در اين فرصت،) بازگشت کنند، (چيزي بر آنها نيست؛ زيرا) خداوند،آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (234, N'و اگر تصميم به جدايي گرفتند، (آن هم با شرايطش مانعي ندارد؛) خداوند شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (235, N'زنان مطلقه، بايد به مدت سه مرتبه عادت ماهانه ديدن (و پاک شدن) انتظار بکشند! [= عده نگه دارند] و اگر به خدا و روز رستاخيز، ايمان دارند، براي آنها حلال نيست که آنچه را خدا در رحمهايشان آفريده، کتمان کنند. و همسرانشان، براي بازگرداندن آنها (و از سرگرفتن زندگي زناشويي) در اين مدت، (از ديگران) سزاوارترند؛ در صورتي که (براستي) خواهان اصلاح باشند. و براي آنان، همانند وظايفي که بر دوش آنهاست، حقوق شايسته‌اي قرار داده شده؛ و مردان بر آنان برتري دارند؛ و خداوند توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (236, N'طلاق، (طلاقي که رجوع و بازگشت دارد،) دو مرتبه است؛ (و در هر مرتبه،) بايد به طور شايسته همسر خود را نگاهداري کند (و آشتي نمايد)، يا با نيکي او را رها سازد (و از او جدا شود). و براي شما حلال نيست که چيزي از آنچه به آنها داده‌ايد، پس بگيريد؛ مگر اينکه دو همسر، بترسند که حدود الهي را برپا ندارند. اگر بترسيد که حدود الهي را رعايت نکنند، مانعي براي آنها نيست که زن، فديه و عوضي بپردازد (و طلاق بگيرد). اينها حدود و مرزهاي الهي است؛ از آن، تجاوز نکنيد! و هر کس از آن تجاوز کند، ستمگر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (237, N'اگر (بعد از دو طلاق و رجوع، بار ديگر) او را طلاق داد، از آن به بعد، زن بر او حلال نخواهد بود؛ مگر اينکه همسر ديگري انتخاب کند (و با او، آميزش‌جنسي نمايد. در اين صورت،) اگر (همسر دوم) او را طلاق گفت، گناهي ندارد که بازگشت کنند؛ (و با همسر اول، دوباره ازدواج نمايد؛) در صورتي که اميد داشته باشند که حدود الهي را محترم ميشمرند. اينها حدود الهي است که (خدا) آن را براي گروهي که آگاهند، بيان مي‌نمايد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (238, N'و هنگامي که زنان را طلاق داديد، و به آخرين روزهاي «عده» رسيدند، يا به طرز صحيحي آنها را نگاه داريد (و آشتي کنيد)، و يا به طرز پسنديده‌اي آنها را رها سازيد! و هيچ‌گاه به خاطر زيان رساندن و تعدي کردن، آنها را نگاه نداريد! و کسي که چنين کند، به خويشتن ستم کرده است. (و با اين اعمال، و سوء استفاده از قوانين الهي،) آيات خدا را به استهزا نگيريد! و به ياد بياوريد نعمت خدا را بر خود، و کتاب آسماني و علم و دانشي که بر شما نازل کرده، و شما را با آن، پند مي‌دهد! و از خدا بپرهيزيد! و بدانيد خداوند از هر چيزي آگاه است (و از نيات کساني که از قوانين او، سوء استفاده مي‌کنند، با خبر است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (239, N'و هنگامي که زنان را طلاق داديد و عده خود را به پايان رساندند، مانع آنها نشويد که با همسران (سابق) خويش، ازدواج کنند! اگر در ميان آنان، به طرز پسنديده‌اي تراضي برقرار گردد. اين دستوري است که تنها افرادي از شما، که ايمان به خدا و روز قيامت دارند، از آن، پند مي‌گيرند (و به آن، عمل مي‌کنند). اين (دستور)، براي رشد (خانواده‌هاي) شما مؤثرتر، و براي شستن آلودگيها مفيدتر است؛ و خدا مي‌داند و شما نمي‌دانيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (240, N'مادران، فرزندان خود را دو سال تمام، شير مي‌دهند. (اين) براي کسي است که بخواهد دوران شيرخوارگي را تکميل کند. و بر آن کس که فرزند براي او متولد شده [= پدر]، لازم است خوراک و پوشاک مادر را به طور شايسته (در مدت شير دادن بپردازد؛ حتي اگر طلاق گرفته باشد.) هيچ کس موظف به بيش از مقدار توانايي خود نيست! نه مادر (به خاطر اختلاف با پدر) حق ضرر زدن به کودک را دارد، و نه پدر. و بر وارث او نيز لازم است اين کار را انجام دهد [= هزينه مادر را در دوران شيرخوارگي تأمين نمايد]. و اگر آن دو، با رضايت يکديگر و مشورت، بخواهند کودک را (زودتر) از شير بازگيرند، گناهي بر آنها نيست. و اگر (با عدم توانايي، يا عدم موافقت مادر) خواستيد دايه‌اي براي فرزندان خود بگيريد، گناهي بر شما نيست؛ به شرط اينکه حق گذشته مادر را به طور شايسته بپردازيد. و از (مخالفت فرمان) خدا بپرهيزيد! و بدانيد خدا، به آنچه انجام مي‌دهيد، بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (241, N'و کساني که از شما مي‌ميرند و همسراني باقي مي‌گذارند، بايد چهار ماه و ده روز، انتظار بکشند (و عده نگه دارند)! و هنگامي که به آخر مدتشان رسيدند، گناهي بر شما نيست که هر چه مي‌خواهند، در باره خودشان به طور شايسته انجام دهند (و با مرد دلخواه خود، ازدواج کنند). و خدا به آنچه عمل مي‌کنيد، آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (242, N'و گناهي بر شما نيست که به طور کنايه، (از زناني که همسرانشان مرده‌اند) خواستگاري کنيد، و يا در دل تصميم بر اين کار بگيريد (بدون اينکه آن را اظهار کنيد). خداوند مي‌دانست شما به ياد آنها خواهيد افتاد؛ (و با خواسته طبيعي شما به شکل معقول، مخالف نيست؛) ولي پنهاني با آنها قرار زناشويي نگذاريد، مگر اينکه به طرز پسنديده‌اي (به طور کنايه) اظهار کنيد! (ولي در هر حال،) اقدام به ازدواج ننماييد، تا عده آنها سرآيد! و بدانيد خداوند آنچه را در دل داريد، مي‌داند! از مخالفت او بپرهيزيد! و بدانيد خداوند، آمرزنده و بردبار است (و در مجازات بندگان، عجله نمي‌کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (243, N'اگر زنان را قبل از آميزش‌جنسي يا تعيين مهر، (به عللي) طلاق دهيد، گناهي بر شما نيست. (و در اين موقع،) آنها را (با هديه‌اي مناسب،) بهره‌مند سازيد! آن کس که توانايي دارد، به اندازه تواناييش، و آن کس که تنگدست است، به اندازه خودش، هديه‌اي شايسته (که مناسب حال دهنده و گيرنده باشد) بدهد! و اين بر نيکوکاران، الزامي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (244, N'و اگر آنان را، پيش از آن که با آنها تماس بگيريد و (آميزش‌جنسي کنيد) طلاق دهيد، در حالي که مهري براي آنها تعيين کرده‌ايد، (لازم است) نصف آنچه را تعيين کرده‌ايد (به آنها بدهيد) مگر اينکه آنها (حق خود را) ببخشند؛ يا (در صورتي که صغير و سفيه باشند، ولي آنها، يعني) آن کس که گره ازدواج به دست اوست، آن را ببخشد. و گذشت کردن شما (و بخشيدن تمام مهر به آنها) به پرهيزکاري نزديکتر است، و گذشت و نيکوکاري را در ميان خود فراموش نکنيد، که خداوند به آنچه انجام مي‌دهيد، بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (245, N'در انجام همه نمازها، (به خصوص) نماز وسطي [= نماز ظهر] کوشا باشيد! و از روي خضوع و اطاعت، براي خدا بپاخيزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (246, N'و اگر (به خاطر جنگ، يا خطر ديگري) بترسيد، (نماز را) در حال پياده يا سواره انجام دهيد! اما هنگامي که امنيت خود را بازيافتيد، خدا را ياد کنيد! [= نماز را به صورت معمولي بخوانيد!] همان‌گونه که خداوند، چيزهايي را که نمي‌دانستيد، به شما تعليم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (247, N'و کساني که از شما در آستانه مرگ قرارمي‌گيرند و همسراني از خود به‌جا مي‌گذارند، بايد براي همسران خود وصيت کنند که تا يک سال، آنها را (با پرداختن هزينه زندگي) بهره‌مند سازند؛ به شرط اينکه آنها (از خانه شوهر) بيرون نروند (و اقدام به ازدواج مجدد نکنند). و اگر بيرون روند، (حقي در هزينه ندارند؛ ولي) گناهي بر شما نيست نسبت به آنچه در باره خود، به طور شايسته انجام مي‌دهند. و خداوند، توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (248, N'و براي زنان مطلقه، هديه مناسبي لازم است (که از طرف شوهر، پرداخت گردد). اين، حقي است بر مردان پرهيزکار.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (249, N'اين چنين، خداوند آيات خود را براي شما شرح مي‌دهد؛ شايد انديشه کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (250, N'آيا نديدي جمعيتي را که از ترس مرگ، از خانه‌هاي خود فرار کردند؟ و آنان، هزارها نفر بودند (که به بهانه بيماري طاعون، از شرکت در ميدان جهاد خودداري نمودند). خداوند به آنها گفت: بميريد! (و به همان بيماري، که آن را بهانه قرار داده بودند، مردند.) سپس خدا آنها را زنده کرد؛ (و ماجراي زندگي آنها را درس عبرتي براي آيندگان قرار داد.) خداوند نسبت به بندگان خود احسان مي‌کند؛ ولي بيشتر مردم، شکر (او را) بجا نمي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (251, N'و در راه خدا، پيکار کنيد! و بدانيد خداوند، شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (252, N'کيست که به خدا «قرض الحسنه‌اي» دهد، (و از اموالي که خدا به او بخشيده، انفاق کند،) تا آن را براي او، چندين برابر کند؟ و خداوند است (که روزي بندگان را) محدود يا گسترده مي‌سازد؛ (و انفاق، هرگز باعث کمبود روزي آنها نمي‌شود). و به سوي او باز مي‌گرديد (و پاداش خود را خواهيد گرفت).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (253, N'آيا مشاهده نکردي جمعي از بني اسرائيل را بعد از موسي، که به پيامبر خود گفتند: «زمامدار (و فرماندهي) براي ما انتخاب کن! تا (زير فرمان او) در راه خدا پيکار کنيم. پيامبر آنها گفت: «شايد اگر دستور پيکار به شما داده شود، (سرپيچي کنيد، و) در راه خدا، جهاد و پيکار نکنيد!» گفتند: «چگونه ممکن است در راه خدا پيکار نکنيم، در حالي که از خانه‌ها و فرزندانمان رانده شده‌ايم، (و شهرهاي ما به وسيله دشمن اشغال، و فرزندان ما اسير شده‌اند)؟!» اما هنگامي که دستور پيکار به آنها داده شد، جز عده کمي از آنان، همه سرپيچي کردند. و خداوند از ستمکاران، آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (254, N'و پيامبرشان به آنها گفت: «خداوند (*طالوت*) را براي زمامداري شما مبعوث (و انتخاب) کرده است.» گفتند: «چگونه او بر ما حکومت کند، با اينکه ما از او شايسته‌تريم، و او ثروت زيادي ندارد؟!» گفت: «خدا او را بر شما برگزيده، و او را در علم و (قدرت) جسم، وسعت بخشيده است. خداوند، ملکش را به هر کس بخواهد، مي‌بخشد؛ و احسان خداوند، وسيع است؛ و (از لياقت افراد براي منصب‌ها) آگاه است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (255, N'و پيامبرشان به آنها گفت: «نشانه حکومت او، اين است که (*صندوق عهد*) به سوي شما خواهد آمد. (همان صندوقي که) در آن، آرامشي از پروردگار شما، و يادگارهاي خاندان موسي و هارون قرار دارد؛ در حالي که فرشتگان، آن را حمل مي‌کنند. در اين موضوع، نشانه‌اي (روشن) براي شماست؛ اگر ايمان داشته باشيد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (256, N'و هنگامي که طالوت (به فرماندهي لشکر بني اسرائيل منصوب شد، و) سپاهيان را با خود بيرون برد، به آنها گفت: «خداوند، شما را به وسيله يک نهر آب، آزمايش مي‌کند؛ آنها (که به هنگام تشنگي،) از آن بنوشند، از من نيستند؛ و آنها که جز يک پيمانه با دست خود، بيشتر از آن نخورند، از من هستند» جز عده کمي، همگي از آن آب نوشيدند. سپس هنگامي که او، و افرادي که با او ايمان آورده بودند، (و از بوته آزمايش، سالم به‌در آمدند،) از آن نهر گذشتند، (از کمي نفرات خود، ناراحت شدند؛ و عده‌اي) گفتند: «امروز، ما توانايي مقابله با (*جالوت*) و سپاهيان او را نداريم.» اما آنها که مي‌دانستند خدا را ملاقات خواهند کرد (و به روز رستاخيز، ايمان داشتند) گفتند: «چه بسيار گروه‌هاي کوچکي که به فرمان خدا، بر گروه‌هاي عظيمي پيروز شدند!» و خداوند، با صابران و استقامت‌کنندگان) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (257, N'و هنگامي که در برابر (*جالوت*) و سپاهيان او قرارگرفتند گفتند: «پروردگارا! پيمانه شکيبايي و استقامت را بر ما بريز! و قدمهاي ما را ثابت بدار! و ما را بر جمعيت کافران، پيروز بگردان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (258, N'[152]سپس به فرمان خدا، آنها سپاه دشمن را به هزيمت واداشتند. و «داوود» (نوجوان نيرومند و شجاعي که در لشکر «طالوت» بود)، «جالوت» را کشت؛ و خداوند، حکومت و دانش را به او بخشيد؛ و از آنچه مي‌خواست به او تعليم داد. و اگر خداوند، بعضي از مردم را به وسيله بعضي ديگر دفع نمي‌کرد، زمين را فساد فرامي‌گرفت، ولي خداوند نسبت به جهانيان، لطف و احسان دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (259, N'اينها، آيات خداست که به حق، بر تو مي‌خوانيم؛ و تو از رسولان (ما) هستي.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (260, N'بعضي از آن رسولان را بر بعضي ديگر برتري داديم؛ برخي از آنها، خدا با او سخن مي‌گفت؛ و بعضي را درجاتي برتر داد؛ و به عيسي بن مريم، نشانه‌هاي روشن داديم؛ و او را با «روح القدس» تأييد نموديم؛ (ولي فضيلت و مقام آن پيامبران، مانع اختلاف امتها نشد.) و اگر خدا مي‌خواست، کساني که بعد از آنها بودند، پس از آن همه نشانه‌هاي روشن که براي آنها آمد، جنگ و ستيز نمي‌کردند؛ (اما خدا مردم را مجبور نساخته؛ و آنها را در پيمودن راه سعادت، آزاد گذارده است؛) ولي اين امتها بودند که با هم اختلاف کردند؛ بعضي ايمان آوردند و بعضي کافر شدند؛ (و جنگ و خونريزي بروز کرد. و باز) اگر خدا مي‌خواست، با هم پيکار نمي‌کردند؛ ولي خداوند، آنچه را مي‌خواهد، (از روي حکمت) انجام مي‌دهد (و هيچ‌کس را به قبول چيزي مجبور نمي‌کند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (261, N'اي کساني که ايمان آورده‌ايد! از آنچه به شما روزي داده‌ايم، انفاق کنيد! پيش از آنکه روزي فرا رسد که در آن، نه خريد و فروش است (تا بتوانيد سعادت و نجات از کيفر را براي خود خريداري کنيد)، و نه دوستي (و رفاقتهاي مادي سودي دارد)، و نه شفاعت؛ (زيرا شما شايسته شفاعت نخواهيد بود.) و کافران، خود ستمگرند؛ (هم به خودشان ستم مي‌کنند، هم به ديگران).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (262, N'هيچ معبودي نيست جز خداوند يگانه زنده، که قائم به ذات خويش است، و موجودات ديگر، قائم به او هستند؛ هيچگاه خواب سبک و سنگيني او را فرانمي‌گيرد؛ (و لحظه‌اي از تدبير جهان هستي، غافل نمي‌ماند؛) آنچه در آسمانها و آنچه در زمين است، از آن اوست؛ کيست که در نزد او، جز به فرمان او شفاعت کند؟! (بنابراين، شفاعت شفاعت‌کنندگان، براي آنها که شايسته شفاعتند، از مالکيت مطلقه او نمي‌کاهد.) آنچه را در پيش روي آنها [= بندگان‌] و پشت سرشان است مي‌داند؛ (و گذشته و آينده، در پيشگاه علم او، يکسان است.) و کسي از علم او آگاه نمي‌گردد؛ جز به مقداري که او بخواهد. (اوست که به همه چيز آگاه است؛ و علم و دانش محدود ديگران، پرتوي از علم بي‌پايان و نامحدود اوست.) تخت (حکومت) او، آسمانها و زمين را دربرگرفته؛ و نگاهداري آن دو [= آسمان و زمين ]، او را خسته نميکند. بلندي مقام و عظمت، مخصوص اوست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (263, N'در قبول دين، اکراهي نيست. (زيرا) راه درست از راه انحرافي، روشن شده است. بنابر اين، کسي که به طاغوت [= بت و شيطان، و هر موجود طغيانگر] کافر شود و به خدا ايمان آورد، به دستگيره محکمي چنگ زده است، که گسستن براي آن نيست. و خداوند، شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (264, N'خداوند، ولي و سرپرست کساني است که ايمان آورده‌اند؛ آنها را از ظلمتها، به سوي نور بيرون مي‌برد. (اما) کساني که کافر شدند، اولياي آنها طاغوتها هستند؛ که آنها را از نور، به سوي ظلمتها بيرون مي‌برند؛ آنها اهل آتشند و هميشه در آن خواهند ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (265, N'آيا نديدي (و آگاهي نداري از) کسي [= نمرود] که با ابراهيم در باره پروردگارش محاجه و گفتگو کرد؟ زيرا خداوند به او حکومت داده بود؛ (و بر اثر کمي ظرفيت، از باده غرور سرمست شده بود؛) هنگامي که ابراهيم گفت: «خداي من آن کسي است که زنده مي‌کند و مي‌ميراند.» او گفت: «من نيز زنده مي‌کنم و مي‌ميرانم!» (و براي اثبات اين کار و مشتبه‌ساختن بر مردم دستور داد دو زنداني را حاضر کردند، فرمان آزادي يکي و قتل ديگري را داد) ابراهيم گفت: «خداوند، خورشيد را از افق مشرق مي‌آورد؛ (اگر راست مي‌گويي که حاکم بر جهان هستي تويي،) خورشيد را از مغرب بياور!» (در اينجا) آن مرد کافر، مبهوت و وامانده شد. و خداوند، قوم ستمگر را هدايت نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (266, N'يا همانند کسي که از کنار يک آبادي (ويران شده) عبور کرد، در حالي که ديوارهاي آن، به روي سقفها فرو ريخته بود، (و اجساد و استخوانهاي اهل آن، در هر سو پراکنده بود؛ او با خود) گفت: «چگونه خدا اينها را پس از مرگ، زنده مي‌کند؟!» (در اين هنگام،) خدا او را يکصد سال ميراند؛ سپس زنده کرد؛ و به او گفت: «چه‌قدر درنگ کردي؟» گفت: «يک روز؛ يا بخشي از يک روز.» فرمود: «نه، بلکه يکصد سال درنگ کردي! نگاه کن به غذا و نوشيدني خود (که همراه داشتي، با گذشت سالها) هيچ‌گونه تغيير نيافته است! (خدايي که يک چنين مواد فاسدشدني را در طول اين مدت، حفظ کرده، بر همه چيز قادر است!) ولي به الاغ خود نگاه کن (که چگونه از هم متلاشي شده! اين زنده شدن تو پس از مرگ، هم براي اطمينان خاطر توست، و هم) براي اينکه تو را نشانه‌اي براي مردم (در مورد معاد) قرار دهيم. (اکنون) به استخوانها(ي مرکب سواري خود نگاه کن که چگونه آنها را برداشته، به هم پيوند مي‌دهيم، و گوشت بر آن مي‌پوشانيم!» هنگامي که (اين حقايق) بر او آشکار شد، گفت: «مي‌دانم خدا بر هر کاري توانا است».')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (267, N'و (به خاطر بياور) هنگامي را که ابراهيم گفت: «خدايا! به من نشان بده چگونه مردگان را زنده مي‌کني؟» فرمود: «مگر ايمان نياورده‌اي؟!» عرض کرد: «آري، ولي مي‌خواهم قلبم آرامش يابد.» فرمود: «در اين صورت، چهار نوع از مرغان را انتخاب کن! و آنها را (پس از ذبح کردن،) قطعه قطعه کن (و در هم بياميز)! سپس بر هر کوهي، قسمتي از آن را قرار بده، بعد آنها را بخوان، به سرعت به سوي تو مي‌آيند! و بدان خداوند قادر و حکيم است؛ (هم از ذرات بدن مردگان آگاه است، و هم توانايي بر جمع آنها دارد)».')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (268, N'کساني که اموال خود را در راه خدا انفاق مي‌کنند، همانند بذري هستند که هفت خوشه بروياند؛ که در هر خوشه، يکصد دانه باشد؛ و خداوند آن را براي هر کس بخواهد (و شايستگي داشته باشد)، دو يا چند برابر مي‌کند؛ و خدا (از نظر قدرت و رحمت،) وسيع، و (به همه چيز) داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (269, N'کساني که اموال خود را در راه خدا انفاق مي‌کنند، سپس به دنبال انفاقي که کرده‌اند، منت نمي‌گذارند و آزاري نمي‌رسانند، پاداش آنها نزد پروردگارشان (محفوظ) است؛ و نه ترسي دارند، و نه غمگين مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (270, N'گفتار پسنديده (در برابر نيازمندان)، و عفو (و گذشت از خشونتهاي آنها)، از بخششي که آزاري به دنبال آن باشد، بهتر است؛ و خداوند، بي‌نياز و بردبار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (271, N'اي کساني که ايمان آورده‌ايد! بخششهاي خود را با منت و آزار، باطل نسازيد! همانند کسي که مال خود را براي نشان دادن به مردم، انفاق مي‌کند؛ و به خدا و روز رستاخيز، ايمان نمي‌آورد؛ (کار او) همچون قطعه سنگي است که بر آن، (قشر نازکي از) خاک باشد؛ (و بذرهايي در آن افشانده شود؛) و رگبار باران به آن برسد، (و همه خاکها و بذرها را بشويد،) و آن را صاف (و خالي از خاک و بذر) رها کند. آنها از کاري که انجام داده‌اند، چيزي به دست نمي‌آورند؛ و خداوند، جمعيت کافران را هدايت نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (272, N'و (کار) کساني که اموال خود را براي خشنودي خدا، و تثبيت (ملکات انساني در) روح خود، انفاق مي‌کنند، همچون باغي است که در نقطه بلندي باشد، و بارانهاي درشت به آن برسد، (و از هواي آزاد و نور آفتاب، به حد کافي بهره گيرد،) و ميوه خود را دو چندان دهد (که هميشه شاداب و با طراوت است.) و خداوند به آنچه انجام مي‌دهيد، بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (273, N'آيا کسي از شما دوست دارد که باغي از درختان خرما و انگور داشته باشد که از زير درختان آن، نهرها بگذرد، و براي او در آن (باغ)، از هر گونه ميوه‌اي وجود داشته باشد، در حالي که به سن پيري رسيده و فرزنداني (کوچک و) ضعيف دارد؛ (در اين هنگام،) گردبادي (کوبنده)، که در آن آتش (سوزاني) است، به آن برخورد کند و شعله‌ور گردد و بسوزد؟! (همين‌طور است حال کساني که انفاقهاي خود را، با ريا و منت و آزار، باطل مي‌کنند.) اين چنين خداوند آيات خود را براي شما آشکار مي‌سازد؛ شايد بينديشيد (و با انديشه، راه حق را بيابيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (274, N'اي کساني که ايمان آورده‌ايد! از قسمتهاي پاکيزه اموالي که (از طريق تجارت) به دست آورده‌ايد، و از آنچه از زمين براي شما خارج ساخته‌ايم (از منابع و معادن و درختان و گياهان)، انفاق کنيد! و براي انفاق، به سراغ قسمتهاي ناپاک نرويد در حالي که خود شما، (به هنگام پذيرش اموال،) حاضر نيستيد آنها را بپذيريد؛ مگر از روي اغماض و کراهت! و بدانيد خداوند، بي‌نياز و شايسته ستايش است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (275, N'شيطان، شما را (به هنگام انفاق،) وعده فقر و تهيدستي مي‌دهد؛ و به فحشا (و زشتيها) امر مي‌کند؛ ولي خداوند وعده «آمرزش» و «فزوني» به شما مي‌دهد؛ و خداوند، قدرتش وسيع، و (به هر چيز) داناست. (به همين دليل، به وعده‌هاي خود، وفا مي‌کند.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (276, N'(خدا) دانش و حکمت را به هر کس بخواهد (و شايسته بداند) مي‌دهد؛ و به هر کس دانش داده شود، خير فراواني داده شده است. و جز خردمندان، (اين حقايق را درک نمي‌کنند، و) متذکر نمي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (277, N'و هر چيز را که انفاق مي‌کنيد، يا (اموالي را که) نذر کرده‌ايد (در راه خدا انفاق کنيد)، خداوند آنها را مي‌داند. و ستمگران ياوري ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (278, N'اگر انفاقها را آشکار کنيد، خوب است! و اگر آنها را مخفي ساخته و به نيازمندان بدهيد، براي شما بهتر است! و قسمتي از گناهان شما را مي‌پوشاند؛ (و در پرتو بخشش در راه خدا، بخشوده خواهيد شد.) و خداوند به آنچه انجام مي‌دهيد، آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (279, N'هدايت آنها (به‌طور اجبار،) بر تو نيست؛ (بنابر اين، ترک انفاق به غير مسلمانان، براي اجبار به اسلام، صحيح نيست؛) ولي خداوند، هر که را بخواهد (و شايسته بداند)، هدايت مي‌کند. و آنچه را از خوبيها و اموال انفاق مي‌کنيد، براي خودتان است؛ (ولي) جز براي رضاي خدا، انفاق نکنيد! و آنچه از خوبيها انفاق مي‌کنيد، (پاداش آن) به طور کامل به شما داده مي‌شود؛ و به شما ستم نخواهد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (280, N'(انفاق شما، مخصوصا بايد) براي نيازمنداني باشد که در راه خدا، در تنگنا قرار گرفته‌اند؛ (و توجه به آيين خدا، آنها را از وطنهاي خويش آواره ساخته؛ و شرکت در ميدان جهاد، به آنها اجازه نمي‌دهد تا براي تأمين هزينه زندگي، دست به کسب و تجارتي بزنند؛) نمي‌توانند مسافرتي کنند (و سرمايه‌اي به دست آورند؛) و از شدت خويشتن‌داري، افراد ناآگاه آنها را بي‌نياز مي‌پندارند؛ اما آنها را از چهره‌هايشان مي‌شناسي؛ و هرگز با اصرار چيزي از مردم نمي‌خواهند. (اين است مشخصات آنها!) و هر چيز خوبي در راه خدا انفاق کنيد، خداوند از آن آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (281, N'آنها که اموال خود را، شب و روز، پنهان و آشکار، انفاق مي‌کنند، مزدشان نزد پروردگارشان است؛ نه ترسي بر آنهاست، و نه غمگين مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (282, N'کساني که ربا مي‌خورند، (در قيامت) برنمي‌خيزند مگر مانند کسي که بر اثر تماس شيطان، ديوانه شده (و نمي‌تواند تعادل خود را حفظ کند؛ گاهي زمين مي‌خورد، گاهي بپا مي‌خيزد). اين، به خاطر آن است که گفتند: «داد و ستد هم مانند ربا است (و تفاوتي ميان آن دو نيست.)» در حالي که خدا بيع را حلال کرده، و ربا را حرام! (زيرا فرق ميان اين دو، بسيار است.) و اگر کسي اندرز الهي به او رسد، و (از رباخواري) خودداري کند، سودهايي که در سابق [= قبل از نزول حکم تحريم‌] به دست آورده، مال اوست؛ (و اين حکم، گذشته را شامل نمي‌گردد؛) و کار او به خدا واگذار مي‌شود؛ (و گذشته او را خواهد بخشيد.) اما کساني که بازگردند (و بار ديگر مرتکب اين گناه شوند)، اهل آتشند؛ و هميشه در آن مي‌مانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (283, N'خداوند، ربا را نابود مي‌کند؛ و صدقات را افزايش مي‌دهد! و خداوند، هيچ انسان ناسپاس گنهکاري را دوست نمي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (284, N'کساني که ايمان آوردند و اعمال صالح انجام دادند و نماز را برپا داشتند و زکات را پرداختند، اجرشان نزد پروردگارشان است؛ و نه ترسي بر آنهاست، و نه غمگين مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (285, N'اي کساني که ايمان آورده‌ايد! از (مخالفت فرمان) خدا بپرهيزيد، و آنچه از (مطالبات) ربا باقي مانده، رها کنيد؛ اگر ايمان داريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (286, N'اگر (چنين) نمي‌کنيد، بدانيد خدا و رسولش، با شما پيکار خواهند کرد! و اگر توبه کنيد، سرمايه‌هاي شما، از آن شماست [= اصل سرمايه، بدون سود]؛ نه ستم مي‌کنيد، و نه بر شما ستم وارد مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (287, N'و اگر (بدهکار،) قدرت پرداخت نداشته باشد، او را تا هنگام توانايي، مهلت دهيد! (و در صورتي که براستي قدرت پرداخت را ندارد،) براي خدا به او ببخشيد بهتر است؛ اگر (منافع اين کار را) بدانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (288, N'و از روزي بپرهيزيد (و بترسيد) که در آن روز، شما را به سوي خدا بازمي‌گردانند؛ سپس به هر کس، آنچه انجام داده، به طور کامل باز پس داده مي‌شود، و به آنها ستم نخواهد شد. (چون هر چه مي‌بينند، نتايج اعمال خودشان است.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (289, N'اي کساني که ايمان آورده‌ايد! هنگامي که بدهي مدت‌داري (به خاطر وام يا داد و ستد) به يکديگر پيدا کنيد، آن را بنويسيد! و بايد نويسنده‌اي از روي عدالت، (سند را) در ميان شما بنويسد! و کسي که قدرت بر نويسندگي دارد، نبايد از نوشتن -همان طور که خدا به او تعليم داده- خودداري کند! پس بايد بنويسد، و آن کس که حق بر عهده اوست، بايد املا کند، و از خدا که پروردگار اوست بپرهيزد، و چيزي را فروگذار ننمايد! و اگر کسي که حق بر ذمه اوست، سفيه (يا از نظر عقل) ضعيف (و مجنون) است، يا (به خاطر لال بودن،) توانايي بر املاکردن ندارد، بايد ولي او (به جاي او،) با رعايت عدالت، املا کند! و دو نفر از مردان (عادل) خود را (بر اين حق) شاهد بگيريد! و اگر دو مرد نبودند، يک مرد و دو زن، از کساني که مورد رضايت و اطمينان شما هستند، انتخاب کنيد! (و اين دو زن، بايد با هم شاهد قرار گيرند،) تا اگر يکي انحرافي يافت، ديگري به او يادآوري کند. و شهود نبايد به هنگامي که آنها را (براي شهادت) دعوت مي‌کنند، خودداري نمايند! و از نوشتن (بدهي خود،) چه کوچک باشد يا بزرگ،ملول نشويد (هر چه باشد بنويسيد)! اين، در نزد خدا به عدالت نزديکتر، و براي شهادت مستقيم تر، و براي جلوگيري از ترديد و شک (و نزاع و گفتگو) بهتر مي‌باشد؛ مگر اينکه داد و ستد نقدي باشد که بين خود، دست به دست مي‌کنيد. در اين صورت، گناهي بر شما نيست که آن را ننويسيد. ولي هنگامي که خريد و فروش (نقدي) مي‌کنيد، شاهد بگيريد! و نبايد به نويسنده و شاهد، (به خاطر حقگويي،) زياني برسد (و تحت فشار قرار گيرند)! و اگر چنين کنيد، از فرمان پروردگار خارج شده‌ايد. از خدا بپرهيزيد! و خداوند به شما تعليم مي‌دهد؛ خداوند به همه چيز داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (290, N'و اگر در سفر بوديد، و نويسنده‌اي نيافتيد، گروگان بگيريد! (گروگاني که در اختيار طلبکار قرار گيرد.) و اگر به يکديگر اطمينان (کامل) داشته باشيد، (گروگان لازم نيست، و) بايد کسي که امين شمرده شده (و بدون گروگان، چيزي از ديگري گرفته)، امانت (و بدهي خود را بموقع) بپردازد؛ و از خدايي که پروردگار اوست. بپرهيزد! و شهادت را کتمان نکنيد! و هر کس آن را کتمان کند، قلبش گناهکار است. و خداوند، به آنچه انجام مي‌دهيد، داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (291, N'آنچه در آسمانها و زمين است، از آن خداست. و (از اين رو) اگر آنچه را در دل داريد، آشکار سازيد يا پنهان، خداوند شما را بر طبق آن، محاسبه مي‌کند.سپس هر کس را بخواهد (و شايستگي داشته باشد)، مي‌بخشد؛ و هر کس را بخواهد (و مستحق باشد)، مجازات مي‌کند. و خداوند به همه چيز قدرت دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (292, N'پيامبر، به آنچه از سوي پروردگارش بر او نازل شده، ايمان آورده است. (و او، به تمام سخنان خود، کاملا مؤمن مي‌باشد.) و همه مؤمنان (نيز)، به خدا و فرشتگان او و کتابها و فرستادگانش، ايمان آورده‌اند؛ (و مي‌گويند:) ما در ميان هيچ يک از پيامبران او، فرق نمي‌گذاريم (و به همه ايمان داريم). و (مؤمنان) گفتند: «ما شنيديم و اطاعت کرديم. پروردگارا! (انتظار) آمرزش تو را (داريم)؛ و بازگشت (ما) به سوي توست.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (293, N'خداوند هيچ کس را، جز به اندازه تواناييش، تکليف نمي‌کند. (انسان،) هر کار (نيکي) را انجام دهد، براي خود انجام داده؛ و هر کار (بدي) کند، به زيان خود کرده است. (مؤمنان مي‌گويند:) پروردگارا! اگر ما فراموش يا خطا کرديم، ما را مؤاخذه مکن! پروردگارا! تکليف سنگيني بر ما قرار مده، آن چنان که (به خاطر گناه و طغيان،) بر کساني که پيش از ما بودند، قرار دادي! پروردگارا! آنچه طاقت تحمل آن را نداريم، بر ما مقرر مدار! و آثار گناه را از ما بشوي! ما را ببخش و در رحمت خود قرار ده! تو مولا و سرپرست مايي، پس ما را بر جمعيت کافران، پيروز گردان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (294, N'الم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (295, N'معبودي جز خداوند يگانه زنده و پايدار و نگهدارنده، نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (296, N'(همان کسي که) کتاب را بحق بر تو نازل کرد، که با نشانه‌هاي کتب پيشين، منطبق است؛ و «تورات» و «انجيل» را.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (297, N'پيش از آن، براي هدايت مردم فرستاد؛ و (نيز) کتابي که حق را از باطل مشخص مي‌سازد، نازل کرد؛ کساني که به آيات خدا کافر شدند، کيفر شديدي دارند؛ و خداوند (براي کيفر بدکاران و کافران لجوج،) توانا و صاحب انتقام است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (298, N'هيچ چيز، در آسمان و زمين، بر خدا مخفي نمي‌ماند. (بنابر اين، تدبير آنها بر او مشکل نيست.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (299, N'او کسي است که شما را در رحم (مادران)، آنچنان که مي‌خواهد تصوير مي‌کند. معبودي جز خداوند توانا و حکيم، نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (300, N'او کسي است که اين کتاب (آسماني) را بر تو نازل کرد، که قسمتي از آن، آيات «محکم» [= صريح و روشن‌] است؛ که اساس اين کتاب مي‌باشد؛ (و هر گونه پيچيدگي در آيات ديگر، با مراجعه به اينها، برطرف مي‌گردد.) و قسمتي از آن، «متشابه» است [= آياتي که به خاطر بالا بودن سطح مطلب و جهات ديگر، در نگاه اول، احتمالات مختلفي در آن مي‌رود؛ ولي با توجه به آيات محکم، تفسير آنها آشکار مي‌گردد.] اما آنها که در قلوبشان انحراف است، به دنبال متشابهاتند، تا فتنه‌انگيزي کنند (و مردم را گمراه سازند)؛ و تفسير (نادرستي) براي آن مي‌طلبند؛ در حالي که تفسير آنها را، جز خدا و راسخان در علم، نمي‌دانند. (آنها که به دنبال فهم و درک اسرار همه آيات قرآن در پرتو علم و دانش الهي) مي‌گويند: «ما به همه آن ايمان آورديم؛ همه از طرف پروردگار ماست.» و جز صاحبان عقل، متذکر نمي‌شوند (و اين حقيقت را درک نمي‌کنند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (301, N'(راسخان در علم، مي‌گويند:) «پروردگارا! دلهايمان را، بعد از آنکه ما را هدايت کردي، (از راه حق) منحرف مگردان! و از سوي خود، رحمتي بر ما ببخش، زيرا تو بخشنده‌اي!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (302, N'پروردگارا! تو مردم را، براي روزي که ترديدي در آن نيست، جمع خواهي کرد؛ زيرا خداوند، از وعده خود، تخلف نمي‌کند. (ما به تو و رحمت بي‌پايانت، و به وعده رستاخيز و قيامت ايمان داريم.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (303, N'ثروتها و فرزندان کساني که کافر شدند، نمي‌تواند آنان را از (عذاب) خداوند باز دارد؛ (و از کيفر، رهايي بخشد.) و آنان خود، آتشگيره دوزخند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (304, N'(عادت آنان در انکار و تحريف حقايق،) همچون عادت آل فرعون و کساني است که پيش از آنها بودند؛ آيات ما را تکذيب کردند، و خداوند آنها را به (کيفر) گناهانشان گرفت؛ و خداوند، شديد العقاب است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (305, N'به آنها که کافر شدند بگو: «(از پيروزي موقت خود در جنگ احد، شاد نباشيد!) بزودي مغلوب خواهيد شد؛ (و سپس در رستاخيز) به سوي جهنم، محشور خواهيد شد. و چه بد جايگاهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (306, N'در دو گروهي که (در ميدان جنگ بدر،) با هم رو به رو شدند، نشانه (و درس عبرتي) براي شما بود: يک گروه، در راه خدا نبرد مي‌کرد؛ و جمع ديگري که کافر بود، (در راه شيطان و بت،) در حالي که آنها (گروه مؤمنان) را با چشم خود، دو برابر آنچه بودند، مي‌ديدند. (و اين خود عاملي براي وحشت و شکست آنها شد.)و خداوند، هر کس را بخواهد (و شايسته بداند)، با ياري خود، تأييد مي‌کند. در اين، عبرتي است براي بينايان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (307, N'محبت امور مادي، از زنان و فرزندان و اموال هنگفت از طلا و نقره و اسبهاي ممتاز و چهارپايان و زراعت، در نظر مردم جلوه داده شده است؛ (تا در پرتو آن، آزمايش و تربيت شوند؛ ولي) اينها (در صورتي که هدف نهايي آدمي را تشکيل دهند،) سرمايه زندگي پست (مادي) است؛ و سرانجام نيک (و زندگي والا و جاويدان)، نزد خداست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (308, N'بگو: «آيا شما را از چيزي آگاه کنم که از اين (سرمايه‌هاي مادي)، بهتر است؟» براي کساني که پرهيزگاري پيشه کرده‌اند، (و از اين سرمايه‌ها، در راه مشروع و حق و عدالت، استفاده مي‌کنند،) در نزد پروردگارشان (در جهان ديگر)، باغهايي است که نهرها از پاي درختانش مي‌گذرد؛ هميشه در آن خواهند بود؛ و همسراني پاکيزه، و خشنودي خداوند (نصيب آنهاست). و خدا به (امور) بندگان، بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (309, N'همان کساني که مي‌گويند: «پروردگارا! ما ايمان آورده‌ايم؛ پس گناهان ما را بيامرز، و ما را از عذاب آتش، نگاهدار!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (310, N'آنها که (در برابر مشکلات، و در مسير اطاعت و ترک گناه،) استقامت مي‌ورزند، راستگو هستند، (در برابر خدا) خضوع، و (در راه او) انفاق مي‌کنند، و در سحرگاهان، استغفار مي‌نمايند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (311, N'خداوند، (با ايجاد نظام واحد جهان هستي،) گواهي مي‌دهد که معبودي جز او نيست؛ و فرشتگان و صاحبان دانش، (هر کدام به گونه‌اي بر اين مطلب،) گواهي مي‌دهند؛ در حالي که (خداوند در تمام عالم) قيام به عدالت دارد؛ معبودي جز او نيست، که هم توانا و هم حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (312, N'دين در نزد خدا، اسلام (و تسليم بودن در برابر حق) است. و کساني که کتاب آسماني به آنان داده شد، اختلافي (در آن) ايجاد نکردند، مگر بعد از آگاهي و علم، آن هم به خاطر ظلم و ستم در ميان خود؛ و هر کس به آيات خدا کفر ورزد، (خدا به حساب او مي‌رسد؛ زيرا) خداوند، سريع الحساب است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (313, N'اگر با تو، به گفتگو و ستيز برخيزند، (با آنها مجادله نکن! و) بگو: «من و پيروانم، در برابر خداوند (و فرمان او)، تسليم شده‌ايم.» و به آنها که اهل کتاب هستند [= يهود و نصاري‌] و بي‌سوادان [= مشرکان‌] بگو: «آيا شما هم تسليم شده‌ايد؟» اگر (در برابر فرمان و منطق حق، تسليم شوند، هدايت مي‌يابند؛ و اگر سرپيچي کنند، (نگران مباش! زيرا) بر تو، تنها ابلاغ (رسالت) است؛ و خدا نسبت به (اعمال و عقايد) بندگان، بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (314, N'کساني که نسبت به آيات خدا کفر مي‌ورزند و پيامبران را بناحق مي‌کشند، و (نيز) مردمي را که امر به عدالت مي‌کنند به قتل مي‌رسانند، و به کيفر دردناک (الهي) بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (315, N'آنها کساني هستند که اعمال (نيکشان، به خاطر اين گناهان بزرگ،) در دنيا و آخرت تباه شده، و ياور و مددکار (و شفاعت کننده‌اي) ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (316, N'آيا نديدي کساني را که بهره‌اي از کتاب (آسماني) داشتند، به سوي کتاب الهي دعوت شدند تا در ميان آنها داوري کند، سپس گروهي از آنان، (با علم و آگاهي،) روي مي‌گردانند، در حالي که (از قبول حق) اعراض دارند؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (317, N'اين عمل آنها، به خاطر آن است که مي‌گفتند: «آتش (دوزخ)، جز چند روزي به ما نمي‌رسد. (و کيفر ما، به خاطر امتيازي که بر اقوام ديگر داريم، بسيار محدود است.)» اين افترا (و دروغي که به خدا بسته بودند،) آنها را در دينشان مغرور ساخت (و گرفتار انواع گناهان شدند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (318, N'پس چه گونه خواهند بود هنگامي که آنها را براي روزي که شکي در آن نيست [= روز رستاخيز] جمع کنيم، و به هر کس، آنچه (از اعمال براي خود) فراهم کرده ، بطور کامل داده شود؟ و به آنها ستم نخواهد شد (زيرا محصول اعمال خود را مي‌چينند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (319, N'بگو: «بارالها! مالک حکومتها تويي؛ به هر کس بخواهي، حکومت مي‌بخشي؛ و از هر کس بخواهي، حکومت را مي‌گيري؛ هر کس را بخواهي، عزت مي‌دهي؛ و هر که را بخواهي خوار مي‌کني. تمام خوبيها به دست توست؛ تو بر هر چيزي قادري.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (320, N'شب را در روز داخل مي‌کني، و روز را در شب؛ و زنده را از مرده بيرون مي‌آوري، و مرده را زنده؛ و به هر کس بخواهي، بدون حساب، روزي مي‌بخشي.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (321, N'افراد باايمان نبايد به جاي مؤمنان، کافران را دوست و سرپرست خود انتخاب کنند؛ و هر کس چنين کند، هيچ رابطه‌اي با خدا ندارد (و پيوند او بکلي از خدا گسسته مي‌شود)؛ مگر اينکه از آنها بپرهيزيد (و به خاطر هدفهاي مهمتري تقيه کنيد). خداوند شما را از (نافرماني) خود، برحذر مي‌دارد؛ و بازگشت (شما) به سوي خداست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (322, N'بگو: «اگر آنچه را در سينه‌هاي شماست، پنهان داريد يا آشکار کنيد، خداوند آن را مي‌داند؛ و (نيز) از آنچه در آسمانها و زمين است، آگاه مي‌باشد؛ و خداوند بر هر چيزي تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (323, N'روزي که هر کس، آنچه را از کار نيک انجام داده، حاضر مي‌بيند؛ و آرزو مي‌کند ميان او، و آنچه از اعمال بد انجام داده، فاصله زماني زيادي باشد. خداوند شما را از (نافرماني) خودش، برحذر مي‌دارد؛ و (در عين حال،) خدا نسبت به همه بندگان، مهربان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (324, N'بگو: «اگر خدا را دوست مي‌داريد، از من پيروي کنيد! تا خدا (نيز) شما را دوست بدارد؛ و گناهانتان را ببخشد؛ و خدا آمرزنده مهربان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (325, N'بگو: «از خدا و فرستاده (او)، اطاعت کنيد! و اگر سرپيچي کنيد، خداوند کافران را دوست نمي‌دارد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (326, N'خداوند، آدم و نوح و آل ابراهيم و آل عمران را بر جهانيان برتري داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (327, N'آنها فرزندان و (دودماني) بودند که (از نظر پاکي و تقوا و فضيلت،) بعضي از بعض ديگر گرفته شده بودند؛ و خداوند، شنوا و داناست (و از کوششهاي آنها در مسير رسالت خود، آگاه مي‌باشد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (328, N'(به ياد آوريد) هنگامي را که همسر «عمران» گفت: «خداوندا! آنچه را در رحم دارم، براي تو نذر کردم، که «محرر» (و آزاد، براي خدمت خانه تو) باشد. از من بپذير، که تو شنوا و دانايي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (329, N'ولي هنگامي که او را به دنيا آورد، (و او را دختر يافت،) گفت: «خداوندا! من او را دختر آوردم -ولي خدا از آنچه او به دنيا آورده بود، آگاهتر بود- و پسر، همانند دختر نيست. (دختر نمي‌تواند وظيفه خدمتگزاري معبد را همانند پسر انجام دهد.) من او را مريم نام گذاردم؛ و او و فرزندانش را از (وسوسه‌هاي) شيطان رانده شده، در پناه تو قرار مي‌دهم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (330, N'خداوند، او [= مريم‌] را به طرز نيکويي پذيرفت؛ و به طرز شايسته‌اي، (نهال وجود) او را رويانيد (و پرورش داد)؛ و کفالت او را به «زکريا» سپرد. هر زمان زکريا وارد محراب او مي‌شد، غذاي مخصوصي در آن جا مي‌ديد. از او پرسيد: «اي مريم! اين را از کجا آورده‌اي؟!» گفت: «اين از سوي خداست. خداوند به هر کس بخواهد، بي حساب روزي مي‌دهد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (331, N'در آنجا بود که زکريا، (با مشاهده آن همه شايستگي در مريم،) پروردگار خويش را خواند و عرض کرد: «خداوندا! از طرف خود، فرزند پاکيزه‌اي (نيز) به من عطا فرما، که تو دعا را مي‌شنوي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (332, N'و هنگامي که او در محراب ايستاده، مشغول نيايش بود، فرشتگان او را صدا زدند که: «خدا تو را به» يحيي «بشارت مي‌دهد؛ (کسي) که کلمه خدا [= مسيح‌] را تصديق مي‌کند؛ و رهبر خواهد بود؛ و از هوسهاي سرکش برکنار، و پيامبري از صالحان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (333, N'او عرض کرد: «پروردگارا! چگونه ممکن است فرزندي براي من باشد، در حالي که پيري به سراغ من آمده، و همسرم نازاست؟!» فرمود: «بدين گونه خداوند هر کاري را بخواهد انجام مي‌دهد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (334, N'(زکريا) عرض کرد: «پروردگارا! نشانه‌اي براي من قرار ده!» گفت: «نشانه تو آن است که سه روز، جز به اشاره و رمز، با مردم سخن نخواهي گفت. (و زبان تو، بدون هيچ علت ظاهري، براي گفتگو با مردم از کار مي‌افتد.) پروردگار خود را (به شکرانه اين نعمت بزرگ،) بسيار ياد کن! و به هنگام صبح و شام، او را تسبيح بگو!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (335, N'و (به ياد آوريد) هنگامي را که فرشتگان گفتند: «اي مريم! خدا تو را برگزيده و پاک ساخته؛ و بر تمام زنان جهان، برتري داده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (336, N'اي مريم! (به شکرانه اين نعمت) براي پروردگار خود، خضوع کن و سجده بجا آور! و با رکوع‌کنندگان، رکوع کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (337, N'(اي پيامبر!) اين، از خبرهاي غيبي است که به تو وحي مي‌کنيم؛ و تو در آن هنگام که قلمهاي خود را (براي قرعه‌کشي) به آب مي‌افکندند تا کداميک کفالت و سرپرستي مريم را عهده‌دار شود، و (نيز) به هنگامي که (دانشمندان بني اسرائيل، براي کسب افتخار سرپرستي او،) با هم کشمکش داشتند، حضور نداشتي؛ و همه اينها، از راه وحي به تو گفته شد.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (338, N'(به ياد آوريد) هنگامي را که فرشتگان گفتند: «اي مريم! خداوند تو را به کلمه‌اي [= وجود باعظمتي‌] از طرف خودش بشارت مي‌دهد که نامش «مسيح، عيسي پسر مريم» است؛ در حالي که در اين جهان و جهان ديگر، صاحب شخصيت خواهد بود؛ و از مقربان (الهي) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (339, N'و با مردم، در گاهواره و در حالت کهولت (و ميانسال شدن) سخن خواهد گفت؛ و از شايستگان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (340, N'(مريم) گفت: «پروردگارا! چگونه ممکن است فرزندي براي من باشد، در حالي که انساني با من تماس نگرفته است؟!» فرمود: «خداوند، اين‌گونه هر چه را بخواهد مي‌آفريند! هنگامي که چيزي را مقرر دارد (و فرمان هستي آن را صادر کند)، فقط به آن مي‌گويد: «موجود باش!» آن نيز فورا موجود مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (341, N'و به او، کتاب و دانش و تورات و انجيل، مي‌آموزد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (342, N'و (او را به عنوان) رسول و فرستاده به سوي بني اسرائيل (قرار داده، که به آنها مي‌گويد:) من نشانه‌اي از طرف پروردگار شما، برايتان آورده‌ام؛ من از گل، چيزي به شکل پرنده مي‌سازم؛ سپس در آن مي‌دمم و به فرمان خدا، پرنده‌اي مي‌گردد. و به اذن خدا، کور مادرزاد و مبتلايان به برص [= پيسي‌] را بهبودي مي‌بخشم؛ و مردگان را به اذن خدا زنده مي‌کنم؛ و از آنچه مي‌خوريد، و در خانه‌هاي خود ذخيره مي‌کنيد، به شما خبر مي‌دهم؛ مسلما در اينها، نشانه‌اي براي شماست، اگر ايمان داشته باشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (343, N'و آنچه را پيش از من از تورات بوده، تصديق مي‌کنم؛ و (آمده‌ام) تا پاره‌اي از چيزهايي را که (بر اثر ظلم و گناه،) بر شما حرام شده، (مانند گوشت بعضي از چهارپايان و ماهيها،) حلال کنم؛ و نشانه‌اي از طرف پروردگار شما، برايتان آورده‌ام؛ پس از خدا بترسيد، و مرا اطاعت کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (344, N'خداوند، پروردگار من و شماست؛ او را بپرستيد (نه من، و نه چيز ديگر را)! اين است راه راست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (345, N'هنگامي که عيسي از آنان احساس کفر (و مخالفت) کرد، گفت: «کيست که ياور من به سوي خدا (براي تبليغ آيين او) گردد؟» حواريان [= شاگردان مخصوص او] گفتند: «ما ياوران خداييم؛ به خدا ايمان آورديم؛ و تو (نيز) گواه باش که ما اسلام آورده‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (346, N'پروردگارا! به آنچه نازل کرده‌اي، ايمان آورديم و از فرستاده (تو) پيروي نموديم؛ ما را در زمره گواهان بنويس!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (347, N'و (يهود و دشمنان مسيح، براي نابودي او و آيينش،) نقشه کشيدند؛ و خداوند (بر حفظ او و آيينش،) چاره‌جويي کرد؛ و خداوند، بهترين چاره‌جويان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (348, N'(به ياد آوريد) هنگامي را که خدا به عيسي فرمود: «من تو را برمي‌گيرم و به سوي خود، بالا مي‌برم و تو را از کساني که کافر شدند، پاک مي‌سازم؛ و کساني را که از تو پيروي کردند، تا روز رستاخيز، برتر از کساني که کافر شدند، قرارمي‌دهم؛ سپس بازگشت شما به سوي من است و در ميان شما، در آنچه اختلاف داشتيد، داوري مي‌کنم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (349, N'اما آنها که کافر شدند، (و پس از شناختن حق، آن را انکار کردند،) در دنيا و آخرت، آنان را مجازات دردناکي خواهم کرد؛ و براي آنها، ياوراني نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (350, N'اما آنها که ايمان آوردند، و اعمال صالح انجام دادند، خداوند پاداش آنان را بطور کامل خواهد داد؛ و خداوند، ستمکاران را دوست نمي‌دارد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (351, N'اينها را که بر تو مي‌خوانيم، از نشانه‌ها(ي حقانيت تو) است، و يادآوري حکيمانه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (352, N'مثل عيسي در نزد خدا، همچون آدم است؛ که او را از خاک آفريد، و سپس به او فرمود: «موجود باش!» او هم فورا موجود شد. (بنابر اين، ولادت مسيح بدون پدر، هرگز دليل بر الوهيت او نيست.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (353, N'اينها حقيقتي است از جانب پروردگار تو؛ بنابر اين، از ترديد کنندگان مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (354, N'هرگاه بعد از علم و دانشي که (در باره مسيح) به تو رسيده، (باز) کساني با تو به محاجه و ستيز برخيزند، به آنها بگو: «بياييد ما فرزندان خود را دعوت کنيم، شما هم فرزندان خود را؛ ما زنان خويش را دعوت نماييم، شما هم زنان خود را؛ ما از نفوس خود دعوت کنيم، شما هم از نفوس خود؛ آنگاه مباهله کنيم؛ و لعنت خدا را بر دروغگويان قرار دهيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (355, N'اين همان سرگذشت واقعي (مسيح) است. (و ادعاهايي همچون الوهيت او، يا فرزند خدا بودنش، بي‌اساس است.) و هيچ معبودي، جز خداوند يگانه نيست؛ و خداوند توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (356, N'اگر (با اين همه شواهد روشن، باز هم از پذيرش حق) روي گردانند، (بدان که طالب حق نيستند؛ و) خداوند از مفسده‌جويان، آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (357, N'بگو: «اي اهل کتاب! بياييد به سوي سخني که ميان ما و شما يکسان است؛ که جز خداوند يگانه را نپرستيم و چيزي را همتاي او قرار ندهيم؛ و بعضي از ما، بعضي ديگر را -غير از خداي يگانه- به خدايي نپذيرد.» هرگاه (از اين دعوت،) سرباز زنند، بگوييد: «گواه باشيد که ما مسلمانيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (358, N'اي اهل کتاب! چرا درباره ابراهيم، گفتگو و نزاع مي‌کنيد (و هر کدام، او را پيرو آيين خودتان معرفي مي‌نماييد)؟! در حالي که تورات و انجيل، بعد از او نازل شده است! آيا انديشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (359, N'شما کساني هستيد که درباره آنچه نسبت به آن آگاه بوديد، گفتگو و ستيز کرديد؛ چرا درباره آنچه آگاه نيستيد، گفتگو مي‌کنيد؟! و خدا مي‌داند، و شما نمي‌دانيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (360, N'ابراهيم نه يهودي بود و نه نصراني؛ بلکه موحدي خالص و مسلمان بود؛ و هرگز از مشرکان نبود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (361, N'سزاوارترين مردم به ابراهيم، آنها هستند که از او پيروي کردند، و (در زمان و عصر او، به مکتب او وفادار بودند؛ همچنين) اين پيامبر و کساني که (به او) ايمان آورده‌اند (از همه سزاوارترند)؛ و خداوند، ولي و سرپرست مؤمنان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (362, N'جمعي از اهل کتاب (از يهود)، دوست داشتند (و آرزو مي‌کردند) شما را گمراه کنند؛ (اما آنها بايد بدانند که نمي‌توانند شما را گمراه سازند،) آنها گمراه نمي‌کنند مگر خودشان را، و نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (363, N'اي اهل کتاب! چرا به آيات خدا کافر مي‌شويد، در حالي که (به درستي آن) گواهي مي‌دهيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (364, N'اي اهل کتاب! چرا حق را با باطل (مي‌زميزيد و) مشتبه مي‌کنيد (تا ديگران نفهمند و گمراه شوند)، و حقيقت را پوشيده مي‌داريد در حالي که مي‌دانيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (365, N'و جمعي از اهل کتاب (از يهود) گفتند: «(برويد در ظاهر) به آنچه بر مؤمنان نازل شده، در آغاز روز ايمان بياوريد؛ و در پايان روز، کافر شويد (و باز گرديد)! شايد آنها (از آيين خود) بازگردند! (زيرا شما را، اهل کتاب و آگاه از بشارات آسماني پيشين مي‌دانند؛ و اين توطئه کافي است که آنها را متزلزل سازد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (366, N'و جز به کسي که از آيين شما پيروي مي‌کند، (واقعا) ايمان نياوريد!» بگو: «هدايت، هدايت الهي است! (و اين توطئه شما، در برابر آن بي اثر است)»! (سپس اضافه کردند: «تصور نکنيد) به کسي همانند شما (کتاب آسماني) داده مي‌شود، يا اينکه مي‌توانند در پيشگاه پروردگارتان، با شما بحث و گفتگو کنند، (بلکه نبوت و منطق، هر دو نزد شماست!)» بگو: «فضل (و موهبت نبوت و عقل و منطق، در انحصار کسي نيست؛ بلکه) به دست خداست؛ و به هر کس بخواهد (و شايسته بداند،) مي‌دهد؛ و خداوند، واسع [= داراي مواهب گسترده‌] و آگاه (از موارد شايسته آن) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (367, N'هر کس را بخواهد، ويژه رحمت خود مي‌کند؛ و خداوند، داراي مواهب عظيم است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (368, N'و در ميان اهل کتاب، کساني هستند که اگر ثروت زيادي به رسم امانت به آنها بسپاري، به تو باز مي‌گردانند؛ و کساني هستند که اگر يک دينار هم به آنان بسپاري، به تو باز نمي‌گردانند؛ مگر تا زماني که بالاي سر آنها ايستاده (و بر آنها مسلط) باشي! اين بخاطر آن است که مي‌گويند: «ما در برابر اميين [= غير يهود]، مسؤول نيستيم.» و بر خدا دروغ مي‌بندند؛ در حالي که مي‌دانند (اين سخن دروغ است).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (369, N'آري، کسي که به پيمان خود وفا کند و پرهيزگاري پيشه نمايد، (خدا او را دوست مي‌دارد؛ زيرا) خداوند پرهيزگاران را دوست دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (370, N'کساني که پيمان الهي و سوگندهاي خود (به نام مقدس او) را به بهاي ناچيزي مي‌فروشند، آنها بهره‌اي در آخرت نخواهند داشت؛ و خداوند با آنها سخن نمي‌گويد و به آنان در قيامت نمي‌نگرد و آنها را (از گناه) پاک نمي‌سازد؛ و عذاب دردناکي براي آنهاست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (371, N'در ميان آنها [= يهود] کساني هستند که به هنگام تلاوت کتاب (خدا)، زبان خود را چنان مي‌گردانند که گمان کنيد (آنچه را مي‌خوانند،) از کتاب (خدا) است؛در حالي که از کتاب (خدا) نيست! (و با صراحت) مي‌گويند: «آن از طرف خداست!» با اينکه از طرف خدا نيست، و به خدا دروغ مي‌بندند در حالي که مي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (372, N'براي هيچ بشري سزاوار نيست که خداوند، کتاب آسماني و حکم و نبوت به او دهد سپس او به مردم بگويد: «غير از خدا، مرا پرستش کنيد!» بلکه (سزاوار مقام او، اين است که بگويد:) مردمي الهي باشيد، آن‌گونه که کتاب خدا را مي‌آموختيد و درس مي‌خوانديد! (و غير از خدا را پرستش نکنيد!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (373, N'و نه اينکه به شما دستور دهد که فرشتگان و پيامبران را، پروردگار خود انتخاب کنيد. آيا شما را، پس از آنکه مسلمان شديد، به کفر دعوت مي‌کند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (374, N'و (به خاطر بياوريد) هنگامي را که خداوند، از پيامبران (و پيروان آنها)،پيمان مؤکد گرفت، که هرگاه کتاب و دانش به شما دادم، سپس پيامبري به سوي شما آمد که آنچه را با شماست تصديق مي‌کند، به او ايمان بياوريد و او را ياري کنيد! سپس (خداوند) به آنها گفت: «آيا به اين موضوع، اقرار داريد؟ و بر آن، پيمان مؤکد بستيد؟» گفتند: «(آري) اقرار داريم!» (خداوند به آنها) گفت: «پس گواه باشيد! و من نيز با شما از گواهانم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (375, N'پس کسي که بعد از اين (پيمان محکم)، روي گرداند، فاسق است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (376, N'آيا آنها غير از آيين خدا مي‌طلبند؟! (آيين او همين اسلام است؛) و تمام کساني که در آسمانها و زمين هستند، از روي اختيار يا از روي اجبار، در برابر (فرمان) او تسليمند، و همه به سوي او بازگردانده مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (377, N'بگو: «به خدا ايمان آورديم؛ و (همچنين) به آنچه بر ما و بر ابراهيم و اسماعيل و اسحاق و يعقوب و اسباط نازل گرديده؛ و آنچه به موسي و عيسي و (ديگر) پيامبران، از طرف پروردگارشان داده شده است؛ ما در ميان هيچ يک از آنان فرقي نمي‌گذاريم؛ و در برابر (فرمان) او تسليم هستيم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (378, N'و هر کس جز اسلام (و تسليم در برابر فرمان حق،) آييني براي خود انتخاب کند، از او پذيرفته نخواهد شد؛ و او در آخرت، از زيانکاران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (379, N'چگونه خداوند جمعيتي را هدايت مي‌کند که بعد از ايمان و گواهي به حقانيت رسول و آمدن نشانه‌هاي روشن براي آنها، کافر شدند؟! و خدا، جمعيت ستمکاران را هدايت نخواهد کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (380, N'کيفر آنها، اين است که لعن (و طرد) خداوند و فرشتگان و مردم همگي بر آنهاست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (381, N'همواره در اين لعن (و طرد و نفرين) مي‌مانند؛ مجازاتشان تخفيف نمي‌يابد؛ و به آنها مهلت داده نمي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (382, N'مگر کساني که پس از آن، توبه کنند و اصلاح نمايند؛ (و در مقام جبران گناهان گذشته برآيند، که توبه آنها پذيرفته خواهد شد؛) زيرا خداوند، آمرزنده و بخشنده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (383, N'کساني که پس از ايمان کافر شدند و سپس بر کفر (خود) افزودند، (و در اين راه اصرار ورزيدند،) هيچ‌گاه توبه آنان، (که از روي ناچاري يا در آستانه مرگ صورت مي‌گيرد،) قبول نمي‌شود؛ و آنها گمراهان (واقعي)اند (چرا که هم راه خدا را گم کرده‌اند، و هم راه توبه را!).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (384, N'کساني که کافر شدند و در حال کفر از دنيا رفتند، اگر چه روي زمين پر از طلا باشد، و آن را بعنوان فديه (و کفاره اعمال بد خويش) بپردازند، هرگز از هيچ‌يک آنها قبول نخواهد شد؛ و براي آنان، مجازات دردناک است؛ و ياوراني ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (385, N'هرگز به (حقيقت) نيکوکاري نمي‌رسيد مگر اينکه از آنچه دوست مي‌داريد، (در راه خدا) انفاق کنيد؛ و آنچه انفاق مي‌کنيد، خداوند از آن آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (386, N'همه غذاها(ي پاک) بر بني اسرائيل حلال بود، جز آنچه اسرائيل (يعقوب)، پيش از نزول تورات، بر خود تحريم کرده بود؛ (مانند گوشت شتر که براي او ضرر داشت.) بگو: «اگر راست مي‌گوييد تورات را بياوريد و بخوانيد! (اين نسبتهايي که به پيامبران پيشين مي‌دهيد، حتي در تورات تحريف شده شما نيست!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (387, N'بنا بر اين، آنها که بعد از اين به خدا دروغ مي‌بندند، ستمگرند! (زيرا از روي علم و عمد چنين مي‌کنند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (388, N'بگو: «خدا راست گفته (و اينها در آيين پاک ابراهيم نبوده) است. بنا بر اين، از آيين ابراهيم پيروي کنيد، که به حق گرايش داشت، و از مشرکان نبود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (389, N'نخستين خانه‌اي که براي مردم (و نيايش خداوند) قرار داده شد، همان است که در سرزمين مکه است، که پر برکت، و مايه هدايت جهانيان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (390, N'در آن، نشانه‌هاي روشن، (از جمله) مقام ابراهيم است؛ و هر کس داخل آن [= خانه خدا] شود؛ در امان خواهد بود، و براي خدا بر مردم است که آهنگ خانه (او) کنند، آنها که توانايي رفتن به سوي آن دارند. و هر کس کفر ورزد (و حج را ترک کند، به خود زيان رسانده)، خداوند از همه جهانيان، بي‌نياز است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (391, N'بگو: «اي اهل کتاب! چرا به آيات خدا کفر مي‌ورزيد؟! و خدا گواه است بر اعمالي که انجام مي‌دهيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (392, N'بگو: «اي اهل کتاب! چرا افرادي را که ايمان آورده‌اند، از راه خدا بازمي‌داريد، و مي‌خواهيد اين راه را کج سازيد؟! در حالي که شما (به درستي اين راه) گواه هستيد؛ و خداوند از آنچه انجام مي‌دهيد، غافل نيست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (393, N'اي کساني که ايمان آورده‌ايد! اگر از گروهي از اهل کتاب، (که کارشان نفاق‌افکني و شعله‌ورساختن آتش کينه و عداوت است) اطاعت کنيد، شما را پس از ايمان، به کفر بازمي‌گردانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (394, N'و چگونه ممکن است شما کافر شويد، با اينکه (در دامان وحي قرار گرفته‌ايد، و) آيات خدا بر شما خوانده مي‌شود، و پيامبر او در ميان شماست؟! (بنابر اين، به خدا تمسک جوييد!) و هر کس به خدا تمسک جويد، به راهي راست، هدايت شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (395, N'اي کساني که ايمان آورده‌ايد! آن گونه که حق تقوا و پرهيزکاري است، از خدا بپرهيزيد! و از دنيا نرويد، مگر اينکه مسلمان باشيد! (بايد گوهر ايمان را تا پايان عمر، حفظ کنيد!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (396, N'و همگي به ريسمان خدا [= قرآن و اسلام، و هرگونه وسيله وحدت‌]، چنگ زنيد ، و پراکنده نشويد! و نعمت (بزرگ) خدا را بر خود، به ياد آريد که چگونه دشمن يکديگر بوديد، و او ميان دلهاي شما، الفت ايجاد کرد، و به برکت نعمت او، برادر شديد! و شما بر لب حفره‌اي از آتش بوديد، خدا شما را از آن نجات داد؛ اين چنين، خداوند آيات خود را براي شما آشکار مي‌سازد؛ شايد پذيراي هدايت شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (397, N'بايد از ميان شما، جمعي دعوت به نيکي، و امر به معروف و نهي از منکر کنند! و آنها همان رستگارانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (398, N'و مانند کساني نباشيد که پراکنده شدند و اختلاف کردند؛ (آن هم) پس از آنکه نشانه‌هاي روشن (پروردگار) به آنان رسيد! و آنها عذاب عظيمي دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (399, N'(آن عذاب عظيم) روزي خواهد بود که چهره‌هائي سفيد، و چهره‌هائي سياه مي‌گردد، اما آنها که صورتهايشان سياه شده، (به آنها گفته مي‌شود:) آيا بعد از ايمان، و (اخوت و برادري در سايه آن،) کافر شديد؟! پس بچشيد عذاب را، به سبب آنچه کفر مي‌ورزيديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (400, N'و اما آنها که چهره‌هايشان سفيد شده، در رحمت خداوند خواهند بود؛ و جاودانه در آن مي‌مانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (401, N'اينها آيات خداست؛ که بحق بر تو مي‌خوانيم. و خداوند (هيچ گاه) ستمي براي (احدي از) جهانيان نمي‌خواهد.')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (402, N'و (چگونه ممکن است خدا ستم کند؟! در حالي که) آنچه در آسمانها و آنچه در زمين است، مال اوست؛ و همه کارها، به سوي او باز مي‌گردد (و به فرمان اوست.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (403, N'شما بهترين امتي بوديد که به سود انسانها آفريده شده‌اند؛ (چه اينکه) امر به معروف و نهي از منکر مي‌کنيد و به خدا ايمان داريد. و اگر اهل کتاب، (به چنين برنامه و آيين درخشاني،) ايمان آورند، براي آنها بهتر است! (ولي تنها) عده کمي از آنها با ايمانند، و بيشتر آنها فاسقند، (و خارج از اطاعت پروردگار)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (404, N'آنها [= اهل کتاب، مخصوصا» يهود] هرگز نمي‌توانند به شما زيان برسانند، جز آزارهاي مختصر؛ و اگر با شما پيکار کنند، به شما پشت خواهند کرد (و شکست مي‌خورند؛ سپس کسي آنها را ياري نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (405, N'هر جا يافت شوند، مهر ذلت بر آنان خورده است؛ مگر با ارتباط به خدا، (و تجديد نظر در روش ناپسند خود،) و (يا) با ارتباط به مردم (و وابستگي به اين و آن)؛ و به خشم خدا، گرفتار شده‌اند؛ و مهر بيچارگي بر آنها زده شده؛ چرا که آنها به آيات خدا، کفر مي‌ورزيدند و پيامبران را بناحق مي‌کشتند. اينها بخاطر آن است که گناه کردند؛ و (به حقوق ديگران،) تجاوز مي‌نمودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (406, N'آنها همه يکسان نيستند؛ از اهل کتاب، جمعيتي هستند که (به حق و ايمان) قيام مي‌کنند؛ و پيوسته در اوقات شب، آيات خدا را مي‌خوانند؛ در حالي که سجده مي‌نمايند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (407, N'به خدا و روز ديگر ايمان مي‌آورند؛ امر به معروف و نهي از منکر مي‌کنند؛ و در انجام کارهاي نيک، پيشي مي‌گيرند؛ و آنها از صالحانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (408, N'و آنچه از اعمال نيک انجام دهند، هرگز کفران نخواهد شد! (و پاداش شايسته آن را مي‌بينند.) و خدا از پرهيزکاران، آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (409, N'کساني که کافر شدند، هرگز نمي‌توانند در پناه اموال و فرزندانشان، از مجازات خدا در امان بمانند! آنها اصحاب دوزخند؛ و جاودانه در آن خواهند ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (410, N'آنچه آنها در اين زندگي پست دنيوي انفاق مي‌کنند، همانند باد سوزاني است که به زراعت قومي که بر خود ستم کرده (و در غير محل و وقت مناسب، کشت نموده‌اند)، بوزد؛ و آن را نابود سازد. خدا به آنها ستم نکرده؛ بلکه آنها، خودشان به خويشتن ستم مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (411, N'اي کساني که ايمان آورده‌ايد! محرم اسراري از غير خود، انتخاب نکنيد! آنها از هرگونه شر و فسادي در باره شما، کوتاهي نمي‌کنند. آنها دوست دارند شما در رنج و زحمت باشيد. (نشانه‌هاي) دشمني از دهان (و کلام)شان آشکار شده؛ و آنچه در دلهايشان پنهان مي‌دارند، از آن مهمتر است. ما آيات (و راه‌هاي پيشگيري از شر آنها) را براي شما بيان کرديم اگر انديشه کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (412, N'شما کساني هستيد که آنها را دوست مي‌داريد؛ اما آنها شما را دوست ندارند! در حالي که شما به همه کتابهاي آسماني ايمان داريد (و آنها به کتاب آسماني شما ايمان ندارند). هنگامي که شما را ملاقات مي‌کنند، (به دروغ) مي‌گويند: «ايمان آورده‌ايم!» اما هنگامي که تنها مي‌شوند، از شدت خشم بر شما، سر انگشتان خود را به دندان مي‌گزند! بگو: «با همين خشمي که داريد بميريد! خدا از (اسرار) درون سينه‌ها آگاه است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (413, N'اگر نيکي به شما برسد، آنها را ناراحت مي‌کند؛ و اگر حادثه ناگواري براي شما رخ دهد، خوشحال مي‌شوند. (اما) اگر (در برابرشان) استقامت و پرهيزگاري پيشه کنيد، نقشه‌هاي (خائنانه) آنان، به شما زياني نمي‌رساند؛ خداوند به آنچه انجام مي‌دهند، احاطه دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (414, N'و (به ياد آور) زماني را که صبحگاهان، از ميان خانواده خود، جهت انتخاب اردوگاه جنگ براي مؤمنان، بيرون رفتي! و خداوند، شنوا و داناست (گفتگوهاي مختلفي را که درباره طرح جنگ گفته مي‌شد، مي‌شنيد؛ و انديشه‌هايي را که بعضي در سر مي‌پروراندند، مي‌دانست).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (415, N'(و نيز به ياد آور) زماني را که دو طايفه از شما تصميم گرفتند سستي نشان دهند (و از وسط راه بازگردند)؛ و خداوند پشتيبان آنها بود (و به آنها کمک کرد که از اين فکر بازگردند)؛ و افراد باايمان، بايد تنها بر خدا توکل کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (416, N'خداوند شما را در «بدر» ياري کرد (و بر دشمنان خطرناک، پيروز ساخت)؛ در حالي که شما (نسبت به آنها)، ناتوان بوديد. پس، از خدا بپرهيزيد (و در برابر دشمن، مخالفت فرمان پيامبر نکنيد)، تا شکر نعمت او را بجا آورده باشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (417, N'در آن هنگام که تو به مؤمنان مي‌گفتني: «آيا کافي نيست که پروردگارتان، شما را به سه هزار نفر از فرشتگان، که از آسمان فرود مي‌آيند، ياري کند؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (418, N'آري، (امروز هم) اگر استقامت و تقوا پيشه کنيد، و دشمن به همين زودي به سراغ شما بيايد، خداوند شما را به پنج هراز نفر از فرشتگان، که نشانه‌هايي با خود دارند، مدد خواهد داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (419, N'ولي اينها را خداوند فقط بشارت، و براي اطمينان خاطر شما قرار داده؛ وگرنه، پيروزي تنها از جانب خداوند تواناي حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (420, N'(اين وعده را که خدا به شما داده،) براي اين است که قسمتي از پيکر لشکر کافران را قطع کند؛ يا آنها را با ذلت برگرداند؛ تا مأيوس و نااميد، (به وطن خود) بازگردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (421, N'هيچ‌گونه اختياري (در باره عفو کافران، يا مؤمنان فراري از جنگ،) براي تو نيست؛ مگر اينکه (خدا) بخواهد آنها را ببخشد، يا مجازات کند؛ زيرا آنها ستمگرند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (422, N'و آنچه در آسمانها و زمين است، از آن خداست. هر کس را بخواهد (و شايسته بداند)، مي‌بخشد؛ و هر کس را بخواهد، مجازات مي‌کند؛ و خداوند آمرزنده مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (423, N'اي کساني که ايمان آورده‌ايد! ربا (و سود پول) را چند برابر نخوريد! از خدا بپرهيزيد، تا رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (424, N'و از آتشي بپرهيزيد که براي کافران آماده شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (425, N'و خدا و پيامبر را اطاعت کنيد، تا مشمول رحمت شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (426, N'و شتاب کنيد براي رسيدن به آمرزش پروردگارتان؛ و بهشتي که وسعت آن، آسمانها و زمين است؛ و براي پرهيزگاران آماده شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (427, N'همانها که در توانگري و تنگدستي، انفاق مي‌کنند؛ و خشم خود را فرو مي‌برند؛ و از خطاي مردم درمي‌گذرند؛ و خدا نيکوکاران را دوست دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (428, N'و آنها که وقتي مرتکب عمل زشتي شوند، يا به خود ستم کنند، به ياد خدا مي‌افتند؛ و براي گناهان خود، طلب آمرزش مي‌کنند -و کيست جز خدا که گناهان را ببخشد؟- و بر گناه، اصرار نمي‌ورزند، با اينکه مي‌دانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (429, N'آنها پاداششان آمرزش پروردگار، و بهشتهايي است که از زير درختانش، نهرها جاري است؛ جاودانه در آن ميمانند؛ چه نيکو است پاداش اهل عمل!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (430, N'پيش از شما، سنت‌هايي وجود داشت؛ (و هر قوم، طبق اعمال و صفات خود، سرنوشتهايي داشتند؛ که شما نيز، همانند آن را داريد.) پس در روي زمين، گردش کنيد و ببينيد سرانجام تکذيب‌کنندگان (آيات خدا) چگونه بود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (431, N'اين، بياني است براي عموم مردم؛ و هدايت و اندرزي است براي پرهيزگاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (432, N'و سست نشويد! و غمگين نگرديد! و شما برتريد اگر ايمان داشته باشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (433, N'اگر (در ميدان احد،) به شما جراحتي رسيد (و ضربه‌اي وارد شد)، به آن جمعيت نيز (در ميدان بدر)، جراحتي همانند آن وارد گرديد. و ما اين روزها(ي پيروزي و شکست) را در ميان مردم مي‌گردانيم؛ (-و اين خاصيت زندگي دنياست-) تا خدا، افرادي را که ايمان آورده‌اند، بداند (و شناخته شوند)؛ و خداوند از ميان شما، شاهداني بگيرد. و خدا ظالمان را دوست نمي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (434, N'و تا خداوند، افراد باايمان را خالص گرداند (و ورزيده شوند)؛ و کافران را به تدريج نابود سازد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (435, N'آيا چنين پنداشتيد که (تنها با ادعاي ايمان) وارد بهشت خواهيد شد، در حالي که خداوند هنوز مجاهدان از شما و صابران را مشخص نساخته است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (436, N'و شما مرگ (و شهادت در راه خدا) را، پيش از آنکه با آن روبه‌رو شويد، آرزو مي‌کرديد؛ سپس آن را با چشم خود ديديد، در حالي که به آن نگاه مي‌کرديد (و حاضر نبوديد به آن تن دردهيد! چقدر ميان گفتار و کردار شما فاصله است؟!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (437, N'محمد (ص) فقط فرستاده خداست؛ و پيش از او، فرستادگان ديگري نيز بودند؛ آيا اگر او بميرد و يا کشته شود، شما به عقب برمي‌گرديد؟ (و اسلام را رها کرده به دوران جاهليت و کفر بازگشت خواهيد نمود؟) و هر کس به عقب باز گردد، هرگز به خدا ضرري نمي‌زند؛ و خداوند بزودي شاکران (و استقامت‌کنندگان) را پاداش خواهد داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (438, N'هيچ‌کس، جز به فرمان خدا، نمي‌ميرد؛ سرنوشتي است تعيين شده؛ (بنابر اين، مرگ پيامبر يا ديگران، يک سنت الهي است.) هر کس پاداش دنيا را بخواهد (و در زندگي خود، در اين راه گام بردارد،) چيزي از آن به او خواهيم داد؛ و هر کس پاداش آخرت را بخواهد، از آن به او مي‌دهيم؛ و بزودي سپاسگزاران را پاداش خواهيم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (439, N'چه بسيار پيامبراني که مردان الهي فراواني به همراه آنان جنگ کردند! آنها هيچ‌گاه در برابر آنچه در راه خدا به آنان مي‌رسيد، سست و ناتوان نشدند (و تن به تسليم ندادند)؛ و خداوند استقامت‌کنندگان را دوست دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (440, N'سخنشان تنها اين بود که: «پروردگارا! گناهان ما را ببخش! و از تندرويهاي ما در کارها، چشم‌پوشي کن! قدمهاي ما را استوار بدار! و ما را بر جمعيت کافران، پيروز گردان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (441, N'از اين‌رو خداوند پاداش اين جهان، و پاداش نيک آن جهان را به آنها داد؛ و خداوند نيکوکاران را دوست مي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (442, N'اي کساني که ايمان آورده‌ايد! اگر از کساني که کافر شده‌اند اطاعت کنيد، شما را به گذشته‌هايتان بازمي‌گردانند؛ و سرانجام، زيانکار خواهيد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (443, N'(آنها تکيه‌گاه شما نيستند،) بلکه ولي و سرپرست شما، خداست؛ و او بهترين ياوران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (444, N'بزودي در دلهاي کافران، بخاطر اينکه بدون دليل، چيزهايي را براي خدا همتا قرار دادند، رعب و ترس مي‌افکنيم؛ و جايگاه آنها، آتش است؛ و چه بد جايگاهي است جايگاه ستمکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (445, N'خداوند، وعده خود را به شما، (در باره پيروزي بر دشمن در احد،) تحقق بخشيد؛ در آن هنگام (که در آغاز جنگ،) دشمنان را به فرمان او، به قتل مي‌رسانديد؛ (و اين پيروزي ادامه داشت) تا اينکه سست شديد؛ و (بر سر رهاکردن سنگرها،) در کار خود به نزاع پرداختيد؛ و بعد از آن که آنچه را دوست مي‌داشتيد (از غلبه بر دشمن) به شما نشان داد، نافرماني کرديد. بعضي از شما، خواهان دنيا بودند؛ و بعضي خواهان آخرت. سپس خداوند شما را از آنان منصرف ساخت؛ (و پيروزي شما به شکست انجاميد؛) تا شما را آزمايش کند. و او شما را بخشيد؛ و خداوند نسبت به مؤمنان، فضل و بخشش دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (446, N'(به خاطر بياوريد) هنگامي را که از کوه بالا ميرفتيد؛ و جمعي در وسط بيابان پراکنده شدند؛ و از شدت وحشت،) به عقب ماندگان نگاه نمي‌کرديد، و پيامبر از پشت سر، شما را صدا مي‌زد. سپس اندوه‌ها را يکي پس از ديگري به شما جزا داد؛ اين بخاطر آن بود که ديگر براي از دست رفتن (غنايم جنگي) غمگين نشويد، و نه بخاطر مصيبت‌هايي که بر شما وارد مي‌گردد. و خداوند از آنچه انجام مي‌دهيد، آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (447, N'سپس بدنبال اين غم و اندوه، آرامشي بر شما فرستاد. اين آرامش، بصورت خواب سبکي بود که (در شب بعد از حادثه احد،) گروهي از شما را فرا گرفت؛ اماگروه ديگري در فکر جان خويش بودند؛ (و خواب به چشمانشان نرفت.) آنها گمانهاي نادرستي -همچون گمانهاي دوران جاهليت- درباره خدا داشتند؛ و مي‌گفتند: «آيا چيزي از پيروزي نصيب ما مي‌شود؟!» بگو: «همه کارها (و پيروزيها) به دست خداست!» آنها در دل خود، چيزي را پنهان مي‌دارند که براي تو آشکار نمي‌سازند؛ مي‌گويند: «اگر ما سهمي از پيروزي داشتيم، در اين جا کشته نمي‌شديم!» بگو: «اگر هم در خانه‌هاي خود بوديد، آنهايي که کشته‌شدن بر آنها مقرر شده بود، قطعا به سوي آرامگاه‌هاي خود، بيرون مي‌آمدند (و آنها را به قتل مي‌رساندند). و اينها براي اين است که خداوند، آنچه در سينه‌هايتان پنهان داريد، بيازمايد؛ و آنچه را در دلهاي شما (از ايمان) است، خالص گرداند؛ و خداوند از آنچه در درون سينه‌هاست، با خبر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (448, N'کساني که در روز روبرو شدن دو جمعيت با يکديگر (در جنگ احد)، فرار کردند، شيطان آنها را بر اثر بعضي از گناهاني که مرتکب شده بودند، به لغزش انداخت؛ و خداوند آنها را بخشيد. خداوند، آمرزنده و بردبار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (449, N'اي کساني که ايمان آورده‌ايد! همانند کافران نباشيد که چون برادرانشان به مسافرتي مي‌روند، يا در جنگ شرکت مي‌کنند (و از دنيا مي‌روند و يا کشته مي‌شوند)، مي‌گويند: «اگر آنها نزد ما بودند، نمي‌مردند و کشته نمي‌شدند!» (شما از اين گونه سخنان نگوييد،) تا خدا اين حسرت را بر دل آنها [= کافران‌] بگذارد. خداوند، زنده مي‌کند و مي‌ميراند؛ (و زندگي و مرگ، به دست اوست؛) و خدا به آنچه انجام مي‌دهيد، بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (450, N'اگر هم در راه خدا کشته شويد يا بميريد، (زيان نکرده‌ايد؛ زيرا) آمرزش و رحمت خدا، از تمام آنچه آنها (در طول عمر خود،) جمع آوري ميکنند، بهتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (451, N'و اگر بميريد يا کشته شويد، به سوي خدا محشور مي‌شويد. (بنابراين، فاني نمي‌شويد که از فنا، وحشت داشته باشيد.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (452, N'به (برکت) رحمت الهي، در برابر آنان [= مردم‌] نرم (و مهربان) شدي! و اگر خشن و سنگدل بودي، از اطراف تو، پراکنده مي‌شدند. پس آنها را ببخش و براي آنها آمرزش بطلب! و در کارها، با آنان مشورت کن! اما هنگامي که تصميم گرفتي، (قاطع باش! و) بر خدا توکل کن! زيرا خداوند متوکلان را دوست دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (453, N'اگر خداوند شما را ياري کند، هيچ کس بر شما پيروز نخواهد شد! و اگر دست از ياري شما بردارد، کيست که بعد از او، شما را ياري کند؟! و مؤمنان، تنها بر خداوند بايد توکل کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (454, N'(گمان کرديد ممکن است پيامبر به شما خيانت کند؟! در حالي که) ممکن نيست هيچ پيامبري خيانت کند! و هر کس خيانت کند، روز رستاخيز، آنچه را در آن خيانت کرده، با خود (به صحنه محشر) مي‌آورد؛ سپس به هر کس، آنچه را فراهم کرده (و انجام داده است)، بطور کامل داده مي‌شود؛ و (به همين دليل) به آنها ستم نخواهد شد (چرا که محصول اعمال خود را خواهند ديد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (455, N'آيا کسي که از رضاي خدا پيروي کرده، همانند کسي است که به خشم و غضب خدا بازگشته؟! و جايگاه او جهنم، و پايان کار او بسيار بد است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (456, N'هر يک از آنان، درجه و مقامي در پيشگاه خدا دارند؛ و خداوند به آنچه انجام مي‌دهند، بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (457, N'خداوند بر مؤمنان منت نهاد [= نعمت بزرگي بخشيد] هنگامي که در ميان آنها، پيامبري از خودشان برانگيخت؛ که آيات او را بر آنها بخواند، و آنها را پاک کند و کتاب و حکمت بياموزد؛ هر چند پيش از آن، در گمراهي آشکاري بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (458, N'آيا هنگامي که مصيبتي (در ميدان جنک احد) به شما رسيد، در حالي که دو برابر آن را (در ميدان جنگ بدر بر دشمن) وارد ساخته بوديد، گفتيد: «اين مصيبت از کجاست؟!» بگو: «از ناحيه خود شماست (که در ميدان جنگ احد، با دستور پيامبر مخالفت کرديد)! خداوند بر هر چيزي قادر است. (و چنانچه روش خود را اصلاح کنيد، در آينده شما را پيروز مي‌کند.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (459, N'و آنچه (در روز احد،) در روزي که دو دسته [= مؤمنان و کافران‌] با هم نبرد کردند به شما رسيد، به فرمان خدا (و طبق سنت الهي) بود؛ و براي اين بود که مؤمنان را مشخص کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (460, N'و نيز براي اين بود که منافقان شناخته شوند؛ آنهايي که به ايشان گفته شد: «بياييد در راه خدا نبرد کنيد! يا (حداقل) از حريم خود، دفاع نماييد!» گفتند: «اگر مي‌دانستيم جنگي روي خواهد داد، از شما پيروي مي‌کرديم. (اما مي‌دانيم جنگي نمي‌شود.)» آنها در آن هنگام، به کفر نزديکتر بودند تا به ايمان؛ به زبان خود چيزي مي‌گويند که در دلهايشان نيست! و خداوند از آنچه کتمان مي‌کنند، آگاهتر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (461, N'(منافقان) آنها هستند که به برادران خود -در حالي که از حمايت آنها دست کشيده بودند- گفتند: «اگر آنها از ما پيروي مي‌کردند، کشته نمي‌شدند!» بگو: «(مگر شما مي‌توانيد مرگ افراد را پيش‌بيني کنيد؟!) پس مرگ را از خودتان دور سازيد اگر راست مي‌گوييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (462, N'(اي پيامبر!) هرگز گمان مبر کساني که در راه خدا کشته شدند، مردگانند! بلکه آنان زنده‌اند، و نزد پروردگارشان روزي داده مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (463, N'آنها بخاطر نعمتهاي فراواني که خداوند از فضل خود به ايشان بخشيده است، خوشحالند؛ و بخاطر کساني که هنوز به آنها ملحق نشده‌اند [= مجاهدان و شهيدان آينده ]، خوشوقتند؛ (زيرا مقامات برجسته آنها را در آن جهان مي‌بينند؛ و مي‌دانند) که نه ترسي بر آنهاست، و نه غمي خواهند داشت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (464, N'و از نعمت خدا و فضل او (نسبت به خودشان نيز) مسرورند؛ و (مي‌بينند که) خداوند، پاداش مؤمنان را ضايع نمي‌کند؛ (نه پاداش شهيدان، و نه پاداش مجاهداني که شهيد نشدند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (465, N'آنها که دعوت خدا و پيامبر (ص) را، پس از آن همه جراحاتي که به ايشان رسيد، اجابت کردند؛ (و هنوز زخمهاي ميدان احد التيام نيافته بود، به سوي ميدان «حمرار الاسد» حرکت نمودند؛) براي کساني از آنها، که نيکي کردند و تقوا پيش گرفتند، پاداش بزرگي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (466, N'اينها کساني بودند که (بعضي از) مردم، به آنان گفتند: «مردم [= لشکر دشمن ] براي (حمله به) شما اجتماع کرده‌اند؛ از آنها بترسيد!» اما اين سخن، بر ايمانشان افزود؛ و گفتند: «خدا ما را کافي است؛ و او بهترين حامي ماست.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (467, N'به همين جهت، آنها (از اين ميدان،) با نعمت و فضل پروردگارشان، بازگشتند؛ در حالي که هيچ ناراحتي به آنان نرسيد؛ و از رضاي خدا، پيروي کردند؛ و خداوند داراي فضل و بخشش بزرگي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (468, N'اين فقط شيطان است که پيروان خود را (با سخنان و شايعات بي‌اساس،) مي‌ترساند. از آنها نترسيد! و تنها از من بترسيد اگر ايمان داريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (469, N'کساني که در راه کفر، شتاب ميکنند، تو را غمگين نسازند! به يقين، آنها هرگز زياني به خداوند نمي‌رسانند. (بعلاوه) خدا مي‌خواهد (آنها را به حال خودشان واگذارد؛ و در نتيجه،) بهره‌اي براي آنها در آخرت قرار ندهد. و براي آنها مجازات بزرگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (470, N'(آري،) کساني که ايمان را دادند و کفر را خريداري کردند، هرگز به خدا زياني نمي‌رسانند؛ و براي آنها، مجازات دردناکي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (471, N'آنها که کافر شدند، (و راه طغيان پيش گرفتند،) تصور نکنند اگر به آنان مهلت مي‌دهيم، به سودشان است! ما به آنان مهلت مي‌دهيم فقط براي اينکه بر گناهان خود بيفزايند؛ و براي آنها، عذاب خوارکننده‌اي (آماده شده) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (472, N'چنين نبود که خداوند، مؤمنان را به همان‌گونه که شما هستيد واگذارد؛ مگر آنکه ناپاک را از پاک جدا سازد. و نيز چنين نبود که خداوند شما را از اسرار غيب، آگاه کند (تا مؤمنان و منافقان را از اين راه بشناسيد؛ اين بر خلاف سنت الهي است؛) ولي خداوند از ميان رسولان خود، هر کس را بخواهد برميگزيند؛ (و قسمتي از اسرار نهان را که براي مقام رهبري او لازم است، در اختيار او مي‌گذارد.) پس (اکنون که اين جهان، بوته آزمايش پاک و ناپاک است،) به خدا و رسولان او ايمان بياوريد! و اگر ايمان بياوريد و تقوا پيشه کنيد، پاداش بزرگي براي شماست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (473, N'کساني که بخل مي‌ورزند، و آنچه را خدا از فضل خويش به آنان داده، انفاق نمي‌کنند، گمان نکنند اين کار به سود آنها است؛ بلکه براي آنها شر است؛ بزودي در روز قيامت، آنچه را نسبت به آن بخل ورزيدند، همانند طوقي به گردنشان مي‌افکنند. و ميراث آسمانها و زمين، از آن خداست؛ و خداوند، از آنچه انجام مي‌دهيد، آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (474, N'خداوند، سخن آنها را که گفتند: «خدا فقير است، و ما بي‌نيازيم»، شنيد! به زودي آنچه را گفتند، خواهيم نوشت؛ و (همچنين) کشتن پيامبران را بناحق (مي‌نويسيم)؛ و به آنها مي‌گوييم: «بچشيد عذاب سوزان را (در برابر کارهايتان!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (475, N'اين بخاطر چيزي است که دستهاي شما از پيش فرستاده (و نتيجه کار شماست) و بخاطر آن است که خداوند، به بندگان (خود)، ستم نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (476, N'(اينها) همان کساني (هستند) که گفتند: «خداوند از ما پيمان گرفته که به هيچ پيامبري ايمان نياوريم تا (اين معجزه را انجام دهد:) يک قرباني بياورد، که آتش [= صاعقه آسماني‌] آن را بخورد!» بگو: «پيامبراني پيش از من، براي شما آمدند؛ و دلايل روشن، و آنچه را گفتيد آوردند؛ پس چرا آنها را به قتل رسانديد اگر راست مي‌گوييد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (477, N'پس (اگر اين بهانه‌جويان،) تو را تکذيب کنند، (چيز تازه‌اي نيست؛) رسولان پيش از تو (نيز) تکذيب شدند؛ پيامبراني که دلايل آشکار، و نوشته‌هاي متين و محکم، و کتاب روشني‌بخش آورده بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (478, N'هر کسي مرگ را مي‌چشد؛ و شما پاداش خود را بطور کامل در روز قيامت خواهيد گرفت؛ آنها که از آتش (دوزخ) دور شده، و به بهشت وارد شوند نجات يافته و رستگار شده‌اند و زندگي دنيا، چيزي جز سرمايه فريب نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (479, N'به يقين (همه شما) در اموال و جانهاي خود، آزمايش مي‌شويد! و از کساني که پيش از شما به آنها کتاب (آسماني) داده شده [= يهود]، و (همچنين) از مشرکان، سخنان آزاردهنده فراوان خواهيد شنيد! و اگر استقامت کنيد و تقوا پيشه سازيد، (شايسته‌تر است؛ زيرا) اين از کارهاي مهم و قابل اطمينان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (480, N'و (به خاطر بياوريد) هنگامي را که خدا، از کساني که کتاب (آسماني) به آنها داده شده، پيمان گرفت که حتما آن را براي مردم آشکار سازيد و کتمان نکنيد! ولي آنها، آن را پشت سر افکندند؛ و به بهاي کمي فروختند؛ و چه بد متاعي مي‌خرند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (481, N'گمان مبر آنها که از اعمال (زشت) خود خوشحال مي‌شوند، و دوست دارند در برابر کار (نيکي) که انجام نداده‌اند مورد ستايش قرار گيرند، از عذاب (الهي) برکنارند! (بلکه) براي آنها، عذاب دردناکي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (482, N'و حکومت آسمانها و زمين، از آن خداست؛ و خدا بر همه چيز تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (483, N'مسلما در آفرينش آسمانها و زمين، و آمد و رفت شب و روز، نشانه‌هاي (روشني) براي خردمندان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (484, N'همانها که خدا را در حال ايستاده و نشسته، و آنگاه که بر پهلو خوابيده‌اند، ياد مي‌کنند؛ و در اسرار آفرينش آسمانها و زمين مي‌انديشند؛ (و مي‌گويند:) بار الها! اينها را بيهوده نيافريده‌اي! منزهي تو! ما را از عذاب آتش، نگاه دار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (485, N'پروردگارا! هر که را تو (بخاطر اعمالش،) به آتش افکني، او را خوار و رسوا ساخته‌اي! و براي افراد ستمگر، هيچ ياوري نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (486, N'پروردگارا! ما صداي منادي (تو) را شنيديم که به ايمان دعوت مي‌کرد که: «به پروردگار خود، ايمان بياوريد!» و ما ايمان آورديم؛ پروردگارا! گناهان ما را ببخش! و بديهاي ما را بپوشان! و ما را با نيکان (و در مسير آنها) بميران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (487, N'پروردگارا! آنچه را به وسيله پيامبرانت به ما وعده فرمودي، به ما عطا کن! و ما را در روز رستاخيز، رسوا مگردان! زيرا تو هيچ‌گاه از وعده خود، تخلف نمي‌کني.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (488, N'خداوند، درخواست آنها را پذيرفت؛ (و فرمود:) من عمل هيچ عمل‌کننده‌اي از شما را، زن باشد يا مرد، ضايع نخواهم کرد؛ شما همنوعيد، و از جنس يکديگر! آنها که در راه خدا هجرت کردند، و از خانه‌هاي خود بيرون رانده شدند و در راه من آزار ديدند، و جنگ کردند و کشته شدند، بيقين گناهانشان را مي‌بخشم؛ و آنها را در باغهاي بهشتي، که از زير درختانش نهرها جاري است، وارد مي‌کنم. اين پاداشي است از طرف خداوند؛ و بهترين پاداشها نزد پروردگار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (489, N'رفت و آمد (پيروزمندانه) کافران در شهرها، تو را نفريبد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (490, N'اين متاع ناچيزي است؛ و سپس جايگاهشان دوزخ، و چه بد جايگاهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (491, N'ولي کساني (که ايمان دارند، و) از پروردگارشان مي‌پرهيزند، براي آنها باغهايي از بهشت است، که از زير درختانش نهرها جاري است؛ هميشه در آن خواهند بود. اين، نخستين پذيرايي است که از سوي خداوند به آنها مي‌رسد؛ و آنچه در نزد خداست، براي نيکان بهتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (492, N'و از اهل کتاب، کساني هستند که به خدا، و آنچه بر شما نازل شده، و آنچه بر خودشان نازل گرديده، ايمان دارند؛ در برابر (فرمان) خدا خاضعند؛ و آيات خدا را به بهاي ناچيزي نمي‌فروشند. پاداش آنها، نزد پروردگارشان است. خداوند، سريع الحساب است. (تمام اعمال نيک آنها را به سرعت حساب مي‌کند، و پاداش مي‌دهد.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (493, N'اي کساني که ايمان آورده‌ايد! (در برابر مشکلات و هوسها،) استقامت کنيد! و در برابر دشمنان (نيز)، پايدار باشيد و از مرزهاي خود، مراقبت کنيد و از خدا بپرهيزيد، شايد رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (494, N'اي مردم! از (مخالفت) پروردگارتان بپرهيزيد! همان کسي که همه شما را از يک انسان آفريد؛ و همسر او را (نيز) از جنس او خلق کرد؛ و از آن دو، مردان و زنان فراواني (در روي زمين) منتشر ساخت. و از خدايي بپرهيزيد که (همگي به عظمت او معترفيد؛ و) هنگامي که چيزي از يکديگر مي‌خواهيد، نام او را مي‌بريد! (و نيز) (از قطع رابطه با) خويشاوندان خود، پرهيز کنيد! زيرا خداوند، مراقب شماست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (495, N'و اموال يتيمان را (هنگامي که به حد رشد رسيدند) به آنها بدهيد! و اموال بد (خود) را، با اموال خوب (آنها) عوض نکنيد! و اموال آنان را همراه اموال خودتان (با مخلوط کردن يا تبديل نمودن) نخوريد، زيرا اين گناه بزرگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (496, N'و اگر مي‌ترسيد که (بهنگام ازدواج با دختران يتيم،) عدالت را رعايت نکنيد، (از ازدواج با آنان، چشم‌پوشي کنيد و) با زنان پاک (ديگر) ازدواج نمائيد، دو يا سه يا چهار همسر و اگر مي‌ترسيد عدالت را (درباره همسران متعدد) رعايت نکنيد، تنها يک همسر بگيريد، و يا از زناني که مالک آنهائيد استفاده کنيد، اين کار، از ظلم و ستم بهتر جلوگيري مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (497, N'و مهر زنان را (بطور کامل) بعنوان يک بدهي (يا عطيه،) به آنان بپردازيد! (ولي) اگر آنها چيزي از آن را با رضايت خاطر به شما ببخشند، حلال و گوارا مصرف کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (498, N'اموال خود را، که خداوند وسيله قوام زندگي شما قرار داده، به دست سفيهان نسپاريد و از آن، به آنها روزي دهيد! و لباس بر آنان بپوشانيد و با آنها سخن شايسته بگوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (499, N'و يتيمان را چون به حد بلوغ برسند، بيازماييد! اگر در آنها رشد (کافي) يافتيد، اموالشان را به آنها بدهيد! و پيش از آنکه بزرگ شوند، اموالشان را از روي اسراف نخوريد! هر کس که بي‌نياز است، (از برداشت حق الزحمه) خودداري کند؛ و آن کس که نيازمند است، به طور شايسته (و مطابق زحمتي که مي‌کشد،) از آن بخورد. و هنگامي که اموالشان را به آنها بازمي‌گردانيد، شاهد بگيريد! اگر چه خداوند براي محاسبه کافي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (500, N'براي مردان، از آنچه پدر و مادر و خويشاوندان از خود بر جاي مي‌گذارند، سهمي است؛ و براي زنان نيز، از آنچه پدر و مادر و خويشاوندان مي‌گذارند، سهمي؛ خواه آن مال، کم باشد يا زياد؛ اين سهمي است تعيين شده و پرداختني.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (501, N'و اگر بهنگام تقسيم (ارث)، خويشاوندان (و طبقه‌اي که ارث نمي‌برند) و يتيمان و مستمندان، حضور داشته باشند، چيزي از آن اموال را به آنها بدهيد! و با آنان به طور شايسته سخن بگوييد!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (502, N'کساني که اگر فرزندان ناتواني از خود بيادگار بگذارند از آينده آنان مي‌ترسند، بايد (از ستم درباره يتيمان مردم) بترسند! از (مخالفت) خدا بپرهيزند، و سخني استوار بگويند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (503, N'کساني که اموال يتيمان را به ظلم و ستم مي‌خورند، (در حقيقت،) تنها آتش مي‌خورند؛ و بزودي در شعله‌هاي آتش (دوزخ) مي‌سوزند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (504, N'خداوند در باره فرزندانتان به شما سفارش مي‌کند که سهم (ميراث) پسر، به اندازه سهم دو دختر باشد؛ و اگر فرزندان شما، (دو دختر و) بيش از دو دختر باشند، دو سوم ميراث از آن آنهاست؛ و اگر يکي باشد، نيمي (از ميراث،) از آن اوست. و براي هر يک از پدر و مادر او، يک ششم ميراث است، اگر (ميت) فرزندي داشته باشد؛ و اگر فرزندي نداشته باشد، و (تنها) پدر و مادر از او ارث برند، براي مادر او يک سوم است (و بقيه از آن پدر است)؛ و اگر او برادراني داشته باشد، مادرش يک ششم مي‌برد (و پنج ششم باقيمانده، براي پدر است). (همه اينها،) بعد از انجام وصيتي است که او کرده، و بعد از اداي دين است -شما نمي‌دانيد پدران و مادران و فرزندانتان، کداميک براي شما سودمندترند!- اين فريضه الهي است؛ و خداوند، دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (505, N'و براي شما، نصف ميراث زنانتان است، اگر آنها فرزندي نداشته باشند؛ و اگر فرزندي داشته باشند، يک چهارم از آن شماست؛ پس از انجام وصيتي که کرده‌اند، و اداي دين (آنها). و براي زنان شما، يک چهارم ميراث شماست، اگر فرزندي نداشته باشيد؛ و اگر براي شما فرزندي باشد، يک هشتم از آن آنهاست؛ بعد از انجام وصيتي که کرده‌ايد، و اداي دين. و اگر مردي بوده باشد که کلاله [= خواهر يا برادر] از او ارث مي‌برد، يا زني که برادر يا خواهري دارد، سهم هر کدام، يک ششم است (اگر برادران و خواهران مادري باشند)؛ و اگر بيش از يک نفر باشند، آنها در يک سوم شريکند؛ پس از انجام وصيتي که شده، و اداي دين؛ بشرط آنکه (از طريق وصيت و اقرار به دين،) به آنها ضرر نزند. اين سفارش خداست؛ و خدا دانا و بردبار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (506, N'اينها مرزهاي الهي است؛ و هر کس خدا و پيامبرش را اطاعت کند، (و قوانين او را محترم بشمرد،) خداوند وي را در باغهايي از بهشت وارد مي‌کند که همواره، آب از زير درختانش جاري است؛ جاودانه در آن مي‌مانند؛ و اين، پيروزي بزرگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (507, N'و آن کس که نافرماني خدا و پيامبرش را کند و از مرزهاي او تجاوز نمايد، او را در آتشي وارد مي‌کند که جاودانه در آن خواهد ماند؛ و براي او مجازات خوارکننده‌اي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (508, N'و کساني از زنان شما که مرتکب زنا شوند، چهار نفر از مسلمانان را بعنوان شاهد بر آنها بطلبيد! اگر گواهي دادند، آنان [= زنان‌] را در خانه ها(ي خود) نگاه داريد تا مرگشان فرارسد؛ يا اينکه خداوند، راهي براي آنها قرار دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (509, N'و از ميان شما، آن مردان و زناني که (همسر ندارند، و) مرتکب آن کار (زشت) مي‌شوند، آنها را آزار دهيد (و حد بر آنان جاري نماييد)! و اگر توبه کنند، و (خود را) اصلاح نمايند، (و به جبران گذشته بپردازند،) از آنها درگذريد! زيرا خداوند، توبه‌پذير و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (510, N'پذيرش توبه از سوي خدا، تنها براي کساني است که کار بدي را از روي جهالت انجام مي‌دهند، سپس زود توبه مي‌کنند. خداوند، توبه چنين اشخاصي را مي‌پذيرد؛ و خدا دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (511, N'براي کساني که کارهاي بد را انجام مي‌دهند، و هنگامي که مرگ يکي از آنها فرا مي‌رسد مي‌گويد: «الان توبه کردم!» توبه نيست؛ و نه براي کساني که در حال کفر از دنيا مي‌روند؛ اينها کساني هستند که عذاب دردناکي برايشان فراهم کرده‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (512, N'اي کساني که ايمان آورده‌ايد! براي شما حلال نيست که از زنان، از روي اکراه (و ايجاد ناراحتي براي آنها،) ارث ببريد! و آنان را تحت فشار قرار ندهيد که قسمتي از آنچه را به آنها داده‌ايد (از مهر)، تملک کنيد! مگر اينکه آنها عمل زشت آشکاري انجام دهند. و با آنان، بطور شايسته رفتار کنيد! و اگر از آنها، (بجهتي) کراهت داشتيد، (فورا تصميم به جدايي نگيريد!) چه بسا چيزي خوشايند شما نباشد، و خداوند خير فراواني در آن قرار مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (513, N'و اگر تصميم گرفتيد که همسر ديگري به جاي همسر خود انتخاب کنيد، و مال فراواني (بعنوان مهر) به او پرداخته‌ايد، چيزي از آن را پس نگيريد! آيا براي بازپس گرفتن مهر آنان، به تهمت و گناه آشکار متوسل مي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (514, N'و چگونه آن را باز پس مي‌گيريد، در حالي که شما با يکديگر تماس و آميزش کامل داشته‌ايد؟ و (از اين گذشته،) آنها (هنگام ازدواج،) از شما پيمان محکمي گرفته‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (515, N'با زناني که پدران شما با آنها ازدواج کرده‌اند، هرگز ازدواج نکنيد! مگر آنچه درگذشته (پيش از نزول اين حکم) انجام شده است؛ زيرا اين کار، عملي زشت و تنفرآور و راه نادرستي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (516, N'حرام شده است بر شما، مادرانتان، و دختران، و خواهران، و عمه‌ها، و خاله‌ها، و دختران برادر، و دختران خواهر شما، و مادراني که شما را شير داده‌اند، و خواهران رضاعي شما، و مادران همسرانتان، و دختران همسرتان که در دامان شما پرورش يافته‌اند از همسراني که با آنها آميزش جنسي داشته‌ايد -و چنانچه با آنها آميزش جنسي نداشته‌ايد، (دختران آنها) براي شما مانعي ندارد- و (همچنين) همسرهاي پسرانتان که از نسل شما هستند (-نه پسرخوانده‌ها-) و (نيز حرام است بر شما) جمع ميان دو خواهر کنيد؛ مگر آنچه در گذشته واقع شده؛ چرا که خداوند، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (517, N'و زنان شوهردار (بر شما حرام است؛) مگر آنها را که (از راه اسارت) مالک شده‌ايد؛ (زيرا اسارت آنها در حکم طلاق است؛) اينها احکامي است که خداوند بر شما مقرر داشته است. اما زنان ديگر غير از اينها (که گفته شد)، براي شما حلال است که با اموال خود، آنان را اختيار کنيد؛ در حالي که پاکدامن باشيد و از زنا، خودداري نماييد. و زناني را که متعه [= ازدواج موقت‌] مي‌کنيد، واجب است مهر آنها را بپردازيد. و گناهي بر شما نيست در آنچه بعد از تعيين مهر، با يکديگر توافق کرده‌ايد. (بعدا مي‌توانيد با توافق، آن را کم يا زياد کنيد.) خداوند، دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (518, N'و آنها که توانايي ازدواج با زنان (آزاد) پاکدامن باايمان را ندارند، مي‌توانند با زنان پاکدامن از بردگان باايماني که در اختيار داريد ازدواج کنند -خدا به ايمان شما آگاه‌تر است؛ و همگي اعضاي يک پيکريد- آنها را با اجازه صاحبان آنان تزويج نماييد، و مهرشان را به خودشان بدهيد؛ به شرط آنکه پاکدامن باشند، نه بطور آشکار مرتکب زنا شوند، و نه دوست پنهاني بگيرند. و در صورتي که «محصنه» باشند و مرتکب عمل منافي عفت شوند، نصف مجازات زنان آزاد را خواهند داشت. اين (اجازه ازدواج با کنيزان) براي کساني از شماست که بترسند (از نظر غريزه جنسي) به زحمت بيفتند؛ و (با اين حال نيز) خودداري (از ازدواج با آنان) براي شما بهتر است. و خداوند، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (519, N'خداوند مي‌خواهد (با اين دستورها، راه‌هاي خوشبختي و سعادت را) براي شما آشکار سازد، و به سنتهاي (صحيح) پيشينيان رهبري کند. و خداوند دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (520, N'خدا مي‌خواهد شما را ببخشد (و از آلودگي پاک نمايد)، اما آنها که پيرو شهواتند، مي‌خواهند شما بکلي منحرف شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (521, N'خدا ميخواهد (با احکام مربوط به ازدواج با کنيزان و مانند آن،) کار را بر شما سبک کند؛ و انسان، ضعيف آفريده شده؛ (و در برابر طوفان غرايز، مقاومت او کم است)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (522, N'اي کساني که ايمان آورده‌ايد! اموال يکديگر را به باطل (و از طرق نامشروع) نخوريد مگر اينکه تجارتي با رضايت شما انجام گيرد. و خودکشي نکنيد! خداوند نسبت به شما مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (523, N'و هر کس اين عمل را از روي تجاوز و ستم انجام دهد، بزودي او را در آتشي وارد خواهيم ساخت؛ و اين کار براي خدا آسان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (524, N'اگر از گناهان بزرگي که از آن نهي مي‌شويد پرهيز کنيد، گناهان کوچک شما را مي‌پوشانيم؛ و شما را در جايگاه خوبي وارد مي‌سازيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (525, N'برتريهايي را که خداوند براي بعضي از شما بر بعضي ديگر قرار داده آرزو نکنيد! (اين تفاوتهاي طبيعي و حقوقي، براي حفظ نظام زندگي شما، و بر طبق عدالت است. ولي با اين حال،) مردان نصيبي از آنچه به دست مي‌آورند دارند، و زنان نيز نصيبي؛ (و نبايد حقوق هيچ‌يک پايمال گردد). و از فضل (و رحمت و برکت) خدا، براي رفع تنگناها طلب کنيد! و خداوند به هر چيز داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (526, N'براي هر کسي، وارثاني قرار داديم، که از ميراث پدر و مادر و نزديکان ارث ببرند؛ و (نيز) کساني که با آنها پيمان بسته‌ايد، نصيبشان را بپردازيد! خداوند بر هر چيز، شاهد و ناظر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (527, N'مردان، سرپرست و نگهبان زنانند، بخاطر برتريهايي که خداوند (از نظر نظام اجتماع) براي بعضي نسبت به بعضي ديگر قرار داده است، و بخاطر انفاقهايي که از اموالشان (در مورد زنان) مي‌کنند. و زنان صالح، زناني هستند که متواضعند، و در غياب (همسر خود،) اسرار و حقوق او را، در مقابل حقوقي که خدا براي آنان قرار داده، حفظ مي‌کنند. و (اما) آن دسته از زنان را که از سرکشي و مخالفتشان بيم داريد، پند و اندرز دهيد! (و اگر مؤثر واقع نشد،) در بستر از آنها دوري نماييد! و (اگر هيچ راهي جز شدت عمل، براي وادار کردن آنها به انجام وظايفشان نبود،) آنها را تنبيه کنيد! و اگر از شما پيروي کردند، راهي براي تعدي بر آنها نجوييد! (بدانيد) خداوند، بلندمرتبه و بزرگ است. (و قدرت او، بالاترين قدرتهاست.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (528, N'و اگر از جدايي و شکاف ميان آن دو (همسر) بيم داشته باشيد، يک داور از خانواده شوهر، و يک داور از خانواده زن انتخاب کنيد (تا به کار آنان رسيدگي کنند). اگر اين دو داور، تصميم به اصلاح داشته باشند، خداوند به توافق آنها کمک مي‌کند؛ زيرا خداوند، دانا و آگاه است (و از نيات همه، با خبر است).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (529, N'و خدا را بپرستيد! و هيچ‌چيز را همتاي او قرار ندهيد! و به پدر و مادر، نيکي کنيد؛ همچنين به خويشاوندان و يتيمان و مسکينان، و همسايه نزديک، و همسايه دور، و دوست و همنشين، و واماندگان در سفر، و بردگاني که مالک آنها هستيد؛ زيرا خداوند، کسي را که متکبر و فخر فروش است، (و از اداي حقوق ديگران سرباز مي‌زند،) دوست نمي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (530, N'آنها کساني هستند که بخل مي‌ورزند، و مردم را به بخل دعوت مي‌کنند، و آنچه را که خداوند از فضل (و رحمت) خود به آنها داده، کتمان مي‌نمايند. (اين عمل، در حقيقت از کفرشان سرچشمه گرفته؛) و ما براي کافران، عذاب خوارکننده‌اي آماده کرده‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (531, N'و آنها کساني هستند که اموال خود را براي نشان‌دادن به مردم انفاق مي‌کنند، و ايمان به خدا و روز بازپسين ندارند؛ (چرا که شيطان، رفيق و همنشين آنهاست؛) و کسي که شيطان قرين او باشد، بد همنشين و قريني است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (532, N'چه مي‌شد اگر آنها به خدا و روز بازپسين ايمان مي‌آوردند، و از آنچه خدا به آنان روزي داده، (در راه او) انفاق مي‌کردند؟! و خداوند از (اعمال و نيات) آنها آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (533, N'خداوند (حتي) به اندازه سنگيني ذره‌اي ستم نمي‌کند؛ و اگر کار نيکي باشد، آن را دو چندان مي‌سازد؛ و از نزد خود، پاداش عظيمي (در برابر آن) مي‌دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (534, N'حال آنها چگونه است آن روزي که از هر امتي، شاهد و گواهي (بر اعمالشان) مي‌آوريم، و تو را نيز بر آنان گواه خواهيم آورد؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (535, N'در آن روز، آنها که کافر شدند و با پيامبر (ص) بمخالفت برخاستند، آرزو مي‌کنند که اي کاش (خاک بودند، و) خاک آنها با زمينهاي اطراف يکسان مي‌شد (و بکلي محو و فراموش مي‌شدند). در آن روز، (با آن همه گواهان،) سخني را نمي‌توانند از خدا پنهان کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (536, N'اي کساني که ايمان آورده‌ايد! در حال مستي به نماز نزديک نشويد، تا بدانيد چه مي‌گوييد! و همچنين هنگامي که جنب هستيد -مگر اينکه مسافر باشيد- تا غسل کنيد. و اگر بيماريد، يا مسافر، و يا «قضاي حاجت» کرده‌ايد، و يا با زنان آميزش جنسي داشته‌ايد، و در اين حال، آب (براي وضو يا غسل) نيافتيد، با خاک پاکي تيمم کنيد! (به اين طريق که) صورتها و دستهايتان را با آن مسح نماييد. خداوند، بخشنده و آمرزنده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (537, N'آيا نديدي کساني را که بهره‌اي از کتاب (خدا) به آنها داده شده بود، (به جاي اينکه از آن، براي هدايت خود و ديگران استفاده کنند، براي خويش) گمراهي مي‌خرند، و مي‌خواهند شما نيز گمراه شويد؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (538, N'خدا به دشمنان شما آگاهتر است؛ (ولي آنها زياني به شما نمي‌رسانند.) و کافي است که خدا ولي شما باشد؛ و کافي است که خدا ياور شما باشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (539, N'بعضي از يهود، سخنان را از جاي خود، تحريف مي‌کنند؛ و (به جاي اينکه بگويند: «شنيديم و اطاعت کرديم»)، مي‌گويند: «شنيديم و مخالفت کرديم! و (نيز مي‌گويند:) بشنو! که هرگز نشنوي! و (از روي تمسخر مي‌گويند:) راعنا [= ما را تحميق کن! ]» تا با زبان خود، حقايق را بگردانند و در آيين خدا، طعنه زنند. ولي اگر آنها (به جاي اين همه لجاجت) مي‌گفتند: «شنيديم و اطاعت کرديم؛ و سخنان ما را بشنو و به ما مهلت ده (تا حقايق را درک کنيم)»، براي آنان بهتر،و با واقعيت سازگارتر بود. ولي خداوند، آنها را بخاطر کفرشان، از رحمت خود دور ساخته است؛ از اين رو جز عده کمي ايمان نمي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (540, N'اي کساني که کتاب (خدا) به شما داده شده! به آنچه (بر پيامبر خود) نازل کرديم -و هماهنگ با نشانه‌هايي است که با شماست- ايمان بياوريد، پيش از آنکه صورتهايي را محو کنيم، سپس به پشت سر بازگردانيم، يا آنها را از رحمت خود دور سازيم، همان گونه که «اصحاب سبت» [= گروهي از تبهکاران بني اسرائيل‌] را دور ساختيم؛ و فرمان خدا، در هر حال انجام شدني است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (541, N'خداوند (هرگز) شرک را نمي‌بخشد! و پايين‌تر از آن را براي هر کس (بخواهد و شايسته بداند) مي‌بخشد. و آن کسي که براي خدا، شريکي قرار دهد، گناه بزرگي مرتکب شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (542, N'آيا نديدي کساني را که خودستايي مي‌کنند؟! (اين خود ستاييها، بي‌ارزش است؛) بلکه خدا هر کس را بخواهد، ستايش مي‌کند؛ و کمترين ستمي به آنها نخواهد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (543, N'ببين چگونه بر خدا دروغ مي‌بندند! و همين گناه آشکار، (براي مجازات آنان) کافي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (544, N'آيا نديدي کساني را که بهره‌اي از کتاب (خدا) به آنان داده شده، (با اين حال)، به «جبت» و «طاغوت» [= بت و بت‌پرستان‌] ايمان مي‌آورند، و درباره کافران مي‌گويند: «آنها، از کساني که ايمان آورده‌اند، هدايت يافته‌ترند»؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (545, N'آنها کساني هستند که خداوند، ايشان را از رحمت خود، دور ساخته است؛ و هر کس را خدا از رحمتش دور سازد، ياوري براي او نخواهي يافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (546, N'آيا آنها [= يهود] سهمي در حکومت دارند (که بخواهند چنين داوري کنند)؟ در حالي که اگر چنين بود، (همه چيز را در انحصار خود مي‌گرفتند،) و کمترين حق را به مردم نمي‌دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (547, N'يا اينکه نسبت به مردم [= پيامبر و خاندانش‌]، و بر آنچه خدا از فضلش به آنان بخشيده، حسد مي‌ورزند؟ ما به آل ابراهيم، (که يهود از خاندان او هستند نيز،) کتاب و حکمت داديم؛ و حکومت عظيمي در اختيار آنها [= پيامبران بني اسرائيل‌] قرار داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (548, N'ولي جمعي از آنها به آن ايمان آوردند؛ و جمعي راه (مردم را) بر آن بستند. و شعله فروزان آتش دوزخ، براي آنها کافي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (549, N'کساني که به آيات ما کافر شدند، بزودي آنها را در آتشي وارد مي‌کنيم که هرگاه پوستهاي تنشان (در آن) بريان گردد (و بسوزد)، پوستهاي ديگري به جاي آن قرار مي‌دهيم، تا کيفر (الهي) را بچشند. خداوند، توانا و حکيم است (و روي حساب، کيفر مي‌دهد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (550, N'و کساني که ايمان آوردند و کارهاي شايسته انجام دادند، بزودي آنها را در باغهايي از بهشت وارد مي‌کنيم که نهرها از زير درختانش جاري است؛ هميشه در آن خواهند ماند؛ و همسراني پاکيزه براي آنها خواهد بود؛ و آنان را در سايه‌هاي گسترده (و فرح بخش) جاي ميدهيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (551, N'خداوند به شما فرمان مي‌دهد که امانتها را به صاحبانش بدهيد! و هنگامي که ميان مردم داوري مي‌کنيد، به عدالت داوري کنيد! خداوند، اندرزهاي خوبي به شما مي‌دهد! خداوند، شنوا و بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (552, N'اي کساني که ايمان آورده‌ايد! اطاعت کنيد خدا را! و اطاعت کنيد پيامبر خدا و اولو الأمر [= اوصياي پيامبر] را! و هرگاه در چيزي نزاع داشتيد، آن را به خدا و پيامبر بازگردانيد (و از آنها داوري بطلبيد) اگر به خدا و روز رستاخيز ايمان داريد! اين (کار) براي شما بهتر، و عاقبت و پايانش نيکوتر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (553, N'آيا نديدي کساني را که گمان مي‌کنند به آنچه (از کتابهاي آسماني که) بر تو و بر پيشينيان نازل شده، ايمان آورده‌اند، ولي مي‌خواهند براي داوري نزد طاغوت و حکام باطل بروند؟! با اينکه به آنها دستور داده شده که به طاغوت کافر شوند. اما شيطان مي‌خواهد آنان را گمراه کند، و به بيراهه‌هاي دور دستي بيفکند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (554, N'و هنگامي که به آنها گفته شود: «به سوي آنچه خداوند نازل کرده، و به سوي پيامبر بياييد»، منافقان را مي‌بيني که (از قبول دعوت) تو، اعراض مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (555, N'پس چگونه وقتي به خاطر اعمالشان، گرفتار مصيبتي مي‌شوند، سپس به سراغ تو مي‌آيند، سوگند ياد مي‌کنند که منظور (ما از بردن داوري نزد ديگران)، جز نيکي کردن و توافق (ميان طرفين نزاع،) نبوده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (556, N'آنها کساني هستند که خدا، آنچه را در دل دارند، مي‌داند. از (مجازات) آنان صرف نظر کن! و آنها را اندرز ده! و با بياني رسا، نتايج اعمالشان را به آنها گوشزد نما!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (557, N'ما هيچ پيامبري را نفرستاديم مگر براي اين که به فرمان خدا، از وي اطاعت شود. و اگر اين مخالفان، هنگامي که به خود ستم مي‌کردند (و فرمانهاي خدا را زير پا مي‌گذاردند)، به نزد تو مي‌آمدند؛ و از خدا طلب آمرزش مي‌کردند؛ و پيامبر هم براي آنها استغفار مي‌کرد؛ خدا را توبه پذير و مهربان مي‌يافتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (558, N'به پروردگارت سوگند که آنها مؤمن نخواهند بود، مگر اينکه در اختلافات خود، تو را به داوري طلبند؛ و سپس از داوري تو، در دل خود احساس ناراحتي نکنند؛ و کاملا تسليم باشند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (559, N'اگر (همانند بعضي از امتهاي پيشين،) به آنان دستور مي‌داديم: «يکديگر را به قتل برسانيد»، و يا: «از وطن و خانه خود، بيرون رويد»، تنها عده کمي از آنها عمل مي‌کردند! و اگر اندرزهايي را که به آنان داده مي‌شد انجام مي‌دادند، براي آنها بهتر بود؛ و موجب تقويت ايمان آنها مي‌شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (560, N'و در اين صورت، پاداش بزرگي از ناحيه خود به آنها مي‌داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (561, N'و آنان را به راه راست، هدايت مي‌کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (562, N'و کسي که خدا و پيامبر را اطاعت کند، (در روز رستاخيز،) همنشين کساني خواهد بود که خدا، نعمت خود را بر آنان تمام کرده؛ از پيامبران و صديقان و شهدا و صالحان؛ و آنها رفيقهاي خوبي هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (563, N'اين موهبتي از ناحيه خداست. و کافي است که او، (از حال بندگان، و نيات و اعمالشان) آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (564, N'اي کساني که ايمان آورده‌ايد! آمادگي خود را (در برابر دشمن) حفظ کنيد و در دسته‌هاي متعدد، يا بصورت دسته واحد، (طبق شرايط هر زمان و هر مکان،) به سوي دشمن حرکت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (565, N'در ميان شما، افرادي (منافق) هستند، که (هم خودشان سست مي‌باشند، و هم) ديگران را به سستي مي‌کشانند؛ اگر مصيبتي به شما برسد، مي‌گويند: «خدا به ما نعمت داد که با مجاهدان نبوديم، تا شاهد (آن مصيبت) باشيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (566, N'و اگر غنيمتي از جانب خدا به شما برسد، درست مثل اينکه هرگز ميان شما و آنها دوستي و مودتي نبوده، مي‌گويند: «اي کاش ما هم با آنها بوديم، و به رستگاري (و پيروزي) بزرگي مي‌رسيديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (567, N'کساني که زندگي دنيا را به آخرت فروخته‌اند، بايد در راه خدا پيکار کنند!و آن کس که در راه خدا پيکار کند، و کشته شود يا پيروز گردد، پاداش بزرگي به او خواهيم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (568, N'چرا در راه خدا، و (در راه) مردان و زنان و کودکاني که (به دست ستمگران)تضعيف شده‌اند، پيکار نمي‌کنيد؟! همان افراد (ستمديده‌اي) که مي‌گويند: «پروردگارا! ما را از اين شهر (مکه)، که اهلش ستمگرند، بيرون ببر! و از طرف خود، براي ما سرپرستي قرار ده! و از جانب خود، يار و ياوري براي ما تعيين فرما!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (569, N'کساني که ايمان دارند، در راه خدا پيکار مي‌کنند؛ و آنها که کافرند، در راه طاغوت [= بت و افراد طغيانگر]. پس شما با ياران شيطان، پيکار کنيد! (و از آنها نهراسيد!) زيرا که نقشه شيطان، (همانند قدرتش) ضعيف است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (570, N'آيا نديدي کساني را که (در مکه) به آنها گفته شد: «فعلا) دست از جهاد بداريد! و نماز را برپا کنيد! و زکات بپردازيد!» (اما آنها از اين دستور، ناراحت بودند)، ولي هنگامي که (در مدينه) فرمان جهاد به آنها داده شد، جمعي از آنان، از مردم مي‌ترسيدند، همان گونه که از خدا مي‌ترسند، بلکه بيشتر! و گفتند: «پروردگارا! چرا جهاد را بر ما مقرر داشتي؟! چرا اين فرمان را تا زمان نزديکي تأخير نينداختي؟!» به آنها بگو: «سرمايه زندگي دنيا، ناچيز است!و سراي آخرت، براي کسي که پرهيزگار باشد، بهتر است! و به اندازه رشته شکاف هسته خرمايي، به شما ستم نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (571, N'هر جا باشيد، مرگ شما را درمي‌يابد؛ هر چند در برجهاي محکم باشيد! و اگر به آنها [= منافقان‌] حسنه (و پيروزي) برسد، مي‌گويند: «اين، از ناحيه خداست.»و اگر سيئه (و شکستي) برسد، مي‌گويند: «اين، از ناحيه توست.» بگو: «همه اينها از ناحيه خداست.» پس چرا اين گروه حاضر نيستند سخني را درک کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (572, N'(آري،) آنچه از نيکيها به تو مي‌رسد، از طرف خداست؛ و آنچه از بدي به تو مي‌رسد، از سوي خود توست. و ما تو را رسول براي مردم فرستاديم؛ و گواهي خدا در اين باره، کافي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (573, N'کسي که از پيامبر اطاعت کند، خدا را اطاعت کرده؛ و کسي که سرباز زند، تو را نگهبان (و مراقب) او نفرستاديم (و در برابر او، مسؤول نيستي).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (574, N'آنها در حضور تو مي‌گويند: «فرمانبرداريم»؛ اما هنگامي که از نزد تو بيرون مي‌روند، جمعي از آنان بر خلاف گفته‌هاي تو، جلسات سري شبانه تشکيل مي‌دهند؛ آنچه را در اين جلسات مي‌گويند، خداوند مي‌نويسد. اعتنايي به آنها نکن! (و از نقشه‌هاي آنان وحشت نداشته باش!) و بر خدا توکل کن! کافي است که او يار و مدافع تو باشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (575, N'آيا درباره قرآن نمي‌انديشند؟! اگر از سوي غير خدا بود، اختلاف فراواني در آن مي‌يافتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (576, N'و هنگامي که خبري از پيروزي يا شکست به آنها برسد، (بدون تحقيق،) آن را شايع مي‌سازند؛ در حالي که اگر آن را به پيامبر و پيشوايان -که قدرت تشخيص کافي دارند- بازگردانند، از ريشه‌هاي مسائل آگاه خواهند شد. و اگر فضل و رحمت خدا بر شما نبود، جز عده کمي، همگي از شيطان پيروي مي‌کرديد (و گمراه مي‌شديد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (577, N'در راه خدا پيکار کن! تنها مسؤول وظيفه خود هستي! و مؤمنان را (بر اين کار،) تشويق نما! اميد است خداوند از قدرت کافران جلوگيري کند (حتي اگر تنها خودت به ميدان بروي)! و خداوند قدرتش بيشتر، و مجازاتش دردناکتر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (578, N'کسي که شفاعت [= تشويق و کمک‌] به کار نيکي کند، نصيبي از آن براي او خواهد بود؛ و کسي که شفاعت [= تشويق و کمک‌] به کار بدي کند، سهمي از آن خواهد داشت. و خداوند، حسابرس و نگهدار هر چيز است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (579, N'هرگاه به شما تحيت گويند، پاسخ آن را بهتر از آن بدهيد؛ يا (لااقل) به همان گونه پاسخ گوييد! خداوند حساب همه چيز را دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (580, N'خداوند، معبودي جز او نيست! و به يقين، همه شما را در روز رستاخيز -که شکي در آن نيست- جمع مي‌کند! و کيست که از خداوند، راستگوتر باشد؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (581, N'چرا درباره منافقين دو دسته شده‌ايد؟! (بعضي جنگ با آنها را ممنوع، و بعضي مجاز مي‌دانيد.) در حالي که خداوند بخاطر اعمالشان، (افکار) آنها را کاملا وارونه کرده است! آيا شما مي‌خواهيد کساني را که خداوند (بر اثر اعمال زشتشان) گمراه کرده، هدايت کنيد؟! در حالي که هر کس را خداوند گمراه کند، راهي براي او نخواهي يافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (582, N'آنان آرزو مي‌کنند که شما هم مانند ايشان کافر شويد، و مساوي يکديگر باشيد. بنابر اين، از آنها دوستاني انتخاب نکنيد، مگر اينکه (توبه کنند، و) در راه خدا هجرت نمايند. هرگاه از اين کار سر باز زنند، (و به اقدام بر ضد شما ادامه دهند،) هر جا آنها را يافتيد، اسير کنيد! و (در صورت احساس خطر) به قتل برسانيد! و از ميان آنها، دوست و يار و ياوري اختيار نکنيد!.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (583, N'مگر آنها که با همپيمانان شما، پيمان بسته‌اند؛ يا آنها که به سوي شما مي‌آيند، و از پيکار با شما، يا پيکار با قوم خود ناتوان شده‌اند؛ (نه سر جنگ با شما دارند، و نه توانايي مبارزه با قوم خود.) و اگر خداوند بخواهد، آنان را بر شما مسلط مي‌کند تا با شما پيکار کنند. پس اگر از شما کناره‌گيري کرده و با شما پيکار ننمودند، (بلکه) پيشنهاد صلح کردند، خداوند به شما اجازه نمي‌دهد که متعرض آنان شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (584, N'بزودي جمعيت ديگري را مي‌يابيد که مي‌خواهند هم از ناحيه شما در امان باشند، و هم از ناحيه قوم خودشان (که مشرکند. لذا نزد شما ادعاي ايمان مي‌کنند؛ ولي) هر زمان آنان را به سوي فتنه (و بت پرستي) بازگردانند، با سر در آن فرو مي‌روند! اگر از درگيري با شما کنار نرفتند و پيشنهاد صلح نکردند و دست از شما نکشيدند، آنها را هر جا يافتيد اسير کنيد و (يا) به قتل برسانيد! آنها کساني هستند که ما براي شما، تسلط آشکاري نسبت به آنان قرار داده‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (585, N'هيچ فرد باايماني مجاز نيست که مؤمني را به قتل برساند، مگر اينکه اين کار از روي خطا و اشتباه از او سر زند؛ (و در عين حال،) کسي که مؤمني را از روي خطا به قتل رساند، بايد يک برده مؤمن را آزاد کند و خونبهايي به کسان او بپردازد؛ مگر اينکه آنها خونبها را ببخشند. و اگر مقتول، از گروهي باشد که دشمنان شما هستند (و کافرند)، ولي مقتول باايمان بوده، (تنها) بايد يک برده مؤمن را آزاد کند (و پرداختن خونبها لازم نيست). و اگر از جمعيتي باشد که ميان شما و آنها پيماني برقرار است، بايد خونبهاي او را به کسان او بپردازد، و يک برده مؤمن (نيز) آزاد کند. و آن کس که دسترسي (به آزاد کردن برده) ندارد، دو ماه پي در پي روزه مي‌گيرد. اين، (يک نوع تخفيف، و) توبه الهي است. و خداوند، دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (586, N'و هر کس، فرد باايماني را از روي عمد به قتل برساند، مجازات او دوزخ است؛ در حالي که جاودانه در آن مي‌ماند؛ و خداوند بر او غضب مي‌کند؛ و او را از رحمتش دور مي‌سازد؛ و عذاب عظيمي براي او آماده ساخته است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (587, N'اي کساني که ايمان آورده‌ايد! هنگامي که در راه خدا گام مي‌زنيد (و به سفري براي جهاد مي‌رويد)، تحقيق کنيد! و بخاطر اينکه سرمايه ناپايدار دنيا (و غنايمي) به دست آوريد، به کسي که اظهار صلح و اسلام مي‌کند نگوييد: «مسلمان نيستي» زيرا غنيمتهاي فراواني (براي شما) نزد خداست. شما قبلا چنين بوديد؛ و خداوند بر شما منت نهاد (و هدايت شديد). پس، (بشکرانه اين نعمت بزرگ،) تحقيق کنيد! خداوند به آنچه انجام مي‌دهيد آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (588, N'(هرگز) افراد باايماني که بدون بيماري و ناراحتي، از جهاد بازنشستند، با مجاهداني که در راه خدا با مال و جان خود جهاد کردند، يکسان نيستند! خداوند، مجاهداني را که با مال و جان خود جهاد نمودند، بر قاعدان [= ترک‌کنندگان جهاد] برتري مهمي بخشيده؛ و به هر يک از اين دو گروه (به نسبت اعمال نيکشان،) خداوند وعده پاداش نيک داده، و مجاهدان را بر قاعدان، با پاداش عظيمي برتري بخشيده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (589, N'درجات (مهمي) از ناحيه خداوند، و آمرزش و رحمت (نصيب آنان مي‌گردد)؛ و (اگر لغزشهايي داشته‌اند،) خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (590, N'کساني که فرشتگان (قبض ارواح)، روح آنها را گرفتند در حالي که به خويشتن ستم کرده بودند، به آنها گفتند: «شما در چه حالي بوديد؟ (و چرا با اينکه مسلمان بوديد، در صف کفار جاي داشتيد؟!)» گفتند: «ما در سرزمين خود، تحت فشار و مستضعف بوديم.» آنها [= فرشتگان‌] گفتند: «مگر سرزمين خدا، پهناور نبود که مهاجرت کنيد؟!» آنها (عذري نداشتند، و) جايگاهشان دوزخ است، و سرانجام بدي دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (591, N'مگر آن دسته از مردان و زنان و کودکاني که براستي تحت فشار قرار گرفته‌اند (و حقيقتا مستضعفند)؛ نه چاره‌اي دارند، و نه راهي (براي نجات از آن محيط آلوده) مي‌يابند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (592, N'ممکن است خداوند، آنها را مورد عفو قرار دهد؛ و خداوند، عفو کننده و آمرزنده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (593, N'کسي که در راه خدا هجرت کند، جاهاي امن فراوان و گسترده‌اي در زمين مي‌يابد. و هر کس بعنوان مهاجرت به سوي خدا و پيامبر او، از خانه خود بيرون رود، سپس مرگش فرا رسد، پاداش او بر خداست؛ و خداوند، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (594, N'هنگامي که سفر مي‌کنيد، گناهي بر شما نيست که نماز را کوتاه کنيد اگر از فتنه (و خطر) کافران بترسيد؛ زيرا کافران، براي شما دشمن آشکاري هستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (595, N'و هنگامي که در ميان آنها باشي، و (در ميدان جنگ) براي آنها نماز را برپا کني، بايد دسته‌اي از آنها با تو (به نماز) برخيزند، و سلاحهايشان را با خود برگيرند؛ و هنگامي که سجده کردند (و نماز را به پايان رساندند)، بايد به پشت سر شما (به ميدان نبرد) بروند، و آن دسته ديگر که نماز نخوانده‌اند (و مشغول پيکار بوده‌اند)، بيايند و با تو نماز بخوانند؛ آنها بايد وسايل دفاعي و سلاحهايشان (را در حال نماز) با خود حمل کنند؛ (زيرا) کافران آرزو دارند که شما از سلاحها و متاعهاي خود غافل شويد و يکباره به شما هجوم آورند. و اگر از باران ناراحتيد، و يا بيمار (و مجروح )هستيد، مانعي ندارد که سلاحهاي خود را بر زمين بگذاريد؛ ولي وسايل دفاعي (مانند زره و خود را) با خود برداريد خداوند، عذاب خوارکننده‌اي براي کافران فراهم ساخته است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (596, N'و هنگامي که نماز را به پايان رسانديد، خدا را ياد کنيد؛ ايستاده، و نشسته، و در حالي که به پهلو خوابيده‌ايد! و هرگاه آرامش يافتيد (و حالت ترس زايل گشت)، نماز را (به طور معمول) انجام دهيد، زيرا نماز، وظيفه ثابت و معيني براي مؤمنان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (597, N'و در راه تعقيب دشمن، (هرگز) سست نشويد! (زيرا) اگر شما درد و رنج مي‌بينيد، آنها نيز همانند شما درد و رنج مي‌بينند؛ ولي شما اميدي از خدا داريد که آنها ندارند؛ و خداوند، دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (598, N'ما اين کتاب را بحق بر تو نازل کرديم؛ تا به آنچه خداوند به تو آموخته، در ميان مردم قضاوت کني؛ و از کساني مباش که از خائنان حمايت نمايي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (599, N'و از خداوند، طلب آمرزش نما! که خداوند، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (600, N'و از آنها که به خود خيانت کردند، دفاع مکن! زيرا خداوند، افراد خيانت‌پيشه گنهکار را دوست ندارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (601, N'آنها زشتکاري خود را از مردم پنهان مي‌دارند؛ اما از خدا پنهان نمي‌دارند، و هنگامي که در مجالس شبانه، سخناني که خدا راضي نبود مي‌گفتند، خدا با آنها بود، خدا به آنچه انجام مي‌دهند، احاطه دارد.')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (602, N'آري، شما همانا هستيد که در زندگي اين جهان، از آنان دفاع کرديد! اما کيست که در برابر خداوند، در روز رستاخيز از آنها دفاع کند؟! يا چه کسي است که وکيل و حامي آنها باشد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (603, N'کسي که کار بدي انجام دهد يا به خود ستم کند، سپس از خداوند طلب آمرزش نمايد، خدا را آمرزنده و مهربان خواهد يافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (604, N'و کسي که گناهي مرتکب شود، به زيان خود مرتکب شده؛ خداوند، دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (605, N'و کسي که خطا يا گناهي مرتکب شود، سپس بيگناهي را متهم سازد، بار بهتان و گناه آشکاري بر دوش گرفته است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (606, N'اگر فضل و رحمت خدا شامل حال تو نبود، گروهي از آنان تصميم داشتند تو را گمراه کنند؛ اما جز خودشان را گمراه نمي‌کنند؛ و هيچ‌گونه زياني به تو نمي‌رسانند. و خداوند، کتاب و حکمت بر تو نازل کرد؛ و آنچه را نمي‌دانستي، به تو آموخت؛ و فضل خدا بر تو (همواره) بزرگ بوده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (607, N'در بسياري از سخنان درگوشي (و جلسات محرمانه) آنها، خير و سودي نيست؛ مگر کسي که (به اين وسيله،) امر به کمک به ديگران، يا کار نيک، يا اصلاح در ميان مردم کند؛ و هر کس براي خشنودي پروردگار چنين کند، پاداش بزرگي به او خواهيم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (608, N'کسي که بعد از آشکار شدن حق، با پيامبر مخالفت کند، و از راهي جز راه مؤمنان پيروي نمايد، ما او را به همان راه که مي‌رود مي‌بريم؛ و به دوزخ داخل مي‌کنيم؛ و جايگاه بدي دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (609, N'خداوند، شرک به او را نمي‌آمرزد؛ (ولي) کمتر از آن را براي هر کس بخواهد (و شايسته بداند) مي‌آمرزد. و هر کس براي خدا همتايي قرار دهد، در گمراهي دوري افتاده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (610, N'آنچه غير از خدا مي‌خوانند، فقط بتهايي است (بي‌روح)، که هيچ اثري ندارد؛ و (يا) شيطان سرکش و ويرانگر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (611, N'خدا او را از رحمت خويش دور ساخته؛ و او گفته است: «از بندگان تو، سهم معيني خواهم گرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (612, N'و آنها را گمراه مي‌کنم! و به آرزوها سرگرم مي‌سازم! و به آنان دستور مي‌دهم که (اعمال خرافي انجام دهند، و) گوش چهارپايان را بشکافند، و آفرينش پاک خدايي را تغيير دهند! (و فطرت توحيد را به شرک بيالايند!)» و هر کس، شيطان را به جاي خدا ولي خود برگزيند، زيان آشکاري کرده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (613, N'شيطان به آنها وعده‌ها(ي دروغين) مي‌دهد؛ و به آرزوها، سرگرم مي‌سازد؛ در حالي که جز فريب و نيرنگ، به آنها وعده نمي‌دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (614, N'آنها [= پيروان شيطان‌] جايگاهشان جهنم است؛ و هيچ راه فراري ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (615, N'و کساني که ايمان آورده‌اند و اعمال صالح انجام داده‌اند، بزودي آن را در باغهايي از بهشت وارد مي‌کنيم که نهرها از زير درختانش جاري است؛ جاودانه در آن خواهند ماند. وعده حق خداوند است و کيست که در گفتار و وعده‌هايش، از خدا صادقتر باشد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (616, N'(فضيلت و برتري) به آرزوهاي شما و آرزوهاي اهل کتاب نيست؛ هر کس عمل بدي انجام دهد، کيفر داده مي‌شود؛ و کسي را جز خدا، ولي و ياور خود نخواهد يافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (617, N'و کسي که چيزي از اعمال صالح را انجام دهد، خواه مرد باشد يا زن، در حالي که ايمان داشته باشد، چنان کساني داخل بهشت مي‌شوند؛ و کمترين ستمي به آنها نخواهد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (618, N'دين و آيين چه کسي بهتر است از آن کس که خود را تسليم خدا کند، و نيکوکار باشد، و پيرو آيين خالص و پاک ابراهيم گردد؟ و خدا ابراهيم را به دوستي خود، انتخاب کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (619, N'آنچه در آسمانها و زمين است، از آن خداست؛ و خداوند به هر چيزي احاطه دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (620, N'از تو درباره حکم زنان سؤال مي‌کنند؛ بگو: «خداوند درباره آنان به شما پاسخ مي دهد: آنچه در قرآن درباره زنان يتيمي که حقوقشان را به آنها نمي‌دهيد، و مي‌خواهيد با آنها ازدواج کنيد، و نيز آنچه درباره کودکان صغير و ناتوان براي شما بيان شده است، (قسمتي از سفارشهاي خداوند در اين زمينه مي‌باشد؛ و نيز به شما سفارش مي‌کند که) با يتيمان به عدالت رفتار کنيد! و آنچه از نيکيها انجام مي‌دهيد؛ خداوند از آن آگاه است (و به شما پاداش شايسته مي‌دهد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (621, N'و اگر زني، از طغيان و سرکشي يا اعراض شوهرش، بيم داشته باشد، مانعي ندارد با هم صلح کنند (و زن يا مرد، از پاره‌اي از حقوق خود، بخاطر صلح، صرف نظر نمايد.) و صلح، بهتر است؛ اگر چه مردم (طبق غريزه حب ذات، در اين گونه موارد) بخل مي‌ورزند. و اگر نيکي کنيد و پرهيزگاري پيشه سازيد (و بخاطر صلح، گذشت نماييد)، خداوند به آنچه انجام مي‌دهيد، آگاه است (و پاداش شايسته به شما خواهد داد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (622, N'شما هرگز نمي‌توانيد (از نظر محبت قلبي) در ميان زنان، عدالت برقرار کنيد، هر چند کوشش نماييد! ولي تمايل خود را بکلي متوجه يک طرف نسازيد که ديگري را بصورت زني که شوهرش را از دست داده درآوريد! و اگر راه صلاح و پرهيزگاري پيش گيريد، خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (623, N'(اما) اگر (راهي براي اصلاح در ميان خود نيابند، و) از هم جدا شوند، خداوند هر کدام از آنها را با فضل و کرم خود، بي‌نياز مي‌کند؛ و خداوند، داراي فضل و کرم، و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (624, N'آنچه در آسمانها و آنچه در زمين است، از آن خداست. و ما به کساني که پيش از شما، کتاب آسماني به آنها داده شده بود، سفارش کرديم، (همچنين) به شما (نيز) سفارش مي‌کنيم که از (نافرماني) خدا بپرهيزيد! و اگر کافر شويد، (به خدا زياني نمي‌رسد؛ زيرا) براي خداست آنچه در آسمانها و آنچه در زمين است، و خداوند، بي‌نياز و ستوده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (625, N'براي خداست آنچه در آسمانها و زمين است؛ و کافي است که خدا، حافظ و نگاهبان آنها باشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (626, N'اي مردم! اگر او بخواهد، شما را از ميان مي‌برد و افراد ديگري را (به جاي شما) مي آورد، و خداوند، بر اين کار تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (627, N'کساني که پاداش دنيوي بخواهند، (و در قيد نتايج معنوي و اخروي نباشند، در اشتباهند؛ زيرا) پاداش دنيا و آخرت نزد خداست؛ و خداوند، شنوا و بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (628, N'اي کساني که ايمان آورده‌ايد! کاملا قيام به عدالت کنيد! براي خدا شهادت دهيد، اگر چه (اين گواهي) به زيان خود شما، يا پدر و مادر و نزديکان شما بوده باشد! (چرا که) اگر آنها غني يا فقير باشند، خداوند سزاوارتر است که از آنان حمايت کند. بنابراين، از هوي و هوس پيروي نکنيد؛ که از حق، منحرف خواهيد شد! و اگر حق را تحريف کنيد، و يا از اظهار آن، اعراض نماييد، خداوند به آنچه انجام مي‌دهيد، آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (629, N'اي کساني که ايمان آورده‌ايد! به خدا و پيامبرش، و کتابي که بر او نازل کرده، و کتب (آسماني) که پيش از اين فرستاده است، ايمان (واقعي) بياوريد کسي که خدا و فرشتگان او و کتابها و پيامبرانش و روز واپسين را انکار کند، در گمراهي دور و درازي افتاده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (630, N'کساني که ايمان آوردند، سپس کافر شدند، باز هم ايمان آوردند، و ديگربار کافر شدند، سپس بر کفر خود افزودند، خدا هرگز آنها را نخواهد بخشيد، و آنها را به راه (راست) هدايت نخواهد کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (631, N'به منافقان بشارت ده که مجازات دردناکي در انتظار آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (632, N'همانها که کافران را به جاي مؤمنان، دوست خود انتخاب مي‌کنند. آيا عزت و آبرو نزد آنان مي‌جويند؟ با اينکه همه عزتها از آن خداست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (633, N'و خداوند (اين حکم را) در قرآن بر شما نازل کرده که هرگاه بشنويد افرادي آيات خدا را انکار و استهزا مي‌کنند، با آنها ننشينيد تا به سخن ديگري بپردازند! وگرنه، شما هم مثل آنان خواهيد بود. خداوند، منافقان و کافران را همگي در دوزخ جمع مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (634, N'منافقان همانها هستند که پيوسته انتظار مي‌کشند و مراقب شما هستند؛ اگر فتح و پيروزي نصيب شما گردد، مي‌گويند: مگر ما با شما نبوديم؟ (پس ما نيز در افتخارات و غنايم شريکيم!) «و اگر بهره‌اي نصيب کافران گردد، به آنان مي‌گويند: مگر ما شما را به مبارزه و عدم تسليم در برابر مؤمنان، تشويق نمي‌کرديم؟ (پس با شما شريک خواهيم بود!)» خداوند در روز رستاخيز، ميان شما داوري مي‌کند؛ و خداوند هرگز کافران را بر مؤمنان تسلطي نداده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (635, N'منافقان مي‌خواهند خدا را فريب دهند؛ در حالي که او آنها را فريب مي‌دهد؛ و هنگامي که به نماز برمي‌خيزند، با کسالت برمي‌خيزند؛ و در برابر مردم ريا مي‌کنند؛ و خدا را جز اندکي ياد نمي‌نمايند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (636, N'آنها افراد بي‌هدفي هستند که نه سوي اينها، و نه سوي آنهايند! (نه در صف مؤمنان قرار دارند، و نه در صف کافران!) و هر کس را خداوند گمراه کند، راهي براي او نخواهي يافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (637, N'اي کساني که ايمان آورده‌ايد! غير از مؤمنان، کافران را ولي و تکيه‌گاه خود قرار ندهيد! آيا مي‌خواهيد (با اين عمل،) دليل آشکاري بر ضد خود در پيشگاه خدا قرار دهيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (638, N'منافقان در پايين‌ترين درکات دوزخ قرار دارند؛ و هرگز ياوري براي آنها نخواهي يافت! (بنابر اين، از طرح دوستي با دشمنان خدا، که نشانه نفاق است، بپرهيزيد!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (639, N'مگر آنها که توبه کنند، و جبران و اصلاح نمايند، به (دامن لطف) خدا، چنگ زنند، و دين خود را براي خدا خالص کنند؛ آنها با مؤمنان خواهند بود؛ و خداوند به افراد باايمان، پاداش عظيمي خواهد داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (640, N'خدا چه نيازي به مجازات شما دارد اگر شکرگزاري کنيد و ايمان آوريد؟ خدا شکرگزار و آگاه است. (اعمال و نيات شما را مي‌داند، و به اعمال نيک، پاداش نيک مي دهد.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (641, N'خداوند دوست ندارد کسي با سخنان خود، بديها(ي ديگران) را اظهار کند؛ مگر آن کس که مورد ستم واقع شده باشد. خداوند، شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (642, N'اگر نيکيها را آشکار يا مخفي سازيد، و از بديها گذشت نماييد، خداوند بخشنده و تواناست (و با اينکه قادر بر انتقام است، عفو و گذشت مي‌کند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (643, N'کساني که خدا و پيامبران او را انکار مي‌کنند، و مي‌خواهند ميان خدا و پيامبرانش تبعيض قائل شوند، و مي‌گويند: «به بعضي ايمان مي‌آوريم، و بعضي را انکار مي کنيم» و مي‌خواهند در ميان اين دو، راهي براي خود انتخاب کنند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (644, N'آنها کافران حقيقي‌اند؛ و براي کافران، مجازات خوارکننده‌اي فراهم ساخته‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (645, N'(ولي) کساني که به خدا و رسولان او ايمان آورده، و ميان احدي از آنها فرق نمي‌گذارند، پاداششان را خواهد داد؛ خداوند، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (646, N'اهل کتاب از تو مي‌خواهند کتابي از آسمان (يکجا) بر آنها نازل کني؛ (در حالي که اين يک بهانه است؛) آنها از موسي، بزرگتر از اين را خواستند، و گفتند: «خدا را آشکارا به ما نشان ده!» و بخاطر اين ظلم و ستم، صاعقه آنها را فرا گرفت. سپس گوساله (سامري) را، پس از آن همه دلايل روشن که براي آنها آمد، (به خدايي) انتخاب کردند، ولي ما از آن درگذشتيم (و عفو کرديم) و به موسي، برهان آشکاري داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (647, N'و کوه طور را بر فراز آنها برافراشتيم؛ و در همان حال از آنها پيمان گرفتيم، و به آنها گفتيم: «(براي توبه،) از در (بيت المقدس) با خضوع درآييد!» و (نيز) گفتيم: «روز شنبه تعدي نکنيد (و دست از کار بکشيد!)» و از آنان (در برابر همه اينها،) پيمان محکمي گرفتيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (648, N'(ولي) بخاطر پيمان‌شکني آنها، و انکار آيات خدا، و کشتن پيامبران بناحق، و بخاطر اينکه (از روي استهزا) مي‌گفتند: «بر دلهاي ما، پرده افکنده (شده و سخنان پيامبر را درک نمي‌کنيم!» رانده درگاه خدا شدند.) آري، خداوند بعلت کفرشان، بر دلهاي آنها مهر زده؛ که جز عده کمي (که راه حق مي‌پويند و لجاج ندارند،) ايمان نمي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (649, N'و (نيز) بخاطر کفرشان، و تهمت بزرگي که بر مريم زدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (650, N'و گفتارشان که: «ما، مسيح عيسي بن مريم، پيامبر خدا را کشتيم!» در حالي که نه او را کشتند، و نه بر دار آويختند؛ لکن امر بر آنها مشتبه شد. و کساني که در مورد (قتل) او اختلاف کردند، از آن در شک هستند و علم به آن ندارند و تنها از گمان پيروي مي‌کنند؛ و قطعا او را نکشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (651, N'بلکه خدا او را به سوي خود، بالا برد. و خداوند، توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (652, N'و هيچ يک از اهل کتاب نيست مگر اينکه پيش از مرگش به او [= حضرت مسيح‌] ايمان مي‌آورد؛ و روز قيامت، بر آنها گواه خواهد بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (653, N'بخاطر ظلمي که از يهود صادر شد، و (نيز) بخاطر جلوگيري بسيار آنها از راه خدا، بخشي از چيزهاي پاکيزه را که بر آنها حلال بود، حرام کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (654, N'و (همچنين) بخاطر ربا گرفتن، در حالي که از آن نهي شده بودند؛ و خوردن اموال مردم بباطل؛ و براي کافران آنها، عذاب دردناکي آماده کرده‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (655, N'ولي راسخان در علم از آنها، و مؤمنان (از امت اسلام،) به تمام آنچه بر تو نازل شده، و آنچه پيش از تو نازل گرديده، ايمان مي‌آورند. (همچنين) نمازگزاران و زکات‌دهندگان و ايمان‌آورندگان به خدا و روز قيامت، بزودي به همه آنان پاداش عظيمي خواهيم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (656, N'ما به تو وحي فرستاديم؛ همان گونه که به نوح و پيامبران بعد از او وحي فرستاديم؛ و (نيز) به ابراهيم و اسماعيل و اسحاق و يعقوب و اسباط [= بني اسرائيل‌] و عيسي و ايوب و يونس و هارون و سليمان وحي نموديم؛ و به داوود زبور داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (657, N'و پيامبراني که سرگذشت آنها را پيش از اين، براي تو باز گفته‌ايم؛ و پيامبراني که سرگذشت آنها را بيان نکرده‌ايم؛ و خداوند با موسي سخن گفت. (و اين امتياز، از آن او بود.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (658, N'پيامبراني که بشارت‌دهنده و بيم‌دهنده بودند، تا بعد از اين پيامبران، حجتي براي مردم بر خدا باقي نماند، (و بر همه اتمام حجت شود؛) و خداوند، توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (659, N'ولي خداوند گواهي مي‌دهد به آنچه بر تو نازل کرده؛ که از روي علمش نازل کرده است، و فرشتگان (نيز) گواهي مي‌دهند؛ هر چند گواهي خدا کافي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (660, N'کساني که کافر شدند، و (مردم را) از راه خدا بازداشتند، در گمراهي دوري گرفتار شده‌اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (661, N'کساني که کافر شدند، و (به خود و ديگران) ستم کردند، هرگز خدا آنها را نخواهد بخشيد، و آنان را به هيچ راهي هدايت نخواهد کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (662, N'مگر به راه دوزخ! که جاودانه در آن خواهند ماند؛ و اين کار براي خدا آسان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (663, N'اي مردم! پيامبر(ي که انتظارش را مي‌کشيديد،) حق را از جانب پروردگارتان آورد؛ به او ايمان بياوريد که براي شما بهتر است! و اگر کافر شويد، (به خدا زياني نمي رسد، زيرا) آنچه در آسمانها و زمين است از آن خداست، و خداوند، دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (664, N'اي اهل کتاب! در دين خود، غلو (و زياده روي) نکنيد! و در باره خدا، غير از حق نگوييد! مسيح عيسي بن مريم فقط فرستاده خدا، و کلمه (و مخلوق) اوست، که او را به مريم القا نمود؛ و روحي (شايسته) از طرف او بود. بنابر اين، به خدا و پيامبران او، ايمان بياوريد! و نگوييد: «(خداوند) سه‌گانه است!» (از اين سخن) خودداري کنيد که براي شما بهتر است! خدا، تنها معبود يگانه است؛ او منزه است که فرزندي داشته باشد؛ (بلکه) از آن اوست آنچه در آسمانها و در زمين است؛ و براي تدبير و سرپرستي آنها، خداوند کافي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (665, N'هرگز مسيح از اين ابا نداشت که بنده خدا باشد؛ و نه فرشتگان مقرب او (از اين ابا دارند). و آنها که از عبوديت و بندگي او، روي برتابند و تکبر کنند، بزودي همه آنها را (در قيامت) نزد خود جمع خواهد کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (666, N'اما آنها که ايمان آوردند و اعمال صالح انجام دادند، پاداششان را بطور کامل خواهد داد؛ و از فضل و بخشش خود، بر آنها خواهد افزود. و آنها را که ابا کردند و تکبر ورزيدند، مجازات دردناکي خواهد کرد؛ و براي خود، غير از خدا، سرپرست و ياوري نخواهند يافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (667, N'اي مردم! دليل روشن از طرف پروردگارتان براي شما آمد؛ و نور آشکاري به سوي شما نازل کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (668, N'اما آنها که به خدا ايمان آوردند و به (آن کتاب آسماني) چنگ زدند، بزودي همه را در رحمت و فضل خود، وارد خواهد ساخت؛ و در راه راستي، به سوي خودش هدايت مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (669, N'از تو (در باره ارث خواهران و برادران) سؤال مي‌کنند، بگو: «خداوند، حکم کلاله (خواهر و برادر) را براي شما بيان مي‌کند: اگر مردي از دنيا برود، که فرزند نداشته باشد، و براي او خواهري باشد، نصف اموالي را که به جا گذاشته، از او (ارث) مي‌برد؛ و (اگر خواهري از دنيا برود، وارث او يک برادر باشد،) او تمام مال را از آن خواهر به ارث مي‌برد، در صورتي که (ميت) فرزند نداشته باشد؛ و اگر دو خواهر (از او) باقي باشند دو سوم اموال را مي‌برند؛ و اگر برادران و خواهران با هم باشند، (تمام اموال را ميان خود تقسيم مي‌کنند؛ و) براي هر مذکر، دو برابر سهم مؤنث است. خداوند (احکام خود را) براي شما بيان مي‌کند تا گمراه نشويد؛ و خداوند به همه چيز داناست.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (670, N'اي کساني که ايمان آورده‌ايد! به پيمانها (و قراردادها) وفا کنيد! چهارپايان (و جنين آنها) براي شما حلال شده است؛ مگر آنچه بر شما خوانده مي‌شود (و استثنا خواهد شد). و به هنگام احرام، صيد را حلال نشمريد! خداوند هر چه بخواهد (و مصلحت باشد) حکم مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (671, N'اي کساني که ايمان آورده‌ايد! شعائر و حدود الهي (و مراسم حج را محترم بشمريد! و مخالفت با آنها) را حلال ندانيد! و نه ماه حرام را، و نه قربانيهاي بي‌نشان و نشاندار را، و نه آنها را که به قصد خانه خدا براي به دست آوردن فضل پروردگار و خشنودي او مي‌آيند! اما هنگامي که از احرام بيرون آمديد، صيدکردن براي شما مانعي ندارد. و خصومت با جمعيتي که شما را از آمدن به مسجد الحرام (در سال حديبيه) بازداشتند، نبايد شما را وادار به تعدي و تجاوز کند! و (همواره) در راه نيکي و پرهيزگاري با هم تعاون کنيد! و (هرگز) در راه گناه و تعدي همکاري ننماييد! و از (مخالفت فرمان) خدا بپرهيزيد که مجازات خدا شديد است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (672, N'گوشت مردار، و خون، و گوشت خوک، و حيواناتي که به غير نام خدا ذبح شوند، و حيوانات خفه‌شده، و به زجر کشته شده، و آنها که بر اثر پرت‌شدن از بلندي بميرند، و آنها که به ضرب شاخ حيوان ديگري مرده باشند، و باقيمانده صيد حيوان درنده -مگر آنکه (بموقع به آن حيوان برسيد، و) آن را سرببريد- و حيواناتي که روي بتها (يا در برابر آنها) ذبح مي‌شوند، (همه) بر شما حرام شده است؛ و (همچنين) قسمت کردن گوشت حيوان به وسيله چوبه‌هاي تير مخصوص بخت آزمايي؛ تمام اين اعمال، فسق و گناه است -امروز، کافران از (زوال) آيين شما،مأيوس شدند؛ بنابر اين، از آنها نترسيد! و از (مخالفت) من بترسيد! امروز، دين شما را کامل کردم؛ و نعمت خود را بر شما تمام نمودم؛ و اسلام را به عنوان آيين (جاودان) شما پذيرفتم- اما آنها که در حال گرسنگي، دستشان به غذاي ديگري نرسد، و متمايل به گناه نباشند، (مانعي ندارد که از گوشتهاي ممنوع بخورند؛) خداوند، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (673, N'از تو سؤال مي‌کنند چه چيزهايي براي آنها حلال شده است؟ بگو: «آنچه پاکيزه است، براي شما حلال گرديده؛ (و نيز صيد) حيوانات شکاري و سگهاي آموخته (و تربيت يافته) که از آنچه خداوند به شما تعليم داده به آنها ياد داده‌ايد، (بر شما حلال است؛) پس، از آنچه اين حيوانات براي شما (صيد مي‌کنند و) نگاه مي‌دارند، بخوريد؛ و نام خدا را (به هنگام فرستادن حيوان براي شکار،) بر آن ببريد؛ و از (معصيت) خدا بپرهيزيد که خداوند سريع الحساب است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (674, N'امروز چيزهاي پاکيزه براي شما حلال شده؛ و (همچنين) طعام اهل کتاب، براي شما حلال است؛ و طعام شما براي آنها حلال؛ و (نيز) آنان پاکدامن از مسلمانان، و آنان پاکدامن از اهل کتاب، حلالند؛ هنگامي که مهر آنها را بپردازيد و پاکدامن باشيد؛ نه زناکار، و نه دوست پنهاني و نامشروع گيريد. و کسي که انکار کند آنچه را بايد به آن ايمان بياورد، اعمال او تباه مي‌گردد؛ و در سراي ديگر، از زيانکاران خواهد بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (675, N'اي کساني که ايمان آورده‌ايد! هنگامي که به نماز مي‌ايستيد، صورت و دستها را تا آرنج بشوييد! و سر و پاها را تا مفصل [= برآمدگي پشت پا] مسح کنيد! و اگر جنب باشيد، خود را بشوييد (و غسل کنيد)! و اگر بيمار يا مسافر باشيد، يا يکي از شما از محل پستي آمده [= قضاي حاجت کرده‌]، يا با آنان تماس گرفته (و آميزش جنسي کرده‌ايد)، و آب (براي غسل يا وضو) نيابيد، با خاک پاکي تيمم کنيد! و از آن، بر صورت [= پيشاني‌] و دستها بکشيد! خداوند نمي‌خواهد مشکلي براي شما ايجاد کند؛ بلکه مي‌خواهد شما را پاک سازد و نعمتش را بر شما تمام نمايد؛ شايد شکر او را بجا آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (676, N'و به ياد آوريد نعمت خدا را بر شما، و پيماني را که با تأکيد از شما گرفت، آن زمان که گفتيد: «شنيديم و اطاعت کرديم»! و از (مخالفت فرمان) خدا بپرهيزيد که خدا، از آنچه درون سينه‌هاست، آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (677, N'اي کساني که ايمان آورده‌ايد! همواره براي خدا قيام کنيد، و از روي عدالت،گواهي دهيد! دشمني با جمعيتي، شما را به گناه و ترک عدالت نکشاند! عدالت کنيد، که به پرهيزگاري نزديکتر است! و از (معصيت) خدا بپرهيزيد، که از آنچه انجام مي‌دهيد، با خبر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (678, N'خداوند، به آنها که ايمان آورده و اعمال صالح انجام داده‌اند، وعده آمرزش و پاداش عظيمي داده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (679, N'و کساني که کافر شدند و آيات ما را تکذيب کردند، اهل دوزخند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (680, N'اي کساني که ايمان آورده‌ايد! نعمتي را که خدا به شما بخشيد، به ياد آوريد؛ آن زمان که جمعي (از دشمنان)، قصد داشتند دست به سوي شما دراز کنند (و شما را از ميان بردارند)، اما خدا دست آنها را از شما باز داشت! از خدا بپرهيزيد! و مؤمنان بايد تنها بر خدا توکل کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (681, N'خدا از بني اسرائيل پيمان گرفت. و از آنها، دوازده نقيب [= سرپرست‌] برانگيختيم. و خداوند (به آنها) گفت: «من با شما هستم! اگر نماز را برپا داريد، و زکات را بپردازيد، و به رسولان من ايمان بياوريد و آنها را ياري کنيد، و به خدا قرض الحسن بدهيد [= در راه او، به نيازمندان کمک کنيد]، گناهان شما را مي‌پوشانم [= مي بخشم‌]؛ و شما را در باغهايي از بهشت، که نهرها از زير درختانش جاري است، وارد مي‌کنم. اما هر کس از شما بعد از اين کافر شود، از راه راست منحرف گرديده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (682, N'ولي بخاطر پيمان‌شکني، آنها را از رحمت خويش دور ساختيم؛ و دلهاي آنان را سخت و سنگين نموديم؛ سخنان (خدا) را از موردش تحريف مي‌کنند؛ و بخشي از آنچه را به آنها گوشزد شده بود، فراموش کردند؛ و هر زمان، از خيانتي (تازه) از آنها آگاه مي‌شوي، مگر عده کمي از آنان؛ ولي از آنها درگذر و صرف‌نظر کن، که خداوند نيکوکاران را دوست مي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (683, N'و از کساني که ادعاي نصرانيت (و ياري مسيح) داشتند (نيز) پيمان گرفتيم؛ ولي آنها قسمت مهمي را از آنچه به آنان تذکر داده شده بود فراموش کردند؛ از اين رو در ميان آنها، تا دامنه قيامت، عداوت و دشمني افکنديم. و خداوند، (در قيامت) آنها را از آنچه انجام مي‌دادند (و نتايج آن)، آگاه خواهد ساخت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (684, N'اي اهل کتاب! پيامبر ما، که بسياري از حقايق کتاب آسماني را که شما کتمان مي‌کرديد روشن مي‌سازد، به سوي شما آمد؛ و از بسياري از آن، (که فعلا افشاي آن مصلحت نيست،) صرف نظر مي‌نمايد. (آري،) از طرف خدا، نور و کتاب آشکاري به سوي شما آمد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (685, N'خداوند به برکت آن، کساني را که از خشنودي او پيروي کنند، به راه‌هاي سلامت، هدايت مي‌کند؛ و به فرمان خود، از تاريکيها به سوي روشنايي مي‌برد؛ و آنها را به سوي راه راست، رهبري مي‌نمايد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (686, N'آنها که گفتند: «خدا، همان مسيح بن مريم است»، بطور مسلم کافر شدند؛ بگو: «اگر خدا بخواهد مسيح بن مريم و مادرش و همه کساني را که روي زمين هستند هلاک کند، چه کسي مي‌تواند جلوگيري کند؟ (آري،) حکومت آسمانها و زمين، و آنچه ميان آن دو قرار دارد از آن خداست؛ هر چه بخواهد، مي‌آفريند؛ (حتي انساني بدون پدر، مانند مسيح؛) و او، بر هر چيزي تواناست.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (687, N'يهود و نصاري گفتند: «ما، فرزندان خدا و دوستان (خاص) او هستيم.» بگو: «پس چرا شما را در برابر گناهانتان مجازات مي‌کند؟! بلکه شما هم بشري هستيد از مخلوقاتي که آفريده؛ هر کس را بخواهد (و شايسته بداند)، مي‌بخشد؛ و هر کس را بخواهد (و مستحق بداند)، مجازات مي‌کند؛ و حکومت آسمانها و زمين و آنچه در ميان آنهاست، از آن اوست؛ و بازگشت همه موجودات، به سوي اوست.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (688, N'اي اهل کتاب! رسول ما، پس از فاصله و فترتي ميان پيامبران، به سوي شما آمد؛ در حالي که حقايق را براي شما بيان مي‌کند؛ تا مبادا (روز قيامت) بگوييد: «نه بشارت دهنده‌اي به سراغ ما آمد، و نه بيم دهنده‌اي»! (هم اکنون، پيامبر) بشارت‌دهنده و بيم‌دهنده، به سوي شما آمد! و خداوند بر همه چيز تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (689, N'(به ياد آوريد) هنگامي را که موسي به قوم خود گفت: «اي قوم من! نعمت خدا را بر خود متذکر شويد هنگامي که در ميان شما، پيامبراني قرار داد؛ (و زنجير بندگي و اسارت فرعوني را شکست) و شما را حاکم و صاحب اختيار خود قرار داد؛ و به شما چيزهايي بخشيد که به هيچ يک از جهانيان نداده بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (690, N'اي قوم! به سرزمين مقدسي که خداوند براي شما مقرر داشته، وارد شويد! و به پشت سر خود بازنگرديد (و عقب گرد نکنيد) که زيانکار خواهيد بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (691, N'گفتند: «اي موسي! در آن (سرزمين)، جمعيتي (نيرومند و) ستمگرند؛ و ما هرگز وارد آن نمي‌شويم تا آنها از آن خارج شوند؛ اگر آنها از آن خارج شوند، ما وارد خواهيم شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (692, N'(ولي) دو نفر از مرداني که از خدا مي‌ترسيدند، و خداوند به آنها، نعمت (عقل و ايمان و شهامت) داده بود، گفتند: «شما وارد دروازه شهر آنان شويد! هنگامي که وارد شديد، پيروز خواهيد شد. و بر خدا توکل کنيد اگر ايمان داريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (693, N'(بني اسرائيل) گفتند: «اي موسي! تا آنها در آنجا هستند، ما هرگز وارد نخواهيم شد! تو و پروردگارت برويد و (با آنان) بجنگيد، ما همينجا نشسته‌ايم»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (694, N'(موسي) گفت: «پروردگارا! من تنها اختيار خودم و برادرم را دارم، ميان ما و اين جمعيت گنهکار، جدايي بيفکن!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (695, N'خداوند (به موسي) فرمود: «اين سرزمين (مقدس)، تا چهل سال بر آنها ممنوع است (و به آن نخواهند رسيد)؛ پيوسته در زمين (در اين بيابان)، سرگردان خواهند بود؛ و در باره (سرنوشت) اين جمعيت گنهکار، غمگين مباش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (696, N'و داستان دو فرزند آدم را بحق بر آنها بخوان: هنگامي که هر کدام، کاري براي تقرب (به پروردگار) انجام دادند؛ اما از يکي پذيرفته شد، و از ديگري پذيرفته نشد؛ (برادري که عملش مردود شده بود، به برادر ديگر) گفت: «به خدا سوگند تو را خواهم کشت!» (برادر ديگر) گفت: «(من چه گناهي دارم؟ زيرا) خدا، تنها از پرهيزگاران مي‌پذيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (697, N'اگر تو براي کشتن من، دست دراز کني، من هرگز به قتل تو دست نمي‌گشايم، چون از پروردگار جهانيان مي‌ترسم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (698, N'من مي‌خواهم تو با گناه من و خودت (از اين عمل) بازگردي (و بار هر دو گناه را به دوش کشي)؛ و از دوزخيان گردي. و همين است سزاي ستمکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (699, N'نفس سرکش، کم کم او را به کشتن برادرش ترغيب کرد؛ (سرانجام) او را کشت؛ و از زيانکاران شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (700, N'سپس خداوند زاغي را فرستاد که در زمين، جستجو (و کندوکاو) مي‌کرد؛ تا به او نشان دهد چگونه جسد برادر خود را دفن کند. او گفت: «واي بر من! آيا من نتوانستم مثل اين زاغ باشم و جسد برادرم را دفن کنم؟!» و سرانجام (از ترس رسوايي، و بر اثر فشار وجدان، از کار خود) پشيمان شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (701, N'به همين جهت، بر بني اسرائيل مقرر داشتيم که هر کس، انساني را بدون ارتکاب قتل يا فساد در روي زمين بکشد، چنان است که گويي همه انسانها را کشته؛ و هر کس، انساني را از مرگ رهايي بخشد، چنان است که گويي همه مردم را زنده کرده است. و رسولان ما، دلايل روشن براي بني اسرائيل آوردند، اما بسياري از آنها، پس از آن در روي زمين، تعدي و اسراف کردند.')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (702, N'کيفر آنها که با خدا و پيامبرش به جنگ برمي‌خيزند، و اقدام به فساد در روي زمين مي‌کنند، (و با تهديد اسلحه، به جان و مال و ناموس مردم حمله مي‌برند،) فقط اين است که اعدام شوند؛ يا به دار آويخته گردند؛ يا (چهار انگشت از) دست (راست) و پاي (چپ) آنها، بعکس يکديگر، بريده شود؛ و يا از سرزمين خود تبعيد گردند. اين رسوايي آنها در دنياست؛ و در آخرت، مجازات عظيمي دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (703, N'مگر آنها که پيش از دست يافتن شما بر آنان، توبه کنند؛ پس بدانيد (خدا توبه آنها را مي‌پذيرد؛) خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (704, N'اي کساني که ايمان آورده‌ايد! از (مخالفت فرمان) خدا بپرهيزيد! و وسيله‌اي براي تقرب به او بجوئيد! و در راه او جهاد کنيد، باشد که رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (705, N'بيقين کساني که کافر شدند، اگر تمام آنچه روي زمين است و همانند آن، مال آنها باشد و همه آن را براي نجات از کيفر روز قيامت بدهند، از آنان پذيرفته نخواهد شد؛ و مجازات دردناکي خواهند داشت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (706, N'پيوسته مي‌خواهند از آتش خارج شوند، ولي نمي‌توانند از آن خارج گردند؛ و براي آنها مجازاتي پايدار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (707, N'دست مرد دزد و زن دزد را، به کيفر عملي که انجام داده‌اند، بعنوان يک مجازات الهي، قطع کنيد! و خداوند توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (708, N'اما آن کس که پس از ستم کردن، توبه و جبران نمايد، خداوند توبه او را مي‌پذيرد؛ (و از اين مجازات؛ معاف مي‌شود، زيرا) خداوند، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (709, N'آيا نمي‌داني که حکومت و فرمانروائي آسمانها و زمين از آن خداست؟ هر کس را بخواهد (و مستحق بداند)، کيفر مي‌کند؛ و هر کس را بخواهد و شايسته بداند، مي‌بخشد؛ و خداوند بر هر چيزي قادر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (710, N'اي فرستاده (خدا)! آنها که در مسير کفر شتاب مي‌کنند و با زبان مي‌گويند: «ايمان آورديم» و قلب آنها ايمان نياورده، تو را اندوهگين نسازند! و (همچنين) گروهي از يهوديان که خوب به سخنان تو گوش مي‌دهند، تا دستاويزي براي تکذيب تو بيابند؛ آنها جاسوسان گروه ديگري هستند که خودشان نزد تو نيامده‌اند؛ آنها سخنان را از مفهوم اصليش تحريف مي‌کنند، و (به يکديگر) مي‌گويند: «اگر اين (که ما مي‌خواهيم) به شما داده شد (و محمد بر طبق خواسته شما داوري کرد،) بپذيريد، وگرنه (از او) دوري کنيد!» (ولي) کسي را که خدا (بر اثر گناهان پي‌درپي او) بخواهد مجازات کند، قادر به دفاع از او نيستي؛ آنها کساني هستند که خدا نخواسته دلهايشان را پاک کند؛ در دنيا رسوايي، و در آخرت مجازات بزرگي نصيبشان خواهد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (711, N'آنها بسيار به سخنان تو گوش مي‌دهند تا آن را تکذيب کنند؛ مال حرام فراوان مي خورند؛ پس اگر نزد تو آمدند، در ميان آنان داوري کن، يا (اگر صلاح دانستي) آنها را به حال خود واگذار! و اگر از آنان صرف‌نظر کني، به تو هيچ زياني نمي‌رسانند؛ و اگر ميان آنها داوري کني، با عدالت داوري کن، که خدا عادلان را دوست دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (712, N'چگونه تو را به داوري مي‌طلبند؟! در حالي که تورات نزد ايشان است؛ و در آن، حکم خدا هست. (وانگهي) پس از داوري‌خواستن از حکم تو، (چرا) روي مي گردانند؟! آنها مؤمن نيستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (713, N'ما تورات را نازل کرديم در حالي که در آن، هدايت و نور بود؛ و پيامبران، که در برابر فرمان خدا تسليم بودند، با آن براي يهود حکم مي‌کردند؛ و (همچنين) علما و دانشمندان به اين کتاب که به آنها سپرده شده و بر آن گواه بودند، داوري مي‌نمودند. بنابر اين، (بخاطر داوري بر طبق آيات الهي،) از مردم نهراسيد! و از من بترسيد! و آيات مرا به بهاي ناچيزي نفروشيد! و آنها که به احکامي که خدا نازل کرده حکم نمي‌کنند، کافرند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (714, N'و بر آنها [= بني اسرائيل‌] در آن [= تورات‌]، مقرر داشتيم که جان در مقابل جان، و چشم در مقابل چشم، و بيني در برابر بيني، و گوش در مقابل گوش، و دندان در برابر دندان مي‌باشد؛ و هر زخمي، قصاص دارد؛ و اگر کسي آن را ببخشد (و از قصاص، صرف‌نظر کند)، کفاره (گناهان) او محسوب مي‌شود؛ و هر کس به احکامي که خدا نازل کرده حکم نکند، ستمگر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (715, N'و بدنبال آنها [= پيامبران پيشين‌]، عيسي بن مريم را فرستاديم در حالي که کتاب تورات را که پيش از او فرستاده شده بود تصديق داشت؛ و انجيل را به او داديم که در آن، هدايت و نور بود؛ و (اين کتاب آسماني نيز) تورات را، که قبل از آن بود، تصديق مي‌کرد؛ و هدايت و موعظه‌اي براي پرهيزگاران بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (716, N'اهل انجيل [= پيروان مسيح‌] نيز بايد به آنچه خداوند در آن نازل کرده حکم کنند! و کساني که بر طبق آنچه خدا نازل کرده حکم نمي‌کنند، فاسقند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (717, N'و اين کتاب [= قرآن‌] را به حق بر تو نازل کرديم، در حالي که کتب پيشين را تصديق مي‌کند، و حافظ و نگاهبان آنهاست؛ پس بر طبق احکامي که خدا نازل کرده، در ميان آنها حکم کن! از هوي و هوسهاي آنان پيروي نکن! و از احکام الهي، روي مگردان! ما براي هر کدام از شما، آيين و طريقه روشني قرار داديم؛ و اگر خدا مي‌خواست، همه شما را امت واحدي قرارمي‌داد؛ ولي خدا مي‌خواهد شما را در آنچه به شما بخشيده بيازمايد؛ (و استعدادهاي مختلف شما را پرورش دهد). پس در نيکيها بر يکديگر سبقت جوييد! بازگشت همه شما، به سوي خداست؛ سپس از آنچه در آن اختلاف مي‌کرديد؛ به شما خبر خواهد داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (718, N'و در ميان آنها [= اهل کتاب‌]، طبق آنچه خداوند نازل کرده، داوري کن! و از هوسهاي آنان پيروي مکن! و از آنها برحذر باش، مبادا تو را از بعض احکامي که خدا بر تو نازل کرده، منحرف سازند! و اگر آنها (از حکم و داوري تو)، روي گردانند، بدان که خداوند مي‌خواهد آنان را بخاطر پاره‌اي از گناهانشان مجازات کند؛ و بسياري از مردم فاسقند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (719, N'آيا آنها حکم جاهليت را (از تو) مي‌خواهند؟! و چه کسي بهتر از خدا، براي قومي که اهل يقين هستند، حکم مي‌کند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (720, N'اي کساني که ايمان آورده‌ايد! يهود و نصاري را ولي (و دوست و تکيه‌گاه خود،) انتخاب نکنيد! آنها اولياي يکديگرند؛ و کساني که از شما با آنان دوستي کنند، از آنها هستند؛ خداوند، جمعيت ستمکار را هدايت نمي‌کند')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (721, N'(ولي) کساني را که در دلهايشان بيماري است مي‌بيني که در (دوستي با آنان)، بر يکديگر پيشي مي‌گيرند، و مي‌گويند: «مي‌ترسيم حادثه‌اي براي ما اتفاق بيفتد (و نياز به کمک آنها داشته باشيم!)» شايد خداوند پيروزي يا حادثه ديگري از سوي خود (به نفع مسلمانان) پيش بياورد؛ و اين دسته، از آنچه در دل پنهان داشتند، پشيمان گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (722, N'آنها که ايمان آورده‌اند مي‌گويند: «آيا اين (منافقان) همانها هستند که با نهايت تأکيد سوگند ياد کردند که با شما هستند؟! (چرا کارشان به اينجا رسيد؟!)» (آري،) اعمالشان نابود گشت، و زيانکار شدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (723, N'اي کساني که ايمان آورده‌ايد! هر کس از شما، از آيين خود بازگردد، (به خدا زياني نمي‌رساند؛ خداوند جمعيتي را مي‌آورد که آنها را دوست دارد و آنان (نيز) او را دوست دارند، در برابر مؤمنان متواضع، و در برابر کافران سرسخت و نيرومندند؛ آنها در راه خدا جهاد مي‌کنند، و از سرزنش هيچ ملامتگري هراسي ندارند. اين، فضل خداست که به هر کس بخواهد (و شايسته ببيند) مي‌دهد؛ و (فضل) خدا وسيع، و خداوند داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (724, N'سرپرست و ولي شما، تنها خداست و پيامبر او و آنها که ايمان آورده‌اند؛ همانها که نماز را برپا مي‌دارند، و در حال رکوع، زکات مي‌دهند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (725, N'و کساني که ولايت خدا و پيامبر او و افراد باايمان را بپذيرند، پيروزند؛ (زيرا) حزب و جمعيت خدا پيروز است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (726, N'اي کساني که ايمان آورده‌ايد! افرادي که آيين شما را به باد استهزاء و بازي مي‌گيرند -از اهل کتاب و مشرکان- ولي خود انتخاب نکنيد؛ و از خدا بپرهيزيد اگر ايمان داريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (727, N'آنها هنگامي که (اذان مي‌گوييد، و مردم را) به نماز فرا مي‌خوانيد، آن را به مسخره و بازي مي‌گيرند؛ اين بخاطر آن است که آنها جمعي نابخردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (728, N'بگو: «اي اهل کتاب! آيا به ما خرده مي‌گيريد؟ (مگر ما چه کرده‌ايم) جز اينکه به خداوند يگانه، و به آنچه بر ما نازل شده، و به آنچه پيش از اين نازل گرديده، ايمان آورده‌ايم. و اين، بخاطر آن است که بيشتر شما، از راه حق، خارج شده‌ايد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (729, N'بگو: «آيا شما را از کساني که موقعيت و پاداششان نزد خدا برتر از اين است، با خبر کنم؟ کساني که خداوند آنها را از رحمت خود دور ساخته، و مورد خشم قرار داده، (و مسخ کرده،) و از آنها، ميمونها و خوکهايي قرار داده، و پرستش بت کرده‌اند؛ موقعيت و محل آنها، بدتر است؛ و از راه راست، گمراهترند. »')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (730, N'هنگامي که نزد شما مي‌آيند، مي‌گويند: «ايمان آورده‌ايم!» (اما) با کفر وارد مي‌شوند، و با کفر خارج مي‌گردند؛ و خداوند، از آنچه کتمان مي‌کردند، آگاهتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (731, N'بسياري از آنان را مي‌بيني که در گناه و تعدي، و خوردن مال حرام، شتاب مي‌کنند! چه زشت است کاري که انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (732, N'چرا دانشمندان نصاري و علماي يهود، آنها را از سخنان گناه‌آميز و خوردن مال حرام، نهي نمي‌کنند؟! چه زشت است عملي که انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (733, N'و يهود گفتند: «دست خدا (با زنجير) بسته است.» دستهايشان بسته باد! و بخاطر اين سخن، از رحمت (الهي) دور شوند! بلکه هر دو دست (قدرت) او، گشاده است؛ هرگونه بخواهد، مي‌بخشد! ولي اين آيات، که از طرف پروردگارت بر تو نازل شده، بر طغيان و کفر بسياري از آنها مي‌افزايد. و ما در ميان آنها تا روز قيامت عداوت و دشمني افکنديم. هر زمان آتش جنگي افروختند، خداوند آن را خاموش ساخت؛ و براي فساد در زمين، تلاش مي‌کنند؛ و خداوند، مفسدان را دوست ندارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (734, N'و اگر اهل کتاب ايمان بياورند و تقوا پيشه کنند، گناهان آنها را مي‌بخشيم؛ و آنها را در باغهاي پرنعمت بهشت، وارد مي‌سازيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (735, N'و اگر آنان، تورات و انجيل و آنچه را از سوي پروردگارشان بر آنها نازل شده [= قرآن‌] برپا دارند، از آسمان و زمين، روزي خواهند خورد؛ جمعي از آنها، معتدل و ميانه‌رو هستند، ولي بيشترشان اعمال بدي انجام مي‌دهند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (736, N'اي پيامبر! آنچه از طرف پروردگارت بر تو نازل شده است، کاملا (به مردم) برسان! و اگر نکني، رسالت او را انجام نداده‌اي! خداوند تو را از (خطرات احتمالي) مردم، نگاه مي‌دارد؛ و خداوند، جمعيت کافران (لجوج) را هدايت نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (737, N'اي اهل کتاب! شما هيچ آيين صحيحي نداريد، مگر اينکه تورات و انجيل و آنچه را از طرف پروردگارتان بر شما نازل شده است، برپا داريد. ولي آنچه بر تو از سوي پروردگارت نازل شده، (نه تنها مايه بيداري آنها نمي‌گردد، بلکه) بر طغيان و کفر بسياري از آنها مي‌افزايد. بنابر اين، از اين قوم کافر، (و مخالفت آنها،) غمگين مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (738, N'آنها که ايمان آورده‌اند، و يهود و صابئان و مسيحيان، هرگاه به خداوند يگانه و روز جزا، ايمان بياورند، و عمل صالح انجام دهند، نه ترسي بر آنهاست، و نه غمگين خواهند شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (739, N'ما از بني اسرائيل پيمان گرفتيم؛ و رسولاني به سوي آنها فرستاديم؛ (ولي) هر زمان پيامبري حکمي بر خلاف هوسها و دلخواه آنها مي‌آورد، عده‌اي را تکذيب مي‌کردند؛ و عده‌اي را مي‌کشتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (740, N'گمان کردند مجازاتي در کار نخواهد بود! از اين‌رو (از ديدن حقايق و شنيدن سخنان حق،) نابينا و کر شدند؛ سپس (بيدار گشتند، و) خداوند توبه آنها را پذيرفت، ديگربار (در خواب غفلت فرو رفتند، و) بسياري از آنها کور و کر شدند؛ و خداوند، به آنچه انجام مي‌دهند، بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (741, N'آنها که گفتند: «خداوند همان مسيح بن مريم است»، بيقين کافر شدند، (با اينکه خود) مسيح گفت: اي بني اسرائيل! خداوند يگانه را، که پروردگار من و شماست، پرستش کنيد! زيرا هر کس شريکي براي خدا قرار دهد، خداوند بهشت را بر او حرام کرده است؛ و جايگاه او دوزخ است؛ و ستمکاران، يار و ياوري ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (742, N'آنها که گفتند: «خداوند، يکي از سه خداست» (نيز) بيقين کافر شدند؛ معبودي جز معبود يگانه نيست؛ و اگر از آنچه مي‌گويند دست بر ندارند، عذاب دردناکي به کافران آنها (که روي اين عقيده ايستادگي کنند،) خواهد رسيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (743, N'يا به سوي خدا بازنمي‌گردند، و از او طلب آمرزش نمي‌کنند؟ (در حالي که) خداوند آمرزنده مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (744, N'مسيح فرزند مريم، فقط فرستاده (خدا) بود؛ پيش از وي نيز، فرستادگان ديگري بودند، مادرش، زن بسيار راستگويي بود؛ هر دو، غذا مي‌خوردند؛ (با اين حال، چگونه دعوي الوهيت مسيح و پرستش مريم را داريد؟!) بنگر چگونه نشانه را براي آنها آشکار مي‌سازيم! سپس بنگر چگونه از حق بازگردانده مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (745, N'بگو: «آيا جز خدا چيزي را مي‌پرستيد که مالک سود و زيان شما نيست؟! و خداوند، شنوا و داناست.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (746, N'بگو: «اي اهل کتاب! در دين خود، غلو (و زياده روي) نکنيد! و غير از حق نگوييد! و از هوسهاي جمعيتي که پيشتر گمراه شدند و ديگران را گمراه کردند و از راه راست منحرف گشتند، پيروي ننماييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (747, N'کافران بني اسرائيل، بر زبان داوود و عيسي بن مريم، لعن (و نفرين) شدند! اين بخاطر آن بود که گناه کردند، و تجاوز مي‌نمودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (748, N'آنها از اعمال زشتي که انجام مي‌دادند، يکديگر را نهي نمي‌کردند؛ چه بدکاري انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (749, N'بسياري از آنها را مي‌بيني که کافران (و بت‌پرستان) را دوست مي‌دارند (و با آنها طرح دوستي مي‌ريزند)؛ نفس (سرکش) آنها، چه بد اعمالي از پيش براي (معاد) آنها فرستاد! که نتيجه آن، خشم خداوند بود؛ و در عذاب (الهي) جاودانه خواهند ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (750, N'و اگر به خدا و پيامبر (ص) و آنچه بر او نازل شده، ايمان مي‌آوردند، (هرگز) آنان [= کافران‌] را به دوستي اختيار نمي‌کردند؛ ولي بسياري از آنها فاسقند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (751, N'بطور مسلم، دشمنترين مردم نسبت به مؤمنان را، يهود و مشرکان خواهي يافت؛ و نزديکترين دوستان به مؤمنان را کساني مي‌يابي که مي‌گويند: «ما نصاري هستيم»؛ اين بخاطر آن است که در ميان آنها، افرادي عالم و تارک دنيا هستند؛ و آنها (در برابر حق) تکبر نمي‌ورزند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (752, N'و هر زمان آياتي را که بر پيامبر (اسلام) نازل شده بشنوند، چشمهاي آنها را مي‌بيني که (از شوق،) اشک مي‌ريزد، بخاطر حقيقتي که دريافته‌اند؛ آنها مي‌گويند: «پروردگارا! ايمان آورديم؛ پس ما را با گواهان (و شاهدان حق، در زمره ياران محمد) بنويس!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (753, N'چرا ما به خدا و آنچه از حق به ما رسيده است، ايمان نياوريم، در حالي که آرزو داريم پروردگارمان ما را در زمره صالحان قرار دهد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (754, N'خداوند بخاطر اين سخن، به آنها باغهايي از بهشت پاداش داد که از زير درختانش، نهرها جاري است؛ جاودانه در آن خواهند ماند؛ و اين است جزاي نيکوکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (755, N'و کساني که کافر شدند و آيات ما را تکذيب کردند، همانها اهل دوزخند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (756, N'اي کساني که ايمان آورده‌ايد! چيزهاي پاکيزه را که خداوند براي شما حلال کرده است، حرام نکنيد! و از حد، تجاوز ننماييد! زيرا خداوند متجاوزان را دوست نمي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (757, N'و از نعمتهاي حلال و پاکيزه‌اي که خداوند به شما روزي داده است، بخوريد! و از (مخالفت) خداوندي که به او ايمان داريد، بپرهيزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (758, N'خداوند شما را بخاطر سوگندهاي بيهوده (و خالي از اراده،) مؤاخذه نمي‌کند؛ ولي در برابر سوگندهايي که (از روي اراده) محکم کرده‌ايد، مؤاخذه مي‌نمايد. کفاره اين‌گونه قسمها، اطعام ده نفر مستمند، از غذاهاي معمولي است که به خانواده خود مي‌دهيد؛ يا لباس پوشاندن بر آن ده نفر؛ و يا آزاد کردن يک برده؛ و کسي که هيچ کدام از اينها را نيابد، سه روز روزه مي‌گيرد؛ اين، کفاره سوگندهاي شماست به هنگامي که سوگند ياد مي‌کنيد (و مخالفت مي‌نماييد). و سوگندهاي خود را حفظ کنيد (و نشکنيد!) خداوند آيات خود را اين چنين براي شما بيان مي‌کند، شايد شکر او را بجا آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (759, N'اي کساني که ايمان آورده‌ايد! شراب و قمار و بتها و ازلام [= نوعي بخت‌آزمايي ]، پليد و از عمل شيطان است، از آنها دوري کنيد تا رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (760, N'شيطان مي‌خواهد به وسيله شراب و قمار، در ميان شما عداوت و کينه ايجاد کند، و شما را از ياد خدا و از نماز بازدارد. آيا (با اين همه زيان و فساد، و با اين نهي اکيد،) خودداري خواهيد کرد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (761, N'اطاعت خدا و اطاعت پيامبر کنيد! و (از مخالفت فرمان او) بترسيد! و اگر روي برگردانيد، (مستحق مجازات خواهيد بود؛ و) بدانيد بر پيامبر ما، جز ابلاغ آشکار، چيز ديگري نيست (و اين وظيفه را در برابر شما، انجام داده است).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (762, N'بر کساني که ايمان آورده و اعمال صالح انجام داده‌اند، گناهي در آنچه خورده‌اند نيست؛ (و نسبت به نوشيدن شراب، قبل از نزول حکم تحريم، مجازات نمي‌شوند؛) اگر تقوا پيشه کنند، و ايمان بياورند، و اعمال صالح انجام دهند؛ سپس تقوا پيشه کنند و ايمان آورند؛ سپس تقوا پيشه کنند و نيکي نمايند. و خداوند، نيکوکاران را دوست مي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (763, N'اي کساني که ايمان آورده‌ايد! خداوند شما را به چيزي از شکار که (به نزديکي شما مي‌آيد، بطوري که) دستها و نيزه‌هايتان به آن مي‌رسد، مي‌آزمايد؛ تا معلوم شود چه کسي باايمان به غيب، از خدا مي‌ترسد؛ و هر کس بعد از آن تجاوز کند، مجازات دردناکي خواهد داشت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (764, N'اي کساني که ايمان آورده‌ايد! در حال احرام، شکار نکنيد، و هر کس از شما عمدا آن را به قتل برساند، بايد کفاره‌اي معادل آن از چهارپايان بدهد؛ کفاره‌اي که دو نفر عادل از شما، معادل بودن آن را تصديق کنند؛ و به صورت قرباني به (حريم) کعبه برسد؛ يا (به جاي قرباني،) اطعام مستمندان کند؛ يا معادل آن، روزه بگيرد، تا کيفر کار خود را بچشد. خداوند گذشته را عفو کرده، ولي هر کس تکرار کند، خدا از او انتقام مي‌گيرد؛ و خداوند، توانا و صاحب انتقام است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (765, N'صيد دريا و طعام آن براي شما و کاروانيان حلال است؛ تا (در حال احرام) از آن بهره‌مند شويد؛ ولي مادام که محرم هستيد، شکار صحرا براي شما حرام است؛ و از (نافرماني) خدايي که به سوي او محشور مي‌شويد، بترسيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (766, N'خداوند، کعبه -بيت الحرام- را وسيله‌اي براي استواري و سامان بخشيدن به کار مردم قرار داده؛ و همچنين ماه حرام، و قربانيهاي بي‌نشان، و قربانيهاي نشاندار را؛ اين‌گونه احکام (حساب شده و دقيق،) بخاطر آن است که بدانيد خداوند، آنچه در آسمانها و آنچه در زمين است، مي‌داند؛ و خدا به هر چيزي داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (767, N'بدانيد خدا داراي مجازات شديد، و (در عين حال) آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (768, N'پيامبر وظيفه‌اي جز رسانيدن پيام (الهي) ندارد؛ (و مسؤول اعمال شما نيست). و خداوند آنچه را آشکار، و آنچه را پنهان مي‌داريد مي‌داند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (769, N'بگو: «(هيچ‌گاه) ناپاک و پاک مساوي نيستند؛ هر چند فزوني ناپاکها، تو را به شگفتي اندازد! از (مخالفت) خدا بپرهيزيد اي صاحبان خرد، شايد رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (770, N'اي کساني که ايمان آورده‌ايد! از چيزهايي نپرسيد که اگر براي شما آشکار گردد، شما را ناراحت مي‌کند! و اگر به هنگام نزول قرآن، از آنها سؤال کنيد، براي شما آشکار مي‌شود؛ خداوند آنها را بخشيده (و ناديده گرفته) است. و خداوند، آمرزنده و بردبار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (771, N'جمعي از پيشينيان شما، از آن سئوال کردند؛ و سپس با آن به مخالفت برخاستند. (ممکن است شما هم چنين سرنوشتي پيدا کنيد.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (772, N'خداوند هيچ‌گونه «بحيره» و «سائبه» و «وصيله» و «حام» قرار نداده است [اشاره به چهار نوع از حيوانات اهلي است که در زمان جاهليت، استفاده از آنها را بعللي حرام مي‌دانستند؛ و اين بدعت، در اسلام ممنوع شد.] ولي کساني که کافر شدند، بر خدا دروغ مي‌بندند؛ و بيشتر آنها نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (773, N'و هنگامي که به آنها گفته شود: «به سوي آنچه خدا نازل کرده، و به سوي پيامبر بياييد!»، مي‌گويند: «آنچه از پدران خود يافته‌ايم، ما را بس است!»؛ آيا اگر پدران آنها چيزي نمي‌دانستند، و هدايت نيافته بودند (باز از آنها پيروي مي‌کنند)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (774, N'اي کساني که ايمان آورده‌ايد! مراقب خود باشيد! اگر شما هدايت يافته‌ايد، گمراهي کساني که گمراه شده‌اند، به شما زياني نمي‌رساند. بازگشت همه شما به سوي خداست؛ و شما را از آنچه عمل مي‌کرديد، آگاه مي‌سازد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (775, N'اي کساني که ايمان آورده‌ايد! هنگامي که مرگ يکي از شما فرا رسد، در موقع وصيت بايد از ميان شما، دو نفر عادل را به شهادت بطلبد؛ يا اگر مسافرت کرديد، و مصيبت مرگ شما فرا رسيد، (و در آن جا مسلماني نيافتيد،) دو نفر از غير خودتان را به گواهي بطلبيد، و اگر به هنگام اداي شهادت، در صدق آنها شک کرديد، آنها را بعد از نماز نگاه مي‌داريد تا سوگند ياد کنند که: «ما حاضر نيستيم حق را به چيزي بفروشيم، هر چند در مورد خويشاوندان ما باشد! و شهادت الهي را کتمان نمي‌کنيم، که از گناهکاران خواهيم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (776, N'و اگر اطلاعي حاصل شود که آن دو، مرتکب گناهي شده‌اند (و حق را کتمان کرده‌اند)، دو نفر از کساني که نسبت به ميت، اولي هستند، به جاي آنها قرار مي‌گيرند، و به خدا سوگند ياد مي‌کنند که: «گواهي ما، از گواهي آن دو، به حق نزديکتر است! و ما تجاوزي نکرده‌ايم؛ که اگر چنين کرده باشيم، از ظالمان خواهيم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (777, N'اين کار، نزديکتر است به اينکه گواهي بحق دهند، (و از خدا بترسند،) و يا (از مردم) بترسند که (دروغشان فاش گردد، و) سوگندهايي جاي سوگندهاي آنها را بگيرد. از (مخالفت) خدا بپرهيزيد، و گوش فرا دهيد! و خداوند، جمعيت فاسقان را هدايت نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (778, N'(از) روزي (بترسيد) که خداوند، پيامبران را جمع مي‌کند، و به آنها مي‌گويد: «(در برابر دعوت شما،) چه پاسخي به شما داده شد؟»، مي‌گويند: «ما چيزي نمي‌دانيم؛ تو خود، از همه اسرار نهان آگاهي.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (779, N'(به خاطر بياور) هنگامي را که خداوند به عيسي بن مريم گفت: «ياد کن نعمتي را که به تو و مادرت بخشيدم! زماني که تو را با» روح القدس «تقويت کردم؛ که در گاهواره و به هنگام بزرگي، با مردم سخن مي‌گفتي؛ و هنگامي که کتاب و حکمت و تورات و انجيل را به تو آموختم؛ و هنگامي که به فرمان من، از گل چيزي بصورت پرنده مي‌ساختي، و در آن مي‌دميدي، و به فرمان من، پرنده‌اي مي‌شد؛ و کور مادرزاد، و مبتلا به بيماري پيسي را به فرمان من، شفا مي‌دادي؛ و مردگان را (نيز) به فرمان من زنده مي‌کردي؛ و هنگامي که بني اسرائيل را از آسيب رساندن به تو، بازداشتم؛ در آن موقع که دلايل روشن براي آنها آوردي، ولي جمعي از کافران آنها گفتند: اينها جز سحر آشکار نيست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (780, N'و (به ياد آور) زماني را که به حواريون وحي فرستادم که: «به من و فرستاده من، ايمان بياوريد!» آنها گفتند: «ايمان آورديم، و گواه باش که ما مسلمانيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (781, N'در آن هنگام که حواريون گفتند: «اي عيسي بن مريم! آيا پروردگارت مي‌تواند مائده‌اي از آسمان بر ما نازل کند؟» او (در پاسخ) گفت: «از خدا بپرهيزيد اگر با ايمان هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (782, N'گفتند: «(ما نظر بدي نداريم،) مي‌خواهيم از آن بخوريم، و دلهاي ما (به رسالت تو) مطمئن گردد؛ و بدانيم به ما راست گفته‌اي؛ و بر آن، گواه باشيم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (783, N'عيسي بن مريم عرض کرد: «خداوندا! پروردگارا! از آسمان مائده‌اي بر ما بفرست! تا براي اول و آخر ما، عيدي باشد، و نشانه‌اي از تو؛ و به ما روزي ده! تو بهترين روزي دهندگاني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (784, N'خداوند (دعاي او را مستجاب کرد؛ و) فرمود: «من آن را بر شما نازل مي‌کنم؛ ولي هر کس از شما بعد از آن کافر گردد (و راه انکار پويد)، او را مجازاتي مي‌کنم که احدي از جهانيان را چنان مجازات نکرده باشم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (785, N'و آنگاه که خداوند به عيسي بن مريم مي‌گويد: «آيا تو به مردم گفتي که من و مادرم را بعنوان دو معبود غير از خدا انتخاب کنيد؟!»، او مي‌گويد: «منزهي تو! من حق ندارم آنچه را که شايسته من نيست، بگويم! اگر چنين سخني را گفته باشم، تو مي‌داني! تو از آنچه در روح و جان من است، آگاهي؛ و من از آنچه در ذات (پاک) توست، آگاه نيستم! بيقين تو از تمام اسرار و پنهانيها باخبري.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (786, N'من، جز آنچه مرا به آن فرمان دادي، چيزي به آنها نگفتم؛ (به آنها گفتم:) خداوندي را بپرستيد که پروردگار من و پروردگار شماست! و تا زماني که در ميان آنها بودم، مراقب و گواهشان بودم؛ ولي هنگامي که مرا از ميانشان برگرفتي، تو خود مراقب آنها بودي؛ و تو بر هر چيز، گواهي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (787, N'(با اين حال،) اگر آنها را مجازات کني، بندگان تواند. (و قادر به فرار از مجازات تو نيستند)؛ و اگر آنان را ببخشي، توانا و حکيمي! (نه کيفر تو نشانه بي‌حکمتي است، و نه بخشش تو نشانه ضعف!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (788, N'خداوند مي‌گويد: «امروز، روزي است که راستي راستگويان، به آنها سود مي‌بخشد؛ براي آنها باغهايي از بهشت است که نهرها از زير (درختان) آن مي‌گذرد، و تا ابد، جاودانه در آن مي‌مانند؛ هم خداوند از آنها خشنود است، و هم آنها از خدا خشنودند؛ اين، رستگاري بزرگ است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (789, N'حکومت آسمانها و زمين و آنچه در آنهاست، از آن خداست؛ و او بر هر چيزي تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (790, N'ستايش براي خداوندي است که آسمانها و زمين را آفريد، و ظلمتها و نور را پديد آورد؛ اما کافران براي پروردگار خود، شريک و شبيه قرارمي‌دهند (با اينکه دلايل توحيد و يگانگي او، در آفرينش جهان آشکار است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (791, N'او کسي است که شما را از گل آفريد؛ سپس مدتي مقرر داشت (تا انسان تکامل يابد)؛ و اجل حتمي نزد اوست (و فقط او از آن آگاه است). با اين همه، شما (مشرکان در توحيد و يگانگي و قدرت او،) ترديد مي‌کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (792, N'اوست خداوند در آسمانها و در زمين؛ پنهان و آشکار شما را مي‌داند؛ و از آنچه (انجام مي‌دهيد و) به دست مي‌آوريد، با خبر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (793, N'هيچ نشانه و آيه‌اي از آيات پروردگارشان براي آنان نمي‌آيد، مگر اينکه از آن رويگردان مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (794, N'آنان، حق را هنگامي که سراغشان آمد، تکذيب کردند! ولي بزودي خبر آنچه را به باد مسخره مي‌گرفتند، به آنان مي‌رسد؛ (و از نتايج کار خود، آگاه مي‌شوند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (795, N'آيا نديدند چقدر از اقوام پيشين را هلاک کرديم؟! اقوامي که (از شما نيرومندتر بودند؛ و) قدرتهايي به آنها داده بوديم که به شما نداديم؛ بارانهاي پي‌درپي براي آنها فرستاديم؛ و از زير (آباديهاي) آنها، نهرها را جاري ساختيم؛ (اما هنگامي که سرکشي و طغيان کردند،) آنان را بخاطر گناهانشان نابود کرديم؛ و جمعيت ديگري بعد از آنان پديد آورديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (796, N'(حتي) اگر ما نامه‌اي روي صفحه‌اي بر تو نازل کنيم، و (علاوه بر ديدن و خواندن،) آن را با دستهاي خود لمس کنند، باز کافران مي‌گويند: «اين، چيزي جز يک سحر آشکار نيست»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (797, N'(از بهانه‌هاي آنها اين بود که) گفتند: «چرا فرشته‌اي بر او نازل نشده (تا او را در دعوت مردم به سوي خدا همراهي کند؟!)» ولي اگر فرشته‌اي بفرستيم، (و موضوع، جنبه حسي و شهود پيدا کند،) کار تمام مي‌شود؛ (يعني اگر مخالفت کنند،) ديگر به آنها مهلت داده نخواهد شد (و همه هلاک مي‌شوند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (798, N'و اگر او را فرشته‌اي قرارمي‌داديم، حتما وي را بصورت انساني درمي‌آورديم؛ (باز به پندار آنان،) کار را بر آنها مشتبه مي‌ساختيم؛ همان‌طور که آنها کار را بر ديگران مشتبه مي‌سازند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (799, N'(با اين حال، نگران نباش!) جمعي از پيامبران پيش از تو را استهزا کردند؛ اما سرانجام، آنچه را مسخره مي‌کردند، دامانشان را مي‌گرفت؛ (و عذاب الهي بر آنها فرود آمد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (800, N'بگو: «روي زمين گردش کنيد! سپس بنگريد سرانجام تکذيب‌کنندگان آيات الهي چه شد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (801, N'بگو: «آنچه در آسمانها و زمين است، از آن کيست؟» بگو: «از آن خداست؛ رحمت (و بخشش) را بر خود، حتم کرده؛ (و به همين دليل،) بطور قطع همه شما را در روز قيامت، که در آن شک و ترديدي نيست، گرد خواهد آورد. (آري،) فقط کساني که سرمايه‌هاي وجود خويش را از دست داده و گرفتار خسران شدند، ايمان نمي‌آورند.')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (802, N'و براي اوست آنچه در شب و روز قرار دارد؛ و او، شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (803, N'بگو: «آيا غير خدا را ولي خود انتخاب کنم؟! (خدايي) که آفريننده آسمانها و زمين است؛ اوست که روزي مي‌دهد، و از کسي روزي نمي‌گيرد.» بگو: «من مأمورم که نخستين مسلمان باشم؛ و (خداوند به من دستور داده که) از مشرکان نباش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (804, N'بگو: «من (نيز) اگر نافرماني پروردگارم کنم، از عذاب روزي بزرگ [= روز رستاخيز] مي‌ترسم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (805, N'آن کس که در آن روز، مجازات الهي به او نرسد، خداوند او را مشمول رحمت خويش ساخته؛ و اين همان پيروزي آشکار است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (806, N'اگر خداوند زياني به تو برساند، هيچ کس جز او نمي‌تواند آن را برطرف سازد! و اگر خيري به تو رساند، او بر همه چيز تواناست؛ (و از قدرت او، هرگونه نيکي ساخته است.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (807, N'اوست که بر بندگان خود، قاهر و مسلط است؛ و اوست حکيم آگاه!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (808, N'بگو: «بالاترين گواهي، گواهي کيست؟» (و خودت پاسخ بده و) بگو: «خداوند، گواه ميان من و شماست؛ و (بهترين دليل آن اين است که) اين قرآن بر من وحي شده، تا شما و تمام کساني را که اين قرآن به آنها مي‌رسد، بيم دهم (و از مخالفت فرمان خدا بترسانم). آيا براستي شما گواهي مي‌دهيد که معبودان ديگري با خداست؟!» بگو: «من هرگز چنين گواهي نمي‌دهم». بگو: «اوست تنها معبود يگانه؛ و من از آنچه براي او شريک قرارمي‌دهيد، بيزارم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (809, N'آنان که کتاب آسماني به ايشان داده‌ايم، بخوبي او [= پيامبر] را مي‌شناسند، همان‌گونه که فرزندان خود را مي‌شناسند؛ فقط کساني که سرمايه وجود خود را از دست داده‌اند، ايمان نمي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (810, N'چه کسي ستمکارتر است از آن کس که بر خدا دروغ بسته [= همتايي براي او قائل شده‌]، و يا آيات او را تکذيب کرده است؟! مسلما ظالمان، رستگار نخواهند شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (811, N'آن روز که همه آنها را محشور مي‌کنيم؛ سپس به مشرکان مي‌گوييم: «معبودهايتان، که همتاي خدا مي‌پنداشتيد، کجايند؟» (چرا به ياري شما نمي‌شتابند؟!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (812, N'سپس پاسخ و عذر آنها، چيزي جز اين نيست که مي‌گويند: «به خداوندي که پروردگار ماست سوگند که ما مشرک نبوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (813, N'ببين چگونه به خودشان (نيز) دروغ مي‌گويند، و آنچه را بدروغ همتاي خدا مي‌پنداشتند، از دست مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (814, N'پاره‌اي از آنها به (سخنان) تو، گوش فرامي‌دهند؛ ولي بر دلهاي آنان پرده‌ها افکنده‌ايم تا آن را نفهمند؛ و در گوش آنها، سنگيني قرار داده‌ايم. و (آنها بقدري لجوجند که) اگر تمام نشانه‌هاي حق را ببينند، ايمان نمي‌آورند؛ تا آنجا که وقتي به سراغ تو مي‌آيند که با تو پرخاشگري کنند، کافران مي‌گويند: «اينها فقط افسانه‌هاي پيشينيان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (815, N'آنها ديگران را از آن بازمي‌دارند؛ و خود نيز از آن دوري مي‌کنند؛ آنها جز خود را هلاک نمي‌کنند، ولي نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (816, N'کاش (حال آنها را) هنگامي که در برابر آتش (دوزخ) ايستاده‌اند، ببيني! مي‌گويند: اي کاش (بار ديگر، به دنيا) بازگردانده مي‌شديم، و آيات پروردگارمان را تکذيب نمي‌کرديم، و از مؤمنان مي‌بوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (817, N'(آنها در واقع پشيمان نيستند،) بلکه اعمال و نياتي را که قبلا پنهان مي‌کردند، در برابر آنها آشکار شده (و به وحشت افتاده‌اند). و اگر بازگردند، به همان اعمالي که از آن نهي شده بودند بازمي‌گردند؛ آنها دروغگويانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (818, N'آنها گفتند: «چيزي جز اين زندگي دنياي ما نيست؛ و ما هرگز برانگيخته نخواهيم شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (819, N'اگر آنها را به هنگامي که در پيشگاه پروردگارشان ايستاده‌اند، ببيني! (به آنها) مي‌گويد: «آيا اين حق نيست؟» مي‌گويند: «آري، قسم به پروردگارمان (حق است!)» مي‌گويد: «پس مجازات را بچشيد به سزاي آنچه انکار مي‌کرديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (820, N'آنها که لقاي پروردگار را تکذيب کردند، مسلما زيان ديدند؛ (و اين تکذيب، ادامه مي يابد) تا هنگامي که ناگهان قيامت به سراغشان بيايد؛ مي‌گويند: «اي افسوس بر ما که درباره آن، کوتاهي کرديم!» و آنها (بار سنگين) گناهانشان را بر دوش مي‌کشند؛ چه بد باري بر دوش خواهند داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (821, N'زندگي دنيا، چيزي جز بازي و سرگرمي نيست! و سراي آخرت، براي آنها که پرهيزگارند، بهتر است! آيا نمي‌انديشيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (822, N'ما مي‌دانيم که گفتار آنها، تو را غمگين مي‌کند؛ ولي (غم مخور! و بدان که) آنها تو را تکذيب نمي‌کنند؛ بلکه ظالمان، آيات خدا را انکار مي‌نمايند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (823, N'پيش از تو نيز پيامبراني تکذيب شدند؛ و در برابر تکذيبها، صبر و استقامت کردند؛ و (در اين راه،) آزار ديدند، تا هنگامي که ياري ما به آنها رسيد. (تو نيز چنين باش! و اين، يکي از سنتهاي الهي است؛) و هيچ چيز نمي‌تواند سنن خدا را تغيير دهد؛ و اخبار پيامبران به تو رسيده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (824, N'و اگر اعراض آنها بر تو سنگين است، چنانچه بتواني نقبي در زمين بزني، يا نردباني به آسمان بگذاري (و اعماق زمين و آسمانها را جستجو کني، چنين کن) تا آيه (و نشانه ديگري) براي آنها بياوري! (ولي بدان که اين لجوجان، ايمان نمي‌آورند!) اما اگر خدا بخواهد، آنها را (به اجبار) بر هدايت جمع خواهد کرد. (ولي هدايت اجباري، چه سودي دارد؟) پس هرگز از جاهلان مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (825, N'تنها کساني (دعوت تو را) مي‌پذيرند که گوش شنوا دارند؛ اما مردگان (و آنها که روح انساني را از دست داده‌اند، ايمان نمي‌آورند؛ و) خدا آنها را (در قيامت) برمي‌انگيزد؛ سپس به سوي او، بازمي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (826, N'و گفتند: «چرا نشانه (و معجزه‌اي) از طرف پروردگارش بر او نازل نمي‌شود؟!» بگو: «خداوند، قادر است که نشانه‌اي نازل کند؛ ولي بيشتر آنها نمي‌دانند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (827, N'هيچ جنبنده‌اي در زمين، و هيچ پرنده‌اي که با دو بال خود پرواز مي‌کند، نيست مگر اينکه امتهايي همانند شما هستند. ما هيچ چيز را در اين کتاب، فرو گذار نکرديم؛ سپس همگي به سوي پروردگارشان محشور مي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (828, N'آنها که آيات ما را تکذيب کردند، کرها و لالهايي هستند که در تاريکيها قرار دارند. هر کس را خدا بخواهد (و مستحق باشد،) گمراه مي‌کند؛ و هر کس را بخواهد (و شايسته بداند،) بر راه راست قرار خواهد داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (829, N'بگو: «به من خبر دهيد اگر عذاب پروردگار به سراغ شما آيد، يا رستاخيز برپا شود، آيا (براي حل مشکلات خود،) غير خدا را مي‌خوانيد اگر راست مي‌گوييد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (830, N'(نه،) بلکه تنها او را مي‌خوانيد! و او اگر بخواهد، مشکلي را که بخاطر آن او را خوانده‌ايد، برطرف مي‌سازد؛ و آنچه را (امروز) همتاي خدا قرارمي‌دهيد، (در آن روز) فراموش خواهيد کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (831, N'ما به سوي امتهايي که پيش از تو بودند، (پيامبراني) فرستاديم؛ (و هنگامي که با اين پيامبران به مخالفت برخاستند،) آنها را با شدت و رنج و ناراحتي مواجه ساختيم؛ شايد (بيدار شوند و در برابر حق،) خضوع کنند و تسليم گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (832, N'چرا هنگامي که مجازات ما به آنان رسيد، (خضوع نکردند و) تسليم نشدند؟! بلکه دلهاي آنها قساوت پيدا کرد؛ و شيطان، هر کاري را که مي‌کردند، در نظرشان زينت داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (833, N'(آري،) هنگامي که (اندرزها سودي نبخشيد، و) آنچه را به آنها يادآوري شده بود فراموش کردند، درهاي همه چيز (از نعمتها) را به روي آنها گشوديم؛ تا (کاملا) خوشحال شدند (و دل به آن بستند)؛ ناگهان آنها را گرفتيم (و سخت مجازات کرديم)؛ در اين هنگام، همگي مأيوس شدند؛ (و درهاي اميد به روي آنها بسته شد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (834, N'و (به اين ترتيب،) دنباله (زندگي) جمعيتي که ستم کرده بودند، قطع شد. و ستايش مخصوص خداوند، پروردگار جهانيان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (835, N'بگو: «به من خبر دهيد اگر خداوند، گوش و چشمهايتان را بگيرد، و بر دلهاي شما مهر نهد (که چيزي را نفهميد)، چه کسي جز خداست که آنها را به شما بدهد؟!» ببين چگونه آيات را به گونه‌هاي مختلف براي آنها شرح مي‌دهيم، سپس آنها روي مي‌گردانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (836, N'بگو: «به من خبر دهيد اگر عذاب خدا بطور ناگهاني (و پنهاني) يا آشکارا به سراغ شما بيايد، آيا جز جمعيت ستمکار هلاک مي‌شوند؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (837, N'ما پيامبران را، جز (به عنوان) بشارت دهنده و بيم دهنده، نمي‌فرستيم؛ آنها که ايمان بياورند و (خويشتن را) اصلاح کنند، نه ترسي بر آنهاست و نه غمگين مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (838, N'و آنها که آيات ما را تکذيب کردند، عذاب (پروردگار) بخاطر نافرمانيها به آنان مي‌رسد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (839, N'بگو: «من نمي‌گويم خزاين خدا نزد من است؛ و من، (جز آنچه خدا به من بياموزد،) از غيب آگاه نيستم! و به شما نمي‌گويم من فرشته‌ام؛ تنها از آنچه به من وحي مي‌شود پيروي مي‌کنم.» بگو: «آيا نابينا و بينا مساويند؟! پس چرا نمي‌انديشيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (840, N'و به وسيله آن (قرآن)، کساني را که از روز حشر و رستاخيز مي‌ترسند، بيم ده! (روزي که در آن،) ياور و سرپرست و شفاعت‌کننده‌اي جز او [= خدا] ندارند؛ شايد پرهيزگاري پيشه کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (841, N'و کساني را که صبح و شام خدا را مي‌خوانند، و جز ذات پاک او نظري ندارند، از خود دور مکن! نه چيزي از حساب آنها بر توست، و نه چيزي از حساب تو بر آنها! اگر آنها را طرد کني، از ستمگران خواهي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (842, N'و اين چنين بعضي از آنها را با بعض ديگر آزموديم (توانگران را بوسيله فقيران)؛ تا بگويند: «آيا اينها هستند که خداوند از ميان ما (برگزيده، و) بر آنها منت گذارده (و نعمت ايمان بخشيده است؟!» آيا خداوند، شاکران را بهتر نمي‌شناسد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (843, N'هرگاه کساني که به آيات ما ايمان دارند نزد تو آيند، به آنها بگو: «سلام بر شما پروردگارتان، رحمت را بر خود فرض کرده؛ هر کس از شما کار بدي از روي ناداني کند، سپس توبه و اصلاح (و جبران) نمايد، (مشمول رحمت خدا مي‌شود چرا که) او آمرزنده مهربان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (844, N'و اين چنين آيات را برميشمريم، (تا حقيقت بر شما روشن شود،) و راه گناهکاران آشکار گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (845, N'بگو: «من از پرستش کساني که غير از خدا مي‌خوانيد، نهي شده‌ام!» بگو: «من از هوي و هوسهاي شما، پيروي نمي‌کنم؛ اگر چنين کنم، گمراه شده‌ام؛ و از هدايت‌يافتگان نخواهم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (846, N'بگو: «من دليل روشني از پروردگارم دارم؛ و شما آن را تکذيب کرده‌ايد! آنچه شما در باره آن (از نزول عذاب الهي) عجله داريد، به دست من نيست !حکم و فرمان، تنها از آن خداست! حق را از باطل جدا مي‌کند، و او بهترين جداکننده (حق از باطل) است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (847, N'بگو: «اگر آنچه درباره آن عجله داريد نزد من بود، (و به درخواست شما ترتيب‌اثر مي‌دادم، عذاب الهي بر شما نازل مي‌گشت؛) و کار ميان من و شما پايان گرفته بود؛ ولي خداوند ظالمان را بهتر مي‌شناسد (و بموقع مجازات مي‌کند.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (848, N'کليدهاي غيب، تنها نزد اوست؛ و جز او، کسي آنها را نمي‌داند. او آنچه را در خشکي و درياست مي‌داند؛ هيچ برگي (از درختي) نمي‌افتد، مگر اينکه از آن آگاه است؛ و نه هيچ دانه‌اي در تاريکيهاي زمين، و نه هيچ تر و خشکي وجود دارد، جز اينکه در کتابي آشکار [= در کتاب علم خدا] ثبت است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (849, N'او کسي است که (روح) شما را در شب (به هنگام خواب) مي‌گيرد؛ و از آنچه در روز کرده‌ايد، با خبر است؛ سپس در روز شما را (از خواب) برمي‌انگيزد؛ و (اين وضع همچنان ادامه مي‌يابد) تا سرآمد معيني فرا رسد؛ سپس بازگشت شما به سوي اوست؛ و سپس شما را از آنچه عمل مي‌کرديد، با خبر مي‌سازد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (850, N'او بر بندگان خود تسلط کامل دارد؛ و مراقباني بر شما مي‌گمارد؛ تا زماني که يکي از شما را مرگ فرا رسد؛ (در اين موقع،) فرستادگان ما جان او را مي‌گيرند؛ و آنها (در نگاهداري حساب عمر و اعمال بندگان،) کوتاهي نمي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (851, N'سپس (تمام بندگان) به سوي خدا، که مولاي حقيقي آنهاست، بازمي‌گردند. بدانيد که حکم و داوري، مخصوص اوست؛ و او، سريعترين حسابگران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (852, N'بگو: «چه کسي شما را از تاريکيهاي خشکي و دريا رهايي مي‌بخشد؟ در حالي که او را با حالت تضرع (و آشکارا) و در پنهاني مي‌خوانيد؛ (و مي‌گوييد:) اگر از اين (خطرات و ظلمتها) ما را رهايي مي‌بخشد، از شکرگزاران خواهيم بود.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (853, N'بگو: «خداوند شما را از اينها، و از هر مشکل و ناراحتي، نجات مي‌دهد؛ باز هم شما براي او شريک قرار مي‌دهيد! (و راه کفر مي‌پوييد.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (854, N'بگو: «او قادر است که از بالا يا از زير پاي شما، عذابي بر شما بفرستد؛ يا بصورت دسته‌هاي پراکنده شما را با هم بياميزد؛ و طعم جنگ (و اختلاف) را به هر يک از شما بوسيله ديگري بچشاند.» ببين چگونه آيات گوناگون را (براي آنها) بازگو مي‌کنيم! شايد بفهمند (و بازگردند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (855, N'قوم و جمعيت تو، آن (آيات الهي) را تکذيب و انکار کردند، در حالي که حق است! (به آنها) بگو: «من مسؤول (ايمان‌آوردن) شما نيستم! (وظيفه من، تنها ابلاغ رسالت است، نه اجبار شما بر ايمان.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (856, N'هر خيري (که خداوند به شما داده،) سرانجام قرارگاهي دارد، (و در موعد خود انجام ميگيرد.) و بزودي خواهيد دانست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (857, N'هرگاه کساني را ديدي که آيات ما را استهزا مي‌کنند، از آنها روي بگردان تا به سخن ديگري بپردازند! و اگر شيطان از ياد تو ببرد، هرگز پس از يادآمدن با اين جمعيت ستمگر منشين!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (858, N'و (اگر) افراد با تقوا (براي ارشاد و اندرز با آنها بنشينند)، چيزي از حساب (و گناه) آنها بر ايشان نيست؛ ولي (اين کار، بايد تنها) براي يادآوري آنها باشد، شايد (بشنوند و) تقوي پيشه کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (859, N'و رها کن کساني را که آيين (فطري) خود را به بازي و سرگرمي گرفتند، و زندگي دنيا، آنها را مغرور ساخته، و با اين (قرآن)، به آنها يادآوري نما، تا گرفتار (عواقب شوم) اعمال خود نشوند! (و در قيامت) جز خدا، نه ياوري دارند، و نه شفاعت‌کننده‌اي! و (چنين کسي) هر گونه عوضي بپردازد، از او پذيرفته نخواهد شد؛ آنها کساني هستند که گرفتار اعمالي شده‌اند که خود انجام داده‌اند؛ نوشابه‌اي از آب سوزان براي آنهاست؛ و عذاب دردناکي بخاطر اينکه کفر مي‌ورزيدند (و آيات الهي را انکار) مي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (860, N'بگو: «آيا غير از خدا، چيزي را بخوانيم (و عبادت کنيم) که نه سودي به حال مال دارد، نه زياني؛ و (به اين ترتيب،) به عقب برگرديم بعد از آنکه خداوند ما را هدايت کرده است؟! همانند کسي که بر اثر وسوسه‌هاي شيطان، در روي زمين راه را گم کرده، و سرگردان مانده است؛ در حالي که ياراني هم دارد که او را به هدايت دعوت مي‌کنند (و مي‌گويند:) به سوي ما بيا!» بگو: «تنها هدايت خداوند، هدايت است؛ و ما دستور داديم که تسليم پروردگار عالميان باشيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (861, N'و (نيز به ما فرمان داده شده به) اينکه: نماز را برپا داريد! و از او بپرهيزيد! و تنها اوست که به سويش محشور خواهيد شد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (862, N'اوست که آسمانها و زمين را بحق آفريد؛ و آن روز که (به هر چيز) مي‌گويد: «موجود باش!» موجود مي‌شود؛ سخن او، حق است؛ و در آن روز که در «صور» دميده مي‌شود، حکومت مخصوص اوست، از پنهان و آشکار با خبر است، و اوست حکيم و آگاه.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (863, N'(به خاطر بياوريد) هنگامي را که ابراهيم به پدرش [= عمويش‌] «آزر» گفت: «آيا بتهائي را معبودان خود انتخاب مي‌کني؟! من، تو و قوم تو را در گمراهي آشکاري مي‌بينم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (864, N'و اين چنين، ملکوت آسمانها و زمين (و حکومت مطلقه خداوند بر آنها) را به ابراهيم نشان داديم؛ (تا به آن استدلال کند،) و اهل يقين گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (865, N'هنگامي که (تاريکي) شب او را پوشانيد، ستاره‌اي مشاهده کرد، گفت: «اين خداي من است؟» اما هنگامي که غروب کرد، گفت: «غروب‌کنندگان را دوست ندارم !»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (866, N'و هنگامي که ماه را ديد که (سينه افق را) مي‌شکافد، گفت: «اين خداي من است؟» اما هنگامي که (آن هم) غروب کرد، گفت: «اگر پروردگارم مرا راهنمايي نکند، مسلما از گروه گمراهان خواهم بود.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (867, N'و هنگامي که خورشيد را ديد که (سينه افق را) مي‌شکافت، گفت: «اين خداي من است؟ اين (که از همه) بزرگتر است!» اما هنگامي که غروب کرد، گفت: «اي قوم من از شريکهايي که شما (براي خدا) مي‌سازيد، بيزارم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (868, N'من روي خود را به سوي کسي کردم که آسمانها و زمين را آفريده؛ من در ايمان خود خالصم؛ و از مشرکان نيستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (869, N'ولي قوم او [= ابراهيم‌]، با وي به گفتگو و ستيز پرداختند؛ گفت: «آيا درباره خدا با من گفتگو و ستيز مي‌کنيد؟! در حالي که خداوند، مرا با دلايل روشن هدايت کرده؛ و من از آنچه شما همتاي (خدا) قرار مي‌دهيد، نمي‌ترسم (و به من زياني نمي رسانند)! مگر پروردگارم چيزي را بخواهد! وسعت آگاهي پروردگارم همه چيز را در برمي‌گيرد؛ آيا متذکر (و بيدار) نمي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (870, N'چگونه من از بتهاي شما بترسم؟! در حالي که شما از اين نمي‌ترسيد که براي خدا، همتايي قرار داده‌ايد که هيچ گونه دليلي درباره آن، بر شما نازل نکرده است! (راست بگوييد) کدام يک از اين دو دسته (بت‌پرستان و خداپرستان)، شايسته‌تر به ايمني (از مجازات) هستند اگر مي‌دانيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (871, N'(آري،) آنها که ايمان آوردند، و ايمان خود را با شرک و ستم نيالودند، ايمني تنها از آن آنهاست؛ و آنها هدايت‌يافتگانند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (872, N'اينها دلايل ما بود که به ابراهيم در برابر قومش داديم! درجات هر کس را بخواهيم (و شايسته بدانيم،) بالا مي‌بريم؛ پروردگار تو، حکيم و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (873, N'و اسحاق و يعقوب را به او [= ابراهيم‌] بخشيديم؛ و هر دو را هدايت کرديم؛ و نوح را (نيز) پيش از آن هدايت نموديم؛ و از فرزندان او، داوود و سليمان و ايوب و يوسف و موسي و هارون را (هدايت کرديم)؛ اين‌گونه نيکوکاران را پاداش مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (874, N'و (همچنين) زکريا و يحيي و عيسي و الياس را؛ همه از صالحان بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (875, N'و اسماعيل و اليسع و يونس و لوط را؛ و همه را بر جهانيان برتري داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (876, N'و از پدران و فرزندان و برادران آنها (افرادي را برتري داديم) و برگزيديم و به راه راست، هدايت نموديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (877, N'اين، هدايت خداست؛ که هر کس از بندگان خود را بخواهد با آن راهنمايي مي‌کند! و اگر آنها مشرک شوند، اعمال (نيکي) که انجام داده‌اند، نابود مي‌گردد (و نتيجه‌اي از آن نمي‌گيرند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (878, N'آنها کساني هستند که کتاب و حکم و نبوت به آنان داديم؛ و اگر (بفرض) نسبت به آن کفر ورزند، (آيين حق زمين نمي‌ماند؛ زيرا) کسان ديگري را نگاهبان آن مي‌سازيم که نسبت به آن، کافر نيستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (879, N'آنها کساني هستند که خداوند هدايتشان کرده؛ پس به هدايت آنان اقتدا کن! (و) بگو: «در برابر اين (رسالت و تبليغ)، پاداشي از شما نمي‌طلبم! اين (رسالت)، چيزي جز يک يادآوري براي جهانيان نيست! (اين وظيفه من است)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (880, N'آنها خدا را درست نشناختند که گفتند: «خدا، هيچ چيز بر هيچ انساني، نفرستاده است!» بگو: «چه کسي کتابي را که موسي آورد، نازل کرد؟! کتابي که براي مردم، نور و هدايت بود؛ 0اما شما) آن را بصورت پراکنده قرارمي‌دهيد؛ قسمتي را آشکار، و قسمت زيادي را پنهان مي‌داريد؛ و مطالبي به شما تعليم داده شده که نه شما و نه پدرانتان، از آن با خبر نبوديد!» بگو: «خدا!» سپس آنها را در گفتگوهاي لجاجت‌آميزشان رها کن، تا بازي کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (881, N'و اين کتابي است که ما آن را نازل کرديم؛ کتابي است پربرکت، که آنچه را پيش از آن آمده، تصديق مي‌کند؛ (آن را فرستاديم تا مردم را به پاداشهاي الهي، بشارت دهي،) و تا (اهل) ام‌القري [= مکه‌] و کساني را که گرد آن هستند، بترساني! (يقين بدان) آنها که به آخرت ايمان دارند، و به آن ايمان مي‌آورند؛ و بر نمازهاي خويش، مراقبت مي کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (882, N'چه کسي ستمکارتر است از کسي که دروغي به خدا ببندد، يا بگويد: «بر من، وحي فرستاده شده»، در حالي که به او وحي نشده است، و کسي که بگويد: «من نيز همانند آنچه خدا نازل کرده است، نازل مي‌کنم»؟! و اگر ببيني هنگامي که (اين) ظالمان در شدايد مرگ فرو رفته‌اند، و فرشتگان دستها را گشوده، به آنان مي‌گويند: «جان خود را خارج سازيد! امروز در برابر دروغهايي که به خدا بستيد و نسبت به آيات او تکبر ورزيديد، مجازات خوارکننده‌اي خواهيد ديد»! (به حال آنها تأسف خواهي خورد)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (883, N'و (روز قيامت به آنها گفته مي‌شود:) همه شما تنها به سوي ما بازگشت نموديد، همان‌گونه که روز اول شما را آفريديم! و آنچه را به شما بخشيده بوديم، پشت سر گذارديد! و شفيعاني را که شريک در شفاعت خود مي‌پنداشتيد، با شما نمي‌بينيم! پيوندهاي شما بريده شده است؛ و تمام آنچه را تکيه‌گاه خود تصور مي‌کرديد، از شما دور و گم شده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (884, N'خداوند، شکافنده دانه و هسته است؛ زنده را از مرده خارج مي‌سازد، و مرده را از زنده بيرون مي‌آورد؛ اين است خداي شما! پس چگونه از حق منحرف مي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (885, N'او شکافنده صبح است؛ و شب را مايه آرامش، و خورشيد و ماه را وسيله حساب قرار داده است؛ اين، اندازه‌گيري خداوند تواناي داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (886, N'او کسي است که ستارگان را براي شما قرار داد، تا در تاريکيهاي خشکي و دريا، به وسيله آنها راه يابيد! ما نشانه‌ها(ي خود) را براي کساني که مي‌دانند، (و اهل فکر و انديشه‌اند) بيان داشتيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (887, N'او کسي است که شما را از يک نفس آفريد! و شما دو گروه هستيد: بعضي پايدار (از نظر ايمان يا خلقت کامل)، و بعضي ناپايدار؛ ما آيات خود را براي کساني که مي‌فهمند، تشريح نموديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (888, N'او کسي است که از آسمان، آبي نازل کرد، و به وسيله آن، گياهان گوناگون رويانديم؛ و از آن، ساقه‌ها و شاخه‌هاي سبز، خارج ساختيم؛ و از آنها دانه‌هاي متراکم، و از شکوفه نخل، شکوفه‌هايي با رشته‌هاي باريک بيرون فرستاديم؛ و باغهايي از انواع انگور و زيتون و انار، (گاه) شبيه به يکديگر، و (گاه) بي‌شباهت! هنگامي که ميوه مي دهد، به ميوه آن و طرز رسيدنش بنگريد که در آن، نشانه‌هايي (از عظمت خدا) براي افراد باايمان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (889, N'آنان براي خدا همتاياني از جن قرار دادند، در حالي که خداوند همه آنها را آفريده است؛ و براي خدا، به دروغ و از روي جهل، پسران و دختراني ساختند؛ منزه است خدا، و برتر است از آنچه توصيف مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (890, N'او پديد آورنده آسمانها و زمين است؛ چگونه ممکن است فرزندي داشته باشد؟! حال آنکه همسري نداشته، و همه چيز را آفريده؛ و او به همه چيز داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (891, N'(آري،) اين است پروردگار شما! هيچ معبودي جز او نيست؛ آفريدگار همه چيز است؛ او را بپرستيد و او نگهبان و مدبر همه موجودات است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (892, N'چشمها او را نمي‌بينند؛ ولي او همه چشمها را مي‌بيند؛ و او بخشنده (انواع نعمتها، و با خبر از دقايق موجودات،) و آگاه (از همه) چيز است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (893, N'دلايل روشن از طرف پروردگارتان براي شما آمد؛ کسي که (به وسيله آن، حق را) ببيند، به سود خود اوست؛ و کسي که از ديدن آن چشم بپوشد، به زيان خودش مي‌باشد؛ و من نگاهبان شما نيستم (و شما را بر قبول ايمان مجبور نمي‌کنم)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (894, N'و اينچنين آيات (خود) را تشريح مي‌کنيم؛ بگذار آنها بگويند: «تو درس خوانده‌اي (و آنها را از ديگري آموخته‌اي)»! مي‌خواهيم آن را براي کساني که آماده درک حقايقند، روشن سازيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (895, N'از آنچه که از سوي پروردگارت بر تو وحي شده، پيروي کن! هيچ معبودي جز او نيست! و از مشرکان، روي بگردان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (896, N'اگر خدا مي‌خواست، (همه به اجبار ايمان مي‌آوردند،) و هيچ يک مشرک نمي‌شدند؛ و ما تو را مسؤول (اعمال) آنها قرار نداده‌ايم؛ و وظيفه نداري آنها را (به ايمان) مجبور سازي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (897, N'(به معبود) کساني که غير خدا را مي‌خوانند دشنام ندهيد، مبادا آنها (نيز) از روي (ظلم و) جهل، خدا را دشنام دهند! اينچنين براي هر امتي عملشان را زينت داديم سپس بازگشت همه آنان به سوي پروردگارشان است؛ و آنها را از آنچه عمل مي‌کردند، آگاه مي‌سازد (و پاداش و کيفر مي‌دهد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (898, N'با نهايت اصرار، به خدا سوگند ياد کردند که اگر نشانه‌اي [= معجزه‌اي‌] براي آنان بيايد، حتما به آن ايمان مي‌آورند؛ بگو: «معجزات فقط از سوي خداست (و در اختيار من نيست که به ميل شما معجزه‌اي بياورم)؛ و شما از کجا مي‌دانيد که هرگاه معجزه‌اي بيايد (ايمان مي‌آورند؟ خير،) ايمان نمي‌آورند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (899, N'و ما دلها و چشمهاي آنها را واژگونه مي‌سازيم؛ (آري آنها ايمان نمي‌آورند) همان‌گونه که در آغاز، به آن ايمان نياوردند! و آنان را در حال طغيان و سرکشي، به خود وامي‌گذاريم تا سرگردان شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (900, N'(و حتي) اگر فرشتگان را بر آنها نازل مي‌کرديم، و مردگان با آنان سخن مي‌گفتند، و همه چيز را در برابر آنها جمع مي‌نموديم، هرگز ايمان نمي‌آوردند؛ مگر آنکه خدا بخواهد! ولي بيشتر آنها نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (901, N'اينچنين در برابر هر پيامبري، دشمني از شياطين انس و جن قرار داديم؛ آنها بطور سري (و درگوشي) سخنان فريبنده و بي‌اساس (براي اغفال مردم) به يکديگر مي‌گفتند؛ و اگر پروردگارت مي‌خواست، چنين نمي‌کردند؛ (و مي‌توانست جلو آنها را بگيرد؛ ولي اجبار سودي ندارد.) بنابر اين، آنها و تهمتهايشان را به حال خود واگذار!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (902, N'نتيجه (وسوسه‌هاي شيطان و تبليغات شيطان‌صفتان) اين خواهد شد که دلهاي منکران قيامت، به آنها متمايل گردد؛ و به آن راضي شوند؛ و هر گناهي که بخواهند، انجام دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (903, N'(با اين حال،) آيا غير خدا را به داوري طلبم؟! در حالي که اوست که اين کتاب آسماني را، که همه چيز در آن آمده، به سوي شما فرستاده است؛ و کساني که به آنها کتاب آسماني داده‌ايم مي‌دانند اين کتاب، بحق از طرف پروردگارت نازل شده؛ بنابر اين از ترديدکنندگان مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (904, N'و کلام پروردگار تو، با صدق و عدل، به حد تمام رسيد؛ هيچ کس نمي‌تواند کلمات او را دگرگون سازد؛ و او شنونده داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (905, N'اگر از بيشتر کساني که در روي زمين هستند اطاعت کني، تو را از راه خدا گمراه مي کنند؛ (زيرا) آنها تنها از گمان پيروي مي‌نمايند، و تخمين و حدس (واهي) مي‌زنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (906, N'پروردگارت به کساني که از راه او گمراه گشته‌اند، آگاهتر است؛ و همچنين از کساني که هدايت يافته‌اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (907, N'از (گوشت) آنچه نام خدا (هنگام سر بريدن) بر آن گفته شده، بخوريد (و غير از آن نخوريد) اگر به آيات او ايمان داريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (908, N'چرا از چيزها [= گوشتها]ئي که نام خدا بر آنها برده شده نمي‌خوريد؟! در حالي که (خداوند) آنچه را بر شما حرام بوده، بيان کرده است! مگر اينکه ناچار باشيد؛ (که در اين صورت، خوردن از گوشت آن حيوانات جايز است.) و بسياري از مردم، به خاطر هوي و هوس و بي‌دانشي، (ديگران را) گمراه مي‌سازند؛ و پروردگارت، تجاوزکاران را بهتر مي‌شناسد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (909, N'گناهان آشکار و پنهان را رها کنيد! زيرا کساني که گناه مي‌کنند، بزودي در برابر آنچه مرتکب مي‌شدند، مجازات خواهند شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (910, N'و از آنچه نام خدا بر آن برده نشده، نخوريد! اين کار گناه است؛ و شياطين به دوستان خود مطالبي مخفيانه القا مي‌کنند، تا با شما به مجادله برخيزند؛ اگر از آنها اطاعت کنيد، شما هم مشرک خواهيد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (911, N'آيا کسي که مرده بود، سپس او را زنده کرديم، و نوري برايش قرار داديم که با آن در ميان مردم راه برود، همانند کسي است که در ظلمتها باشد و از آن خارج نگردد؟! اين گونه براي کافران، اعمال (زشتي) که انجام مي‌دادند، تزيين شده (و زيبا جلوه کرده) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (912, N'و (نيز) اين گونه در هر شهر و آبادي، بزرگان گنهکاري قرار داديم؛ (افرادي که همه گونه قدرت در اختيارشان گذارديم؛ اما آنها سوء استفاده کرده، و راه خطا پيش گرفتند؛) و سرانجام کارشان اين شد که به مکر (و فريب مردم) پرداختند؛ ولي تنها خودشان را فريب مي‌دهند و نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (913, N'و هنگامي که آيه‌اي براي آنها بيايد، مي‌گويند: «ما هرگز ايمان نمي‌آوريم، مگر اينکه همانند چيزي که به پيامبران خدا داده شده، به ما هم داده شود!» خداوند آگاهتر است که رسالت خويش را کجا قرار دهد! بزودي کساني که مرتکب گناه شدند، (و مردم را از راه حق منحرف ساختند،) در مقابل مکر (و فريب و نيرنگي) که مي‌کردند، گرفتار حقارت در پيشگاه خدا، و عذاب شديد خواهند شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (914, N'آن کس را که خدا بخواهد هدايت کند، سينه‌اش را براي (پذيرش) اسلام، گشاده مي سازد؛ و آن کس را که بخاطر اعمال خلافش بخواهد گمراه سازد، سينه‌اش را آنچنان تنگ مي‌کند که گويا مي‌خواهد به آسمان بالا برود؛ اين گونه خداوند پليدي را بر افرادي که ايمان نمي‌آورند قرار مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (915, N'و اين راه مستقيم (و سنت جاويدان) پروردگار توست؛ ما آيات خود را براي کساني که پند مي‌گيرند، بيان کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (916, N'براي آنها (در بهشت) خانه امن و امان نزد پروردگارشان خواهد بود؛ و او، ولي و ياور آنهاست بخاطر اعمال (نيکي) که انجام مي‌دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (917, N'در آن روز که (خدا) همه آنها را جمع و محشور ميسازد، (مي‌گويد:) اي جمعيت شياطين و جن! شما افراد زيادي از انسانها را گمراه ساختيد! دوستان و پيروان آنها از ميان انسانها مي‌گويند: «پروردگارا! هر يک از ما دو گروه [= پيشوايان و پيروان‌گمراه‌] از ديگري استفاده کرديم؛ (ما به لذات هوس آلود و زودگذر رسيديم؛ و آنها بر ما حکومت کردند؛) و به اجلي که براي ما مقرر داشته بودي رسيديم.» (خداوند) مي‌گويد: «آتش جايگاه شماست؛ جاودانه در آن خواهيد ماند، مگر آنچه خدا بخواهد» پروردگار تو حکيم و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (918, N'ما اين‌گونه بعضي از ستمگران را به بعضي ديگر وامي‌گذاريم به سبب اعمالي که انجام مي‌دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (919, N'(در آن روز به آنها مي‌گويد:) اي گروه جن و انس! آيا رسولاني از شما به سوي شما نيامدند که آيات مرا برايتان بازگو مي‌کردند، و شما را از ملاقات چنين روزي بيم مي دادند؟! آنها مي‌گويند: «بر ضد خودمان گواهي مي‌دهيم؛ (آري،) ما بد کرديم)» و زندگي (پر زرق و برق) دنيا آنها را فريب داد؛ و به زيان خود گواهي مي‌دهند که کافر بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (920, N'اين بخاطر آن است که پروردگارت هيچ‌گاه (مردم) شهرها و آباديها را بخاطر ستمهايشان در حال غفلت و بي‌خبري هلاک نمي‌کند. (بلکه قبلا رسولاني براي آنها مي‌فرستد.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (921, N'و براي هر يک (از اين دو دسته)، درجات (و مراتبي) است از آنچه عمل کردند؛ و پروردگارت از اعمالي که انجام مي‌دهند، غافل نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (922, N'پروردگارت بي‌نياز و مهربان است؛ (پس به کسي ستم نمي‌کند؛ بلکه همه، نتيجه اعمال خود را مي‌گيرند؛) اگر بخواهد، همه شما را مي‌برد؛ سپس هر کس را بخواهد جانشين شما مي‌سازد؛ همان‌طور که شما را از نسل اقوام ديگري به وجود آورد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (923, N'آنچه به شما وعده داده مي‌شود، يقينا مي‌آيد؛ و شما نمي‌توانيد (خدا را) ناتوان سازيد (و از عدالت و کيفر او فرار کنيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (924, N'بگو: «اي قوم من! هر کار در قدرت داريد بکنيد! من (هم به وظيفه خود) عمل مي‌کنم؛ اما بزودي خواهيد دانست چه کسي سرانجام نيک خواهد داشت (و پيروزي با چه کسي است! اما) به يقين، ظالمان رستگار نخواهند شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (925, N'آنها [= مشرکان‌] سهمي از آنچه خداوند از زراعت و چهارپايان آفريده، براي او قرار دادند؛ (و سهمي براي بتها!) و بگمان خود گفتند: «اين مال خداست! و اين هم مال شرکاي ما [= يعني بتها] است!» آنچه مال شرکاي آنها بود، به خدا نمي‌رسيد؛ ولي آنچه مال خدا بود، به شرکايشان مي‌رسيد! (آري، اگر سهم بتها با کمبودي مواجه مي‌شد، مال خدا را به بتها مي‌دادند؛ اما عکس آن را مجاز نمي‌دانستند!) چه بد حکم مي‌کنند (که علاوه بر شرک، حتي خدا را کمتر از بتها مي‌دانند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (926, N'همين‌گونه شرکاي آنها [= بتها]، قتل فرزندانشان را در نظرشان جلوه دادند؛ (کودکان خود را قرباني بتها مي‌کردند، و افتخار مي‌نمودند!) سرانجام آنها را به هلاکت افکندند؛ و آيينشان را بر آنان مشتبه ساختند. و اگر خدا مي‌خواست، چنين نمي‌کردند؛ (زيرا مي‌توانست جلو آنان را بگيرد؛ ولي اجبار سودي ندارد.) بنابر اين، آنها و تهمتهايشان را به حال خود واگذار (و به آنها اعتنا مکن)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (927, N'و گفتند: «اين قسمت از چهارپايان و زراعت (که مخصوص بتهاست، براي همه) ممنوع است؛ و جز کساني که ما بخواهيم -به گمان آنها- نبايد از آن بخورند! و (اينها) چهارپاياني است که سوارشدن بر آنها (بر ما) حرام شده است!» و (نيز) چهارپاياني (بود) که (هنگام ذبح،) نام خدا را بر آن نمي‌بردند، و به خدا دروغ مي‌بستند؛ (و مي‌گفتند: «اين احکام، همه از ناحيه اوست.») بزودي (خدا) کيفر افتراهاي آنها را مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (928, N'و گفتند: «آنچه (از بچه) در شکم اين حيوانات است، مخصوص مردان ماست؛ و بر همسران ما حرام است! اما اگر مرده باشد [= مرده متولد شود]، همگي در آن شريکند.» بزودي (خدا) کيفر اين توصيف (و احکام دروغين) آنها را مي‌دهد؛ او حکيم و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (929, N'به يقين آنها که فرزندان خود را از روي جهل و ناداني کشتند، گرفتار خسران شدند؛ (زيرا) آنچه را خدا به آنها روزي داده بود، بر خود تحريم کردند؛ و بر خدا افترا بستند. آنها گمراه شدند؛ و (هرگز) هدايت نيافته بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (930, N'اوست که باغهاي معروش [= باغهايي که درختانش روي داربست‌ها قرار دارد]، و باغهاي غيرمعروش [= باغهايي که نياز به داربست ندارد] را آفريد؛ همچنين نخل و انواع زراعت را، که از نظر ميوه و طعم با هم متفاوتند؛ و (نيز) درخت زيتون و انار را، که از جهتي با هم شبيه، و از جهتي تفاوت دارند؛ (برگ و ساختمان ظاهريشان شبيه يکديگر است، در حالي که طعم ميوه آنها متفاوت مي‌باشد.) از ميوه آن، به هنگامي که به ثمر مي‌نشيند، بخوريد! و حق آن را به هنگام درو، بپردازيد! و اسراف نکنيد، که خداوند مسرفان را دوست ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (931, N'(او کسي است که) از چهارپايان، براي شما حيوانات باربر، و حيوانات کوچک (براي منافع ديگر) آفريد؛ از آنچه به شما روزي داده است، بخوريد! و از گامهاي شيطان پيروي ننماييد، که او دشمن آشکار شماست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (932, N'هشت جفت از چهارپايان (براي شما) آفريد؛ از ميش دو جفت، و از بز دو جفت؛ بگو: «آيا خداوند نرهاي آنها را حرام کرده، يا ماده‌ها را؟ يا آنچه شکم ماده‌ها در برگرفته؟ اگر راست مي‌گوييد (و بر تحريم اينها دليلي داريد)، به من خبر دهيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (933, N'و از شتر يک جفت، و از گاو هم يک جفت (براي شما آفريد)؛ بگو: «کداميک از اينها را خدا حرام کرده است؟ نرها يا ماده‌ها را؟ يا آنچه را شکم ماده‌ها دربرگرفته؟ يا هنگامي که خدا شما را به اين موضوع توصيه کرد، شما گواه (بر اين تحريم) بوديد؟! پس چه کسي ستمکارتر است از آن کس که بر خدا دروغ مي‌بندد ، تا مردم را از روي جهل گمراه سازد؟! خداوند هيچ گاه ستمگران را هدايت نمي‌کند»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (934, N'بگو: «در آنچه بر من وحي شده، هيچ غذاي حرامي نمي‌يابم؛ بجز اينکه مردار باشد، يا خوني که (از بدن حيوان) بيرون ريخته، يا گوشت خوک -که اينها همه پليدند- يا حيواني که به گناه، هنگام سر بريدن، نام غير خدا [= نام بتها] بر آن برده شده است.» اما کسي که مضطر (به خوردن اين محرمات) شود، بي آنکه خواهان لذت باشد و يا زياده روي کند (گناهي بر او نيست)؛ زيرا پروردگارت، آمرزنده مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (935, N'و بر يهوديان، هر حيوان ناخن‌دار [= حيواناتي که سم يکپارچه دارند] را حرام کرديم؛ و از گاو و گوسفند، پيه و چربيشان را بر آنان تحريم نموديم؛ مگر چربيهايي که بر پشت آنها قرار دارد، و يا در دو طرف پهلوها، و يا آنها که با استخوان آميخته است؛ اين را بخاطر ستمي که مي‌کردند به آنها کيفر داديم؛ و ما راست مي‌گوييم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (936, N'اگر تو را تکذيب کنند (و اين حقايق را نپذيرند)، به آنها بگو: «پروردگار شما، رحمت گسترده‌اي دارد؛ اما مجازات او هم از مجرمان دفع شدني نيست! (و اگر ادامه دهيد کيفر شما حتمي است)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (937, N'بزودي مشرکان (براي تبرئه خويش) مي‌گويند: «اگر خدا مي‌خواست، نه ما مشرک مي‌شديم و نه پدران ما؛ و نه چيزي را تحريم مي‌کرديم!» کساني که پيش از آنها بودند نيز، همين گونه دروغ مي‌گفتند؛ و سرانجام (طعم) کيفر ما را چشيدند. بگو: «آيا دليل روشني (بر اين موضوع) داريد؟ پس آن را به ما نشان دهيد؟ شما فقط از پندارهاي بي‌اساس پيروي مي‌کنيد، و تخمينهاي نابجا مي‌زنيد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (938, N'بگو: «دليل رسا (و قاطع) براي خداست (دليلي که براي هيچ کس بهانه‌اي باقي نمي‌گذارد). و اگر او بخواهد، همه شما را (به اجبار) هدايت مي‌کند. (ولي چون هدايت اجباري بي‌ثمر است، اين کار را نمي‌کند.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (939, N'بگو: «گواهان خود را، که گواهي مي‌دهند خداوند اينها را حرام کرده است، بياوريد!» اگر آنها (بدروغ) گواهي دهند، تو با آنان (همصدا نشو! و) گواهي نده! و از هوي و هوس کساني که آيات ما را تکذيب کردند، و کساني که به آخرت ايمان ندارند و براي خدا شريک قائلند، پيروي مکن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (940, N'بگو: «بياييد آنچه را پروردگارتان بر شما حرام کرده است برايتان بخوانم: اينکه چيزي را شريک خدا قرار ندهيد! و به پدر و مادر نيکي کنيد! و فرزندانتان را از (ترس) فقر، نکشيد! ما شما و آنها را روزي مي‌دهيم؛ و نزديک کارهاي زشت نرويد، چه آشکار باشد چه پنهان! و انساني را که خداوند محترم شمرده، به قتل نرسانيد! مگر بحق (و از روي استحقاق)؛ اين چيزي است که خداوند شما را به آن سفارش کرده، شايد درک کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (941, N'و به مال يتيم، جز به بهترين صورت (و براي اصلاح)، نزديک نشويد، تا به حد رشد خود برسد! و حق پيمانه و وزن را بعدالت ادا کنيد! -هيچ کس را، جز بمقدار تواناييش، تکليف نمي‌کنيم- و هنگامي که سخني مي‌گوييد، عدالت را رعايت نماييد، حتي اگر در مورد نزديکان (شما) بوده باشد و به پيمان خدا وفا کنيد، اين چيزي است که خداوند شما را به آن سفارش مي‌کند، تا متذکر شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (942, N'اين راه مستقيم من است، از آن پيروي کنيد! و از راه‌هاي پراکنده (و انحرافي) پيروي نکنيد، که شما را از طريق حق، دور مي‌سازد! اين چيزي است که خداوند شما را به آن سفارش مي‌کند، شايد پرهيزگاري پيشه کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (943, N'سپس به موسي کتاب (آسماني) داديم؛ (و نعمت خود را) بر آنها که نيکوکار بودند، کامل کرديم؛ و همه چيز را (که مورد نياز آنها بود، در آن) روشن ساختيم؛ کتابي که مايه هدايت و رحمت بود؛ شايد به لقاي پروردگارشان (و روز رستاخيز)، ايمان بياورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (944, N'و اين کتابي است پر برکت، که ما (بر تو) نازل کرديم؛ از آن پيروي کنيد،و پرهيزگاري پيشه نمائيد، باشد که مورد رحمت (خدا) قرار گيريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (945, N'(ما اين کتاب را با اين امتيازات نازل کرديم) تا نگوييد: «کتاب آسماني تنها بر دو طايفه پيش از ما [= يهود و نصاري‌] نازل شده بود؛ و ما از بحث و بررسي آنها بي خبر بوديم»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (946, N'يا نگوييد: «اگر کتاب آسماني بر ما نازل مي‌شد، از آنها هدايت يافته‌تر بوديم»! اينک آيات و دلايل روشن از جانب پروردگارتان، و هدايت و رحمت براي شما آمد! پس، چه کسي ستمکارتر است از کسي که آيات خدا را تکذيب کرده، و از آن روي گردانده است؟! اما بزودي کساني را که از آيات ما روي مي‌گردانند، بخاطر همين اعراض بي‌دليلشان، مجازات شديد خواهيم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (947, N'آيا جز اين انتظار دارند که فرشتگان (مرگ) به سراغشان آيند، يا خداوند (خودش) به سوي آنها بيايد، يا بعضي از آيات پروردگارت (و نشانه‌هاي رستاخيز)؟! اما آن روز که بعضي از آيات پروردگارت تحقق پذيرد، ايمان‌آوردن افرادي که قبلا ايمان نياورده‌اند، يا در ايمانشان عمل نيکي انجام نداده‌اند، سودي به حالشان نخواهد داشت! بگو: «(اکنون که شما چنين انتظارات نادرستي داريد،) انتظار بکشيد ما هم انتظار (کيفر شما را) مي‌کشيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (948, N'کساني که آيين خود را پراکنده ساختند، و به دسته‌هاي گوناگون (و مذاهب مختلف) تقسيم شدند، تو هيچ گونه رابطه‌اي با آنها نداري! سر و کار آنها تنها با خداست؛ سپس خدا آنها را از آنچه انجام مي‌دادند، با خبر مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (949, N'هر کس کار نيکي بجا آورد، ده برابر آن پاداش دارد، و هر کس کار بدي انجام دهد، جز بمانند آن، کيفر نخواهد ديد؛ و ستمي بر آنها نخواهد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (950, N'بگو: «پروردگارم مرا به راه راست هدايت کرده؛ آييني پابرجا (و ضامن سعادت دين و دنيا)؛ آيين ابراهيم؛ که از آيينهاي خرافي روي برگرداند؛ و از مشرکان نبود.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (951, N'بگو: «نماز و تمام عبادات من، و زندگي و مرگ من، همه براي خداوند پروردگار جهانيان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (952, N'همتايي براي او نيست؛ و به همين مأمور شده‌ام؛ و من نخستين مسلمانم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (953, N'بگو: «آيا غير خدا، پروردگاري را بطلبم، در حالي که او پروردگار همه چيز است؟! هيچ کس، عمل (بدي) جز به زيان خودش، انجام نمي‌دهد؛ و هيچ گنهکاري گناه ديگري را متحمل نمي‌شود؛ سپس بازگشت همه شما به سوي پروردگارتان است؛ و شما را از آنچه در آن اختلاف داشتيد، خبر خواهد داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (954, N'و او کسي است که شما را جانشينان (و نمايندگان) خود در زمين ساخت، و درجات بعضي از شما را بالاتر از بعضي ديگر قرار داد، تا شما را به وسيله آنچه در اختيارتان قرار داده بيازمايد؛ به يقين پروردگار تو سريع العقاب و آمرزنده مهربان است. (کيفر کساني را که از بوته امتحان نادرست درآيند، زود مي‌دهد؛ و نسبت به حق پويان مهربان است.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (955, N'المص‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (956, N'اين کتابي است که بر تو نازل شده؛ و نبايد از ناحيه آن، ناراحتي در سينه داشته باشي! تا به وسيله آن، (مردم را از عواقب سوء عقايد و اعمال نادرستشان) بيم دهي؛ و تذکري است براي مؤمنان.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (957, N'از چيزي که از طرف پروردگارتان بر شما نازل شده، پيروي کنيد! و از اوليا و معبودهاي ديگر جز او، پيروي نکنيد! اما کمتر متذکر مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (958, N'چه بسيار شهرها و آباديها که آنها را (بر اثر گناه فراوانشان) هلاک کرديم!و عذاب ما شب‌هنگام، يا در روز هنگامي که استراحت کرده بودند، به سراغشان آمد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (959, N'و در آن موقع که عذاب ما به سراغ آنها آمد، سخني نداشتند جز اينکه گفتند:«ما ظالم بوديم!» (ولي اين اعتراف به گناه، ديگر دير شده بود؛ و سودي به حالشان نداشت.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (960, N'به يقين، (هم) از کساني که پيامبران به سوي آنها فرستاده شدند سؤال خواهيم کرد؛ (و هم) از پيامبران سؤال مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (961, N'و مسلما (اعمالشان را) با علم (خود) براي آنان شرح خواهيم داد؛ و ما هرگز غايب نبوديم (بلکه همه جا حاضر و ناظر اعمال بندگان هستيم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (962, N'وزن کردن (اعمال، و سنجش ارزش آنها) در آن روز، حق است! کساني که ميزانهاي (عمل) آنها سنگين است، همان رستگارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (963, N'و کساني که ميزانهاي (عمل) آنها سبک است، افرادي هستند که سرمايه وجود خود را، بخاطر ظلم و ستمي که نسبت به آيات ما مي‌کردند، از دست داده‌اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (964, N'ما تسلط و مالکيت و حکومت بر زمين را براي شما قرار داديم؛ و انواع وسايل زندگي را براي شما فراهم ساختيم؛ اما کمتر شکرگزاري مي‌کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (965, N'ما شما را آفريديم؛ سپس صورت بندي کرديم؛ بعد به فرشتگان گفتيم: «براي آدم خضوع کنيد!» آنها همه سجده کردند؛ جز ابليس که از سجده‌کنندگان نبود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (966, N'(خداوند به او) فرمود: «در آن هنگام که به تو فرمان دادم، چه چيز تو را مانع شد که سجده کني؟» گفت: «من از او بهترم؛ مرا از آتش آفريده‌اي و او را از گل!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (967, N'گفت: «از آن (مقام و مرتبه‌ات) فرود آي! تو حق نداري در آن (مقام و مرتبه) تکبر کني! بيرون رو، که تو از افراد پست و کوچکي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (968, N'گفت: «مرا تا روزي که (مردم) برانگيخته مي‌شوند مهلت ده (و زنده بگذار!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (969, N'فرمود: «تو از مهلت داده شدگاني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (970, N'گفت: «اکنون که مرا گمراه ساختي، من بر سر راه مستقيم تو، در برابر آنها کمين مي کنم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (971, N'سپس از پيش رو و از پشت سر، و از طرف راست و از طرف چپ آنها، به سراغشان مي‌روم؛ و بيشتر آنها را شکرگزار نخواهي يافت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (972, N'فرمود: «از آن (مقام)، با ننگ و عار و خواري، بيرون رو! و سوگند ياد مي‌کنم که هر کس از آنها از تو پيروي کند، جهنم را از شما همگي پر مي‌کنم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (973, N'و اي آدم! تو و همسرت در بهشت ساکن شويد! و از هر جا که خواستيد، بخوريد! اما به اين درخت نزديک نشويد، که از ستمکاران خواهيد بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (974, N'سپس شيطان آن دو را وسوسه کرد، تا آنچه را از اندامشان پنهان بود، آشکار سازد؛ و گفت: «پروردگارتان شما را از اين درخت نهي نکرده مگر بخاطر اينکه (اگر از آن بخوريد،) فرشته خواهيد شد، يا جاودانه (در بهشت) خواهيد ماند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (975, N'و براي آنها سوگند ياد کرد که من براي شما از خيرخواهانم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (976, N'و به اين ترتيب، آنها را با فريب (از مقامشان) فرودآورد. و هنگامي که از آن درخت چشيدند، اندامشان [= عورتشان‌] بر آنها آشکار شد؛ و شروع کردند به قرار دادن برگهاي (درختان) بهشتي بر خود، تا آن را بپوشانند. و پروردگارشان آنها را نداد داد که: «آيا شما را از آن درخت نهي نکردم؟! و نگفتم که شيطان براي شما دشمن آشکاري است؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (977, N'گفتند: «پروردگارا! ما به خويشتن ستم کرديم! و اگر ما را نبخشي و بر ما رحم نکني، از زيانکاران خواهيم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (978, N'فرمود: «(از مقام خويش،) فرود آييد، در حالي که بعضي از شما نسبت به بعض ديگر، دشمن خواهيد بود! (شيطان دشمن شماست، و شما دشمن او!) و براي شما در زمين، قرارگاه و وسيله بهره‌گيري تا زمان معيني خواهد بود.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (979, N'فرمود: «در آن [= زمين‌] زنده مي‌شويد؛ و در آن مي‌ميريد؛ و (در رستاخيز) از آن خارج خواهيد شد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (980, N'اي فرزندان آدم! لباسي براي شما فرستاديم که اندام شما را مي‌پوشاند و مايه زينت شماست؛ اما لباس پرهيزگاري بهتر است! اينها (همه) از آيات خداست، تا متذکر (نعمتهاي او) شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (981, N'اي فرزندان آدم! شيطان شما را نفريبد، آن گونه که پدر و مادر شما را از بهشت بيرون کرد، و لباسشان را از تنشان بيرون ساخت تا عورتشان را به آنها نشان دهد! چه اينکه او و همکارانش شما را مي‌بينند از جايي که شما آنها را نمي‌بينيد؛ (اما بدانيد) ما شياطين را اولياي کساني قرار داديم که ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (982, N'و هنگامي که کار زشتي انجام مي‌دهند مي‌گويند: «پدران خود را بر اين عمل يافتيم؛ و خداوند ما را به آن دستور داده است!» بگو: «خداوند (هرگز) به کار زشت فرمان نمي‌دهد! آيا چيزي به خدا نسبت مي‌دهيد که نمي‌دانيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (983, N'بگو: «پروردگارم امر به عدالت کرده است؛ و توجه خويش را در هر مسجد (و به هنگام عبادت) به سوي او کنيد! و او را بخوانيد، در حالي که دين (خود) را براي او خالص گردانيد! (و بدانيد) همان گونه که در آغاز شما را آفريد، (بار ديگر در رستاخيز) بازمي‌گرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (984, N'جمعي را هدايت کرده؛ و جمعي (که شايستگي نداشته‌اند،) گمراهي بر آنها مسلم شده است. آنها (کساني هستند که) شياطين را به جاي خداوند، اولياي خود انتخاب کردند؛ و گمان مي‌کنند هدايت يافته‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (985, N'اي فرزندان آدم! زينت خود را به هنگام رفتن به مسجد، با خود برداريد! و (از نعمتهاي الهي) بخوريد و بياشاميد، ولي اسراف نکنيد که خداوند مسرفان را دوست نمي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (986, N'بگو: «چه کسي زينتهاي الهي را که براي بندگان خود آفريده، و روزيهاي پاکيزه را حرام کرده است؟!» بگو: «اينها در زندگي دنيا، براي کساني است که ايمان آورده‌اند؛ (اگر چه ديگران نيز با آنها مشارکت دارند؛ ولي) در قيامت، خالص (براي مؤمنان) خواهد بود.» اين گونه آيات (خود) را براي کساني که آگاهند، شرح مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (987, N'بگو: «خداوند، تنها اعمال زشت را، چه آشکار باشد چه پنهان، حرام کرده است؛ و (همچنين) گناه و ستم بناحق را؛ و اينکه چيزي را که خداوند دليلي براي آن نازل نکرده، شريک او قرار دهيد؛ و به خدا مطلبي نسبت دهيد که نمي‌دانيد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (988, N'براي هر قوم و جمعيتي، زمان و سرآمد (معيني) است؛ و هنگامي که سرآمد آنها فرا رسد، نه ساعتي از آن تأخير مي‌کنند، و نه بر آن پيشي مي‌گيرند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (989, N'اي فرزندان آدم! اگر رسولاني از خود شما به سراغتان بيايند که آيات مرا براي شما بازگو کنند، (از آنها پيروي کنيد؛) کساني که پرهيزگاري پيشه کنند و عمل صالح انجام دهند (و در اصلاح خويش و ديگران بکوشند)، نه ترسي بر آنهاست و نه غمناک مي شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (990, N'و آنها که آيات ما را تکذيب کنند، و در برابر آن تکبر ورزند، اهل دوزخند؛ جاودانه در آن خواهند ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (991, N'چه کسي ستمکارتر است از آنها که بر خدا دروغ مي‌بندند، يا آيات او را تکذيب مي کنند؟! آنها نصيبشان را از آنچه مقدر شده (از نعمتها و مواهب اين جهان) مي‌برند؛ تا زماني که فرستادگان ما [= فرشتگان قبض ارواح‌] به سراغشان روند و جانشان را بگيرند؛ از آنها مي‌پرسند: «کجايند معبودهايي که غير از خدا مي‌خوانديد؟! (چرا به ياري شما نمي‌آيند؟!)» مي‌گويند: «آنها (همه) گم شدند (و از ما دور گشتند!)» و بر ضد خود گواهي مي‌دهند که کافر بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (992, N'(خداوند به آنها) مي‌گويد: «در صف گروه‌هاي مشابه خود از جن و انس در آتش وارد شويد!» هر زمان که گروهي وارد مي‌شوند، گروه ديگر را لعن مي‌کنند؛ تا همگي با ذلت در آن قرار گيرند. (در اين هنگام) گروه پيروان درباره پيشوايان خود مي‌گويند: «خداوندا! اينها بودند که ما را گمراه ساختند؛ پس کيفر آنها را از آتش دو برابر کن! (کيفري براي گمراهيشان، و کيفري بخاطر گمراه ساختن ما.)» مي‌فرمايد: «براي هر کدام (از شما) عذاب مضاعف است؛ ولي نمي‌دانيد! (چرا که پيروان اگر گرد پيشوايان گمراه را نگرفته بودند، قدرتي بر اغواي مردم نداشتند.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (993, N'و پيشوايان آنها به پيروان خود مي‌گويند: «شما امتيازي بر ما نداشتيد؛ پس بچشيد عذاب (الهي) را در برابر آنچه انجام مي‌داديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (994, N'کساني که آيات ما را تکذيب کردند، و در برابر آن تکبر ورزيدند، (هرگز) درهاي آسمان به رويشان گشوده نمي‌شود؛ و (هيچ گاه) داخل بهشت نخواهند شد مگر اينکه شتر از سوراخ سوزن بگذرد! اين گونه، گنهکاران را جزا مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (995, N'براي آنها بستري از (آتش) دوزخ، و روي آنها پوششهايي (از آن) است؛ و اينچنين ظالمان را جزا ميدهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (996, N'و کساني که ايمان آورده و اعمال صالح انجام داده‌اند -البته هيچ کس را جز به اندازه تواناييش تکليف نمي‌کنيم- آنها اهل بهشتند؛ و جاودانه در آن خواهند ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (997, N'و آنچه در دلها از کينه و حسد دارند، برمي‌کنيم (تا در صفا و صميميت با هم زندگي کنند)؛ و از زير (قصرها و درختان) آنها، نهرها جريان دارد؛ مي‌گويند: «ستايش مخصوص خداوندي است که ما را به اين (همه نعمتها) رهنمون شد؛ و اگر خدا ما را هدايت نکرده بود، ما (به اينها) راه نمي‌يافتيم! مسلما فرستادگان پروردگار ما حق را آوردند!» و (در اين هنگام) به آنان ندا داده مي‌شود که: «اين بهشت را در برابر اعمالي که انجام مي‌داديد، به ارث برديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (998, N'و بهشتيان دوزخيان را صدا مي‌زنند که: «آنچه را پروردگارمان به ما وعده داده بود، همه را حق يافتيم؛ آيا شما هم آنچه را پروردگارتان به شما وعده داده بود حق يافتيد؟!» در اين هنگام، ندادهنده‌اي در ميان آنها ندا مي‌دهد که: «لعنت خدا بر ستمگران باد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (999, N'همانها که (مردم را) از راه خدا بازمي‌دارند، و (با القاي شبهات) مي‌خواهند آن را کج و معوج نشان دهند؛ و آنها به آخرت کافرند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1000, N'و در ميان آن دو [= بهشتيان و دوزخيان‌]، حجابي است؛ و بر «اعراف» مرداني هستند که هر يک از آن دو را از چهره‌شان مي‌شناسند؛ و به بهشتيان صدا مي‌زنند که: «درود بر شما باد!» اما داخل بهشت نمي‌شوند، در حالي که اميد آن را دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1001, N'و هنگامي که چشمشان به دوزخيان مي‌افتد مي‌گويند: «پروردگارا! ما را با گروه ستمگران قرار مده!»')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1002, N'و اصحاب اعراف، مرداني (از دوزخيان را) که از سيمايشان آنها را مي‌شناسند، صدا مي‌زنند و مي‌گويند: «(ديديد که) گردآوري شما (از مال و ثروت و آن و فرزند) و تکبرهاي شما، به حالتان سودي نداد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1003, N'آيا اينها [= اين واماندگان بر اعراف‌] همانان نيستند که سوگند ياد کرديد رحمت خدا هرگز شامل حالشان نخواهد شد؟! (ولي خداوند بخاطر ايمان و بعضي اعمال خيرشان، آنها را بخشيد؛ هم اکنون به آنها گفته مي‌شود:) داخل بهشت شويد، که نه ترسي داريد و نه غمناک مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1004, N'و دوزخيان، بهشتيان را صدا مي‌زنند که: «(محبت کنيد) و مقداري آب، يا از آنچه خدا به شما روزي داده، به ما ببخشيد!» آنها (در پاسخ) مي‌گويند: «خداوند اينها را بر کافران حرام کرده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1005, N'همانها که دين و آيين خود را سرگرمي و بازيچه گرفتند؛ و زندگي دنيا آنان را مغرور ساخت؛ امروز ما آنها را فراموش مي‌کنيم، همان گونه که لقاي چنين روزي را فراموش کردند و آيات ما را انکار نمودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1006, N'ما کتابي براي آنها آورديم که (اسرار و رموز) آن را با آگاهي شرح داديم؛ (کتابي) که مايه هدايت و رحمت براي جمعيتي است که ايمان مي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1007, N'آيا آنها جز انتظار تأويل آيات (و فرا رسيدن تهديدهاي الهي) دارند؟ آن روز که تأويل آنها فرا رسد، (کار از کار گذشته، و پشيماني سودي ندارد؛ و) کساني که قبلا آن را فراموش کرده بودند مي‌گويند: «فرستادگان پروردگار ما، حق را آوردند؛ آيا (امروز) شفيعاني براي ما وجود دارند که براي ما شفاعت کنند؟ يا (به ما اجازه داده شود به دنيا) بازگرديم، و اعمالي غير از آنچه انجام مي‌داديم، انجام دهيم؟!» (ولي) آنها سرمايه وجود خود را از دست داده‌اند؛ و معبودهايي را که به دروغ ساخته بودند، همگي از نظرشان :گم مي‌شوند. (نه راه بازگشتي دارند، و نه شفيعاني!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1008, N'پروردگار شما، خداوندي است که آسمانها و زمين را در شش روز [= شش دوران‌] آفريد؛ سپس به تدبير جهان هستي پرداخت؛ با (پرده تاريک) شب، روز را مي پوشاند؛ و شب به دنبال روز، به سرعت در حرکت است؛ و خورشيد و ماه و ستارگان را آفريد، که مسخر فرمان او هستند. آگاه باشيد که آفرينش و تدبير (جهان)، از آن او (و به فرمان او)ست! پر برکت (و زوال‌ناپذير) است خداوندي که پروردگار جهانيان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1009, N'پروردگار خود را (آشکارا) از روي تضرع، و در پنهاني، بخوانيد! (و از تجاوز، دست برداريد که) او متجاوزان را دوست نمي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1010, N'و در زمين پس از اصلاح آن فساد نکنيد، و او را با بيم و اميد بخوانيد! (بيم از مسؤوليتها، و اميد به رحمتش. و نيکي کنيد) زيرا رحمت خدا به نيکوکاران نزديک است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1011, N'او کسي است که بادها را بشارت دهنده در پيشاپيش (باران) رحمتش مي‌فرستد؛ تا ابرهاي سنگين‌بار را (بر دوش) کشند؛ (سپس) ما آنها را به سوي زمينهاي مرده مي‌فرستيم؛ و به وسيله آنها، آب (حياتبخش) را نازل مي‌کنيم؛ و با آن، از هرگونه ميوه‌اي (از خاک تيره) بيرون مي‌آوريم؛ اين گونه (که زمينهاي مرده را زنده کرديم،) مردگان را (نيز در قيامت) زنده مي‌کنيم، شايد (با توجه به اين مثال) متذکر شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1012, N'سرزمين پاکيزه 0و شيرين)، گياهش به فرمان پروردگار مي‌رويد؛ اما سرزمينهاي بد طينت (و شوره‌زار)، جز گياه ناچيز و بي‌ارزش، از آن نمي‌رويد؛ اين گونه آيات (خود) را براي آنها که شکرگزارند، بيان مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1013, N'ما نوح را به سوي قومش فرستاديم؛ او به آنان گفت: «اي قوم من! (تنها) خداوند يگانه را پرستش کنيد، که معبودي جز او براي شما نيست! (و اگر غير او را عبادت کنيد،) من بر شما از عذاب روز بزرگي مي‌ترسم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1014, N'(ولي) اشراف قومش به او گفتند: «ما تو را در گمراهي آشکاري مي‌بينيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1015, N'گفت: «اي قوم من! هيچ گونه گمراهي در من نيست؛ ولي من فرستاده‌اي از جانب پروردگار جهانيانم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1016, N'رسالتهاي پروردگارم را به شما ابلاغ مي‌کنم؛ و خيرخواه شما هستم؛ و از خداوند چيزهايي مي‌دانم که شما نمي‌دانيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1017, N'آيا تعجب کرده‌ايد که دستور آگاه کننده پروردگارتان به وسيله مردي از ميان شما به شما برسد، تا (از عواقب اعمال خلاف) بيمتان دهد، و (در پرتو اين دستور،) پرهيزگاري پيشه کنيد و شايد مشمول رحمت (الهي) گرديد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1018, N'اما سرانجام او را تکذيب کردند؛ و ما او و کساني را که با وي در کشتي بودند، رهايي بخشيديم؛ و کساني که آيات ما را تکذيب کردند، غرق کرديم؛ چه اينکه آنها گروهي نابينا (و کوردل) بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1019, N'و به سوي قوم عاد، برادرشان «هود» را (فرستاديم)؛ گفت: «اي قوم من! (تنها) خدا را پرستش کنيد، که جز او معبودي براي شما نيست! آيا پرهيزگاري پيشه نمي‌کنيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1020, N'اشراف کافر قوم او گفتند: «ما تو را در سفاهت (و ناداني و سبک مغزي) مي‌بينيم، و ما مسلما تو را از دروغگويان مي‌دانيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1021, N'گرفت: «اي قوم من! هيچ گونه سفاهتي در من نيست؛ ولي فرستاده‌اي از طرف پروردگار جهانيانم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1022, N'رسالتهاي پروردگارم را به شما ابلاغ مي‌کنم؛ و من خيرخواه اميني براي شما هستم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1023, N'آيا تعجب کرده‌ايد که دستور آگاه کننده پروردگارتان به وسيله مردي از ميان شما به شما برسد تا (از مجازات الهي) بيمتان دهد؟! و به ياد آوريد هنگامي که شما را جانشينان قوم نوح قرار داد؛ و شما را از جهت خلقت (جسماني) گسترش (و قدرت) داد؛ پس نعمتهاي خدا را به ياد آوريد، شايد رستگار شويد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1024, N'گفتند: «آيا به سراغ ما آمده‌اي که تنها خداي يگانه را بپرستيم، و آنچه را پدران ما مي‌پرستند، رها کنيم؟! پس اگر راست مي‌گوئي آنچه را (از بلا و عذاب الهي) به ما وعده مي‌دهي، بياور»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1025, N'گفت: «پليدي و غضب پروردگارتان، شما را فرا گرفته است! آيا با من در مورد نامهايي مجادله مي‌کنيد که شما و پدرانتان 0بعنوان معبود و خدا، بر بتها) گذارده‌ايد، در حالي که خداوند هيچ دليلي درباره آن نازل نکرده است؟! پس شما منتظر باشيد، من هم با شما انتظار مي‌کشم! (شما انتظار شکست من، و من انتظار عذاب الهي براي شما!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1026, N'سرانجام، او و کساني را که با او بودند، برحمت خود نجات بخشيديم؛ و ريشه کساني که آيات ما را تکذيب کردند و ايمان نياوردند، قطع کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1027, N'و به سوي (قوم) ثمود، برادرشان صالح را (فرستاديم)؛ گفت: «اي قوم من! (تنها) خدا را بپرستيد، که جز او، معبودي براي شما نيست! دليل روشني از طرف پروردگارتان براي شما آمده: اين «ناقه» الهي براي شما معجزه‌اي است؛ او را به حال خود واگذاريد که در زمين خدا (از علفهاي بيابان) بخورد! و آن را آزار نرسانيد، که عذاب دردناکي شما را خواهد گرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1028, N'و به خاطر بياوريد که شما را جانشينان قوم «عاد» قرار داد، و در زمين مستقر ساخت، که در دشتهايش، قصرها براي خود بنا مي‌کنيد؛ و در کوه‌ها، براي خود خانه‌ها مي تراشيد! بنابر اين، نعمتهاي خدا را متذکر شويد! و در زمين، به فساد نکوشيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1029, N'(ولي) اشراف متکبر قوم او، به مستضعفاني که ايمان آورده بودند، گفتند: «آيا (براستي) شما يقين داريد که صالح از طرف پروردگارش فرستاده شده است؟!» آنها گفتند: «ما به آنچه او بدان مأموريت يافته، ايمان آورده‌ايم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1030, N'متکبران گفتند: «(ولي) ما به آنچه شما به آن ايمان آورده‌ايد، کافريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1031, N'سپس «ناقه» را پي کردند، و از فرمان پروردگارشان سرپيچيدند؛ و گفتند: «اي صالح! اگر تو از فرستادگان (خدا) هستي، آنچه ما را با آن تهديد مي‌کني، بياور!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1032, N'سرانجام زمين لرزه آنها را فرا گرفت؛ و صبحگاهان، (تنها) جسم بي‌جانشان در خانه‌هاشان باقي مانده بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1033, N'(صالح) از آنها روي برتافت؛ و گفت: «اي قوم! من رسالت پروردگارم را به شما ابلاغ کردم، و شرط خيرخواهي را انجام دادم، ولي (چه کنم که) شما خيرخواهان را دوست نداريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1034, N'و (به خاطر آوريد) لوط را، هنگامي که به قوم خود گفت: «آيا عمل بسيار زشتي را انجام مي‌دهيد که هيچيک از جهانيان، پيش از شما انجام نداده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1035, N'آيا شما از روي شهوت به سراغ مردان مي‌رويد، نه زنان؟! شما گروه اسرافکار (و منحرفي) هستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1036, N'ولي پاسخ قومش چيزي جز اين نبود که گفتند: «اينها را از شهر و ديار خود بيرون کنيد، که اينها مردمي هستند که پاکدامني را مي‌طلبند (و با ما همصدا نيستند!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1037, N'(چون کار به اينجا رسيد،) ما او و خاندانش را رهايي بخشيديم؛ جز همسرش، که از بازماندگان (در شهر) بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1038, N'و (سپس چنان) باراني (از سنگ) بر آنها فرستاديم؛ (که آنها را در هم کوبيد و نابود ساخت.) پس بنگر سرانجام کار مجرمان چه شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1039, N'و به سوي مدين، برادرشان شعيب را (فرستاديم)؛ گفت: «اي قوم من! خدا را بپرستيد، که جز او معبودي نداريد! دليل روشني از طرف پروردگارتان براي شما آمده است؛ بنابر اين، حق پيمانه و وزن را ادا کنيد! و از اموال مردم چيزي نکاهيد! و در روي زمين، بعد از آنکه (در پرتو ايمان و دعوت انبياء) اصلاح شده است، فساد نکنيد! اين براي شما بهتر است اگر با ايمان هستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1040, N'و بر سر هر راه ننشينيد که (مردم با ايمان را) تهديد کنيد و مؤمنان را از راه خدا باز داريد، و با (القاي شبهات،) آن را کج و معوج نشان دهيد! و به خاطر بياوريد زماني را که اندک بوديد، و او شما را فزوني داد! و بنگريد سرانجام مفسدان چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1041, N'و اگر گروهي از شما به آنچه من به آن فرستاده شده‌ام ايمان آورده‌اند، و گروهي ايمان نياورده‌اند، صبر کنيد تا خداوند ميان ما داوري کند، که او بهترين داوران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1042, N'اشراف زورمند و متکبر از قوم او گفتند: «اي شعيب! به يقين، تو و کساني را که به تو ايمان آورده‌اند، از شهر و ديار خود بيرون خواهيم کرد، يا به آيين ما بازگرديد!» کفت: «آيا (مي‌خواهيد ما را بازگردانيد) اگر چه مايل نباشيم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1043, N'اگر ما به آيين شما بازگرديم، بعد از آنکه خدا ما را از آن نجات بخشيده، به خدا دروغ بسته‌ايم؛ و شايسته نيست که ما به آن بازگرديم مگر اينکه خدايي که پروردگار ماست بخواهد؛ علم پروردگار ما، به همه چيز احاطه دارد. تنها بر خدا توکل کرده‌ايم. پروردگارا! ميان ما و قوم ما بحق داوري کن، که تو بهترين داوراني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1044, N'اشراف زورمند از قوم او که کافر شده بودند گفتند: «اگر از شعيب پيروي کنيد، شما هم زيانکار خواهيد شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1045, N'سپس زمين لرزه آنها را فرا گرفت؛ و صبحگاهان بصورت اجسادي بي‌جان در خانه‌هاشان مانده بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1046, N'آنها که شعيب را تکذيب کردند، (آنچنان نابود شدند که) گويا هرگز در آن (خانه‌ها) سکونت نداشتند! آنها که شعيب را تکذيب کردند، زيانکار بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1047, N'سپس از آنان روي برتافت و گفت: «اي قوم من! من رسالتهاي پروردگارم را به شما ابلاغ کردم و براي شما خيرخواهي نمودم؛ با اين حال، چگونه بر حال قوم بي‌ايمان تأسف بخورم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1048, N'و ما در هيچ شهر و آبادي پيامبري نفرستاديم مگر اينکه اهل آن را به ناراحتيها و خسارتها گرفتار ساختيم؛ شايد (به خود آيند، و به سوي خدا) بازگردند و تضرع کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1049, N'سپس (هنگامي که اين هشدارها در آنان اثر نگذاشت)، نيکي (و فراواني نعمت و رفاه) را به جاي بدي (و ناراحتي و گرفتاري) قرار داديم؛ آنچنان که فزوني گرفتند، (و همه‌گونه نعمت و برکت يافتند، و مغرور شدند،) و گفتند: «(تنها ما نبوديم که گرفتار اين مشکلات شديم؛) به پدران ما نيز ناراحتيهاي جسمي و مالي رسيد.» چون چنين شد، آنها را ناگهان (به سبب اعمالشان) گرفتيم (و مجازات کرديم)، در حالي که نمي‌فهميدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1050, N'و اگر اهل شهرها و آباديها، ايمان مي‌آوردند و تقوا پيشه مي‌کردند، برکات آسمان و زمين را بر آنها مي‌گشوديم؛ ولي (آنها حق را) تکذيب کردند؛ ما هم آنان را به کيفر اعمالشان مجازات کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1051, N'آيا اهل اين آباديها، از اين ايمنند که عذاب ما شبانه به سراغ آنها بيايد در حالي که در خواب باشند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1052, N'آيا اهل اين آباديها، از اين ايمنند که عذاب ما هنگام روز به سراغشان بيايد در حالي که سرگرم بازي هستند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1053, N'آيا آنها خود را از مکر الهي در امان مي‌دانند؟! در حالي که جز زيانکاران، خود را از مکر (و مجازات) خدا ايمن نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1054, N'آيا کساني که وارث روي زمين بعد از صاحبان آن مي‌شوند، عبرت نمي‌گيرند که اگر بخواهيم، آنها را نيز به گناهانشان هلاک مي‌کنيم، و بر دلهايشان مهر مي‌نهيم تا (صداي حق را) نشنوند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1055, N'اينها، شهرها و آباديهايي است که قسمتي از اخبار آن را براي تو شرح مي‌دهيم؛ پيامبرانشان دلايل روشن براي آنان آوردند؛ (ولي آنها چنان لجوج بودند که) به آنچه قبلا تکذيب کرده بودند، ايمان نمي‌آوردند! اين‌گونه خداوند بر دلهاي کافران مهر مي‌نهد (و بر اثر لجاجت و ادامه گناه، حس تشخيصشان را سلب مي‌کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1056, N'و بيشتر آنها را بر سر پيمان خود نيافتيم؛ (بلکه) اکثر آنها را فاسق و گنهکار يافتيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1057, N'سپس بدنبال آنها [= پيامبران پيشين‌] موسي را با آيات خويش به سوي فرعون و اطرافيان او فرستاديم؛ اما آنها (با عدم پذيرش)، به آن (آيات) ظلم کردند. ببين عاقبت مفسدان چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1058, N'و موسي گفت: «اي فرعون! من فرستاده‌اي از سوي پروردگار جهانيانم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1059, N'سزاوار است که بر خدا جز حق نگويم. من دليل روشني از پروردگارتان براي شما آورده‌ام؛ پس بني اسرائيل را با من بفرست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1060, N'(فرعون) گفت: «اگر نشانه‌اي آورده‌اي، نشان بده اگر از راستگوياني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1061, N'(موسي) عصاي خود را افکند؛ ناگهان اژدهاي آشکاري شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1062, N'و دست خود را (از گريبان) بيرون آورد؛ سفيد (و درخشان) براي بينندگان بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1063, N'اطرافيان فرعون گفتند: «بي‌شک، اين ساحري ماهر و دانا است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1064, N'مي‌خواهد شما را از سرزمينتان بيرون کند؛ (نظر شما چيست، و) در برابر او چه دستوري داريد؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1065, N'(سپس به فرعون) گفتند: «(کار) او و برادرش را به تأخير انداز، و جمع‌آوري‌کنندگان را به همه شهرها بفرست...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1066, N'تا هر ساحر دانا (و کارآزموده‌اي) را به خدمت تو بياورند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1067, N'ساحران نزد فرعون آمدند و گفتند: «آيا اگر ما پيروز گرديم، اجر و پاداش مهمي خواهيم داشت؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1068, N'گفت: «آري، و شما از مقربان خواهيد بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1069, N'(روز مبارزه فرا رسيد. ساحران) گفتند: «اي موسي! يا تو (وسايل سحرت را) بيفکن، يا ما مي‌افکنيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1070, N'گفت: «شما بيفکنيد!» و هنگامي (که وسايل سحر خود را) افکندند، مردم را چشم‌بندي کردند و ترساندند؛ و سحر عظيمي پديد آوردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1071, N'(ما) به موسي وحي کرديم که: «عصاي خود را بيفکن!» ناگهان (بصورت مار عظيمي در آمد که) وسايل دروغين آنها را بسرعت برمي‌گرفت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1072, N'(در اين هنگام،) حق آشکار شد؛ و آنچه آنها ساخته بودند، باطل گشت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1073, N'و در آنجا (همگي) مغلوب شدند؛ و خوار و کوچک گشتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1074, N'و ساحران (بي‌اختيار) به سجده افتادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1075, N'و گفتند: «ما به پروردگار جهانيان ايمان آورديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1076, N'پروردگار موسي و هارون!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1077, N'فرعون گفت: «آيا پيش از آنکه به شما اجازه دهم، به او ايمان آورديد؟! حتما اين نيرنگ و توطئه‌اي است که در اين شهر (و ديار) چيده‌ايد، تا اهلش را از آن بيرون کنيد؛ ولي بزودي خواهيد دانست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1078, N'سوگند مي‌خورم که دستها و پاهاي شما را بطور مخالف [= دست راست با پاي چپ، يا دست چپ با پاي راست‌] قطع مي‌کنم؛ سپس همگي را به دار مي‌آويزم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1079, N'(ساحران) گفتند: «(مهم نيست،) ما به سوي پروردگارمان بازمي‌گرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1080, N'انتقام تو از ما، تنها بخاطر اين است که ما به آيات پروردگار خويش -هنگامي که به سراغ ما آمد- ايمان آورديم. بار الها! صبر و استقامت بر ما فرو ريز! (و آخرين درجه شکيبائي را به ما مرحمت فرما!) و ما را مسلمان بميران!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1081, N'و اشراف قوم فرعون (به او) گفتند: «آيا موسي و قومش را رها مي‌کني که در زمين فساد کنند، و تو و خدايانت را رها سازد؟!» گفت: «بزودي پسرانشان را مي‌کشيم، و دخترانشان را زنده نگه مي‌داريم (تا به ما خدمت کنند)؛ و ما بر آنها کاملا مسلطيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1082, N'موسي به قوم خود گفت: «از خدا ياري جوييد، و استقامت پيشه کنيد، که زمين از آن خداست، و آن را به هر کس از بندگانش که بخواهد، واگذار مي‌کند؛ و سرانجام (نيک) براي پرهيزکاران است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1083, N'گفتند: «پيش از آنکه به سوي ما بيايي آزار ديديم، (هم اکنون) پس از آمدنت نيز آزار مي‌بينيم! (کي اين آزارها سر خواهد آمد؟)» گفت: «اميد است پروردگارتان دشمن شما را هلاک کند، و شما را در زمين جانشين (آنها) سازد، و بنگرد چگونه عمل مي‌کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1084, N'و ما نزديکان فرعون (و قوم او) را به خشکسالي و کمبود ميوه‌ها گرفتار کرديم، شايد متذکر گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1085, N'(اما آنها نه تنها پند نگرفتند، بلکه) هنگامي که نيکي (و نعمت) به آنها مي‌رسيد، مي‌گفتند: «بخاطر خود ماست.» ولي موقعي که بدي (و بلا) به آنها مي‌رسيد، مي‌گفتند: «از شومي موسي و کسان اوست»! آگاه باشيد سرچشمه همه اينها، نزد خداست؛ ولي بيشتر آنها نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1086, N'و گفتند: «هر زمان نشانه و معجزه‌اي براي ما بياوري که سحرمان کني، ما به تو ايمان نمي‌آوريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1087, N'سپس (بلاها را پشت سر هم بر آنها نازل کرديم:) طوفان و ملخ و آفت گياهي و قورباغه‌ها و خون را -که نشانه‌هايي از هم جدا بودند- بر آنها فرستاديم؛ (ولي باز بيدار نشدند، و) تکبر ورزيدند، و جمعيت گنهکاري بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1088, N'هنگامي که بلا بر آنها مسلط مي‌شد، مي‌گفتند: «اي موسي! از خدايت براي ما بخواه به عهدي که با تو کرده، رفتار کند! اگر اين بلا را از ما مرتفع سازي، قطعا به تو ايمان مي‌آوريم، و بني اسرائيل را با تو خواهيم فرستاد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1089, N'اما هنگامي که بلا را، پس از مدت معيني که به آن مي‌رسيدند، از آنها برمي‌داشتيم، پيمان خويش را مي‌شکستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1090, N'سرانجام از آنها انتقام گرفتيم، و آنان را در دريا غرق کرديم؛ زيرا آيات ما را تکذيب کردند، و از آن غافل بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1091, N'و مشرقها و مغربهاي پر برکت زمين را به آن قوم به ضعف کشانده شده (زير زنجير ظلم و ستم)، واگذار کرديم؛ و وعده نيک پروردگارت بر بني اسرائيل، بخاطر صبر و استقامتي که به خرج دادند، تحقق يافت؛ و آنچه فرعون و فرعونيان (از کاخهاي مجلل) مي‌ساختند، و آنچه از باغهاي داربست‌دار فراهم ساخته بودند، در هم کوبيديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1092, N'و بني اسرائيل را (سالم) از دريا عبور داديم؛ (ناگاه) در راه خود به گروهي رسيدند که اطراف بتهايشان، با تواضع و خضوع، گرد آمده بودند. (در اين هنگام، بني اسرائيل) به موسي گفتند: «تو هم براي ما معبودي قرار ده، همان‌گونه که آنها معبودان (و خداياني) دارند!» گفت: «شما جمعيتي جاهل و نادان هستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1093, N'اينها (را که مي‌بينيد)، سرانجام کارشان نابودي است؛ و آنچه انجام مي‌دهند، باطل (و بيهوده) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1094, N'(سپس) گفت: «آيا غير از خداوند، مبعودي براي شما بطلبم؟! خدايي که شما را بر جهانيان (و مردم عصرتان) برتري داد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1095, N'(به خاطر بياوريد) زماني را که از (چنگال) فرعونيان نجاتتان بخشيديم! آنها که پيوسته شما را شکنجه مي‌دادند، پسرانتان را مي‌کشتند، و زنانتان را (براي خدمتگاري) زنده مي‌گذاشتند؛ و در اين، آزمايش بزرگي از سوي خدا براي شما بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1096, N'و ما با موسي، سي شب وعده گذاشتيم؛ سپس آن را با ده شب (ديگر) تکميل نموديم؛ به اين ترتيب، ميعاد پروردگارش (با او)، چهل شب تمام شد. و موسي به برادرش هارون گفت: «جانشين من در ميان قومم باش. و (آنها) را اصلاح کن! و از روش مفسدان، پيروي منما!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1097, N'و هنگامي که موسي به ميعادگاه ما آمد، و پروردگارش با او سخن گفت، عرض کرد: «پروردگارا! خودت را به من نشان ده، تا تو را ببينم!» گفت: «هرگز مرا نخواهي ديد! ولي به کوه بنگر، اگر در جاي خود ثابت ماند، مرا خواهي ديد!» اما هنگامي که پروردگارش بر کوه جلوه کرد، آن را همسان خاک قرار داد؛ و موسي مدهوش به زمين افتاد. چون به هوش آمد، عرض کرد: «خداوندا! منزهي تو (از اينکه با چشم تو را ببينم)! من به سوي تو بازگشتم! و من نخستين مؤمنانم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1098, N'(خداوند) فرمود: «اي موسي! من تو را با رسالتهاي خويش، و با سخن‌گفتنم (با تو)، بر مردم برتري دادم و برگزيدم؛ پس آنچه را به تو داده‌ام بگير و از شکرگزاران باش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1099, N'و براي او در الواح اندرزي از هر موضوعي نوشتيم؛ و بياني از هر چيز کرديم -«پس آن را با جديت بگير! و به قوم خود بگو: به نيکوترين آنها عمل کنند! (و آنها که به مخالفت برخيزند، کيفرشان دوزخ است؛) و بزودي جايگاه فاسقان را به شما نشان خواهم داد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1100, N'بزودي کساني را که در روي زمين بناحق تکبر مي‌ورزند، از (ايمان به) آيات خود، منصرف مي‌سازم! آنها چنانند که اگر هر آيه و نشانه‌اي را ببينند، به آن ايمان نمي‌آورند؛ اگر راه هدايت را ببينند، آن را راه خود انتخاب نمي‌کنند؛ و اگر طريق گمراهي را ببينند، آن را راه خود انتخاب مي‌کنند! (همه اينها) بخاطر آن است که آيات ما را تکذيب کردند، و از آن غافل بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1101, N'و کساني که آيات، و ديدار رستاخيز را تکذيب (و انکار) کنند، اعمالشان نابود مي گردد؛ آيا جز آنچه را عمل مي‌کردند پاداش داده مي‌شوند؟!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1102, N'قوم موسي بعد (از رفتن) او (به ميعادگاه خدا)، از زيورهاي خود گوساله‌اي ساختند؛ جسد بي‌جاني که صداي گوساله داشت! آيا آنها نمي‌ديدند که با آنان سخن نمي‌گويد، و به راه (راست) هدايتشان نمي‌کند؟! آن را (خداي خود) انتخاب کردند، و ظالم بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1103, N'و هنگامي که حقيقت به دستشان افتاد، و ديدند گمراه شده‌اند، گفتند: «اگر پروردگارمان به ما رحم نکند، و ما را نيامرزد، بطور قطع از زيانکاران خواهيم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1104, N'و هنگامي که موسي خشمگين و اندوهناک به سوي قوم خود بازگشت، گفت: «پس از من، بد جانشيناني برايم بوديد (و آيين مرا ضايع کرديد)! آيا درمورد فرمان پروردگارتان (و تمديد مدت ميعاد او)، عجله نموديد (و زود قضاوت کرديد؟!)» سپس الواح را افکند، و سر برادر خود را گرفت (و با عصبانيت) به سوي خود کشيد؛ او گفت: «فرزند مادرم! اين گروه، مرا در فشار گذاردند و ناتوان کردند؛ و نزديک بود مرا بکشند، پس کاري نکن که دشمنان مرا شماتت کنند و مرا با گروه ستمکاران قرار مده!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1105, N'(موسي) گفت: «پروردگارا! من و برادرم را بيامرز، و ما را در رحمت خود داخل فرما، و تو مهربانترين مهرباناني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1106, N'کساني که گوساله را (معبود خود) قرار دادند، بزودي خشم پروردگارشان، و ذلت در زندگي دنيا به آنها مي‌رسد؛ و اينچنين، کساني را که (بر خدا) افترا مي‌بندند، کيفر مي دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1107, N'و آنها که گناه کردند، و بعد از آن توبه نمودند و ايمان آوردند، (اميد عفو او را دارند؛ زيرا) پروردگار تو، در پي اين کار، آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1108, N'هنگامي که خشم موسي فرو نشست؛ الواح (تورات) را برگرفت؛ و در نوشته‌هاي آن، هدايت و رحمت براي کساني بود که از پروردگار خويش مي‌ترسند (و از مخالفت فرمانش بيم دارند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1109, N'موسي از قوم خود، هفتاد تن از مردان را براي ميعادگاه ما برگزيد؛ و هنگامي که زمين‌لرزه آنها را فرا گرفت (و هلاک شدند)، گفت: «پروردگارا! اگر مي‌خواستي، مي توانستي آنها و مرا پيش از اين نيز هلاک کني! آيا ما را به آنچه سفيهانمان انجام داده‌اند، (مجازات و) هلاک مي‌کني؟! اين، جز آزمايش تو، چيز ديگر نيست؛ که هر کس را بخواهي (و مستحق بداني)، به وسيله آن گمراه مي‌سازي؛ و هر کس را بخواهي (و شايسته ببيني)، هدايت مي‌کني! تو ولي مايي، و ما را بيامرز، بر ما رحم کن، و تو بهترين آمرزندگاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1110, N'و براي ما، در اين دنيا و سراي ديگر، نيکي مقرر فرما؛ چه اينکه ما به سوي تو بازگشت کرده‌ايم! «(خداوند در برابر اين تقاضا، به موسي) گفت:» مجازاتم را به هر کس بخواهم مي‌رسانم؛ و رحمتم همه چيز را فراگرفته؛ و آن را براي آنها که تقوا پيشه کنند، و زکات را بپردازند، و آنها که به آيات ما ايمان مي‌آورند، مقرر خواهم داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1111, N'همانها که از فرستاده (خدا)، پيامبر «امي» پيروي مي‌کنند؛ پيامبري که صفاتش را، در تورات و انجيلي که نزدشان است، مي‌يابند؛ آنها را به معروف دستور مي‌دهد، و از منکر باز ميدارد؛ اشيار پاکيزه را براي آنها حلال مي‌شمرد، و ناپاکيها را تحريم مي کند؛ و بارهاي سنگين، و زنجيرهايي را که بر آنها بود، (از دوش و گردنشان) بر مي‌دارد، پس کساني که به او ايمان آوردند، و حمايت و ياريش کردند، و از نوري که با او نازل شده پيروي نمودند، آنان رستگارانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1112, N'بگو: «اي مردم! من فرستاده خدا به سوي همه شما هستم؛ همان خدايي که حکومت آسمانها و زمين، از آن اوست؛ معبودي جز او نيست؛ زنده مي‌کند و مي‌ميراند؛ پس ايمان بياوريد به خدا و فرستاده‌اش، آن پيامبر درس نخوانده‌اي که به خدا و کلماتش ايمان دارد؛ و از او پيروي کنيد تا هدايت يابيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1113, N'و از قوم موسي، گروهي هستند که به سوي حق هدايت مي‌کنند؛ و به حق و عدالت حکم مي‌نمايند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1114, N'ما آنها را به دوازده گروه -که هر يک شاخه‌اي (از دودمان اسرائيل) بود - تقسيم کرديم. و هنگامي که قوم موسي (در بيابان) از او تقاضاي آب کردند، به او وحي فرستاديم که: «عصاي خود را بر سنگ بزن!» ناگهان دوازده چشمه از آن بيرون جست؛ آنچنان که هر گروه، چشمه و آبشخور خود را مي‌شناخت. و ابر را بر سر آنها سايبان ساختيم؛ و بر آنها «من» و «سلوي» فرستاديم؛ (و به آنان گفتيم:) از روزيهاي پاکيزه‌اي که به شما داده‌ايم، بخوريد! (و شکر خدا را بجا آوريد! آنها نافرماني و ستم کردند؛ ولي) به ما ستم نکردند، لکن به خودشان ستم مي‌نمودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1115, N'و (به خاطر بياوريد) هنگامي را که به آنها گفته شد: «در اين شهر [= بيت المقدس ] ساکن شويد، و از هر جا (و به هر کيفيت) بخواهيد، از آن بخوريد (و بهره گيريد)! و بگوييد: خداوندا! گناهان ما را بريز! و از در (بيت المقدس) با تواضع وارد شويد! که اگر چنين کنيد، گناهان شما را مي‌بخشم؛ و نيکوکاران را پاداش بيشتر خواهيم داد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1116, N'اما ستمگران آنها، اين سخن (و آن فرمانها) را، بغير آنچه به آنها گفته شده بود، تغيير دادند؛ از اين‌رو بخاطر ستمي که روا ميداشتند، بلايي از آسمان بر آنها فرستاديم (و مجازاتشان کرديم).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1117, N'و از آنها درباره (سرگذشت) شهري که در ساحل دريا بود بپرس! زماني که آنها در روزهاي شنبه، تجاوز (و نافرماني) خدا مي‌کردند؛ همان هنگام که ماهيانشان، روز شنبه (که روز تعطيل و استراحت و عبادت بود، بر سطح آب،) آشکار مي‌شدند؛ اما در غير روز شنبه، به سراغ آنها نمي‌آمدند؛ اين چنين آنها را به چيزي آزمايش کرديم که نافرماني مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1118, N'و (به ياد آر) هنگامي را که گروهي از آنها (به گروه ديگر) گفتند: «چرا جمعي (گنهکار) را اندرز مي‌دهيد که سرانجام خداوند آنها را هلاک خواهد کرد، يا به عذاب شديدي گرفتار خواهد ساخت؟! (آنها را به حال خود واگذاريد تا نابود شوند!)» گفتند: «(اين اندرزها،) براي اعتذار (و رفع مسؤوليت) در پيشگاه پروردگار شماست؛ بعلاوه شايد آنها (بپذيرند، و از گناه باز ايستند، و) تقوا پيشه کنند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1119, N'اما هنگامي که تذکراتي را که به آنها داده شده بود فراموش کردند، (لحظه عذاب فرا رسيد؛ و) نهي‌کنندگان از بدي را رهايي بخشيديم؛ و کساني را که ستم کردند، بخاطر نافرمانيشان به عذاب شديدي گرفتار ساختيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1120, N'(آري،) هنگامي که در برابر آنچه از آن نهي شده بودند سرکشي کردند، به آنها گفتيم: «به شکل ميمونهايي طردشده در آييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1121, N'و (نيز به خاطر بياور) هنگامي را که پروردگارت اعلام کرد: تا دامنه قيامت، کسي را بر آنها مسلط خواهد ساخت که همواره آنها را در عذاب سختي قرار دهد؛ زيرا پروردگارت مجازاتش سريع، (و در عين حال، نسبت به توبه‌کاران) آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1122, N'و آنها را در زمين بصورت گروه‌هايي، پراکنده ساختيم؛ گروهي از آنها صالح، و گروهي ناصالحند. و آنها را با نيکي‌ها و بدي‌ها آزموديم، شايد بازگردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1123, N'پس از آنها، فرزنداني جاي آنها را گرفتند که وارث کتاب (آسماني، تورات) شدند؛ (اما با اين حال،) متاع اين دنياي پست را گرفته، (بر اطاعت فرمان خدا ترجيح مي‌دهند) و مي‌گويند: «(اگر ما گنهکاريم توبه مي‌کنيم و) بزودي بخشيده خواهيم شد!» اما اگر متاع ديگري همانند آن به دستشان بيفتد، آن را (نيز) مي‌گيرند، (و باز حکم خدا را پشت سر مي‌افکنند.) آيا پيمان کتاب (خدا) از آنها گرفته نشده که بر خدا (دروغ نبندند، و) جز حق نگويند، و آنان بارها آنرا خوانده‌اند؟! و سراي آخرت براي پرهيزگاران بهتر است، آيا نمي‌فهميد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1124, N'و آنها که به کتاب (خدا) تمسک جويند، و نماز را برپا دارند، (پاداش بزرگي خواهند داشت؛ زيرا) ما پاداش مصلحان را ضايع نخواهيم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1125, N'و (نيز به خاطر بياور) هنگامي که کوه را همچون سايباني بر فراز آنها بلند کرديم، آنچنان که گمان کردند بر آنان فرود مي‌آمد؛ (و در همين حال، از آنها پيمان گرفتيم و گفتيم:) آنچه را (از احکام و دستورها) به شما داده‌ايم، با قوت (و جديت) بگيريد! و آنچه در آن است، به ياد داشته باشيد، (و عمل کنيد،) تا پرهيزگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1126, N'و (به خاطر بياور) زماني را که پروردگارت از پشت و صلب فرزندان آدم، ذريه آنها را برگرفت؛ و آنها را گواه بر خويشتن ساخت؛ (و فرمود:) «آيا من پروردگار شما نيستم؟» گفتند: «آري، گواهي مي‌دهيم!» (چنين کرد مبادا) روز رستاخيز بگوييد: «ما از اين، غافل بوديم؛ (و از پيمان فطري توحيد بي‌خبر مانديم)»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1127, N'يا بگوييد: «پدرانمان پيش از ما مشرک بودند، ما هم فرزنداني بعد از آنها بوديم؛ (و چاره‌اي جز پيروي از آنان نداشتيم؛) آيا ما را به آنچه باطل‌گرايان انجام دادند مجازات مي‌کني؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1128, N'اين گونه، آيات را توضيح مي‌دهيم؛ و شايد به سوي حق بازگردند (و بدانند نداي توحيد در درون جانشان، از روز نخست بوده است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1129, N'و بر آنها بخوان سرگذشت آن کس را که آيات خود را به او داديم؛ ولي (سرانجام) خود را از آن تهي ساخت و شيطان در پي او افتاد، و از گمراهان شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1130, N'و اگر مي‌خواستيم، (مقام) او را با اين آيات (و علوم و دانشها) بالا مي‌برديم؛ (اما اجبار، بر خلاف سنت ماست؛ پس او را به حال خود رها کرديم) و او به پستي گراييد، و از هواي نفس پيروي کرد! مثل او همچون سگ (هار) است که اگر به او حمله کني، دهانش را باز، و زبانش را برون مي‌آورد، و اگر او را به حال خود واگذاري، باز همين کار را مي‌کند؛ (گويي چنان تشنه دنياپرستي است که هرگز سيراب نمي‌شود! (اين مثل گروهي است که آيات ما را تکذيب کردند؛ اين داستانها را (براي آنها) بازگو کن، شايد بينديشند (و بيدار شوند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1131, N'چه بد مثلي دارند گروهي که آيات ما را تکذيب کردند؛ و آنها تنها به خودشان ستم مي‌نمودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1132, N'آن کس را که خدا هدايت کند، هدايت يافته (واقعي) اوست؛ و کساني را که (بخاطر اعمالشان) گمراه سازد، زيانکاران (واقعي) آنها هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1133, N'به يقين، گروه بسياري از جن و انس را براي دوزخ آفريديم؛ آنها دلها [= عقلها ]يي دارند که با آن (انديشه نمي‌کنند، و) نمي‌فهمند؛ و چشماني که با آن نمي‌بينند؛ و گوشهايي که با آن نمي‌شنوند؛ آنها همچون چهارپايانند؛ بلکه گمراهتر! اينان همان غافلانند (چرا که با داشتن همه‌گونه امکانات هدايت، باز هم گمراهند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1134, N'و براي خدا، نامهاي نيک است؛ خدا را به آن (نامها) بخوانيد! و کساني را که در اسماء خدا تحريف مي‌کنند (و بر غير او مي‌نهند، و شريک برايش قائل مي‌شوند)، رها سازيد! آنها بزودي جزاي اعمالي را که انجام مي‌دادند، مي‌بينند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1135, N'و از آنها که آفريديم، گروهي بحق هدايت مي‌کنند، و بحق اجراي عدالت مي‌نمايند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1136, N'و آنها که آيات ما را تکذيب کردند، به تدريج از جائي که نمي‌دانند، گرفتار مجازاتشان خواهيم کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1137, N'و به آنها مهلت مي‌دهم (تا مجازاتشان دردناکتر باشد)؛ زيرا طرح و نقشه من، قوي (و حساب شده) است. (و هيچ کس را قدرت فرار از آن نيست.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1138, N'آيا فکر نکردند که همنشين آنها [= پيامبر] هيچ‌گونه (اثري از) جنون ندارد؟! (پس چگونه چنين نسبت ناروايي به او مي‌دهند؟!) او فقط بيم دهنده آشکاري است (که مردم را متوجه وظايفشان مي‌سازد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1139, N'آيا در حکومت و نظام آسمانها و زمين، و آنچه خدا آفريده است، (از روي دقت و عبرت) نظر نيفکندند؟! (و آيا در اين نيز انديشه نکردند که) شايد پايان زندگي آنها نزديک شده باشد؟! (اگر به اين کتاب آسماني روشن ايمان نياورند،) بعد از آن به کدام سخن ايمان خواهند آورد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1140, N'هر کس را خداوند (به جرم اعمال زشتش) گمراه سازد، هدايت کننده‌اي ندارد؛ و آنها را در طغيان و سرکشي‌شان رها مي‌سازد، تا سرگردان شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1141, N'درباره قيامت از تو سؤال مي‌کنند، کي فرامي‌رسد؟! بگو: «علمش فقط نزد پروردگار من است؛ و هيچ‌کس جز او (نمي‌تواند) وقت آن را آشکار سازد؛ (اما قيام قيامت، حتي) در آسمانها و زمين، سنگين (و بسيار پر اهميت) است؛ و جز بطور ناگهاني، به سراغ شما نمي‌آيد!» (باز) از تو سؤال مي‌کنند، چنان که گويي تو از زمان وقوع آن باخبري! بگو: «علمش تنها نزد خداست؛ ولي بيشتر مردم نمي‌دانند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1142, N'بگو: «من مالک سود و زيان خويش نيستم، مگر آنچه را خدا بخواهد؛ (و از غيب و اسرار نهان نيز خبر ندارم، مگر آنچه خداوند اراده کند؛) و اگر از غيب باخبر بودم، سود فراواني براي خود فراهم مي‌کردم، و هيچ بدي (و زياني) به من نمي‌رسيد؛ من فقط بيم‌دهنده و بشارت‌دهنده‌ام براي گروهي که ايمان مي‌آورند! (و آماده پذيرش حقند)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1143, N'او خدايي است که (همه) شما را از يک فرد آفريد؛ و همسرش را نيز از جنس او قرار داد، تا در کنار او بياسايد. سپس هنگامي که با او آميزش کرد، حملي سبک برداشت، که با وجود آن، به کارهاي خود ادامه مي‌داد؛ و چون سنگين شد، هر دو از خداوند و پروردگار خود خواستند «اگر فرزند صالحي به ما دهي، از شاکران خواهيم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1144, N'اما هنگامي که خداوند فرزند صالحي به آنها داد، (موجودات ديگر را در اين موهبت مؤثر دانستند؛ و) براي خدا، در اين نعمت که به آنها بخشيده بود، همتاياني قائل شدند؛ خداوند برتر است از آنچه همتاي او قرار مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1145, N'آيا موجوداتي را همتاي او قرارمي‌دهند که چيزي را نمي‌آفرينند، و خودشان مخلوقند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1146, N'و نمي‌توانند آنان را ياري کنند، و نه خودشان را ياري مي‌دهند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1147, N'و هرگاه آنها را به سوي هدايت دعوت کنيد، از شما پيروي نمي‌کنند؛ و براي شما يکسان است چه آنها را دعوت کنيد و چه خاموش باشيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1148, N'آنهايي را که غير از خدا مي‌خوانيد (و پرستش مي‌کنيد)، بندگاني همچون خود شما هستند؛ آنها را بخوانيد، و اگر راست مي‌گوييد بايد به شما پاسخ دهند (و تقاضايتان را برآورند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1149, N'آيا (آنها حداقل همانند خود شما) پاهايي دارند که با آن راه بروند؟! يا دستهايي دارند که با آن چيزي را بگيرند (و کاري انجام دهند)؟! يا چشماني دارند که با آن ببينند؟! يا گوشهايي دارند که با آن بشنوند؟! (نه، هرگز، هيچ‌کدام،) بگو: «(اکنون که چنين است،) بتهاي خويش را که شريک خدا قرار داده‌ايد (بر ضد من) بخوانيد، و براي من نقشه بکشيد، و لحظه‌اي مهلت ندهيد، (تا بدانيد کاري از آنها ساخته نيست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1150, N'ولي و سرپرست من، خدايي است که اين کتاب را نازل کرده؛ و او همه صالحان را سرپرستي مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1151, N'و آنهايي را که جز او مي‌خوانيد، نمي‌توانند ياريتان کنند، و نه (حتي) خودشان را ياري دهند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1152, N'و اگر آنها را به هدايت فرا خوانيد، سخنانتان را نمي‌شنوند! و آنها را مي‌بيني (که با چشمهاي مصنوعيشان) به تو نگاه مي‌کنند، اما در حقيقت نمي‌بينند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1153, N'(به هر حال) با آنها مدارا کن و عذرشان را بپذير، و به نيکي‌ها دعوت نما، و از جاهلان روي بگردان (و با آنان ستيزه مکن)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1154, N'و هرگاه وسوسه‌اي از شيطان به تو رسد، به خدا پناه بر؛ که او شنونده و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1155, N'پرهيزگاران هنگامي که گرفتار وسوسه‌هاي شيطان شوند، به ياد (خدا و پاداش و کيفر او) مي‌افتند؛ و (در پرتو ياد او، راه حق را مي‌بينند و) ناگهان بينا مي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1156, N'و (ناپرهيزگاران را) برادرانشان (از شياطين) پيوسته در گمراهي پيش مي‌برند، و باز نمي‌ايستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1157, N'هنگامي که (در نزول وحي تاخير افتد، و) آيه‌اي براي آنان نياوري، مي‌گويند: «چرا خودت (از پيش خود) آن را برنگزيدي؟!» بگو: «من تنها از چيزي پيروي مي‌کنم که بر من وحي مي‌شود؛ اين وسيله بينايي از طرف پروردگارتان، و مايه هدايت و رحمت است براي جمعيتي که ايمان مي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1158, N'هنگامي که قرآن خوانده شود، گوش فرا دهيد و خاموش باشيد؛ شايد مشمول رحمت خدا شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1159, N'پروردگارت را در دل خود، از روي تضرع و خوف، آهسته و آرام، صبحگاهان و شامگاهان، ياد کن؛ و از غافلان مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1160, N'آنها که (در مقام قرب) نزد پروردگار تو هستند، (هيچ‌گاه) از عبادتش تکبر نمي‌ورزند، و او را تسبيح مي‌گويند، و برايش سجده مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1161, N'از تو درباره انفال [= غنايم، و هرگونه مال بدون مالک مشخص‌] سؤال مي‌کنند ؛ بگو: «انفال مخصوص خدا و پيامبر است؛ پس، از (مخالفت فرمان) خدا بپرهيزيد! و خصومتهايي را که در ميان شماست، آشتي دهيد! و خدا و پيامبرش را اطاعت کنيد اگر ايمان داريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1162, N'مؤمنان، تنها کساني هستند که هرگاه نام خدا برده شود، دلهاشان ترسان ميگردد؛ و هنگامي که آيات او بر آنها خوانده مي‌شود، ايمانشان فزونتر مي‌گردد؛ و تنها بر پروردگارشان توکل دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1163, N'آنها که نماز را برپا مي‌دارند؛ و از آنچه به آنها روزي داده‌ايم، انفاق مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1164, N'(آري،) مؤمنان حقيقي آنها هستند؛ براي آنان درجاتي (مهم) نزد پروردگارشان است؛ و براي آنها، آمرزش و روزي بي‌نقص و عيب است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1165, N'همان‌گونه که خدا تو را بحق از خانه (به سوي ميدان بدر،) بيرون فرستاد، در حالي که گروهي از مؤمنان ناخشنود بودند (؛ولي سرانجامش پيروزي بود! ناخشنودي عده‌اي از چگونگي تقسيم غنايم بدر نيز چنين است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1166, N'آنها پس از روشن شدن حق، باز با تو مجادله مي‌کردند؛ (و چنان ترس و وحشت آنها را فراگرفته بود، که) گويي به سوي مرگ رانده مي‌شوند، و آن را با چشم خود مي‌نگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1167, N'و (به ياد آريد) هنگامي را که خداوند به شما وعده داد که يکي از دو گروه [= کاروان تجاري قريش، يا لشکر مسلح آنها] نصيب شما خواهد بود؛ و شما دوست مي‌داشتيد که کاروان (غير مسلح) براي شما باشد (و بر آن پيروز شويد)؛ ولي خداوند مي‌خواهد حق را با کلمات خود تقويت، و ريشه کافران را قطع کند؛ (از اين رو شما را بر خلاف ميلتان با لشکر قريش درگير ساخت، و آن پيروزي بزرگ نصيبتان شد.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1168, N'تا حق را تثبيت کند، و باطل را از ميان بردارد، هر چند مجرمان کراهت داشته باشند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1169, N'(به خاطر بياوريد) زماني را (که از شدت ناراحتي در ميدان بدر،) از پروردگارتان کمک مي‌خواستيد؛ و او خواسته شما را پذيرفت (و گفت): من شما را با يکهزار از فرشتگان، که پشت سر هم فرود مي‌آيند، ياري مي‌کنم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1170, N'ولي خداوند، اين را تنها براي شادي و اطمينان قلب شما قرار داد؛ وگرنه، پيروزي جز از طرف خدا نيست؛ خداوند توانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1171, N'و (ياد آوريد) هنگامي را که خواب سبکي که مايه آرامش از سوي خدا بود، شما را فراگرفت؛ و آبي از آسمان برايتان فرستاد، تا شما را با آن پاک کند؛ و پليدي شيطان را از شما دور سازد؛ و دلهايتان را محکم، و گامها را با آن استوار دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1172, N'و (به ياد آر) موقعي را که پروردگارت به فرشتگان وحي کرد: «من با شما هستم؛ کساني را که ايمان آورده‌اند، ثابت قدم داريد! بزودي در دلهاي کافران ترس و وحشت مي‌افکنم؛ ضربه‌ها را بر بالاتر از گردن (بر سرهاي دشمنان) فرود آريد! و همه انگشتانشان را قطع کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1173, N'اين بخاطر آن است که آنها با خدا و پيامبرش (ص) دشمني ورزيدند؛ و هر کس با خدا و پيامبرش دشمني کند، (کيفر شديدي مي‌بيند؛) و خداوند شديد العقاب است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1174, N'اين (مجازات دنيا) را بچشيد! و براي کافران، مجازات آتش (در جهان ديگر) خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1175, N'اي کساني که ايمان آورده‌ايد! هنگامي که با انبوه کافران در ميدان نبرد روبه‌رو شويد، به آنها پشت نکنيد (و فرار ننماييد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1176, N'و هر کس در آن هنگام به آنها پشت کند -مگر آنکه هدفش کناره‌گيري از ميدان براي حمله مجدد، و يا به قصد پيوستن به گروهي (از مجاهدان) بوده باشد- (چنين کسي) به غضب خدا گرفتار خواهد شد؛ و جايگاه او جهنم، و چه بد جايگاهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1177, N'اين شما نبوديد که آنها را کشتيد؛ بلکه خداوند آنها را کشت! و اين تو نبودي (اي پيامبر که خاک و سنگ به صورت آنها) انداختي؛ بلکه خدا انداخت! و خدا مي‌خواست مؤمنان را به اين وسيله امتحان خوبي کند؛ خداوند شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1178, N'سرنوشت مؤمنان و کافران، همان بود که ديديد! و خداوند سست‌کننده نقشه‌هاي کافران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1179, N'اگر شما فتح و پيروزي مي‌خواهيد، پيروزي به سراغ شما آمد! و اگر (از مخالفت) خودداري کنيد، براي شما بهتر است! و اگر بازگرديد، ما هم باز خواهيم گشت؛ و جمعيت شما هر چند زياد باشد، شما را (از ياري خدا) بي‌نياز نخواهد کرد؛ و خداوند با مؤمنان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1180, N'اي کساني که ايمان آورده‌ايد! خدا و پيامبرش را اطاعت کنيد؛ و سرپيچي ننماييد در حالي که (سخنان او را) مي‌شنويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1181, N'و همانند کساني نباشيد که مي‌گفتند: «شنيديم!» ولي در حقيقت نمي‌شنيدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1182, N'بدترين جنبندگان نزد خدا، افراد کر و لالي هستند که انديشه نمي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1183, N'و اگر خداوند خيري در آنها مي‌دانست، (حرف حق را) به گوش آنها مي‌رساند؛ ولي (با اين حال که دارند،) اگر حق را به گوش آنها برساند، سرپيچي کرده و روگردان مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1184, N'اي کساني که ايمان آورده‌ايد! دعوت خدا و پيامبر را اجابت کنيد هنگامي که شما را به سوي چيزي مي‌خواند که شما را حيات مي‌بخشد! و بدانيد خداوند ميان انسان و قلب او حايل مي‌شود، و همه شما (در قيامت) نزد او گردآوري مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1185, N'و از فتنه‌اي بپرهيزيد که تنها به ستمکاران شما نمي‌رسد؛ (بلکه همه را فرا خواهد گرفت؛ چرا که ديگران سکوت اختيار کردند.) و بدانيد خداوند کيفر شديد دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1186, N'و به خاطر بياوريد هنگامي را که شما در روي زمين، گروهي کوچک و اندک و زبون بوديد؛ آنچنان که مي‌ترسيديد مردم شما را بربايند! ولي او شما را پناه داد؛ و با ياري خود تقويت کرد؛ و از روزيهاي پاکيزه بهره‌مند ساخت؛ شايد شکر نعمتش را بجا آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1187, N'اي کساني که ايمان آورده‌ايد! به خدا و پيامبر خيانت نکنيد! و (نيز) در امانات خود خيانت روا مداريد، در حالي که ميدانيد (اين کار، گناه بزرگي است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1188, N'و بدانيد اموال و اولاد شما، وسيله آزمايش است؛ و (براي کساني که از عهده امتحان برآيند،) پاداش عظيمي نزد خداست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1189, N'اي کساني که ايمان آورده‌ايد! اگر از (مخالفت فرمان) خدا بپرهيزيد، براي شما وسيله‌اي جهت جدا ساختن حق از باطل قرارمي‌دهد؛ (روشن‌بيني خاصي که در پرتو آن، حق را از باطل خواهيد شناخت؛) و گناهانتان را مي‌پوشاند؛ و شما را مي‌زمرزد؛ و خداوند صاحب فضل و بخشش عظيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1190, N'(به خاطر بياور) هنگامي را که کافران نقشه مي‌کشيدند که تو را به زندان بيفکنند، يا به قتل برسانند، و يا (از مکه) خارج سازند؛ آنها چاره مي‌انديشيدند (و نقشه مي‌کشيدند)، و خداوند هم تدبير مي‌کرد؛ و خدا بهترين چاره جويان و تدبيرکنندگان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1191, N'و هنگامي که آيات ما بر آنها خوانده مي‌شود، مي‌گويند: «شنيديم؛ (چيز مهمي نيست؛) ما هم اگر بخواهيم مثل آن را مي‌گوييم؛ اينها همان افسانه‌هاي پيشينيان است!» (ولي دروغ مي‌گويند، و هرگز مثل آن را نمي‌آورند.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1192, N'و (به خاطر بياور) زماني را که گفتند: «پروردگارا! اگر اين حق است و از طرف توست، باراني از سنگ از آسمان بر ما فرود آر! يا عذاب دردناکي براي ما بفرست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1193, N'ولي (اي پيامبر!) تا تو در ميان آنها هستي، خداوند آنها را مجازات نخواهد کرد؛ و (نيز) تا استغفار مي‌کنند، خدا عذابشان نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1194, N'چرا خدا آنها را مجازات نکند، با اينکه از (عبادت موحدان در کنار) مسجد الحرام جلوگيري مي‌کنند در حالي که سرپرست آن نيستند؟! سرپرست آن، فقط پرهيزگارانند؛ ولي بيشتر آنها نمي‌دانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1195, N'(آنها که مدعي هستند ما هم نماز داريم،) نمازشان نزد خانه (خدا)، چيزي جز «سوت کشيدن» «کف زدن» نبود؛ پس بچشيد عذاب (الهي) را بخاطر کفرتان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1196, N'آنها که کافر شدند، اموالشان را براي بازداشتن (مردم) از راه خدا خرج مي‌کنند؛ آنان اين اموال را (که براي به دست آوردنش زحمت کشيده‌اند، در اين راه) مصرف مي‌کنند، اما مايه حسرت و اندوهشان خواهد شد؛ و سپس شکست خواهند خورد؛ و (در جهان ديگر) کافران همگي به سوي دوزخ گردآوري خواهند شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1197, N'(اينها همه) بخاطر آن است که خداوند (مي‌خواهد) ناپاک را از پاک جدا سازد، و ناپاکها را روي هم بگذارد، و همه را متراکم سازد، و يکجا در دوزخ قرار دهد؛ و اينها هستند زيانکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1198, N'به آنها که کافر شدند بگو: «چنانچه از مخالفت باز ايستند، (و ايمان آورند،) گذشته آنها بخشوده خواهد شد؛ و اگر به اعمال سابق بازگردند، سنت خداوند در گذشتگان، درباره آنها جاري مي‌شود (؛و حکم نابودي آنان صادر مي‌گردد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1199, N'و با آنها پيکار کنيد، تا فتنه [= شرک و سلب آزادي‌] برچيده شود، و دين (و پرستش) همه مخصوص خدا باشد! و اگر آنها (از راه شرک و فساد بازگردند، و از اعمال نادرست) خودداري کنند، (خداوند آنها را مي‌پذيرد؛) خدا به آنچه انجام مي‌دهند بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1200, N'و اگر سرپيچي کنند، بدانيد (ضرري به شما نمي‌رسانند؛) خداوند سرپرست شماست! چه سرپرست خوبي! و چه ياور خوبي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1201, N'بدانيد هرگونه غنيمتي به دست آوريد، خمس آن براي خدا، و براي پيامبر، و براي ذي‌القربي و يتيمان و مسکينان و واماندگان در راه (از آنها) است، اگر به خدا و آنچه بر بنده خود در روز جدايي حق از باطل، روز درگيري دو گروه (باايمان و بي‌ايمان) [= روز جنگ بدر] نازل کرديم، ايمان آورده‌ايد؛ و خداوند بر هر چيزي تواناست!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1202, N'در آن هنگام که شما در طرف پايين بوديد، و آنها در طرف بالأ (و دشمن بر شما برتري داشت؛) و کاروان (قريش)، پايين تر از شما بود؛ (و وضع چنان سخت بود که) اگر با يکديگر وعده مي‌گذاشتيد (که در ميدان نبرد حاضر شويد)، در انجام وعده خود اختلاف مي‌کرديد؛ ولي (همه اينها) براي آن بود که خداوند، کاري را که مي‌بايست انجام شود، تحقق بخشد؛ تا آنها که هلاک (و گمراه) مي‌شوند، از روي اتمام حجت باشد؛ و آنها که زنده مي‌شوند (و هدايت مي‌يابند)، از روي دليل روشن باشد؛ و خداوند شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1203, N'در آن هنگام که خداوند تعداد آنها را در خواب به تو کم نشان داد؛ و اگر فراوان نشان مي‌داد، مسلما سست مي‌شديد؛ و (درباره شروع جنگ با آنها) کارتان به اختلاف مي‌کشيد؛ ولي خداوند (شما را از شر اينها) سالم نگه داشت؛ خداوند به آنچه درون سينه‌هاست، داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1204, N'و در آن هنگام (که در ميدان نبرد،) با هم روبه‌رو شديد، آنها را به چشم شما کم نشان مي‌داد؛ و شما را (نيز) به چشم آنها کم مي‌نمود؛ تا خداوند، کاري را که مي‌بايست انجام گيرد، صورت بخشد؛ (شما نترسيد و اقدام به جنگ کنيد، آنها هم وحشت نکنند و حاضر به جنگ شوند، و سرانجام شکست بخورند!) و همه کارها به خداوند باز مي‌گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1205, N'اي کساني که ايمان آورده‌ايد! هنگامي که (در ميدان نبرد) با گروهي رو به رو مي‌شويد، ثابت قدم باشيد! و خدا را فراوان ياد کنيد، تا رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1206, N'و (فرمان) خدا و پيامبرش را اطاعت نماييد! و نزاع (و کشمکش) نکنيد، تا سست نشويد، و قدرت (و شوکت) شما از ميان نرود! و صبر و استقامت کنيد که خداوند با استقامت کنندگان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1207, N'و مانند کساني نباشيد که از روي هوي پرستي و غرور و خودنمايي در برابر مردم، از سرزمين خود به (سوي ميدان بدر) بيرون آمدند؛ و (مردم را) از راه خدا بازمي‌داشتند؛ (و سرانجام شکست خوردند) و خداوند به آنچه عمل مي‌کنند، احاطه (و آگاهي) دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1208, N'و (به ياد آور) هنگامي را که شيطان، اعمال آنها [= مشرکان‌] را در نظرشان جلوه داد، و گفت: «امروز هيچ کس از مردم بر شما پيروز نمي‌گردد! و من، همسايه (و پناه‌دهنده) شما هستم!» اما هنگامي که دو گروه (کافران، و مؤمنان مورد حمايت فرشتگان) در برابر يکديگر قرار گرفتند، به عقب برگشت و گفت: «من از شما (دوستان و پيروانم) بيزارم! من چيزي مي‌بينم که شما نمي‌بينيد؛ من از خدا مي‌ترسم، خداوند شديدالعقاب است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1209, N'و هنگامي را که منافقان، و آنها که در دلهايشان بيماري است مي‌گفتند: «اين گروه (مسلمانان) را دينشان مغرور ساخته است.» (آنها نمي‌دانستند که) هر کس بر خدا توکل کند، (پيروز مي‌گردد؛) خداوند قدرتمند و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1210, N'و اگر ببيني کافران را هنگامي که فرشتگان (مرگ)، جانشان را مي‌گيرند و بر صورت و پشت آنها مي‌زنند و (مي‌گويند:) بچشيد عذاب سوزنده را (،به حال آنان تأسف خواهي خورد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1211, N'اين، در مقابل کارهايي است که از پيش فرستاده‌ايد؛ و خداوند نسبت به بندگانش، هرگز ستم روا نمي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1212, N'(حال اين گروه مشرکان،) همانند حال نزديکان فرعون، و کساني است که پيش از آنان بودند؛ آنها آيات خدا را انکار کردند؛ خداوند هم آنان را به گناهانشان کيفر داد؛ خداوند قوي، و کيفرش شديد است‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1213, N'اين، بخاطر آن است که خداوند، هيچ نعمتي را که به گروهي داده، تغيير نمي‌دهد؛ جز آنکه آنها خودشان را تغيير دهند؛ و خداوند، شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1214, N'اين، (درست) شبيه (حال) فرعونيان و کساني است که پيش از آنها بودند؛ آيات پروردگارشان را تکذيب کردند؛ ما هم بخاطر گناهانشان، آنها را هلاک کرديم، و فرعونيان را غرق نموديم؛ و همه آنها ظالم (و ستمگر) بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1215, N'به يقين، بدترين جنبندگان نزد خدا، کساني هستند که کافر شدند و ايمان نمي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1216, N'همان کساني که با آنها پيمان بستي؛ سپس هر بار عهد و پيمان خود را مي‌شکنند؛ و (از پيمان شکني و خيانت،) پرهيز ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1217, N'اگر آنها را در (ميدان) جنگ بيابي، آنچنان به آنها حمله کن که جمعيتهايي که پشت سر آنها هستند، پراکنده شوند؛ شايد متذکر گردند (و عبرت گيرند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1218, N'و هرگاه (با ظهور نشانه‌هايي،) از خيانت گروهي بيم داشته باشي (که عهد خود را شکسته، حمله غافلگيرانه کنند)، بطور عادلانه به آنها اعلام کن که پيمانشان لغو شده است؛ زيرا خداوند، خائنان را دوست نمي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1219, N'آنها که راه کفر پيش گرفتند، گمان نکنند (با اين اعمال،) پيش برده‌اند (و از قلمرو کيفر ما، بيرون رفته‌اند)! آنها هرگز ما را ناتوان نخواهند کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1220, N'هر نيرويي در قدرت داريد، براي مقابله با آنها [= دشمنان‌]، آماده سازيد! و (همچنين) اسبهاي ورزيده (براي ميدان نبرد)، تا به وسيله آن، دشمن خدا و دشمن خويش را بترسانيد! و (همچنين) گروه ديگري غير از اينها را، که شما نمي‌شناسيد و خدا آنها را مي‌شناسد! و هر چه در راه خدا (و تقويت بنيه دفاعي اسلام) انفاق کنيد، بطور کامل به شما بازگردانده مي‌شود، و به شما ستم نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1221, N'و اگر تمايل به صلح نشان دهند، تو نيز از در صلح درآي؛ و بر خدا توکل کن، که او شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1222, N'و اگر بخواهند تو را فريب دهند، خدا براي تو کافي است؛ او همان کسي است که تو را، با ياري خود و مؤمنان، تقويت کرد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1223, N'و دلهاي آنها را با هم، الفت داد! اگر تمام آنچه را روي زمين است صرف مي‌کردي که ميان دلهاي آنان الفت دهي، نمي‌توانستي! ولي خداوند در ميان آنها الفت ايجاد کرد! او توانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1224, N'اي پيامبر! خداوند و مؤمناني که از تو پيروي مي‌کنند، براي حمايت تو کافي است (؛فقط بر آنها تکيه کن)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1225, N'اي پيامبر! مؤمنان را به جنگ (با دشمن) تشويق کن! هرگاه بيست نفر با استقامت از شما باشند، بر دويست نفر غلبه مي‌کنند؛ و اگر صد نفر باشند، بر هزار نفر از کساني که کافر شدند، پيروز مي‌گردند؛ چرا که آنها گروهي هستند که نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1226, N'هم اکنون خداوند به شما تخفيف داد، و دانست که در شما ضعفي است؛ بنابراين، هرگاه يکصد نفر با استقامت از شما باشند، بر دويست نفر پيروز مي‌شوند؛ و اگر يکهزار نفر باشند، بر دو هزار نفر به فرمان خدا غلبه خواهند کرد! و خدا با صابران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1227, N'هيچ پيامبري حق ندارد اسيراني (از دشمن) بگيرد تا کاملا بر آنها پيروز گردد (؛و جاي پاي خود را در زمين محکم کند)! شما متاع ناپايدار دنيا را مي‌خواهيد؛ (و مايليد اسيران بيشتري بگيريد، و در برابر گرفتن فديه آزاد کنيد؛ ولي خداوند، سراي ديگر را (براي شما) مي‌خواهد؛ و خداوند قادر و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1228, N'اگر فرمان سابق خدا نبود (که بدون ابلاغ، هيچ امتي را کيفر ندهد)، بخاطر چيزي [= اسيراني‌] که گرفتيد، مجازات بزرگي به شما مي‌رسيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1229, N'از آنچه به غنيمت گرفته‌ايد، حلال و پاکيزه بخوريد؛ و از خدا بپرهيزيد؛ خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1230, N'اي پيامبر! به اسيراني که در دست شما هستند بگو: «اگر خداوند، خيري در دلهاي شما بداند، (و نيات پاکي داشته باشيد،) بهتر از آنچه از شما گرفته شده به شما مي‌دهد؛ و شما را مي‌بخشد؛ و خداوند آمرزنده و مهربان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1231, N'اما اگر بخواهند با تو خيانت کنند، (تازگي ندارد) آنها پيش از اين (نيز) به خدا خيانت کردند؛ و خداوند (شما را) بر آنها پيروز کرد؛ خداوند دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1232, N'کساني که ايمان آوردند و هجرت نمودند و با اموال و جانهاي خود در راه خدا جهاد کردند، و آنها که پناه دادند و ياري نمودند، آنها ياران يکديگرند؛ و آنها که ايمان آوردند و مهاجرت نکردند، هيچ گونه ولايت [= دوستي و تعهدي‌] در برابر آنها نداريد تا هجرت کنند! و (تنها) اگر در (حفظ) دين (خود) از شما ياري طلبند، بر شماست که آنها را ياري کنيد، جز بر ضد گروهي که ميان شما و آنها، پيمان (ترک مخاصمه) است؛ و خداوند به آنچه عمل مي‌کنيد، بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1233, N'کساني که کافر شدند، اولياء (و ياوران و مدافعان) يکديگرند؛ اگر (اين دستور را) انجام ندهيد، فتنه و فساد عظيمي در زمين روي مي‌دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1234, N'و آنها که ايمان آوردند و هجرت نمودند و در راه خدا جهاد کردند، و آنها که پناه دادند و ياري نمودند، آنان مؤمنان حقيقي‌اند؛ براي آنها، آمرزش (و رحمت خدا) و روزي شايسته‌اي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1235, N'و کساني که بعدا ايمان آوردند و هجرت کردند و با شما جهاد نمودند، از شما هستند؛ و خويشاوندان نسبت به يکديگر، در احکامي که خدا مقرر داشته، (از ديگران) سزاوارترند؛ خداوند به همه چيز داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1236, N'(اين، اعلام) بيزاري از سوي خدا و پيامبر او، به کساني از مشرکان است که با آنها عهد بسته‌ايد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1237, N'با اين حال، چهار ماه (مهلت داريد که آزادانه) در زمين سير کنيد (و هر جا مي‌خواهيد برويد، و بينديشيد)! و بدانيد شما نمي‌توانيد خدا را ناتوان سازيد، (و از قدرت او فرار کنيد! و بدانيد) خداوند خوارکننده کافران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1238, N'و اين، اعلامي است از ناحيه خدا و پيامبرش به (عموم) مردم در روز حج اکبر [= روز عيد قربان‌] که: خداوند و پيامبرش از مشرکان بيزارند! با اين حال، اگر توبه کنيد، براي شما بهتر است! و اگر سرپيچي نماييد، بدانيد شما نمي‌توانيد خدا را ناتوان سازيد (و از قلمرو قدرتش خارج شويد)! و کافران را به مجازات دردناک بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1239, N'مگر کساني از مشرکان که با آنها عهد بستيد، و چيزي از آن را در حق شما فروگذار نکردند، و احدي را بر ضد شما تقويت ننمودند؛ پيمان آنها را تا پايان مدتشان محترم بشمريد؛ زيرا خداوند پرهيزگاران را دوست دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1240, N'(اما) وقتي ماه‌هاي حرام پايان گرفت، مشرکان را هر جا يافتيد به قتل برسانيد؛ و آنها را اسير سازيد؛ و محاصره کنيد؛ و در هر کمينگاه، بر سر راه آنها بنشينيد! هرگاه توبه کنند، و نماز را برپا دارند، و زکات را بپردازند، آنها را رها سازيد؛ زيرا خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1241, N'و اگر يکي از مشرکان از تو پناهندگي بخواهد، به او پناه ده تا سخن خدا را بشنود (و در آن بينديشد)! سپس او را به محل امنش برسان، چرا که آنها گروهي ناآگاهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1242, N'چگونه براي مشرکان پيماني نزد خدا و رسول او خواهد بود (در حالي که آنها همواره آماده شکستن پيمانشان هستند)؟! مگر کساني که نزد مسجد الحرام با آنان پيمان بستيد؛ (و پيمان خود را محترم شمردند؛) تا زماني که در برابر شما وفادار باشند، شما نيز وفاداري کنيد، که خداوند پرهيزگاران را دوست دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1243, N'چگونه (پيمان مشرکان ارزش دارد)، در حالي که اگر بر شما غالب شوند، نه ملاحظه خويشاوندي با شما را مي‌کنند، و نه پيمان را؟! شما را با زبان خود خشنود مي‌کنند، ولي دلهايشان ابا دارد؛ و بيشتر آنها فرمانبردار نيستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1244, N'آنها آيات خدا را به بهاي کمي فروختند؛ و (مردم را) از راه او باز داشتند؛ آنها اعمال بدي انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1245, N'(نه تنها درباره شما،) درباره هيچ فرد باايماني رعايت خويشاوندي و پيمان را نمي‌کنند؛ و آنها همان تجاوز کارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1246, N'(ولي) اگر توبه کنند، نماز را برپا دارند، و زکات را بپردازند، برادر ديني شما هستند؛و ما آيات خود را براي گروهي که مي‌دانند (و مي‌انديشند)، شرح مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1247, N'و اگر پيمانهاي خود را پس از عهد خويش بشکنند، و آيين شما را مورد طعن قرار دهند، با پيشوايان کفر پيکار کنيد؛ چرا که آنها پيماني ندارند؛ شايد (با شدت عمل) دست بردارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1248, N'آيا با گروهي که پيمانهاي خود را شکستند، و تصميم به اخراج پيامبر گرفتند، پيکار نمي‌کنيد؟! در حالي که آنها نخستين بار (پيکار با شما را) آغاز کردند؛ آيا از آنها مي‌ترسيد؟! با اينکه خداوند سزاوارتر است که از او بترسيد، اگر مؤمن هستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1249, N'با آنها پيکار کنيد، که خداوند آنان را به دست شما مجازات مي‌کند؛ و آنان را رسوا مي‌سازد؛ و سينه گروهي از مؤمنان را شفا مي‌بخشد(؛ و بر قلب آنها مرهم مي‌نهد)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1250, N'و خشم دلهاي آنان را از ميان مي‌برد! و خدا توبه هر کس را بخواهد (و شايسته بداند)، مي‌پذيرد؛ و خداوند دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1251, N'آيا گمان کرديد که (به حال خود) رها مي‌شويد در حالي که هنوز کساني که از شما جهاد کردند، و غير از خدا و رسولش و مؤمنان را محرم اسرار خويش انتخاب ننمودند، (از ديگران) مشخص نشده‌اند؟! (بايد آزمون شويد؛ و صفوف از هم جدا گردد؛) و خداوند به آنچه عمل مي‌کنيد، آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1252, N'مشرکان حق ندارند مساجد خدا را آباد کنند در حالي که به کفر خويش گواهي مي‌دهند! آنها اعمالشان نابود (و بي‌ارزش) شده؛ و در آتش (دوزخ)، جاودانه خواهند ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1253, N'مساجد خدا را تنها کسي آباد مي‌کند که ايمان به خدا و روز قيامت آورده، و نماز را برپا دارد، و زکات را بپردازد، و جز از خدا نترسد؛ اميد است چنين گروهي از هدايت‌يافتگان باشند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1254, N'آيا سيراب کردن حجاج، و آباد ساختن مسجد الحرام را، همانند (عمل) کسي قرار داديد که به خدا و روز قيامت ايمان آورده، و در راه او جهاد کرده است؟!(اين دو،) نزد خدا مساوي نيستند! و خداوند گروه ظالمان را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1255, N'آنها که ايمان آوردند، و هجرت کردند، و با اموال و جانهايشان در راه خدا جهاد نمودند، مقامشان نزد خدا برتر است؛ و آنها پيروز و رستگارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1256, N'پروردگارشان آنها را به رحمتي از ناحيه خود، و رضايت (خويش)، و باغهايي از بهشت بشارت مي‌دهد که در آن، نعمتهاي جاودانه دارند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1257, N'همواره و تا ابد در اين باغها (و در ميان اين نعمتها) خواهند بود؛ زيرا پاداش عظيم نزد خداوند است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1258, N'اي کساني که ايمان آورده‌ايد! هرگاه پدران و برادران شما، کفر را بر ايمان ترجيح دهند، آنها را ولي (و يار و ياور و تکيه‌گاه) خود قرار ندهيد! و کساني از شما که آنان را ولي خود قرار دهند، ستمگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1259, N'بگو: «اگر پدران و فرزندان و برادران و همسران و طايفه شما، و اموالي که به دست آورده‌ايد، و تجارتي که از کساد شدنش مي‌ترسيد، و خانه هائي که به آن علاقه داريد، در نظرتان از خداوند و پيامبرش و جهاد در راهش محبوبتر است، در انتظار باشيد که خداوند عذابش را بر شما نازل کند؛ و خداوند جمعيت نافرمانبردار را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1260, N'خداوند شما را در جاهاي زيادي ياري کرد (و بر دشمن پيروز شديد)؛ و در روز حنين (نيز ياري نمود)؛ در آن هنگام که فزوني جمعيتتان شما را مغرور ساخت، ولي (اين فزوني جمعيت) هيچ به دردتان نخورد و زمين با همه وسعتش بر شما تنگ شده؛ سپس پشت (به دشمن) کرده، فرار نموديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1261, N'سپس خداوند «سکينه» خود را بر پيامبرش و بر مؤمنان نازل کرد؛ و لشکرهايي فرستاد که شما نمي‌ديديد؛ و کافران را مجازات کرد؛ و اين است جزاي کافران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1262, N'سپس خداوند -بعد از آن- توبه هر کس را بخواهد (و شايسته بداند)، مي‌پذيرد؛ و خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1263, N'اي کساني که ايمان آورده‌ايد! مشرکان ناپاکند؛ پس نبايد بعد از امسال، نزديک مسجد الحرام شوند! و اگر از فقر مي‌ترسيد، خداوند هرگاه بخواهد، شما را به کرمش بي‌نياز مي‌سازد؛ (و از راه ديگر جبران مي‌کند؛) خداوند دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1264, N'با کساني از اهل کتاب که نه به خدا، و نه به روز جزا ايمان دارند، و نه آنچه را خدا و رسولش تحريم کرده حرام مي‌شمرند، و نه آيين حق را مي‌پذيرند، پيکار کنيد تا زماني که با خضوع و تسليم، جزيه را به دست خود بپردازند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1265, N'يهود گفتند: «عزير پسر خداست!» و نصاري کفتند: «مسيح پسر خداست!» اين سخني است که با زبان خود مي‌گويند، که همانند گفتار کافران پيشين است؛ خدا آنان را بکشد، چگونه از حق انحراف مي‌يابند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1266, N'(آنها) دانشمندان و راهبان خويش را معبودهايي در برابر خدا قرار دادند، و (همچنين) مسيح فرزند مريم را؛ در حالي که دستور نداشتند جز خداوند يکتائي را که معبودي جز او نيست، بپرستند، او پاک و منزه است از آنچه همتايش قرار مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1267, N'آنها مي‌خواهند نور خدا را با دهان خود خاموش کنند؛ ولي خدا جز اين نمي‌خواهد که نور خود را کامل کند، هر چند کافران ناخشنود باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1268, N'او کسي است که رسولش را با هدايت و آيين حق فرستاد، تا آن را بر همه آيين‌ها غالب گرداند، هر چند مشرکان کراهت داشته باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1269, N'اي کساني که ايمان آورده‌ايد! بسياري از دانشمندان (اهل کتاب) و راهبان، اموال مردم را بباطل مي‌خورند، و (آنان را) از راه خدا بازمي‌دارند! و کساني که طلا و نقره را گنجينه (و ذخيره و پنهان) مي‌سازند، و در راه خدا انفاق نمي‌کنند، به مجازات دردناکي بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1270, N'در آن روز که آن را در آتش جهنم، گرم و سوزان کرده، و با آن صورتها و پهلوها و پشتهايشان را داغ مي‌کنند؛ (و به آنها مي‌گويند): اين همان چيزي است که براي خود اندوختيد (و گنجينه ساختيد)! پس بچشيد چيزي را که براي خود مي‌اندوختيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1271, N'تعداد ماه‌ها نزد خداوند در کتاب الهي، از آن روز که آسمانها و زمين را آفريده، دوازده ماه است؛ که چهار ماه از آن، ماه حرام است؛ (و جنگ در آن ممنوع مي‌باشد.) اين، آيين ثابت و پابرجا(ي الهي) است! بنابر اين، در اين ماه‌ها به خود ستم نکنيد (و از هرگونه خونريزي بپرهيزيد)! و (به هنگام نبرد) با مشرکان، دسته جمعي پيکار کنيد، همان گونه که آنها دسته جمعي با شما پيکار مي‌کنند؛ و بدانيد خداوند با پرهيزگاران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1272, N'نسي‌ء [= جا به جا کردن و تأخير ماه‌هاي حرام‌]، افزايشي در کفر (مشرکان)است؛ که با آن، کافران گمراه مي‌شوند؛ يک سال، آن را حلال، و سال ديگر آن را حرام مي کنند، تا به مقدار ماه‌هايي که خداوند تحريم کرده بشود (و عدد چهار ماه، به پندارشان تکميل گردد)؛ و به اين ترتيب، آنچه را خدا حرام کرده، حلال بشمرند. اعمال زشتشان در نظرشان زيبا جلوه داده شده؛ و خداوند جمعيت کافران را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1273, N'اي کساني که ايمان آورده‌ايد! چرا هنگامي که به شما گفته مي‌شود: «به سوي جهاد در راه خدا حرکت کنيد!» بر زمين سنگيني مي‌کنيد (و سستي به خرج مي‌دهيد)؟! آيا به زندگي دنيا به جاي آخرت راضي شده‌ايد؟! با اينکه متاع زندگي دنيا، در برابر آخرت، جز اندکي نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1274, N'اگر (به سوي ميدان جهاد) حرکت نکنيد، شما را مجازات دردناکي مي‌کند، و گروه ديگري غير از شما را به جاي شما قرارمي‌دهد؛ و هيچ زياني به او نمي‌رسانيد؛ و خداوند بر هر چيزي تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1275, N'اگر او را ياري نکنيد، خداوند او را ياري کرد؛ (و در مشکلترين ساعات، او را تنها نگذاشت؛) آن هنگام که کافران او را (از مکه) بيرون کردند، در حالي که دومين نفر بود (و يک نفر بيشتر همراه نداشت)؛ در آن هنگام که آن دو در غار بودند، و او به همراه خود مي‌گفت: «غم مخور، خدا با ماست!» در اين موقع، خداوند سکينه (و آرامش) خود را بر او فرستاد؛ و با لشکرهايي که مشاهده نمي‌کرديد، او را تقويت نمود؛ و گفتار (و هدف) کافران را پايين قرار داد، (و آنها را با شکست مواجه ساخت؛) و سخن خدا (و آيين او)، بالا (و پيروز)است؛ و خداوند عزيز و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1276, N'(همگي به سوي ميدان جهاد) حرکت کنيد؛ سبکبار باشيد يا سنگين بار! و با اموال و جانهاي خود، در راه خدا جهاد نماييد؛ اين براي شما بهتر است اگر بدانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1277, N'(اما گروهي از آنها، چنانند که) اگر غنايمي نزديک (و در دسترس)، و سفري آسان باشد، (به طمع دنيا) از تو پيروي مي‌کنند؛ ولي (اکنون که براي ميدان تبوک،) راه بر آنها دور (و پر مشقت) است، (سرباز مي‌زنند؛) و بزودي به خدا سوگند ياد مي‌کنند که: «اگر توانايي داشتيم، همراه شما حرکت مي‌کرديم!» (آنها با اين اعمال و اين دروغها، در واقع) خود را هلاک مي‌کنند؛ و خداوند مي‌داند آنها دروغگو هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1278, N'خداوند تو را بخشيد؛ چرا پيش از آنکه راستگويان و دروغگويان را بشناسي، به آنها اجازه دادي؟! (خوب بود صبر مي‌کردي، تا هر دو گروه خود را نشان دهند!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1279, N'آنها که به خدا و روز جزا ايمان دارند، هيچ گاه براي ترک جهاد (در راه خدا) با اموال و جانهايشان، از تو اجازه نمي‌گيرند؛ و خداوند پرهيزگاران را مي‌شناسد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1280, N'تنها کساني از تو اجازه (اين کار را) مي‌گيرند که به خدا و روز جزا ايمان ندارند، و دلهايشان با شک و ترديد آميخته است؛ آنها در ترديد خود سرگردانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1281, N'اگر آنها (راست مي‌گفتند، و) اراده داشتند که (بسوي ميدان جهاد) خارج شوند، وسيله‌اي براي آن فراهم مي‌ساختند! ولي خدا از حرکت آنها کراهت داشت؛ از اين رو (توفيقش را از آنان سلب کرد؛ و) آنها را (از جهاد) باز داشت؛ و به آنان گفته شد: «با «قاعدين» [= کودکان و پيران و بيماران‌] بنشينيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1282, N'اگر آنها همراه شما (بسوي ميدان جهاد) خارج مي‌شدند، جز اضطراب و ترديد، چيزي بر شما نمي‌افزودند؛ و بسرعت در بين شما به فتنه‌انگيزي (و ايجاد تفرقه و نفاق) مي‌پرداختند؛ و در ميان شما، افرادي (سست و ضعيف) هستند که به سخنان آنها کاملا گوش فرامي‌دهند؛ و خداوند، ظالمان را مي‌شناسد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1283, N'آنها پيش از اين (نيز) در پي فتنه‌انگيزي بودند، و کارها را بر تو دگرگون ساختند (و به هم ريختند)؛ تا آن که حق فرا رسيد، و فرمان خدا آشکار گشت (و پيروز شديد)، در حالي که آنها کراهت داشتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1284, N'بعضي از آنها مي‌گويند: «به ما اجازه ده (تا در جهاد شرکت نکنيم)، و ما را به گناه نيفکن»! آگاه باشيد آنها (هم اکنون) در گناه سقوط کرده‌اند؛ و جهنم، کافران را احاطه کرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1285, N'هرگاه نيکي به تو رسد، آنها را ناراحت مي‌کند؛ و اگر مصيبتي به تو رسد، مي‌گويند: «ما تصميم خود را از پيش گرفته‌ايم.» و بازمي‌گردند در حالي که خوشحالند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1286, N'بگو: «هيچ حادثه‌اي براي ما رخ نمي‌دهد، مگر آنچه خداوند براي ما نوشته و مقرر داشته است؛ او مولا (و سرپرست) ماست؛ و مؤمنان بايد تنها بر خدا توکل کنند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1287, N'بگو: «آيا درباره ما، جز يکي از دو نيکي را انتظار داريد؟!(: يا پيروزي يا شهادت) ولي ما انتظار داريم که خداوند، عذابي از سوي خودش (در آن جهان) به شما برساند، يا (در اين جهان) به دست ما (مجازات شويد) اکنون که چنين است، شما انتظار بکشيد، ما هم با شما انتظار مي‌کشيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1288, N'بگو: «انفاق کنيد؛ خواه از روي ميل باشد يا اکراه، هرگز از شما پذيرفته نمي‌شود؛ چرا که شما قوم فاسقي بوديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1289, N'هيچ چيز مانع قبول انفاقهاي آنها نشد، جز اينکه آنها به خدا و پيامبرش کافر شدند، و نماز بجا نمي‌آورند جز با کسالت، و انفاق نمي‌کنند مگر با کراهت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1290, N'و (فزوني) اموال و اولاد آنها، تو را در شگفتي فرو نبرد؛ خدا مي‌خواهد آنان را به وسيله آن، در زندگي دنيا عذاب کند، و در حال کفر بميرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1291, N'آنها به خدا سوگند مي‌خورند که از شما هستند، در حالي که از شما نيستند؛ ولي آنها گروهي هستند که مي‌ترسند (و به خاطر ترس از فاش شدن اسرارشان دروغ مي‌گويند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1292, N'اگر پناهگاه يا غارها يا راهي در زير زمين بيابند، بسوي آن حرکت مي‌کنند، و با سرعت و شتاب فرار مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1293, N'و در ميان آنها کساني هستند که در (تقسيم) غنايم به تو خرده ميگيرند؛ اگر از آن (غنايم، سهمي) به آنها داده شود، راضي مي‌شوند؛ و اگر داده نشود، خشم مي‌گيرند (؛خواه حق آنها باشد يا نه)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1294, N'(در حالي که) اگر به آنچه خدا و پيامبرش به آنان داده راضي باشند، و بگويند: «خداوند براي ما کافي است! و بزودي خدا و رسولش، از فضل خود به ما مي‌بخشند؛ ما تنها رضاي او را مي‌طلبيم.» (براي آنها بهتر است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1295, N'زکاتها مخصوص فقرا و مساکين و کارکناني است که براي (جمع آوري) آن زحمت مي‌کشند، و کساني که براي جلب محبتشان اقدام مي‌شود، و براي (آزادي) بردگان، و (اداي دين) بدهکاران، و در راه (تقويت آيين) خدا، و واماندگان در راه؛ اين، يک فريضه (مهم) الهي است؛ و خداوند دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1296, N'از آنها کساني هستند که پيامبر را آزار مي‌دهند و مي‌گويند: «او آدم خوش‌باوري است!» بگو: «خوش‌باور بودن او به نفع شماست! (ولي بدانيد) او به خدا ايمان دارد؛ و (تنها) مؤمنان را تصديق مي‌کند؛ و رحمت است براي کساني از شما که ايمان آورده‌اند!» و آنها که رسول خدا را آزار مي‌دهند، عذاب دردناکي دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1297, N'آنها براي شما به خدا سوگند ياد مي‌کنند، تا شما را راضي سازند؛ در حالي که شايسته‌تر اين است که خدا و رسولش را راضي کنند، اگر ايمان دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1298, N'آيا نمي‌دانند هر کس با خدا و رسولش دشمني کند، براي او آتش دوزخ است؛ جاودانه در آن مي‌ماند؟! اين، همان رسوايي بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1299, N'منافقان از آن بيم دارند که سوره‌اي بر ضد آنان نازل گردد، و به آنها از اسرار درون قلبشان خبر دهد. بگو: «استهزا کنيد! خداوند، آنچه را از آن بيم داريد، آشکار مي‌سازد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1300, N'و اگر از آنها بپرسي(: «چرا اين اعمال خلاف را انجام داديد؟!»)، مي‌گويند: «ما بازي و شوخي مي‌کرديم!» بگو:« آيا خدا و آيات او و پيامبرش را مسخره مي‌کرديد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1301, N'(بگو:) عذر خواهي نکنيد (که بيهوده است؛ چرا که) شما پس از ايمان آوردن، کافر شديد! اگر گروهي از شما را (بخاطر توبه) مورد عفو قرار دهيم، گروه ديگري را عذاب خواهيم کرد؛ زيرا مجرم بودند!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1302, N'مردان منافق و زنان منافق، همه از يک گروهند! آنها امر به منکر، و نهي از معروف مي‌کنند؛ و دستهايشان را (از انفاق و بخشش) مي‌بندند؛ خدا را فراموش کردند، و خدا (نيز) آنها را فراموش کرد (،و رحمتش را از آنها قطع نمود)؛ به يقين، منافقان همان فاسقانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1303, N'خداوند به مردان و زنان منافق و کفار، وعده آتش دوزخ داده؛ جاودانه در آن خواهند ماند -همان براي آنها کافي است!- و خدا آنها را از رحمت خود دور ساخته؛ و عذاب هميشگي براي آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1304, N'(شما منافقان،) همانند کساني هستيد که قبل از شما بودند (و راه نفاق پيمودند؛ بلکه) آنها از شما نيرومندتر، و اموال و فرزندانشان بيشتر بود! آنها از بهره خود (از مواهب الهي در راه گناه و هوس) استفاده کردند؛ شما نيز از بهره خود، (در اين راه) استفاده کرديد، همان گونه که آنها استفاده کردند؛ شما (در کفر و نفاق و استهزاي مؤمنان) فرو رفتيد، همان گونه که آنها فرو رفتند؛ (ولي سرانجام) اعمالشان در دنيا و آخرت نابود شد؛ و آنها همان زيانکارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1305, N'آيا خبر کساني که پيش از آنها بودند، به آنان نرسيده است؟! «قوم نوح» و «عاد» و «ثمود» و «قوم ابراهيم» و «اصحاب مدين» [= قوم شعيب‌] و «شهرهاي زير و رو شده» [= قوم لوط]؛ پيامبرانشان دلايل روشن براي آنان آوردند، (ولي نپذيرفتند؛) خداوند به آنها ستم نکرد، اما خودشان بر خويشتن ستم مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1306, N'مردان و زنان باايمان، ولي (و يار و ياور) يکديگرند؛ امر به معروف، و نهي از منکر مي‌کنند؛ نماز را برپا مي‌دارند؛ و زکات را مي‌پردازند؛ و خدا و رسولش را اطاعت مي‌کنند؛ بزودي خدا آنان را مورد رحمت خويش قرارمي‌دهد؛ خداوند توانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1307, N'خداوند به مردان و زنان باايمان، باغهايي از بهشت وعده داده که نهرها از زير درختانش جاري است؛ جاودانه در آن خواهند ماند؛ و مسکن‌هاي پاکيزه‌اي در بهشتهاي جاودان (نصيب آنها ساخته)؛ و (خشنودي و) رضاي خدا، (از همه اينها) برتر است؛ و پيروزي بزرگ، همين است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1308, N'اي پيامبر! با کافران و منافقان جهاد کن، و بر آنها سخت بگير! جايگاهشان جهنم است؛ و چه بد سرنوشتي دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1309, N'به خدا سوگند مي‌خورند که (در غياب پيامبر، سخنان نادرست) نگفته‌اند؛ در حالي که قطعا سخنان کفرآميز گفته‌اند؛ و پس از اسلام‌آوردنشان، کافر شده‌اند؛ و تصميم (به کار خطرناکي) گرفتند، که به آن نرسيدند. آنها فقط از اين انتقام مي‌گيرند که خداوند و رسولش، آنان را به فضل (و کرم) خود، بي‌نياز ساختند! (با اين حال،) اگر توبه کنند، براي آنها بهتر است؛ و اگر روي گردانند، خداوند آنها را در دنيا و آخرت، به مجازات دردناکي کيفر خواهد داد؛ و در سراسر زمين، نه ولي و حامي دارند، و نه ياوري!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1310, N'بعضي از آنها با خدا پيمان بسته بودند که: «اگر خداوند ما را از فضل خود روزي دهد، قطعا صدقه خواهيم داد؛ و از صالحان (و شاکران) خواهيم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1311, N'اما هنگامي که خدا از فضل خود به آنها بخشيد، بخل ورزيدند و سرپيچي کردند و روي برتافتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1312, N'اين عمل، (روح) نفاق را، تا روزي که خدا را ملاقات کنند، در دلهايشان برقرار ساخت. اين بخاطر آن است که از پيمان الهي تخلف جستند؛ و بخاطر آن است که دروغ مي‌گفتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1313, N'آيا نمي‌دانستند که خداوند، اسرار و سخنان درگوشي آنها را مي‌داند؛ و خداوند داناي همه غيبها (و امور پنهاني) است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1314, N'آنهايي که از مؤمنان اطاعت کار، در صدقاتشان عيبجويي مي‌کنند، و کساني را که (براي انفاق در راه خدا) جز به مقدار (ناچيز) توانائي خود دسترسي ندارند، مسخره مي‌نمايند، خدا آنها را مسخره مي‌کند؛ (و کيفر استهزاکنندگان را به آنها مي‌دهد؛) و براي آنها عذاب دردناکي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1315, N'چه براي آنها استغفار کني، و چه نکني، (حتي) اگر هفتاد بار براي آنها استغفار کني، هرگز خدا آنها را نمي‌آمرزد! چرا که خدا و پيامبرش را انکار کردند؛ و خداوند جمعيت فاسقان را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1316, N'تخلف‌جويان (از جنگ تبوک،) از مخالفت با رسول خدا خوشحال شدند؛ و کراهت داشتند که با اموال و جانهاي خود، در راه خدا جهاد کنند؛ و (به يکديگر و به مؤمنان) گفتند: «در اين گرما، (بسوي ميدان) حرکت نکنيد!» (به آنان) بگو: «آتش دوزخ از اين هم گرمتر است!» اگر مي‌دانستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1317, N'از اين‌رو آنها بايد کمتر بخندند و بسيار بگريند! (چرا که آتش جهنم در انتظارشان است) اين، جزاي کارهايي است که انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1318, N'هرگاه خداوند تو را بسوي گروهي از آنان بازگرداند، و از تو اجازه خروج (بسوي ميدان جهاد) بخواهند، بگو: «هيچ گاه با من خارج نخواهيد شد! و هرگز همراه من، با دشمني نخواهيد جنگيد! شما نخستين بار به کناره‌گيري راضي شديد، اکنون نيز با متخلفان بمانيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1319, N'هرگز بر مرده هيچ يک از آنان، نماز نخوان! و بر کنار قبرش، (براي دعا و طلب آمرزش،) نايست! چرا که آنها به خدا و رسولش کافر شدند؛ و در حالي که فاسق بودند از دنيا رفتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1320, N'مبادا اموال و فرزندانشان، مايه شگفتي تو گردد! (اين براي آنها نعمت نيست؛ بلکه) خدا مي‌خواهد آنها را به اين وسيله در دنيا عذاب کند، و جانشان برآيد در حالي که کافرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1321, N'و هنگامي که سوره‌اي نازل شود (و به آنان دستور دهد) که: «به خدا ايمان بياوريد! و همراه پيامبرش جهاد کنيد!»، افرادي از آنها [= گروه منافقان‌] که توانايي دارند، از تو اجازه مي‌خواهند و مي‌گويند: «بگذار ما با قاعدين [= آنها که از جهاد معافند] باشيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1322, N'(آري،) آنها راضي شدند که با متخلفان باشند؛ و بر دلهايشان مهر نهاده شده؛ از اين رو (چيزي) نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1323, N'ولي پيامبر و کساني که با او ايمان آوردند، با اموال و جانهايشان جهاد کردند؛ و همه نيکيها براي آنهاست؛ و آنها همان رستگارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1324, N'خداوند براي آنها باغهايي از بهشت فراهم ساخته که نهرها از زير درختانش جاري است؛ جاودانه در آن خواهند بود؛ و اين است رستگاري (و پيروزي) بزرگ!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1325, N'و عذرآورندگان از اعراب، (نزد تو) آمدند که به آنها اجازه (عدم شرکت در جهاد) داده شود؛ و آنها که به خدا و پيامبرش دروغ گفتند، (بدون هيچ عذري در خانه خود) نشستند؛ بزودي به کساني از آنها که مخالفت کردند (و معذور نبودند)، عذاب دردناکي خواهد رسيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1326, N'بر ضعيفان و بيماران و آنها که وسيله‌اي براي انفاق (در راه جهاد) ندارند، ايرادي نيست (که در ميدان جنگ شرکت نجويند،) هرگاه براي خدا و رسولش خيرخواهي کنند (؛و از آنچه در توان دارند، مضايقه ننمايند). بر نيکوکاران راه مؤاخذه نيست؛ و خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1327, N'و (نيز) ايرادي نيست بر آنها که وقتي نزد تو آمدند که آنان را بر مرکبي (براي جهاد) سوار کني، گفتي: «مرکبي که شما را بر آن سوار کنم، ندارم!» (از نزد تو) بازگشتند در حالي که چشمانشان از اندوه اشکبار بود؛ زيرا چيزي نداشتند که در راه خدا انفاق کنند (و با آن به ميدان بروند)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1328, N'راه مؤاخذه تنها به روي کساني باز است که از تو اجازه مي‌خواهند در حالي که توانگرند؛ (و امکانات کافي براي جهاد دارند؛) آنها راضي شدند که با متخلفان [= زنان و کودکان و بيماران‌] بمانند؛ و خداوند بر دلهايشان مهر نهاده؛ به همين جهت چيزي نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1329, N'هنگامي که بسوي آنها (که از جهاد تخلف کردند) باز گرديد، از شما عذرخواهي مي‌کنند؛ بگو: «عذرخواهي نکنيد، ما هرگز به شما ايمان نخواهيم آورد! چرا که خدا ما را از اخبارتان آگاه ساخته؛ و خدا و رسولش، اعمال شما را مي‌بينند؛ سپس به سوي کسي که داناي پنهان و آشکار است بازگشت داده مي‌شويد؛ و او شما را به آنچه انجام مي‌داديد، آگاه مي‌کند (و جزا مي‌دهد!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1330, N'هنگامي که بسوي آنان بازگرديد، براي شما به خدا سوگند ياد مي‌کنند، تا از آنها اعراض (و صرف نظر) کنيد؛ از آنها اعراض کنيد (و روي بگردانيد)؛ چرا که پليدند! و جايگاهشان دوزخ است، بکيفر اعمالي که انجام مي‌دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1331, N'براي شما قسم ياد مي‌کنند تا از آنها راضي شويد؛ اگر شما از آنها راضي شويد، خداوند (هرگز) از جمعيت فاسقان راضي نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1332, N'باديه‌نشينان عرب، کفر و نفاقشان شديدتر است؛ و به ناآگاهي از حدود و احکامي که خدا بر پيامبرش نازل کرده، سزاوارترند؛ و خداوند دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1333, N'گروهي از (اين) اعراب باديه‌نشين، چيزي را که (در راه خدا) انفاق مي‌کنند،غرامت محسوب مي‌دارند؛ و انتظار حوادث دردناکي براي شما مي‌کشند؛ حوادث دردناک براي خود آنهاست؛ و خداوند شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1334, N'گروهي (ديگر) از عربهاي باديه‌نشين، به خدا و روز رستاخيز ايمان دارند؛ و آنچه را انفاق مي‌کنند، مايه تقرب به خدا، و دعاي پيامبر مي‌دانند؛ آگاه باشيد اينها مايه تقرب آنهاست! خداوند بزودي آنان را در رحمت خود وارد خواهد ساخت؛ به يقين، خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1335, N'پيشگامان نخستين از مهاجرين و انصار، و کساني که به نيکي از آنها پيروي کردند، خداوند از آنها خشنود گشت، و آنها (نيز) از او خشنود شدند؛ و باغهايي از بهشت براي آنان فراهم ساخته، که نهرها از زير درختانش جاري است؛ جاودانه در آن خواهند ماند؛ و اين است پيروزي بزرگ!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1336, N'و از (ميان) اعراب باديه‌نشين که اطراف شما هستند، جمعي منافقند؛ و از اهل مدينه (نيز)، گروهي سخت به نفاق پاي بندند. تو آنها را نمي‌شناسي، ولي ما آنها را مي شناسيم. بزودي آنها را دو بار مجازات مي‌کنيم (:مجازاتي با رسوايي در دنيا، و مجازاتي به هنگام مرگ)؛ سپس بسوي مجازات بزرگي (در قيامت)فرستاده مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1337, N'و گروهي ديگر، به گناهان خود اعتراف کردند؛ و کار خوب و بد را به هم آميختند؛ اميد مي‌رود که خداوند توبه آنها را بپذيرد؛ به يقين، خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1338, N'از اموال آنها صدقه‌اي (بعنوان زکات) بگير، تا بوسيله آن، آنها را پاک سازي و پرورش دهي! و (به هنگام گرفتن زکات،) به آنها دعا کن؛ که دعاي تو، مايه آرامش آنهاست؛ و خداوند شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1339, N'آيا نمي‌دانستند که فقط خداوند توبه را از بندگانش مي‌پذيرد، و صدقات را مي‌گيرد، و خداوند توبه‌پذير و مهربان است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1340, N'بگو: «عمل کنيد! خداوند و فرستاده او و مؤمنان، اعمال شما را مي‌بينند! و بزودي، بسوي داناي نهان و آشکار، بازگردانده مي‌شويد؛ و شما را به آنچه عمل مي‌کرديد، خبر مي‌دهد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1341, N'و گروهي ديگر، به فرمان خدا واگذار شده‌اند (و کارشان با خداست)؛ يا آنها را مجازات مي‌کند، و يا توبه آنان را مي‌پذيرد (،هر طور که شايسته باشند)؛ و خداوند دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1342, N'(گروهي ديگر از آنها) کساني هستند که مسجدي ساختند براي زيان (به مسلمانان)، و (تقويت) کفر، و تفرقه‌افکني ميان مؤمنان، و کمينگاه براي کسي که از پيش با خدا و پيامبرش مبارزه کرده بود؛ آنها سوگند ياد مي‌کنند که: «جز نيکي (و خدمت)، نظري نداشته‌ايم!» اما خداوند گواهي مي‌دهد که آنها دروغگو هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1343, N'هرگز در آن (مسجد به عبادت) نايست! آن مسجدي که از روز نخست بر پايه تقوا بنا شده، شايسته‌تر است که در آن (به عبادت) بايستي؛ در آن، مرداني هستند که دوست مي‌دارند پاکيزه باشند؛ و خداوند پاکيزگان را دوست دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1344, N'آيا کسي که شالوده آن را بر تقواي الهي و خشنودي او بنا کرده بهتر است،يا کسي که اساس آن را بر کنار پرتگاه سستي بنا نموده که ناگهان در آتش دوزخ فرومي‌ريزد؟ و خداوند گروه ستمگران را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1345, N'(اما) اين بنايي را که آنها ساختند، همواره بصورت يک وسيله شک و ترديد،در دلهايشان باقي مي‌ماند؛ مگر اينکه دلهايشان پاره پاره شود (و بميرند؛ وگرنه، هرگز از دل آنها بيرون نمي‌رود)؛ و خداوند دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1346, N'خداوند از مؤمنان، جانها و اموالشان را خريداري کرده، که (در برابرش) بهشت براي آنان باشد؛ (به اين گونه که:) در راه خدا پيکار مي‌کنند، مي‌کشند و کشته مي‌شوند؛ اين وعده حقي است بر او، که در تورات و انجيل و قرآن ذکر فرموده؛ و چه کسي از خدا به عهدش وفادارتر است؟! اکنون بشارت باد بر شما، به داد و ستدي که با خدا کرده‌ايد؛ و اين است آن پيروزي بزرگ!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1347, N'توبه‌کنندگان، عبادت کاران، سپاسگويان، سياحت کنندگان، رکوع کنندگان، سجده‌آوران، آمران به معروف، نهي کنندگان از منکر، و حافظان حدود (و مرزهاي)الهي، (مؤمنان حقيقي‌اند)؛ و بشارت ده به (اينچنين) مؤمنان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1348, N'براي پيامبر و مؤمنان، شايسته نبود که براي مشرکان (از خداوند) طلب آمرزش کنند، هر چند از نزديکانشان باشند؛ (آن هم) پس از آنکه بر آنها روشن شد که اين گروه، اهل دوزخند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1349, N'و استغفار ابراهيم براي پدرش [= عمويش آزر]، فقط بخاطر وعده‌اي بود که به او داده بود (تا وي را بسوي ايمان جذب کند)؛ اما هنگامي که براي او روشن شد که وي دشمن خداست، از او بيزاري جست؛ به يقين، ابراهيم مهربان و بردبار بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1350, N'چنان نبود که خداوند قومي را، پس از آن که آنها را هدايت کرد (و ايمان آوردند) گمراه (و مجازات) کند؛ مگر آنکه اموري را که بايد از آن بپرهيزند، براي آنان بيان نمايد (و آنها مخالفت کنند)؛ زيرا خداوند به هر چيزي داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1351, N'حکومت آسمانها و زمين تنها از آن خداست؛ زنده مي‌کند و مي‌ميراند؛ و جز خدا، ولي و ياوري نداريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1352, N'مسلما خداوند رحمت خود را شامل حال پيامبر و مهاجران و انصار، که در زمان عسرت و شدت (در جنگ تبوک) از او پيروي کردند، نمود؛ بعد از آنکه نزديک بود دلهاي گروهي از آنها، از حق منحرف شود (و از ميدان جنگ بازگردند)؛ سپس خدا توبه آنها را پذيرفت، که او نسبت به آنان مهربان و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1353, N'(همچنين) آن سه نفر که (از شرکت در جنگ تبوک) تخلف جستند، (و مسلمانان با آنان قطع رابطه نمودند،) تا آن حد که زمين با همه وسعتش بر آنها تنگ شد؛ (حتي) در وجود خويش، جايي براي خود نمي‌يافتند؛ (در آن هنگام) دانستند پناهگاهي از خدا جز بسوي او نيست؛ سپس خدا رحمتش را شامل حال آنها نمود، (و به آنان توفيق داد) تا توبه کنند؛ خداوند بسيار توبه‌پذير و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1354, N'اي کساني که ايمان آورده‌ايد! از (مخالفت فرمان) خدا بپرهيزيد، و با صادقان باشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1355, N'سزاوار نيست که اهل مدينه، و باديه‌نشيناني که اطراف آنها هستند، از رسول خدا تخلف جويند؛ و براي حفظ جان خويش، از جان او چشم بپوشند! اين بخاطر آن است که هيچ تشنگي و خستگي، و گرسنگي در راه خدا به آنها نمي‌رسد و هيچ گامي که موجب خشم کافران مي‌شود برنمي‌دارند، و ضربه‌اي از دشمن نمي‌خورند، مگر اينکه به خاطر آن، عمل صالحي براي آنها نوشته مي‌شود؛ زيرا خداوند پاداش نيکوکاران را تباه نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1356, N'و هيچ مال کوچک يا بزرگي را (در اين راه) انفاق نمي‌کنند، و هيچ سرزميني را (بسوي ميدان جهاد و يا در بازگشت) نمي‌پيمايند، مگر اينکه براي آنها نوشته مي‌شود؛ تا خداوند آن را بعنوان بهترين اعمالشان، پاداش دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1357, N'شايسته نيست مؤمنان همگي (بسوي ميدان جهاد) کوچ کنند؛ چرا از هر گروهي از آنان، طايفه‌اي کوچ نمي‌کند (و طايفه‌اي در مدينه بماند)، تا در دين (و معارف و احکام اسلام) آگاهي يابند و به هنگام بازگشت بسوي قوم خود، آنها را بيم دهند؟! شايد (از مخالفت فرمان پروردگار) بترسند، و خودداري کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1358, N'اي کساني که ايمان آورده‌ايد! با کافراني که به شما نزديکترند، پيکار کنيد! (و دشمن دورتر، شما را از دشمنان نزديک غافل نکند!) آنها بايد در شما شدت و خشونت (و قدرت) احساس کنند؛ و بدانيد خداوند با پرهيزگاران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1359, N'و هنگامي که سوره‌اي نازل مي‌شود، بعضي از آنان (به ديگران) مي‌گويند: «اين سوره، ايمان کدام يک از شما را افزون ساخت؟!» (به آنها بگو:) اما کساني که ايمان آورده‌اند، بر ايمانشان افزوده؛ و آنها (به فضل و رحمت الهي) خوشحالند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1360, N'و اما آنها که در دلهايشان بيماري است، پليدي بر پليديشان افزوده؛ و از دنيا رفتند در حالي که کافر بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1361, N'آيا آنها نمي‌بينند که در هر سال، يک يا دو بار آزمايش مي‌شوند؟! باز توبه نمي‌کنند، و متذکر هم نمي‌گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1362, N'و هنگامي که سوره‌اي نازل مي‌شود، بعضي از آنها [= منافقان‌] به يکديگر نگاه مي‌کنند و مي‌گويند: «آيا کسي شما را مي‌بيند؟ (اگر از حضور پيامبر بيرون رويم، کسي متوجه ما نمي‌شود!)» سپس منصرف مي‌شوند (و بيرون مي‌روند)؛ خداوند دلهايشان را (از حق) منصرف ساخته؛ چرا که آنها، گروهي هستند که نمي‌فهمند (و بي‌دانشند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1363, N'به يقين، رسولي از خود شما بسويتان آمد که رنجهاي شما بر او سخت است؛ و اصرار بر هدايت شما دارد؛ و نسبت به مؤمنان، رئوف و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1364, N'اگر آنها (از حق) روي بگردانند، (نگران مباش!) بگو: «خداوند مرا کفايت مي‌کند؛ هيچ معبودي جز او نيست؛ بر او توکل کردم؛ و او صاحب عرش بزرگ است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1365, N'الر، اين آيات کتاب استوار و حکمت آميز است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1366, N'آيا براي مردم، موجب شگفتي بود که به مردي از آنها وحي فرستاديم که مردم را (از عواقب کفر و گناه) بترسان، و به کساني که ايمان آورده‌اند بشارت ده که براي آنها، سابقه نيک (و پاداشهاي مسلم) نزد پروردگارشان است؟! (اما) کافران گفتند: «اين مرد، ساحر آشکاري است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1367, N'پروردگار شما، خداوندي است که آسمانها و زمين را در شش روز [= شش دوران‌] آفريد؛ سپس بر تخت (قدرت) قرار گرفت، و به تدبير کار (جهان) پرداخت؛ هيچ شفاعت کننده‌اي، جز با اذن او نيست؛ اين است خداوند، پروردگار شما! پس او را پرستش کنيد! آيا متذکر نمي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1368, N'بازگشت همه شما بسوي اوست! خداوند وعده حقي فرموده؛ او آفرينش را آغاز مي‌کند، سپس آن را بازمي‌گرداند، تا کساني را که ايمان آورده و کارهاي شايسته انجام داده‌اند، بعدالت جزا دهد؛ و براي کساني که کافر شدند، نوشيدني از مايع سوزان است؛ و عذابي دردناک، بخاطر آنکه کفر مي‌ورزيدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1369, N'او کسي است که خورشيد را روشنايي، و ماه را نور قرار داد؛ و براي آن منزلگاه‌هايي مقدر کرد، تا عدد سالها و حساب (کارها) را بدانيد؛ خداوند اين را جز بحق نيافريده؛ او آيات (خود را) براي گروهي که اهل دانشند، شرح مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1370, N'مسلما در آمد و شد شب و روز، و آنچه خداوند در آسمانها و زمين آفريده، آيات (و نشانه‌هايي) است براي گروهي که پرهيزگارند (و حقايق را مي‌بينند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1371, N'آنها که ايمان به ملاقات ما (و روز رستاخيز) ندارند، و به زندگي دنيا خشنود شدند و بر آن تکيه کردند، و آنها که از آيات ما غافلند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1372, N'(همه) آنها جايگاهشان آتش است، بخاطر کارهايي که انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1373, N'(ولي) کساني که ايمان آوردند و کارهاي شايسته انجام دادند، پروردگارشان آنها را در پرتو ايمانشان هدايت مي‌کند؛ از زير (قصرهاي) آنها در باغهاي بهشت، نهرها جاري است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1374, N'گفتار (و دعاي) آنها در بهشت اين است که: «خداوندا، منزهي تو!» و تحيت آنها در آنجا: سلام؛ و آخرين سخنشان اين است که: «حمد، مخصوص پروردگار عالميان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1375, N'اگر همان گونه که مردم در به دست آوردن «خوبي»ها عجله دارند، خداوند در مجازاتشان شتاب مي‌کرد، (بزودي) عمرشان به پايان مي‌رسيد (و همگي نابود مي‌شدند)؛ ولي کساني را که ايمان به لقاي ما ندارند، به حال خود رها مي‌کنيم تا در طغيانشان سرگردان شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1376, N'هنگامي که به انسان زيان (و ناراحتي) رسد، ما را (در هر حال:) در حالي که به پهلو خوابيده، يا نشسته، يا ايستاده است، مي‌خواند؛ اما هنگامي که ناراحتي را از او برطرف ساختيم، چنان مي‌رود که گويي هرگز ما را براي حل مشکلي که به او رسيده بود، نخوانده است! اين گونه براي اسرافکاران، اعمالشان زينت داده شده است (که زشتي اين عمل را درک نمي‌کنند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1377, N'ما امتهاي پيش از شما را، هنگامي که ظلم کردند، هلاک نموديم؛ در حالي که پيامبرانشان دلايل روشن براي آنها آوردند، ولي آنها ايمان نياوردند؛ اين‌گونه گروه مجرمان را کيفر مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1378, N'سپس شما را جانشينان آنها در روي زمين -پس از ايشان- قرار داديم؛ تا ببينيم شما چگونه عمل مي‌کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1379, N'و هنگامي که آيات روشن ما بر آنها خوانده مي‌شود، کساني که ايمان به لقاي ما (و روز رستاخيز) ندارند مي‌گويند: «قرآني غير از اين بياور، يا آن را تبديل کن! (و آيات نکوهش بتها را بردار)» بگو: «من حق ندارم که از پيش خود آن را تغيير دهم؛ فقط از چيزي که بر من وحي مي‌شود، پيروي مي‌کنم! من اگر پروردگارم را نافرماني کنم، از مجازات روز بزرگ (قيامت) مي‌ترسم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1380, N'بگو: «اگر خدا مي‌خواست، من اين آيات را بر شما نمي‌خواندم؛ و خداوند از آن آگاهتان نمي‌کرد؛ چه اينکه مدتها پيش از اين، در ميان شما زندگي نمودم؛ (و هرگز آيه‌اي نياوردم؛) آيا نمي‌فهميد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1381, N'چه کسي ستمکارتر است از آن کس که بر خدا دروغ مي‌بندد، يا آيات او را تکذيب مي‌کند؟! مسلما مجرمان رستگار نخواهند شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1382, N'آنها غير از خدا، چيزهايي را مي‌پرستند که نه به آنان زيان مي‌رساند، و نه سودي مي‌بخشد؛ و مي‌گويند: «اينها شفيعان ما نزد خدا هستند!» بگو: «آيا خدا را به چيزي خبر مي‌دهيد که در آسمانها و زمين سراغ ندارد؟!» منزه است او، و برتر است از آن همتاياني که قرار مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1383, N'(در آغاز) همه مردم امت واحدي بودند؛ سپس اختلاف کردند؛ و اگر فرماني از طرف پروردگارت (درباره عدم مجازات سريع آنان) از قبل صادر نشده بود، در ميان آنها در آنچه اختلاف داشتند داوري مي‌شد (و سپس همگي به مجازات مي‌رسيدند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1384, N'مي‌گويند: «چرا معجزه‌اي از پروردگارش بر او نازل نمي‌شود؟!» بگو: «غيب (و معجزات) تنها براي خدا (و به فرمان او) است! شما در انتظار باشيد، من هم با شما در انتظارم! (شما در انتظار معجزات بهانه‌جويانه باشيد، و من هم در انتظار مجازات شما!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1385, N'هنگامي که به مردم، پس از ناراحتي که به آنها رسيده است، رحمتي بچشانيم، در آيات ما نيرنگ مي‌کنند (،و براي آن نعمت و رحمت توجيهات ناروا مي‌کنند)؛ بگو: «خداوند سريعتر از شما مکر [= چاره‌جويي‌] مي‌کند؛ و رسولان [= فرشتگان ] ما، آنچه نيرنگ مي‌کنيد (و نقشه مي‌کشيد)، مي‌نويسند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1386, N'او کسي است که شما را در خشکي و دريا سير مي‌دهد؛ زماني که در کشتي قرارمي‌گيريد، و بادهاي موافق آنان را (بسوي مقصد) حرکت ميدهد و خوشحال مي‌شوند، ناگهان طوفان شديدي مي‌وزد؛ و امواج از هر سو به سراغ آنها مي‌آيد؛ و گمان مي‌کنند هلاک خواهند شد؛ در آن هنگام، خدا را از روي اخلاص مي‌خوانند که: «اگر ما را از اين گرفتاري نجات دهي، حتما از سپاسگزاران خواهيم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1387, N'اما هنگامي که خدا آنها را رهايي بخشيد، (باز) به ناحق، در زمين ستم ميکنند. اي مردم! ستمهاي شما، به زيان خود شماست! از زندگي دنيا بهره (ميبريد)، سپس بازگشت شما بسوي ماست؛ و ما، شما را به آنچه عمل ميکرديد، خبر ميدهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1388, N'مثل زندگي دنيا، همانند آبي است که از آسمان نازل کرده‌ايم؛ که در پي آن، گياهان (گوناگون) زمين -که مردم و چهارپايان از آن مي‌خورند- مي‌رويد؛ تا زماني که زمين، زيبايي خود را يافته و آراسته مي‌گردد، و اهل آن مطمئن مي‌شوند که مي‌توانند از آن بهره‌مند گردند، (ناگهان) فرمان ما، شب‌هنگام يا در روز، (براي نابودي آن) فرامي‌رسد؛ (سرما يا صاعقه‌اي را بر آن مسلط مي‌سازيم؛) و آنچنان آن را درو مي‌کنيم که گويي ديروز هرگز (چنين کشتزاري) نبوده است! اين گونه، آيات خود را براي گروهي که مي‌انديشند، شرح مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1389, N'و خداوند به سراي صلح و سلامت دعوت مي‌کند؛ و هر کس را بخواهد (و شايسته و لايق ببيند)، به راه راست هدايت مي‌نمايد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1390, N'کساني که نيکي کردند، پاداش نيک و افزون بر آن دارند؛ و تاريکي و ذلت، چهره‌هايشان را نمي‌پوشاند؛ آنها اهل بهشتند، و جاودانه در آن خواهند ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1391, N'اما کساني که مرتکب گناهان شدند، جزاي بدي بمقدار آن دارند؛ و ذلت و خواري، چهره آنان را مي‌پوشاند؛ و هيچ چيز نمي‌تواند آنها را از (مجازات) خدا نگه دارد! (چهره‌هايشان آنچنان تاريک است که) گويي با پاره‌هايي از شب تاريک، صورت آنها پوشيده شده! آنها اهل دوزخند؛ و جاودانه در آن خواهند ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1392, N'(به خاطر بياوريد) روزي را که همه آنها را جمع مي‌کنيم، سپس به مشرکان مي‌گوييم: «شما و معبودهايتان در جاي خودتان باشيد (تا به حسابتان رسيدگي شود!)» سپس آنها را از هم جدا مي‌سازيم (و از هر يک جداگانه سؤال مي‌کنيم). و معبودهايشان (به آنها) مي‌گويند: «شما (هرگز) ما را عبادت نمي‌کرديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1393, N'(آنها در پاسخ مي‌گويند:) همين بس که خدا ميان ما و شما گواه باشد، اگر ما از عبادت شما غافل بوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1394, N'در آن جا، هر کس عملي را که قبلا انجام داده است، مي‌آزمايد. و همگي بسوي «الله» -مولا و سرپرست حقيقي خود- بازگردانده مي‌شوند؛ و چيزهايي را که بدروغ همتاي خدا قرار داده بودند، گم و نابود مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1395, N'بگو: «چه کسي شما را از آسمان و زمين روزي مي‌دهد؟ يا چه کسي مالک (و خالق) گوش و چشمهاست؟ و چه کسي زنده را از مرده، و مرده را از زنده بيرون مي‌آورد؟ و چه کسي امور (جهان) را تدبير مي‌کند؟» بزودي (در پاسخ) مي‌گويند: «خدا»، بگو: «پس چرا تقوا پيشه نمي‌کنيد (و از خدا نمي‌ترسيد)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1396, N'آن است خداوند، پروردگار حق شما (داراي همه اين صفات)! با اين حال، بعد از حق، چه چيزي جز گمراهي وجود دارد؟! پس چرا (از پرستش او) روي گردان مي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1397, N'اينچنين فرمان پروردگارت بر فاسقان مسلم شده که آنها (پس از اين همه لجاجت و گناه)، ايمان نخواهند آورد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1398, N'بگو: «آيا هيچ يک از معبودهاي شما، آفرينش را ايجاد مي‌کند و سپس بازمي‌گرداند؟!» بگو: «تنها خدا آفرينش را ايجاد کرده، سپس بازمي‌گرداند؛ با اين حال، چرا از حق روي‌گردان مي‌شويد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1399, N'بگو: «آيا هيچ يک از معبودهاي شما، به سوي حق هدايت مي‌کند؟! بگو: «تنها خدا به حق هدايت مي‌کند! آيا کسي که هدايت به سوي حق مي‌کند براي پيروي شايسته‌تر است، يا آن کس که خود هدايت نمي‌شود مگر هدايتش کنند؟ شما را چه مي‌شود، چگونه داوري مي‌کنيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1400, N'و بيشتر آنها، جز از گمان (و پندارهاي بي‌اساس)، پيروي نمي‌کنند؛ (در حالي که) گمان، هرگز انسان را از حق بي‌نياز نمي‌سازد (و به حق نمي‌رساند)! به يقين، خداوند از آنچه انجام مي‌دهند، آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1401, N'شايسته نبود (و امکان نداشت) که اين قرآن، بدون وحي الهي به خدا نسبت داده شود؛ ولي تصديقي است براي آنچه پيش از آن است (از کتب آسماني)، و شرح و تفصيلي بر آنها است؛ شکي در آن نيست، و از طرف پروردگار جهانيان است.')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1402, N'آيا آنها مي‌گويند: «او قرآن را بدروغ به خدا نسبت داده است»؟! بگو: «اگر راست مي گوييد، يک سوره همانند آن بياوريد؛ و غير از خدا، هر کس را مي‌توانيد (به ياري) طلبيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1403, N'(ولي آنها از روي علم و دانش قرآن را انکار نکردند؛) بلکه چيزي را تکذيب کردند که آگاهي از آن نداشتند، و هنوز واقعيتش بر آنان روشن نشده است! پيشينيان آنها نيز همين گونه تکذيب کردند؛ پس بنگر عاقبت کار ظالمان چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1404, N'بعضي از آنها، به آن ايمان مي‌آورند؛ و بعضي ايمان نمي‌آورند؛ و پروردگارت به مفسدان آگاهتر است (و آنها را بهتر مي‌شناسد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1405, N'و اگر تو را تکذيب کردند، بگو: «عمل من براي من، و عمل شما براي شماست! شما از آنچه من انجام مي‌دهم بيزاريد و من (نيز) از آنچه شماانجام مي‌دهيد بيزارم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1406, N'گروهي از آنان، بسوي تو گوش فرامي‌دهند (؛اما گويي هيچ نمي‌شنوند و کرند)! آيا تو مي‌تواني سخن خود را به گوش کران برساني، هر چند نفهمند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1407, N'و گروهي از آنان، به سوي تو مي‌نگرند (اما گويي هيچ نمي‌بينند)! آيا تو مي‌تواني نابينايان را هدايت کني، هر چند نبينند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1408, N'خداوند هيچ به مردم ستم نمي‌کند؛ ولي اين مردمند که به خويشتن ستم مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1409, N'(به ياد آور) روزي را که (خداوند) آنها را جمع (و محشور) مي‌سازد؛ آنچنان که (احساس مي‌کنند) گويي جز ساعتي از روز، (در دنيا) توقف نکردند؛ به آن مقدار که يکديگر را (ببينند و) بشناسند! مسلما آنها که لقاي خداوند (و روز رستاخيز) را تکذيب کردند، زيان بردند و هدايت نيافتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1410, N'اگر ما، پاره‌اي از مجازاتهايي را که به آنها وعده داده‌ايم، (در حال حيات تو (به تو نشان دهيم، و يا) پيش از آنکه گرفتار عذاب شوند،) تو را از دنيا ببريم، در هر حال، بازگشتشان به سوي ماست؛ سپس خداوند بر آنچه آنها انجام مي‌دادند گواه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1411, N'براي هر امتي، رسولي است؛ هنگامي که رسولشان به سوي آنان بيايد، بعدالت در ميان آنها داوري مي‌شود؛ و ستمي به آنها نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1412, N'و مي‌گويند: «اگر راست مي‌گوئي، اين وعده (مجازات) کي خواهد بود؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1413, N'بگو: «من (حتي) براي خودم زيان و سودي را مالک نيستم، (تا چه رسد براي شما!) مگر آنچه خدا بخواهد. (اين مقدار مي‌دانم که) براي هر قوم و ملتي، سرآمدي است؛ هنگامي که اجل آنها فرا رسد، (و فرمان مجازات يا مرگشان صادر شود،) نه ساعتي تأخير مي‌کنند، و نه پيشي مي‌گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1414, N'بگو: «اگر مجازات او، شب‌هنگام يا در روز به سراغ شما آيد (،آيا مي‌توانيد آن را از خود دفع کنيد؟!)» پس مجرمان براي چه عجله مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1415, N'يا اينکه آنگاه که واقع شد، به آن ايمان مي‌آوريد! (به شما گفته مي‌شود:) حالا؟! در حالي که قبلا براي آن عجله مي‌کرديد! (ولي اکنون چه سود!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1416, N'سپس به کساني که ستم کردند گفته مي‌شود: عذاب ابدي را بچشيد! آيا جز به آنچه انجام مي‌داديد کيفر داده مي‌شويد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1417, N'از تو مي‌پرسند: «آيا آن (وعده مجازات الهي) حق است؟» بگو: «آري، به پروردگارم سوگند، قطعا حق است؛ و شما نمي‌توانيد از آن جلوگيري کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1418, N'و هر کس که ستم کرده، اگر تمامي آنچه روي زمين است در اختيار داشته باشد، (همه را از هول عذاب،) براي نجات خويش مي‌دهد! و هنگامي که عذاب را ببينند،( پشيمان مي‌شوند؛ اما) پشيماني خود را کتمان مي‌کنند (،مبادا رسواتر شوند)! و در ميان آنها، بعدالت داوري مي‌شود؛ و ستمي بر آنها نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1419, N'آگاه باشيد آنچه در آسمانها و زمين است، از آن خداست! آگاه باشيد وعده خدا حق است، ولي بيشتر آنها نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1420, N'اوست که زنده مي‌کند و مي‌ميراند، و به سوي او بازگردانده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1421, N'اي مردم! اندرزي از سوي پروردگارتان براي شما آمده است؛ و درماني براي آنچه در سينه‌هاست؛ (درماني براي دلهاي شما؛) و هدايت و رحمتي است براي مؤمنان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1422, N'بگو: «به فضل و رحمت خدا بايد خوشحال شوند؛ که اين، از تمام آنچه گردآوري کرده‌اند، بهتر است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1423, N'بگو: «آيا روزيهايي را که خداوند بر شما نازل کرده ديده‌ايد، که بعضي از آن را حلال، و بعضي را حرام نموده‌ايد؟!» بگو: «آيا خداوند به شما اجازه داده، يا بر خدا افترا مي بنديد (و از پيش خود، حلال و حرام مي‌کنيد؟!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1424, N'آنها که بر خدا افترا مي‌بندند، درباره (مجازات) روز رستاخيز، چه مي‌انديشند؟! خداوند نسبت به همه مردم فضل (و بخشش) دارد، اما اکثر آنها سپاسگزاري نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1425, N'در هيچ حال (و انديشه‌اي) نيستي، و هيچ قسمتي از قرآن را تلاوت نمي‌کني، و هيچ عملي را انجام نمي‌دهيد، مگر اينکه ما گواه بر شما هستيم در آن هنگام که وارد آن مي شويد! و هيچ چيز در زمين و آسمان، از پروردگار تو مخفي نمي‌ماند؛ حتي به اندازه سنگيني ذره‌اي، و نه کوچکتر از آن و نه بزرگتر، مگر اينکه (همه آنها) در کتاب آشکار (و لوح محفوظ علم خداوند) ثبت است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1426, N'آگاه باشيد (دوستان و) اولياي خدا، نه ترسي دارند و نه غمگين مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1427, N'همانها که ايمان آوردند، و (از مخالفت فرمان خدا) پرهيز مي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1428, N'در زندگي دنيا و در آخرت، شاد (و مسرور)ند؛ وعده‌هاي الهي تخلف ناپذير است! اين است آن رستگاري بزرگ!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1429, N'سخن آنها تو را غمگين نسازد! تمام عزت (و قدرت)، از آن خداست؛ و او شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1430, N'آگاه باشيد تمام کساني که در آسمانها و زمين هستند، از آن خدا مي‌باشند! و آنها که غير خدا را همتاي او مي‌خوانند، (از منطق و دليلي) پيروي نمي‌کنند؛ آنها فقط از پندار بي اساس پيروي مي‌کنند؛ و آنها فقط دروغ مي‌گويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1431, N'او کسي است که شب را براي شما آفريد، تا در آن آرامش بيابيد؛ و روز را روشني بخش (تا به تلاش زندگي پردازيد) در اينها نشانه‌هايي است براي کساني که گوش شنوا دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1432, N'گفتند: «خداوند فرزندي براي خود انتخاب کرده است»! (از هر عيب و نقص و احتياجي) منزه است! او بي‌نياز است! از آن اوست آنچه در آسمانها و آنچه در زمين است! شما هيچ‌گونه دليلي بر اين ادعا نداريد! آيا به خدا نسبتي مي‌دهيد که نمي‌دانيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1433, N'بگو: «آنها که به خدا دروغ مي‌بندند، (هرگز) رستگار نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1434, N'بهره‌اي (ناچيز) از دنيا دارند؛ سپس بازگشتشان بسوي ماست؛ و بعد، به آنها مجازات شديد به سزاي کفرشان مي‌چشانيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1435, N'سرگذشت نوح را بر آنها بخوان! در آن هنگام که به قوم خود گفت: «اي قوم من! اگر تذکرات من نسبت به آيات الهي، بر شما سنگين (و غير قابل تحمل) است، (هر کار از دستتان ساخته است بکنيد.) من بر خدا توکل کرده‌ام! فکر خود، و قدرت معبودهايتان را جمع کنيد؛ سپس هيچ چيز بر شما پوشيده نماند؛ (تمام جوانب کارتان را بنگريد؛) سپس به حيات من پايان دهيد، و (لحظه‌اي) مهلتم ندهيد! (اما توانايي نداريد!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1436, N'و اگر از قبول دعوتم روي بگردانيد، (کار نادرستي کرده‌ايد؛ چه اينکه) من از شما مزدي نمي‌خواهم؛ مزد من، تنها بر خداست! و من مأمورم که از مسلمين [= تسليم شدگان در برابر فرمان خدا] باشم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1437, N'اما آنها او را تکذيب کردند! و ما، او و کساني را که با او در کشتي بودند، نجات داديم؛ و آنان را جانشين (و وارث کافران) قرار داديم؛ و کساني را که آيات ما را تکذيب کردند، غرق نموديم! پس ببين عاقبت کار کساني که انذار شدند (و به انذار الهي اهميت ندادند)، چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1438, N'سپس بعد از نوح، رسولاني به سوي قومشان فرستاديم؛ آنان دلايل روشن برايشان آوردند؛ اما آنها، به چيزي که پيش از آن تکذيب کرده بودند، ايمان نياوردند! اينچنين بر دلهاي تجاوزکاران مهر مي‌نهيم (تا چيزي را درک نکنند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1439, N'بعد از آنها، موسي و هارون را با آيات خود به سوي فرعون و اطرافيانش فرستاديم؛ اما آنها تکبر کردند (و زير بار حق نرفتند؛ چرا که) آنها گروهي مجرم بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1440, N'و هنگامي که حق از نزد ما بسراغ آنها آمد، گفتند: «اين، سحري است آشکار !»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1441, N'موسي گفت: «آيا درباره حق، هنگامي که به سوي شما آمد، (چنين) مي‌گوييد؟! آيا اين سحر است؟! در حالي که ساحران (هرگز) رستگار (و پيروز) نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1442, N'گفتند: «آيا آمده‌اي که ما را، از آنچه پدرانمان را بر آن يافتيم، منصرف سازي؛ و بزرگي (و رياست) در روي زمين، از آن شما دو تن باشد؟! ما (هرگز) به شما ايمان نمي‌آوريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1443, N'فرعون گفت: «(برويد و) هر جادوگر (و ساحر) دانايي را نزد من آوريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1444, N'هنگامي که ساحران (به ميدان مبارزه) آمدند، موسي به آنها گفت: «آنچه (از وسايل سحر) را مي‌توانيد بيفکنيد، بيفکنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1445, N'هنگامي که افکندند، موسي گفت: «آنچه شما آورديد، سحر است؛ که خداوند بزودي آن را باطل مي‌کند؛ چرا که خداوند (هرگز) عمل مفسدان را اصلاح نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1446, N'او حق را به وعده خويش، تحقق مي‌بخشد؛ هر چند مجرمان کراهت داشته باشند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1447, N'(در آغاز،) هيچ کس به موسي ايمان نياورد، مگر گروهي از فرزندان قوم او؛ (آن هم) با ترس از فرعون و اطرافيانش، مبادا آنها را شکنجه کنند؛ زيرا فرعون، برتري‌جويي در زمين داشت؛ و از اسرافکاران بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1448, N'موسي گفت: «اي قوم من! اگر شما به خدا ايمان آورده‌ايد، بر او توکل کنيد اگر تسليم فرمان او هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1449, N'گفتند: «تنها بر خدا توکل داريم؛ پروردگارا! ما را مورد شکنجه گروه ستمگر قرار مده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1450, N'و ما را با رحمتت از (دست) قوم کافر رهايي بخش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1451, N'و به موسي و برادرش وحي کرديم که: «براي قوم خود، خانه‌هايي در سرزمين مصر انتخاب کنيد؛ و خانه‌هايتان را مقابل يکديگر (و متمرکز) قرار دهيد! و نماز را برپا داريد! و به مؤمنان بشارت ده (که سرانجام پيروز مي‌شوند!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1452, N'موسي گفت: «پروردگارا! تو فرعون و اطرافيانش را زينت و اموالي (سرشار) در زندگي دنيا داده‌اي، پروردگارا! در نتيجه (بندگانت را) از راه تو گمراه مي‌سازند! پروردگارا! اموالشان را نابود کن! و (بجرم گناهانشان،) دلهايشان را سخت و سنگين ساز، به گونه‌اي که ايمان نياورند تا عذاب دردناک را ببينند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1453, N'فرمود: «دعاي شما پذيرفته شد! استقامت به خرج دهيد؛ و از راه (و رسم) کساني که نمي‌دانند، تبعيت نکنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1454, N'(سرانجام) بني اسرائيل را از دريا [= رود عظيم نيل‌] عبور داديم؛ و فرعون و لشکرش از سر ظلم و تجاوز، به دنبال آنها رفتند؛ هنگامي که غرقاب دامن او را گرفت، گفت: «ايمان آوردم که هيچ معبودي، جز کسي که بني اسرائيل به او ايمان آورده‌اند، وجود ندارد؛ و من از مسلمين هستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1455, N'(اما به او خطاب شد:) الآن؟!! در حالي که قبلا عصيان کردي، و از مفسدان بودي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1456, N'ولي امروز، بدنت را (از آب) نجات مي‌دهيم، تا عبرتي براي آيندگان باشي! و بسياري از مردم، از آيات ما غافلند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1457, N'(سپس) بني اسرائيل را در جايگاه صدق (و راستي) منزل داديم؛ و از روزيهاي پاکيزه به آنها عطا کرديم؛ (اما آنها به نزاع و اختلاف برخاستند!) و اختلاف نکردند، مگر بعد از آنکه علم و آگاهي به سراغشان آمد! پروردگار تو روز قيامت، در آنچه اختلاف مي‌کردند، ميان آنها داوري مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1458, N'و اگر در آنچه بر تو نازل کرده‌ايم ترديدي داري، از کساني که پيش از تو کتاب آسماني را مي‌خواندند بپرس، به يقين، «حق» از طرف پروردگارت به تو رسيده است؛ بنابر اين، هرگز از ترديدکنندگان مباش! [مسلما او ترديدي نداشت! اين درسي براي مردم بود!]')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1459, N'و از آنها مباش که آيات خدا را تکذيب کردند، که از زيانکاران خواهي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1460, N'(و بدان) آنها که فرمان پروردگار تو بر آنان تحقق يافته، (و بجرم اعمالشان، توفيق هدايت را از آنها گرفته هرگز) ايمان نمي‌آورند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1461, N'هر چند تمام آيات (و نشانه‌هاي الهي) به آنان برسد، تا زماني که عذاب دردناک را ببينند! (زيرا تاريکي گناه، قلبهايشان را فرا گرفته، و راهي به روشنايي ندارند!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1462, N'چرا هيچ يک از شهرها و آباديها ايمان نياوردند که (ايمانشان بموقع باشد، و) به حالشان مفيد افتد؟! مگر قوم يونس، هنگامي که آنها ايمان آوردند، عذاب رسوا کننده را در زندگي دنيا از آنان برطرف ساختيم؛ و تا مدت معيني [= پايان زندگي و اجلشان‌] آنها را بهره‌مند ساختيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1463, N'و اگر پروردگار تو مي‌خواست، تمام کساني که روي زمين هستند، همگي به (اجبار) ايمان مي‌آوردند؛ آيا تو مي‌خواهي مردم را مجبور سازي که ايمان بياورند؟! (ايمان اجباري چه سودي دارد؟!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1464, N'(اما) هيچ کس نمي‌تواند ايمان بياورد، جز به فرمان خدا (و توفيق و ياري و هدايت او)! و پليدي (کفر و گناه) را بر کساني قرارمي‌دهد که نمي‌انديشند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1465, N'بگو: «نگاه کنيد چه چيز (از آيات خدا و نشانه‌هاي توحيدش) در آسمانها و زمين است!» اما اين آيات و انذارها به حال کساني که (به خاطر لجاجت) ايمان نمي‌آورند مفيد نخواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1466, N'آيا آنها (چيزي) جز همانند روزهاي پيشينيان (و بلاها و مجازاتهايشان) را انتظار مي‌کشند؟! بگو: «شما انتظار بکشيد، من نيز با شما انتظار مي‌کشم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1467, N'سپس (هنگام نزول بلا و مجازات،) فرستادگان خود و کساني را که (به آنان) ايمان مي‌آورند، نجات مي‌دهيم و همين گونه، بر ما حق است که مؤمنان (به تو) را (نيز) رهايي بخشيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1468, N'بگو: «اي مردم! اگر در عقيده من شک داريد، من آنهايي را که جز خدا مي‌پرستيد، نمي‌پرستم! تنها خداوندي را پرستش مي‌کنم که شما را مي‌ميراند! و من مأمورم که از مؤمنان باشم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1469, N'و (به من دستور داده شده که:) روي خود را به آييني متوجه ساز که از هر گونه شرک، خالي است؛ و از مشرکان مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1470, N'و جز خدا، چيزي را که نه سودي به تو مي‌رساند و نه زياني، مخوان! که اگر چنين کني، از ستمکاران خواهي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1471, N'و اگر خداوند، (براي امتحان يا کيفر گناه،) زياني به تو رساند، هيچ کس جز او آن را برطرف نمي‌سازد؛ و اگر اراده خيري براي تو کند، هيچ کس مانع فضل او نخواهد شد! آنرا به هر کس از بندگانش بخواهد مي‌رساند؛ و او غفور و رحيم است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1472, N'بگو: «اي مردم! حق از طرف پروردگارتان به سراغ شما آمده؛ هر کس (در پرتو آن) هدايت يابد، براي خود هدايت شده؛ و هر کس گمراه گردد، به زيان خود گمراه مي‌گردد؛ و من مأمور (به اجبار) شما نيستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1473, N'و از آنچه بر تو وحي مي‌شود پيروي کن، و شکيبا باش (و استقامت نما)، تا خداوند فرمان (پيروزي) را صادر کند؛ و او بهترين حاکمان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1474, N'الر، اين کتابي است که آياتش استحکام يافته؛ سپس تشريح شده و از نزد خداوند حکيم و آگاه (نازل گرديده) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1475, N'(دعوت من اين است) که: جز «الله» را نپرستيد! من از سوي او براي شما بيم دهنده و بشارت دهنده‌ام!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1476, N'و اينکه: از پروردگار خويش آمرزش بطلبيد؛ سپس بسوي او بازگرديد؛ تا شما را تا مدت معيني، (از مواهب زندگي اين جهان،) به خوبي بهره‌مند سازد؛ و به هر صاحب فضيلتي، به مقدار فضيلتش ببخشد! و اگر (از اين فرمان) روي گردان شويد، من بر شما از عذاب روز بزرگي بيمناکم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1477, N'(بدانيد) بازگشت شما بسوي «الله» است، و او بر هر چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1478, N'آگاه باشيد، آنها (سرها را به هم نزديک ساخته، و) سينه‌هاشان را در کنار هم قرارمي‌دهند، تا خود (و سخنان خويش) را از او [= پيامبر] پنهان دارند! آگاه باشيد، آنگاه که آنها لباسهايشان را به خود مي‌پيچند و خويش را در آن پنهان مي‌کنند، (خداوند) مي‌داند آنچه را پنهان مي‌کنند و آنچه را آشکار مي‌سازند؛ چرا که او، از اسرار درون سينه‌ها، آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1479, N'هيچ جنبنده‌اي در زمين نيست مگر اينکه روزي او بر خداست! او قرارگاه و محل نقل و انتقالش را مي‌داند؛ همه اينها در کتاب آشکاري ثبت است! [= در لوح محفوظ ، در کتاب علم خدا]')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1480, N'او کسي است که آسمانها و زمين را در شش روز [= شش دوران‌] آفريد؛ و عرش (حکومت) او، بر آب قرار داشت؛ (بخاطر اين آفريد) تا شما را بيازمايد که کداميک عملتان بهتر است! و اگر (به آنها) بگويي: «شما بعد از مرگ، برانگيخته مي‌شويد! «، مسلما» کافران مي‌گويند: «اين سحري آشکار است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1481, N'و اگر مجازات را تا زمان محدودي از آنها به تأخير اندازيم، (از روي استهزا مي‌گويند: «چه چيز مانع آن شده است؟!» آگاه باشيد، آن روز که (عذاب) به سراغشان آيد، از آنها بازگردانده نخواهد شد؛ (و هيچ قدرتي مانع آن نخواهد بود؛) و آنچه را مسخره مي‌کردند، دامانشان را مي‌گيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1482, N'و اگر از جانب خويش، نعمتي به انسان بچشانيم، سپس آن را از او بگيريم، بسيار نوميد و ناسپاس خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1483, N'و اگر بعد از شدت و رنجي که به او رسيده، نعمتهايي به او بچشانيم، مي‌گويد: «مشکلات از من برطرف شد، و ديگر باز نخواهد گشت!» و غرق شادي و غفلت و فخرفروشي مي‌شود...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1484, N'مگر آنها که (در سايه ايمان راستين،) صبر و استقامت ورزيدند و کارهاي شايسته انجام دادند؛ که براي آنها، آمرزش و اجر بزرگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1485, N'شايد (ابلاغ) بعض آياتي را که به تو وحي مي‌شود، (بخاطر عدم پذيرش آنها) ترک کني (و به تأخير اندازي)؛ و سينه‌ات از اين جهت تنگ (و ناراحت) شود که مي‌گويند: «چرا گنجي بر او نازل نشده؟! و يا چرا فرشته‌اي همراه او نيامده است؟!» (ابلاغ کن، و نگران و ناراحت مباش! چرا که) تو فقط بيم دهنده‌اي؛ و خداوند، نگاهبان و ناظر بر همه چيز است (؛و به حساب آنان مي‌رسد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1486, N'آنها مي‌گويند: «او به دروغ اين (قرآن) را (به خدا) نسبت داده (و ساختگي است)!» بگو: «اگر راست مي‌گوييد، شما هم ده سوره ساختگي همانند اين قرآن بياوريد؛ و تمام کساني را که مي‌توانيد -غير از خدا- (براي اين کار) دعوت کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1487, N'و اگر آنها دعوت شما را نپذيرفتند، بدانيد (قرآن) تنها با علم الهي نازل شده؛ و هيچ معبودي جز او نيست! آيا با اين حال، تسليم مي‌شويد؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1488, N'کساني که زندگي دنيا و زينت آن را بخواهند، (نتيجه) اعمالشان را در همين دنيا بطور کامل به آنها مي‌دهيم؛ و چيزي کم و کاست از آنها نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1489, N'(ولي) آنها در آخرت، جز آتش، (سهمي) نخواهند داشت؛ و آنچه را در دنيا (براي غير خدا) انجام دادند، بر باد مي‌رود؛ و آنچه را عمل مي‌کردند، باطل و بي‌اثر مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1490, N'آيا آن کس که دليل آشکاري از پروردگار خويش دارد، و بدنبال آن، شاهدي از سوي او مي‌باشد، و پيش از آن، کتاب موسي که پيشوا و رحمت بود (گواهي بر آن مي‌دهد، همچون کسي است که چنين نباشد)؟! آنها [= حق‌طلبان و حقيقت‌جويان‌] به او (که داراي اين ويژگيهاست،) ايمان مي‌آورند! و هر کس از گروه‌هاي مختلف به او کافر شود، آتش وعده‌گاه اوست! پس، ترديدي در آن نداشته باش که آن حق است از پروردگارت! ولي بيشتر مردم ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1491, N'چه کسي ستمکارتر است از کساني که بر خدا افترا مي‌بندند؟! آنان (روز رستاخيز) بر پروردگارشان عرضه مي‌شوند، در حالي که شاهدان [= پيامبران و فرشتگان‌] مي‌گويند: «اينها همانها هستند که به پروردگارشان دروغ بستند! اي لعنت خدا بر ظالمان باد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1492, N'همانها که (مردم را) از راه خدا بازمي‌دارند؛ و راه حق را کج و معوج نشان مي‌دهند؛ و به سراي آخرت کافرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1493, N'آنها هيچ گاه توانايي فرار در زمين را ندارند؛ و جز خدا، پشتيبانهايي نمي‌يابند! عذاب خدا براي آنها مضاعف خواهد بود؛ (چرا که هم خودشان گمراه بودند، و هم ديگران را گمراه ساختند؛) آنها هرگز توانايي شنيدن (حق را) نداشتند؛ و (حقيقت را) نمي‌ديدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1494, N'آنان کساني هستند که سرمايه وجود خود را از دست داده‌اند؛ و تمام معبودهاي دروغين از نظرشان گم شدند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1495, N'(به ناچار) آنها در سراي آخرت، قطعا از همه زيانکارترند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1496, N'کساني که ايمان آوردند و کارهاي شايسته انجام دادند و در برابر پروردگارشان خضوع و خشوع کردند، آنها اهل بهشتند؛ و جاودانه در آن خواهند ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1497, N'حال اين دو گروه [= مؤمنان و منکران‌]، حال «نابينا و کر» و «بينا و شنوا» است؛ آيا اين دو، همانند يکديگرند؟! آيا پند نمي‌گيرند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1498, N'ما نوح را بسوي قومش فرستاديم (؛نخستين بار به آنها گفت): «من براي شما بيم‌دهنده‌اي آشکارم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1499, N'جز «الله» [= خداي يگانه يکتا] را نپرستيد؛ زيرا بر شما از عذاب روز دردناکي مي‌ترسم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1500, N'اشراف کافر قومش (در پاسخ او) گفتند: «ما تو را جز بشري همچون خودمان نمي‌بينيم! و کساني را که از تو پيروي کرده‌اند، جز گروهي اراذل ساده‌لوح، مشاهده نمي‌کنيم؛ و براي شما فضيلتي نسبت به خود نمي‌بينيم؛ بلکه شما را دروغگو تصور مي‌کنيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1501, N'(نوح) گفت: «اگر من دليل روشني از پروردگارم داشته باشم، و از نزد خودش رحمتي به من داده باشد -و بر شما مخفي مانده- (آيا باز هم رسالت مرا انکار مي‌کنيد)؟! آيا ما مي‌توانيم شما را به پذيرش اين دليل روشن مجبور سازيم، با اينکه شما کراهت داريد؟!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1502, N'اي قوم! من به خاطر اين دعوت، اجر و پاداشي از شما نمي‌طلبم؛ اجر من، تنها بر خداست! و من، آنها را که ايمان آورده‌اند، (بخاطر شما) از خود طرد نمي‌کنم؛ چرا که آنها پروردگارشان را ملاقات خواهند کرد؛ (اگر آنها را از خود برانم، در دادگاه قيامت، خصم من خواهند بود؛) ولي شما را قوم جاهلي مي‌بينم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1503, N'اي قوم! چه کسي مرا در برابر (مجازات) خدا ياري مي‌دهد اگر آنان را طرد کنم؟! آيا انديشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1504, N'من هرگز به شما نمي‌گويم خزائن الهي نزد من است! و غيب هم نمي‌دانم! و نمي‌گويم من فرشته‌ام! و (نيز) نمي‌گويم کساني که در نظر شما خوار مي‌آيند، خداوند خيري به آنها نخواهد داد؛ خدا از دل آنان آگاهتر است! (با اين حال، اگر آنها را برانم،) در اين صورت از ستمکاران خواهم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1505, N'گفتند: «اي نوح! با ما جر و بحث کردي، و زياد هم جر و بحث کردي! (بس است!) اکنون اگر راستي مي‌گويي، آنچه را (از عذاب الهي) به ما وعده مي‌دهي بياور!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1506, N'(نوح) گفت: «اگر خدا اراده کند، خواهد آورد؛ و شما قدرت فرار (از آن را) نخواهيد داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1507, N'(اما چه سود که) هرگاه خدا بخواهد شما را (بخاطر گناهانتان) گمراه سازد، و من بخواهم شما را اندرز دهم، اندرز من سودي به حالتان نخواهد داشت! او پروردگار شماست؛ و بسوي او بازگشت داده مي‌شويد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1508, N'(مشرکان) مي‌گويند: «او [= محمد (ص)] اين سخنان را بدروغ به خدا نسبت داده است! « بگو: «اگر من اينها را از پيش خود ساخته باشم و به او نسبت دهم، گناهش بر عهده من است؛ ولي من از گناهان شما بيزارم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1509, N'به نوح وحي شد که: «جز آنها که (تاکنون) ايمان آورده‌اند، ديگر هيچ کس از قوم تو ايمان نخواهد آورد! پس، از کارهايي که مي‌کردند، غمگين مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1510, N'و (اکنون) در حضور ما و طبق وحي ما، کشتي بساز! و درباره آنها که ستم کردند شفاعت مکن، که (همه) آنها غرق شدني هستند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1511, N'او مشغول ساختن کشتي بود، و هر زمان گروهي از اشراف قومش بر او مي‌گذشتند، او را مسخره مي‌کردند؛ (ولي نوح) گفت: «اگر ما را مسخره مي‌کنيد، ما نيز شما را همين‌گونه مسخره خواهيم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1512, N'بزودي خواهيد دانست چه کسي عذاب خوارکننده به سراغش خواهد آمد، و مجازات جاودان بر او وارد خواهد شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1513, N'(اين وضع همچنان ادامه يافت) تا آن زمان که فرمان ما فرا رسيد، و تنور جوشيدن گرفت؛ (به نوح) گفتيم: «از هر جفتي از حيوانات (از نر و ماده) يک زوج در آن (کشتي) حمل کن! همچنين خاندانت را (بر آن سوار کن) -مگر آنها که قبلا وعده هلاک آنان داده شده [= همسر و يکي از فرزندانت‌]- و همچنين مؤمنان را!» اما جز عده کمي همراه او ايمان نياوردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1514, N'او گفت: «به نام خدا بر آن سوار شويد! و هنگام حرکت و توقف کشتي، ياد او کنيد، که پروردگارم آمرزنده و مهربان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1515, N'و آن کشتي، آنها را از ميان امواجي همچون کوه‌ها حرکت ميداد؛ (در اين هنگام،) نوح فرزندش را که در گوشه‌اي بود صدا زد: «پسرم! همراه ما سوار شو، و با کافران مباش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1516, N'گفت: «بزودي به کوهي پناه ميبرم تا مرا از آب حفظ کند!» (نوح) گفت: «امروز هيچ نگهداري در برابر فرمان خدا نيسست؛ مگر آن کس را که او رحم کند!» در اين هنگام، موج در ميان آن دو حايل شد؛ و او در زمره غرق‌شدگان قرار گرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1517, N'و گفته شد: «اي زمين، آبت را فرو بر! و اي آسمان، خودداري کن! و آب فرو نشست و کار پايان يافت و (کشتي) بر (دامنه کوه) جودي، پهلو گرفت؛ و (در اين هنگام،) گفته شد:» دور باد قوم ستمگر (از سعادت و نجات و رحمت خدا!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1518, N'نوح به پروردگارش عرض کرد: «پروردگارا! پسرم از خاندان من است؛ و وعده تو (در مورد نجات خاندانم) حق است؛ و تو از همه حکم‌کنندگان برتري!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1519, N'فرمود: «اي نوح! او از اهل تو نيست! او عمل غير صالحي است [= فرد ناشايسته‌اي است‌]! پس، آنچه را از آن آگاه نيستي، از من مخواه! من به تو اندرز مي‌دهم تا از جاهلان نباشي!!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1520, N'عرض کرد: «پروردگارا! من به تو پناه مي‌برم که از تو چيزي بخواهم که از آن آگاهي ندارم! و اگر مرا نبخشي، و بر من رحم نکني، از زيانکاران خواهم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1521, N'(به نوح) گفته شد: «اي نوح! با سلامت و برکاتي از ناحيه ما بر تو و بر تمام امتهايي که با تواند، فرود آي! و امتهاي نيز هستند که ما آنها را از نعمتها بهره‌مند خواهيم ساخت، سپس عذاب دردناکي از سوي ما به آنها مي‌رسد (،چرا که اين نعمتها را کفران مي‌کنند!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1522, N'اينها از خبرهاي غيب است که به تو (اي پيامبر) وحي مي‌کنيم؛ نه تو، و نه قومت، اينها را پيش از اين نمي‌دانستيد! بنابر اين، صبر و استقامت کن، که عاقبت از آن پرهيزگاران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1523, N'(ما) به سوي (قوم) عاد، برادرشان «هود» را فرستاديم؛ (به آنها) گفت: «اي قوم من! خدا را پرستش کنيد، که معبودي جز او براي شما نيست! شما فقط تهمت مي‌زنيد (و بتها را شريک او مي‌خوانيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1524, N'اي قوم من! من از شما براي اين (رسالت)، پاداشي نمي‌طلبم؛ پاداش من، تنها بر کسي است که مرا آفريده است؛ آيا نمي‌فهميد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1525, N'و اي قوم من! از پروردگارتان طلب آمرزش کنيد، سپس به سوي او بازگرديد، تا (باران) آسمان را پي در پي بر شما بفرستد؛ و نيرويي بر نيرويتان بيفزايد! و گنهکارانه، روي (از حق) بر نتابيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1526, N'گفتند: «اي هود! تو دليل روشني براي ما نياورده‌اي! و ما خدايان خود را بخاطر حرف تو، رها نخواهيم کرد! و ما (اصلا) به تو ايمان نمي‌آوريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1527, N'ما (درباره تو) فقط مي‌گوييم: بعضي از خدايان ما، به تو زيان رسانده (و عقلت را ربوده)اند!» (هود) گفت: «من خدا را به شهادت مي‌طلبم، شما نيز گواه باشيد که من بيزارم از آنچه شريک (خدا) قرارمي‌دهيد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1528, N'از آنچه غير او (مي‌پرستيد)! حال که چنين است، همگي براي من نقشه بکشيد؛ و مرا مهلت ندهيد! (اما بدانيد کاري از دست شما ساخته نيست!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1529, N'من، بر «الله» که پروردگار من و شماست، توکل کرده‌ام! هيچ جنبنده‌اي نيست مگر اينکه او بر آن تسلط دارد؛ (اما سلطه‌اي با عدالت! چرا که) پروردگار من بر راه راست است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1530, N'پس اگر روي برگردانيد، من رسالتي را که مأمور بودم به شما رساندم؛ و پروردگارم گروه ديگري را جانشين شما مي‌کند؛ و شما کمترين ضرري به او نمي‌رسايند؛ پروردگارم حافظ و نگاهبان هر چيز است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1531, N'و هنگامي که فرمان ما فرا رسيد، «هود» و کساني را که با او ايمان آورده بودند، به رحمت خود نجات داديم؛ و آنها را از عذاب شديد، رهايي بخشيديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1532, N'و اين قوم «عاد» بود که آيات پروردگارشان را انکار کردند؛ و پيامبران او را معصيت نمودند؛ و از فرمان هر ستمگر دشمن حق، پيروي کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1533, N'آنان، در اين دنيا و روز قيامت، لعنت (و نام ننگيني) بدنبال دارند! بدانيد «عاد»نسبت به پروردگارشان کفر ورزيدند! دور باد «عاد» -قوم هود- (از رحمت خدا، و خير و سعادت)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1534, N'و بسوي قوم «ثمود»، برادرشان «صالح» را (فرستاديم)؛ گفت: «اي قوم من! خدا را پرستش کنيد، که معبودي جز او براي شما نيست! اوست که شما را از زمين آفريد، و آبادي آن را به شما واگذاشت! از او آمرزش بطلبيد، سپس به سوي او بازگرديد، که پروردگارم (به بندگان خود) نزديک، و اجابت‌کننده (خواسته‌هاي آنها) است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1535, N'گفتند: «اي صالح! تو پيش از اين، مايه اميد ما بودي! آيا ما را از پرستش آنچه پدرانمان مي‌پرستيدند، نهي مي‌کني؟! در حالي که ما، در مورد آنچه به سوي آن دعوتمان مي‌کني، در شک و ترديد هستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1536, N'گفت: «اي قوم! اگر من دليل آشکاري از پروردگارم داشته باشم، و رحمتي از جانب خود به من داده باشد (،مي‌توانم از ابلاغ رسالت او سرپيچي کنم)؟! اگر من نافرماني او کنم، چه کسي مي‌تواند مرا در برابر وي ياري دهد؟! پس، (سخنان) شما، جز اطمينان به زيانکار بودنتان، چيزي بر من نمي‌افزايد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1537, N'اي قوم من! اين «ناقه» خداوند است، که براي شما نشانه‌اي است؛ بگذاريد در زمين خدا به چرا مشغول شود؛ هيچ گونه آزاري به آن نرسانيد، که بزودي عذاب خدا شما را خواهد گرفت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1538, N'(اما) آنها آن (ناقه) را از پاي در آوردند! و (صالح به آنها) گفت: «(مهلت شما تمام شد!) سه روز در خانه‌هايتان بهره‌مند گرديد؛ (و بعد از آن، عذاب الهي فرا خواهد رسيد؛) اين وعده‌اي است که دروغ نخواهد بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1539, N'و هنگامي که فرمان (مجازات) ما فرا رسيد، صالح و کساني را که با او ايمان آورده بودند، به رحمت خود (از آن عذاب) و از رسوايي آن روز، رهايي بخشيديم؛ چرا که پروردگارت قوي و شکست ناپذير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1540, N'و کساني را که ستم کرده بودند، صيحه (آسماني) فروگرفت؛ و در خانه‌هايشان به روي افتادند و مردند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1541, N'آنچنان که گويي هرگز ساکن آن ديار نبودند! بدانيد قوم ثمود، پروردگارشان را انکار کردند! دور باد قوم ثمود (از رحمت پروردگار)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1542, N'فرستادگان ما [= فرشتگان‌] براي ابراهيم بشارت آوردند؛ گفتند: «سلام!» (او نيز) گفت: «سلام!» و طولي نکشيد که گوساله برياني (براي آنها) آورد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1543, N'(اما) هنگامي که ديد دست آنها به آن نمي‌رسد (و از آن نمي‌خورند، کار) آنها را زشت شمرد؛ و در دل احساس ترس نمود. به او گفتند: «نترس! ما به سوي قوم لوط فرستاده شده‌ايم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1544, N'و همسرش ايستاده بود، (از خوشحالي) خنديد؛ پس او را بشارت به اسحاق، و بعد از او يعقوب داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1545, N'گفت: «اي واي بر من! آيا من فرزند مي‌آورم در حالي که پيرزنم، و اين شوهرم پيرمردي است؟! اين راستي چيز عجيبي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1546, N'گفتند: «آيا از فرمان خدا تعجب ميکني؟! اين رحمت خدا و برکاتش بر شما خانواده است؛ چرا که او ستوده و والا است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1547, N'هنگامي که ترس ابراهيم فرو نشست، و بشارت به او رسيد، درباره قوم لوط با ما مجادله مي‌کرد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1548, N'چرا که ابراهيم، بردبار و دلسوز و بازگشت‌کننده (بسوي خدا) بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1549, N'اي ابراهيم! از اين (درخواست) صرف‌نظر کن، که فرمان پروردگارت فرا رسيده؛ و بطور قطع عذاب (الهي) به سراغ آنها مي‌آيد؛ و برگشت ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1550, N'و هنگامي که رسولان ما [= فرشتگان عذاب‌] به سراغ لوط آمدند، از آمدنشان ناراحت شد؛ و قلبش پريشان گشت؛ و گفت: «امروز روز سختي است!» (زيرا آنها را نشناخت؛ و ترسيد قوم تبهکار مزاحم آنها شوند.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1551, N'قوم او (بقصد مزاحمت ميهمانان) بسرعت به سراغ او آمدند -و قبلا کارهاي بد انجام مي‌دادند- گفت: «اي قوم من! اينها دختران منند؛ براي شما پاکيزه‌ترند! (با آنها ازدواج کنيد؛ و از زشتکاري چشم بپوشيد!) از خدا بترسيد؛ و مرا در مورد ميهمانانم رسوا نسازيد! آيا در ميان شما يک مرد فهميده و آگاه وجود ندارد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1552, N'گفتند: «تو که مي‌داني ما تمايلي به دختران تو نداريم؛ و خوب مي‌داني ما چه مي‌خواهيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1553, N'گفت: «(افسوس!) اي کاش در برابر شما قدرتي داشتم؛ يا تکيه‌گاه و پشتيبان محکمي در اختيار من بود! (آنگاه مي‌دانستم با شما زشت‌سيرتان ددمنش چه کنم!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1554, N'(فرشتگان عذاب) گفتند: «اي لوط! ما فرستادگان پروردگار توايم! آنها هرگز دسترسي به تو پيدا نخواهند کرد! در دل شب، خانواده‌ات را (از اين شهر) حرکت ده! و هيچ يک از شما پشت سرش را نگاه نکند؛ مگر همسرت، که او هم به همان بلايي که آنها گرفتار مي‌شوند، گرفتار خواهد شد! موعد آنها صبح است؛ آيا صبح نزديک نيست؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1555, N'و هنگامي که فرمان ما فرا رسيد، آن (شهر و ديار) را زير و رو کرديم؛ و باراني از سنگ [= گلهاي متحجر] متراکم بر روي هم، بر آنها نازل نموديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1556, N'(سنگهايي که) نزد پروردگارت نشاندار بود؛ و آن، (از ساير) ستمگران دور نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1557, N'و بسوي «مدين» برادرشان شعيب را (فرستاديم)؛ گفت: «اي قوم من! خدا را پرستش کنيد، که جز او، معبود ديگري براي شما نيست! پيمانه و وزن را کم نکنيد (و دست به کم‌فروشي نزنيد)! من (هم اکنون) شما را در نعمت مي‌بينم؛ (ولي) از عذاب روز فراگير، بر شما بيمناکم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1558, N'و اي قوم من! پيمانه و وزن را با عدالت، تمام دهيد! و بر اشياء (و اجناس) مردم، عيب نگذاريد؛ و از حق آنان نکاهيد! و در زمين به فساد نکوشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1559, N'آنچه خداوند براي شما باقي گذارده (از سرمايه‌هاي حلال)، برايتان بهتر است اگر ايمان داشته باشيد! و من، پاسدار شما (و مأمور بر اجبارتان به ايمان) نيستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1560, N'گفتند: «اي شعيب! آيا نمازت به تو دستور مي‌دهد که آنچه را پدرانمان مي‌پرستيدند، ترک کنيم؛ يا آنچه را مي‌خواهيم در اموالمان انجام ندهيم؟! تو که مرد بردبار و فهميده‌اي هستي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1561, N'گفت: «اي قوم! به من بگوييد، هرگاه من دليل آشکاري از پروردگارم داشته باشم، و رزق (و موهبت) خوبي به من داده باشد، (آيا مي‌توانم بر خلاف فرمان او رفتار کنم؟!) من هرگز نمي‌خواهم چيزي که شما را از آن باز مي‌دارم، خودم مرتکب شوم! من جز اصلاح -تا آنجا که توانايي دارم- نمي‌خواهم! و توفيق من، جز به خدا نيست! بر او توکل کردم؛ و به سوي او بازمي‌گردم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1562, N'و اي قوم من! دشمني و مخالفت با من، سبب نشود که شما به همان سرنوشتي که قوم نوح يا قوم هود يا قوم صالح گرفتار شدند، گرفتار شويد! و قوم لوط از شما چندان دور نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1563, N'از پروردگار خود، آمرزش بطلبيد؛ و به سوي او بازگرديد؛ که پروردگارم مهربان و دوستدار (بندگان توبه‌کار) است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1564, N'گفتند: «اي شعيب! بسياري از آنچه را مي‌گويي، ما نمي‌فهميم! و ما تو را در ميان خود، ضعيف مي‌يابيم؛ و اگر (بخاطر) قبيله کوچکت نبود، تو را سنگسار مي‌کرديم؛ و تو در برابر ما قدرتي نداري!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1565, N'گفت: «اي قوم! آيا قبيله کوچک من، نزد شما عزيزتر از خداوند است؟! در حالي که (فرمان) او را پشت سر انداخته‌ايد! پروردگارم به آنچه انجام مي‌دهيد، احاطه دارد (و آگاه است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1566, N'اي قوم! هر کاري از دستتان ساخته است، انجام دهيد، من هم کار خود را خواهم کرد؛ و بزودي خواهيد دانست چه کسي عذاب خوارکننده به سراغش مي‌آيد، و چه کسي دروغگوست! شما انتظار بکشيد، من هم در انتظارم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1567, N'و هنگامي که فرمان ما فرا رسيد، شعيب و کساني را که با او ايمان آورده بودند، به رحمت خود نجات داديم؛ و آنها را که ستم کردند، صيحه (آسماني) فرو گرفت؛ و در ديار خود، به رو افتادند (و مردند)...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1568, N'آنچنان که گويي هرگز از ساکنان آن (ديار) نبودند! دور باد مدين (و اهل آن) از رحمت خدا، همان گونه که قوم ثمود دور شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1569, N'ما، موسي را با آيات خود و دليل آشکاري فرستاديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1570, N'بسوي فرعون و اطرافيانش؛ اما آنها از فرمان فرعون پيروي کردند؛ در حالي که فرمان فرعون، مايه رشد و نجات نبود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1571, N'روز قيامت، او در پيشاپيش قومش خواهد بود؛ و (به جاي چشمه‌هاي زلال بهشت) آنها را وارد آتش مي‌کند! و چه بد آبشخوري است (آتش)، که بر آن وارد مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1572, N'آنان در اين جهان و روز قيامت، لعنتي بدنبال دارند؛ و چه بد عطايي است (لعن و دوري از رحمت خدا)، که نصيب آنان مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1573, N'اين از اخبار شهرها و آباديهاست که ما براي تو بازگو مي‌کنيم؛ که بعضي (هنوز) برپا هستند، و بعضي درو شده‌اند (و از ميان رفته‌اند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1574, N'ما به آنها ستم نکرديم؛ بلکه آنها خودشان بر خويشتن ستم روا داشتند! و هنگامي که فرمان مجازات الهي فرا رسيد، معبوداني را که غير از خدا مي‌خواندند، آنها را ياري نکردند؛ و جز بر هلاکت آنان نيفزودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1575, N'و اينچنين است مجازات پروردگار تو، هنگامي که شهرها و آباديهاي ظالم را مجازات مي‌کند! (آري،) مجازات او، دردناک و شديد است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1576, N'در اين، نشانه‌اي است براي کسي که از عذاب آخرت مي‌ترسد؛ همان روزي است که مردم در آن جمع مي‌شوند، و روزي که همه آن را مشاهده مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1577, N'و ما آن (مجازات) را، جز تا زمان محدودي، تأخير نمي‌اندازيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1578, N'آن روز که (قيامت و زمان مجازات) فرا رسد، هيچ کس جز به اجازه او سخن نمي‌گويد؛ گروهي بدبختند و گروهي خوشبخت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1579, N'اما آنها که بدبخت شدند، در آتشند؛ و براي آنان در آنجا، «زفير» و «شهيق» [= ناله‌هاي طولاني دم و بازدم‌] است...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1580, N'جاودانه در آن خواهند ماند، تا آسمانها و زمين برپاست؛ مگر آنچه پروردگارت بخواهد! پروردگارت هر چه را بخواهد انجام مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1581, N'اما آنها که خوشبخت و سعادتمند شدند، جاودانه در بهشت خواهند ماند، تا آسمانها و زمين برپاست، مگر آنچه پروردگارت بخواهد! بخششي است قطع نشدني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1582, N'پس شک و ترديدي (در باطل بودن) معبودهايي که آنها مي‌پرستند، به خود راه مده! آنها همان‌گونه اين معبودها را پرستش مي‌کنند که پدرانشان قبلا مي‌پرستيدند، و ما نصيب آنان را بي‌کم و کاست خواهيم داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1583, N'ما به موسي کتاب آسماني داديم؛ سپس در آن اختلاف شد؛ و اگر فرمان قبلي خدا (در زمينه آزمايش و اتمام حجت بر آنها) نبود، در ميان آنان داوري مي‌شد! و آنها (هنوز) در شک‌اند، شکي آميخته به بدگماني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1584, N'و پروردگارت اعمال هر يک را بي‌کم و کاست به آنها خواهد داد؛ او به آنچه عمل مي‌کنند آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1585, N'پس همان‌گونه که فرمان يافته‌اي، استقامت کن؛ و همچنين کساني که با تو بسوي خدا آمده‌اند (بايد استقامت کنند)! و طغيان نکنيد، که خداوند آنچه را انجام مي‌دهيد مي‌بيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1586, N'و بر ظالمان تکيه ننماييد، که موجب مي‌شود آتش شما را فرا گيرد؛ و در آن حال، هيچ ولي و سرپرستي جز خدا نخواهيد داشت؛ و ياري نمي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1587, N'در دو طرف روز، و اوايل شب، نماز را برپا دار؛ چرا که حسنات، سيئات (و آثار آنها را) از بين مي‌برند؛ اين تذکري است براي کساني که اهل تذکرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1588, N'و شکيبايي کن، که خداوند پاداش نيکوکاران را ضايع نخواهد کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1589, N'چرا در قرون (و اقوام) قبل از شما، دانشمندان صاحب قدرتي نبودند که از فساد در زمين جلوگيري کنند؟! مگر اندکي از آنها، که نجاتشان داديم! و آنان که ستم مي‌کردند، از تنعم و کامجوئي پيروي کردند؛ و گناهکار بودند (و نابود شدند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1590, N'و چنين نبود که پروردگارت آباديها را بظلم و ستم نابود کند در حالي که اهلش در صدد اصلاح بوده باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1591, N'و اگر پروردگارت مي‌خواست، همه مردم را يک امت (بدون هيچ گونه اختلاف) قرار مي‌داد؛ ولي آنها همواره مختلفند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1592, N'مگر کسي را که پروردگارت رحم کند! و براي همين (پذيرش رحمت) آنها را آفريد! و فرمان پروردگارت قطعي شده که: جهنم را از همه (سرکشان و طاغيان) جن و انس پر خواهم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1593, N'ما از هر يک از سرگذشتهاي انبيا براي تو بازگو کرديم، تا به وسيله آن، قلبت را آرامش بخشيم؛ و اراده‌ات قوي گردد. و در اين (اخبار و سرگذشتها،) براي تو حق، و براي مؤمنان موعظه و تذکر آمده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1594, N'و به آنها که ايمان نمي‌آورند، بگو: «هر چه در قدرت داريد، انجام دهيد! ما هم انجام مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1595, N'و انتظار بکشيد! ما هم منتظريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1596, N'و (آگاهي از) غيب (و اسرار نهان) آسمانها و زمين، تنها از آن خداست؛ و همه کارها به سوي او بازمي‌گردد! پس او را پرستش کن! و بر او توکل نما! و پروردگارت از کارهايي که مي‌کنيد، هرگز غافل نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1597, N'الر، آن آيات کتاب آشکار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1598, N'ما آن را قرآني عربي نازل کرديم، شايد شما درک کنيد (و بينديشيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1599, N'ما بهترين سرگذشتها را از طريق اين قرآن -که به تو وحي کرديم- بر تو بازگو مي‌کنيم؛ و مسلما پيش از اين، از آن خبر نداشتي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1600, N'(به خاطر بياور) هنگامي را که يوسف به پدرش گفت: «پدرم! من در خواب ديدم که يازده ستاره، و خورشيد و ماه در برابرم سجده مي‌کنند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1601, N'گفت: «فرزندم! خواب خود را براي برادرانت بازگو مکن، که براي تو نقشه (خطرناکي) مي‌کشند؛ چرا که شيطان، دشمن آشکار انسان است!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1602, N'و اين گونه پروردگارت تو را برمي‌گزيند؛ و از تعبير خوابها به تو مي‌آموزد؛ و نعمتش را بر تو و بر خاندان يعقوب تمام و کامل مي‌کند، همان‌گونه که پيش از اين، بر پدرانت ابراهيم و اسحاق تمام کرد؛ به يقين، پروردگار تو دانا و حکيم است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1603, N'در (داستان) يوسف و برادرانش، نشانه‌ها(ي هدايت) براي سؤال‌کنندگان بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1604, N'هنگامي که (برادران) گفتند: «يوسف و برادرش [= بنيامين‌] نزد پدر، از ما محبوبترند؛ در حالي که ما گروه نيرومندي هستيم! مسلما پدر ما، در گمراهي آشکاري است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1605, N'يوسف را بکشيد؛ يا او را به سرزمين دوردستي بيفکنيد؛ تا توجه پدر، فقط به شما باشد؛ و بعد از آن، (از گناه خود توبه مي‌کنيد؛ و) افراد صالحي خواهيد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1606, N'يکي از آنها گفت: «يوسف را نکشيد! و اگر مي‌خواهيد کاري انجام دهيد، او را در نهانگاه چاه بيفکنيد؛ تا بعضي از قافله‌ها او را برگيرند (و با خود به مکان دوري ببرند)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1607, N'(و براي انجام اين کار، برادران نزد پدر آمدند و) گفتند: «پدرجان! چرا تو درباره (برادرمان) يوسف، به ما اطمينان نمي‌کني؟! در حالي که ما خيرخواه او هستيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1608, N'فردا او را با ما (به خارج شهر) بفرست، تا غذاي کافي بخورد و تفريح کند؛ و ما نگهبان او هستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1609, N'(پدر) گفت: «من از بردن او غمگين مي‌شوم؛ و از اين مي‌ترسم که گرگ او را بخورد، و شما از او غافل باشيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1610, N'گفتند: «با اينکه ما گروه نيرومندي هستيم، اگر گرگ او را بخورد، ما از زيانکاران خواهيم بود (و هرگز چنين چيزي) ممکن نيست!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1611, N'هنگامي که او را با خود بردند، و تصميم گرفتند وي را در مخفي‌گاه چاه قرار دهند، (سرانجام مقصد خود را عملي ساختند؛) و به او وحي فرستاديم که آنها را در آينده از اين کارشان با خبر خواهي ساخت؛ در حالي که آنها نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1612, N'(برادران يوسف) شب هنگام، گريان به سراغ پدر آمدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1613, N'گفتند: «اي پدر! ما رفتيم و مشغول مسابقه شديم، و يوسف را نزد اثاث خود گذارديم؛ و گرگ او را خورد! تو هرگز سخن ما را باور نخواهي کرد، هر چند راستگو باشيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1614, N'و پيراهن او را با خوني دروغين (آغشته ساخته، نزد پدر) آوردند؛ گفت: «هوسهاي نفساني شما اين کار را برايتان آراسته! من صبر جميل (و شکيبائي خالي از ناسپاسي) خواهم داشت؛ و در برابر آنچه مي‌گوييد، از خداوند ياري مي‌طلبم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1615, N'و (در همين حال) کارواني فرا رسيد؛ و مأمور آب را (به سراغ آب) فرستادند؛ او دلو خود را در چاه افکند؛ (ناگهان) صدا زد: «مژده باد! اين کودکي است (زيبا و دوست داشتني!)» و اين امر را بعنوان يک سرمايه از ديگران مخفي داشتند. و خداوند به آنچه آنها انجام مي‌دادند، آگاه بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1616, N'و (سرانجام،) او را به بهاي کمي -چند درهم- فروختند؛ و نسبت به( فروختن) او، بي رغبت بودند (؛چرا که مي‌ترسيدند رازشان فاش شود).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1617, N'و آن کس که او را از سرزمين مصر خريد [= عزيز مصر]، به همسرش گفت: «مقام وي را گرامي دار، شايد براي ما سودمند باشد؛ و يا او را بعنوان فرزند انتخاب کنيم!» و اينچنين يوسف را در آن سرزمين متمکن ساختيم! (ما اين کار را کرديم، تا او را بزرگ داريم؛ و) از علم تعبير خواب به او بياموزيم؛ خداوند بر کار خود پيروز است، ولي بيشتر مردم نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1618, N'و هنگامي که به بلوغ و قوت رسيد، ما «حکم» [= نبوت‌] و «علم» به او داديم؛ و اينچنين نيکوکاران را پاداش مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1619, N'و آن زن که يوسف در خانه او بود، از او تمناي کامجويي کرد؛ درها را بست و گفت: «بيا (بسوي آنچه براي تو مهياست!)» (يوسف) گفت: «پناه مي‌برم به خدا! او [= عزيز مصر] صاحب نعمت من است؛ مقام مرا گرامي داشته؛ (آيا ممکن است به او ظلم و خيانت کنم؟!) مسلما ظالمان رستگار نمي‌شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1620, N'آن زن قصد او کرد؛ و او نيز -اگر برهان پروردگار را نمي‌ديد- قصد وي مي‌نمود! اينچنين کرديم تا بدي و فحشا را از او دور سازيم؛ چرا که او از بندگان مخلص ما بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1621, N'و هر دو به سوي در، دويدند (در حالي که همسر عزيز، يوسف را تعقيب مي‌کرد)؛ و پيراهن او را از پشت (کشيد و) پاره کرد. و در اين هنگام، آقاي آن زن را دم در يافتند! آن زن گفت: «کيفر کسي که بخواهد نسبت به اهل تو خيانت کند، جز زندان و يا عذاب دردناک، چه خواهد بود؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1622, N'(يوسف) گفت: «او مرا با اصرار به سوي خود دعوت کرد!» و در اين هنگام، شاهدي از خانواده آن زن شهادت داد که: «اگر پيراهن او از پيش رو پاره شده، آن آن راست مي‌گويد، و او از دروغگويان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1623, N'و اگر پيراهنش از پشت پاره شده، آن زن دروغ مي‌گويد، و او از راستگويان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1624, N'هنگامي که (عزيز مصر) ديد پيراهن او [= يوسف‌] از پشت پاره شده، گفت: «اين از مکر و حيله شما زنان است؛ که مکر و حيله شما زنان، عظيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1625, N'يوسف از اين موضوع، صرف‌نظر کن! و تو اي زن نيز از گناهت استغفار کن، که از خطاکاران بودي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1626, N'(اين جريان در شهر منعکس شد؛) گروهي از زنان شهر گفتند: «همسر عزيز، جوانش [= غلامش‌] را بسوي خود دعوت مي‌کند! عشق اين جوان، در اعماق قلبش نفوذ کرده، ما او را در گمراهي آشکاري مي‌بينيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1627, N'هنگامي که (همسر عزيز) از فکر آنها باخبر شد، به سراغشان فرستاد (و از آنها دعوت کرد)؛ و براي آنها پشتي (گرانبها، و مجلس باشکوهي) فراهم ساخت؛ و به دست هر کدام، چاقويي (براي بريدن ميوه) داد؛ و در اين موقع (به يوسف) گفت: «وارد مجلس آنان شو!» هنگامي که چشمشان به او افتاد، او را بسيار بزرگ (و زيبا) شمردند؛ و (بي‌توجه) دستهاي خود را بريدند؛ و گفتند: «منزه است خدا! اين بشر نيست؛ اين يک فرشته بزرگوار است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1628, N'(همسر عزيز) گفت: «اين همان کسي است که بخاطر (عشق) او مرا سرزنش کرديد! (آري،) من او را به خويشتن دعوت کردم؛ و او خودداري کرد! و اگر آنچه را دستور مي‌دهم انجام ندهد، به زندان خواهد افتاد؛ و مسلما خوار و ذليل خواهد شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1629, N'(يوسف) گفت: «پروردگارا! زندان نزد من محبوبتر است از آنچه اينها مرا بسوي آن مي‌خوانند! و اگر مکر و نيرنگ آنها را از من باز نگرداني، بسوي آنان متمايل خواهم شد و از جاهلان خواهم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1630, N'پروردگارش دعاي او را اجابت کرد؛ و مکر آنان را از او بگردانيد؛ چرا که او شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1631, N'و بعد از آنکه نشانه‌هاي (پاکي يوسف) را ديدند، تصميم گرفتند او را تا مدتي زنداني کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1632, N'و دو جوان، همراه او وارد زندان شدند؛ يکي از آن دو گفت: «من در خواب ديدم که (انگور براي) شراب مي‌فشارم!» و ديگري گفت: «من در خواب ديدم که نان بر سرم حمل مي‌کنم؛ و پرندگان از آن مي‌خورند؛ ما را از تعبير اين خواب آگاه کن که تو را از نيکوکاران مي‌بينيم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1633, N'(يوسف) گفت: «پيش از آنکه جيره غذايي شما فرا رسد، شما را از تعبير خوابتان آگاه خواهم ساخت. اين، از دانشي است که پروردگارم به من آموخته است. من آيين قومي را که به خدا ايمان ندارند، و به سراي ديگر کافرند، ترک گفتم (و شايسته چنين موهبتي شدم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1634, N'من از آيين پدرانم ابراهيم و اسحاق و يعقوب پيروي کردم! براي ما شايسته نبود چيزي را همتاي خدا قرار دهيم؛ اين از فضل خدا بر ما و بر مردم است؛ ولي بيشتر مردم شکرگزاري نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1635, N'اي دوستان زنداني من! آيا خدايان پراکنده بهترند، يا خداوند يکتاي پيروز؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1636, N'اين معبودهايي که غير از خدا مي‌پرستيد، چيزي جز اسمهائي (بي‌مسما) که شما و پدرانتان آنها را خدا ناميده‌ايد، نيست؛ خداوند هيچ دليلي بر آن نازل نکرده؛ حکم تنها از آن خداست؛ فرمان داده که غير از او را نپرستيد! اين است آيين پابرجا؛ ولي بيشتر مردم نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1637, N'اي دوستان زنداني من! اما يکي از شما (دو نفر، آزاد مي‌شود؛ و) ساقي شراب براي صاحب خود خواهد شد؛ و اما ديگري به دار آويخته مي‌شود؛ و پرندگان از سر او مي‌خورند! و مطلبي که درباره آن (از من) نظر خواستيد، قطعي و حتمي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1638, N'و به آن يکي از آن دو نفر، که مي‌دانست رهايي مي‌يابد، گفت: «مرا نزد صاحبت [= سلطان مصر] يادآوري کن!» ولي شيطان يادآوري او را نزد صاحبش از خاطر وي برد؛ و بدنبال آن، (يوسف) چند سال در زندان باقي ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1639, N'پادشاه گفت: «من در خواب ديدم هفت گاو چاق را که هفت گاو لاغر آنها را مي‌خورند؛ و هفت خوشه سبز و هفت خوشه خشکيده؛ (که خشکيده‌ها بر سبزها پيچيدند؛ و آنها را از بين بردند.) اي جمعيت اشراف! درباره خواب من نظر دهيد، اگر خواب را تعبير مي‌کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1640, N'گفتند: «خوابهاي پريشان و پراکنده‌اي است؛ و ما از تعبير اين گونه خوابها آگاه نيستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1641, N'و يکي از آن دو که نجات يافته بود -و بعد از مدتي به خاطرش آمد- گفت: «من تأويل آن را به شما خبر مي‌دهم؛ مرا (به سراغ آن جوان زنداني) بفرستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1642, N'(او به زندان آمد، و چنين گفت:) يوسف، اي مرد بسيار راستگو! درباره اين خواب اظهار نظر کن که هفت گاو چاق را هفت گاو لاغر مي‌خورند؛ و هفت خوشه تر، و هفت خوشه خشکيده؛ تا من بسوي مردم بازگردم، شايد (از تعبير اين خواب) آگاه شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1643, N'گفت: «هفت سال با جديت زراعت مي‌کنيد؛ و آنچه را درو کرديد، جز کمي که مي‌خوريد، در خوشه‌هاي خود باقي بگذاريد (و ذخيره نماييد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1644, N'پس از آن، هفت سال سخت (و خشکي و قحطي) مي‌آيد، که آنچه را براي آن سالها ذخيره کرده‌ايد، مي‌خورند؛ جز کمي که (براي بذر) ذخيره خواهيد کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1645, N'سپس سالي فرامي‌رسد که باران فراوان نصيب مردم مي‌شود؛ و در آن سال، مردم عصاره (ميوه‌ها و دانه‌هاي روغني را) مي‌گيرند (و سال پر برکتي است.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1646, N'پادشاه گفت: «او را نزد من آوريد!» ولي هنگامي که فرستاده او نزد وي [= يوسف‌] آمد گفت: «به سوي صاحبت بازگرد، و از او بپرس ماجراي زناني که دستهاي خود را بريدند چه بود؟ که خداي من به نيرنگ آنها آگاه است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1647, N'(پادشاه آن زنان را طلبيد و) گفت: «به هنگامي که يوسف را به سوي خويش دعوت کرديد، جريان کار شما چه بود؟» گفتند: «منزه است خدا، ما هيچ عيبي در او نيافتيم!» (در اين هنگام) همسر عزيز گفت: «الآن حق آشکار گشت! من بودم که او را به سوي خود دعوت کردم؛ و او از راستگويان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1648, N'اين سخن را بخاطر آن گفتم تا بداند من در غياب به او خيانت نکردم؛ و خداوند مکر خائنان را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1649, N'من هرگز خودم را تبرئه نمي‌کنم، که نفس (سرکش) بسيار به بديها امر مي‌کند؛ مگر آنچه را پروردگارم رحم کند! پروردگارم آمرزنده و مهربان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1650, N'پادشاه گفت: «او [= يوسف‌] را نزد من آوريد، تا وي را مخصوص خود گردانم!» هنگامي که (يوسف نزد وي آمد و) با او صحبت کرد، (پادشاه به عقل و درايت او پي برد؛ و) گفت: «تو امروز نزد ما جايگاهي والا داري، و مورد اعتماد هستي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1651, N'(يوسف) گفت: «مرا سرپرست خزائن سرزمين (مصر) قرار ده، که نگهدارنده و آگاهم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1652, N'و اين‌گونه ما به يوسف در سرزمين (مصر) قدرت داديم، که هر جا مي‌خواست در آن منزل مي‌گزيد (و تصرف مي‌کرد)! ما رحمت خود را به هر کس بخواهيم (و شايسته بدانيم) ميبخشيم؛ و پاداش نيکوکاران را ضايع نمي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1653, N'(اما) پاداش آخرت، براي کساني که ايمان آورده و پرهيزگاري داشتند، بهتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1654, N'(سرزمين کنعان را قحطي فرا گرفت؛) برادران يوسف (در پي مواد غذايي به مصر) آمدند؛ و بر او وارد شدند. او آنان را شناخت؛ ولي آنها او را نشناختند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1655, N'و هنگامي که (يوسف) بارهاي آنان را آماده ساخت، گفت: «(نوبت آينده) آن برادري را که از پدر داريد، نزد من آوريد! آيا نمي‌بينيد من حق پيمانه را ادا مي‌کنم، و من بهترين ميزبانان هستم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1656, N'و اگر او را نزد من نياوريد، نه کيل (و پيمانه‌اي از غله) نزد من خواهيد داشت؛ و نه (اصلا) به من نزديک شويد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1657, N'گفتند: «ما با پدرش گفتگو خواهيم کرد؛ (و سعي مي‌کنيم موافقتش را جلب نمائيم؛) و ما اين کار را خواهيم کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1658, N'(سپس) به کارگزاران خود گفت: «آنچه را بعنوان قيمت پرداخته‌اند، در بارهايشان بگذاريد! شايد پس از بازگشت به خانواده خويش، آن را بشناسند؛ و شايد برگردند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1659, N'هنگامي که به سوي پدرشان بازگشتند، گفتند: «اي پدر! دستور داده شده که (بدون حضور برادرمان بنيامين) پيمانه‌اي (از غله) به ما ندهند؛ پس برادرمان را با ما بفرست، تا سهمي (از غله) دريافت داريم؛ و ما او را محافظت خواهيم کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1660, N'گفت: «آيا نسبت به او به شما اطمينان کنم همان‌گونه که نسبت به برادرش (يوسف) اطمينان کردم (و ديديد چه شد)؟! و (در هر حال،) خداوند بهترين حافظ، و مهربانترين مهربانان است»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1661, N'و هنگامي که متاع خود را گشودند، ديدند سرمايه آنها به آنها بازگردانده شده! گفتند: «پدر! ما ديگر چه ميخواهيم؟! اين سرمايه ماست که به ما باز پس گردانده شده است! (پس چه بهتر که برادر را با ما بفرستي؛) و ما براي خانواده خويش مواد غذايي مي‌آوريم؛ و برادرمان را حفظ خواهيم کرد؛ و يک بار شتر زيادتر دريافت خواهيم داشت؛ اين پيمانه (بار) کوچکي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1662, N'گفت: «من هرگز او را با شما نخواهم فرستاد، تا پيمان مؤکد الهي بدهيد که او را حتما نزد من خواهيد آورد! مگر اينکه (بر اثر مرگ يا علت ديگر،) قدرت از شما سلب گردد. و هنگامي که آنها پيمان استوار خود را در اختيار او گذاردند، گفت: «خداوند، نسبت به آنچه مي‌گوييم، ناظر و نگهبان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1663, N'و (هنگامي که مي‌خواستند حرکت کنند، يعقوب) گفت: «فرزندان من! از يک در وارد نشويد؛ بلکه از درهاي متفرق وارد گرديد (تا توجه مردم به سوي شما جلب نشود)! و (من با اين دستور،) نمي‌توانم حادثه‌اي را که از سوي خدا حتمي است، از شما دفع کنم! حکم و فرمان، تنها از آن خداست! بر او توکل کرده‌ام؛ و همه متوکلان بايد بر او توکل کنند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1664, N'و هنگامي که از همان طريق که پدر به آنها دستور داده بود وارد شدند، اين کار هيچ حادثه حتمي الهي را نمي‌توانست از آنها دور سازد، جز حاجتي در دل يعقوب (که از اين طريق) انجام شد (و خاطرش آرام گرفت)؛ و او به خاطر تعليمي که ما به او داديم، علم فراواني داشت؛ ولي بيشتر مردم نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1665, N'هنگامي که (برادران) بر يوسف وارد شدند، برادرش را نزد خود جاي داد و گفت: «من برادر تو هستم، از آنچه آنها انجام مي‌دادند، غمگين و ناراحت نباش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1666, N'و هنگامي که (مأمور يوسف) بارهاي آنها را بست، ظرف آبخوري پادشاه را در بار برادرش گذاشت؛ سپس کسي صدا زد؛ «اي اهل قافله، شما دزد هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1667, N'آنها رو به سوي او کردند و گفتند: «چه چيز گم کرده‌ايد؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1668, N'گفتند: «پيمانه پادشاه را! و هر کس آن را بياورد، يک بار شتر (غله) به او داده مي‌شود؛ و من ضامن اين (پاداش) هستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1669, N'گفتند: «به خدا سوگند شما مي‌دانيد ما نيامده‌ايم که در اين سرزمين فساد کنيم؛ و ما (هرگز) دزد نبوده‌ايم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1670, N'آنها گفتند: «اگر دروغگو باشيد، کيفرش چيست؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1671, N'گفتند: «هر کس (آن پيمانه) در بار او پيدا شود، خودش کيفر آن خواهد بود؛(و بخاطر اين کار، برده شما خواهد شد؛) ما اين‌گونه ستمگران را کيفر مي‌دهيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1672, N'در اين هنگام، (يوسف) قبل از بار برادرش، به کاوش بارهاي آنها پرداخت؛ سپس آن را از بار برادرش بيرون آورد؛ اين گونه راه چاره را به يوسف ياد داديم! او هرگز نمي‌توانست برادرش را مطابق آيين پادشاه (مصر) بگيرد، مگر آنکه خدا بخواهد! درجات هر کس را بخواهيم بالا مي‌بريم؛ و برتر از هر صاحب علمي، عالمي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1673, N'(برادران) گفتند: «اگر او [بنيامين‌] دزدي کند، (جاي تعجب نيست؛) برادرش (يوسف) نيز قبل از او دزدي کرد» يوسف (سخت ناراحت شد، و) اين (ناراحتي) را در درون خود پنهان داشت، و براي آنها آشکار نکرد؛ (همين اندازه) گفت: «شما (از ديدگاه من،) از نظر منزلت بدترين مردميد! و خدا از آنچه توصيف مي‌کنيد، آگاهتر است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1674, N'گفتند: «اي عزيز! او پدر پيري دارد (که سخت ناراحت مي‌شود)؛ يکي از ما را به جاي او بگير؛ ما تو را از نيکوکاران مي‌بينيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1675, N'گفت: «پناه بر خدا که ما غير از آن کس که متاع خود را نزد او يافته‌ايم بگيريم؛ در آن صورت، از ظالمان خواهيم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1676, N'هنگامي که (برادران) از او مأيوس شدند، به کناري رفتند و با هم به نجوا پرداختند؛ (برادر) بزرگشان گفت: «آيا نمي‌دانيد پدرتان از شما پيمان الهي گرفته؛ و پيش از اين درباره يوسف کوتاهي کرديد؟! من از اين سرزمين حرکت نمي‌کنم، تا پدرم به من اجازه دهد؛ يا خدا درباره من داوري کند، که او بهترين حکم‌کنندگان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1677, N'شما به سوي پدرتان بازگرديد و بگوييد: پدر(جان)، پسرت دزدي کرد! و ما جز به آنچه مي‌دانستيم گواهي نداديم؛ و ما از غيب آگاه نبوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1678, N'(و اگر اطمينان نداري،) از آن شهر که در آن بوديم سؤال کن، و نيز از آن قافله که با آن آمديم (بپرس)! و ما (در گفتار خود) صادق هستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1679, N'(يعقوب) گفت: «(هواي) نفس شما، مسأله را چنين در نظرتان آراسته است! من صبر مي‌کنم، صبري زيبا (و خالي از کفران)! اميدوارم خداوند همه آنها را به من بازگرداند؛ چرا که او دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1680, N'و از آنها روي برگرداند و گفت: «وا اسفا بر يوسف!» و چشمان او از اندوه سفيد شد، اما خشم خود را فرو مي‌برد (و هرگز کفران نمي‌کرد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1681, N'گفتند: «به خدا تو آنقدر ياد يوسف مي‌کني تا در آستانه مرگ قرار گيري، يا هلاک گردي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1682, N'گفت: «من غم و اندوهم را تنها به خدا مي‌گويم (و شکايت نزد او مي‌برم)! و از خدا چيزهايي مي‌دانم که شما نمي‌دانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1683, N'پسرانم! برويد، و از يوسف و برادرش جستجو کنيد؛ و از رحمت خدا مأيوس نشويد؛ که تنها گروه کافران، از رحمت خدا مأيوس مي‌شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1684, N'هنگامي که آنها بر او [= يوسف‌] وارد شدند، گفتند: «اي عزيز! ما و خاندان ما را ناراحتي فرا گرفته، و متاع کمي (براي خريد مواد غذايي) با خود آورده‌ايم؛ پيمانه را براي ما کامل کن؛ و بر ما تصدق و بخشش نما، که خداوند بخشندگان را پاداش مي‌دهد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1685, N'گفت: «آيا دانستيد با يوسف و برادرش چه کرديد، آنگاه که جاهل بوديد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1686, N'گفتند: «آيا تو همان يوسفي؟!» گفت: «(آري،) من يوسفم، و اين برادر من است! خداوند بر ما منت گذارد؛ هر کس تقوا پيشه کند، و شکيبايي و استقامت نمايد، (سرانجام پيروز مي‌شود؛) چرا که خداوند پاداش نيکوکاران را ضايع نمي‌کند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1687, N'گفتند: «به خدا سوگند، خداوند تو را بر ما برتري بخشيده؛ و ما خطاکار بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1688, N'(يوسف) گفت: «امروز ملامت و توبيخي بر شما نيست! خداوند شما را مي‌بخشد؛ و او مهربانترين مهربانان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1689, N'اين پيراهن مرا ببريد، و بر صورت پدرم بيندازيد، بينا مي‌شود! و همه نزديکان خود را نزد من بياوريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1690, N'هنگامي که کاروان (از سرزمين مصر) جدا شد، پدرشان [= يعقوب‌] گفت: «من بوي يوسف را احساس مي‌کنم، اگر مرا به ناداني و کم عقلي نسبت ندهيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1691, N'گفتند: «به خدا تو در همان گمراهي سابقت هستي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1692, N'اما هنگامي که بشارت دهنده فرا رسيد، آن (پيراهن) را بر صورت او افکند؛ ناگهان بينا شد! گفت: «آيا به شما نگفتم من از خدا چيزهايي مي‌دانم که شما نمي‌دانيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1693, N'گفتند: «پدر! از خدا آمرزش گناهان ما را بخواه، که ما خطاکار بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1694, N'گفت: «بزودي براي شما از پروردگارم آمرزش مي‌طلبم، که او آمرزنده و مهربان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1695, N'و هنگامي که بر يوسف وارد شدند، او پدر و مادر خود را در آغوش گرفت، و گفت: «همگي داخل مصر شويد، که انشاء الله در امن و امان خواهيد بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1696, N'و پدر و مادر خود را بر تخت نشاند؛ و همگي بخاطر او به سجده افتادند؛ و گفت: «پدر! اين تعبير خوابي است که قبلا ديدم؛ پروردگارم آن را حق قرار داد! و او به من نيکي کرد هنگامي که مرا از زندان بيرون آورد، و شما را از آن بيابان (به اينجا) آورد بعد از آنکه شيطان، ميان من و برادرانم فساد کرد.پروردگارم نسبت به آنچه مي‌خواهد (و شايسته مي‌داند،) صاحب لطف است؛ چرا که او دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1697, N'پروردگارا! بخشي (عظيم) از حکومت به من بخشيدي، و مرا از علم تعبير خوابها آگاه ساختي! اي آفريننده آسمانها و زمين! تو ولي و سرپرست من در دنيا و آخرت هستي، مرا مسلمان بميران؛ و به صالحان ملحق فرما!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1698, N'اين از خبرهاي غيب است که به تو وحي مي‌فرستيم! تو (هرگز) نزد آنها نبودي هنگامي که تصميم مي‌گرفتند و نقشه مي‌کشيدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1699, N'و بيشتر مردم، هر چند اصرار داشته باشي، ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1700, N'و تو (هرگز) از آنها پاداشي نمي‌طلبي؛ آن نيست مگر تذکري براي جهانيان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1701, N'و چه بسيار نشانه‌اي (از خدا) در آسمانها و زمين که آنها از کنارش مي‌گذرند، و از آن رويگردانند!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1702, N'و بيشتر آنها که مدعي ايمان به خدا هستند، مشرکند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1703, N'آيا ايمن از آنند که عذاب فراگيري از سوي خدا به سراغ آنان بيايد، يا ساعت رستاخيز ناگهان فرارسد، در حالي که متوجه نيستند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1704, N'بگو: «اين راه من است من و پيروانم، و با بصيرت کامل، همه مردم را به سوي خدا دعوت مي‌کنيم! منزه است خدا! و من از مشرکان نيستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1705, N'و ما نفرستاديم پيش از تو، جز مرداني از اهل آباديها که به آنها وحي مي‌کرديم! آيا (مخالفان دعوت تو،) در زمين سير نکردند تا ببينند عاقبت کساني که پيش از آنها بودند چه شد؟! و سراي آخرت براي پرهيزکاران بهتر است! آيا فکر نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1706, N'(پيامبران به دعوت خود، و دشمنان آنها به مخالفت خود همچنان ادامه دادند) تا آنگاه که رسولان مأيوس شدند، و (مردم) گمان کردند که به آنان دروغ گفته شده است؛ در اين هنگام، ياري ما به سراغ آنها آمد؛ آنان را که خواستيم نجات يافتند؛ و مجازات و عذاب ما از قوم گنهکار بازگردانده نمي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1707, N'در سرگذشت آنها درس عبرتي براي صاحبان انديشه بود! اينها داستان دروغين نبود؛ بلکه (وحي آسماني است، و) هماهنگ است با آنچه پيش روي او (از کتب آسماني پيشين) قرار دارد؛ و شرح هر چيزي (که پايه سعادت انسان است)؛ و هدايت و رحمتي است براي گروهي که ايمان مي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1708, N'المر، اينها آيات کتاب (آسماني) است؛ و آنچه از طرف پروردگارت بر تو نازل شده، حق است؛ ولي بيشتر مردم ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1709, N'خدا همان کسي است که آسمانها را، بدون ستونهايي که براي شما ديدني باشد، برافراشت، سپس بر عرش استيلا يافت (و زمام تدبير جهان را در کف قدرت گرفت)؛ و خورشيد و ماه را مسخر ساخت، که هر کدام تا زمان معيني حرکت دارند! کارها را او تدبير مي‌کند؛ آيات را (براي شما) تشريح مي‌نمايد؛ شايد به لقاي پروردگارتان يقين پيدا کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1710, N'و او کسي است که زمين را گسترد؛ و در آن کوه‌ها و نهرهايي قرار داد؛ و در آن از تمام ميوه‌ها دو جفت آفريد؛ (پرده سياه) شب را بر روز مي‌پوشاند؛ در اينها آياتي است براي گروهي که تفکر مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1711, N'و در روي زمين، قطعاتي در کنار هم قرار دارد که با هم متفاوتند؛ و (نيز) باغهايي از انگور و زراعت و نخلها، (و درختان ميوه گوناگون) که گاه بر يک پايه مي‌رويند و گاه بر دو پايه؛ (و عجيب‌تر آنکه) همه آنها از يک آب سيراب مي‌شوند! و با اين حال، بعضي از آنها را از جهت ميوه بر ديگري برتري مي‌دهيم؛ در اينها نشانه‌هايي است براي گروهي که عقل خويش را به کار مي‌گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1712, N'و اگر (از چيزي) تعجب مي‌کني، عجيب گفتار آنهاست که مي‌گويند: «آيا هنگامي که ما خاک شديم، (بار ديگر زنده مي‌شويم و) به خلقت جديدي بازمي‌گرديم؟!» آنها کساني هستند که به پروردگارشان کافر شده‌اند؛ و آنان غل و زنجيرها در گردنشان است؛ و آنها اهل دوزخند، و جاودانه در آن خواهند ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1713, N'آنها پيش از حسنه (و رحمت)، از تو تقاضاي شتاب در سيئه (و عذاب) مي‌کنند؛ با اينکه پيش از آنها بلاهاي عبرت انگيز نازل شده است! و پروردگار تو نسبت به مردم -با اينکه ظلم مي‌کنند- داراي مغفرت است؛ و (در عين حال،) پروردگارت داراي عذاب شديد است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1714, N'کساني که کافر شدند مي‌گويند: «چرا آيه (و معجزه‌اي) از پروردگارش بر او نازل نشده؟!» تو فقط بيم دهنده‌اي! و براي هر گروهي هدايت کننده‌اي است (؛و اينها همه بهانه است، نه براي جستجوي حقيقت)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1715, N'خدا از جنين‌هايي که هر (انسان يا حيوان) ماده‌اي حمل مي‌کند آگاه است؛ و نيز از آنچه رحمها کم مي‌کنند (و پيش از موعد مقرر مي‌زايند)، و هم از آنچه افزون مي‌کنند (و بعد از موقع ميزايند)؛ و هر چيز نزد او مقدار معيني دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1716, N'او از غيب و شهود آگاه، و بزرگ و متعالي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1717, N'براي او يکسان است کساني از شما که پنهاني سخن بگويند، يا آن را آشکار سازند؛ و کساني که شبانگاه مخفيانه حرکت مي‌کنند، يا در روشنايي روز.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1718, N'براي انسان، مأموراني است که پي در پي، از پيش رو، و از پشت سرش او را از فرمان خدا [= حوادث غير حتمي‌] حفظ مي‌کنند؛ (اما) خداوند سرنوشت هيچ قوم (و ملتي) را تغيير نمي‌دهد مگر آنکه آنان آنچه را در خودشان است تغيير دهند! و هنگامي که خدا اراده سوئي به قومي (بخاطر اعمالشان) کند، هيچ چيز مانع آن نخواهد شد؛ و جز خدا، سرپرستي نخواهند داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1719, N'او کسي است که برق را به شما نشان مي‌دهد، که هم مايه ترس است و هم مايه اميد؛ و ابرهاي سنگين‌بار ايجاد مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1720, N'و رعد، تسبيح و حمد او مي‌گويد؛ و (نيز) فرشتگان از ترس او! و صاعقه‌ها را مي‌فرستد؛ و هر کس را بخواهد گرفتار آن مي‌سازد، (در حالي که آنها با مشاهده اين همه آيات الهي، باز هم) درباره خدا به مجادله مشغولند! و او قدرتي بي‌انتها (و مجازاتي دردناک) دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1721, N'دعوت حق از آن اوست! و کساني را که (مشرکان) غير از خدا مي‌خوانند، (هرگز) به دعوت آنها پاسخ نمي‌گويند! آنها همچون کسي هستند که کفهاي (دست) خود را به سوي آب مي‌گشايد تا آب به دهانش برسد، و هرگز نخواهد رسيد! و دعاي کافران، جز در ضلال (و گمراهي) نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1722, N'تمام کساني که در آسمانها و زمين هستند -از روي اطاعت يا اکراه- و همچنين سايه‌هايشان، هر صبح و عصر براي خدا سجده مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1723, N'بگو: «چه کسي پروردگار آسمانها و زمين است؟» بگو: «الله!» (سپس) بگو: «آيا اوليا (و خداياني) غير از او براي خود برگزيده‌ايد که (حتي) مالک سود و زيان خود نيستند (تا چه رسد به شما؟!)» بگو: «آيا نابينا و بينا يکسانند؟! يا ظلمتها و نور برابرند؟! آيا آنها همتاياني براي خدا قرار دادند بخاطر اينکه آنان همانند خدا آفرينشي داشتند، و اين آفرينشها بر آنها مشتبه شده است؟!» بگو: «خدا خالق همه چيز است؛ و اوست يکتا و پيروز!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1724, N'خداوند از آسمان آبي فرستاد؛ و از هر دره و رودخانه‌اي به اندازه آنها سيلابي جاري شد؛ سپس سيل بر روي خود کفي حمل کرد؛ و از آنچه (در کوره‌ها،) براي به دست آوردن زينت آلات يا وسايل زندگي، آتش روي آن روشن مي‌کنند نيز کفهايي مانند آن به وجود مي‌آيد -خداوند، حق و باطل را چنين مثل مي‌زند!- اما کفها به بيرون پرتاب مي‌شوند، ولي آنچه به مردم سود مي‌رساند [= آب يا فلز خالص ] در زمين مي‌ماند؛ خداوند اينچنين مثال مي‌زند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1725, N'براي آنها که دعوت پروردگارشان را اجابت کردند، (سرانجام و) نتيجه نيکوتر است؛ و کساني که دعوت او را اجابت نکردند، (آنچنان در وحشت عذاب الهي فرو مي‌روند، که) اگر تمام آنچه روي زمين است، و همانندش، از آن آنها باشد، همه را براي رهايي از عذاب مي‌دهند! (ولي از آنها پذيرفته نخواهد شد!)براي آنها حساب بدي است؛ و جايگاهشان جهنم، و چه بد جايگاهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1726, N'آيا کسي که مي‌داند آنچه از طرف پروردگارت بر تو نازل شده حق است، همانند کسي است که نابيناست؟! تنها صاحبان انديشه متذکر مي‌شوند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1727, N'آنها که به عهد الهي وفا مي‌کنند، و پيمان را نمي‌شکنند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1728, N'و آنها که پيوندهايي را که خدا دستور به برقراري آن داده، برقرار ميدارند؛ و از پروردگارشان مي‌ترسند؛ و از بدي حساب (روز قيامت) بيم دارند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1729, N'و آنها که بخاطر ذات (پاک) پروردگارشان شکيبايي مي‌کنند؛ و نماز را برپا مي‌دارند؛ و از آنچه به آنها روزي داده‌ايم، در پنهان و آشکار، انفاق مي‌کنند؛ و با حسنات، سيئات را از ميان مي‌برند؛ پايان نيک سراي ديگر، از آن آنهاست...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1730, N'(همان) باغهاي جاويدان بهشتي که وارد آن مي‌شوند؛ و همچنين پدران و همسران و فرزندان صالح آنها؛ و فرشتگان از هر دري بر آنان وارد مي‌گردند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1731, N'(و به آنان مي‌گويند:) سلام بر شما بخاطر صبر و استقامتتان! چه نيکوست سرانجام آن سرا(ي جاويدان)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1732, N'آنها که عهد الهي را پس از محکم کردن مي‌شکنند، و پيوندهايي را که خدا دستور به برقراري آن داده قطع مي‌کنند، و در روي زمين فساد مي‌نمايند، لعنت براي آنهاست؛ و بدي (و مجازات) سراي آخرت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1733, N'خدا روزي را براي هر کس بخواهد (و شايسته بداند) وسيع، براي هر کس بخواهد (و مصلحت بداند،) تنگ قرارمي‌دهد؛ ولي آنها [= کافران‌] به زندگي دنيا، شاد (و خوشحال) شدند؛ در حالي که زندگي دنيا در برابر آخرت، متاع ناچيزي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1734, N'کساني که کافر شدند مي‌گويند: «چرا آيه (و معجزه)اي از پروردگارش بر او نازل نشده است؟!» بگو: «خداوند هر کس را بخواهد گمراه، و هر کس را که بازگردد، به سوي خودش هدايت مي‌کند! (کمبودي در معجزه‌ها نيست؛ لجاجت آنها مانع است!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1735, N'آنها کساني هستند که ايمان آورده‌اند، و دلهايشان به ياد خدا مطمئن (و آرام) است؛ آگاه باشيد، تنها با ياد خدا دلها آرامش مي‌يابد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1736, N'آنها که ايمان آوردند و کارهاي شايسته انجام دادند، پاکيزه‌ترين (زندگي)نصيبشان است؛ و بهترين سرانجامها!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1737, N'همان گونه (که پيامبران پيشين را مبعوث کرديم،) تو را به ميان امتي فرستاديم که پيش از آنها امتهاي ديگري آمدند و رفتند، تا آنچه را به تو وحي نموده‌ايم بر آنان بخواني، در حالي که به رحمان [= خداوندي که رحمتش همگان را فراگرفته‌] کفر مي‌ورزند؛ بگو: «او پروردگار من است! معبودي جز او نيست! بر او توکل کردم؛ و بازگشتم بسوي اوست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1738, N'اگر بوسيل؛ قرآن، کوه‌ها به حرکت درآيند يا زمينها قطعه قطعه شوند، يا بوسيل؛ آن با مردگان سخن گفته شود، (باز هم ايمان نخواهند آورد!) ولي همه کارها در اختيار خداست! آيا آنها که ايمان آورده‌اند نمي‌دانند که اگر خدا بخواهد همه مردم را (به اجبار) هدايت مي‌کند (اما هدايت اجباري سودي ندارد)! و پيوسته بلاهاي کوبنده‌اي بر کافران بخاطر اعمالشان وارد مي‌شود، و يا بنزديکي خانه آنها فرود مي‌آيد، تا وعده (نهايي) خدا فرا رسد؛ به يقين خداوند در وعد؛ خود تخلف نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1739, N'(تنها تو را استهزا نکردند،) پيامبران پيش از تو را نيز مورد استهزا قرار دادند؛ من به کافران مهلت دادم؛ سپس آنها را گرفتم؛ ديدي مجازات من چگونه بود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1740, N'آيا کسي که بالاي سر همه ايستاده، (و حافظ و نگهبان و مراقب همه است،) و اعمال همه را مي‌بييند (،همچون کسي است که هيچ يک از اين صفات را ندارد)؟! آنان براي خدا همتاياني قرار دادند؛ بگو: «آنها را نام ببريد! آيا چيزي را به او خبر مي‌دهيد که از وجود آن در روي زمين بي‌خبر است، يا سخنان ظاهري (و تو خالي) مي‌گوييد؟!» (نه، شريکي براي خدا وجود ندارد؛) بلکه در نظر کافران، دروغهايشان زينت داده شده، (و بر اثر ناپاکي درون، چنين مي‌پندارند که واقعيتي دارد؛) و آنها از راه (خدا) بازداشته شده‌اند؛ و هر کس را خدا گمراه کند، راهنمايي براي او وجود نخواهد داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1741, N'در دنيا، براي آنها عذابي (دردناک) است؛ و عذاب آخرت سخت‌تر است؛ و در برابر (عذاب) خدا، هيچ کس نمي‌تواند آنها را نگه دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1742, N'توصيف بهشتي که به پرهيزگاران وعده داده شده، (اين است که) نهرهاي آب از زير درختانش جاري است، ميوه آن هميشگي، و سايه‌اش دائمي است؛ اين سرانجام کساني است که پرهيزگاري پيشه کردند؛ و سرانجام کافران، آتش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1743, N'کساني که کتاب آسماني به آنان داده‌ايم، از آنچه بر تو نازل شده، خوشحالند؛ و بعضي از احزاب (و گروه‌ها)، قسمتي از آن را انکار مي‌کنند؛ بگو: «من مأمورم که «الله» را بپرستم؛ و شريکي براي او قائل نشوم! به سوي او دعوت مي‌کنم؛ و بازگشت من بسوي اوست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1744, N'همان‌گونه (که به پيامبران پيشين کتاب آسماني داديم،) بر تو نيز اين (قرآن) را بعنوان فرمان روشن و صريحي نازل کرديم؛ و اگر از هوسهاي آنان -بعد از آنکه آگاهي براي تو آمده- پيروي کني، هيچ کس در برابر خدا، از تو حمايت و جلوگيري نخواهد کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1745, N'ما پيش از تو (نيز) رسولاني فرستاديم؛ و براي آنها همسران و فرزنداني قرار داديم؛ و هيچ رسولي نمي‌توانست ا(ز پيش خود) معجزه‌اي بياورد، مگر بفرمان خدا! هر زماني نوشته‌اي دارد (و براي هر کاري، موعدي مقرر است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1746, N'خداوند هر چه را بخواهد محو، و هر چه را بخواهد اثبات مي‌کند؛ و «ام الکتاب» [= لوح محفوظ] نزد اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1747, N'و اگر پاره‌اي از مجازاتها را که به آنها وعده مي‌دهيم به تو نشان دهيم، يا (پيش از فرا رسيدن اين مجازاتها) تو را بميرانيم، در هر حال تو فقط مأمور ابلاغ هستي؛ و حساب (آنها) برماست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1748, N'آيا نديدند که ما پيوسته به سراغ زمين مي‌آييم و از اطراف (و جوانب) آن کم مي‌کنيم؟! (و جامعه‌ها، تمدنها، و دانشمندان تدريجا از ميان مي‌روند.) و خداوند حکومت مي‌کند؛ و هيچ کس را ياراي جلوگيري يا رد احکام او نيست؛ و او سريع الحساب است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1749, N'پيش از آنان نيز کساني طرحها و نقشه‌ها کشيدند؛ ولي تمام طرحها و نقشه‌ها از آن خداست! او از کار هر کس آگاه است؛ و بزودي کفار مي‌دانند سرانجام (نيک و بد) در سراي ديگر از آن کيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1750, N'آنها که کافر شدند مي‌گويند: «تو پيامبر نيستي!» بگو: «کافي است که خداوند، و کسي که علم کتاب (و آگاهي بر قرآن) نزد اوست، ميان من و شما گواه باشند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1751, N'الر، (اين) کتابي است که بر تو نازل کرديم، تا مردم را از تاريکيها(ي شرک و ظلم و جهل،) به سوي روشنايي (ايمان و عدل و آگاهي،) بفرمان پروردگارشان در آوري، بسوي راه خداوند عزيز و حميد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1752, N'همان خدايي که آنچه در آسمانها و آنچه در زمين است، از آن اوست؛ واي بر کافران از مجازات شديد (الهي)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1753, N'همانها که زندگي دنيا را بر آخرت ترجيح مي‌دهند؛ و (مردم را) از راه خدا باز مي‌دارند؛ و مي‌خواهند راه حق را منحرف سازند؛ آنها در گمراهي دوري هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1754, N'ما هيچ پيامبري را، جز به زبان قومش، نفرستاديم؛ تا (حقايق را) براي آنها آشکار سازد؛ سپس خدا هر کس را بخواهد (و مستحق بداند) گمراه، و هر کس را بخواهد (و شايسته بداند) هدايت مي‌کند؛ و او توانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1755, N'ما موسي را با آيات خود فرستاديم؛ (و دستور داديم:) قومت را از ظلمات به نور بيرون آر! و «ايام الله» را به آنان ياد آور! در اين، نشانه‌هايي است براي هر صبر کننده شکرگزار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1756, N'و (به خاطر بياور) هنگامي را که موسي به قومش گفت: «نعمت خدا را بر خود به ياد داشته باشيد، زماني که شما را از (چنگال) آل فرعون رهايي بخشيد! همانها که شما را به بدترين وجهي عذاب مي‌کردند؛ پسرانتان را سر مي‌بريدند، و زنانتان را (براي خدمتگاري) زنده مي‌گذاشتند؛ و در اين، آزمايش بزرگي از طرف پروردگارتان براي شما بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1757, N'و (همچنين به خاطر بياوريد) هنگامي را که پروردگارتان اعلام داشت: «اگر شکرگزاري کنيد، (نعمت خود را) بر شما خواهم افزود؛ و اگر ناسپاسي کنيد، مجازاتم شديد است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1758, N'و موسي (به بني اسرائيل) گفت: «اگر شما و همه مردم روي زمين کافر شويد، (به خدا زياني نمي‌رسد؛ چرا که) خداوند، بي‌نياز و شايسته ستايش است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1759, N'آيا خبر کساني که پيش از شما بودند، به شما نرسيد؟! «قوم نوح» و «عاد» و «ثمود» و آنها که پس از ايشان بودند؛ همانها که جز خداوند از آنان آگاه نيست؛ پيامبرانشان دلايل روشن براي آنان آوردند، ولي آنها (از روي تعجب و استهزا) دست بر دهان گرفتند و گفتند: «ما به آنچه شما به آن فرستاده شده‌ايد، کافريم! و نسبت به آنچه ما را به سوي آن مي‌خوانيد، شک و ترديد داريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1760, N'رسولان آنها گفتند: «آيا در خدا شک است؟! خدايي که آسمانها و زمين را آفريده؛ او شما را دعوت مي‌کند تا گناهانتان را ببخشد، و تا موعد مقرري شما را باقي گذارد!» آنها گفتند: «(ما اينها را نمي‌فهميم! همين اندازه مي‌دانيم که) شما انسانهايي همانند ما هستيد، مي‌خواهيد ما را از آنچه پدرانمان مي‌پرستيدند بازداريد؛ شما دليل و معجزه روشني براي ما بياوريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1761, N'پيامبرانشان به آنها گفتند: «درست است که ما بشري همانند شما هستيم، ولي خداوند بر هر کس از بندگانش بخواهد (و شايسته بداند)، نعمت مي‌بخشد (و مقام رسالت عطا مي‌کند)! و ما هرگز نمي‌توانيم معجزه‌اي جز بفرمان خدا بياوريم! (و از تهديدهاي شما نمي‌هراسيم؛) افراد باايمان بايد تنها بر خدا توکل کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1762, N'و چرا بر خدا توکل نکنيم، با اينکه ما را به راه‌هاي (سعادت) رهبري کرده است؟! و ما بطور مسلم در برابر آزارهاي شما صبر خواهيم کرد (و دست از رسالت خويش بر نمي‌داريم)! و توکل‌کنندگان، بايد فقط بر خدا توکل کنند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1763, N'(ولي) کافران به پيامبران خود گفتند: «ما قطعا شما را از سرزمين خود بيرون خواهيم کرد، مگر اينکه به آيين ما بازگرديد!» در اين حال، پروردگارشان به آنها وحي فرستاد که: «ما ظالمان را هلاک مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1764, N'و شما را بعد از آنان در زمين سکوت خواهيم داد، اين (موفقيت)، براي کسي است که از مقام (عدالت) من بترسد؛ و از عذاب (من) بيمناک باشد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1765, N'و آنها (از خدا) تقاضاي فتح و پيروزي (بر کفار) کردند؛ و (سرانجام) هر گردنکش منحرفي نوميد و نابود شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1766, N'به دنبال او جهنم خواهد بود؛ و از آب بد بوي متعفني نوشانده مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1767, N'بزحمت جرعه جرعه آن را سرمي‌کشد؛ و هرگز حاضر نيست به ميل خود آن را بياشامد؛ و مرگ از هرجا به سراغ او مي‌آيد؛ ولي با اين همه نمي‌ميرد! و بدنبال آن، عذاب شديدي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1768, N'اعمال کساني که به پروردگارشان کافر شدند، همچون خاکستري است در برابر تندباد در يک روز طوفاني! آنها توانايي ندارند کمترين چيزي از آنچه را انجام داده‌اند، به دست آورند؛ و اين همان گمراهي دور و دراز است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1769, N'آيا نديدي خداوند، آسمانها و زمين را بحق آفريده است؟! اگر بخواهد، شما را مي‌برد و خلق تازه‌اي مي‌آورد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1770, N'و اين کار براي خدا مشکل نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1771, N'و (در قيامت)، همه آنها در برابر خدا ظاهر مي‌شوند؛ در اين هنگام، ضعفا [= دنباله‌روان نادان‌] به مستکبران (و رهبران گمراه) مي‌گويند: «ما پيروان شما بوديم! آيا (اکنون که بخاطر پيروي از شما گرفتار مجازات الهي شده‌ايم،) شما حاضريد سهمي از عذاب الهي را بپذيريد و از ما برداريد؟» آنها مي‌گويند: «اگر خدا ما را هدايت کرده بود، ما نيز شما را هدايت مي‌کرديم! (ولي کار از اينها گذشته است،) چه بيتابي کنيم و چه شکيبايي، تفاوتي براي ما ندارد؛ راه گريزي براي ما نيست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1772, N'و شيطان، هنگامي که کار تمام مي‌شود، مي‌گويد: «خداوند به شما وعده حق داد؛ و من به شما وعده (باطل) دادم، و تخلف کردم! من بر شما تسلطي نداشتم، جز اينکه دعوتتان کردم و شما دعوت مرا پذيرفتيد! بنابر اين، مرا سرزنش نکنيد؛ خود را سرزنش کنيد! نه من فريادرس شما هستم، و نه شما فريادرس من! من نسبت به شرک شما درباره خود، که از قبل داشتيد، (و اطاعت مرا همرديف اطاعت خدا قرار داديد) بيزار و کافرم!» مسلما ستمکاران عذاب دردناکي دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1773, N'و کساني را که ايمان آوردند و اعمال صالح انجام دادند، به باغهاي بهشت وارد مي‌کنند؛ باغهايي که نهرها از زير درختانش جاري است؛ به اذن پروردگارشان، جاودانه در آن مي‌مانند؛ و تحيت آنها در آن، «سلام» است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1774, N'آيا نديدي چگونه خداوند «کلمه طيبه» (و گفتار پاکيزه) را به درخت پاکيزه‌اي تشبيه کرده که ريشه آن (در زمين) ثابت، و شاخه آن در آسمان است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1775, N'هر زمان ميوه خود را به اذن پروردگارش مي‌دهد. و خداوند براي مردم مثلها مي‌زند، شايد متذکر شوند (و پند گيرند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1776, N'(همچنين) «کلمه خبيثه» (و سخن آلوده) را به درخت ناپاکي تشبيه کرده که از روي زمين برکنده شده، و قرار و ثباتي ندارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1777, N'خداوند کساني را که ايمان آوردند، به خاطر گفتار و اعتقاد ثابتشان، استوار مي‌دارد؛ هم در اين جهان، و هم در سراي ديگر! و ستمگران را گمراه مي‌سازد، (و لطف خود را از آنها برمي‌گيرد)؛ خداوند هر کار را بخواهد (و مصلحت بداند) انجام مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1778, N'آيا نديدي کساني را که نعمت خدا را به کفران تبديل کردند، و قوم خود را به سراي نيستي و نابودي کشاندند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1779, N'(سراي نيستي و نابودي، همان) جهنم است که آنها در آتش آن وارد مي‌شوند؛ و بد قرارگاهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1780, N'آنها براي خدا همتاياني قرار داده‌اند، تا (مردم را) از راه او (منحرف و) گمراه سازند؛ بگو: «(چند روزي از زندگي دنيا و لذات آن) بهره گيريد؛ اما عاقبت کار شما به سوي آتش (دوزخ) است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1781, N'به بندگان من که ايمان آورده‌اند بگو نماز را برپا دارند؛ و از آنچه به آنها روزي داده‌ايم، پنهان و آشکار، انفاق کنند؛ پيش از آنکه روزي فرا رسد که نه در آن خريد و فروش است، و نه دوستي! (نه با مال مي‌توانند از کيفر خدا رهايي يابند، و نه با پيوندهاي مادي!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1782, N'خداوند همان کسي است که آسمانها و زمين را آفريد؛ و از آسمان، آبي نازل کرد؛ و با آن، ميوه‌ها(ي مختلف) را براي روزي شما (از زمين) بيرون آورد؛ و کشتي‌ها را مسخر شما گردانيد، تا بر صفحه دريا به فرمان او حرکت کنند؛ و نهرها را (نيز) مسخر شما نمود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1783, N'و خورشيد و ماه را -که با برنامه منظمي درکارند- به تسخير شما درآورد؛ و شب و روز را (نيز) مسخر شما ساخت؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1784, N'و از هر چيزي که از او خواستيد، به شما داد؛ و اگر نعمتهاي خدا را بشماريد، هرگز آنها را شماره نتوانيد کرد! انسان، ستمگر و ناسپاس است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1785, N'(به ياد آوريد) زماني را که ابراهيم گفت: «پروردگارا! اين شهر [= مکه‌] را شهر امني قرار ده! و من و فرزندانم را از پرستش بتها دور نگاه دار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1786, N'پروردگارا! آنها [= بتها] بسياري از مردم را گمراه ساختند! هر کس از من پيروي کند از من است؛ و هر کس نافرماني من کند، تو بخشنده و مهرباني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1787, N'پروردگارا! من بعضي از فرزندانم را در سرزمين بي‌آب و علفي، در کنار خانه‌اي که حرم توست، ساکن ساختم تا نماز را برپا دارند؛ تو دلهاي گروهي از مردم را متوجه آنها ساز؛ و از ثمرات به آنها روزي ده؛ شايد آنان شکر تو را بجاي آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1788, N'پروردگارا! تو مي‌داني آنچه را ما پنهان و يا آشکار مي‌کنيم؛ و چيزي در زمين و آسمان بر خدا پنهان نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1789, N'حمد خداي را که در پيري، اسماعيل و اسحاق را به من بخشيد؛ مسلما پروردگار من، شنونده (و اجابت کننده) دعاست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1790, N'پروردگارا: مرا برپا کننده نماز قرار ده، و از فرزندانم (نيز چنين فرما)، پروردگارا: دعاي مرا بپذير!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1791, N'پروردگارا! من و پدر و مادرم و همه مؤمنان را، در آن روز که حساب برپا مي‌شود، بيامرز!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1792, N'گمان مبر که خدا، از آنچه ظالمان انجام مي‌دهند، غافل است! (نه، بلکه کيفر) آنها را براي روزي تأخير انداخته است که چشمها در آن (به خاطر ترس و وحشت) از حرکت بازمي‌ايستد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1793, N'گردنها را کشيده، سرها را به آسمان بلندکرده، حتي پلک چشمهايشان از حرکت بازمي‌ماند؛ زيرا به هر طرف نگاه کنند، آثار عذاب آشکار است!) و (در اين حال) دلهايشان (فرومي‌ريزد؛ و از انديشه و اميد،) خالي مي‌گردد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1794, N'و مردم را از روزي که عذاب الهي به سراغشان مي‌آيد، بترسان! آن روز که ظالمان مي‌گويند: «پروردگارا! مدت کوتاهي ما را مهلت ده، تا دعوت تو را بپذيريم و از پيامبران پيروي کنيم!» (اما پاسخ مي‌شنوند که:) مگر قبلا سوگند ياد نکرده بوديد که زوال و فنايي براي شما نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1795, N'(آري شما بوديد که) در منازل (و کاخهاي) کساني که به خويشتن ستم کردند، ساکن شديد؛ و براي شما آشکار شد چگونه با آنان رفتار کرديم؛ و براي شما، مثلها (از سرگذشت پيشينيان) زديم (باز هم بيدار نشديد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1796, N'آنها نهايت مکر (و نيرنگ) خود را به کار زدند؛ و همه مکرها (و توطئه‌هايشان) نزد خدا آشکار است، هر چند مکرشان چنان باشد که کوه‌ها را از جا برکند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1797, N'پس گمان مبر که خدا وعده‌اي را که به پيامبرانش داده، تخلف کند! چرا که خداوند قادر و انتقام گيرنده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1798, N'در آن روز که اين زمين به زمين ديگر، و آسمانها (به آسمانهاي ديگري) مبدل مي‌شود، و آنان در پيشگاه خداوند واحد قهار ظاهر مي‌گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1799, N'و در آن روز، مجرمان را با هم در غل و زنجير مي‌بيني! (که دستها و گردنهايشان را به هم بسته است!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1800, N'لباسهايشان از قطران [= ماده چسبنده بد بوي قابل اشتعال‌] است؛ و صورتهايشان را آتش مي‌پوشاند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1801, N'تا خداوند هر کس را، هر آنچه انجام داده، جزا دهد! به يقين، خداوند سريع الحساب است!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1802, N'اين (قرآن،) پيام (و ابلاغي) براي (عموم) مردم است؛ تا همه به وسيله آن انذار شوند، و بدانند او خدا يکتاست؛ و تا صاحبان مغز (و انديشه) پند گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1803, N'الر، اين آيات کتاب، و قرآن مبين است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1804, N'کافران (هنگامي که آثار شوم اعمال خود را ببينند،) چه بسا آرزو مي‌کنند که اي کاش مسلمان بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1805, N'بگذار آنها بخورند، و بهره گيرند، و آرزوها آنان را غافل سازد؛ ولي بزودي خواهند فهميد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1806, N'ما اهل هيچ شهر و دياري را هلاک نکرديم مگر اينکه اجل معين (و زمان تغيير ناپذيري) داشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1807, N'هيچ گروهي از اجل خود پيشي نمي‌گيرد؛ و از آن عقب نخواهد افتاد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1808, N'و گفتند: «اي کسي که «ذکر» [= قرآن‌] بر او نازل شده، مسلما تو ديوانه‌اي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1809, N'اگر راست مي‌گويي، چرا فرشتگان را نزد ما نمي‌زوري؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1810, N'(اما اينها بايد بدانند) ما فرشتگان را، جز بحق، نازل نمي‌کنيم،؛و هرگاه نازل شوند، ديگر به اينها مهلت داده نمي‌شود (؛و در صورت انکار، به عذاب الهي نابود مي‌گردند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1811, N'ما قرآن را نازل کرديم؛ و ما بطور قطع نگهدار آنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1812, N'ما پيش از تو (نيز) پيامبراني در ميان امتهاي نخستين فرستاديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1813, N'هيچ پيامبري به سراغ آنها نمي‌آمد مگر اينکه او را مسخره مي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1814, N'ما اينچنين (و از هر طريق ممکن) قرآن را به درون دلهاي مجرمان راه مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1815, N'(اما با اين حال،) آنها به آن ايمان نمي‌آورند؛ روش اقوام پيشين نيز چنين بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1816, N'و اگر دري از آسمان به روي آنان بگشاييم، و آنها پيوسته در آن بالا روند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1817, N'باز مي‌گويند: «ما را چشم‌بندي کرده‌اند؛ بلکه ما (سر تا پا) سحر شده‌ايم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1818, N'ما در آسمان برجهايي قرار داديم؛ و آن را براي بينندگان آراستيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1819, N'و آن را از هر شيطان رانده شده‌اي حفظ کرديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1820, N'مگر آن کس که استراق سمع کند (و دزدانه گوش فرا دهد) که «شهاب مبين» او را تعقيب مي‌کند (و مي‌راند)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1821, N'و زمين را گسترديم؛ و در آن کوه‌هاي ثابتي افکنديم؛ و از هر گياه موزون، در آن رويانديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1822, N'و براي شما انواع وسايل زندگي در آن قرار داديم؛ همچنين براي کساني که شما نمي‌توانيد به آنها روزي دهيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1823, N'و خزائن همه چيز، تنها نزد ماست؛ ولي ما جز به اندازه معين آن را نازل نمي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1824, N'ما بادها را براي بارور ساختن (ابرها و گياهان) فرستاديم؛ و از آسمان آبي نازل کرديم، و شما را با آن سيراب ساختيم؛ در حالي که شما توانايي حفظ و نگهداري آن را نداشتيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1825, N'ماييم که زنده مي‌کنيم و مي‌ميرانيم؛ و ماييم وارث (همه جهان)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1826, N'ما، هم پيشينيان شما را دانستيم؛ و هم متأخران را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1827, N'پروردگار تو، قطعا آنها را (در قيامت) جمع و محشور مي‌کند؛ چرا که او حکيم و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1828, N'ما انسان را از گل خشکيده‌اي (همچون سفال) که از گل بد بوي (تيره رنگي) گرفته شده بود آفريديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1829, N'و جن را پيش از آن، از آتش گرم و سوزان خلق کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1830, N'و (به خاطر بياور) هنگامي که پروردگارت به فرشتگان گفت: «من بشري را از گل خشکيده‌اي که از گل بدبويي گرفته شده، مي‌آفرينم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1831, N'هنگامي که کار آن را به پايان رساندم، و در او از روح خود (يک روح شايسته و بزرگ) دميدم، همگي براي او سجده کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1832, N'همه فرشتگان، بي استثنا، سجده کردند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1833, N'جز ابليس، که ابا کرد از اينکه با سجده‌کنندگان باشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1834, N'(خداوند) فرمود: «اي ابليس! چرا با سجده‌کنندگان نيستي؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1835, N'گفت: «من هرگز براي بشري که او را از گل خشکيده‌اي که از گل بدبويي گرفته شده است آفريده‌اي، سجده نخواهم کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1836, N'فرمود: «از صف آنها [= فرشتگان‌] بيرون رو، که رانده‌شده‌اي (از درگاه ما!).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1837, N'و لعنت (و دوري از رحمت حق) تا روز قيامت بر تو خواهد بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1838, N'گفت: «پروردگارا! مرا تا روز رستاخيز مهلت ده (و زنده بگذار!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1839, N'فرمود: «تو از مهلت يافتگاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1840, N'(اما نه تا روز رستاخيز، بلکه) تا روز وقت معيني.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1841, N'گفت: «پروردگارا! چون مرا گمراه ساختي، من (نعمتهاي مادي را) در زمين در نظر آنها زينت مي‌دهم، و همگي را گمراه خواهم ساخت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1842, N'مگر بندگان مخلصت را.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1843, N'فرمود: «اين راه مستقيمي است که بر عهده من است (و سنت هميشگيم)...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1844, N'که بر بندگانم تسلط نخواهي يافت؛ مگر گمراهاني که از تو پيروي مي‌کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1845, N'و دوزخ، ميعادگاه همه آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1846, N'هفت در دارد؛ و براي هر دري، گروه معيني از آنها تقسيم شده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1847, N'به يقين، پرهيزگاران در باغها(ي سرسبز بهشت) و در کنار چشمه‌ها هستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1848, N'(فرشتگان به آنها مي‌گويند:) داخل اين باغها شويد با سلامت و امنيت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1849, N'هر گونه غل [= حسد و کينه و دشمني‌] را از سينه آنها برمي‌کنيم (و روحشان را پاک مي‌سازيم)؛ در حالي که همه برابرند، و بر تختها روبه‌روي يکديگر قرار دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1850, N'هيچ خستگي و تعبي در آنجا به آنها نمي‌رسد، و هيچ گاه از آن اخراج نمي‌گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1851, N'بندگانم را آگاه کن که من بخشنده مهربانم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1852, N'و (اينکه) عذاب و کيفر من، همان عذاب دردناک است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1853, N'و به آنها از مهمانهاي ابراهيم خبر ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1854, N'هنگامي که بر او وارد شدند و سلام کردند؛ (ابراهيم) گفت: «ما از شما بيمناکيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1855, N'گفتند: «نترس، ما تو را به پسري دانا بشارت مي‌دهيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1856, N'گفت: «آيا به من (چنين) بشارت مي‌دهيد با اينکه پير شده‌ام؟! به چه چيز بشارت مي‌دهيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1857, N'گفتند: «تو را به حق بشارت داديم؛ از مأيوسان مباش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1858, N'گفت: «جز گمراهان، چه کسي از رحمت پروردگارش مأيوس مي‌شود؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1859, N'(سپس) گفت: «مأموريت شما چيست اي فرستادگان خدا؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1860, N'گفتند: «ما به سوي قومي گنهکار مأموريت يافته‌ايم (تا آنها را هلاک کنيم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1861, N'مگر خاندان لوط، که همگي آنها را نجات خواهيم داد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1862, N'بجز همسرش، که مقدر داشتيم از بازماندگان (در شهر، و هلاک‌شوندگان) باشد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1863, N'هنگامي که فرستادگان (خدا) به سراغ خاندان لوط آمدند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1864, N'(لوط) گفت: «شما گروه ناشناسي هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1865, N'گفتند: «ما همان چيزي را براي تو آورده‌ايم که آنها [= کافران‌] در آن ترديد داشتند (آري،) ما مأمور عذابيم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1866, N'ما واقعيت مسلمي را براي تو آورده‌ايم؛ و راست مي‌گوييم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1867, N'پس، خانواده‌ات را در اواخر شب با خود بردار، و از اينجا ببر؛ و خودت به دنبال آنها حرکت کن؛ و کسي از شما به پشت سر خويش ننگرد؛ مأمور هستيد برويد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1868, N'و ما به لوط اين موضوع را وحي فرستاديم که صبحگاهان، همه آنها ريشه‌کن خواهند شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1869, N'(از سوي ديگر،) اهل شهر (از ورود ميهمانان با خبر شدند، و بطرف خانه لوط) آمدند در حالي که شادمان بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1870, N'(لوط) گفت: «اينها ميهمانان منند؛ آبروي مرا نريزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1871, N'و از خدا بترسيد، و مرا شرمنده نسازيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1872, N'گفتند: «مگر ما تو را از جهانيان نهي نکرديم (و نگفتيم کسي را به ميهماني نپذير؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1873, N'گفت: «دختران من حاضرند، اگر مي‌خواهيد کار صحيحي انجام دهيد (با آنها ازدواج کنيد، و از گناه و آلودگي بپرهيزيد!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1874, N'به جان تو سوگند، اينها در مستي خود سرگردانند (و عقل و شعور خود را از دست داده‌اند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1875, N'سرانجام، هنگام طلوع آفتاب، صيحه (مرگبار -بصورت صاعقه يا زمين‌لرزه-) آنها را فراگرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1876, N'سپس (شهر و آبادي آنها را زير و رو کرديم؛) بالاي آن را پايين قرار داديم؛ و باراني از سنگ بر آنها فرو ريختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1877, N'در اين (سرگذشت عبرت‌انگيز)، نشانه‌هايي است براي هوشياران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1878, N'و ويرانه‌هاي سرزمين آنها، بر سر راه (کاروانها)، همواره ثابت و برقرار است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1879, N'در اين، نشانه‌اي است براي مؤمنان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1880, N'«اصحاب الأيکه» [= صاحبان سرزمينهاي پردرخت = قوم شعيب‌] مسلما قوم ستمگري بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1881, N'ما از آنها انتقام گرفتيم؛ و (شهرهاي ويران شده) اين دو [= قوم لوط و اصحاب الأيکه‌] بر سر راه (شما در سفرهاي شام)، آشکار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1882, N'و «اصحاب حجر» [= قوم ثمود] پيامبران را تکذيب کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1883, N'ما آيات خود را به آنان داديم؛ ولي آنها از آن روي گرداندند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1884, N'آنها خانه‌هاي امن در دل کوه‌ها مي‌تراشيدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1885, N'اما سرانجام صيحه (مرگبار)، صبحگاهان آنان را فرا گرفت؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1886, N'و آنچه را به دست آورده بودند، آنها را از عذاب الهي نجات نداد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1887, N'ما آسمانها و زمين و آنچه را ميان آن دو است، جز بحق نيافريديم؛ و ساعت موعود [= قيامت‌] قطعا فرا خواهد رسيد (و جزاي هر کس به او مي‌رسد)! پس، از آنها به طرز شايسته‌اي صرف‌نظر کن (و آنها را بر نادانيهايشان ملامت ننما)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1888, N'به يقين، پروردگار تو، آفريننده آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1889, N'ما به تو سوره حمد و قرآن عظيم داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1890, N'(بنابر اين،) هرگز چشم خود را به نعمتهاي (مادي)، که به گروه‌هايي از آنها [= کفار] داديم، ميفکن! و بخاطر آنچه آنها دارند، غمگين مباش! و بال (عطوفت) خود را براي مؤمنين فرود آر!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1891, N'و بگو: «من انذارکننده آشکارم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1892, N'(ما بر آنها عذابي مي‌فرستيم) همان گونه که بر تجزيه‌گران (آيات الهي) فرستاديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1893, N'همانها که قرآن را تقسيم کردند (؛آنچه را به سودشان بود پذيرفتند، و آنچه را بر خلاف هوسهايشان بود رها نمودند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1894, N'به پروردگارت سوگند، (در قيامت) از همه آنها سؤال خواهيم کرد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1895, N'از آنچه عمل مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1896, N'آنچه را مأموريت داري، آشکارا بيان کن! و از مشرکان روي گردان (و به آنها اعتنا نکن)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1897, N'ما شر استهزاکنندگان را از تو دفع خواهيم کرد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1898, N'همانها که معبود ديگري با خدا قرار دادند؛ اما بزودي مي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1899, N'ما مي‌دانيم سينه‌ات از آنچه آنها مي‌گويند تنگ مي‌شود (و تو را سخت ناراحت مي‌کنند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1900, N'(براي دفع ناراحتي آنان) پروردگارت را تسبيح و حمد گو! و از سجده‌کنندگان باش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1901, N'و پروردگارت را عبادت کن تا يقين [= مرگ‌] تو فرا رسد!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1902, N'فرمان خدا (براي مجازات مشرکان و مجرمان،) فرا رسيده است؛ براي آن عجله نکنيد! منزه و برتر است خداوند از آنچه همتاي او قرارمي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1903, N'فرشتگان را با روح (الهي) بفرمانش بر هر کس از بندگانش بخواهد نازل مي‌کند؛ (و به آنها دستور مي‌دهد) که مردم را انذار کنيد؛ (و بگوييد:) معبودي جز من نيست؛ از (مخالفت دستور) من، بپرهيزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1904, N'آسمانها و زمين را بحق آفريد؛ او برتر است از اينکه همتايي براي او قرار مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1905, N'انسان را از نطفه بي‌ارزشي آفريد؛ و سرانجام (او موجودي فصيح، و) مدافع آشکار از خويشتن گرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1906, N'و چهارپايان را آفريد؛ در حالي که در آنها، براي شما وسيله پوشش، و منافع ديگري است؛ و از گوشت آنها مي‌خوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1907, N'و در آنها براي شما زينت و شکوه است به هنگامي که آنها را به استراحتگاهشان بازمي‌گردانيد، و هنگامي که (صبحگاهان) به صحرا مي‌فرستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1908, N'آنها بارهاي سنگين شما را به شهري حمل مي‌کنند که جز با مشقت زياد، به آن نمي‌رسيديد؛ پروردگارتان رؤوف و رحيم است (که اين وسايل حيات را در اختيارتان قرار داده)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1909, N'همچنين اسبها و استرها و الاغها را آفريد؛ تا بر آنها سوار شويد و زينت شما باشد، و چيزهايي مي‌آفريند که نمي‌دانيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1910, N'و بر خداست که راه راست را (به بندگان) نشان دهد؛ اما بعضي از راه‌ها بيراهه است! و اگر خدا بخواهد، همه شما را (به اجبار) هدايت مي‌کند (؛ولي اجبار سودي ندارد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1911, N'او کسي است که از آسمان، آبي فرستاد، که نوشيدن شما از آن است؛ و (همچنين) گياهان و درختاني که حيوانات خود را در آن به چرا مي‌بريد، نيز از آن است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1912, N'خداوند با آن (آب باران)، براي شما زراعت و زيتون و نخل و انگور، و از همه ميوه‌ها مي‌روياند؛ مسلما در اين، نشانه روشني براي انديشمندان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1913, N'او شب و روز و خورشيد و ماه را مسخر شما ساخت؛ و ستارگان نيز به فرمان او مسخر شمايند؛ در اين، نشانه‌هايي است (از عظمت خدا،) براي گروهي که عقل خود را به کار مي‌گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1914, N'(علاوه بر اين،) مخلوقاتي را که در زمين به رنگهاي گوناگون آفريده نيز مسخر (فرمان شما) ساخت؛ در اين، نشانه روشني است براي گروهي که متذکر مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1915, N'او کسي است که دريا را مسخر (شما) ساخت تا از آن، گوشت تازه بخوريد؛ و زيوري براي پوشيدن (مانند مرواريد) از آن استخراج کنيد؛ و کشتيها را مي‌بيني که سينه دريا را مي‌شکافند تا شما (به تجارت پردازيد و) از فضل خدا بهره گيريد؛ شايد شکر نعمتهاي او را بجا آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1916, N'و در زمين، کوه‌هاي ثابت و محکمي افکند تا لرزش آن را نسبت به شما بگيرد؛ و نهرها و راه‌هايي ايجاد کرد، تا هدايت شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1917, N'و (نيز) علاماتي قرار داد؛ و (شب هنگام) به وسيله ستارگان هدايت مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1918, N'آيا کسي که (اين گونه مخلوقات را) مي‌آفريند، همچون کسي است که نمي‌آفريند؟! آيا متذکر نمي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1919, N'و اگر نعمتهاي خدا را بشماريد، هرگز نمي‌توانيد آنها را احصا کنيد؛ خداوند بخشنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1920, N'خداوند آنچه را پنهان مي‌داريد و آنچه را آشکار مي‌سازيد، مي‌داند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1921, N'معبودهايي را که غير از خدا مي‌خوانند، چيزي را خلق نمي‌کنند؛ بلکه خودشان هم مخلوقند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1922, N'آنها مردگاني هستند که هرگز استعداد حيات ندارند؛ و نمي‌دانند (عبادت‌کنندگانشان) در چه زماني محشور مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1923, N'معبود شما خداوند يگانه است؛ اما کساني که به آخرت ايمان نمي‌آورند، دلهايشان (حق را) انکار مي‌کند و مستکبرند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1924, N'قطعا خداوند از آنچه پنهان مي‌دارند و آنچه آشکار مي‌سازند با خبر است؛ او مستکبران را دوست نمي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1925, N'و هنگامي که به آنها گفته شود: «پروردگار شما چه نازل کرده است؟» مي‌گويند: «اينها (وحي الهي نيست؛) همان افسانه‌هاي دروغين پيشينيان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1926, N'آنها بايد روز قيامت، (هم) بار گناهان خود را بطور کامل بر دوش کشند؛ و هم سهمي از گناهان کساني که بخاطر جهل، گمراهشان مي‌سازند! بدانيد آنها بار سنگين بدي بر دوش مي‌کشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1927, N'کساني که قبل از ايشان بودند (نيز) از اين توطئه‌ها داشتند؛ ولي خداوند به سراغ شالوده (زندگي) آنها رفت؛ و آن را از اساس ويران کرد؛ و سقف از بالا بر سرشان فرو ريخت؛ و عذاب (الهي) از آن جايي که نمي‌دانستند به سراغشان آمد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1928, N'سپس روز قيامت خدا آنها را رسوا مي‌سازد؛ و مي‌گويد: «شريکاني که شما براي من ساختيد، و بخاطر آنها با ديگران دشمني مي‌کرديد، کجا هستيد؟!» (در اين هنگام،) کساني که به آنها علم داده شده مي‌گويند: «رسوايي و بدبختي، امروز بر کافران است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1929, N'همانها که فرشتگان (مرگ) روحشان را مي‌گيرند در حالي که به خود ظلم کرده بودند! در اين موقع آنها تسليم مي‌شوند (و بدروغ مي‌گويند:) ما کار بدي انجام نمي‌داديم! آري، خداوند به آنچه انجام مي‌داديد عالم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1930, N'(به آنها گفته مي‌شود:) اکنون از درهاي جهنم وارد شويد در حالي که جاودانه در آن خواهيد بود! چه جاي بدي است جايگاه مستکبران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1931, N'(ولي هنگامي که) به پرهيزگاران گفته مي‌شد: «پروردگار شما چه چيز نازل کرده است؟» مي‌گفتند: «خير (و سعادت)» (آري،) براي کساني که نيکي کردند، در اين دنيا نيکي است؛ و سراي آخرت از آن هم بهتر است؛ و چه خوب است سراي پرهيزگاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1932, N'باغهايي از بهشت جاويدان است که همگي وارد آن مي‌شوند؛ نهرها از زير درختانش مي‌گذرد؛ هر چه بخواهند در آنجا هست؛ خداوند پرهيزگاران را چنين پاداش مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1933, N'همانها که فرشتگان (مرگ) روحشان را مي‌گيرند در حالي که پاک و پاکيزه‌اند؛ به آنها مي‌گويند: «سلام بر شما! وارد بهشت شويد به خاطر اعمالي که انجام مي‌داديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1934, N'آيا آنها انتظاري جز اين دارند که فرشتگان (قبض ارواح) به سراغشان بيايند، يا فرمان پروردگارت (براي مجازاتشان) فرا رسد (آنگاه توبه کنند؟! ولي توبه آنها در آن زمان بي اثر است! آري،) کساني که پيش از ايشان بودند نيز چنين کردند! خداوند به آنها ستم نکرد؛ ولي آنان به خويشتن ستم مي‌نمودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1935, N'و سرانجام بديهاي اعمالشان به آنها رسيد؛ و آنچه را (از وعده‌هاي عذاب) استهزا مي‌کردند، بر آنان وارد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1936, N'مشرکان گفتند: «اگر خدا مي‌خواست، نه ما و نه پدران ما، غير او را پرستش نمي‌کرديم؛ و چيزي را بدون اجازه او حرام نمي‌ساختيم!» (آري،) کساني که پيش از ايشان بودند نيز همين کارها را انجام دادند؛ ولي آيا پيامبران وظيفه‌اي جز ابلاغ آشکار دارند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1937, N'ما در هر امتي رسولي برانگيختيم که: «خداي يکتا را بپرستيد؛ و از طاغوت اجتناب کنيد!» خداوند گروهي را هدايت کرد؛ و گروهي ضلالت و گمراهي دامانشان را گرفت؛ پس در روي زمين بگرديد و ببينيد عاقبت تکذيب‌کنندگان چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1938, N'هر قدر بر هدايت آنها حريص باشي، (سودي ندارد؛ چرا) که خداوند کسي را که گمراه ساخت، هدايت نمي‌کند؛ و آنها ياوراني نخواهند داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1939, N'آنها سوگندهاي شديد به خدا ياد کردند که: «هرگز خداوند کسي را که مي‌ميرد، برنمي‌انگيزد!» آري، اين وعده قطعي خداست (که همه مردگان را براي جزا بازمي‌گرداند)؛ ولي بيشتر مردم نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1940, N'هدف اين است که آنچه را در آن اختلاف داشتند، براي آنها روشن سازد؛ و کساني که منکر شدند، بدانند دروغ مي‌گفتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1941, N'(رستاخيز مردگان براي ما مشکل نيست؛ زيرا) وقتي چيزي را اراده مي‌کنيم، فقط به آن مي‌گوييم: «موجود باش!» بلافاصله موجود مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1942, N'آنها که پس از ستم ديدن در راه خدا، هجرت کردند، در اين دنيا جايگاه (و مقام) خوبي به آنها مي‌دهيم؛ و پاداش آخرت، از آن هم بزرگتر است اگر مي‌دانستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1943, N'آنها کساني هستند که صبر و استقامت پيشه کردند، و تنها بر پروردگارشان توکل مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1944, N'و پيش از تو، جز مرداني که به آنها وحي مي‌کرديم، نفرستاديم! اگر نمي‌دانيد، از آگاهان بپرسيد (تا تعجب نکنيد از اينکه پيامبر اسلام از ميان همين مردان برانگيخته شده است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1945, N'(از آنها بپرسيد که) از دلايل روشن و کتب (پيامبران پيشين آگاهند!) و ما اين ذکر [= قرآن‌] را بر تو نازل کرديم، تا آنچه به سوي مردم نازل شده است براي آنها روشن سازي؛ و شايد انديشه کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1946, N'آيا توطئه‌گران از اين ايمن گشتند که ممکن است خدا آنها را در زمين فروبرد، و يا مجازات (الهي)، از آن جا که انتظارش را ندارند، به سراغشان آيد؟!...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1947, N'يا به هنگامي (که براي کسب مال و ثروت افزونتر) در رفت و آمدند، دامانشان را بگيرد در حالي که قادر به فرار نيستند؟!...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1948, N'يا بطور تدريجي، با هشدارهاي خوف‌انگيز آنان را گرفتار سازد؟! چرا که پروردگار شما، رؤوف و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1949, N'آيا آنها مخلوقات خدا را نديدند که سايه‌هايشان از راست و چپ حرکت دارند، و با خضوع براي خدا سجده مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1950, N'(نه تنها سايه‌ها، بلکه) تمام آنچه در آسمانها و زمين از جنبندگان وجود دارد، و همچنين فرشتگان، براي خدا سجده مي‌کنند و تکبر نمي‌ورزند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1951, N'آنها (تنها) از (مخالفت) پروردگارشان، که حاکم بر آنهاست، مي‌ترسند؛ و آنچه را مأموريت دارند انجام مي‌دهند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1952, N'خداوند فرمان داده: «دو معبود (براي خود) انتخاب نکنيد؛ معبود (شما) همان خداي يگانه است؛ تنها از (کيفر) من بترسيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1953, N'آنچه در آسمانها و زمين است، از آن اوست؛ و دين خالص (نيز) همواره از آن او مي‌باشد؛ آيا از غير او مي‌ترسيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1954, N'آنچه از نعمتها داريد، همه از سوي خداست! و هنگامي که ناراحتي به شما رسد، فقط او را مي‌خوانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1955, N'(اما) هنگامي که ناراحتي و رنج را از شما برطرف مي‌سازد، ناگاه گروهي از شما براي پروردگارشان همتا قائل مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1956, N'(بگذار) تا نعمتهايي را که به آنها داده‌ايم کفران کنند! (اکنون) چند روزي (از متاع دنيا) بهره گيريد، اما بزودي خواهيد دانست (سرانجام کارتان به کجا خواهد کشيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1957, N'آنان براي بتهايي که هيچ گونه سود و زياني از آنها سراغ ندارند، سهمي از آنچه به آنان روزي داده‌ايم قرارمي‌دهند؛ به خدا سوگند، (در دادگاه قيامت،) از اين افتراها که مي‌بنديد، بازپرسي خواهيد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1958, N'آنها (در پندار خود،) براي خداوند دختران قرارمي‌دهند؛ -منزه است (از اينکه فرزندي داشته باشد)- ولي براي خودشان، آنچه را ميل دارند قائل مي‌شوند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1959, N'در حالي که هرگاه به يکي از آنها بشارت دهند دختر نصيب تو شده، صورتش (از فراب ناراحتي) سياه مي‌شود؛ و به شدت خشمگين مي‌گردد؛...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1960, N'بخاطر بشارت بدي که به او داده شده، از قوم و قبيله خود متواري مي‌گردد؛ (و نمي‌داند) آيا او را با قبول ننگ نگهدارد، يا در خاک پنهانش کند؟! چه بد حکم مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1961, N'براي آنها که به سراي آخرت ايمان ندارند، صفات زشت است؛ و براي خدا، صفات عالي است؛ و او قدرتمند و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1962, N'و اگر خداوند مردم را بخاطر ظلمشان مجازات مي‌کرد، جنبنده‌اي را بر پشت زمين باقي نمي‌گذارد؛ ولي آنها را تا زمان معيني به تأخير مي‌اندازد. و هنگامي که اجلشان فرا رسد، نه ساعتي تأخير مي‌کنند، و نه ساعتي پيشي مي‌گيرند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1963, N'آنها براي خدا چيزهايي قرارمي‌دهند که خودشان از آن کراهت دارند [= فرزندان دختر]؛ با اين حال زبانشان به دروغ مي‌گويد سرانجام نيکي دارند! از اين رو براي آنان آتش است؛ و آنها از پيشگامان (دوزخ)اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1964, N'به خدا سوگند، به سوي امتهاي پيش از تو پيامبراني فرستاديم؛ اما شيطان اعمالشان را در نظرشان آراست؛ و امروز او ولي و سرپرستشان است؛ و مجازات دردناکي براي آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1965, N'ما قرآن را بر تو نازل نکرديم مگر براي اينکه آنچه را در آن اختلاف دارند، براي آنها روشن کني؛ و (اين قرآن) مايه هدايت و رحمت است براي قومي که ايمان مي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1966, N'خداوند از آسمان، آبي فرستاد؛ و زمين را، پس از آنکه مرده بود، حيات بخشيد! در اين، نشانه روشني است براي جمعيتي که گوش شنوا دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1967, N'و در وجود چهارپايان، براي شما (درسهاي) عبرتي است: از درون شکم آنها، از ميان غذاهاي هضم‌شده و خون، شير خالص و گوارا به شما مي‌نوشانيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1968, N'و از ميوه‌هاي درختان نخل و انگور، مسکرات (ناپاک) و روزي خوب و پاکيزه مي‌گيريد؛ در اين، نشانه روشني است براي جمعيتي که انديشه مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1969, N'و پروردگار تو به زنبور عسل «وحي» (و الهام غريزي) نمود که: «از کوه‌ها و درختان و داربستهايي که مردم مي‌سازند، خانه‌هايي برگزين!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1970, N'سپس از تمام ثمرات (و شيره گلها) بخور و راه‌هايي را که پروردگارت براي تو تعيين کرده است، براحتي بپيما! «از درون شکم آنها، نوشيدني با رنگهاي مختلف خارج مي‌شود که در آن، شفا براي مردم است؛ به يقين در اين امر، نشانه روشني است براي جمعيتي که مي‌انديشند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1971, N'خداوند شما را آفريد؛ سپس شما را مي‌ميراند؛ بعضي از شما به نامطلوب‌ترين سنين بالاي عمر مي‌رسند، تا بعد از علم و آگاهي، چيزي ندانند (و همه چيز را فراموش کنند)؛ خداوند دانا و تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1972, N'خداوند بعضي از شما را بر بعضي ديگر از نظر روزي برتري داد (چرا که استعدادها و تلاشهايتان متفاوت است)! اما آنها که برتري داده شده‌اند، حاضر نيستند از روزي خود به بردگانشان بدهند و همگي در آن مساوي گردند؛ آيا آنان نعمت خدا را انکار مي‌نمايند (که شکر او را ادا نمي‌کنند)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1973, N'خداوند براي شما از جنس خودتان همسراني قرار داد؛ و از همسرانتان براي شما فرزندان و نوه‌هايي به وجود آورد؛ و از پاکيزه‌ها به شما روزي داد؛ آيا به باطل ايمان مي‌آورند، و نعمت خدا را انکار مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1974, N'آنها غير از خدا، موجوداتي را مي‌پرستند که هيچ رزقي را براي آنان از آسمانها و زمين در اختيار ندارند؛ و توان اين کار را نيز ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1975, N'پس، براي خدا امثال (و شبيه‌ها) قائل نشويد! خدا مي‌داند، و شما نمي‌دانيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1976, N'خداوند مثالي زده: برده مملوکي را که قادر بر هيچ چيز نيست؛ و انسان (با ايماني) را که از جانب خود، رزقي نيکو به او بخشيده‌ايم، و او پنهان و آشکار از آنچه خدا به او داده، انفاق مي‌کند؛ آيا اين دو نفر يکسانند؟! شکر مخصوص خداست، ولي اکثر آنها نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1977, N'خداوند مثالي (ديگر) زده است: دو نفر را، که يکي از آن دو، گنگ مادرزاد است؛ و قادر بر هيچ کاري نيست؛ و سربار صاحبش مي‌باشد؛ او را در پي هر کاري بفرستد، خوب انجام نمي‌دهد؛ آيا چنين انساني، با کسي که امر به عدل و داد مي‌کند، و بر راهي راست قرار دارد، برابر است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1978, N'غيب آسمانها و زمين، مخصوص خداست (و او همه را مي‌داند)؛ و امر قيامت (بقدري نزديک و آسان است) درست همانند چشم برهم زدن، و يا از آن هم نزديکتر؛ چرا که خدا بر هر چيزي تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1979, N'و خداوند شما را از شکم مادرانتان خارج نمود در حالي که هيچ چيز نمي‌دانستيد؛ و براي شما، گوش و چشم و عقل قرار داد، تا شکر نعمت او را بجا آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1980, N'آيا آنها به پرندگاني که بر فراز آسمانها نگه‌داشته شده، نظر نيفکندند؟ هيچ کس جز خدا آنها را نگاه نمي‌دارد؛ در اين امر، نشانه‌هايي (از عظمت و قدرت خدا) است براي کساني که ايمان مي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1981, N'و خدا براي شما از خانه‌هايتان محل سکونت (و آرامش) قرار داد؛ و از پوست چهارپايان نيز براي شما خانه‌هايي قرار داد که روز کوچ کردن و روز اقامتتان، به آساني مي‌توانيد آنها را جا به جا کنيد؛ و از پشم و کرک و موي آنها، براي شما اثاث و متاع (و وسايل مختلف زندگي) تا زمان معيني قرار داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1982, N'و (نيز) خداوند از آنچه آفريده است سايه‌هايي براي شما قرار داده؛ و از کوه‌ها پناهگاه‌هايي؛ و براي شما پيراهنهايي آفريده، که شما را از گرما (و سرما) حفظ مي‌کند؛ و پيراهنهايي که به هنگام جنگ، حافظ شماست؛ اين گونه نعمتهايش را بر شما کامل مي‌کند، شايد تسليم فرمان او شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1983, N'(با اين همه،) اگر روي برتابند، (نگران مباش؛) تو فقط وظيفه ابلاغ آشکار داري.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1984, N'آنها نعمت خدا را مي‌شناسند؛ سپس آن را انکار مي‌کنند؛ و اکثرشان کافرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1985, N'(به خاطر بياوريد) روزي را که از هر امتي گواهي بر آنان برمي‌انگيزيم؛ سپس به آنان که کفر ورزيدند، اجازه (سخن گفتن) داده نمي‌شود؛ (بلکه دست و پا و گوش و چشم، حتي پوست تن آنها گواهي مي‌دهند!) و (نيز) اجازه عذرخواهي و تقاضاي عفو به آنان نمي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1986, N'و هنگامي که ظالمان عذاب را ببينند، نه به آنها تخفيف داده مي‌شود، و نه مهلت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1987, N'و هنگامي که مشرکان معبودهايي را که همتاي خدا قرار دادند مي‌بينند، مي‌گويند: «پروردگارا! اينها همتاياني هستند که ما به جاي تو، آنها را مي‌خوانديم! «در اين هنگام، معبودان به آنها مي‌گويند: «شما دروغگو هستيد! (شما هواي نفس خود را پرستش مي‌کرديد!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1988, N'و در آن روز، همگي (ناگزير) در پيشگاه خدا تسليم مي‌شوند؛ و تمام آنچه را (نسبت به خدا) دروغ مي‌بستند، گم و نابود مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1989, N'کساني که کافر شدند و (مردم را) از راه خدا بازداشتند، بخاطر فسادي که مي‌کردند، عذابي بر عذابشان مي‌افزاييم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1990, N'(به ياد آوريد) روزي را که از هر امتي، گواهي از خودشان بر آنها برمي‌انگيزيم؛ و تو را گواه بر آنان قرارمي‌دهيم! و ما اين کتاب را بر تو نازل کرديم که بيانگر همه چيز، و مايه هدايت و رحمت و بشارت براي مسلمانان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1991, N'خداوند به عدل و احسان و بخشش به نزديکان فرمان مي‌دهد؛ و از فحشا و منکر و ستم، نهي مي‌کند؛ خداوند به شما اندرز مي‌دهد، شايد متذکر شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1992, N'و هنگامي که با خدا عهد بستيد، به عهد او وفا کنيد! و سوگندها را بعد از محکم ساختن نشکنيد، در حالي که خدا را کفيل و ضامن بر (سوگند) خود قرار داده‌ايد، به يقين خداوند از آنچه انجام مي‌دهيد، آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1993, N'همانند آن زن (سبک مغز) نباشيد که پشمهاي تابيده خود را، پس از استحکام،وامي‌تابيد! در حالي که (سوگند و پيمان) خود را وسيله خيانت و فساد قرار مي‌دهيد؛ بخاطر اينکه گروهي، جمعيتشان از گروه ديگر بيشتر است (و کثرت دشمن را بهانه‌اي براي شکستن بيعت با پيامبر مي‌شمريد)! خدا فقط شما را با اين وسيله آزمايش مي‌کند؛ و به يقين روز قيامت، آنچه را در آن اختلاف داشتيد، براي شما روشن مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1994, N'و اگر خدا مي‌خواست، همه شما را امت واحدي قرارمي‌داد؛ (و همه را به اجبار وادار به ايمان مي‌کرد؛ اما ايمان اجباري فايده‌اي ندارد!) ولي خدا هر کس را بخواهد (و شايسته بداند) گمراه، و هر کس را بخواهد (و لايق بداند) هدايت مي‌کند! (به گروهي توفيق هدايت داده، و از گروهي سلب مي‌کند!) و يقينا شما از آنچه انجام مي‌داديد، بازپرسي خواهيد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1995, N'سوگندهايتان را وسيله تقلب و خيانت در ميان خود قرار ندهيد، مبادا گامي بعد از ثابت‌گشتن (بر ايمان) متزلزل شود؛ و به خاطر بازداشتن (مردم) از راه خدا، آثار سوء آن را بچشيد! و براي شما، عذاب عظيمي خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1996, N'و (هرگز) پيمان الهي را با بهاي کمي مبادله نکنيد (و هر بهايي در برابر آن ناچيز است!) آنچه نزد خداست، براي شما بهتر است اگر مي‌دانستيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1997, N'آنچه نزد شماست فاني مي‌شود؛ اما آنچه نزد خداست باقي است؛ و به کساني که صبر و استقامت پيشه کنند، مطابق بهترين اعمالي که انجام مي‌دادند پاداش خواهيم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1998, N'هر کس کار شايسته‌اي انجام دهد، خواه مرد باشد يا زن، در حالي که مؤمن است، او را به حياتي پاک زنده مي‌داريم؛ و پاداش آنها را به بهترين اعمالي که انجام مي‌دادند، خواهيم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (1999, N'هنگامي که قرآن مي‌خواني، از شر شيطان مطرود، به خدا پناه بر!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2000, N'چرا که او، بر کساني که ايمان دارند و بر پروردگارشان توکل مي‌کنند، تسلطي ندارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2001, N'تسلط او تنها بر کساني است که او را به سرپرستي خود برگزيده‌اند، و آنها که نسبت به او [= خدا] شرک مي‌ورزند (و فرمان شيطان را به جاي فرمان خدا، گردن مي‌نهند)')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2002, N'و هنگامي که آيه‌اي را به آيه ديگر مبدل کنيم [= حکمي را نسخ نماييم‌] -و خدا بهتر مي‌داند چه حکمي را نازل کند- آنها مي‌گويند: «تو افترا مي‌بندي!» اما بيشترشان (حقيقت را) نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2003, N'بگو، روح القدس آن را از جانب پروردگارت بحق نازل کرده، تا افراد باايمان را ثابت‌قدم گرداند؛ و هدايت و بشارتي است براي عموم مسلمانان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2004, N'ما مي‌دانيم که آنها مي‌گويند: «اين آيات را انساني به او تعليم مي‌دهد!» در حالي که زبان کسي که اينها را به او نسبت مي‌دهند عجمي است؛ ولي اين (قرآن)، زبان عربي آشکار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2005, N'به يقين، کساني که به آيات الهي ايمان نمي‌آورند، خدا آنها را هدايت نمي‌کند؛ و براي آنان عذاب دردناکي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2006, N'تنها کساني دروغ مي‌بندند که به آيات خدا ايمان ندارند؛ (آري،) دروغگويان واقعي آنها هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2007, N'کساني که بعد از ايمان کافر شوند -بجز آنها که تحت فشار واقع شده‌اند در حالي که قلبشان آرام و با ايمان است- آري، آنها که سينه خود را براي پذيرش کفر گشوده‌اند، غضب خدا بر آنهاست؛ و عذاب عظيمي در انتظارشان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2008, N'اين به خاطر آن است که زندگي دنيا (و پست را) بر آخرت ترجيح دادند؛ و خداوند افراد بي‌ايمان (لجوج) را هدايت نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2009, N'آنها کساني هستند که (بر اثر فزوني گناه،) خدا بر قلب و گوش و چشمانشان مهر نهاده؛ (به همين دليل نمي‌فهمند،) و غافلان واقعي همانها هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2010, N'و ناچار آنها در آخرت زيانکارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2011, N'اما پروردگار تو نسبت به کساني که بعد از فريب‌خوردن، (به ايمان بازگشتند و) هجرت کردند؛ سپس جهاد کردند و در راه خدا استقامت نمودند؛ پروردگارت، بعد از انجام اين کارها، بخشنده و مهربان است (و آنها را مشمول رحمت خود مي‌سازد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2012, N'(به ياد آوريد) روزي را که هر کس (در فکر خويشتن است؛ و تنها) به دفاع از خود برمي‌خيزد؛ و نتيجه اعمال هر کسي، بي‌کم و کاست، به او داده مي‌شود؛ و به آنها ظلم نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2013, N'خداوند (براي آنان که کفران نعمت مي‌کنند،) مثلي زده است: منطقه آبادي که امن و آرام و مطمئن بود؛ و همواره روزيش از هر جا مي‌رسيد؛ اما به نعمتهاي خدا ناسپاسي کردند؛ و خداوند به خاطر اعمالي که انجام مي‌دادند، لباس گرسنگي و ترس را بر اندامشان پوشانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2014, N'پيامبري از خودشان به سراغ آنها آمد، او را تکذيب کردند؛ از اين رو عذاب الهي آنها را فراگرفت در حالي که ظالم بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2015, N'پس، از آنچه خدا روزيتان کرده است، حلال و پاکيزه بخوريد؛ و شکر نعمت خدا را بجا آوريد اگر او را مي‌پرستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2016, N'خداوند، تنها مردار، خون، گوشت خوک و آنچه را با نام غير خدا سر بريده‌اند، بر شما حرام کرده است؛ اما کساني که ناچار شوند، در حالي که تجاوز و تعدي از حد ننمايند، (خدا آنها را مي‌بخشد؛ چرا که) خدا بخشنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2017, N'به خاطر دروغي که بر زبانتان جاري مي‌شود (و چيزي را مجاز و چيزي را ممنوع مي‌کنيد،) نگوييد: «اين حلال است و آن حرام»، تا بر خدا افترا ببنديد به يقين کساني که به خدا دروغ مي‌بندند، رستگار نخواهند شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2018, N'بهره کمي است (که در اين دنيا نصيبشان مي‌شود)؛ و عذاب دردناکي در انتظار آنان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2019, N'چيزهايي را که پيش از اين براي تو شرح داديم، بر يهود حرام کرديم؛ ما به آنها ستم نکرديم، اما آنها به خودشان ظلم و ستم مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2020, N'اما پروردگارت نسبت به آنها که از روي جهالت، بدي کرده‌اند، سپس توبه کرده و در مقام جبران برآمده‌اند، پروردگارت بعد از آن آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2021, N'ابراهيم (به تنهايي) امتي بود مطيع فرمان خدا؛ خالي از هر گونه انحراف؛ و از مشرکان نبود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2022, N'شکرگزار نعمتهاي پروردگار بود؛ خدا او را برگزيد؛ و به راهي راست هدايت نمود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2023, N'ما در دنيا به او (همت) نيکويي داديم؛ و در آخرت از نيکان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2024, N'سپس به تو وحي فرستاديم که از آيين ابراهيم -که ايماني خالص داشت و از مشرکان نبود- پيروي کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2025, N'(تحريمهاي) روز شنبه (براي يهود) فقط بعنوان يک مجازات بود، که در آن هم اختلاف کردند؛ و پروردگارت روز قيامت، در آنچه اختلاف داشتند، ميان آنها داوري مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2026, N'با حکمت و اندرز نيکو، به راه پروردگارت دعوت نما! و با آنها به روشي که نيکوتر است، استدلال و مناظره کن! پروردگارت، از هر کسي بهتر مي‌داند چه کسي از راه او گمراه شده است؛ و او به هدايت‌يافتگان داناتر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2027, N'و هر گاه خواستيد مجازات کنيد، تنها بمقداري که به شما تعدي شده کيفر دهيد! و اگر شکيبايي کنيد، اين کار براي شکيبايان بهتر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2028, N'صبر کن، و صبر تو فقط براي خدا و به توفيق خدا باشد! و بخاطر (کارهاي) آنها، اندوهگين و دلسرد مشو! و از توطئه‌هاي آنها، در تنگنا قرار مگير!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2029, N'خداوند با کساني است که تقوا پيشه کرده‌اند، و کساني که نيکوکارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2030, N'پاک و منزه است خدايي که بنده‌اش را در يک شب، از مسجد الحرام به مسجد الاقصي -که گرداگردش را پربرکت ساخته‌ايم- برد، تا برخي از آيات خود را به او نشان دهيم؛ چرا که او شنوا و بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2031, N'ما به موسي کتاب آسماني داديم؛ و آن را وسيله هدايت بني اسرائيل ساختيم؛ (و گفتيم:) غير ما را تکيه‌گاه خود قرار ندهيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2032, N'اي فرزندان کساني که با نوح (بر کشتي) سوار کرديم! او بنده شکرگزاري بود. (شما هم مانند او باشيد، تا نجات يابيد!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2033, N'ما به بني اسرائيل در کتاب (تورات) اعلام کرديم که دوبار در زمين فساد خواهيد کرد، و برتري‌جويي بزرگي خواهيد نمود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2034, N'هنگامي که نخستين وعده فرا رسد، گروهي از بندگان پيکارجوي خود را بر ضد شما ميانگيزيم (تا شما را سخت در هم کوبند؛ حتي براي به دست آوردن مجرمان)، خانه‌ها را جستجو مي‌کنند؛ و اين وعده‌اي است قطعي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2035, N'سپس شما را بر آنها چيره مي‌کنيم؛ و شما را به وسيله داراييها و فرزنداني کمک خواهيم کرد؛ و نفرات شما را بيشتر (از دشمن) قرارمي‌دهيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2036, N'اگر نيکي کنيد، به خودتان نيکي مي‌کنيد؛ و اگر بدي کنيد باز هم به خود مي‌کنيد. و هنگامي که وعده دوم فرا رسد، (آنچنان دشمن بر شما سخت خواهد گرفت که) آثار غم و اندوه در صورتهايتان ظاهر مي‌شود؛ و داخل مسجد (الاقصي) مي‌شوند همان گونه که بار اول وارد شدند؛ و آنچه را زير سلطه خود مي‌گيرند، در هم مي‌کوبند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2037, N'اميد است پروردگارتان به شما رحم کند! هرگاه برگرديد، ما هم بازمي‌گرديم؛ و جهنم را براي کافران، زندان سختي قرار داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2038, N'اين قرآن، به راهي که استوارترين راه‌هاست، هدايت مي‌کند؛ و به مؤمناني که اعمال صالح انجام مي‌دهند، بشارت مي‌دهد که براي آنها پاداش بزرگي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2039, N'و اينکه آنها که به قيامت ايمان نمي‌آورند، عذاب دردناکي براي آنان آماده ساخته‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2040, N'انسان (بر اثر شتابزدگي)، بديها را طلب مي‌کند آن گونه که نيکيها را مي‌طلبد؛ و انسان، هميشه عجول بوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2041, N'ما شب و روز را دو نشانه توحيد و عظمت خود قرار داديم؛ سپس نشانه شب را محو کرده، و نشانه روز را روشني‌بخش ساختيم تا (در پرتو آن،) فضل پروردگارتان را بطلبيد (و به تلاش زندگي برخيزيد)، و عدد سالها و حساب را بدانيد؛ و هر چيزي را بطور مشخص و آشکار، بيان کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2042, N'و هر انساني، اعمالش را بر گردنش آويخته‌ايم؛ و روز قيامت، کتابي براي او بيرون مي‌آوريم که آن را در برابر خود، گشوده مي‌بيند! (اين همان نامه اعمال اوست!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2043, N'(و به او مي‌گوييم:) کتابت را بخوان، کافي است که امروز، خود حسابگر خويش باشي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2044, N'هر کس هدايت شود، براي خود هدايت يافته؛ و آن کس که گمراه گردد، به زيان خود گمراه شده است؛ و هيچ کس بار گناه ديگري را به دوش نمي‌کشد؛ و ما هرگز (قومي را) مجازات نخواهيم کرد، مگر آنکه پيامبري مبعوث کرده باشيم (تا وظايفشان را بيان کند.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2045, N'و هنگامي که بخواهيم شهر و دياري را هلاک کنيم، نخست اوامر خود را براي «مترفين» (و ثروتمندان مست شهوت) آنجا، بيان مي‌داريم، سپس هنگامي که به مخالفت برخاستند و استحقاق مجازات يافتند، آنها را به شدت درهم مي‌کوبيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2046, N'چه بسيار مردمي که در قرون بعد از نوح، زندگي مي‌کردند؛ (و طبق همين سنت،) آنها را هلاک کرديم! و کافي است که پروردگارت از گناهان بندگانش آگاه، و نسبت به آن بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2047, N'آن کس که (تنها) زندگي زودگذر (دنيا) را مي‌طلبد، آن مقدار از آن را که بخواهيم -و به هر کس اراده کنيم- مي‌دهيم؛ سپس دوزخ را براي او قرار خواهيم داد، که در آتش سوزانش مي‌سوزد در حالي که نکوهيده و رانده (درگاه خدا) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2048, N'و آن کس که سراي آخرت را بطلبد، و براي آن سعي و کوشش کند -در حالي که ايمان داشته باشد- سعي و تلاش او، (از سوي خدا) پاداش داده خواهد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2049, N'هر يک از اين دو گروه را از عطاي پروردگارت، بهره و کمک مي‌دهيم؛ و عطاي پروردگارت هرگز (از کسي) منع نشده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2050, N'ببين چگونه بعضي را (در دنيا بخاطر تلاششان) بر بعضي ديگر برتري بخشيده‌ايم؛ درجات آخرت و برتريهايش، از اين هم بيشتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2051, N'هرگز معبود ديگري را با خدا قرار مده، که نکوهيده و بي‌يار و ياور خواهي نشست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2052, N'و پروردگارت فرمان داده: جز او را نپرستيد! و به پدر و مادر نيکي کنيد! هرگاه يکي از آن دو، يا هر دوي آنها، نزد تو به سن پيري رسند، کمترين اهانتي به آنها روا مدار! و بر آنها فرياد مزن! و گفتار لطيف و سنجيده و بزرگوارانه به آنها بگو!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2053, N'و بالهاي تواضع خويش را از محبت و لطف، در برابر آنان فرود آر! و بگو: «پروردگارا! همان‌گونه که آنها مرا در کوچکي تربيت کردند، مشمول رحمتشان قرار ده!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2054, N'پروردگار شما از درون دلهايتان آگاهتر است؛ (اگر لغزشي در اين زمينه داشتيد) هر گاه صالح باشيد (و جبران کنيد) او بازگشت‌کنندگان را مي‌بخشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2055, N'و حق نزديکان را بپرداز، و (همچنين حق) مستمند و وامانده در راه را! و هرگز اسراف و تبذير مکن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2056, N'چرا که تبذيرکنندگان، برادران شياطينند؛ و شيطان در برابر پروردگارش، بسيار ناسپاس بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2057, N'و هرگاه از آنان [= مستمندان‌] روي برتابي، و انتظار رحمت پروردگارت را داشته باشي (تا گشايشي در کارت پديد آيد و به آنها کمک کني)، با گفتار نرم و آميخته با لطف با آنها سخن بگو!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2058, N'هرگز دستت را بر گردنت زنجير مکن، (و ترک انفاق و بخشش منما) و بيش از حد (نيز) دست خود را مگشاي، تا مورد سرزنش قرار گيري و از کار فروماني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2059, N'به يقين، پروردگارت روزي را براي هر کس بخواهد، گشاده يا تنگ مي‌دارد؛ او نسبت به بندگانش، آگاه و بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2060, N'و فرزندانتان را از ترس فقر، نکشيد! ما آنها و شما را روزي مي‌دهيم؛ مسلما کشتن آنها گناه بزرگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2061, N'و نزديک زنا نشويد، که کار بسيار زشت، و بد راهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2062, N'و کسي را که خداوند خونش را حرام شمرده، نکشيد، جز بحق! و آن کس که مظلوم کشته شده، براي وليش سلطه (و حق قصاص) قرار داديم؛ اما در قتل اسراف نکند، چرا که او مورد حمايت است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2063, N'و به مال يتيم، جز به بهترين راه نزديک نشويد، تا به سر حد بلوغ رسد! و به عهد (خود) وفا کنيد، که از عهد سؤال مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2064, N'و هنگامي که پيمانه مي‌کنيد، حق پيمانه را ادا نماييد، و با ترازوي درست وزن کنيد! اين براي شما بهتر، و عاقبتش نيکوتر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2065, N'از آنچه به آن آگاهي نداري، پيروي مکن، چرا که گوش و چشم و دل، همه مسؤولند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2066, N'و روي زمين، با تکبر راه مرو! تو نمي‌تواني زمين را بشکافي، و طول قامتت هرگز به کوه‌ها نمي‌رسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2067, N'همه اينها گناهش نزد پروردگار تو ناپسند است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2068, N'اين (احکام)، از حکمتهايي است که پروردگارت به تو وحي فرستاده؛ و هرگز معبودي با خدا قرار مده، که در جهنم افکنده مي‌شوي، در حالي که سرزنش شده، و رانده (درگاه خدا) خواهي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2069, N'آيا پروردگارتان فرزندان پسر را مخصوص شما ساخته، و خودش دختراني از فرشتگان برگزيده است؟! شما سخن بزرگ (و بسيار زشتي) مي‌گوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2070, N'ما در اين قرآن، انواع بيانات مؤثر را آورديم تا متذکر شوند! ولي (گروهي از کوردلان،) جز بر نفرتشان نمي‌افزايد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2071, N'بگو: «اگر آنچنان که آنها مي‌گويند با او خداياني بود، در اين صورت، (خدايان) سعي مي‌کردند راهي به سوي (خداوند) صاحب عرش پيدا کنند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2072, N'او پاک و برتر است از آنچه آنها مي‌گويند، بسيار برتر و منزه‌تر!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2073, N'آسمانهاي هفتگانه و زمين و کساني که در آنها هستند، همه تسبيح او مي‌گويند؛ و هر موجودي، تسبيح و حمد او مي‌گويد؛ ولي شما تسبيح آنها را نمي‌فهميد؛ او بردبار و آمرزنده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2074, N'و هنگامي که قرآن مي‌خواني، ميان تو و آنها که به آخرت ايمان نمي‌آورند، حجاب ناپيدايي قرارمي‌دهيم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2075, N'و بر دلهايشان پوششهايي، تا آن را نفهمند؛ و در گوشهايشان سنگيني؛ و هنگامي که پروردگارت را در قرآن به يگانگي ياد مي‌کني، آنها پشت مي‌کنند و از تو روي بر مي‌گردانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2076, N'هنگامي که به سخنان تو گوش فرامي‌دهند، ما بهتر مي‌دانيم براي چه گوش فرا مي‌دهند؛ (و همچنين) در آن هنگام که با هم نجوا مي‌کنند؛ آنگاه که ستمگران مي‌گويند: «شما جز از انساني که افسون شده، پيروي نمي‌کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2077, N'ببين چگونه براي تو مثلها زدند! در نتيجه گمراه شدند، و نمي‌توانند راه حق را پيدا کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2078, N'و گفتند: «آيا هنگامي که ما، استخوانهاي پوسيده و پراکنده‌اي شديم، دگر بار آفرينش تازه‌اي خواهيم يافت؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2079, N'بگو: «شما سنگ باشيد يا آهن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2080, N'يا هر مخلوقي که در نظر شما، از آن هم سخت‌تر است (و از حيات و زندگي دورتر مي‌باشد، باز خدا قادر است شما را به زندگي مجدد بازگرداند). آنها بزودي مي گويند: «چه کسي ما را بازمي‌گرداند؟!» بگو: «همان کسي که نخستين بار شما را آفريد.» آنان سر خود را (از روي تعجب و انکار،) به سوي تو خم مي‌کنند و مي‌گويند: «در چه زماني خواهد بود؟!» بگو: «شايد نزديک باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2081, N'همان روز که شما را (از قبرهايتان) فرامي‌خواند؛ شما هم اجابت مي‌کنيد در حالي که حمد او را مي‌گوييد؛ مي‌پنداريد تنها مدت کوتاهي (در جهان برزخ) درنگ کرده‌ايد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2082, N'به بندگانم بگو: «سخني بگويند که بهترين باشد! چرا که (شيطان بوسيله سخنان ناموزون)، ميان آنها فتنه و فساد مي‌کند؛ هميشه شيطان دشمن آشکاري براي انسان بوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2083, N'پروردگار شما، از (نيات و اعمال) شما آگاهتر است؛ اگر بخواهد (و شايسته بداند)، شما را مشمول رحمت خود مي‌سازد؛ و اگر بخواهد، مجازات مي‌کند؛ و ما تو را بعنوان مأمور بر آنان نفرستاده‌ايم (که آنان را مجبور به ايمان کني!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2084, N'پروردگار تو، از حال همه کساني که در آسمانها و زمين هستند، آگاهتر است؛ و (اگر تو را بر ديگران برتري داديم، بخاطر شايستگي توست،) ما بعضي از پيامبران را بر بعضي ديگر برتري داديم؛ و به داوود، زبور بخشيديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2085, N'بگو: «کساني را که غير از خدا (معبود خود) مي‌پنداريد، بخوانيد! آنها نه مي‌توانند مشکلي را از شما برطرف سازند، و نه تغييري در آن ايجاد کنند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2086, N'کساني را که آنان مي‌خوانند، خودشان وسيله‌اي (براي تقرب) به پروردگارشان مي‌جويند، وسيله‌اي هر چه نزديکتر؛ و به رحمت او اميدوارند؛ و از عذاب او مي‌ترسند؛ چرا که عذاب پروردگارت، همواره در خور پرهيز و وحشت است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2087, N'هيچ شهر و آبادي نيست مگر اينکه آن را پيش از روز قيامت هلاک مي‌کنيم؛ يا (اگر گناهکارند،) به عذاب شديدي گرفتارشان خواهيم ساخت؛ اين، در کتاب الهي [= لوح محفوظ] ثبت است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2088, N'هيچ چيز مانع ما نبود که اين معجزات (درخواستي بهانه‌جويان) را بفرستيم جز اينکه پيشينيان (که همين درخواستها را داشتند، و با ايشان هماهنگ بودند)، آن را تکذيب کردند؛ (از جمله،) ما به (قوم) ثمود، ناقه داديم؛ (معجزه‌اي) که روشنگر بود؛ اما بر آن ستم کردند (و ناقه را کشتند). ما معجزات را فقط براي بيم دادن (و اتمام حجت) مي‌فرستيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2089, N'(به ياد آور) زماني را که به تو گفتيم: «پروردگارت احاطه کامل به مردم دارد؛ (و از وضعشان کاملا آگاه است.) و ما آن رؤيايي را که به تو نشان داديم، فقط براي آزمايش مردم بود؛ همچنين شجره ملعونه [= درخت نفرين شده‌] را که در قرآن ذکر کرده‌ايم. ما آنها را بيم داده (و انذار) مي‌کنيم؛ اما جز طغيان عظيم، چيزي بر آنها نمي‌افزايد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2090, N'(به ياد آوريد) زماني را که به فرشتگان گفتيم: «براي آدم سجده کنيد!» آنها همگي سجده کردند، جز ابليس که گفت: «آيا براي کسي سجده کنم که او را از خاک آفريده‌اي؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2091, N'(سپس) گفت: «به من بگو، اين کسي را که بر من برتري داده‌اي (به چه دليل بوده است؟) اگر مرا تا روز قيامت زنده بگذاري، همه فرزندانش را، جز عده کمي، گمراه و ريشه‌کن خواهم ساخت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2092, N'فرمود: «برو! هر کس از آنان از تو تبعيت کند، جهنم کيفر شماست، کيفري است فراوان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2093, N'هر کدام از آنها را مي‌تواني با صدايت تحريک کن! و لشکر سواره و پياده‌ات را بر آنها گسيل دار! و در ثروت و فرزندانشان شرکت جوي! و آنان را با وعده‌ها سرگرم کن! -ولي شيطان، جز فريب و دروغ، وعده‌اي به آنها نمي‌دهد-')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2094, N'(اما بدان) تو هرگز سلطه‌اي بر بندگان من، نخواهي يافت (و آنها هيچ‌گاه به دام تو گرفتار نمي‌شوند)! همين قدر کافي است که پروردگارت حافظ آنها باشد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2095, N'پروردگارتان کسي است که کشتي را در دريا براي شما به حرکت درمي‌آورد، تا از نعمت او بهره‌مند شويد؛ او نسبت به شما مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2096, N'و هنگامي که در دريا ناراحتي به شما برسد، جز او، تمام کساني را که (براي حل مشکلات خود) مي‌خوانيد، فراموش مي‌کنيد؛ اما هنگامي که شما را به خشکي نجات دهد، روي مي‌گردانيد؛ و انسان، بسيار ناسپاس است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2097, N'آيا از اين ايمن هستيد که در خشکي (با يک زلزله شديد) شما را در زمين فرو ببرد، يا طوفاني از سنگريزه بر شما بفرستد (و در آن مدفونتان کند)، سپس حافظ (و ياوري) براي خود نيابيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2098, N'يا اينکه ايمن هستيد که بار ديگر شما را به دريا بازگرداند، و تندباد کوبنده‌اي بر شما بفرستد، و شما را بخاطر کفرتان غرق کند، سپس دادخواه و خونخواهي در برابر ما پيدا نکنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2099, N'ما آدميزادگان را گرامي داشتيم؛ و آنها را در خشکي و دريا، (بر مرکبهاي راهوار) حمل کرديم؛ و از انواع روزيهاي پاکيزه به آنان روزي داديم؛ و آنها را بر بسياري از موجوداتي که خلق کرده‌ايم، برتري بخشيديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2100, N'(به ياد آوريد) روزي را که هر گروهي را با پيشوايشان مي‌خوانيم! کساني که نامه عملشان به دست راستشان داده شود، آن را (با شادي و سرور) مي‌خوانند؛ و بقدر رشته شکاف هسته خرمايي به آنان ستم نمي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2101, N'اما کسي که در اين جهان (از ديدن چهره حق) نابينا بوده است، در آخرت نيز نابينا و گمراهتر است!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2102, N'نزديک بود آنها تو را (با وسوسه‌هاي خود) از آنچه بر تو وحي کرده‌ايم بفريبند، تا غير آن را به ما نسبت دهي؛ و در آن صورت، تو را به دوستي خود برميگزينند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2103, N'و اگر ما تو را ثابت قدم نمي‌ساختيم (و در پرتو مقام عصمت، مصون از انحراف نبودي)، نزديک بود به آنان تمايل کني.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2104, N'اگر چنين مي‌کردي، ما دو برابر مجازات (مشرکان) در زندگي دنيا، و دو برابر (مجازات) آنها را بعد از مرگ، به تو مي‌چشانديم؛ سپس در برابر ما، ياوري براي خود نمي‌يافتي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2105, N'و نزديک بود (با نيرنگ و توطئه) تو را از اين سرزمين بلغزانند، تا از آن بيرونت کنند! و هرگاه چنين مي‌کردند، (گرفتار مجازات سخت الهي شده،) و پس از تو، جز مدت کمي باقي نمي‌ماندند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2106, N'اين سنت (ما در مورد) پيامبراني است که پيش از تو فرستاديم؛ و هرگز براي سنت ما تغيير و دگرگوني نخواهي يافت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2107, N'نماز را از زوال خورشيد (هنگام ظهر) تا نهايت تاريکي شب [= نيمه شب‌] برپا دار؛ و همچنين قرآن فجر [= نماز صبح‌] را؛ چرا که قرآن فجر، مشهود (فرشتگان شب و روز) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2108, N'و پاسي از شب را (از خواب برخيز، و) قرآن (و نماز) بخوان! اين يک وظيفه اضافي براي توست؛ اميد است پروردگارت تو را به مقامي در خور ستايش برانگيزد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2109, N'و بگو: «پروردگارا! مرا (در هر کار،) با صداقت وارد کن، و با صداقت خارج ساز! و از سوي خود، حجتي ياري کننده برايم قرار ده!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2110, N'و بگو: «حق آمد، و باطل نابود شد؛ يقينا باطل نابود شدني است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2111, N'و از قرآن، آنچه شفا و رحمت است براي مؤمنان، نازل مي‌کنيم؛ و ستمگران را جز خسران (و زيان) نمي‌افزايد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2112, N'هنگامي که به انسان نعمت مي‌بخشيم، (از حق) روي مي‌گرداند و متکبرانه دور مي‌شود؛ و هنگامي که (کمترين) بدي به او مي‌رسد، (از همه چيز) مايوس مي‌گردد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2113, N'بگو: «هر کس طبق روش (و خلق و خوي) خود عمل مي‌کند؛ و پروردگارتان کساني را که راهشان نيکوتر است، بهتر مي‌شناسد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2114, N'و از تو درباره «روح» سؤال مي‌کنند، بگو: «روح از فرمان پروردگار من است؛ و جز اندکي از دانش، به شما داده نشده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2115, N'و اگر بخواهيم، آنچه را بر تو وحي فرستاده‌ايم، از تو مي‌گيريم؛ سپس کسي را نمي‌يابي که در برابر ما، از تو دفاع کند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2116, N'مگر رحمت پروردگارت (شامل حالت گردد،) که فضل پروردگارت بر تو بزرگ بوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2117, N'بگو: «اگر انسانها و پريان (جن و انس) اتفاق کنند که همانند اين قرآن را بياورند، همانند آن را نخواهند آورد؛ هر چند يکديگر را (در اين کار) کمک کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2118, N'ما در اين قرآن، براي مردم از هر چيز نمونه‌اي آورديم (و همه معارف در آن جمع است)؛ اما بيشتر مردم (در برابر آن، از هر کاري) جز انکار، ابا داشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2119, N'و گفتند: «ما هرگز به تو ايمان نمي‌آوريم تا اينکه چشمه‌جوشاني از اين سرزمين (خشک و سوزان) براي ما خارج سازي...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2120, N'يا باغي از نخل و انگور از آن تو باشد؛ و نهرها در لابه‌لاي آن جاري کني...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2121, N'يا قطعات (سنگهاي) آسمان را -آنچنان که مي‌پنداري- بر سر ما فرود آري؛ يا خداوند و فرشتگان را در برابر ما بياوري...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2122, N'يا براي تو خانه‌اي پر نقش و نگار از طلا باشد؛ يا به آسمان بالا روي؛ حتي اگر به آسمان روي، ايمان نمي‌آوريم مگر آنکه نامه‌اي بر ما فرود آوري که آن را بخوانيم! «بگو:» منزه است پروردگارم (از اين سخنان بي‌معني)! مگر من جز انساني فرستاده خدا هستم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2123, N'تنها چيزي که بعد از آمدن هدايت مانع شد مردم ايمان بياورند، اين بود (که از روي ناداني و بي‌خبري) گفتند: «آيا خداوند بشري را بعنوان رسول فرستاده است؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2124, N'بگو: «(حتي) اگر در روي زمين فرشتگاني (زندگي مي‌کردند، و) با آرامش گام برمي‌داشتند، ما فرشته‌اي را به عنوان رسول، بر آنها مي‌فرستاديم!» (چرا که رهنماي هر گروهي بايد از جنس خودشان باشد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2125, N'بگو: «همين کافي است که خداوند، ميان من و شما گواه باشد؛ چرا که او نسبت به بندگانش آگاه و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2126, N'هر کس را خدا هدايت کند، هدايت يافته واقعي اوست؛ و هر کس را (بخاطر اعمالش) گمراه سازد، هاديان و سرپرستاني غير خدا براي او نخواهي يافت؛ و روز قيامت، آنها را بر صورتهايشان محشور مي‌کنيم، در حالي که نابينا و گنگ و کرند؛ جايگاهشان دوزخ است؛ هر زمان آتش آن فرونشيند، شعله تازه‌اي بر آنان مي‌افزاييم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2127, N'اين کيفر آنهاست، بخاطر اينکه نسبت به آيات ما کافر شدند و گفتند: «آيا هنگامي که ما استخوانهاي پوسيده و خاکهاي پراکنده‌اي شديم، بار ديگر آفرينش تازه‌اي خواهيم يافت؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2128, N'آيا نمي‌دانند خدايي که آسمانها و زمين را آفريده، قادر است مثل آنان را بيافريند (و به زندگي جديد بازشان گرداند)؟! و براي آنان سرآمدي قطعي -که شکي در آن نيست- قرار داده؛ اما ظالمان، جز کفر و انکار را پذيرا نيستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2129, N'بگو: «اگر شما مالک خزائن رحمت پروردگار من بوديد. در آن صورت، (بخاطر تنگ نظري) امساک مي‌کرديد، مبادا انفاق، مايه تنگدستي شما شود» و انسان تنگ نظر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2130, N'ما به موسي نه معجزه روشن داديم؛ پس از بني اسرائيل سؤال کن آن زمان که اين (معجزات نه گانه) به سراغ آنها آمد (چگونه بودند)؟! فرعون به او گفت: «اي موسي! گمان مي‌کنم تو ديوانه (يا ساحري)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2131, N'(موسي) گفت: «تو مي‌داني اين آيات را جز پروردگار آسمانها و زمين -براي روشني دلها- نفرستاده؛ و من گمان مي‌کنم اي فرعون، تو (بزودي) هلاک خواهي شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2132, N'پس (فرعون) تصميم گرفت آنان را از آن سرزمين ريشه کن سازد؛ ولي ما، او و تمام کساني را که با او بودند، غرق کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2133, N'و بعد از آن به بني اسرائيل گفتيم: «در اين سرزمين [= مصر و شام‌] ساکن شويد! اما هنگامي که وعده آخرت فرا رسد، همه شما را دسته جمعي (به آن دادگاه عدل) مي‌آوريم»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2134, N'و ما قرآن را بحق نازل کرديم؛ و بحق نازل شد؛ و تو را، جز بعنوان بشارت‌دهنده و بيم‌دهنده، نفرستاديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2135, N'و قرآني که آياتش را از هم جدا کرديم، تا آن را با درنگ بر مردم بخواني؛ و آن را بتدريج نازل کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2136, N'بگو: «خواه به آن ايمان بياوريد، و خواه ايمان نياوريد، کساني که پيش از آن به آنها دانش داده شده، هنگامي که (اين آيات) بر آنان خوانده مي‌شود، سجده‌کنان به خاک مي‌افتند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2137, N'و مي‌گويند: «منزه است پروردگار ما، که وعده‌هايش به يقين انجام‌شدني است !»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2138, N'آنها (بي‌اختيار) به زمين مي‌افتند و گريه مي‌کنند؛ و (تلاوت اين آيات، همواره) بر خشوعشان مي‌افزايد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2139, N'بگو: ««الله» را بخوانيد يا «رحمان» را، هر کدام را بخوانيد، (ذات پاکش يکي است؛ و) براي او بهترين نامهاست!» و نمازت را زياد بلند، يا خيلي آهسته نخوان؛ و در ميان آن دو، راهي (معتدل) انتخاب کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2140, N'و بگو: «ستايش براي خداوندي است که نه فرزندي براي خود انتخاب کرده، و نه شريکي در حکومت دارد، و نه بخاطر ضعف و ذلت، (حامي و) سرپرستي براي اوست!» و او را بسيار بزرگ بشمار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2141, N'حمد مخصوص خدايي است که اين کتاب (آسماني) را بر بنده (برگزيده)اش نازل کرد، و هيچ گونه کژي در آن قرار نداد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2142, N'در حالي که ثابت و مستقيم و نگاهبان کتابهاي (آسماني) ديگر است؛ تا (بدکاران را) از عذاب شديد او بترساند؛ و مؤمناني را که کارهاي شايسته انجام مي‌دهند، بشارت دهد که پاداش نيکويي براي آنهاست...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2143, N'(همان بهشت برين) که جاودانه در آن خواهند ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2144, N'و (نيز) آنها را که گفتند: «خداوند، فرزندي (براي خود) انتخاب کرده است»، انذار کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2145, N'نه آنها (هرگز) به اين سخن يقين دارند، و نه پدرانشان! سخن بزرگي از دهانشان خارج مي‌شود! آنها فقط دروغ مي‌گويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2146, N']گويي مي‌خواهي بخاطر اعمال آنان، خود را از غم و اندوه هلاک کني اگر به اين گفتار ايمان نياورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2147, N'ما آنچه را روي زمين است زينت آن قرار داديم، تا آنها را بيازماييم که کدامينشان بهتر عمل مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2148, N'(ولي) اين زرق و برقها پايدار نيست، و ما (سرانجام) قشر روي زمين را خاک بي گياهي قرار مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2149, N'آيا گمان کردي اصحاب کهف و رقيم از آيات عجيب ما بودند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2150, N'زماني را به خاطر بياور که آن جوانان به غار پناه بردند، و گفتند: «پروردگارا! ما را از سوي خودت رحمتي عطا کن، و راه نجاتي براي ما فراهم ساز!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2151, N'ما (پرده خواب را) در غار بر گوششان زديم، و سالها در خواب فرو رفتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2152, N'سپس آنان را برانگيختيم تا بدانيم (و اين امر آشکار گردد که) کدام يک از آن دو گروه، مدت خواب خود را بهتر حساب کرده‌اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2153, N'ما داستان آنان را بحق براي تو بازگو مي‌کنيم؛ آنها جواناني بودند که به پروردگارشان ايمان آوردند، و ما بر هدايتشان افزوديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2154, N'و دلهايشان را محکم ساختيم در آن موقع که قيام کردند و گفتند: «پروردگار ما، پروردگار آسمانها و زمين است؛ هرگز غير او معبودي را نمي‌خوانيم؛ که اگر چنين کنيم، سخني بگزاف گفته‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2155, N'اين قوم ما هستند که معبودهايي جز خدا انتخاب کرده‌اند؛ چرا دليل آشکاري (بر اين کار) نمي‌آورند؟! و چه کسي ظالمتر است از آن کس که بر خدا دروغ ببندد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2156, N'و (به آنها گفتيم:) هنگامي که از آنان و آنچه جز خدا مي‌پرستند کناره‌گيري کرديد، به غار پناه بريد؛ که پروردگارتان (سايه) رحمتش را بر شما مي‌گستراند؛ و در اين امر، آرامشي براي شما فراهم مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2157, N'و (اگر در آنجا بودي) خورشيد را مي‌ديدي که به هنگام طلوع، به سمت راست غارشان متمايل مي‌گردد؛ و به هنگام غروب، به سمت چپ؛ و آنها در محل وسيعي از آن (غار) قرار داشتند؛ اين از آيات خداست! هر کس را خدا هدايت کند، هدايت يافته واقعي اوست؛ و هر کس را گمراه نمايد، هرگز ولي و راهنمايي براي او نخواهي يافت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2158, N'و (اگر به آنها نگاه مي‌کردي) مي‌پنداشتي بيدارند؛ در حالي که در خواب فرو رفته بودند! و ما آنها را به سمت راست و چپ ميگردانديم (تا بدنشان سالم بماند). و سگ آنها دستهاي خود را بر دهانه غار گشوده بود (و نگهباني مي‌کرد). اگر نگاهشان مي‌کردي، از آنان مي‌گريختي؛ و سر تا پاي تو از ترس و وحشت پر مي‌شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2159, N'اين‌گونه آنها را (از خواب) برانگيختيم تا از يکديگر سؤال کنند؛ يکي از آنها گفت: «چه مدت خوابيديد؟!» گفتند: «يک روز، يا بخشي از يک روز!» (و چون نتوانستند مدت خوابشان را دقيقا بدانند) گفتند: «پروردگارتان از مدت خوابتان آگاهتر است! اکنون يک نفر از خودتان را با اين سکه‌اي که داريد به شهر بفرستيد، تا بنگرد کدام يک از آنها غذاي پاکيزه‌تري دارند، و مقداري از آن براي روزي شما بياورد. اما بايد دقت کند، و هيچ کس را از وضع شما آگاه نسازد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2160, N'چرا که اگر آنان از وضع شما آگاه شوند، سنگسارتان مي‌کنند؛ يا شما را به آيين خويش بازمي‌گردانند؛ و در آن صورت، هرگز روي رستگاري را نخواهيد ديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2161, N'و اينچنين مردم را متوجه حال آنها کرديم، تا بدانند که وعده خداوند (در مورد رستاخيز) حق است؛ و در پايان جهان و قيام قيامت شکي نيست! در آن هنگام که ميان خود درباره کار خويش نزاع داشتند، گروهي مي‌گفتند: «بنايي بر آنان بسازيد (تا براي هميشه از نظر پنهان شوند! و از آنها سخن نگوييد که) پروردگارشان از وضع آنها آگاهتر است!» ولي آنها که از رازشان آگاهي يافتند (و آن را دليلي بر رستاخيز ديدند) گفتند: «ما مسجدي در کنار (مدفن) آنها مي‌سازيم (تا خاطره آنان فراموش نشود.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2162, N'گروهي خواهند گفت: «آنها سه نفر بودند، که چهارمين آنها سگشان بود!» و گروهي مي‌گويند: «پنچ نفر بودند، که ششمين آنها سگشان بود.» -همه اينها سخناني بي‌دليل است- و گروهي مي‌گويند: «آنها هفت نفر بودند، و هشتمين آنها سگشان بود.» بگو: «پروردگار من از تعدادشان آگاهتر است!» جز گروه کمي، تعداد آنها را نمي‌دانند. پس درباره آنان جز با دليل سخن مگو؛ و از هيچ کس درباره آنها سؤال مکن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2163, N'و هرگز در مورد کاري نگو: «من فردا آن را انجام مي‌دهم»...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2164, N'مگر اينکه خدا بخواهد! و هرگاه فراموش کردي، (جبران کن) و پروردگارت را به خاطر بياور؛ و بگو: «اميدوارم که پروردگارم مرا به راهي روشنتر از اين هدايت کند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2165, N'آنها در غارشان سيصد سال درنگ کردند، و نه سال (نيز) بر آن افزودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2166, N'بگو: «خداوند از مدت توقفشان آگاهتر است؛ غيب آسمانها و زمين از آن اوست! راستي چه بينا و شنواست! آنها هيچ ولي و سرپرستي جز او ندارند! و او هيچ کس را در حکم خود شرکت نمي‌دهد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2167, N'آنچه را از کتاب پروردگارت به تو وحي شده تلاوت کن! هيچ چيز سخنان او را دگرگون نمي‌سازد؛ و هرگز پناهگاهي جز او نمي‌يابي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2168, N'با کساني باش که پروردگار خود را صبح و عصر مي‌خوانند، و تنها رضاي او را مي‌طلبند! و هرگز بخاطر زيورهاي دنيا، چشمان خود را از آنها برمگير! و از کساني که قلبشان را از ياد خود غافل ساختيم اطاعت مکن! همانها که از هواي نفس پيروي کردند، و کارهايشان افراطي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2169, N'بگو: «اين حق است از سوي پروردگارتان! هر کس مي‌خواهد ايمان بياورد (و اين حقيقت را پذيرا شود)، و هر کس ميخواهد کافر گردد!» ما براي ستمگران آتشي آماده کرديم که سراپرده‌اش آنان را از هر سو احاطه کرده است! و اگر تقاضاي آب کنند، آبي براي آنان مياورند که همچون فلز گداخته صورتها را بريان مي‌کند! چه بد نوشيدني، و چه بد محل اجتماعي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2170, N'مسلما کساني که ايمان آوردند و کارهاي شايسته انجام دادند، ما پاداش نيکوکاران را ضايع نخواهيم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2171, N'آنها کساني هستند که بهشت جاودان براي آنان است؛ باغهايي از بهشت که نهرها از زير درختان و قصرهايش جاري است؛ در آنجا با دستبندهايي از طلا آراسته مي‌شوند؛ و لباسهايي (فاخر) به رنگ سبز، از حرير نازک و ضخيم، دربر مي‌کنند؛ در حالي که بر تختها تکيه کرده‌اند. چه پاداش خوبي، و چه جمع نيکويي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2172, N'(اي پيامبر!) براي آنان مثالي بزن: آن دو مرد، که براي يکي از آنها دو باغ از انواع انگورها قرار داديم؛ و گرداگرد آن دو (باغ) را با درختان نخل پوشانديم؛ و در ميانشان زراعت پر برکتي قرارداديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2173, N'هر دو باغ، ميوه آورده بود، (ميوه‌هاي فراوان،) و چيزي فروگذار نکرده بود؛ و ميان آن دو، نهر بزرگي جاري ساخته بوديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2174, N'صاحب اين باغ، درآمد فراواني داشت؛ به همين جهت، به دوستش -در حالي که با او گفتگو مي‌کرد- چنين گفت: «من از نظر ثروت از تو برتر، و از نظر نفرات نيرومندترم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2175, N'و در حالي که نسبت به خود ستمکار بود، در باغ خويش گام نهاد، و گفت: «من گمان نمي‌کنم هرگز اين باغ نابود شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2176, N'و باور نمي‌کنم قيامت برپا گردد! و اگر به سوي پروردگارم بازگردانده شوم (و قيامتي در کار باشد)، جايگاهي بهتر از اين جا خواهم يافت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2177, N'دوست (با ايمان) وي -در حالي که با او گفتگو مي‌کرد- گفت: «آيا به خدايي که تو را از خاک، و سپس از نطفه آفريد، و پس از آن تو را مرد کاملي قرار داد ، کافر شدي؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2178, N'ولي من کسي هستم که «الله» پروردگار من است؛ و هيچ کس را شريک پروردگارم قرارنمي‌دهم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2179, N'چرا هنگامي که وارد باغت شدي، نگفتي اين نعمتي است که خدا خواسته است؟! قوت (و نيرويي) جز از ناحيه خدا نيست! و اگر مي‌بيني من از نظر مال و فرزند از تو کمترم (مطلب مهمي نيست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2180, N'شايد پروردگارم بهتر از باغ تو به من بدهد؛ و مجازات حساب شده‌اي از آسمان بر باغ تو فروفرستد، بگونه‌اي که آن را به زمين بي‌گياه لغزنده‌اي مبدل کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2181, N'و يا آب آن در اعمال زمين فرو رود، آن گونه که هرگز نتواني آن را به دست آوري!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2182, N'(به هر حال عذاب الهي فرا رسيد،) و تمام ميوه‌هاي آن نابود شد؛ و او بخاطر هزينه هايي که در آن صرف کرده بود، پيوسته دستهاي خود را به هم مي‌ماليد -در حالي که تمام باغ بر داربستهايش فرو ريخته بود- و مي‌گفت: «اي کاش کسي را همتاي پروردگارم قرار نداده بودم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2183, N'و گروهي نداشت که او را در برابر (عذاب) خداوند ياري دهند؛ و از خودش (نيز) نمي‌توانست ياري گيرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2184, N'در آنجا ثابت شد که ولايت (و قدرت) از آن خداوند بر حق است! اوست که برترين ثواب، و بهترين عاقبت را (براي مطيعان) دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2185, N'(اي پيامبر!) زندگي دنيا را براي آنان به آبي تشبيه کن که از آسمان فرو مي‌فرستيم؛ و بوسيله آن، گياهان زمين (سرسبز مي‌شود و) در هم فروميرود. اما بعد از مدتي مي‌خشکد؛ و بادها آن را به هر سو پراکنده مي‌کند؛ و خداوند بر همه چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2186, N'مال و فرزند، زينت زندگي دنياست؛ و باقيات صالحات [= ارزشهاي پايدار و شايسته‌] ثوابش نزد پروردگارت بهتر و اميدبخش‌تر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2187, N'و روزي را به خاطر بياور که کوه‌ها را به حرکت درآوريم؛ و زمين را آشکار (و مسطح) مي‌بيني؛ و همه آنان [= انسانها] را برمي‌انگيزيم، و احدي از ايشان را فروگذار نخواهيم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2188, N'آنها همه در يک صف به (پيشگاه) پروردگارت عرضه مي‌شوند؛ (و به آنان گفته مي‌شود:) همگي نزد ما آمديد، همان گونه که نخستين بار شما را آفريديم! اما شما گمان مي‌کرديد ما هرگز موعدي برايتان قرار نخواهيم داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2189, N'و کتاب [= کتابي که نامه اعمال همه انسانهاست‌] در آن جا گذارده مي‌شود، پس گنهکاران را مي‌بيني که از آنچه در آن است، ترسان و هراسانند؛ و مي‌گويند: «اي واي بر ما! اين چه کتابي است که هيچ عمل کوچک و بزرگي را فرونگذاشته مگر اينکه آن را به شمار آورده است؟! و (اين در حالي است که) همه اعمال خود را حاضر مي‌بينند؛ و پروردگارت به هيچ کس ستم نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2190, N'به ياد آريد زماني را که به فرشتگان گفتيم: «براي آدم سجده کنيد!» آنها همگي سجده کردند جز ابليس -که از جن بود- و از فرمان پروردگارش بيرون شد آيا (با اين حال،) او و فرزندانش را به جاي من اولياي خود انتخاب مي‌کنيد، در حالي که آنها دشمن شما هستند؟! (فرمانبرداري از شيطان و فرزندانش به جاي اطاعت خدا،) چه جايگزيني بدي است براي ستمکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2191, N'من هرگز آنها [= ابليس و فرزندانش‌] را به هنگام آفرينش آسمانها و زمين، و نه به هنگام آفرينش خودشان، حاضر نساختم! و من هيچ گاه گمراه‌کنندگان را دستيار خود قرار نمي‌دهم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2192, N'به خاطر بياوريد روزي را که (خداوند) مي‌گويد: «همتاياني را که براي من مي‌پنداشتيد، بخوانيد (تا به کمک شما بشتابند!)» ولي هر چه آنها را مي‌خوانند،جوابشان نمي‌دهند؛ و در ميان اين دو گروه، کانون هلاکتي قرارداده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2193, N'و گنهکاران، آتش (دوزخ) را مي‌بينند؛ و يقين مي‌کنند که با آن درمي‌آميزند،و هيچ گونه راه گريزي از آن نخواهند يافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2194, N'و در اين قرآن، از هر گونه مثلي براي مردم بيان کرده‌ايم؛ ولي انسان بيش از هر چيز، به مجادله مي‌پردازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2195, N'و چيزي مردم را بازنداشت از اينکه -وقتي هدايت به سراغشان آمد- ايمان بياورند و از پروردگارشان طلب آمرزش کنند، جز اينکه (خيره‌سري کردند؛ گويي مي‌خواستند) سرنوشت پيشينيان براي آنان بيايد، يا عذاب (الهي) در برابرشان قرار گيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2196, N'ما پيامبران را، جز بعنوان بشارت دهنده و انذار کننده، نمي‌فرستيم؛ اما کافران همواره مجادله به باطل مي‌کنند، تا (به گمان خود،) حق را بوسيله آن از ميان بردارند! و آيات ما، و مجازاتهايي را که به آنان وعده داده شده است،به باد مسخره گرفتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2197, N'چه کسي ستمکارتر است از آن کس که آيات پروردگارش به او تذکر داده شده، و از آن روي گرداند، و آنچه را با دستهاي خود پيش فرستاد فراموش کرد؟! ما بر دلهاي اينها پرده‌هايي افکنده‌ايم تا نفهمند؛ و در گوشهايشان سنگيني قرار داده‌ايم (تا صداي حق را نشنوند)! و از اين رو اگر آنها را به سوي هدايت بخواني، هرگز هدايت نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2198, N'و پروردگارت، آمرزنده و صاحب رحمت است؛ اگر مي‌خواست آنان را به خاطر اعمالشان مجازات کند، عذاب را هر چه زودتر براي آنها مي‌فرستاد؛ ولي براي آنان موعدي است که هرگز از آن راه فراري نخواهند داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2199, N'اين شهرها و آباديهايي است که ما آنها را هنگامي که ستم کردند هلاک نموديم؛ و براي هلاکتشان موعدي قرار داديم! (آنها ويرانه‌هايش را با چشم مي‌بينند، و عبرت نمي‌گيرند!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2200, N'به خاطر بياور هنگامي را که موسي به دوست خود گفت: دست از جستجو برنمي‌دارم تا به محل تلاقي دو دريا برسم؛ هر چند مدت طولاني به راه خود ادامه دهم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2201, N'(ولي) هنگامي که به محل تلاقي آن دو دريا رسيدند، ماهي خود را (که براي تغذيه همراه داشتند) فراموش کردند؛ و ماهي راه خود را در دريا پيش گرفت (و روان شد).')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2202, N'هنگامي که از آن جا گذشتند، (موسي) به يار همسفرش گفت: «غذاي ما را بياور، که سخت از اين سفر خسته شده‌ايم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2203, N'گفت: «به خاطر داري هنگامي که ما (براي استراحت) به کنار آن صخره پناه برديم، من (در آن جا) فراموش کردم جريان ماهي را بازگو کنم -و فقط شيطان بود که آن را از خاطر من برد- و ماهي بطرز شگفت‌آوري راه خود را در دريا پيش گرفت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2204, N'(موسي) گفت: «آن همان بود که ما مي‌خواستيم!» سپس از همان راه بازگشتند، در حالي که پي‌جويي مي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2205, N'(در آن جا) بنده‌اي از بندگان ما را يافتند که رحمت (و موهبت عظيمي) از سوي خود به او داده، و علم فراواني از نزد خود به او آموخته بوديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2206, N'موسي به او گفت: «آيا از تو پيروي کنم تا از آنچه به تو تعليم داده شده و مايه رشد و صلاح است، به من بياموزي؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2207, N'گفت: «تو هرگز نمي‌تواني با من شکيبايي کني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2208, N'و چگونه مي‌تواني در برابر چيزي که از رموزش آگاه نيستي شکيبا باشي؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2209, N'(موسي) گفت: «به خواست خدا مرا شکيبا خواهي يافت؛ و در هيچ کاري مخالفت فرمان تو نخواهم کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2210, N'(خضر) گفت: «پس اگر مي‌خواهي بدنبال من بيايي، از هيچ چيز مپرس تا خودم (به موقع) آن را براي تو بازگو کنم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2211, N'آن دو به راه افتادند؛ تا آن که سوار کشتي شدند، (خضر) کشتي را سوراخ کرد. (موسي) گفت: «آيا آن را سوراخ کردي که اهلش را غرق کني؟! راستي که چه کار بدي انجام دادي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2212, N'گفت: «آيا نگفتم تو هرگز نمي‌تواني با من شکيبايي کني؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2213, N'(موسي) گفت: «مرا بخاطر اين فراموشکاريم مؤاخذه مکن و از اين کارم بر من سخت مگير!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2214, N'باز به راه خود ادامه دادند، تا اينکه نوجواني را ديدند؛ و او آن نوجوان را کشت. (موسي) گفت: «آيا انسان پاکي را، بي آنکه قتلي کرده باشد، کشتي؟! براستي کار زشتي انجام دادي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2215, N'(باز آن مرد عالم) گفت: «آيا به تو نگفتم که تو هرگز نمي‌تواني با من صبر کني؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2216, N'(موسي) گفت: «بعد از اين اگر درباره چيزي از تو سؤال کردم، ديگر با من همراهي نکن؛ (زيرا) از سوي من معذور خواهي بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2217, N'باز به راه خود ادامه دادند تا به مردم قريه‌اي رسيدند؛ از آنان خواستند که به ايشان غذا دهند؛ ولي آنان از مهمان کردنشان خودداري نمودند؛ (با اين حال) در آن جا ديواري يافتند که مي‌خواست فروريزد؛ و (آن مرد عالم) آن را برپا داشت. (موسي) گفت: «(لااقل) مي‌خواستي در مقابل اين کار مزدي بگيري!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2218, N'او گفت: «اينک زمان جدايي من و تو فرا رسيده؛ اما بزودي راز آنچه را که نتوانستي در برابر آن صبر کني، به تو خبر مي‌دهم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2219, N'اما آن کشتي مال گروهي از مستمندان بود که با آن در دريا کار مي‌کردند؛ و من خواستم آن را معيوب کنم؛ (چرا که) پشت سرشان پادشاهي (ستمگر) بود که هر کشتي (سالمي) را بزور ميگرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2220, N'و اما آن نوجوان، پدر و مادرش با ايمان بودند؛ و بيم داشتيم که آنان را به طغيان و کفر وادارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2221, N'از اين رو، خواستيم که پروردگارشان به جاي او، فرزندي پاکتر و با محبت‌تر به آن دو بدهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2222, N'و اما آن ديوار، از آن دو نوجوان يتيم در آن شهر بود؛ و زير آن، گنجي متعلق به آن دو وجود داشت؛ و پدرشان مرد صالحي بود؛ و پروردگار تو مي‌خواست آنها به حد بلوغ برسند و گنجشان را استخراج کنند؛ اين رحمتي از پروردگارت بود؛ و من آن (کارها) را خودسرانه انجام ندادم؛ اين بود راز کارهايي که نتوانستي در برابر آنها شکيبايي به خرج دهي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2223, N'و از تو درباره «ذو القرنين» مي‌پرسند؛ بگو: «بزودي بخشي از سرگذشت او را براي شما بازگو خواهم کرد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2224, N'ما به او در روي زمين، قدرت و حکومت داديم؛ و اسباب هر چيز را در اختيارش گذاشتيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2225, N'او از اين اسباب، (پيروي و استفاده) کرد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2226, N'تا به غروبگاه آفتاب رسيد؛ (در آن جا) احساس کرد (و در نظرش مجسم شد) که خورشيد در چشمه تيره و گل‌آلودي فرو مي‌رود؛ و در آن جا قومي را يافت؛ گفتيم: «اي ذو القرنين! آيا مي‌خواهي (آنان) را مجازات کني، و يا روش نيکويي در مورد آنها انتخاب نمايي؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2227, N'گفت: «اما کسي را که ستم کرده است، مجازات خواهيم کرد؛ سپس به سوي پروردگارش بازمي‌گردد، و خدا او را مجازات شديدي خواهد کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2228, N'و اما کسي که ايمان آورد و عمل صالح انجام دهد، پاداشي نيکوتر خواهد داشت؛ و ما دستور آساني به او خواهيم داد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2229, N'سپس (بار ديگر) از اسبابي (که در اختيار داشت) بهره گرفت...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2230, N'تا به خاستگاه خورشيد رسيد؛ (در آن جا) ديد خورشيد بر جمعيتي طلوع مي‌کند که در برابر (تابش) آفتاب، پوششي براي آنها قرار نداده بوديم (و هيچ گونه سايباني نداشتند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2231, N'(آري) اينچنين بود (کار ذو القرنين)! و ما بخوبي از امکاناتي که نزد او بود آگاه بوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2232, N'(باز) از اسباب مهمي (که در اختيار داشت) استفاده کرد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2233, N'(و همچنان به راه خود ادامه داد) تا به ميان دو کوه رسيد؛ و در کنار آن دو (کوه) قومي را يافت که هيچ سخني را نمي‌فهميدند (و زبانشان مخصوص خودشان بود)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2234, N'(آن گروه به او) گفتند: «اي ذو القرنين يأجوج و مأجوج در اين سرزمين فساد مي‌کنند؛ آيا ممکن است ما هزينه‌اي براي تو قرار دهيم، که ميان ما و آنها سدي ايجاد کني؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2235, N'(ذو القرنين) گفت: «آنچه پروردگارم در اختيار من گذارده، بهتر است (از آنچه شما پيشنهاد مي‌کنيد)! مرا با نيرويي ياري دهيد، تا ميان شما و آنها سد محکمي قرار دهم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2236, N'قطعات بزرگ آهن برايم بياوريد (و آنها را روي هم بچينيد)!» تا وقتي که کاملا ميان دو کوه را پوشانيد، گفت: «(در اطراف آن آتش بيفروزيد، و) در آن بدميد!» (آنها دميدند) تا قطعات آهن را سرخ و گداخته کرد، و گفت: «(اکنون) مس مذاب برايم بياوريد تا بر روي آن بريزم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2237, N'(سرانجام چنان سد نيرومندي ساخت) که آنها [= طايفه يأجوج و مأجوج‌] قادر نبودند از آن بالا روند؛ و نمي‌توانستند نقبي در آن ايجاد کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2238, N'(آنگاه) گفت: «اين از رحمت پروردگار من است! اما هنگامي که وعده پروردگارم فرا رسد، آن را در هم مي‌کوبد؛ و وعده پروردگارم حق است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2239, N'و در آن روز (که جهان پايان مي‌گيرد)، ما آنان را چنان رها مي‌کنيم که درهم موج مي‌زنند؛ و در صور [= شيپور] دميده مي‌شود؛ و ما همه را جمع مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2240, N'در آن روز، جهنم را بر کافران عرضه مي‌داريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2241, N'همانها که پرده‌اي چشمانشان را از ياد من پوشانده بود، و قدرت شنوايي نداشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2242, N'آيا کافران پنداشتند مي‌توانند بندگانم را به جاي من اولياي خود انتخاب کنند؟! ما جهنم را براي پذيرايي کافران آماده کرده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2243, N'بگو: «آيا به شما خبر دهيم که زيانکارترين (مردم) در کارها، چه کساني هستند؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2244, N'آنها که تلاشهايشان در زندگي دنيا گم (و نابود) شده؛ با اين حال، مي‌پندارند کار نيک انجام مي‌دهند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2245, N'آنها کساني هستند که به آيات پروردگارشان و لقاي او کافر شدند؛ به همين جهت، اعمالشان حبط و نابود شد! از اين رو روز قيامت، ميزاني براي آنها برپا نخواهيم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2246, N'(آري،) اين گونه است! کيفرشان دوزخ است، بخاطر آنکه کافر شدند، و آيات من و پيامبرانم را به سخريه گرفتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2247, N'اما کساني که ايمان آوردند و کارهاي شايسته انجام دادند، باغهاي بهشت برين محل پذيرايي آنان خواهد بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2248, N'آنها جاودانه در آن خواهند ماند؛ و هرگز تقاضاي نقل مکان از آن جا نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2249, N'بگو: «اگر درياها براي (نوشتن) کلمات پروردگارم مرکب شود، درياها پايان مي‌گيرد. پيش از آنکه کلمات پروردگارم پايان يابد؛ هر چند همانند آن (درياها) را کمک آن قرار دهيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2250, N'بگو: «من فقط بشري هستم مثل شما؛ (امتيازم اين است که) به من وحي مي‌شود که تنها معبودتان معبود يگانه است؛ پس هر که به لقاي پروردگارش اميد دارد، بايد کاري شايسته انجام دهد، و هيچ کس را در عبادت پروردگارش شريک نکند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2251, N'کهيعص‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2252, N'(اين) يادي است از رحمت پروردگار تو نسبت به بنده‌اش زکريا...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2253, N'در آن هنگام که پروردگارش را در خلوتگاه (عبادت) پنهان خواند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2254, N'گفت: «پروردگارا! استخوانم سست شده؛ و شعله پيري تمام سرم را فراگرفته؛ و من هرگز در دعاي تو، از اجابت محروم نبوده‌ام!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2255, N'و من از بستگانم بعد از خودم بيمناکم (که حق پاسداري از آيين تو را نگاه ندارند)! و (از طرفي) همسرم نازا و عقيم است؛ تو از نزد خود جانشيني به من ببخش...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2256, N'که وارث من و دودمان يعقوب باشد؛ و او را مورد رضايتت قرار ده!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2257, N'اي زکريا! ما تو را به فرزندي بشارت مي‌دهيم که نامش «يحيي» است؛ و پيش از اين، همنامي براي او قرار نداده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2258, N'گفت: «پروردگارا! چگونه براي من فرزندي خواهد بود؟! در حالي که همسرم نازا و عقيم است، و من نيز از شدت پيري افتاده شده‌ام!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2259, N'فرمود: «پروردگارت اين گونه گفته (و اراده کرده)! اين بر من آسان است؛ و قبلا تو را آفريدم در حالي که چيزي نبودي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2260, N'عرض کرد: «پروردگارا! نشانه‌اي براي من قرار ده!» فرمود: «نشانه تو اين است که سه شبانه روز قدرت تکلم (با مردم) نخواهي داشت؛ در حالي که زبانت سالم است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2261, N'او از محراب عبادتش به سوي مردم بيرون آمد؛ و با اشاره به آنها گفت: «(بشکرانه اين موهبت،) صبح و شام خدا را تسبيح گوييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2262, N'اي يحيي! کتاب (خدا) را با قوت بگير! و ما فرمان نبوت (و عقل کافي) در کودکي به او داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2263, N'و رحمت و محبتي از ناحيه خود به او بخشيديم، و پاکي (دل و جان)! و او پرهيزگار بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2264, N'او نسبت به پدر و مادرش نيکوکار بود؛ و جبار (و متکبر) و عصيانگر نبود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2265, N'سلام بر او، آن روز که تولد يافت، و آن روز که مي‌ميرد، و آن روز که زنده برانگيخته مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2266, N'و در اين کتاب (آسماني)، مريم را ياد کن، آن هنگام که از خانواده‌اش جدا شد، و در ناحيه شرقي (بيت المقدس) قرار گرفت؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2267, N'و ميان خود و آنان حجابي افکند (تا خلوتگاهش از هر نظر براي عبادت آماده باشد). در اين هنگام، ما روح خود را بسوي او فرستاديم؛ و او در شکل انساني بي‌عيب و نقص، بر مريم ظاهر شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2268, N'او (سخت ترسيد و) گفت: «من از شر تو، به خداي رحمان پناه مي‌برم اگر پرهيزگاري!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2269, N'گفت: «من فرستاده پروردگار توام؛ (آمده‌ام) تا پسر پاکيزه‌اي به تو ببخشم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2270, N'گفت: «چگونه ممکن است فرزندي براي من باشد؟! در حالي که تاکنون انساني با من تماس نداشته، و زن آلوده‌اي هم نبوده‌ام!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2271, N'گفت: «مطلب همين است! پروردگارت فرموده: اين کار بر من آسان است! (ما او را مي‌آفرينيم، تا قدرت خويش را آشکار سازيم؛) و او را براي مردم نشانه‌اي قرار دهيم؛ و رحمتي باشد از سوي ما! و اين امري است پايان يافته (و جاي گفتگو ندارد)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2272, N'سرانجام (مريم) به او باردار شد؛ و او را به نقطه دور دستي برد (و خلوت گزيد)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2273, N'درد زايمان او را به کنار تنه درخت خرمايي کشاند؛ (آنقدر ناراحت شد که) گفت: «اي کاش پيش از اين مرده بودم، و بکلي فراموش مي‌شدم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2274, N'ناگهان از طرف پايين پايش او را صدا زد که: «غمگين مباش! پروردگارت زير پاي تو چشمه آبي (گوارا) قرار داده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2275, N'و اين تنه نخل را به طرف خود تکان ده، رطب تازه‌اي بر تو فرو مي‌ريزد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2276, N'(از اين غذاي لذيذ) بخور؛ و (از آن آب گوارا) بنوش؛ و چشمت را (به اين مولود جديد) روشن دار! و هرگاه کسي از انسانها را ديدي، (با اشاره) بگو: من براي خداوند رحمان روزه‌اي نذر کرده‌ام؛ بنابراين امروز با هيچ انساني هيچ سخن نمي‌گويم! (و بدان که اين نوزاد، خودش از تو دفاع خواهد کرد!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2277, N'(مريم) در حالي که او را در آغوش گرفته بود، نزد قومش آورد؛ گفتند: «اي مريم! کار بسيار عجيب و بدي انجام دادي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2278, N'اي خواهر هارون! نه پدرت مرد بدي بود، و نه مادرت زن بد کاره‌اي!!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2279, N'(مريم) به او اشاره کرد؛ گفتند: «چگونه با کودکي که در گاهواره است سخن بگوييم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2280, N'(ناگهان عيسي زبان به سخن گشود و) گفت: «من بنده خدايم؛ او کتاب (آسماني) به من داده؛ و مرا پيامبر قرار داده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2281, N'و مرا -هر جا که باشم- وجودي پربرکت قرار داده؛ و تا زماني که زنده‌ام، مرا به نماز و زکات توصيه کرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2282, N'و مرا نسبت به مادرم نيکوکار قرار داده؛ و جبار و شقي قرار نداده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2283, N'و سلام (خدا) بر من، در آن روز که متولد شدم، و در آن روز که مي‌ميرم، و آن روز که زنده برانگيخته خواهم شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2284, N'اين است عيسي پسر مريم؛ گفتار حقي که در آن ترديد مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2285, N'هرگز براي خدا شايسته نبود که فرزندي اختيار کند! منزه است او! هرگاه چيزي را فرمان دهد، مي‌گويد: «موجود باش!» همان دم موجود مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2286, N'و خداوند، پروردگار من و شماست! او را پرستش کنيد؛ اين است راه راست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2287, N'ولي (بعد از او) گروه‌هايي از ميان پيروانش اختلاف کردند؛ واي به حال کافران از مشاهده روز بزرگ (رستاخيز)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2288, N'در آن روز که نزد ما مي‌آيند، چه گوشهاي شنوا و چه چشمهاي بينايي پيدا مي‌کنند! ولي اين ستمگران امروز در گمراهي آشکارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2289, N'آنان را از روز حسرت [= روز رستاخيز که براي همه مايه تأسف است‌] بترسان، در آن هنگام که همه چيز پايان مي‌يابد! و آنها در غفلتند و ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2290, N'ما، زمين و تمام کساني را که بر آن هستند، به ارث مي‌بريم؛ و همگي بسوي ما بازگردانده مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2291, N'در اين کتاب، ابراهيم را ياد کن، که او بسيار راستگو، و پيامبر (خدا) بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2292, N'هنگامي که به پدرش گفت: «اي پدر! چرا چيزي را مي‌پرستي که نه مي‌شنود، و نه مي‌بيند، و نه هيچ مشکلي را از تو حل مي‌کند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2293, N'اي پدر! دانشي براي من آمده که براي تو نيامده است؛ بنابر اين از من پيروي کن، تا تو را به راه راست هدايت کنم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2294, N'اي پدر! شيطان را پرستش مکن، که شيطان نسبت به خداوند رحمان، عصيانگر بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2295, N'اي پدر! من از اين مي‌ترسم که از سوي خداوند رحمان عذابي به تو رسد، در نتيجه از دوستان شيطان باشي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2296, N'گفت: «اي ابراهيم! آيا تو از معبودهاي من روي گرداني؟! اگر (از اين کار) دست برنداري، تو را سنگسار مي‌کنم! و براي مدتي طولاني از من دور شو!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2297, N'(ابراهيم) گفت: «سلام بر تو! من بزودي از پروردگارم برايت تقاضاي عفو مي‌کنم؛ چرا که او همواره نسبت به من مهربان بوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2298, N'و از شما، و آنچه غير خدا مي‌خوانيد، کناره‌گيري مي‌کنم؛ و پروردگارم را مي‌خوانم؛ و اميدوارم در خواندن پروردگارم بي‌پاسخ نمانم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2299, N'هنگامي که از آنان و آنچه غير خدا مي‌پرستيدند کناره‌گيري کرد، ما اسحاق و يعقوب را به او بخشيديم؛ و هر يک را پيامبري (بزرگ) قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2300, N'و از رحمت خود به آنان عطا کرديم؛ و براي آنها نام نيک و مقام برجسته‌اي (در ميان همه امتها) قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2301, N'و در اين کتاب (آسماني) از موسي ياد کن، که او مخلص بود، و رسول و پيامبري والا مقام!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2302, N'ما او را از طرف راست (کوه) طور فراخوانديم؛ و نجواکنان او را (به خود) نزديک ساختيم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2303, N'و ما از رحمت خود، برادرش هارون را -که پيامبر بود- به او بخشيديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2304, N'و در اين کتاب (آسماني) از اسماعيل (نيز) ياد کن، که او در وعده‌هايش صادق، و رسول و پيامبري (بزرگ) بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2305, N'او همواره خانواده‌اش را به نماز و زکات فرمان مي‌داد؛ و همواره مورد رضايت پروردگارش بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2306, N'و در اين کتاب، از ادريس (نيز) ياد کن، او بسيار راستگو و پيامبر (بزرگي) بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2307, N'و ما او را به مقام والايي رسانديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2308, N'آنها پيامبراني بودند که خداوند مشمول نعمتشان قرار داده بود، از فرزندان آدم، و از کساني که با نوح بر کشتي سوار کرديم، و از دودمان ابراهيم و يعقوب، و از کساني که هدايت کرديم و برگزيديم. آنها کساني بودند که وقتي آيات خداوند رحمان بر آنان خوانده مي‌شد به خاک مي‌افتادند، در حالي که سجده مي‌کردند و گريان بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2309, N'اما پس از آنان، فرزندان ناشايسته‌اي روي کار آمدند که نماز را تباه کردند، و از شهوات پيروي نمودند؛ و بزودي (مجازات) گمراهي خود را خواهند ديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2310, N'مگر آنان که توبه کنند، و ايمان بياورند، و کار شايسته انجام دهند؛ چنين کساني داخل بهشت مي‌شوند، و کمترين ستمي به آنان نخواهد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2311, N'وارد باغهايي جاوداني مي‌شوند که خداوند رحمان بندگانش را به آن وعده داده است؛ هر چند آن را نديده‌اند؛ مسلما وعده خدا تحقق يافتني است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2312, N'در آن جا هرگز گفتار لغو و بيهوده‌اي نمي‌شنوند؛ و جز سلام در آن جا سخني نيست؛ و هر صبح و شام، روزي آنان در بهشت مقرر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2313, N'اين همان بهشتي است که به بندگان پرهيزگار خود، به ارث مي‌دهيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2314, N'(پس از تأخير وحي، جبرئيل به پيامبر عرض کرد:) ما جز بفرمان پروردگار تو، نازل نمي‌شويم؛ آنچه پيش روي ما، و پشت سر ما، و آنچه ميان اين دو مي‌باشد، همه از آن اوست؛ و پروردگارت هرگز فراموشکار نبوده (و نيست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2315, N'همان پروردگار آسمانها و زمين، و آنچه ميان آن دو قرار دارد! او را پرستش کن؛ و در راه عبادتش شکيبا باش! آيا مثل و مانندي براي او مي‌يابي؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2316, N'انسان مي‌گويد: «آيا پس از مردن، زنده (از قبر) بيرون خواهم آمد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2317, N'آيا انسان به خاطر نمي‌آورد که ما پيش از اين او را آفريديم در حالي که چيزي نبود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2318, N'سوگند به پروردگارت که همه آنها را همراه با شياطين در قيامت جمع مي‌کنيم؛ سپس همه را -در حالي که به زانو درآمده‌اند- گرداگرد جهنم حاضر مي‌سازيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2319, N'سپس از هر گروه و جمعيتي، کساني را که در برابر خداوند رحمان از همه سرکش تر بوده‌اند، جدا مي‌کنيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2320, N'بعد از آن، ما بخوبي از کساني که براي سوختن در آتش سزاوارترند، آگاهتريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2321, N'و همه شما (بدون استثنا) وارد جهنم مي‌شويد؛ اين امري است حتمي و قطعي بر پروردگارت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2322, N'سپس آنها را که تقوا پيشه کردند از آن رهايي مي‌بخشيم؛ و ظالمان را -در حالي که (از ضعف و ذلت) به زانو درآمده‌اند- در آن رها مي‌سازيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2323, N'و هنگامي که آيات روشن ما بر آنان خوانده مي‌شود، کافران به مؤمنان مي‌گويند: «کدام يک از دو گروه [= ما و شما] جايگاهش بهتر، و جلسات انس و مشورتش زيباتر، و بخشش او بيشتر است؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2324, N'چه بسيار اقوامي را پيش از آنان نابود کرديم که هم مال و ثروتشان از آنها بهتر بود، و هم ظاهرشان آراسته‌تر!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2325, N'بگو: «کسي که در گمراهي است، بايد خداوند به او مهلت دهد تا زماني که وعده الهي را با چشم خود ببينند: يا عذاب (اين دنيا)، يا (عذاب) قيامت! (آن روز) خواهند دانست چه کسي جايش بدتر، و لشکرش ناتوانتر است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2326, N'(اما) کساني که در راه هدايت گام نهادند، خداوند بر هدايتشان مي‌افزايد؛ و آثار شايسته‌اي که (از انسان) باقي مي‌ماند، ثوابش در پيشگاه پروردگارت بهتر، و عاقبتش خوبتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2327, N'آيا ديدي کسي را که به آيات ما کافر شد، و گفت: «اموال و فرزندان فراواني به من داده خواهد شد»؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2328, N'آيا او از غيب آگاه گشته، يا نزد خدا عهد و پيماني گرفته است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2329, N'هرگز چنين نيست! ما بزودي آنچه را مي‌گويد مي‌نويسيم و عذاب را بر او مستمر خواهيم داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2330, N'آنچه را او مي‌گويد (از اموال و فرزندان)، از او به ارث مي‌بريم، و به صورت تنها نزد ما خواهد آمد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2331, N'و آنان غير از خدا، معبوداني را براي خود برگزيدند تا مايه عزتشان باشد! (چه پندار خامي!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2332, N'هرگز چنين نيست! به زودي (معبودها) منکر عبادت آنان خواهند شد؛ (بلکه) بر ضدشان قيام مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2333, N'آيا نديدي که ما شياطين را بسوي کافران فرستاديم تا آنان را شديدا تحريک کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2334, N'پس درباره آنان شتاب مکن؛ ما آنها (و اعمالشان) را به دقت شماره ميکنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2335, N'در آن روز که پرهيزگاران را دسته جمعي بسوي خداوند رحمان (و پاداشهاي او) محشور مي‌کنيم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2336, N'و مجرمان را (همچون شتران تشنه‌کامي که به سوي آبگاه مي‌روند) به جهنم مي‌رانيم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2337, N'آنان هرگز مالک شفاعت نيستند؛ مگر کسي که نزد خداوند رحمان، عهد و پيماني دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2338, N'و گفتند: «خداوند رحمان فرزندي براي خود برگزيده است».')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2339, N'راستي مطلب زشت و زننده‌اي گفتيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2340, N'نزديک است به خاطر اين سخن آسمانها از هم متلاشي گردد، و زمين شکافته شود، و کوه‌ها بشدت فرو ريزد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2341, N'از اين رو که براي خداوند رحمان فرزندي قائل شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2342, N'در حالي که هرگز براي خداوند رحمان سزاوار نيست که فرزندي برگزيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2343, N'تمام کساني که در آسمانها و زمين هستند، بنده اويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2344, N'خداوند همه آنها را احصا کرده، و به دقت شمرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2345, N'و همگي روز رستاخيز، تک و تنها نزد او حاضر مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2346, N'مسلما کساني که ايمان آورده و کارهاي شايسته انجام داده‌اند، خداوند رحمان محبتي براي آنان در دلها قرار مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2347, N'و ما فقط آن [= قرآن‌] را بر زبان تو آسان ساختيم تا پرهيزگاران را بوسيله آن بشارت دهي، و دشمنان سرسخت را با آنان انذار کني.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2348, N'چه بسيار اقوام (بي‌ايمان و گنهکاري) را که پيش از آنان هلاک کرديم؛ آيا احدي از آنها را احساس مي‌کني؟! يا کمترين صدايي از آنان مي‌شنوي؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2349, N'طه‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2350, N'ما قرآن را بر تو نازل نکرديم که خود را به زحمت بيفکني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2351, N'آن را فقط براي يادآوري کساني که (از خدا) مي‌ترسند نازل ساختيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2352, N'(اين قرآن) از سوي کسي نازل شده که زمين و آسمانهاي بلند را آفريده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2353, N'همان بخشنده‌اي که بر عرش مسلط است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2354, N'از آن اوست آنچه در آسمانها، و آنچه در زمين، و آنچه ميان آن دو، و آنچه در زير خاک (پنهان) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2355, N'اگر سخن آشکارا بگويي (يا مخفي کني)، او اسرار -و حتي پنهان‌تر از آن- را نيز مي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2356, N'او خداوندي است که معبودي جز او نيست؛ و نامهاي نيکوتر از آن اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2357, N'و آيا خبر موسي به تو رسيده است؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2358, N'هنگامي که (از دور) آتشي مشاهده کرد، و به خانواده خود گفت: «(اندکي) درنگ کنيد که من آتشي ديدم! شايد شعله‌اي از آن براي شما بياورم؛ يا بوسيله اين آتش راه را پيدا کنم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2359, N'هنگامي که نزد آتش آمد، ندا داده شد که: «اي موسي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2360, N'من پروردگار توام! کفشهايت را بيرون آر، که تو در سرزمين مقدس «طوي» هستي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2361, N'و من تو را (براي مقام رسالت) برگزيدم؛ اکنون به آنچه بر تو وحي مي‌شود، گوش فراده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2362, N'من «الله» هستم؛ معبودي جز من نيست! مرا بپرست، و نماز را براي ياد من بپادار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2363, N'بطور قطع رستاخيز خواهد آمد! مي‌خواهم آن را پنهان کنم، تا هر کس در برابر سعي و کوشش خود، جزا داده شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2364, N'پس مبادا کسي که به آن ايمان ندارد و از هوسهاي خويش پيروي مي‌کند، تو را از آن بازدارد؛ که هلاک خواهي شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2365, N'و آن چيست در دست راست تو، اي موسي؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2366, N'گفت: «اين عصاي من است؛ بر آن تکيه مي‌کنم، برگ درختان را با آن براي گوسفندانم فرومي‌ريزم؛ و مرا با آن کارها و نيازهاي ديگري است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2367, N'گفت: «اي موسي! آن را بيفکن.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2368, N'پس موسي آن (عصا) را افکند، که ناگهان اژدهايي شد که به هر سو مي‌شتافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2369, N'گفت: «آن را بگير و نترس، ما آن را به صورت اولش بازمي‌گردانيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2370, N'و دستت را به گريبانت ببر، تا سفيد و بي‌عيب بيرون آيد؛ اين نشانه ديگري (از سوي خداوند) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2371, N'تا از نشانه‌هاي بزرگ خويش به تو نشان دهيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2372, N'اينک به سوي فرعون برو، که او طغيان کرده است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2373, N'(موسي) گفت: «پروردگارا! سينه‌ام را گشاده کن؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2374, N'و کارم را برايم آسان گردان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2375, N'و گره از زبانم بگشاي؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2376, N'تا سخنان مرا بفهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2377, N'و وزيري از خاندانم براي من قرار ده...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2378, N'برادرم هارون را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2379, N'با او پشتم را محکم کن؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2380, N'و او را در کارم شريک ساز؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2381, N'تا تو را بسيار تسبيح گوييم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2382, N'و تو را بسيار ياد کنيم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2383, N'چرا که تو هميشه از حال ما آگاه بوده‌اي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2384, N'فرمود: «اي موسي! آنچه را خواستي به تو داده شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2385, N'و ما بار ديگر تو را مشمول نعمت خود ساختيم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2386, N'آن زمان که به مادرت آنچه لازم بود الهام کرديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2387, N'که: «او را در صندوقي بيفکن، و آن صندوق را به دريا بينداز، تا دريا آن را به ساحل افکند؛ و دشمن من و دشمن او، آن را برگيرد!» و من محبتي از خودم بر تو افکندم، تا در برابر ديدگان [= علم‌] من، ساخته شوي (و پرورش يابي)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2388, N'در آن هنگام که خواهرت (در نزديکي کاخ فرعون) راه مي‌رفت و مي‌گفت: «آيا کسي را به شما نشان دهم که اين نوزاد را کفالت مي‌کند (و دايه خوبي براي او خواهد بود)!» پس تو را به مادرت بازگردانديم، تا چشمش به تو روشن شود؛ و غمگين نگردد! و تو يکي (از فرعونيان) را کشتي؛ اما ما تو را از اندوه نجات داديم! و بارها تو را آزموديم! پس از آن، سالياني در ميان مردم «مدين» توقف نمودي؛ سپس در زمان مقدر (براي فرمان رسالت) به اين جا آمدي، اي موسي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2389, N'و من تو را براي خودم ساختم (و پرورش دادم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2390, N'(اکنون) تو و برادرت با آيات من برويد، و در ياد من کوتاهي نکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2391, N'بسوي فرعون برويد؛ که طغيان کرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2392, N'اما بنرمي با او سخن بگوييد؛ شايد متذکر شود، يا (از خدا) بترسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2393, N'(موسي و هارون) گفتند: «پروردگارا! از اين مي‌ترسيم که بر ما پيشي گيرد (و قبل از بيان حق، ما را آزار دهد)؛ يا طغيان کند (و نپذيرد)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2394, N'فرمود: «نترسيد! من با شما هستم؛ (همه چيز را) مي‌شنوم و مي‌بينم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2395, N'به سراغ او برويد و بگوييد: «ما فرستادگان پروردگار توئيم! بني اسرائيل را با ما بفرست؛ و آنان را شکنجه و آزار مکن! ما نشانه روشني از سوي پروردگارت براي تو آورده‌ايم! و درود بر آن کس باد که از هدايت پيروي مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2396, N'به ما وحي شده که عذاب بر کسي است که (آيات الهي را) تکذيب کند و سرپيچي نمايد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2397, N'(فرعون) گفت: «پروردگار شما کيست، اي موسي؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2398, N'گفت: «پروردگار ما همان کسي است که به هر موجودي، آنچه را لازمه آفرينش او بوده داده؛ سپس هدايت کرده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2399, N'گفت: «پس تکليف نسلهاي گذشته (که به اينها ايمان نداشتند) چه خواهد شد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2400, N'گفت: «آگاهي مربوط به آنها، نزد پروردگارم در کتابي ثبت است؛ پروردگارم هرگز گمراه نمي‌شود، و فراموش نمي‌کند (و آنچه شايسته آنهاست به ايشان مي‌دهد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2401, N'همان خداوندي که زمين را براي شما محل آسايش قرار داد؛ و راه‌هايي در آن ايجاد کرد؛ و از آسمان، آبي فرستاد!» که با آن، انواع گوناگون گياهان را (از خاک تيره) برآورديم.')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2402, N'هم خودتان بخوريد؛ و هم چهارپايانتان را در آن به چرا بريد! مسلما در اينها نشانه‌هاي روشني براي خردمندان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2403, N'ما شما را از آن [= زمين‌] آفريديم؛ و در آن بازمي‌گردانيم؛ و بار ديگر (در قيامت) شما را از آن بيرون مي‌آوريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2404, N'ما همه آيات خود را به او نشان داديم؛ اما او تکذيب کرد و سرباز زد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2405, N'گفت: «اي موسي! آيا آمده‌اي که با سحر خود، ما را از سرزمينمان بيرون کني؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2406, N'قطعا ما هم سحري همانند آن براي تو خواهيم آورد! هم اکنون (تاريخش را تعيين کن، و) موعدي ميان ما و خودت قرار ده که نه ما و نه تو، از آن تخلف نکنيم؛ آن هم در مکاني که نسبت به همه يکسان باشد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2407, N'گفت: «ميعاد ما و شما روز زينت [= روز عيد] است؛ به شرط اينکه همه مردم ، هنگامي که روز، بالا مي‌آيد، جمع شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2408, N'فرعون آن مجلس را ترک گفت؛ و تمام مکر و فريب خود را جمع کرد؛ و سپس همه را (در روز موعود) آورد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2409, N'موسي به آنان گفت: «واي بر شما! دروغ بر خدا نبنديد، که شما را با عذابي نابود مي‌سازد! و هر کس که (بر خدا) دروغ ببندد، نوميد (و شکست خورده) مي‌شود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2410, N'آنها در ميان خود، در مورد ادامه راهشان به نزاع برخاستند؛ و مخفيانه و درگوشي با هم سخن گفتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2411, N'گفتند: «اين دو (نفر) مسلما ساحرند! مي‌خواهند با سحرشان شما را از سرزمينتان بيرون کنند و راه و رسم نمونه شما را از بين ببرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2412, N'اکنون که چنين است، تمام نيرو و نقشه خود را جمع کنيد، و در يک صف (به ميدان مبارزه) بياييد؛ امروز رستگاري از آن کسي است که برتري خود را اثبات کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2413, N'(ساحران) گفتند: «اي موسي! آيا تو اول (عصاي خود را) مي‌افکني، يا ما کساني باشيم که اول بيفکنيم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2414, N'گفت: «شما اول بيفکنيد!» در اين هنگام طنابها و عصاهاي آنان بر اثر سحرشان چنان به نظر مي‌رسيد که حرکت مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2415, N'موسي ترس خفيفي در دل احساس کرد (مبادا مردم گمراه شوند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2416, N'گفتيم: «نترس! تو مسلما (پيروز و) برتري!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2417, N'و آنچه را در دست راست داري بيفکن، تمام آنچه را ساخته‌اند مي‌بلعد! آنچه ساخته‌اند تنها مکر ساحر است؛ و ساحر هر جا رود رستگار نخواهد شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2418, N'(موسي عصاي خود را افکند، و آنچه را که آنها ساخته بودند بلعيد.) ساحران همگي به سجده افتادند و گفتند: «ما به پروردگار هارون و موسي ايمان آورديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2419, N'(فرعون) گفت: «آيا پيش از آنکه به شما اذن دهم به او ايمان آورديد؟! مسلما او بزرگ شماست که به شما سحر آموخته است! به يقين دستها و پاهايتان را بطور مخالف قطع مي‌کنم؛ و شما را از تنه‌هاي نخل به دار مي‌آويزم؛ و خواهيد دانست مجازات کدام يک از ما دردناکتر و پايدارتر است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2420, N'گفتند: «سوگند به آن کسي که ما را آفريده، هرگز تو را بر دلايل روشني که براي ما آمده، مقدم نخواهيم داشت! هر حکمي مي‌خواهي بکن؛ تو تنها در اين زندگي دنيا مي‌تواني حکم کني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2421, N'ما به پروردگارمان ايمان آورديم تا گناهانمان و آنچه را از سحر بر ما تحميل کردي ببخشايد؛ و خدا بهتر و پايدارتر است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2422, N'هر کس در محضر پروردگارش خطاکار حاضر شود، آتش دوزخ براي اوست؛ در آن جا، نه مي‌ميرد و نه زندگي مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2423, N'و هر کس با ايمان نزد او آيد، و اعمال صالح انجام داده باشد، چنين کساني درجات عالي دارند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2424, N'باغهاي جاويدان بهشت، که نهرها از زير درختانش جاري است، در حالي که هميشه در آن خواهند بود؛ اين است پاداش کسي که خود را پاک نمايد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2425, N'ما به موسي وحي فرستاديم که: «شبانه بندگانم را (از مصر) با خود ببر؛ و براي آنها راهي خشک در دريا بگشا؛ که نه از تعقيب (فرعونيان) خواهي ترسيد، و نه از غرق شدن در دريا!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2426, N'(به اين ترتيب) فرعون با لشکريانش آنها را دنبال کردند؛ و دريا آنان را (در ميان امواج خروشان خود) بطور کامل پوشانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2427, N'فرعون قوم خود را گمراه ساخت؛ و هرگز هدايت نکرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2428, N'اي بني اسرائيل! ما شما را از چنگال دشمنتان نجات داديم؛ و در طرف راست کوه طور، با شما وعده گذارديم؛ و «من» و «سلوي» بر شما نازل کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2429, N'بخوريد از روزيهاي پاکيزه‌اي که به شما داده‌ايم؛ و در آن طغيان نکنيد، که غضب من بر شما وارد شود و هر کس غضبم بر او وارد شود، سقوب مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2430, N'و من هر که را توبه کند، و ايمان آورد، و عمل صالح انجام دهد، سپس هدايت شود، مي‌آمرزم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2431, N'اي موسي! چه چيز سبب شد که از قومت پيشي گيري، و (براي آمدن به کوه طور)عجله کني؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2432, N'عرض کرد: «پروردگارا! آنان در پي منند؛ و من به سوي تو شتاب کردم، تا از من خشنود شوي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2433, N'فرمود: «ما قوم تو را بعد از تو، آزموديم و سامري آنها را گمراه ساخت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2434, N'موسي خشمگين و اندوهناک به سوي قوم خود بازگشت و گفت: «اي قوم من! مگر پروردگارتان وعده نيکويي به شما نداد؟! آيا مدت جدايي من از شما به طول انجاميد، يا مي‌خواستيد غضب پروردگارتان بر شما نازل شود که با وعده من مخالفت کرديد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2435, N'گفتند: «ما به ميل و اراده خود از وعده تو تخلف نکرديم؛ بلکه مقداري از زيورهاي قوم را که با خود داشتيم افکنديم!» و سامري اينچنين القا کرد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2436, N'و براي آنان مجسمه گوساله‌اي که صدايي همچون صداي گوساله (واقعي) داشت پديد آورد؛ و (به يکديگر) گفتند: «اين خداي شما، و خداي موسي است!» و او فراموش کرد (پيماني را که با خدا بسته بود)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2437, N'آيا نمي‌بينند که (اين گوساله) هيچ پاسخي به آنان نمي‌دهد، و مالک هيچ گونه سود و زياني براي آنها نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2438, N'و پيش از آن، هارون به آنها گفته بود: «اي قوم من! شما به اين وسيله مورد آزمايش قرار گرفته‌ايد! پروردگار شما خداوند رحمان است! پس، از من پيروي کنيد، و فرمانم را اطاعت نماييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2439, N'ولي آنها گفتند: «ما همچنان گرد آن مي‌گرديم (و به پرستش گوساله ادامه مي‌دهيم) تا موسي به سوي ما بازگردد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2440, N'(موسي) گفت: «اي هارون! چرا هنگامي که ديدي آنها گمراه شدند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2441, N'از من پيروي نکردي؟! آيا فرمان مرا عصيان نمودي؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2442, N'(هارون) گفت: «اي فرزند مادرم! [= اي برادر!] ريش و سر مرا مگير! من ترسيدم بگويي تو ميان بني اسرائيل تفرقه انداختي، و سفارش مرا به کار نبستي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2443, N'(موسي رو به سامري کرد و) گفت: «تو چرا اين کار را کردي، اي سامري؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2444, N'گفت: «من چيزي ديدم که آنها نديدند؛ من قسمتي از آثار رسول (و فرستاده خدا) را گرفتم، سپس آن را افکندم، و اينچنين (هواي) نفس من اين کار را در نظرم جلوه داد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2445, N'(موسي) گفت: «برو، که بهره تو در زندگي دنيا اين است که (هر کس با تو نزديک شود) بگوئي «با من تماس نگير!» و تو ميعادي (از عذاب خدا) داري، که هرگز تخلف نخواهد شد! (اکنون) بنگر به اين معبودت که پيوسته آن را پرستش مي‌کردي! و ببين ما آن را نخست مي‌سوزانيم؛ سپس ذرات آن را به دريا مي‌پاشيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2446, N'معبود شما تنها خداوندي است که جز او معبودي نيست؛ و علم او همه چيز را فرا گرفته است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2447, N'اين گونه بخشي از اخبار پيشين را براي تو بازگو مي‌کنيم؛ و ما از نزد خود، ذکر (و قرآني) به تو داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2448, N'هر کس از آن روي گردان شود، روز قيامت بار سنگيني (از گناه و مسؤوليت) بر دوش خواهد داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2449, N'در حالي که جاودانه در آن خواهند ماند؛ و بد باري است براي آنها در روز قيامت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2450, N'همان روزي که در «صور» دميده مي‌شود؛ و مجرمان را با بدنهاي کبود، در آن روز جمع مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2451, N'آنها آهسته با هم گفتگو مي‌کنند؛ (بعضي مي‌گويند:) شما فقط ده (شبانه روز در عالم برزخ) توقف کرديد! (و نمي‌دانند چقدر طولاني بوده است!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2452, N'ما به آنچه آنها مي‌گويند آگاهتريم، هنگامي که نيکوروش‌ترين آنها مي‌گويد: «شما تنها يک روز درنگ کرديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2453, N'و از تو درباره کوه‌ها سؤال مي‌کنند؛ بگو: «پروردگارم آنها را (متلاشي کرده) برباد مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2454, N'سپس زمين را صاف و هموار و بي‌آب و گياه رها مي‌سازد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2455, N'به گونه‌اي که در آن، هيچ پستي و بلندي نمي‌بيني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2456, N'در آن روز، همه از دعوت کننده الهي پيروي نموده، و قدرت بر مخالفت او نخواهند داشت (و همگي از قبرها برمي‌خيزند)؛ و همه صداها در برابر (عظمت) خداوند رحمان، خاضع مي‌شود؛ و جز صداي آهسته چيزي نمي‌شنوي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2457, N'در آن روز، شفاعت هيچ کس سودي نمي‌بخشد، جز کسي که خداوند رحمان به او اجازه داده، و به گفتار او راضي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2458, N'آنچه را پيش رو دارند، و آنچه را (در دنيا) پشت سرگذاشته‌اند مي‌داند؛ ولي آنها به (علم) او احاطه ندارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2459, N'و (در آن روز) همه چهره‌ها در برابر خداوند حي قيوم، خاضع مي‌شود؛ و مأيوس (و زيانکار) است آن که بار ستمي بر دوش دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2460, N'(اما) آن کس که کارهاي شايسته انجام دهد، در حالي که مؤمن باشد، نه از ظلمي مي‌ترسد، و نه از نقصان حقش.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2461, N'و اين گونه آن را قرآني عربي [= فصيح و گويا] نازل کرديم، و انواع وعيدها (و انذارها) را در آن بازگو نموديم، شايد تقوا پيشه کنند؛ يا براي آنان تذکري پديد آورد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2462, N'پس بلندمرتبه است خداوندي که سلطان حق است! پس نسبت به (تلاوت) قرآن عجله مکن، پيش از آنکه وحي آن بر تو تمام شود؛ و بگو: «پروردگارا! علم مرا افزون کن!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2463, N'پيش از اين، از آدم پيمان گرفته بوديم؛ اما او فراموش کرد؛ و عزم استواري براي او نيافتيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2464, N'و به ياد آور هنگامي را که به فرشتگان گفتيم: «براي آدم سجده کنيد!» همگي سجده کردند؛ جز ابليس که سرباز زد (و سجده نکرد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2465, N'پس گفتيم: «اي آدم! اين (ابليس) دشمن تو و (دشمن) همسر توست! مبادا شما را از بهشت بيرون کند؛ که به زحمت و رنج خواهي افتاد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2466, N'(اما تو در بهشت راحت هستي! و مزيتش) براي تو اين است که در آن گرسنه و برهنه نخواهي شد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2467, N'و در آن تشنه نمي‌شوي، و حرارت آفتاب آزارت نمي‌دهد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2468, N'ولي شيطان او را وسوسه کرد و گفت: «اي آدم! آيا مي‌خواهي تو را به درخت زندگي جاويد، و ملکي بي‌زوال راهنمايي کنم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2469, N'سرانجام هر دو از آن خوردند، (و لباس بهشتيشان فرو ريخت،) و عورتشان آشکار گشت و براي پوشاندن خود، از برگهاي (درختان) بهشتي جامه دوختند! (آري) آدم پروردگارش را نافرماني کرد، و از پاداش او محروم شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2470, N'سپس پروردگارش او را برگزيد، و توبه‌اش را پذيرفت، و هدايتش نمود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2471, N'(خداوند) فرمود: «هر دو از آن (بهشت) فرود آييد، در حالي که دشمن يکديگر خواهيد بود! ولي هرگاه هدايت من به سراغ شما آيد، هر کس از هدايت من پيروي کند، نه گمراه مي‌شود، و نه در رنج خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2472, N'و هر کس از ياد من روي گردان شود، زندگي (سخت و) تنگي خواهد داشت؛ و روز قيامت، او را نابينا محشور مي‌کنيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2473, N'مي‌گويد: «پروردگارا! چرا نابينا محشورم کردي؟! من که بينا بودم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2474, N'مي‌فرمايد: «آن گونه که آيات من براي تو آمد، و تو آنها را فراموش کردي؛ امروز نيز تو فراموش خواهي شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2475, N'و اين گونه جزا مي‌دهيم کسي را که اسراف کند، و به آيات پروردگارش ايمان نياورد! و عذاب آخرت، شديدتر و پايدارتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2476, N'آيا براي هدايت آنان کافي نيست که بسياري از نسلهاي پيشين را (که طغيان و فساد کردند) هلاک نموديم، و اينها در مسکنهاي (ويران شده) آنان راه مي‌روند! مسلما در اين امر، نشانه‌هاي روشني براي خردمندان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2477, N'و اگر سنت و تقدير پروردگارت و ملاحظه زمان مقرر نبود، عذاب الهي بزودي دامان آنان را مي‌گرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2478, N'پس در برابر آنچه مي‌گويند، صبر کن! و پيش از طلوع آفتاب، و قبل از غروب آن؛ تسبيح و حمد پروردگارت را بجا آور؛ و همچنين (برخي) از ساعات شب و اطراف روز (پروردگارت را) تسبيح گوي؛ باشد که (از الطاف الهي) خشنود شوي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2479, N'و هرگز چشمان خود را به نعمتهاي مادي، که به گروه‌هايي از آنان داده‌ايم، ميفکن! اينها شکوفه‌هاي زندگي دنياست؛ تا آنان را در آن بيازماييم؛ و روزي پروردگارت بهتر و پايدارتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2480, N'خانواده خود را به نماز فرمان ده؛ و بر انجام آن شکيبا باش! از تو روزي نمي‌خواهيم؛ (بلکه) ما به تو روزي مي‌دهيم؛ و عاقبت نيک براي تقواست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2481, N'گفتند: «چرا (پيامبر) معجزه و نشانه‌اي از سوي پروردگارش براي ما نمي‌آورد؟! (بگو:) آيا خبرهاي روشني که در کتابهاي (آسماني) نخستين بوده، براي آنها نيامد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2482, N'اگر ما آنان را پيش از آن (که قرآن نازل شود) با عذابي هلاک مي‌کرديم، (در قيامت) مي‌گفتند: «پروردگارا! چرا پيامبري براي ما نفرستادي تا از آيات تو پيروي کنيم، پيش از آنکه ذليل و رسوا شويم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2483, N'بگو: «همه (ما و شما) در انتظاريم! (ما در انتظار وعده پيروزي، و شما در انتظار شکست ما!) حال که چنين است، انتظار بکشيد! اما بزودي مي‌دانيد چه کسي از اصحاب صراط مستقيم، و چه کسي هدايت يافته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2484, N'حساب مردم به آنان نزديک شده، در حالي که در غفلتند و روي گردانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2485, N'هيچ يادآوري تازه‌اي از طرف پروردگارشان براي آنها نمي‌آيد، مگر آنکه با بازي (و شوخي) به آن گوش مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2486, N'اين در حالي است که دلهايشان در لهو و بي‌خبري فرو رفته است! و ستمگران پنهاني نجوا کردند (و گفتند): «آيا جز اين است که او بشري همانند شماست؟! آيا به سراغ سحر مي‌رويد، با اينکه (چشم داريد و) مي‌بينيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2487, N'(پيامبر) گفت: «پروردگارم همه سخنان را، چه در آسمان باشد و چه در زمين، مي‌داند؛ و او شنوا و داناست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2488, N'آنها گفتند: «(آنچه محمد (ص) آورده وحي نيست؛) بلکه خوابهايي آشفته است! اصلا آن را بدروغ به خدا بسته؛ نه، بلکه او يک شاعر است! (اگر راست مي‌گويد) بايد معجزه‌اي براي ما بياورد؛ همان گونه که پيامبران پيشين (با معجزات) فرستاده شدند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2489, N'تمام آباديهايي که پيش از اينها هلاک کرديم (تقاضاي معجزات گوناگون کردند،و خواسته آنان عملي شد، ولي) هرگز ايمان نياوردند؛ آيا اينها ايمان مي‌آورند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2490, N'ما پيش از تو، جز مرداني که به آنان وحي مي‌کرديم، نفرستاديم! (همه انسان بودند، و از جنس بشر!) اگر نمي‌دانيد، از آگاهان بپرسيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2491, N'آنان را پيکرهايي که غذا نخورند قرار نداديم! عمر جاويدان هم نداشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2492, N'سپس وعده‌اي را که به آنان داده بوديم، وفا کرديم! آنها و هر کس را که مي‌خواستيم (از چنگ دشمنانشان) نجات داديم؛ و مسرفان را هلاک نموديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2493, N'ما بر شما کتابي نازل کرديم که وسيله تذکر (و بيداري) شما در آن است! آيا نمي‌فهميد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2494, N'چه بسيار آباديهاي ستمگري را در هم شکستيم؛ و بعد از آنها، قوم ديگري روي کار آورديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2495, N'هنگامي که عذاب ما را احساس کردند، ناگهان پا به فرار گذاشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2496, N'(گفتيم:) فرار نکنيد؛ و به زندگي پر ناز و نعمت، و به مسکنهاي پر زرق و برقتان بازگرديد! شايد (سائلان بيايند و) از شما تقاضا کنند (شما هم آنان را محروم بازگردانيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2497, N'گفتند: «اي واي بر ما! به يقين ما ستمگر بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2498, N'و همچنان اين سخن را تکرار مي‌کردند، تا آنها را درو کرده و خاموش ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2499, N'ما آسمان و زمين، و آنچه را در ميان آنهاست از روي بازي نيافريديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2500, N'(بفرض محال) اگر مي‌خواستيم سرگرمي انتخاب کنيم، چيزي متناسب خود انتخاب مي‌کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2501, N'بلکه ما حق را بر سر باطل مي‌کوبيم تا آن را هلاک سازد؛ و اين گونه، باطل محو و نابود مي‌شود! اما واي بر شما از توصيفي که (درباره خدا و هدف آفرينش)مي‌کنيد!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2502, N'از آن اوست آنان که در آسمانها و زمينند! و آنها که نزد اويند [= فرشتگان‌] هيچ گاه از عبادتش استکبار نمي‌ورزند، و هرگز خسته نمي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2503, N'(تمام) شب و روز را تسبيح مي‌گويند؛ و سست نمي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2504, N'آيا آنها خداياني از زمين برگزيدند که (خلق مي‌کنند و) منتشر مي‌سازند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2505, N'اگر در آسمان و زمين، جز «الله» خدايان ديگري بود، فاسد مي‌شدند (و نظام جهان به هم مي‌خورد)! منزه است خداوند پروردگار عرش، از توصيفي که آنها مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2506, N'هيچ کس نمي‌تواند بر کار او خرده بگيرد؛ ولي در کارهاي آنها، جاي سؤال و ايراد است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2507, N'آيا آنها معبوداني جز خدا برگزيدند؟! بگو: «دليلتان را بياوريد! اين سخن کساني است که با من هستند، و سخن کساني [= پيامبراني‌] است که پيش از من بودند!» اما بيشتر آنها حق را نمي‌دانند؛ و به همين دليل (از آن) روي گردانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2508, N'ما پيش از تو هيچ پيامبري را نفرستاديم مگر اينکه به او وحي کرديم که: «معبودي جز من نيست؛ پس تنها مرا پرستش کنيد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2509, N'آنها گفتند: «خداوند رحمان فرزندي براي خود انتخاب کرده است»! او منزه است (از اين عيب و نقص)؛ آنها [= فرشتگان‌] بندگان شايسته اويند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2510, N'هرگز در سخن بر او پيشي نمي‌گيرند؛ و (پيوسته) به فرمان او عمل مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2511, N'او اعمال امروز و آينده و اعمال گذشته آنها را مي‌داند؛ و آنها جز براي کسي که خدا راضي (به شفاعت براي او) است شفاعت نمي‌کنند؛ و از ترس او بيمناکند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2512, N'و هر کس از آنها بگويد: «من جز خدا، معبودي ديگرم»، کيفر او را جهنم مي‌دهيم! و ستمگران را اين گونه کيفر خواهيم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2513, N'آيا کافران نديدند که آسمانها و زمين به هم پيوسته بودند، و ما آنها را از يکديگر باز کرديم؛ و هر چيز زنده‌اي را از آب قرار داديم؟! آيا ايمان نمي‌آورند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2514, N'و در زمين، کوه‌هاي ثابت و پابرجايي قرار داديم، مبادا آنها را بلرزاند! و در آن، دره‌ها و راه‌هايي قرار داديم تا هدايت شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2515, N'و آسمان را سقف محفوظي قرار داديم؛ ولي آنها از آيات آن روي‌گردانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2516, N'او کسي است که شب و روز و خورشيد و ماه را آفريد؛ هر يک در مداري در حرکتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2517, N'پيش از تو (نيز) براي هيچ انساني جاودانگي قرار نداديم؛ (وانگهي آنها که انتظار مرگ تو را مي‌کشند،) آيا اگر تو بميري، آنان جاويد خواهند بود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2518, N'هر انساني طعم مرگ را مي‌چشد! و شما را با بديها و خوبيها آزمايش مي‌کنيم؛ و سرانجام بسوي ما بازگردانده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2519, N'هنگامي که کافران تو را مي‌بينند، کاري جز استهزا کردن تو ندارند؛ (و مي‌گويند:) آيا اين همان کسي است که سخن از خدايان شما مي‌گويد؟! در حالي که خودشان ذکر خداوند رحمان را انکار مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2520, N'(آري،) انسان از عجله آفريده شده؛ ولي عجله نکنيد؛ بزودي آياتم را به شما نشان خواهم داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2521, N'آنها مي‌گويند: «اگر راست ميگوييد، اين وعده (قيامت) کي فرا مي‌رسد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2522, N'ولي اگر کافران مي‌دانستند زماني که (فرا مي‌رسد) نمي‌توانند شعله‌هاي آتش را از صورت و از پشتهاي خود دور کنند، و هيچ کس آنان را ياري نمي‌کند (اين قدر درباره قيامت شتاب نمي‌کردند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2523, N'(آري، اين مجازات الهي) بطور ناگهاني به سراغشان مي‌آيد و مبهوتشان مي‌کند؛ آنچنان که توانايي دفع آن را ندارند، و به آنها مهلت داده نمي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2524, N'(اگر تو را استهزا کنند نگران نباش،) پيامبران پيش از تو را (نيز) استهزا کردند؛ اما سرانجام، آنچه را استهزا مي‌کردند دامان مسخره‌کنندگان را گرفت (و مجازات الهي آنها را در هم کوبيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2525, N'بگو: «چه کسي شما را در شب و روز از (مجازات) خداوند بخشنده نگاه مي‌دارد؟!» ولي آنان از ياد پروردگارشان روي‌گردانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2526, N'آيا آنها خداياني دارند که مي‌توانند در برابر ما از آنان دفاع کنند؟! (اين خدايان ساختگي، حتي) نمي‌توانند خودشان را ياري دهند (تا چه رسد به ديگران)؛ و نه از ناحيه ما با نيرويي ياري مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2527, N'ما آنها و پدرانشان را (از نعمتها) بهره‌مند ساختيم، تا آنجا که عمر طولاني پيدا کردند (و مايه غرور و طغيانشان شد)؛ آيا نمي‌بينند که ما پيوسته به سراغ زمين آمده، و از آن (و اهلش) مي‌کاهيم؟! آيا آنها غالبند (يا ما)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2528, N'بگو: «من تنها بوسيله وحي شما را انذار مي‌کنم!» ولي آنها که گوشهايشان کر است، هنگامي که انذار مي‌شوند، سخنان را نمي‌شنوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2529, N'اگر کمترين عذاب پروردگارت به آنان برسد، فريادشان بلند مي‌شود که: «اي واي بر ما! ما همگي ستمگر بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2530, N'ما ترازوهاي عدل را در روز قيامت برپا مي‌کنيم؛ پس به هيچ کس کمترين ستمي نمي‌شود؛ و اگر بمقدار سنگيني يک دانه خردل (کار نيک و بدي) باشد، ما آن را حاضر مي‌کنيم؛ و کافي است که ما حساب‌کننده باشيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2531, N'ما به موسي و هارون، «فرقان» [= وسيله جدا کردن حق از باطل‌] و نور، و آنچه مايه يادآوري براي پرهيزگاران است، داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2532, N'همانان که از پروردگارشان در نهان مي‌ترسند، و از قيامت بيم دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2533, N'و اين (قرآن) ذکر مبارکي است که (بر شما) نازل کرديم؛ آيا شما آن را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2534, N'ما وسيله رشد ابراهيم را از قبل به او داديم؛ و از (شايستگي) او آگاه بوديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2535, N'آن هنگام که به پدرش (آزر) و قوم او گفت: «اين مجسمه‌هاي بي‌روح چيست که شما همواره آنها را پرستش مي‌کنيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2536, N'گفتند: «ما پدران خود را ديديم که آنها را عبادت مي‌کنند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2537, N'گفت: «مسلما هم شما و هم پدرانتان، در گمراهي آشکاري بوده‌ايد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2538, N'گفتند: «آيا مطلب حقي براي ما آورده‌اي، يا شوخي مي‌کني؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2539, N'گفت: «(کاملا حق آورده‌ام) پروردگار شما همان پروردگار آسمانها و زمين است که آنها را ايجاد کرده؛ و من بر اين امر، از گواهانم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2540, N'و به خدا سوگند، در غياب شما، نقشه‌اي براي نابودي بتهايتان مي‌کشم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2541, N'سرانجام (با استفاده از يک فرصت مناسب)، همه آنها -جز بت بزرگشان- را قطعه قطعه کرد؛ شايد سراغ او بيايند (و او حقايق را بازگو کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2542, N'(هنگامي که منظره بتها را ديدند،) گفتند: «هر کس با خدايان ما چنين کرده، قطعا از ستمگران است (و بايد کيفر سخت ببيند)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2543, N'(گروهي) گفتند: «شنيديم نوجواني از (مخالفت با) بتها سخن مي‌گفت که او را ابراهيم مي‌گويند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2544, N'(جمعيت) گفتند: «او را در برابر ديدگان مردم بياوريد، تا گواهي دهند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2545, N'(هنگامي که ابراهيم را حاضر کردند،) گفتند: «تو اين کار را با خدايان ما کرده‌اي، اي ابراهيم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2546, N'گفت: «بلکه اين کار را بزرگشان کرده است! از آنها بپرسيد اگر سخن مي‌گويند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2547, N'آنها به وجدان خويش بازگشتند؛ و (به خود) گفتند: «حقا که شما ستمگريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2548, N'سپس بر سرهايشان واژگونه شدند؛ (و حکم وجدان را بکلي فراموش کردند و گفتند:) تو مي‌داني که اينها سخن نمي‌گويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2549, N'(ابراهيم) گفت: «آيا جز خدا چيزي را مي‌پرستيد که نه کمترين سودي براي شما دارد، و نه زياني به شما مي‌رساند! (نه اميدي به سودشان داريد، و نه ترسي از زيانشان!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2550, N'اف بر شما و بر آنچه جز خدا مي‌پرستيد! آيا انديشه نمي‌کنيد (و عقل نداريد)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2551, N'گفتند: «او را بسوزانيد و خدايان خود را ياري کنيد، اگر کاري از شما ساخته است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2552, N'(سرانجام او را به آتش افکندند؛ ولي ما) گفتيم: «اي آتش! بر ابراهيم سرد و سالم باش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2553, N'آنها مي‌خواستند ابراهيم را با اين نقشه نابود کنند؛ ولي ما آنها را زيانکارترين مردم قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2554, N'و او و لوط را به سرزمين (شام) -که آن را براي همه جهانيان پربرکت ساختيم- نجات داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2555, N'و اسحاق، و علاوه بر او، يعقوب را به وي بخشيديم؛ و همه آنان را مرداني صالح قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2556, N'و آنان را پيشواياني قرار داديم که به فرمان ما، (مردم را) هدايت مي‌کردند؛ و انجام کارهاي نيک و برپاداشتن نماز و اداي زکات را به آنها وحي کرديم؛ و تنها ما را عبادت مي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2557, N'و لوط را (به ياد آور) که به او حکومت و علم داديم؛ و از شهري که اعمال زشت و کثيف انجام مي‌دادند، رهايي بخشيديم؛ چرا که آنها مردم بد و فاسقي بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2558, N'و او را در رحمت خود داخل کرديم؛ و او از صالحان بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2559, N'و نوح را (به ياد آور) هنگامي که پيش از آن (زمان، پروردگار خود را) خواند! ما دعاي او را مستجاب کرديم؛ و او و خاندانش را از اندوه بزرگ نجات داديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2560, N'و او را در برابر جمعيتي که آيات ما را تکذيب کرده بودند ياري داديم؛ چرا که قوم بدي بودند؛ از اين رو همه آنها را غرق کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2561, N'و داوود و سليمان را (به خاطر بياور) هنگامي که درباره کشتزاري که گوسفندان بي شبان قوم، شبانگاه در آن چريده (و آن را تباه کرده) بودند، داوري مي‌کردند؛ و ما بر حکم آنان شاهد بوديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2562, N'ما (حکم واقعي) آن را به سليمان فهمانديم؛ و به هر يک از آنان (شايستگي) داوري، و علم فراواني داديم؛ و کوه‌ها و پرندگان را با داوود مسخر ساختيم، که (همراه او) تسبيح (خدا) مي‌گفتند؛ و ما اين کار را انجام داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2563, N'و ساختن زره را بخاطر شما به او تعليم داديم، تا شما را در جنگهايتان حفظ کند؛ آيا شکرگزار (اين نعمتهاي خدا) هستيد؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2564, N'و تندباد را مسخر سليمان ساختيم، که بفرمان او بسوي سرزميني که آن را پربرکت کرده بوديم جريان مي‌يافت؛ و ما از همه چيز آگاه بوده‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2565, N'و گروهي از شياطين (را نيز مسخر او قرار داديم، که در دريا) برايش غواصي مي‌کردند؛ و کارهايي غير از اين (نيز) براي او انجام مي‌دادند؛ و ما آنها را (از سرکشي) حفظ مي‌کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2566, N'و ايوب را (به ياد آور) هنگامي که پروردگارش را خواند (و عرضه داشت): «بدحالي و مشکلات به من روي آورده؛ و تو مهربانترين مهرباناني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2567, N'ما دعاي او را مستجاب کرديم؛ و ناراحتيهايي را که داشت برطرف ساختيم؛ و خاندانش را به او بازگردانديم؛ و همانندشان را بر آنها افزوديم؛ تا رحمتي از سوي ما و تذکري براي عبادت‌کنندگان باشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2568, N'و اسماعيل و ادريس و ذاالکفل را (به ياد آور) که همه از صابران بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2569, N'و ما آنان را در رحمت خود وارد ساختيم؛ چرا که آنها از صالحان بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2570, N'و ذاالنون [= يونس‌] را (به ياد آور) در آن هنگام که خشمگين (از ميان قوم خود) رفت؛ و چنين مي‌پنداشت که ما بر او تنگ نخواهيم گرفت؛ (اما موقعي که در کام نهنگ فرو رفت،) در آن ظلمتها(ي متراکم) صدا زد: «(خداوندا!) جز تو معبودي نيست! منزهي تو! من از ستمکاران بودم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2571, N'ما دعاي او را به اجابت رسانديم؛ و از آن اندوه نجاتش بخشيديم؛ و اين گونه مؤمنان را نجات مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2572, N'و زکريا را (به ياد آور) در آن هنگام که پروردگارش را خواند (و عرض کرد): «پروردگار من! مرا تنها مگذار (و فرزند برومندي به من عطا کن)؛ و تو بهترين وارثاني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2573, N'ما هم دعاي او را پذيرفتيم، و يحيي را به او بخشيديم؛ و همسرش را (که نازا بود) برايش آماده (بارداري) کرديم؛ چرا که آنان (خانداني بودند که) همواره در کارهاي خير بسرعت اقدام مي‌کردند؛ و در حال بيم و اميد ما را مي‌خواندند؛ و پيوسته براي ما (خاضع و) خاشع بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2574, N'و به ياد آور زني را که دامان خود را پاک نگه داشت؛ و ما از روح خود در او دميديم؛ و او و فرزندش [= مسيح‌] را نشانه بزرگي براي جهانيان قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2575, N'اين (پيامبران بزرگ و پيروانشان) همه امت واحدي بودند (و پيرو يک هدف)؛ و من پروردگار شما هستم؛ پس مرا پرستش کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2576, N'(گروهي از پيروان ناآگاه آنها) کار خود را به تفرقه در ميان خود کشاندند؛ (ولي سرانجام) همگي بسوي ما بازمي‌گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2577, N'و هر کس چيزي از اعمال شايسته بجا آورد، در حالي که ايمان داشته باشد، کوشش او ناسپاسي نخواهد شد؛ و ما تمام اعمال او را (براي پاداش) مي‌نويسيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2578, N'و حرام است بر شهرها و آباديهايي که (بر اثر گناه) نابودشان کرديم (که به دنيا بازگردند؛) آنها هرگز باز نخواهند گشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2579, N'تا آن زمان که «يأجوج» و «مأجوج» گشوده شوند؛ و آنها از هر محل مرتفعي بسرعت عبور مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2580, N'و وعده حق [= قيامت‌] نزديک مي‌شود؛ در آن هنگام چشمهاي کافران از وحشت از حرکت بازمي‌ماند؛ (مي‌گويند:) اي واي بر ما که از اين (جريان) در غفلت بوديم؛ بلکه ما ستمکار بوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2581, N'شما و آنچه غير خدا مي‌پرستيد، هيزم جهنم خواهيد بود؛ و همگي در آن وارد مي‌شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2582, N'اگر اينها خداياني بودند، هرگز وارد آن نمي‌شدند! در حالي که همگي در آن جاودانه خواهند بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2583, N'براي آنان در آن [= دوزخ‌] ناله‌هاي دردناکي است و چيزي نمي‌شنوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2584, N'(اما) کساني که از قبل، وعده نيک از سوي ما به آنها داده شده [= مؤمنان صالح‌] از آن دور نگاهداشته مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2585, N'آنها صداي آتش دوزخ را نمي‌شوند؛ و در آنچه دلشان بخواهد، جاودانه متنعم هستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2586, N'وحشت بزرگ، آنها را اندوهگين نمي‌کند؛ و فرشتگان به استقبالشان مي‌آيند، (و مي‌گويند:) اين همان روزي است که به شما وعده داده مي‌شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2587, N'در آن روز که آسمان را چون طوماري در هم مي‌پيچيم، (سپس) همان گونه که آفرينش را آغاز کرديم، آن را بازمي‌گردانيم؛ اين وعده‌اي است بر ما، و قطعا آن را انجام خواهيم داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2588, N'در «زبور» بعد از ذکر (تورات) نوشتيم: «بندگان شايسته‌ام وارث (حکومت) زمين خواهند شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2589, N'در اين، ابلاغ روشني است براي جمعيت عبادت‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2590, N'ما تو را جز براي رحمت جهانيان نفرستاديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2591, N'بگو: «تنها چيزي که به من وحي مي‌شود اين است که معبود شما خداي يگانه است؛ آيا (با اين حال) تسليم (حق) مي‌شويد؟ (و بتها را کنار مي‌گذاريد؟)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2592, N'اگر باز (روي گردان شوند، بگو: «من به همه شما يکسان اعلام خطر مي‌کنم؛ و نمي‌دانم آيا وعده (عذاب خدا) که به شما داده مي‌شود نزديک است يا دور!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2593, N'او سخنان آشکار را مي‌داند، و آنچه را کتمان مي‌کنيد (نيز) مي‌داند (و چيزي بر او پوشيده نيست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2594, N'و من نمي‌دانم شايد اين آزمايشي براي شماست؛ و مايه بهره‌گيري تا مدتي (معين)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2595, N'(و پيامبر) گفت: «پروردگارا! بحق داوري فرما (و اين طغيانگران را کيفر ده)! و پروردگار ما (خداوند) رحمان است که در برابر نسبتهاي نارواي شما، از او استمداد مي‌طلبم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2596, N'اي مردم! از (عذاب) پروردگارتان بترسيد، که زلزله رستاخيز امر عظيمي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2597, N'روزي که آن را مي‌بينيد، (آنچنان وحشت سراپاي همه را فرامي‌گيرد که) هر مادر شيردهي، کودک شيرخوارش را فراموش مي‌کند؛ و هر بارداري جنين خود را بر زمين مي‌نهد؛ و مردم را مست مي‌بيني، در حالي که مست نيستند؛ ولي عذاب خدا شديد است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2598, N'گروهي از مردم، بدون هيچ علم و دانشي، به مجادله درباره خدا برمي‌خيزند؛ و از هر شيطان سرکشي پيروي مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2599, N'بر او نوشته شده که هر کس ولايتش را بر گردن نهد، بطور مسلم گمراهش مي‌سازد، و به آتش سوزان راهنماييش مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2600, N'اي مردم! اگر در رستاخيز شک داريد، (به اين نکته توجه کنيد که:) ما شما را از خاک آفريديم، سپس از نطفه، و بعد از خون بسته شده، سپس از «مضغه» [= چيزي شبيه گوشت جويده شده‌]، که بعضي داراي شکل و خلقت است و بعضي بدون شکل؛ تا براي شما روشن سازيم (که بر هر چيز قادريم)! و جنين‌هايي را که بخواهيم تا مدت معيني در رحم (مادران) قرارمي‌دهيم؛ (و آنچه را بخواهيم ساقظ مي‌کنيم؛)بعد شما را بصورت طفل بيرون مي‌آوريم؛ سپس هدف اين است که به حد رشد و بلوغ خويش برسيد. در اين ميان بعضي از شما مي‌ميرند؛ و بعضي آن قدر عمر مي‌کنند که به بدترين مرحله زندگي (و پيري) مي‌رسند؛ آنچنان که بعد از علم و آگاهي، چيزي نمي‌دانند! (از سوي ديگر،) زمين را (در فصل زمستان) خشک و مرده مي‌بيني، اما هنگامي که آب باران بر آن فرو مي‌فرستيم، به حرکت درمي‌آيد و مي‌رويد؛ و از هر نوع گياهان زيبا مي‌روياند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2601, N'اين بخاطر آن است که (بدانيد) خداوند حق است؛ و اوست که مردگان را زنده مي‌کند؛ و بر هر چيزي تواناست.')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2602, N'و اينکه رستاخيز آمدني است، و شکي در آن نيست؛ و خداوند تمام کساني را که در قبرها هستند زنده مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2603, N'و گروهي از مردم، بدون هيچ دانش و هيچ هدايت و کتاب روشني بخشي، درباره خدا مجادله مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2604, N'آنها با تکبر و بي‌اعتنايي (نسبت به سخنان الهي)، مي‌خواهند مردم را از راه خدا گمراه سازند! براي آنان در دنيا رسوايي است؛ و در قيامت، عذاب سوزان به آنها مي‌چشانيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2605, N'(و به آنان مي‌گوييم:) اين در برابر چيزي است که دستهايتان از پيش براي شما فرستاده؛ و خداوند هرگز به بندگان ظلم نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2606, N'بعضي از مردم خدا را تنها با زبان مي‌پرستند (و ايمان قلبيشان بسيار ضعيف است)؛ همين که (دنيا به آنها رو کند و نفع و) خيري به آنان برسد، حالت اطمينان پيدا مي‌کنند؛ اما اگر مصيبتي براي امتحان به آنها برسد، دگرگون مي‌شوند (و به کفر رومي‌آورند)! (به اين ترتيب) هم دنيا را از دست داده‌اند، و هم آخرت را؛ و اين همان خسران و زيان آشکار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2607, N'او جز خدا کسي را مي‌خواند که نه زياني به او مي‌رساند، و نه سودي؛ اين همان گمراهي بسيار عميق است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2608, N'او کسي را مي‌خواند که زيانش از نفعش نزديکتر است؛ چه بد مولا و ياوري، و چه بد مونس و معاشري!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2609, N'خداوند کساني را که ايمان آورده و اعمال صالح انجام داده‌اند، در باغهايي از بهشت وارد مي‌کند که نهرها زير درختانش جاري است؛ (آري،) خدا هر چه را اراده کند انجام مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2610, N'هر کس گمان مي‌کند که خدا پيامبرش را در دنيا و آخرت ياري نخواهد کرد (و از اين نظر عصباني است، هر کاري از دستش ساخته است بکند)، ريسماني به سقف خانه خود بياويزد، و خود را حلق آويز و نفس خود را قطع کند (و تا لبه پرتگاه مرگ پيش رود)؛ ببيند آيا اين کار خشم او را فرو مي‌نشاند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2611, N'اين گونه ما آن [= قرآن‌] را بصورت آيات روشني نازل کرديم؛ و خداوند هر کس را بخواهد هدايت مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2612, N'مسلما کساني که ايمان آورده‌اند، و يهود و صابئان [= ستاره‌پرستان‌] و نصاري و مجوس و مشرکان، خداوند در ميان آنان روز قيامت داوري مي‌کند؛ (و حق را از باطل جدا مي‌سازد؛) خداوند بر هر چيز گواه (و از همه چيز آگاه) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2613, N'آيا نديدي که تمام کساني که در آسمانها و کساني که در زمينند براي خدا سجده مي‌کنند؟! و (همچنين) خورشيد و ماه و ستارگان و کوه‌ها و درختان و جنبندگان، و بسياري از مردم! اما بسياري (ابا دارند، و) فرمان عذاب درباره آنان حتمي است؛ و هر کس را خدا خوار کند، کسي او را گرامي نخواهد داشت! خداوند هر کار را بخواهد (و صلاح بداند) انجام مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2614, N'اينان دو گروهند که درباره پروردگارشان به مخاصمه و جدال پرداختند؛ کساني که کافر شدند، لباسهايي از آتش براي آنها بريده شده، و مايع سوزان و جوشان بر سرشان ريخته مي‌شود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2615, N'آنچنان که هم درونشان با آن آب مي‌شود، و هم پوستهايشان.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2616, N'و براي آنان گرزهايي از آهن (سوزان) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2617, N'هر گاه بخواهند از غم و اندوه‌هاي دوزخ خارج شوند، آنها را به آن بازمي‌گردانند؛ و (به آنان گفته مي‌شود:) بچشيد عذاب سوزان را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2618, N'خداوند کساني را که ايمان آورده و اعمال صالح انجام داده‌اند، در باغهايي از بهشت وارد مي‌کند که از زير درختانش نهرها جاري است؛ آنان با دستبندهايي از طلا و مرواريد زينت مي‌شوند؛ و در آنجا لباسهايشان از حرير است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2619, N'و بسوي سخنان پاکيزه هدايت مي‌شوند، و به راه خداوند شايسته ستايش، راهنمايي مي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2620, N'کساني که کافر شدند، و مؤمنان را از راه خدا بازداشتند، و (همچنين) از مسجد الحرام، که آن را براي همه مردم، برابر قرار داديم، چه کساني که در آنجا زندگي مي‌کنند يا از نقاط دور وارد مي‌شوند (،مستحق عذابي دردناکند)؛ و هر کس بخواهد در اين سرزمين از راه حق منحرف گردد و دست به ستم زند، ما از عذابي دردناک به او مي‌چشانيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2621, N'(به خاطر بياور) زماني را که جاي خانه (کعبه) را براي ابراهيم آماده ساختيم (تا خانه را بنا کند؛ و به او گفتيم:) چيزي را همتاي من قرار مده! و خانه‌ام را براي طواف‌کنندگان و قيام‌کنندگان و رکوع‌کنندگان و سجودکنندگان (از آلودگي بتها و از هر گونه آلودگي) پاک ساز!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2622, N'و مردم را دعوت عمومي به حج کن؛ تا پياده و سواره بر مرکبهاي لاغر از هر راه دوري بسوي تو بيايند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2623, N'تا شاهد منافع گوناگون خويش (در اين برنامه حياتبخش) باشند؛ و در ايام معيني نام خدا را، بر چهارپاياني که به آنان داده است، (به هنگام قرباني‌کردن) ببرند؛ پس از گوشت آنها بخوريد؛ و بينواي فقير را نيز اطعام نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2624, N'سپس، بايد آلودگيهايشان را برطرف سازند؛ و به نذرهاي خود وفا کنند؛ و بر گرد خانه گرامي کعبه، طواف کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2625, N'(مناسک حج) اين است! و هر کس برنامه‌هاي الهي را بزرگ دارد، نزد پروردگارش براي او بهتر است! و چهارپايان براي شما حلال شده، مگر آنچه (ممنوع بودنش) بر شما خوانده مي‌شود. از پليديهاي بتها اجتناب کنيد! و از سخن باطل بپرهيزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2626, N'(برنامه و مناسک حج را انجام دهيد) در حالي که همگي خالص براي خدا باشد!هيچ گونه همتايي براي او قائل نشويد! و هر کس همتايي براي خدا قرار دهد، گويي از آسمان سقوب کرده، و پرندگان (در وسط هوا) او را مي‌ربايند؛ و يا تندباد او را به جاي دوردستي پرتاب مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2627, N'اين است (مناسک حج)! و هر کس شعائر الهي را بزرگ دارد، اين کار نشانه تقواي دلهاست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2628, N'در آن (حيوانات قرباني)، منافعي براي شماست تا زمان معيني [= روز ذبح آنها] سپس محل آن، خانه قديمي و گرامي (کعبه) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2629, N'براي هر امتي قربانگاهي قرار داديم، تا نام خدا را (به هنگام قرباني) بر چهارپاياني که به آنان روزي داده‌ايم ببرند، و خداي شما معبود واحدي است؛ در برابر (فرمان) او تسليم شويد و بشارت ده متواضعان و تسليم‌شوندگان را.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2630, N'همانها که چون نام خدا برده مي‌شود، دلهايشان پر از خوف (پروردگار) مي‌گردد؛ و شکيبايان در برابر مصيبتهايي که به آنان مي‌رسد؛ و آنها که نماز را برپا مي‌دارند، و از آنچه به آنان روزي داده‌ايم انفاق مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2631, N'و شترهاي چاق و فربه را (در مراسم حج) براي شما از شعائر الهي قرار داديم؛ در آنها براي شما خير و برکت است؛ نام خدا را (هنگام قرباني کردن) در حالي که به صف ايستاده‌اند بر آنها ببريد؛ و هنگامي که پهلوهايشان آرام گرفت (و جان دادند)، از گوشت آنها بخوريد، و مستمندان قانع و فقيران را نيز از آن اطعام کنيد! اين گونه ما آنها را مسخرتان ساختيم، تا شکر خدا را بجا آوريد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2632, N'نه گوشتها و نه خونهاي آنها، هرگز به خدا نمي‌رسد. آنچه به او مي‌رسد، تقوا و پرهيزگاري شماست. اين گونه خداوند آنها را مسخر شما ساخته، تا او را بخاطر آنکه شما را هدايت کرده است بزرگ بشمريد؛ و بشارت ده نيکوکاران را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2633, N'خداوند از کساني که ايمان آورده‌اند دفاع مي‌کند؛ خداوند هيچ خيانتکار ناسپاسي را دوست ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2634, N'به کساني که جنگ بر آنان تحميل گرديده، اجازه جهاد داده شده است؛ چرا که مورد ستم قرار گرفته‌اند؛ و خدا بر ياري آنها تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2635, N'همانها که از خانه و شهر خود، به ناحق رانده شدند، جز اينکه مي‌گفتند: «پروردگار ما، خداي يکتاست!» و اگر خداوند بعضي از مردم را بوسيله بعضي ديگر دفع نکند، ديرها و صومعه‌ها، و معابد يهود و نصارا، و مساجدي که نام خدا در آن بسيار برده مي‌شود، ويران مي‌گردد! و خداوند کساني را که ياري او کنند (و از آيينش دفاع نمايند) ياري مي‌کند؛ خداوند قوي و شکست ناپذير است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2636, N'همان کساني که هر گاه در زمين به آنها قدرت بخشيديم، نماز را برپا مي‌دارند، و زکات مي‌دهند، و امر به معروف و نهي از منکر مي‌کنند، و پايان همه کارها از آن خداست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2637, N'اگر تو را تکذيب کنند، (امر تازه‌اي نيست؛) پيش از آنها قوم نوح و عاد و ثمود (پيامبرانشان را) تکذيب کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2638, N'و همچنين قوم ابراهيم و قوم لوط؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2639, N'و اصحاب مدين (قوم شعيب)؛ و نيز موسي (از سوي فرعونيان) تکذيب شد؛ اما من به کافران مهلت دادم، سپس آنها را مجازات کردم. ديدي چگونه (عمل آنها را)انکار نمودم (و چگونه به آنان پاسخ گفتم)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2640, N'چه بسيار شهرها و آباديهايي که آنها را نابود و هلاک کرديم در حالي که (مردمش) ستمگر بودند، بگونه‌اي که بر سقفهاي خود فروريخت! (نخست سقفها ويران گشت؛ و بعد ديوارها بر روي سقفها!) و چه بسيار چاه پر آب که بي‌صاحب ماند؛ و چه بسيار قصرهاي محکم و مرتفع!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2641, N'آيا آنان در زمين سير نکردند، تا دلهايي داشته باشند که حقيقت را با آن درک کنند؛ يا گوشهاي شنوايي که با آن (نداي حق را) بشنوند؟! چرا که چشمهاي ظاهر نابينا نمي‌شود، بلکه دلهايي که در سينه‌هاست کور مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2642, N'آنان از تو تقاضاي شتاب در عذاب مي‌کنند؛ در حالي که خداوند هرگز از وعده خود تخلف نخواهد کرد! و يک روز نزد پروردگارت، همانند هزار سال از سالهايي است که شما مي‌شمريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2643, N'و چه بسيار شهرها و آباديهايي که به آنها مهلت دادم، در حالي که ستمگر بودند؛ (اما از اين مهلت براي اصلاح خويش استفاده نکردند.) سپس آنها را مجازات کردم؛ و بازگشت، تنها بسوي من است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2644, N'بگو: «اي مردم! من براي شما بيم‌دهنده آشکاري هستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2645, N'آنها که ايمان آوردند و اعمال صالح انجام دادند، آمرزش و روزي پر ارزشي براي آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2646, N'و آنها که در (محو) آيات ما تلاش کردند، و چنين مي‌پنداشتند که مي‌توانند بر اراده حتمي ما غالب شوند، اصحاب دوزخند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2647, N'هيچ پيامبري را پيش از تو نفرستاديم مگر اينکه هرگاه آرزو مي‌کرد (و طرحي براي پيشبرد اهداف الهي خود مي‌ريخت)، شيطان القائاتي در آن مي‌کرد؛ اما خداوند القائات شيطان را از ميان مي‌برد، سپس آيات خود را استحکام مي‌بخشيد؛ و خداوند عليم و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2648, N'هدف اين بود که خداوند القاي شيطان را آزموني قرار دهد براي آنها که در دلهايشان بيماري است، و آنها که سنگدلند؛ و ظالمان در عداوت شديد دور از حق قرار گرفته‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2649, N'و (نيز) هدف اين بود که آگاهان بدانند اين حقي است از سوي پروردگارت، و در نتيجه به آن ايمان بياورند، و دلهايشان در برابر آن خاضع گردد؛ و خداوند کساني را که ايمان آوردند، بسوي صراط مستقيم هدايت مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2650, N'کافران همواره در باره قرآن در شک هستند، تا آنکه روز قيامت بطور ناگهاني فرارسد، يا عذاب روز عقيم [= روزي که قادر بر جبران گذشته نيستند] به سراغشان آيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2651, N'حکومت و فرمانروايي در آن روز از آن خداست؛ و ميان آنها داوري مي‌کند: کساني که ايمان آورده و کارهاي شايسته انجام داده‌اند، در باغهاي پرنعمت بهشتند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2652, N'و کساني که کافر شدند و آيات ما را تکذيب کردند، عذاب خوارکننده‌اي براي آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2653, N'و کساني که در راه خدا هجرت کردند، سپس کشته شدند يا به مرگ طبيعي از دنيا رفتند، خداوند به آنها روزي نيکويي مي‌دهد؛ که او بهترين روزي‌دهندگان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2654, N'خداوند آنان را در محلي وارد مي‌کند که از آن خشنود خواهند بود؛ و خداوند دانا و بردبار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2655, N'(آري،) مطلب چنين است! و هر کس به همان مقدار که به او ستم شده مجازات کند، سپس مورد تعدي قرار گيرد، خدا او را ياري خواهد کرد؛ يقينا خداوند بخشنده و آمرزنده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2656, N'اين (وعده نصرت الهي) بخاطر آن است (که او بر هر چيز قادر است؛ خداوندي) که شب را در روز، و روز را در شب داخل مي‌کند؛ و خداوند شنوا و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2657, N'اين بخاطر آن است که خداوند حق است؛ و آنچه را غير از او مي‌خوانند باطل است؛ و خداوند بلندمقام و بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2658, N'آيا نديدي خداوند از آسمان، آبي فرستاد، و زمين (بر اثر آن) سرسبز و خرم مي‌گردد؟! و خداوند لطيف و آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2659, N'آنچه در آسمانها و آنچه در زمين است از آن اوست؛ و خداوند بي‌نياز، و شايسته هر گونه ستايش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2660, N'آيا نديدي که خداوند آنچه را در زمين است مسخر شما کرد؛ و (نيز) کشتيهايي را که به فرمان او بر صفحه اقيانوسها حرکت مي‌کنند؛ و آسمان [= کرات و سنگهاي آسماني‌] را نگه مي‌دارد، تا جز بفرمان او، بر زمين فرو نيفتند؟ خداوند نسبت به مردم رحيم و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2661, N'و او کسي است که شما را زنده کرد، سپس مي‌ميراند، بار ديگر زنده مي‌کند، اما اين انسان بسيار ناسپاس است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2662, N'براي هر امتي عبادتي قرار داديم، تا آن عبادت را (در پيشگاه خدا) انجام دهند؛ پس نبايد در اين امر با تو به نزاع برخيزند! بسوي پروردگارت دعوت کن، که بر هدايت مستقيم قرار داري (و راه راست همين است که تو مي‌پويي).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2663, N'و اگر آنان با تو به جدال برخيزند، بگو: «خدا از کارهايي که شما انجام مي‌دهيد آگاهتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2664, N'و خداوند در روز قيامت، ميان شما در آنچه اختلاف مي‌کرديد، داوري مي‌کند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2665, N'آيا نمي‌دانستي خداوند آنچه را در آسمان و زمين است مي‌داند؟! همه اينها در کتابي ثبت است (همان کتاب علم بي‌پايان پروردگار)؛ و اين بر خداوند آسان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2666, N'آنها غير از خداوند، چيزهايي را مي‌پرستند که او هيچ گونه دليلي بر آن نازل نکرده است، و چيزهايي را که علم و آگاهي به آن ندارند! و براي ستمگران، ياور و راهنمايي نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2667, N'و هنگامي که آيات روشن ما بر آنان خوانده مي‌شود، در چهره کافران آثار انکار مشاهده مي‌کني، آنچنان که نزديک است برخيزند و با مشت به کساني که آيات ما را بر آنها ميخوانند حمله کنند! بگو: «آيا شما را به بدتر از اين خبر دهم؟ همان آتش سوزنده [= دوزخ‌] که خدا به کافران وعده داده؛ و بد سرانجامي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2668, N'اي مردم! مثلي زده شده است، به آن گوش فرا دهيد: کساني را که غير از خدا مي‌خوانيد، هرگز نمي‌توانند مگسي بيافرينند، هر چند براي اين کار دست به دست هم دهند! و هرگاه مگس چيزي از آنها بربايد، نمي‌توانند آن را باز پس گيرند! هم اين طلب‌کنندگان ناتوانند، و هم آن مطلوبان (هم اين عابدان، و هم آن معبودان)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2669, N'خدا را آن گونه که بايد بشناسند نشناختند؛ خداوند قوي و شکست‌ناپذير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2670, N'خداوند از فرشتگان رسولاني برمي‌گزيند، و همچنين از مردم؛ خداوند شنوا و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2671, N'آنچه را در پيش روي آنها و پشت سر آنهاست مي‌داند؛ و همه امور بسوي خدا بازمي‌گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2672, N'اي کساني که ايمان آورده‌ايد! رکوع کنيد، و سجود بجا آوريد، و پروردگارتان را عبادت کنيد، و کار نيک انجام دهيد، شايد رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2673, N'و در راه خدا جهاد کنيد، و حق جهادش را ادا نماييد! او شما را برگزيد، و در دين (اسلام) کار سنگين و سختي بر شما قرار ندارد؛ از آيين پدرتان ابراهيم پيروي کنيد؛ خداوند شما را در کتابهاي پيشين و در اين کتاب آسماني «مسلمان» ناميد، تا پيامبر گواه بر شما باشد، و شما گواهان بر مردم! پس نماز را برپا داريد، و زکات را بدهيد، و به خدا تمسک جوييد، که او مولا و سرپرست شماست! چه مولاي خوب، و چه ياور شايسته‌اي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2674, N'مؤمنان رستگار شدند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2675, N'آنها که در نمازشان خشوع دارند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2676, N'و آنها که از لغو و بيهودگي روي‌گردانند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2677, N'و آنها که زکات را انجام مي‌دهند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2678, N'و آنها که دامان خود را (از آلوده‌شدن به بي‌عفتي) حفظ مي‌کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2679, N'تنها آميزش جنسي با همسران و کنيزانشان دارند، که در بهره‌گيري از آنان ملامت نمي‌شوند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2680, N'و کساني که غير از اين طريق را طلب کنند، تجاوزگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2681, N'و آنها که امانتها و عهد خود را رعايت مي‌کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2682, N'و آنها که بر نمازهايشان مواظبت مي‌نمايند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2683, N'(آري،) آنها وارثانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2684, N'(وارثاني) که بهشت برين را ارث مي‌برند، و جاودانه در آن خواهند ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2685, N'و ما انسان را از عصاره‌اي از گل آفريديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2686, N'سپس او را نطفه‌اي در قرارگاه مطمئن [= رحم‌] قرار داديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2687, N'سپس نطفه را بصورت علقه [= خون بسته‌]، و علقه را بصورت مضغه [= چيزي شبيه گوشت جويده شده‌]، و مضغه را بصورت استخوانهايي درآورديم؛ و بر استخوانها گوشت پوشانديم؛ سپس آن را آفرينش تازه‌اي داديم؛ پس بزرگ است خدايي که بهترين آفرينندگان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2688, N'سپس شما بعد از آن مي‌ميريد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2689, N'سپس در روز قيامت برانگيخته مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2690, N'ما بر بالاي سر شما هفت راه [= طبقات هفتگانه آسمان‌] قرار داديم؛ و ما (هرگز) از خلق (خود) غافل نبوده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2691, N'و از آسمان، آبي به اندازه معين نازل کرديم؛ و آن را در زمين (در جايگاه مخصوصي) ساکن نموديم؛ و ما بر از بين بردن آن کاملا قادريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2692, N'سپس بوسيله آن باغهايي از درختان نخل و انگور براي شما ايجاد کرديم؛ باغهايي که در آن ميوه‌هاي بسيار است؛ و از آن ميخوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2693, N'و (نيز) درختي را که از طور سينا مي‌رويد [= درخت زيتون‌]، و از آن روغن و «نان خورش» براي خورندگان فراهم مي‌گردد (آفريديم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2694, N'و براي شما در چهارپايان عبرتي است؛ از آنچه در درون آنهاست [= از شير] شما را سيراب مي‌کنيم؛ و براي شما در آنها منافع بسياري است؛ و از گوشت آنها مي‌خوريد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2695, N'و بر آنها و بر کشتيها سوار مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2696, N'و ما نوح را بسوي قومش فرستاديم؛ او به آنها گفت: «اي قوم من! خداوند يکتا را بپرستيد، که جز او معبودي براي شما نيست! آيا (از پرستش بتها) پرهيز نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2697, N'جمعيت اشرافي (و مغرور) از قوم نوح که کافر بودند گفتند: «اين مرد جز بشري همچون شما نيست، که مي‌خواهد بر شما برتري جويد! اگر خدا مي‌خواست (پيامبري بفرستد) فرشتگاني نازل مي‌کرد؛ ما چنين چيزي را هرگز در نياکان خود نشنيده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2698, N'او فقط مردي است که به نوعي جنون مبتلاست! پس مدتي درباره او صبر کنيد (تا مرگش فرا رسد، يا از اين بيماري رهايي يابد!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2699, N'(نوح) گفت: «پروردگارا! مرا در برابر تکذيبهاي آنان ياري کن!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2700, N'ما به نوح وحي کرديم که: «کشتي را در حضور ما، و مطابق وحي ما بساز. و هنگامي که فرمان ما (براي غرق آنان) فرا رسد، و آب از تنور بجوشد (که نشانه فرا رسيدن طوفان است)، از هر يک از انواع حيوانات يک جفت در کشتي سوار کن؛ و همچنين خانواده‌ات را، مگر آناني که قبلا وعده هلاکشان داده شده [= همسر و فرزند کافرت‌]؛ و ديگر درباره ستمگران با من سخن مگو، که آنان همگي هلاک خواهند شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2701, N'و هنگامي که تو و همه کساني که با تو هستند بر کشتي سوار شديد، بگو: «ستايش براي خدايي است که ما را از قوم ستمگر نجات بخشيد!»')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2702, N'و بگو: «پروردگارا! ما را در منزلگاهي پربرکت فرود آر، و تو بهترين فرودآورندگاني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2703, N'(آري،) در اين ماجرا (براي صاحبان عقل و انديشه) آيات و نشانه‌هايي است؛ و ما مسلما همگان را آزمايش مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2704, N'سپس جمعيت ديگري را بعد از آنها به وجود آورديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2705, N'و در ميان آنان رسولي از خودشان فرستاديم که: «خدا را بپرستيد؛ جز او معبودي براي شما نيست؛ آيا (با اين همه، از شرک و بت پرستي) پرهيز نمي‌کنيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2706, N'ولي اشرافيان (خودخواه) از قوم او که کافر بودند، و ديدار آخرت را تکذيب مي‌کردند، و در زندگي دنيا به آنان ناز و نعمت داده بوديم، گفتند: «اين بشري است مثل شما؛ از آنچه مي‌خوريد مي‌خورد؛ و از آنچه مي‌نوشيد مي‌نوشد! (پس چگونه مي‌تواند پيامبر باشد؟!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2707, N'و اگر از بشري همانند خودتان اطاعت کنيد، مسلما زيانکاريد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2708, N'آيا او به شما وعده مي‌دهد هنگامي که مرديد و خاک و استخوانهايي (پوسيده) شديد، بار ديگر (از قبرها) بيرون آورده مي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2709, N'هيهات، هيهات از اين وعده‌هايي که به شما داده مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2710, N'مسلما غير از اين زندگي دنياي ما، چيزي در کار نيست؛ پيوسته گروهي از ما مي‌ميريم، و نسل ديگري جاي ما را مي‌گيرد؛ و ما هرگز برانگيخته نخواهيم شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2711, N'او فقط مردي دروغگوست که بر خدا افترا بسته؛ و ما هرگز به او ايمان نخواهيم آورد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2712, N'(پيامبرشان) گفت: «پروردگارا! مرا در برابر تکذيبهاي آنان ياري کن!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2713, N'خداوند) فرمود: «بزودي از کار خود پشيمان خواهند شد! (اما زماني که ديگر سودي به حالشان ندارد.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2714, N'سرانجام صيحه آسماني آنها را بحق فرو گرفت؛ و ما آنها را همچون خاشاکي بر سيلاب قرار داديم؛ دور باد قوم ستمگر (از رحمت خدا)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2715, N'سپس اقوام ديگري را پس از آنها پديد آورديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2716, N'هيچ ارمتي بر اجل و سر رسيد حتمي خود پيشي نمي‌گيرد، و از آن تأخير نيز نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2717, N'سپس رسولان خود را يکي پس از ديگري فرستاديم؛ هر زمان رسولي براي (هدايت) قومي مي‌آمد، او را تکذيب مي‌کردند؛ ولي ما اين امتهاي سرکش را يکي پس از ديگري هلاک نموديم، و آنها را احاديثي قرار داديم (چنان محو شدند که تنها نام و گفتگويي از آنان باقي ماند.) دور باد (از رحمت خدا) قومي که ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2718, N'سپس موسي و برادرش هارون را با آيات خود و دليلي روشن فرستاديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2719, N'بسوي فرعون و اطرافيان اشرافي او؛ اما آنها تکبر کردند، و آنها مردمي برتري‌جوي بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2720, N'آنها گفتند: «آيا ما به دو انسان همانند خودمان ايمان بياوريم، در حالي که قوم آنها بردگان ما هستند؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2721, N'(آري،) آنها اين دو را تکذيب کردند؛ و سرانجام همگي هلاک شدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2722, N'و ما به موسي کتاب (آسماني) داديم؛ شايد آنان [= بني اسرائيل‌] هدايت شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2723, N'و ما فرزند مريم [= عيسي‌] و مادرش را آيت و نشانه‌اي قرار داديم؛ و آنها را در سرزمين مرتفعي که داراي امنيت و آب جاري بود جاي داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2724, N'اي پيامبران! از غذاهاي پاکيزه بخوريد، و عمل صالح انجام دهيد، که من به آنچه انجام مي‌دهيد آگاهم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2725, N'و اين امت شما امت واحدي است؛ و من پروردگار شما هستم؛ پس، از مخالفت فرمان من بپرهيزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2726, N'اما آنها کارهاي خود را در ميان خويش به پراکندگي کشاندند، و هر گروهي به راهي رفتند؛ (و عجب اينکه) هر گروه به آنچه نزد خود دارند خوشحالند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2727, N'آنها را در جهل و غفلتشان بگذار تا زماني (که مرگشان فرا رسد يا گرفتار عذاب الهي شوند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2728, N'آنها گمان مي‌کنند اموال و فرزنداني که بعنوان کمک به آنان مي‌دهيم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2729, N'براي اين است که درهاي خيرات را با شتاب به روي آنها بگشاييم!! (چنين نيست) بلکه آنها نمي‌فهمند (که اين وسيله امتحانشان است).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2730, N'مسلما کساني که از خوف پروردگارشان بيمناکند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2731, N'و آنان که به آيات پروردگارشان ايمان مي‌آورند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2732, N'و آنها که به پروردگارشان شرک نمي‌ورزند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2733, N'و آنها که نهايت کوشش را در انجام طاعات به خرج مي‌دهند و با اين حال، دلهايشان هراسناک است از اينکه سرانجام بسوي پروردگارشان بازمي‌گردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2734, N'(آري) چنين کساني در خيرات سرعت مي‌کنند و از ديگران پيشي مي‌گيرند (و مشمول عنايات ما هستند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2735, N'و ما هيچ کس را جز به اندازه تواناييش تکليف نمي‌کنيم؛ و نزد ما کتابي است که (تمام اعمال بندگان را ثبت کرده و) بحق سخن مي‌گويد؛ و به آنان هيچ ستمي نمي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2736, N'ولي دلهاي آنها از اين نامه اعمال (و روز حساب و آيات قرآن) در بي‌خبري فرورفته؛ و اعمال (زشت) ديگري جز اين دارند که پيوسته آن را انجام مي‌دهند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2737, N'تا زماني که متنعمان مغرور آنها را در چنگال عذاب گرفتار سازيم؛ در اين هنگام، ناله‌هاي دردناک و استغاثه‌آميز سرمي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2738, N'(اما به آنها گفته مي‌شود:) امروز فرياد نکنيد، زيرا از سوي ما ياري نخواهيد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2739, N'(آيا فراموش کرده‌ايد که) در گذشته آيات من پيوسته بر شما خوانده مي‌شد؛ اما شما اعراض کرده به عقب بازمي‌گشتيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2740, N'در حالي که در برابر او [=پيامبر] استکبار مي‌کرديد، و شبها در جلسات خود به بدگويي مي‌پرداختيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2741, N'آيا آنها در اين گفتار نينديشيدند، يا اينکه چيزي براي آنان آمده که براي نياکانشان نيامده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2742, N'يا اينکه پيامبرشان را نشناختند (و از سوابق او آگاه نيستند)، از اين رو او را انکار مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2743, N'يا مي‌گويند او ديوانه است؟! ولي او حق را براي آنان آورده؛ اما بيشترشان از حق کراهت دارند (و گريزانند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2744, N'و اگر حق از هوسهاي آنها پيروي کند، آسمانها و زمين و همه کساني که در آنها هستند تباه مي‌شوند! ولي ما قرآني به آنها داديم که مايه يادآوري (و عزت و شرف) براي آنهاست، اما آنان از (آنچه مايه) يادآوريشان (است) رويگردانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2745, N'يا اينکه تو از آنها مزد و هزينه‌اي (در برابر دعوتت) مي‌خواهي؟ با اينکه مزد پروردگارت بهتر، و او بهترين روزي دهندگان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2746, N'بطور قطع و يقين، تو آنان را به راه راست دعوت مي‌کني.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2747, N'اما کساني که به آخرت ايمان ندارند از اين راه منحرفند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2748, N'و اگر به آنان رحم کنيد و گرفتاريها و مشکلاتشان را برطرف سازيم، (نه تنها بيدار نمي‌شوند، بلکه) در طغيانشان لجاجت مي‌ورزند و (در اين وادي) سرگردان مي‌مانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2749, N'ما آنها را به عذاب و بلا گرفتار ساختيم (تا بيدار شوند)، اما آنان نه در برابر پروردگارشان تواضع کردند، و نه به درگاهش تضرع مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2750, N'(اين وضع همچنان ادامه مي‌يابد) تا زماني که دري از عذاب شديد به روي آنان بگشاييم، (و چنان گرفتار مي‌شوند که) ناگهان بکلي مأيوس گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2751, N'و او کسي است که براي شما گوش و چشم و قلب [=عقل‌] ايجاد کرد؛ اما کمتر شکر او را بجا مي‌آوريد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2752, N'و او کسي است که شما را در زمين آفريد؛ و به سوي او محشور مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2753, N'و او کسي است که زنده مي‌کند و مي‌ميراند؛ و رفت و آمد شب و روز از آن اوست؛ آيا انديشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2754, N'(نه،) بلکه آنان نيز مثل آنچه پيشينيان گفته بودند گفتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2755, N'آنها گفتند: «آيا هنگامي که مرديم و خاک و استخوانهايي (پوسيده) شديم، آيا بار ديگر برانگيخته خواهيم شد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2756, N'اين وعده به ما و پدرانمان از قبل داده شده؛ اين فقط افسانه‌هاي پيشينيان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2757, N'بگو: «زمين و کساني که در آن هستند از آن کيست، اگر شما مي‌دانيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2758, N'بزودي (در پاسخ تو) مي‌گويند: «همه از آن خداست!» بگو: «آيا متذکر نمي‌شويد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2759, N'بگو: «چه کسي پروردگار آسمانهاي هفتگانه، و پروردگار عرش عظيم است؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2760, N'بزودي خواهند گفت: «همه اينها از آن خداست!» بگو: «آيا تقوا پيشه نمي‌کنيد (و از خدا نمي‌ترسيد و دست از شرک برنمي‌داريد)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2761, N'بگو: «اگر مي‌دانيد، چه کسي حکومت همه موجودات را در دست دارد، و به بي پناهان پناه مي‌دهد، و نياز به پناه‌دادن ندارد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2762, N'خواهند گفت: «(همه اينها) از آن خداست» بگو: «با اين حال چگونه مي‌گوييد سحر شده‌ايد (و اين سخنان سحر و افسون است)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2763, N'نه، (واقع اين است که) ما حق را براي آنها آورديم؛ و آنان دروغ مي‌گويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2764, N'خدا هرگز فرزندي براي خود انتخاب نکرده؛ و معبود ديگري با او نيست؛ که اگر چنين مي‌شد، هر يک از خدايان مخلوقات خود را تدبير و اداره مي‌کردند و بعضي بر بعضي ديگر برتري مي‌جستند (و جهان هستي به تباهي کشيده مي‌شد)؛ منزه است خدا از آنچه آنان توصيف مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2765, N'او داناي نهان و آشکار است؛ پس برتر است از آنچه براي او همتا قرار مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2766, N'بگو: «پروردگار من! اگر عذابهايي را که به آنان وعده داده مي‌شود به من نشان دهي (و در زندگيم آن را ببينم)...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2767, N'پروردگار من! مرا (در اين عذابها) با گروه ستمگران قرار مده!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2768, N'و ما تواناييم که آنچه را به آنها وعده مي‌دهيم به تو نشان دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2769, N'بدي را به بهترين راه و روش دفع کن (و پاسخ بدي را به نيکي ده)! ما به آنچه توصيف مي‌کنند آگاهتريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2770, N'و بگو: «پروردگارا! از وسوسه‌هاي شياطين به تو پناه مي‌برم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2771, N'و از اينکه آنان نزد من حاضر شوند (نيز) -اي پروردگار من- به تو پناه مي‌برم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2772, N'(آنها همچنان به راه غلط خود ادامه مي‌دهند) تا زماني که مرگ يکي از آنان فرارسد، مي‌گويد: «پروردگار من! مرا بازگردانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2773, N'شايد در آنچه ترک کردم (و کوتاهي نمودم) عمل صالحي انجام دهم!» (ولي به او مي‌گويند:) چنين نيست! اين سخني است که او به زبان مي‌گويد (و اگر بازگردد، کارش همچون گذشته است)! و پشت سر آنان برزخي است تا روزي که برانگيخته شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2774, N'هنگامي که در «صور» دميده شود، هيچ يک از پيوندهاي خويشاوندي ميان آنها در آن روز نخواهد بود؛ و از يکديگر تقاضاي کمک نمي‌کنند (چون کاري از کسي ساخته نيست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2775, N'و کساني که وزنه اعمالشان سنگين است، همان رستگارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2776, N'و آنان که وزنه اعمالشان سبک باشد، کساني هستند که سرمايه وجود خود را از دست داده، در جهنم جاودانه خواهند ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2777, N'شعله‌هاي سوزان آتش همچون شمشير به صورتهايشان نواخته مي‌شود؛ و در دوزخ چهره‌اي عبوس دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2778, N'(به آنها گفته مي‌شود:) آيا آيات من بر شما خوانده نمي‌شد، پس آن را تکذيب مي‌کرديد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2779, N'مي‌گويند: «پروردگارا! بدبختي ما بر ما چيره شد، و ما قوم گمراهي بوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2780, N'پروردگارا! ما را از اين (دوزخ) بيرون آر، اگر بار ديگر تکرار کرديم قطعا ستمگريم (و مستحق عذاب)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2781, N'(خداوند) مي‌گويد: «دور شويد در دوزخ، و با من سخن مگوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2782, N'(فراموش کرده‌ايد) گروهي از بندگانم مي‌گفتند: پروردگارا! ما ايمان آورديم؛ ما را ببخش و بر ما رحم کن؛ و تو بهترين رحم کنندگاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2783, N'اما شما آنها را به باد مسخره گرفتيد تا شما را از ياد من غافل کردند؛ و شما به آنان مي‌خنديديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2784, N'ولي من امروز آنها را بخاطر صبر و استقامتشان پاداش دادم؛ آنها پيروز و رستگارند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2785, N'(خداوند) مي‌گويد: «چند سال در روي زمين توقف کرديد؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2786, N'(در پاسخ) مي‌گويند: «تنها به اندازه يک روز، يا قسمتي از يک روز! از آنها که مي‌توانند بشمارند بپرس!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2787, N'مي‌گويد: «(آري،) شما مقدار کمي توقف نموديد اگر مي‌دانستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2788, N'آيا گمان کرديد شما را بيهوده آفريده‌ايم، و بسوي ما باز نمي‌گرديد؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2789, N'پس برتر است خداوندي که فرمانرواي حق است (از اينکه شما را بي‌هدف آفريده باشد)! معبودي جز او نيست؛ و او پروردگار عرش کريم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2790, N'و هر کس معبود ديگري را با خدا بخواند -و مسلما هيچ دليلي بر آن نخواهد داشت- حساب او نزد پروردگارش خواهد بود؛ يقينا کافران رستگار نخواهند شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2791, N'و بگو: «پروردگارا! مرا ببخش و رحمت کن؛ و تو بهترين رحم کنندگاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2792, N'(اين) سوره‌اي است که آن را فرو فرستاديم، و (عمل به آن را) واجب نموديم، و در آن آيات روشني نازل کرديم، شايد شما متذکر شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2793, N'هر يک از زن و مرد زناکار را صد تازيانه بزنيد؛ و نبايد رأفت (و محبت کاذب) نسبت به آن دو شما را از اجراي حکم الهي مانع شود، اگر به خدا و روز جزا ايمان داريد! و بايد گروهي از مؤمنان مجازاتشان را مشاهده کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2794, N'مرد زناکار جز با زن زناکار يا مشرک ازدواج نمي‌کند؛ و زن زناکار را، جز مرد زناکار يا مشرک، به ازدواج خود درنمي‌آورد؛ و اين کار بر مؤمنان حرام شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2795, N'و کساني که آنان پاکدامن را متهم مي‌کنند، سپس چهار شاهد (بر مدعاي خود) نمي‌آورند ، آنها را هشتاد تازيانه بزنيد و شهادتشان را هرگز نپذيريد؛ و آنها همان فاسقانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2796, N'مگر کساني که بعد از آن توبه کنند و جبران نمايند (که خداوند آنها را مي‌بخشد) زيرا خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2797, N'و کساني که همسران خود را (به عمل منافي عفت) متهم مي‌کنند، و گواهاني جز خودشان ندارند، هر يک از آنها بايد چهار مرتبه به نام خدا شهادت دهد که از راستگويان است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2798, N'و در پنجمين بار بگويد که لعنت خدا بر او باد اگر از دروغگويان باشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2799, N'آن زن نيز مي‌تواند کيفر (زنا) را از خود دور کند، به اين طريق که چهار بار خدا را به شهادت طلبد که آن مرد (در اين نسبتي که به او مي‌دهد) از دروغگويان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2800, N'و بار پنجم بگويد که غضب خدا بر او باد اگر آن مرد از راستگويان باشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2801, N'و اگر فضل و رحمت خدا شامل حال شما نبود و اينکه او توبه‌پذير و حکيم است (بسياري از شما گرفتار مجازات سخت الهي مي‌شديد)!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2802, N'مسلما کساني که آن تهمت عظيم را عنوان کردند گروهي (متشکل و توطئه‌گر) از شما بودند؛ اما گمان نکنيد اين ماجرا براي شما بد است، بلکه خير شما در آن است؛ آنها هر کدام سهم خود را از اين گناهي که مرتکب شدند دارند؛ و از آنان کسي که بخش مهم آن را بر عهده داشت عذاب عظيمي براي اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2803, N'چرا هنگامي که اين (تهمت) را شنيديد، مردان و زنان با ايمان نسبت به خود (و کسي که همچون خود آنها بود) گمان خير نبردند و نگفتند اين دروغي بزرگ و آشکار است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2804, N'چرا چهار شاهد براي آن نياوردند؟! اکنون که اين گواهان را نياوردند، آنان در پيشگاه خدا دروغگويانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2805, N'و اگر فضل و رحمت الهي در دنيا و آخرت شامل شما نمي‌شد، بخاطر اين گناهي که کرديد عذاب سختي به شما مي‌رسيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2806, N'به خاطر بياوريد زماني را که اين شايعه را از زبان يکديگر مي‌گرفتيد، و با دهان خود سخني مي‌گفتيد که به آن يقين نداشتيد؛ و آن را کوچک مي‌پنداشتيد در حالي که نزد خدا بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2807, N'چرا هنگامي که آن را شنيديد نگفتيد: «ما حق نداريم که به اين سخن تکلم کنيم؛ خداوندا منزهي تو، اين بهتان بزرگي است»؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2808, N'خداوند شما را اندرز مي‌دهد که هرگز چنين کاري را تکرار نکنيد اگر ايمان داريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2809, N'و خداوند آيات را براي شما بيان مي‌کند، و خدا دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2810, N'کساني که دوست دارند زشتيها در ميان مردم با ايمان شيوع يابد، عذاب دردناکي براي آنان در دنيا و آخرت است؛ و خداوند مي‌داند و شما نمي‌دانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2811, N'و اگر فضل و رحمت الهي شامل حال شما نبود و اينکه خدا مهربان و رحيم است (مجازات سختي دامانتان را مي‌گرفت)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2812, N'اي کساني که ايمان آورده‌ايد! از گامهاي شيطان پيروي نکنيد! هر کس پيرو شيطان شود (گمراهش مي‌سازد، زيرا) او به فحشا و منکر فرمان مي‌دهد! و اگر فضل و رحمت الهي بر شما نبود، هرگز احدي از شما پاک نمي‌شد؛ ولي خداوند هر که را بخواهد تزکيه مي‌کند، و خدا شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2813, N'آنها که از ميان شما داراي برتري (مالي) و وسعت زندگي هستند نبايد سوگند ياد کنند که از انفاق نسبت به نزديکان و مستمندان و مهاجران در راه خدا دريغ نمايند؛ آنها بايد عفو کنند و چشم بپوشند؛ آيا دوست نمي‌داريد خداوند شما را ببخشد؟! و خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2814, N'کساني که زنان پاکدامن و بي‌خبر (از هرگونه آلودگي) و مؤمن را متهم مي‌سازند، در دنيا و آخرت از رحمت الهي بدورند و عذاب بزرگي براي آنهاست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2815, N'در آن روز زبانها و دستها و پاهايشان بر ضد آنها به اعمالي که مرتکب مي‌شدند گواهي مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2816, N'آن روز، خداوند جزاي واقعي آنان را بي‌کم و کاست مي‌دهد؛ و مي‌دانند که خداوند حق آشکار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2817, N'زنان ناپاک از آن مردان ناپاکند، و مردان ناپاک نيز به زنان ناپاک تعلق دارند؛ و زنان پاک از آن مردان پاک، و مردان پاک از آن زنان پاکند! اينان از نسبتهاي ناروايي که (ناپاکان) به آنان مي‌دهند مبرا هستند؛ و براي آنان آمرزش (الهي) و روزي پرارزشي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2818, N'اي کساني که ايمان آورده‌ايد! در خانه‌هايي غير از خانه خود وارد نشويد تا اجازه بگيريد و بر اهل آن خانه سلام کنيد؛ اين براي شما بهتر است؛ شايد متذکر شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2819, N'و اگر کسي را در آن نيافتيد، وارد نشويد تا به شما اجازه داده شود؛ و اگر گفته شد: «بازگرديد!» بازگرديد؛ اين براي شما پاکيزه‌تر است؛ و خداوند به آنچه انجام مي‌دهيد آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2820, N'(ولي) گناهي بر شما نيست که وارد خانه‌هاي غير مسکوني بشويد که در آن متاعي متعلق به شما وجود دارد؛ و خدا آنچه را آشکار مي‌کنيد و آنچه را پنهان مي‌داريد، مي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2821, N'به مؤمنان بگو چشمهاي خود را (از نگاه به نامحرمان) فروگيرند، و عفاف خود را حفظ کنند؛ اين براي آنان پاکيزه‌تر است؛ خداوند از آنچه انجام مي‌دهيد آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2822, N'و به آنان با ايمان بگو چشمهاي خود را (از نگاه هوس‌آلود) فروگيرند، و دامان خويش را حفظ کنند و زينت خود را -جز آن مقدار که نمايان است- آشکار ننمايند و (اطراف) روسري‌هاي خود را بر سينه خود افکنند (تا گردن و سينه با آن پوشانده شود)، و زينت خود را آشکار نسازند مگر براي شوهرانشان، يا پدرانشان، يا پدر شوهرانشان، يا پسرانشان، يا پسران همسرانشان، يا برادرانشان، يا پسران برادرانشان، يا پسران خواهرانشان، يا زنان هم‌کيششان، يا بردگانشان [=کنيزانشان‌]، يا افراد سفيه که تمايلي به زن ندارند، يا کودکاني که از امور جنسي مربوط به زنان آگاه نيستند؛ و هنگام راه رفتن پاهاي خود را به زمين نزنند تا زينت پنهانيشان دانسته شود (و صداي خلخال که برپا دارند به گوش رسد). و همگي بسوي خدا بازگرديد اي مؤمنان، تا رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2823, N'مردان و زنان بي‌همسر خود را همسر دهيد، همچنين غلامان و کنيزان صالح و درستکارتان را؛ اگر فقير و تنگدست باشند، خداوند از فضل خود آنان را بي‌نياز مي‌سازد؛ خداوند گشايش‌دهنده و آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2824, N'و کساني که امکاني براي ازدواج نمي‌يابند، بايد پاکدامني پيشه کنند تا خداوند از فضل خود آنان را بي‌نياز گرداند! و آن بردگانتان که خواستار مکاتبه [=قرار داد مخصوص براي آزاد شدن‌] هستند، با آنان قرار داد ببنديد اگر رشد و صلاح در آنان احساس مي‌کنيد (که بعد از آزادي، توانايي زندگي مستقل را دارند)؛ و چيزي از مال خدا را که به شما داده است به آنان بدهيد! و کنيزان خود را براي دستيابي متاع ناپايدار زندگي دنيا مجبور به خود فروشي نکنيد اگر خودشان مي‌خواهند پاک بمانند! و هر کس آنها را (بر اين کار) اجبار کند، (سپس پشيمان گردد،) خداوند بعد از اين اجبار آنها غفور و رحيم است! (توبه کنيد و بازگرديد، تا خدا شما را ببخشد!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2825, N'ما بر شما آياتي فرستاديم که حقايق بسياري را تبيين مي‌کند، و اخباري از کساني که پيش از شما بودند، و موعظه و اندرزي براي پرهيزگاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2826, N'خداوند نور آسمانها و زمين است؛ مثل نور خداوند همانند چراغداني است که در آن چراغي (پر فروغ) باشد، آن چراغ در حبابي قرار گيرد، حبابي شفاف و درخشنده همچون يک ستاره فروزان، اين چراغ با روغني افروخته مي‌شود که از درخت پربرکت زيتوني گرفته شده که نه شرقي است و نه غربي؛ (روغنش آنچنان صاف و خالص است که) نزديک است بدون تماس با آتش شعله‌ور شود؛ نوري است بر فراز نوري؛ و خدا هر کس را بخواهد به نور خود هدايت مي‌کند، و خداوند به هر چيزي داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2827, N'(اين چراغ پرفروغ) در خانه‌هايي قرار دارد که خداوند اذن فرموده ديوارهاي آن را بالا برند (تا از دستبرد شياطين و هوسبازان در امان باشد)؛ خانه‌هايي که نام خدا در آنها برده مي‌شود، و صبح و شام در آنها تسبيح او مي‌گويند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2828, N'مرداني که نه تجارت و نه معامله‌اي آنان را از ياد خدا و برپاداشتن نماز و اداي زکات غافل نمي‌کند؛ آنها از روزي مي‌ترسند که در آن، دلها و چشمها زير و رو مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2829, N'(آنها به سراغ اين کارها مي‌روند) تا خداوند آنان را به بهترين اعمالي که انجام داده‌اند پاداش دهد، و از فضل خود بر پاداششان بيفزايد؛ و خداوند به هر کس بخواهد بي حساب روزي مي‌دهد (و از مواهب بي‌انتهاي خويش بهره‌مند مي‌سازد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2830, N'کساني که کافر شدند، اعمالشان همچون سرابي است در يک کوير که انسان تشنه از دور آن را آب مي‌پندارد؛ اما هنگامي که به سراغ آن مي‌آيد چيزي نمي‌يابد، و خدا را نزد آن مي‌يابد که حساب او را بطور کامل مي‌دهد؛ و خداوند سريع الحساب است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2831, N'يا همچون ظلماتي در يک درياي عميق و پهناور که موج آن را پوشانده، و بر فراز آن موج ديگري، و بر فراز آن ابري تاريک است؛ ظلمتهايي است يکي بر فراز ديگري، آن گونه که هر گاه دست خود را خارج کند ممکن نيست آن را ببيند! و کسي که خدا نوري براي او قرار نداده، نوري براي او نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2832, N'آيا نديدي تمام آنان که در آسمانها و زمينند براي خدا تسبيح مي‌کنند، و همچنين پرندگان به هنگامي که بر فراز آسمان بال گسترده‌اند؟! هر يک از آنها نماز و تسبيح خود را مي‌داند؛ و خداوند به آنچه انجام ميدهند داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2833, N'و از براي خداست حکومت و مالکيت آسمانها و زمين؛ و بازگشت (تمامي موجودات) بسوي اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2834, N'آيا نديدي که خداوند ابرهايي را به آرامي مي‌راند، سپس ميان آنها پيوند مي‌دهد، و بعد آن را متراکم مي‌سازد؟! در اين حال، دانه‌هاي باران را مي‌بيني که از لابه‌لاي آن خارج مي‌شود؛ و از آسمان -از کوه‌هايي که در آن است [=ابرهايي که همچون کوه‌ها انباشته شده‌اند]- دانه‌هاي تگرگ نازل مي‌کند، و هر کس را بخواهد بوسيله آن زيان مي‌رساند، و از هر کس بخواهد اين زيان را برطرف مي‌کند؛ نزديک است درخشندگي برق آن (ابرها) چشمها را ببرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2835, N'خداوند شب و روز را دگرگون مي‌سازد؛ در اين عبرتي است براي صاحبان بصيرت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2836, N'و خداوند هر جنبنده‌اي را از آبي آفريد؛ گروهي از آنها بر شکم خود راه مي‌روند، و گروهي بر دو پاي خود، و گروهي بر چهار پا راه مي‌روند؛ خداوند هر چه را بخواهد مي‌آفريند، زيرا خدا بر همه چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2837, N'ما آيات روشنگري نازل کرديم؛ و خدا هر که را بخواهد به صراط مستقيم هدايت مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2838, N'آنها مي‌گويند: «به خدا و پيامبر ايمان داريم و اطاعت مي‌کنيم!» ولي بعد از اين ادعا، گروهي از آنان رويگردان مي‌شوند؛ آنها (در حقيقت) مؤمن نيستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2839, N'و هنگامي که از آنان دعوت شود که بسوي خدا و پيامبرش بيايند تا در ميانشان داوري کند، ناگهان گروهي از آنان رويگردان مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2840, N'ولي اگر حق داشته باشند (و داوري به نفع آنان شود) با سرعت و تسليم بسوي او مي‌آيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2841, N'آيا در دلهاي آنان بيماري است، يا شک و ترديد دارند، يا مي‌ترسند خدا و رسولش بر آنان ستم کنند؟! نه، بلکه آنها خودشان ستمگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2842, N'سخن مؤمنان، هنگامي که بسوي خدا و رسولش دعوت شوند تا ميان آنان داوري کند، تنها اين است که مي‌گويند: «شنيديم و اطاعت کرديم!» و اينها همان رستگاران واقعي هستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2843, N'و هر کس خدا و پيامبرش را اطاعت کند، و از خدا بترسد و از مخالفت فرمانش بپرهيزد، چنين کساني همان پيروزمندان واقعي هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2844, N'آنها با نهايت تأکيد سوگند ياد کردند که اگر به آنان فرمان دهي، (از خانه و اموال خود) بيرون مي‌روند (و جان را در طبق اخلاص گذارده تقديم مي‌کنند)؛ بگو: «سوگند ياد نکنيد؛ شما طاعت خالصانه نشان دهيد که خداوند به آنچه انجام مي‌دهيد آگاه است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2845, N'بگو: «خدا را اطاعت کنيد، و از پيامبرش فرمان بريد! و اگر سرپيچي نماييد، پيامبر مسؤول اعمال خويش است و شما مسؤول اعمال خود! اما اگر از او اطاعت کنيد، هدايت خواهيد شد؛ و بر پيامبر چيزي جز رساندن آشکار نيست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2846, N'خداوند به کساني از شما که ايمان آورده و کارهاي شايسته انجام داده‌اند وعده مي‌دهد که قطعا آنان را حکمران روي زمين خواهد کرد، همان گونه که به پيشينيان آنها خلافت روي زمين را بخشيد؛ و دين و آييني را که براي آنان پسنديده، پابرجا و ريشه‌دار خواهد ساخت؛ و ترسشان را به امنيت و آرامش مبدل مي‌کند، آنچنان که تنها مرا مي پرستند و چيزي را شريک من نخواهند ساخت. و کساني که پس از آن کافر شوند، آنها فاسقانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2847, N'و نماز را برپا داريد، و زکات را بدهيد، و رسول (خدا) را اطاعت کنيد تا مشمول رحمت (او) شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2848, N'گمان مبر کافران مي‌توانند از چنگال مجازات الهي در زمين فرار کنند! جايگاه آنان آتش است، و چه بد جايگاهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2849, N'اي کساني که ايمان آورده‌ايد! بردگان شما، و همچنين کودکانتان که به حد بلوغ نرسيده‌اند، در سه وقت بايد از شما اجازه بگيرند: پيش از نماز صبح، و نيمروز هنگامي که لباسهاي (معمولي) خود را بيرون مي‌آوريد، و بعد از نماز عشا؛ اين سه وقت خصوصي براي شماست؛ اما بعد از اين سه وقت، گناهي بر شما و بر آنان نيست (که بدون اذن وارد شوند) و بر گرد يکديگر بگرديد (و با صفا و صميميت به يکديگر خدمت نماييد). اين گونه خداوند آيات را براي شما بيان مي‌کند، و خداوند دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2850, N'و هنگامي که اطفال شما به سن بلوغ رسند بايد اجازه بگيرند، همان گونه که اشخاصي که پيش از آنان بودند اجازه مي‌گرفتند؛ اينچنين خداوند آياتش را براي شما بيان مي‌کند، و خدا دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2851, N'و زنان از کارافتاده‌اي که اميد به ازدواج ندارند، گناهي بر آنان نيست که لباسهاي (رويين) خود را بر زمين بگذارند، بشرط اينکه در برابر مردم خودآرايي نکنند؛ و اگر خود را بپوشانند براي آنان بهتر است؛ و خداوند شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2852, N'بر نابينا و افراد لنگ و بيمار گناهي نيست (که با شما هم غذا شوند)، و بر شما نيز گناهي نيست که از خانه‌هاي خودتان [=خانه‌هاي فرزندان يا همسرانتان که خانه خود شما محسوب مي‌شود بدون اجازه خاصي‌] غذا بخوريد؛ و همچنين خانه‌هاي پدرانتان، يا خانه‌هاي مادرانتان، يا خانه‌هاي برادرانتان، يا خانه‌هاي خواهرانتان، يا خانه‌هاي عموهايتان، يا خانه‌هاي عمه‌هايتان، يا خانه‌هاي داييهايتان، يا خانه‌هاي خاله‌هايتان، يا خانه‌اي که کليدش در اختيار شماست، يا خانه‌هاي دوستانتان، بر شما گناهي نيست که بطور دسته‌جمعي يا جداگانه غذا بخوريد؛ و هنگامي که داخل خانه‌اي شديد، بر خويشتن سلام کنيد، سلام و تحيتي از سوي خداوند، سلامي پربرکت و پاکيزه! اين گونه خداوند آيات را براي شما روشن مي‌کند، باشد که بينديشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2853, N'مؤمنان واقعي کساني هستند که به خدا و رسولش ايمان آورده‌اند و هنگامي که در کار مهمي با او باشند، بي‌اجازه او جايي نمي‌روند؛ کساني که از تو اجازه مي‌گيرند، براستي به خدا و پيامبرش ايمان آورده‌اند! در اين صورت، هر گاه براي بعضي کارهاي مهم خود از تو اجازه بخواهند، به هر يک از آنان که مي‌خواهي (و صلاح مي‌بيني) اجازه ده، و برايشان از خدا آمرزش بخواه که خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2854, N'صدا کردن پيامبر را در ميان خود، مانند صدا کردن يکديگر قرار ندهي؛ خداوند کساني از شما را که پشت سر ديگران پنهان مي‌شوند، و يکي پس از ديگري فرار مي‌کنند مي‌داند! پس آنان که فرمان او را مخالفت مي‌کنند، بايد بترسند از اينکه فتنه‌اي دامنشان را بگيرد، يا عذابي دردناک به آنها برسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2855, N'آگاه باشيد که براي خداست آنچه در آسمانها و زمين است؛ او مي‌داند آنچه را که شما بر آن هستيد، و (مي‌داند) روزي را که بسوي او بازمي‌گردند؛ و (در آن روز) آنها را از اعمالي که انجام دادند آگاه مي‌سازد؛ و خداوند به هر چيزي داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2856, N'زوال ناپذير و پر برکت است کسي که قرآن را بر بنده‌اش نازل کرد تا بيم‌دهنده جهانيان باشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2857, N'خداوندي که حکومت آسمانها و زمين از آن اوست، و فرزندي براي خود انتخاب نکرد، و همتايي در حکومت و مالکيت ندارد، و همه چيز را آفريد، و به دقت اندازه‌گيري نمود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2858, N'آنان غير از خداوند معبوداني براي خود برگزيدند؛ معبوداني که چيزي را نمي‌آفرينند، بلکه خودشان مخلوقند، و مالک زيان و سود خويش نيستند، و نه مالک مرگ و حيات و رستاخيز خويشند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2859, N'و کافران گفتند: «اين فقط دروغي است که او ساخته، و گروهي ديگر او را بر اين کار ياري داده‌اند.» آنها (با اين سخن،) ظلم و دروغ بزرگي را مرتکب شدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2860, N'و گفتند: «اين همان افسانه‌هاي پيشينيان است که وي آن را رونويس کرده، و هر صبح و شام بر او املا مي‌شود.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2861, N'بگو: «کسي آن را نازل کرده که اسرار آسمانها و زمين را مي‌داند؛ او (هميشه) آمرزنده و مهربان بوده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2862, N'و گفتند: «چرا اين پيامبر غذا مي‌خورد و در بازارها راه مي‌رود؟! (نه سنت فرشتگان را دارد و نه روش شاهان را!) چرا فرشته‌اي بر او نازل نشده که همراه وي مردم را انذار کند (و گواه صدق دعوي او باشد)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2863, N'يا گنجي (از آسمان) براي او فرستاده شود، يا باغي داشته باشد که از (ميوه) آن بخورد (و امرار معاش کند)؟!» و ستمگران گفتند: «شما تنها از مردي مجنون پيروي مي‌کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2864, N'ببين چگونه براي تو مثلها زدند و گمراه شدند، آن گونه که قدرت پيدا کردن راه را ندارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2865, N'زوال ناپذير و بزرگ است خدايي که اگر بخواهد براي تو بهتر از اين قرار مي‌دهد: باغهايي که نهرها از زير درختانش جاري است، و (اگر بخواهد) براي تو کاخهايي مجلل قرارمي‌دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2866, N'(اينها همه بهانه است) بلکه آنان قيامت را تکذيب کرده‌اند؛ و ما براي کسي که قيامت را تکذيب کند، آتشي شعله‌ور و سوزان فراهم کرده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2867, N'هنگامي که اين آتش آنان را از مکاني دور ببيند، صداي وحشتناک و خشم آلودش را که با نفس‌زدن شديد همراه است مي‌شنوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2868, N'و هنگامي که در جاي تنگ و محدودي از آن افکنده شوند در حالي که در غل و زنجيرند، فرياد واويلاي آنان بلند مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2869, N'(به آنان گفته مي‌شود:) امروز يک بار واويلا نگوييد، بلکه بسيار واويلا بگوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2870, N'(اي پيامبر!) بگو: «آيا اين بهتر است يا بهشت جاويداني که به پرهيزگاران وعده داده شده؟! بهشتي که پاداش اعمال آنها، و قرارگاهشان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2871, N'هر چه بخواهند در آنجا برايشان فراهم است؛ جاودانه در آن خواهند ماند؛ اين وعده‌اي است مسلم که پروردگارت بر عهده گرفته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2872, N'(به خاطر بياور) روزي را که همه آنان و معبودهايي را که غير از خدا مي‌پرستند جمع مي‌کند، آنگاه به آنها مي‌گويد: «آيا شما اين بندگان مرا گمراه کرديد يا خود آنان راه را گم کردند؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2873, N'(در پاسخ) مي‌گويند: «منزهي تو! براي ما شايسته نبود که غير از تو اوليايي برگزينيم، ولي آنان و پدرانشان را از نعمتها برخوردار نمودي تا اينکه (به جاي شکر نعمت) ياد تو را فراموش کردند و تباه و هلاک شدند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2874, N'(خداوند به آنان مي‌گويد: ببينيد) اين معبودان، شما را در آنچه مي‌گوييد تکذيب کردند! اکنون نمي‌توانيد عذاب الهي را برطرف بسازيد، يا از کسي ياري بطلبيد! و هر کس از شما ستم کند، عذاب شديدي به او مي‌چشانيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2875, N'ما هيچ يک از رسولان را پيش از تو نفرستاديم مگر اينکه غذا مي‌خوردند و در بازارها راه مي‌رفتند؛ و بعضي از شما را وسيله امتحان بعضي ديگر قرارداديم، آيا صبر و شکيبايي مي‌کنيد (و از عهده امتحان برمي‌آييد)؟! و پروردگار تو همواره بصير و بينا بوده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2876, N'و کساني که اميدي به ديدار ما ندارند (و رستاخيز را انکار مي‌کنند) گفتند: «چرا فرشتگان بر ما نازل نشدند و يا پروردگارمان را با چشم خود نمي‌بينيم؟!» آنها درباره خود تکبر ورزيدند و طغيان بزرگي کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2877, N'(آنها به آرزوي خود مي‌رسند،) اما روزي که فرشتگان را مي‌بينند، روز بشارت براي مجرمان نخواهد بود (بلکه روز مجازات و کيفر آنان است)! و مي‌گويند: «ما را امان دهيد، ما را معاف داريد!» (اما سودي ندارد!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2878, N'و ما به سراغ اعمالي که انجام داده‌اند مي‌رويم، و همه را همچون ذرات غبار پراکنده در هوا قرار مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2879, N'بهشتيان در آن روز قرارگاهشان از همه بهتر، و استراحتگاهشان نيکوتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2880, N'و (بخاطر آور) روزي را که آسمان با ابرها شکافته مي‌شود، و فرشتگان نازل مي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2881, N'حکومت در آن روز از آن خداوند رحمان است؛ و آن روز، روز سختي براي کافران خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2882, N'و (به خاطر آور) روزي را که ستمکار دست خود را (از شدت حسرت) به دندان مي‌گزد و مي‌گويد: «اي کاش با رسول (خدا) راهي برگزيده بودم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2883, N'اي واي بر من، کاش فلان (شخص گمراه) را دوست خود انتخاب نکرده بودم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2884, N'او مرا از يادآوري (حق) گمراه ساخت بعد از آنکه (ياد حق) به سراغ من آمده بود!» و شيطان هميشه خوارکنند* انسان بوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2885, N'و پيامبر عرضه داشت: «پروردگارا! قوم من قرآن را رها کردند».')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2886, N'(آري،) اين گونه براي هر پيامبري دشمني از مجرمان قرار داديم؛ اما (براي تو) همين بس که پروردگارت هادي و ياور (تو) باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2887, N'و کافران گفتند: «چرا قرآن يکجا بر او نازل نمي‌شود؟!» اين بخاطر آن است که قلب تو را بوسيله آن محکم داريم، و (از اين رو) آن را به تدريج بر تو خوانديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2888, N'آنان هيچ مثلي براي تو نمي‌آورند مگر اينکه ما حق را براي تو مي‌آوريم، و تفسيري بهتر (و پاسخي دندان شکن که در برابر آن ناتوان شوند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2889, N'(تو گمراه نيستي،) آنان که بر صورتهايشان بسوي جهنم محشور مي‌شوند، بدترين محل را دارند و گمراه‌ترين افرادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2890, N'و ما به موسي کتاب (آسماني) داديم؛ و برادرش هارون را ياور او قرار داديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2891, N'و گفتيم: «به سوي اين قوم که آيات ما را تکذيب کردند برويد!» (اما آن مردم به مخالفت برخاستند) و ما به شدت آنان را درهم کوبيديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2892, N'و قوم نوح را هنگامي که رسولان (ما) را تکذيب کردند غرق نموديم، و آنان را درس عبرتي براي مردم قرار داديم؛ و براي ستمگران عذاب دردناکي فراهم ساخته‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2893, N'(همچنين) قوم عاد و ثمود و اصحاب الرس [= گروهي که درختان صنوبر را مي‌پرستيدند] و اقوام بسيار ديگري را که در اين ميان بودند، هلاک کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2894, N'و براي هر يک از آنها مثلها زديم؛ و (چون سودي نداد،) همگي را نابود کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2895, N'آنها [= مشرکان مکه‌] از کنار شهري که باران شر [= باراني از سنگهاي آسماني‌] بر آن باريده بود [= ديار قوم لوط] گذشتند؛ آيا آن را نمي‌ديدند؟! (آري، مي‌ديدند) ولي به رستاخيز ايمان نداشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2896, N'و هنگامي که تو را مي‌بينند، تنها به باد استهزايت مي‌گيرند (و مي‌گويند:) آيا اين همان کسي است که خدا او را بعنوان پيامبر برانگيخته است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2897, N'اگر ما بر پرستش خدايانمان استقامت نمي‌کرديم، بيم آن مي‌رفت که ما را گمراه سازد! اما هنگامي که عذاب الهي را ببينند، بزودي مي‌فهمند چه کسي گمراهتر بوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2898, N'آيا ديدي کسي را که هواي نفسش را معبود خود برگزيده است؟! آيا تو مي‌تواني او را هدايت کني (يا به دفاع از او برخيزي)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2899, N'آيا گمان مي‌بري بيشتر آنان مي‌شنوند يا مي‌فهمند؟! آنان فقط همچون چهارپايانند، بلکه گمراهترند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2900, N'آيا نديدي چگونه پروردگارت سايه را گسترده ساخت؟! و اگر مي‌خواست آن را ساکن قرار مي‌داد؛ سپس خورشيد را بر وجود آن دليل قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2901, N'سپس آن را آهسته جمع مي‌کنيم (و نظام سايه و آفتاب را حاکم مي‌سازيم)!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2902, N'او کسي است که شب را براي شما لباس قرار داد، و خواب را مايه استراحت، و روز را وسيله حرکت و حيات!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2903, N'او کسي است که بادها را بشارتگراني پيش از رحمتش فرستاد، و از آسمان آبي پاک‌کننده نازل کرديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2904, N'تا بوسيله آن، سرزمين مرده‌اي را زنده کنيم؛ و آن را به مخلوقاتي که آفريده‌ايم -چهارپايان و انسانهاي بسيار- مي‌نوشانيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2905, N'ما اين آيات را بصورتهاي گوناگون براي آنان بيان کرديم تا متذکر شوند، ولي بيشتر مردم از هر کاري جز انکار و کفر ابا دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2906, N'و اگر مي‌خواستيم، در هر شهر و دياري بيم‌دهنده‌اي برمي‌انگيختيم (ولي اين کار لزومي نداشت).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2907, N'بنابر اين از کافران اطاعت مکن، و بوسيله آن [= قرآن‌] با آنان جهاد بزرگي بنما!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2908, N'او کسي است که دو دريا را در کنار هم قرار داد؛ يکي گوارا و شيرين، و ديگر شور و تلخ؛ و در ميان آنها برزخي قرار داد تا با هم مخلوط نشوند (گويي هر يک به ديگري مي‌گويد:) دور باش و نزديک نيا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2909, N'او کسي است که از آب، انساني را آفريد؛ سپس او را نسب و سبب قرار داد (و نسل او را از اين دو طريق گسترش داد)؛ و پروردگار تو همواره توانا بوده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2910, N'آنان جز خدا چيزهايي را مي‌پرستند که نه به آنان سودي مي‌رساند و نه زياني؛ و کافران هميشه در برابر پروردگارشان (در طريق کفر) پشتيبان يکديگرند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2911, N'(اي پيامبر!) ما تو را جز بعنوان بشارت دهنده و انذار کننده نفرستاديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2912, N'بگو: «من در برابر آن (ابلاغ آيين خدا) هيچ گونه پاداشي از شما نمي‌طلبم؛ مگر کسي که بخواهد راهي بسوي پروردگارش برگزيند (اين پاداش من است.)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2913, N'و توکل کن بر آن زنده‌اي که هرگز نمي‌ميرد؛ و تسبيح و حمد او را بجا آور؛ و همين بس که او از گناهان بندگانش آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2914, N'همان (خدايي) که آسمانها و زمين و آنچه را ميان اين دو وجود دارد، در شش روز [= شش دوران‌] آفريد؛ سپس بر عرش (قدرت) قرار گرفت (و به تدبير جهان پرداخت، او خداوند) رحمان است؛ از او بخواه که از همه چيز آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2915, N'و هنگامي که به آنان گفته شود: «براي خداوند رحمان سجده کنيد!» مي‌گويند:«رحمان چيست؟! (ما اصلا رحمان را نمي‌شناسيم!) آيا براي چيزي سجده کنيم که تو به ما دستور مي‌دهي؟!» (اين سخن را مي‌گويند) و بر نفرتشان افزوده مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2916, N'جاودان و پربرکت است آن (خدايي) که در آسمان منزلگاه‌هائي براي ستارگان قرار داد؛ و در ميان آن، چراغ روشن و ماه تاباني آفريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2917, N'و او همان کسي است که شب و روز را جانشين يکديگر قرار داد براي کسي که بخواهد متذکر شود يا شکرگزاري کند (و آنچه را در روز کوتاهي کرده در شب انجام دهد و به عکس).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2918, N'بندگان (خاص خداوند) رحمان، کساني هستند که با آرامش و بي‌تکبر بر زمين راه مي‌روند؛ و هنگامي که جاهلان آنها را مخاطب سازند (و سخنان نابخردانه گويند)، به آنها سلام مي‌گويند (و با بي‌اعتنايي و بزرگواري مي‌گذرند)؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2919, N'کساني که شبانگاه براي پروردگارشان سجده و قيام مي‌کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2920, N'و کساني که مي‌گويند: «پروردگارا! عذاب جهنم را از ما برطرف گردان، که عذابش سخت و پر دوام است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2921, N'مسلما آن (جهنم)، بد جايگاه و بد محل اقامتي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2922, N'و کساني که هرگاه انفاق کنند، نه اسراف مي‌نمايند و نه سخت‌گيري؛ بلکه در ميان اين دو، حد اعتدالي دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2923, N'و کساني که معبود ديگري را با خداوند نمي‌خوانند؛ و انساني را که خداوند خونش را حرام شمرده، جز بحق نمي‌کشند؛ و زنا نمي‌کنند؛ و هر کس چنين کند، مجازات سختي خواهد ديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2924, N'عذاب او در قيامت مضاعف مي‌گردد، و هميشه با خواري در آن خواهد ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2925, N'مگر کساني که توبه کنند و ايمان آورند و عمل صالح انجام دهند، که خداوند گناهان آنان را به حسنات مبدل مي‌کند؛ و خداوند همواره آمرزنده و مهربان بوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2926, N'و کسي که توبه کند و عمل صالح انجام دهد، بسوي خدا بازگشت مي‌کند (و پاداش خود را از او مي‌گيرد.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2927, N'و کساني که شهادت به باطل نمي‌دهند (و در مجالس باطل شرکت نمي‌کنند)؛ و هنگامي که با لغو و بيهودگي برخورد کنند، بزرگوارانه از آن مي‌گذرند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2928, N'و کساني که هرگاه آيات پروردگارشان به آنان گوشزد شود، کر و کور روي آن نمي افتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2929, N'و کساني که مي‌گويند: «پروردگارا! از همسران و فرزندانمان مايه روشني چشم ما قرارده، و ما را براي پرهيزگاران پيشوا گردان!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2930, N'(آري،) آنها هستند که درجات عالي بهشت در برابر شکيباييشان به آنان پاداش داده مي‌شود؛ و در آن، با تحيت و سلام روبه‌رو مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2931, N'در حالي که جاودانه در آن خواهند ماند؛ چه قرارگاه و محل اقامت خوبي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2932, N'بگو: «پروردگارم براي شما ارجي قائل نيست اگر دعاي شما نباشد؛ شما (آيات خدا و پيامبران را) تکذيب کرديد، و (اين عمل) دامان شما را خواهد گرفت و از شما جدا نخواهد شد!»ظ')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2933, N'طسم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2934, N'اين آيات کتاب روشنگر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2935, N'گويي مي‌خواهي جان خود را از شدت اندوه از دست دهي بخاطر اينکه آنها ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2936, N'اگر ما اراده کنيم، از آسمان بر آنان آيه‌اي نازل مي‌کنيم که گردنهايشان در برابر آن خاضع گردد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2937, N'و هيچ ذکر تازه‌اي از سوي خداوند مهربان براي آنها نمي‌آيد مگر اينکه از آن روي‌گردان مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2938, N'آنان تکذيب کردند؛ اما بزودي اخبار (کيفر) آنچه را استهزا مي‌کردند به آنان مي‌رسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2939, N'آيا آنان به زمين نگاه نکردند که چقدر از انواع گياهان پرارزش در آن رويانديم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2940, N'در اين، نشانه روشني است (بر وجود خدا)؛ ولي بيشترشان هرگز مؤمن نبوده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2941, N'و پروردگار تو عزيز و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2942, N'(به خاطر بياور) هنگامي را که پروردگارت موسي را ندا داد که به سراغ قوم ستمگر برو...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2943, N'قوم فرعون، آيا آنان (از مخالفت فرمان پروردگار) پرهيز نمي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2944, N'(موسي) عرض کرد: «پروردگارا! از آن بيم دارم که مرا تکذيب کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2945, N'و سينه‌ام تنگ شود، و زبانم بقدر کافي گويا نيست؛ (برادرم) هارون را نيز رسالت ده (تا مرا ياري کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2946, N'و آنان (به اعتقاد خودشان) بر گردن من گناهي دارند؛ مي‌ترسم مرا بکشند (و اين رسالت به پايان نرسد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2947, N'فرمود: «چنين نيست، (آنان کاري نمي‌توانند انجام دهند)! شما هر دو با آيات ما (براي هدايتشان) برويد؛ ما با شما هستيم و (سخنانتان را) مي‌شنويم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2948, N'به سراغ فرعون برويد و بگوييد: ما فرستاده پروردگار جهانيان هستيم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2949, N'بني اسرائيل را با ما بفرست!» (آنها به سراغ فرعون آمدند)؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2950, N'(فرعون) گفت: «آيا ما تو را در کودکي در ميان خود پرورش نداديم، و سالهايي از زندگيت را در ميان ما نبودي؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2951, N'و سرانجام، آن کارت را (که نمي‌بايست انجام دهي) انجام دادي (و يک نفر از ما را کشتي)، و تو از ناسپاساني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2952, N'(موسي) گفت: «من آن کار را انجام دادم در حالي که از بي‌خبران بودم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2953, N'پس هنگامي که از شما ترسيدم فرار کردم؛ و پروردگارم به من حکمت و دانش بخشيد، و مرا از پيامبران قرار داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2954, N'آيا اين منتي است که تو بر من مي‌گذاري که بني اسرائيل را برده خود ساخته‌اي؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2955, N'فرعون گفت: «پروردگار عالميان چيست؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2956, N'(موسي) گفت: «پروردگار آسمانها و زمين و آنچه ميان آن دو است، اگر اهل يقين هستيد!».')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2957, N'(فرعون) به اطرافيانش گفت: «آيا نمي‌شنويد (اين مرد چه مي‌گويد)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2958, N'(موسي) گفت: «او پروردگار شما و پروردگار نياکان شماست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2959, N'(فرعون) گفت: «پيامبري که بسوي شما فرستاده شده مسلما ديوانه است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2960, N'(موسي) گفت: «او پروردگار مشرق و مغرب و آنچه ميان آن دو است مي‌باشد، اگر شما عقل و انديشه خود را به کار مي‌گرفتيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2961, N'(فرعون خشمگين شد و) گفت: «اگر معبودي غير از من برگزيني، تو را از زندانيان قرار خواهم داد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2962, N'(موسي) گفت: «حتي اگر نشانه آشکاري براي تو بياورم (باز ايمان نمي‌آوري)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2963, N'گفت: «اگر راست مي‌گويي آن را بياور!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2964, N'در اين هنگام موسي عصاي خود را افکند، و ناگهان مار عظيم و آشکاري شد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2965, N'و دست خود را (در گريبان فرو برد و) بيرون آورد، و در برابر بينندگان سفيد و روشن بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2966, N'(فرعون) به گروهي که اطراف او بودند گفت: «اين ساحر آگاه و ماهري است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2967, N'او مي‌خواهد با سحرش شما را از سرزمينتان بيرون کند! شما چه نظر مي‌دهيد؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2968, N'گفتند: «او و برادرش را مهلت ده؛ و مأموران را براي بسيج به تمام شهرها اعزام کن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2969, N'تا هر ساحر ماهر و دانايي را نزد تو آورند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2970, N'سرانجام ساحران براي وعده‌گاه روز معيني جمع‌آوري شدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2971, N'و به مردم گفته شد: «آيا شما نيز (در اين صحنه) اجتماع مي‌کنيد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2972, N'تا اگر ساحران پيروز شوند، از آنان پيروي کنيم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2973, N'هنگامي که ساحران آمدند، به فرعون گفتند: «آيا اگر ما پيروز شويم، پاداش مهمي خواهيم داشت؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2974, N'گفت: «(آري،) و در آن صورت شما از مقربان خواهيد بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2975, N'(روز موعود فرا رسيد و همگي جمع شدند؛) موسي به ساحران گفت: «آنچه را مي‌خواهيد بيفکنيد، بيفکنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2976, N'آنها طنابها و عصاهاي خود را افکندند و گفتند: «به عزت فرعون، ما قطعا پيروزيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2977, N'سپس موسي عصايش را افکند، ناگهان تمام وسايل دروغين آنها را بلعيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2978, N'فورا همه ساحران به سجده افتادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2979, N'گفتند: «ما به پروردگار عالميان ايمان آورديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2980, N'پروردگار موسي و هارون!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2981, N'(فرعون) گفت: «آيا پيش از اينکه به شما اجازه دهم به او ايمان آورديد؟! مسلما او بزرگ و استاد شماست که به شما سحر آموخته (و اين يک توطئه است)! اما بزودي خواهيد دانست! دستها و پاهاي شما را بعکس يکديگر قطع مي‌کنم، و همه شما را به دار مي‌آويزم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2982, N'گفتند: «مهم نيست، (هر کاري از دستت ساخته است بکن)! ما بسوي پروردگارمان بازمي‌گرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2983, N'ما اميدواريم که پروردگارمان خطاهاي ما را ببخشد، چرا که ما نخستين ايمان‌آورندگان بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2984, N'و به موسي وحي کرديم که شبانه بندگانم را (از مصر) کوچ ده، زيرا شما مورد تعقيب هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2985, N'فرعون (از اين ماجرا آگاه شد و) مأموران بسيج نيرو را به شهرها فرستاد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2986, N'(و گفت:) اينها مسلما گروهي اندکند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2987, N'و اينها ما را به خشم آورده‌اند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2988, N'و ما همگي آماده پيکاريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2989, N'(سرانجام فرعونيان مغلوب شدند،) و ما آنها را از باغها و چشمه‌ها بيرون رانديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2990, N'و از گنجها و قصرهاي مجلل!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2991, N'(آري،) اينچنين کرديم! و بني اسرائيل را وارث آنها ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2992, N'آنان به تعقيب بني اسرائيل پرداختند، و به هنگام طلوع آفتاب به آنها رسيدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2993, N'هنگامي که دو گروه يکديگر را ديدند، ياران موسي گفتند: «ما در چنگال فرعونيان گرفتار شديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2994, N'(موسي) گفت: «چنين نيست! يقينا پروردگارم با من است، بزودي مرا هدايت خواهد کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2995, N'و بدنبال آن به موسي وحي کرديم: «عصايت را به دريا بزن!» (عصايش را به دريا زد،) و دريا از هم شکافته شد، و هر بخشي همچون کوه عظيمي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2996, N'و در آنجا ديگران [= لشکر فرعون‌] را نيز (به دريا) نزديک ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2997, N'و موسي و تمام کساني را که با او بودند نجات داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2998, N'سپس ديگران را غرق کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (2999, N'در اين جريان، نشانه روشني است ولي بيشترشان ايمان نياوردند! (چرا که طالب حق نبودند)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3000, N'و پروردگارت شکست‌ناپذير و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3001, N'و بر آنان خبر ابراهيم را بخوان،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3002, N'هنگامي که به پدر و قومش گفت: «چه چيز را مي‌پرستيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3003, N'گفتند: «بتهايي را مي‌پرستيم، و همه روز ملازم عبادت آنهاييم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3004, N'گفت: «آيا هنگامي که آنها را مي‌خوانيد صداي شما را مي‌شنوند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3005, N'يا سود و زياني به شما مي‌رسانند؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3006, N'گفتند: «ما فقط نياکان خود را يافتيم که چنين مي‌کنند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3007, N'گفت: «آيا ديديد (اين) چيزهايي را که پيوسته پرستش مي‌کرديد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3008, N'شما و پدران پيشين شما،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3009, N'همه آنها دشمن من هستند (و من دشمن آنها)، مگر پروردگار عالميان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3010, N'همان کسي که مرا آفريد، و پيوسته راهنمائيم مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3011, N'و کسي که مرا غذا مي‌دهد و سيراب مي‌نمايد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3012, N'و هنگامي که بيمار شوم مرا شفا مي‌دهد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3013, N'و کسي که مرا مي‌ميراند و سپس زنده مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3014, N'و کسي که اميد دارم گناهم را در روز جزا ببخشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3015, N'پروردگارا! به من علم و دانش ببخش، و مرا به صالحان ملحق کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3016, N'و براي من در ميان امتهاي آينده، زبان صدق (و ذکر خيري) قرار ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3017, N'و مرا وارثان بهشت پرنعمت گردان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3018, N'و پدرم [= عمويم‌] را بيامرز، که او از گمراهان بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3019, N'و در آن روز که مردم برانگيخته مي‌شوند، مرا شرمنده و رسوا مکن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3020, N'در آن روز که مال و فرزندان سودي نمي‌بخشد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3021, N'مگر کسي که با قلب سليم به پيشگاه خدا آيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3022, N'(در آن روز،) بهشت براي پرهيزکاران نزديک مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3023, N'و دوزخ براي گمراهان آشکار مي‌گردد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3024, N'و به آنان گفته مي‌شود: «کجا هستند معبوداني که آنها را پرستش مي‌کرديد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3025, N'معبودهايي غير از خدا؟! آيا آنها شما را ياري مي‌کنند، يا کسي به ياري آنها مي‌آيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3026, N'در آن هنگام همه آن معبودان با عابدان گمراه به دوزخ افکنده مي‌شوند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3027, N'و همچنين همگي لشکريان ابليس!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3028, N'آنها در آنجا در حالي که به مخاصمه برخاسته‌اند مي‌گويند:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3029, N'«به خدا سوگند که ما در گمراهي آشکاري بوديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3030, N'چون شما را با پروردگار عالميان برابر مي‌شمرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3031, N'اما کسي جز مجرمان ما را گمراه نکرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3032, N'(افسوس که امروز) شفاعت‌کنندگاني براي ما وجود ندارد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3033, N'و نه دوست گرم و پرمحبتي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3034, N'اي کاش بار ديگر (به دنيا) بازگرديم و از مؤمنان باشيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3035, N'در اين ماجرا، نشانه (و عبرتي) است؛ ولي بيشتر آنان مؤمن نبودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3036, N'و پروردگار تو عزيز و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3037, N'قوم نوح رسولان را تکذيب کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3038, N'هنگامي که برادرشان نوح به آنان گفت: «آيا تقوا پيشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3039, N'مسلما من براي شما پيامبري امين هستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3040, N'تقواي الهي پيشه کنيد و مرا اطاعت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3041, N'من براي اين دعوت، هيچ مزدي از شما نمي‌طلبم؛ اجر من تنها بر پروردگار عالميان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3042, N'پس، تقواي الهي پيشه کنيد و مرا اطاعت نماييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3043, N'گفتند: «آيا ما به تو ايمان بياوريم در حالي که افراد پست و بي‌ارزش از تو پيروي کرده‌اند؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3044, N'(نوح) گفت: «من چه مي‌دانم آنها چه کاري داشته‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3045, N'حساب آنها تنها با پروردگار من است اگر شما مي‌فهميديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3046, N'و من هرگز مؤمنان را طرد نخواهم کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3047, N'من تنها انذارکننده‌اي آشکارم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3048, N'گفتند: «اي نوح! اگر (از حرفهايت) دست برنداري، سنگباران خواهي شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3049, N'گفت: «پروردگارا! قوم من، مرا تکذيب کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3050, N'اکنون ميان من و آنها جدايي بيفکن؛ و مرا و مؤمناني را که با من هستند رهايي بخش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3051, N'ما، او و کساني را که با او بودند، در آن کشتي که پر (از انسان و انواع حيوانات) بود، رهايي بخشيديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3052, N'سپس بقيه را غرق کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3053, N'در اين ماجرا نشانه روشني است؛ اما بيشتر آنان مؤمن نبودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3054, N'و پروردگار تو عزيز و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3055, N'قوم عاد (نيز) رسولان (خدا) را تکذيب کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3056, N'هنگامي که برادرشان هود گفت: «آيا تقوا پيشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3057, N'مسلما من براي شما پيامبري امين هستم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3058, N'پس تقواي الهي پيشه کنيد و مرا اطاعت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3059, N'من در برابر اين دعوت، هيچ اجر و پاداشي از شما نمي‌طلبم؛ اجر و پاداش من تنها بر پروردگار عالميان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3060, N'آيا شما بر هر مکان مرتفعي نشانه‌اي از روي هوا و هوس مي‌سازيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3061, N'و قصرها و قلعه‌هاي زيبا و محکم بنا مي‌کنيد شايد در دنيا جاودانه بمانيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3062, N'و هنگامي که کسي را مجازات مي‌کنيد همچون جباران کيفر مي‌دهيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3063, N'پس تقواي الهي پيشه کنيد و مرا اطاعت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3064, N'و از (نافرماني) خدايي بپرهيزيد که شما را به نعمتهايي که مي‌دانيد امداد کرده؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3065, N'شما را به چهارپايان و نيز پسران (لايق و برومند) امداد فرموده؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3066, N'همچنين به باغها و چشمه‌ها!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3067, N'(اگر کفران کنيد،) من بر شما از عذاب روزي بزرگ مي‌ترسم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3068, N'آنها [= قوم عاد] گفتند: «براي ما تفاوت نمي‌کند، چه ما را انذار کني يا نکني؛ (بيهوده خود را خسته مکن)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3069, N'اين همان روش (و افسانه‌هاي) پيشينيان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3070, N'و ما هرگز مجازات نخواهيم شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3071, N'آنان هود را تکذيب کردند، ما هم نابودشان کرديم؛ و در اين، آيت و نشانه‌اي است (براي آگاهان)؛ ولي بيشتر آنان مؤمن نبودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3072, N'و پروردگار تو عزيز و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3073, N'قوم ثمود رسولان (خدا) را تکذيب کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3074, N'هنگامي که صالح به آنان گفت: «آيا تقوا پيشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3075, N'من براي شما پيامبري امين هستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3076, N'پس تقواي الهي پيشه کنيد و مرا اطاعت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3077, N'من در برابر اين دعوت، اجر و پاداشي از شما نمي‌طلبم؛ اجر من تنها بر پروردگار عالميان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3078, N'آيا شما تصور مي‌کنيد هميشه در نهايت امنيت در نعمتهايي که اينجاست مي‌مانيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3079, N'در اين باغها و چشمه‌ها،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3080, N'در اين زراعتها و نخلهايي که ميوه‌هايش شيرين و رسيده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3081, N'و از کوه‌ها خانه‌هايي مي‌تراشيد، و در آن به عيش و نوش مي‌پردازيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3082, N'پس از خدا بترسيد و مرا اطاعت کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3083, N'و فرمان مسرفان را اطاعت نکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3084, N'همانها که در زمين فساد مي‌کنند و اصلاح نمي‌کنند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3085, N'گفتند: «(اي صالح!) تو از افسون شدگاني (و عقل خود را از دست داده‌اي!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3086, N'تو فقط بشري همچون مائي؛ اگر راست مي‌گويي آيت و نشانه‌اي بياور!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3087, N'گفت: «اين ناقه‌اي است (که آيت الهي است) براي او سهمي (از آب قريه)، و براي شما سهم روز معيني است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3088, N'کمترين آزاري به آن نرسانيد، که عذاب روزي بزرگ شما را فرا خواهد گرفت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3089, N'سرانجام بر آن (ناقه) حمله نموده آن را «پي» کردند؛ (و هلاک نمودند) سپس از کرده خود پشيمان شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3090, N'و عذاب الهي آنان را فرا گرفت؛ در اين، آيت و نشانه‌اي است؛ ولي بيشتر آنان مؤمن نبودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3091, N'و پروردگار تو عزيز و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3092, N'قوم لوط فرستادگان (خدا) را تکذيب کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3093, N'هنگامي که برادرشان لوط به آنان گفت: «آيا تقوا پيشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3094, N'من براي شما پيامبري امين هستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3095, N'پس تقواي الهي پيشه کنيد و مرا اطاعت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3096, N'من در برابر اين دعوت، اجري از شما نمي‌طلبم، اجر من فقط بر پروردگار عالميان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3097, N'آيا در ميان جهانيان، شما به سراغ جنس ذکور مي‌رويد (و همجنس بازي مي‌کنيد، آيا اين زشت و ننگين نيست؟!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3098, N'و همسراني را که پروردگارتان براي شما آفريده است رها مي‌کنيد؟! (حقا) شما قوم تجاوزگري هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3099, N'گفتند: «اي لوط! اگر (از اين سخنان) دست برنداري، به يقين از اخراج شدگان خواهي بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3100, N'گفت: «من دشمن سرسخت اعمال شما هستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3101, N'پروردگارا! من و خاندانم را از آنچه اينها انجام مي‌دهند رهايي بخش!»')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3102, N'ما او و تمامي خاندانش را نجات داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3103, N'جز پيرزني که در ميان بازماندگان بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3104, N'سپس ديگران را هلاک کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3105, N'و باراني (از سنگ) بر آنها فرستاديم؛ چه باران بدي بود باران انذارشدگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3106, N'در اين (ماجراي قوم لوط و سرنوشت شوم آنها) آيتي است؛ اما بيشترشان مؤمن نبودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3107, N'و پروردگار تو عزيز و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3108, N'اصحاب ايکه [= شهري نزديک مدين‌] رسولان (خدا) را تکذيب کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3109, N'هنگامي که شعيب به آنها گفت: «آيا تقوا پيشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3110, N'مسلما من براي شما پيامبري امين هستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3111, N'پس تقواي الهي پيشه کنيد و مرا اطاعت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3112, N'من در برابر اين دعوت، پاداشي از شما نمي‌طلبم؛ اجر من تنها بر پروردگار عالميان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3113, N'حق پيمانه را ادا کنيد (و کم فروشي نکنيد)، و ديگران را به خسارت نيفکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3114, N'و با ترازوي صحيح وزن کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3115, N'و حق مردم را کم نگذاريد، و در زمين تلاش براي فساد نکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3116, N'و از (نافرماني) کسي که شما و اقوام پيشين را آفريد بپرهيزيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3117, N'آنها گفتند: «تو فقط از افسون‌شدگاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3118, N'تو بشري همچون مائي، تنها گماني که درباره تو داريم اين است که از دروغگوياني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3119, N'اگر راست مي‌گويي، سنگهايي از آسمان بر سر ما بباران!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3120, N'(شعيب) گفت: «پروردگار من به اعمالي که شما انجام مي‌دهيد داناتر است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3121, N'سرانجام او را تکذيب کردند، و عذاب روز سايبان (سايباني از ابر صاعقه‌خيز) آنها را فراگرفت؛ يقينا آن عذاب روز بزرگي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3122, N'در اين ماجرا، آيت و نشانه‌اي است؛ ولي بيشتر آنها مؤمن نبودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3123, N'و پروردگار تو عزيز و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3124, N'مسلما اين (قرآن) از سوي پروردگار جهانيان نازل شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3125, N'روح الامين آن را نازل کرده است...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3126, N'بر قلب (پاک) تو، تا از انذارکنندگان باشي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3127, N'آن را به زبان عربي آشکار (نازل کرد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3128, N'و توصيف آن در کتابهاي پيشينيان نيز آمده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3129, N'آيا همين نشانه براي آنها کافي نيست که علماي بني اسرائيل بخوبي از آن آگاهند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3130, N'هرگاه ما آن را بر بعضي از عجم [= غير عرب‌]ها نازل مي‌کرديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3131, N'و او آن را بر ايشان مي‌خواند، به آن ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3132, N'(آري،) اين گونه (با بياني رسا) قرآن را در دلهاي مجرمان وارد مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3133, N'(اما) به آن ايمان نمي‌آورند تا عذاب دردناک را با چشم خود ببينند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3134, N'ناگهان به سراغشان مي‌آيد، در حالي که توجه ندارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3135, N'و (در آن هنگام) مي‌گويند: «آيا به ما مهلتي داده خواهد شد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3136, N'آيا براي عذاب ما عجله مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3137, N'به ما خبر ده، اگر (باز هم) سالياني آنها را از اين زندگي بهره‌مند سازيم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3138, N'سپس عذابي که به آنها وعده داده شده به سراغشان بيايد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3139, N'اين تمتع و بهره‌گيري از دنيا براي آنها سودي نخواهد داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3140, N'ما هيچ شهر و دياري را هلاک نکرديم مگر اينکه انذارکنندگاني (از پيامبران الهي) داشتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3141, N'تا متذکر شوند؛ و ما هرگز ستمکار نبوديم! (که بدون اتمام حجت مجازات کنيم)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3142, N'شياطين و جنيان (هرگز) اين آيات را نازل نکردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3143, N'و براي آنها سزاوار نيست؛ و قدرت ندارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3144, N'آنها از استراق سمع (و شنيدن اخبار آسمانها) برکنارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3145, N'(اي پيامبر!) هيچ معبودي را با خداوند مخوان، که از معذبين خواهي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3146, N'و خويشاوندان نزديکت را انذار کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3147, N'و بال و پر خود را براي مؤمناني که از تو پيروي مي‌کنند بگستر!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3148, N'اگر تو را نافرماني کنند بگو: «من از آنچه شما انجام مي‌دهيد بيزارم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3149, N'و بر خداوند عزيز و رحيم توکل کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3150, N'همان کسي که تو را به هنگامي که (براي عبادت) برمي‌خيزي مي‌بيند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3151, N'و (نيز) حرکت تو را در ميان سجده‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3152, N'اوست خداي شنوا و دانا.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3153, N'آيا به شما خبر دهم که شياطين بر چه کسي نازل مي‌شوند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3154, N'آنها بر هر دروغگوي گنهکار نازل مي‌گردند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3155, N'آنچه را مي‌شنوند (به ديگران) القا مي‌کنند؛ و بيشترشان دروغگو هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3156, N'(پيامبر اسلام شاعر نيست؛) شاعران کساني هستند که گمراهان از آنان پيروي مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3157, N'آيا نمي‌بيني آنها در هر وادي سرگردانند؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3158, N'و سخناني مي‌گويند که (به آنها) عمل نمي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3159, N'مگر کساني که ايمان آورده و کارهاي شايسته انجام مي‌دهند و خدا را بسيار ياد مي کنند، و به هنگامي که مورد ستم واقع مي‌شوند به دفاع از خويشتن (و مؤمنان) برمي‌خيزند (و از شعر در اين راه کمک مي‌گيرند)؛ آنها که ستم کردند به زودي مي‌دانند که بازگشتشان به کجاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3160, N'طس - اين آيات قرآن و کتاب مبين است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3161, N'وسيله هدايت و بشارت براي مؤمنان است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3162, N'همان کساني که نماز را برپا مي‌دارند، و زکات را ادا مي‌کنند، و آنان به آخرت يقين دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3163, N'کساني که به آخرت ايمان ندارند، اعمال (بد)شان را براي آنان زينت مي‌دهيم بطوري که سرگردان مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3164, N'آنان کساني هستند که عذاب بد (و دردناک) براي آنهاست؛ و آنها در آخرت، زيانکارترين مردمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3165, N'به يقين اين قرآن از سوي حکيم و دانايي بر تو القا مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3166, N'(به خاطر بياور) هنگامي را که موسي به خانواده خود گفت: «من آتشي از دور ديدم؛ (همين جا توقف کنيد؛) بزودي خبري از آن براي شما مي‌آورم، يا شعله آتشي تا گرم شويد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3167, N'هنگامي که نزد آتش آمد، ندايي برخاست که: «مبارک باد آن کس که در آتش است و کسي که در اطراف آن است [= فرشتگان و موسي‌] و منزه است خداوندي که پروردگار جهانيان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3168, N'اي موسي! من خداوند عزيز و حکيمم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3169, N'و عصايت را بيفکن! -هنگامي که (موسي) به آن نگاه کرد، ديد (با سرعت) همچون ماري به هر سو مي‌دود (ترسيد و) به عقب برگشت، و حتي پشت سر خود را نگاه نکرد- اي موسي! نترس، که رسولان در نزد من نمي‌ترسند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3170, N'مگر کسي که ستم کند؛ سپس بدي را به نيکي تبديل نمايد، که (توبه او را مي‌پذيرم، و) من غفور و رحيمم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3171, N'و دستت را در گريبانت داخل کن؛ هنگامي که خارج مي‌شود، سفيد و درخشنده است بي آنکه عيبي در آن باشد؛ اين در زمره معجزات نه‌گانه‌اي است که تو با آنها بسوي فرعون و قومش فرستاده مي‌شوي؛ آنان قومي فاسق و طغيانگرند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3172, N'و هنگامي که آيات روشني‌بخش ما به سراغ آنها آمد گفتند: «اين سحري است آشکار!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3173, N'و آن را از روي ظلم و سرکشي انکار کردند، در حالي که در دل به آن يقين داشتند! پس بنگر سرانجام تبهکاران (و مفسدان) چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3174, N'و ما به داوود و سليمان، دانشي عظيم داديم؛ و آنان گفتند: «ستايش از آن خداوندي است که ما را بر بسياري از بندگان مؤمنش برتري بخشيد.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3175, N'و سليمان وارث داوود شد، و گفت: «اي مردم! زبان پرندگان به ما تعليم داده شده، و از هر چيز به ما عطا گرديده؛ اين فضيلت آشکاري است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3176, N'لشکريان سليمان، از جن و انس و پرندگان، نزد او جمع شدند؛ آنقدر زياد بودند که بايد توقف مي‌کردند تا به هم ملحق شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3177, N'(آنها حرکت کردند) تا به سرزمين مورچگان رسيدند؛ مورچه‌اي گفت: «به لانه‌هاي خود برويد تا سليمان و لشکرش شما را پايمال نکنند در حالي که نمي‌فهمند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3178, N'سليمان از سخن او تبسمي کرد و خنديد و گفت: «پروردگارا! شکر نعمتهايي را که بر من و پدر و مادرم ارزاني داشته‌اي به من الهام کن، و توفيق ده تا عمل صالحي که موجب رضاي توست انجام دهم، و مرا برحمت خود در زمره بندگان صالحت وارد کن!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3179, N'(سليمان) در جستجوي آن پرنده [= هدهد] برآمد و گفت: «چرا هدهد را نمي‌بينم، يا اينکه او از غايبان است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3180, N'قطعا او را کيفر شديدي خواهم داد، يا او را ذبح مي‌کنم، يا بايد دليل روشني (براي غيبتش) براي من بياورد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3181, N'چندان درنگ نکرد (که هدهد آمد و) گفت: «من بر چيزي آگاهي يافتم که تو بر آن آگاهي نيافتي؛ من از سرزمين «سبا» يک خبر قطعي براي تو آورده‌ام!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3182, N'من زني را ديدم که بر آنان حکومت مي‌کند، و همه چيز در اختيار دارد، و (به خصوص) تخت عظيمي دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3183, N'او و قومش را ديدم که براي غير خدا -خورشيد- سجده مي‌کنند؛ و شيطان اعمالشان را در نظرشان جلوه داده، و آنها را از راه بازداشته؛ و از اين رو هدايت نمي‌شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3184, N'چرا براي خداوندي سجده نمي‌کنند که آنچه را در آسمانها و زمين پنهان است خارج (و آشکار) مي‌سازد، و آنچه را پنهان مي‌داريد يا آشکار مي‌کنيد مي‌داند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3185, N'خداوندي که معبودي جز او نيست، و پروردگار عرش عظيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3186, N'(سليمان) گفت: «ما تحقيق مي‌کنيم ببينيم راست گفتي يا از دروغگويان هستي؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3187, N'اين نامه مرا ببر و بر آنان بيفکن؛ سپس برگرد (و در گوشه‌اي توقف کن) ببين آنها چه عکس العملي نشان مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3188, N'(ملکه سبا) گفت: «اي اشراف! نامه پرارزشي به سوي من افکنده شده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3189, N'اين نامه از سليمان است، و چنين مي‌باشد: به نام خداوند بخشنده مهربان‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3190, N'توصيه من اين است که نسبت به من برتري‌جويي نکنيد، و بسوي من آييد در حالي که تسليم حق هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3191, N'(سپس) گفت: «اي اشراف (و اي بزرگان)! نظر خود را در اين امر مهم به من بازگو کنيد، که من هيچ کار مهمي را بدون حضور (و مشورت) شما انجام نداده‌ام!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3192, N'گفتند: «ما داراي نيروي کافي و قدرت جنگي فراوان هستيم، ولي تصميم نهايي با توست؛ ببين چه دستور مي‌دهي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3193, N'گفت: پادشاهان هنگامي که وارد منطقه آبادي شوند آن را به فساد و تباهي مي‌کشند، و عزيزان آنجا را ذليل مي‌کنند؛ (آري) کار آنان همين‌گونه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3194, N'و من (اکنون جنگ را صلاح نمي‌بينم،) هديه گرانبهايي براي آنان مي‌فرستم تا ببينم فرستادگان من چه خبر مي‌آورند (و از اين طريق آنها را بيازمايم)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3195, N'هنگامي که (فرستاده ملکه سبا)ا نزد سليمان آمد، گفت: «مي‌خواهيد مرا با مال کمک کنيد (و فريب دهيد)؟! آنچه خدا به من داده، بهتر است از آنچه به شما داده است؛ بلکه شما هستيد که به هديه‌هايتان خوشحال مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3196, N'بسوي آنان بازگرد (و اعلام کن) با لشکرياني به سراغ آنان مي‌آييم که قدرت مقابله با آن را نداشته باشند؛ و آنان را از آن (سرزمين آباد) با ذلت و خواري بيرون مي‌رانيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3197, N'(سليمان) گفت: «اي بزرگان! کدام يک از شما تخت او را براي من مي‌آورد پيش از آنکه به حال تسليم نزد من آيند؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3198, N'عفريتي از جن گفت: «من آن را نزد تو مي‌آورم پيش از آنکه از مجلست برخيزي و من نسبت به اين امر، توانا و امينم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3199, N'(اما) کسي که دانشي از کتاب (آسماني) داشت گفت: «پيش از آنکه چشم بر هم زني، آن را نزد تو خواهم آورد!» و هنگامي که (سليمان) آن (تخت) را نزد خود ثابت و پابرجا ديد گفت: «اين از فضل پروردگار من است، تا مرا آزمايش کند که آيا شکر او را بجا مي‌آورم يا کفران مي‌کنم؟! و هر کس شکر کند، به نفع خود شکر مي‌کند؛ و هر کس کفران نمايد (بزيان خويش نموده است، که) پروردگار من، غني و کريم است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3200, N'(سليمان) گفت: «تخت او را برايش ناشناس سازيد؛ ببينم آيا متوجه مي‌شود يا از کساني است که هدايت نخواهند شد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3201, N'هنگامي که آمد، به او گفته شد: «آيا تخت تو اين گونه است؟» گفت: گويا خود آن است! و ما پيش از اين هم آگاه بوديم و اسلام آورده بوديم!»')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3202, N'و او را از آنچه غير از خدا ميپرستيد بازداشت، که او [= ملکه سبا] از قوم کافران بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3203, N'به او گفته شد: «داخل حياط (قصر) شو!» هنگامي که نظر به آن افکند، پنداشت نهر آبي است و ساق پاهاي خود را برهنه کرد (تا از آب بگذرد؛ اما سليمان) گفت: «(اين آب نيست،) بلکه قصري است از بلور صاف!» (ملکه سبا) گفت: «پروردگارا! من به خود ستم کردم؛ و (اينک) با سليمان براي خداوندي که پروردگار عالميان است اسلام آوردم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3204, N'ما به سوي «ثمود»، برادرشان «صالح» را فرستاديم که: خداي يگانه را بپرستيد! اما آنان به دو گروه تقسيم شدند که به مخاصمه پرداختند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3205, N'(صالح) گفت: «اي قوم من! چرا براي بدي قبل از نيکي عجله مي‌کنيد (و عذاب الهي را مي‌طلبيد نه رحمت او را)؟! چرا از خداوند تقاضاي آمرزش نمي‌کنيد تا شايد مشمول رحمت (او) شويد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3206, N'آنها گفتند: «ما تو را و کساني که با تو هستند به فال بد گرفتيم!» (صالح) گفت: «فال (نيک و) بد شما نزد خداست (و همه مقدرات به قدرت او تعيين مي‌گردد)؛ بلکه شما گروهي هستيد فريب‌خورده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3207, N'و در آن شهر، نه گروهک بودند که در زمين فساد مي‌کردند و اصلاح نمي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3208, N'آنها گفتند: «بياييد قسم ياد کنيد به خدا که بر او [= صالح‌] و خانواده اش شبيخون مي زنيم (و آنها را به قتل مي‌رسانيم؛) سپس به ولي دم او مي‌گوييم: ما هرگز از هلاکت خانواده او خبر نداشتيم و در اين گفتار خود صادق هستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3209, N'آنها نقشه مهمي کشيدند، و ما هم نقشه مهمي؛ در حالي که آنها درک نمي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3210, N'بنگر عاقبت توطئه آنها چه شد، که ما آنها و قومشان همگي را نابود کرديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3211, N'اين خانه‌هاي آنهاست که بخاطر ظلم و ستمشان خالي مانده؛ و در اين نشانه روشني است براي کساني که آگاهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3212, N'و کساني را که ايمان آورده و تقوا پيشه کرده بودند نجات داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3213, N'و لوط را (به ياد آور) هنگامي که به قومش گفت: «آيا شما به سراغ کار بسيار زشتي مي‌رويد در حالي که (نتايج شوم آن را) مي‌بينيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3214, N'آيا شما بجاي زنان، از روي شهوت به سراغ مردان مي‌رويد؟! شما قومي نادانيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3215, N'آنها پاسخي جز اين نداشتند که (به يکديگر) گفتند: «خاندان لوط را از شهر و ديار خود بيرون کنيد، که اينها افرادي پاکدامن هستند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3216, N'ما او و خانواده‌اش را نجات داديم، بجز همسرش که مقدر کرديم جزء باقي‌ماندگان (در آن شهر) باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3217, N'سپس باراني (از سنگ) بر سر آنها بارانديم (و همگي زير آن مدفون شدند)؛ و چه بد است باران انذارشدگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3218, N'بگو: «حمد مخصوص خداست؛ و سلام بر بندگان برگزيده‌اش!» آيا خداوند بهتر است يا بتهايي که همتاي او قرارمي‌دهند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3219, N'(آيا بتهايي که معبود شما هستند بهترند) يا کسي که آسمانها و زمين را آفريده؟! و براي شما از آسمان، آبي فرستاد که با آن، باغهايي زيبا و سرورانگيز رويانديم؛ شما هرگز قدرت نداشتيد درختان آن را برويانيد! آيا معبود ديگري با خداست؟! نه، بلکه آنها گروهي هستند که (از روي ناداني، مخلوقات را) همطراز (پروردگارشان) قرار مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3220, N'يا کسي که زمين را مستقر و آرام قرار داد، و ميان آن نهرهايي روان ساخت، و براي آن کوه‌هاي ثابت و پابرجا ايجاد کرد، و ميان دو دريا مانعي قرار داد (تا با هم مخلوط نشوند؛ با اين حال) آيا معبودي با خداست؟! نه، بلکه بيشتر آنان نمي‌دانند (و جاهلند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3221, N'يا کسي که دعاي مضطر را اجابت مي‌کند و گرفتاري را برطرف مي‌سازد، و شما را خلفاي زمين قرارمي‌دهد؛ آيا معبودي با خداست؟! کمتر متذکر مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3222, N'يا کسي که شما را در تاريکيهاي صحرا و دريا هدايت مي‌کند، و کسي که بادها را بعنوان بشارت پيش از نزول رحمتش مي‌فرستد؛ آيا معبودي با خداست؟! خداوند برتر است از آنچه براي او شريک قرارمي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3223, N'يا کسي که آفرينش را آغاز کرد، سپس آن را تجديد مي‌کند، و کسي که شما را از آسمان و زمين روزي مي‌دهد؛ آيا معبودي با خداست؟! بگو: «دليلتان را بياوريد اگر راست مي‌گوييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3224, N'بگو: «کساني که در آسمانها و زمين هستند غيب نمي‌دانند جز خدا، و نمي‌دانند کي برانگيخته مي‌شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3225, N'آنها [= مشرکان‌] اطلاع صحيحي درباره آخرت ندارند؛ بلکه در اصل آن شک دارند؛ بلکه نسبت به آن نابينايند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3226, N'و کافران گفتند: «آيا هنگامي که ما و پدرانمان خاک شديم، (زنده مي‌شويم و) از دل خاک بيرون مي‌آييم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3227, N'اين وعده‌اي است که به ما و پدرانمان از پيش داده شده؛ اينها همان افسانه‌هاي خرافي پيشينيان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3228, N'بگو: «در روي زمين سير کنيد و ببينيد عاقبت کار مجرمان به کجا رسيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3229, N'از (تکذيب و انکار) آنان غمگين مباش، و سينه‌ات از توطئه آنان تنگ نشود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3230, N'آنها مي‌گويند: «اين وعده (عذاب که به ما مي‌دهيد) کي خواهد آمد اگر راست مي‌گوييد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3231, N'بگو: «شايد پاره‌اي از آنچه درباره آن شتاب مي‌کنيد، نزديک و در کنار شما باشد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3232, N'مسلما پروردگار تو نسبت به مردم، فضل (و رحمت) دارد؛ ولي بيشترشان شکرگزار نيستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3233, N'و پروردگارت آنچه را در سينه‌هايشان پنهان مي‌دارند و آنچه را آشکار مي‌کنند بخوبي مي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3234, N'و هيچ موجود پنهاني در آسمان و زمين نيست مگر اينکه در کتاب مبين (در لوح محفوظ و علم بي‌پايان پروردگار) ثبت است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3235, N'اين قرآن اکثر چيزهايي را که بني اسرائيل در آن اختلاف دارند براي آنان بيان مي‌کند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3236, N'و مايه هدايت و رحمت براي مؤمنان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3237, N'پروردگار تو ميان آنها در قيامت به حکم خود داوري مي‌کند؛ و اوست قادر دانا.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3238, N'پس بر خدا توکل کن، که تو بر حق آشکار هستي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3239, N'مسلما تو نمي‌تواني سخنت را به گوش مردگان برساني، و نمي‌تواني کران را هنگامي که روي برمي‌گردانند و پشت مي‌کنند فراخواني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3240, N'و نيز نمي‌تواني کوران را از گمراهيشان برهاني؛ تو فقط ميتواني سخن خود را به گوش کساني برساني که آماده پذيرش ايمان به آيات ما هستند و در برابر حق تسليمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3241, N'و هنگامي که فرمان عذاب آنها رسد (و در آستانه رستاخيز قرار گيرند)، جنبنده‌اي را از زمين براي آنها خارج مي‌کنيم که با آنان تکلم مي‌کند (و مي‌گويد) که مردم به آيات ما ايمان نمي‌آوردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3242, N'(به خاطر آور) روزي را که ما از هر امتي، گروهي را از کساني که آيات ما را تکذيب مي‌کردند محشور مي‌کنيم؛ و آنها را نگه مي‌داريم تا به يکديگر ملحق شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3243, N'تا زماني که (به پاي حساب) مي‌آيند، (به آنان) مي‌گويد: «آيا آيات مرا تکذيب کرديد و در صدد تحقيق برنيامديد؟! شما چه اعمالي انجام مي‌داديد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3244, N'در اين هنگام، فرمان عذاب بخاطر ظلمشان بر آنها واقع مي‌شود، و آنها سخني ندارند که بگويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3245, N'يا نديدند که ما شب را براي آرامش آنها قرار داديم و روز را روشني‌بخش؟! در اين امور نشانه‌هاي روشني است براي کساني که ايمان مي‌آورند (و آماده قبول حقند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3246, N'و (به خاطر آوريد) روزي را که در «صور» دميده مي‌شود، و تمام کساني که در آسمانها و زمين هستند در وحشت فرو مي‌روند، جز کساني که خدا خواسته؛ و همگي با خضوع در پيشگاه او حاضر مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3247, N'کوه‌ها را مي‌بيني، و آنها را ساکن و جامد مي‌پنداري، در حالي که مانند ابر در حرکتند؛ اين صنع و آفرينش خداوندي است که همه چيز را متقن آفريده؛ او از کارهايي که شما انجام مي‌دهيد مسلما آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3248, N'کساني که کار نيکي انجام دهند پاداشي بهتر از آن خواهند داشت؛ و آنان از وحشت آن روز درامانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3249, N'و آنها که اعمال بدي انجام دهند، به صورت در آتش افکنده مي‌شوند؛ آيا جزايي جز آنچه عمل مي‌کرديد خواهيد داشت؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3250, N'(بگو:) من مأمورم پروردگار اين شهر (مقدس مکه) را عبادت کنم، همان کسي که اين شهر را حرمت بخشيده؛ در حالي که همه چيز از آن اوست! و من مأمورم که از مسلمين باشم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3251, N'و اينکه قرآن را تلاوت کنم! هر کس هدايت شود بسود خود هدايت شده؛ و هر کس گمراه گردد (زيانش متوجه خود اوست؛) بگو: «من فقط از انذارکنندگانم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3252, N'بگو: «حمد و ستايش مخصوص ذات خداست؛ بزودي آياتش را به شما نشان مي‌دهد تا آن را بشناسيد؛ و پروردگار تو از آنچه انجام مي‌دهيد غافل نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3253, N'طسم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3254, N'اينها از آيات کتاب مبين است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3255, N'ما از داستان موسي و فرعون بحق بر تو مي‌خوانيم، براي گروهي که (طالب حقند و) ايمان مي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3256, N'فرعون در زمين برتري‌جويي کرد، و اهل آن را به گروه‌هاي مختلفي تقسيم نمود؛ گروهي را به ضعف و ناتواني مي‌کشاند، پسرانشان را سر مي‌بريد و زنانشان را (براي کنيزي و خدمت) زنده نگه مي‌داشت؛ او به يقين از مفسدان بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3257, N'ما مي‌خواهيم بر مستضعفان زمين منت نهيم و آنان را پيشوايان و وارثان روي زمين قرار دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3258, N'و حکومتشان را در زمين پابرجا سازيم؛ و به فرعون و هامان و لشکريانشان، آنچه را از آنها [= بني اسرائيل‌] بيم داشتند نشان دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3259, N'ما به مادر موسي الهام کرديم که: «او را شير ده؛ و هنگامي که بر او ترسيدي، وي را در دريا(ي نيل) بيفکن؛ و نترس و غمگين مباش، که ما او را به تو بازمي‌گردانيم، و او را از رسولان قرار مي‌دهيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3260, N'(هنگامي که مادر بفرمان خدا او را به دريا افکند) خاندان فرعون او را از آب گرفتند، تا سرانجام دشمن آنان و مايه اندوهشان گردد! مسلما فرعون و هامان و لشکريانشان خطاکار بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3261, N'همسر فرعون (چون ديد آنها قصد کشتن کودک را دارند) گفت: «نور چشم من و توست! او را نکشيد شايد براي ما مفيد باشد، يا او را بعنوان پسر خود برگزينيم!» و آنها نمي‌فهميدند (که دشمن اصلي خود را در آغوش خويش مي‌پرورانند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3262, N'(سرانجام) قلب مادر موسي از همه چيز (جز ياد فرزندش) تهي گشت؛ و اگر دل او را (بوسيله ايمان و اميد) محکم نکرده بوديم، نزديک بود مطلب را افشا کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3263, N'و (مادر موسي) به خواهر او گفت: «وضع حال او را پيگيري کن!» او نيز از دور ماجرا را مشاهده کرد در حالي که آنان بي‌خبر بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3264, N'ما همه زنان شيرده را از پيش بر او حرام کرديم (تا تنها به آغوش مادر بازگردد)؛ و خواهرش (که بيتابي مأموران را براي پيدا کردن دايه مشاهده کرد) گفت: «آيا شما را به خانواده‌اي راهنمايي کنم که مي‌توانند اين نوزاد را براي شما کفالت کنند و خيرخواه او باشند؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3265, N'ما او را به مادرش بازگردانديم تا چشمش روشن شود و غمگين نباشد و بداند که وعده الهي حق است؛ ولي بيشتر آنان نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3266, N'و هنگامي که (موسي) نيرومند و کامل شد، حکمت و دانش به او داديم؛ و اين گونه نيکوکاران را جزا مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3267, N'او به هنگامي که اهل شهر در غفلت بودند وارد شهر شد؛ ناگهان دو مرد را ديد که به جنگ و نزاع مشغولند؛ يکي از پيروان او بود (و از بني اسرائيل)، و ديگري از دشمنانش، آن که از پيروان او بود در برابر دشمنش از وي تقاضاي کمک نمود؛ موسي مشت محکمي بر سينه او زد و کار او را ساخت (و بر زمين افتاد و مرد)؛ موسي گفت: «اين (نزاع شما) از عمل شيطان بود، که او دشمن و گمراه‌کننده آشکاري است»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3268, N'(سپس) عرض کرد: «پروردگارا! من به خويشتن ستم کردم؛ مرا ببخش!» خداوند او را بخشيد، که او غفور و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3269, N'عرض کرد: «پروردگارا! بشکرانه نعمتي که به من دادي، هرگز پشتيبان مجرمان نخواهم بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3270, N'موسي در شهر ترسان بود و هر لحظه در انتظار حادثه‌اي (و در جستجوي اخبار)؛ ناگهان ديد همان کسي که ديروز از او ياري طلبيده بود فرياد مي‌زند و از او کمک مي‌خواهد، موسي به او گفت: «تو آشکارا انسان (ماجراجو و) گمراهي هستي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3271, N'و هنگامي که خواست با کسي که دشمن هر دوي آنها بود درگير شود و با قدرت مانع او گردد، (فريادش بلند شد،) گفت: «اي موسي مي‌خواهي! مرا بکشي همان گونه که ديروز انساني را کشتي؟! تو فقط مي‌خواهي جباري در روي زمين باشي، و نمي‌خواهي از مصلحان باشي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3272, N'(در اين هنگام) مردي با سرعت از دورترين نقطه شهر [= مرکز فرعونيان‌] آمد و گفت: «اي موسي! اين جمعيت براي کشتن تو به مشورت نشسته‌اند؛ فورا از شهر خارج شو، که من از خيرخواهان توام!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3273, N'موسي از شهر خارج شد در حالي که ترسان بود و هر لحظه در انتظار حادثه‌اي؛ عرض کرد: «پروردگارا! مرا از اين قوم ظالم رهايي بخش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3274, N'و هنگامي که متوجه جانب مدين شد گفت: «اميدوارم پروردگارم مرا به راه راست هدايت کند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3275, N'و هنگامي که به (چاه) آب مدين رسيد، گروهي از مردم را در آنجا ديد که چهارپايان خود را سيراب مي‌کنند؛ و در کنار آنان دو زن را ديد که مراقب گوسفندان خويشند (و به چاه نزديک نمي‌شوند؛ موسي) به آن دو گفت: «کار شما چيست؟ (چرا گوسفندان خود را آب نمي‌دهيد؟!)» گفتند: «ما آنها را آب نمي‌دهيم تا چوپانها همگي خارج شوند؛ و پدر ما پيرمرد کهنسالي است (و قادر بر اين کارها نيست.)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3276, N'موسي براي (گوسفندان) آن دو آب کشيد؛ سپس رو به سايه آورد و عرض کرد: «پروردگارا! هر خير و نيکي بر من فرستي، به آن نيازمندم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3277, N'ناگهان يکي از آن دو (زن) به سراغ او آمد در حالي که با نهايت حيا گام برمي‌داشت، گفت: «پدرم از تو دعوت مي‌کند تا مزد آب دادن (به گوسفندان) را که براي ما انجام دادي به تو بپردازد.» هنگامي که موسي نزد او [= شعيب‌] آمد و سرگذشت خود را شرح داد، گفت: «نترس، از قوم ظالم نجات يافتي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3278, N'يکي از آن دو (دختر) گفت: «پدرم! او را استخدام کن، زيرا بهترين کسي را که مي‌تواني استخدام کني آن کسي است که قوي و امين باشد (و او همين مرد است)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3279, N'(شعيب) گفت: «من مي‌خواهم يکي از اين دو دخترم را به همسري تو درآورم به اين شرط که هشت سال براي من کار کني؛ و اگر آن را تا ده سال افزايش دهي، محبتي از ناحيه توست؛ من نمي‌خواهم کار سنگيني بر دوش تو بگذارم؛ و ان شاء الله مرا از صالحان خواهي يافت»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3280, N'(موسي) گفت: «(مانعي ندارد،) اين قراردادي ميان من و تو باشد؛ البته هر کدام از اين دو مدت را انجام دهم ستمي بر من نخواهد بود (و من در انتخاب آن آزادم)! و خدا بر آنچه ما مي‌گوييم گواه است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3281, N'هنگامي که موسي مدت خود را به پايان رسانيد و همراه خانواده‌اش (از مدين به سوي مصر) حرکت کرد، از جانب طور آتشي ديد! به خانواده‌اش گفت: «درنگ کنيد که من آتشي ديدم! (مي‌روم) شايد خبري از آن براي شما بياورم، يا شعله‌اي از آتش تا با آن گرم شويد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3282, N'هنگامي که به سراغ آتش آمد، از کرانه راست دره، در آن سرزمين پر برکت، از ميان يک درخت ندا داده شد که: «اي موسي! منم خداوند، پروردگار جهانيان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3283, N'عصايت را بيفکن!» هنگامي که (عصا را افکند و) ديد همچون ماري با سرعت حرکت مي‌کند، ترسيد و به عقب برگشت، و حتي پشت سر خود را نگاه نکرد! ندا آمد: «برگرد و نترس، تو در امان هستي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3284, N'دستت را در گريبان خود فروبر، هنگامي که خارج مي‌شود سفيد و درخشنده است بدون عيب و نقص؛ و دستهايت را بر سينه‌ات بگذار، تا ترس و وحشت از تو دور شود! اين دو [= معجزه عصا و يد بيضا] برهان روشن از پروردگارت بسوي فرعون و اطرافيان اوست، که آنان قوم فاسقي هستند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3285, N'عرض کرد: «پروردگارا! من يک تن از آنان را کشته‌ام؛ مي‌ترسم مرا به قتل برسانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3286, N'و برادرم هارون زبانش از من فصيحتر است؛ او را همراه من بفرست تا ياور من باشد و مرا تصديق کند؛ مي‌ترسم مرا تکذيب کنند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3287, N'فرمود: «بزودي بازوان تو را بوسيله برادرت محکم (و نيرومند) مي‌کنيم، و براي شما سلطه و برتري قرارمي‌دهيم؛ و به برکت آيات ما، بر شما دست نمي‌يابند؛ شما و پيروانتان پيروزيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3288, N'هنگامي که موسي معجزات روشن ما را براي آنان آورد، گفتند: «اين چيزي جز سحر نيست که بدروغ به خدا بسته شده؛ ما هرگز چنين چيزي را در نياکان خود نشنيده‌ايم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3289, N'موسي گفت: «پروردگارم از حال کساني که هدايت را از نزد او آورده‌اند، و کساني که عاقبت نيک سرا(ي دنيا و آخرت) از آن آنهاست آگاهتر است! مسلما ظالمان رستگار نخواهند شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3290, N'فرعون گفت: «اي جمعيت اشراف! من خدايي جز خودم براي شما سراغ ندارم. (اما براي تحقيق بيشتر،) اي هامان، برايم آتشي بر گل بيفروز (و آجرهاي محکم بساز)، و براي من برج بلندي ترتيب ده تا از خداي موسي خبر گيرم؛ هر چند من گمان مي‌کنم او از دروغگويان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3291, N'(سرانجام) فرعون و لشکريانش بدون حق در زمين استکبار کردند، و پنداشتند بسوي ما بازگردانده نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3292, N'ما نيز او و لشکريانش را گرفتيم و به دريا افکنديم؛ اکنون بنگر پايان کار ظالمان چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3293, N'و آنان [= فرعونيان‌] را پيشواياني قرار داديم که به آتش (دوزخ) دعوت مي‌کنند؛ و روز رستاخيز ياري نخواهند شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3294, N'و در اين دنيا نيز لعنتي بدنبال آنان قرار داديم؛ و روز قيامت از زشت‌رويانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3295, N'و ما به موسي کتاب آسماني داديم بعد از آنکه اقوام قرون نخستين را هلاک نموديم؛ کتابي که براي مردم بصيرت‌آفرين بود، و مايه هدايت و رحمت؛ شايد متذکر شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3296, N'تو در جانب غربي نبودي هنگامي که ما فرمان نبوت را به موسي داديم؛ و تو از شاهدان نبودي (در آن هنگام که معجزات را در اختيار موسي گذارديم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3297, N'ولي ما اقوامي را در اعصار مختلف خلق کرديم، و زمانهاي طولاني بر آنها گذشت (که آثار انبيا از دلهايشان محو شد؛ پس تو را با کتاب آسمانيت فرستاديم)! تو هرگز در ميان مردم مدين اقامت نداشتي تا (از وضع آنان آگاه باشي و) آيات ما را براي آنها [= مشرکان مکه‌] بخواني، ولي ما بوديم که تو را فرستاديم (و اين آيات را در اختيارت قرار داديم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3298, N'تو در کنار طور نبودي زماني که ما ندا داديم؛ ولي اين رحمتي از سوي پروردگارت بود (که اين اخبار را در اختيار تو نهاد) تا بوسيله آن قومي را انذار کني که پيش از تو هيچ انذارکننده‌اي براي آنان نيامده است؛ شايد متذکر شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3299, N'هرگاه (پيش از فرستادن پيامبري) مجازات و مصيبتي بر اثر اعمالشان به آنان مي‌رسيد، مي‌گفتند: «پروردگارا! چرا رسولي براي ما نفرستادي تا از آيات تو پيروي کنيم و از مؤمنان باشيم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3300, N'ولي هنگامي که حق از نزد ما براي آنها آمد گفتند: «چرا مثل همان چيزي که به موسي داده شد به اين پيامبر داده نشده است؟!» مگر بهانه‌جوياني همانند آنان، معجزاتي را که در گذشته به موسي داده شد، انکار نکردند و گفتند: «اين دو نفر [= موسي و هارون‌] دو ساحرند که دست به دست هم داده‌اند (تا ما را گمراه کنند) و ما به هر دو کافريم»؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3301, N'بگو: «اگر راست مي‌گوييد (که تورات و قرآن از سوي خدا نيست)، کتابي هدايت‌بخش‌تر از اين دو از نزد خدا بياوريد، تا من از آن پيروي کنم!»')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3302, N'اگر اين پيشنهاد تو را نپذيرند، بدان که آنان تنها از هوسهاي خود پيروي مي‌کنند! و آيا گمراهتر از آن کس که پيروي هواي نفس خويش کرده و هيچ هدايت الهي را نپذيرفته، کسي پيدا مي‌شود؟! مسلما خداوند قوم ستمگر را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3303, N'ما آيات قرآن را يکي پس از ديگري براي آنان آورديم شايد متذکر شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3304, N'کساني که قبلا کتاب آسماني به آنان داده‌ايم به آن [= قرآن‌] ايمان مي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3305, N'و هنگامي که بر آنان خوانده شود مي‌گويند: «به آن ايمان آورديم؛ اينها همه حق است و از سوي پروردگار ماست؛ ما پيش از اين هم مسلمان بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3306, N'آنها کساني هستند که بخاطر شکيباييشان، اجر و پادششان را دو بار دريافت مي‌دارند؛ و بوسيله نيکيها بديها را دفع مي‌کنند؛ و از آنچه به آنان روزي داده‌ايم انفاق مي‌نمايند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3307, N'و هرگاه سخن لغو و بيهوده بشنوند، از آن روي مي‌گردانند و مي‌گويند: «اعمال ما از آن ماست و اعمال شما از آن خودتان؛ سلام بر شما (سلام وداع)؛ ما خواهان جاهلان نيستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3308, N'تو نمي‌تواني کسي را که دوست داري هدايت کني؛ ولي خداوند هر کس را بخواهد هدايت مي‌کند؛ و او به هدايت يافتگان آگاهتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3309, N'آنها گفتند: «ما اگر هدايت را همراه تو پذيرا شويم، ما را از سرزمينمان مي‌ربايند!» آيا ما حرم امني در اختيار آنها قرار نداديم که ثمرات هر چيزي (از هر شهر و دياري) بسوي آن آورده مي‌شود؟! رزقي است از جانب ما؛ ولي بيشتر آنان نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3310, N'و چه بسيار از شهرها و آباديهايي را که بر اثر فراواني نعمت، مست و مغرور شده بودند هلاک کرديم! اين خانه‌هاي آنهاست (که ويران شده)، و بعد از آنان جز اندکي کسي در آنها سکونت نکرد؛ و ما وارث آنان بوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3311, N'و پروردگار تو هرگز شهرها و آباديها را هلاک نمي‌کرد تا اينکه در کانون آنها پيامبري مبعوث کند که آيات ما را بر آنان بخواند؛ و ما هرگز آباديها و شهرها را هلاک نکرديم مگر آنکه اهلش ظالم بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3312, N'آنچه به شما داده شده، متاع زندگي دنيا و زينت آن است؛ و آنچه نزد خداست بهتر و پايدارتر است؛ آيا انديشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3313, N'آيا کسي که به او وعده نيکو داده‌ايم و به آن خواهد رسيد، همانند کسي است که متاع زندگي دنيا به او داده‌ايم سپس روز قيامت (براي حساب و جزا) از احضارشدگان خواهد بود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3314, N'روزي را (به خاطر بياوريد) که خداوند آنان را ندا ميدهد و ميگويد: «کجا هستند همتاياني که براي من مي‌پنداشتيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3315, N'گروهي (از معبودان) که فرمان عذاب درباره آنها مسلم شده است ميگويند: «پروردگارا! ما اينها [= عابدان‌] را گمراه کرديم؛ (آري) ما آنها را گمراه کرديم همان‌گونه که خودمان گمراه شديم؛ ما از آنان به سوي تو بيزاري مي‌جوييم؛ آنان در حقيقت ما را نمي‌پرستيدند (بلکه هواي نفس خود را پرستش مي‌کردند)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3316, N'و به آنها [= عابدان‌] گفته مي‌شود: «معبودهايتان را که همتاي خدا مي‌پنداشتيد بخوانيد (تا شما را ياري کنند)!» معبودهايشان را مي‌خوانند، ولي جوابي به آنان نمي‌دهند! و (در اين هنگام) عذاب الهي را (با چشم خود) مي‌بينند، و آرزو مي‌کنند اي کاش هدايت يافته بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3317, N'(به خاطر آوريد) روزي را که خداوند آنان را ندا مي‌دهد و مي‌گويد: «چه پاسخي به پيامبران (من) گفتيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3318, N'در آن روز، همه اخبار به آنان پوشيده مي‌ماند، (حتي نمي‌توانند) از يکديگر سؤالي کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3319, N'اما کسي که توبه کند، و ايمان آورد و عمل صالحي انجام دهد، اميد است از رستگاران باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3320, N'پروردگار تو هر چه بخواهد مي‌آفريند، و هر چه بخواهد برمي‌گزيند؛ آنان (در برابر او) اختياري ندارند؛ منزه است خداوند، و برتر است از همتاياني که براي او قائل مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3321, N'و پروردگار تو مي‌داند آنچه را که سينه‌هايشان پنهان مي‌دارد و آنچه را آشکار مي‌سازند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3322, N'و او خدايي است که معبودي جز او نيست؛ ستايش براي اوست در اين جهان و در جهان ديگر؛ حاکميت (نيز) از آن اوست؛ و همه شما به سوي او بازگردانده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3323, N'بگو: «به من خبر دهيد اگر خداوند شب را تا قيامت بر شما جاودان سازد، آيا معبودي جز خدا مي‌تواند روشنايي براي شما بياورد؟! آيا نمي‌شنويد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3324, N'بگو: «به من خبر دهيد اگر خداوند روز را تا قيامت بر شما جاودان کند، کدام معبود غير از خداست که شبي براي شما بياورد تا در آن آرامش يابيد؟ آيا نمي‌بينيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3325, N'و از رحمت اوست که براي شما شب و روز قرار داد تا هم در آن آرامش داشته باشيد و هم براي بهره‌گيري از فضل خدا تلاش کنيد، و شايد شکر نعمت او را بجا آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3326, N'(به خاطر آوريد) روزي را که آنها را ندا مي‌دهد و مي‌گويد: «کجايند همتاياني که براي من مي‌پنداشتيد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3327, N'(در آن روز) از هر امتي گواهي برمي‌گزينيم و (به مشرکان) مي‌گوييم: «دليل خود را بياوريد!» اما آنها مي‌دانند که حق از آن خداست، و تمام آنچه را افترا مي‌بستند از (نظر) آنها گم خواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3328, N'قارون از قوم موسي بود، اما بر آنان ستم کرد؛ ما آنقدر از گنجها به او داده بوديم که حمل کليدهاي آن براي يک گروه زورمند مشکل بود! (به خاطر آوريد) هنگامي را که قومش به او گفتند: «اين همه شادي مغرورانه مکن، که خداوند شادي‌کنندگان مغرور را دوست نمي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3329, N'و در آنچه خدا به تو داده، سراي آخرت را بطلب؛ و بهره‌ات را از دنيا فراموش مکن؛ و همان‌گونه که خدا به تو نيکي کرده نيکي کن؛ و هرگز در زمين در جستجوي فساد مباش، که خدا مفسدان را دوست ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3330, N'(قارون) گفت: «اين ثروت را بوسيله دانشي که نزد من است به دست آورده‌ام!» آيا او نمي‌دانست که خداوند اقوامي را پيش از او هلاک کرد که نيرومندتر و ثروتمندتر از او بودند؟! (و هنگامي که عذاب الهي فرا رسد،) مجرمان از گناهانشان سؤال نمي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3331, N'(روزي قارون) با تمام زينت خود در برابر قومش ظاهر شد، آنها که خواهان زندگي دنيا بودند گفتند: «اي کاش همانند آنچه به قارون داده شده است ما نيز داشتيم! به راستي که او بهره عظيمي دارد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3332, N'اما کساني که علم و دانش به آنها داده شده بود گفتند: «واي بر شما ثواب الهي براي کساني که ايمان آورده‌اند و عمل صالح انجام مي‌دهند بهتر است، اما جز صابران آن را دريافت نمي‌کنند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3333, N'سپس ما، او و خانه‌اش را در زمين فرو برديم، و گروهي نداشت که او را در برابر عذاب الهي ياري کنند، و خود نيز نمي‌توانست خويشتن را ياري دهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3334, N'و آنها که ديروز آرزو مي‌کردند بجاي او باشند (هنگامي که اين صحنه را ديدند) گفتند: «واي بر ما! گويي خدا روزي را بر هر کس از بندگانش بخواهد گسترش مي‌دهد يا تنگ مي‌گيرد! اگر خدا بر ما منت ننهاده بود، ما را نيز به قعر زمين فرو مي برد! اي واي گويي کافران هرگز رستگار نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3335, N'(آري،) اين سراي آخر تو را (تنها) براي کساني قرارمي‌دهيم که اراده برتري‌جويي در زمين و فساد را ندارند؛ و عاقبت نيک براي پرهيزگاران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3336, N'کسي که کار نيکي انجام دهد، براي او پاداشي بهتر از آن است؛ و به کساني که کارهاي بد انجام دهند، مجازات بدکاران جز (به مقدار) اعمالشان نخواهد بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3337, N'آن کس که قرآن را بر تو فرض کرد، تو را به جايگاهت [= زادگاهت‌] بازمي‌گرداند! بگو: «پروردگار من از همه بهتر مي‌داند چه کسي (برنامه) هدايت آورده، و چه کسي در گمراهي آشکار است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3338, N'و تو هرگز اميد نداشتي که اين کتاب آسماني به تو القا گردد؛ ولي رحمت پروردگارت چنين ايجاب کرد! اکنون که چنين است، هرگز از کافران پشتيباني مکن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3339, N'و هرگز (آنها) تو را از آيات خداوند، بعد از آنکه بر تو نازل گشت، بازندارند! و بسوي پروردگارت دعوت کن، و هرگز از مشرکان مباش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3340, N'معبود ديگري را با خدا مخوان، که هيچ معبودي جز او نيست؛ همه چيز جز ذات (پاک) او فاني مي‌شود؛ حاکميت تنها از آن اوست؛ و همه بسوي او بازگردانده مي شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3341, N'الم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3342, N'آيا مردم گمان کردند همين که بگويند: «ايمان آورديم»، به حال خود رها مي‌شوند و آزمايش نخواهند شد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3343, N'ما کساني را که پيش از آنان بودند آزموديم (و اينها را نيز امتحان مي‌کنيم)؛ بايد علم خدا درباره کساني که راست مي‌گويند و کساني که دروغ مي‌گويند تحقق يابد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3344, N'آيا کساني که اعمال بد انجام مي‌دهند گمان کردند بر قدرت ما چيره خواهند شد؟! چه بد داوري مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3345, N'کسي که اميد به لقاء الله (و رستاخيز) دارد (بايد در اطاعت فرمان او بکوشد!) زيرا سرآمدي را که خدا تعيين کرده فرامي‌رسد؛ و او شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3346, N'کسي که جهاد و تلاش کند، براي خود جهاد مي‌کند؛ چرا که خداوند از همه جهانيان بي نياز است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3347, N'و کساني که ايمان آورده و کارهاي شايسته انجام دادند، گناهان آنان را مي‌پوشانيم (و مي‌بخشيم) و آنان را به بهترين اعمالي که انجام مي‌دادند پاداش مي‌دهيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3348, N'ما به انسان توصيه کرديم که به پدر و مادرش نيکي کند، و اگر آن دو (مشرک باشند و) تلاش کنند که براي من همتايي قائل شوي که به آن علم نداري، از آنها پيروي مکن! بازگشت شما به سوي من است، و شما را از آنچه انجام مي‌داديد با خبر خواهم ساخت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3349, N'و کساني که ايمان آورده و کارهاي شايسته انجام دادند، آنها را در زمره صالحان وارد خواهيم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3350, N'و از مردم کساني هستند که مي‌گويند: «به خدا ايمان آورده‌ايم!» اما هنگامي که در راه خدا شکنجه و آزار مي‌بينند، آزار مردم را همچون عذاب الهي مي‌شمارند (و از آن سخت وحشت مي‌کنند)؛ ولي هنگامي که پيروزي از سوي پروردگارت (براي شما) بيايد، مي‌گويند: «ما هم با شما بوديم (و در اين پيروزي شريکيم)»!! آيا خداوند به آنچه در سينه‌هاي جهانيان است آگاه‌تر نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3351, N'مسلما خداوند مؤمنان را مي‌شناسد، و به يقين منافقان را (نيز) مي‌شناسد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3352, N'و کافران به مؤمنان گفتند: «شما از راه ما پيروي کنيد، (و اگر گناهي دارد) ما گناهانتان را بر عهده خواهيم گرفت!» آنان هرگز چيزي از گناهان اينها را بر دوش نخواهند گرفت؛ آنان به يقين دروغگو هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3353, N'آنها بار سنگين (گناهان) خويش را بر دوش مي‌کشند، و (همچنين) بارهاي سنگين ديگري را اضافه بر بارهاي سنگين خود؛ و روز قيامت به يقين از تهمتهائي که مي‌بستند سؤال خواهند شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3354, N'و ما نوح را بسوي قومش فرستاديم؛ و او را در ميان آنان هزار سال مگر پنجاه سال، درنگ کرد؛ اما سرانجام طوفان و سيلاب آنان را فراگرفت در حالي که ظالم بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3355, N'ما او و سرنشينان کشتي را رهايي بخشيديم، و آن را آيتي براي جهانيان قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3356, N'ما ابراهيم را (نيز) فرستاديم، هنگامي که به قومش گفت: «خدا را پرستش کنيد و از (عذاب) او بپرهيزيد که اين براي شما بهتر است اگر بدانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3357, N'شما غير از خدا فقط بتهايي (از سنگ و چوب) را مي‌پرستيد و دروغي به هم مي‌بافيد؛ آنهايي را که غير از خدا پرستش مي‌کنيد، مالک هيچ رزقي براي شما نيستند؛ روزي را تنها نزد خدا بطلبيد و او را پرستش کنيد و شکر او را بجا آوريد که بسوي او بازگشت داده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3358, N'اگر شما (مرا) تکذيب کنيد (جاي تعجب نيست)، امتهايي پيش از شما نيز (پيامبرانشان را) تکذيب کردند؛ وظيفه فرستاده (خدا) جز ابلاغ آشکار نيست».')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3359, N'آيا آنان نديدند چگونه خداوند آفرينش را آغاز مي‌کند، سپس بازمي‌گرداند؟! اين کار براي خدا آسان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3360, N'بگو: «در زمين بگرديد و بنگريد خداوند چگونه آفرينش را آغاز کرده است؟ سپس خداوند (به همين‌گونه) جهان آخرت را ايجاد مي‌کند؛ يقينا خدا بر هر چيز توانا است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3361, N'هر کس را بخواهد (و مستحق بداند) مجازات مي‌کند، و هر کس را بخواهد مورد رحمت قرارمي‌دهد؛ و شما را به سوي او بازمي‌گردانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3362, N'شما هرگز نمي‌توانيد بر اراده خدا چيره شويد و از حوزه قدرت او در زمين و آسمان بگريزيد؛ و براي شما جز خدا، ولي و ياوري نيست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3363, N'کساني که به آيات خدا و ديدار او کافر شدند، از رحمت من مأيوسند؛ و براي آنها عذاب دردناکي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3364, N'اما جواب قوم او [= ابراهيم‌] جز اين نبود که گفتند: «او را بکشيد يا بسوزانيد!» ولي خداوند او را از آتش رهايي بخشيد؛ در اين ماجرا نشانه‌هايي است براي کساني که ايمان مي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3365, N'(ابراهيم) گفت: «شما غير از خدا بتهايي براي خود انتخاب کرده‌ايد که مايه دوستي و محبت ميان شما در زندگي دنيا باشد؛ سپس روز قيامت از يکديگر بيزاري مي‌جوييد و يکديگر را لعن مي‌کنيد؛ و جايگاه (همه) شما آتش است و هيچ يار و ياوري براي شما نخواهد بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3366, N'و لوط به او [= ابراهيم‌] ايمان آورد، و (ابراهيم) گفت: «من بسوي پروردگارم هجرت مي‌کنم که او صاحب قدرت و حکيم است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3367, N'و (در اواخر عمر،) اسحاق و يعقوب را به او بخشيديم و نبوت و کتاب آسماني را در دودمانش قرار داديم و پاداش او را در دنيا داديم و او در آخرت از صالحان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3368, N'و لوط را فرستاديم هنگامي که به قوم خود گفت: «شما عمل بسيار زشتي انجام مي‌دهيد که هيچ يک از مردم جهان پيش از شما آن را انجام نداده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3369, N'آيا شما به سراغ مردان مي‌رويد و راه (تداوم نسل انسان) را قطع مي‌کنيد و در مجلستان اعمال ناپسند انجام مي‌دهيد؟! «اما پاسخ قومش جز اين نبود که گفتند:» اگر راست مي‌گويي عذاب الهي را براي ما بياور!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3370, N'(لوط) عرض کرد: «پروردگارا! مرا در برابر اين قوم تبهکار ياري فرما!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3371, N'و هنگامي که فرستادگان ما (از فرشتگان) بشارت (تولد فرزند) براي ابراهيم آوردند، گفتند: «ما اهل اين شهر و آبادي را [و به شهرهاي قوم لوط اشاره کردند] هلاک خواهيم کرد، چرا که اهل آن ستمگرند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3372, N'(ابراهيم) گفت: «در اين آبادي لوط است!» گفتند: «ما به کساني که در آن هستند آگاهتريم! او و خانواده‌اش را نجات مي‌دهيم؛ جز همسرش که در ميان قوم (گنهکار) باقي خواهد ماند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3373, N'هنگامي که فرستادگان ما نزد لوط آمدند، از ديدن آنها بدحال و دلتنگ شد؛ گفتند: «نترس و غمگين مباش، ما تو و خانواده‌ات را نجات خواهيم داد، جز همسرت که در ميان قوم باقي مي‌ماند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3374, N'ما بر اهل اين شهر و آبادي به خاطر گناهانشان، عذابي از آسمان فرو خواهيم ريخت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3375, N'و از اين آبادي نشانه روشني (و درس عبرتي) براي کساني که مي‌انديشند باقي گذارديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3376, N'و ما بسوي «مدين»، برادرشان «شعيب» را فرستاديم؛ گفت: «اي قوم من! خدا را بپرستيد، و به روز بازپسين اميدوار باشيد، و در زمين فساد نکنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3377, N'(ولي) آنها او را تکذيب کردند، و به اين سبب زلزله آنان را فراگرفت، و بامدادان در خانه‌هاي خود به رو در افتاده و مرده بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3378, N'ما طايفه «عاد» و «ثمود» را نيز (هلاک کرديم)، و مساکن (ويران شده) آنان براي شما آشکار است؛ شيطان اعمالشان را براي آنان آراسته بود، از اين رو آنان را از راه (خدا) بازداشت در حالي که بينا بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3379, N'و «قارون» و «فرعون» و «هامان» را نيز هلاک کرديم؛ موسي با دلايل روشن به سراغشان آمد، اما آنان در زمين برتري جويي کردند، ولي نتوانستند بر خدا پيشي گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3380, N'ما هر يک از آنان را به گناهانشان گرفتيم، بر بعضي از آنها طوفاني از سنگريزه فرستاديم، و بعضي از آنان را صيحه آسماني فروگرفت، و بعضي ديگر را در زمين فرو برديم، و بعضي را غرق کرديم؛ خداوند هرگز به آنها ستم نکرد، ولي آنها خودشان بر خود ستم مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3381, N'مثل کساني که غير از خدا را اولياي خود برگزيدند، مثل عنکبوت است که خانه‌اي براي خود انتخاب کرده؛ در حالي که سست‌ترين خانه‌هاي خانه عنکبوت است اگر مي‌دانستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3382, N'خداوند آنچه را غير از او مي‌خوانند مي‌داند، و او شکست‌ناپذير و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3383, N'اينها مثالهايي است که ما براي مردم مي‌زنيم، و جز دانايان آن را درک نمي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3384, N'خداوند، آسمانها و زمين را بحق آفريد؛ و در اين آيتي است براي مؤمنان.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3385, N'آنچه را از کتاب (آسماني) به تو وحي شده تلاوت کن، و نماز را برپا دار، که نماز (انسان را) از زشتيها و گناه بازمي‌دارد، و ياد خدا بزرگتر است؛ و خداوند مي‌داند شما چه کارهايي انجام مي‌دهيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3386, N'با اهل کتاب جز به روشي که از همه نيکوتر است مجادله نکنيد، مگر کساني از آنان که ستم کردند؛ و (به آنها) بگوييد: «ما به تمام آنچه از سوي خدا بر ما و شما نازل شده ايمان آورده‌ايم، و معبود ما و شما يکي است، و ما در برابر او تسليم هستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3387, N'و اين گونه، کتاب [= قرآن‌] را بر تو نازل کرديم، کساني که کتاب (آسماني)به آنها داده‌ايم به اين کتاب ايمان مي‌آورند؛ و بعضي از اين گروه [= مشرکان‌] نيز به آن مؤمن مي‌شوند؛ و آيات ما را جز کافران انکار نمي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3388, N'تو هرگز پيش از اين کتابي نمي‌خواندي، و با دست خود چيزي نمي‌نوشتي، مبادا کساني که در صدد (تکذيب و) ابطال سخنان تو هستند، شک و ترديد کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3389, N'ولي اين آيات روشني است که در سينه دانشوران جاي دارد؛ و آيات ما را جز ستمگران انکار نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3390, N'گفتند: «چرا معجزاتي از سوي پروردگارش بر او نازل نشده؟!» بگو: «معجزات همه نزد خداست (و به فرمان او نازل مي‌شود، نه به ميل من و شما)؛ من تنها بيم دهنده‌اي آشکارم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3391, N'آيا براي آنان کافي نيست که اين کتاب را بر تو نازل کرديم که پيوسته بر آنها تلاوت مي‌شود؟! در اين، رحمت و تذکري است براي کساني که ايمان مي‌آورند (و اين معجزه بسيار واضحي است).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3392, N'بگو: «همين بس که خدا ميان من و شما گواه است؛ آنچه را در آسمانها و زمين است مي‌داند؛ و کساني که به باطل ايمان آوردند و به خدا کافر شدند زيانکاران واقعي هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3393, N'آنان با شتاب از تو عذاب را مي‌طلبند؛ و اگر موعد مقرري تعيين نشده بود، عذاب (الهي) به سراغ آنان مي‌آمد؛ و سرانجام اين عذاب بطور ناگهاني بر آنها نازل مي‌شود در حالي که نمي‌دانند (و غافلند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3394, N'آنان با عجله از تو عذاب مي‌طلبند، در حالي که جهنم به کافران احاطه دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3395, N'آن روز که عذاب (الهي) آنها را از بالاي سر و پايين پايشان فرامي‌گيرد و به آنها مي‌گويد: «بچشيد آنچه را عمل مي‌کرديد» (روز سخت و دردناکي براي آنهاست!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3396, N'اي بندگان من که ايمان آورده‌ايد! زمين من وسيع است، پس تنها مرا بپرستيد (و در برابر فشارهاي دشمنان تسليم نشويد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3397, N'هر انساني مرگ را مي‌چشد، سپس شما را بسوي ما بازمي‌گردانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3398, N'و کساني که ايمان آورده و کارهاي شايسته انجام دادند، آنان را در غرفه‌هايي از بهشت جاي مي‌دهيم که نهرها در زير آن جاري است؛ جاودانه در آن خواهند ماند؛ چه خوب است پاداش عمل‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3399, N'همانها که (در برابر مشکلات) صبر (و استقامت) کردند و بر پروردگارشان توکل مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3400, N'چه بسا جنبنده‌اي که قدرت حمل روزي خود را ندارد، خداوند او و شما را روزي مي‌دهد؛ و او شنوا و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3401, N'و هر گاه از آنان بپرسي: «چه کسي آسمانها و زمين را آفريده، و خورشيد و ماه را مسخر کرده است؟» مي‌گويند: «الله»! پس با اين حال چگونه آنان را (از عبادت خدا) منحرف مي‌سازند؟!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3402, N'خداوند روزي را براي هر کس از بندگانش بخواهد گسترده مي‌کند، و براي هر کس بخواهد محدود مي‌سازد؛ خداوند به همه چيز داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3403, N'و اگر از آنان بپرسي: «چه کسي از آسمان آبي فرستاد و بوسيله آن زمين را پس از مردنش زنده کرد؟ مي‌گويند: «الله»! بگو: «حمد و ستايش مخصوص خداست!»اما بيشتر آنها نمي‌دانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3404, N'اين زندگي دنيا چيزي جز سرگرمي و بازي نيست؛ و زندگي واقعي سراي آخرت است، اگر مي‌دانستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3405, N'هنگامي که بر سوار بر کشتي شوند، خدا را با اخلاص مي‌خوانند (و غير او را فراموش مي‌کنند)؛ اما هنگامي که خدا آنان را به خشکي رساند و نجات داد، باز مشرک مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3406, N'(بگذار) آنچه را (از آيات) به آنها داده‌ايم انکار کنند و از لذات زودگذر زندگي بهره گيرند؛ اما بزودي خواهند فهميد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3407, N'آيا نديدند که ما حرم امني (براي آنها) قرار داديم در حالي که مردم را در اطراف آنان (در بيرون اين حرم) مي‌ربايند؟! آيا به باطل ايمان مي‌آورند و نعمت خدا را کفران مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3408, N'چه کسي ستمکارتر از آن کس است که بر خدا دروغ بسته يا حق را پس از آنکه به سراغش آمده تکذيب نمايد؟! آيا جايگاه کافران در دوزخ نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3409, N'و آنها که در راه ما (با خلوص نيت) جهاد کنند، قطعا به راه‌هاي خود، هدايتشان خواهيم کرد؛ و خداوند با نيکوکاران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3410, N'الم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3411, N'روميان مغلوب شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3412, N'(و اين شکست) در سرزمين نزديکي رخ داد؛ اما آنان پس از (اين) مغلوبيت بزودي غلبه خواهند کرد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3413, N'در چند سال همه کارها از آن خداست؛ چه قبل و چه بعد (از اين شکست و پيروزي)؛ و در آن روز، مؤمنان (بخاطر پيروزي ديگري) خوشحال خواهند شد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3414, N'به سبب ياري خداوند؛ و او هر کس را بخواهد ياري مي‌دهد؛ و او صاحب قدرت و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3415, N'اين وعده‌اي است که خدا کرده؛ و خداوند هرگز از وعده‌اش تخلف نمي‌کند؛ ولي بيشتر مردم نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3416, N'آنها فقط ظاهري از زندگي دنيا را مي‌دانند، و از آخرت (و پايان کار) غافلند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3417, N'آيا آنان با خود نينديشيدند که خداوند، آسمانها و زمين و آنچه را ميان آن دو است جز بحق و براي زمان معيني نيافريده است؟! ولي بسياري از مردم (رستاخيز و) لقاي پروردگارشان را منکرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3418, N'آيا در زمين گردش نکردند تا ببينند عاقبت کساني که قبل از آنان بودند چگونه بود؟! آنها نيرومندتر از اينان بودند، و زمين را (براي زراعت و آبادي)بيش از اينان دگرگون ساختند و آباد کردند، و پيامبرانشان با دلايل روشن به سراغشان آمدند (اما آنها انکار کردند و کيفر خود را ديدند)؛ خداوند هرگز به آنان ستم نکرد، آنها به خودشان ستم مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3419, N'سپس سرانجام کساني که اعمال بد مرتکب شدند به جايي رسيد که آيات خدا را تکذيب کردند و آن را به مسخره گرفتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3420, N'خداوند آفرينش را آغاز مي‌کند، سپس آن را بازمي‌گرداند، سپس شما را بسوي او باز مي‌گردانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3421, N'آن روز که قيامت برپا مي‌شود، مجرمان در نوميدي و غم و اندوه فرو مي‌روند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3422, N'و براي آنان شفيعاني از معبودانشان نخواهد بود، و نسبت به معبودهايي که آنها را همتاي خدا قرار داده بودند کافر مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3423, N'آن روز که قيامت برپا مي‌گردد، (مردم) از هم جدا مي‌شوند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3424, N'اما آنان که ايمان آورده و اعمال صالح انجام دادند، در باغي از بهشت شاد و مسرور خواهند بود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3425, N'و اما آنان که به آيات ما و لقاي آخرت کافر شدند، در عذاب الهي احضار مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3426, N'منزه است خداوند به هنگامي که شام مي‌کنيد و صبح مي‌کنيد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3427, N'و حمد و ستايش مخصوص اوست در آسمان و زمين، و به هنگام عصر و هنگامي که ظهر مي‌کنيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3428, N'او زنده را از مرده بيرون مي‌آورد، و مرده را از زنده، و زمين را پس از مردنش حيات مي‌بخشد، و به همين گونه روز قيامت (از گورها) بيرون آورده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3429, N'از نشانه‌هاي او اين است که شما را از خاک آفريد، سپس بناگاه انسانهايي شديد و در روي زمين گسترش يافتيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3430, N'و از نشانه‌هاي او اينکه همسراني از جنس خودتان براي شما آفريد تا در کنار آنان آرامش يابيد، و در ميانتان مودت و رحمت قرار داد؛ در اين نشانه‌هايي است براي گروهي که تفکر مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3431, N'و از آيات او آفرينش آسمانها و زمين، و تفاوت زبانها و رنگهاي شماست؛ در اين نشانه‌هايي است براي عالمان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3432, N'و از نشانه‌هاي او خواب شما در شب و روز است و تلاش و کوششتان براي بهره‌گيري از فضل پروردگار (و تأمين معاش)؛ در اين امور نشانه‌هايي است براي آنان که گوش شنوا دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3433, N'و از آيات او اين است که برق و رعد را به شما نشان مي‌دهد که هم مايه ترس و هم اميد است (ترس از صاعقه، و اميد به نزول باران)، و از آسمان آبي فرو مي‌فرستد که زمين را بعد از مردنش بوسيله آن زنده مي‌کند؛ در اين نشانه‌هايي است براي جمعيتي که مي‌انديشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3434, N'و از آيات او اين است که آسمان و زمين به فرمان او برپاست؛ سپس هنگامي که شما را (در قيامت) از زمين فراخواند، ناگهان همه خارج مي‌شويد (و در صحنه محشر حضور مي‌يابيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3435, N'و از آن اوست تمام کساني که در آسمانها و زمين‌اند و همگي در برابر او خاضع و مطيع‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3436, N'او کسي است که آفرينش را آغاز مي‌کند، سپس آن را بازمي‌گرداند، و اين کار براي او آسانتر مي‌باشد؛ و براي اوست توصيف برتر در آسمانها و زمين؛ و اوست توانمند و حکيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3437, N'خداوند مثالي از خودتان، براي شما زده است: آيا (اگر مملوک و برده‌اي داشته باشيد)، اين برده‌هاي شما هرگز در روزيهايي که به شما داده‌ايم شريک شما مي‌باشند؛ آنچنان که هر دو مساوي بوده و از تصرف مستقل و بدون اجازه آنان بيم داشته باشيد، آن گونه که در مورد شرکاي آزاد خود بيم داريد؟! اينچنين آيات خود را براي کساني که تعقل مي‌کنند شرح مي‌دهيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3438, N'ولي ظالمان بدون علم و آگاهي، از هوي و هوسهاي خود پيروي کردند! پس چه کسي مي‌تواند آنان را که خدا گمراه کرده است هدايت کند؟! و براي آنها هيچ ياوري نخواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3439, N'پس روي خود را متوجه آيين خالص پروردگار کن! اين فطرتي است که خداوند،انسانها را بر آن آفريده؛ دگرگوني در آفرينش الهي نيست؛ اين است آيين استوار؛ ولي اکثر مردم نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3440, N'اين بايد در حالي باشد که شما بسوي او بازگشت مي‌کنيد و از (مخالفت فرمان) او بپرهيزيد، نماز را برپا داريد و از مشرکان نباشيد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3441, N'از کساني که دين خود را پراکنده ساختند و به دسته‌ها و گروه‌ها تقسيم شدند! و (عجب اينکه) هر گروهي به آنچه نزد آنهاست (دلبسته و) خوشحالند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3442, N'هنگامي که رنج و زياني به مردم برسد، پروردگار خود را مي‌خوانند و توبه‌کنان بسوي او بازمي‌گردند؛ اما همين که رحمتي از خودش به آنان بچشاند، بناگاه گروهي از آنان نسبت به پروردگارشان مشرک مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3443, N'(بگذار) نعمتهايي را که ما به آنها داده‌ايم کفران کنند! و (از نعمتهاي زودگذر دنيا هر چه مي‌توانيد) بهره گيريد؛ اما بزودي خواهيد دانست (که نتيجه کفران و کامجوييهاي بي حساب شما چه بوده است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3444, N'آيا ما دليل محکمي بر آنان فرستاديم که از شرکشان سخن مي‌گويد (و آن را موجه مي‌شمارد)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3445, N'و هنگامي که رحمتي به مردم بچشانيم، از آن خوشحال مي‌شوند؛ و هرگاه رنج و مصيبتي بخاطر اعمالي که انجام داده‌اند به آنان رسد، ناگهان مأيوس مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3446, N'آيا نديدند که خداوند روزي را براي هر کس بخواهد گسترده يا تنگ مي‌سازد؟!در اين نشانه‌هايي است براي گروهي که ايمان مي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3447, N'پس حق نزديکان و مسکينان و در راه‌ماندگان را ادا کن! اين براي آنها که رضاي خدا را مي‌طلبند بهتر است، و چنين کساني رستگارانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3448, N'آنچه بعنوان ربا مي‌پردازيد تا در اموال مردم فزوني يابد، نزد خدا فزوني نخواهد يافت؛ و آنچه را بعنوان زکات مي‌پردازيد و تنها رضاي خدا را مي‌طلبيد (مايه برکت است؛ و) کساني که چنين مي‌کنند داراي پاداش مضاعفند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3449, N'خداوند همان کسي است که شما را آفريد، سپس روزي داد، بعد مي‌ميراند، سپس زنده مي‌کند؛ آيا هيچ يک از همتاياني که براي خدا قرار داده‌ايد چيزي از اين کارها را مي‌توانند انجام دهند؟! او منزه و برتر است از آنچه همتاي او قرار مي‌دهند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3450, N'فساد، در خشکي و دريا بخاطر کارهايي که مردم انجام داده‌اند آشکار شده است؛ خدا مي‌خواهد نتيجه بعضي از اعمالشان را به آنان بچشاند، شايد (بسوي حق)بازگردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3451, N'بگو: «در زمين سير کنيد و بنگريد عاقبت کساني که قبل از شما بودند چگونه بود؟ بيشتر آنها مشرک بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3452, N'روي خود را بسوي آيين مستقيم و پايدار بدار، پيش از آنکه روزي فرا رسد که هيچ کس نمي‌تواند آن را از خدا بازگرداند؛ در آن روز مردم به گروه‌هايي تقسيم مي‌شوند:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3453, N'هر کس کافر شود، کفرش بر زيان خود اوست؛ و آنها که کار شايسته انجام دهند، به سود خودشان آماده مي‌سازند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3454, N'اين براي آن است که خداوند کساني را که ايمان آورده و اعمال صالح انجام داده‌اند، از فضلش پاداش دهد؛ او کافران را دوست نمي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3455, N'و از آيات (عظمت) خدا اين است که بادها را بعنوان بشارتگراني مي‌فرستد تا شما را از رحمتش بچشاند (و سيراب کند) و کشتيها بفرمانش حرکت کنند و از فضل او بهره گيريد؛ شايد شکرگزاري کنيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3456, N'و پيش از تو پيامبراني را بسوي قومشان فرستاديم؛ آنها با دلايل روشن به سراغ قوم خود رفتند، ولي (هنگامي که اندرزها سودي نداد) از مجرمان انتقام گرفتيم (و مؤمنان را ياري کرديم)؛ و ياري مؤمنان، همواره حقي است بر عهده ما!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3457, N'خداوند همان کسي است که بادها را مي‌فرستد تا ابرهايي را به حرکت در آورند، سپس آنها را در پهنه آسمان آن گونه که بخواهد مي‌گستراند و متراکم مي‌سازد؛ در اين هنگام دانه‌هاي باران را مي‌بيني که از لا به لاي آن خارج مي‌شود،هنگامي که اين (باران حياتبخش) را به هر کس از بندگانش که بخواهد مي‌رساند، ناگهان خوشحال مي‌شوند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3458, N'و قطعا پيش از آنکه بر آنان نازل شود مايوس بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3459, N'به آثار رحمت الهي بنگر که چگونه زمين را بعد از مردنش زنده مي‌کند؛ چنين کسي (که زمين مرده را زنده کرد) زنده‌کننده مردگان (در قيامت) است؛ و او بر همه چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3460, N'و اگر ما بادي بفرستيم (داغ و سوزان)، و بر اثر آن زراعت و باغ خود را زرد و پژمرده ببينند، (مأيوس شده و) پس از آن راه کفران پيش مي‌گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3461, N'تو نمي‌تواني صداي خود را به گوش مردگان برساني، و نه سخنت را به گوش کران هنگامي که روي برگردانند و دور شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3462, N'و (نيز) نمي‌تواني نابينايان را از گمراهيشان هدايت کني؛ تو تنها سخنت را به گوش کساني مي‌رساني که ايمان به آيات ما مي‌آورند و در برابر حق تسليمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3463, N'خدا همان کسي است که شما را آفريد در حالي که ضعيف بوديد؛ سپس بعد از ناتواني ، قوت بخشيد و باز بعد از قوت، ضعف و پيري قرار داد؛ او هر چه بخواهد مي‌آفريند، و دانا و تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3464, N'و روزي که قيامت برپا شود، مجرمان سوگند ياد مي‌کنند که جز ساعتي (در عالم برزخ) درنگ نکردند! اينچنين از درک حقيقت بازگردانده مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3465, N'ولي کساني که علم و ايمان به آنان داده شده مي‌گويند: «شما بفرمان خدا تا روز قيامت (در عالم برزخ) درنگ کرديد، و اکنون روز رستاخيز است، اما شما نمي‌دانستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3466, N'آن روز عذرخواهي ظالمان سودي به حالشان ندارد، و توبه آنان پذيرفته نمي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3467, N'ما براي مردم در اين قرآن از هر گونه مثال و مطلبي بيان کرديم؛ و اگر آيه‌اي براي آنان بياوري، کافران مي‌گويند: «شما اهل باطليد (و اينها سحر و جادو است)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3468, N'اين گونه خداوند بر دلهاي آنان که آگاهي ندارند مهر مي‌نهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3469, N'اکنون که چنين است صبر پيشه کن که وعده خدا حق است؛ و هرگز کساني که ايمان ندارند تو را خشمگين نسازند (و از راه خود منحرف نکنند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3470, N'الم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3471, N'اين آيات کتاب حکيم است (کتابي پرمحتوا و استوار)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3472, N'مايه هدايت و رحمت براي نيکوکاران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3473, N'همانان که نماز را برپا مي‌دارند، و زکات را مي‌پردازند و آنها به آخرت يقين دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3474, N'آنان بر طريق هدايت از پروردگارشانند، و آنانند رستگاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3475, N'و بعضي از مردم سخنان بيهوده را مي‌خرند تا مردم را از روي ناداني، از راه خدا گمراه سازند و آيات الهي را به استهزا گيرند؛ براي آنان عذابي خوارکننده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3476, N'و هنگامي که آيات ما بر او خوانده مي‌شود، مستکبرانه روي برمي‌گرداند، گويي آن را نشنيده است؛ گويي اصلا گوشهايش سنگين است! او را به عذابي دردناک بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3477, N'(ولي) کساني که ايمان آورده و اعمال صالح انجام داده‌اند، باغهاي پرنعمت بهشت از آن آنهاست؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3478, N'جاودانه در آن خواهند ماند؛ اين وعده حتمي الهي است؛ و اوست عزيز و حکيم (شکست‌ناپذير و دانا).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3479, N'(او) آسمانها را بدون ستوني که آن را ببينيد آفريد، و در زمين کوه‌هايي افکند تا شما را نلرزاند (و جايگاه شما آرام باشد) و از هر گونه جنبنده‌اي روي آن منتشر ساخت؛ و از آسمان آبي نازل کرديم و بوسيله آن در روي زمين انواع گوناگوني از جفتهاي گياهان پر ارزش رويانديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3480, N'اين آفرينش خداست؛ اما به من نشان دهيد معبوداني غير او چه چيز را آفريده‌اند؟! ولي ظالمان در گمراهي آشکارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3481, N'ما به لقمان حکمت داديم؛ (و به او گفتيم:) شکر خدا را بجاي آور هر کس شکرگزاري کند، تنها به سود خويش شکر کرده؛ و آن کس که کفران کند، (زياني به خدا نمي‌رساند)؛ چرا که خداوند بي‌نياز و ستوده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3482, N'(به خاطر بياور) هنگامي را که لقمان به فرزندش -در حالي که او را موعظه مي‌کرد- گفت: «پسرم! چيزي را همتاي خدا قرار مده که شرک، ظلم بزرگي است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3483, N'و ما به انسان درباره پدر و مادرش سفارش کرديم؛ مادرش او را با ناتواني روي ناتواني حمل کرد (به هنگام بارداري هر روز رنج و ناراحتي تازه‌اي را متحمل مي‌شد)، و دوران شيرخوارگي او در دو سال پايان مي‌يابد؛ (آري به او توصيه کردم) که براي من و براي پدر و مادرت شکر بجا آور که بازگشت (همه شما)به سوي من است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3484, N'و هرگاه آن دو، تلاش کنند که تو چيزي را همتاي من قرار دهي، که از آن آگاهي نداري (بلکه مي‌داني باطل است)، از ايشان اطاعت مکن، ولي با آن دو، در دنيا به طرز شايسته‌اي رفتار کن؛ و از راه کساني پيروي کن که توبه‌کنان به سوي من آمده‌اند؛ سپس بازگشت همه شما به سوي من است و من شما را از آنچه عمل مي‌کرديد آگاه مي‌کنم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3485, N'پسرم! اگر به اندازه سنگيني دانه خردلي (کار نيک يا بد) باشد، و در دل سنگي يا در (گوشه‌اي از) آسمانها و زمين قرار گيرد، خداوند آن را (در قيامت براي حساب) مي‌آورد؛ خداوند دقيق و آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3486, N'پسرم! نماز را برپا دار، و امر به معروف و نهي از منکر کن، و در برابر مصايبي که به تو مي‌رسد شکيبا باش که اين از کارهاي مهم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3487, N'(پسرم!) با بي‌اعتنايي از مردم روي مگردان، و مغرورانه بر زمين راه مرو که خداوند هيچ متکبر مغروري را دوست ندارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3488, N'(پسرم!) در راه‌رفتن، اعتدال را رعايت کن؛ از صداي خود بکاه (و هرگز فرياد مزن) که زشت‌ترين صداها صداي خران است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3489, N'آيا نديديد خداوند آنچه را در آسمانها و زمين است مسخر شما کرده، و نعمتهاي آشکار و پنهان خود را به طور فراوان بر شما ارزاني داشته است؟! ولي بعضي از مردم بدون هيچ دانش و هدايت و کتاب روشنگري درباره خدا مجادله مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3490, N'و هنگامي که به آنان گفته شود: «از آنچه خدا نازل کرده پيروي کنيد!»، مي‌گويند: «نه، بلکه ما از چيزي پيروي مي‌کنيم که پدران خود را بر آن يافتيم!»آيا حتي اگر شيطان آنان را دعوت به عذاب آتش فروزان کند (باز هم تبعيت مي‌کنند)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3491, N'کسي که روي خود را تسليم خدا کند در حالي که نيکوکار باشد، به دستگيره محکمي چنگ زده (و به تکيه‌گاه مطمئني تکيه کرده است)؛ و عاقبت همه کارها به سوي خداست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3492, N'و کسي که کافر شود، کفر او تو را غمگين نسازد؛ بازگشت همه آنان به سوي ماست و ما آنها را از اعمالي که انجام داده‌اند (و نتايج شوم آن) آگاه خواهيم ساخت؛ خداوند به آنچه درون سينه‌هاست آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3493, N'ما اندکي آنها را از متاع دنيا بهره‌مند مي‌کنيم، سپس آنها را به تحمل عذاب شديدي وادار مي‌سازيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3494, N'و هرگاه از آنان سؤال کني: «چه کسي آسمانها و زمين را آفريده است؟» مسلما مي‌گويند: «الله»، بگو: «الحمد لله (که خود شما معترفيد)!» ولي بيشتر آنان نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3495, N'آنچه در آسمانها و زمين است از آن خداست، چرا که خداوند بي‌نياز و شايسته ستايش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3496, N'و اگر همه درختان روي زمين قلم شود، و دريا براي آن مرکب گردد، و هفت درياچه به آن افزوده شود، اينها همه تمام مي‌شود ولي کلمات خدا پايان نمي‌گيرد؛ خداوند عزيز و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3497, N'آفرينش و برانگيختن (و زندگي دوباره) همه شما (در قيامت) همانند يک فرد بيش نيست؛ خداوند شنوا و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3498, N'آيا نديدي که خداوند شب را در روز، و روز را در شب داخل مي‌کند، و خورشيد و ماه را مسخر ساخته و هر کدام تا سرآمد يعني به حرکت خود ادامه مي‌دهند؟! خداوند به آنچه انجام مي‌دهيد آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3499, N'اينها همه دليل بر آن است که خداوند حق است، و آنچه غير از او مي‌خوانند باطل است، و خداوند بلند مقام و بزرگ مرتبه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3500, N'آيا نديدي کشتيها بر (صفحه) درياها به فرمان خدا، و به (برکت) نعمت او حرکت مي‌کنند تا بخشي از آياتش را به شما نشان دهد؟! در اينها نشانه‌هايي است براي کساني که شکيبا و شکرگزارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3501, N'و هنگامي که (در سفر دريا) موجي همچون ابرها آنان را بپوشاند (و بالا رود و بالاي سرشان قرار گيرد)، خدا را با اخلاص مي‌خوانند؛ اما وقتي آنها را به خشکي رساند و نجات داد، بعضي راه اعتدال را پيش مي‌گيرند (و به ايمان خود وفادار مي‌مانند، در حالي که بعضي ديگر فراموش کرده راه کفر پيش مي‌گيرند)؛ ولي آيات ما را هيچ کس جز پيمان‌شکنان ناسپاس انکار نمي‌کنند!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3502, N'اي مردم! تقواي الهي پيشه کنيد و بترسيد از روزي که نه پدر کيفر اعمال فرزندش را تحمل مي‌کند، و نه فرزند چيزي از کيفر (اعمال) پدرش را؛ به يقين وعده الهي حق است؛ پس مبادا زندگاني دنيا شما را بفريبد، و مبادا (شيطان) فريبکار شما را (به کرم) خدا مغرور سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3503, N'آگاهي از زمان قيام قيامت مخصوص خداست، و اوست که باران را نازل مي‌کند، و آنچه را که در رحم‌ها(ي مادران) است مي‌داند، و هيچ کس نمي‌داند فردا چه به دست مي‌آورد، و هيچ کس نمي‌داند در چه سرزميني مي‌ميرد؟ خداوند عالم و آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3504, N'الم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3505, N'اين کتابي است که از سوي پروردگار جهانيان نازل شده، و شک و ترديدي در آن نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3506, N'ولي آنان مي‌گويند: «(محمد) آن را بدروغ به خدا بسته است»، اما اين سخن حقي است از سوي پروردگارت تا گروهي را انذار کني که پيش از تو هيچ انذارکننده‌اي براي آنان نيامده است، شايد (پند گيرند و) هدايت شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3507, N'خداوند کسي است که آسمانها و زمين و آنچه را ميان اين دو است در شش روز [= شش دوران‌] آفريد، سپس بر عرش (قدرت) قرار گرفت؛ هيچ سرپرست و شفاعت کننده‌اي براي شما جز او نيست؛ آيا متذکر نمي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3508, N'امور اين جهان را از آسمان به سوي زمين تدبير مي‌کند؛ سپس در روزي که مقدار آن هزار سال از سالهايي است که شما مي‌شمريد بسوي او بالا مي‌رود (و دنيا پايان مي‌يابد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3509, N'او خداوندي است که از پنهان و آشکار با خبر است، و شکست‌ناپذير و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3510, N'او همان کسي است که هر چه را آفريد نيکو آفريد؛ و آفرينش انسان را از گل آغاز کرد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3511, N'سپس نسل او را از عصاره‌اي از آب ناچيز و بي‌قدر آفريد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3512, N'سپس (اندام) او را موزون ساخت و از روح خويش در وي دميد؛ و براي شما گوش و چشمها و دلها قرار داد؛ اما کمتر شکر نعمتهاي او را بجا مي‌آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3513, N'آنها گفتند: «آيا هنگامي که ما (مرديم و) در زمين گم شديم، آفرينش تازه‌اي خواهيم يافت؟!» ولي آنان لقاي پروردگارشان را انکار مي‌کنند (و مي‌خواهند با انکار معاد، آزادانه به هوسراني خويش ادامه دهند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3514, N'بگو: «فرشته مرگ که بر شما مأمور شده، (روح) شما را مي‌گيرد؛ سپس شما را بسوي پروردگارتان بازمي‌گردانند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3515, N'و اگر ببيني مجرمان را هنگامي که در پيشگاه پروردگارشان سر به زير افکنده، مي‌گويند: «پروردگارا! آنچه وعده کرده بودي ديديم و شنيديم؛ ما را بازگردان تا کار شايسته‌اي انجام دهيم؛ ما (به قيامت) يقين داريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3516, N'و اگر مي‌خواستيم به هر انساني هدايت لازمش را (از روي اجبار بدهيم) مي‌داديم؛ ولي (من آنها را آزاد گذارده‌ام و) سخن و وعده‌ام حق است که دوزخ را (از افراد بي‌ايمان و گنهکار) از جن و انس همگي پر کنم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3517, N'(و به آنها مي‌گويم:) بچشيد (عذاب جهنم را)! بخاطر اينکه ديدار امروزتان را فراموش کرديد، ما نيز شما را فراموش کرديم؛ و بچشيد عذاب جاودان را به خاطر اعمالي که انجام مي‌داديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3518, N'تنها کساني که به آيات ما ايمان مي‌آورند که هر وقت اين آيات به آنان يادآوري شود به سجده مي‌افتند و تسبيح و حمد پروردگارشان را بجا مي‌آورند، و تکبر نمي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3519, N'پهلوهايشان از بسترها در دل شب دور مي‌شود (و بپا مي‌خيزند و رو به درگاه خدا مي آورند) و پروردگار خود را با بيم و اميد مي‌خوانند، و از آنچه به آنان روزي داده‌ايم انفاق مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3520, N'هيچ کس نمي‌داند چه پاداشهاي مهمي که مايه روشني چشمهاست براي آنها نهفته شده، اين پاداش کارهائي است که انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3521, N'آيا کسي که باايمان باشد همچون کسي است که فاسق است؟! نه، هرگز اين دو برابر نيستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3522, N'اما کساني که ايمان آوردند و کارهاي شايسته انجام دادند، باغهاي بهشت جاويدان از آن آنها خواهد بود، اين وسيله پذيرايي (خداوند) از آنهاست به پاداش آنچه انجام مي‌دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3523, N'و اما کساني که فاسق شدند (و از اطاعت خدا سرباز زدند)، جايگاه هميشگي آنها آتش است؛ هر زمان بخواهند از آن خارج شوند، آنها را به آن بازمي‌گردانند و به آنان گفته مي‌شود: «بچشيد عذاب آتشي را که انکار مي‌کرديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3524, N'به آنان از عذاب نزديک (عذاب اين دنيا) پيش از عذاب بزرگ (آخرت) مي‌چشانيم، شايد بازگردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3525, N'چه کسي ستمکارتر است از آن کس که آيات پروردگارش به او يادآوري شده و او از آن اعراض کرده است؟! مسلما ما از مجرمان انتقام خواهيم گرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3526, N'ما به موسي کتاب آسماني داديم؛ و شک نداشته باش که او آيات الهي را دريافت داشت؛ و ما آن را وسيله هدايت بني اسرائيل قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3527, N'و از آنان امامان (و پيشواياني) قرار داديم که به فرمان ما (مردم را) هدايت مي‌کردند؛ چون شکيبايي نمودند، و به آيات ما يقين داشتند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3528, N'البته پروردگار تو ميان آنان روز قيامت در آنچه اختلاف داشتند داوري مي‌کند (و هر کس را به سزاي اعمالش مي‌رساند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3529, N'آيا براي هدايت آنها همين کافي نيست که افراد زيادي را که در قرن پيش از آنان زندگي داشتند هلاک کرديم؟! اينها در مساکن (ويران شده) آنان راه مي‌روند؛ در اين آياتي است (از قدرت خداوند و مجازات دردناک مجرمان)؛ آيا نمي‌شنوند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3530, N'آيا نديدند که ما آب را بسوي زمينهاي خشک مي‌رانيم و بوسيله آن زراعتهايي مي‌رويانيم که هم چهارپايانشان از آن مي‌خورند و هم خودشان تغذيه مي‌کنند؛ آيا نمي بينند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3531, N'آنان مي‌گويند: «اگر راست مي‌گوييد، اين پيروزي شما کي خواهد بود؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3532, N'بگو: «روز پيروزي، ايمان آوردن، سودي به حال کافران نخواهد داشت؛ و به آنها هيچ مهلت داده نمي‌شود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3533, N'حال که چنين است، از آنها روي بگردان و منتظر باش؛ آنها نيز منتظرند! (تو منتظر رحمت خدا و آنها هم منتظر عذاب او!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3534, N'اي پيامبر! تقواي الهي پيشه کن و از کافران و منافقان اطاعت مکن که خداوند عالم و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3535, N'و از آنچه از سوي پروردگارت به تو وحي مي‌شود پيروي کن که خداوند به آنچه انجام مي‌دهيد آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3536, N'و بر خدا توکل کن، و همين بس که خداوند حافظ و مدافع (انسان) باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3537, N'خداوند براي هيچ کس دو دل در درونش نيافريده؛ و هرگز همسرانتان را که مورد «ظهار» قرارمي‌دهيد مادران شما قرار نداده؛ و (نيز) فرزندخوانده‌هاي شما را فرزند حقيقي شما قرارنداده است؛ اين سخن شماست که به دهان خود مي‌گوييد (سخني باطل و بي‌پايه)؛ اما خداوند حق را مي‌گويد و او به راه راست هدايت مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3538, N'آنها را به نام پدرانشان بخوانيد که اين کار نزد خدا عادلانه‌تر است؛ و اگر پدرانشان را نمي‌شناسيد، آنها برادران ديني و موالي شما هستند؛ اما گناهي بر شما نيست در خطاهايي که از شما سرمي‌زند (و بي‌توجه آنها را به نام ديگران صدا مي‌زنيد)، ولي آنچه را از روي عمد مي‌گوييد (مورد حساب قرار خواهد داد)؛ و خداوند آمرزنده و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3539, N'پيامبر نسبت به مؤمنان از خودشان سزاوارتر است؛ و همسران او مادران آنها [= مؤمنان‌] محسوب مي‌شوند؛ و خويشاوندان نسبت به يکديگر از مؤمنان و مهاجران در آنچه خدا مقرر داشته اولي هستند، مگر اينکه بخواهيد نسبت به دوستانتان نيکي کنيد (و سهمي از اموال خود را به آنها بدهيد)؛ اين حکم در کتاب (الهي) نوشته شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3540, N'(به خاطر آور) هنگامي را که از پيامبران پيمان گرفتيم، و (همچنين) از تو و از نوح و ابراهيم و موسي و عيسي بن مريم، و ما از همه آنان پيمان محکمي گرفتيم (که در اداي مسؤوليت تبليغ و رسالت کوتاهي نکنند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3541, N'به اين منظور که خدا راستگويان را از صدقشان (در ايمان و عمل صالح) سؤال کند؛ و براي کافران عذابي دردناک آماده ساخته است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3542, N'اي کساني که ايمان آورده‌ايد! نعمت خدا را بر خود به ياد آوريد در آن هنگام که لشکرهايي (عظيم) به سراغ شما آمدند؛ ولي ما باد و طوفان سختي بر آنان فرستاديم و لشکرياني که آنها را نمي‌ديديد (و به اين وسيله آنها را در هم شکستيم)؛ و خداوند هميشه به آنچه انجام مي‌دهيد بينا بوده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3543, N'(به خاطر بياوريد) زماني را که آنها از طرف بالا و پايين (شهر) بر شما وارد شدند (و مدينه را محاصره کردند) و زماني را که چشمها از شدت وحشت خيره شده و جانها به لب رسيده بود، و گمانهاي گوناگون بدي به خدا مي‌برديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3544, N'آنجا بود که مؤمنان آزمايش شدند و تکان سختي خوردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3545, N'و (نيز) به خاطر آوريد زماني را که منافقان و بيماردلان مي‌گفتند: «خدا و پيامبرش جز وعده‌هاي دروغين به ما نداده‌اند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3546, N'و (نيز) به خاطر آوريد زماني را که گروهي از آنها گفتند: «اي اهل يثرب (اي مردم مدينه)! اينجا جاي توقف شما نيست؛ به خانه‌هاي خود بازگرديد!» و گروهي از آنان از پيامبر اجازه بازگشت مي‌خواستند و مي‌گفتند: «خانه‌هاي ما بي‌حفاظ است!»، در حالي که بي‌حفاظ نبود؛ آنها فقط مي‌خواستند (از جنگ) فرار کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3547, N'آنها چنان بودند که اگر دشمنان از اطراف مدينه بر آنان وارد مي‌شدند و پيشنهاد بازگشت به سوي شرک به آنان مي‌کردند مي‌پذيرفتند، و جز مدت کمي (براي انتخاب اين راه) درنگ نمي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3548, N'(در حالي که) آنان قبل از اين با خدا عهد کرده بودند که پشت به دشمن نکنند؛ و عهد الهي مورد سؤال قرار خواهد گرفت (و در برابر آن مسؤولند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3549, N'بگو: «اگر از مرگ يا کشته‌شدن فرار کنيد، سودي به حال شما نخواهد داشت؛ و در آن هنگام جز بهره کمي از زندگاني نخواهيد گرفت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3550, N'بگو: «چه کسي مي‌تواند شما را در برابر اراده خدا حفظ کند اگر او بدي يا رحمتي را براي شما اراده کند؟!» و آنها جز خدا هيچ سرپرست و ياوري براي خود نخواهند يافت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3551, N'خداوند کساني که مردم را از جنگ بازمي‌داشتند و کساني را که به برادران خود مي‌گفتند: «بسوي ما بياييد (و خود را از معرکه بيرون کشيد)» بخوبي مي‌شناسد؛ و آنها (مردمي ضعيفند و) جز اندکي پيکار نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3552, N'آنها در همه چيز نسبت به شما بخيلند؛ و هنگامي که (لحظات) ترس (و بحراني) پيش آيد، مي‌بيني آنچنان به تو نگاه مي‌کنند، و چشمهايشان در حدقه مي‌چرخد، که گويي مي‌خواهند قالب تهي کنند! اما وقتي حالت خوف و ترس فرو نشست، زبانهاي تند و خشن خود را با انبوهي از خشم و عصبانيت بر شما مي‌گشايند (و سهم خود را از غنايم مطالبه مي‌کنند!) در حالي که در آن نيز حريص و بخيلند؛ آنها (هرگز) ايمان نياورده‌اند، از اين رو خداوند اعمالشان را حبط و نابود کرد؛ و اين کار بر خدا آسان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3553, N'آنها گمان مي‌کنند هنوز لشکر احزاب نرفته‌اند؛ و اگر برگردند (از ترس آنان) دوست مي‌دارند در ميان اعراب باديه‌نشين پراکنده (و پنهان) شوند و از اخبار شما جويا گردند؛ و اگر در ميان شما باشند جز اندکي پيکار نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3554, N'مسلما براي شما در زندگي رسول خدا سرمشق نيکويي بود، براي آنها که اميد به رحمت خدا و روز رستاخيز دارند و خدا را بسيار ياد مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3555, N'(اما) مؤمنان وقتي لشکر احزاب را ديدند گفتند: «اين همان است که خدا و رسولش به ما وعده داده، و خدا و رسولش راست گفته‌اند!» و اين موضوع جز بر ايمان و تسليم آنان نيفزود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3556, N'در ميان مؤمنان مرداني هستند که بر سر عهدي که با خدا بستند صادقانه ايستاده‌اند؛ بعضي پيمان خود را به آخر بردند (و در راه او شربت شهادت نوشيدند)، و بعضي ديگر در انتظارند؛ و هرگز تغيير و تبديلي در عهد و پيمان خود ندادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3557, N'هدف اين است که خداوند صادقان را بخاطر صدقشان پاداش دهد، و منافقان را هرگاه اراده کند عذاب نمايد يا (اگر توبه کنند) توبه آنها را بپذيرد؛ چرا که خداوند آمرزنده و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3558, N'خدا کافران را با دلي پر از خشم بازگرداند بي‌آنکه نتيجه‌اي از کار خود گرفته باشند؛ و خداوند (در اين ميدان)، مؤمنان را از جنگ بي‌نياز ساخت (و پيروزي را نصيبشان کرد)؛ و خدا قوي و شکست‌ناپذير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3559, N'و خداوند گروهي از اهل کتاب [= يهود] را که از آنان [= مشرکان عرب‌] حمايت کردند از قلعه‌هاي محکمشان پايين کشيد و در دلهايشان رعب افکند؛ (و کارشان به جايي رسيد که) گروهي را به قتل مي‌رسانديد و گروهي را اسير مي‌کرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3560, N'و زمينها و خانه‌ها و اموالشان را در اختيار شما گذاشت، و (همچنين) زميني را که هرگز در آن گام ننهاده بوديد؛ و خداوند بر هر چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3561, N'اي پيامبر! به همسرانت بگو: «اگر شما زندگي دنيا و زرق و برق آن را مي‌خواهيد بياييد با هديه‌اي شما را بهره‌مند سازم و شما را بطرز نيکويي رها سازم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3562, N'و اگر شما خدا و پيامبرش و سراي آخرت را مي‌خواهيد، خداوند براي نيکوکاران شما پاداش عظيمي آماده ساخته است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3563, N'اي همسران پيامبر! هر کدام از شما گناه آشکار و فاحشي مرتکب شود، عذاب او دوچندان خواهد بود؛ و اين براي خدا آسان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3564, N'و هر کس از شما براي خدا و پيامبرش خضوع کند و عمل صالح انجام دهد، پاداش او را دو چندان خواهيم ساخت، و روزي پرارزشي براي او آماده کرده‌ايم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3565, N'اي همسران پيامبر! شما همچون يکي از زنان معمولي نيستيد اگر تقوا پيشه کنيد؛ پس به گونه‌اي هوس‌انگيز سخن نگوييد که بيماردلان در شما طمع کنند، و سخن شايسته بگوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3566, N'و در خانه‌هاي خود بمانيد، و همچون دوران جاهليت نخستين (در ميان مردم) ظاهر نشويد، و نماز را برپا داريد، و زکات را بپردازيد، و خدا و رسولش را اطاعت کنيد؛ خداوند فقط مي‌خواهد پليدي و گناه را از شما اهل بيت دور کند و کاملا شما را پاک سازد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3567, N'آنچه را در خانه‌هاي شما از آيات خداوند و حکمت و دانش خوانده مي‌شود ياد کنيد؛ خداوند لطيف و خبير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3568, N'به يقين، مردان مسلمان و زنان مسلمان، مردان با ايمان و زنان با ايمان، مردان مطيع فرمان خدا و زنان مطيع فرمان خدا، مردان راستگو و زنان راستگو، مردان صابر و شکيبا و زنان صابر و شکيبا، مردان با خشوع و زنان با خشوع، مردان انفاق کننده و زنان انفاق کننده، مردان روزه‌دار و زنان روزه‌دار، مردان پاکدامن و زنان پاکدامن و مرداني که بسيار به ياد خدا هستند و زناني که بسيار ياد خدا مي‌کنند، خداوند براي همه آنان مغفرت و پاداش عظيمي فراهم ساخته است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3569, N'هيچ مرد و زن با ايماني حق ندارد هنگامي که خدا و پيامبرش امري را لازم بدانند، اختياري (در برابر فرمان خدا) داشته باشد؛ و هر کس نافرماني خدا و رسولش را کند، به گمراهي آشکاري گرفتار شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3570, N'(به خاطر بياور) زماني را که به آن کس که خداوند به او نعمت داده بود و تو نيز به او نعمت داده بودي [به فرزند خوانده‌ات «زيد»] مي‌گفتي: «همسرت را نگاه‌دار و از خدا بپرهيز!» (و پيوسته اين امر را تکرار مي‌کردي)؛ و در دل چيزي را پنهان مي‌داشتي که خداوند آن را آشکار مي‌کند؛ و از مردم مي‌ترسيدي در حالي که خداوند سزاوارتر است که از او بترسي! هنگامي که زيد نيازش را از آن زن به سرآورد (و از او جدا شد)، ما او را به همسري تو درآورديم تا مشکلي براي مؤمنان در ازدواج با همسران پسر خوانده‌هايشان -هنگامي که طلاق گيرند- نباشد؛ و فرمان خدا انجام شدني است (و سنت غلط تحريم اين زنان بايد شکسته شود).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3571, N'هيچ گونه منعي بر پيامبر در آنچه خدا بر او واجب کرده نيست؛ اين سنت الهي در مورد کساني که پيش از اين بوده‌اند نيز جاري بوده؛ و فرمان خدا روي حساب و برنامه دقيقي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3572, N'(پيامبران) پيشين کساني بودند که تبليغ رسالتهاي الهي مي‌کردند و (تنها) از او مي ترسيدند، و از هيچ کس جز خدا بيم نداشتند؛ و همين بس که خداوند حسابگر (و پاداش‌دهنده اعمال آنها) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3573, N'محمد (ص) پدر هيچ يک از مردان شما نبوده و نيست؛ ولي رسول خدا و ختم‌کننده و آخرين پيامبران است؛ و خداوند به همه چيز آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3574, N'اي کساني که ايمان آورده‌ايد! خدا را بسيار ياد کنيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3575, N'و صبح و شام او را تسبيح گوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3576, N'او کسي است که بر شما درود و رحمت مي‌فرستد، و فرشتگان او (نيز) براي شما تقاضاي رحمت مي‌کنند تا شما را از ظلمات (جهل و شرک گناه) به سوي نور (ايمان و علم و تقوا) رهنمون گردد؛ او نسبت به مؤمنان همواره مهربان بوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3577, N'تحيت آنان در روزي که او را ديدار مي‌کنند سلام است؛ و براي آنها پاداش پرارزشي فراهم ساخته است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3578, N'اي پيامبر! ما تو را گواه فرستاديم و بشارت‌دهنده و انذارکننده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3579, N'و تو را دعوت‌کننده بسوي خدا به فرمان او قرار داديم، و چراغي روشني‌بخش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3580, N'و مؤمنان را بشارت ده که براي آنان از سوي خدا فضل بزرگي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3581, N'و از کافران و منافقان اطاعت مکن، و به آزارهاي آنها اعتنا منما، و بر خدا توکل کن، و همين بس که خدا حامي و مدافع (تو) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3582, N'اي کساني که ايمان آورده‌ايد! هنگامي که با زنان با ايمان ازدواج کرديد و قبل از همبستر شدن طلاق داديد، عده‌اي براي شما بر آنها نيست که بخواهيد حساب آن را نگاه داريد؛ آنها را با هديه مناسبي بهره‌مند سازيد و بطرز شايسته‌اي رهايشان کنيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3583, N'اي پيامبر! ما همسران تو را که مهرشان را پرداخته‌اي براي تو حلال کرديم، و همچنين کنيزاني که از طريق غنايمي که خدا به تو بخشيده است مالک شده‌اي و دختران عموي تو، و دختران عمه‌ها، و دختران دايي تو، و دختران خاله‌ها که با تو مهاجرت کردند (ازدواج با آنها براي تو حلال است) و هرگاه زن با ايماني خود را به پيامبر ببخشد (و مهري براي خود نخواهد) چنانچه پيامبر بخواهد مي‌تواند او را به همسري برگزيند؛ اما چنين ازدواجي تنها براي تو مجاز است نه ديگر مؤمنان؛ ما مي‌دانيم براي آنان در مورد همسرانشان و کنيزانشان چه حکمي مقرر داشته‌ايم (و مصلحت آنان چه حکمي را ايجاب مي‌کند)؛ اين بخاطر آن است که مشکلي (در اداي رسالت) بر تو نباشد (و از اين راه حاميان فزونتري فراهم سازي)؛ و خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3584, N'(موعد) هر يک از همسرانت را بخواهي مي‌تواني به تأخير اندازي، و هر کدام را بخواهي نزد خود جاي دهي؛ و هرگاه بعضي از آنان را که برکنار ساخته‌اي بخواهي نزد خود جاي دهي، گناهي بر تو نيست؛ اين حکم الهي براي روشني چشم آنان، و اينکه غمگين نباشند و به آنچه به آنان مي‌دهي همگي راضي شوند نزديکتر است؛ و خدا آنچه را در قلوب شماست مي‌داند، و خداوند دانا و بردبار است (از مصالح بندگان خود با خبر است، و در کيفر آنها عجله نمي‌کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3585, N'بعد از اين ديگر زني بر تو حلال نيست، و نمي‌تواني همسرانت را به همسران ديگري مبدل کني [= بعضي را طلاق دهي و همسر ديگري به جاي او برگزيني‌] هر چند جمال آنها مورد توجه تو واقع شود، مگر آنچه که بصورت کنيز در ملک تو درآيد! و خداوند ناظر و مراقب هر چيز است (و با اين حکم فشار قبايل عرب را در اختيار همسر از آنان، از تو برداشتيم).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3586, N'اي کساني که ايمان آورده‌ايد! در خانه‌هاي پيامبر داخل نشويد مگر به شما براي صرف غذا اجازه داده شود، در حالي که (قبل از موعد نياييد و) در انتظار وقت غذا ننشينيد؛ اما هنگامي که دعوت شديد داخل شويد؛ و وقتي غذا خورديد پراکنده شويد، و (بعد از صرف غذا) به بحث و صحبت ننشينيد؛ اين عمل، پيامبر را ناراحت مي‌نمايد، ولي از شما شرم مي‌کند (و چيزي نمي‌گويد)؛ اما خداوند از (بيان) حق شرم ندارد! و هنگامي که چيزي از وسايل زندگي را (بعنوان عاريت) از آنان [= همسران پيامبر] مي‌خواهيد از پشت پرده بخواهيد؛ اين کار براي پاکي دلهاي شما و آنها بهتر است! و شما حق نداريد رسول خدا را آزار دهيد، و نه هرگز همسران او را بعد از او به همسري خود درآوريد که اين کار نزد خدا بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3587, N'اگر چيزي را آشکار کنيد يا آن را پنهان داريد، خداوند از همه چيز آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3588, N'بر آنان [= همسران پيامبر] گناهي نيست در مورد پدران و فرزندان و برادران و فرزندان برادران و فرزندان خواهران خود و زنان مسلمان و بردگان خويش (که بدون حجاب و پرده با آنها تماس بگيرند)؛ و تقواي الهي را پيشه کنيد که خداوند نسبت به هر چيزي شاهد و آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3589, N'خدا و فرشتگانش بر پيامبر درود مي‌فرستد؛ اي کساني که ايمان آورده‌ايد، بر او درود فرستيد و سلام گوييد و کاملا تسليم (فرمان او) باشيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3590, N'آنها که خدا و پيامبرش را آزار مي‌دهند، خداوند آنان را از رحمت خود در دنيا و آخرت دور ساخته، و براي آنها عذاب خوارکننده‌اي آماده کرده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3591, N'و آنان که مردان و زنان باايمان را به خاطر کاري که انجام نداده‌اند آزار مي‌دهند؛ بار بهتان و گناه آشکاري را به دوش کشيده‌اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3592, N'اي پيامبر! به همسران و دخترانت و زنان مؤمنان بگو: «جلبابها [= روسري‌هاي بلند] خود را بر خويش فروافکنند، اين کار براي اينکه شناخته شوند و مورد آزار قرار نگيرند بهتر است؛ (و اگر تاکنون خطا و کوتاهي از آنها سر زده توبه کنند) خداوند همواره آمرزنده رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3593, N'اگر منافقان و بيماردلان و آنها که اخبار دروغ و شايعات بي‌اساس در مدينه پخش مي کنند دست از کار خود بر ندارند، تو را بر ضد آنان مي‌شورانيم، سپس جز مدت کوتاهي نمي‌توانند در کنار تو در اين شهر بمانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3594, N'و از همه جا طرد مي‌شوند، و هر جا يافته شوند گرفته خواهند شد و به سختي به قتل خواهند رسيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3595, N'اين سنت خداوند در اقوام پيشين است، و براي سنت الهي هيچ گونه تغيير نخواهي يافت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3596, N'مردم از تو درباره (زمان قيام) قيامت سؤال مي‌کنند، بگو: «علم آن تنها نزد خداست!» و چه مي‌داني شايد قيامت نزديک باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3597, N'خداوند کافران را لعن کرده (و از رحمت خود دور داشته) و براي آنان آتش سوزاننده‌اي آماده نموده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3598, N'در حالي که همواره در آن تا ابد مي‌مانند، و ولي و ياوري نخواهند يافت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3599, N'در آن روز که صورتهاي آنان در آتش (دوزخ) دگرگون خواهد شد (از کار خويش پشيمان مي‌شوند و) مي‌گويند: «اي کاش خدا و پيامبر را اطاعت کرده بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3600, N'و مي‌گويند: «پروردگارا! ما از سران و بزرگان خود اطاعت کرديم و ما را گمراه ساختند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3601, N'پروردگارا! آنان را از عذاب، دو چندان ده و آنها را لعن بزرگي فرما!»')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3602, N'اي کساني که ايمان آورده‌ايد! همانند کساني نباشيد که موسي را آزار دادند؛ و خداوند او را از آنچه در حق او مي‌گفتند مبرا ساخت؛ و او نزد خداوند، آبرومند (و گرانقدر) بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3603, N'اي کساني که ايمان آورده‌ايد! تقواي الهي پيشه کنيد و سخن حق بگوييد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3604, N'تا خدا کارهاي شما را اصلاح کند و گناهانتان را بيامرزد؛ و هر کس اطاعت خدا و رسولش کند، به رستگاري (و پيروزي) عظيمي دست يافته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3605, N'ما امانت (تعهد، تکليف، و ولايت الهيه) را بر آسمانها و زمين و کوه‌ها عرضه داشتيم، آنها از حمل آن سر برتافتند، و از آن هراسيدند؛ اما انسان آن را بر دوش کشيد؛ او بسيار ظالم و جاهل بود، (چون قدر اين مقام عظيم را نشناخت و به خود ستم کرد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3606, N'هدف اين بود که خداوند مردان و زنان منافق و مردان و زنان مشرک را (از مؤمنان جدا سازد و آنان را) عذاب کند، و خدا رحمت خود را بر مردان و زنان باايمان بفرستد؛ خداوند همواره آمرزنده و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3607, N'حمد (و ستايش) مخصوص خداوندي است که تمام آنچه در آسمانها و زمين است از آن اوست؛ و (نيز) حمد (و سپاس) براي اوست در سراي آخرت؛ و او حکيم و آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3608, N'آنچه در زمين فرومي‌رود و آنچه را از آن برمي‌آيد مي‌داند، و (همچنين) آنچه از آسمان نازل مي‌شود و آنچه بر آن بالا مي‌رود؛ و او مهربان و آمرزنده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3609, N'کافران گفتند: «قيامت هرگز به سراغ ما نخواهد آمد!» بگو: «آري به پروردگارم سوگند که به سراغ شما خواهد آمد، خداوندي که از غيب آگاه است و به اندازه سنگيني ذره‌اي در آسمانها و زمين از علم او دور نخواهد ماند، و نه کوچکتر از آن و نه بزرگتر، مگر اينکه در کتابي آشکار ثبت است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3610, N'تا کساني را که ايمان آورده و کارهاي شايسته انجام داده‌اند پاداش دهد؛ براي آنان مغفرت و روزي پرارزشي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3611, N'و کساني که سعي در (تکذيب) آيات ما داشتند و گمان کردند مي‌توانند از حوزه قدرت ما بگريزند، عذابي بد و دردناک خواهند داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3612, N'کساني که به ايشان علم داده شده، آنچه را از سوي پروردگارت بر تو نازل شده حق مي دانند و به راه خداوند عزيز و حميد هدايت مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3613, N'و کافران گفتند: «آيا مردي را به شما نشان دهيم که به شما خبر مي‌دهد هنگامي که (مرديد و) سخت از هم متلاشي شديد، (بار ديگر) آفرينش تازه‌اي خواهيد يافت؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3614, N'آيا او بر خدا دروغ بسته يا به نوعي جنون گرفتار است؟!» (چنين نيست)، بلکه کساني که به آخرت ايمان نمي‌آورند، در عذاب و گمراهي دوري هستند (و نشانه گمراهي آنها همين انکار شديد است).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3615, N'آيا به آنچه پيش رو و پشت سر آنان از آسمان و زمين قرار دارد نگاه نکردند (تا به قدرت خدا بر همه چيز واقف شوند)؟! اگر ما بخواهيم آنها را (با يک زمين‌لرزه) در زمين فرومي‌بريم، يا قطعه‌هايي از سنگهاي آسماني را بر آنها فرومي‌ريزيم؛ در اين نشانه‌اي است (بر قدرت خدا) براي هر بنده توبه‌کار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3616, N'و ما به داوود از سوي خود فضيلتي بزرگ بخشيديم؛ (ما به کوه‌ها و پرندگان گفتيم:) اي کوه‌ها و اي پرندگان! با او هم‌آواز شويد و همراه او تسبيح خدا گوييد! و آهن را براي او نرم کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3617, N'(و به او گفتيم:) زره‌هاي کامل و فراخ بساز، و حلقه‌ها را به اندازه و متناسب کن! و عمل صالح بجا آوريد که من به آنچه انجام ميدهيد بينا هستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3618, N'و براي سليمان باد را مسخر ساختيم که صبحگاهان مسير يک ماه را مي‌پيمود و عصرگاهان مسير يک ماه را؛ و چشمه مس (مذاب) را براي او روان ساختيم؛ و گروهي از جن پيش روي او به اذن پروردگارش کار مي‌کردند؛ و هر کدام از آنها که از فرمان ما سرپيچي مي‌کرد، او را عذاب آتش سوزان مي‌چشانيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3619, N'آنها هر چه سليمان مي‌خواست برايش درست مي‌کردند: معبدها، تمثالها، ظروف بزرگ غذا همانند حوضها، و ديگهاي ثابت (که از بزرگي قابل حمل و نقل نبود؛ و به آنان گفتيم:) اي آل داوود! شکر (اين همه نعمت را) بجا آوريد؛ ولي عده کمي از بندگان من شکرگزارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3620, N'(با اين همه جلال و شکوه سليمان) هنگامي که مرگ را بر او مقرر داشتيم، کسي آنها را از مرگ وي آگاه نساخت مگر جنبنده زمين [= موريانه‌] که عصاي او را مي‌خورد (تا شکست و پيکر سليمان فرو افتاد)؛ هنگامي که بر زمين افتاد جنيان فهميدند که اگر از غيب آگاه بودند در عذاب خوارکننده باقي نمي‌ماندند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3621, N'براي قوم «سبا» در محل سکونتشان نشانه‌اي (از قدرت الهي) بود: دو باغ (بزرگ و گسترده) از راست و چپ (رودخانه عظيم با ميوه‌هاي فراوان؛ و به آنها گفتيم:) از روزي پروردگارتان بخوريد و شکر او را بجا آوريد؛ شهري است پاک و پاکيزه، و پروردگاري آمرزنده (و مهربان)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3622, N'اما آنها (از خدا) روي‌گردان شدند، و ما سيل ويرانگر را بر آنان فرستاديم، و دو باغ (پربرکت)شان را به دو باغ (بي‌ارزش) با ميوه‌هاي تلخ و درختان شوره گز و اندکي درخت سدر مبدل ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3623, N'اين کيفر را بخاطر کفرانشان به آنها داديم؛ و آيا جز کفران کننده را کيفر مي‌دهيم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3624, N'و ميان آنها و شهرهايي که برکت داده بوديم، آباديهاي آشکاري قرار داديم؛ و سفر در ميان آنها را بطور متناسب (با فاصله نزديک) مقرر داشتيم؛ (و به آنان گفتيم:) شبها و روزها در اين آباديها با ايمني (کامل) سفر کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3625, N'ولي (اين ناسپاس مردم) گفتند: «پروردگارا! ميان سفرهاي ما دوري بيفکن» (تا بينوايان نتوانند دوش به دوش اغنيا سفر کنند! و به اين طريق) آنها به خويشتن ستم کردند! و ما آنان را داستانهايي (براي عبرت ديگران) قرار داديم و جمعيتشان را متلاشي ساختيم؛ در اين ماجرا، نشانه‌هاي عبرتي براي هر صابر شکرگزار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3626, N'(آري) بيقين، ابليس گمان خود را درباره آنها محقق يافت که همگي از او پيروي کردند جز گروه اندکي از مؤمنان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3627, N'او سلطه بر آنان نداشت جز براي اينکه مؤمنان به آخرت را از آنها که در شک هستند باز شناسيم؛ و پروردگار تو، نگاهبان همه چيز است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3628, N'بگو: «کساني را که غير از خدا (معبود خود) مي‌پنداريد بخوانيد! (آنها هرگز گرهي از کار شما نمي‌گشايند، چرا که) آنها به اندازه ذره‌اي در آسمانها و زمين مالک نيستند، و نه در (خلقت و مالکيت) آنها شريکند، و نه ياور او (در آفرينش) بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3629, N'هيچ شفاعتي نزد او، جز براي کساني که اذن داده، سودي ندارد! (در آن روز همه در اضطرابند) تا زماني که اضطراب از دلهاي آنان زايل گردد (و فرمان از ناحيه او صادر شود؛ در اين هنگام مجرمان به شفيعان) مي‌گويند: «پروردگارتان چه دستوري داده؟» مي‌گويند: «حق را (بيان کرد و اجازه شفاعت درباره مستحقان داد)؛ و اوست بلندمقام و بزرگ‌مرتبه!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3630, N'بگو: «چه کسي شما را از آسمانها و زمين روزي مي‌دهد؟» بگو: «الله! و ما يا شما بر (طريق) هدايت يا در ضلالت آشکاري هستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3631, N'بگو: «شما از گناهي که ما کرده‌ايم سؤال نخواهيد شد، (همان گونه که) ما در برابر اعمال شما مسؤول نيستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3632, N'بگو: «پروردگار ما همه ما را جمع مي‌کند، سپس در ميان ما بحق داوري مي‌نمايد (و صفوف مجرمان را از نيکوکاران جدا مي‌سازد)، و اوست داور (و جداکننده) آگاه!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3633, N'بگو: «کساني را که بعنوان شريک به او ملحق ساخته‌ايد به من نشان دهيد! هرگز چنين نيست! (او شريک و شبيهي ندارد)، بلکه او خداوند عزيز و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3634, N'و ما تو را جز براي همه مردم نفرستاديم تا (آنها را به پاداشهاي الهي) بشارت دهي و (از عذاب او) بترساني؛ ولي بيشتر مردم نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3635, N'مي‌گويند: «اگر راست مي‌گوييد، اين وعده (رستاخيز) کي خواهد بود؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3636, N'بگو: «وعده شما روزي خواهد بود که نه ساعتي از آن تأخير مي‌کنيد و نه (بر آن) پيشي خواهيد گرفت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3637, N'کافران گفتند: «ما هرگز به اين قرآن و کتابهاي ديگري که پيش از آن بوده ايمان نخواهيم آورد!» اگر ببيني هنگامي که اين ستمگران در پيشگاه پروردگارشان (براي حساب و جزا) نگه داشته شده‌اند در حالي که هر کدام گناه خود را به گردن ديگري مي‌اندازد (از وضع آنها تعجب مي‌کني)! مستضعفان به مستکبران مي‌گويند: «اگر شما نبوديد ما مؤمن بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3638, N'(اما) مستکبران به مستضعفان پاسخ مي‌دهند: «آيا ما شما را از هدايت بازداشتيم بعد از آنکه به سراغ شما آمد (و آن را بخوبي دريافتيد)؟! بلکه شما خود مجرم بوديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3639, N'و مستضعفان به مستکبران مي‌گويند: «وسوسه‌هاي فريبکارانه شما در شب و روز (مايه گمراهي ما شد)، هنگامي که به ما دستور مي‌داديد که به خداوند کافر شويم و همتاياني براي او قرار دهيم!» و آنان هنگامي که عذاب (الهي) را مي‌بينند پشيماني خود را پنهان مي‌کنند (تا بيشتر رسوا نشوند)! و ما غل و زنجيرها در گردن کافران مي نهيم؛ آيا جز آنچه عمل مي‌کردند به آنها جزا داده مي‌شود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3640, N'و ما در هيچ شهر و دياري پيامبري بيم‌دهنده نفرستاديم مگر اينکه مترفين آنها (که مست ناز و نعمت بودند) گفتند: «ما به آنچه فرستاده شده‌ايد کافريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3641, N'و گفتند: «اموال و اولاد ما (از همه) بيشتر است (و اين نشانه علاقه خدا به ماست!)؛ و ما هرگز مجازات نخواهيم شد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3642, N'بگو: «پروردگار من روزي را براي هر کس بخواهد وسيع يا تنگ مي‌کند، (اين ربطي به قرب در درگاه او ندارد)؛ ولي بيشتر مردم نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3643, N'اموال و فرزندانتان هرگز شما را نزد ما مقرب نمي‌سازد، جز کساني که ايمان بياورند و عمل صالحي انجام دهند که براي آنان پاداش مضاعف در برابر کارهايي است که انجام داده‌اند؛ و آنها در غرفه‌هاي (بهشتي) در (نهايت) امنيت خواهند بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3644, N'و کساني که براي انکار و ابطال آيات ما تلاش مي‌کنند و مي‌پندارند از چنگ قدرت ما فرار خواهند کرد، در عذاب (الهي) احضار مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3645, N'بگو: «پروردگارم روزي را براي هر کس بخواهد وسعت مي‌بخشد، و براي هر کس بخواهد تنگ (و محدود) مي‌سازد؛ و هر چيزي را (در راه او) انفاق کنيد، عوض آن را مي‌دهد (و جاي آن را پر مي‌کند)؛ و او بهترين روزي‌دهندگان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3646, N'(به خاطر بياور) روزي را که خداوند همه آنان را بر مي‌انگيزد، سپس به فرشتگان مي‌گويد: «آيا اينها شما را پرستش مي‌کردند؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3647, N'آنها مي‌گويند: «منزهي (از اينکه همتايي داشته باشي)! تنها تو ولي مائي، نه آنها؛ (آنها ما را پرستش نمي‌کردند) بلکه جن را پرستش مي‌نمودند؛ و اکثرشان به آنها ايمان داشتند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3648, N'(آري) امروز هيچ يک از شما نسبت به ديگري مالک سود و زياني نيست! و به ظالمان مي‌گوييم: «بچشيد عذاب آتشي را که تکذيب مي‌کرديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3649, N'و هنگامي که آيات روشنگر ما بر آنان خوانده مي‌شود، مي‌گويند: «او فقط مردي است که مي‌خواهد شما را از آنچه پدرانتان مي‌پرستيدند بازدارد!» و مي‌گويند: «اين جز دروغ بزرگي که (به خدا) بسته شده چيز ديگري نيست!» و کافران هنگامي که حق به سراغشان آمد گفتند: «اين، جز افسوني آشکار نيست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3650, N'ما (قبلا) چيزي از کتابهاي آسماني را به آنان نداده‌ايم که آن را بخوانند (و به اتکاي آن سخنان تو را تکذيب کنند)، و پيش از تو هيچ بيم‌دهنده [= پيامبري ] براي آنان نفرستاديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3651, N'کساني که پيش از آنان بودند (نيز آيات الهي را) تکذيب کردند، در حالي که اينها به يک دهم آنچه به آنان داديم نمي‌رسند! (آري) آنها رسولان مرا تکذيب کردند؛ پس ببين مجازات من (نسبت به آنها) چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3652, N'بگو: «شما را تنها به يک چيز اندرز مي‌دهم، و آن اينکه: دو نفر دو نفر يا يک نفر يک نفر براي خدا قيام کنيد، سپس بينديشيد اين دوست و همنشين شما [= محمد] هيچ گونه جنوني ندارد؛ او فقط بيم‌دهنده شما در برابر عذاب شديد (الهي) است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3653, N'بگو: «هر اجر و پاداشي از شما خواسته‌ام براي خود شماست؛ اجر من تنها بر خداوند است، و او بر همه چيز گواه است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3654, N'بگو: «پروردگار من حق را (بر دل پيامبران خود) مي‌افکند، که او داناي غيبها (و اسرار نهان) است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3655, N'بگو: «حق آمد! و باطل (کاري از آن ساخته نيست و) نمي‌تواند آغازگر چيزي باشد و نه تجديدکننده آن!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3656, N'بگو: «اگر من گمراه شوم، از ناحيه خود گمراه مي‌شوم؛ و اگر هدايت يابم، به وسيله آنچه پروردگارم به من وحي مي‌کند هدايت مي‌يابم؛ او شنواي نزديک است !»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3657, N'اگر ببيني هنگامي که فريادشان بلند مي‌شود اما نمي‌توانند (از عذاب الهي) بگريزند، و آنها را از جاي نزديکي (که حتي انتظارش را ندارند) مي‌گيرند (از درماندگي آنها تعجب خواهي کرد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3658, N'و (در آن حال) مي‌گويند: «به حق ايمان آورديم!»، ولي چگونه مي‌توانند از فاصله دور به آن دسترسي پيدا کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3659, N'آنها پيش از اين (که در نهايت آزادي بودند) به آن کافر شدند و دورا دور، و غائبانه (و بدون آگاهي). نسبت‌هاي ناروا مي‌دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3660, N'(سرانجام) ميان آنها و خواسته‌هايشان جدايي افکنده شد، همان گونه که با پيروان (و هم‌مسلکان) آنها از قبل عمل شد، چرا که آنها در شک و ترديد بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3661, N'ستايش مخصوص خداوندي است آفريننده آسمانها و زمين، که فرشتگان را رسولاني قرار داد داراي بالهاي دوگانه و سه‌گانه و چهارگانه، او هر چه بخواهد در آفرينش مي‌افزايد، و او بر هر چيزي تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3662, N'هر رحمتي را خدا به روي مردم بگشايد، کسي نمي‌تواند جلو آن را بگيرد؛ و هر چه را امساک کند، کسي غير از او قادر به فرستادن آن نيست؛ و او عزيز و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3663, N'اي مردم! به ياد آوريد نعمت خدا را بر شما؛ آيا آفريننده‌اي جز خدا هست که شما را از آسمان و زمين روزي دهد؟! هيچ معبودي جز او نيست؛ با اين حال چگونه به سوي باطل منحرف مي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3664, N'اگر تو را تکذيب کنند (غم مخور، موضوع تازه‌اي نيست) پيامبران پيش از تو نيز تکذيب شدند؛ و همه کارها بسوي خدا بازمي‌گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3665, N'اي مردم! وعده خداوند حق است؛ مبادا زندگي دنيا شما را بفريبد، و مبادا شيطان شما را فريب دهد و به (کرم) خدا مغرور سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3666, N'البته شيطان دشمن شماست، پس او را دشمن بدانيد؛ او فقط حزبش را به اين دعوت مي‌کند که اهل آتش سوزان (جهنم) باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3667, N'کساني که راه کفر پيش گرفتند، براي آنان عذابي سخت است؛ و کساني که ايمان آوردند و کارهاي شايسته انجام دادند، آمرزش و پاداش بزرگ از آن آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3668, N'آيا کسي که عمل بدش براي او آراسته شده و آن را خوب و زيبا مي‌بيند (همانند کسي است که واقع را آنچنان که هست مي‌يابد)؟! خداوند هر کس را بخواهد گمراه مي سازد و هر کس را بخواهد هدايت مي‌کند؛ پس جانت به خاطر شدت تأسف بر آنان از دست نرود؛ خداوند به آنچه انجام مي‌دهند داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3669, N'خداوند کسي است که بادها را فرستاد تا ابرهايي را به حرکت درآورند؛ سپس ما اين ابرها را به سوي زمين مرده‌اي رانديم و به وسيله آن، زمين را پس از مردنش زنده مي‌کنيم؛ رستاخيز نيز همين گونه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3670, N'کسي که خواهان عزت است (بايد از خدا بخواهد چرا که) تمام عزت براي خداست؛ سخنان پاکيزه به سوي او صعود مي‌کند، و عمل صالح را بالا مي‌برد؛ و آنها که نقشه‌هاي بد مي‌کشند، عذاب سختي براي آنهاست و مکر (و تلاش افسادگرانه) آنان نابود مي‌شود (و به جايي نمي‌رسد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3671, N'خداوند شما را از خاکي آفريد، سپس از نطفه‌اي؛ سپس شما را بصورت زوجهايي قرار داد؛ هيچ جنس ماده‌اي باردار نمي‌شود و وضع حمل نمي‌کند مگر به علم او، و هيچ کس عمر طولاني نمي‌کند، يا از عمرش کاسته نمي‌شود مگر اينکه در کتاب (علم خداوند) ثبت است؛ اينها همه براي خداوند آسان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3672, N'دو دريا يکسان نيستند: اين يکي دريايي است که آبش گوارا و شيرين و نوشيدنش خوشگوار است، و آن يکي شور و تلخ و گلوگير؛ (اما) از هر دو گوشتي تازه مي خوريد و وسايل زينتي استخراج کرده مي‌پوشيد؛ و کشتيها را در آن مي‌بيني که آنها را مي‌شکافند (و به سوي مقصد پيش مي‌روند) تا از فضل خداوند بهره‌گيريد، و شايد شکر (نعمتهاي او را) بجا آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3673, N'او شب را در روز داخل مي‌کند و روز را در شب؛ و خورشيد و ماه را مسخر (شما) کرده، هر يک تا سرآمد معيني به حرکت خود ادامه مي‌دهد؛ اين است خداوند، پروردگار شما؛ حاکميت (در سراسر عالم) از آن اوست: و کساني را که جز او مي‌خوانيد (و مي‌پرستيد) حتي به اندازه پوست نازک هسته خرما مالک نيستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3674, N'اگر آنها را بخوانيد صداي شما را نمي‌شنوند، و اگر بشنوند به شما پاسخ نمي‌گويند؛ و روز قيامت، شرک (و پرستش) شما را منکر مي‌شوند، و هيچ کس مانند (خداوند آگاه و) خبير تو را (از حقايق) با خبر نمي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3675, N'اي مردم شما (همگي) نيازمند به خدائيد؛ تنها خداوند است که بي‌نياز و شايسته هر گونه حمد و ستايش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3676, N'اگر بخواهد شما را مي‌برد و خلق جديدي مي‌آورد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3677, N'و اين براي خداوند مشکل نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3678, N'هيچ گنهکاري بار گناه ديگري را بر دوش نمي‌کشد؛ و اگر شخص سنگين‌باري ديگري را براي حمل گناه خود بخواند، چيزي از آن را بر دوش نخواهد گرفت، هر چند از نزديکان او باشد! تو فقط کساني را بيم‌مي‌دهي که از پروردگار خود در پنهاني مي‌ترسند و نماز را برپا مي‌دارند؛ و هر کس پاکي (و تقوا) پيشه کند، نتيجه آن به خودش بازمي‌گردد؛ و بازگشت (همگان) به سوي خداست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3679, N'و نابينا و بينا هرگز برابر نيستند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3680, N'و نه ظلمتها و روشنايي،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3681, N'و نه سايه (آرامبخش) و باد داغ و سوزان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3682, N'و هرگز مردگان و زندگان يکسان نيستند! خداوند پيام خود را به گوش هر کس بخواهد مي‌رساند، و تو نمي‌تواني سخن خود را به گوش آنان که در گور خفته‌اند برساني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3683, N'تو فقط انذارکننده‌اي، (اگر ايمان نياورند نگران نباش، وظيفه‌ات را انجام ده.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3684, N'ما تو را بحق براي بشارت و انذار فرستاديم؛ و هر امتي در گذشته انذارکننده‌اي داشته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3685, N'اگر تو را تکذيب کنند (عجيب نيست)؛ کساني که پيش از آنان بودند (نيز پيامبران خود را) تکذيب کردند؛ آنها با دلايل روشن و کتابهاي پند و موعظه و کتب آسماني روشنگر (مشتمل بر معارف و احکام) به سراغ آنان آمدند (اما کوردلان ايمان نياوردند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3686, N'سپس من کافران را (بعد از اتمام حجت) گرفتم (و سخت مجازات کردم)؛ مجازات من نسبت به آنان چگونه بود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3687, N'آيا نديدي خداوند از آسمان آبي فرو فرستاد که بوسيله آن ميوه‌هايي رنگارنگ (از زمين) خارج ساختيم و از کوه‌ها نيز (به لطف پروردگار) جاده‌هايي آفريده شده سفيد و سرخ و به رنگهاي مختلف و گاه به رنگ کاملا سياه!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3688, N'و از انسانها و جنبندگان و چهارپايان انواعي با رنگهاي مختلف، (آري) حقيقت چنين است: از ميان بندگان خدا، تنها دانشمندان از او مي‌ترسند؛ خداوند عزيز و غفور است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3689, N'کساني که کتاب الهي را تلاوت مي‌کنند و نماز را برپا مي‌دارند و از آنچه به آنان روزي داده‌ايم پنهان و آشکار انفاق مي‌کنند، تجارتي (پرسود و) بي‌زيان و خالي از کساد را اميد دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3690, N'(آنها اين اعمال صالح را انجام مي‌دهند) تا خداوند اجر و پاداش کامل به آنها دهد و از فضلش بر آنها بيفزايد که او آمرزنده و شکرگزار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3691, N'و آنچه از کتاب به تو وحي کرديم حق است و تصديق‌کننده و هماهنگ با کتب پيش از آن؛ خداوند نسبت به بندگانش خبير و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3692, N'سپس اين کتاب (آسماني) را به گروهي از بندگان برگزيده خود به ميراث داديم؛ (اما) از ميان آنها عده‌اي بر خود ستم کردند، و عده‌اي ميانه رو بودند،و گروهي به اذن خدا در نيکيها (از همه) پيشي گرفتند، و اين، همان فضيلت بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3693, N'(پاداش آنان) باغهاي جاويدان بهشت است که در آن وارد مي‌شوند در حالي که با دستبندهايي از طلا و مرواريد آراسته‌اند، و لباسشان در آنجا حرير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3694, N'آنها مي‌گويند: «حمد (و ستايش) براي خداوندي است که اندوه را از ما برطرف ساخت؛ پروردگار ما آمرزنده و سپاسگزار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3695, N'همان کسي که با فضل خود ما را در اين سراي اقامت (جاويدان) جاي داد که نه در آن رنجي به ما مي‌رسد و نه سستي و واماندگي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3696, N'و کساني که کافر شدند، آتش دوزخ براي آنهاست؛ هرگز فرمان مرگشان صادر نمي‌شود تا بميرند، و نه چيزي از عذابش از آنان تخفيف داده مي‌شود؛ اين گونه هر کفران‌کننده‌اي را کيفر مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3697, N'آنها در دوزخ فرياد مي‌زنند: «پروردگارا! ما را خارج کن تا عمل صالحي انجام دهيم غير از آنچه انجام مي‌داديم!» (در پاسخ به آنان گفته مي‌شود:) آيا شما را به اندازه‌اي که هر کس اهل تذکر است در آن متذکر مي‌شود عمر نداديم، و انذارکننده (الهي) به سراغ شما نيامد؟! اکنون بچشيد که براي ظالمان هيچ ياوري نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3698, N'خداوند از غيب آسمانها و زمين آگاه است، و آنچه را در درون دلهاست مي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3699, N'اوست که شما را جانشيناني در زمين قرار داد؛ هر کس کافر شود، کفر او به زيان خودش خواهد بود، و کافران را کفرشان جز خشم و غضب در نزد پروردگار چيزي نمي‌افزايد، و (نيز) کفرشان جز زيان و خسران چيزي بر آنها اضافه نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3700, N'بگو: «اين معبوداني را که جز خدا مي‌خوانيد به من نشان دهيد چه چيزي از زمين را آفريده‌اند، يا اينکه شرکتي در (آفرينش و مالکيت) آسمانها دارند؟! يا به آنان کتابي (آسماني) داده‌ايم و دليلي از آن براي (شرک) خود دارند؟!» نه هيچ يک از اينها نيست، ظالمان فقط وعده‌هاي دروغين به يکديگر مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3701, N'خداوند آسمانها و زمين را نگاه مي‌دارد تا از نظام خود منحرف نشوند؛ و هرگاه منحرف گردند، کسي جز او نمي‌تواند آنها را نگاه دارد، او بردبار و غفور است!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3702, N'آنان با نهايت تأکيد به خدا سوگند خوردند که اگر پيامبري انذارکننده به سراغشان آيد، هدايت يافته‌ترين امتها خواهند بود؛ اما چون پيامبري براي آنان آمد، جز فرار و فاصله‌گرفتن از (حق) چيزي بر آنها نيفزود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3703, N'اينها همه بخاطر استکبار در زمين و نيرنگهاي بدشان بود؛ اما اين نيرنگها تنها دامان صاحبانش را مي‌گيرد؛ آيا آنها چيزي جز سنت پيشينيان و (عذابهاي دردناک آنان) را انتظار دارند؟! هرگز براي سنت خدا تبديل نخواهي يافت، و هرگز براي سنت الهي تغييري نمي‌يابي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3704, N'آيا آنان در زمين نگشتند تا ببينند عاقبت کساني که پيش از آنها بودند چگونه بود؟! همانها که از اينان قويتر (و نيرومندتر) بودند؛ نه چيزي در آسمانها و نه چيزي در زمين از حوزه قدرت او بيرون نخواهد رفت؛ او دانا و تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3705, N'اگر خداوند مردم را به سبب کارهايي که انجام داده‌اند مجازات کند، جنبنده‌اي را بر پشت زمين باقي نخواهد گذاشت! ولي (به لطفش) آنها را تا سرآمد معيني تأخير مي‌اندازد (و مهلت اصلاح مي‌دهد) اما هنگامي که اجل آنان فرا رسد، (خداوند هر کس را به مقتضاي عملش جزا مي‌دهد) او نسبت به بندگانش بيناست (و از اعمال و نيات همه آگاه است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3706, N'يس.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3707, N'سوگند به قرآن حکيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3708, N'که تو قطعا از رسولان (خداوند) هستي،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3709, N'بر راهي راست (قرار داري)؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3710, N'اين قرآني است که از سوي خداوند عزيز و رحيم نازل شده است‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3711, N'تا قومي را بيم دهي که پدرانشان انذار نشدند، از اين رو آنان غافلند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3712, N'فرمان (الهي) درباره بيشتر آنها تحقق يافته، به همين جهت ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3713, N'ما در گردنهاي آنان غلهايي قرار داديم که تا چانه‌ها ادامه دارد و سرهاي آنان را به بالا نگاه داشته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3714, N'و در پيش روي آنان سدي قرار داديم، و در پشت سرشان سدي؛ و چشمانشان را پوشانده‌ايم، لذا نمي‌بينند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3715, N'براي آنان يکسان است: چه انذارشان کني يا نکني، ايمان نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3716, N'تو فقط کسي را انذار مي‌کني که از اين يادآوري (الهي) پيروي کند و از خداوند رحمان در نهان بترسد؛ چنين کسي را به آمرزش و پاداشي پرارزش بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3717, N'به يقين ما مردگان را زنده مي‌کنيم و آنچه را از پيش فرستاده‌اند و تمام آثار آنها را مي نويسيم؛ و همه چيز را در کتاب آشکار کننده‌اي برشمرده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3718, N'و براي آنها، اصحاب قريه (انطاکيه) را مثال بزن هنگامي که فرستادگان خدا به سوي آنان آمدند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3719, N'هنگامي که دو نفر از رسولان را بسوي آنها فرستاديم، اما آنان رسولان (ما) را تکذيب کردند؛ پس براي تقويت آن دو، شخص سومي فرستاديم، آنها همگي گفتند: «ما فرستادگان (خدا) به سوي شما هستيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3720, N'اما آنان (در جواب) گفتند: «شما جز بشري همانند ما نيستيد، و خداوند رحمان چيزي نازل نکرده، شما فقط دروغ مي‌گوييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3721, N'(رسولان ما) گفتند: «پروردگار ما آگاه است که ما قطعا فرستادگان (او) به سوي شما هستيم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3722, N'و بر عهده ما چيزي جز ابلاغ آشکار نيست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3723, N'آنان گفتند: «ما شما را به فال بد گرفته‌ايم (و وجود شما را شوم مي‌دانيم)، و اگر (از اين سخنان) دست برنداريد شما را سنگسار خواهيم کرد و شکنجه دردناکي از ما به شما خواهد رسيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3724, N'(رسولان) گفتند: «شومي شما از خودتان است اگر درست بينديشيد، بلکه شما گروهي اسرافکاريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3725, N'و مردي (با ايمان) از دورترين نقطه شهر با شتاب فرا رسيد، گفت: «اي قوم من! از فرستادگان (خدا) پيروي کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3726, N'از کساني پيروي کنيد که از شما مزدي نمي‌خواهند و خود هدايت يافته‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3727, N'من چرا کسي را پرستش نکنم که مرا آفريده، و همگي به سوي او بازگشت داده مي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3728, N'آيا غير از او معبوداني را انتخاب کنم که اگر خداوند رحمان بخواهد زياني به من برساند، شفاعت آنها کمترين فايده‌اي براي من ندارد و مرا (از مجازات او) نجات نخواهند داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3729, N'اگر چنين کنم، من در گمراهي آشکاري خواهم بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3730, N'(به همين دليل) من به پروردگارتان ايمان آوردم؛ پس به سخنان من گوش فرا دهيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3731, N'(سرانجام او را شهيد کردند و) به او گفته شد: «وارد بهشت شو!» گفت: «اي کاش قوم من مي‌دانستند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3732, N'که پروردگارم مرا آمرزيده و از گرامي‌داشتگان قرار داده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3733, N'و ما بعد از او بر قومش هيچ لشکري از آسمان نفرستاديم، و هرگز سنت ما بر اين نبود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3734, N'(بلکه) فقط يک صيحه آسماني بود، ناگهان همگي خاموش شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3735, N'افسوس بر اين بندگان که هيچ پيامبري براي هدايت آنان نيامد مگر اينکه او را استهزا مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3736, N'آيا نديدند چقدر از اقوام پيش از آنان را (بخاطر گناهانشان) هلاک کرديم، آنها هرگز به سوي ايشان بازنمي‌گردند (و زنده نمي‌شوند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3737, N'و همه آنان (روز قيامت) نزد ما احضار مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3738, N'زمين مرده براي آنها آيتي است، ما آن را زنده کرديم و دانه‌هاي (غذايي) از آن خارج ساختيم که از آن مي‌خورند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3739, N'و در آن باغهايي از نخلها و انگورها قرار داديم و چشمه‌هايي از آن جاري ساختيم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3740, N'تا از ميوه آن بخورند در حالي که دست آنان هيچ دخالتي در ساختن آن نداشته است! آيا شکر خدا را بجا نمي‌آورند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3741, N'منزه است کسي که تمام زوجها را آفريد، از آنچه زمين مي‌روياند، و از خودشان، و از آنچه نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3742, N'شب (نيز) براي آنها نشانه‌اي است (از عظمت خدا)؛ ما روز را از آن برمي‌گيريم، ناگهان تاريکي آنان را فرا مي‌گيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3743, N'و خورشيد (نيز براي آنها آيتي است) که پيوسته بسوي قرارگاهش در حرکت است؛ اين تقدير خداوند قادر و داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3744, N'و براي ماه منزلگاه‌هايي قرار داديم، (و هنگامي که اين منازل را طي کرد) سرانجام بصورت «شاخه کهنه قوسي شکل و زرد رنگ خرما» در مي‌آيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3745, N'نه خورشيد را سزاست که به ماه رسد، و نه شب بر روز پيشي مي‌گيرد؛ و هر کدام در مسير خود شناورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3746, N'نشانه‌اي (ديگر از عظمت پروردگار) براي آنان است که ما فرزندانشان را در کشتيهايي پر (از وسايل و بارها) حمل کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3747, N'و براي آنها مرکبهاي ديگري همانند آن آفريديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3748, N'و اگر بخواهيم آنها را غرق مي‌کنيم بطوري که نه فريادرسي داشته باشند و نه نجات داده شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3749, N'مگر اينکه رحمت ما شامل حال آنان شود، و تا زمان معيني از اين زندگي بهره گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3750, N'و هرگاه به آنها گفته شود: «از آنچه پيش رو و پشت سر شماست [= از عذابهاي الهي‌] بترسيد تا مشمول رحمت الهي شويد!» (اعتنا نمي‌کنند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3751, N'و هيچ آيه‌اي از آيات پروردگارشان براي آنها نمي‌آيد مگر اينکه از آن روي‌گردان مي شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3752, N'و هنگامي که به آنان گفته شود: «از آنچه خدا به شما روزي کرده انفاق کنيد!»، کافران به مؤمنان مي‌گويند: «آيا ما کسي را اطعام کنيم که اگر خدا مي‌خواست او را اطعام مي‌کرد؟! (پس خدا خواسته است او گرسنه باشد)، شما فقط در گمراهي آشکاريد»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3753, N'آنها مي‌گويند: «اگر راست مي‌گوييد، اين وعده (قيامت) کي خواهد بود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3754, N'(اما)جز اين انتظار نمي‌کشند که يک صيحه عظيم (آسماني) آنها را فراگيرد، در حالي که مشغول جدال (در امور دنيا) هستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3755, N'(چنان غافلگير مي‌شوند که حتي) نمي‌توانند وصيتي کنند يا به سوي خانواده خود بازگردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3756, N'(بار ديگر) در «صور» دميده مي‌شود، ناگهان آنها از قبرها، شتابان به سوي (دادگاه) پروردگارشان مي‌روند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3757, N'مي‌گويند: «اي واي بر ما! چه کسي ما را از خوابگاهمان برانگيخت؟! (آري) اين همان است که خداوند رحمان وعده داده، و فرستادگان (او) راست گفتند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3758, N'صيحه واحدي بيش نيست، (فريادي عظيم برمي‌خيزد) ناگهان همگي نزد ما احضار مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3759, N'(و به آنها گفته مي‌شود:) امروز به هيچ کس ذره‌اي ستم نمي‌شود، و جز آنچه را عمل مي‌کرديد جزا داده نمي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3760, N'بهشتيان، امروز به نعمتهاي خدا مشغول و مسرورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3761, N'آنها و همسرانشان در سايه‌هاي (قصرها و درختان بهشتي) بر تختها تکيه زده‌اند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3762, N'براي آنها در بهشت ميوه بسيار لذت‌بخشي است، و هر چه بخواهند در اختيار آنان خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3763, N'بر آنها سلام (و درود الهي) است؛ اين سخني است از سوي پروردگاري مهربان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3764, N'(و به آنها مي‌گويند:) جدا شويد امروز اي گنهکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3765, N'آيا با شما عهد نکردم اي فرزندان آدم که شيطان را نپرستيد، که او براي شما دشمن آشکاري است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3766, N'و اينکه مرا بپرستيد که راه مستقيم اين است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3767, N'او گروه زيادي از شما را گمراه کرد، آيا انديشه نکرديد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3768, N'اين همان دوزخي است که به شما وعده داده مي‌شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3769, N'امروز وارد آن شويد و به خاطر کفري که داشتيد به آتش آن بسوزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3770, N'امروز بر دهانشان مهر مي‌نهيم، و دستهايشان با ما سخن مي‌گويند و پاهايشان کارهايي را که انجام مي‌دادند شهادت مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3771, N'و اگر بخواهيم چشمانشان را محو کنيم؛ سپس براي عبور از راه، مي‌خواهند بر يکديگر پيشي بگيرند، اما چگونه مي‌توانند ببينند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3772, N'و اگر بخواهيم آنها را در جاي خود مسخ مي‌کنيم (و به مجسمه‌هايي بي‌روح مبدل مي‌سازيم) تا نتوانند راه خود را ادامه دهند يا به عقب برگردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3773, N'هر کس را طول عمر دهيم، در آفرينش واژگونه‌اش مي‌کنيم (و به ناتواني کودکي باز مي‌گردانيم)؛ آيا انديشه نمي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3774, N'ما هرگز شعر به او [= پيامبر] نياموختيم، و شايسته او نيست (شاعر باشد)؛ اين (کتاب آسماني) فقط ذکر و قرآن مبين است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3775, N'تا افرادي را که زنده‌اند بيم دهد (و بر کافران اتمام حجت شود) و فرمان عذاب بر آنان مسلم گردد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3776, N'آيا نديدند که از آنچه با قدرت خود به عمل آورده‌ايم چهارپاياني براي آنان آفريديم که آنان مالک آن هستند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3777, N'و آنها را رام ايشان ساختيم، هم مرکب آنان از آن است و هم از آن تغذيه مي‌کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3778, N'و براي آنان بهره‌هاي ديگري در آن (حيوانات) است و نوشيدنيهائي گوارا؛ آيا با اين حال شکرگزاري نمي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3779, N'آنان غير از خدا معبوداني براي خويش برگزيدند به اين اميد که ياري شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3780, N'ولي آنها قادر به ياري ايشان نيستند، و اين (عبادت‌کنندگان در قيامت) لشکري براي آنها خواهند بود که در آتش دوزخ احضار مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3781, N'پس سخنانشان تو را غمگين نسازد، ما آنچه را پنهان مي‌دارند و آنچه را آشکار مي‌کنند مي‌دانيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3782, N'آيا انسان نمي‌داند که ما او را از نطفه‌اي بي‌ارزش آفريديم؟! و او (چنان صاحب قدرت و شعور و نطق شد که) به مخاصمه آشکار (با ما) برخاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3783, N'و براي ما مثالي زد و آفرينش خود را فراموش کرد و گفت: «چه کسي اين استخوانها را زنده مي‌کند در حالي که پوسيده است؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3784, N'بگو: «همان کسي آن را زنده مي‌کند که نخستين بار آن را آفريد؛ و او به هر مخلوقي داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3785, N'همان کسي که براي شما از درخت سبز، آتش آفريد و شما بوسيله آن، آتش مي‌افروزيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3786, N'آيا کسي که آسمانها و زمين را آفريد، نمي‌تواند همانند آنان [= انسانهاي خاک شده‌] را بيافريند؟! آري (مي‌تواند)، و او آفريدگار داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3787, N'فرمان او چنين است که هرگاه چيزي را اراده کند، تنها به آن مي‌گويد: «موجود باش!»، آن نيز بي‌درنگ موجود مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3788, N'پس منزه است خداوندي که مالکيت و حاکميت همه چيز در دست اوست؛ و شما را به سوي او بازمي‌گردانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3789, N'سوگند به (فرشتگان) صف‌کشيده (و منظم)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3790, N'و به نهي‌کنندگان و (بازدارندگان)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3791, N'و تلاوت‌کنندگان پياپي آيات الهي...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3792, N'که معبود شما يگانه است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3793, N'پروردگار آسمانها و زمين و آنچه ميان آنهاست، و پروردگار مشرقها!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3794, N'ما آسمان نزديک [= پايين‌] را با ستارگان آراستيم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3795, N'تا آن را از هر شيطان خبيثي حفظ کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3796, N'آنها نمي‌توانند به (سخنان) فرشتگان عالم بالا گوش فرادهند، (و هرگاه چنين کنند) از هر سو هدف قرارمي‌گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3797, N'آنها به شدت به عقب رانده مي‌شوند؛ و براي آنان مجازاتي دائم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3798, N'مگر آنها که در لحظه‌اي کوتاه براي استراق سمع به آسمان نزديک شوند، که «شهاب ثاقب» آنها را تعقيب مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3799, N'از آنان بپرس: «آيا آفرينش (و معاد) آنان سخت‌تر است يا آفرينش فرشتگان (و آسمانها و زمين)؟! ما آنان را از گل چسبنده‌اي آفريديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3800, N'تو از انکارشان تعجب مي‌کني، ولي آنها مسخره مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3801, N'و هنگامي که به آنان تذکر داده شود، هرگز متذکر نمي‌شوند!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3802, N'و هنگامي که معجزه‌اي را ببينند، ديگران را نيز به استهزا دعوت مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3803, N'و مي‌گويند: «اين فقط سحري آشکار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3804, N'آيا هنگامي که ما مرديم و به خاک و استخوان مبدل شديم، بار ديگر برانگيخته خواهيم شد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3805, N'يا پدران نخستين ما (بازمي‌گردند)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3806, N'بگو: «آري، همه شما زنده مي‌شويد در حالي که خوار و کوچک خواهيد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3807, N'تنها يک صيحه عظيم واقع مي‌شود، ناگهان همه (از قبرها برمي‌خيزند و) نگاه مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3808, N'و مي‌گويند: «اي واي بر ما، اين روز جزاست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3809, N'(آري) اين همان روز جدايي (حق از باطل) است که شما آن را تکذيب مي‌کرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3810, N'(در اين هنگام به فرشتگان دستور داده مي‌شود:) ظالمان و همرديفانشان و آنچه را مي‌پرستيدند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3811, N'(آري آنچه را) جز خدا مي‌پرستيدند جمع کنيد و بسوي راه دوزخ هدايتشان کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3812, N'آنها را نگهداريد که بايد بازپرسي شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3813, N'شما را چه شده که از هم ياري نمي‌طلبيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3814, N'ولي آنان در آن روز تسليم قدرت خداوندند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3815, N'(و در اين حال) رو به يکديگر کرده و از هم مي‌پرسند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3816, N'گروهي (مي‌گويند: «شما رهبران گمراهي بوديد که به ظاهر) از طريق خيرخواهي و نيکي وارد شديد اما جز فريب چيزي در کارتان نبود)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3817, N'(آنها در جواب) مي‌گويند: «شما خودتان اهل ايمان نبوديد (تقصير ما چيست)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3818, N'ما هيچ گونه سلطه‌اي بر شما نداشتيم، بلکه شما خود قومي طغيانگر بوديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3819, N'اکنون فرمان پروردگارمان بر همه ما مسلم شده، و همگي از عذاب او مي‌چشيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3820, N'ما شما را گمراه کرديم، همان گونه که خود گمراه بوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3821, N'(آري) همه آنها [= پيشوايان و پيروان گمراه‌] در آن روز در عذاب الهي مشترکند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3822, N'ما اين گونه با مجرمان رفتار مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3823, N'چرا که وقتي به آنها گفته مي‌شد: «معبودي جز خدا وجود ندارد»، تکبر و سرکشي مي‌کردند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3824, N'و پيوسته مي‌گفتند: «آيا ما معبودان خود را بخاطر شاعري ديوانه رها کنيم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3825, N'چنين نيست، او حق را آورده و پيامبران پيشين را تصديق کرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3826, N'اما شما (مستکبران کوردل) بطور مسلم عذاب دردناک (الهي) را خواهيد چشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3827, N'و جز به آنچه انجام مي‌داديد کيفر داده نمي‌شويد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3828, N'جز بندگان مخلص خدا (که از اين کيفرها برکنارند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3829, N'براي آنان [= بندگان مخلص‌] روزي معين و ويژه‌اي است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3830, N'ميوه‌ها(ي گوناگون پر ارزش)، و آنها گرامي داشته مي‌شوند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3831, N'در باغهاي پر نعمت بهشت؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3832, N'در حالي که بر تختها رو به روي يکديگر تکيه زده‌اند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3833, N'و گرداگردشان قدحهاي لبريز از شراب طهور را مي‌گردانند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3834, N'شرابي سفيد و درخشنده، و لذتبخش براي نوشندگان؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3835, N'شرابي که نه در آن مايه تباهي عقل است و نه از آن مست مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3836, N'و نزد آنها همسراني زيبا چشم است که جز به شوهران خود عشق نمي‌ورزند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3837, N'گويي از (لطافت و سفيدي) همچون تخم‌مرغهايي هستند که (در زير بال و پر مرغ) پنهان مانده (و دست انساني هرگز آن را لمس نکرده است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3838, N'(در حالي که آنها غرق گفتگو هستند) بعضي رو به بعضي ديگر کرده مي‌پرسند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3839, N'کسي از آنها مي‌گويد: «من همنشيني داشتم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3840, N'که پيوسته مي‌گفت: آيا (به راستي) تو اين سخن را باور کرده‌اي...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3841, N'که وقتي ما مرديم و به خاک و استخوان مبدل شديم، (بار ديگر) زنده مي‌شويم و جزا داده خواهيم شد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3842, N'(سپس) مي‌گويد: «آيا شما مي‌توانيد از او خبري بگيريد؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3843, N'اينجاست که نگاهي مي‌کند، ناگهان او را در ميان دوزخ مي‌بيند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3844, N'مي‌گويد: «به خدا سوگند نزديک بود مرا (نيز) به هلاکت بکشاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3845, N'و اگر نعمت پروردگارم نبود، من نيز از احضارشدگان (در دوزخ) بودم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3846, N'(سپس به ياران خود مي‌گويد: اي دوستان!) آيا ما هرگز نمي‌ميريم (و در بهشت جاودانه خواهيم بود)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3847, N'و جز همان مرگ اول، مرگي به سراغ ما نخواهد آمد، و ما هرگز عذاب نخواهيم شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3848, N'راستي اين همان پيروزي بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3849, N'آري، براي مثل اين، بايد عمل‌کنندگان عمل کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3850, N'آيا اين (نعمتهاي جاويدان بهشتي) بهتر است يا درخت (نفرت‌انگيز) زقوم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3851, N'ما آن را مايه درد و رنج ظالمان قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3852, N'آن درختي است که از قعر جهنم مي‌رويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3853, N'شکوفه آن مانند سرهاي شياطين است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3854, N'آنها [= مجرمان‌] از آن مي‌خورند و شکمها را از آن پر مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3855, N'سپس روي آن آب داغ متعفني مي‌نوشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3856, N'سپس بازگشت آنها به سوي جهنم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3857, N'چرا که آنها پدران خود را گمراه يافتند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3858, N'با اين حال به سرعت بدنبال آنان کشانده مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3859, N'و قبل از آنها بيشتر پيشينيان (نيز) گمراه شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3860, N'ما در ميان آنها انذارکنندگاني فرستاديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3861, N'ولي بنگر عاقبت انذارشوندگان چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3862, N'مگر بندگان مخلص خدا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3863, N'و نوح، ما را خواند (و ما دعاي او را اجابت کرديم)؛ و چه خوب اجابت کننده‌اي هستيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3864, N'و او و خاندانش را از اندوه بزرگ رهايي بخشيديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3865, N'و فرزندانش را همان بازماندگان (روي زمين) قرار داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3866, N'و نام نيک او را در ميان امتهاي بعد باقي نهاديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3867, N'سلام بر نوح در ميان جهانيان باد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3868, N'ما اين گونه نيکوکاران را پاداش مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3869, N'چرا که او از بندگان باايمان ما بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3870, N'سپس ديگران [= دشمنان او] را غرق کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3871, N'و از پيروان او ابراهيم بود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3872, N'(به خاطر بياور) هنگامي را که با قلب سليم به پيشگاه پروردگارش آمد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3873, N'هنگامي که به پدر و قومش گفت: «اينها چيست که مي‌پرستيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3874, N'آيا غير از خدا به سراغ اين معبودان دروغين مي‌رويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3875, N'شما درباره پروردگار عالميان چه گمان مي‌بريد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3876, N'(سپس) نگاهي به ستارگان افکند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3877, N'و گفت: «من بيمارم (و با شما به مراسم جشن نمي‌آيم)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3878, N'آنها از او روي برتافته و به او پشت کردند (و بسرعت دور شدند.)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3879, N'(او وارد بتخانه شد) مخفيانه نگاهي به معبودانشان کرد و از روي تمسخر گفت: «چرا (از اين غذاها) نمي‌خوريد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3880, N'(اصلا) چرا سخن نمي‌گوييد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3881, N'سپس بسوي آنها رفت و ضربه‌اي محکم با دست راست بر پيکر آنها فرود آورد (و جز بت بزرگ، همه را درهم شکست).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3882, N'آنها با سرعت به او روي آوردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3883, N'گفت: «آيا چيزي را مي‌پرستيد که با دست خود مي‌تراشيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3884, N'با اينکه خداوند هم شما را آفريده و هم بتهايي که مي‌سازيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3885, N'(بت‌پرستان) گفتند: «بناي مرتفعي براي او بسازيد و او را در جهنمي از آتش بيفکنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3886, N'آنها طرحي براي نابودي ابراهيم ريخته بودند، ولي ما آنان را پست و مغلوب ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3887, N'(او از اين مهلکه بسلامت بيرون آمد) و گفت: «من به سوي پروردگارم مي‌روم، او مرا هدايت خواهد کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3888, N'پروردگارا! به من از صالحان [= فرزندان صالح‌] ببخش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3889, N'ما او [= ابراهيم‌] را به نوجواني بردبار و صبور بشارت داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3890, N'هنگامي که با او به مقام سعي و کوشش رسيد، گفت: «پسرم! من در خواب ديدم که تو را ذبح مي‌کنم، نظر تو چيست؟» گفت «پدرم! هر چه دستور داري اجرا کن، به خواست خدا مرا از صابران خواهي يافت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3891, N'هنگامي که هر دو تسليم شدند و ابراهيم جبين او را بر خاک نهاد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3892, N'او را ندا داديم که: «اي ابراهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3893, N'آن رؤيا را تحقق بخشيدي (و به مأموريت خود عمل کردي)!» ما اين گونه، نيکوکاران را جزا مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3894, N'اين مسلما همان امتحان آشکار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3895, N'ما ذبح عظيمي را فداي او کرديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3896, N'و نام نيک او را در امتهاي بعد باقي نهاديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3897, N'سلام بر ابراهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3898, N'اين گونه نيکوکاران را پاداش مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3899, N'او از بندگان باايمان ما است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3900, N'ما او را به اسحاق -پيامبري از شايستگان- بشارت داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3901, N'ما به او و اسحاق برکت داديم؛ و از دودمان آن دو، افرادي بودند نيکوکار و افرادي آشکارا به خود ستم کردند!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3902, N'ما به موسي و هارون نعمت بخشيديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3903, N'و آن دو و قومشان را از اندوه بزرگ نجات داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3904, N'و آنها را ياري کرديم تا بر دشمنان خود پيروز شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3905, N'ما به آن دو، کتاب روشنگر داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3906, N'و آن دو را به راه راست هدايت نموديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3907, N'و نام نيکشان را در اقوام بعد باقي گذارديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3908, N'سلام بر موسي و هارون!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3909, N'ما اين گونه نيکوکاران را پاداش مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3910, N'آن دو از بندگان مؤمن ما بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3911, N'و الياس از رسولان (ما) بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3912, N'به خاطر بياور هنگامي را که به قومش گفت: «آيا تقوا پيشه نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3913, N'آيا بت «بعل» را مي‌خوانيد و بهترين آفريدگارها را رها مي‌سازيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3914, N'خدايي که پروردگار شما و پروردگار نياکان شماست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3915, N'اما آنها او را تکذيب کردند؛ ولي به يقين همگي (در دادگاه عدل الهي) احضار مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3916, N'مگر بندگان مخلص خدا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3917, N'ما نام نيک او را در ميان امتهاي بعد باقي گذارديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3918, N'سلام بر الياسين!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3919, N'ما اين گونه نيکوکاران را پاداش مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3920, N'او از بندگان مؤمن ما است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3921, N'و لوط از رسولان (ما) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3922, N'و به خاطر بياور زماني را که او و خاندانش را همگي نجات داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3923, N'مگر پيرزني که از بازماندگان بود (و به سرنوشت آنان گرفتار شد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3924, N'سپس بقيه را نابود کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3925, N'و شما پيوسته صبحگاهان از کنار (ويرانه‌هاي شهرهاي) آنها مي‌گذريد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3926, N'و (همچنين) شبانگاه؛ آيا نمي‌انديشيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3927, N'و يونس از رسولان (ما) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3928, N'به خاطر بياور زماني را که به سوي کشتي پر (از جمعيت و بار) فرار کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3929, N'و با آنها قرعه افکند، (و قرعه به نام او افتاد و) مغلوب شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3930, N'(او را به دريا افکندند) و ماهي عظيمي او را بلعيد، در حالي که مستحق سرزنش بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3931, N'و اگر او از تسبيح‌کنندگان نبود...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3932, N'تا روز قيامت در شکم ماهي مي‌ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3933, N'(به هر حال ما او را رهايي بخشيديم و) او را در يک سرزمين خشک خالي از گياه افکنديم در حالي که بيمار بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3934, N'و بوته کدوئي بر او رويانديم (تا در سايه برگهاي پهن و مرطوبش آرامش يابد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3935, N'و او را به سوي جمعيت يکصد هزار نفري -يا بيشتر- فرستاديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3936, N'آنها ايمان آوردند، از اين رو تا مدت معلومي آنان را از مواهب زندگي بهره‌مند ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3937, N'از آنان بپرس: آيا پروردگارت دختراني دارد و پسران از آن آنهاست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3938, N'آيا ما فرشتگان را مؤنث آفريديم و آنها ناظر بودند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3939, N'دانيد آنها با اين تهمت بزرگشان مي‌گويند:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3940, N'«خداوند فرزند آورده!» ولي آنها به يقين دروغ مي‌گويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3941, N'آيا دختران را بر پسران ترجيح داده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3942, N'شما را چه شده است؟! چگونه حکم مي‌کنيد؟! (هيچ مي‌فهميد چه مي‌گوييد؟!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3943, N'آيا متذکر نمي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3944, N'يا شما دليل روشني در اين باره داريد؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3945, N'کتابتان را بياوريد اگر راست مي‌گوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3946, N'آنها [= مشرکان‌] ميان او [= خداوند] و جن، (خويشاوندي و) نسبتي قائل شدند؛ در حالي که جنيان بخوبي مي‌دانند که اين بت‌پرستان در دادگاه الهي احضار مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3947, N'منزه است خداوند از آنچه توصيف مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3948, N'مگر بندگان مخلص خدا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3949, N'شما و آنچه را پرستش مي‌کنيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3950, N'هرگز نمي‌توانيد کسي را (با آن) فريب دهيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3951, N'مگر آنها که در آتش دوزخ وارد مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3952, N'و هيچ يک از ما نيست جز آنکه مقام معلومي دارد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3953, N'و ما همگي (براي اطاعت فرمان خداوند) به صف ايستاده‌ايم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3954, N'و ما همه تسبيح‌گوي او هستيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3955, N'آنها پيوسته مي‌گفتند:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3956, N'«اگر يکي از کتابهاي پيشينيان نزد ما بود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3957, N'به يقين، ما بندگان مخلص خدا بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3958, N'(اما هنگامي که اين کتاب بزرگ آسماني بر آنها نازل شد،) به آن کافر شدند؛ ولي بزودي (نتيجه کار خود را) خواهند دانست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3959, N'وعده قطعي ما براي بندگان فرستاده ما از پيش مسلم شده...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3960, N'که آنان ياري شدگانند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3961, N'و لشکر ما پيروزند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3962, N'از آنها [= کافران‌] روي بگردان تا زمان معيني (که فرمان جهاد فرارسد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3963, N'و وضع آنها را بنگر (چه بي‌محتواست) اما بزودي (نتيجه اعمال خود را) مي‌بينند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3964, N'آيا آنها براي عذاب ما شتاب مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3965, N'اما هنگامي که عذاب ما در آستانه خانه‌هايشان فرود آيد، انذارشدگان صبحگاه بدي خواهند داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3966, N'از آنان روي بگردان تا زمان معيني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3967, N'و وضع کارشان را ببين؛ آنها نيز به زودي (نتيجه اعمال خود را) مي‌بينند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3968, N'منزه است پروردگار تو، پروردگار عزت (و قدرت) از آنچه آنان توصيف مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3969, N'و سلام بر رسولان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3970, N'حمد و ستايش مخصوص خداوندي است که پروردگار جهانيان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3971, N'ص، سوگند به قرآني که داراي ذکر است (که اين کتاب، معجزه الهي است).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3972, N'ولي کافران گرفتار غرور اختلافند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3973, N'چه بسيار اقوامي را که پيش از آنها هلاک کرديم؛ و به هنگام نزول عذاب فرياد مي‌زدند (و کمک مي‌خواستند) ولي وقت نجات گذشته بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3974, N'آنها تعجب کردند که پيامبر بيم‌دهنده‌اي از ميان آنان به سويشان آمده؛ و کافران گفتند: اين ساحر بسيار دروغگويي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3975, N'آيا او بجاي اينهمه خدايان، خداي واحدي قرار داده؟! اين براستي چيز عجيبي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3976, N'سرکردگان آنها بيرون آمدند و گفتند: «برويد و خدايانتان را محکم بچسبيد، اين چيزي است که خواسته‌اند (شما را گمراه کنند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3977, N'ما هرگز چنين چيزي در آيين ديگري نشنيده‌ايم؛ اين تنها يک آئين ساختگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3978, N'آيا از ميان همه ما، قرآن تنها بر او [= محمد] نازل شده؟!» آنها در حقيقت در اصل وحي من ترديد دارند، بلکه آنان هنوز عذاب الهي را نچشيده‌اند (که اين چنين گستاخانه سخن مي‌گويند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3979, N'مگر خزاين رحمت پروردگار توانا و بخشنده‌ات نزد آنهاست (تا به هر کس ميل دارند بدهند)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3980, N'يا اينکه مالکيت و حاکميت آسمانها و زمين و آنچه ميان اين دو است از آن آنهاست؟! (اگر چنين است) با هر وسيله ممکن به آسمانها بروند (و جلو نزول وحي را بر قلب پاک محمد بگيرند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3981, N'(آري) اينها لشکر کوچک شکست‌خورده‌اي از احزابند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3982, N'پيش از آنان قوم نوح و عاد و فرعون صاحب قدرت (پيامبران ما را) تکذيب کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3983, N'و (نيز) قوم ثمود و لوط و اصحاب الأيکه [= قوم شعيب‌]، اينها احزابي بودند (که به تکذيب پيامبران برخاستند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3984, N'هر يک (از اين گروه‌ها) رسولان را تکذيب کردند، و عذاب الهي درباره آنان تحقق يافت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3985, N'اينها (با اين اعمالشان) جز يک صيحه آسماني را انتظار نمي‌کشند که هيچ مهلت و بازگشتي براي آن وجود ندارد (و همگي را نابود مي‌سازد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3986, N'آنها (از روي خيره‌سري) گفتند: «پروردگارا! بهره ما را از عذاب هر چه زودتر قبل از روز حساب به ما ده!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3987, N'در برابر آنچه مي‌گويند شکيبا باش، و به خاطر بياور بنده ما داوود صاحب قدرت را، که او بسيار توبه‌کننده بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3988, N'ما کوه‌ها را مسخر او ساختيم که هر شامگاه و صبحگاه با او تسبيح مي‌گفتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3989, N'پرندگان را نيز دسته جمعي مسخر او کرديم (تا همراه او تسبيح خدا گويند)؛و همه اينها بازگشت‌کننده به سوي او بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3990, N'و حکومت او را استحکام بخشيديم، (هم) دانش به او داديم و (هم) داوري عادلانه!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3991, N'آيا داستان شاکيان هنگامي که از محراب (داوود) بالا رفتند به تو رسيده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3992, N'در آن هنگام که (بي‌مقدمه) بر او وارد شدند و او از ديدن آنها وحشت کرد؛ گفتند: «نترس، دو نفر شاکي هستيم که يکي از ما بر ديگري ستم کرده؛ اکنون در ميان ما بحق داوري کن و ستم روا مدار و ما را به راه راست هدايت کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3993, N'اين برادر من است؛ و او نود و نه ميش دارد و من يکي بيش ندارم اما او اصرار مي‌کند که: اين يکي را هم به من واگذار؛ و در سخن بر من غلبه کرده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3994, N'(داوود) گفت: «مسلما او با درخواست يک ميش تو براي افزودن آن به ميشهايش، بر تو ستم نموده؛ و بسياري از شريکان (و دوستان) به يکديگر ستم مي‌کنند، مگر کساني که ايمان آورده و اعمال صالح انجام داده‌اند؛ اما عده آنان کم است!» داوود دانست که ما او را (با اين ماجرا) آزموده‌ايم، از اين رو از پروردگارش طلب آمرزش نمود و به سجده افتاد و توبه کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3995, N'ما اين عمل را بر او بخشيديم؛ و او نزد ما داراي مقامي والا و سرانجامي نيکوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3996, N'اي داوود! ما تو را خليفه و (نماينده خود) در زمين قرار داديم؛ پس در ميان مردم بحق داوري کن، و از هواي نفس پيروي مکن که تو را از راه خدا منحرف سازد؛ کساني که از راه خدا گمراه شوند، عذاب شديدي بخاطر فراموش کردن روز حساب دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3997, N'ما آسمان و زمين و آنچه را ميان آنهاست بيهوده نيافريديم؛ اين گمان کافران است؛ واي بر کافران از آتش (دوزخ)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3998, N'آيا کساني را که ايمان آورده و کارهاي شايسته انجام داده‌اند همچون مفسدان در زمين قرار مي‌دهيم، يا پرهيزگاران را همچون فاجران؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (3999, N'اين کتابي است پربرکت که بر تو نازل کرده‌ايم تا در آيات آن تدبر کنند و خردمندان متذکر شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4000, N'ما سليمان را به داوود بخشيديم؛ چه بنده خوبي! زيرا همواره به سوي خدا بازگشت مي‌کرد (و به ياد او بود)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4001, N'به خاطر بياور هنگامي را که عصرگاهان اسبان چابک تندرو را بر او عرضه داشتند،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4002, N'گفت: «من اين اسبان را بخاطر پروردگارم دوست دارم (و مي‌خواهم از آنها در جهاد استفاده کنم»، او همچنان به آنها نگاه مي‌کرد) تا از ديدگانش پنهان شدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4003, N'(آنها به قدري جالب بودند که گفت:) بار ديگر آنها را نزد من بازگردانيد! و دست به ساقها و گردنهاي آنها کشيد (و آنها را نوازش داد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4004, N'ما سليمان را آزموديم و بر تخت او جسدي افکنديم؛ سپس او به درگاه خداوند توبه کرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4005, N'گفت: پروردگارا! مرا ببخش و حکومتي به من عطا کن که بعد از من سزاوار هيچ کس نباشد، که تو بسيار بخشنده‌اي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4006, N'پس ما باد را مسخر او ساختيم تا به فرمانش بنرمي حرکت کند و به هر جا او مي‌خواهد برود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4007, N'و شياطين را مسخر او کرديم، هر بنا و غواصي از آنها را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4008, N'و گروه ديگري (از شياطين) را در غل و زنجير (تحت سلطه او) قرار داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4009, N'(و به او گفتيم:) اين عطاي ما است، به هر کس مي‌خواهي (و صلاح مي‌بيني) ببخش، و از هر کس مي‌خواهي امساک کن، و حسابي بر تو نيست (تو امين هستي)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4010, N'و براي او [= سليمان‌] نزد ما مقامي ارجمند و سرانجامي نيکوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4011, N'و به خاطر بياور بنده ما ايوب را، هنگامي که پروردگارش را خواند (و گفت: پروردگارا!) شيطان مرا به رنج و عذاب افکنده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4012, N'(به او گفتيم:) پاي خود را بر زمين بکوب! اين چشمه آبي خنک براي شستشو و نوشيدن است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4013, N'و خانواده‌اش را به او بخشيديم، و همانند آنها را بر آنان افزوديم، تا رحمتي از سوي ما باشد و تذکري براي انديشمندان.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4014, N'(و به او گفتيم:) بسته‌اي از ساقه‌هاي گندم (يا مانند آن) را برگير و با آن (همسرت را) بزن و سوگند خود را مشکن! ما او را شکيبا يافتيم؛ چه بنده خوبي که بسيار بازگشت‌کننده (به سوي خدا) بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4015, N'و به خاطر بياور بندگان ما ابراهيم و اسحاق و يعقوب را، صاحبان دستها(ي نيرومند) و چشمها(ي بينا)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4016, N'ما آنها را با خلوص ويژه‌اي خالص کرديم، و آن يادآوري سراي آخرت بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4017, N'و آنها نزد ما از برگزيدگان و نيکانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4018, N'و به خاطر بياور «اسماعيل» و «اليسع» و «ذا الکفل» را که همه از نيکان بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4019, N'اين يک يادآوري است، و براي پرهيزکاران فرجام نيکويي است:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4020, N'باغهاي جاويدان بهشتي که درهايش به روي آنان گشوده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4021, N'در حالي که در آن بر تختها تکيه کرده‌اند و ميوه‌هاي بسيار و نوشيدنيها در اختيار آنان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4022, N'و نزد آنان همسراني است که تنها چشم به شوهرانشان دوخته‌اند، و همسن و سالند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4023, N'اين همان است که براي روز حساب به شما وعده داده مي‌شود (وعده‌اي تخلف ناپذير)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4024, N'اين روزي ما است که هرگز آن را پاياني نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4025, N'اين (پاداش پرهيزگاران است)، و براي طغيانگران بدترين محل بازگشت است:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4026, N'دوزخ، که در آن وارد مي‌شوند؛ و چه بستر بدي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4027, N'اين نوشابه «حميم» و «غساق» است [= دو مايع سوزان و تيره رنگ‌] که بايد از آن بچشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4028, N'و جز اينها کيفرهاي ديگري همانند آن دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4029, N'(به آنان گفته مي‌شود:) اين گروهي است که همراه شما وارد دوزخ مي‌شوند (اينها همان سران گمراهيند)؛ خوشامد بر آنها مباد، همگي در آتش خواهند سوخت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4030, N'آنها (به رؤساي خود) مي‌گويند: «بلکه خوشامد بر شما مباد که اين عذاب را شما براي ما فراهم ساختيد! چه بد قرارگاهي است اينجا!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4031, N'(سپس) مي‌گويند: «پروردگارا! هر کس اين عذاب را براي ما فراهم ساخته، عذابي مضاعف در آتش بر او بيفزا!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4032, N'آنها مي‌گويند: «چرا مرداني را که ما از اشرار مي‌شمرديم (در اينجا، در آتش دوزخ) نمي‌بينيم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4033, N'آيا ما آنان را به مسخره گرفتيم يا (به اندازه‌اي حقيرند که) چشمها آنها را نمي‌بيند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4034, N'اين يک واقعيت است گفتگوهاي خصمانه دوزخيان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4035, N'بگو: «من تنها يک بيم‌دهنده‌ام؛ و هيچ معبودي جز خداوند يگانه قهار نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4036, N'پروردگار آسمانها و زمين و آنچه ميان آن دو است، پروردگار عزيز و غفار!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4037, N'بگو: «اين خبري بزرگ است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4038, N'که شما از آن روي‌گردانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4039, N'من از ملأ اعلي (و فرشتگان عالم بالا) به هنگامي که (درباره آفرينش آدم) مخاصمه مي‌کردند خبر ندارم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4040, N'تنها چيزي که به من وحي مي‌شود اين است که من انذارکننده آشکاري هستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4041, N'و به خاطر بياور هنگامي را که پروردگارت به فرشتگان گفت: «من بشري را از گل مي‌آفرينم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4042, N'هنگامي که آن را نظام بخشيدم و از روح خود در آن دميدم، براي او به سجده افتيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4043, N'در آن هنگام همه فرشتگان سجده کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4044, N'جز ابليس که تکبر ورزيد و از کافران بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4045, N'گفت: «اي ابليس! چه چيز مانع تو شد که بر مخلوقي که با قدرت خود او را آفريدم سجده کني؟! آيا تکبر کردي يا از برترينها بودي؟! (برتر از اينکه فرمان سجود به تو داده شود!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4046, N'گفت: «من از او بهترم؛ مرا از آتش آفريده‌اي و او را از گل!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4047, N'فرمود: «از آسمانها (و صفوف ملائکه) خارج شو، که تو رانده درگاه مني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4048, N'و مسلما لعنت من بر تو تا روز قيامت خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4049, N'گفت: «پروردگارا! مرا تا روزي که انسانها برانگيخته مي‌شوند مهلت ده!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4050, N'فرمود: «تو از مهلت داده‌شدگاني،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4051, N'ولي تا روز و زمان معين!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4052, N'گفت: «به عزتت سوگند، همه آنان را گمراه خواهم کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4053, N'مگر بندگان خالص تو، از ميان آنها!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4054, N'فرمود: «به حق سوگند، و حق مي‌گويم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4055, N'که جهنم را از تو و هر کدام از آنان که از تو پيروي کند، پر خواهم کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4056, N'(اي پيامبر!) بگو: «من براي دعوت نبوت هيچ پاداشي از شما نمي‌طلبم، و من از متکلفين نيستم! (سخنانم روشن و همراه با دليل است!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4057, N'اين (قرآن) تذکري براي همه جهانيان است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4058, N'و خبر آن را بعد از مدتي مي‌شنويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4059, N'اين کتابي است که از سوي خداوند عزيز و حکيم نازل شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4060, N'ما اين کتاب را بحق بر تو نازل کرديم؛ پس خدا را پرستش کن و دين خود را براي او خالص گردان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4061, N'آگاه باشيد که دين خالص از آن خداست، و آنها که غير خدا را اولياي خود قرار دادند و دليلشان اين بود که: «اينها را نمي‌پرستيم مگر بخاطر اينکه ما را به خداوند نزديک کنند»، خداوند روز قيامت ميان آنان در آنچه اختلاف داشتند داوري مي‌کند؛ خداوند آن کس را که دروغگو و کفران‌کننده است هرگز هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4062, N'اگر (بفرض محال) خدا مي‌خواست فرزندي انتخاب کند، از ميان مخلوقاتش آنچه را مي‌خواست برميگزيد؛ منزه است (از اينکه فرزندي داشته باشد)! او خداوند يکتاي پيروز است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4063, N'آسمانها و زمين را بحق آفريد؛ شب را بر روز مي‌پيچد و روز را بر شب؛ و خورشيد و ماه را مسخر فرمان خويش قرار داد؛ هر کدام تا سرآمد معيني به حرکت خود ادامه مي‌دهند؛ آگاه باشيد که او قادر و آمرزنده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4064, N'او شما را از يک نفس آفريد، و همسرش را از (باقيمانده گل) او خلق کرد؛ و براي شما هشت زوج از چهارپايان ايجاد کرد؛ او شما را در شکم مادرانتان آفرينشي بعد از آفرينش ديگر، در ميان تاريکيهاي سه گانه، مي‌بخشد! اين است خداوند، پروردگار شما که حکومت (عالم هستي) از آن اوست؛ هيچ معبودي جز او نيست؛ پس چگونه از راه حق منحرف مي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4065, N'اگر کفران کنيد، خداوند از شما بي‌نياز است و هرگز کفران را براي بندگانش نمي پسندد؛ و اگر شکر او را بجا آوريد آن را براي شما مي‌پسندد! و هيچ گنهکاري گناه ديگري را بر دوش نمي‌کشد! سپس بازگشت همه شما به سوي پروردگارتان است، و شما را از آنچه انجام مي‌داديد آگاه مي‌سازد؛ چرا که او به آنچه در سينه‌هاست آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4066, N'هنگامي که انسان را زياني رسد، پروردگار خود را مي‌خواند و بسوي او باز مي گردد؛ اما هنگامي که نعمتي از خود به او عطا کند، آنچه را به خاطر آن قبلا خدا را مي خواند از ياد مي‌برد و براي خداوند همتاياني قرارمي‌دهد تا مردم را از راه او منحرف سازد؛ بگو: «چند روزي از کفرت بهره‌گير که از دوزخياني!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4067, N'(آيا چنين کسي با ارزش است) يا کسي که در ساعات شب به عبادت مشغول است و در حال سجده و قيام، از عذاب آخرت مي‌ترسد و به رحمت پروردگارش اميدوار است؟! بگو: «آيا کساني که مي‌دانند با کساني که نمي‌دانند يکسانند؟! تنها خردمندان متذکر مي‌شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4068, N'بگو: «اي بندگان من که ايمان آورده‌ايد! از (مخالفت) پروردگارتان بپرهيزيد! براي کساني که در اين دنيا نيکي کرده‌اند پاداش نيکي است! و زمين خدا وسيع است، (اگر تحت فشار سران کفر بوديد مهاجرت کنيد) که صابران اجر و پاداش خود را بي‌حساب دريافت مي‌دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4069, N'بگو: «من مأمورم که خدا را پرستش کنم در حالي که دينم را براي او خالص کرده باشم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4070, N'و مأمورم که نخستين مسلمان باشم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4071, N'بگو: «من اگر نافرماني پروردگارم کنم، از عذاب روز بزرگ (قيامت) مي‌ترسم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4072, N'بگو: «من تنها خدا را مي‌پرستم در حالي که دينم را براي او خالص مي‌کنم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4073, N'شما هر چه را جز او مي‌خواهيد بپرستيد!» بگو: «زيانکاران واقعي آنانند که سرمايه وجود خويش و بستگانشان را در روز قيامت از دست داده‌اند! آگاه باشيد زيان آشکار همين است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4074, N'براي آنان از بالاي سرشان سايبانهايي از آتش، و در زير پايشان نيز سايبانهايي از آتش است؛ اين چيزي است که خداوند با آن بندگانش را مي‌ترساند! اي بندگان من! از نافرماني من بپرهيزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4075, N'و کساني که از عبادت طاغوت پرهيز کردند و به سوي خداوند بازگشتند، بشارت از آن آنهاست؛ پس بندگان مرا بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4076, N'همان کساني که سخنان را مي‌شنوند و از نيکوترين آنها پيروي مي‌کنند؛ آنان کساني هستند که خدا هدايتشان کرده، و آنها خردمندانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4077, N'آيا تو مي‌تواني کسي را که فرمان عذاب درباره او قطعي شده رهايي بخشي؟! آيا تو ميتواني کسي را که در درون آتش است برگيري و نجات دهي؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4078, N'ولي آنها که تقواي الهي پيشه کردند، غرفه‌هايي در بهشت دارند که بر فراز آنها غرفه‌هاي ديگري بنا شده و از زير آنها نهرها جاري است اين وعده الهي است، و خداوند در وعده خود تخلف نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4079, N'آيا نديدي که خداوند از آسمان آبي فرستاد و آن را بصورت چشمه‌هايي در زمين وارد نمود، سپس با آن زراعتي را خارج مي‌سازد که رنگهاي مختلف دارد؛ بعد آن گياه خشک مي‌شود، بگونه‌اي که آن را زرد و بي‌روح مي‌بيني؛ سپس آن را در هم مي‌شکند و خرد مي‌کند؛ در اين مثال تذکري است براي خردمندان (از ناپايداري دنيا)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4080, N'آيا کسي که خدا سينه‌اش را براي اسلام گشاده است و بر فراز مرکبي از نور الهي قرار گرفته (همچون کوردلان گمراه است؟!) واي بر آنان که قلبهايي سخت در برابر ذکر خدا دارند! آنها در گمراهي آشکاري هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4081, N'خداوند بهترين سخن را نازل کرده، کتابي که آياتش (در لطف و زيبايي و عمق و محتوا) همانند يکديگر است؛ آياتي مکرر دارد (با تکراري شوق‌انگيز) که از شنيدن آياتش لرزه بر اندام کساني که از پروردگارشان مي‌ترسند مي‌افتد؛ سپس برون و درونشان نرم و متوجه ذکر خدا مي‌شود؛ اين هدايت الهي است که هر کس را بخواهد با آن راهنمايي مي‌کند؛ و هر کس را خداوند گمراه سازد، راهنمايي براي او نخواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4082, N'آيا کسي که با صورت خود عذاب دردناک (الهي) را در روز قيامت دور مي‌سازد (همانند کسي است که هرگز آتش دوزخ به او نمي‌رسد)؟! و به ظالمان گفته مي شود: «بچشيد آنچه را به دست مي‌آورديد (و انجام مي‌داديد)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4083, N'کساني که قبل از آنها بودند نيز (آيات ما را) تکذيب نمودند، و عذاب (الهي) از جايي که فکر نمي‌کردند به سراغشان آمد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4084, N'پس خداوند خواري را در زندگي اين دنيا به آنها چشانيد، و عذاب آخرت شديدتر است اگر مي‌دانستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4085, N'ما براي مردم در اين قرآن از هر نوع مثلي زديم، شايد متذکر شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4086, N'قرآني است فصيح و خالي از هر گونه کجي و نادرستي، شايد آنان پرهيزگاري پيشه کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4087, N'خداوند مثالي زده است: مردي را که مملوک شريکاني است که درباره او پيوسته با هم به مشاجره مشغولند، و مردي که تنها تسليم يک نفر است؛ آيا اين دو يکسانند؟! حمد، مخصوص خداست، ولي بيشتر آنان نمي‌دانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4088, N'تو مي‌ميري و آنها نيز خواهند مرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4089, N'سپس شما روز قيامت نزد پروردگارتان مخاصمه مي‌کنيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4090, N'پس چه کسي ستمکارتر است از آن کسي که بر خدا دروغ ببندد و سخن راست را هنگامي که به سراغ او آمده تکذيب کند؟! آيا در جهنم جايگاهي براي کافران نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4091, N'اما کسي که سخن راست بياورد و کسي که آن را تصديق کند، آنان پرهيزگارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4092, N'آنچه بخواهند نزد پروردگارشان براي آنان موجود است؛ و اين است جزاي نيکوکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4093, N'تا خداوند بدترين اعمالي را که انجام داده‌اند (در سايه ايمان و صداقت آنها) بيامرزد، و آنها را به بهترين اعمالي که انجام مي‌دادند پاداش دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4094, N'آيا خداوند براي (نجات و دفاع از) بنده‌اش کافي نيست؟! اما آنها تو را از غير او مي‌ترسانند. و هر کس را خداوند گمراه کند، هيچ هدايت‌کننده‌اي ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4095, N'و هر کس را خدا هدايت کند، هيچ گمراه‌کننده‌اي نخواهد داشت آيا خداوند توانا و داراي مجازات نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4096, N'و اگر از آنها بپرسي: «چه کسي آسمانها و زمين را آفريده؟» حتما مي‌گويند:«خدا!» بگو: «آيا هيچ درباره معبوداني که غير از خدا مي‌خوانيد انديشه مي‌کنيد که اگر خدا زياني براي من بخواهد، آيا آنها مي‌توانند گزند او را برطرف سازند؟! و يا اگر رحمتي براي من بخواهد، آيا آنها مي‌توانند جلو رحمت او را بگيرند؟!» بگو: «خدا مرا کافي است؛ و همه متوکلان تنها بر او توکل مي‌کنند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4097, N'بگو: «اي قوم من! شما هر چه در توان داريد انجام دهيد، من نيز به وظيفه خود عمل مي‌کنم؛ اما بزودي خواهيد دانست...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4098, N'چه کسي عذاب خوارکننده‌اي (در دنيا) به سراغش مي‌آيد، و (سپس) عذابي جاويدان (در آخرت) بر او وارد مي‌گردد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4099, N'ما اين کتاب (آسماني) را براي مردم بحق بر تو نازل کرديم؛ هر کس هدايت را پذيرد به نفع خود اوست؛ و هر کس گمراهي را برگزيند، تنها به زيان خود گمراه مي‌گردد؛ و تو مأمور اجبار آنها به هدايت نيستي.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4100, N'خداوند ارواح را به هنگام مرگ قبض مي‌کند، و ارواحي را که نمرده‌اند نيز به هنگام خواب مي‌گيرد؛ سپس ارواح کساني که فرمان مرگشان را صادر کرده نگه مي‌دارد و ارواح ديگري را (که بايد زنده بمانند) بازمي‌گرداند تا سرآمدي معين؛در اين امر نشانه‌هاي روشني است براي کساني که انديشه مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4101, N'آيا آنان غير از خدا شفيعاني گرفته‌اند؟! به آنان بگو: «آيا (از آنها شفاعت مي‌طلبيد) هر چند مالک چيزي نباشند و درک و شعوري براي آنها نباشد؟!»')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4102, N'بگو: «تمام شفاعت از آن خداست، (زيرا) حاکميت آسمانها و زمين از آن اوست و سپس همه شما را به سوي او بازمي‌گردانند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4103, N'هنگامي که خداوند به يگانگي ياد مي‌شود، دلهاي کساني که به آخرت ايمان ندارند مشمئز (و متنفر) مي‌گردد؛ اما هنگامي که از معبودهاي ديگر ياد مي‌شود، آنان خوشحال مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4104, N'بگو: «خداوندا! اي آفريننده آسمانها و زمين، و آگاه از اسرار نهان و آشکار، تو در ميان بندگانت در آنچه اختلاف داشتند داوري خواهي کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4105, N'اگر ستمکاران تمام آنچه را روي زمين است مالک باشند و همانند آن بر آن افزوده شود، حاضرند همه را فدا کنند تا از عذاب شديد روز قيامت رهايي يابند؛و از سوي خدا براي آنها اموري ظاهر مي‌شود که هرگز گمان نمي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4106, N'در آن روز اعمال بدي را که انجام داده‌اند براي آنها آشکار مي‌شود، و آنچه را استهزا مي‌کردند بر آنها واقع مي‌گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4107, N'هنگامي که انسان را زياني رسد، ما را (براي حل مشکلش) مي‌خواند؛ سپس هنگامي که از جانب خود به او نعمتي دهيم، مي‌گويد: «اين نعمت را بخاطر کارداني خودم به من داده‌اند»؛ ولي اين وسيله آزمايش (آنها) است، اما بيشترشان نمي‌دانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4108, N'اين سخن را کساني که قبل از آنها بودند نيز گفتند، ولي آنچه را به دست مي‌آوردند براي آنها سودي نداشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4109, N'سپس بديهاي اعمالشان به آنها رسيد؛ و ظالمان اين گروه [= اهل مکه‌] نيز بزودي گرفتار بديهاي اعمالي که انجام داده‌اند خواهند شد، و هرگز نمي‌توانند از چنگال عذاب الهي بگريزند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4110, N'آيا آنها ندانستند که خداوند روزي را براي هر کس بخواهد گسترده يا تنگ مي‌سازد؟! در اين، آيات و نشانه‌هايي است براي گروهي که ايمان مي‌آورند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4111, N'بگو: «اي بندگان من که بر خود اسراف و ستم کرده‌ايد! از رحمت خداوند نوميد نشويد که خدا همه گناهان را مي‌آمرزد، زيرا او بسيار آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4112, N'و به درگاه پروردگارتان بازگرديد و در برابر او تسليم شويد، پيش از آنکه عذاب به سراغ شما آيد، سپس از سوي هيچ کس ياري نشويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4113, N'و از بهترين دستورهائي که از سوي پروردگارتان بر شما نازل شده پيروي کنيد پيش از آنکه عذاب (الهي) ناگهان به سراغ شما آيد در حالي که از آن خبر نداريد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4114, N'(اين دستورها براي آن است که) مبادا کسي روز قيامت بگويد: «افسوس بر من از کوتاهيهايي که در اطاعت فرمان خدا کردم و از مسخره‌کنندگان (آيات او) بودم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4115, N'يا بگويد: «اگر خداوند مرا هدايت مي‌کرد، از پرهيزگاران بودم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4116, N'يا هنگامي که عذاب را مي‌بيند بگويد: «اي کاش بار ديگر (به دنيا) بازمي‌گشتم و از نيکوکاران بودم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4117, N'آري، آيات من به سراغ تو آمد، اما آن را تکذيب کردي و تکبر نمودي و از کافران بودي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4118, N'و روز قيامت کساني را که بر خدا دروغ بستند مي‌بيني که صورتهايشان سياه است؛ آيا در جهنم جايگاهي براي متکبران نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4119, N'و خداوند کساني را که تقوا پيشه کردند با رستگاري رهايي مي‌بخشد؛ هيچ بدي به آنان نمي‌رسد و هرگز غمگين نخواهند شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4120, N'خداوند آفريدگار همه چيز است و حافظ و ناظر بر همه اشيا است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4121, N'کليدهاي آسمانها و زمين از آن اوست؛ و کساني که به آيات خداوند کافر شدند زيانکارانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4122, N'بگو: «آيا به من دستور مي‌دهيد که غير خدا را بپرستم اي جاهلان؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4123, N'به تو و همه پيامبران پيشين وحي شده که اگر مشرک شوي، تمام اعمالت تباه مي‌شود و از زيانکاران خواهي بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4124, N'بلکه تنها خداوند را عبادت کن و از شکرگزاران باش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4125, N'آنها خدا را آن گونه که شايسته است نشناختند، در حالي که تمام زمين در روز قيامت در قبضه اوست و آسمانها پيچيده در دست او؛ خداوند منزه و بلندمقام است از شريکيهايي که براي او مي‌پندارند')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4126, N'و در «صور» دميده مي‌شود، پس همه کساني که در آسمانها و زمينند مي‌ميرند، مگر کساني که خدا بخواهد؛ سپس بار ديگر در «صور» دميده مي‌شود، ناگهان همگي به پا مي‌خيزند و در انتظار (حساب و جزا) هستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4127, N'و زمين (در آن روز) به نور پروردگارش روشن مي‌شود، و نامه‌هاي اعمال را پيش مي‌نهند و پيامبران و گواهان را حاضر مي‌سازند، و ميان آنها بحق داوري مي‌شود و به آنان ستم نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4128, N'و به هر کس آنچه انجام داده است بي‌کم و کاست داده مي‌شود؛ و او نسبت به آنچه انجام مي‌دادند از همه آگاهتر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4129, N'و کساني که کافر شدند گروه گروه به سوي جهنم رانده مي‌شوند؛ وقتي به دوزخ مي‌رسند، درهاي آن گشوده مي‌شود و نگهبانان دوزخ به آنها مي‌گويند: «آيا رسولاني از ميان شما به سويتان نيامدند که آيات پروردگارتان را براي شما بخوانند و از ملاقات اين روز شما را بر حذر دارند؟!» مي‌گويند: «آري، (پيامبران آمدند و آيات الهي را بر ما خواندند، و ما مخالفت کرديم!) ولي فرمان عذاب الهي بر کافران مسلم شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4130, N'به آنان گفته مي‌شود: «از درهاي جهنم وارد شويد، جاودانه در آن بمانيد؛ چه بد جايگاهي است جايگاه متکبران!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4131, N'و کساني که تقواي الهي پيشه کردند گروه گروه به سوي بهشت برده مي‌شوند؛ هنگامي که به آن مي‌رسند درهاي بهشت گشوده مي‌شود و نگهبانان به آنان مي‌گويند:«سلام بر شما! گوارايتان باد اين نعمتها! داخل بهشت شويد و جاودانه بمانيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4132, N'آنها مي‌گويند: «حمد و ستايش مخصوص خداوندي است که به وعده خويش درباره ما وفا کرد و زمين (بهشت) را ميراث ما قرار داد که هر جا را بخواهيم منزلگاه خود قرار دهيم؛ چه نيکوست پاداش عمل کنندگان!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4133, N'(در آن روز) فرشتگان را مي‌بيني که بر گرد عرش خدا حلقه زده‌اند و با ستايش پروردگارشان تسبيح مي‌گويند؛ و در ميان بندگان بحق داوري مي‌شود؛ و (سرانجام) گفته خواهد شد: «حمد مخصوص خدا پروردگار جهانيان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4134, N'حم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4135, N'اين کتابي است که از سوي خداوند قادر و دانا نازل شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4136, N'خداوندي که آمرزنده گناه، پذيرنده توبه، داراي مجازات سخت، و صاحب نعمت فراوان است؛ هيچ معبودي جز او نيست؛ و بازگشت (همه شما) تنها بسوي اوست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4137, N'تنها کساني که در آيات ما مجادله مي‌کنند که (از روي عناد) کافر شده‌اند؛ پس مبادا رفت و آمد آنان در شهرها (و قدرت‌نمايي آنان) تو را بفريبد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4138, N'پيش از آنها قوم نوح و اقوامي که بعد از ايشان بودند (پيامبرانشان را) تکذيب کردند؛ و هر امتي در پي آن بود که توطئه کند و پيامبرش را بگيرد (و آزار دهد)، و براي محو حق به مجادله باطل دست زدند؛ اما من آنها را گرفتم (و سخت مجازات کردم)؛ ببين که مجازات من چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4139, N'و اين گونه فرمان پروردگارت درباره کساني که کافر شدند مسلم شده که آنها همه اهل آتشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4140, N'فرشتگاني که حاملان عرشند و آنها که گرداگرد آن (طواف مي‌کنند) تسبيح و حمد پروردگارشان را مي‌گويند و به او ايمان دارند و براي مؤمنان استغفار مي‌کنند (و مي گويند:) پروردگارا! رحمت و علم تو همه چيز را فراگرفته است؛ پس کساني را که توبه کرده و راه تو را پيروي مي‌کنند بيامرز، و آنان را از عذاب دوزخ نگاه دار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4141, N'پروردگارا! آنها را در باغهاي جاويدان بهشت که به آنها وعده فرموده‌اي وارد کن، همچنين از پدران و همسران و فرزندانشان هر کدام که صالح بودند، که تو توانا و حکيمي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4142, N'و آنان را از بديها نگاه دار، و هر کس را که در آن روز از بديها نگاه داري، مشمول رحمتت ساخته‌اي؛ و اين است همان رستگاري عظيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4143, N'کساني را که کافر شدند روز قيامت صدا مي‌زنند که عداوت و خشم خداوند نسبت به شما از عداوت و خشم خودتان نسبت به خودتان بيشتر است، چرا که بسوي ايمان دعوت مي‌شديد، ولي انکار مي‌کرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4144, N'آنها مي‌گويند: «پروردگارا! ما را دو بار ميراندي و دو بار زنده کردي؛ اکنون به گناهان خود معترفيم؛ آيا راهي براي خارج شدن (از دوزخ) وجود دارد؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4145, N'اين بخاطر آن است که وقتي خداوند به يگانگي خوانده مي‌شد انکار مي‌کرديد، و اگر براي او همتايي مي‌پنداشتند ايمان مي‌آورديد؛ اکنون داوري مخصوص خداوند بلندمرتبه و بزرگ است (و شما را مطابق عدل خود کيفر مي‌دهد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4146, N'او کسي است که آيات خود را به شما نشان مي‌دهد و از آسمان براي شما روزي (با ارزشي) مي‌فرستد؛ تنها کساني متذکر اين حقايق مي‌شوند که بسوي خدا باز مي‌گردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4147, N'(تنها) خدا را بخوانيد و دين خود را براي او خالص کنيد، هرچند کافران ناخشنود باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4148, N'او درجات (بندگان صالح) را بالا مي‌برد، او صاحب عرش است، روح (مقدس) را به فرمانش بر هر کس از بندگانش که بخواهد القاء مي‌کند تا (مردم را) از روز ملاقات [= روز رستاخيز] بيم دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4149, N'روزي که همه آنان آشکار مي‌شوند و چيزي از آنها بر خدا پنهان نخواهد ماند؛ (و گفته مي‌شود:) حکومت امروز براي کيست؟ براي خداوند يکتاي قهار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4150, N'امروز هر کس در برابر کاري که انجام داده است پاداش داده مي‌شود؛ امروز هيچ ظلمي نيست؛ خداوند سريع الحساب است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4151, N'و آنها را از روز نزديک بترسان، هنگامي که از شدت وحشت دلها به گلوگاه مي‌رسد و تمامي وجود آنها مملو از اندوه مي‌گردد؛ براي ستمکاران دوستي وجود ندارد، و نه شفاعت کننده‌اي که شفاعتش پذيرفته شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4152, N'او چشمهايي را که به خيانت مي‌گردد و آنچه را سينه‌ها پنهان مي‌دارند، مي‌داند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4153, N'خداوند بحق داوري مي‌کند، و معبودهايي را که غير از او مي‌خوانند هيچ گونه داوري ندارند؛ خداوند شنوا و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4154, N'آيا آنها روي زمين سير نکردند تا ببينند عاقبت کساني که پيش از آنان بودند چگونه بود؟! آنها در قدرت و ايجاد آثار مهم در زمين از اينها برتر بودند؛ ولي خداوند ايشان را به گناهانشان گرفت، و در برابر عذاب او مدافعي نداشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4155, N'اين براي آن بود که پيامبرانشان پيوسته با دلايل روشن به سراغشان مي‌آمدند، ولي آنها انکار مي‌کردند؛ خداوند هم آنان را گرفت (و کيفر داد) که او قوي و مجازاتش شديد است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4156, N'ما موسي را با آيات خود و دليل روشن فرستاديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4157, N'بسوي فرعون و هامان و قارون؛ ولي آنها گفتند: «او ساحري بسيار دروغگو است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4158, N'و هنگامي که حق را از سوي ما براي آنها آورد، گفتند: «پسران کساني را که با موسي ايمان آورده‌اند بکشيد و زنانشان را (براي اسارت و خدمت) زنده بگذاريد!» اما نقشه کافران جز در گمراهي نيست (و نقش بر آب مي‌شود).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4159, N'و فرعون گفت: «بگذاريد موسي را بکشم، و او پروردگارش را بخواند (تا نجاتش دهد)! زيرا من مي‌ترسم که آيين شما را دگرگون سازد، و يا در اين سرزمين فساد بر پا کند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4160, N'موسي گفت: «من به پروردگارم و پروردگار شما پناه مي‌برم از هر متکبري که به روز حساب ايمان نمي‌آورد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4161, N'و مرد مؤمني از آل فرعون که ايمان خود را پنهان مي‌داشت گفت: «آيا مي‌خواهيد مردي را بکشيد بخاطر اينکه مي‌گويد: پروردگار من «الله» است، در حالي که دلايل روشني از سوي پروردگارتان براي شما آورده است؟! اگر دروغگو باشد، دروغش دامن خودش را خواهد گرفت؛ و اگر راستگو باشد، (لااقل) بعضي از عذابهايي را که وعده مي‌دهد به شما خواهد رسيد؛ خداوند کسي را که اسرافکار و بسيار دروغگوست هدايت نمي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4162, N'اي قوم من! امروز حکومت از آن شماست و در اين سرزمين پيروزيد؛ اگر عذاب خدا به سراغ ما آيد، چه کسي ما را ياري خواهد کرد؟!» فرعون گفت: «من جز آنچه را معتقدم به شما ارائه نمي‌دهم، و شما را جز به راه صحيح راهنمايي نمي‌کنم! (دستور، همان قتل موسي است!)»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4163, N'آن مرد باايمان گفت: «اي قوم من! من بر شما از روزي همانند روز (عذاب) اقوام پيشين بيمناکم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4164, N'و از عادتي همچون عادت قوم نوح و عاد و ثمود و کساني که بعد از آنان بودند (از شرک و کفر و طغيان) مي‌ترسم؛ و خداوند ظلم و ستمي بر بندگانش نمي‌خواهد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4165, N'اي قوم من! من بر شما از روزي که مردم يکديگر را صدا مي‌زنند (و از هم ياري مي‌طلبند و صدايشان به جايي نمي‌رسد) بيمناکم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4166, N'همان روزي که روي مي‌گردانيد و فرار مي‌کنيد؛ اما هيچ پناهگاهي در برابر عذاب خداوند براي شما نيست؛ و هر کس را خداوند (بخاطر اعمالش) گمراه سازد، هدايت‌کننده‌اي براي او نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4167, N'پيش از اين يوسف دلايل روشن براي شما آورد، ولي شما همچنان در آنچه او براي شما آورده بود ترديد داشتيد؛ تا زماني که از دنيا رفت، گفتيد: هرگز خداوند بعد از او پيامبري مبعوث نخواهد کرد! اين گونه خداوند هر اسرافکار ترديدکننده‌اي را گمراه مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4168, N'همانها که در آيات خدا بي‌آنکه دليلي برايشان آمده باشد به مجادله برمي‌خيزند؛ (اين کارشان) خشم عظيمي نزد خداوند و نزد آنان که ايمان آورده‌اند به بار مي‌آورد؛ اين گونه خداوند بر دل هر متکبر جباري مهر مي‌نهد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4169, N'فرعون گفت: «اي هامان! براي من بناي مرتفعي بساز، شايد به وسايلي دست يابم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4170, N'وسايل (صعود به) آسمانها تا از خداي موسي آگاه شوم؛ هر چند گمان مي‌کنم او دروغگو باشد!» اينچنين اعمال بد فرعون در نظرش آراسته جلوه کرد و از راه حق باز داشته شد؛ و توطئه فرعون (و همفکران او) جز به نابودي نمي‌انجامد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4171, N'کسي که (از قوم فرعون) ايمان آورده بود گفت: «اي قوم من! از من پيروي کنيد تا شما را به راه درست هدايت کنم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4172, N'اي قوم من! اين زندگي دنيا، تنها متاع زودگذري است؛ و آخرت سراي هميشگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4173, N'هر کس بدي کند، جز بمانند آن کيفر داده نمي‌شود؛ ولي هر کس کار شايسته‌اي انجام دهد -خواه مرد يا زن- در حالي که مؤمن باشد آنها وارد بهشت مي‌شوند و در آن روزي بي‌حسابي به آنها داده خواهد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4174, N'اي قوم من! چرا من شما را به سوي نجات دعوت مي‌کنم، اما شما مرا بسوي آتش فرا مي‌خوانيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4175, N'مرا دعوت مي‌کنيد که به خداوند يگانه کافر شوم و همتايي که به آن علم ندارم براي او قرار دهم، در حالي که من شما را بسوي خداوند عزيز غفار دعوت مي‌کنم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4176, N'قطعا آنچه مرا بسوي آن مي‌خوانيد، نه دعوت (و حاکميتي) در دنيا دارد و نه در آخرت؛ و تنها بازگشت ما در قيامت بسوي خداست؛ و مسرفان اهل آتشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4177, N'و بزودي آنچه را به شما مي‌گويم به خاطر خواهيد آورد! من کار خود را به خدا واگذارم که خداوند نسبت به بندگانش بيناست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4178, N'خداوند او را از نقشه‌هاي سوء آنها نگه داشت، و عذاب شديد بر آل فرعون وارد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4179, N'عذاب آنها آتش است که هر صبح و شام بر آن عرضه مي‌شوند؛ و روزي که قيامت برپا شود (مي‌فرمايد:) «آل فرعون را در سخت‌ترين عذابها وارد کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4180, N'به خاطر بياور هنگامي را که در آتش دوزخ با هم محاجه مي‌کنند؛ ضعيفان به مستکبران مي‌گويند: «ما پيرو شما بوديم، آيا شما (امروز) سهمي از آتش را بجاي ما پذيرا مي‌شويد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4181, N'مستکبران مي‌گويند: «ما همگي در آن هستيم، زيرا خداوند در ميان بندگانش (بعدالت) حکم کرده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4182, N'و آنها که در آتشند به مأموران دوزخ مي‌گويند: «از پروردگارتان بخواهيد يک روز عذاب را از ما بردارد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4183, N'آنها مي‌گويند: «آيا پيامبران شما دلايل روشن برايتان نياوردند؟!» مي‌گويند: «آري!» آنها مي‌گويند: «پس هر چه مي‌خواهيد (خدا را) بخوانيد؛ ولي دعاي کافران (به جايي نمي‌رسد و) جز در ضلالت نيست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4184, N'ما به يقين پيامبران خود و کساني را که ايمان آورده‌اند، در زندگي دنيا و (در آخرت) روزي که گواهان به پا مي‌خيزند ياري مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4185, N'روزي که عذرخواهي ظالمان سودي به حالشان نمي‌بخشد؛ و لعنت خدا براي آنها، و خانه (و جايگاه) بد نيز براي آنان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4186, N'و ما به موسي هدايت بخشيديم، و بني اسرائيل را وارثان کتاب (تورات) قرار داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4187, N'کتابي که مايه هدايت و تذکر براي صاحبان عقل بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4188, N'پس (اي پيامبر!) صبر و شکيبايي پيشه کن که وعده خدا حق است، و براي گناهت استغفار کن، و هر صبح و شام تسبيح و حمد پروردگارت را بجا آور!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4189, N'کساني که در آيات خداوند بدون دليلي که براي آنها آمده باشد ستيزه‌جويي مي‌کنند، در سينه‌هايشان فقط تکبر (و غرور) است، و هرگز به خواسته خود نخواهند رسيد، پس به خدا پناه بر که او شنوا و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4190, N'آفرينش آسمانها و زمين از آفرينش انسانها مهمتر است، ولي بيشتر مردم نمي‌دانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4191, N'هرگز نابينا و بينا يکسان نيستند؛ همچنين کساني که ايمان آورده، و اعمال صالح انجام داده‌اند با بدکاران يکسان نخواهند بود؛ اما کمتر متذکر مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4192, N'روز قيامت به يقين آمدني است، و شکي در آن نيست؛ ولي اکثر مردم ايمان نمي آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4193, N'پروردگار شما گفته است: «مرا بخوانيد تا (دعاي) شما را بپذيرم! کساني که از عبادت من تکبر مي‌ورزند به زودي با ذلت وارد دوزخ مي‌شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4194, N'خداوند کسي است که شب را براي شما آفريد تا در آن بياساييد، و روز را روشني‌بخش قرار داد؛ خداوند نسبت به مردم صاحب فضل و کرم است؛ ولي بيشتر مردم شکرگزاري نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4195, N'اين است خداوند، پروردگار شما که آفريننده همه چيز است؛ هيچ معبودي جز او نيست؛ با اين حال چگونه از راه حق منحرف مي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4196, N'اينچنين کساني که آيات خدا را انکار مي‌کردند (از راه راست) منحرف مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4197, N'خداوند کسي است که زمين را براي شما جايگاه امن و آرامش قرار داد و آسمان را همچون سقفي (بالاي سرتان)؛ و شما را صورتگري کرد، و صورتتان را نيکو آفريد؛ و از چيزهايي پاکيزه به شما روزي داد؛ اين است خداوند پروردگار شما! جاويد و پربرکت است خداوندي که پروردگار عالميان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4198, N'زنده (واقعي) اوست؛ معبودي جز او نيست؛ پس او را بخوانيد در حالي که دين خود را براي او خالص کرده‌ايد! ستايش مخصوص خداوندي است که پروردگار جهانيان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4199, N'بگو: «من نهي شده‌ام از اينکه معبودهايي را که شما غير از خدا مي‌خوانيد بپرستم، چون دلايل روشن از جانب پروردگارم براي من آمده است؛ و مأمورم که تنها در برابر پروردگار عالميان تسليم باشم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4200, N'او کسي است که شما را از خاک آفريد، سپس از نطفه، سپس از علقه (خون بسته شده)، سپس شما را بصورت طفلي (از شکم مادر) بيرون مي‌فرستد، بعد به مرحله کمال قوت خود مي‌رسيد، و بعد از آن پير مي‌شويد و (در اين ميان) گروهي از شما پيش از رسيدن به اين مرحله مي‌ميرند و در نهايت به سرآمد عمر خود مي‌رسيد؛ و شايد تعقل کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4201, N'او کسي است که زنده مي‌کند و مي‌ميراند؛ و هنگامي که کاري را مقرر کند، تنها به آن مي‌گويد: «موجود باش!» بي‌درنگ موجود مي‌شود!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4202, N'آيا نديدي کساني را که در آيات خدا مجادله مي‌کنند، چگونه از راه حق منحرف مي شوند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4203, N'همان کساني که کتاب (آسماني) و آنچه رسولان خود را بدان فرستاده‌ايم تکذيب کردند؛ اما بزودي (نتيجه کار خود را) مي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4204, N'در آن هنگام که غل و زنجيرها بر گردن آنان قرار گرفته و آنها را مي‌کشند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4205, N'و در آب جوشان وارد مي‌کنند؛ سپس در آتش دوزخ افروخته مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4206, N'سپس به آنها گفته مي‌شود: «کجايند آنچه را همتاي خدا قرار مي‌داديد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4207, N'همان معبودهايي را که جز خدا پرستش مي‌کرديد؟!» آنها مي‌گويند: «همه از نظر ما پنهان و گم شدند؛ بلکه ما اصلا پيش از اين چيزي را پرستش نمي‌کرديم»! اين گونه خداوند کافران را گمراه مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4208, N'اين (عذاب) بخاطر آن است که بناحق در زمين شادي مي‌کرديد و از روي غرور و مستي به خوشحالي مي‌پرداختيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4209, N'از درهاي جهنم وارد شويد و جاودانه در آن بمانيد؛ و چه بد است جايگاه متکبران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4210, N'پس (اي پيامبر) صبر کن که وعده خدا حق است؛ و هرگاه قسمتي از مجازاتهايي را که به آنها وعده داده‌ايم در حال حياتت به تو ارائه دهيم، يا تو را (پيش از آن) از دنيا ببريم (مهم نيست)؛ چرا که همه آنان را تنها بسوي ما باز مي‌گردانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4211, N'ما پيش از تو رسولاني فرستاديم؛ سرگذشت گروهي از آنان را براي تو بازگفته، و گروهي را براي تو بازگو نکرده‌ايم؛ و هيچ پيامبري حق نداشت معجزه‌اي جز بفرمان خدا بياورد و هنگامي که فرمان خداوند (براي مجازات آنها) صادر شود، بحق داوري خواهد شد؛ و آنجا اهل باطل زيان خواهند کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4212, N'خداوند کسي است که چهارپايان را براي شما آفريد تا بعضي را سوار شويد و از بعضي تغذيه کنيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4213, N'و براي شما در آنها منافع بسياري (جز اينها) است، تا بوسيله آنها به مقصدي که در دل داريد برسيد؛ و بر آنها و بر کشتيها سوار مي‌شويد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4214, N'او آياتش را همواره به شما نشان مي‌دهد؛ پس کدام يک از آيات او را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4215, N'آيا روي زمين سير نکردند تا ببينند عاقبت کساني که پيش از آنها بودند چه شد؟! همانها که نفراتشان از اينها بيشتر، و نيرو و آثارشان در زمين فزونتر بود؛ اما هرگز آنچه را به دست مي‌آوردند نتوانست آنها را بي‌نياز سازد (و عذاب الهي را از آنان دور کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4216, N'هنگامي که رسولانشان دلايل روشني براي آنان آوردند، به دانشي که خود داشتند خوشحال بودند (و غير آن را هيچ مي‌شمردند)؛ ولي آنچه را (از عذاب) به تمسخر مي‌گرفتند آنان را فراگرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4217, N'هنگامي که عذاب (شديد) ما را ديدند گفتند: «هم اکنون به خداوند يگانه ايمان آورديم و به معبودهايي که همتاي او مي‌شمرديم کافر شديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4218, N'اما هنگامي که عذاب ما را مشاهده کردند، ايمانشان براي آنها سودي نداشت!اين سنت خداوند است که همواره در ميان بندگانش اجرا شده، و آنجا کافران زيانکار شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4219, N'حم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4220, N'اين کتابي است که از سوي خداوند رحمان و رحيم نازل شده است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4221, N'کتابي که آياتش هر مطلبي را در جاي خود بازگو کرده، در حالي که فصيح و گوياست براي جمعيتي که آگاهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4222, N'قرآني که بشارت دهنده و بيم دهنده است؛ ولي بيشتر آنان روي گردان شدند؛ از اين رو چيزي نمي‌شنوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4223, N'آنها گفتند: «قلبهاي ما نسبت به آنچه ما را به آن دعوت مي‌کني در پوششهايي قرار گرفته و در گوشهاي ما سنگيني است، و ميان ما و تو حجابي وجود دارد؛ پس تو بدنبال عمل خود باش، ما هم براي خود عمل مي‌کنيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4224, N'بگو: من فقط انساني مثل شما هستم؛ اين حقيقت بر من وحي مي‌شود که معبود شما معبودي يگانه است؛ پس تمام توجه خويش را به او کنيد و از وي آمرزش طلبيد؛ واي بر مشرکان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4225, N'همانها که زکات را نمي‌پردازند، و آخرت را منکرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4226, N'اما کساني که ايمان آورده و کارهاي شايسته انجام دادند، پاداشي دائمي دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4227, N'بگو: آيا شما به آن کس که زمين را در دو روز آفريد کافر هستيد و براي او همانندهايي قرارمي‌دهيد؟! او پروردگار جهانيان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4228, N'او در زمين کوه‌هاي استواري قرار داد و برکاتي در آن آفريد و مواد غذايي آن را مقدر فرمود، - ينها همه در چهار روز بود- درست به اندازه نياز تقاضا کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4229, N'سپس به آفرينش آسمان پرداخت، در حالي که بصورت دود بود؛ به آن و به زمين دستور داد: «به وجود آييد (و شکل گيريد)، خواه از روي اطاعت و خواه اکراه!» آنها گفتند: «ما از روي طاعت مي‌آييم (و شکل مي‌گيريم)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4230, N'در اين هنگام آنها را بصورت هفت آسمان در دو روز آفريد، و در هر آسماني کار آن (آسمان) را وحي (و مقرر) فرمود، و آسمان پايين را با چراغهايي [= ستارگان‌] زينت بخشيديم، و (با شهابها از رخنه شياطين) حفظ کرديم، اين است تقدير خداوند توانا و دانا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4231, N'اگر آنها روي‌گردان شوند، بگو: من شما را از صاعقه‌اي همانند صاعقه عاد و ثمود مي‌ترسانم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4232, N'در آن هنگام که رسولان از پيش رو و پشت سر (و از هر سو) به سراغشان آمدند (و آنان را دعوت کردند) که جز خدا را نپرستيد آنها گفتند: «اگر پروردگار ما مي‌خواست فرشتگاني نازل مي‌کرد؛ از اين رو ما به آنچه شما مبعوث به آن هستيد کافريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4233, N'اما قوم عاد بناحق در زمين تکبر ورزيدند و گفتند: «چه کسي از ما نيرومندتر است؟!» آيا نمي‌دانستند خداوندي که آنان را آفريده از آنها قويتر است؟ و (به خاطر اين پندار) پيوسته آيات ما را انکار مي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4234, N'سرانجام تندبادي شديد و هول‌انگيز و سرد و سخت در روزهايي شوم و پرغبار بر آنها فرستاديم تا عذاب خوارکننده را در زندگي دنيا به آنها بچشانيم؛ و عذاب آخرت از آن هم خوارکننده‌تر است، و (از هيچ طرف) ياري نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4235, N'اما ثمود را هدايت کرديم، ولي آنها نابينايي را بر هدايت ترجيح دادند؛ به همين جهت صاعقه -آن عذاب خوارکننده- به خاطر اعمالي که انجام مي‌دادند آنها را فروگرفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4236, N'و کساني را که ايمان آوردند و پرهيزگار بودند نجات بخشيديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4237, N'به خاطر بياوريد روزي را که دشمنان خدا را جمع کرده به سوي دوزخ مي‌برند،و صفوف پيشين را نگه مي‌دارند (تا صفهاي بعد به آنها ملحق شوند!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4238, N'وقتي به آن مي‌رسند، گوشها و چشمها و پوستهاي تنشان به آنچه مي‌کردند گواهي مي‌دهند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4239, N'آنها به پوستهاي تنشان مي‌گويند: «چرا بر ضد ما گواهي داديد؟!» آنها جواب مي‌دهند: «همان خدايي که هر موجودي را به نطق درآورده ما را گويا ساخته ؛ و او شما را نخستين بار آفريد، و بازگشتتان بسوي اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4240, N'شما اگر گناهانتان را مخفي مي‌کرديد نه بخاطر اين بود که از شهادت گوش و چشمها و پوستهاي تنتان بيم داشتيد، بلکه شما گمان مي‌کرديد که خداوند بسياري از اعمالي را که انجام مي‌دهيد نمي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4241, N'آري اين گمان بدي بود که درباره پروردگارتان داشتيد و همان موجب هلاکت شما گرديد، و سرانجام از زيانکاران شديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4242, N'اگر صبر کنند (يا نکنند، به هر حال) دوزخ جايگاه آنهاست؛ و اگر تقاضاي عفو کنند، مورد عفو قرار نمي‌گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4243, N'ما براي آنها همنشيناني (زشت‌سيرت) قرار داديم که زشتيها را از پيش رو و پشت سر آنها در نظرشان جلوه دادند؛ و فرمان الهي در باره آنان تحقق يافت و به سرنوشت اقوام گمراهي از جن و انس که قبل از آنها بودند گرفتار شدند؛ آنها مسلما زيانکار بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4244, N'کافران گفتند: «گوش به اين قرآن فراندهيد؛ و به هنگام تلاوت آن جنجال کنيد، شايد پيروز شويد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4245, N'به يقين به کافران عذاب شديدي مي‌چشمانيم، و آنها را به بدترين اعمالي که انجام مي دادند کيفر مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4246, N'اين آتش کيفر دشمنان خدا است، سراي جاويدشان در آن خواهد بود، کيفري است بخاطر اينکه آيات ما را انکار مي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4247, N'کافران گفتند: «پروردگارا! آنهايي که از جن و انس ما را گمراه کردند به ما نشان ده تا زير پاي خود نهيم (و لگدمالشان کنيم) تا از پست ترين مردم باشند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4248, N'به يقين کساني که گفتند: «پروردگار ما خداوند يگانه است!» سپس استقامت کردند، فرشتگان بر آنان نازل مي‌شوند که: «نترسيد و غمگين مباشيد، و بشارت باد بر شما به آن بهشتي که به شما وعده داده شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4249, N'ما ياران و مددکاران شما در زندگي دنيا و آخرت هستيم؛ و براي شما هر چه دلتان بخواهد در بهشت فراهم است، و هر چه طلب کنيد به شما داده مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4250, N'اينها وسيله پذيرايي از سوي خداوند غفور و رحيم است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4251, N'چه کسي خوش گفتارتر است از آن کس که دعوت به سوي خدا مي‌کند و عمل صالح انجام مي‌دهد و مي‌گويد: «من از مسلمانانم»؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4252, N'هرگز نيکي و بدي يکسان نيست؛ بدي را با نيکي دفع کن، ناگاه (خواهي ديد) همان کس که ميان تو و او دشمني است، گويي دوستي گرم و صميمي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4253, N'اما جز کساني که داراي صبر و استقامتند به اين مقام نمي‌رسند، و جز کساني که بهره عظيمي (از ايمان و تقوا) دارند به آن نايل نمي‌گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4254, N'و هرگاه وسوسه‌هايي از شيطان متوجه تو گردد، از خدا پناه بخواه که او شنوده و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4255, N'و از نشانه‌هاي او، شب و روز و خورشيد و ماه است؛ براي خورشيد و ماه سجده نکنيد، براي خدايي که آفريننده آنهاست سجده کنيد اگر مي‌خواهيد او را بپرستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4256, N'و اگر (از عبادت پروردگار) تکبر کنند، کساني که نزد پروردگار تو هستند شب و روز براي او تسبيح مي‌گويند و خسته نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4257, N'و از آيات او اين است که زمين را خشک (و بي‌جان) مي‌بيني، اما هنگامي که آب (باران) بر آن مي‌فرستيم به جنبش درمي‌آيد و نمو مي‌کند؛ همان کسي که آن را زنده کرد، مردگان را نيز زنده مي‌کند؛ او بر هر چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4258, N'کساني که آيات ما را تحريف مي‌کنند بر ما پوشيده نخواهند بود! آيا کسي که در آتش افکنده مي‌شود بهتر است يا کسي که در نهايت امن و امان در قيامت به عرصه محشر مي‌آيد؟! هر کاري مي‌خواهيد بکنيد، او به آنچه انجام مي‌دهيد بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4259, N'کساني که به اين ذکر [= قرآن‌] هنگامي که به سراغشان آمد کافر شدند (نيز بر ما مخفي نخواهد ماند)! و اين کتابي است قطعا شکست ناپذير...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4260, N'که هيچ گونه باطلي، نه از پيش رو و نه از پشت سر، به سراغ آن نمي‌آيد؛ چرا که از سوي خداوند حکيم و شايسته ستايش نازل شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4261, N'آنچه به ناروا درباره تو مي‌گويند همان است که درباره پيامبران قبل از تو نيز گفته شده؛ پروردگار تو داراي مغفرت و (هم) داراي مجازات دردناکي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4262, N'هرگاه آن را قرآني عجمي قرار مي‌داديم حتما مي‌گفتند: «چرا آياتش روشن نيست؟! قرآن عجمي از پيغمبري عربي؟!» بگو: «اين (کتاب) براي کساني که ايمان آورده‌اند هدايت و درمان است؛ ولي کساني که ايمان نمي‌آورند، در گوشهايشان سنگيني است و گويي نابينا هستند و آن را نمي‌بينند؛ آنها (همچون کساني هستند که گوئي) از راه دور صدا زده مي‌شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4263, N'ما به موسي کتاب آسماني داديم؛ سپس در آن اختلاف شد؛ و اگر فرماني از ناحيه پروردگارت در اين باره صادر نشده بود (که بايد به آنان مهلت داد تا اتمام حجت شود)، در ميان آنها داوري مي‌شد (و به کيفر مي‌رسيدند)؛ ولي آنها هنوز درباره آن شکي تهمت‌انگيز دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4264, N'کسي که عمل صالحي انجام دهد، سودش براي خود اوست؛ و هر کس بدي کند، به خويشتن بدي کرده است؛ و پروردگارت هرگز به بندگان ستم نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4265, N'علم به قيامت (و لحظه وقوع آن) تنها به خدا بازمي‌گردد؛ هيچ ميوه‌اي از غلاف خود خارج نمي‌شود، و هيچ زني باردار نمي‌گردد و وضع حمل نمي‌کند مگر به علم او؛ و آن روز که آنها را ندا مي‌دهد (و مي‌گويد:) کجايند شريکاني که براي من مي‌پنداشتيد؟! مي‌گويند: «(پروردگارا!) ما عرضه داشتيم که هيچ گواهي بر گفته خود نداريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4266, N'و همه معبوداني را که قبلا مي‌خواندند محو و گم مي‌شوند؛ و مي‌دانند هيچ گريزگاهي ندارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4267, N'انسان هرگز از تقاضاي نيکي (و نعمت) خسته نمي‌شود؛ و هرگاه شر و بدي به او رسد، بسيار مأيوس و نوميد مي‌گردد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4268, N'و هرگاه او را رحمتي از سوي خود بعد از ناراحتي که به او رسيده بچشانيم مي‌گويد: «اين بخاطر شايستگي و استحقاق من بوده، و گمان نمي‌کنم قيامت برپا شود؛ و (بفرض که قيامتي باشد،) هرگاه بسوي پروردگارم بازگردانده شوم، براي من نزد او پاداشهاي نيک است. ما کافران را از اعمالي که انجام داده‌اند (بزودي) آگاه خواهيم کرد و از عذاب شديد به آنها مي‌چشانيم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4269, N'و هرگاه به انسان (غافل و بي‌خبر) نعمت دهيم، روي مي‌گرداند و به حال تکبر از حق دور مي‌شود؛ ولي هرگاه مختصر ناراحتي به او رسد، تقاضاي فراوان و مستمر (براي بر طرف شدن آن) دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4270, N'بگو: «به من خبر دهيد اگر اين قرآن از سوي خداوند باشد و شما به آن کافر شويد، چه کسي گمراهتر خواهد بود از کسي که در مخالفت شديدي قرار دارد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4271, N'به زودي نشانه‌هاي خود را در اطراف جهان و در درون جانشان به آنها نشان مي‌دهيم تا براي آنان آشکار گردد که او حق است؛ آيا کافي نيست که پروردگارت بر همه چيز شاهد و گواه است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4272, N'آگاه باشيد که آنها از لقاي پروردگارشان در شک و ترديدند؛ و آگاه باشيد که خداوند به همه چيز احاطه دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4273, N'حم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4274, N'عسق.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4275, N'اين گونه خداوند عزيز و حکيم به تو و پيامبراني که پيش از تو بودند وحي مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4276, N'آنچه در آسمانها و آنچه در زمين است از آن اوست؛ و او بلندمرتبه و بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4277, N'نزديک است آسمانها (بخاطر نسبتهاي نارواي مشرکان) از بالا متلاشي شوند و فرشتگان پيوسته تسبيح و حمد پروردگارشان را بجا مي‌آورند و براي کساني که در زمين هستند استغفار مي‌کنند؛ آگاه باشيد خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4278, N'کساني که غير خدا را ولي خود انتخاب کردند، خداوند حساب همه اعمال آنها را نگه مي‌دارد؛ و تو مأمور نيستي که آنان را مجبور به قبول حق کني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4279, N'و اين گونه قرآني عربي [= فصيح و گويا] را بر تو وحي کرديم تا «ام‌القري» [= مکه‌] و مردم پيرامون آن را انذار کني و آنها را از روزي که همه خلايق در آن روز جمع مي‌شوند و شک و ترديد در آن نيست بترساني؛ گروهي در بهشتند و گروهي در آتش سوزان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4280, N'و اگر خدا مي‌خواست همه آنها را امت واحدي قرار مي‌داد (و به زور هدايت مي‌کرد، ولي هدايت اجباري سودي ندارد)؛ اما خداوند هر کس را بخواهد در رحمتش وارد مي‌کند، و براي ظالمان ولي و ياوري نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4281, N'آيا آنها غير از خدا را ولي خود برگزيدند؟! در حالي که «ولي» فقط خداوند است و اوست که مردگان را زنده مي‌کند، و اوست که بر هر چيزي تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4282, N'در هر چيز اختلاف کنيد، داوريش با خداست؛ اين است خداوند، پروردگار من، بر او توکل کرده‌ام و به سوي او بازمي‌گردم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4283, N'او آفريننده آسمانها و زمين است و از جنس شما همسراني براي شما قرار داد و جفتهايي از چهارپايان آفريد؛ و شما را به اين وسيله [= بوسيله همسران‌]زياد مي کند؛ هيچ چيز همانند او نيست و او شنوا و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4284, N'کليدهاي آسمانها و زمين از آن اوست؛ روزي را براي هر کس بخواهد گسترش مي دهد يا محدود مي‌سازد؛ او به همه چيز داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4285, N'آييني را براي شما تشريع کرد که به نوح توصيه کرده بود؛ و آنچه را بر تو وحي فرستاديم و به ابراهيم و موسي و عيسي سفارش کرديم اين بود که: دين را برپا داريد و در آن تفرقه ايجاد نکنيد! و بر مشرکان گران است آنچه شما آنان را به سويش دعوت مي کنيد! خداوند هر کس را بخواهد برمي‌گزيند، و کسي را که به سوي او بازگردد هدايت مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4286, N'آنان پراکنده نشدند مگر بعد از آنکه علم و آگاهي به سراغشان آمد؛ و اين تفرقه جويي بخاطر انحراف از حق( و عداوت و حسد) بود؛ و اگر فرماني از سوي پروردگارت صادر نشده بود که تا سرآمد معيني (زنده و آزاد) باشند، در ميان آنها داوري مي‌شد؛ و کساني که بعد از آنها وارثان کتاب شدند نسبت به آن در شک و ترديدند، شکي همراه با بدبيني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4287, N'پس به همين خاطر تو نيز آنان را به سوي اين آيين واحد الهي دعوت کن و آنچنان که مأمور شده‌اي استقامت نما، و از هوي و هوسهاي آنان پيروي مکن، و بگو: «به هر کتابي که خدا نازل کرده ايمان آورده‌ام و مأمورم در ميان شما عدالت کنم؛ خداوند پروردگار ما و شماست؛ نتيجه اعمال ما از آن ما است و نتيجه اعمال شما از آن شما، خصومت شخصي در ميان ما نيست؛ و خداوند ما و شما را در يکجا جمع مي‌کند، و بازگشت (همه) به سوي اوست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4288, N'کساني که (از روي لجاجت) درباره خدا بعد از پذيرفتن (و ايمان به) او، محاجه مي کنند، دليلشان نزد پروردگارشان باطل و بي‌پايه است؛ و غضب بر آنهاست و عذابي شديد دارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4289, N'خداوند کسي است که کتاب را بحق نازل کرد و ميزان (سنجش حق و باطل و خبر قيامت) را نيز؛ تو چه مي‌داني شايد ساعت (قيام قيامت) نزديک باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4290, N'کساني که به قيامت ايمان ندارند درباره آن شتاب مي‌کنند؛ ولي آنها که ايمان آورده‌اند پيوسته از آن هراسانند، و مي‌دانند آن حق است؛ آگاه باشيد کساني که در قيامت ترديد مي‌کنند، در گمراهي عميقي هستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4291, N'خداوند نسبت به بندگانش لطف (و آگاهي) دارد؛ هر کس را بخواهد روزي مي‌دهد و او قوي و شکست‌ناپذير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4292, N'کسي که زراعت آخرت را بخواهد، به کشت او برکت و افزايش مي‌دهيم و بر محصولش مي‌افزاييم؛ و کسي که فقط کشت دنيا را بطلبد، کمي از آن به او مي‌دهيم اما در آخرت هيچ بهره‌اي ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4293, N'آيا معبوداني دارند که بي‌اذن خداوند آييني براي آنها ساخته‌اند؟! اگر مهلت معيني براي آنها نبود، در ميانشان داوري مي‌شد (و دستور عذاب صادر مي‌گشت) و براي ظالمان عذاب دردناکي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4294, N'(در آن روز) ستمگران را مي‌بيني که از اعمالي که انجام داده‌اند سخت بيمناکند، ولي آنها را فرامي‌گيرد! اما کساني که ايمان آورده و کارهاي شايسته انجام داده‌اند در باغهاي بهشتند و هر چه بخواهند نزد پروردگارشان براي آنها فراهم است؛ اين است فضل (و بخشش) بزرگ!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4295, N'اين همان چيزي است که خداوند بندگانش را که ايمان آورده و اعمال صالح انجام داده‌اند به آن نويد مي‌دهد! بگو: «من هيچ پاداشي از شما بر رسالتم درخواست نمي‌کنم جز دوست‌داشتن نزديکانم [= اهل بيتم‌]؛ و هر کس کار نيکي انجام دهد ، بر نيکي‌اش مي‌افزاييم؛ چرا که خداوند آمرزنده و سپاسگزار است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4296, N'آيا مي‌گويند: «او بر خدا دروغ بسته است»؟! در حالي که اگر خدا بخواهد بر قلب تو مهر مي‌نهد (و اگر خلاف بگوئي قدرت اظهار اين آيات را از تو مي‌گيرد) و باطل را محو مي‌کند و حق را بفرمانش پابرجا مي‌سازد؛ چرا که او از آنچه درون سينه‌هاست آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4297, N'او کسي است که توبه را از بندگانش مي‌پذيرد و بديها را مي‌بخشد، و آنچه را انجام مي‌دهيد مي‌داند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4298, N'و درخواست کساني را که ايمان آورده و کارهاي نيک انجام داده‌اند مي‌پذيرد و از فضل خود بر آنها مي‌افزايد؛ اما براي کافران عذاب شديدي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4299, N'هرگاه خداوند روزي را براي بندگانش وسعت بخشد، در زمين طغيان و ستم مي‌کنند؛ از اين‌رو بمقداري که مي‌خواهد (و مصلحت مي‌داند) نازل مي‌کند، که نسبه به بندگانش آگاه و بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4300, N'او کسي است که باران سودمند را پس از آنکه مأيوس شدند نازل مي‌کند و رحمت خويش را مي‌گستراند؛ و او ولي و (سرپرست) و ستوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4301, N'و از آيات اوست آفرينش آسمانها و زمين و آنچه از جنبندگان در آنها منتشر نموده؛ و او هرگاه بخواهد بر جمع آنها تواناست!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4302, N'هر مصيبتي به شما رسد بخاطر اعمالي است که انجام داده‌ايد، و بسياري را نيز عفو مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4303, N'و شما هرگز نمي‌توانيد در زمين از قدرت خداوند فرار کنيد؛ و غير از خدا هيچ ولي و ياوري براي شما نيست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4304, N'از نشانه‌هاي او کشتيهايي است که در دريا همچون کوه‌ها به نظر مي‌رسند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4305, N'اگر او اراده کند، باد را ساکن مي‌سازد تا آنها بر پشت دريا بي‌حرکت بمانند؛ در اين نشانه‌هايي است براي هر صبرکننده شکرگزار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4306, N'يا اگر بخواهد آنها را بخاطر اعمالي که سرنشينانش مرتکب شده‌اند نابود مي‌سازد؛ و در عين حال بسياري را مي‌بخشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4307, N'کساني که در آيات ما مجادله مي‌کنند بدانند هيچ گريزگاهي ندارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4308, N'آنچه به شما عطا شده متاع زودگذر زندگي دنياست، و آنچه نزد خداست براي کساني که ايمان آورده و بر پروردگارشان توکل مي‌کنند بهتر و پايدارتر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4309, N'همان کساني که از گناهان بزرگ و اعمال زشت اجتناب مي‌ورزند، و هنگامي که خشمگين شوند عفو مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4310, N'و کساني که دعوت پروردگارشان را اجابت کرده و نماز را برپا مي‌دارند و کارهايشان به صورت مشورت در ميان آنهاست و از آنچه به آنها روزي داده‌ايم انفاق مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4311, N'و کساني که هرگاه ستمي به آنها رسد، (تسليم ظلم نمي‌شوند و) ياري مي‌طلبند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4312, N'کيفر بدي، مجازاتي است همانند آن؛ و هر کس عفو و اصلاح کند، پاداش او با خداست؛ خداوند ظالمان را دوست ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4313, N'و کسي که بعد از مظلوم‌شدن ياري طلبد، ايرادي بر او نيست؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4314, N'ايراد و مجازات بر کساني است که به مردم ستم مي‌کنند و در زمين بناحق ظلم روا مي‌دارند؛ براي آنان عذاب دردناکي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4315, N'اما کساني که شکيبايي و عفو کنند، اين از کارهاي پرارزش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4316, N'کسي را که خدا گمراه کند، ولي و ياوري جز او نخواهد داشت؛ و ظالمان را (روز قيامت) مي‌بيني هنگامي که عذاب الهي را مشاهده مي‌کنند ميگويند: «آيا راهي به سوي بازگشت (و جبران) وجود دارد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4317, N'و آنها را مي‌بيني که بر آتش عرضه مي‌شوند در حالي که از شدت مذلت خاشعند و زير چشمي (به آن) نگاه مي‌کنند؛ و کساني که ايمان آورده‌اند مي‌گويند: «زيانکاران واقعي آنانند که خود و خانواده خويش را روز قيامت از دست داده‌اند؛ آگاه باشيد که ظالمان (آن روز) در عذاب دائمند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4318, N'آنها جز خدا اوليا و ياوراني ندارند که ياريشان کنند؛ و هر کس را خدا گمراه سازد، هيچ راه نجاتي براي او نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4319, N'اجابت کنيد دعوت پروردگار خود را پيش از آنکه روزي فرا رسد که بازگشتي براي آن در برابر اراده خدا نيست؛ و در آن روز، نه پناهگاهي داريد و نه مدافعي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4320, N'و اگر روي‌گردان شوند (غمگين مباش)، ما تو را حافظ آنان (و مأمور اجبارشان) قرار نداده‌ايم؛ وظيف‌ه تو تنها ابلاغ رسالت است! و هنگامي که ما رحمتي از سوي خود به انسان بچشانيم به آن دلخوش مي‌شود، و اگر بلايي بخاطر اعمالي که انجام داده‌اند به آنها رسد (به کفران مي‌پردازند)، چرا که انسان بسيار کفران‌کننده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4321, N'مالکيت و حاکميت آسمانها و زمين از آن خداست؛ هر چه را بخواهد مي‌آفريند؛ به هر کس اراده کند دختر مي‌بخشد و به هر کس بخواهد پسر،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4322, N'يا (اگر بخواهد) پسر و دختر -هر دو- را براي آنان جمع ميکند و هر کس را بخواهد عقيم مي‌گذارد؛ زيرا که او دانا و قادر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4323, N'و شايسته هيچ انساني نيست که خدا با او سخن گويد، مگر از راه وحي يا از پشت حجاب، يا رسولي مي‌فرستد و بفرمان او آنچه را بخواهد وحي مي‌کند؛ چرا که او بلندمقام و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4324, N'همان گونه (که بر پيامبران پيشين وحي فرستاديم) بر تو نيز روحي را بفرمان خود وحي کرديم؛ تو پيش از اين نمي‌دانستي کتاب و ايمان چيست (و از محتواي قرآن آگاه نبودي)؛ ولي ما آن را نوري قرار داديم که بوسيله آن هر کس از بندگان خويش را بخواهيم هدايت مي‌کنيم؛ و تو مسلما به سوي راه راست هدايت مي‌کني.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4325, N'راه خداوندي که تمامي آنچه در آسمانها و آنچه در زمين است از آن اوست؛ آگاه باشيد که همه کارها تنها بسوي خدا بازمي‌گردد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4326, N'حم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4327, N'سوگند به کتاب مبين (و روشنگر)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4328, N'که ما آن را قرآني فصيح و عربي قرار داديم، شايد شما (آن را) درک کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4329, N'و آن در «ام‌الکتاب» [= لوح محفوظ] نزد ما بلندپايه و استوار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4330, N'آيا اين ذکر [= قرآن‌] را از شما بازگيريم بخاطر اينکه قومي اسرافکاريد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4331, N'چه بسيار پيامبراني که (براي هدايت) در ميان اقوام پيشين فرستاديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4332, N'ولي هيچ پيامبري به سوي آنها نمي‌آمد مگر اينکه او را استهزا مي‌کردند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4333, N'ولي ما کساني را که نيرومندتر از آنها بودند هلاک کرديم، و داستان پيشينيان گذشت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4334, N'هر گاه از آنان بپرسي: «چه کسي آسمانها و زمين را آفريده است؟» مسلما مي‌گويند: «خداوند قادر و دانا آنها را آفريده است»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4335, N'همان کسي که زمين را محل آرامش شما قرار داد، و براي شما در آن راه‌هايي آفريد باشد، که هدايت شويد (و به مقصد برسيد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4336, N'همان کسي که از آسمان آبي فرستاد بمقدار معين، و بوسيله آن سرزمين مرده را حيات بخشيديم؛ همين گونه (در قيامت از قبرها) شما را خارج مي‌سازند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4337, N'و همان کسي که همه زوجها را آفريد، و براي شما از کشتيها و چهارپايان مرکبهايي قرارداد که بر آن سوار مي‌شويد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4338, N'تا بر پشت آنها بخوبي قرار گيريد؛ سپس هنگامي که بر آنها سوار شديد، نعمت پروردگارتان را متذکر شويد و بگوييد: «پاک و منزه است کسي که اين را مسخر ما ساخت، وگرنه ما توانايي تسخير آن را نداشتيم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4339, N'و ما به سوي پروردگارمان بازمي‌گرديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4340, N'آنها براي خداوند از ميان بندگانش جزئي قرار دادند (و ملائکه را دختران خدا خواندند)؛ انسان کفران‌کننده آشکاري است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4341, N'آيا از ميان مخلوقاتش دختران را براي خود انتخاب کرده و پسران را براي شما برگزيده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4342, N'در حالي که هرگاه يکي از آنها را به همان چيزي که براي خداوند رحمان شبيه قرار داده [= به تولد دختر] بشارت دهند، صورتش (از فرط ناراحتي) سياه مي‌شود و خشمگين مي‌گردد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4343, N'آيا کسي را که در لابلاي زينتها پرورش مي‌يابد و به هنگام جدال قادر به تبيين مقصود خود نيست (فرزند خدا مي‌خوانيد)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4344, N'آنها فرشتگان را که بندگان خداوند رحمانند مؤنث پنداشتند؛ آيا شاهد آفرينش آنها بوده اند؟! گواهي آنان نوشته مي‌شود و (از آن) بازخواست خواهند شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4345, N'آنان گفتند: «اگر خداوند رحمان مي‌خواست ما آنها را پرستش نمي‌کرديم!» ولي به اين امر هيچ گونه علم و يقين ندارند و جز دروغ چيزي نمي‌گويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4346, N'يا اينکه ما کتابي پيش از اين به آنان داده‌ايم و آنها به آن تمسک مي‌جويند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4347, N'بلکه آنها مي‌گويند: «ما نياکان خود را بر آئيني يافتيم، و ما نيز به پيروي آنان هدايت يافته‌ايم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4348, N'و اين‌گونه در هيچ شهر و دياري پيش از تو پيامبر انذارکننده‌اي نفرستاديم مگر اينکه ثروتمندان مست و مغرور آن گفتند: «ما پدران خود را بر آئيني يافتيم و به آثار آنان اقتدا مي‌کنيم.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4349, N'(پيامبرشان) گفت: «آيا اگر من آييني هدايت‌بخش‌تر از آنچه پدرانتان را بر آن يافتيد آورده باشم (باز هم انکار مي‌کنيد)؟!» گفتند: «(آري،) ما به آنچه شما به آن فرستاده شده‌ايد کافريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4350, N'به همين جهت از آنها انتقام گرفتيم؛ بنگر پايان کار تکذيب‌کنندگان چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4351, N'و به خاطر بياور هنگامي را که ابراهيم به پدرش [= عمويش آزر] و قومش گفت: «من از آنچه شما مي‌پرستيد بيزارم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4352, N'مگر آن کسي که مرا آفريده، که او هدايتم خواهد کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4353, N'او کلمه توحيد را کلمه پاينده‌اي در نسلهاي بعد از خود قرار داد، شايد به سوي خدا باز گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4354, N'ولي من اين گروه و پدرانشان را از مواهب دنيا بهره‌مند ساختم تا حق و فرستاده آشکار (الهي) به سراغشان آمد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4355, N'هنگامي که حق به سراغشان آمد؛ گفتند: «اين سحر است، و ما نسبت به آن کافريم»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4356, N'و گفتند: «چرا اين قرآن بر مرد بزرگ (و ثروتمندي) از اين دو شهر (مکه و طائف) ناز ل نشده است؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4357, N'آيا آنان رحمت پروردگارت را تقسيم مي‌کنند؟! ما معيشت آنها را در حيات دنيا در ميانشان تقسيم کرديم و بعضي را بر بعضي برتري داديم تا يکديگر را مسخر کرده (و با هم تعاون نمايند)؛ و رحمت پروردگارت از تمام آنچه جمع‌آوري مي‌کنند بهتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4358, N'اگر (تمکن کفار از مواهب مادي) سبب نمي‌شد که همه مردم امت واحد (گمراهي) شوند، ما براي کساني که به (خداوند) رحمان کافر مي‌شدند خانه‌هايي قرار مي‌داديم با سقفهايي از نقره و نردبانهايي که از آن بالا روند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4359, N'و براي خانه‌هايشان درها و تختهايي (زيبا و نقره‌اي) قرار مي‌داديم که بر آن تکيه کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4360, N'و انواع زيورها؛ ولي تمام اينها بهره زندگي دنياست، و آخرت نزد پروردگارت از آن پرهيزگاران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4361, N'و هر کس از ياد خدا روي‌گردان شود شيطان را به سراغ او ميفرستيم پس همواره قرين اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4362, N'و آنها [= شياطين‌] اين گروه را از راه خدا بازمي‌دارند، در حالي که گمان مي‌کنند هدايت‌يافتگان حقيقي آنها هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4363, N'تا زماني که (در قيامت) نزد ما حاضر شود مي‌گويد: اي کاش ميان من و تو فاصله مشرق و مغرب بود؛ چه بد همنشيني بودي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4364, N'(ولي به آنها مي‌گوييم:) هرگز اين گفتگوها امروز به حال شما سودي ندارد، چرا که ظلم کرديد؛ و همه در عذاب مشترکيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4365, N'(اي پيامبر!) آيا تو مي‌تواني سخن خود را به گوش کران برساني، يا کوران و کساني را که در گمراهي آشکاري هستند هدايت کني؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4366, N'و هرگاه تو را از ميان آنها ببريم، حتما از آنان انتقام خواهيم گرفت؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4367, N'يا اگر (زنده بماني و) و آنچه را (از عذاب) به آنان وعده داده‌ايم به تو نشان دهيم، باز ما بر آنها مسلطيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4368, N'آنچه را بر تو وحي شده محکم بگير که تو بر صراط مستقيمي.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4369, N'و اين مايه يادآوري (و عظمت) تو و قوم تو است و بزودي سؤال خواهيد شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4370, N'از رسولاني که پيش از تو فرستاديم بپرس: آيا غير از خداوند رحمان معبوداني براي پرستش قرار داديم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4371, N'ما موسي را با آيات خود به سوي فرعون و درباريان او فرستاديم؛ (موسي به آنها) گفت: «من فرستاده پروردگار جهانيانم»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4372, N'ولي هنگامي که او آيات ما را براي آنها آورد، به آن مي‌خنديدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4373, N'ما هيچ آيه (و معجزه‌اي) به آنان نشان نمي‌داديم مگر اينکه از ديگري بزرگتر (و مهمتر) بود؛ و آنها را به (انواع) عذاب گرفتار کرديم شايد بازگردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4374, N'(وقتي گرفتار بلا مي‌شدند مي)گفتند: «اي ساحر! پروردگارت را به عهدي که با تو کرده بخوان (تا ما را از اين بلا برهاند) که ما هدايت خواهيم يافت (و ايمان مي‌آوريم)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4375, N'اما هنگامي که عذاب را از آنها برطرف مي‌ساختيم پيمان خود را مي‌شکستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4376, N'فرعون در ميان قوم خود ندا داد و گفت: «اي قوم من! آيا حکومت مصر از آن من نيست، و اين نهرها تحت فرمان من جريان ندارد؟ آيا نمي‌بينيد؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4377, N'مگر نه اين است که من از اين مردي که از خانواده و طبقه پستي است و هرگز نمي‌تواند فصيح سخن بگويد برترم؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4378, N'(اگر راست مي‌گويد) چرا دستبندهاي طلا به او داده نشده، يا اينکه چرا فرشتگان دوشادوش او نيامده‌اند (تا گفتارش را تأييد کنند)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4379, N'(فرعون) قوم خود را سبک شمرد، در نتيجه از او اطاعت کردند؛ آنان قومي فاسق بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4380, N'اما هنگامي که ما را به خشم آوردند، از آنها انتقام گرفتيم و همه را غرق کرديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4381, N'و آنها را پيشگامان (در عذاب) و عبرتي براي ديگران قرار داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4382, N'و هنگامي که درباره فرزند مريم مثلي زده شد، ناگهان قوم تو بخاطر آن داد و فرياد راه انداختند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4383, N'و گفتند: «آيا خدايان ما بهترند يا او [= مسيح‌]؟! (اگر معبودان ما در دوزخند، مسيح نيز در دوزخ است، چرا که معبود واقع شده)!» ولي آنها اين مثل را جز از طريق جدال (و لجاج) براي تو نزدند؛ آنان گروهي کينه‌توز و پرخاشگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4384, N'مسيح فقط بنده‌اي بود که ما نعمت به او بخشيديم و او را نمونه و الگوئي براي بني اسرائيل قرار داديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4385, N'و هرگاه بخواهيم به جاي شما در زمين فرشتگاني قرار مي‌دهيم که جانشين (شما) گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4386, N'و او [= مسيح‌] سبب آگاهي بر روز قيامت است. (زيرا نزول عيسي گواه نزديکي رستاخيز است)؛ هرگز در آن ترديد نکنيد؛ و از من پيروي کنيد که اين راه مستقيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4387, N'و شيطان شما را (از راه خدا) باز ندارد، که او دشمن آشکار شماست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4388, N'و هنگامي که عيسي دلايل روشن (براي آنها) آورد گفت: «من براي شما حکمت آورده‌ام، و آمده‌ام تا برخي از آنچه را که در آن اختلاف داريد روشن کنم؛ پس تقواي الهي پيشه کنيد و از من اطاعت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4389, N'خداوند پروردگار من و پروردگار شماست؛ (تنها) او را پرستش کنيد که راه راست همين است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4390, N'ولي گروه‌هايي از ميان آنها (درباره مسيح) اختلاف کردند (و بعضي او را خدا پنداشتند)؛ واي بر کساني که ستم کردند از عذاب روزي دردناک!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4391, N'آيا جز اين انتظار دارند که قيامت ناگهان به سراغشان آيد در حالي که نمي‌فهمند؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4392, N'دوستان در آن روز دشمن يکديگرند، مگر پرهيزگاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4393, N'اي بندگان من! امروز نه ترسي بر شماست و نه اندوهگين مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4394, N'همان کساني که به آيات ما ايمان آوردند و تسليم بودند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4395, N'(به آنها خطاب مي‌شود:) شما و همسرانتان در نهايت شادماني وارد بهشت شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4396, N'(اين در حالي است که) ظرفها(ي غذا) و جامهاي طلائي (شراب طهور) را گرداگرد آنها مي‌گردانند؛ و در آن (بهشت) آنچه دلها مي‌خواهد و چشمها از آن لذت مي‌برد موجود است؛ و شما هميشه در آن خواهيد ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4397, N'اين بهشتي است که شما وارث آن مي‌شويد بخاطر اعمالي که انجام مي‌داديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4398, N'و در آن براي شما ميوه‌هاي فراوان است که از آن مي‌خوريد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4399, N'(ولي) مجرمان در عذاب دوزخ جاودانه مي‌مانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4400, N'هرگز عذاب آنان تخفيف نمي‌يابد، و در آنجا از همه چيز مأيوسند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4401, N'ما به آنها ستم نکرديم، آنان خود ستمکار بودند!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4402, N'آنها فرياد مي‌کشند: «اي مالک دوزخ! (اي کاش) پروردگارت ما را بميراند (تا آسوده شويم)!» مي‌گويد: «شما در اين جا ماندني هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4403, N'ما حق را براي شما آورديم؛ ولي بيشتر شما از حق کراهت داشتيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4404, N'بلکه آنها تصميم محکم بر توطئه گرفتند؛ ما نيز اراده محکمي (درباره آنها) داريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4405, N'آيا آنان مي‌پندارند که ما اسرار نهاني و سخنان درگوشي آنان را نمي‌شنويم؟ آري، رسولان (و فرشتگان) ما نزد آنها هستند و مي‌نويسند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4406, N'بگو: «اگر براي خداوند فرزندي بود، من نخستين پرستنده او بودم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4407, N'منزه است پروردگار آسمانها و زمين، پروردگار عرش، از توصيفي که آنها مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4408, N'آنان را به حال خود واگذار تا در باطل غوطه‌ور باشند و سرگرم بازي شوند تا روزي را که به آنها وعده داده شده است ملاقات کنند (و نتيجه کار خود را ببينند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4409, N'او کسي است که در آسمان معبود است و در زمين معبود؛ و او حکيم و عليم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4410, N'پر برکت و پايدار است کسي که حکومت آسمانها و زمين و آنچه در ميان آن دو است از آن اوست؛ و آگاهي از قيام قيامت نزد اوست و به سوي او بازگردانده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4411, N'کساني را که غير از او مي‌خوانند قادر بر شفاعت نيستند؛ مگر آنها که شهادت به حق داده‌اند و بخوبي آگاهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4412, N'و اگر از آنها بپرسي چه کسي آنان را آفريده، قطعا مي‌گويند: خدا؛ پس چگونه از عبادت او منحرف مي‌شوند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4413, N'آنها چگونه از شکايت پيامبر که مي‌گويد: «پروردگارا! اينها قومي هستند که ايمان نمي آورند» (غافل مي‌شوند؟!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4414, N'پس (اکنون که چنين است) از آنان روي برگردان و بگو: «سلام بر شما»، اما بزودي خواهند دانست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4415, N'حم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4416, N'سوگند به اين کتاب روشنگر،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4417, N'که ما آن را در شبي پر برکت نازل کرديم؛ ما همواره انذارکننده بوده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4418, N'در آن شب هر امري بر اساس حکمت (الهي) تدبير و جدا مي‌گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4419, N'(آري، نزول قرآن) فرماني بود از سوي ما؛ ما (محمد (ص) را) فرستاديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4420, N'اينها همه بخاطر رحمتي است از سوي پروردگارت، که شنونده و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4421, N'(همان) پروردگار آسمانها و زمين و آنچه در ميان آنهاست، اگر اهل يقين هستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4422, N'هيچ معبودي جز او نيست؛ زنده مي‌کند و مي‌ميراند؛ او پروردگار شما و پروردگار پدران نخستين شماست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4423, N'ولي آنها در شکند و (با حقايق) بازي مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4424, N'پس منتظر روزي باش که آسمان دود آشکاري پديد آورد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4425, N'که همه مردم را فرامي‌گيرد؛ اين عذاب دردناکي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4426, N'(مي‌گويند:) پروردگارا! عذاب را از ما برطرف کن که ايمان مي‌آوريم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4427, N'چگونه: و از کجا متذکر مي‌شوند با اينکه رسول روشنگر (با معجزات و منطق روشن) به سراغشان آمد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4428, N'سپس از او روي گردان شدند و گفتند: «او تعليم يافته‌اي ديوانه است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4429, N'ما عذاب را کمي برطرف مي‌سازيم، ولي باز به کارهاي خود بازمي‌گرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4430, N'(ما از آنها انتقام مي‌گيريم) در آن روز که آنها را با قدرت خواهيم گرفت؛ آري ما انتقام گيرنده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4431, N'ما پيش از اينها قوم فرعون را آزموديم و رسول بزرگواري به سراغشان آمد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4432, N'(و به آنان گفت: امور) بندگان خدا را به من واگذاريد که من فرستاده اميني براي شما هستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4433, N'و در برابر خداوند تکبر نورزيد که من براي شما دليل روشني آورده‌ام!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4434, N'و من به پروردگار خود و پروردگار شما پناه مي‌برم از اينکه مرا متهم کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4435, N'و اگر به من ايمان نمي‌آوريد، از من کناره‌گيري کنيد (و مانع ايمان آوردن مردم نشويد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4436, N'(آنها هيچ يک از اين پندها را نپذيرفتند، و موسي) به پيشگاه پروردگارش عرضه داشت: اينها قومي مجرمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4437, N'(به او دستور داده شد:) بندگان مرا شبانه حرکت ده که شما تعقيب مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4438, N'(هنگامي که از دريا گذشتيد) دريا را آرام و گشاده بگذار (و بگذر) که آنها لشکري غرق‌شده خواهند بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4439, N'(سرانجام همگي نابود شدند و) چه بسيار باغها و چشمه‌ها که از خود به جاي گذاشتند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4440, N'و زراعتها و قصرهاي زيبا و گرانقيمت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4441, N'و نعمتهاي فراوان ديگر که در آن (غرق) بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4442, N'اينچنين بود ماجراي آنان! و ما (اموال و حکومت) اينها را ميراث براي اقوام ديگري قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4443, N'نه آسمان بر آنان گريست و نه زمين، و نه به آنها مهلتي داده شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4444, N'ما بني اسرائيل را از عذاب ذلت‌بار رهايي بخشيديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4445, N'از فرعون که مردي متکبر و از اسرافکاران بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4446, N'ما آنها را با علم (خويش) بر جهانيان برگزيديم و برتري داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4447, N'و آياتي (از قدرت خويش) را به آنها داديم که آزمايش آشکاري در آن بود (ولي آنان کفران کردند و مجازات شدند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4448, N'اينها [= مشرکان‌] مي‌گويند:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4449, N'«مرگ ما جز همان مرگ اول نيست و هرگز برانگيخته نخواهيم شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4450, N'اگر راست مي‌گوييد پدران ما را (زنده کنيد و) بياوريد (تا گواهي دهند)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4451, N'آيا آنان بهترند يا قوم «تبع» و کساني که پيش از آنها بودند؟! ما آنان را هلاک کرديم، چرا که مجرم بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4452, N'ما آسمانها و زمين و آنچه را که در ميان اين دو است به بازي (و بي‌هدف) نيافريديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4453, N'ما آن دو را جز بحق نيافريديم؛ ولي بيشتر آنان نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4454, N'روز جدايي (حق از باطل) وعده‌گاه همه آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4455, N'روزي که هيچ دوستي کمترين کمکي به دوستش نمي‌کند، و از هيچ‌سو ياري نمي‌شوند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4456, N'مگر کسي که خدا او را مورد رحمت قرار داده، چرا که او عزيز و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4457, N'مسلما درخت زقوم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4458, N'غذاي گنهکاران است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4459, N'همانند فلز گداخته در شکمها مي‌جوشد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4460, N'جوششي همچون آب سوزان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4461, N'(آنگاه به مأموران دوزخ خطاب مي‌شود:) اين کافر مجرم را بگيريد و به ميان دوزخ پرتابش کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4462, N'سپس بر سر او از عذاب جوشان بريزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4463, N'(به او گفته مي‌شود:) بچش که (به پندار خود) بسيار قدرتمند و محترم بودي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4464, N'اين همان چيزي است که پيوسته در آن ترديد مي‌کرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4465, N'(ولي) پرهيزگاران در جايگاه امني قرار دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4466, N'در ميان باغها و چشمه‌ها؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4467, N'آنها لباسهايي از حرير نازک و ضخيم مي‌پوشند و در مقابل يکديگر مي‌نشينند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4468, N'اينچنين‌اند بهشتيان؛ و آنها را با «حور العين» تزويج مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4469, N'آنها در آنجا هر نوع ميوه‌اي را بخواهند در اختيارشان قرارمي‌گيرد، و در نهايت امنيت به سر مي‌برند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4470, N'هرگز مرگي جز همان مرگ اول (که در دنيا چشيده‌اند) نخواهند چشيد، و خداوند آنها را از عذاب دوزخ حفظ مي‌کند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4471, N'اين فضل و بخششي است از سوي پروردگارت، اين همان رستگاري بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4472, N'ما آن [= قرآن‌] را بر زبان تو آسان ساختيم، شايد آنان متذکر شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4473, N'(اما اگر نپذيرفتند) منتظر باش، آنها نيز منتظرند (تو منتظر پيروزي الهي و آنها منتظر عذاب و شکست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4474, N'حم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4475, N'اين کتاب از سوي خداوند عزيز و حکيم نازل شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4476, N'بي‌شک در آسمانها و زمين نشانه‌هاي (فراواني) براي مؤمنان وجود دارد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4477, N'و نيز در آفرينش شما و جنبندگاني که (در سراسر زمين) پراکنده ساخته، نشانه‌هايي است براي جمعيتي که اهل يقينند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4478, N'و نيز در آمد و شد شب و روز، و رزق (و باراني) که خداوند از آسمان نازل کرده و بوسيله آن زمين را بعد از مردنش حيات بخشيده و همچنين در وزش بادها، نشانه‌هاي روشني است براي گروهي که اهل تفکرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4479, N'اينها آيات خداوند است که ما آن را بحق بر تو تلاوت مي‌کنيم؛ اگر آنها به اين آيات ايمان نياورند، به کدام سخن بعد از سخن خدا و آياتش ايمان مي‌آورند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4480, N'واي بر هر دروغگوي گنهکار...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4481, N'که پيوسته آيات خدا را مي‌شنود که بر او تلاوت مي‌شود، اما از روي تکبر اصرار بر مخالفت دارد؛ گويي اصلا آن را هيچ نشنيده است؛ چنين کسي را به عذابي دردناک بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4482, N'و هرگاه از بعضي آيات ما آگاه شود، آن را به باد استهزا مي‌گيرد؛ براي آنان عذاب خوارکننده‌اي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4483, N'و پشت سرشان دوزخ است؛ و هرگز آنچه را به دست آورده‌اند آنها را (از عذاب الهي) رهايي نمي‌بخشد، و نه اوليايي که غير از خدا براي خود برگزيدند (مايه نجاتشان خواهند بود)؛ و عذاب بزرگي براي آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4484, N'اين (قرآن) مايه هدايت است، و کساني که به آيات پروردگارشان کافر شدند، عذابي سخت و دردناک دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4485, N'خداوند همان کسي است که دريا را مسخر شما کرد تا کشتيها بفرمانش در آن حرکت کنند و بتوانند از فضل او بهره گيريد، و شايد شکر نعمتهايش را بجا آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4486, N'او آنچه در آسمانها و آنچه در زمين است همه را از سوي خودش مسخر شما ساخته؛ در اين نشانه‌هاي (مهمي) است براي کساني که انديشه مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4487, N'به مؤمنان بگو: «کساني را که اميد به ايام الله [= روز رستاخيز] ندارند مورد عفو قرار دهند تا خداوند هر قومي را به اعمالي که انجام مي‌دادند جزا دهد»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4488, N'هر کس کار شايسته‌اي بجا آورد، براي خود بجا آورده است؛ و کسي که کار بد مي‌کند، به زيان خود اوست؛ سپس همه شما به سوي پروردگارتان بازگردانده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4489, N'ما بني اسرائيل را کتاب (آسماني) و حکومت و نبوت بخشيديم و از روزيهاي پاکيزه به آنها عطا کرديم و آنان را بر جهانيان (و مردم عصر خويش) برتري بخشيديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4490, N'و دلايل روشني از امر نبوت و شريعت در اختيارشان قرار داديم؛ آنها اختلاف نکردند مگر بعد از علم و آگاهي؛ و اين اختلاف بخاطر ستم و برتري‌جويي آنان بود؛ اما پروردگارت روز قيامت در ميان آنها در آنچه اختلاف داشتند داوري مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4491, N'سپس تو را بر شريعت و آيين حقي قرار داديم؛ از آن پيروي کن و از هوسهاي کساني که آگاهي ندارند پيروي مکن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4492, N'آنها هرگز نمي‌توانند تو را در برابر خداوند بي‌نياز کنند (و از عذابش برهانند)؛ و ظالمان يار و ياور يکديگرند، اما خداوند يار و ياور پرهيزگاران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4493, N'اين (قرآن و شريعت آسماني) وسايل بينايي و مايه هدايت و رحمت است براي مردمي که (به آن) يقين دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4494, N'آيا کساني که مرتکب بديها و گناهان شدند گمان کردند که ما آنها را همچون کساني قرارمي‌دهيم که ايمان آورده و اعمال صالح انجام داده‌اند که حيات و مرگشان يکسان باشد؟! چه بد داوري مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4495, N'و خداوند آسمانها و زمين را بحق آفريده است تا هر کس در برابر اعمالي که انجام داده است جزا داده شود؛ و به آنها ستمي نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4496, N'آيا ديدي کسي را که معبود خود را هواي نفس خويش قرار داده و خداوند او را با آگاهي (بر اينکه شايسته هدايت نيست) گمراه ساخته و بر گوش و قلبش مهر زده و بر چشمش پرده‌اي افکنده است؟! با اين حال چه کسي مي‌تواند غير از خدا او را هدايت کند؟! آيا متذکر نمي‌شويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4497, N'آنها گفتند: «چيزي جز همين زندگي دنياي ما در کار نيست؛ گروهي از ما مي‌ميرند و گروهي جاي آنها را مي‌گيرند؛ و جز طبيعت و روزگار ما را هلاک نمي‌کند!» آنان به اين سخن که مي‌گويند علمي ندارند، بلکه تنها حدس مي‌زنند (و گماني بي‌پايه دارند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4498, N'و هنگامي که آيات روشن ما بر آنها خوانده مي‌شود، دليلي در برابر آن ندارند جز اينکه مي‌گويند: «اگر راست مي‌گوييد پدران ما را (زنده کنيد) و بياوريد (تا گواهي دهند)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4499, N'بگو: «خداوند شما را زنده مي‌کند، سپس مي‌ميراند، بار ديگر در روز قيامت که در آن ترديدي نيست گردآوري مي‌کند؛ ولي بيشتر مردم نمي‌دانند.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4500, N'مالکيت و حاکميت آسمانها و زمين براي خداست؛ و آن روز که قيامت برپا شود اهل باطل زيان مي‌بينند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4501, N'در آن روز هر امتي را مي‌بيني (که از شدت ترس و وحشت) بر زانو نشسته؛ هر امتي بسوي کتابش خوانده مي‌شود، و (به آنها مي‌گويند:) امروز جزاي آنچه را انجام مي‌داديد به شما مي‌دهند!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4502, N'اين کتاب ما است که بحق با شما سخن مي‌گويد (و اعمال شما را بازگو مي‌کند)؛ ما آنچه را انجام مي‌داديد مي‌نوشتيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4503, N'اما کساني که ايمان آوردند و اعمال صالح انجام دادند، پروردگارشان آنها را در رحمت خود وارد مي‌کند؛ اين همان پيروزي بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4504, N'اما کساني که کافر شدند (به آنها گفته مي‌شود:) مگر آيات من بر شما خوانده نمي‌شد و شما استکبار کرديد و قوم مجرمي بوديد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4505, N'و هنگامي که گفته مي‌شد: «وعده خداوند حق است، و در قيامت هيچ شکي نيست» ، شما مي‌گفتيد: «ما نمي‌دانيم قيامت چيست؟ ما تنها گماني در اين باره داريم، و به هيچ‌وجه يقين نداريم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4506, N'و بديهاي اعمالشان براي آنان آشکار مي‌شود، و سرانجام آنچه را استهزا مي‌کردند آنها را فرامي‌گيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4507, N'و به آنها گفته مي‌شود: «امروز شما را فراموش مي‌کنيم همان‌گونه که شما ديدار امروزتان را فراموش کرديد؛ و جايگاه شما دوزخ است و هيچ ياوري نداريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4508, N'اين بخاطر آن است که شما آيات خدا را به مسخره گرفتيد و زندگي دنيا شما را فريب داد! «امروز نه آنان را از دوزخ بيرون مي‌آورند، و نه هيچ‌گونه عذري از آنها پذيرفته مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4509, N'پس حمد و ستايش مخصوص خداست، پروردگار آسمانها و پروردگار زمين و پروردگار همه جهانيان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4510, N'و براي اوست کبريا و عظمت در آسمانها و زمين، و اوست عزيز و حکيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4511, N'حم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4512, N'اين کتاب از سوي خداوند عزيز و حکيم نازل شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4513, N'ما آسمانها و زمين و آنچه را در ميان اين دو است جز بحق و براي سرآمد معيني نيافريديم؛ اما کافران از آنچه انذار مي‌شوند روي گردانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4514, N'به آنان بگو: «اين معبودهايي را که غير از خدا پرستش مي‌کنيد به من نشان دهيد چه چيزي از زمين را آفريده‌اند، يا شرکتي در آفرينش آسمانها دارند؟ کتابي آسماني پيش از اين، يا اثر علمي از گذشتگان براي من بياوريد (که دليل صدق گفتار شما باشد) اگر راست مي‌گوييد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4515, N'چه کسي گمراهتر است از آن کس که معبودي غير خدا را مي‌خواند که تا قيامت هم به او پاسخ نمي‌گويد و از خواندن آنها (کاملا) بي‌خبر است؟! (و صداي آنها را هيچ نمي‌شنود!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4516, N'و هنگامي که مردم محشور مي‌شوند، معبودهاي آنها دشمنانشان خواهند بود؛ حتي عبادت آنها را انکار مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4517, N'هنگامي که آيات روشن ما بر آنان خوانده مي‌شود، کافران در برابر حقي که براي آنها آمده مي‌گويند: «اين سحري آشکار است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4518, N'بلکه مي‌گويند: «اين آيات را بر خدا افترا بسته است!» بگو: «اگر من آن را بدروغ به خدا نسبت داده باشم (لازم است مرا رسوا کند و) شما نمي‌توانيد در برابر خداوند از من دفاع کنيد! او کارهايي را که شما در آن وارد مي‌شويد بهتر مي‌داند؛ همين بس که خداوند گواه ميان من و شما باشد؛ و او آمرزنده و مهربان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4519, N'بگو: «من پيامبر نوظهوري نيستم؛ و نمي‌دانم با من و شما چه خواهد شد؛ من تنها از آنچه بر من وحي مي‌شود پيروي مي‌کنم، و جز بيم‌دهنده آشکاري نيستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4520, N'بگو: «به من خبر دهيد اگر اين قرآن از سوي خدا باشد و شما به آن کافر شويد، در حالي که شاهدي از بني اسرائيل بر آن شهادت دهد، و او ايمان آورد و شما استکبار کنيد (چه کسي گمراهتر از شما خواهد بود)؟! خداوند گروه ستمگر را هدايت نمي‌کند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4521, N'کافران درباره مؤمنان چنين گفتند: «اگر (اسلام) چيز خوبي بود، هرگز آنها (در پذيرش آن) بر ما پيشي نمي‌گرفتند!» و چون خودشان بوسيله آن هدايت نشدند مي‌گويند: «اين يک دروغ قديمي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4522, N'و پيش از آن، کتاب موسي که پيشوا و رحمت بود (نشانه‌هاي آن را بيان کرده)، و اين کتاب هماهنگ با نشانه‌هاي تورات است در حالي که به زبان عربي و فصيح و گوياست، تا ظالمان را بيم دهد و براي نيکوکاران بشارتي باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4523, N'کساني که گفتند: «پروردگار ما الله است»، سپس استقامت کردند، نه ترسي براي آنان است و نه اندوهگين مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4524, N'آنها اهل بهشتند و جاودانه در آن مي‌مانند؛ اين پاداش اعمالي است که انجام مي‌دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4525, N'ما به انسان توصيه کرديم که به پدر و مادرش نيکي کند، مادرش او را با ناراحتي حمل مي‌کند و با ناراحتي بر زمين مي‌گذارد؛ و دوران حمل و از شير بازگرفتنش سي ماه است؛ تا زماني که به کمال قدرت و رشد برسد و به چهل سالگي بالغ گردد مي‌گويد: «پروردگارا! مرا توفيق ده تا شکر نعمتي را که به من و پدر و مادرم دادي بجا آورم و کار شايسته‌اي انجام دهم که از آن خشنود باشي، و فرزندان مرا صالح گردان؛ من به سوي تو بازمي‌گردم و توبه مي‌کنم، و من از مسلمانانم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4526, N'آنها کساني هستند که ما بهترين اعمالشان را قبول مي‌کنيم و از گناهانشان مي‌گذريم و در ميان بهشتيان جاي دارند؛ اين وعده راستي است که وعده داده مي‌شدند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4527, N'و کسي که به پدر و مادرش مي‌گويد: «اف بر شما! آيا به من وعده مي‌دهيد که من روز قيامت مبعوث مي‌شوم؟! در حالي که پيش از من اقوام زيادي بودند (و هرگز مبعوث نشدند)! و آن دو پيوسته فرياد مي‌کشند و خدا را به ياري مي‌طلبند که: واي بر تو، ايمان بياور که وعده خدا حق است اما او پيوسته مي‌گويد:» اينها چيزي جز افسانه‌هاي پيشينيان نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4528, N'آنها کساني هستند که فرمان عذاب درباره آنان همراه اقوام (کافري) که پيش از آنان از جن و انس بودند مسلم شده، چرا که همگي زيانکار بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4529, N'و براي هر کدام از آنها درجاتي است بر طبق اعمالي که انجام داده‌اند، تا خداوند کارهايشان را بي‌کم و کاست به آنان تحويل دهد؛ و به آنها هيچ ستمي نخواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4530, N'آن روز که کافران را بر آتش عرضه مي‌کنند (به آنها گفته مي‌شود:) از طيبات و لذائذ در زندگي دنيا خود استفاده کرديد و از آن بهره گرفتيد؛ اما امروز عذاب ذلت‌بار بخاطر استکباري که در زمين بناحق کرديد و بخاطر گناهاني که انجام مي‌داديد؛ جزاي شما خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4531, N'(سرگذشت هود) برادر قوم عاد را ياد کن، آن زمان که قومش را در سرزمين «احقاف» بيم داد در حالي که پيامبران زيادي قبل از او در گذشته‌هاي دور و نزديک آمده بودند که: جز خداي يگانه را نپرستيد! (و گفت:) من بر شما از عذاب روزي بزرگ مي‌ترسم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4532, N'آنها گفتند: «آيا آمده‌اي که ما را (با دروغهايت) از معبودانمان بازگرداني؟! اگر راست مي‌گويي عذابي را که به ما وعده مي‌دهي بياور!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4533, N'گفت: «علم (آن) تنها نزد خداست (و او مي‌داند چه زماني شما را مجازات کند)؛ من آنچه را به آن فرستاده شده‌ام به شما مي‌رسانم، (وظيفه من همين است!) ولي شما را قومي مي‌بينيم که پيوسته در ناداني هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4534, N'هنگامي که آن (عذاب الهي) را بصورت ابر گسترده‌اي ديدند که بسوي دره‌ها و آبگيرهاي آنان در حرکت است (خوشحال شدند) گفتند: «اين ابري است که بر ما مي‌بارد!» (ولي به آنها گفته شد:) اين همان چيزي است که براي آمدنش شتاب مي‌کرديد، تندبادي است (وحشتناک) که عذاب دردناکي در آن است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4535, N'همه چيز را بفرمان پروردگارش در هم ميکوبد و نابود ميکند (آري) آنها صبح کردند در حالي که چيزي جز خانه‌هايشان به چشم نمي‌خورد؛ ما اين‌گونه گروه مجرمان را کيفر مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4536, N'ما به آنها [= قوم عاد] قدرتي داديم که به شما نداديم، و براي آنان گوش و چشم و دل قرار داديم؛ (اما به هنگام نزول عذاب) نه گوشها و چشمها و نه عقلهايشان براي آنان هيچ سودي نداشت، چرا که آيات خدا را انکار مي‌کردند؛ و سرانجام آنچه را استهزا مي‌کردند بر آنها وارد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4537, N'ما آباديهايي را که پيرامون شما بودند نابود ساختيم، و آيات خود را بصورتهاي گوناگون (براي مردم آنها) بيان کرديم شايد بازگردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4538, N'پس چرا معبوداني را که غير از خدا برگزيدند -به گمان اينکه به خدا نزديکشان سازد- آنها را ياري نکردند؟! بلکه از ميانشان گم شدند! اين بود نتيجه دروغ آنها و آنچه افترا مي‌بستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4539, N'(به ياد آور) هنگامي که گروهي از جن را به سوي تو متوجه ساختيم که قرآن را بشنوند؛ وقتي حضور يافتند به يکديگر گفتند: «خاموش باشيد و بشنويد!» و هنگامي که پايان گرفت، به سوي قوم خود بازگشتند و آنها را بيم دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4540, N'گفتند: «اي قوم ما! ما کتابي را شنيديم که بعد از موسي نازل شده، هماهنگ با نشانه‌هاي کتابهاي پيش از آن، که به سوي حق و راه راست هدايت مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4541, N'اي قوم ما! دعوت کننده الهي را اجابت کنيد و به او ايمان آوريد تا گناهانتان را ببخشد و شما را از عذابي دردناک پناه دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4542, N'و هر کس به دعوت کننده الهي پاسخ نگويد، هرگز نمي‌تواند از چنگال عذاب الهي در زمين فرار کند، و غير از خدا يار و ياوري براي او نيست؛ چنين کساني در گمراهي آشکارند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4543, N'آيا آنها نمي‌دانند خداوندي که آسمانها و زمين را آفريده و از آفرينش آنها ناتوان نشده است، مي‌تواند مردگان را زنده کند؟! آري او بر هر چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4544, N'روزي را به ياد آور که کافران را بر آتش عرضه مي‌دارند (و به آنها گفته مي‌شود:) آيا اين حق نيست؟! مي‌گويند: «آري، به پروردگارمان سوگند (که حق است)!» (در اين هنگام خداوند) مي‌گويد: «پس عذاب را بخاطر کفرتان بچشيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4545, N'پس صبر کن آن‌گونه که پيامبران «اولو العزم» صبر کردند، و براي (عذاب) آنان شتاب مکن! هنگامي که وعده‌هايي را که به آنها داده مي‌شود ببينند، احساس مي‌کنند که گويي فقط ساعتي از يک روز (در دنيا) توقف داشتند؛ اين ابلاغي است براي همگان؛ آيا جز قوم فاسق هلاک مي‌شوند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4546, N'کساني که کافر شدند و (مردم را) از راه خدا بازداشتند، (خداوند) اعمالشان را نابود مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4547, N'و کساني که ايمان آوردند و کارهاي شايسته انجام دادند و به آنچه بر محمد (ص) نازل شده -و همه حق است و از سوي پروردگارشان- نيز ايمان آوردند، خداوند گناهانشان را مي‌بخشد و کارشان را اصلاح مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4548, N'اين بخاطر آن است که کافران از باطل پيروي کردند، و مؤمنان از حقي که از سوي پروردگارشان بود تبعيت نمودند؛ اين‌گونه خداوند براي مردم مثلهاي (زندگي) آنان را بيان مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4549, N'و هنگامي که با کافران (جنايت‌پيشه) در ميدان جنگ روبه‌رو شديد گردنهايشان را بزنيد، (و اين کار را همچنان ادامه دهيد) تا به اندازه کافي دشمن را در هم بکوبيد؛ در اين هنگام اسيران را محکم ببنديد؛ سپس يا بر آنان منت گذاريد (و آزادشان کنيد) يا در برابر آزادي از آنان فديه [= غرامت‌] بگيريد؛ (و اين وضع بايد همچنان ادامه يابد) تا جنگ بار سنگين خود را بر زمين نهد، (آري) برنامه اين است! و اگر خدا مي‌خواست خودش آنها را مجازات مي‌کرد، اما مي‌خواهد بعضي از شما را با بعضي ديگر بيازمايد؛ و کساني که در راه خدا کشته شدند، خداوند هرگز اعمالشان را از بين نمي‌برد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4550, N'بزودي آنان را هدايت نموده و کارشان را اصلاح مي‌کند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4551, N'و آنها را در بهشت (جاويدانش) که اوصاف آن را براي آنان بازگو کرده وارد مي‌کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4552, N'اي کساني که ايمان آورده‌ايد! اگر (آيين) خدا را ياري کنيد، شما را ياري مي‌کند و گامهايتان را استوار مي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4553, N'و کساني که کافر شدند، مرگ بر آنان! و اعمالشان نابود باد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4554, N'اين بخاطر آن است که از آنچه خداوند نازل کرده کراهت داشتند؛ از اين رو خدا اعمالشان را حبط و نابود کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4555, N'آيا در زمين سير نکردند تا ببينند عاقبت کساني که قبل از آنان بودند چگونه بود؟! خداوند آنها را هلاک کرد؛ و براي کافران امثال اين مجازاتها خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4556, N'اين براي آن است که خداوند مولا و سرپرست کساني است که ايمان آوردند؛ اما کافران مولايي ندارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4557, N'خداوند کساني را که ايمان آوردند و اعمال صالح انجام دادند وارد باغهايي از بهشت مي‌کند که نهرها از زير (درختانش) جاري است؛ در حالي که کافران از متاع زودگذر دنيا بهره مي‌گيرند و همچون چهارپايان مي‌خورند، و سرانجام آتش دوزخ جايگاه آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4558, N'و چه بسيار شهرهايي که از شهري که تو را بيرون کرد نيرومندتر بودند؛ ما همه آنها را نابود کرديم و هيچ ياوري نداشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4559, N'آيا کسي که دليل روشني از سوي پروردگارش دارد، همانند کسي است که زشتي اعمالش در نظرش آراسته شده و از هواي نفسشان پيروي مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4560, N'توصيف بهشتي که به پرهيزگاران وعده داده شده، چنين است: در آن نهرهايي از آب صاف و خالص که بدبو نشده، و نهرهايي از شير که طعم آن دگرگون نگشته، و نهرهايي از شراب (طهور) که مايه لذت نوشندگان است، و نهرهايي از عسل مصفاست، و براي آنها در آن از همه انواع ميوه‌ها وجود دارد؛ و (از همه بالاتر) آمرزشي است از سوي پروردگارشان! آيا اينها همانند کساني هستند که هميشه در آتش دوزخند و از آب جوشان نوشانده مي‌شوند که اندرونشان را از هم متلاشي مي‌کند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4561, N'گروهي از آنان به سخنانت گوش فرامي‌دهند، اما هنگامي که از نزد تو خارج مي‌شوند به کساني که علم و دانش به آنان بخشيده شده (از روي استهزا) مي‌گويند: «(اين مرد) الان چه گفت؟!» آنها کساني هستند که خداوند بر دلهايشان مهر نهاده و از هواي نفسشان پيروي کرده‌اند (از اين رو چيزي نمي‌فهمند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4562, N'کساني که هدايت يافته‌اند، خداوند بر هدايتشان مي‌افزايد و روح تقوا به آنان مي‌بخشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4563, N'آيا آنها [= کافران‌] جز اين انتظاري دارند که قيامت ناگهان فرا رسد (آنگاه ايمان آورند)، در حالي که هم‌اکنون نشانه‌هاي آن آمده است؛ اما هنگامي که بيايد، تذکر (و ايمان) آنها سودي نخواهد داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4564, N'پس بدان که معبودي جز «الله» نيست؛ و براي گناه خود و مردان و زنان باايمان استغفار کن! و خداوند محل حرکت و قرارگاه شما را مي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4565, N'کساني که ايمان آورده‌اند مي‌گويند: «چرا سوره‌اي نازل نمي‌شود (که در آن فرمان جهاد باشد)؟!» اما هنگامي که سوره واضح و روشني نازل مي‌گردد که در آن سخني از جنگ است، منافقان بيماردل را مي‌بيني که همچون کسي که در آستانه مرگ قرار گرفته به تو نگاه مي‌کنند؛ پس مرگ و نابودي براي آنان سزاوارتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4566, N'(ولي) اطاعت و سخن سنجيده براي آنان بهتر است؛ و اگر هنگامي که فرمان جهاد قطعي مي‌شود به خدا راست گويند (و از در صدق و صفا درآيند) براي آنها بهتر مي‌باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4567, N'اگر (از اين دستورها) روي گردان شويد، جز اين انتظار مي‌رود که در زمين فساد و قطع پيوند خويشاوندي کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4568, N'آنها کساني هستند که خداوند از رحمت خويش دورشان ساخته، گوشهايشان را کر و چشمهايشان را کور کرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4569, N'آيا آنها در قرآن تدبر نمي‌کنند، يا بر دلهايشان قفل نهاده شده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4570, N'کساني که بعد از روشن شدن هدايت براي آنها، پشت به حق کردند، شيطان اعمال زشتشان را در نظرشان زينت داده و آنان را با آرزوهاي طولاني فريفته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4571, N'اين بخاطر آن است که آنان به کساني که نزول وحي الهي را کراهت داشتند گفتند: «ما در بعضي از امور از شما پيروي مي‌کنيم؟» در حالي که خداوند پنهانکاري آنان را مي‌داند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4572, N'حال آنها چگونه خواهد بود هنگامي که فرشتگان (مرگ) بر صورت و پشت آنان مي‌زنند و جانشان را مي‌گيرند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4573, N'اين بخاطر آن است که آنها از آنچه خداوند را به خشم مي‌آورد پيروي کردند، و آنچه را موجب خشنودي اوست کراهت داشتند؛ از اين رو (خداوند) اعمالشان را نابود کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4574, N'آيا کساني که در دلهايشان بيماري است گمان کردند خدا کينه‌هايشان را آشکار نمي‌کند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4575, N'و اگر ما بخواهيم آنها را به تو نشان مي‌دهيم تا آنان را با قيافه‌هايشان بشناسي، هر چند مي‌تواني آنها را از طرز سخنانشان بشناسي؛ و خداوند اعمال شما را مي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4576, N'ما همه شما را قطعا مي‌آزمائيم تا معلوم شود مجاهدان واقعي و صابران از ميان شما کيانند، و اخبار شما را بيازماييم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4577, N'آنان که کافر شدند و (مردم را) از راه خدا بازداشتند و بعد از روشن‌شدن هدايت براي آنان (باز) به مخالفت با رسول (خدا) برخاستند، هرگز زياني به خدا نمي‌رسانند و (خداوند) بزودي اعمالشان را نابود مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4578, N'اي کساني که ايمان آورده‌ايد! اطاعت کنيد خدا را، و اطاعت کنيد رسول (خدا) را، و اعمال خود را باطل نسازيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4579, N'کساني که کافر شدند و (مردم را) از راه خدا بازداشتند سپس در حال کفر از دنيا رفتند، خدا هرگز آنها را نخواهد بخشيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4580, N'پس هرگز سست نشويد و (دشمنان را) به صلح (ذلت‌بار) دعوت نکنيد در حالي که شما برتريد، و خداوند با شماست و چيزي از (ثواب) اعمالتان را کم نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4581, N'زندگي دنيا تنها بازي و سرگرمي است؛ و اگر ايمان آوريد و تقوا پيشه کنيد، پاداشهاي شما را مي‌دهد و اموال شما را نمي‌طلبد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4582, N'چرا که هر گاه اموال شما را مطالبه کند و حتي اصرار نمايد، بخل مي‌ورزيد؛ و کينه و خشم شما را آشکار مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4583, N'آري، شما همان گروهي هستيد که براي انفاق در راه خدا دعوت مي‌شويد، بعضي از شما بخل مي‌ورزند؛ و هر کس بخل ورزد، نسبت به خود بخل کرده است؛ و خداوند بي‌نياز است و شما همه نيازمنديد؛ و هرگاه سرپيچي کنيد، خداوند گروه ديگري را جاي شما مي‌آورد پس آنها مانند شما نخواهند بود (و سخاوتمندانه در راه خدا انفاق مي‌کنند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4584, N'ما براي تو پيروزي آشکاري فراهم ساختيم!...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4585, N'تا خداوند گناهان گذشته و آينده‌اي را که به تو نسبت مي‌دادند ببخشد (و حقانيت تو را ثابت نموده) و نعمتش را بر تو تمام کند و به راه راست هدايتت فرمايد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4586, N'و پيروزي شکست‌ناپذيري نصيب تو کند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4587, N'او کسي است که آرامش را در دلهاي مؤمنان نازل کرد تا ايماني بر ايمانشان بيفزايند؛ لشکريان آسمانها و زمين از آن خداست، و خداوند دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4588, N'هدف (ديگر از آن فتح مبين) اين بود که مردان و زنان با ايمان را در باغهايي (از بهشت) وارد کند که نهرها از زير (درختانش) جاري است، در حالي که جاودانه در آن مي‌مانند، و گناهانشان را مي‌بخشد، و اين نزد خدا رستگاري بزرگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4589, N'و (نيز) مردان و زنان منافق و مردان و زنان مشرک را که به خدا گمان بد مي‌برند مجازات کند؛ (آري) حوادث ناگواري (که براي مؤمنان انتظار مي‌کشند) تنها بر خودشان نازل مي‌شود! خداوند بر آنان غضب کرده و از رحمت خود دورشان ساخته و جهنم را براي آنان آماده کرده؛ و چه بد سرانجامي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4590, N'لشکريان آسمانها و زمين تنها از آن خداست؛ و خداوند شکست‌ناپذير و حکيم است.!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4591, N'به يقين ما تو را گواه (بر اعمال آنها) و بشارت‌دهنده و بيم‌دهنده فرستاديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4592, N'تا (شما مردم) به خدا و رسولش ايمان بياوريد و از او دفاع کنيد و او را بزرگ داريد، و خدا را صبح و شام تسبيح گوييد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4593, N'کساني که با تو بيعت مي‌کنند (در حقيقت) تنها با خدا بيعت مي‌نمايند، و دست خدا بالاي دست آنهاست؛ پس هر کس پيمان‌شکني کند، تنها به زيان خود پيمان شکسته است؛ و آن کس که نسبت به عهدي که با خدا بسته وفا کند، بزودي پاداش عظيمي به او خواهد داد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4594, N'بزودي متخلفان از اعراب باديه‌نشين (عذرتراشي کرده) مي‌گويند: «(حفظ) اموال و خانواده‌هاي ما، ما را به خود مشغول داشت (و نتوانستيم در سفر حديبيه تو را همراهي کنيم)، براي ما طلب آمرزش کن!» آنها به زبان خود چيزي مي‌گويند که در دل ندارند! بگو: «چه کسي مي‌تواند در برابر خداوند از شما دفاع کند هرگاه زياني براي شما بخواهد، و يا اگر نفعي اراده کند (مانع گردد)؟! و خداوند به همه کارهايي که انجام مي‌دهيد آگاه است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4595, N'ولي شما گمان کرديد پيامبر و مؤمنان هرگز به خانواده‌هاي خود بازنخواهند گشت؛ و اين (پندار غلط) در دلهاي شما زينت يافته بود و گمان بد کرديد؛ و سرانجام (در دام شيطان افتاديد و) هلاک شديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4596, N'آن کس که به خدا و پيامبرش ايمان نياورده (سرنوشتش دوزخ است)، چرا که ما براي کافران آتش فروزان آماده کرده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4597, N'مالکيت و حاکميت آسمانها و زمين از آن خداست؛ هر کس را بخواهد (و شايسته بداند) مي‌بخشد، و هر کس را بخواهد مجازات مي‌کند؛ و خداوند آمرزنده و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4598, N'هنگامي که شما براي به دست آوردن غنايمي حرکت کنيد، متخلفان (حديبيه) مي‌گويند: «بگذاريد ما هم در پي شما بيائيم، آنها مي‌خواهند کلام خدا را تغيير دهند؛ بگو: «هرگز نبايد بدنبال ما بياييد؛ اين گونه خداوند از قبل گفته است!»آنها به زودي مي‌گويند: «شما نسبت به ما حسد مي‌ورزيد!» ولي آنها جز اندکي نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4599, N'به متخلفان از اعراب بگو: «بزودي از شما دعوت مي‌شود که بسوي قومي نيرومند و جنگجو برويد و با آنها پيکار کنيد تا اسلام بياورند؛ اگر اطاعت کنيد، خداوند پاداش نيکي به شما مي‌دهد؛ و اگر سرپيچي نماييد -همان گونه که در گذشته نيز سرپيچي کرديد- شما را با عذاب دردناکي کيفر مي‌دهد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4600, N'بر نابينا و لنگ و بيمار گناهي نيست (اگر در ميدان جهاد شرکت نکنند)؛ و هر کس خدا و رسولش را اطاعت نمايد، او را در باغهاي (از بهشت) وارد مي‌کند که نهرها از زير (درختانش) جاري است؛ و آن کس که سرپيچي کند، او را به عذاب دردناکي گرفتار مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4601, N'خداوند از مؤمنان -هنگامي که در زير آن درخت با تو بيعت کردند- راضي و خشنود شد؛ خدا آنچه را در درون دلهايشان (از ايمان و صداقت) نهفته بود مي‌دانست؛ از اين رو آرامش را بر دلهايشان نازل کرد و پيروزي نزديکي بعنوان پاداش نصيب آنها فرمود؛')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4602, N'و (همچنين) غنايم بسياري که آن را به دست مي‌آوريد؛ و خداوند شکست ناپذير و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4603, N'خداوند غنايم فراواني به شما وعده داده بود که آنها را به دست مي‌آوريد، ولي اين يکي را زودتر براي شما فراهم ساخت؛ و دست تعدي مردم [= دشمنان‌] را از شما بازداشت تا نشانه‌اي براي مؤمنان باشد و شما را به راه راست هدايت کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4604, N'و نيز غنايم و فتوحات ديگري (نصيبتان مي‌کند) که شما توانايي آن را نداريد، ولي قدرت خدا به آن احاطه دارد؛ و خداوند بر همه چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4605, N'و اگر کافران (در سرزمين حديبيه) با شما پيکار مي‌کردند بزودي فرار مي‌کردند، سپس ولي و ياوري نمي‌يافتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4606, N'اين سنت الهي است که در گذشته نيز بوده است؛ و هرگز براي سنت الهي تغيير و تبديلي نخواهي يافت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4607, N'او کسي است که دست آنها را از شما، و دست شما را از آنان در دل مکه کوتاه کرد، بعد از آنکه شما را بر آنها پيروز ساخت؛ و خداوند به آنچه انجام مي‌دهيد بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4608, N'آنها کساني هستند که کافر شدند و شما را از (زيارت) مسجد الحرام و رسيدن قربانيهايتان به محل قربانگاه بازداشتند؛ و هرگاه مردان و زنان با ايماني در اين ميان بدون آگاهي شما، زير دست و پا، از بين نمي‌رفتند که از اين راه عيب و عاري ناآگاهانه به شما مي‌رسيد، (خداوند هرگز مانع اين جنگ نمي‌شد)! هدف اين بود که خدا هر کس را مي‌خواهد در رحمت خود وارد کند؛ و اگر مؤمنان و کفار (در مکه) از هم جدا مي‌شدند، کافران را عذاب دردناکي مي‌کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4609, N'(به خاطر بياوريد) هنگامي را که کافران در دلهاي خود خشم و نخوت جاهليت داشتند؛ و (در مقابل،) خداوند آرامش و سکينه خود را بر فرستاده خويش و مؤمنان نازل فرمود و آنها را به حقيقت تقوا ملزم ساخت، و آنان از هر کس شايسته‌تر و اهل آن بودند؛ و خداوند به همه چيز دانا است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4610, N'خداوند آنچه را به پيامبرش در عالم خواب نشان داد راست گفت؛ بطور قطع همه شما بخواست خدا وارد مسجد الحرام مي‌شويد در نهايت امنيت و در حالي که سرهاي خود را تراشيده يا کوتاه کرده‌ايد و از هيچ کس ترس و وحشتي نداريد؛ ولي خداوند چيزهايي را مي‌دانست که شما نمي‌دانستيد (و در اين تأخير حکمتي بود)؛ و قبل از آن، فتح نزديکي (براي شما) قرار داده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4611, N'او کسي است که رسولش را با هدايت و دين حق فرستاده تا آن را بر همه ادايان پيروز کند؛ و کافي است که خدا گواه اين موضوع باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4612, N'محمد (ص) فرستاده خداست؛ و کساني که با او هستند در برابر کفار سرسخت و شديد، و در ميان خود مهربانند؛ پيوسته آنها را در حال رکوع و سجود مي‌بيني در حالي که همواره فضل خدا و رضاي او را مي‌طلبند؛ نشانه آنها در صورتشان از اثر سجده نمايان است؛ اين توصيف آنان در تورات و توصيف آنان در انجيل است، همانند زراعتي که جوانه‌هاي خود را خارج ساخته، سپس به تقويت آن پرداخته تا محکم شده و بر پاي خود ايستاده است و بقدري نمو و رشد کرده که زارعان را به شگفتي وامي‌دارد؛ اين براي آن است که کافران را به خشم آورد (ولي) کساني از آنها را که ايمان آورده و کارهاي شايسته انجام داده‌اند، خداوند وعده آمرزش و اجر عظيمي داده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4613, N'اي کساني که ايمان آورده‌ايد! چيزي را بر خدا و رسولش مقدم نشمريد (و پيشي مگيريد)، و تقواي الهي پيشه کنيد که خداوند شنوا و داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4614, N'اي کساني که ايمان آورده‌ايد! صداي خود را فراتر از صداي پيامبر نکنيد، و در برابر او بلند سخن مگوييد (و داد و فرياد نزنيد) آن گونه که بعضي از شما در برابر بعضي بلند صدا مي‌کنند، مبادا اعمال شما نابود گردد در حالي که نمي‌دانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4615, N'آنها که صداي خود را نزد رسول خدا کوتاه مي‌کنند همان کساني هستند که خداوند دلهايشان را براي تقوا خالص نموده، و براي آنان آمرزش و پاداش عظيمي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4616, N'(ولي) کساني که تو را از پشت حجره‌ها بلند صدا مي‌زنند، بيشترشان نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4617, N'اگر آنها صبر مي‌کردند تا خود به سراغشان آيي، براي آنان بهتر بود؛ و خداوند آمرزنده و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4618, N'اي کساني که ايمان آورده‌ايد! اگر شخص فاسقي خبري براي شما بياورد، درباره آن تحقيق کنيد، مبادا به گروهي از روي ناداني آسيب برسانيد و از کرده خود پشيمان شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4619, N'و بدانيد رسول خدا در ميان شماست؛ هرگاه در بسياري از کارها از شما اطاعت کند، به مشقت خواهيد افتاد؛ ولي خداوند ايمان را محبوب شما قرار داده و آن را در دلهايتان زينت بخشيده، و (به عکس) کفر و فسق و گناه را منفورتان قرار داده است؛ کساني که داراي اين صفاتند هدايت يافتگانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4620, N'(و اين براي شما بعنوان) فضل و نعمتي از سوي خداست؛ و خداوند دانا و حکيم است.!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4621, N'و هرگاه دو گروه از مؤمنان با هم به نزاع و جنگ پردازند، آنها را آشتي دهيد؛ و اگر يکي از آن دو بر ديگري تجاوز کند، با گروه متجاوز پيکار کنيد تا به فرمان خدا بازگردد؛ و هرگاه بازگشت (و زمينه صلح فراهم شد)، در ميان آن دو به عدالت صلح برقرار سازيد؛ و عدالت پيشه کنيد که خداوند عدالت پيشگان را دوست مي‌دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4622, N'مؤمنان برادر يکديگرند؛ پس دو برادر خود را صلح و آشتي دهيد و تقواي الهي پيشه کنيد، باشد که مشمول رحمت او شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4623, N'اي کساني که ايمان آورده‌ايد! نبايد گروهي از مردان شما گروه ديگر را مسخره کنند، شايد آنها از اينها بهتر باشند؛ و نه زناني زنان ديگر را، شايد آنان بهتر از اينان باشند؛ و يکديگر را مورد طعن و عيبجويي قرار ندهيد و با القاب زشت و ناپسند يکديگر را ياد نکنيد، بسيار بد است که بر کسي پس از ايمان نام کفرآميز بگذاريد؛ و آنها که توبه نکنند، ظالم و ستمگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4624, N'اي کساني که ايمان آورده‌ايد! از بسياري از گمانها بپرهيزيد، چرا که بعضي از گمانها گناه است؛ و هرگز (در کار ديگران) تجسس نکنيد؛ و هيچ يک از شما ديگري را غيبت نکند، آيا کسي از شما دوست دارد که گوشت برادر مرده خود را بخورد؟! (به يقين) همه شما از اين امر کراهت داريد؛ تقواي الهي پيشه کنيد که خداوند توبه‌پذير و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4625, N'اي مردم! ما شما را از يک مرد و زن آفريديم و شما را تيره‌ها و قبيله‌ها قرار داديم تا يکديگر را بشناسيد؛ (اينها ملاک امتياز نيست،) گرامي‌ترين شما نزد خداوند با تقواترين شماست؛ خداوند دانا و آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4626, N'عربهاي باديه‌نشين گفتند: «ايمان آورده‌ايم» بگو: «شما ايمان نياورده‌ايد، ولي بگوييد اسلام آورده‌ايم، اما هنوز ايمان وارد قلب شما نشده است! و اگر از خدا و رسولش اطاعت کنيد، چيزي از پاداش کارهاي شما را فروگذار نمي‌کند، خداوند، آمرزنده مهربان است.»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4627, N'مؤمنان واقعي تنها کساني هستند که به خدا و رسولش ايمان آورده‌اند، سپس هرگز شک و ترديدي به خود راه نداده و با اموال و جانهاي خود در راه خدا جهاد کرده‌اند؛ آنها راستگويانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4628, N'بگو: «آيا خدا را از ايمان خود با خبر مي‌سازيد؟! او تمام آنچه را در آسمانها و زمين است مي‌داند؛ و خداوند از همه چيز آگاه است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4629, N'آنها بر تو منت مي‌نهند که اسلام آورده‌اند؛ بگو: «اسلام آوردن خود را بر من منت نگذاريد، بلکه خداوند بر شما منت مي‌نهد که شما را به سوي ايمان هدايت کرده است، اگر (در ادعاي ايمان) راستگو هستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4630, N'خداوند غيبت آسمانها و زمين را مي‌داند و نسبت به آنچه انجام مي‌دهيد بيناست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4631, N'ق، سوگند به قرآن مجيد (که قيامت و رستاخيز حق است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4632, N'آنها تعجب کردند که پيامبري انذارگر از ميان خودشان آمده؛ و کافران گفتند: «اين چيز عجيبي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4633, N'آيا هنگامي که مرديم و خاک شديم (دوباره به زندگي بازمي‌گرديم)؟! اين بازگشتي بعيد است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4634, N'ولي ما مي‌دانيم آنچه را زمين از بدن آنها مي‌کاهد؛ و نزد ما کتابي است که همه چيز در آن محفوظ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4635, N'آنها حق را هنگامي که به سراغشان آمد تکذيب کردند؛ از اين رو پيوسته در کار پراکنده خود متحيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4636, N'آيا آنان به آسمان بالاي سرشان نگاه نکردند که چگونه ما آن را بنا کرده‌ايم، و چگونه آن را (بوسيله ستارگان) زينت بخشيده‌ايم و هيچ شکاف و شکستي در آن نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4637, N'و زمين را گسترش داديم و در آن کوه‌هائي عظيم و استوار افکنديم و از هر نوع گياه بهجت‌انگيز در آن رويانديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4638, N'تا وسيله بينايي و يادآوري براي هر بنده توبه کاري باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4639, N'و از آسمان، آبي پربرکت نازل کرديم، و بوسيله آن باغها و دانه‌هايي را که درو مي‌کند رويانديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4640, N'و نخلهاي بلندقامت که ميوه‌هاي متراکم دارند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4641, N'همه اينها براي روزي‌بخشيدن به بندگان است و بوسيله باران سرزمين مرده را زنده کرديم؛ (آري) زنده‌شدن مردگان نيز همين گونه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4642, N'پيش از آنان قوم نوح و «اصحاب الرس» [= قومي که در يمامه زندگي مي‌کردند و پيامبري به نام حنظله داشتند] و قوم ثمود (پيامبرانشان را) تکذيب کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4643, N'و همچنين قوم عاد و فرعون و قوم لوط،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4644, N'و «اصحاب الايکه» [= قوم شعيب‌] و قوم تبع (که در سرزمين يمن زندگي مي‌کردند)، هر يک از آنها فرستادگان الهي را تکذيب کردند و وعده عذاب درباره آنان تحقق يافت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4645, N'آيا ما از آفرينش نخستين عاجز مانديم (که قادر بر آفرينش رستاخيز نباشيم)؟! ولي آنها (با اين همه دلايل روشن) باز در آفرينش جديد ترديد دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4646, N'ما انسان را آفريديم و وسوسه‌هاي نفس او را مي‌دانيم، و ما به او از رگ قلبش نزديکتريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4647, N'(به خاطر بياوريد) هنگامي را که دو فرشته راست و چپ که ملازم انسانند اعمال او را دريافت مي‌دارند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4648, N'انسان هيچ سخني را بر زبان نمي‌آورد مگر اينکه همان دم، فرشته‌اي مراقب و آماده براي انجام مأموريت (و ضبط آن) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4649, N'و سرانجام، سکرات (و بيخودي در آستانه) مرگ بحق فرامي‌رسد (و به انسان گفته مي‌شود:) اين همان چيزي است که تو از آن مي‌گريختي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4650, N'و در «صور» دميده مي‌شود؛ آن روز، روز تحقق وعده وحشتناک است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4651, N'هر انساني وارد محشر مي‌گردد در حالي که همراه او حرکت دهنده و گواهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4652, N'(به او خطاب مي‌شود:) تو از اين صحنه (و دادگاه بزرگ) غافل بودي و ما پرده را از چشم تو کنار زديم، و امروز چشمت کاملا تيزبين است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4653, N'فرشته همنشين او مي‌گويد: «اين نامه اعمال اوست که نزد من حاضر و آماده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4654, N'(خداوند فرمان مي‌دهد:) هر کافر متکبر لجوج را در جهنم افکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4655, N'آن کسي که به شدت مانع خير و متجاوز و در شک و ترديد است (حتي ديگران را به ترديد مي افکند)؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4656, N'همان کسي که معبود ديگري با خدا قرار داده، (آري) او را در عذاب شديد بيفکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4657, N'و همنشينش (از شياطين) مي‌گويد: «پروردگارا! من او را به طغيان وانداشتم، لکن او خود در گمراهي دور و درازي بود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4658, N'(خداوند) مي‌گويد: «نزد من جدال و مخاصمه نکنيد؛ من پيشتر به شما هشدار داده‌ام (و اتمام حجت کرده‌ام)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4659, N'سخن من تغيير ناپذير است، و من هرگز به بندگان ستم نخواهم کرد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4660, N'(به خاطر بياوريد) روزي را که به جهنم مي‌گوييم: «آيا پر شده‌اي؟» و او مي‌گويد: «آيا افزون بر اين هم هست؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4661, N'(در آن روز) بهشت را به پرهيزگاران نزديک مي‌کنند، و فاصله‌اي از آنان ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4662, N'اين چيزي است که به شما وعده داده مي‌شود، و براي کساني است که بسوي خدا بازمي‌گردند و پيمانها و احکام او را حفظ مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4663, N'آن کس که از خداوند رحمان در نهان بترسد و با قلبي پرانابه در محضر او حاضر شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4664, N'(به آنان گفته مي‌شود:) بسلامت وارد بهشت شويد، امروز روز جاودانگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4665, N'هر چه بخواهند در آنجا براي آنها هست، و نزد ما نعمتهاي بيشتري است (که به فکر هيچ کس نمي‌رسد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4666, N'چه بسيار اقوامي را که پيش از آنها هلاک کرديم، اقوامي که از آنان قويتر بودند و شهرها (و کشورها) را گشودند؛ آيا راه فراري (از عذاب الهي) وجود دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4667, N'در اين تذکري است براي آن کس که عقل دارد، يا گوش دل فرادهد در حالي که حاضر باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4668, N'ما آسمانها و زمين و آنچه را در ميان آنهاست در شش روز [= شش دوران‌] آفريديم، و هيچ گونه رنج و سختي به ما نرسيد! (با اين حال چگونه زنده‌کردن مردگان براي ما مشکل است؟!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4669, N'در برابر آنچه آنها مي‌گويند شکيبا باش، و پيش از طلوع آفتاب و پيش از غروب تسبيح و حمد پروردگارت را بجا آور،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4670, N'و در بخشي از شب او را تسبيح کن، و بعد از سجده‌ها!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4671, N'و گوش فرا ده و منتظر روزي باش که منادي از مکاني نزديک ندا مي‌دهد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4672, N'روزي که همگان صيحه رستاخيز را بحق مي‌شنوند؛ آن روز، روز خروج (از قبرها) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4673, N'ماييم که زنده مي‌کنيم و مي‌ميرانيم، و بازگشت تنها بسوي ماست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4674, N'روزي که زمين به سرعت از روي آنها شکافته مي‌شود و (از قبرها) خارج مي‌گردند؛ و اين جمع کردن براي ما آسان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4675, N'ما به آنچه آنها مي‌گويند آگاهتريم، و تو مأمور به اجبار آنها (به ايمان) نيستي؛ پس بوسيله قرآن، کساني را که از عذاب من مي‌ترسند متذکر ساز (وظيفه تو همين است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4676, N'سوگند به بادهايي که (ابرها را) به حرکت درمي‌آورند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4677, N'سوگند به آن ابرها که بار سنگيني (از باران را) با خود حمل مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4678, N'و سوگند به کشتيهايي که به آساني به حرکت درمي‌آيند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4679, N'و سوگند به فرشتگاني که کارها را تقسيم مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4680, N'(آري سوگند به همه اينها) که آنچه به شما وعده شده قطعا راست است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4681, N'و بي‌شک (رستاخيز) و جزاي اعمال واقع‌شدني است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4682, N'قسم به آسمان که داراي چين و شکنهاي زيباست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4683, N'که شما (درباره قيامت) در گفتاري مختلف و گوناگونيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4684, N'(تنها) کسي از ايمان به آن منحرف مي‌شود که از قبول حق سرباز مي‌زند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4685, N'کشته باد دروغگويان (و مرگ بر آنها)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4686, N'همانها که در جهل و غفلت فرو رفته‌اند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4687, N'و پيوسته سؤال مي‌کنند: «روز جزا چه موقع است؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4688, N'(آري) همان روزي است که آنها را بر آتش مي‌سوزانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4689, N'(و گفته مي‌شود:) بچشيد عذاب خود را، اين همان چيزي است که براي آن شتاب داشتيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4690, N'به يقين، پرهيزگاران در باغهاي بهشت و در ميان چشمه‌ها قرار دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4691, N'و آنچه پروردگارشان به آنها بخشيده دريافت مي‌دارند، زيرا پيش از آن (در سراي دنيا) از نيکوکاران بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4692, N'آنها کمي از شب را مي‌خوابيدند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4693, N'و در سحرگاهان استغفار مي‌کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4694, N'و در اموال آنها حقي براي سائل و محروم بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4695, N'و در زمين آياتي براي جويندگان يقين است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4696, N'و در وجود خود شما (نيز آياتي است)؛ آيا نمي‌بينيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4697, N'و روزي شما در آسمان است و آنچه به شما وعده داده مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4698, N'سوگند به پروردگار آسمان و زمين که اين مطلب حق است همان گونه که شما سخن مي‌گوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4699, N'آيا خبر مهمانهاي بزرگوار ابراهيم به تو رسيده است؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4700, N'در آن زمان که بر او وارد شدند و گفتند: «سلام بر تو!» او گفت: «سلام بر شما که جمعيتي ناشناخته‌ايد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4701, N'سپس پنهاني به سوي خانواده خود رفت و گوساله فربه (و بريان شده‌اي را براي آنها) آورد،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4702, N'و نزديک آنها گذارد، (ولي با تعجب ديد دست بسوي غذا نمي‌برند) گفت: «آيا شما غذا نمي‌خوريد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4703, N'و از آنها احساس وحشت کرد، گفتند: «نترس (ما رسولان و فرشتگان پروردگار توايم)!» و او را بشارت به تولد پسري دانا دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4704, N'در اين هنگام همسرش جلو آمد در حالي که (از خوشحالي و تعجب) فرياد مي‌کشيد به صورت خود زد و گفت: «(آيا پسري خواهم آورد در حالي که) پيرزني نازا هستم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4705, N'گفتند: «پروردگارت چنين گفته است، و او حکيم و داناست!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4706, N'(ابراهيم) گفت: «مأموريت شما چيست اي فرستادگان (خدا)؟»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4707, N'گفتند: «ما به سوي قوم مجرمي فرستاده شده‌ايم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4708, N'تا باراني از «سنگ - گل» بر آنها بفرستيم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4709, N'سنگهايي که از ناحيه پروردگارت براي اسرافکاران نشان گذاشته شده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4710, N'ما مؤمناني را که در آن شهرها(ي قوم لوط) زندگي مي‌کردند (قبل از نزول عذاب) خارج کرديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4711, N'ولي جز يک خانواده باايمان در تمام آنها نيافتيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4712, N'و در آن (شهرهاي بلا ديده) نشانه‌اي روشن براي کساني که از عذاب دردناک مي‌ترسند به جاي گذارديم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4713, N'و در (زندگي) موسي نيز (نشانه و درس عبرتي بود) هنگامي که او را با دليلي آشکار به سوي فرعون فرستاديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4714, N'اما او با تمام وجودش از وي روي برتافت و گفت: «اين مرد يا ساحر است يا ديوانه!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4715, N'از اين رو ما او و لشکريانش را گرفتيم و به دريا افکنديم در حالي که در خور سرزنش بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4716, N'و (همچنين) در سرگذشت «عاد» (آيتي است) در آن هنگام که تندبادي بي‌باران بر آنها فرستاديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4717, N'که بر هيچ چيز نمي‌گذشت مگر اينکه آن را همچون استخوانهاي پوسيده مي‌ساخت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4718, N'و نيز در سرگذشت قوم «ثمود» عبرتي است در آن هنگام که به آنان گفته شد: «مدتي کوتاه بهره‌مند باشيد (و سپس منتظر عذاب)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4719, N'آنها از فرمان پروردگارشان سرباز زدند، و صاعقه آنان را فراگرفت در حالي که (خيره خيره) نگاه مي‌کردند (بي‌آنکه قدرت دفاع داشته باشند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4720, N'چنان بر زمين افتادند که توان برخاستن نداشتند و نتوانستند از کسي ياري طلبند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4721, N'همچنين قوم نوح را پيش از آنها هلاک کرديم، چرا که قوم فاسقي بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4722, N'و ما آسمان را با قدرت بنا کرديم، و همواره آن را وسعت مي‌بخشيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4723, N'و زمين را گسترديم، و چه خوب گستراننده‌اي هستيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4724, N'و از هر چيز دو جفت آفريديم، شايد متذکر شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4725, N'پس به سوي خدا بگريزيد، که من از سوي او براي شما بيم‌دهنده‌اي آشکارم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4726, N'و با خدا معبود ديگري قرار ندهيد، که من براي شما از سوي او بيم‌دهنده‌اي آشکارم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4727, N'اين گونه است که هيچ پيامبري قبل از اينها بسوي قومي فرستاده نشد مگر اينکه گفتند: «او ساحر است يا ديوانه!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4728, N'آيا يکديگر را به آن سفارش مي‌کردند (که همه چنين تهمتي بزنند)؟! نه، بلکه آنها قومي طغيانگرند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4729, N'حال که چنين است از آنها روي بگردان که هرگز در خور ملامت نخواهي بود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4730, N'و پيوسته تذکر ده، زيرا تذکر مؤمنان را سود مي‌بخشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4731, N'من جن و انس را نيافريدم جز براي اينکه عبادتم کنند (و از اين راه تکامل يابند و به من نزديک شوند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4732, N'هرگز از آنها روزي نمي‌خواهم، و نمي‌خواهم مرا اطعام کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4733, N'خداوند روزي‌دهنده و صاحب قوت و قدرت است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4734, N'و براي کساني که ستم کردند، سهم بزرگي از عذاب است همانند سهم يارانشان (از اقوام ستمگر پيشين)؛ بنابر اين عجله نکنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4735, N'پس واي بر کساني که کافر شدند از روزي که به آنها وعده داده مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4736, N'سوگند به کوه طور،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4737, N'و کتابي که نوشته شده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4738, N'در صفحه‌اي گسترده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4739, N'و سوگند به «بيت المعمور»،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4740, N'و سقف برافراشته،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4741, N'و درياي مملو و برافروخته،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4742, N'که عذاب پروردگارت واقع مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4743, N'و چيزي از آن مانع نخواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4744, N'(اين عذاب الهي) در آن روزي است که آسمان به شدت به حرکت درمي‌آيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4745, N'و کوه‌ها از جا کنده و متحرک مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4746, N'واي در آن روز بر تکذيب‌کنندگان،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4747, N'همانها که در سخنان باطل به بازي مشغولند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4748, N'در آن روز که آنها را بزور به سوي آتش دوزخ مي‌رانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4749, N'(به آنها مي‌گويند:) اين همان آتشي است که آن را انکار مي‌کرديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4750, N'آيا اين سحر است يا شما نمي‌بينيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4751, N'در آن وارد شويد و بسوزيد؛ مي‌خواهيد صبر کنيد يا نکنيد، براي شما يکسان است؛ چرا که تنها به اعمالتان جزا داده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4752, N'ولي پرهيزگاران در ميان باغهاي بهشت و نعمتهاي فراوان جاي دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4753, N'و از آنچه پروردگارشان به آنها داده و آنان را از عذاب دوزخ نگاه داشته است شاد و مسرورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4754, N'(به آنها گفته مي‌شود:) بخوريد و بياشاميد گوارا؛ اينها در برابر اعمالي است که انجام مي‌داديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4755, N'اين در حالي که بر تختهاي صف‌کشيده در کنار هم تکيه مي‌زنند، و «حور العين» را به همسري آنها درمي‌آوريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4756, N'کساني که ايمان آوردند و فرزندانشان به پيروي از آنان ايمان اختيار کردند، فرزندانشان را (در بهشت) به آنان ملحق مي‌کنيم؛ و از (پاداش) عملشان چيزي نمي‌کاهيم؛ و هر کس در گرو اعمال خويش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4757, N'و همواره از انواع ميوه‌ها و گوشتها -از هر نوع که بخواهند- در اختيارشان مي‌گذاريم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4758, N'آنها در بهشت جامهاي پر از شراب طهور را که نه بيهوده‌گويي در آن است و نه گناه، از يکديگر مي‌گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4759, N'و پيوسته بر گردشان نوجواناني براي (خدمت) آنان گردش مي‌کنند که همچون مرواريدهاي درون صدفند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4760, N'در اين هنگام رو به يکديگر کرده (از گذشته) سؤال مي‌نمايند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4761, N'مي‌گويند: «ما در ميان خانواده خود ترسان بوديم (مبادا گناهان آنها دامن ما را بگيرد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4762, N'اما خداوند بر ما منت نهاد و از عذاب‌کشنده ما را حفظ کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4763, N'ما از پيش او را مي‌خوانديم (و مي‌پرستيديم)، که اوست نيکوکار و مهربان!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4764, N'پس تذکر ده، که به لطف پروردگارت تو کاهن و مجنون نيستي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4765, N'بلکه آنها مي‌گويند: «او شاعري است که ما انتظار مرگش را مي‌کشيم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4766, N'بگو: «انتظار بکشيد که من هم با (شما انتظار مي‌کشم شما انتظار مرگ مرا، و من انتظار نابودي شما را با عذاب الهي)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4767, N'آيا عقلهايشان آنها را به اين اعمال دستور مي‌دهد، يا قومي طغيانگرند؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4768, N'يا مي‌گويند: «قرآن را به خدا افترا بسته»، ولي آنان ايمان ندارند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4769, N'اگر راست مي‌گويند سخني همانند آن بياورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4770, N'يا آنها بي هيچ آفريده شده‌اند، يا خود خالق خويشند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4771, N'آيا آنها آسمانها و زمين را آفريده‌اند؟! بلکه آنها جوياي يقين نيستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4772, N'آيا خزاين پروردگارت نزد آنهاست؟! يا بر همه چيز عالم سيطره دارند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4773, N'آيا نردباني دارند (که به آسمان بالا مي‌روند) و بوسيله آن اسرار وحي را مي‌شنوند؟! کسي که از آنها اين ادعا را دارد دليل روشني بياورد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4774, N'آيا سهم خدا دختران است و سهم شما پسران (که فرشتگان را دختران خدا مي‌ناميد)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4775, N'آيا تو از آنها پاداشي مي‌طلبي که در زير بار گران آن قرار دارند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4776, N'آيا اسرار غيب نزد آنهاست و از روي آن مي‌نويسند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4777, N'آيا مي‌خواهند نقشه شيطاني براي تو بکشند؟! ولي بدانند خود کافران در دام اين نقشه‌ها گرفتار مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4778, N'يا معبودي غير خداوند دارند (که قول ياري به آنها داده)؟! منزه است خدا از آنچه همتاي او قرارمي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4779, N'آنها (چنان لجوجند که) اگر ببينند قطعه سنگي از آسمان (براي عذابشان) سقوط مي‌کند مي‌گويند: «اين ابر متراکمي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4780, N'حال که چنين است آنها را رها کن تا روز مرگ خود را ملاقات کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4781, N'روزي که نقشه‌هاي آنان سودي به حالشان نخواهد داشت و (از هيچ سو) ياري نمي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4782, N'و براي ستمگران عذابي قبل از آن است (در همين جهان)؛ ولي بيشترشان نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4783, N'در راه ابلاغ حکم پروردگارت صبر و استقامت کن، چرا که تو در حفاظت کامل ما قرار داري! و هنگامي که برمي‌خيزي پروردگارت را تسبيح و حمد گوي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4784, N'(همچنين) به هنگام شب او را تسبيح کن و به هنگام پشت کردن ستارگان (و طلوع صبح)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4785, N'سوگند به ستاره هنگامي که افول مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4786, N'که هرگز دوست شما [= محمد «ص»] منحرف نشده و مقصد را گم نکرده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4787, N'و هرگز از روي هواي نفس سخن نمي‌گويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4788, N'آنچه مي‌گويد چيزي جز وحي که بر او نازل شده نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4789, N'آن کس که قدرت عظيمي دارد [= جبرئيل امين‌] او را تعليم داده است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4790, N'همان کس که توانايي فوق العاده دارد؛ او سلطه يافت...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4791, N'در حالي که در افق اعلي قرار داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4792, N'سپس نزديکتر و نزديکتر شد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4793, N'تا آنکه فاصله او (با پيامبر) به اندازه فاصله دو کمان يا کمتر بود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4794, N'در اينجا خداوند آنچه را وحي کردني بود به بنده‌اش وحي نمود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4795, N'قلب (پاک او) در آنچه ديد هرگز دروغ نگفت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4796, N'آيا با او درباره آنچه (با چشم خود) ديده مجادله مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4797, N'و بار ديگر نيز او را مشاهده کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4798, N'نزد «سدرة المنتهي»،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4799, N'که «جنت المأوي» در آنجاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4800, N'در آن هنگام که چيزي [= نور خيره‌کننده‌اي‌] سدرة المنتهي را پوشانده بود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4801, N'چشم او هرگز منحرف نشد و طغيان نکرد (آنچه ديد واقعيت بود)!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4802, N'او پاره‌اي از آيات و نشانه‌هاي بزرگ پروردگارش را ديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4803, N'به من خبر دهيد آيا بتهاي «لات» و «عزي»...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4804, N'و «منات» که سومين آنهاست (دختران خدا هستند)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4805, N'آيا سهم شما پسر است و سهم او دختر؟! (در حالي که بزعم شما دختران کم ارزش‌ترند!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4806, N'در اين صورت اين تقسيمي ناعادلانه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4807, N'اينها فقط نامهايي است که شما و پدرانتان بر آنها گذاشته‌ايد (نامهايي بي‌محتوا و اسمهايي بي مسما)، و هرگز خداوند دليل و حجتي بر آن نازل نکرده؛ آنان فقط از گمانهاي بي‌اساس و هواي نفس پيروي مي‌کنند در حالي که هدايت از سوي پروردگارشان براي آنها آمده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4808, N'يا آنچه انسان تمنا دارد به آن مي‌رسد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4809, N'در حالي که آخرت و دنيا از آن خداست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4810, N'و چه بسيار فرشتگان آسمانها که شفاعت آنها سودي نمي‌بخشد مگر پس از آنکه خدا براي هر کس بخواهد و راضي باشد اجازه (شفاعت) دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4811, N'کساني که به آخرت ايمان ندارند، فرشتگان را دختر (خدا) نامگذاري مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4812, N'آنها هرگز به اين سخن دانشي ندارند، تنها از گمان بي‌پايه پيروي مي‌کنند با اينکه «گمان» هرگز انسان را از حق بي‌نياز نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4813, N'حال که چنين است از کسي که از ياد ما روي مي‌گرداند و جز زندگي مادي دنيا را نمي‌طلبد، اعراض کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4814, N'اين آخرين حد آگاهي آنهاست؛ پروردگار تو کساني را که از راه او گمراه شده‌اند بهتر مي‌شناسد، و (همچنين) هدايت‌يافتگان را از همه بهتر مي‌شناسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4815, N'و براي خداست آنچه در آسمانها و آنچه در زمين است تا بدکاران را به کيفر کارهاي بدشان برساند و نيکوکاران را در برابر اعمال نيکشان پاداش دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4816, N'همانها که از گناهان بزرگ و اعمال زشت دوري مي‌کنند، جز گناهان صغيره (که گاه آلوده آن مي‌شوند)؛ آمرزش پروردگار تو گسترده است؛ او نسبت به شما از همه آگاهتر است از آن هنگام که شما را از زمين آفريد و در آن موقع که بصورت جنينهايي در شکم مادرانتان بوديد؛ پس خودستايي نکنيد، او پرهيزگاران را بهتر مي‌شناسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4817, N'آيا ديدي آن کس را که (از اسلام -يا انفاق-) روي گردان شد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4818, N'و کمي عطا کرد، و از بيشتر امساک نمود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4819, N'آيا نزد او علم غيب است و مي‌بيند (که ديگران مي‌توانند گناهان او را بر دوش گيرند)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4820, N'يا از آنچه در کتب موسي نازل گرديده با خبر نشده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4821, N'و در کتب ابراهيم، همان کسي که وظيفه خود را بطور کامل ادا کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4822, N'که هيچ کس بار گناه ديگري را بر دوش نمي‌گيرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4823, N'و اينکه براي انسان بهره‌اي جز سعي و کوشش او نيست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4824, N'و اينکه تلاش او بزودي ديده مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4825, N'سپس به او جزاي کافي داده خواهد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4826, N'(و آيا از کتب پيشين انبيا به او نرسيده است) که همه امور به پروردگارت منتهي مي‌گردد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4827, N'و اينکه اوست که خنداند و گرياند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4828, N'و اوست که ميراند و زنده کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4829, N'و اوست که دو زوج نر و ماده را آفريد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4830, N'از نطفه‌اي هنگامي که خارج مي‌شود (و در رحم مي‌ريزد)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4831, N'و اينکه بر خداست ايجاد عالم ديگر (تا عدالت اجرا گردد)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4832, N'و اينکه اوست که بي‌نياز کرد و سرمايه باقي بخشيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4833, N'و اينکه اوست پروردگار ستاره «شعرا»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4834, N'(و آيا به انسان نرسيده است که در کتب انبياي پيشين آمده) که خداوند قوم «عاد نخستين» را هلاک کرد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4835, N'و همچنين قوم «ثمود» را، و کسي از آنان را باقي نگذارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4836, N'و نيز قوم نوح را پيش از آنها، چرا که آنان از همه ظالمتر و طغيانگرتر بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4837, N'و نيز شهرهاي زير و رو شده (قوم لوط) را فرو کوبيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4838, N'سپس آنها را با عذاب سنگين پوشانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4839, N'(بگو:) در کدام يک از نعمتهاي پروردگارت ترديد داري؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4840, N'اين (پيامبر) بيم‌دهنده‌اي از بيم‌دهندگان پيشين است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4841, N'آنچه بايد نزديک شود، نزديک شده است (و قيامت فرامي‌رسد)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4842, N'و هيچ کس جز خدا نمي‌تواند سختيهاي آن را برطرف سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4843, N'آيا از اين سخن تعجب مي‌کنيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4844, N'و مي‌خنديد و نمي‌گرييد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4845, N'و پيوسته در غفلت و هوسراني به سر مي‌بريد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4846, N'حال که چنين است همه براي خدا سجده کنيد و او را بپرستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4847, N'قيامت نزديک شد و ماه از هم شکافت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4848, N'و هرگاه نشانه و معجزه‌اي را ببينند روي گردانده، مي‌گويند: «اين سحري مستمر است»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4849, N'آنها (آيات خدا را) تکذيب کردند و از هواي نفسشان پيروي نمودند؛ و هر امري قرارگاهي دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4850, N'به اندازه کافي براي بازداشتن از بديها اخبار (انبيا و امتهاي پيشين) به آنان رسيده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4851, N'اين آيات، حکمت بالغه الهي است؛ اما انذارها (براي افراد لجوج) فايده نمي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4852, N'بنابر اين از آنها روي بگردان، و روزي را به ياد آور که دعوت کننده الهي مردم را به امر وحشتناکي دعوت مي‌کند (دعوت به حساب اعمال)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4853, N'آنان در حالي که چشمهايشان از شدت وحشت به زير افتاده، همچون ملخهاي پراکنده از قبرها خارج مي‌شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4854, N'در حالي که (بر اثر وحشت و اضطراب) بسوي اين دعوت کننده گردن مي‌کشند؛ کافران مي‌گويند: «امروز روز سخت و دردناکي است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4855, N'پيش از آنها قوم نوح تکذيب کردند، (آري) بنده ما (نوح) را تکذيب کرده و گفتند: «او ديوانه است!» و (با انواع آزارها از ادامه رسالتش) بازداشته شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4856, N'او به درگاه پروردگار عرضه داشت: «من مغلوب (اين قوم طغيانگر) شده‌ام، انتقام مرا از آنها بگير!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4857, N'در اين هنگام درهاي آسمان را با آبي فراوان و پي‌درپي گشوديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4858, N'و زمين را شکافتيم و چشمه‌هاي زيادي بيرون فرستاديم؛ و اين دو آب به اندازه مقدر با هم درآميختند (و درياي وحشتناکي شد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4859, N'و او را بر مرکبي از الواح و ميخهايي ساخته شده سوار کرديم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4860, N'مرکبي که زير نظر ما حرکت مي‌کرد! اين کيفري بود براي کساني که (به او) کافر شده بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4861, N'ما اين ماجرا را بعنوان نشانه‌اي در ميان امتها باقي گذارديم؛ آيا کسي هست که پند گيرد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4862, N'(اکنون بنگريد) عذاب و انذارهاي من چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4863, N'ما قرآن را براي تذکر آسان ساختيم؛ آيا کسي هست که متذکر شود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4864, N'قوم عاد (نيز پيامبر خود را) تکذيب کردند؛ پس (ببينيد) عذاب و انذارهاي من چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4865, N'ما تندباد وحشتناک و سردي را در يک روز شوم مستمر بر آنان فرستاديم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4866, N'که مردم را همچون تنه‌هاي نخل ريشه‌کن شده از جا برمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4867, N'پس (ببينيد) عذاب و انذارهاي من چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4868, N'ما قرآن را براي تذکر آسان ساختيم؛ آيا کسي هست که متذکر شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4869, N'طايفه ثمود (نيز) انذارهاي الهي را تکذيب کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4870, N'و گفتند: «آيا ما از بشري از جنس خود پيروي کنيم؟! اگر چنين کنيم در گمراهي و جنون خواهيم بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4871, N'آيا از ميان ما تنها بر او وحي نازل شده؟! نه، او آدم بسيار دروغگوي هوسبازي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4872, N'ولي فردا مي‌فهمند چه کسي دروغگوي هوسباز است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4873, N'ما «ناقه» را براي آزمايش آنها مي‌فرستيم؛ در انتظار پايان کار آنان باش و صبر کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4874, N'و به آنها خبر ده که آب (قريه) بايد در ميانشان تقسيم شود، (يک روز سهم ناقه، و يک روز براي آنها) و هر يک در نوبت خود بايد حاضر شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4875, N'آنها يکي از ياران خود را صدا زدند، او به سراغ اين کار آمد و (ناقه را)پي کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4876, N'پس (بنگريد) عذاب و انذارهاي من چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4877, N'ما فقط يک صيحه [= صاعقه عظيم‌] بر آنها فرستاديم و بدنبال آن همگي بصورت گياه خشکي درآمدند که صاحب چهارپايان (در آغل) جمع‌آوري مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4878, N'ما قرآن را براي يادآوري آسان ساختيم؛ آيا کسي هست که متذکر شود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4879, N'قوم لوط انذارها(ي پي‌درپي پيامبرشان) را تکذيب کردند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4880, N'ما بر آنها تندبادي که ريگها را به حرکت درمي‌آورد فرستاديم (و همه را هلاک کرديم)، جز خاندان لوط را که سحرگاهان نجاتشان داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4881, N'اين نعمتي بود از ناحيه ما؛ اين گونه هر کسي را که شکر کند پاداش مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4882, N'او آنها را از مجازات ما بيم داد، ولي بر آنها اصرار بر مجادله و القاي شک داشتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4883, N'آنها از لوط خواستند ميهمانانش را در اختيارشان بگذارد؛ ولي ما چشمانشان را نابينا و محو کرديم (و گفتيم:) بچشيد عذاب و انذارهاي مرا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4884, N'سرانجام صبحگاهان و در اول روز عذابي پايدار و ثابت به سراغشان آمد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4885, N'(و گفتيم:) پس بچشيد عذاب و انذارهاي مرا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4886, N'ما قرآن را براي يادآوري آسان ساختيم؛ آيا کسي هست که متذکر شود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4887, N'و (همچنين) انذارها و هشدارها (يکي پس از ديگري) به سراغ آل فرعون آمد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4888, N'اما آنها همه آيات ما را تکذيب کردند، و ما آنها را گرفتيم و مجازات کرديم، گرفتن شخصي قدرتمند و توانا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4889, N'آيا کفار شما بهتر از آنانند يا براي شما امان‌نامه‌اي در کتب آسماني نازل شده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4890, N'يا مي‌گويند: «ما جماعتي متحد و نيرومند و پيروزيم»؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4891, N'(ولي بدانند) که بزودي جمعشان شکست مي‌خورد و پا به فرارمي‌گذارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4892, N'(علاوه بر اين) رستاخيز موعد آنهاست، و مجازات قيامت هولناکتر و تلختر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4893, N'مجرمان در گمراهي و شعله‌هاي آتشند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4894, N'در آن روز که در آتش دوزخ به صورتشان کشيده مي‌شوند (و به آنها گفته مي‌شود:) بچشيد آتش دوزخ را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4895, N'البته ما هر چيز را به اندازه آفريديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4896, N'و فرمان ما يک امر بيش نيست، همچون يک چشم بر هم زدن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4897, N'ما کساني را که در گذشته شبيه شما بودند هلاک کرديم؛ آيا کسي هست که پند گيرد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4898, N'و هر کاري را انجام دادند در نامه‌هاي اعمالشان ثبت است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4899, N'و هر کار کوچک و بزرگي نوشته شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4900, N'يقينا پرهيزگاران در باغها و نهرهاي بهشتي جاي دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4901, N'در جايگاه صدق نزد خداوند مالک مقتدر!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4902, N'خداوند رحمان،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4903, N'قرآن را تعليم فرمود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4904, N'انسان را آفريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4905, N'و به او «بيان» را آموخت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4906, N'خورشيد و ماه با حساب منظمي مي‌گردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4907, N'و گياه و درخت براي او سجده مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4908, N'و آسمان را برافراشت، و ميزان و قانون (در آن) گذاشت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4909, N'تا در ميزان طغيان نکنيد (و از مسير عدالت منحرف نشويد)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4910, N'و وزن را بر اساس عدل برپا داريد و ميزان را کم نگذاريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4911, N'زمين را براي خلايق آفريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4912, N'که در آن ميوه‌ها و نخلهاي پرشکوفه است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4913, N'و دانه‌هايي که همراه با ساقه و برگي است که بصورت کاه درمي‌آيد، و گياهان خوشبو!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4914, N'پس کدامين نعمتهاي پروردگارتان را تکذيب مي‌کنيد (شما اي گروه جن و انس)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4915, N'انسان را از گل خشکيده‌اي همچون سفال آفريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4916, N'و جن را از شعله‌هاي مختلط و متحرک آتش خلق کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4917, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4918, N'او پروردگار دو مشرق و پروردگار دو مغرب است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4919, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4920, N'دو درياي مختلف (شور و شيرين، گرم و سرد) را در کنار هم قرار داد، در حالي که با هم تماس دارند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4921, N'در ميان آن دو برزخي است که يکي بر ديگري غلبه نمي‌کند (و به هم نمي‌آميزند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4922, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4923, N'از آن دو، لؤلؤ و مرجان خارج مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4924, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4925, N'و براي اوست کشتيهاي ساخته شده که در دريا به حرکت درمي‌آيند و همچون کوهي هستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4926, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4927, N'همه کساني که روي آن [= زمين‌] هستند فاني مي‌شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4928, N'و تنها ذات ذوالجلال و گرامي پروردگارت باقي مي‌ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4929, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4930, N'تمام کساني که در آسمانها و زمين هستند از او تقاضا مي‌کنند، و او هر روز در شأن و کاري است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4931, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4932, N'بزودي به حساب شما مي‌پردازيم اي دو گروه انس و جن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4933, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4934, N'اي گروه جن و انس! اگر مي‌توانيد از مرزهاي آسمانها و زمين بگذريد، پس بگذريد، ولي هرگز نمي‌توانيد، مگر با نيرويي (فوق العاده)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4935, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4936, N'شعله‌هايي از آتش بي‌دود، و دودهايي متراکم بر شما فرستاده مي‌شود؛ و نمي‌توانيد از کسي ياري بطلبيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4937, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4938, N'در آن هنگام که آسمان شکافته شود و همچون روغن مذاب گلگون گردد (حوادث هولناکي رخ مي‌دهد که تاب تحمل آن را نخواهيد داشت)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4939, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4940, N'در آن روز هيچ کس از انس و جن از گناهش سؤال نمي‌شود (و همه چيز روشن است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4941, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4942, N'مجرمان از چهره‌هايشان شناخته مي‌شوند؛ و آنگاه آنها را از موهاي پيش سر، و پاهايشان مي‌گيرند (و به دوزخ مي‌افکنند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4943, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4944, N'اين همان دوزخي است که مجرمان آن را انکار مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4945, N'امروز در ميان آن و آب سوزان در رفت و آمدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4946, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4947, N'و براي کسي که از مقام پروردگارش بترسد، دو باغ بهشتي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4948, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4949, N'(آن دو باغ بهشتي) داراي انواع نعمتها و درختان پرطراوت است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4950, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4951, N'در آنها دو چشمه هميشه جاري است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4952, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4953, N'در آن دو، از هر ميوه‌اي دو نوع وجود دارد (هر يک از ديگري بهتر)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4954, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4955, N'اين در حالي است که آنها بر فرشهايي تکيه کرده‌اند با آسترهائي از ديبا و ابريشم، و ميوه‌هاي رسيده آن دو باغ بهشتي در دسترس است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4956, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4957, N'در آن باغهاي بهشتي زناني هستند که جز به همسران خود عشق نمي‌ورزند؛ و هيچ انس و جن پيش از اينها با آنان تماس نگرفته است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4958, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4959, N'آنها همچون ياقوت و مرجانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4960, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4961, N'آيا جزاي نيکي جز نيکي است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4962, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4963, N'و پايين تر از آنها، دو باغ بهشتي ديگر است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4964, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4965, N'هر دو خرم و سرسبزند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4966, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4967, N'در آنها دو چشمه جوشنده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4968, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4969, N'در آنها ميوه‌هاي فراوان و درخت خرما و انار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4970, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4971, N'و در آن باغهاي بهشتي زناني نيکو خلق و زيبايند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4972, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4973, N'حورياني که در خيمه‌هاي بهشتي مستورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4974, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4975, N'هيچ انس و جن پيش از ايشان با آنها تماس نگرفته (و دوشيزه‌اند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4976, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4977, N'اين در حالي است که بهشتيان بر تختهايي تکيه زده‌اند که با بهترين و زيباترين پارچه‌هاي سبزرنگ پوشانده شده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4978, N'پس کدامين نعمتهاي پروردگارتان را انکار مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4979, N'پربرکت و زوال‌ناپذير است نام پروردگار صاحب جلال و بزرگوار تو!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4980, N'هنگامي که واقعه عظيم (قيامت) واقع شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4981, N'هيچ کس نمي‌تواند آن را انکار کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4982, N'(اين واقعه) گروهي را پايين مي‌آورد و گروهي را بالا مي‌برد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4983, N'در آن هنگام که زمين بشدت به لرزه درمي‌آيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4984, N'و کوه‌ها در هم کوبيده مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4985, N'و بصورت غبار پراکنده درمي‌آيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4986, N'و شما سه گروه خواهيد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4987, N'(نخست) سعادتمندان و خجستگان (هستند)؛ چه سعادتمندان و خجستگاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4988, N'گروه ديگر شقاوتمندان و شومانند، چه شقاوتمندان و شوماني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4989, N'و (سومين گروه) پيشگامان پيشگامند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4990, N'آنها مقربانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4991, N'در باغهاي پرنعمت بهشت (جاي دارند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4992, N'گروه زيادي (از آنها) از امتهاي نخستينند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4993, N'و اندکي از امت آخرين!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4994, N'آنها [= مقربان‌] بر تختهايي که صف‌کشيده و به هم پيوسته است قراردارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4995, N'در حالي که بر آن تکيه زده و رو به روي يکديگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4996, N'نوجواناني جاودان (در شکوه و طراوت) پيوسته گرداگرد آنان مي‌گردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4997, N'با قدحها و کوزه‌ها و جامهايي از نهرهاي جاري بهشتي (و شراب طهور)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4998, N'اما شرابي که از آن درد سر نمي‌گيرند و نه مست مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (4999, N'و ميوه‌هايي از هر نوع که انتخاب کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5000, N'و گوشت پرنده از هر نوع که مايل باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5001, N'و همسراني از حور العين دارند،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5002, N'همچون مرواريد در صدف پنهان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5003, N'اينها پاداشي است در برابر اعمالي که انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5004, N'در آن (باغهاي بهشتي) نه لغو و بيهوده‌اي مي‌شنوند نه سخنان گناه آلود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5005, N'تنها چيزي که مي‌شنوند «سلام» است «سلام»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5006, N'و اصحاب يمين و خجستگان، چه اصحاب يمين و خجستگاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5007, N'آنها در سايه درختان «سدر» بي‌خار قرار دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5008, N'و در سايه درخت «طلح» پربرگ [= درختي خوشرنگ و خوشبو]،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5009, N'و سايه کشيده و گسترده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5010, N'و در کنار آبشارها،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5011, N'و ميوه‌هاي فراوان،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5012, N'که هرگز قطع و ممنوع نمي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5013, N'و همسراني بلندمرتبه!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5014, N'ما آنها را آفرينش نويني بخشيديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5015, N'و همه را دوشيزه قرار داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5016, N'زناني که تنها به همسرشان عشق مي‌ورزند و خوش زبان و فصيح و هم سن و سالند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5017, N'اينها همه براي اصحاب يمين است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5018, N'که گروهي از امتهاي نخستينند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5019, N'و گروهي از امتهاي آخرين!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5020, N'و اصحاب شمال، چه اصحاب شمالي (که نامه اعمالشان به نشانه جرمشان به دست چپ آنها داده مي‌شود)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5021, N'آنها در ميان بادهاي کشنده و آب سوزان قرار دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5022, N'و در سايه دودهاي متراکم و آتشزا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5023, N'سايه‌اي که نه خنک است و نه آرامبخش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5024, N'آنها پيش از اين (در عالم دنيا) مست و مغرور نعمت بودند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5025, N'و بر گناهان بزرگ اصرار مي‌ورزيدند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5026, N'و مي‌گفتند: «هنگامي که ما مرديم و خاک و استخوان شديم، آيا برانگيخته خواهيم شد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5027, N'يا نياکان نخستين ما (برانگيخته مي‌شوند)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5028, N'بگو: «اولين و آخرين،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5029, N'همگي در موعد روز معيني گردآوري مي‌شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5030, N'سپس شما اي گمراهان تکذيب‌کننده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5031, N'قطعا از درخت زقوم مي‌خوريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5032, N'و شکمها را از آن پر مي‌کنيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5033, N'و روي آن از آب سوزان مي‌نوشيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5034, N'و همچون شتران مبتلا به بيماري عطش، از آن مي‌آشاميد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5035, N'اين است وسيله پذيرايي از آنها در قيامت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5036, N'»ما شما را آفريديم؛ پس چرا (آفرينش مجدد را) تصديق نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5037, N'آيا از نطفه‌اي که در رحم مي‌ريزيد آگاهيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5038, N'آيا شما آن را (در دوران جنيني) آفرينش (پي در پي) مي‌دهيد يا ما آفريدگاريم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5039, N'ما در ميان شما مرگ را مقدر ساختيم؛ و هرگز کسي بر ما پيشي نمي‌گيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5040, N'تا گروهي را به جاي گروه ديگري بياوريم و شما را در جهاني که نمي‌دانيد آفرينش تازه‌اي بخشيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5041, N'شما عالم نخستين را دانستيد؛ چگونه متذکر نمي‌شويد (که جهاني بعد از آن است)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5042, N'آيا هيچ درباره آنچه کشت مي‌کنيد انديشيده‌ايد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5043, N'آيا شما آن را مي‌رويانيد يا ما مي‌رويانيم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5044, N'هرگاه بخواهيم آن را مبدل به کاه در هم کوبيده مي‌کنيم که تعجب کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5045, N'(بگونه‌اي که بگوييد:) براستي ما زيان کرده‌ايم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5046, N'بلکه ما بکلي محروميم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5047, N'آيا به آبي که مي‌نوشيد انديشيده‌ايد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5048, N'آيا شما آن را از ابر نازل کرده‌ايد يا ما نازل مي‌کنيم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5049, N'هرگاه بخواهيم، اين آب گوارا را تلخ و شور قرار مي‌دهيم؛ پس چرا شکر نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5050, N'آيا درباره آتشي که مي‌افروزيد فکر کرده‌ايد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5051, N'آيا شما درخت آن را آفريده‌ايد يا ما آفريده‌ايم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5052, N'ما آن را وسيله يادآوري (براي همگان) و وسيله زندگي براي مسافران قرار داده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5053, N'حال که چنين است به نام پروردگار بزرگت تسبيح کن (و او را پاک و منزه بشمار)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5054, N'سوگند به جايگاه ستارگان (و محل طلوع و غروب آنها)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5055, N'و اين سوگندي است بسيار بزرگ، اگر بدانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5056, N'که آن، قرآن کريمي است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5057, N'که در کتاب محفوظي جاي دارد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5058, N'و جز پاکان نمي‌توانند به آن دست زنند [= دست يابند].')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5059, N'آن از سوي پروردگار عالميان نازل شده؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5060, N'آيا اين سخن را [= اين قرآن را با اوصافي که گفته شد] سست و کوچک مي‌شمريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5061, N'و به جاي شکر روزيهايي که به شما داده شده آن را تکذيب مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5062, N'پس چرا هنگامي که جان به گلوگاه مي‌رسد (توانايي بازگرداندن آن را نداريد)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5063, N'و شما در اين حال نظاره مي‌کنيد (و کاري از دستتان ساخته نيست)؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5064, N'و ما از شما به او نزديکتريم ولي نمي‌بينيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5065, N'اگر هرگز در برابر اعمالتان جزا داده نمي‌شويد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5066, N'پس آن (روح) را بازگردانيد اگر راست مي‌گوييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5067, N'پس اگر او از مقربان باشد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5068, N'در روح و ريحان و بهشت پرنعمت است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5069, N'اما اگر از اصحاب يمين باشد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5070, N'(به او گفته مي‌شود:) سلام بر تو از سوي دوستانت که از اصحاب يمينند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5071, N'اما اگر او از تکذيب‌کنندگان گمراه باشد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5072, N'با آب جوشان دوزخ از او پذيرايي مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5073, N'و سرنوشت او ورود در آتش جهنم است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5074, N'اين مطلب حق و يقين است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5075, N'پس به نام پروردگار بزرگت تسبيح کن (و او را منزه بشمار)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5076, N'آنچه در آسمانها و زمين است براي خدا تسبيح مي‌گويند؛ و او عزيز و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5077, N'مالکيت (و حاکميت) آسمانها و زمين از آن اوست؛ زنده مي‌کند و مي‌ميراند؛ و او بر هر چيز توانا است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5078, N'اول و آخر و پيدا و پنهان اوست؛ و او به هر چيز داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5079, N'او کسي است که آسمانها و زمين را در شش روز [= شش دوران‌] آفريد؛ سپس بر تخت قدرت قرار گرفت (و به تدبير جهان پرداخت)؛ آنچه را در زمين فرو مي‌رود مي‌داند، و آنچه را از آن خارج مي‌شود و آنچه از آسمان نازل مي‌گردد و آنچه به آسمان بالا مي‌رود؛ و هر جا باشيد او با شما است، و خداوند نسبت به آنچه انجام مي‌دهيد بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5080, N'مالکيت آسمانها و زمين از آن اوست؛ و همه کارها به سوي او بازمي‌گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5081, N'شب را در روز مي‌کند و روز را در شب؛ و او به آنچه در دل سينه‌ها وجود دارد داناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5082, N'به خدا و رسولش ايمان بياوريد و از آنچه شما را جانشين و نماينده (خود) در آن قرار داده انفاق کنيد؛ (زيرا) کساني که از شما ايمان بياورند و انفاق کنند، اجر بزرگي دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5083, N'چرا به خدا ايمان نياوريد در حالي که رسول (او) شما را مي‌خواند که به پروردگارتان ايمان بياوريد، و از شما پيمان گرفته است (پيماني از طريق فطرت و خرد)، اگر آماده ايمان‌آوردنيد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5084, N'او کسي است که آيات روشني بر بنده‌اش [= محمد] نازل مي‌کند تا شما را از تاريکيها به سوي نور برد؛ و خداوند نسبت به شما مهربان و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5085, N'چرا در راه خدا انفاق نکنيد در حالي که ميراث آسمانها و زمين همه از آن خداست (و کسي چيزي را با خود نمي‌برد)! کساني که قبل از پيروزي انفاق کردند و جنگيدند (با کساني که پس از پيروزي انفاق کردند) يکسان نيستند؛ آنها بلندمقامتر از کساني هستند که بعد از فتح انفاق نمودند و جهاد کردند؛ و خداوند به هر دو وعده نيک داده؛ و خدا به آنچه انجام مي‌دهيد آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5086, N'کيست که به خدا وام نيکو دهد (و از اموالي که به او ارزاني داشته انفاق کند) تا خداوند آن را براي او چندين برابر کند؟ و براي او پاداش پرارزشي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5087, N'(اين پاداش بزرگ) در روزي است که مردان و زنان باايمان را مي‌نگري که نورشان پيش‌رو و در سمت راستشان بسرعت حرکت مي‌کند (و به آنها مي‌گويند:) بشارت باد بر شما امروز به باغهايي از بهشت که نهرها زير (درختان) آن جاري است؛ جاودانه در آن خواهيد ماند! و اين همان رستگاري بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5088, N'روزي که مردان و زنان منافق به مؤمنان مي‌گويند: «نظري به ما بيفکنيد تا از نور شما پرتوي برگيريم!» به آنها گفته مي‌شود: «به پشت سر خود بازگرديد و کسب نور کنيد!» در اين هنگام ديواري ميان آنها زده مي‌شود که دري دارد، درونش رحمت است و برونش عذاب!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5089, N'آنها را صدا مي‌زنند: «مگر ما با شما نبوديم؟!» مي‌گويند: «آري، ولي شما خود را به هلاکت افکنديد و انتظار (مرگ پيامبر را) کشيديد، و (در همه چيز) شک و ترديد داشتيد، و آرزوهاي دور و دراز شما را فريب داد تا فرمان خدا فرا رسيد، و شيطان فريبکار شما را در برابر (فرمان) خداوند فريب داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5090, N'پس امروز نه از شما فديه‌اي پذيرفته مي‌شود، و نه از کافران؛ و جايگاهتان آتش است و همان سرپرستتان مي‌باشد؛ و چه بد جايگاهي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5091, N'آيا وقت آن نرسيده است که دلهاي مؤمنان در برابر ذکر خدا و آنچه از حق نازل کرده است خاشع گردد؟! و مانند کساني نباشند که در گذشته به آنها کتاب آسماني داده شد، سپس زماني طولاني بر آنها گذشت و قلبهايشان قساوت پيدا کرد؛ و بسياري از آنها گنهکارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5092, N'بدانيد خداوند زمين را بعد از مرگ آن زنده مي‌کند! ما آيات (خود) را براي شما بيان کرديم، شايد انديشه کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5093, N'مردان و زنان انفاق‌کننده، و آنها که (از اين راه) به خدا «قرض الحسنه» دهند، (اين قرض الحسنه) براي آنان مضاعف مي‌شود و پاداش پرارزشي دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5094, N'کساني که به خدا و رسولانش ايمان آوردند، آنها صديقين و شهدا نزد پروردگارشانند؛ براي آنان است پاداش (اعمال)شان و نور (ايمان)شان؛ و کساني که کافر شدند و آيات ما را تکذيب کردند، آنها دوزخيانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5095, N'بدانيد زندگي دنيا تنها بازي و سرگرمي و تجمل پرستي و فخرفروشي در ميان شما و افزون طلبي در اموال و فرزندان است، همانند باراني که محصولش کشاورزان را در شگفتي فرو مي‌برد، سپس خشک مي‌شود بگونه‌اي که آن را زردرنگ مي‌بيني؛ سپس تبديل به کاه مي‌شود! و در آخرت، عذاب شديد است يا مغفرت و رضاي الهي؛ و (به هر حال) زندگي دنيا چيزي جز متاع فريب نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5096, N'به پيش تازيد براي رسيدن به مغفرت پروردگارتان و بهشتي که پهنه آن مانند پهنه آسمان و زمين است و براي کساني که به خدا و رسولانش ايمان آورده‌اند؛ آماده شده است، اين فضل خداوند است که به هر کس بخواهد مي‌دهد؛ و خداوند صاحب فضل عظيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5097, N'هيچ مصيبتي (ناخواسته) در زمين و نه در وجود شما روي نمي‌دهد مگر اينکه همه آنها قبل از آنکه زمين را بيافرينيم در لوح محفوظ ثبت است؛ و اين امر براي خدا آسان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5098, N'اين بخاطر آن است که براي آنچه از دست داده‌ايد تأسف نخوريد، و به آنچه به شما داده است دلبسته و شادمان نباشيد؛ و خداوند هيچ متکبر فخرفروشي را دوست ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5099, N'همانها که بخل مي‌ورزند و مردم را به بخل دعوت مي‌کنند؛ و هر کس (از اين فرمان) روي‌گردان شود، (به خود زيان مي‌رساند نه به خدا)، چرا که خداوند بي‌نياز و شايسته ستايش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5100, N'ما رسولان خود را با دلايل روشن فرستاديم، و با آنها کتاب (آسماني) و ميزان (شناسايي حق از باطل و قوانين عادلانه) نازل کرديم تا مردم قيام به عدالت کنند؛ و آهن را نازل کرديم که در آن نيروي شديد و منافعي براي مردم است، تا خداوند بداند چه کسي او و رسولانش را ياري مي‌کند بي‌آنکه او را ببينند؛ خداوند قوي و شکست‌ناپذير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5101, N'ما نوح و ابراهيم را فرستاديم، و در دودمان آن دو نبوت و کتاب قرار داديم؛ بعضي از آنها هدايت يافته‌اند و بسياري از آنها گنهکارند.')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5102, N'سپس در پي آنان رسولان ديگر خود را فرستاديم، و بعد از آنان عيسي بن مريم را مبعوث کرديم و به او انجيل عطا کرديم، و در دل کساني که از او پيروي کردند رأفت و رحمت قرار داديم؛ و رهبانيتي را که ابداع کرده بودند، ما بر آنان مقرر نداشته بوديم؛ گرچه هدفشان جلب خشنودي خدا بود، ولي حق آن را رعايت نکردند؛ از اين‌رو ما به کساني از آنها که ايمان آوردند پاداششان را داديم؛ و بسياري از آنها فاسقند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5103, N'اي کساني که ايمان آورده‌ايد! تقواي الهي پيشه کنيد و به رسولش ايمان بياوريد تا دو سهم از رحمتش به شما ببخشد و براي شما نوري قرار دهد که با آن (در ميان مردم و در مسير زندگي خود) راه برويد و گناهان شما را ببخشد؛ و خداوند غفور و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5104, N'تا اهل کتاب بدانند که قادر بر چيزي از فضل خدا نيستند، و تمام فضل (و رحمت) به دست اوست، به هر کس بخواهد آن را مي‌بخشد؛ و خداوند داراي فضل عظيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5105, N'خداوند سخن زني را که درباره شوهرش به تو مراجعه کرده بود و به خداوند شکايت مي‌کرد شنيد (و تقاضاي او را اجابت کرد)؛ خداوند گفتگوي شما را با هم (و اصرار آن زن را درباره حل مشکلش) مي‌شنيد؛ و خداوند شنوا و بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5106, N'کساني که از شما نسبت به همسرانشان «ظهار» مي‌کنند (و مي‌گويند: «أنت علي کظهر أمي = تو نسبت به من بمنزله مادرم هستي»)، آنان هرگز مادرانشان نيستند؛ مادرانشان تنها کساني‌اند که آنها را به دنيا آورده‌اند! آنها سخني زشت و باطل مي‌گويند؛ و خداوند بخشنده و آمرزنده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5107, N'کساني که همسران خود را «ظهار» مي‌کنند، سپس از گفته خود بازمي‌گردند، بايد پيش از آميزش جنسي با هم، برده‌اي را آزاد کنند؛ اين دستوري است که به آن اندرز داده مي‌شويد؛ و خداوند به آنچه انجام مي‌دهيد آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5108, N'و کسي که توانايي (آزاد کردن برده‌اي) نداشته باشد، دو ماه پياپي قبل از آميزش روزه بگيرد؛ و کسي که اين را هم نتواند، شصت مسکين را اطعام کند؛ اين براي آن است که به خدا و رسولش ايمان بياوريد؛ اينها مرزهاي الهي است؛ و کساني که با آن مخالفت کنند، عذاب دردناکي دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5109, N'کساني که با خدا و رسولش دشمني مي‌کنند خوار و ذليل شدند آن‌گونه که پيشينيان خوار و ذليل شدند؛ ما آيات روشني نازل کرديم، و براي کافران عذاب خوارکننده‌اي است...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5110, N'در آن روز که خداوند همه آنها را برمي‌انگيزد و از اعمالي که انجام دادند با خبر مي‌سازد، اعمالي که خداوند حساب آن را نگه داشته و آنها فراموشش کردند؛ و خداوند بر هر چيز شاهد و ناظر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5111, N'آيا نمي‌داني که خداوند آنچه را در آسمانها و آنچه را در زمين است مي‌داند؛ هيچ گاه سه نفر با هم نجوا نمي‌کنند مگر اينکه خداوند چهارمين آنهاست، و هيچ گاه پنج نفر با هم نجوا نمي‌کنند مگر اينکه خداوند ششمين آنهاست، و نه تعدادي کمتر و نه بيشتر از آن مگر اينکه او همراه آنهاست هر جا که باشند، سپس روز قيامت آنها را از اعمالشان آگاه مي‌سازد، چرا که خداوند به هر چيزي داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5112, N'آيا نديدي کساني را که از نجوا [= سخنان درگوشي‌] نهي شدند، سپس به کاري که از آن نهي شده بودند بازمي‌گردند و براي انجام گناه و تعدي و نافرماني رسول خدا به نجوا مي‌پردازند و هنگامي که نزد تو مي‌آيند تو را تحيتي (و خوشامدي) مي‌گويند که خدا به تو نگفته است، و در دل مي‌گويند: «چرا خداوند ما را بخاطر گفته‌هايمان عذاب نمي‌کند؟!» جهنم براي آنان کافي است، وارد آن مي‌شوند، و چه بد فرجامي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5113, N'اي کساني که ايمان آورده‌ايد! هنگامي که نجوا مي‌کنيد، و به گناه و تعدي و نافرماني رسول (خدا) نجوا نکنيد، و به کار نيک و تقوا نجوا کنيد، و از خدايي که همگي نزد او جمع مي‌شويد بپرهيزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5114, N'نجوا تنها از سوي شيطان است؛ مي‌خواهد با آن مؤمنان غمگين شوند؛ ولي نمي تواند هيچ گونه ضرري به آنها برساند جز بفرمان خدا؛ پس مؤمنان تنها بر خدا توکل کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5115, N'اي کساني که ايمان آورده‌ايد! هنگامي که به شما گفته شود: «مجلس را وسعت بخشيد (و به تازه‌واردها جا دهيد)»، وسعت بخشيد، خداوند (بهشت را) براي شما وسعت مي‌بخشد؛ و هنگامي که گفته شود: «برخيزيد»، برخيزيد؛ اگر چنين کنيد، خداوند کساني را که ايمان آورده‌اند و کساني را که علم به آنان داده شده درجات عظيمي مي‌بخشد؛ و خداوند به آنچه انجام مي‌دهيد آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5116, N'اي کساني که ايمان آورده‌ايد! هنگامي که مي‌خواهيد با رسول خدا نجوا کنيد (و سخنان درگوشي بگوييد)، قبل از آن صدقه‌اي (در راه خدا) بدهيد؛ اين براي شما بهتر و پاکيزه‌تر است و اگر توانايي نداشته باشيد، خداوند غفور و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5117, N'آيا ترسيديد فقير شويد که از دادن صدقات قبل از نجوا خودداري کرديد؟! اکنون که اين کار را نکرديد و خداوند توبه شما را پذيرفت، نماز را برپا داريد و زکات را ادا کنيد و خدا و پيامبرش را اطاعت نماييد و (بدانيد) خداوند از آنچه انجام مي‌دهيد با خبر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5118, N'آيا نديدي کساني را که طرح دوستي با گروهي که مورد غضب خدا بودند ريختند؟! آنها نه از شما هستند و نه از آنان! سوگند دروغ ياد مي‌کنند (که از شما هستند) در حالي که خودشان مي‌دانند (دروغ نمي‌گويند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5119, N'خداوند عذاب شديدي براي آنان فراهم ساخته، چرا که اعمال بدي انجام مي‌دادند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5120, N'آنها سوگندهاي خود را سپري قرار دادند و مردم را از راه خدا بازداشتند؛ از اين‌رو براي آنان عذاب خوارکننده‌اي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5121, N'هرگز اموال و اولادشان آنها را از عذاب الهي حفظ نمي‌کند؛ آنها اهل آتشند و جاودانه در آن مي‌مانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5122, N'(به خاطر بياوريد) روزي را که خداوند همه آنها را برمي‌انگيزد، آنها براي خدا نيز سوگند (دروغ) ياد مي‌کنند همان‌گونه که (امروز) براي شما ياد مي‌کنند؛ و گمان مي‌کنند کاري مي‌توانند انجام دهند؛ بدانيد آنها دروغگويانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5123, N'شيطان بر آنان مسلط شده و ياد خدا را از خاطر آنها برده؛ آنان حزب شيطانند! بدانيد حزب شيطان زيانکارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5124, N'کساني که با خدا و رسولش دشمني مي‌کنند، آنها در زمره ذليل‌ترين افرادند.!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5125, N'خداوند چنين مقرر داشته که من و رسولانم پيروز مي‌شويم؛ چرا که خداوند قوي و شکست‌ناپذير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5126, N'هيچ قومي را که ايمان به خدا و روز رستاخيز دارند نمي‌يابي که با دشمنان خدا و رسولش دوستي کنند، هر چند پدران يا فرزندان يا برادران يا خويشاوندانشان باشند؛ آنان کساني هستند که خدا ايمان را بر صفحه دلهايشان نوشته و با روحي از ناحيه خودش آنها را تقويت فرموده، و آنها را در باغهايي از بهشت وارد مي‌کند که نهرها از زير (درختانش) جاري است، جاودانه در آن مي‌مانند؛ خدا از آنها خشنود است، و آنان نيز از خدا خشنودند؛ آنها «حزب الله»اند؛ بدانيد «حزب الله» پيروزان و رستگارانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5127, N'آنچه در آسمانها و آنچه در زمين است، براي خدا تسبيح مي‌گويد؛ و او عزيز و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5128, N'او کسي است که کافران اهل کتاب را در نخستين برخورد (با مسلمانان) از خانه‌هايشان بيرون راند! گمان نمي‌کرديد آنان خارج شوند، و خودشان نيز گمان مي‌کردند که دژهاي محکمشان آنها را از عذاب الهي مانع مي‌شود؛ اما خداوند از آنجا که گمان نمي‌کردند به سراغشان آمد و در دلهايشان ترس و وحشت افکند، بگونه‌اي که خانه‌هاي خود را با دست خويش و با دست مؤمنان ويران مي‌کردند؛ پس عبرت بگيريد اي صاحبان چشم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5129, N'و اگر نه اين بود که خداوند ترک وطن را بر آنان مقرر داشته بود، آنها را در همين دنيا مجازات مي‌کرد؛ و براي آنان در آخرت نيز عذاب آتش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5130, N'اين به خاطر آن است که آنها با خدا و رسولش دشمني کردند؛ و هر کس با خدا دشمني کند (بايد بداند) که خدا مجازات شديدي دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5131, N'هر درخت باارزش نخل را قطع کرديد يا آن را به حال خود واگذاشتيد، همه به فرمان خدا بود؛ و براي اين بود که فاسقان را خوار و رسوا کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5132, N'و آنچه را خدا از آنان [= يهود] به رسولش بازگردانده (و بخشيده) چيزي است که شما براي به دست آوردن آن (زحمتي نکشيديد،) نه اسبي تاختيد و نه شتري؛ ولي خداوند رسولان خود را بر هر کس بخواهد مسلط مي‌سازد؛ و خدا بر هر چيز توانا است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5133, N'آنچه را خداوند از اهل اين آباديها به رسولش بازگرداند، از آن خدا و رسول و خويشاوندان او، و يتيمان و مستمندان و در راه ماندگان است، تا (اين اموال عظيم) در ميان ثروتمندان شما دست به دست نگردد! آنچه را رسول خدا براي شما آورده بگيريد (و اجرا کنيد)، و از آنچه نهي کرده خودداري نماييد؛ و از (مخالفت) خدا بپرهيزيد که خداوند کيفرش شديد است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5134, N'اين اموال براي فقيران مهاجراني است که از خانه و کاشانه و اموال خود بيرون رانده شدند در حالي که فضل الهي و رضاي او را مي‌طلبند و خدا و رسولش را ياري مي‌کنند؛ و آنها راستگويانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5135, N'و براي کساني است که در اين سرا [= سرزمين مدينه‌] و در سراي ايمان پيش از مهاجران مسکن گزيدند و کساني را که به سويشان هجرت کنند دوست مي‌دارند، و در دل خود نيازي به آنچه به مهاجران داده شده احساس نمي‌کنند و آنها را بر خود مقدم مي‌دارند هر چند خودشان بسيار نيازمند باشند؛ کساني که از بخل و حرص نفس خويش باز داشته شده‌اند رستگارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5136, N'(همچنين) کساني که بعد از آنها [= بعد از مهاجران و انصار] آمدند و مي‌گويند: «پروردگارا! ما و برادرانمان را که در ايمان بر ما پيشي گرفتند بيامرز، و در دلهايمان حسد و کينه‌اي نسبت به مؤمنان قرار مده! پروردگارا، تو مهربان و رحيمي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5137, N'آيا منافقان را نديدي که پيوسته به برادران کفارشان از اهل کتاب مي‌گفتند: «هرگاه شما را (از وطن) بيرون کنند، ما هم با شما بيرون خواهيم رفت و هرگز سخن هيچ کس را درباره شما اطاعت نخواهيم کرد؛ و اگر با شما پيکار شود، ياريتان خواهيم نمود!» خداوند شهادت مي‌دهد که آنها دروغگويانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5138, N'اگر آنها را بيرون کنند با آنان بيرون نمي‌روند، و اگر با آنها پيکار شود ياريشان نخواهند کرد، و اگر ياريشان کنند پشت به ميدان کرده فرار مي‌کنند؛ سپس کسي آنان را ياري نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5139, N'وحشت از شما در دلهاي آنها بيش از ترس از خداست؛ اين به خاطر آن است که آنها گروهي نادانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5140, N'آنها هرگز با شما بصورت گروهي نمي‌جنگند جز در دژهاي محکم يا از پشت ديوارها! پيکارشان در ميان خودشان شديد است، (اما در برابر شما ضعيف!) آنها را متحد مي‌پنداري، در حالي که دلهايشان پراکنده است؛ اين به خاطر آن است که آنها قومي هستند که تعقل نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5141, N'کار اين گروه از يهود همانند کساني است که کمي قبل از آنان بودند، طعم تلخ کار خود را چشيدند و براي آنها عذابي دردناک است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5142, N'کار آنها همچون شيطان است که به انسان گفت: «کافر شو (تا مشکلات تو را حل کنم)!» اما هنگامي که کافر شد گفت: «من از تو بيزارم، من از خداوندي که پروردگار عالميان است بيم دارم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5143, N'سرانجام کارشان اين شد که هر دو در آتش دوزخ خواهند بود، جاودانه در آن مي‌مانند؛ و اين است کيفر ستمکاران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5144, N'اي کساني که ايمان آورده‌ايد از (مخالفت) خدا بپرهيزيد؛ و هر کس بايد بنگرد تا براي فردايش چه چيز از پيش فرستاده؛ و از خدا بپرهيزيد که خداوند از آنچه انجام مي‌دهيد آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5145, N'و همچون کساني نباشيد که خدا را فراموش کردند و خدا نيز آنها را به «خود فراموشي» گرفتار کرد، آنها فاسقانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5146, N'هرگز دوزخيان و بهشتيان يکسان نيستند؛ اصحاب بهشت رستگار و پيروزند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5147, N'اگر اين قرآن را بر کوهي نازل مي‌کرديم، مي‌ديدي که در برابر آن خاشع مي‌شود و از خوف خدا مي‌شکافد! اينها مثالهايي است که براي مردم مي‌زنيم، شايد در آن بينديشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5148, N'او خدايي است که معبودي جز او نيست، داناي آشکار و نهان است، و او رحمان و رحيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5149, N'و خدايي است که معبودي جز او نيست، حاکم و مالک اصلي اوست، از هر عيب منزه است، به کسي يتم نمي‌کند، امنيت بخش است، مراقب همه چيز است، قدرتمندي شکست‌ناپذير که با اراده نافذ خود هر امري را اصلاح مي‌کند، و شايسته عظمت است؛ خداوند منزه است از آنچه شريک براي او قرارمي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5150, N'او خداوندي است خالق، آفريننده‌اي بي‌سابقه، و صورتگري (بي‌نظير)؛ براي او نامهاي نيک است؛ آنچه در آسمانها و زمين است تسبيح او مي‌گويند؛ و او عزيز و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5151, N'اي کساني که ايمان آورده‌ايد! دشمن من و دشمن خودتان را دوست نگيريد! شما نسبت به آنان اظهار محبت مي‌کنيد، در حالي که آنها به آنچه از حق براي شما آمده کافر شده‌اند و رسول الله و شما را به خاطر ايمان به خداوندي که پروردگار همه شماست از شهر و ديارتان بيرون مي‌رانند؛ اگر شما براي جهاد در راه من و جلب خشنوديم هجرت کرده‌ايد؛ (پيوند دوستي با آنان برقرار نسازيد!) شما مخفيانه با آنها رابطه دوستي برقرار مي‌کنيد در حالي که من به آنچه پنهان يا آشکار مي‌سازيد از همه داناترم! و هر کس از شما چنين کاري کند، از راه راست گمراه شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5152, N'اگر آنها بر شما مسلط شوند، دشمنانتان خواهند بود و دست و زبان خود را به بدي کردن نسبت به شما مي‌گشايند، و دوست دارند شما به کفر بازگرديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5153, N'هرگز بستگان و فرزندانتان روز قيامت سودي به حالتان نخواهند داشت؛ ميان شما جدايي مي‌افکند؛ و خداوند به آنچه انجام مي‌دهيد بيناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5154, N'براي شما سرمشق خوبي در زندگي ابراهيم و کساني که با او بودند وجود داشت، در آن هنگامي که به قوم (مشرک) خود گفتند: «ما از شما و آنچه غير از خدا مي‌پرستيد بيزاريم؛ ما نسبت به شما کافريم؛ و ميان ما و شما عداوت و دشمني هميشگي آشکار شده است؛ تا آن زمان که به خداي يگانه ايمان بياوريد! -جز آن سخن ابراهيم که به پدرش [= عمويش آزر] گفت (و وعده داد) که براي تو آمرزش طلب مي‌کنم، و در عين حال در برابر خداوند براي تو مالک چيزي نيستم (و اختياري ندارم)!- پروردگارا! ما بر تو توکل کرديم و به سوي تو بازگشتيم، و همه فرجامها بسوي تو است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5155, N'پروردگارا! ما را مايه گمراهي کافران قرار مده، و ما را ببخش، اي پروردگار ما که تو عزيز و حکيمي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5156, N'(آري) براي شما در زندگي آنها اسوه حسنه (و سرمشق نيکويي) بود، براي کساني که اميد به خدا و روز قيامت دارند؛ و هر کس سرپيچي کند به خويشتن ضرر زده است، زيرا خداوند بي‌نياز و شايسته ستايش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5157, N'اميد است خدا ميان شما و کساني از مشرکين که با شما دشمني کردند (از راه اسلام) پيوند محبت برقرار کند؛ خداوند تواناست و خداوند آمرزنده و مهربان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5158, N'خدا شما را از نيکي کردن و رعايت عدالت نسبت به کساني که در راه دين با شما پيکار نکردند و از خانه و ديارتان بيرون نراندند نهي نمي‌کند؛ چرا که خداوند عدالت‌پيشگان را دوست دارد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5159, N'تنها شما را از دوستي و رابطه با کساني نهي مي‌کند که در امر دين با شما پيکار کردند و شما را از خانه هايتان بيرون راندند يا به بيرون‌راندن شما کمک کردند و هر کس با آنان رابطه دوستي داشته باشد ظالم و ستمگر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5160, N'اي کساني که ايمان آورده‌ايد! هنگامي که زنان باايمان بعنوان هجرت نزد شما آيند، آنها را آزمايش کنيد -خداوند به ايمانشان آگاهتر است- هرگاه آنان را مؤمن يافتيد، آنها را بسوي کفار بازنگردانيد؛ نه آنها براي کفار حلالند و نه کفار براي آنها حلال؛ و آنچه را همسران آنها (براي ازدواج با اين زنان) پرداخته‌اند به آنان بپردازيد؛ و گناهي بر شما نيست که با آنها ازدواج کنيد هرگاه مهرشان را به آنان بدهيد و هرگز زنان کافر را در همسري خود نگه نداريد (و اگر کسي از زنان شما کافر شد و به بلاد کفر فرار کرد،) حق داريد مهري را که پرداخته‌ايد مطالبه کنيد همان‌گونه که آنها حق دارند مهر زنانشان را که از آنان جدا شده‌اند) از شما مطالبه کنند؛ اين حکم خداوند است که در ميان شما حکم مي‌کند، و خداوند دانا و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5161, N'و اگر بعضي از همسران شما از دستتان بروند (و به سوي کفار بازگردند) و شما در جنگي بر آنان پيروز شديد و غنايمي گرفتيد، به کساني که همسرانشان رفته‌اند، همانند مهري را که پرداخته‌اند بدهيد؛ و از (مخالفت) خداوندي که همه به او ايمان داريد بپرهيزيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5162, N'اي پيامبر! هنگامي که زنان مؤمن نزد تو آيند و با تو بيعت کنند که چيزي را شريک خدا قرار ندهند، دزدي و زنا نکنند، فرزندان خود را نکشند، تهمت و افترايي پيش دست و پاي خود نياورند و در هيچ کار شايسته‌اي مخالفت فرمان تو نکنند، با آنها بيعت کن و براي آنان از درگاه خداوند آمرزش بطلب که خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5163, N'اي کساني که ايمان آورده‌ايد! با قومي که خداوند آنان را مورد غضب قرار داده دوستي نکنيد؛ آنان از آخرت مأيوسند همان‌گونه که کفار مدفون در قبرها مأيوس مي‌باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5164, N'آنچه در آسمانها و آنچه در زمين است همه تسبيح خدا مي‌گويند؛ و او شکست‌ناپذير و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5165, N'اي کساني که ايمان آورده‌ايد! چرا سخني مي‌گوييد که عمل نمي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5166, N'نزد خدا بسيار موجب خشم است که سخني بگوييد که عمل نمي‌کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5167, N'خداوند کساني را دوست مي‌دارد که در راه او پيکار مي‌کنند گوئي بنايي آهنين‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5168, N'(به ياد آوريد) هنگامي را که موسي به قومش گفت: «اي قوم من! چرا مرا آزار مي‌دهيد با اينکه مي‌دانيد من فرستاده خدا به سوي شما هستم؟!» هنگامي که آنها از حق منحرف شدند، خداوند قلوبشان را منحرف ساخت؛ و خدا فاسقان را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5169, N'و (به ياد آوريد) هنگامي را که عيسي بن مريم گفت: «اي بني اسرائيل! من فرستاده خدا به سوي شما هستم در حالي که تصديق‌کننده کتابي که قبل از من فرستاده شده [= تورات‌] مي‌باشم، و بشارت‌دهنده به رسولي که بعد از من مي‌آيد و نام او احمد است!» هنگامي که او [= احمد] با معجزات و دلايل روشن به سراغ آنان آمد، گفتند: «اين سحري است آشکار»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5170, N'چه کسي ظالمتر است از آن کس که بر خدا دروغ بسته در حالي که دعوت به اسلام مي‌شود؟! خداوند گروه ستمکاران را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5171, N'آنان مي‌خواهند نور خدا را با دهان خود خاموش سازند؛ ولي خدا نور خود را کامل مي‌کند هر چند کافران خوش نداشته باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5172, N'او کسي است که رسول خود را با هدايت و دين حق فرستاد تا او را بر همه اديان غالب سازد، هر چند مشرکان کراهت داشته باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5173, N'اي کساني که ايمان آورده‌ايد! آيا شما را به تجارتي راهنمائي کنم که شما را از عذاب دردناک رهايي مي‌بخشد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5174, N'به خدا و رسولش ايمان بياوريد و با اموال و جانهايتان در راه خدا جهاد کنيد؛ اين براي شما (از هر چيز) بهتر است اگر بدانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5175, N'(اگر چنين کنيد) گناهانتان را مي‌بخشد و شما را در باغهايي از بهشت داخل مي‌کند که نهرها از زير درختانش جاري است و در مسکنهاي پاکيزه در بهشت جاويدان جاي مي‌دهد؛ و اين پيروزي عظيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5176, N'و (نعمت) ديگري که آن را دوست داريد به شما مي‌بخشد، و آن ياري خداوند و پيروزي نزديک است؛ و مؤمنان را بشارت ده (به اين پيروزي بزرگ)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5177, N'اي کساني که ايمان آورده‌ايد! ياوران خدا باشيد همان‌گونه که عيسي بن مريم به حواريون گفت: «چه کساني در راه خدا ياوران من هستند؟!» حواريون گفتند: «ما ياوران خدائيم» در اين هنگام گروهي از بني اسرائيل ايمان آوردند و گروهي کافر شدند؛ ما کساني را که ايمان آورده بودند در برابر دشمنانشان تأييد کرديم و سرانجام بر آنان پيروز شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5178, N'آنچه در آسمانها و آنچه در زمين است همواره تسبيح خدا مي‌گويند، خداوندي که مالک و حاکم است و از هر عيب و نقصي مبرا، و عزيز و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5179, N'و کسي است که در ميان جمعيت درس نخوانده رسولي از خودشان برانگيخت که آياتش را بر آنها مي‌خواند و آنها را تزکيه مي‌کند و به آنان کتاب (قرآن) و حکمت مي‌آموزد هر چند پيش از آن در گمراهي آشکاري بودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5180, N'و (همچنين) رسول است بر گروه ديگري که هنوز به آنها ملحق نشده‌اند؛ و او عزيز و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5181, N'اين فضل خداست که به هر کس بخواهد (و شايسته بداند) مي‌بخشد؛ و خداوند صاحب فضل عظيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5182, N'کساني که مکلف به تورات شدند ولي حق آن را ادا نکردند، مانند درازگوشي هستند که کتابهايي حمل مي‌کند، (آن را بر دوش مي‌کشد اما چيزي از آن نمي‌فهمد)! گروهي که آيات خدا را انکار کردند مثال بدي دارند، و خداوند قوم ستمگر را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5183, N'بگو: «اي يهوديان! اگر گمان مي‌کنيد که (فقط) شما دوستان خدائيد نه ساير مردم، پس آرزوي مرگ کنيد اگر راست مي‌گوييد (تا به لقاي محبوبتان برسيد)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5184, N'ولي آنان هرگز تمناي مرگ نمي‌کنند بخاطر اعمالي که از پيش فرستاده‌اند؛ و خداوند ظالمان را بخوبي مي‌شناسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5185, N'بگو: «اين مرگي که از آن فرار مي‌کنيد سرانجام با شما ملاقات خواهد کرد؛ سپس به سوي کسي که داناي پنهان و آشکار است بازگردانده مي‌شويد؛ آنگاه شما را از آنچه انجام مي‌داديد خبر مي‌دهد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5186, N'اي کساني که ايمان آورده‌ايد! هنگامي که براي نماز روز جمعه اذان گفته شود، به سوي ذکر خدا بشتابيد و خريد و فروش را رها کنيد که اين براي شما بهتر است اگر مي‌دانستيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5187, N'و هنگامي که نماز پايان گرفت (شما آزاديد) در زمين پراکنده شويد و از فضل خدا بطلبيد، و خدا را بسيار ياد کنيد شايد رستگار شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5188, N'هنگامي که آنها تجارت يا سرگرمي و لهوي را ببينند پراکنده مي‌شوند و به سوي آن مي روند و تو را ايستاده به حال خود رها مي‌کنند؛ بگو: آنچه نزد خداست بهتر از لهو و تجارت است، و خداوند بهترين روزي‌دهندگان است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5189, N'هنگامي که منافقان نزد تو آيند مي‌گويند: «ما شهادت مي‌دهيم که يقينا تو رسول خدايي!» خداوند مي‌داند که تو رسول او هستي، ولي خداوند شهادت مي‌دهد که منافقان دروغگو هستند (و به گفته خود ايمان ندارند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5190, N'آنها سوگندهايشان را سپر ساخته‌اند تا مردم را از راه خدا باز دارند، و کارهاي بسيار بدي انجام مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5191, N'اين بخاطر آن است که نخست ايمان آوردند سپس کافر شدند؛ از اين رو بر دلهاي آنان مهر نهاده شده، و حقيقت را درک نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5192, N'هنگامي که آنها را مي‌بيني، جسم و قيافه آنان تو را در شگفتي فرو مي‌برد؛ و اگر سخن بگويند، به سخنانشان گوش فرا مي‌دهي؛ اما گويي چوبهاي خشکي هستند که به ديوار تکيه داده شده‌اند! هر فريادي از هر جا بلند شود بر ضد خود مي‌پندارند؛ آنها دشمنان واقعي تو هستند، پس از آنان بر حذر باش! خداوند آنها را بکشد، چگونه از حق منحرف مي‌شوند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5193, N'هنگامي که به آنان گفته شود: «بياييد تا رسول خدا براي شما استغفار کند!»، سرهاي خود را (از روي استهزا و کبر و غرور) تکان مي‌دهند؛ و آنها را مي‌بيني که از سخنان تو اعراض کرده و تکبر مي‌ورزند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5194, N'براي آنها تفاوت نمي‌کند، خواه استغفار برايشان کني يا نکني، هرگز خداوند آنان را نمي‌بخشد؛ زيرا خداوند قوم فاسق را هدايت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5195, N'آنها کساني هستند که مي‌گويند: «به افرادي که نزد رسول خدا هستند انفاق نکنيد تا پراکنده شوند!» (غافل از اينکه) خزاين آسمانها و زمين از آن خداست، ولي منافقان نمي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5196, N'آنها مي‌گويند: «اگر به مدينه بازگرديم، عزيزان ذليلان را بيرون مي‌کنند!» در حالي که عزت مخصوص خدا و رسول او و مؤمنان است؛ ولي منافقان نمي‌دانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5197, N'اي کساني که ايمان آورده‌ايد! اموال و فرزندانتان شما را از ياد خدا غافل نکند! و کساني که چنين کنند، زيانکارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5198, N'از آنچه به شما روزي داده‌ايم انفاق کنيد، پيش از آنکه مرگ يکي از شما فرا رسد و بگويد: «پروردگارا! چرا (مرگ) مرا مدت کمي به تأخير نينداختي تا (در راه خدا) صدقه دهم و از صالحان باشم؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5199, N'خداوند هرگز مرگ کسي را هنگامي که اجلش فرا رسد به تأخير نمي‌اندازد، و خداوند به آنچه انجام مي‌دهيد آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5200, N'آنچه در آسمانها و آنچه در زمين است براي خدا تسبيح مي‌گويند؛ مالکيت و حکومت از آن اوست و ستايش از آن او؛ و او بر همه چيز تواناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5201, N'او کسي است که شما را آفريد (و به شما آزادي و اختيار داد)؛ گروهي از شما کافريد و گروهي مؤمن؛ و خداوند به آنچه انجام مي‌دهيد بيناست!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5202, N'آسمانها و زمين را بحق آفريد؛ و شما را (در عالم جنين) تصوير کرد، تصويري زيبا و دلپذير؛ و سرانجام (همه) بسوي اوست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5203, N'آنچه را در آسمانها و زمين است مي‌داند، و از آنچه پنهان يا آشکار مي‌کنيد با خبر است؛ و خداوند از آنچه در درون سينه‌هاست آگاه است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5204, N'آيا خبر کساني که پيش از اين کافر شدند به شما نرسيده است؟! (آري) آنها طعم کيفر گناهان بزرگ خود را چشيدند؛ و عذاب دردناک براي آنهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5205, N'اين بخاطر آن است که رسولان آنها (پيوسته) با دلايل روشن به سراغشان مي‌آمدند، ولي آنها (از روي کبر و غرور) گفتند: «آيا بشرهايي (مثل ما) مي‌خواهند ما را هدايت کنند؟!» از اين رو کافر شدند و روي برگرداندند؛ و خداوند (از ايمان و طاعتشان) بي نياز بود، و خدا غني و شايسته ستايش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5206, N'کافران پنداشتند که هرگز برانگيخته نخواهند شد، بگو: «آري به پروردگارم سوگند که همه شما (در قيامت) برانگيخته خواهيد شد، سپس آنچه را عمل مي‌کرديد به شما خبر داده مي‌شود، و اين براي خداوند آسان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5207, N'حال که چنين است، به خدا و رسول او و نوري که نازل کرده‌ايم ايمان بياوريد؛ و بدانيد خدا به آنچه انجام مي‌دهيد آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5208, N'اين در زماني خواهد بود که همه شما را در روز اجتماع [= روز رستاخيز] گردآوري مي‌کند؛ آن روز روز تغابن است (روز احساس خسارت و پشيماني)! و هر کس به خدا ايمان بياورد و عمل صالح انجام دهد، گناهان او را مي‌بخشد و او را در باغهايي از بهشت که نهرها از زير درختانش جاري است وارد مي‌کند، جاودانه در آن مي مانند؛ و اين پيروزي بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5209, N'اما کساني که کافر شدند و آيات ما را تکذيب کردند اصحاب دوزخند، جاودانه در آن مي‌مانند، و (سرانجام آنها) سرانجام بدي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5210, N'هيچ مصيبتي رخ نمي‌دهد مگر به اذن خدا! و هر کس به خدا ايمان آورد، خداوند قلبش را هدايت مي‌کند؛ و خدا به هر چيز داناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5211, N'اطاعت کنيد خدا را، و اطاعت کنيد پيامبر را؛ و اگر روي‌گردان شويد، رسول ما جز ابلاغ آشکار وظيفه‌اي ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5212, N'خداوند کسي است که هيچ معبودي جز او نيست، و مؤمنان بايد فقط بر او توکل کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5213, N'اي کساني که ايمان آورده‌ايد! بعضي از همسران و فرزندانتان دشمنان شما هستند، از آنها برحذر باشيد؛ و اگر عفو کنيد و چشم بپوشيد و ببخشيد، (خدا شما را مي‌بخشد)؛ چرا که خداوند بخشنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5214, N'اموال و فرزندانتان فقط وسيله آزمايش شما هستند؛ و خداست که پاداش عظيم نزد اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5215, N'پس تا مي‌توانيد تقواي الهي پيشه کنيد و گوش دهيد و اطاعت نماييد و انفاق کنيد که براي شما بهتر است؛ و کساني که از بخل و حرص خويشتن مصون بمانند رستگارانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5216, N'اگر به خدا قرض‌الحسنه دهيد، آن را براي شما مضاعف مي‌سازد و شما را مي‌بخشد؛ و خداوند شکرکننده و بردبار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5217, N'او داناي پنهان و آشکار است؛ و او عزيز و حکيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5218, N'اي پيامبر! هر زمان خواستيد زنان را طلاق دهيد، در زمان عده، آنها را طلاق گوييد [= زماني که از عادت ماهانه پاک شده و با همسرشان نزديکي نکرده باشند ]، و حساب عده را نگه داريد؛ و از خدايي که پروردگار شماست بپرهيزيد؛ نه شما آنها را از خانه‌هايشان بيرون کنيد و نه آنها (در دوران عده) بيرون روند، مگر آنکه کار زشت آشکاري انجام دهند؛ اين حدود خداست، و هر کس از حدود الهي تجاوز کند به خويشتن ستم کرده؛ تو نمي‌داني شايد خداوند بعد از اين، وضع تازه (و وسيله اصلاحي) فراهم کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5219, N'و چون عده آنها سرآمد، آنها را بطرز شايسته‌اي نگه داريد يا بطرز شايسته‌اي از آنان جدا شويد؛ و دو مرد عادل از خودتان را گواه گيريد؛ و شهادت را براي خدا برپا داريد؛ اين چيزي است که مؤمنان به خدا و روز قيامت به آن اندرز داده مي‌شوند! و هر کس تقواي الهي پيشه کند، خداوند راه نجاتي براي او فراهم مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5220, N'و او را از جايي که گمان ندارد روزي مي‌دهد؛ و هر کس بر خدا توکل کند، کفايت امرش را مي‌کند؛ خداوند فرمان خود را به انجام مي‌رساند؛ و خدا براي هر چيزي اندازه‌اي قرار داده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5221, N'و از زنانتان، آنان که از عادت ماهانه مأيوسند، اگر در وضع آنها (از نظر بارداري) شک کنيد، عده آنان سه ماه است، و همچنين آنها که عادت ماهانه نديده‌اند؛ و عده زنان باردار اين است که بار خود را بر زمين بگذارند؛ و هر کس تقواي الهي پيشه کند، خداوند کار را بر او آسان مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5222, N'اين فرمان خداست که بر شما نازل کرده؛ و هر کس تقواي الهي پيشه کند، خداوند گناهانش را مي‌بخشد و پاداش او را بزرگ مي‌دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5223, N'آنها [= زنان مطلقه‌] را هر جا خودتان سکونت داريد و در توانايي شماست سکونت دهيد؛ و به آنها زيان نرسانيد تا کار را بر آنان تنگ کنيد (و مجبور به ترک منزل شوند)؛ و اگر باردار باشند، نفقه آنها را بپردازيد تا وضع حمل کنند؛ و اگر براي شما (فرزند را) شير مي‌دهند، پاداش آنها را بپردازيد؛ و (درباره فرزندان، کار را) با مشاوره شايسته انجام دهيد؛ و اگر به توافق نرسيديد، آن ديگري شيردادن آن بچه را بر عهده مي‌گيرد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5224, N'آنان که امکانات وسيعي دارند، بايد از امکانات وسيع خود انفاق کنند و آنها که تنگدستند، از آنچه که خدا به آنها داده انفاق نمايند؛ خداوند هيچ کس را جز به مقدار توانايي که به او داده تکليف نمي‌کند؛ خداوند بزودي بعد از سختيها آساني قرار مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5225, N'چه بسيار شهرها و آباديها که اهل آن از فرمان خدا و رسولانش سرپيچي کردند و ما بشدت به حسابشان رسيديم و به مجازات کم‌نظيري گرفتار ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5226, N'آنها آثار سوء کار خود را چشيدند؛ و عاقبت کارشان خسران بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5227, N'خداوند عذاب سختي براي آنها فراهم ساخته؛ پس از (مخالفت فرمان) خدا بپرهيزيد اي خردمنداني که ايمان آورده‌ايد! (زيرا) خداوند چيزي که مايه تذکر است بر شما نازل کرده؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5228, N'رسولي به سوي شما فرستاده که آيات روشن خدا را بر شما تلاوت مي‌کند تا کساني را که ايمان آورده و کارهاي شايسته انجام داده‌اند، از تاريکيها بسوي نور خارج سازد! و هر کس به خدا ايمان آورده و اعمال صالح انجام دهد، او را در باغهايي از بهشت وارد سازد که از زير (درختانش) نهرها جاري است، جاودانه در آن مي‌مانند، و خداوند روزي نيکويي براي او قرار داده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5229, N'خداوند همان کسي است که هفت آسمان را آفريد، و از زمين نيز همانند آنها را؛ فرمان او در ميان آنها پيوسته فرود مي‌آيد تا بدانيد خداوند بر هر چيز تواناست و اينکه علم او به همه چيز احاطه دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5230, N'اي پيامبر! چرا چيزي را که خدا بر تو حلال کرده بخاطر جلب رضايت همسرانت بر خود حرام مي‌کني؟! و خداوند آمرزنده و رحيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5231, N'خداوند راه گشودن سوگندهايتان را (در اين گونه موارد) روشن ساخته؛ و خداوند مولاي شماست و او دانا و حکيم است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5232, N'(به خاطر بياوريد) هنگامي را که پيامبر يکي از رازهاي خود را به بعضي از همسرانش گفت، ولي هنگامي که وي آن را افشا کرد و خداوند پيامبرش را از آن آگاه ساخت، قسمتي از آن را براي او بازگو کرد و از قسمت ديگر خودداري نمود؛ هنگامي که پيامبر همسرش را از آن خبر داد، گفت: «چه کسي تو را از اين راز آگاه ساخت؟» فرمود: «خداوند عالم و آگاه مرا باخبر ساخت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5233, N'اگر شما (همسران پيامبر) از کار خود توبه کنيد (به نفع شماست، زيرا) دلهايتان از حق منحرف گشته؛ و اگر بر ضد او دست به دست هم دهيد، (کاري از پيش نخواهيد برد) زيرا خداوند ياور اوست و همچنين جبرئيل و مؤمنان صالح، و فرشتگان بعد از آنان پشتيبان اويند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5234, N'اميد است که اگر او شما را طلاق دهد، پروردگارش به جاي شما همسراني بهتر براي او قرار دهد، همسراني مسلمان، مؤمن، متواضع، توبه کار، عابد، هجرت‌کننده ، زناني غيرباکره و باکره!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5235, N'اي کساني که ايمان آورده‌ايد خود و خانواده خويش را از آتشي که هيزم آن انسانها و سنگهاست نگه داريد؛ آتشي که فرشتگاني بر آن گمارده شده که خشن و سختگيرند و هرگز فرمان خدا را مخالفت نمي‌کنند و آنچه را فرمان داده شده‌اند (به طور کامل) اجرا مي‌نمايند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5236, N'اي کساني که کافر شده‌ايد امروز عذرخواهي نکنيد، چرا که تنها به اعمالتان جزا داده مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5237, N'اي کساني که ايمان آورده‌ايد بسوي خدا توبه کنيد، توبه‌اي خالص؛ اميد است (با اين کار) پروردگارتان گناهانتان را ببخشد و شما را در باغهايي از بهشت که نهرها از زير درختانش جاري است وارد کند، در آن روزي که خداوند پيامبر و کساني را که با او ايمان آوردند خوار نمي‌کند؛ اين در حالي است که نورشان پيشاپيش آنان و از سوي راستشان در حرکت است، و مي‌گويند: «پروردگارا! نور ما را کامل کن و ما را ببخش که تو بر هر چيز توانائي!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5238, N'اي پيامبر! با کفار و منافقين پيکار کن و بر آنان سخت بگير! جايگاهشان جهنم است، و بد فرجامي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5239, N'خداوند براي کساني که کافر شده‌اند به همسر نوح و همسر لوط مثل زده است، آن دو تحت سرپرستي دو بنده از بندگان صالح ما بودند، ولي به آن دو خيانت کردند و ارتباط با اين دو (پيامبر) سودي به حالشان (در برابر عذاب الهي) نداشت، و به آنها گفته شد: «وارد آتش شويد همراه کساني که وارد مي‌شوند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5240, N'و خداوند براي مؤمنان، به همسر فرعون مثل زده است، در آن هنگام که گفت: «پروردگارا! خانه‌اي براي من نزد خودت در بهشت بساز، و مرا از فرعون و کار او نجات ده و مرا از گروه ستمگران رهايي بخش!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5241, N'و همچنين به مريم دختر عمران که دامان خود را پاک نگه داشت، و ما را از روح خود در آن دميديم؛ او کلمات پروردگار و کتابهايش را تصديق کرد و از مطيعان فرمان خدا بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5242, N'پربرکت و زوال‌ناپذير است کسي که حکومت جهان هستي به دست اوست، و او بر هر چيز تواناست.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5243, N'آن کس که مرگ و حيات را آفريد تا شما را بيازمايد که کدام يک از شما بهتر عمل مي‌کنيد، و او شکست‌ناپذير و بخشنده است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5244, N'همان کسي که هفت آسمان را بر فراز يکديگر آفريد؛ در آفرينش خداوند رحمان هيچ تضاد و عيبي نمي‌بيني! بار ديگر نگاه کن، آيا هيچ شکاف و خللي مشاهده مي‌کني؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5245, N'بار ديگر (به عالم هستي) نگاه کن، سرانجام چشمانت (در جستجوي خلل و نقصان ناکام مانده) به سوي تو باز مي‌گردد در حالي که خسته و ناتوان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5246, N'ما آسمان پايين (نزديک) را با چراغهاي فروزاني زينت بخشيديم، و آنها [= شهابها ] را تيرهايي براي شياطين قرار داديم، و براي آنان عذاب آتش فروزان فراهم ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5247, N'و براي کساني که به پروردگارشان کافر شدند عذاب جهنم است، و بد فرجامي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5248, N'هنگامي که در آن افکنده شوند صداي وحشتناکي از آن مي‌شنوند، و اين در حالي است که پيوسته مي‌جوشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5249, N'نزديک است (دوزخ) از شدت غضب پاره پاره شود؛ هر زمان که گروهي در آن افکنده مي‌شوند، نگهبانان دوزخ از آنها مي‌پرسند: «مگر بيم‌دهنده الهي به سراغ شما نيامد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5250, N'مي‌گويند: «آري، بيم‌دهنده به سراغ ما آمد، ولي ما او را تکذيب کرديم و گفتيم: خداوند هرگز چيزي نازل نکرده، و شما در گمراهي بزرگي هستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5251, N'و مي‌گويند: «اگر ما گوش شنوا داشتيم يا تعقل مي‌کرديم، در ميان دوزخيان نبوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5252, N'اينجاست که به گناه خود اعتراف مي‌کنند؛ دور باشند دوزخيان از رحمت خدا!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5253, N'(اما) کساني که از پروردگارشان در نهان مي‌ترسند، مسلما آمرزش و پاداش بزرگي دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5254, N'گفتار خود را پنهان کنيد يا آشکار (تفاوتي نمي‌کند)، او به آنچه در سينه‌هاست آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5255, N'آيا آن کسي که موجودات را آفريده از حال آنها آگاه نيست؟! در حالي که او (از اسرار دقيق) باخبر و آگاه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5256, N'او کسي است که زمين را براي شما رام کرد، بر شانه‌هاي آن راه برويد و از روزيهاي خداوند بخوريد؛ و بازگشت و اجتماع همه به سوي اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5257, N'آيا خود را از عذاب کسي که حاکم بر آسمان است در امان مي‌دانيد که دستور دهد زمين بشکافد و شما را فرو برد و به لرزش خود ادامه دهد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5258, N'يا خود را از عذاب خداوند آسمان در امان مي‌دانيد که تندبادي پر از سنگريزه بر شما فرستد؟! و بزودي خواهيد دانست تهديدهاي من چگونه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5259, N'کساني که پيش از آنان بودند (آيات الهي را) تکذيب کردند، اما (ببين) مجازات من چگونه بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5260, N'آيا به پرندگاني که بالاي سرشان است، و گاه بالهاي خود را گسترده و گاه جمع مي‌کنند، نگاه نکردند؟! جز خداوند رحمان کسي آنها را بر فراز آسمان نگه نمي‌دارد، چرا که او به هر چيز بيناست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5261, N'آيا اين کسي که لشکر شماست مي‌تواند شما را در برابر خداوند ياري دهد؟ ولي کافران تنها گرفتار فريبند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5262, N'يا آن کسي که شما را روزي مي‌دهد، اگر روزيش را بازدارد (چه کسي مي‌تواند نياز شما را تأمين کند)؟! ولي آنها در سرکشي و فرار از حقيقت لجاجت مي‌ورزند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5263, N'آيا کسي که به رو افتاده حرکت مي‌کند به هدايت نزديکتر است يا کسي که راست‌قامت در صراط مستقيم گام برمي‌دارد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5264, N'بگو: «او کسي است که شما را آفريد و براي شما گوش و چشم و قلب قرار داد؛ اما کمتر سپاسگزاري مي‌کنيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5265, N'بگو: «او کسي است که شما را در زمين آفريد و به سوي او محشور مي‌شويد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5266, N'آنها مي‌گويند: «اگر راست مي‌گوييد اين وعده قيامت چه زماني است؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5267, N'بگو: «علم آن تنها نزد خداست؛ و من فقط بيم‌دهنده آشکاري هستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5268, N'هنگامي که آن (وعده الهي) را از نزديک مي‌بينند، صورت کافران زشت و سياه مي‌گردد، و به آنها گفته مي‌شود: «اين همان چيزي است که تقاضاي آن را داشتيد»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5269, N'بگو: «به من خبر دهيد اگر خداوند مرا و تمام کساني را که با من هستند هلاک کند، يا مورد ترحم قرار دهد، چه کسي کافران را از عذاب دردناک پناه مي‌دهد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5270, N'بگو: «او خداوند رحمان است، ما به او ايمان آورده و بر او توکل کرده‌ايم؛ و بزودي مي‌دانيد چه کسي در گمراهي آشکار است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5271, N'بگو: «به من خبر دهيد اگر آبهاي (سرزمين) شما در زمين فرو رود، چه کسي مي‌تواند آب جاري و گوارا در دسترس شما قرار دهد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5272, N'ن، سوگند به قلم و آنچه مينويسند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5273, N'که به نعمت پروردگارت تو مجنون نيستي،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5274, N'و براي تو پاداشي عظيم و هميشگي است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5275, N'و تو اخلاق عظيم و برجسته‌اي داري!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5276, N'و بزودي تو مي‌بيني و آنان نيز مي‌بينند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5277, N'که کدام يک از شما مجنونند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5278, N'پروردگارت بهتر از هر کس مي‌داند چه کسي از راه او گمراه شده، و هدايت‌يافتگان را نيز بهتر مي‌شناسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5279, N'حال که چنين است از تکذيب‌کنندگان اطاعت مکن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5280, N'آنها دوست دارند نرمش نشان دهي تا آنها (هم) نرمش نشان دهند (نرمشي توأم با انحراف از مسير حق)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5281, N'و از کسي که بسيار سوگند ياد مي‌کند و پست است اطاعت مکن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5282, N'کسي که بسيار عيبجوست و به سخن چيني آمد و شد مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5283, N'و بسيار مانع کار خير، و متجاوز و گناهکار است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5284, N'علاوه بر اينها کينه توز و پرخور و خشن و بدنام است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5285, N'مبادا بخاطر اينکه صاحب مال و فرزندان فراوان است (از او پيروي کني)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5286, N'هنگامي که آيات ما بر او خوانده مي‌شود مي‌گويد: «اينها افسانه‌هاي خرافي پيشينيان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5287, N'(ولي) ما بزودي بر بيني او علامت و داغ ننگ مي‌نهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5288, N'ما آنها را آزموديم، همان گونه که «صاحبان باغ» را آزمايش کرديم، هنگامي که سوگند ياد کردند که ميوه‌هاي باغ را صبحگاهان (دور از چشم مستمندان) بچينند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5289, N'و هيچ از آن استثنا نکنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5290, N'اما عذابي فراگير (شب هنگام) بر (تمام) باغ آنها فرود آمد در حالي که همه در خواب بودند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5291, N'و آن باغ سرسبز همچون شب سياه و ظلماني شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5292, N'صبحگاهان يکديگر را صدا زدند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5293, N'که بسوي کشتزار و باغ خود حرکت کنيد اگر قصد چيدن ميوه‌ها را داريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5294, N'آنها حرکت کردند در حالي که آهسته با هم مي‌گفتند:»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5295, N'«مواظب باشيد امروز حتي يک فقير وارد بر شما نشود!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5296, N'(آري) آنها صبحگاهان تصميم داشتند که با قدرت از مستمندان جلوگيري کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5297, N'هنگامي که (وارد باغ شدند و) آن را ديدند گفتند: «حقا» ما گمراهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5298, N'(آري، همه چيز از دست ما رفته) بلکه ما محروميم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5299, N'يکي از آنها که از همه عاقلتر بود گفت: «آيا به شما نگفتم چرا تسبيح خدا نمي‌گوييد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5300, N'گفتند: «منزه است پروردگار ما، مسلما ما ظالم بوديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5301, N'سپس رو به يکديگر کرده به ملامت هم پرداختند،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5302, N'(و فريادشان بلند شد) گفتند: «واي بر ما که طغيانگر بوديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5303, N'اميدواريم پروردگارمان (ما را ببخشد و) بهتر از آن به جاي آن به ما بدهد، چرا که ما به او علاقه‌منديم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5304, N'اين گونه است عذاب (خداوند در دنيا)، و عذاب آخرت از آن هم بزرگتر است اگر مي‌دانستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5305, N'مسلما براي پرهيزگاران نزد پروردگارشان باغهاي پر نعمت بهشت است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5306, N'آيا مؤمنان را همچون مجرمان قرار مي‌دهيم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5307, N'شما را چه مي‌شود؟! چگونه داوري مي‌کنيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5308, N'آيا کتابي داريد که از آن درس مي‌خوانيد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5309, N'که آنچه را شما انتخاب مي‌کنيد از آن شماست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5310, N'يا اينکه عهد و پيمان مؤکد و مستمري تا روز قيامت بر ما داريد که هر چه را حکم کنيد براي شما باشد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5311, N'از آنها بپرس کدام يک از آنان چنين چيزي را تضمين مي‌کند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5312, N'يا اينکه معبوداني دارند که آنها را شريک خدا قرار داده‌اند (و براي آنان شفاعت مي‌کنند)؟! اگر راست مي‌گويند معبودان خود را بياورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5313, N'(به خاطر بياوريد) روزي را که ساق پاها (از وحشت) برهنه مي‌گردد و دعوت به سجود مي‌شوند، اما نمي‌توانند (سجود کنند).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5314, N'اين در حالي است که چشمهايشان (از شدت شرمساري) به زير افتاده، و ذلت و خواري وجودشان را فراگرفته؛ آنها پيش از اين دعوت به سجود مي‌شدند در حالي که سالم بودند (ولي امروز ديگر توانايي آن را ندارند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5315, N'اکنون مرا با آنها که اين سخن را تکذيب مي‌کنند واگذار! ما آنان را از آنجا که نمي‌دانند به تدريج به سوي عذاب پيش مي‌بريم.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5316, N'و به آنها مهلت (بازگشت) مي‌دهم؛ چرا که نقشه‌هاي من محکم و دقيق است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5317, N'يا اينکه تو از آنها مزدي مي‌طلبي که پرداختش براي آنها سنگين است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5318, N'يا اسرار غيب نزد آنهاست و آن را مي‌نويسند (و به يکديگر مي‌دهند)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5319, N'اکنون که چنين است صبر کن و منتظر فرمان پروردگارت باش، و مانند صاحب ماهي [= يونس‌] مباش (که در تقاضاي مجازات قومش عجله کرد و گرفتار مجازات ترک اولي شد) در آن زمان که با نهايت اندوه خدا را خواند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5320, N'و اگر رحمت خدا به ياريش نيامده بود، (از شکم ماهي) بيرون افکنده مي‌شد در حالي که نکوهيده بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5321, N'ولي پروردگارش او را برگزيد و از صالحان قرار داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5322, N'نزديک است کافران هنگامي که آيات قرآن را مي‌شنوند با چشم‌زخم خود تو را از بين ببرند، و مي‌گويند: «او ديوانه است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5323, N'در حالي که اين (قرآن) جز مايه بيداري براي جهانيان نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5324, N'(روز رستاخيز) روزي است که مسلما واقع مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5325, N'چه روز واقع شدني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5326, N'و تو چه مي‌داني آن روز واقع شدني چيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5327, N'قوم «ثمود» و «عاد» عذاب کوبنده الهي را انکار کردند (و نتيجه شومش را ديدند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5328, N'اما قوم «ثمود» با عذابي سرکش هلاک شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5329, N'و اما قوم «عاد» با تندبادي طغيانگر و سرد و پرصدا به هلاکت رسيدند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5330, N'(خداوند) اين تندباد بنيان‌کن را هفت شب و هشت روز پي در پي بر آنها مسلط ساخت، (و اگر آنجا بودي) مي‌ديدي که آن قوم همچون تنه‌هاي پوسيده و تو خالي درختان نخل در ميان اين تند باد روي زمين افتاده و هلاک شده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5331, N'آيا کسي از آنها را باقي مي‌بيني؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5332, N'و فرعون و کساني که پيش از او بودند و همچنين اهل شهرهاي زير و رو شده [=قوم لوط] مرتکب گناهان بزرگ شدند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5333, N'و با فرستاده پروردگارشان مخالفت کردند؛ و خداوند (نيز) آنها را به عذاب شديدي گرفتار ساخت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5334, N'و هنگامي که آب طغيان کرد، ما شما را سوار بر کشتي کرديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5335, N'تا آن را وسيله تذکري براي شما قرار دهيم و گوشهاي شنوا آن را دريابد و بفهمد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5336, N'به محض اينکه يک بار در «صور» دميده شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5337, N'و زمين و کوه‌ها از جا برداشته شوند و يکباره در هم کوبيده و متلاشي گردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5338, N'در آن روز «واقعه عظيم» روي مي‌دهد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5339, N'و آسمان از هم مي‌شکافد و سست مي‌گردد و فرومي‌ريزد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5340, N'فرشتگان در اطراف آسمان قرارمي‌گيرند (و براي انجام مأموريتها آماده مي‌شوند)؛ و آن روز عرش پروردگارت را هشت فرشته بر فراز همه آنها حمل مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5341, N'در آن روز همگي به پيشگاه خدا عرضه مي‌شويد و چيزي از کارهاي شما پنهان نمي‌ماند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5342, N'پس کسي که نامه اعمالش را به دست راستش دهند (از شدت شادي و مباهات) فرياد مي‌زند که: «(اي اهل محشر!) نامه اعمال مرا بگيريد و بخوانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5343, N'من يقين داشتم که (قيامتي در کار است و) به حساب اعمالم مي‌رسم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5344, N'او در يک زندگي (کاملا) رضايتبخش قرار خواهد داشت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5345, N'در بهشتي عالي،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5346, N'که ميوه هايش در دسترس است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5347, N'(و به آنان گفته مي‌شود:) بخوريد و بياشاميد گوارا در برابر اعمالي که در ايام گذشته انجام داديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5348, N'اما کسي که نامه اعمالش را به دست چپش بدهند مي‌گويد: «اي کاش هرگز نامه اعمالم را به من نمي‌دادند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5349, N'و نمي‌دانستم حساب من چيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5350, N'اي کاش مرگم فرا مي‌رسيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5351, N'مال و ثروتم هرگز مرا بي‌نياز نکرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5352, N'قدرت من نيز از دست رفت!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5353, N'او را بگيريد و دربند و زنجيرش کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5354, N'سپس او را در دوزخ بيفکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5355, N'بعد او را به زنجيري که هفتاد ذراع است ببنديد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5356, N'چرا که او هرگز به خداوند بزرگ ايمان نمي‌آورد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5357, N'و هرگز مردم را بر اطعام مستمندان تشويق نمي‌نمود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5358, N'از اين رو امروز هم در اينجا يار مهرباني ندارد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5359, N'و نه طعامي، جز از چرک و خون!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5360, N'غذايي که جز خطاکاران آن را نمي‌خورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5361, N'سوگند به آنچه مي‌بينيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5362, N'و آنچه نمي‌بينيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5363, N'که اين قرآن گفتار رسول بزرگواري است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5364, N'و گفته شاعري نيست، اما کمتر ايمان مي‌آوريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5365, N'و نه گفته کاهني، هر چند کمتر متذکر مي‌شويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5366, N'کلامي است که از سوي پروردگار عالميان نازل شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5367, N'اگر او سخني دروغ بر ما مي‌بست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5368, N'ما او را با قدرت مي‌گرفتيم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5369, N'سپس رگ قلبش را قطع مي‌کرديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5370, N'و هيچ کس از شما نمي‌توانست از (مجازات) او مانع شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5371, N'و آن مسلما تذکري براي پرهيزگاران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5372, N'و ما مي‌دانيم که بعضي از شما (آن را) تکذيب مي‌کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5373, N'و آن مايه حسرت کافران است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5374, N'و آن يقين خالص است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5375, N'حال که چنين است به نام پروردگار بزرگت تسبيح گوي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5376, N'تقاضاکننده‌اي تقاضاي عذابي کرد که واقع شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5377, N'اين عذاب مخصوص کافران است، و هيچ کس نمي‌تواند آن را دفع کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5378, N'از سوي خداوند ذي المعارج [= خداوندي که فرشتگانش بر آسمانها صعود و عروج مي‌کنند]!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5379, N'فرشتگان و روح [= فرشته مقرب خداوند] بسوي او عروج مي‌کنند در آن روزي که مقدارش پنجاه هزار سال است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5380, N'پس صبر جميل پيشه کن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5381, N'زيرا آنها آن روز را دور مي‌بينند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5382, N'و ما آن را نزديک مي‌بينيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5383, N'همان روز که آسمان همچون فلز گداخته مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5384, N'و کوه‌ها مانند پشم رنگين متلاشي خواهد بود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5385, N'و هيچ دوست صميمي سراغ دوستش را نمي‌گيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5386, N'آنها را نشانشان مي‌دهند (ولي هر کس گرفتار کار خويشتن است)، چنان است که گنهکار دوست مي‌دارد فرزندان خود را در برابر عذاب آن روز فدا کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5387, N'و همسر و برادرش را،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5388, N'و قبيله‌اش را که هميشه از او حمايت مي‌کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5389, N'و همه مردم روي زمين را تا مايه نجاتش گردند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5390, N'اما هرگز چنين نيست (که با اينها بتوان نجات يافت، آري) شعله‌هاي سوزان آتش است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5391, N'دست و پا و پوست سر را مي‌کند و مي‌برد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5392, N'و کساني را که به فرمان خدا پشت کردند صدا مي‌زند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5393, N'و (همچنين آنها که) اموال را جمع و ذخيره کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5394, N'به يقين انسان حريص و کم‌طاقت آفريده شده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5395, N'هنگامي که بدي به او رسد بيتابي مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5396, N'و هنگامي که خوبي به او رسد مانع ديگران مي‌شود (و بخل مي‌ورزد)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5397, N'مگر نمازگزاران،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5398, N'آنها که نمازها را پيوسته بجا مي‌آورند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5399, N'و آنها که در اموالشان حق معلومي است...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5400, N'براي تقاضاکننده و محروم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5401, N'و آنها که به روز جزا ايمان دارند،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5402, N'و آنها که از عذاب پروردگارشان بيمناکند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5403, N'چرا که هيچ کس از عذاب پروردگارش در امان نيست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5404, N'و آنها که دامان خويش را (از بي‌عفتي) حفظ مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5405, N'جز با همسران و کنيزان (که در حکم همسرند آميزش ندارند)، چرا که در بهره‌گيري از اينها مورد سرزنش نخواهند بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5406, N'و هر کس جز اينها را طلب کند، متجاوز است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5407, N'و آنها که امانتها و عهد خود را رعايت مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5408, N'و آنها که با اداي شهادتشان قيام مي‌نمايند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5409, N'و آنها که بر نماز مواظبت دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5410, N'آنان در باغهاي بهشتي (پذيرايي و) گرامي داشته مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5411, N'اين کافران را چه مي‌شود که با سرعت نزد تو مي‌آيند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5412, N'از راست و چپ، گروه گروه (و آرزوي بهشت دارند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5413, N'آيا هر يک از آنها (با اين اعمال زشتش) طمع دارد که او را در بهشت پر نعمت الهي وارد کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5414, N'هرگز چنين نيست؛ ما آنها را از آنچه خودشان مي‌دانند آفريده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5415, N'سوگند به پروردگار مشرقها و مغربها که ما قادريم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5416, N'که جاي آنان را به کساني بدهيم که از آنها بهترند؛ و ما هرگز مغلوب نخواهيم شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5417, N'آنان را به حال خود واگذار تا در باطل خود فروروند و بازي کنند تا زماني که روز موعود خود را ملاقات نمايند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5418, N'همان روز که از قبرها بسرعت خارج مي‌شوند، گويي به سوي بتها مي‌دوند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5419, N'در حالي که چشمهايشان از شرم و وحشت به زير افتاده، و پرده‌اي از ذلت و خواري آنها را پوشانده است! اين همان روزي است که به آنها وعده داده مي‌شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5420, N'ما نوح را به سوي قومش فرستاديم و گفتيم: «قوم خود را انذار کن پيش از آنکه عذاب دردناک به سراغشان آيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5421, N'گفت: «اي قوم! من براي شما بيم‌دهنده آشکاري هستم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5422, N'که خدا را پرستش کنيد و از مخالفت او بپرهيزيد و مرا اطاعت نماييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5423, N'اگر چنين کنيد، خدا گناهانتان را مي‌آمرزد و تا زمان معيني شما را عمر مي‌دهد؛ زيرا هنگامي که اجل الهي فرا رسد، تأخيري نخواهد داشت اگر مي‌دانستيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5424, N'(نوح) گفت: «پروردگارا! من قوم خود را شب و روز (بسوي تو) دعوت کردم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5425, N'اما دعوت من چيزي جز فرار از حق بر آنان نيفزود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5426, N'و من هر زمان آنها را دعوت کردم که (ايمان بياورند و) تو آنها را بيامرزي، انگشتان خويش را در گوشهايشان قرار داده و لباسهايشان را بر خود پيچيدند، و در مخالفت اصرار ورزيدند و به شدت استکبار کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5427, N'سپس من آنها را با صداي بلند (به اطاعت فرمان تو) دعوت کردم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5428, N'سپس آشکارا و نهان (حقيقت توحيد و ايمان را) براي آنان بيان داشتم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5429, N'به آنها گفتم: «از پروردگار خويش آمرزش بطلبيد که او بسيار آمرزنده است...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5430, N'تا بارانهاي پربرکت آسمان را پي در پي بر شما فرستد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5431, N'و شما را با اموال و فرزندان فراوان کمک کند و باغهاي سرسبز و نهرهاي جاري در اختيارتان قرار دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5432, N'چرا شما براي خدا عظمت قائل نيستيد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5433, N'در حالي که شما را در مراحل مختلف آفريد (تا از نطفه به انسان کامل رسيديد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5434, N'آيا نمي‌دانيد چگونه خداوند هفت آسمان را يکي بالاي ديگري آفريده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5435, N'و ماه را در ميان آسمانها مايه روشنايي، و خورشيد را چراغ فروزاني قرار داده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5436, N'و خداوند شما را همچون گياهي از زمين رويانيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5437, N'سپس شما را به همان زمين بازمي‌گرداند، و بار ديگر شما را خارج مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5438, N'و خداوند زمين را براي شما فرش گسترده‌اي قرار داد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5439, N'تا از راههاي وسيع و دره‌هاي آن بگذريد (و به هر جا مي‌خواهيد برويد)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5440, N'نوح (بعد از نوميدي از هدايت آنان) گفت: «پروردگارا! آنها نافرماني من کردند و از کساني پيروي نمودند که اموال و فرزندانشان چيزي جز زيانکاري بر آنها نيفزوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5441, N'و (اين رهبران گمراه) مکر عظيمي به کار بردند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5442, N'و گفتند: دست از خدايان و بتهاي خود برنداريد (به خصوص) بتهاي «ود»، «سواع»، «يغوث»، «يعوق» و «نسر» را رها نکنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5443, N'و آنها گروه بسياري را گمراه کردند! خداوندا، ظالمان را جز ضلالت ميفزا!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5444, N'(آري، سرانجام) همگي بخاطر گناهانشان غرق شدند و در آتش دوزخ وارد گشتند، و جز خدا ياوراني براي خود نيافتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5445, N'نوح گفت: «پروردگارا! هيچ يک از کافران را بر روي زمين باقي مگذار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5446, N'چرا که اگر آنها را باقي بگذاري، بندگانت را گمراه مي‌کنند و جز نسلي فاجر و کافر به وجود نمي‌آورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5447, N'پروردگارا! مرا، و پدر و مادرم و تمام کساني را که با ايمان وارد خانه من شدند، و جميع مردان و زنان باايمان را بيامرز؛ و ظالمان را جز هلاکت ميفزا!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5448, N'بگو: به من وحي شده است که جمعي از جن به سخنانم گوش فراداده‌اند، سپس گفته‌اند: «ما قرآن عجيبي شنيده‌ايم...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5449, N'که به راه راست هدايت مي‌کند، پس ما به آن ايمان آورده‌ايم و هرگز کسي را شريک پروردگارمان قرارنمي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5450, N'و اينکه بلند است مقام باعظمت پروردگار ما، و او هرگز براي خود همسر و فرزندي انتخاب نکرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5451, N'و اينکه سفيه ما (ابليس) درباره خداوند سخنان ناروا مي‌گفت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5452, N'و اينکه ما گمان مي‌کرديم که انس و جن هرگز بر خدا دروغ نمي‌بندند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5453, N'و اينکه مرداني از بشر به مرداني از جن پناه مي‌بردند، و آنها سبب افزايش گمراهي و طغيانشان مي‌شدند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5454, N'و اينکه آنها گمان کردند -همان‌گونه که شما گمان مي‌کرديد- که خداوند هرگز کسي را (به نبوت )مبعوث نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5455, N'و اينکه ما آسمان را جستجو کرديم و همه را پر از محافظان قوي و تيرهاي شهاب يافتيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5456, N'و اينکه ما پيش از اين به استراق سمع در آسمانها مي‌نشستيم؛ اما اکنون هر کس بخواهد استراق سمع کند، شهابي را در کمين خود مي‌يابد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5457, N'و اينکه (با اين اوضاع) ما نمي‌دانيم آيا اراده شري درباره اهل زمين شده يا پروردگارشان خواسته است آنان را هدايت کند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5458, N'و اينکه در ميان ما، افرادي صالح و افرادي غير صالحند؛ و ما گروه‌هاي متفاوتي هستيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5459, N'و اينکه ما يقين داريم هرگز نمي‌توانيم بر اراده خداوند در زمين غالب شويم و نمي‌توانيم از (پنجه قدرت) او بگريزيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5460, N'و اينکه ما هنگامي که هدايت قرآن را شنيديم به آن ايمان آورديم؛ و هر کس به پروردگارش ايمان بياورد، نه از نقصان مي‌ترسد و نه از ظلم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5461, N'و اينکه گروهي از ما مسلمان و گروهي ظالمند؛ هر کس اسلام را اختيار کند راه راست را برگزيده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5462, N'و اما ظالمان آتشگيره و هيزم دوزخند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5463, N'و اينکه اگر آنها [= جن و انس‌] در راه (ايمان) استقامت ورزند، با آب فراوان سيرابشان مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5464, N'هدف اين است که ما آنها را با اين نعمت فراوان بيازماييم؛ و هر کس از ياد پروردگارش روي گرداند، او را به عذاب شديد و فزاينده‌اي گرفتار مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5465, N'و اينکه مساجد از آن خداست، پس هيچ کس را با خدا نخوانيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5466, N'و اينکه هنگامي که بنده خدا [= محمد (ص)] به عبادت برمي‌خاست و او را مي‌خواند، گروهي پيرامون او بشدت ازدحام مي‌کردند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5467, N'بگو: «من تنها پروردگارم را مي‌خوانم و هيچ کس را شريک او قرار نمي‌دهم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5468, N'بگو: «من مالک زيان و هدايتي براي شما نيستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5469, N'بگو: «(اگر من نيز بر خلاف فرمانش رفتار کنم) هيچ کس مرا در برابر او حمايت نمي‌کند و پناهگاهي جز او نمي‌يابم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5470, N'تنها وظيفه من ابلاغ از سوي خدا و رساندن رسالات اوست؛ و هر کس نافرماني خدا و رسولش کند، آتش دوزخ از آن اوست و جاودانه در آن مي‌مانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5471, N'(اين کار شکني کفار همچنان ادامه مي‌يابد) تا آنچه را به آنها وعده داده شده ببينند؛ آنگاه مي‌دانند چه کسي ياورش ضعيفتر و جمعيتش کمتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5472, N'بگو: «من نمي‌دانم آنچه به شما وعده داده شده نزديک است يا پروردگارم زماني براي آن قرارمي‌دهد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5473, N'داناي غيب اوست و هيچ کس را بر اسرار غيبش آگاه نمي‌سازد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5474, N'مگر رسولاني که آنان را برگزيده و مراقبيني از پيش رو و پشت سر براي آنها قرار مي‌دهد...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5475, N'تا بداند پيامبرانش رسالتهاي پروردگارشان را ابلاغ کرده‌اند؛ و او به آنچه نزد آنهاست احاطه دارد و همه چيز را احصار کرده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5476, N'اي جامه به خود پيچيده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5477, N'شب را، جز کمي، بپاخيز!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5478, N'نيمي از شب را، يا کمي از آن کم کن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5479, N'يا بر نصف آن بيفزا، و قرآن را با دقت و تأمل بخوان؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5480, N'چرا که ما بزودي سخني سنگين به تو القا خواهيم کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5481, N'مسلما نماز و عبادت شبانه پابرجاتر و با استقامت‌تر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5482, N'و تو در روز تلاش مستمر و طولاني خواهي داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5483, N'و نام پروردگارت را ياد کن و تنها به او دل ببند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5484, N'همان پروردگار شرق و غرب که معبودي جز او نيست، او را نگاهبان و وکيل خود انتخاب کن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5485, N'و در برابر آنچه (دشمنان) مي‌گويند شکيبا باش و بطرزي شايسته از آنان دوري گزين!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5486, N'و مرا با تکذيب‌کنندگان صاحب نعمت واگذار، و آنها را کمي مهلت ده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5487, N'که نزد ما غل و زنجيرها و (آتش) دوزخ است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5488, N'و غذايي گلوگير، و عذابي دردناک،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5489, N'در آن روز که زمين و کوه‌ها سخت به لرزه درمي‌آيد، و کوه‌ها (چنان درهم کوبيده مي‌شود که) به شکل توده‌هايي از شن نرم درمي‌آيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5490, N'ما پيامبري به سوي شما فرستاديم که گواه بر شماست، همان گونه که به سوي فرعون رسولي فرستاديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5491, N'(ولي) فرعون به مخالفت و نافرماني آن رسول برخاست، و ما او را سخت مجازات کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5492, N'شما (نيز) اگر کافر شويد، چگونه خود را (از عذاب الهي) بر کنار مي‌داريد؟! در آن روز که کودکان را پير مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5493, N'و آسمان از هم شکافته مي‌شود، و وعده او شدني و حتمي است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5494, N'اين هشدار و تذکري است، پس هر کس بخواهد راهي به سوي پروردگارش برمي‌گزيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5495, N'پروردگارت مي‌داند که تو و گروهي از آنها که با تو هستند نزديک دو سوم از شب يا نصف يا ثلث آن را به پا مي‌خيزند؛ خداوند شب و روز را اندازه‌گيري مي‌کند؛ او مي‌داند که شما نمي‌توانيد مقدار آن را (به دقت) اندازه‌گيري کنيد (براي عبادت کردن)، پس شما را بخشيد؛ اکنون آنچه براي شما ميسر است قرآن بخوانيد او مي‌داند بزودي گروهي از شما بيمار مي‌شوند، و گروهي ديگر براي به دست آوردن فضل الهي (و کسب روزي) به سفر مي‌روند، و گروهي ديگر در راه خدا جهاد مي‌کنند (و از تلاوت قرآن بازمي‌مانند)، پس به اندازه‌اي که براي شما ممکن است از آن تلاوت کنيد و نماز را بر پا داريد و زکات بپردازيد و به خدا «قرض الحسنه» دهيد [= در راه او انفاق نماييد] و (بدانيد) آنچه را از کارهاي نيک براي خود از پيش مي‌فرستيد نزد خدا به بهترين وجه و بزرگترين پاداش خواهيد يافت؛ و از خدا آمرزش بطلبيد که خداوند آمرزنده و مهربان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5496, N'اي جامه خواب به خود پيچيده (و در بستر آرميده)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5497, N'برخيز و انذار کن (و عالميان را بيم ده)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5498, N'و پروردگارت را بزرگ بشمار،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5499, N'و لباست را پاک کن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5500, N'و از پليدي دوري کن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5501, N'و منت مگذار و فزوني مطلب،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5502, N'و بخاطر پروردگارت شکيبايي کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5503, N'هنگامي که در «صور» دميده شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5504, N'آن روز، روز سختي است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5505, N'و براي کافران آسان نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5506, N'مرا با کسي که او را خود به تنهايي آفريده‌ام واگذار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5507, N'همان کسي که براي او مال گسترده‌اي قرار دادم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5508, N'و فرزنداني که همواره نزد او (و در خدمت او) هستند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5509, N'و وسايل زندگي را از هر نظر براي وي فراهم ساختم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5510, N'باز هم طمع دارد که بر او بيفزايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5511, N'هرگز چنين نخواهد شد؛ چرا که او نسبت به آيات ما دشمني مي‌ورزد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5512, N'و بزودي او را مجبور مي‌کنم که از قله زندگي بالا رود (سپس او را به زير مي‌افکنم)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5513, N'او (براي مبارزه با قرآن) انديشه کرد و مطلب را آماده ساخت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5514, N'مرگ بر او باد! چگونه (براي مبارزه با حق) مطلب را آماده کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5515, N'باز هم مرگ بر او، چگونه مطلب (و نقشه شيطاني خود را) آماده نمود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5516, N'سپس نگاهي افکند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5517, N'بعد چهره درهم کشيد و عجولانه دست به کار شد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5518, N'سپس پشت (به حق) کرد و تکبر ورزيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5519, N'و سرانجام گفت: «اين (قرآن) چيزي جز افسون و سحري همچون سحرهاي پيشينيان نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5520, N'اين فقط سخن انسان است (نه گفتار خدا)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5521, N'(اما) بزودي او را وارد سقر [= دوزخ‌] مي‌کنم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5522, N'و تو نمي‌داني «سقر» چيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5523, N'(آتشي است که) نه چيزي را باقي مي‌گذارد و نه چيزي را رها مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5524, N'پوست تن را بکلي دگرگون مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5525, N'نوزده نفر (از فرشتگان عذاب) بر آن گمارده شده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5526, N'مأموران دوزخ را فقط فرشتگان (عذاب) قرار داديم، و تعداد آنها را جز براي آزمايش کافران معين نکرديم تا اهل کتاب [= يهود و نصاري‌] يقين پيدا کنند و بر ايمان مؤمنان بيفزايد، و اهل کتاب و مؤمنان (در حقانيت اين کتاب آسماني) ترديد به خود راه ندهند، و بيماردلان و کافران بگويند: «خدا از اين توصيف چه منظوري دارد؟!» (آري) اين گونه خداوند هر کس را بخواهد گمراه مي‌سازد و هر کس را بخواهد هدايت مي‌کند! و لشکريان پروردگارت را جز او کسي نمي‌داند، و اين جز هشدار و تذکري براي انسانها نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5527, N'اينچنين نيست که آنها تصور مي‌کنند سوگند به ماه،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5528, N'و به شب، هنگامي که (دامن برچيند و) پشت کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5529, N'و به صبح هنگامي که چهره بگشايد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5530, N'که آن (حوادث هولناک قيامت) از مسائل مهم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5531, N'هشدار و انذاري است براي همه انسانها،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5532, N'براي کساني از شما که مي‌خواهند پيش افتند يا عقب بمانند [= بسوي هدايت و نيکي پيش روند يا نروند]!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5533, N'(آري) هر کس در گرو اعمال خويش است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5534, N'مگر «اصحاب يمين» (که نامه اعمالشان را به نشانه ايمان و تقوايشان به دست راستشان مي‌دهند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5535, N'آنها در باغهاي بهشتند، و سؤال مي‌کنند...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5536, N'از مجرمان:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5537, N'چه چيز شما را به دوزخ وارد ساخت؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5538, N'مي‌گويند: «ما از نمازگزاران نبوديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5539, N'و اطعام مستمند نمي‌کرديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5540, N'و پيوسته با اهل باطل همنشين و همصدا بوديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5541, N'و همواره روز جزا را انکار مي‌کرديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5542, N'تا زماني که مرگ ما فرا رسيد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5543, N'از اين رو شفاعت شفاعت‌کنندگان به حال آنها سودي نمي‌بخشد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5544, N'چرا آنها از تذکر روي گردانند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5545, N'گويي گورخراني رميده‌اند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5546, N'که از (مقابل) شيري فرار کرده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5547, N'بلکه هر کدام از آنها انتظار دارد نامه جداگانه‌اي (از سوي خدا) براي او فرستاده شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5548, N'چنين نيست که آنان مي‌گويند، بلکه آنها از آخرت نمي‌ترسند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5549, N'چنين نيست که آنها مي‌گويند، آن (قرآن) يک تذکر و يادآوري است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5550, N'هر کس بخواهد از آن پند مي‌گيرد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5551, N'و هيچ کس پند نمي‌گيرد مگر اينکه خدا بخواهد؛ او اهل تقوا و اهل آمرزش است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5552, N'سوگند به روز قيامت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5553, N'و سوگند به (نفس لوامه و) وجدان بيدار و ملامتگر (که رستاخيز حق است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5554, N'آيا انسان مي‌پندارد که هرگز استخوانهاي او را جمع نخواهيم کرد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5555, N'آري قادريم که (حتي خطوط سر) انگشتان او را موزون و مرتب کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5556, N'(انسان شک در معاد ندارد) بلکه او مي‌خواهد (آزاد باشد و بدون ترس از دادگاه قيامت) در تمام عمر گناه کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5557, N'(از اين رو) مي‌پرسد: «قيامت کي خواهد بود»!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5558, N'(بگو:) در آن هنگام که چشمها از شدت وحشت به گردش درآيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5559, N'و ماه بي‌نور گردد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5560, N'و خورشيد و ماه يک جا جمع شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5561, N'آن روز انسان مي‌گويد: «راه فرار کجاست؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5562, N'هرگز چنين نيست، راه فرار و پناهگاهي وجود ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5563, N'آن روز قرارگاه نهايي تنها بسوي پروردگار تو است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5564, N'و در آن روز انسان را از تمام کارهايي که از پيش يا پس فرستاده آگاه مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5565, N'بلکه انسان خودش از وضع خود آگاه است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5566, N'هر چند (در ظاهر) براي خود عذرهايي بتراشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5567, N'زبانت را بخاطر عجله براي خواندن آن [= قرآن‌] حرکت مده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5568, N'چرا که جمع‌کردن و خواندن آن بر عهده ماست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5569, N'پس هر گاه آن را خوانديم، از خواندن آن پيروي کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5570, N'سپس بيان و (توضيح) آن (نيز) بر عهده ماست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5571, N'چنين نيست که شما مي‌پنداريد (و دلايل معاد را کافي نمي‌دانيد)؛ بلکه شما دنياي زودگذر را دوست داريد (و هوسراني بي‌قيد و شرط را)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5572, N'و آخرت را رها مي‌کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5573, N'(آري) در آن روز صورتهايي شاداب و مسرور است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5574, N'و به پروردگارش مي‌نگرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5575, N'و در آن روز صورتهايي عبوس و در هم کشيده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5576, N'زيرا مي‌داند عذابي در پيش دارد که پشت را در هم مي‌شکند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5577, N'چنين نيست (که انسان مي‌پندارد! او ايمان نمي‌آورد) تا موقعي که جان به گلوگاهش رسد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5578, N'و گفته شود: «آيا کسي هست که (اين بيمار را از مرگ) نجات دهد؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5579, N'و به جدائي از دنيا يقين پيدا کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5580, N'و ساق پاها (از سختي جان‌دادن) به هم بپيچد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5581, N'(آري) در آن روز مسير همه بسوي (دادگاه) پروردگارت خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5582, N'(در آن روز گفته مي‌شود:) او هرگز ايمان نياورد و نماز نخواند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5583, N'بلکه تکذيب کرد و روي‌گردان شد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5584, N'سپس بسوي خانواده خود بازگشت در حالي که متکبرانه قدم برمي‌داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5585, N'(با اين اعمال) عذاب الهي براي تو شايسته‌تر است، شايسته‌تر!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5586, N'سپس عذاب الهي براي تو شايسته‌تر است، شايسته‌تر!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5587, N'آيا انسان گمان مي‌کند بي‌هدف رها مي‌شود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5588, N'آيا او نطفه‌اي از مني که در رحم ريخته مي‌شود نبود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5589, N'سپس بصورت خون بسته در آمد، و خداوند او را آفريد و موزون ساخت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5590, N'و از او دو زوج مرد و زن آفريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5591, N'آيا چنين کسي قادر نيست که مردگان را زنده کند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5592, N'آيا زماني طولاني بر انسان گذشت که چيز قابل ذکري نبود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5593, N'ما انسان را از نطفه مختلطي آفريديم، و او را مي‌آزماييم؛ (بدين جهت) او را شنوا و بينا قرار داديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5594, N'ما راه را به او نشان داديم، خواه شاکر باشد (و پذيرا گردد) يا ناسپاس!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5595, N'ما براي کافران، زنجيرها و غلها و شعله‌هاي سوزان آتش آماده کرده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5596, N'به يقين ابرار (و نيکان) از جامي مي‌نوشند که با عطر خوشي آميخته است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5597, N'از چشمه‌اي که بندگان خاص خدا از آن مي‌نوشند، و از هر جا بخواهند آن را جاري مي‌سازند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5598, N'آنها به نذر خود وفا مي‌کنند، و از روزي که شر و عذابش گسترده است مي‌ترسند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5599, N'و غذاي (خود) را با اينکه به آن علاقه (و نياز) دارند، به «مسکين» و «يتيم» و «اسير» مي‌دهند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5600, N'(و مي‌گويند:) ما شما را بخاطر خدا اطعام مي‌کنيم، و هيچ پاداش و سپاسي از شما نمي‌خواهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5601, N'ما از پروردگارمان خائفيم در آن روزي که عبوس و سخت است!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5602, N'(بخاطر اين عقيده و عمل) خداوند آنان را از شر آن روز نگه مي‌دارد و آنها را مي‌پذيرد در حالي که غرق شادي و سرورند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5603, N'و در برابر صبرشان، بهشت و لباسهاي حرير بهشتي را به آنها پاداش مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5604, N'اين در حالي است که در بهشت بر تختهاي زيبا تکيه کرده‌اند، نه آفتاب را در آنجا مي‌بينند و نه سرما را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5605, N'و در حالي است که سايه‌هاي درختان بهشتي بر آنها فرو افتاده و چيدن ميوه‌هايش بسيار آسان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5606, N'و در گرداگرد آنها ظرفهايي سيمين و قدحهايي بلورين مي‌گردانند (پر از بهترين غذاها و نوشيدني‌ها)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5607, N'ظرفهاي بلوريني از نقره، که آنها را به اندازه مناسب آماده کرده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5608, N'و در آنجا از جامهايي سيراب مي‌شوند که لبريز از شراب طهوري آميخته با زنجبيل است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5609, N'از چشمه‌اي در بهشت که نامش سلسبيل است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5610, N'و بر گردشان (براي پذيرايي) نوجواناني جاوداني مي‌گردند که هرگاه آنها را ببيني گمان مي‌کني مرواريد پراکنده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5611, N'و هنگامي که آنجا را ببيني نعمتها و ملک عظيمي را مي‌بيني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5612, N'بر اندام آنها [= بهشتيان‌] لباسهايي است از حرير نازک سبزرنگ، و از ديباي ضخيم، و با دستبندهايي از نقره آراسته‌اند، و پروردگارشان شراب طهور به آنان مي‌نوشاند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5613, N'اين پاداش شماست، و سعي و تلاش شما مورد قدرداني است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5614, N'مسلما ما قرآن را بر تو نازل کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5615, N'پس در (تبليغ و اجراي) حکم پروردگارت شکيبا (و با استقامت) باش، و از هيچ گنهکار يا کافري از آنان اطاعت مکن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5616, N'و نام پروردگارت را هر صبح و شام به ياد آور!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5617, N'و در شبانگاه براي او سجده کن، و مقداري طولاني از شب، او را تسبيح گوي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5618, N'آنها زندگي زودگذر دنيا را دوست دارند، در حالي که روز سختي را پشت سر خود رها مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5619, N'ما آنها را آفريديم و پيوندهاي وجودشان را محکم کرديم، و هر زمان بخواهيم جاي آنان را به گروه ديگري مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5620, N'اين يک تذکر و يادآوري است، و هر کس بخواهد (با استفاده از آن) راهي به سوي پروردگارش برمي‌گزيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5621, N'و شما هيچ چيز را نمي‌خواهيد مگر اينکه خدا بخواهد، خداوند دانا و حکيم بوده و هست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5622, N'و هر کس را بخواهد (و شايسته بداند) در رحمت (وسيع) خود وارد مي‌کند، و براي ظالمان عذاب دردناکي آماده ساخته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5623, N'سوگند به فرشتگاني که پي در پي فرستاده مي‌شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5624, N'و آنها که همچون تند باد حرکت مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5625, N'و سوگند به آنها که (ابرها را) مي‌گسترانند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5626, N'و آنها که جدا مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5627, N'و سوگند به آنها که آيات بيدارگر (الهي) را (به انبيا) القا مي‌نمايند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5628, N'براي اتمام حجت يا براي انذار،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5629, N'که آنچه به شما (درباره قيامت) وعده داده مي‌شود، يقينا واقع‌شدني است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5630, N'در آن هنگام که ستارگان محو و تاريک شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5631, N'و (کرات) آسمان از هم بشکافند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5632, N'و در آن زمان که کوه‌ها از جا کنده شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5633, N'و در آن هنگام که براي پيامبران (بمنظور اداي شهادت) تعيين وقت شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5634, N'(اين امر) براي چه روزي به تأخير افتاده؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5635, N'براي روز جدايي (حق از باطل)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5636, N'تو چه مي‌داني روز جدايي چيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5637, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5638, N'آيا ما اقوام (مجرم) نخستين را هلاک نکرديم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5639, N'سپس ديگر (مجرمان) را به دنبال آنها مي‌فرستيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5640, N'(آري) اين گونه با مجرمان رفتار مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5641, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5642, N'آيا شما را از آبي پست و ناچيز نيافريديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5643, N'سپس آن را در قرارگاهي محفوظ و آماده قرار داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5644, N'تا مدتي معين؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5645, N'ما قدرت بر اين کار داشتيم، پس ما قدرتمند خوبي هستيم (و امر معاد براي ما آسان است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5646, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5647, N'آيا زمين را مرکز اجتماع انسانها قرار نداديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5648, N'هم در حال حياتشان و هم مرگشان؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5649, N'و در آن کوه‌هاي استوار و بلندي قرار داديم، و آبي گوارا به شما نوشانديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5650, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5651, N'(در آن روز به آنها گفته مي‌شود:) بي‌درنگ، به سوي همان چيزي که پيوسته آن را تکذيب مي‌کرديد برويد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5652, N'برويد به سوي سايه سه شاخه (دودهاي خفقان‌بار و آتش‌زا)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5653, N'سايه‌اي که نه آرامبخش است و نه از شعله‌هاي آتش جلوگيري مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5654, N'شراره‌هايي از خود پرتاب مي‌کند مانند يک کاخ!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5655, N'گويي (در سرعت و کثرت) همچون شتران زردرنگي هستند (که به هر سو پراکنده مي‌شوند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5656, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5657, N'امروز روزي است که سخن نمي‌گويند (و قادر بر دفاع از خويشتن نيستند)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5658, N'و به آنها اجازه داده نمي‌شود که عذرخواهي کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5659, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5660, N'(و به آنها گفته مي‌شود:) امروز همان روز جدايي (حق از باطل) است که شما و پيشينيان را در آن جمع کرده‌ايم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5661, N'اگر چاره‌اي در برابر من (براي فرار از چنگال مجازات) داريد انجام دهيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5662, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5663, N'(در آن روز) پرهيزگاران در سايه‌هاي (درختان بهشتي) و در ميان چشمه‌ها قرار دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5664, N'و ميوه‌هايي از آنچه مايل باشند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5665, N'بخوريد و بنوشيد گوارا، اينها در برابر اعمالي است که انجام مي‌داديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5666, N'ما اين گونه نيکوکاران را پاداش مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5667, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5668, N'(و به مجرمان بگو:) بخوريد و بهره گيريد در اين مدت کم (از زندگي دنيا، ولي بدانيد عذاب الهي در انتظار شماست) چرا که شما مجرميد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5669, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5670, N'و هنگامي که به آنها گفته شود رکوع کنيد رکوع نمي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5671, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5672, N'(و اگر آنها به اين قرآن ايمان نمي‌آورند) پس به کدام سخن بعد از آن ايمان مي‌آورند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5673, N'آنها از چه چيز از يکديگر سؤال مي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5674, N'از خبر بزرگ و پراهميت (رستاخيز)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5675, N'همان خبري که پيوسته در آن اختلاف دارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5676, N'چنين نيست که آنها فکر مي‌کنند، و بزودي مي‌فهمند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5677, N'باز هم چنين نيست که آنها مي‌پندارند، و بزودي مي‌فهمند (که قيامت حق است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5678, N'آيا زمين را محل آرامش (شما) قرار نداديم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5679, N'و کوه‌ها را ميخهاي زمين؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5680, N'و شما را بصورت زوجها آفريديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5681, N'و خواب شما را مايه آرامشتان قرار داديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5682, N'و شب را پوششي (براي شما)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5683, N'و روز را وسيله‌اي براي زندگي و معاش!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5684, N'و بر فراز شما هفت (آسمان) محکم بنا کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5685, N'و چراغي روشن و حرارت‌بخش آفريديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5686, N'و از ابرهاي باران‌زا آبي فراوان نازل کرديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5687, N'تا بوسيله آن دانه و گياه بسيار برويانيم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5688, N'و باغهايي پردرخت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5689, N'(آري) روز جدايي، ميعاد همگان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5690, N'روزي که در «صور» دميده مي‌شود و شما فوج فوج (به محشر) مي‌آييد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5691, N'و آسمان گشوده مي‌شود و بصورت درهاي متعددي درمي‌آيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5692, N'و کوه‌ها به حرکت درمي‌آيد و بصورت سرابي مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5693, N'مسلما (در آن روز) جهنم کمينگاهي است بزرگ،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5694, N'و محل بازگشتي براي طغيانگران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5695, N'مدتهاي طولاني در آن مي‌مانند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5696, N'در آنجا نه چيز خنکي مي‌چشند و نه نوشيدني گوارايي،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5697, N'جز آبي سوزان و مايعي از چرک و خون!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5698, N'اين مجازاتي است موافق و مناسب (اعمالشان)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5699, N'چرا که آنها هيچ اميدي به حساب نداشتند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5700, N'و آيات ما را بکلي تکذيب کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5701, N'و ما همه چيز را شمارش و ثبت کرده‌ايم!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5702, N'پس بچشيد که چيزي جز عذاب بر شما نمي‌افزاييم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5703, N'مسلما براي پرهيزگاران نجات و پيروزي بزرگي است:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5704, N'باغهايي سرسبز، و انواع انگورها،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5705, N'و حورياني بسيار جوان و هم‌سن و سال،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5706, N'و جامهايي لبريز و پياپي (از شراب طهور)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5707, N'در آنجا نه سخن لغو و بيهوده‌اي مي‌شنوند و نه دروغي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5708, N'اين کيفري است از سوي پروردگارت و عطيه‌اي است کافي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5709, N'همان پروردگار آسمانها و زمين و آنچه در ميان آن دو است، پروردگار رحمان! و (در آن روز) هيچ کس حق ندارد بي اجازه او سخني بگويد (يا شفاعتي کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5710, N'روزي که «روح» و «ملائکه» در يک صف مي‌ايستند و هيچ يک، جز به اذن خداوند رحمان، سخن نمي‌گويند، و (آنگاه که مي‌گويند) درست مي‌گويند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5711, N'آن روز حق است؛ هر کس بخواهد راهي به سوي پروردگارش برمي‌گزيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5712, N'و ما شما را از عذاب نزديکي بيم داديم! اين عذاب در روزي خواهد بود که انسان آنچه را از قبل با دستهاي خود فرستاده مي‌بيند، و کافر مي‌گويد: «اي کاش خاک بودم (و گرفتار عذاب نمي‌شدم)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5713, N'سوگند به فرشتگاني که (جان مجرمان را بشدت از بدنهايشان) برمي‌کشند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5714, N'و فرشتگاني که (روح مؤمنان) را با مدارا و نشاط جدا مي‌سازند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5715, N'و سوگند به فرشتگاني که (در اجراي فرمان الهي) با سرعت حرکت مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5716, N'و سپس بر يکديگر سبقت مي‌گيرند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5717, N'و آنها که امور را تدبير مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5718, N'آن روز که زلزله‌هاي وحشتناک همه چيز را به لرزه درمي‌آورد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5719, N'و بدنبال آن، حادثه دومين [= صيحه عظيم محشر] رخ مي‌دهد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5720, N'دلهايي در آن روز سخت مضطرب است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5721, N'و چشمهاي آنان از شدت ترس فروافتاده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5722, N'(ولي امروز) مي‌گويند: «آيا ما به زندگي مجدد بازمي‌گرديم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5723, N'آيا هنگامي که استخوانهاي پوسيده‌اي شديم (ممکن است زنده شويم)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5724, N'مي‌گويند: «اگر قيامتي در کار باشد، بازگشتي است زيانبار!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5725, N'ولي (بدانيد) اين بازگشت تنها با يک صيحه عظيم است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5726, N'ناگهان همگي بر عرصه زمين ظاهر مي‌گردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5727, N'آيا داستان موسي به تو رسيده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5728, N'در آن هنگام که پروردگارش او را در سرزمين مقدس «طوي» ندا داد (و گفت):')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5729, N'به سوي فرعون برو که طغيان کرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5730, N'و به او بگو: «آيا مي‌خواهي پاکيزه شوي؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5731, N'و من تو را به سوي پروردگارت هدايت کنم تا از او بترسي (و گناه نکني)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5732, N'سپس موسي بزرگترين معجزه را به او نشان داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5733, N'اما او تکذيب و عصيان کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5734, N'سپس پشت کرد و پيوسته (براي محو آيين حق) تلاش نمود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5735, N'و ساحران را جمع کرد و مردم را دعوت نمود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5736, N'و گفت: «من پروردگار برتر شما هستم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5737, N'از اين رو خداوند او را به عذاب آخرت و دنيا گرفتار ساخت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5738, N'در اين عبرتي است براي کسي که (از خدا) بترسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5739, N'آيا آفرينش شما (بعد از مرگ) مشکل‌تر است يا آفرينش آسمان که خداوند آن را بنا نهاد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5740, N'سقف آن را برافراشت و آن را منظم ساخت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5741, N'و شبش را تاريک و روزش را آشکار نمود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5742, N'و زمين را بعد از آن گسترش داد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5743, N'و از آن آب و چراگاهش را بيرون آورد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5744, N'و کوه‌ها را ثابت و محکم نمود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5745, N'همه اينها براي بهره‌گيري شما و چهارپايانتان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5746, N'هنگامي که آن حادثه بزرگ رخ دهد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5747, N'در آن روز انسان به ياد کوششهايش مي‌افتد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5748, N'و جهنم براي هر بيننده‌اي آشکار مي‌گردد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5749, N'اما آن کسي که طغيان کرده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5750, N'و زندگي دنيا را مقدم داشته،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5751, N'مسلما دوزخ جايگاه اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5752, N'و آن کس که از مقام پروردگارش ترسان باشد و نفس را از هوي بازدارد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5753, N'قطعا بهشت جايگاه اوست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5754, N'و از تو درباره قيامت مي‌پرسند که در چه زماني واقع مي‌شود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5755, N'تو را با يادآوري اين سخن چه کار؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5756, N'نهايت آن به سوي پروردگار تو است (و هيچ کس جز خدا از زمانش آگاه نيست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5757, N'کار تو فقط بيم‌دادن کساني است که از آن مي‌ترسند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5758, N'آنها در آن روز که قيام قيامت را مي‌بينند چنين احساس مي‌کنند که گويي توقفشان (در دنيا و برزخ) جز شامگاهي يا صبح آن بيشتر نبوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5759, N'چهره در هم کشيد و روي برتافت...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5760, N'از اينکه نابينايي به سراغ او آمده بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5761, N'تو چه مي‌داني شايد او پاکي و تقوا پيشه کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5762, N'يا متذکر گردد و اين تذکر به حال او مفيد باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5763, N'اما آن کس که توانگر است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5764, N'تو به او روي مي‌آوري،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5765, N'در حالي که اگر او خود را پاک نسازد، چيزي بر تو نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5766, N'اما کسي که به سراغ تو مي‌آيد و کوشش مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5767, N'و از خدا ترسان است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5768, N'تو از او غافل مي‌شوي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5769, N'هرگز چنين نيست که آنها مي‌پندارند؛ اين (قرآن) تذکر و يادآوري است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5770, N'و هر کس بخواهد از آن پند مي‌گيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5771, N'در الواح پرارزشي ثبت است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5772, N'الواحي والاقدر و پاکيزه،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5773, N'به دست سفيراني است‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5774, N'والا مقام و فرمانبردار و نيکوکار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5775, N'مرگ بر اين انسان، چقدر کافر و ناسپاس است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5776, N'(خداوند) او را از چه چيز آفريده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5777, N'او را از نطفه ناچيزي آفريد، سپس اندازه‌گيري کرد و موزون ساخت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5778, N'سپس راه را براي او آسان کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5779, N'بعد او را ميراند و در قبر پنهان نمود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5780, N'سپس هرگاه بخواهد او را زنده مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5781, N'چنين نيست که او مي‌پندارد؛ او هنوز آنچه را (خدا) فرمان داده، اطاعت نکرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5782, N'انسان بايد به غذاي خويش (و آفرينش آن) بنگرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5783, N'ما آب فراوان از آسمان فرو ريختيم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5784, N'سپس زمين را از هم شکافتيم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5785, N'و در آن دانه‌هاي فراواني رويانديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5786, N'و انگور و سبزي بسيار،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5787, N'و زيتون و نخل فراوان،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5788, N'و باغهاي پردرخت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5789, N'و ميوه و چراگاه،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5790, N'تا وسيله‌اي براي بهره‌گيري شما و چهارپايانتان باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5791, N'هنگامي که آن صداي مهيب [= صيحه رستاخيز] بيايد، (کافران در اندوه عميقي فرومي‌روند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5792, N'در آن روز که انسان از برادر خود مي‌گريزد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5793, N'و از مادر و پدرش،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5794, N'و زن و فرزندانش؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5795, N'در آن روز هر کدام از آنها وضعي دارد که او را کاملا به خود مشغول مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5796, N'چهره‌هائي در آن روز گشاده و نوراني است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5797, N'خندان و مسرور است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5798, N'و صورتهايي در آن روز غبارآلود است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5799, N'و دود تاريکي آنها را پوشانده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5800, N'آنان همان کافران فاجرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5801, N'در آن هنگام که خورشيد در هم پيچيده شود،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5802, N'و در آن هنگام که ستارگان بي‌فروغ شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5803, N'و در آن هنگام که کوه‌ها به حرکت درآيند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5804, N'و در آن هنگام که باارزش‌ترين اموال به دست فراموشي سپرده شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5805, N'و در آن هنگام که وحوش جمع شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5806, N'و در آن هنگام که درياها برافروخته شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5807, N'و در آن هنگام که هر کس با همسان خود قرين گردد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5808, N'و در آن هنگام که از دختران زنده به گور شده سؤال شود:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5809, N'به کدامين گناه کشته شدند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5810, N'و در آن هنگام که نامه‌هاي اعمال گشوده شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5811, N'و در آن هنگام که پرده از روي آسمان برگرفته شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5812, N'و در آن هنگام که دوزخ شعله‌ور گردد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5813, N'و در آن هنگام که بهشت نزديک شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5814, N'(آري در آن هنگام) هر کس مي‌داند چه چيزي را آماده کرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5815, N'سوگند به ستارگاني که بازمي‌گردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5816, N'حرکت مي‌کنند و از ديده‌ها پنهان مي‌شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5817, N'و قسم به شب، هنگامي که پشت کند و به آخر رسد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5818, N'و به صبح، هنگامي که تنفس کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5819, N'که اين (قرآن) کلام فرستاده بزرگواري است [= جبرئيل امين‌]')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5820, N'که صاحب قدرت است و نزد (خداوند) صاحب عرش، مقام والائي دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5821, N'در آسمانها مورد اطاعت (فرشتگان) و امين است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5822, N'و مصاحب شما [= پيامبر] ديوانه نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5823, N'او (جبرئيل) را در افق روشن ديده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5824, N'و او نسبت به آنچه از طريق وحي دريافت داشته بخل ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5825, N'اين (قرآن) گفته شيطان رجيم نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5826, N'پس به کجا مي‌رويد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5827, N'اين قرآن چيزي جز تذکري براي جهانيان نيست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5828, N'براي کسي از شما که بخواهد راه مستقيم در پيش گيرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5829, N'و شما اراده نمي‌کنيد مگر اينکه خداوند -پروردگار جهانيان- اراده کند و بخواهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5830, N'آن زمان که آسمان [= کرات آسماني‌] از هم شکافته شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5831, N'و آن زمان که ستارگان پراکنده شوند و فرو ريزند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5832, N'و آن زمان که درياها به هم پيوسته شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5833, N'و آن زمان که قبرها زير و رو گردد (و مردگان خارج شوند)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5834, N'(در آن زمان) هر کس مي‌داند آنچه را از پيش فرستاده و آنچه را براي بعد گذاشته است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5835, N'اي انسان! چه چيز تو را در برابر پروردگار کريمت مغرور ساخته است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5836, N'همان خدايي که تو را آفريد و سامان داد و منظم ساخت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5837, N'و در هر صورتي که خواست تو را ترکيب نمود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5838, N'(آري) آن گونه که شما مي‌پنداريد نيست؛ بلکه شما روز جزا را منکريد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5839, N'و بي‌شک نگاهباناني بر شما گمارده شده...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5840, N'والا مقام و نويسنده (اعمال نيک و بد شما)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5841, N'که مي‌دانند شما چه مي‌کنيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5842, N'به يقين نيکان در نعمتي فراوانند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5843, N'و بدکاران در دوزخند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5844, N'روز جزا وارد آن مي‌شوند و مي‌سوزند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5845, N'و آنان هرگز از آن غايب و دور نيستند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5846, N'تو چه مي‌داني روز جزا چيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5847, N'باز چه مي‌داني روز جزا چيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5848, N'روزي است که هيچ کس قادر بر انجام کاري به سود ديگري نيست، و همه امور در آن روز از آن خداست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5849, N'واي بر کم‌فروشان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5850, N'آنان که وقتي براي خود پيمانه مي‌کنند، حق خود را بطور کامل مي‌گيرند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5851, N'اما هنگامي که مي‌خواهند براي ديگران پيمانه يا وزن کنند، کم مي‌گذارند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5852, N'آيا آنها گمان نمي‌کنند که برانگيخته مي‌شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5853, N'در روزي بزرگ؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5854, N'روزي که مردم در پيشگاه پروردگار جهانيان مي‌ايستند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5855, N'چنين نيست که آنها (درباره قيامت) مي‌پندارند، به يقين نامه اعمال بدکاران در «سجين» است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5856, N'تو چه مي‌داني «سجين» چيست؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5857, N'نامه‌اي است رقم زده شده و سرنوشتي است حتمي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5858, N'واي در آن روز بر تکذيب‌کنندگان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5859, N'همانها که روز جزا را انکار مي‌کنند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5860, N'تنها کسي آن را انکار مي‌کند که متجاوز و گنهکار است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5861, N'(همان کسي که) وقتي آيات ما بر او خوانده مي‌شود مي‌گويد: «اين افسانه‌هاي پيشينيان است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5862, N'چنين نيست که آنها مي‌پندارند، بلکه اعمالشان چون زنگاري بر دلهايشان نشسته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5863, N'چنين نيست که مي‌پندارند، بلکه آنها در آن روز از پروردگارشان محجوبند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5864, N'سپس آنها به يقين وارد دوزخ مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5865, N'بعد به آنها گفته مي‌شود: «اين همان چيزي است که آن را انکار مي‌کرديد!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5866, N'چنان نيست که آنها (درباره معاد) مي‌پندارند، بلکه نامه اعمال نيکان در «عليين» است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5867, N'و تو چه مي‌داني «عليين» چيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5868, N'نامه‌اي است رقم‌خورده و سرنوشتي است قطعي،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5869, N'که مقربان شاهد آنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5870, N'مسلما نيکان در انواع نعمت‌اند:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5871, N'بر تختهاي زيباي بهشتي تکيه کرده و (به زيباييهاي بهشت) مي‌نگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5872, N'در چهره‌هايشان طراوت و نشاط نعمت را مي‌بيني و مي‌شناسي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5873, N'آنها از شراب (طهور) زلال دست‌نخورده و سربسته‌اي سيراب مي‌شوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5874, N'مهري که بر آن نهاده شده از مشک است؛ و در اين نعمتهاي بهشتي راغبان بايد بر يکديگر پيشي گيرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5875, N'اين شراب (طهور) آميخته با «تسنيم» است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5876, N'همان چشمه‌اي که مقربان از آن مي‌نوشند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5877, N'بدکاران (در دنيا) پيوسته به مؤمنان مي‌خنديدند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5878, N'و هنگامي که از کنارشان مي‌گذشتند آنان را با اشاره تمسخر مي‌کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5879, N'و چون به سوي خانواده خود بازمي‌گشتند مسرور و خندان بودند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5880, N'و هنگامي که آنها را مي‌ديدند مي‌گفتند: «اينها گمراهانند!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5881, N'در حالي که هرگز مأمور مراقبت و متکفل آنان [= مؤمنان‌] نبودند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5882, N'ولي امروز مؤمنان به کفار مي‌خندند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5883, N'در حالي که بر تختهاي آراسته بهشتي نشسته و (به سرنوشت شوم آنها) مي‌نگرند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5884, N'آيا (با اين حال) کافران پاداش اعمال خود را گرفتند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5885, N'در آن هنگام که آسمان [= کرات آسماني‌] شکافته شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5886, N'و تسليم فرمان پروردگارش شود -و سزاوار است چنين باشد-')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5887, N'و در آن هنگام که زمين گسترده شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5888, N'و آنچه در درون دارد بيرون افکنده و خالي شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5889, N'و تسليم فرمان پروردگارش گردد -و شايسته است که چنين باشد-')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5890, N'اي انسان! تو با تلاش و رنج بسوي پروردگارت مي‌روي و او را ملاقات خواهي کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5891, N'پس کسي که نامه اعمالش به دست راستش داده شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5892, N'بزودي حساب آساني براي او مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5893, N'و خوشحال به اهل و خانواده‌اش بازمي‌گردد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5894, N'و اما کسي که نامه اعمالش به پشت سرش داده شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5895, N'بزودي فرياد مي‌زند واي بر من که هلاک شدم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5896, N'و در شعله‌هاي سوزان آتش مي‌سوزد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5897, N'چرا که او در ميان خانواده‌اش پيوسته (از کفر و گناه خود) مسرور بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5898, N'او گمان مي‌کرد هرگز بازگشت نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5899, N'آري، پروردگارش نسبت به او بينا بود (و اعمالش را براي حساب ثبت کرد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5900, N'سوگند به شفق،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5901, N'و سوگند به شب و آنچه را جمع‌آوري مي‌کند،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5902, N'و سوگند به ماه آنگاه که بدر کامل مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5903, N'که همه شما پيوسته از حالي به حال ديگر منتقل مي‌شويد (تا به کمال برسيد).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5904, N'پس چرا آنان ايمان نمي‌آورند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5905, N'و هنگامي که قرآن بر آنها خوانده مي‌شود سجده نمي‌کنند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5906, N'بلکه کافران پيوسته آيات الهي را انکار مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5907, N'و خداوند آنچه را در دل پنهان مي‌دارند بخوبي مي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5908, N'پس آنها را به عذابي دردناک بشارت ده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5909, N'مگر کساني که ايمان آورده و اعمال صالح انجام داده‌اند، که براي آنان پاداشي است قطع‌نشدني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5910, N'سوگند به آسمان که داراي برجهاي بسيار است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5911, N'و سوگند به آن روز موعود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5912, N'و سوگند به «شاهد» و «مشهود»! [«شاهد»: پيامبر و گواهان اعمال، و «مشهود» : اعمال امت است‌]')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5913, N'مرگ بر شکنجه‌گران صاحب گودال (آتش)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5914, N'آتشي عظيم و شعله‌ور!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5915, N'هنگامي که در کنار آن نشسته بودند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5916, N'و آنچه را با مؤمنان انجام مي‌دادند (با خونسردي و قساوت) تماشا مي‌کردند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5917, N'آنها هيچ ايرادي بر مؤمنان نداشتند جز اينکه به خداوند عزيز و حميد ايمان آورده بودند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5918, N'همان کسي که حکومت آسمانها و زمين از آن اوست و خداوند بر همه چيز گواه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5919, N'کساني که مردان و زنان باايمان را شکنجه دادند سپس توبه نکردند، براي آنها عذاب دوزخ و عذاب آتش سوزان است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5920, N'و براي کساني که ايمان آوردند و اعمال شايسته انجام دادند، باغهايي از بهشت است که نهرها زير درختانش جاري است؛ و اين نجات و پيروزي بزرگ است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5921, N'گرفتن قهرآميز و مجازات پروردگارت به يقين بسيار شديد است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5922, N'اوست که آفرينش را آغاز مي‌کند و بازمي‌گرداند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5923, N'و او آمرزنده و دوستدار (مؤمنان) است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5924, N'صاحب عرش و داراي مجد و عظمت است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5925, N'و آنچه را مي‌خواهد انجام مي‌دهد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5926, N'آيا داستان لشکرها به تو رسيده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5927, N'لشکريان فرعون و ثمود؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5928, N'ولي کافران پيوسته در تکذيب حقند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5929, N'و خداوند به همه آنها احاطه دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5930, N'(اين آيات، سحر و دروغ نيست،) بلکه قرآن باعظمت است...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5931, N'که در لوح محفوظ جاي دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5932, N'سوگند به آسمان و کوبنده شب!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5933, N'و تو نمي‌داني کوبنده شب چيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5934, N'همان ستاره درخشان و شکافنده تاريکيهاست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5935, N'(به اين آيت بزرگ الهي سوگند) که هر کس مراقب و محافظي دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5936, N'انسان بايد بنگرد که از چه چيز آفريده شده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5937, N'از يک آب جهنده آفريده شده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5938, N'آبي که از ميان پشت و سينه‌ها خارج مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5939, N'مسلما او [= خدائي که انسان را از چنين چيز پستي آفريد] مي‌تواند او را بازگرداند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5940, N'در آن روز که اسرار نهان (انسان) آشکار ميشود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5941, N'و براي او هيچ نيرو و ياوري نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5942, N'سوگند به آسمان پرباران،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5943, N'و سوگند به زمين پرشکاف (که گياهان از آن سر برمي‌آورند)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5944, N'که اين (قرآن) سخني است که حق را از باطل جدا مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5945, N'و هرگز شوخي نيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5946, N'آنها پيوسته حيله مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5947, N'و من هم در برابر آنها چاره مي‌کنم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5948, N'حال که چنين است کافران را (فقط) اندکي مهلت ده (تا سزاي اعمالشان را ببينند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5949, N'منزه شمار نام پروردگار بلندمرتبه‌ات را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5950, N'همان خداوندي که آفريد و منظم کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5951, N'و همان که اندازه‌گيري کرد و هدايت نمود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5952, N'و آن کس را که چراگاه را به وجود آورد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5953, N'سپس آن را خشک و تيره قرار داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5954, N'ما بزودي (قرآن را) بر تو مي‌خوانيم و هرگز فراموش نخواهي کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5955, N'مگر آنچه را خدا بخواهد، که او آشکار و نهان را مي‌داند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5956, N'و ما تو را براي انجام هر کار خير آماده مي‌کنيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5957, N'پس تذکر ده اگر تذکر مفيد باشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5958, N'و بزودي کسي که از خدا مي‌ترسد متذکر مي‌شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5959, N'اما بدبخت‌ترين افراد از آن دوري مي‌گزيند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5960, N'همان کسي که در آتش بزرگ وارد مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5961, N'سپس در آن آتش نه مي‌ميرد و نه زنده مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5962, N'به يقين کسي که پاکي جست (و خود را تزکيه کرد)، رستگار شد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5963, N'و (آن که) نام پروردگارش را ياد کرد سپس نماز خواند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5964, N'ولي شما زندگي دنيا را مقدم مي‌داريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5965, N'در حالي که آخرت بهتر و پايدارتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5966, N'اين دستورها در کتب آسماني پيشين (نيز) آمده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5967, N'در کتب ابراهيم و موسي.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5968, N'آيا داستان غاشيه [= روز قيامت که حوادث وحشتناکش همه را مي‌پوشاند] به تو رسيده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5969, N'چهره‌هايي در آن روز خاشع و ذلت‌بارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5970, N'آنها که پيوسته عمل کرده و خسته شده‌اند (و نتيجه‌اي عايدشان نشده است)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5971, N'و در آتش سوزان وارد مي‌گردند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5972, N'از چشمه‌اي بسيار داغ به آنان مي‌نوشانند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5973, N'غذائي جز از ضريع [= خار خشک تلخ و بدبو] ندارند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5974, N'غذايي که نه آنها را فربه مي‌کند و نه از گرسنگي مي‌رهاند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5975, N'چهره‌هايي در آن روز شاداب و باطراوتند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5976, N'و از سعي و تلاش خود خشنودند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5977, N'در بهشتي عالي جاي دارند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5978, N'که در آن هيچ سخن لغو و بيهوده‌اي نمي‌شنوند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5979, N'در آن چشمه‌اي جاري است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5980, N'در آن تختهاي زيباي بلندي است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5981, N'و قدحهايي (که در کنار اين چشمه) نهاده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5982, N'و بالشها و پشتيهاي صف‌داده شده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5983, N'و فرشهاي فاخر گسترده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5984, N'آيا آنان به شتر نمي‌نگرند که چگونه آفريده شده است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5985, N'و به آسمان نگاه نمي‌کنند که چگونه برافراشته شده؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5986, N'و به کوه‌ها که چگونه در جاي خود نصب گرديده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5987, N'و به زمين که چگونه گسترده و هموار گشته است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5988, N'پس تذکر ده که تو فقط تذکر دهنده‌اي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5989, N'تو سلطه‌گر بر آنان نيستي که (بر ايمان) مجبورشان کني،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5990, N'مگر کسي که پشت کند و کافر شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5991, N'که خداوند او را به عذاب بزرگ مجازات مي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5992, N'به يقين بازگشت (همه) آنان به سوي ماست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5993, N'و مسلما حسابشان (نيز) با ماست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5994, N'به سپيده دم سوگند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5995, N'و به شبهاي دهگانه،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5996, N'و به زوج و فرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5997, N'و به شب، هنگامي که (به سوي روشنايي روز) حرکت مي‌کند سوگند (که پروردگارت در کمين ظالمان است)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5998, N'آيا در آنچه گفته شد، سوگند مهمي براي صاحبان خرد نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (5999, N'آيا نديدي پروردگارت با قوم «عاد» چه کرد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6000, N'و با آن شهر «ارم» باعظمت،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6001, N'همان شهري که مانندش در شهرها آفريده نشده بود!')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6002, N'و قوم «ثمود» که صخره‌هاي عظيم را از (کنار) دره مي‌بريدند (و از آن خانه و کاخ مي‌ساختند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6003, N'و فرعوني که قدرتمند و شکنجه‌گر بود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6004, N'همان اقوامي که در شهرها طغيان کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6005, N'و فساد فراوان در آنها به بار آوردند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6006, N'به همين سبب خداوند تازيانه عذاب را بر آنان فرو ريخت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6007, N'به يقين پروردگار تو در کمينگاه (ستمگران) است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6008, N'اما انسان هنگامي که پروردگارش او را براي آزمايش، اکرام مي‌کند و نعمت مي‌بخشد (مغرور مي‌شود و) مي‌گويد: «پروردگارم مرا گرامي داشته است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6009, N'و اما هنگامي که براي امتحان، روزيش را بر او تنگ مي‌گيرد (مأيوس مي‌شود و) مي‌گويد: «پروردگارم مرا خوار کرده است!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6010, N'چنان نيست که شما مي‌پنداريد؛ شما يتيمان را گرامي نمي‌داريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6011, N'و يکديگر را بر اطعام مستمندان تشويق نمي‌کنيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6012, N'و ميراث را (از راه مشروع و نامشروع) جمع کرده مي‌خوريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6013, N'و مال و ثروت را بسيار دوست داريد (و بخاطر آن گناهان زيادي مرتکب مي‌شويد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6014, N'چنان نيست که آنها مي‌پندارند! در آن هنگام که زمين سخت در هم کوبيده شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6015, N'و فرمان پروردگارت فرا رسد و فرشتگان صف در صف حاضر شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6016, N'و در آن روز جهنم را حاضر مي‌کنند؛ (آري) در آن روز انسان متذکر مي‌شود؛ اما اين تذکر چه سودي براي او دارد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6017, N'مي‌گويد: «اي کاش براي (اين) زندگيم چيزي از پيش فرستاده بودم!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6018, N'در آن روز هيچ کس همانند او [= خدا] عذاب نمي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6019, N'و هيچ کس همچون او کسي را به بند نمي‌کشد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6020, N'تو اي روح آرام‌يافته!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6021, N'به سوي پروردگارت بازگرد در حالي که هم تو از او خشنودي و هم او از تو خشنود است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6022, N'پس در سلک بندگانم درآي،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6023, N'و در بهشتم وارد شو!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6024, N'قسم به اين شهر مقدس [= مکه‌]،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6025, N'شهري که تو در آن ساکني،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6026, N'و قسم به پدر و فرزندش [= ابراهيم خليل و فرزندش اسماعيل ذبيح‌]،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6027, N'که ما انسان را در رنج آفريديم (و زندگي او پر از رنجهاست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6028, N'آيا او گمان مي‌کند که هيچ کس نمي‌تواند بر او دست يابد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6029, N'مي‌گويد: «مال زيادي را (در کارهاي خير) نابود کرده‌ام!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6030, N'آيا (انسان) گمان مي‌کند هيچ کس او را نديده (که عمل خيري انجام نداده) است؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6031, N'آيا براي او دو چشم قرار نداديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6032, N'و يک زبان و دو لب؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6033, N'و او را به راه خير و شر هدايت کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6034, N'ولي او از آن گردنه مهم نگذشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6035, N'و تو نمي‌داني آن گردنه چيست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6036, N'آزادکردن برده‌اي،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6037, N'يا غذا دادن در روز گرسنگي...')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6038, N'يتيمي از خويشاوندان،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6039, N'يا مستمندي خاک‌نشين را،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6040, N'سپس از کساني باشد که ايمان آورده و يکديگر را به شکيبايي و رحمت توصيه مي‌کنند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6041, N'آنها «اصحاب اليمين»اند (که نامه اعمالشان را به دست راستشان مي‌دهند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6042, N'و کساني که آيات ما را انکار کرده‌اند افرادي شومند (که نامه اعمالشان به دست چپشان داده مي‌شود).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6043, N'بر آنها آتشي است فروبسته (که راه فراري از آن نيست)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6044, N'به خورشيد و گسترش نور آن سوگند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6045, N'و به ماه هنگامي که بعد از آن درآيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6046, N'و به روز هنگامي که صفحه زمين را روشن سازد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6047, N'و به شب آن هنگام که زمين را بپوشاند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6048, N'و قسم به آسمان و کسي که آسمان را بنا کرده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6049, N'و به زمين و کسي که آن را گسترانيده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6050, N'و قسم به جان آدمي و آن کس که آن را (آفريده و) منظم ساخته،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6051, N'سپس فجور و تقوا (شر و خيرش) را به او الهام کرده است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6052, N'که هر کس نفس خود را پاک و تزکيه کرده، رستگار شده؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6053, N'و آن کس که نفس خويش را با معصيت و گناه آلوده ساخته، نوميد و محروم گشته است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6054, N'قوم «ثمود» بر اثر طغيان، (پيامبرشان را) تکذيب کردند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6055, N'آنگاه که شقي‌ترين آنها بپاخاست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6056, N'و فرستاده الهي [= صالح‌] به آنان گفت: «ناقه خدا [= همان شتري که معجزه الهي بود] را با آبشخورش واگذاريد (و مزاحم آن نشويد)!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6057, N'ولي آنها او را تکذيب و ناقه را پي کردند (و به هلاکت رساندند)؛ از اين رو پروردگارشان آنها (و سرزمينشان) را بخاطر گناهانشان در هم کوبيد و با خاک يکسان و صاف کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6058, N'و او هرگز از فرجام اين کار [= مجازات ستمگران‌] بيم ندارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6059, N'قسم به شب در آن هنگام که (جهان را) بپوشاند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6060, N'و قسم به روز هنگامي که تجلي کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6061, N'و قسم به آن کس که جنس مذکر و مؤنث را آفريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6062, N'که سعي و تلاش شما مختلف است:')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6063, N'اما آن کس که (در راه خدا) انفاق کند و پرهيزگاري پيش گيرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6064, N'و جزاي نيک (الهي) را تصديق کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6065, N'ما او را در مسير آساني قرار مي‌دهيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6066, N'اما کسي که بخل ورزد و (از اين راه) بي‌نيازي طلبد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6067, N'و پاداش نيک (الهي) را انکار کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6068, N'بزودي او را در مسير دشواري قرار مي‌دهيم؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6069, N'و در آن هنگام که (در جهنم) سقوط مي‌کند، اموالش به حال او سودي نخواهد داشت!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6070, N'به يقين هدايت کردن بر ماست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6071, N'و آخرت و دنيا از آن ماست،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6072, N'و من شما را از آتشي که زبانه مي‌کشد بيم مي‌دهم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6073, N'کسي جز بدبخت‌ترين مردم وارد آن نمي‌شود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6074, N'همان کس که (آيات خدا را) تکذيب کرد و به آن پشت نمود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6075, N'و بزودي با تقواترين مردم از آن دور داشته مي‌شود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6076, N'همان کس که مال خود را (در راه خدا) مي‌بخشد تا پاک شود.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6077, N'و هيچ کس را نزد او حق نعمتي نيست تا بخواهد (به اين وسيله) او را جزا دهد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6078, N'بلکه تنها هدفش جلب رضاي پروردگار بزرگ اوست؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6079, N'و بزودي راضي و خشنود مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6080, N'قسم به روز در آن هنگام که آفتاب برآيد (و همه جا را فراگيرد)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6081, N'و سوگند به شب در آن هنگام که آرام گيرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6082, N'که خداوند هرگز تو را وانگذاشته و مورد خشم قرار نداده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6083, N'و مسلما آخرت براي تو از دنيا بهتر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6084, N'و بزودي پروردگارت آنقدر به تو عطا خواهد کرد که خشنود شوي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6085, N'آيا او تو را يتيم نيافت و پناه داد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6086, N'و تو را گمشده يافت و هدايت کرد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6087, N'و تو را فقير يافت و بي‌نياز نمود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6088, N'حال که چنين است يتيم را تحقير مکن،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6089, N'و سؤال‌کننده را از خود مران،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6090, N'و نعمتهاي پروردگارت را بازگو کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6091, N'آيا ما سينه تو را گشاده نساختيم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6092, N'و بار سنگين تو را از تو برنداشتيم؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6093, N'همان باري که سخت بر پشت تو سنگيني مي‌کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6094, N'و آوازه تو را بلند ساختيم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6095, N'به يقين با (هر) سختي آساني است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6096, N'(آري) مسلما با (هر) سختي آساني است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6097, N'پس هنگامي که از کار مهمي فارغ مي‌شوي به مهم ديگري پرداز،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6098, N'و به سوي پروردگارت توجه کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6099, N'قسم به انجير و زيتون [يا: قسم به سرزمين شام و بيت المقدس‌]،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6100, N'و سوگند به «طور سينين»،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6101, N'و قسم به اين شهر امن [= مکه‌]،')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6102, N'که ما انسان را در بهترين صورت و نظام آفريديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6103, N'سپس او را به پايين‌ترين مرحله بازگردانديم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6104, N'مگر کساني که ايمان آورده و اعمال صالح انجام داده‌اند که براي آنها پاداشي تمام‌نشدني است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6105, N'پس چه چيز سبب مي‌شود که بعد از اين همه (دلايل روشن) روز جزا را انکار کني؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6106, N'آيا خداوند بهترين حکم‌کنندگان نيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6107, N'بخوان به نام پروردگارت که (جهان را) آفريد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6108, N'همان کس که انسان را از خون بسته‌اي خلق کرد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6109, N'بخوان که پروردگارت (از همه) بزرگوارتر است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6110, N'همان کسي که بوسيله قلم تعليم نمود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6111, N'و به انسان آنچه را نمي‌دانست ياد داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6112, N'چنين نيست (که شما مي‌پنداريد) به يقين انسان طغيان مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6113, N'از اينکه خود را بي‌نياز ببيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6114, N'و به يقين بازگشت (همه) به سوي پروردگار تو است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6115, N'به من خبر ده آيا کسي که نهي مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6116, N'بنده‌اي را به هنگامي که نماز مي‌خواند (آيا مستحق عذاب الهي نيست)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6117, N'به من خبر ده اگر اين بنده به راه هدايت باشد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6118, N'يا مردم را به تقوا فرمان دهد (آيا نهي کردن او سزاوار است)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6119, N'به من خبر ده اگر (اين طغيانگر) حق را انکار کند و به آن پشت نمايد (آيا مستحق مجازات الهي نيست)؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6120, N'آيا او ندانست که خداوند (همه اعمالش را) مي‌بيند؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6121, N'چنان نيست که او خيال مي‌کند، اگر دست از کار خود برندارد، ناصيه‌اش [= موي پيش سرش‌] را گرفته (و به سوي عذاب مي‌کشانيم)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6122, N'همان ناصيه دروغگوي خطاکار را!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6123, N'سپس هر که را مي‌خواهد صدا بزند (تا ياريش کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6124, N'ما هم بزودي مأموران دوزخ را صدا مي‌زنيم (تا او را به دوزخ افکنند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6125, N'چنان نيست (که آن طغيانگر مي‌پندارد)؛ هرگز او را اطاعت مکن، و سجده نما و (به خدا) تقرب جوي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6126, N'ما آن [= قرآن‌] را در شب قدر نازل کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6127, N'و تو چه مي‌داني شب قدر چيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6128, N'شب قدر بهتر از هزار ماه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6129, N'فرشتگان و «روح» در آن شب به اذن پروردگارشان براي (تقدير) هر کاري نازل مي‌شوند.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6130, N'شبي است سرشار از سلامت (و برکت و رحمت) تا طلوع سپيده!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6131, N'کافران از اهل کتاب و مشرکان (مي‌گفتند:) دست از آيين خود برنمي‌دارند تا دليل روشني براي آنها بيايد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6132, N'پيامبري از سوي خدا (بيايد) که صحيفه‌هاي پاکي را (بر آنها) بخواند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6133, N'و در آن نوشته‌هاي صحيح و پرارزشي باشد! (ولي هنگامي که آمد ايمان نياوردند، مانند اهل کتاب).')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6134, N'اهل کتاب (نيز در دين خدا) اختلاف نکردند مگر بعد از آنکه دليل روشن براي آنان آمد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6135, N'و به آنها دستوري داده نشده بود جز اينکه خدا را بپرستند در حالي که دين خود را براي او خالص کنند و از شرک به توحيد بازگردند، نماز را برپا دارند و زکات را بپردازند؛ و اين است آيين مستقيم و پايدار!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6136, N'کافران از اهل کتاب و مشرکان در آتش دوزخند، جاودانه در آن مي‌مانند؛ آنها بدترين مخلوقاتند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6137, N'(اما) کساني که ايمان آوردند و اعمال صالح انجام دادند، بهترين مخلوقات (خدا)يند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6138, N'پاداش آنها نزد پروردگارشان باغهاي بهشت جاويدان است که نهرها از زير درختانش جاري است؛ هميشه در آن مي‌مانند! (هم) خدا از آنها خشنود است و (هم) آنها از خدا خشنودند؛ و اين (مقام والا) براي کسي است که از پروردگارش بترسد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6139, N'هنگامي که زمين شديدا به لرزه درآيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6140, N'و زمين بارهاي سنگينش را خارج سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6141, N'و انسان مي‌گويد: «زمين را چه مي‌شود (که اين گونه مي‌لرزد)؟!»')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6142, N'در آن روز زمين تمام خبرهايش را بازگو مي‌کند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6143, N'چرا که پروردگارت به او وحي کرده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6144, N'در آن روز مردم بصورت گروه‌هاي پراکنده (از قبرها) خارج مي‌شوند تا اعمالشان به آنها نشان داده شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6145, N'پس هر کس هموزن ذره‌اي کار خير انجام دهد آن را مي‌بيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6146, N'و هر کس هموزن ذره‌اي کار بد کرده آن را مي‌بيند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6147, N'سوگند به اسبان دونده (مجاهدان) در حالي که نفس‌زنان به پيش مي‌رفتند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6148, N'و سوگند به افروزندگان جرقه آتش (در برخورد سمهايشان با سنگهاي بيابان)،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6149, N'و سوگند به هجوم آوران سپيده دم‌')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6150, N'که گرد و غبار به هر سو پراکندند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6151, N'و (ناگهان) در ميان دشمن ظاهر شدند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6152, N'که انسان در برابر نعمتهاي پروردگارش بسيار ناسپاس و بخيل است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6153, N'و او خود (نيز) بر اين معني گواه است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6154, N'و او علاقه شديد به مال دارد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6155, N'آيا نمي‌داند در آن روز که تمام کساني که در قبرها هستند برانگيخته مي‌شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6156, N'و آنچه در درون سينه‌هاست آشکار مي‌گردد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6157, N'در آن روز پروردگارشان از آنها کاملا باخبر است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6158, N'آن حادثه کوبنده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6159, N'و چه حادثه کوبنده‌اي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6160, N'و تو چه مي‌داني که حادثه کوبنده چيست؟! (آن حادثه همان روز قيامت است!)')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6161, N'روزي که مردم مانند پروانه‌هاي پراکنده خواهند بود،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6162, N'و کوه‌ها مانند پشم رنگين حلاجي‌شده مي‌گردد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6163, N'اما کسي که (در آن روز) ترازوهاي اعمالش سنگين است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6164, N'در يک زندگي خشنودکننده خواهد بود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6165, N'و اما کسي که ترازوهايش سبک است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6166, N'پناهگاهش «هاويه» [= دوزخ‌] است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6167, N'و تو چه مي‌داني «هاويه» چيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6168, N'آتشي است سوزان!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6169, N'افزون طلبي (و تفاخر) شما را به خود مشغول داشته (و از خدا غافل نموده) است.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6170, N'تا آنجا که به ديدار قبرها رفتيد (و قبور مردگان خود را برشمرديد و به آن افتخار کرديد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6171, N'چنين نيست که مي‌پنداريد، (آري) بزودي خواهيد دانست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6172, N'باز چنان نيست که شما مي‌پنداريد؛ بزودي خواهيد دانست!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6173, N'چنان نيست که شما خيال مي‌کنيد؛ اگر شما علم اليقين (به آخرت) داشتيد (افزون طلبي شما را از خدا غافل نمي‌کرد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6174, N'قطعا شما جهنم را خواهيد ديد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6175, N'سپس (با ورود در آن) آن را به عين اليقين خواهيد ديد.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6176, N'سپس در آن روز (همه شما) از نعمتهايي که داشته‌ايد بازپرسي خواهيد شد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6177, N'به عصر سوگند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6178, N'که انسانها همه در زيانند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6179, N'مگر کساني که ايمان آورده و اعمال صالح انجام داده‌اند، و يکديگر را به حق سفارش کرده و يکديگر را به شکيبايي و استقامت توصيه نموده‌اند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6180, N'واي بر هر عيبجوي مسخره‌کننده‌اي!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6181, N'همان کس که مال فراواني جمع‌آوري و شماره کرده (بي‌آنکه مشروع و نامشروع آن را حساب کند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6182, N'او گمان مي‌کند که اموالش او را جاودانه مي‌سازد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6183, N'چنين نيست که مي‌پندارد؛ بزودي در «حطمه» [= آتشي خردکننده‌] پرتاب مي‌شود!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6184, N'و تو چه مي‌داني «حطمه» چيست؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6185, N'آتش برافروخته الهي است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6186, N'آتشي که از دلها سرمي‌زند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6187, N'اين آتش بر آنها فروبسته شده،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6188, N'در ستونهاي کشيده و طولاني!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6189, N'آيا نديدي پروردگارت با فيل سواران [= لشکر ابرهه که براي نابودي کعبه آمده بودند] چه کرد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6190, N'آيا نقشه آنها را در ضلالت و تباهي قرار نداد؟!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6191, N'و بر سر آنها پرندگاني را گروه گروه فرستاد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6192, N'که با سنگهاي کوچکي آنان را هدف قرارمي‌دادند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6193, N'سرانجام آنها را همچون کاه خورده‌شده (و متلاشي) قرار داد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6194, N'(کيفر لشکر فيل‌سواران) بخاطر اين بود که قريش (به اين سرزمين مقدس) الفت گيرند (و زمينه ظهور پيامبر فراهم شود)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6195, N'الفت آنها در سفرهاي زمستانه و تابستانه (و بخاطر اين الفت به آن بازگردند)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6196, N'پس (بشکرانه اين نعمت بزرگ) بايد پروردگار اين خانه را عبادت کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6197, N'همان کس که آنها را از گرسنگي نجات داد و از ترس و ناامني ايمن ساخت.')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6198, N'آيا کسي که روز جزا را پيوسته انکار مي‌کند ديدي؟')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6199, N'او همان کسي است که يتيم را با خشونت مي‌راند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6200, N'و (ديگران را) به اطعام مسکين تشويق نمي‌کند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6201, N'پس واي بر نمازگزاراني که...')
GO
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6202, N'در نماز خود سهل‌انگاري مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6203, N'همان کساني که ريا مي‌کنند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6204, N'و ديگران را از وسايل ضروري زندگي منع مي‌نمايند!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6205, N'ما به تو کوثر [= خير و برکت فراوان‌] عطا کرديم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6206, N'پس براي پروردگارت نماز بخوان و قرباني کن!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6207, N'(و بدان) دشمن تو قطعا بريده‌نسل و بي‌عقب است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6208, N'بگو: اي کافران!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6209, N'آنچه را شما مي‌پرستيد من نمي‌پرستم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6210, N'و نه شما آنچه را من مي‌پرستم مي‌پرستيد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6211, N'و نه من هرگز آنچه را شما پرستش کرده‌ايد مي‌پرستم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6212, N'و نه شما آنچه را که من مي‌پرستم پرستش مي‌کنيد؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6213, N'(حال که چنين است) آيين شما براي خودتان، و آيين من براي خودم!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6214, N'هنگامي که ياري خدا و پيروزي فرارسد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6215, N'و ببيني مردم گروه گروه وارد دين خدا مي‌شوند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6216, N'پروردگارت را تسبيح و حمد کن و از او آمرزش بخواه که او بسيار توبه‌پذير است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6217, N'بريده باد هر دو دست ابولهب (و مرگ بر او باد)!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6218, N'هرگز مال و ثروتش و آنچه را به دست آورد به حالش سودي نبخشيد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6219, N'و بزودي وارد آتشي شعله‌ور و پرلهيب مي‌شود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6220, N'و (نيز) همسرش، در حالي که هيزم‌کش (دوزخ) است،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6221, N'و در گردنش طنابي است از ليف خرما!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6222, N'بگو: خداوند، يکتا و يگانه است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6223, N'خداوندي است که همه نيازمندان قصد او مي‌کنند؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6224, N'(هرگز) نزاد، و زاده نشد،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6225, N'و براي او هيچگاه شبيه و مانندي نبوده است!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6226, N'بگو: پناه مي‌برم به پروردگار سپيده صبح،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6227, N'از شر تمام آنچه آفريده است؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6228, N'و از شر هر موجود شرور هنگامي که شبانه وارد مي‌شود؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6229, N'و از شر آنها که با افسون در گره‌ها مي‌دمند (و هر تصميمي را سست مي‌کنند)؛')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6230, N'و از شر هر حسودي هنگامي که حسد مي‌ورزد!')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6231, N'بگو: پناه مي‌برم به پروردگار مردم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6232, N'به مالک و حاکم مردم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6233, N'به (خدا و) معبود مردم،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6234, N'از شر وسوسه‌گر پنهانکار،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6235, N'که در درون سينه انسانها وسوسه مي‌کند،')
INSERT [dbo].[Quran_Ayeh_FA] ([ID], [ayeh_text]) VALUES (6236, N'خواه از جن باشد يا از انسان!')
